INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17C9493E811881418C7F16526A29A45F', X'9F92C6112785E84BA695D961DD927047', X'9F92C6112785E84BA695D961DD927047', 2, NULL, NULL, 2, NULL, 0, 'In a different mixing bowl, mix together flour, cinnamon, baking soda, and salt.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'375E82BC6613C947B98D46AE3720AD17', X'9F92C6112785E84BA695D961DD927047', X'9F92C6112785E84BA695D961DD927047', 3, NULL, NULL, 2, NULL, 0, 'Add banana mixture to mixing bowl containing dry ingredients. Carefully mix together with a wooden spoon and add chocolate and almonds. Be careful not to overdo it.');
