INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE4D8452F773C041819123DD0E2BC12A', X'145B5F1DD79EE64CB0E0242597A77877', X'145B5F1DD79EE64CB0E0242597A77877', 1, NULL, NULL, 2, NULL, 0, 'Combine flours, sugar, butter, nuts, vanilla, and salt in a medium mixing bowl. Mix for 4 minutes using a dough hook or knead with your hands until the mixture is well combined and there are no large chunks of butter.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'637A0A3E905652498EE5470B5E447277', X'145B5F1DD79EE64CB0E0242597A77877', X'145B5F1DD79EE64CB0E0242597A77877', 2, NULL, NULL, 2, NULL, 0, 'Using your hands, form 3 different sized balls ( 1 3/4 inch, 1 1/4 inch, 3/4 inch). Place the large balls at the back of the cookie sheet and the smaller towards the front when placing in the oven. Bake for 11-14 minutes. The cookies should gain a slight amount of brownish color.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3DFC32039C14044995DA20FDF48B865', X'145B5F1DD79EE64CB0E0242597A77877', X'145B5F1DD79EE64CB0E0242597A77877', 3, NULL, NULL, 2, NULL, 0, 'Remove cookies from oven and allow them to cool completely on a cookie rack.');
