INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8376623C9C52274599BF4A6D09EC1573', X'145B5F1DD79EE64CB0E0242597A77877', X'145B5F1DD79EE64CB0E0242597A77877', 5, NULL, NULL, 2, NULL, 0, 'Fill melted chocolate into a pastry bag with a medium sized round tip and place a small amount of chocolate on the top of a large ball. Quickly place a medium sized ball on top, add chocolate and finish with the smallest ball. Continue this until you have used up all the cookie balls.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70230B867770514791BA7D08CC174612', X'145B5F1DD79EE64CB0E0242597A77877', X'145B5F1DD79EE64CB0E0242597A77877', 6, NULL, NULL, 2, NULL, 0, 'Allow for Chocolate to melt completely, then cover in powdered sugar. Enjoy!');
