INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F1FE33177BDFE4F8788738B756C7E2D', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 6, NULL, NULL, 2, NULL, 0, 'Pour boiling water over tomatoes and let stand for 3 minutes until softened; drain and set aside.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13C6DD03A7E67049A76BE4D7192FCFAB', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 7, NULL, NULL, 2, NULL, 0, 'Simmer eggplant in chicken broth for 10 minutes; drain well.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D534751F7941F47B286650E2EADA730', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 8, NULL, NULL, 2, NULL, 0, 'In blender or food processor, place tomatoes, drained eggplant, onion, herbs, garlic, salt and pepper. Cover and process until nearly smooth.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7D62DCFF607DB46A7A9ABA6ADFFDE44', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 9, NULL, NULL, 2, NULL, 0, 'Stir in chopped artichoke hearts.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28B032825CB5B548926813FB0244CE70', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 10, NULL, NULL, 2, NULL, 0, 'Arrange artichoke leaves on a serving platter.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83D8215CF54CBF4E9D29825DE6068A56', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 11, NULL, NULL, 2, NULL, 0, 'Spoon 1 tsp of of the eggplant mixture onto the wide end of artichoke leaves. Garnish with a fresh herb leaf, if desired.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B19A3B6E82D93E4E83AA415B1FDE864C', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 12, NULL, NULL, 2, NULL, 0, 'Arrange artichoke leaves on platter, surrounding a bowl of the eggplant mixtures.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C63DF6496B3314994D7E85B8C94EEED', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', X'3E94E0BA3B34714BB78C4FF3FFF8C4C4', 13, NULL, NULL, 2, NULL, 0, 'Use artichoke leaves to scoop up individual servings of the dip. Dip can be prepared up to 24 hours ahead and chilled until serving time.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C50E706CE031A84D893B69039B20449A', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 0, NULL, NULL, 2, NULL, 0, 'Cut off stems and snap off first few small leaves. Slice through tips, if desired. Trim dark green from bottom.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85FECC0B927B1E4F862A6BAE51526C0A', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 1, NULL, NULL, 2, NULL, 0, 'Plunge into acidified water to prevent discoloration.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BACF872A268EC4C9F53A7987BCD4EDA', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 2, NULL, NULL, 2, NULL, 0, 'Cook by preferred method. Drain.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47A16C9F1C9A22449E5F4E3414FE015D', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 3, NULL, NULL, 2, NULL, 0, 'Separate leaves from bottom, discarding any purple or thorny center leaves; set aside. Remove fuzzy center from bottom; set aside.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62362DD3F2D4FF4CA8D46BCA5BB05FC9', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 4, NULL, NULL, 2, NULL, 0, 'Trim sweet anise; slice thinly.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFD96BA0D15DBB4FB4844235AA1A3CA2', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 5, NULL, NULL, 2, NULL, 0, 'Combine olive oil and lemon juice in a non-reactive pan.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4116AB1CACDAE4ABF3D214AD1DB0AA2', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 6, NULL, NULL, 2, NULL, 0, 'Braise sweet anise, tightly covered, over low heat, stirring occasionally, until very tender for about 30 minutes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'218140D8CE987B4894E23A1594AA9BD5', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 7, NULL, NULL, 2, NULL, 0, 'Arrange prepared artichoke bottoms on top of braising sweet anise.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'095907F2BE8CBC43A2EB54B5A8938731', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 8, NULL, NULL, 2, NULL, 0, 'Cover; heat artichokes through, about 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3529BD74CA57A04CA2838A75A50FECBC', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 9, NULL, NULL, 2, NULL, 0, 'Add a tablespoon of water, as needed to keep food moist.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CC1167701180541948C3FECD11ECD61', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 10, NULL, NULL, 2, NULL, 0, 'Remove artichoke bottoms from pan. Hold warm.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3260F77D3DC9F2409DAC96B3F8CFFBD1', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 11, NULL, NULL, 2, NULL, 0, 'In a food processor or blender, puree the cooked sweet anise, chick peas, salt, pepper, and cayenne.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4B6380A0277BD49B22BC748D9E24B66', X'1AD4B75B959B5E4BADC4E1510FF2F27B', X'1AD4B75B959B5E4BADC4E1510FF2F27B', 12, NULL, NULL, 2, NULL, 0, 'Fill center of cooked artichoke bottom with a few tablespoons of the chick pea puree. Arrange cooked artichoke leaves around center.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'816053A71EA4A849B0BF7C65B4A87F00', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 0, NULL, NULL, 2, NULL, 0, 'Trim all the dark green parts from the artichokes leaving the base and the inner, light green leaves.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85F19C3561A71743A9B212C8E710D237', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 1, NULL, NULL, 2, NULL, 0, 'Slice top of leaves to about 1/2 inch above base. Cut the stem level with the base.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5180B9CC73EF084299B80A2B865F4D18', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 2, NULL, NULL, 2, NULL, 0, 'Cook artichokes in boiling salted water with the lemon and garlic until tender, about 20 to 25 minutes. Remove from water and cool.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A31E09F91F6C044BA93028864AC2DC1', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 3, NULL, NULL, 2, NULL, 0, 'Remove tender yellow leaves from artichokes; set aside. Remove and discard any purple leaves or fuzz from the center of artichokes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03F51F46A0E2774BBDF256468B53542D', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 4, NULL, NULL, 2, NULL, 0, 'Slice the artichoke bottoms crosswise into three even disks.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1EDAA6276BEE84EB8F9D3E96D759356', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 5, NULL, NULL, 2, NULL, 0, 'Salt and pepper artichoke disks and the reserved leaves; dredge in flour.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEA401161D10F14480F4693025BDC0F6', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 6, NULL, NULL, 2, NULL, 0, 'In skillet, cook artichoke disks and leaves in hot butter or oil until golden brown. Remove from skillet and drain on paper towels.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'787EADB9E8269E43B74290E203004C90', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 7, NULL, NULL, 2, NULL, 0, 'Place an artichoke disk on plate; top with layer of Mushroom Duxelle.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11F451A22E344C459C9CEEE2FE1D296F', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 8, NULL, NULL, 2, NULL, 0, 'Add another artichoke disk, another layer of Mushroom Duxelle, and top with final artichoke disk.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E9C1242D4AFB34F8AC811716A11EEBD', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 9, NULL, NULL, 2, NULL, 0, 'Place one stack on each plate for an appetizer or three stacks for main dish.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8C1EE9D260DB14A81B3FAB2F1517BBC', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 10, NULL, NULL, 2, NULL, 0, 'Surround stack(s) with Tomato Coulis and garnish with browned artichoke leaves, chives, lemon zest and fluted mushroom cap, as desired.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE3DCFAEB4C1C54EA864B09E261BDA26', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 11, NULL, NULL, 2, NULL, 0, 'Finely chop mushrooms with a knife.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B62C98022B2E44ABBA436C7EDCACB6F', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 12, NULL, NULL, 2, NULL, 0, 'Heat butter in large skillet, add shallots and garlic; cook two minutes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62813A0B7E11DF48A5F310BB63E86BFB', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 13, NULL, NULL, 2, NULL, 0, 'Add chopped mushrooms.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F228D7820C735A4181E94BB40DF7278F', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 14, NULL, NULL, 2, NULL, 0, 'Cook over high heat until dry. Season to taste with salt and pepper. Stir in truffle oil.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1EF5E77FF2941478CD302DAE7BD1CB0', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 15, NULL, NULL, 2, NULL, 0, 'In a nonreactive saucepan, heat butter.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'965147ADB77D0B4E879A5D7D3855B9EF', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 16, NULL, NULL, 2, NULL, 0, 'Add shallots and garlic; cook two minutes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49ECC061DE0DBF42998886A0A079CF0D', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 17, NULL, NULL, 2, NULL, 0, 'Add wine and tomatoes. Boil rapidly until tomatoes are soft.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C1DD7E28FC21441A5180D5D741E7BD8', X'33C3FEB480FA6D4389D576C2FBADFF11', X'33C3FEB480FA6D4389D576C2FBADFF11', 18, NULL, NULL, 2, NULL, 0, 'Puree mixture and press through sieve. Return mixture to saucepan and reduce to approximately 5 cups or until mixture coats a spoon. Season to taste.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB3E231B6765045AD9929CB31BDFEE2', X'D7AD657165E0EA4381105C99F1EFE192', X'D7AD657165E0EA4381105C99F1EFE192', 0, NULL, NULL, 2, NULL, 0, 'Wash artichokes; remove outer leaves until pale yellow leaves are exposed.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550D846759C401488E79D316345FA95B', X'D7AD657165E0EA4381105C99F1EFE192', X'D7AD657165E0EA4381105C99F1EFE192', 1, NULL, NULL, 2, NULL, 0, 'Cut off top two inches and stems so artichokes will sit upright. Remove fuzzy centers and rub all surfaces with half of lemon.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A92117F5EB5054BA550CEAE2A5639AB', X'D7AD657165E0EA4381105C99F1EFE192', X'D7AD657165E0EA4381105C99F1EFE192', 2, NULL, NULL, 2, NULL, 0, 'Add juice of remaining lemon to water and dip artichokes to preserve green color.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDBBE81AB4C39B42A92370F5D6B41AB5', X'D7AD657165E0EA4381105C99F1EFE192', X'D7AD657165E0EA4381105C99F1EFE192', 3, NULL, NULL, 2, NULL, 0, 'Mix mint and garlic with ¼ cup olive oil and spoon into hollows of artichokes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7F7EB52099C2E44A47BF880E12D9CEC', X'D7AD657165E0EA4381105C99F1EFE192', X'D7AD657165E0EA4381105C99F1EFE192', 4, NULL, NULL, 2, NULL, 0, 'Place in pan just large enough to hold them upright.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'011F704CB1427E46AB79AF14A8101432', X'D7AD657165E0EA4381105C99F1EFE192', X'D7AD657165E0EA4381105C99F1EFE192', 5, NULL, NULL, 2, NULL, 0, 'Add water to depth of three inches, remaining olive oil and salt to taste.');
