INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BACE4F3BA99AF4F8B309F92EA875A5D', X'A81354A6BCD3474EA3746C11DC19BC4B', X'A81354A6BCD3474EA3746C11DC19BC4B', 1, NULL, NULL, 2, NULL, 0, 'Stand artichokes upright in deep saucepan large enough to hold snugly. Add 1 teaspoon salt and 2 to 3 inches boiling water.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20F9A54546B57642BECB5EACC6F7E5E8', X'A81354A6BCD3474EA3746C11DC19BC4B', X'A81354A6BCD3474EA3746C11DC19BC4B', 2, NULL, NULL, 2, NULL, 0, 'Cover and boil gently 35 to 45 minutes, or until base can be pierced easily with fork. (Add a little more boiling water, if needed.)');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE59EF4175F48A4894D94C69F1CE3EAD', X'A81354A6BCD3474EA3746C11DC19BC4B', X'A81354A6BCD3474EA3746C11DC19BC4B', 3, NULL, NULL, 2, NULL, 0, 'Turn artichokes upside down to drain. Carefully remove center petals and fuzzy centers with a spoon and discard. Keep warm or chill as desired.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ADE087C4BEA8942890D875DFA9D9933', X'A81354A6BCD3474EA3746C11DC19BC4B', X'A81354A6BCD3474EA3746C11DC19BC4B', 4, NULL, NULL, 2, NULL, 0, 'In medium saucepan combine chicken broth, curry powder, cumin and garlic salt; bring to a boil. Remove from heat; stir in couscous and currants. Cover and let stand 5 minutes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E08B6F2371CE99498A109ED5CF022514', X'A81354A6BCD3474EA3746C11DC19BC4B', X'A81354A6BCD3474EA3746C11DC19BC4B', 5, NULL, NULL, 2, NULL, 0, 'Fluff couscous with a fork. Stir in green onion and almonds. Combine lemon peel, lemon juice and vegetable oil; stir into couscous.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75B8C48465229A4C86E6DEB49164C73F', X'A81354A6BCD3474EA3746C11DC19BC4B', X'A81354A6BCD3474EA3746C11DC19BC4B', 6, NULL, NULL, 2, NULL, 0, 'Gently spread artichoke leaves until center cone of leaves is revealed. Pull out center cone. With a spoon, scrape out any purple-tipped leaves and fuzz.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F6F384149C224B91B981312068698B', X'A81354A6BCD3474EA3746C11DC19BC4B', X'A81354A6BCD3474EA3746C11DC19BC4B', 7, NULL, NULL, 2, NULL, 0, 'Fill centers of artichokes with couscous mixture. Serve with plain yogurt, if desired.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D44631D820AB43A10DBEE03B25A9D1', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 0, NULL, NULL, 2, NULL, 0, 'Wash artichokes under cold running water, cut off stems at base and remove small bottom leaves.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'557CB95CBCE5374F9E14CACA60740C33', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 1, NULL, NULL, 2, NULL, 0, 'Cut off top quarter of artichokes; discard.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AA55998D1B7EA42BD3FF6FA70476724', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 2, NULL, NULL, 2, NULL, 0, 'Spread leaves, remove center leaves and fuzzy centers with a spoon and discard.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05E2E6A826BA7142A0772A05F965E366', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 3, NULL, NULL, 2, NULL, 0, 'Toss bread crumbs with cheese, olive oil, garlic, parsley, oregano, salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DA1236A98FA9B47BBE71B93ECF406BA', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 4, NULL, NULL, 2, NULL, 0, 'Stuff bread crumb mixture between leaves of artichokes and fill centers. Place stuffed artichokes in a 9-inch square baking dish.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'357E9D3E7769AC40A21EEC32CB3B3A6A', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 5, NULL, NULL, 2, NULL, 0, 'Pour 2 cups boiling water around the artichokes. Cover with lid or foil.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D47EB5FA510F5409862492F449A8851', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 6, NULL, NULL, 2, NULL, 0, 'Bake at 350 degrees F. for 40 minutes or until artichokes are tender.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07FA45E9E51AC5418F01D29E1FCB2B31', X'CC9578E9178A524E91A556322C52178D', X'CC9578E9178A524E91A556322C52178D', 7, NULL, NULL, 2, NULL, 0, 'Remove artichokes from baking dish and place on rack; cool to room temperature.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA8C9B4CEAB8D24C846344942797DB6D', X'C2766CD074FAA045A525881E1036034F', X'C2766CD074FAA045A525881E1036034F', 0, NULL, NULL, 2, NULL, 0, 'Heat shortening to 350°F.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89D9E183134AEA41B370CBA96FEF6951', X'C2766CD074FAA045A525881E1036034F', X'C2766CD074FAA045A525881E1036034F', 1, NULL, NULL, 2, NULL, 0, 'Mix together the batter mix, baking powder, water and beer.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3AD34CDBA94E24F9A5C9B2704D02FCA', X'C2766CD074FAA045A525881E1036034F', X'C2766CD074FAA045A525881E1036034F', 2, NULL, NULL, 2, NULL, 0, 'Dip artichokes in batter mix, then roll in bread crumbs mixed with Parmesan cheese.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFD2FC4B689A5F4C90761BECC014E146', X'C2766CD074FAA045A525881E1036034F', X'C2766CD074FAA045A525881E1036034F', 3, NULL, NULL, 2, NULL, 0, 'Fry 2 or 3 minutes or until light golden brown. *For preparation of baby artichokes, see the directions and photos under Basic Preparation. Trim to hearts, quarter, and place in water with lemon juice to prevent browning. Drain well before dipping in batter.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB6767ECF749C94082643084A4C738A3', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 0, NULL, NULL, 2, NULL, 0, 'Slice artichoke tops off, crosswise. Trim Stems.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3089BAC7B9E9BB45BCBE53853918919F', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 1, NULL, NULL, 2, NULL, 0, 'Boil or steam artichokes until bottoms pierce easily, or a petal pulls off easily.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD45AAFC882D3346A978E2916816918C', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 2, NULL, NULL, 2, NULL, 0, 'Drain artichokes. Cool.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E3073DCE3DEF458FAC922090C6855F', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 3, NULL, NULL, 2, NULL, 0, 'Cut each artichoke in half lengthwise and scrape out fuzzy center and any purple tipped petals.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DBBE5D75A39E54EBC2C459B8CED6DAC', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 4, NULL, NULL, 2, NULL, 0, 'Mix remaining ingredients in a large plastic bag. Place artichokes in the bag and coat all sides of the artichokes. For best flavor marinate in the mixture overnight in the refrigerator but should marinate at least one hour.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B0C88400F124549930172491B658987', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 5, NULL, NULL, 2, NULL, 0, 'Drain artichokes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0239AB165269C4EAD0D0B69D78FAD7F', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 6, NULL, NULL, 2, NULL, 0, 'Place cut side down on a grill over a solid bed of medium coals or gas grill on medium. Grill until lightly browned on the cut side, 5 to 7 minutes. Turn artichokes over and drizzle some of the remaining marinade over the artichokes.');
INSERT INTO `VWR INTERNAL Artichokes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60B029B3DD18B74394E9B3C58A3A6003', X'5659CF2839C7B64181795B58D9E80F07', X'5659CF2839C7B64181795B58D9E80F07', 7, NULL, NULL, 2, NULL, 0, 'Grill until petal tips are lightly charred, 3 to 4 minutes more.');
