INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'419C1F97A305A4478556AFADACF097CA', X'405A74009620DD41B10716E315EA4007', X'405A74009620DD41B10716E315EA4007', 0, 'https://www.moms-mexican-recipes.com/beef-quesadillas-recipe', 'Beef Quesadillas Recipe', 2, NULL, 0, 'Beef Quesadillas Recipe', '1', 'Main Dishes', 'https://www.moms-mexican-recipes.com/beef-quesadillas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/12/Beef-Quesadillas-Recipe.jpg', 'Laura', '6', '', '', '', 'December 15, 2017', 'Ingredients?#&?#&1 lbs ground beef?#&1 teaspoon salt to taste?#&½ teaspoon black pepper, freshly ground?#&2 cloves garlic, minced?#&1 onion, diced?#&1 tablespoon taco seasoning?#&2 tablespoon tomato paste?#&8 oz. can black beans, rinsed and drained?#&2 avocados, diced?#&4 tablespoons fresh cilantro, chopped?#&4 tablespoons canola oil, for cooking?#&4 large tortillas?#&8 oz. white Cheddar, shredded?#&1 lemon, cut into wedges?#&Your favorite sour cream, for serving', 'Instructions?#&?#&In a pan, add 2 tablespoons oil. Add beef, breaking up with a wooden spoon, season with salt and pepper, sauté with garlic and onion until softened about 3 to 4 minutes on medium heat.?#&Add taco seasoning, tomato paste, 1 tbsp water and black beans; cook on medium-low for 5 minutes. Turn off heat and mix in avocado and cilantro.?#&Preheat another a large non-stick pan over medium heat with 2 teaspoon oil. Place a tortilla in the pan and sprinkle half with cheese, beef mixture. Fold tortilla over and fry lightly on both sides until cheese is melted. Repeat with remaining tortillas.?#&Cut quesadillas half and serve with lime wedges and sour cream. Enjoy!');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'72E4B414C8B9D246A8CD6E380A4B8D30', X'1263B5BAE45B2F469D251B1BED601258', X'1263B5BAE45B2F469D251B1BED601258', 0, 'https://www.moms-mexican-recipes.com/enchilada-style-burrito-recipe', 'Enchilada Style Burrito Recipe', 2, NULL, 0, 'Enchilada Style Burrito Recipe', '0', 'Burritos, Enchiladas', 'https://www.moms-mexican-recipes.com/enchilada-style-burrito-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/11/Enchilada-Style-Burrito-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/11/Enchilada-Style-Burrito-Preparations-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/11/Enchilada-Style-Burritos-300x232.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/11/Enchilada-Style-Burritos-Final-300x300.jpg', 'Laura', '6', '20 mins', '20 mins', '40 mins', 'November 15, 2017', 'Ingredients?#&?#&½ tablespoon butter?#&½ medium onion, chopped?#&1 can (15 oz.) black beans?#&½ teaspoon red chile, powder?#&Salt to taste?#&1 cup Mexican cheese blend?#&1 cup Enchilada sauce?#&2 tortillas?#&?#&For the rice:?#&?#&¾ cup rice, raw?#&2 cup cold water?#&1 medium green chile or Serrano pepper, chopped?#&2 tablespoon cilantro leaves, chopped?#&1 tablespoon butter?#&2 teaspoon lime juice', 'Instructions?#&?#&In a pan, add butter or oil. Add rice and saute until gold color, about 3 to 4 minutes on medium heat.?#&Add the green chillis, cilantro and mix well. Add the water and cook the rice around 5-10 minutes. When rice is ready add in the lemon juice and mix.?#&Meanwhile, heat ½ tbsp of butter in small frying pan. Add onion, pinch of salt and saute till it becomes translucent. Add in the black beans and red chilli powder, drizzle 2 tablespoons water and saute it for 2 to 3 minutes.?#&Preheat the oven to 350 F and then put it on broil mode.?#&Place two tortillas in two separate cooking pans.?#&Add in the rice prepared at the center followed by the black beans mixture. Now pour the sauce, Mexican cheese blend. Roll it up and place it fold side down.?#&Pour the remaining enchilada sauce on top of the tortillas. Sprinkle the remaining cheese.?#&Broil in the oven for about 3 to 5 minutes or until the cheese melts and sauce bubbles.?#&Remove the burritos from the oven, add lettuce, guacamole if desired.Enchilada Style Burrito is now ready.?#&Serve hot and enjoy.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'714F8F891DC7774E87A99DCF34380868', X'8046B29CC8670F4CA230DA2EA7EF8BBD', X'8046B29CC8670F4CA230DA2EA7EF8BBD', 0, 'https://www.moms-mexican-recipes.com/mexican-chocolate-brownies', 'Mexican Chocolate Brownies', 2, NULL, 0, 'Mexican Chocolate Brownies', '1', 'Desserts', 'https://www.moms-mexican-recipes.com/mexican-chocolate-brownies', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/11/Mexican-Chocolate-Brownies.jpg', 'Laura', '6', '25 mins', '35 mins', '1 hour', 'November 11, 2017', 'Ingredients?#&?#&½ cup flour?#&1 ½ tablespoons ground cinnamon?#&½ teaspoons chili powder?#&10 tablespoons unsalted butter?#&1 ¼ cups sugar?#&1 cup unsweetened cocoa powder?#&¼ teaspoon salt?#&½ teaspoon vanilla extract?#&2 large eggs?#&?#&For frosting:?#&?#&3 cups powdered sugar?#&16 tablespoons unsalted butter, room temperature?#&pinch of salt?#&2 teaspoons vanilla extract?#&1 tablespoon heavy cream or milk?#&1 ½ tablespoons ground cinnamon', 'Instructions?#&?#&Preheat the oven to 325 -350 F degrees.?#&Prepare baking pan with parchment paper or oil. Set aside.?#&Whisk together flour, cinnamon and ground red pepper in a bowl.?#&Place the butter, sugar, cocoa, and salt in a large microwavable bowl. Heat in 45-second increments over medium-high heat, stirring after each increment, until butter is fully melted, allow to cool slightly.?#&Stir in vanilla extract and eggs, one at a time, until batter is thick and shiny.?#&Add flour mixture, stir until just mixed then beat vigorously by hand for another 30 seconds.?#&Pour batter into prepared pan and bake for 27-32 minutes or until the toothpick inserted in center of brownies have just a few crumbs sticking to it.?#&Cool brownies in pan for 15 minutes and then lift parchment sides and place brownies on a cooling rack to cool completely before frosting.?#&Beat together powdered sugar and butter in a medium bowl for 2-3 minutes. Add salt, vanilla, cream, and cinnamon and continue to mix until frosting is thick and smooth. Add the frosting to your brownies once they have cooled');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'516D3298F1507A409913188035D1D687', X'7AAA6A10FDCA8B4D9DF722DEE4665454', X'7AAA6A10FDCA8B4D9DF722DEE4665454', 0, 'https://www.moms-mexican-recipes.com/top-5-mexican-christmas-recipes', 'Top 5 Mexican Christmas Recipes', 2, NULL, 0, 'Top 5 Mexican Christmas Recipes', '2', 'Mexican Cooking Essentials', 'https://www.moms-mexican-recipes.com/top-5-mexican-christmas-recipes', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/10/Top-5-Mexican-Christmas-Recipes.jpg', '', '', '', '', '', 'October 23, 2017', '', '');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'A6F2A268941EE847A0214D173D7DFE0E', X'E0C5C4D4DA5F2D40B7EB4E9C6AA96F50', X'E0C5C4D4DA5F2D40B7EB4E9C6AA96F50', 0, 'https://www.moms-mexican-recipes.com/horchata-recipe', 'Horchata Recipe', 2, NULL, 0, 'Horchata Recipe', '0', 'Appetizers, Main Dishes', 'https://www.moms-mexican-recipes.com/horchata-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/10/Horchata-Recipe.jpg', 'Laura', '3', '20 mins', '', '20 mins', 'October 18, 2017', 'Ingredients?#&?#&1 cup of white rice?#&⅔ cup sugar?#&½ cup chopped almonds?#&1½ teaspoon ground cinnamon or 1 cinnamon stick?#&1 teaspoon vanilla?#&12 ounces evaporated milk?#&1½ cup almond milk or milk?#&7 cups cold water?#&Ice for serving', 'Instructions?#&?#&Wash and soak the rice.?#&Add cinnamon, and almonds in a bowl of water and keep in the fridge per night, or at least for 5 hours ( that the rice would be soft and slight ).?#&Strain the water from the cinnamon, rice, and almond mixture that were soaking, disposing of water.?#&Blend together the cinnamon, rice, and almond mixture with evaporated milk until a smoother mix is formed and the grains of rice are completely ground.?#&Strain the resulting liquid into a pitcher, and add the sugar, vanilla, and milk. Mix well until everything is well combined.?#&Add a liter of water, and serve with ice.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'BDB29D6F434A6D4E92C6BD7C10A87027', X'7D3D1E0213E1F34C9B16798D8AFC8881', X'7D3D1E0213E1F34C9B16798D8AFC8881', 0, 'https://www.moms-mexican-recipes.com/croquetas-de-pollo-recipe', 'Croquetas De Pollo Recipe', 2, NULL, 0, 'Croquetas De Pollo Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/croquetas-de-pollo-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/10/Croquetas-de-Pollo-Recipe-Full.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/10/Croquetas-de-Pollo-Preparation.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/10/Croquetas-de-Pollo-Preparation-Last.jpg', 'Laura', '4', '30 mins', '10 mins', '40 mins', 'October 10, 2017', 'Ingredients?#&?#&¼ cup unsalted butter?#&½ Spanish onion?#&½ cup all-purpose flour?#&2½cup whole milk?#&8 ounces chicken breast, boiled, cutted?#&1 teaspoon salt?#&⅓ teaspoon nutmeg?#&2 large eggs?#&1 cup breadcrumbs?#&Oil for frying', 'Instructions?#&?#&Heat the butter in a medium pan over medium-high heat. Cook the onions until they become golden, about 5 minutes. Add flour, mix well and continue cook for 5 minutes until the flour is cooked through and golden brown. Pour the milk into the flour mixture and cook, stirring continuously, for about 2 minutes, until you have a thick béchamel sauce.?#&Add the chicken, and sprinkle with salt and the nutmeg, and cook for another 2 minutes. You should now have a thick mixture that you can mold in your hands. Spread the mixture on a cookie sheet to cool for 1-2 hours in the fridge.?#&Take a spoon of the mixture and roll in your hands to make a small cylinder. Roll the cylinder in breadcrumbs, then in the eggs, and then again in the breadcrumbs and set aside on the plate. Repeat with the remaining mixture until you have about 36 croquetas.?#&In a small deep frying pan, heat the frying oil to 375 degrees. Working in small batches, add the croquetas, making sure they are covered completely in oil. Fry until golden, about 1 minute, then transfer to a paper towel-lined tray to let drain. Season with salt and serve hot.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'AC406D50EA216041854960B0B49B0D91', X'5918105C5B36634480FE80F6469A4E64', X'5918105C5B36634480FE80F6469A4E64', 0, 'https://www.moms-mexican-recipes.com/traditional-yellow-queso-recipe', 'Traditional Yellow Queso Recipe', 2, NULL, 0, 'Traditional Yellow Queso Recipe', '0', 'Salsa & Sauces', 'https://www.moms-mexican-recipes.com/traditional-yellow-queso-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/09/Traditional-Yellow-Queso-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/09/Traditional-Yellow-Queso-Ingredients-300x200.jpg', 'Laura', '6', '5 mins', '10 mins', '15 mins', 'September 29, 2017', 'Ingredients?#&?#&4 oz Mild Cheddar Cheese?#&4 oz Yellow American Cheese?#&4 oz Cream Cheese?#&¼ cup Sour Cream?#&5 oz Rotel Tomatoes and Chillies?#&½ tsp Paprika?#&½ tsp Garlic Powder?#&½ tsp Onion Powder?#&½ tsp Chili Powder', 'Instructions?#&?#&Combine all ingredients in a non-stick pot.?#&Simmer over low heat until all of the cheeses are fully melted.?#&Serve hot with chips or fresh Tortilla.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'015B4E1B36E472449DBE4F85993C9536', X'0CA2C4DA2A223E45A478BEE29F4E28CD', X'0CA2C4DA2A223E45A478BEE29F4E28CD', 0, 'https://www.moms-mexican-recipes.com/slow-cooker-skirt-steak-tacos-recipe', 'Slow Cooker Skirt Steak Tacos Recipe', 2, NULL, 0, 'Slow Cooker Skirt Steak Tacos Recipe', '0', 'Tacos', 'https://www.moms-mexican-recipes.com/slow-cooker-skirt-steak-tacos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/09/Slow-Cooker-Skirt-Steak-Tacos-Recipe-Final.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/09/Slow-Cooker-Skirt-Steak-Tacos.jpg', 'Laura', '3', '20 mins', '8 hours', '8 hours 20 mins', 'September 14, 2017', 'Ingredients?#&?#&2 lbs skirt steak?#&3 cloves garlic, minced?#&1 teaspoons cumin powder?#&1 teaspoon salt?#&1 teaspoon oregano?#&1 teaspoon black pepper?#&½ teaspoon granulated onion?#&1 large onion, sliced?#&3 tablespoons olive oil?#&½ cup water?#&8 corn tortillas?#&1 cup cuacamole?#&1 cup tomatos?#&⅓ cup sliced jalapeno?#&1 cup red onion,slicer?#&1 cup cilantro leaves?#&2 cups green leaf lettuce', 'Instructions?#&?#&Mix the minced garlic, cumin, salt, oregano, pepper, granulated onion in a small bowl, add the skirt steak and shake to combine.?#&Refrigerate for minimum 8 hours (better overnight).?#&Please sliced onion in the bottom of the slow cooker.?#&Transfer the flank steak on top of onion.?#&Add in the top 3 tablespoons of olive oil and ½ cup of water.?#&Cook over low heat for 6-9 hours.?#&Serve with Guacamole, tomatoes, red onion, jalapeno, cilantro and green leaf in Corn Tortillas.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'14084BB1EA436F488055588D98D49953', X'F91C2DEED9EE0F488FE1B9D4A46B5C4C', X'F91C2DEED9EE0F488FE1B9D4A46B5C4C', 0, 'https://www.moms-mexican-recipes.com/slow-cooker-steak-fajitas-recipe', 'Slow Cooker Steak Fajitas Recipe', 2, NULL, 0, 'Slow Cooker Steak Fajitas Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/slow-cooker-steak-fajitas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/09/Slow-Cooker-Steak-Fajitas-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/09/Slow-Cooker-Steak-Fajitas-Cooking-Preparation-300x300.jpg', 'Laura', '3', '10 mins', '4 hours', '4 hours 10 mins', 'September 8, 2017', 'Ingredients?#&?#&1 lbs beef, sliced?#&1 bell pepper, sliced?#&1 onion, sliced?#&10 ounces salsa?#&?#&2 tablespoon fajita seasoning:?#&?#&1 teaspoon cumin?#&1 teaspoon chili powder?#&1 teaspoon salt?#&1 teaspoon black pepper?#&½ teaspoon oregano?#&½ teaspoon sweet paprika?#&½ teaspoon onion powder?#&½ teaspoon garlic powder?#&?#&For serving:?#&?#&8 corn tortillas?#&2 cups green leaf lettuce', 'Instructions?#&?#&Prepare Fajita seasoning, in a small bowl mix all fajita ingredients together – cumin, chili powder, salt, black pepper, oregano, sweet paprika, onion, and garlic.?#&In the slow cooker put salsa sauce, beef, pepper, onion, on the top puy fajita and mix all well.?#&Cook on the LOW temperature around 6 hours, on high around 4 hours.?#&Serve with green leaf lettuce in the corn tortillas.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'8B3DCE999E0188408070F29E35E81D41', X'47FE8BE58AEB204DB549BC054C729355', X'47FE8BE58AEB204DB549BC054C729355', 0, 'https://www.moms-mexican-recipes.com/slow-cooker-carne-asada-street-tacos', 'Slow Cooker Carne Asada Street Tacos', 2, NULL, 0, 'Slow Cooker Carne Asada Street Tacos', '1', 'Main Dishes, Tacos', 'https://www.moms-mexican-recipes.com/slow-cooker-carne-asada-street-tacos', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Slow-Cooker-Carne-Asada-Street-Tacos-Recipes-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Slow-Cooker-Carne-Asada-Street-Tacos-Ingredients-300x240.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Slow-Cooker-Carne-Asada-Street-Tacos-Preparations-300x300.jpg', 'Laura', '6', '10 mins', '7 hours', '7 hours 10 mins', 'August 29, 2017', 'Ingredients?#&?#&1 pounds flank steak?#&¼ cup olive oil?#&¼ cup orange juice?#&⅛ cup Worcestire sauce?#&⅛ cup Soy Sauce?#&¼ cup cilantro leaves, chopped?#&3 cloves garlic, minced?#&1 tablespoon honey?#&1 teaspoon chili, powder?#&1 teaspoon cumin?#&1 cup avocado, chopped?#&1 cup onion, chopped?#&½ cup sour cream?#&2 cups tomatoes, chopped?#&10 corn tortilla', 'Instructions?#&?#&Mix oil, orange juice, Worcester sauce, soy sauce, cilantro, garlic, honey, chili, and cumin together in a blender.?#&Transfer the flank steak with sauce to a bowl, let cool for 5-10 minutes.?#&Place the flank steak with sauce in the crock pot.?#&Cook over low heat for 6-9 hours.?#&Serve with tomatoes, avocado, onion, cilantro, sour cream, and lemon.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'5605C331F98F554396C3B733E6B78178', X'DD22593369A40546BB17CBF284658E4A', X'DD22593369A40546BB17CBF284658E4A', 0, 'https://www.moms-mexican-recipes.com/red-chile-enchiladas-recipe', 'Red Chile Enchiladas Recipe', 2, NULL, 0, 'Red Chile Enchiladas Recipe', '1', 'Enchiladas, Main Dishes', 'https://www.moms-mexican-recipes.com/red-chile-enchiladas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Red-Chile-Enchiladas-Ingredients-300x200.jpg', 'Laura', '6', '20 mins', '15 mins', '35 mins', 'August 26, 2017', 'Ingredients?#&?#&10 dried New Mexico Chiles?#&2 cups canola oil?#&5 cloves garlic?#&1 fresh lime, juiced?#&½ tablespoon cumin, ground?#&1 teaspoon sugar?#&1 teaspoon salt?#&6 corn tortillas?#&2 cups mozzarella, grated?#&½ cup red onion, chopped?#&½ cup Cotija cheese, crumbled?#&½ cup sour cream', 'Instructions?#&?#&Put Chiles in a small bowl, cover with 4 cups boiling water, and let stay for 20 minutes.?#&Drain chiles, remove the seeds and stem.?#&Transfer chiles with 2 cups soaking liquid to a blender, let cool for 5-10 minutes.?#&Preheat the oven to 400º F?#&Heat 3 tablespoon canola oil in a medium skillet over medium heat, add garlic and cook until golden brown.?#&Transfer garlic with all oil to the food processor with the reserved chiles, also add lime juice, cumin, sugar, salt.?#&Mix until smooth and set sauce aside, strain through a sieve.?#&Transfer chile sauce to a medium size skillet and heat sauce over low - medium heat 10 minutes.?#&Pour rest 1 ½ oil into a smaller skillet over medium-high heat.Using tongs and working with one tortilla at a time, dip tortilla in oil and cook until slightly crisp, around 15 seconds. After pun in chile sauce to coat, and transfer to a plate.?#&Sprinkle with some of the mozzarella and onions in the middle of a Tortilla and roll up.?#&Prepared rolled enchiladas put on a baking sheet and bake until cheese is melted about 5 minutes.?#&Serve with Cotija cheese and sour cream.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'1272361F387649429DCE23F99755C1C4', X'0D4EED59D4B4E7488702F493FD157B18', X'0D4EED59D4B4E7488702F493FD157B18', 0, 'https://www.moms-mexican-recipes.com/beer-battered-fish-tacos-recipe', 'Beer Battered Fish Tacos Recipe', 2, NULL, 0, 'Beer Battered Fish Tacos Recipe', '0', 'Burritos, Main Dishes', 'https://www.moms-mexican-recipes.com/beer-battered-fish-tacos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Fish-Tacos-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Beer-Battered-Fish-Tacos-Recipe-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Fish-Tacos-Preparation-300x300.jpg', 'Laura', '4', '20 mins', '90 mins', '1 hour 50 mins', 'August 23, 2017', 'Ingredients?#&?#&2 cups shredded green cabbage?#&2 spoons sprigs cilantro, chopped?#&1 teaspoon chili powder?#&1 tablespoon salt?#&2 limes (1 cut into wedges)?#&1 ½ cups flour?#&1⁄2 cup cornstarch?#&1 (12-oz.) bottle dark beer?#&1 egg?#&1 cup canola oil?#&1 lb. boneless, skinless red snapper, cut into 1½` strips?#&1⁄4 red onion, thinly sliced?#&1 tomato, cored and chopped?#&1 cup sour cream or crema?#&Mexican hot sauce?#&?#&For serving:?#&?#&16 corn tortillas', 'Instructions?#&?#&In a medium size bowl, combine cabbage, chili powder, cilantro, 1 teaspoon salt and juice of 1 lime on top, mix everything well. Put in the fridge for 10 minutes.?#&In another bowl, put flour, cornstarch, 1 tablespoon salt and put in the fridge to get cool for 5 minutes. After put beer and egg, mix to make a batter. And put back to the fridge to get hydrate for 5 minutes more.?#&Heat tortillas on medium – low high heat around 1 minutes for each side. Put in the bowl and cover.?#&Heat frying pan with oil till high temperature, meanwhile prepare the fish, on the top of the strips of fish put 1 teaspoon salt after each piece steep to the batter and after directly to the heated oil and fry until crisp, about 3 minutes (Transfer to a rack set inside a sheet pan;keep warm in 200˚ oven).?#&To serve, layer 2 tortillas together, fill with some of the fish and cabbage, squeeze with a lime wedge, and garnish with onion, cilantro, tomato, sour cream, and hot sauce.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'955E578B3137C1469159298AC9ACA971', X'459E2DD962BFE240941A26BFFE8E0A69', X'459E2DD962BFE240941A26BFFE8E0A69', 0, 'https://www.moms-mexican-recipes.com/arroz-a-la-mexicana-recipe-mexican-rice', 'Arroz A La Mexicana Recipe (Mexican Rice)', 2, NULL, 0, 'Arroz A La Mexicana Recipe (Mexican Rice)', '1', 'Breakfasts, Main Dishes', 'https://www.moms-mexican-recipes.com/arroz-a-la-mexicana-recipe-mexican-rice', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Arroz-A-La-Mexicana-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Arroz-A-La-Mexicana-Ingredients-300x200.jpg', 'Laura', '5', '15 Mins', '35 Mins', '50 Mins', 'August 21, 2017', 'Ingredients?#&?#&2 cups chicken stock?#&2 ripe tomatoes chopped?#&2 cloves garlic?#&1⁄2 small onion?#&2 tablespoon canola oil?#&1 cup white rice?#&1 teaspoon salt?#&1⁄2 teaspoon black pepper?#&?#&For serving:?#&?#&cilantro leaves (not necessary)', 'Instructions?#&?#&In a blender chopped: chicken stock, tomatoes, 1 clove garlic, and onion until smooth texture.?#&Place in the hot oil pan 1 clove garlic and rice, cook a fairly high heat around 3-6 minutes until rice starts turn to golden.?#&Put mixed tomatoes in the frying pan, add salt and pepper, and reduce heat to low.?#&Cook until all of the water is absorbed and you can no longer see it bubbling to the top (around 20-30 minutes).?#&Remove from heat and let it rest for a few minutes, and you’re ready to serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'E063062C5240A64697AC19537AF671EE', X'AD8144AD10CB8B4E9A3373AC72A78E23', X'AD8144AD10CB8B4E9A3373AC72A78E23', 0, 'https://www.moms-mexican-recipes.com/cemita-poblana-recipe', 'Cemita Poblana Recipe', 2, NULL, 0, 'Cemita Poblana Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/cemita-poblana-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Cemita-Poblana-Recipe-300x300.jpg', 'Laura', '3', '15 mins', '25 mins', '40 mins', 'August 17, 2017', 'Ingredients?#&?#&1 cup flour?#&4 eggs, beaten?#&1 cup bread crumbs?#&4 (1⁄4″) thick veal cutlets?#&Kosher salt and freshly ground black pepper, to taste?#&1⁄4 cup canola oil?#&4 round rolls, split and toasted?#&2 avocados, pitted, peeled, and thinly sliced?#&12 oz. queso blanco or mozzarella, grated?#&8 thin slices yellow onion?#&8 chipotle chiles in adobo, finely chopped, plus 3 tbsp. sauce from the can', 'Instructions?#&?#&Put the Flour, Eggs and the Bread Crumbs in separate Bowl?#&Season veal with salt and pepper, and coat with flour, shaking off excess.?#&Dip in eggs, then dredge in bread crumbs and set aside.?#&Heat oil in a 12″ skillet over medium-high heat, and cook veal cutlets, turning once, until golden brown on both sides, about 6 minutes.?#&Transfer to paper towels to drain.?#&Place 1 veal cutlet on the bottom half of each roll and top with half an avocado, 3 oz. cheese, 2 slices onion, and 1⁄4 of the chipotle sauce.?#&Cover with top bun.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'88562110033E4C46AB0C5D43D6E0B40E', X'1D24F17359D78C4C89114100C609235C', X'1D24F17359D78C4C89114100C609235C', 0, 'https://www.moms-mexican-recipes.com/carne-adobada-recipe', 'Carne Adobada Recipe (Red Chile Pork Stew)', 2, NULL, 0, 'Carne Adobada Recipe (Red Chile Pork Stew)', '1', 'Breakfasts, Main Dishes', 'https://www.moms-mexican-recipes.com/carne-adobada-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Red-Chile-and-Pork-Stew-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Carne-Adobada-Method-of-Cooking-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Carne-Adobada-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Carne-Adobada-Serving-300x240.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Carne-Adobada-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/08/Carne-Adobada-Ready-to-Serve-Final-300x240.jpg', 'Laura', '4', '35 Mins', '45 Mins', '1 hour 20 Mins', 'August 14, 2017', 'Ingredients?#&?#&3 oz. New Mexico chiles?#&1 teaspoon New Mexico chile powder?#&1 tablespoon. honey?#&1 tablespoon white wine vinegar?#&1 teaspoon ground cumin?#&1 teaspoon cloves?#&1⁄8 teaspoon cayenne pepper?#&1⁄2 lime (juice)?#&3 tablespoon olive oil?#&1 lb. boneless pork shoulder, cut into chunks?#&1 teaspoon salt?#&1 teaspoon freshly ground black pepper?#&Warm corn tortillas, for serving?#&?#&For Serving:?#&?#&cilantro leaves (not necessary)?#&tortillas or rice', 'Instructions?#&?#&In the oven over medium-high heat dry chiles, around 10-15 minutes (turning once, that both sides toasted ).?#&Put a large bowl, cover with 4 cups boiling water, and let stay for 20 minutes.?#&Drain chiles, reserving 1 cup soaking liquid, and transfer chiles with liquid to a blender, also put: chile powder, honey, vinegar, cumin, cloves, cayenne, and lime juice. Mix until smooth and set sauce aside.?#&In big frying pan heat to medium-high, add oil with pork, salt, and pepper.?#&Cook until browned on all sides (5 to 15 minutes)?#&Add sauce and reduce heat to medium-low and continue cook, until liquid is thickened and pork is tender, it could take around from 50 to 90 minutes.?#&Serve with warm corn tortillas or rice.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'8748DAE9D2333A4CB5A8DC6408098E62', X'280C0D1E058E8A41995BC6D935A7D6A9', X'280C0D1E058E8A41995BC6D935A7D6A9', 0, 'https://www.moms-mexican-recipes.com/chicken-tostadas-recipe', 'Easy Chicken Tostadas Recipe', 2, NULL, 0, 'Easy Chicken Tostadas Recipe', '0', 'Burritos, Main Dishes', 'https://www.moms-mexican-recipes.com/chicken-tostadas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Chicken-Tostadas-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Chicken-Tostadas-Ingredients-300x200.jpg', 'Laura', '6', '1 hour', '20 Mins', '1 hour 20 Mins', 'August 2, 2017', 'Ingredients?#&?#&Vegetable oil for frying?#&6 corn tortillas?#&Kosher salt?#&2 cups cooked shredded chicken?#&2 tablespoon freshly squeezed lime juice?#&2 tablespoon olive oil?#&¼ cup chopped fresh cilantro?#&Kosher salt and pepper?#&?#&Tostadas:?#&?#&Refried Beans, warm?#&Grated parmesan cheese for sprinkling?#&4 large leaves of iceberg lettuce, cored and shredded?#&1 Hass avocado, thinly sliced?#&2 cups Salsa Cruda?#&2 tablespoon sour cream?#&fresh coriander cilantro?#&?#&Refried Beans:?#&?#&2 tablespoons olive oil?#&½ cup Spanish onion, finely chopped?#&1 teaspoon ground coriander?#&3 cloves garlic, minced?#&One can of puree beans?#&¼ teaspoon kosher salt?#&Freshly ground black pepper?#&?#&Salsa Cruda:?#&?#&2 ripe tomatoes- cored and roughly chopped?#&2 tablespoon onion, finely chopped?#&¼ jalapeno, finely chopped?#&2 tablespoons chopped fresh cilantro?#&1 teaspoon kosher salt?#&Freshly ground black pepper', 'Instructions?#&?#&Refried Beans:?#&?#&Heat the oil in a large skillet over medium-high heat. Add the onion, coriander, and cook, stirring, until lightly browned, about 2 minutes. Add the garlic and cook, until lightly browned, about 1 minute more. Add the beans and cook, stirring frequently, until thick and amber brown in color, about 3 minutes. Stir in the salt and season with pepper to taste.?#&?#&Salsa Cruda:?#&?#&In a small bowl, mix together the tomatoes, onion, jalapeno, coriander, salt, and season with pepper to taste.?#&?#&Tostadas:?#&?#&To make the tortillas: Pour the oil for frying into a large heavy-bottomed pot. Place over medium heat and heat to 375 degrees F. Add the tortillas, one at a time, and fry until golden brown and crispy, about ½ minutes. Put aside for paper.?#&To make the chicken: In a small bowl, mix together the chicken, lime juice, oil, coriander, and salt and season with pepper to taste. Set aside.?#&To assemble the tostadas: Preheat the broiler.?#&Evenly mound the chicken on each tortilla and top with the lettuce. Top each tostada with avocado, salsa, and a dollop of sour cream. Sprinkle with Parmesan cheese and garnish the tostada with the coriander leaves.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'8B8B8E68F30CD3439385525C95489634', X'2731E42A7733EA48AA80A7F9168530B8', X'2731E42A7733EA48AA80A7F9168530B8', 0, 'https://www.moms-mexican-recipes.com/chiles-rellenos-recipe', 'Chiles Rellenos Recipe', 2, NULL, 0, 'Chiles Rellenos Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/chiles-rellenos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Chiles-Rellenos-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Chiles-Rellenos-Ingredients-300x200.jpg', 'Laura', '4', '30 mins', '25 mins', '55 mins', 'August 1, 2017', 'Ingredients?#&?#&4 poblano peppers?#&4 eggs, yolks and whites separated?#&¼ cup all-purpose flour?#&1 tablespoon salt?#&⅓ tablespoon freshly ground black pepper?#&1½ cups shredded jack cheese?#&½ cup fresh corn?#&1½ cup Vegetable oil, for frying?#&?#&For serving - Chile Relleno Tomato Sauce:?#&?#&1 tablespoon olive oil?#&½ yellow onion, chopped?#&¼ teaspoon cayenne pepper?#&2 cloves garlic?#&½ cup chicken stock?#&1 pound Roma tomatoes, cored and halved?#&1 tablespoon fresh cilantro leaves?#&1 teaspoon white wine vinegar?#&1 teaspoon salt?#&⅓ tablespoon freshly ground black pepper', 'Instructions?#&?#&First need to char the peppers. Turn a gas burner on high. Char the chiles on the burner grate, turning with tongs, until blackened all over, around 5-7 minutes. Or another way would be - char the peppers under the broiler.?#&Cover the bowl tightly with plastic wrap or a baking sheet and let the chiles steam until cool enough to handle, about 15 minutes.?#&Gently rub the chiles with paper towels to remove as much skin as possible.?#&In a medium bowl, use a hand mixer to beat the egg whites to stiff peaks. In a small bowl, mix 2 egg yolks with 1 tablespoon flour and ½ teaspoon salt.?#&Place the remaining flour on a small plate. Mix one-third of the egg whites into the 2 egg yolks, and then gently fold the egg yolk mixture into the remaining egg whites just until incorporated.?#&In a third bowl, mix together the cheese and corn.?#&Remove seeds and membranes from peppers by slicing them open along one side. Slice about two-thirds the length of the pepper, just long enough to remove the seeds with a small spoon. Stuff the peppers with the cheese mixture, squeezing the cheese gently that it would stay together.?#&Heat 1½ cup of oil in a large saucepan over medium-high heat.?#&Gently roll the peppers in the flour. Coat the peppers in the egg batter by dunking and spooning the batter all around. Lift the pepper, and gently spoon off any excess batter hanging from the bottom. Place in the hot oil and fry until golden, about 3 minutes per side. Place on paper towels to absorb the excess oil.?#&For serving Chile Relleno Tomato Sauce. Add the olive oil to a medium saute pan over medium heat. Then add the onions and cook until softened about 3-5 minutes, add the cayenne and garlic, and cook until fragrant, 1 minute longer. Add the chicken stock and the tomatoes with their juice and bring to a boil for 2 minutes. Remove from the heat and cool a few minutes. Mix in the blender, but the sauce will not be smooth. Add the cilantro, vinegar, salt, and pepper to taste.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'234087296D872E4BA47872DD1FC869AC', X'163C7F04831642418308260270F96563', X'163C7F04831642418308260270F96563', 0, 'https://www.moms-mexican-recipes.com/chicken-beef-fajitas-recipe', 'Chicken and Beef Fajitas Recipe', 2, NULL, 0, 'Chicken and Beef Fajitas Recipe', '0', 'Breakfasts, Burritos, Main Dishes', 'https://www.moms-mexican-recipes.com/chicken-beef-fajitas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Chicken-and-Beef-Fajitas-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Chicken-and-Beef-Fajitas-Ingredients-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Chicken-and-Beef-Fajitas-Preparation-300x300.jpg', 'Laura', '6', '8 hours', '10 mins', '8 hours 10 mins', 'July 31, 2017', 'Ingredients?#&?#&For the marinade?#&?#&1 tbsp. Olive Oil?#&1 tbsp. Chili Powder?#&1 tbsp.Worcestershire Sauce?#&1 tsp. Cumin Powder?#&¼ tsp. Red Pepper Flakes?#&Juice of 1 Lime?#&½ tsp. Salt?#&¼ tsp. Pepper?#&?#&For the Pico de Gallo?#&?#&2 Roma Tomatoes, diced?#&1 Jalapeno, chopped?#&1 Small Onion, diced?#&1 tbsp. Lime Juice?#&1 tbsp. Chopped Cilantro?#&Salt, to taste?#&?#&For the Vegetables?#&?#&1 White Onion, chopped?#&1 Bell Pepper, chopped?#&1 tbsp. Olive Oil?#&Salt, to taste?#&?#&Fixin''s:?#&?#&1 cup Chili Beans?#&6 pieces Flour Tortilla?#&¼ cup Grated Cheddar Cheese?#&¼ cup Sour Cream?#&Lime Wedges', 'Instructions?#&?#&Blend all ingredients for the marinade in a food processor.?#&Marinate the beef and chicken breast overnight.?#&Toss all ingredients for the pico de gallo in a bowl. Chill until ready to serve.?#&Grill the chicken for 4 minutes each side.?#&Grill the beef for 3 minutes per side. Rest for at least 10 minutes before carving.?#&Sautee the onions and bell peppers in a grill pan.?#&Reheat the tortillas on a non-stick pan.?#&Serve fajitas with tortilla, pico de gallo, sour cream, cheese, and chili beans.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'049E04FFB07BD94A92CE40048131456C', X'A218766E0756B742A493329D21942FF1', X'A218766E0756B742A493329D21942FF1', 0, 'https://www.moms-mexican-recipes.com/mexican-beef-empanada-recipe', 'Mexican Beef Empanada Recipe', 2, NULL, 0, 'Mexican Beef Empanada Recipe', '2', 'Breakfasts, Main Dishes', 'https://www.moms-mexican-recipes.com/mexican-beef-empanada-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Beef-Empanada-Pastry-Preparation.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Beef-Empanada-Recipe.jpg', 'Laura', '7', '1 hour', '20 Mins', '1 hour 20 Mins', 'July 29, 2017', 'Ingredients?#&?#&For the Pastry?#&?#&5 grams Yeast?#&Pinch of Salt?#&100 grams Butter?#&30 ml Warm Milk?#&150 grams Flour?#&1 Egg Yolk?#&Pinch of Sugar?#&?#&For the Filling?#&?#&1 clove Garlic?#&1 Jalapeno, chopped?#&Salt, to taste?#&2 Tbsp. Creme Fraiche?#&1 Tbsp. Chopped Parsley?#&Pepper, to taste?#&1 small White Onion, diced?#&1 small Bell Pepper, diced?#&200 grams Ground Beef?#&1 Egg?#&1 tbsp. Tomato Paste?#&¼ tsp. Chili Powder', 'Instructions?#&?#&Mix milk, sugar, and yeast in a bowl.?#&Set the flour in a bowl and make a well in the center.?#&Add the yeast mixture, egg yolk, and butter into the flour.?#&Knead the dough until it comes into a smooth ball.?#&Chill the dough overnight.?#&Sautee garlic and onions in a pan.?#&Add the bell pepper and jalapenos. Sautee for 1-2 minutes.?#&Add the beef and saute until brown.?#&Season with salt and pepper.?#&Mix in the egg, creme fraiche, tomato paste, and parsley.?#&Roll out the dough and cut into 4-6 inch circles with a pastry cutter.?#&Spoon the filling into the center of your dough.?#&Fold the filled dough close and seal by crimping or by pressing on the edges with a fork.?#&Pre-heat the oven to 200C.?#&Arrange the empanadas on a baking sheet.?#&Brush the empanadas with egg wash.?#&Bake for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'83720DBA0699C24F957499929E38CBB1', X'62652E957C5E49419F7085773C3C84BD', X'62652E957C5E49419F7085773C3C84BD', 0, 'https://www.moms-mexican-recipes.com/tacos-carne-asada-recipe', 'Tacos Carne Asada Recipe', 2, NULL, 0, 'Tacos Carne Asada Recipe', '0', 'Burritos, Main Dishes', 'https://www.moms-mexican-recipes.com/tacos-carne-asada-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Tacos-Carne-Asada-Recipe.jpg', 'Laura', '6', '2 hours', '45 mins', '2 hours 45 mins', 'July 28, 2017', 'Ingredients?#&?#&2 pounds flank steak, trimmed and cut with the grain into 5- to 6-inch lengths?#&?#&For Marinating the Steak (red Mojo sauce):?#&?#&4 garlic cloves, minced?#&2-4 small chili pepper, minced?#&1 large handful fresh cilantro leaves, finely chopped?#&1 tablespoons salt?#&1 teaspoon ground black pepper?#&2 limes, juiced?#&1 orange, juiced?#&⅓ cup white vinegar?#&½ cup olive oil?#&1 tablespoons salt?#&1 teaspoon ground black pepper?#&⅓ cup olive oil for coating the grill?#&?#&For serving:?#&?#&16 (7-inch) corn tortillas?#&Shredded romaine or iceberg lettuce?#&Chopped white onion?#&Shredded Jack cheese?#&2 limes, cut in wedges?#&?#&Pico de Gallo:?#&?#&2 vine-ripe tomatoes, chopped?#&½ medium red onion, chopped?#&1 green onions, sliced?#&1 chile pepper, minced?#&1 small handful fresh cilantro leaves, chopped?#&2 garlic cloves, minced?#&½ lime, juiced?#&¼ cup extra-virgin olive oil?#&1 teaspoon salt', 'Instructions?#&?#&Marinate the steak with red Mojo sauce. In a bowl, mash together the garlic, chili peppers ( 2 or 4 depends on how spicy you like), cilantro, salt, and pepper to make a paste. Add limes juice, orange juice, vinegar, and oil. Shake it up really well to combine. Pour the marinade over the steak, wrap tightly in plastic wrap and refrigerate for 1 hour or up to 8 hours, the best time is around 4 hours, it allows the citrus acid to tenderizes the meat and absorbs all the flavor.?#&Preheat an outdoor grill or a ridged grill pan over medium-high flame. Clean and oil the grilling grate, to prevent the meat from sticking.?#&Pull the steak out of the marinade and season the steak on both sides with salt and pepper.?#&Grill steaks immediately over high heat, until browned on both sides and cooked until desired doneness, about 5 to 7 minutes per side for medium.?#&Remove the steak to a cutting board and let it rest for 5 minutes to allow the juices to settle. Thinly slice the steak across the grain on a diagonal. Slice steaks against the grain into ¼ inch strips.?#&Before serving, need to make Pico De Gallo. In a bowl, mix all ingredients – tomatoes, onions, pepper, cilantro, garlic, lime juice, oil, and salt together. Toss thoroughly. Let it sit for 15 minutes to allow the flavors to mix.?#&Warm the tortillas on a grill for about a minute on each. Assemble tacos by stacking 2 warm tortillas and adding the steak down the center, sprinkle with some lettuce, onion. On the top with a sprinkle of the onion relish and a large spoonful of the pureed salsa Pico De Gallo, add as much cheese as you like. Garnish with lime wedges.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'BC74C4918FC7394D983621D76336A05B', X'EF07402EC1656D45B349DBC06899CDE1', X'EF07402EC1656D45B349DBC06899CDE1', 0, 'https://www.moms-mexican-recipes.com/one-pan-mexican-chicken-rice-recipe', 'One Pan Mexican Chicken Rice Recipe', 2, NULL, 0, 'One Pan Mexican Chicken Rice Recipe', '0', 'Breakfasts, Main Dishes', 'https://www.moms-mexican-recipes.com/one-pan-mexican-chicken-rice-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Chicken-Rice-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Chicken-Rice-Ingredients-300x200.jpg', 'Laura', '2-3', '10 mins', '20 mins', '30 mins', 'July 13, 2017', 'Ingredients?#&?#&1 tablespoon extra virgin olive oil?#&½ white onion, diced finely?#&½ pound boneless skinless chicken breasts, cut into bite-sized pieces?#&½ teaspoon of kosher salt and freshly ground pepper to taste?#&1 clove of garlic, minced?#&½ cup uncooked long grain white rice?#&½ (10 oz) can Old El Paso red enchilada sauce?#&½ (10 oz) can diced tomatoes and green chilies, undrained?#&½ cup water?#&¼ teaspoon ground cumin?#&½ cup shredded cheddar cheese or Mexican-blend cheese?#&(optional) Toppings: sour cream, tomatoes, diced avocado, cilantro', 'Instructions?#&?#&In a large skillet (about 12-inch) or pan, sautée onions in two tablespoons of olive oil over medium heat. Season chicken with salt and pepper.?#&Once onions soften, increase the heat to medium-high and add chicken to the pan.?#&Brown the chicken pieces and add the garlic. Cook for about 1 more minute.?#&Push chicken to one side of the pan and add an additional tablespoon of olive oil to another side.?#&Add the uncooked rice in the olive oil and saute it for a couple of minutes.?#&Add the enchilada sauce, diced tomatoes, water, and cumin. Stir to combine. Bring mixture to a boil then lower the heat to a simmer and cover the pan with a tight fitting lid.?#&Cook chicken and rice mixture covered for about 15 minutes or until rice is tender and the liquid is absorbed making sure to stir every once in a while.?#&Sprinkle cheese on top and cover with a lid, letting it sit for 1-2 minutes or until cheese has melted.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'D797EC1430F57344928961D24044274A', X'5749C6D77C45CF449E26E9921FBBF989', X'5749C6D77C45CF449E26E9921FBBF989', 0, 'https://www.moms-mexican-recipes.com/tequila-lime-shrimp-recipe', 'Tequila Lime Shrimp Recipe', 2, NULL, 0, 'Tequila Lime Shrimp Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/tequila-lime-shrimp-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Tiquela-Lime-Shrimp-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Tequila-Lime-Shrimp-Ingredients-300x200.jpg', 'Laura', '3', '10 mins', '20 mins', '30 mins', 'July 12, 2017', 'Ingredients?#&?#&½ pound peeled and deveined shrimp raw?#&½ teaspoon salt?#&¼ teaspoon pepper?#&2 dried ancho chili peppers stems and seeds removed?#&1 tablespoon olive oil?#&2 cloves garlic grated?#&1 oz tequila?#&¼ cup lime juice?#&1 tablespoon butter', 'Instructions?#&?#&Sprinkle the shrimp evenly with salt and pepper and set aside.?#&Place the dried chilis in a medium bowl and cover with boiling water. Steep chilis in the hot water until softened, about 20 minutes.?#&Puree softened chilis and ½ cup of the steeping liquid in a food processor or blender until smooth and set aside.?#&Heat a large, deep skillet over medium-high heat and add olive oil to the pan. Stir in garlic and warm until fragrant, about 15 seconds, being careful not to burn.?#&Add shrimp to the pan, without overcrowding, and heat for 90 seconds per side. The minute the shrimp turns pink, it is cooked so be careful not to overcook. It shouldn''t take more than 3-4 minutes total. Remove cooked shrimp from the pan and set aside.?#&Stir the tequila into the hot skillet and use a wooden spoon to deglaze any browned bits from the bottom of the pan.?#&Return the cooked shrimp along with the pureed chilis and lime juice to the pan. Bring to a simmer then reduce to low and heat for 4-5 minutes or until warmed through.?#&Right before serving, finish the shrimp with butter and stir until melted through. Check for seasoning and add more salt or pepper to taste.?#&Serve with warmed tortillas, rice or tossed into pasta if you wish.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'E290E0DC81B7F246AE0D2AFC56710C38', X'F4B34709EF7B1C4489F2B7923C6340FD', X'F4B34709EF7B1C4489F2B7923C6340FD', 0, 'https://www.moms-mexican-recipes.com/pork-carnitas-recipe', 'Pork Carnitas Recipe', 2, NULL, 0, 'Pork Carnitas Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/pork-carnitas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Pork-Carnitas-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Pork-Carnitas-Ingredients-300x200.jpg', 'Laura', '6-8', '15 mins', '45 mins', '1 hour', 'July 11, 2017', 'Ingredients?#&?#&2-pound boneless pork butt, fat trimmed and cut into 2-inch cubes?#&¾ tsp salt?#&½ tsp pepper?#&½ tsp ground cumin?#&½ onion, peeled and halved?#&1 bay leaf?#&½ tsp dried oregano?#&1 Tb fresh lime juice?#&1 C water?#&1 medium orange, juiced and keep the spent halves', 'Instructions?#&?#&Adjust oven rack to lower-middle position and heat to 300 degrees. Combine all the ingredients in a large Dutch oven. Bring the mixture to a simmer over medium-high heat, uncovered. Once it simmers, cover pot and transfer it to the oven. Cook until the meat falls apart when prodded with a fork, about 2 hours.?#&Remove the pot from the oven and turn on the broiler. Use a slotted spoon to remove the meat from the pan and place it on a large foil-lined jelly roll pan. Remove and discard everything from the pot except for the cooking liquid. Place the pot over high heat on the stove and boil until it thickens and syrupy, about 20 - 30 minutes. You should have about 1 C of liquid remaining when it is finished.?#&While the liquid is reducing, use two forks to pull each cube of pork into three equal sized pieces. Once the liquid has reduced, gently fold in the pieces of pork into the pot. Try not to break up the pork any further. Taste and add additional salt and pepper.?#&Spread the pork back onto the foil lined the pan and evenly spread the meat around so there is a single layer of meat. Place the jelly roll pan on the lower middle rack of the oven and broil until the top of the meat is well browned and edges are slightly crisp about 5 to 8 minutes. Using a wide metal spatula, flip the pieces of meat and broil the other side until well browned and edges are slightly crisp, 5 to 8 minutes. Serve immediately in a tortilla with all your favorite toppings.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'276186B550688044B6501206C7DF7E10', X'8B556D622250CB458F276614E69B48C9', X'8B556D622250CB458F276614E69B48C9', 0, 'https://www.moms-mexican-recipes.com/mexican-chicken-pasta-recipe', 'Mexican Chicken Pasta Recipe', 2, NULL, 0, 'Mexican Chicken Pasta Recipe', '2', 'Pasta', 'https://www.moms-mexican-recipes.com/mexican-chicken-pasta-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Chicken-Pasta-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Chicken-Pasta-Ingredients-300x200.jpg', 'Laura', '3', '20 mins', '30 mins', '50 mins', 'July 10, 2017', 'Ingredients?#&?#&1 tablespoon olive oil?#&½ lb chicken breast?#&½ tablespoon cumin, divided?#&½ tablespoon chili powder, divided?#&¼ teaspoon salt, divided?#&1 large bell pepper, cleaned and sliced?#&2 oz diced green chiles?#&4 oz pasta (fettuccine)?#&¼ cups heavy cream?#&⅓ cup milk?#&½ cup cheddar cheese, shredded?#&¼ cup mozzarella cheese, shredded', 'Instructions?#&?#&Slice chicken into small bites.?#&Heat olive oil in a large skillet.?#&Add chicken slices and immediately add half of cumin, half of chili powder and half the amount of salt. Cook for several minutes on high heat, constantly turning, until the chicken is completely cooked through. Remove the chicken from the skillet.?#&To the same skillet, add sliced bell peppers. Cook for a couple of minutes until they soften. Add diced green chiles. Add back cooked chicken to the skillet.?#&In the meantime, cook pasta according to package instructions, drain.?#&Add cooked and drained pasta to the skillet with the chicken and vegetables. Add heavy cream and milk, bring to boil. Immediately add cheese (cheddar and mozzarella) and mix until the cheese is melted and the sauce is smooth. Reduce heat to simmer. Add the remaining half of the spices, and mix to combine. Adjust seasonings, and add more salt, cumin, and chili powder, if needed.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'9FD7A1924FB1A547AF272C014D26B1B8', X'F0FC94B5C6C856429DAC0B28CBEE33CF', X'F0FC94B5C6C856429DAC0B28CBEE33CF', 0, 'https://www.moms-mexican-recipes.com/mexican-lasagna-recipe', 'Mexican Lasagna Recipe', 2, NULL, 0, 'Mexican Lasagna Recipe', '0', 'Pasta', 'https://www.moms-mexican-recipes.com/mexican-lasagna-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Lasagna-Recipe-300x240.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Mexican-Lasagna-Ingredients-300x200.jpg', 'Laura', '4-5', '10 mins', '100 mins', '1 hour 50 mins', 'July 9, 2017', 'Ingredients?#&?#&Olive oil?#&½ lb ground beef?#&½ Tsp. salt?#&½ Tbsp. chili powder?#&½ Tbsp. ground cumin?#&½ Tbsp. ground coriander?#&¼ Tsp. cayenne?#&7 oz can refried beans?#&1 medium onion, chopped?#&1 red bell pepper, chopped?#&2 garlic clove, minced?#&14 oz can diced tomatoes?#&4 oz can dice green Anaheim chilis?#&¼ Tsp. dried oregano?#&3 10” flour tortillas?#&1 cup Monterey Jack cheese, grated?#&1 cup Cheddar cheese, grated', 'Instructions?#&?#&Brown the ground beef.?#&Sprinkle in salt, chili powder, cayenne, cumin, and coriander.?#&Remove the beef from the pan and set aside.?#&In the same pan, saute onions, bell peppers, garlic, tomatoes, chili, and oregano.?#&Preheat oven to 350F. Oil a 10” deep round baking pan.?#&Layer the tortillas, beans, meat, cheese, and sauce.?#&Bake for 35 minutes.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'ABCA7FABA4653C42815F5023804121AB', X'5EEAEB8BFED38D4EB9E5461B4B3FDAEE', X'5EEAEB8BFED38D4EB9E5461B4B3FDAEE', 0, 'https://www.moms-mexican-recipes.com/picadillo-recipe', 'Mexican Picadillo Recipe', 2, NULL, 0, 'Mexican Picadillo Recipe', '2', 'Main Dishes', 'https://www.moms-mexican-recipes.com/picadillo-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Picadillo-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Picadillo-Ingredients-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/07/Picadillo-Preparation-300x300.jpg', 'Laura', '2', '10 mins', '35 mins', '45 mins', 'July 8, 2017', 'Ingredients?#&?#&½ lb ground beef?#&1 medium russet potato, peeled and diced to ½” cubes?#&¼ cup diced yellow onion?#&¼ cup diced bell pepper?#&1 tbsp. canola oil?#&½ tsp. salt?#&3 cloves garlic?#&¼ tsp. pepper?#&½ tsp. ground cumin?#&½ cup beef stock?#&¼ cup cilantro?#&1 tomato, cored and diced', 'Instructions?#&?#&Brown the beef in canola oil.?#&Add onions, bell pepper, tomatoes, garlic, and all spices. Cook for 10 minutes.?#&Add potatoes and cilantro.?#&Add stock and simmer for 25 minutes.?#&Serve');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'2AB4C2B26FF2CC438BC214A096BA71BF', X'49A5E3C4F1BB99449FE7DE96740CB024', X'49A5E3C4F1BB99449FE7DE96740CB024', 0, 'https://www.moms-mexican-recipes.com/chorizo-breakfast-burritos-recipe', 'Chorizo Breakfast Burritos Recipe', 2, NULL, 0, 'Chorizo Breakfast Burritos Recipe', '0', 'Burritos', 'https://www.moms-mexican-recipes.com/chorizo-breakfast-burritos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Chorizo-Breakfast-Burritos-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Chorizo-Breakfast-Burritos-300x200.jpg', 'Laura', '2', '10 mins', '30 mins', '40 mins', 'July 1, 2017', 'Ingredients?#&?#&For the Filling?#&?#&¼ Onion, chopped?#&1 Jalapeño Pepper, chopped?#&4 Eggs, beaten?#&2 Flour Tortillas?#&½ cup Monterey Jack Cheese?#&¼ lb Chorizo?#&For the Potatoes?#&1 small Potato, diced?#&½ tsp. Chili Powder?#&Salt?#&Black Pepper?#&Olive Oil?#&?#&For the Tomatillo-Chipotle Salsa?#&?#&2 Tomatillos, husked?#&1 clove Garlic?#&1 Chipotle in Adobo stemmed', 'Instructions?#&?#&Toss the potato in olive oil, salt, pepper, and chili powder.?#&Bake for 20 minutes at 400F.?#&Roast the tomatillos for 10-15 minutes at 400F.?#&Blend roasted tomatillos, chipotle, and garlic.?#&Sautee chorizo for 3-5 minutes per side in a skillet. Set aside.?#&In the same skillet, saute onions and jalapeños. Whisk in the eggs.?#&Heat up a tortilla for 30-60 seconds in a skillet.?#&Stuff with potatoes, eggs, and chorizo.?#&Roll tight and toast in a skillet until edges are crisp.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'55EF98D0B74BB24A8AEC53E8431D78CB', X'AA0640FE97E5A542B1DD3AB54DBEB5BD', X'AA0640FE97E5A542B1DD3AB54DBEB5BD', 0, 'https://www.moms-mexican-recipes.com/turkey-enchilada-stuffed-poblanos-rellenos', 'Turkey Enchilada Stuffed Poblanos Rellenos', 2, NULL, 0, 'Turkey Enchilada Stuffed Poblanos Rellenos', '0', 'Enchiladas, Main Dishes', 'https://www.moms-mexican-recipes.com/turkey-enchilada-stuffed-poblanos-rellenos', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Poblanos-Rellenos-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Poblanos-Rellenos-Ingredients-300x200.jpg', 'Laura', '2', '15 mins', '1 hour', '1 hour 15 mins', 'June 30, 2017', 'Ingredients?#&?#&For the Poblanos?#&?#&2 large Fresh Poblano Chiles?#&½ cup Enchilada Sauce?#&¼ cup shredded Colby Jack Cheese?#&Chopped Scallions for garnish?#&?#&For the Turkey?#&?#&6 oz Ground Turkey?#&2 tbsp. finely chopped Onion?#&1 clove Garlic, minced?#&1 Roma Tomato, chopped?#&2 tbsp. finely chopped Bell Pepper?#&1 tbsp. chopped Cilantro?#&1 oz Tomato Sauce?#&Salt?#&Pepper?#&½ tsp. Ground Cumin?#&⅛ tsp. Oregano?#&1 Bay Leaf', 'Instructions?#&?#&Lay the poblano peppers on a work surface. Using a small, sharp knife, cut a lengthwise slit along one side of each of the poblano peppers, then make a small crosswise slit along the top to create a T-shaped slit, careful not to cut the stem off. Carefully cut out and remove the core, then scoop out the seeds.?#&Roast the poblano peppers over an open flame until skin is completely blistered.?#&Transfer to a plastic bag and let steam for 10-15 minutes.?#&Once cool, scrape charred skins with a butter knife, careful not to tear the flesh. Set aside.?#&Preheat oven to 350F.?#&Pour enchilada sauce into the bottom of a casserole dish.?#&Meanwhile, brown the turkey in a saute pan. Season with salt and pepper.?#&Add onions, garlic, pepper, tomato, cilantro and saute for another minute.?#&Add cumin, oregano, and bay leaves.?#&Add tomato sauce and water. Simmer for 15 minutes.?#&Stuff each poblano with the turkey mixture.?#&Place the peppers seam side up in the casserole and top with cheese.?#&Cover the dish with foil and bake for 30 minutes.?#&Serve hot with scallions on top.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'E1E4500AA9DB0B4EB2376A1ACD0C674D', X'E358A5B7DA27B242B5684E6193E44E8D', X'E358A5B7DA27B242B5684E6193E44E8D', 0, 'https://www.moms-mexican-recipes.com/baja-fried-shrimp-tacos', 'Baja Fried Shrimp Tacos', 2, NULL, 0, 'Baja Fried Shrimp Tacos', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/baja-fried-shrimp-tacos', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Baja-Fried-Shrimp-Tacos-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Baja-Shrimp-Tacos-Ingredients-300x200.jpg', 'Laura', '2', '15 mins', '15 mins', '30 mins', 'June 29, 2017', 'Ingredients?#&?#&¼ cup Mayonnaise?#&1 Chipotle in Adobo stemmed?#&¼ cup Buttermilk?#&Salt?#&Pepper?#&1 tbsp. Butter?#&1.5 cups shredded Green Cabbage?#&1 Carrot, shredded?#&Vegetable Oil, for frying?#&12 pcs. Shrimp, shelled and deveined?#&1 cup Panko?#&6 Corn Tortillas?#&2 Pickled Jalapeños, thinly sliced?#&½ Red Onion, thinly sliced?#&¼ cup Chopped Cilantro?#&¼ cup Crumbled Feta?#&Lime for Serving', 'Instructions?#&?#&Blend the mayonnaise, chipotle, and 1 tablespoon of buttermilk in a food processor. Season with salt and pepper.?#&Sautee cabbage and carrots in butter. Season with salt and pepper.?#&Dip the shrimp in buttermilk then coat in panko.?#&Deep fry the shrimp for 2 minutes. Drain on paper towels.?#&Assemble the tortillas. Spoon some of the cabbage and carrot mixes into the center of each tortilla and top with 2 pieces fried shrimp.?#&Top with chipotle mayo, jalapeños, red onions, cilantro, and feta.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'E9086C7D6CDE0C4C922ECE5FA72213DD', X'68B2651590D0C443AD72F3931D35D3E6', X'68B2651590D0C443AD72F3931D35D3E6', 0, 'https://www.moms-mexican-recipes.com/grilled-chicken-mole-recipe', 'Grilled Chicken Mole Recipe', 2, NULL, 0, 'Grilled Chicken Mole Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/grilled-chicken-mole-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Grilled-Chicken-Mole-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Grilled-Chicken-Mole-Ingredients-300x200.jpg', 'Laura', '3', '15 mins', '45 mins', '1 hour', 'June 28, 2017', 'Ingredients?#&?#&For the Mole Sauce?#&?#&1 Large Roma Tomato, chopped?#&½ White Onion, chopped?#&1 clove Garlic, mashed?#&1 Serrano Pepper, sliced?#&1 tbsp. Sesame Seeds, toasted?#&2 cups Chicken Broth?#&Salt?#&Pepper?#&2 tbsp. Canola Oil?#&4 oz. Dark Mole Paste?#&?#&For the Chicken?#&?#&1 lb. Boneless Chicken Breast?#&½ tsp. Salt?#&½ tsp. Pepper?#&½ tsp. Garlic Powder?#&½ tsp. Cumin Powder?#&¼ tsp. Red Pepper Flakes?#&1 tbsp. Lime Juice', 'Instructions?#&?#&Blend tomatoes, onion, garlic, serranos, sesame seeds, and a cup chicken broth until smooth. Season with salt and pepper.?#&Heat oil in a skillet. Pour in the sauce and simmer for 5 minutes.?#&Add the mole paste and remaining chicken broth. Cook for 25 minutes.?#&Combine salt, pepper, garlic powder, cumin, and chili flakes in a bowl.?#&Season chicken with seasoning blend.?#&Cook the chicken on the grill for 4-5 minutes per side.?#&Serve chicken topped with mole sauce and sesame seeds.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'7318A2D88B988E42BC616342E60C3A69', X'D602C3D21CC50F4F81231DC7D3EFBBA6', X'D602C3D21CC50F4F81231DC7D3EFBBA6', 0, 'https://www.moms-mexican-recipes.com/mexican-pizza-recipe', 'Mexican Pizza Recipe', 2, NULL, 0, 'Mexican Pizza Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/mexican-pizza-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Homemade-Mexican-Pizza-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Mexican-Pizza-Ingredients-300x200.jpg', 'Laura', '2-4', '10 mins', '15 mins', '25 mins', 'June 26, 2017', 'Ingredients?#&?#&¼ lb Ground Beef?#&½ cup diced Onion?#&1 clove Garlic, minced?#&½ tsp. Ground Cumin?#&¼ tsp. Black Pepper?#&¼ tsp. Salt?#&dash of Cayenne Pepper?#&8oz Refried Beans?#&2 10 inch Flour Tortillas?#&¼ cup Salsa?#&1 cup Mexican Cheese Blend?#&1 Green Onion, chopped?#&4 Grape Tomatoes, diced?#&2 tbsp. chopped Jalapeño Peppers?#&2 tbsp. Sour Cream', 'Instructions?#&?#&Preheat oven to 350F.?#&Line a baking sheet with parchment paper and spray with non-stick cooking spray.?#&Cook beef, onion, and garlic in a skillet.?#&Stir in chili, cumin, salt, pepper, and cayenne.?#&Place tortillas side by side on the baking sheet.?#&Cover with a layer of refried beans.?#&Scatter half of the beef over each tortilla, then cover with the second tortilla.?#&Bake 10 minutes.?#&Remove tortillas out of the oven. Spread salsa on top of each and top with cheese, tomatoes, green onions, and jalapeños.?#&Bake for another 5-10 minutes.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'00B3837F436E4F4590F0BF7FB287CDFA', X'8C9AAA90C8C8294DA5457FAA1042941F', X'8C9AAA90C8C8294DA5457FAA1042941F', 0, 'https://www.moms-mexican-recipes.com/shredded-chicken-tamales-recipe', 'Shredded Chicken Tamales Recipe', 2, NULL, 0, 'Shredded Chicken Tamales Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/shredded-chicken-tamales-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Shredded-Chicken-Tamales-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Shredded-Chicken-Tamales-Ingredients-300x200.jpg', 'Laura', '7', '2 hours', '50 mins', '2 hours 50 mins', 'June 9, 2017', 'Ingredients?#&?#&Chicken?#&?#&1-2 Skinless boneless chicken breasts?#&2 Cups of chicken broth?#&1 cup 15 oz can of tomato sauce?#&2 Tablespoons Sofrito?#&1 Tablespoon Recarito?#&1 Tablespoon garlic salt?#&?#&Batter?#&?#&2 Cups instant corn masa mix (corn flour)?#&1 Cups chicken broth or water?#&1 Teaspoon Baking Powder?#&1 Teaspoon Salt?#&¾ Cup Lard or Vegetable Shortening?#&?#&Tamales?#&?#&1 Tablespoon Vegetable Oil?#&1 Tablespoon butter?#&6 Corn Husks?#&1 Red bell pepper diced?#&½ Orange Bell Pepper diced?#&½ White Onion diced?#&1 Cups shredded cheese – any flavor?#&½ cup refried beans?#&½ Cup HERDEZ Salsa – either Salsa Casera or Salsa Verde', 'Instructions?#&?#&Rinse and cut chicken.?#&Prepare the crockpot or slow cooker. Pour two cups of chicken broth into the crockpot. Add the 15 oz. can of tomato sauce, two tablespoons of Sofrito, 1 tablespoon of Ricarito, and one tablespoon of garlic salt. Mix well with a spoon. Add the chicken, and make sure the chicken is covered in the broth and tomato sauce mixture. If your crock pot happens to be deeper or wider than mine, just add more broth or water until the chicken breasts are submerged. Cook for 3 hours on high.?#&Shred the chicken. Using two forks, shred the chicken into pieces. The chicken should pull apart easily.?#&Soak the corn husks.?#&Prepare the Veggies. I brought the tablespoon of butter and tablespoon of cooking oil to boil in a pan and I pan fried the diced onions, orange bell peppers, and red bell peppers.?#&Prepare the masa (batter). Mix the corn flour, baking powder and salt in a bowl. Add the chicken broth or water and mix thoroughly.?#&Prepare the lard or vegetable shortening. I used a hand mixer to mix the shortening to whip air into it and to ensure a nice smooth mix. Once it was soft and smooth, I added it to the masa mix and blended until completely combined.?#&Prepare the tamales with filling. Take a corn husk and remove from the water. Lay it on a flat surface. First, take ½ to ¾ cup of masa and place it on the corn husk – create a ditch inside the masa for the filling. Add a teaspoon to a tablespoon of the cooked bell peppers and onions to the masa. You can also add a few pieces of diced olives. Add one to two tablespoons of the shredded chicken mix to the “moat”. Cover the chicken with a flattened piece of masa and use your fingers to join the masa top to the masa bottom.?#&Enclose the masa in the corn husk. Fold the shortest side up over the masa first, then the side across from it, and then the two other sides. Use baker’s twine to tie everything together. You don’t want the corn husk too tight – you need to leave a little room for the masa dough to expand.?#&Steam the tamales for 45 minutes.?#&Serve');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'6A464ED398548547BCE7ED176BD68659', X'DDD98BB296B31D4EBDE689DBF8974FEA', X'DDD98BB296B31D4EBDE689DBF8974FEA', 0, 'https://www.moms-mexican-recipes.com/mexican-chicken-torta-recipe', 'Mexican Chicken Torta Recipe', 2, NULL, 0, 'Mexican Chicken Torta Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/mexican-chicken-torta-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Mexican-Chicken-Torta-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Mexican-Chicken-Torta-Ingredients-300x200.jpg', 'Laura', '4', '30 mins', '15 mins', '45 mins', 'June 8, 2017', 'Ingredients?#&?#&12 Mini Tostada shells, store-bought or homemade*?#&1 medium ripe avocados?#&1 tbsp fresh lime juice?#&¼ tsp garlic powder, divided?#&Salt and freshly ground black pepper?#&½ cup Rosarita Traditional Refried Beans?#&½ cup Rotel Diced Tomatoes & Green Chilies, drained?#&¾ tsp ancho chili powder?#&½ tsp ground cumin?#&1 cup shredded iceberg or romaine lettuce?#&1 cup shredded rotisserie chicken or shredded cooked chicken breasts?#&½ cup shredded Mexican blend cheese?#&½ cup Pico de gallo, homemade or store-bought**?#&¼ cup Sour Cream', 'Instructions?#&?#&Using a fork mash avocado with lime juice and ⅛ tsp garlic powder to a slightly chunky texture. Season with salt and pepper to taste.?#&In a small saucepan mix together refried beans, drained canned tomatoes, chili powder, cumin and remaining ⅛ tsp garlic powder. Season with salt and pepper to taste.?#&Cook over medium-low heat, stirring frequently until heated through.?#&To assemble tostadas, Layer each tostada shell with guacamole (avocado mixture), bean mixture, lettuce, chicken, cheese, Pico de gallo and sour cream.?#&Serve immediately.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'775079B373F5B14CAA4C5C4D811C338E', X'06A27E0B23770240B5032E44DEDE4621', X'06A27E0B23770240B5032E44DEDE4621', 0, 'https://www.moms-mexican-recipes.com/chicken-guacamole-bean-tostadas', 'Chicken Guacamole & Bean Tostadas', 2, NULL, 0, 'Chicken Guacamole & Bean Tostadas', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/chicken-guacamole-bean-tostadas', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Chicken-Guacamole-and-Bean-Tostadas-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/06/Chicken-Guacamole-and-Bean-Tostadas-ingredients-300x200.jpg', 'Laura', '6-12', '20 mins', '20 mins', '40 mins', 'June 7, 2017', 'Ingredients?#&?#&12 mini tostada shells, store-bought or homemade*?#&1 medium ripe avocados?#&1 Tbsp fresh lime juice?#&¼ tsp garlic powder, divided?#&Salt and freshly ground black pepper?#&½ cup Rosarita Traditional Refried Beans?#&½ cup Rotel Diced Tomatoes & Green Chilies, drained?#&¾ tsp ancho chili powder?#&½ tsp ground cumin?#&1 cups shredded iceberg or romaine lettuce?#&1 cups shredded rotisserie chicken or shredded cooked chicken breasts?#&½ cup shredded Mexican blend cheese?#&½ cup Pico de gallo, homemade or store-bought**?#&¼ cup Sour Cream', 'Instructions?#&?#&Using a fork mash avocado with lime juice and ⅛ tsp garlic powder to a slightly chunky texture. Season with salt and pepper to taste.?#&In a small saucepan mix together refried beans, drained canned tomatoes, chili powder, cumin and remaining ⅛ tsp garlic powder. Season with salt and pepper to taste.?#&Cook over medium-low heat, stirring frequently until heated through.?#&To assemble tostadas, Layer each tostada shell with guacamole (avocado mixture), bean mixture, lettuce, chicken, cheese, Pico de gallo and sour cream.?#&Serve immediately.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'47ED0095FB17D2458BA0213E418A8CF4', X'1BC0A3B7E04AD04397A3963C2DCE14B2', X'1BC0A3B7E04AD04397A3963C2DCE14B2', 0, 'https://www.moms-mexican-recipes.com/chicken-taquitos-recipe', 'Chicken Taquitos Recipe', 2, NULL, 0, 'Chicken Taquitos Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/chicken-taquitos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Chicken-Taquitos-Recipe-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Chicken-Taquitos-Ingredients-300x200.jpg', 'Laura', '6', '', '', '', 'June 2, 2017', 'Ingredients?#&?#&1 Onion?#&1 Zucchini?#&1 T avocado oil?#&1 lb. ground Chicken?#&3 T Chili Powder?#&1 tsp. ground cumin?#&½ tsp salt?#&6 6 inch corn tortillas?#&¾ cup shredded pepper jack', 'Instructions?#&?#&Preheat oven to 425 F.?#&Line a baking sheet with foil, grease with oil.?#&Shred Zucchini using the large holes of a box grater, squeeze dry in a clean kitchen towel.?#&Heat oil in a large skillet over medium-high heat.?#&Add zucchini, ground chicken, chili powder, onions, cumin, and salt. to pan and cook, stirring until the chicken is cooked through 8-10 minutes.?#&Spread tortillas out on a baking sheet in two overlapping rows.?#&Bake until hot, about 2 minutes, transfer to a plate and cover.?#&Line the baking sheet with foil or parchment paper (for easier clean up) and coat with cooking spray.?#&Place 6 tortillas on a clean cutting board or extra pan.?#&Working quickly, spread a generous ¼ cup chicken mixture along with the bottom third of a Tortilla, sprinkle with about 1T cheese and tightly roll into a cigar shape.?#&Generously coat the top and sides of taquitos with cooking spray.?#&Sprinkle the top with salt, liberally.?#&Bake the taquitos until browned and crispy, 14 to 18 minutes.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'719C86DB47B08945B15CABF8B8B60BFA', X'88D99BFEF23817469CAC9D81388D1F0E', X'88D99BFEF23817469CAC9D81388D1F0E', 0, 'https://www.moms-mexican-recipes.com/elote-recipe-mexican-sweet-corn', 'Elote Recipe (Mexican Sweet Corn)', 2, NULL, 0, 'Elote Recipe (Mexican Sweet Corn)', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/elote-recipe-mexican-sweet-corn', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Elote-Recipe-Mexican-Sweet-Corn-Ingredients-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Elote-Recipe-Mexican-Sweet-Corn-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Elote-Sweet-Corn-300x200.jpg', 'Laura', '3', '15 mins', '40 mins', '55 mins', 'June 2, 2017', 'Ingredients?#&?#&3 pcs. Fresh Sweet Corn?#&3 Tablespoons Mayonnaise?#&½ Teaspoon Garlic Powder?#&½ Cup Cotija Cheese, Crumbled?#&3 Tablespoons Fresh Cilantro, Roughly Chopped?#&½ Teaspoon Chipotle Chili Powder?#&2 Limes, Quartered', 'Instructions?#&?#&Preheat oven to 350 degrees F.?#&Place corn on the oven rack.?#&Bake for 35-40 minutes or until fork tender.?#&In a small bowl combine garlic powder and mayonnaise.?#&Brush the Corn with Mayo when done, Roll in cotija cheese.?#&Sprinkle with cilantro and chili powder.?#&Squeeze with fresh lime juice.?#&Serve');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'E8FF3F36D08B6C44A843B38164646E0D', X'A7101EED93BF244C98DB3234D2AA2BC0', X'A7101EED93BF244C98DB3234D2AA2BC0', 0, 'https://www.moms-mexican-recipes.com/mexican-stuffed-bell-peppers-recipe', 'Mexican Stuffed Bell Peppers Recipe', 2, NULL, 0, 'Mexican Stuffed Bell Peppers Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/mexican-stuffed-bell-peppers-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Mexican-Stuffed-Bell-Peppers-Ingredients-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/DSCF4561-300x200.jpg', 'Laura', '3', '30 mins', '1 hour', '1 hour 30 mins', 'June 2, 2017', 'Ingredients?#&?#&3 medium size green bell peppers?#&Mozzarella cheese?#&Cheddar cheese?#&1 tbsp. olive oil?#&½ pound beef?#&1 tsp. chili powder?#&1 tsp. cumin?#&¼ tsp. salt?#&1 cup rice, cooked?#&½ can black beans (rinsed)?#&1 large tomato, chopped into small slices?#&⅔ cup Marinara meatless spaghetti tomato sauce?#&⅔ cup water or less', 'Instructions?#&?#&Preheat oven to 350 degrees F.?#&Heat the skillet on stove top until hot and add olive oil?#&Add ground beef and cook it constantly stirring until nicely browned and cooked.?#&Remove from heat.?#&Drain all excess fat.?#&Add 1 teaspoon chili powder, 1 teaspoon cumin, ¼ teaspoon salt to cooked ground beef, stir to combine.?#&To the same skillet, off heat, add black beans, chopped tomatoes, and cooked rice.?#&Prepare bell peppers for stuffing: cut the tops off, stem and seed them.?#&Stuff each bell pepper with the filling.?#&Fill the baking dish with marinara spaghetti sauce and up to ⅔ cups of water (don''t pour all water at once - just pour enough to make the marinara sauce less dense).?#&Put stuffed bell peppers tightly into prepared baking dish, cavity side up. In my case, I used 2 large bread pans.?#&Put each pepper with both grated Mozzarella and Cheddar cheese.?#&Sprinkle some cheese over the tomato sauce too. Cover with foil?#&Bake for 30-40 minutes, until the peppers are tender and the filling is hot.?#&Serve with some of the tomato sauce on the bottom.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'A98869425ABA844095E535500BA70E71', X'D4D32B90BBB70A44A7792FA9E29D10C9', X'D4D32B90BBB70A44A7792FA9E29D10C9', 0, 'https://www.moms-mexican-recipes.com/new-mexican-sopaipillas-recipe', 'New Mexican Sopaipillas Recipe', 2, NULL, 0, 'New Mexican Sopaipillas Recipe', '3', 'Main Dishes', 'https://www.moms-mexican-recipes.com/new-mexican-sopaipillas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/New-Mexican-Sopaipillas-Ingredients-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/New-Mexican-Sopaipillas-Recipe-300x200.jpg', 'Laura', '5', '15 mins', '15 mins', '30 mins', 'June 2, 2017', 'Ingredients?#&?#&2 cups Flour (sifted)?#&½ tbsp. baking powder?#&½ tsp. salt?#&½ tbsp. sugar?#&1 tbsp. vegetable oil?#&1 cup warm water?#&Vegetable oil for frying', 'Instructions?#&?#&In the bowl of a stand mixer (or a regular mixing bowl if making by hand) whisk the flour, baking powder, salt and sugar together.?#&Create a small depression in the center and pour in the 2 tablespoons of vegetable oil.?#&Whisk in the oil.?#&Gently pour in the warm water and knead the dough until it is smooth.?#&It only takes one to two minutes using the dough hook in a stand mixer.?#&If kneading by hand, gently knead in the bowl until the dough sticks together.?#&Place on a lightly floured surface and continue until the dough is smooth.?#&Divide the dough in half, and seal each half in a zipper lock bag to rest the dough while you heat the oil for frying.?#&Heat frying oil in a stove top dutch oven, or in a heavy-duty frying pan, to around 340 to 350 degrees.?#&The oil should be at least 2 inches deep.?#&Remove one portion of dough and roll out on a clean, flourless surface.?#&Roll the dough to a thickness of ⅛ inch.?#&Using a pizza cutter, cut the dough into squares approximately 4 X 4 for a nice restaurant look.?#&Place a sheet of plastic wrap over the rolled out dough.?#&It''s very important the dough stay moist and not dry out.?#&Gently place an individual square of dough into the hot oil.?#&It should begin to puff up within about 5 seconds.?#&Carefully splash oil over the top, this really seems to help the sopaipilla puff.?#&Once it''s finished puffing up, fry until it reaches a light golden brown on both sides.?#&Place in a large bowl lined with paper towels.?#&Repeat with the remaining dough.?#&Serve immediately with honey.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'D684CA282D1AF74FB47BD4B78C20C085', X'398E88B03D046B44AC2B3A33E99B74A9', X'398E88B03D046B44AC2B3A33E99B74A9', 0, 'https://www.moms-mexican-recipes.com/pork-tacos-recipe', 'Pork Tacos Recipe', 2, NULL, 0, 'Pork Tacos Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/pork-tacos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Tacos-Recipe-300x300.jpg', 'Laura', '5', '20 mins', '5 hours', '5 hours 20 mins', 'May 19, 2017', 'Ingredients?#&?#&1 large yellow onion, chopped, divided (1/4 of the chopped onion into the crockpot, ¾ of it into the sauce)?#&1 fresh pineapple, peeled, cut into ½ inch chunks, save the pineapple juice, divided (half into the sauce, ¼ whole into the crockpot, ¼ for topping)?#&¼ cup apple cider vinegar?#&1 tbsp chile powder?#&4 garlic cloves?#&2 tsp salt?#&1 tsp dried Mexican oregano?#&1 tbsp cumin?#&2-4 canned chipotle chiles + 1-2 tbsp adobo sauce (depending on how spicy you want it)?#&3 lbs pork roast?#&Corn tortillas?#&Cilantro?#&Small white onion, finely chopped?#&Cotija?#&Crema Mexicana?#&Lime wedges', 'Instructions?#&?#&To make the sauce, place ¾ of the yellow onion, ½ of the pineapple, the pineapple juice, apple cider vinegar, chile powder, garlic, salt, oregano, cumin, chipotle chiles, adobo sauce, and ¼ cup water in a blender and puree until smooth.?#&Place pork roast into the crockpot and pour in the sauce to cover the pork. Add in the remaining ¼ of the yellow onion and a quarter of the pineapple. Reserve the last ¼ of the pineapple for topping the tacos.?#&Cook pork in the crockpot for 4 hours on high or 8 hours on low, checking occasionally.?#&When pork has finished cooking, remove from the crockpot and chop/shred. Return to crockpot and mix pork in with the sauce, allow pork to simmer in the sauce.?#&While pork and sauce simmer, heat a skillet over medium-high heat. Place remaining pineapple chunks in skillet and char for 2-3 minutes.?#&To serve tacos, warm tortillas in a skillet for 1-2 minutes.?#&Top tortillas with pork, charred pineapples, cilantro, chopped white onion, cotija, crema, and a lime wedge.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'4D7F9175F2E8FB47AD680767E20862E3', X'76F3B1FDAE713945803696B9FBAA589E', X'76F3B1FDAE713945803696B9FBAA589E', 0, 'https://www.moms-mexican-recipes.com/aporreadillo-recipe', 'Aporreadillo Recipe', 2, NULL, 0, 'Aporreadillo Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/aporreadillo-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Apporeadillo-Recipe-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Apporeadillo-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Apporeadillo-Recipe-Final-225x300@2x.jpg', 'Laura', '4', '15 mins', '30 mins', '45 mins', 'May 18, 2017', 'Ingredients?#&?#&6 Roma Tomatoes?#&2 Dried Guajillo Chiles stems and Seeds Removed?#&2 Chilis?#&1 Garlic Clove?#&3 cups water?#&2 tbsp. vegetable oil?#&½ Medium White Onion - finely chopped?#&1 lb. thin cut Steaks cut in thin strips?#&6 eggs lightly beaten?#&¼ tsp. ground Cumin', 'Instructions?#&?#&Bring the Tomatoes, Guajillo Chiles, Serrano Chiles and Garlic to a boil over medium-high heat in a medium saucepan.?#&Cover and reduce heat to low.?#&Let simmer for 8-10 mins. until tomatoes are completely cooked.?#&Remove from heat, let cool slightly.?#&Pour ingredients into a blender cup.?#&Puree until smooth, season with salt to taste.?#&Heat 1 tbsp. of the vegetable oil in a large nonstick skillet over medium-high heat.?#&Saute the onion for about 3 mins. or until translucent.?#&Add the thin-cut steak and cook until meat is no longer pink.?#&Season slightly with salt.?#&Reduce heat to low?#&Cover and simmer for 10 mins. until the meat''s juice has evaporated.?#&Add the remaining tablespoon of vegetable oil to the skillet with the cooked meat.?#&Season with the ground cumin.?#&Stir in the eggs.?#&Let Cook.?#&Stirring occasionally until the eggs are completely cooked.?#&Season to taste with salt and black pepper.?#&Serve');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'B1C1EA6ADBDFD542926966D4C09EA800', X'AB4B7BC9DB379D4589B1D245852E3DB1', X'AB4B7BC9DB379D4589B1D245852E3DB1', 0, 'https://www.moms-mexican-recipes.com/late-night-nachos-recipe', 'Late Night Nachos Recipe', 2, NULL, 0, 'Late Night Nachos Recipe', '0', 'Appetizers', 'https://www.moms-mexican-recipes.com/late-night-nachos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Late-Night-Nachos-Ingredients-1-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Late-Night-Nachos-Recipe-300x200.jpg', 'Laura', '4', '15 mins', '20 mins', '35 mins', 'May 18, 2017', 'Ingredients?#&?#&1 14 ounce bag corn tortilla chips?#&1 15 ounces jar salsa con queso?#&4-6 ounces shredded cheddar cheese + extra as desired?#&6 ounces chorizo (or 12 ounces if you want it meatier)?#&2 jalapeno peppers, chopped or sliced into rings?#&1 large ripe avocado, peeled, pitted and diced?#&Fresh tomato, diced?#&Chopped cilantro?#&Sour cream or Crema, if desired', 'Instructions?#&?#&To a large oven-safe dish, add corn chips and spread them out.?#&Pour salsa con queso over the top and mix well.?#&Top with shredded cheddar cheese.?#&Heat a small pan over medium heat and add chorizo.?#&Cook about 8-9 minutes, or until chorizo is cooked through.?#&Crumble over the top of the cheeses.?#&Heat oven to 220 degrees and bake the dish until the cheeses melt through.?#&Remove from oven and top with fresh jalapeno slices, avocado, tomato, and chopped cilantro, with cream if desired.?#&Serve immediately');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'14DE13E8C13E174BAAC4C46E21E481F3', X'A064A3D9E7B93942B09E509D9BC115E1', X'A064A3D9E7B93942B09E509D9BC115E1', 0, 'https://www.moms-mexican-recipes.com/roasted-poblano-soup-recipe', 'Roasted Poblano Soup Recipe', 2, NULL, 0, 'Roasted Poblano Soup Recipe', '0', 'Soups', 'https://www.moms-mexican-recipes.com/roasted-poblano-soup-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Roasted-Poblano-Soup-Ingredients-300x300.jpg', 'Laura', '4', '30 mins', '30 mins', '1 hour', 'May 15, 2017', 'Ingredients?#&?#&5 Poblano peppers?#&3 tablespoon butter?#&1 onion?#&3 garlic cloves?#&3 tablespoons flour?#&4 cups stock?#&salt?#&?#&For Decoration?#&?#&sour cream or Greek Yoghurt?#&cilantro stems chopped?#&hot sauce?#&ground pepper', 'Instructions?#&?#&Heat oven on 400 F. Rinse poblanos and put them to roast in the oven for 15 min.?#&After 15 minutes turn them on baking tray and roast for 10-15 minutes.?#&After roasting put the poblanos in a ziplock bag and set aside to rest for 25 minutes.?#&In the meantime chop onion and mince garlic.?#&Heat a saucepan over medium heat and add butter and onion, sauteing 7 minutes.?#&Add minced garlic and cook 2 minutes. Add 3 tablespoons flour and whisk well.?#&Add 4 cups of stock and half teaspoon salt.?#&After boiling lower the heat and simmer for 10 minutes.?#&After cooling poblanos, pull off as much as you can skin and destem and deseed the poblanos. Add roughly chopped poblanos in saucepan and heat to simmering for 3 minutes.?#&Blend the mixture with a hand blender until you got a uniform texture.?#&Cook for 5 minutes.?#&Serve the soup and decorate with sour cream, few drops of hot sauce and fresh cilantro stems.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'F8B90F956C3C5E4BB1385F7F02C75105', X'6FAC9E08E6116741B4BACF611EABC9DF', X'6FAC9E08E6116741B4BACF611EABC9DF', 0, 'https://www.moms-mexican-recipes.com/jalapeno-beef-enchiladas-recipe', 'Jalapeno Beef Enchiladas Recipe', 2, NULL, 0, 'Jalapeno Beef Enchiladas Recipe', '0', 'Enchiladas, Main Dishes', 'https://www.moms-mexican-recipes.com/jalapeno-beef-enchiladas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Jalapeno-Beef-Enchiladas-Ingredients-300x300.jpg', 'Laura', '4', '15 mins', '35 mins', '50 mins', 'May 15, 2017', 'ingredients in a bowl and stir with ½ lbs. skirt steak.?#&Put it in a ziplock bag and set aside for 2,5 hours.?#&?#&For Enchiladas?#&?#&12 flour tortillas?#&1 white onion sliced?#&1 cup cilantro lives chopped?#&2 chopped Jalapeno?#&1 cup shredded cheddar cheese?#&1 cup shredded mozzarella cheese?#&1 sliced avocado', 'Instructions?#&?#&Separate skirt steak from marinade.?#&Put the skillet over medium high heat and add marinade.?#&Add chopped Jalapeno and onion and cook until vegetables softens.?#&Add cilantro leaves and stir it well.?#&Put all of mixture in blender and blend it until uniform texture of salsa.?#&In the same skillet over medium high heat add skirt steak and cook for 8 minutes per each side until meat gets dark brown caramel color.?#&Set aside meat to cool a little bit down.?#&Slice the skirt steak as it is shown in the picture.?#&Preheat the oven to 375F.?#&Microwave tortillas on high for 30 seconds and coat the bottom of a squared baking tray with 2 tablespoons of the salsa.?#&Put the softened tortilla on flat surface and spread 3 tablespoons beef down center of each and wrap tightly, placing the seam facing down in baking tray.?#&Repeat these step for all tortillas and place them tightly in baking tray.?#&Cover them with salsa and sprinkle with shredded cheddar and mozzarella cheese.?#&Bake it in the oven for 10 minutes.?#&Decorate it with sliced avocado and enjoy immediately.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'7CB9B6D85DAD9A40BD509B0D85A8B593', X'A9D084B591150A4EB3078966CB876B79', X'A9D084B591150A4EB3078966CB876B79', 0, 'https://www.moms-mexican-recipes.com/chicken-avocado-arepas-recipe', 'Chicken Avocado Arepas Recipe', 2, NULL, 0, 'Chicken Avocado Arepas Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/chicken-avocado-arepas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Chicken-Avocado-Arepas-Blending-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Chicken-Avocado-Arepas-Ingredients-300x300.jpg', 'Laura', '4', '20 mins', '40 mins', '1 hour', 'May 12, 2017', 'Ingredients?#&?#&For Guasacaca Sauce?#&?#&½ chopped white onion?#&1 minced garlic cloves?#&1 large sliced, peeled and pitted avocado?#&1 lime juiced?#&¼ cup stemmed parsley leaves?#&¼ cup stemmed cilantro leaves?#&3 tablespoons red wine vinegar?#&¼ cup extra virgin olive oil?#&1 Jalapeno pepper seeded and sliced?#&Salt and pepper to taste?#&?#&For Arepas?#&?#&2 cups precooked corn meal?#&3 cups warm water?#&1 teaspoon salt?#&?#&For Filling Arepas?#&?#&2 chicken breasts, boneless, skinless?#&2 tablespoons olive oil?#&1 cup cooked black bean?#&1 cup cooked corn kernels?#&8 oz mozzarella cheese shredded?#&1 large pureed, peeled and pitted avocado', 'Instructions?#&?#&For making Arepas?#&?#&Put the salt in bowl with warm water.?#&Add cornmeal in a bowl stirring it well to form a dough.?#&Set aside dough for 5 minutes. Form 8 balls from dough.?#&Preheat the oven to 400F.?#&Form from 8 ball dough disk shape arepas.?#&Put the coated olive oil skillet on medium high heat.?#&Place as many disk shape arepas dough in skillet and cook 5 minutes on each side or until golden brown.?#&Repeat these steps for all arepas.?#&Place all arepas in baking tray in oven for 10 minutes.?#&Keep them warm for filling.?#&?#&For Guasacaca Sauce?#&?#&Place all the ingredients for the guasacaca in a blender or food processor and blend until smooth. Season with salt and pepper.?#&Cut chicken breasts into strips and marinate with ¼ Guasacaca sauce.?#&Heat the skillet over medium-high heat and put marinated chicken for 5 minutes.?#&Stir it all the time. Cover the skillet and lower the heat at low. Cook for 10 minutes.?#&Now you are ready to staff arepas.?#&With sharp knife open arepas and spread the avocado.?#&Put the cooked chicken, black bean and corn kernels.?#&Finish it with Guasacaca sauce and a little bit shredded cheese and enjoy.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'99592AC2CE65484DB0F152F80FE5B1B0', X'6BC9DD004314C146B6B44960655BE854', X'6BC9DD004314C146B6B44960655BE854', 0, 'https://www.moms-mexican-recipes.com/beef-bean-and-cheese-burritos-recipe', 'Beef Bean and Cheese Burritos Recipe', 2, NULL, 0, 'Beef Bean and Cheese Burritos Recipe', '0', 'Burritos', 'https://www.moms-mexican-recipes.com/beef-bean-and-cheese-burritos-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Beef-Bean-and-Cheese-Burritos-Recipe-Wrapping-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/05/Beef-Bean-and-Cheese-Burritos-Ingredients-300x300.jpg', 'Laura', '3', '15 mins', '20 mins', '35 mins', 'May 11, 2017', 'Ingredients?#&?#&For Burrito Filling?#&?#&1 pound lean ground beef?#&1 chopped onion?#&5 minced garlic cloves,?#&2 teaspoons chili powder?#&1 teaspoon ground cumin?#&½ teaspoon smoked paprika?#&½ teaspoon salt?#&¼ teaspoon pepper?#&¼ teaspoon dried oregano?#&1 can refried beans (16oz)?#&¼ cup sour cream?#&½ cup medium-hot salsa?#&½ cup cilantro leaves?#&hot sauce?#&?#&For the Burrito?#&?#&6 flour tortillas?#&2 cups sharp cheddar cheese?#&Sour cream?#&Diced tomatoes?#&Lettuce', 'Instructions?#&?#&Put the large skillet over medium-high heat and add chopped onion and ground beef.?#&Cook for 15 minutes stirring occasionally.?#&Add garlic and cook for 1 minute.?#&Add chili powder, ground cumin, smoked paprika, oregano, salt, and pepper.?#&Mix it until all seasoning are well combined with meat.?#&Add refried bean, sour cream, and salsa.?#&Add cilantro leaves and mix it until is fully combined.?#&Heat tortilla in the microwave for 30 seconds.?#&Line the middle of each tortilla with shredded cheese.?#&Put lettuce over the cheese and add burrito filling.?#&Sprinkle a little bit more cheese over the filling and add 2 teaspoons sour cream following with the diced tomatoes.?#&Add few drops of hot sauce if you like it hot.?#&Fold in the edges and rolled up burrito style.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'CDD8EF5663CAE54191C778C134078A41', X'72E596BC7433E84782E0653130FCF381', X'72E596BC7433E84782E0653130FCF381', 0, 'https://www.moms-mexican-recipes.com/chicken-avocado-quesadillas-recipe', 'Chicken Avocado Quesadillas Recipe', 2, NULL, 0, 'Chicken Avocado Quesadillas Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/chicken-avocado-quesadillas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/04/Chicken-Avocado-Quesadillas-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/04/Chicken-Avocado-Quesadillas-Ingredients-300x300.jpg', 'Laura', '6', '15 mins', '25 mins', '40 mins', 'April 25, 2017', 'Ingredients?#&?#&For chicken?#&?#&3 medium chicken breasts, boneless, skinless?#&Salt and ground black pepper?#&3 tablespoons olive oil?#&1 onion?#&2 bell peppers?#&4 garlic cloves?#&3 tomatoes?#&3 tablespoons fajita or taco seasoning?#&?#&For the quesadillas?#&?#&6 medium flour tortillas?#&2 avocado?#&6 tablespoons low-fat sour cream?#&1 pound low-fat mozzarella cheese?#&2 Limes?#&Fresh cilantro', 'Instructions?#&?#&Dice the onion, bell peppers and garlic.?#&Season the chicken with salt and ground pepper.?#&Place the 1 tablespoon of olive oil in heated heavy duty pan.?#&Cook chicken breasts in pan 7 minutes per each side.?#&Place the cooked chicken breasts aside to rest.?#&In the same pan add 1 tablespoon olive oil and cook on medium heat diced onion, garlic, and bell peppers.?#&After 5 minutes when the onion is caramelized add diced tomatoes and 3 tablespoons fajita seasoning. Stir well and cook for 5 minutes.?#&?#&For the Chicken?#&?#&Dice the cooked chicken breasts and add to pan with vegetables.?#&Stir until the mixture is fully combined.?#&Cut the avocado and mozzarella on slices.?#&Add 1 teaspoon of olive oil in hot pan and place 2 tortillas.?#&Spread 1 teaspoon sour cream on half of each tortilla, place over sliced mozzarella and avocado, chicken mixture and sprinkle with few drops of lime juice and minced cilantro.?#&Fold the tortillas over the filling.?#&Cook the tortillas 2 minutes per?#&each side or until the mozzarella melted.?#&Repeat for rest 4 tortillas.?#&Cut the tortillas in 4 pieces and serve with guacamole.?#&Close your eyes, relax and enjoy.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'906F4B6A6A93364CBD6EB622EFF94B0C', X'93D39DF7CDEA9843B95CA57CD942FE6F', X'93D39DF7CDEA9843B95CA57CD942FE6F', 0, 'https://www.moms-mexican-recipes.com/chuys-creamy-jalapeno-dip-recipe', 'Chuy’s Creamy Jalapeno Dip Recipe', 2, NULL, 0, 'Chuy’s Creamy Jalapeno Dip Recipe', '0', 'Salsa & Sauces', 'https://www.moms-mexican-recipes.com/chuys-creamy-jalapeno-dip-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/04/Chuys-Creamy-Jalapeno-Dip-Recipe-300x300.jpg', 'Laura', '3 cups', '5 mins', '1 min', '6 mins', 'April 5, 2017', 'Ingredients?#&?#&⅓ cup Cilantro (Fresh)?#&½ tsp Garlic (Fresh is ideal)?#&4 oz. can Green chiles?#&⅓ 4 oz. can Jalapenos?#&1 cup Mayonnaise, real?#&1 1 oz. Packet Ranch dressing mix, dry?#&⅓ cup Buttermilk?#&⅓ cup Sour cream', 'Instructions?#&?#&Wash and dry the cilantro (you don''t want to be adding water to your dip) and chop finely until you''ve got about ⅓ rd cup.?#&Make sure you only use the leafy cilantro tops, no stems.?#&Place all ingredients into a blender and use your pulse setting 7-10 times, 2-3 seconds each.?#&If you want it spicier add more Jalapenos.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'9B9853203DAAE948AF7403707932B16F', X'F3BC40F9A9D2954E88394B3992B4B5FA', X'F3BC40F9A9D2954E88394B3992B4B5FA', 0, 'https://www.moms-mexican-recipes.com/mexican-tortilla-soup', 'Mexican Tortilla Soup', 2, NULL, 0, 'Mexican Tortilla Soup', '0', 'Soups', 'https://www.moms-mexican-recipes.com/mexican-tortilla-soup', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Tortilla-Soup-Blend-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Tortilla-Soup-Fry-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Tortilla-Soup-Add-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Tortilla-Soup-Recipe-300x300.jpg', 'Laura', '8', '15 mins', '15 mins', '30 mins', 'February 24, 2017', 'Ingredients?#&?#&4 ripe tomatoes?#&½ white onion?#&1 garlic clove?#&8 cups chicken broth?#&2 spoonfuls fresh cilantro leaves?#&1 cup cooked chickpea beans, peeled?#&1 chipotle pepper (canned)?#&1 peeled and chopped avocado?#&4 ounces Mexican queso fresco or other crumbly fresh cheese cut into ½ inch cubes or 3.5 ounces Monterey Jack grated cheese?#&½ cup sour cream.', 'Instructions?#&?#&Fry the tortilla strips in hot and enough oil. Stir around in the oil nearly constantly until they are golden and crispy. Place on an absorbent paper towel to remove excess oil.?#&Blend the tomatoes, garlic and onion. Strain this mixture and set aside.?#&Preheat a frying pan with 3 spoonfuls of cooking oil.?#&Add the blended and strained mixture.?#&Once well fried, add the chicken broth, cilantro, and chipotle pepper.?#&Season with salt and pepper and allow to cook on low heat for 15 minutes.?#&Divide the cheese and avocado among the soup bowls.?#&Serve a portion of the broth into each bowl, top with a portion of the tortillas strips and 1 tablespoon of sour cream.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'79341528B35BBF409A479BAFD562B423', X'0F8D3EFD26439A4386D8B7F9D94E490E', X'0F8D3EFD26439A4386D8B7F9D94E490E', 0, 'https://www.moms-mexican-recipes.com/mexican-seafood-soup', 'Mexican Seafood Soup', 2, NULL, 0, 'Mexican Seafood Soup', '0', 'Soups', 'https://www.moms-mexican-recipes.com/mexican-seafood-soup', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Seafood-Soup-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Seafood-Soup-Blender-Tomatoes-and-Other-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Seafood-Soup-Add-the-4-Ingredients-for-Flavor-Seasoning-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Seafood-Soup-Ingredients-300x300.jpg', 'Laura', '6', '5 mins', '15 mins', '20 mins', 'February 24, 2017', 'Ingredients for flavor, aroma and seasoning:?#&?#&2 to 3 large sprigs fresh epazote (or a handful of fresh cilantro or parsley sprigs, if no epazote is at hand)?#&4 bay leaves?#&1 teaspoon black pepper?#&2 chicken bouillon cubes', 'Instructions?#&?#&In a large pot bring the 8 ingredients for the broth to a boil.?#&In order to make the sauce, use a blender for the tomatoes, garlic, onion and guajillo pepper. Strain this mixture and fry it in the olive oil.?#&Add the fried tomato sauce to the broth.?#&Partially cover the pot and simmer over medium heat until the vegetables are tender.?#&Raise the heat a little and add the fish cubes.?#&When the broth returns to a gentle boil, add the rest of seafood ingredients: squid, shrimp, octopus and clams and let cook for about 3 minutes?#&Add the 4 ingredients for flavor, aroma and seasoning and let cook for 1 minute before turning off the heat.?#&For a better presentation remove the onion halved, the 4 garlic cloves and the bay leaves.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'9B2C6EBA71E2A84385A61F02A950C1D7', X'E578DC625C21C84DA5DC7FDB9B3ECF7C', X'E578DC625C21C84DA5DC7FDB9B3ECF7C', 0, 'https://www.moms-mexican-recipes.com/cocido-recipe-mexican-beef-soup', 'Cocido Recipe (Mexican Beef Soup)', 2, NULL, 0, 'Cocido Recipe (Mexican Beef Soup)', '6', 'Soups', 'https://www.moms-mexican-recipes.com/cocido-recipe-mexican-beef-soup', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Cocido-Recipe-Skimming-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Cocido-Recipe-300x300.jpg', 'Laura', '8', '15 mins', '30 mins', '45 mins', 'February 24, 2017', 'Ingredients?#&?#&2 quarts water?#&3 pounds beef shank, cut?#&½ white onion, cut in wedges?#&3 garlic cloves, peeled?#&2 large carrots, peeled and cut into chunks?#&2 large ears of corn, husked, and cut across the cob into 1 ½ inch pieces?#&2 medium red skin potatoes, scrubbed and quartered?#&1 stalk celery, cut into 1 inch?#&⅓ head green cabbage, sliced?#&2 zucchini cut into 1-inch pieces?#&⅓ cup fresh cilantro leaves?#&Salt added to taste?#&Hot Sauce added to taste', 'Instructions?#&?#&Begin to cook the meat in a pot of water.?#&At boiling point skim the impurities from the surface as appropriate.?#&Add the onion and garlic.?#&Reduce the heat and cook until the meat is tender, which takes about 2 hours.?#&Add salt to taste, then add the potatoes, corn, carrot, cabbage and celery and continue cooking for 15 to 20 minutes.?#&Add zucchini and cook just long enough until the vegetables are tender-crisp.?#&Before removing from flame add the cilantro leaves.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'E9FD71821A315F4B93A84C23ABE69D50', X'625D595A4C3B514196A67B989E4D2CB1', X'625D595A4C3B514196A67B989E4D2CB1', 0, 'https://www.moms-mexican-recipes.com/albondigas-recipe', 'Albondigas Recipe', 2, NULL, 0, 'Albondigas Recipe', '1', 'Soups', 'https://www.moms-mexican-recipes.com/albondigas-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/04/Albondigas-Recipe-300x300.jpg', 'Laura', '6', '15 mins', '50 mins', '1 hour 5 mins', 'February 24, 2017', 'Ingredients for the sauce?#&3 tomatoes roasted then peeled?#&2 chipotle peppers (canned)?#&½ diced stock of celery?#&1 diced carrot?#&3 tablespoons of fresh cilantro leaves?#&2 cups of water?#&3 slices of onion?#&1 pinch of cumin?#&salt and pepper added to taste.', 'Instructions?#&?#&Mix all the ingredients for the meatballs.?#&Shape grounded meat into meatballs and set aside.?#&Blend the tomatoes and peppers. Strain this mixture and set aside.?#&Bring the water to boil.?#&At boiling point, add the meatballs.?#&Skim the impurities and fat from the surface as appropriate.?#&Add the blended and strained mixture, celery, carrot, cilantro and onion slices.?#&Season with salt, pepper, and cumin.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'04622AB99F4B5549A70D14CAA7482E7D', X'B54F318F3CA006438312000D4814017D', X'B54F318F3CA006438312000D4814017D', 0, 'https://www.moms-mexican-recipes.com/salsa-sauce-recipe', 'Salsa Sauce Recipe', 2, NULL, 0, 'Salsa Sauce Recipe', '0', 'Mexican Cooking Essentials, Salsa & Sauces', 'https://www.moms-mexican-recipes.com/salsa-sauce-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/SALSA-SAUCE-Ingredients-300x300.jpg', 'Laura', '1½ Cups', '10 mins', '', '10 mins', 'February 24, 2017', 'Ingredients?#&?#&2 medium ripe tomatoes?#&2 medium size jalapeno peppers or 4 serrano peppers?#&¼ cup chopped fresh cilantro (optional)?#&Salt added to taste', 'Instructions?#&?#&Dry roast or griddle roast the tomatoes and hot peppers on a baking sheet or griddle at medium heat, turning occasionally until they are blackened and soft.?#&Remove from heat and allow to cool.?#&Remove as much skin as possible.?#&Cut the peppers in half in order to remove the veins and seeds from the interior.?#&Put the peppers, the fresh chopped cilantro, and tomatoes into a blender and blend repeatedly for intervals of no more than 4 or 5 seconds at a time. The purpose of this is to help preserve a certain amount of grittiness, as opposed to making a completely liquefied puree.?#&Transfer contents into a bowl. Thin with a small amount of water and season with salt added to taste.?#&Enjoy it as a topping on many foods');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'B51ED3B7B2EE2649AFA2A0B2673A733C', X'E8B9366DD44E1B43B5729328FD2C32ED', X'E8B9366DD44E1B43B5729328FD2C32ED', 0, 'https://www.moms-mexican-recipes.com/pico-de-gallo-recipe', 'Pico de Gallo Recipe', 2, NULL, 0, 'Pico de Gallo Recipe', '0', 'Salsa & Sauces', 'https://www.moms-mexican-recipes.com/pico-de-gallo-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/pico-de-gallo-recipe.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Pico-De-Gallo-Ingredients-300x300.jpg', 'Laura', '2 Cups', '15 mins', '', '15 mins', 'February 24, 2017', 'Ingredients?#&?#&2 medium ripe tomatoes, chopped?#&2 jalapeno or Serrano peppers, seeded and finely chopped?#&1 medium white onion, chopped?#&4 tablespoons chopped fresh cilantro?#&2 tablespoons fresh lemon juice?#&Salt added to taste', 'Instructions?#&?#&In a medium bowl add lemon juice to onion.?#&Allow to marinade for approx. 10 minutes.?#&Add the rest of ingredients to the mixture and allow to marinade for an additional 15 minutes.?#&Serve and enjoy as a salsa dip for chips or as condiment for main dishes.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'C8869D45DD1D484FA8873C4B38B1DB91', X'4E7D4DE55B854E40A75B30F7392534FF', X'4E7D4DE55B854E40A75B30F7392534FF', 0, 'https://www.moms-mexican-recipes.com/green-sauce-recipe', 'Green Sauce Recipe', 2, NULL, 0, 'Green Sauce Recipe', '0', 'Salsa & Sauces', 'https://www.moms-mexican-recipes.com/green-sauce-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/GREEN-SAUCE-Ingredients-300x300.jpg', 'Laura', '1½ Cups', '10 mins', '15 mins', '25 mins', 'February 24, 2017', 'Ingredients?#&?#&4 or 5 small green tomatoes (tomatillos), husked and rinsed?#&1 cup water?#&2 fresh medium size jalapeno peppers or 4 serrano peppers, stemmed?#&½ cup of fresh cilantro roughly chopped?#&1 to 2 tablespoons finely chopped white onion (optional)?#&Salt and pepper added to taste.', 'Instructions?#&?#&Boil the peppers and tomatillos in water until soft.?#&Cut the softened peppers in half in order to remove the veins and seeds from the interior.?#&Blend to a coarse puree the peppers, tomatillos, and cilantro. There should be about 1½ cups puree.?#&Scrape the mixture into a serving bowl.?#&Rinse the onion under cold water, then shake to remove excess moisture and stir into the salsa. (optional)?#&Season with salt added to taste.?#&Serve as a condiment for scrambled eggs, tacos, grilled meats, chicken...whatever you wish.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'278F69A9C980F8439446B3367DA09487', X'00505714E86D8B4CAB80BD36CDF4F744', X'00505714E86D8B4CAB80BD36CDF4F744', 0, 'https://www.moms-mexican-recipes.com/mexican-rice-recipe', 'Mexican Rice Recipe', 2, NULL, 0, 'Mexican Rice Recipe', '0', 'Main Dishes, Mexican Cooking Essentials', 'https://www.moms-mexican-recipes.com/mexican-rice-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Rice-Blend-the-tomatoes-onion-garlic-salt-and-pepper-step-1-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Rice-Add-the-celery-peas-carrots-jalapenos-parsley-chicken-broth-step-5-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Rice-Recipe-300x300.jpg', 'Laura', '8', '10 mins', '20 mins', '30 mins', 'February 24, 2017', 'Ingredients?#&?#&4 spoonfuls of sunflower oil?#&2 cups of rice moistened in hot water, then rinsed in cold?#&2 tomatoes?#&1 white onion?#&1 garlic clove, peeled?#&Salt and pepper added to taste?#&1 stalk celery?#&2 tablespoons parsley?#&½ cup tender peas?#&½ cup of finely chopped carrots?#&2 whole jalapeno peppers?#&4 cups chicken broth?#&½ lemon (the juice).', 'Instructions?#&?#&Blend the tomatoes, onion, garlic, salt and pepper. Strain and set aside.?#&Fry the rice in the oil.?#&Add the blended mixture –step 1.?#&Fry until crisp.?#&Add the celery, peas, carrots, jalapenos, parsley, chicken broth, and lemon juice, with salt added to taste.?#&As it begins to steam, cover and continue cooking with low heat.?#&Cook until done, a period of about 20 minutes.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'93387AE9D7B2C547ACB27123D3FDE80F', X'BA049257156ED84FBEFBE0E808E8740C', X'BA049257156ED84FBEFBE0E808E8740C', 0, 'https://www.moms-mexican-recipes.com/mexican-pork-menudo-recipe', 'Mexican Pork Menudo Recipe', 2, NULL, 0, 'Mexican Pork Menudo Recipe', '7', 'Main Dishes', 'https://www.moms-mexican-recipes.com/mexican-pork-menudo-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Menudo-Ingredients-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Menudo-Recipe-300x300.jpg', 'Laura', '2', '15 mins', '45 mins', '1 hour', 'February 24, 2017', 'Ingredients?#&?#&500 grams Pork Belly?#&100 grams Pork Liver?#&4 cloves Garlic, minced?#&1 Onion, chopped?#&1 small Potato, cut into cubes?#&1 small Carrot, cut into cubes?#&1 Red Bell Pepper, diced?#&1 Cup Tomato Sauce?#&1 tbsp. Lemon Juice?#&2 tbsp. Soy Sauce?#&3 tbsp. Vegetable Oil?#&1 cup Water?#&1 cup Sliced Hotdog?#&¼ cup Green Peas?#&2 tbsp. Liver Spread?#&Brown Sugar, Salt, and Pepper to taste', 'Instructions?#&?#&Marinate the pork in soy sauce and lemon juice for an hour.?#&In a pot, heat oil and fry potatoes and carrots. Drain and set aside.?#&In the same pot, sautee garlic and onion.?#&Add marinated pork belly and water. Simmer for 15 minutes.?#&Add the pork liver and tomato sauce. Simmer until pork and liver are tender.?#&Add potatoes, carrots, red bell pepper, raisins, hotdog, sweet peas, and liver spread. Simmer for 5 minutes.?#&Season with sugar, salt, and pepper.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'DF9A821569274946A8FC0F0382E50A55', X'AB7141BFA75AE04F874D11B08A24FFCF', X'AB7141BFA75AE04F874D11B08A24FFCF', 0, 'https://www.moms-mexican-recipes.com/cornbread-recipe', 'Cornbread Recipe', 2, NULL, 0, 'Cornbread Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/cornbread-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/CORNBREAD__Ingredients-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Corn-Bread-Recipe-300x300.jpg', 'Laura', '8', '20 mins', '', '20 mins', 'February 24, 2017', 'Ingredients?#&?#&9 fresh ears of corn, husked?#&3 eggs?#&1 cup sour cream?#&1 spoonful baking powder?#&1 teaspoon salt?#&pepper added to taste?#&1 cup grated cheese (cheddar or Monterrey Jack)?#&1 large fresh poblano pepper?#&1 red bell pepper.', 'Instructions?#&?#&Preheat oven to 350° F.?#&Cut the corn from the cob with a knife. Set aside.?#&Roast the chili peppers. Rub off the blackened skin, then cut out the stem and seed pod. Roughly chop. Set aside.?#&Grease and flour a 9-by-13 inch baking dish.?#&Grind in a blender the corn, eggs, sour cream, baking powder, salt and pepper to make a paste.?#&Place half of paste at the bottom of the baking dish.?#&Add the filling: cheese and chili peppers. Then top with remaining paste.?#&Bake at 350° F for approx. 50 minutes or until golden brown.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'2DF6FFFC3DAAD449955846B41495403B', X'09770EBE29D6DD4D8695742B0E0D28E5', X'09770EBE29D6DD4D8695742B0E0D28E5', 0, 'https://www.moms-mexican-recipes.com/charro-beans-recipe', 'Charro Beans Recipe', 2, NULL, 0, 'Charro Beans Recipe', '5', 'Main Dishes', 'https://www.moms-mexican-recipes.com/charro-beans-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Charro-Beans-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Charro-Beans-with-Onions-and-Garlic-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Charro-Beans-Add-the-Cilantro-and-Sausage-300x300.jpg', 'Laura', '8', '15 mins', '45 mins', '1 hour', 'February 24, 2017', 'Ingredients?#&?#&1 pound of beans?#&2 cloves of peeled garlic?#&1 white onion (1/2 whole, ½ chopped)?#&2 chopped tomatoes?#&3 tablespoons of chopped fresh cilantro?#&Salt?#&Water?#&1 teaspoon of powdered chicken broth?#&1 sliced serrano or jalapeno pepper?#&5 ounces of bacon cut into small pieces?#&5 ounces of chorizo?#&5 ounces of pork rinds in small pieces (optional)?#&2 sliced turkey sausages (optional).', 'Instructions?#&?#&Leave a pot of beans covered in water overnight.?#&The following day replace the water - adding a sufficient amount to begin cooking.?#&As the water reaches its boiling point, add the ½ onion and garlic cloves.?#&Cook until done, then add salt according to taste.?#&In a frying pan with a small amount of cooking oil, fry the bacon and chorizo for about 4-5 minutes.?#&Add the chopped onion, the chilli pepper, and tomato and continue to fry.?#&Add the fried ingredients to the pot of beans.?#&Once the mixture is brought to boil, add the cilantro, pork rinds, and sausages. Season with salt and powdered chicken broth.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'2F4CCED3AD4C8046839D6FA09714DAE7', X'542A285E1BCE264280303BE693FD5643', X'542A285E1BCE264280303BE693FD5643', 0, 'https://www.moms-mexican-recipes.com/arroz-con-pollo-recipe', 'Arroz con Pollo Recipe', 2, NULL, 0, 'Arroz con Pollo Recipe', '0', 'Main Dishes', 'https://www.moms-mexican-recipes.com/arroz-con-pollo-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/arroz-con-pollo-recipe-4-300x300.jpg', 'Laura', '8', '5 mins', '45 mins', '50 mins', 'February 24, 2017', 'Ingredients?#&?#&8 chicken wings and 1 cut chicken breast?#&7 Poblano peppers, washed and with seeds removed?#&1 garlic clove?#&1 white onion?#&2 ½ cups of rice, ½ to be placed in a pan of hot water for 15 minutes; rinsed and strained?#&2 spoonfuls of oil?#&5 cups of water?#&16 fresh clams, cleaned?#&2 spoonfuls of fresh cilantro leaves or parsley.', 'Instructions?#&?#&Blend the peppers, garlic, and onion.?#&In a frying pan cooks the rice in oil.?#&When the rice turns into a gold color discard excess oil.?#&Add the blended mixture to the rice and allow to dry a little bit.?#&Add the 5 cups of water, the clams, and chicken.?#&Cover the pan and cook with a low flame.?#&Decorate with cilantro leaves or parsley.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'BF1584A66945B04192C47ACF8C9E429A', X'BC4CB87789BAA84793ED0B704CFE640C', X'BC4CB87789BAA84793ED0B704CFE640C', 0, 'https://www.moms-mexican-recipes.com/sweet-rice-recipe', 'Sweet Rice Recipe', 2, NULL, 0, 'Sweet Rice Recipe', '0', 'Desserts', 'https://www.moms-mexican-recipes.com/sweet-rice-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Sweet-Rice-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Sweet-Rice-Ingredients-300x300.jpg', 'Laura', '6', '10 mins', '35 mins', '45 mins', 'February 24, 2017', 'Ingredients?#&?#&1 cup rice, preferably medium-grain?#&1 cinnamon stick?#&1 slice lemon rind?#&1 cup of water?#&⅓ cup of raisins?#&⅓ cup of sliced pecans?#&2 cups of hot milk?#&1 can of condensed milk?#&ground cinnamon added according to taste. for serving', 'Instructions?#&?#&Wash the rice and cook in water, along with the cinnamon stick and lemon rind.?#&Once the mixture softens, add the raisins and hot milk.?#&Cook over low heat for 5 minutes.?#&Add the condensed milk and stir.?#&Spoon the mixture into a serving dish or individual bowls.?#&Sprinkle on top the ground cinnamon and the pecans.?#&Enjoy.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'27A4647397F3164983D0E0273DB9E7FF', X'37CCB5986C5555489D74A1759FC6094E', X'37CCB5986C5555489D74A1759FC6094E', 0, 'https://www.moms-mexican-recipes.com/mexican-flan-recipe', 'Mexican Flan Recipe', 2, NULL, 0, 'Mexican Flan Recipe', '0', 'Desserts', 'https://www.moms-mexican-recipes.com/mexican-flan-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Flan-300x200.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Mexican-Flan-Ingredients-300x200.jpg', 'Laura', '3', '20 mins', '20 mins', '40 mins', 'February 24, 2017', 'Ingredients?#&?#&¼ cup Evaporated Milk?#&¼ cup Condensed Milk?#&½ cup Full-Cream Milk?#&2 Eggs?#&1 tbsp. Vanilla Extract?#&2 tbsp. Sugar?#&¼ cup Diced Pecans?#&2 tbsp. Melted Butter', 'Instructions?#&?#&Place the sugar in a saucepan.?#&Heat until fully dissolved and slightly syrupy.?#&Brush ramekins with melted butter.?#&Pour a thin layer of syrup into each ramekin.?#&Leave to cool until the syrup has solidified.?#&Whisk together the evaporated milk, condensed milk, eggs, and vanilla extract in a bowl.?#&Pour milk and egg mixture into the ramekins.?#&Cover each ramekin with foil or plastic wrap.?#&Steam for 15-20 minutes.?#&Top with diced pecans.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'C69BCA169222874D86657D46A539DFC5', X'621BDCFBCC75484EB244852BEB69855D', X'621BDCFBCC75484EB244852BEB69855D', 0, 'https://www.moms-mexican-recipes.com/churros-recipe', 'Churros Recipe', 2, NULL, 0, 'Churros Recipe', '0', 'Desserts', 'https://www.moms-mexican-recipes.com/churros-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Churros-Recipe.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Churros-Ingredients-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Churros-combining-the-Dough-300x300.jpg', 'Laura', '12 to 15 five inch churros', '15 mins', '10 mins', '25 mins', 'February 24, 2017', 'Ingredients?#&?#&1 cup all purpose flour sifted?#&Vegetable oil to a depth of 1 inch for frying?#&2½ cups of water?#&⅓ teaspoon salt?#&1 teaspoon vanilla?#&1 teaspoon ground cinnamon?#&1 cinnamon stick (approx 5-inch)?#&1 teaspoon baking powder?#&⅔ cup sugar', 'Instructions?#&?#&Spread the sugar over the bottom of a baking pan, set aside.?#&Mix flour, baking powder, and ground cinnamon set aside.?#&In a medium saucepan, combine the salt, vanilla and the cinnamon stick with the water. Set over high heat and bring to boil.?#&Allow water slow boil for about 3 minutes before removing the cinnamon stick. Then add the mixture of flour all at once, stirring vigorously for 2 minutes or until the mixture forms a thick, smooth-textured ball.?#&Remove from heat and let cool in the pan.?#&Heat the oil in a large heavy pan or cast-iron skillet (that''s about 9 inches across and 3 inches deep) over medium to medium-high heat.?#&Scoop the dough into a cookie press fitted with a ⅜ inch fluted opening or a heavy duty pastry bag fitted with a ⅜ inch star tip.?#&Place the pressing apparatus a few inches above the hot oil and press out 5-inch portions of dough, pulling each part freely from the press using fingertips. Repeat this step 4 o 5 times depending on the desired number of churros to be fried at a given time. Turn occasionally until golden brown.?#&Place the golden brown churros on paper towels to remove excess oil.?#&Roll the churros in the sugar while still warm.?#&Ready to enjoy.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'3FFD1C27A719CE4FA4A7A31F4680FA4F', X'77033E5C96675B4A9C75884C37A7D5AD', X'77033E5C96675B4A9C75884C37A7D5AD', 0, 'https://www.moms-mexican-recipes.com/capirotada-recipe', 'Capirotada Recipe or Mexican Bread Pudding', 2, NULL, 0, 'Capirotada Recipe or Mexican Bread Pudding', '0', 'Desserts', 'https://www.moms-mexican-recipes.com/capirotada-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Capirotada-Recipe.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Capirotada-Recipe-Ingredients-300x300.jpg', 'Laura', '6', '30 mins', '30 mins', '1 hour', 'February 24, 2017', 'Ingredients?#&?#&½ loaf French bread cut into 1-inch slices?#&14 ounces raw brown sugar?#&4 cups of water?#&4-5 slices of onion?#&1 cinnamon stick (approx 5-inch)?#&4 cloves?#&½ cup of sliced pecans?#&½ cup of peanuts, with no salt, peeled?#&½ cup of raisins?#&½ cup of grated coconut?#&½ cup of small candy?#&½ cup of grated Cheddar cheese.', 'Instructions?#&?#&Preheat the oven to 350° F.?#&Toast the bread slices until crispy and set aside.?#&In a medium saucepan bring the water to boil.?#&At boiling point, add the brown sugar, onion, cinnamon, and cloves.?#&Set over medium heat and bring to a gentle boil until the mixture thickens into a syrup like texture. Allow to cool, then strain.?#&To assemble the Mexican bread pudding use a 9 x 9-inch baking pan in order to make 2 layers of bread, syrup, peanuts, pecans, raisins, coconut, small candies, and cheese. Cover with aluminum foil.?#&Bake in the preheated 350° F oven for about 40 minutes.?#&Capirotada recipe is ready to enjoy with a glass of fresh milk.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'CAE057FAD62FD542B5D5DCD520CA62DF', X'FD245A227A3D7646933CE197438A2E68', X'FD245A227A3D7646933CE197438A2E68', 0, 'https://www.moms-mexican-recipes.com/chicken-burrito-recipe', 'Chicken Burrito Recipe', 2, NULL, 0, 'Chicken Burrito Recipe', '0', 'Burritos', 'https://www.moms-mexican-recipes.com/chicken-burrito-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Chicken-Burrito-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Chicken-Burrito-Ingredients-300x300.jpg', 'Laura', '4', '15 mins', '20 mins', '35 mins', 'February 24, 2017', 'Ingredients?#&?#&1 chicken breast?#&1 quart of water?#&1 garlic clove?#&1 white onion (1/2 whole, ½ chopped)?#&1 chopped sweet green pepper?#&2 chopped tomatoes?#&oil?#&8 flour tortillas?#&mayonnaise?#&salt and pepper added to taste', 'Instructions?#&?#&Cook the chicken in a pot with water, garlic, and the ½ onion.?#&Once cooked, remove the chicken from the broth and allow to cool then shred.?#&Season the chicken with salt and pepper and fry until golden brown.?#&Add the chopped onion, the green sweet pepper, and the tomato.?#&Once the onion becomes transparent add 1 cup of broth from the pot where the chicken was cooked.?#&Cook over low heat until the stew has dried.?#&Heat and prepare the flour tortillas: spread the mayonnaise on top.?#&Put a portion of shredded chicken on each tortilla, then fold.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'A7A7F360F4083F48B1B8B7713F449319', X'4F29A4EABB4D9843949BDDC6D7162A98', X'4F29A4EABB4D9843949BDDC6D7162A98', 0, 'https://www.moms-mexican-recipes.com/beef-burrito-recipe', 'Beef Burrito Recipe', 2, NULL, 0, 'Beef Burrito Recipe', '0', 'Burritos', 'https://www.moms-mexican-recipes.com/beef-burrito-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/beef-burrito-recipe-final-5-300x300.jpg', 'Laura', '6', '25 mins', '40 mins', '1 hour 5 mins', 'February 24, 2017', 'Ingredients?#&?#&1 pound of beef flank steak or pork tenderloin?#&6 cups of water?#&2 chopped tomatoes?#&1 onion ((1/2 whole, ½ chopped)?#&1 garlic clove?#&2 green chilli peppers chopped with seeds removed?#&Olive oil?#&Salt and pepper added to taste?#&12 flour tortillas?#&Mayonnaise.', 'Instructions?#&?#&Cook the beef in a pot or pressure cooker with water, garlic and the ½ onion.?#&Once cooked remove the beef from the broth. Allow to cool, then shred.?#&Lightly fry the tomatoes, the chilli peppers, and the chopped onion.?#&When the onion becomes transparent, add the shredded beef with one cup of broth from the pot where the beef was cooked.?#&Season to taste.?#&Cook over low heat until the stew has dried.?#&Heat and prepare the flour tortillas; spread lightly mayonnaise on inside.?#&Put a portion of the shredded beef then fold.?#&Your burrito is ready to enjoy.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'69C87C98AC943542A4F4F5A747E824E4', X'84F6ED72290BD9439975B15188D37182', X'84F6ED72290BD9439975B15188D37182', 0, 'https://www.moms-mexican-recipes.com/huevos-a-la-mexicana-recipe', 'Huevos a la Mexicana Recipe', 2, NULL, 0, 'Huevos a la Mexicana Recipe', '1', 'Breakfasts', 'https://www.moms-mexican-recipes.com/huevos-a-la-mexicana-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Huevos-A-La-Mexicana-Recipe-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Huevos-a-la-mexicana-Ingredients-300x300.jpg', 'Laura', '6', '15 mins', '12 mins', '27 mins', 'February 22, 2017', 'Ingredients?#&?#&10 eggs?#&1 diced onion?#&2 diced tomatoes?#&2 diced chilli peppers?#&3 spoonfuls vegetable oil?#&Salt?#&Pepper?#&Olive oil', 'Instructions?#&?#&In a deep bowl, lightly batter the eggs and set aside.?#&In a large frying pan fry the onion and chilli peppers.?#&When the onion becomes transparent, add the tomatoes and keep frying.?#&Add eggs with salt and pepper.?#&Cook over medium heat and stir periodically.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'3B98A2268C59FD478820046AEC2BAEA4', X'EA866EB654F58644A4DAB3CE5FC3FD2B', X'EA866EB654F58644A4DAB3CE5FC3FD2B', 0, 'https://www.moms-mexican-recipes.com/huevos-rancheros-recipe', 'Huevos Rancheros Recipe', 2, NULL, 0, 'Huevos Rancheros Recipe', '0', 'Breakfasts', 'https://www.moms-mexican-recipes.com/huevos-rancheros-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Huevos-Rancheros-Recipe-300x300.jpg', 'Laura', '6', '', '40 mins', '40 mins', 'February 22, 2017', 'Ingredients?#&?#&12 eggs?#&12 corn tortillas?#&6 slices of bacon?#&4 tomatoes, grilled and peeled?#&1 onion thinly sliced.?#&2 Poblano peppers, grilled, peeled and with seeds removed, cut into small slices.?#&1 cup chicken broth, which can be substituted for 1 cup water with a chicken bouillon cube.?#&Oil?#&Salt?#&Black pepper.', 'Instructions?#&?#&Blend the tomatoes.?#&Fry in small amount of oil the sliced onion and poblano peppers.?#&Add to this mixture the tomatoes, blended and strained.?#&Add the cup of broth and season to taste.?#&Keep in low heat until boiling.?#&Fry the bacon strips in a small amount of oil and set aside.?#&Fry the tortillas, one by one, in sufficient oil, then place on absorbent paper towel to remove excess oil.?#&Serve 2 fried tortillas on each plate, topped with 2 fried eggs.?#&Apply the pre-heated sauce.?#&Add a strip of bacon with each serving.?#&Serve immediately.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'8B8E00F05B02EE419063A4E4925BC055', X'DB2D521635FE43498B6524C5F5D094DA', X'DB2D521635FE43498B6524C5F5D094DA', 0, 'https://www.moms-mexican-recipes.com/chilaquiles-recipe', 'Chilaquiles Recipe', 2, NULL, 0, 'Chilaquiles Recipe', '0', 'Breakfasts', 'https://www.moms-mexican-recipes.com/chilaquiles-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Chilaquiles-Ingredients-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Chilaquiles-Recipe-300x300.jpg', 'Laura', '8', '15 mins', '30 mins', '45 mins', 'February 22, 2017', 'Ingredients?#&?#&1 pound of small green tomatoes (tomatillos), quartered?#&1 cup water?#&1 clove garlic, whole?#&2 medium size jalapeno peppers or 4 serrano peppers?#&½ cup of fresh cilantro?#&Salt and pepper added to taste?#&20 corn tortillas cut in triangles?#&2 chicken breasts, cooked and shredded?#&1 cup sour cream?#&1 cup Monterrey Jack or Cheddar cheese, grated?#&2 cups chicken broth?#&1 cup vegetable oil.', 'Instructions?#&?#&Preheat the oven to 400° F.?#&Boil the tomatillos in water with garlic, chili peppers, salt and pepper until soft.?#&Transfer everything to a blender. Add the cilantro. Then blend to an almost smooth puree. There should be about 2½ cups puree.?#&Fry this mixture in 2 spoonfuls of oil.?#&Add the chicken broth and allow to boil.?#&Fry the tortillas triangles in remaining oil. Place on an absorbent paper towel to remove excess oil.?#&Apply grease to a 9-by-12 inch baking dish. Make 2 or 3 layers of tortillas, shredded chicken, sauce and cheese, ending with a moderately thick layer of sour cream on top.?#&Bake for 20 minutes or until piping hot.?#&Serve.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'CF23F5F6577E064B98D829874CED2F41', X'DC0B6BFA5F0DEA4DB3CE126C56258DFD', X'DC0B6BFA5F0DEA4DB3CE126C56258DFD', 0, 'https://www.moms-mexican-recipes.com/refried-beans-recipe', 'Refried Beans Recipe', 2, NULL, 0, 'Refried Beans Recipe', '0', 'Appetizers, Mexican Cooking Essentials', 'https://www.moms-mexican-recipes.com/refried-beans-recipe', 'https://s.w.org/images/core/emoji/13.0.1/svg/1f642.svghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Refried-Beans-Ingredients-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/refried-beans-recipe.jpg', 'Laura', '6', '10 mins', '10 mins', '20 mins', 'February 21, 2017', 'Ingredients?#&?#&¾ cup of lard or 1 cup of sunflower oil?#&½ white onion finely diced?#&3 cups cooked beans (mashed)?#&1 cup bean broth?#&Salt added to taste?#&Cheddar grated cheese added according to taste (optional)?#&10-12 tortilla chips to adorn (optional)', 'Instructions?#&?#&Fry the onion in hot grease until golden in color.?#&Add the mashed beans in its broth and season with salt.?#&Keep frying until the mass becomes solid and you are able to see the bottom of the skillet.?#&Remove from heat and place refried beans on a serving dish.?#&Spread the grated cheese on top and adorn with tortilla chips.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'82CEF4D83120144A82E2136B13090FB0', X'65D441EE637C8241BA8E9DE49C589C0F', X'65D441EE637C8241BA8E9DE49C589C0F', 0, 'https://www.moms-mexican-recipes.com/homemade-tortilla-recipe', 'Homemade Tortilla Recipe', 2, NULL, 0, 'Homemade Tortilla Recipe', '0', 'Appetizers, Mexican Cooking Essentials', 'https://www.moms-mexican-recipes.com/homemade-tortilla-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/corn-tortilla-rolling-pin-300x300.jpg', 'Laura', '15', '', '', '', 'February 21, 2017', 'Ingredients?#&?#&1 pound of fresh-ground corn masa for corn or 1¾ cups masa harina for flour?#&1 cup plus 2 tablespoons of warm water?#&1 pinch of salt.', 'Instructions?#&?#&Place the corn dough and salt in a container.?#&Add water a little at a time and begin to knead.?#&Continue to hand knead until you have obtained a good soft consistency that is not sticky.?#&Cover with a cloth and let the dough stand 10 minutes.?#&Pre-heat a lightly greased frying pan.?#&Take about 1 – 1 ½ spoonfuls of the corn dough at a time; hand roll it into a small ball.?#&if you use the tortilla press then line it with the sheets of plastic. A ball of corn meal dough is placed in center and it gets flattened once you press. If you use a roller: Place the ball of corn dough on a sheet of plastic then place another on top. Use a roller to flatten it out. It should be about 3.5 inches in diameter.?#&Place a lightly humid cloth on a large tray where you can begin placing the tortillas.?#&Cook the tortillas on each side until done.?#&Serve hot, wrapped in a nice cloth to preserve warmth.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'21D218BE2DF92145BA57DF2146869BFB', X'6C8FA8CC7BC54C4292779FDD9085F752', X'6C8FA8CC7BC54C4292779FDD9085F752', 0, 'https://www.moms-mexican-recipes.com/guacamole-recipe', 'Guacamole Recipe', 2, NULL, 0, 'Guacamole Recipe', '0', 'Appetizers, Mexican Cooking Essentials', 'https://www.moms-mexican-recipes.com/guacamole-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/guacamole-recipe.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/02/Guacamole-Ingredients-300x300.jpg', 'Laura', '6', '15 mins', '', '15 mins', 'February 21, 2017', 'Ingredients?#&?#&1 or 2 fresh serrano or jalapeno peppers, seeded and finely chopped?#&½ cup chopped onion?#&½ cup chopped tomato?#&¼ cup coarsely chopped fresh cilantro, plus a little extra for garnish?#&3 medium-large ripe avocados (about 1¼ pounds total)?#&Salt added to taste?#&1 tablespoon fresh lime juice or lemon juice.', 'Instructions?#&?#&Place avocado pulp in a medium bowl.?#&Using a fork mash the pulp with the lime juice - mixture will be slightly chunky.?#&In a food processor or blender, combine tomato, onion, cilantro and chili peppers. Process or blend until finely chopped -guacamole tastes harmonious when all the flavorings are finely chopped.?#&Stir blended mixture and salt into mashed avocados until combined.?#&To serve, scoop into a decorative bowl. Guacamole is good when freshly made, but it tastes even better when the flavors are allowed to mingle for about 30 minutes.');
INSERT INTO `VWR INTERNAL Moms-mexican-recipes recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, commentCount, recipeCategory, url, image, author, recipeYield, prepTime, cookTime, totalTime, datePublished, recipeIngredientHTML, recipeInstructionHTML) VALUES (X'1B212DB6FE644F439FD149FF91134A7D', X'3A828DCF4644CC40A0D2A11913212955', X'3A828DCF4644CC40A0D2A11913212955', 0, 'https://www.moms-mexican-recipes.com/ceviche-recipe', 'Ceviche Recipe', 2, NULL, 0, 'Ceviche Recipe', '0', 'Appetizers', 'https://www.moms-mexican-recipes.com/ceviche-recipe', 'https://www.moms-mexican-recipes.com/wp-content/uploads/2017/04/Ceviche-Recipe.jpg-300x300.jpghttps://www.moms-mexican-recipes.com/wp-content/uploads/2017/04/Ceviche-Main-Ingredients-300x300.jpg', 'Laura', '8', '45 mins', '30 mins', '1 hour 15 mins', 'February 21, 2017', 'Ingredients?#&?#&1 pound of fresh, skinless red snapper well cleaned and cut into ½ inch cubes (remove all black and red)?#&1 pound of oysters?#&6 ripe tomatoes chopped into ¼ inch pieces?#&1 can of sliced pickled jalapeno peppers?#&20 lemons (the juice)?#&¾ cup of olive oil, preferably extra-virgin to give a glistening appearance?#&1 white onion chopped into ¼ inch pieces?#&2 tablespoons of oregano?#&½ cup chopped fresh cilantro?#&10 olives?#&1 large avocado peeled and diced?#&1 cup catsup?#&salt and pepper added to taste.', 'Instructions?#&?#&Place the fish in a glass or stainless steel bowl.?#&Add the lemon juice and allow to marinade for 4 hours.?#&Rinse out the fish and dispose of the juice.?#&In a large bowl, mix the remaining ingredients.?#&Stir in the fish, then taste and season with salt and pepper.?#&Serve with parted lemon, tortilla chips or saltine crackers.');
