INSERT INTO `VWR INTERNAL Smokingmeat` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'6C7D061C3513844C84682C489FA2A897', X'23523B7D0D6D754F930412941AFFCB2E', NULL, 0, 'https://www.smoking-meat.com/category/newsletters/page/1', NULL, 2, NULL, 0);
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F080C894AED347BB47537510DCB949', X'A8C679E127B9A741BF5E508C48A19F49', X'A8C679E127B9A741BF5E508C48A19F49', 0, NULL, NULL, 2, NULL, 0, 'Make a brine using ½ gallon of cold water, ½ cup of coarse kosher salt, and ½ cup brown sugar (the brown sugar adds some flavor but is optional).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E0F495887A8FB4BBCAD7B0EE74B2A57', X'A8C679E127B9A741BF5E508C48A19F49', X'A8C679E127B9A741BF5E508C48A19F49', 1, NULL, NULL, 2, NULL, 0, 'Pour the salt and brown sugar into the water and stir for about a minute or until the water becomes a clear amber color.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0704B4A7696F1489F519248B95CF246', X'7B1ABD4F08BC3A40A24B96926EB09AEA', X'7B1ABD4F08BC3A40A24B96926EB09AEA', 0, NULL, NULL, 2, NULL, 0, 'Place the tenderloins into a medium sized plastic/glass container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61B3C4987CF5D24DA9820F2525C53E30', X'7B1ABD4F08BC3A40A24B96926EB09AEA', X'7B1ABD4F08BC3A40A24B96926EB09AEA', 1, NULL, NULL, 2, NULL, 0, 'Pour the brine over the chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B4BC958FCC95841857073F541B27416', X'7B1ABD4F08BC3A40A24B96926EB09AEA', X'7B1ABD4F08BC3A40A24B96926EB09AEA', 2, NULL, NULL, 2, NULL, 0, 'Lid it and place in the fridge for about 60-90 minutes. 60 minutes will do the trick if you''re in a hurry.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61BBFD23705B994B88B858E5F54F6B9E', X'7B1ABD4F08BC3A40A24B96926EB09AEA', X'7B1ABD4F08BC3A40A24B96926EB09AEA', 3, NULL, NULL, 2, NULL, 0, 'When the brining process is complete, rinse the chicken one time in cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CF888A5BB461B4799F31805B23F4083', X'25E190C4CE4C764EAF0314267C5BB31E', X'25E190C4CE4C764EAF0314267C5BB31E', 0, NULL, NULL, 2, NULL, 0, 'Lay the brined chicken out on a pan/rack if you have one otherwise you can just lay them out on parchment paper or a large sheet of foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'246A34A29E314040A6A981AE7CB67D20', X'25E190C4CE4C764EAF0314267C5BB31E', X'25E190C4CE4C764EAF0314267C5BB31E', 1, NULL, NULL, 2, NULL, 0, 'Lightly sprinkle my Texas style rub on the top of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18B488331B2D4A4C91F7C69CB19FE52A', X'D56D443DA580FD4DBF47F01AEE39652C', X'D56D443DA580FD4DBF47F01AEE39652C', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 225-240°F and if your smoker uses a water pan, fill it up. I used a mix of hickory and cherry. Should take 80-90 minutes to reach 155-160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F8CD41D7940134AAB8074FA2D9D8883', X'D496EEEAD018014CBD2744469B9600A2', X'D496EEEAD018014CBD2744469B9600A2', 0, NULL, NULL, 2, NULL, 0, 'When the chicken reaches about 135°F, if you have a griddle or a hot pan, throw them chicken tenderloins in and give them a quick sear on both sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6228A23878BE51499297285B61BD8B55', X'D496EEEAD018014CBD2744469B9600A2', X'D496EEEAD018014CBD2744469B9600A2', 1, NULL, NULL, 2, NULL, 0, 'When they reach anywhere between 155 and 160°F, place them in a covered foil pan to keep them hot as they get finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7FA38B4E7F13248ADA8BD5C3F871DAC', X'390FDA7D79765A4A85F0EDB7415482ED', X'390FDA7D79765A4A85F0EDB7415482ED', 0, NULL, NULL, 2, NULL, 0, 'Bring into the house and serve them up with some sides or use them to make an amazing sandwich with melted cheese, veggies and condiments.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DB7FFA3752B484DA6640E8F4CFD7C68', X'E9EDE952E4E23E4A95DF1B6124A26472', X'E9EDE952E4E23E4A95DF1B6124A26472', 0, NULL, NULL, 2, NULL, 0, 'Slice the rack of lamb into individual chops');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4514739AE032B34A91C15FAB3DA3FB99', X'0C5BEC1F1D63EC41A0C45B4103A2EB7A', X'0C5BEC1F1D63EC41A0C45B4103A2EB7A', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle a pinch of coarse kosher salt onto the top side of each chop. Place pan of chops into fridge for at least 2 hours or overnight. No need to rinse when finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'805011E2B1242F4F837D52038C2C3016', X'0E15418C7D646E4EBD128C61069E7970', X'0E15418C7D646E4EBD128C61069E7970', 0, NULL, NULL, 2, NULL, 0, 'Make a glaze using 3/4 cup of Jeff''s barbecue sauce, 1/2 cup balsamic vinegar, 3 TBS of A1 sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30EF2082615DDD43AE9F23E7A892222E', X'0E15418C7D646E4EBD128C61069E7970', X'0E15418C7D646E4EBD128C61069E7970', 1, NULL, NULL, 2, NULL, 0, 'Brush the top side of each chop with the glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AC08CC05C8B3F49AE8BF3AA742B9525', X'0E15418C7D646E4EBD128C61069E7970', X'0E15418C7D646E4EBD128C61069E7970', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the top of each chop with Jeff''s Texas style rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D5358A2C3882748816FE865264DBEB1', X'72276BF937DEE74488C2D3AE139C17F7', X'72276BF937DEE74488C2D3AE139C17F7', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 220℉ using indirect heat. If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37B8D8562E0A684B88E41CECC40721DF', X'72276BF937DEE74488C2D3AE139C17F7', X'72276BF937DEE74488C2D3AE139C17F7', 1, NULL, NULL, 2, NULL, 0, 'I recommend oak for smoke wood if you have some, otherwise use whatever smoking wood you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'087C62A044F1EE4AB3CDF59BC5025469', X'72276BF937DEE74488C2D3AE139C17F7', X'72276BF937DEE74488C2D3AE139C17F7', 2, NULL, NULL, 2, NULL, 0, 'Smoke cook chops for about 25-30 minutes or until they reach medium rare (128-130℉).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B9299546C255945A417943493E63E63', X'72276BF937DEE74488C2D3AE139C17F7', X'72276BF937DEE74488C2D3AE139C17F7', 3, NULL, NULL, 2, NULL, 0, 'Rest for 10 minutes under foil before searing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E0B95C12A52C24B848E0E3783E74C59', X'AB0127A1D56B164E8EB98637C38306C4', X'AB0127A1D56B164E8EB98637C38306C4', 0, NULL, NULL, 2, NULL, 0, 'Use a griddle, hot pan, hot grill or broiler to quickly sear the top and bottom sides of the lamb chops.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F77D2712F3383641BBF6612647937B63', X'8BB3D1AB67130E4D95AEDD723BD41005', X'8BB3D1AB67130E4D95AEDD723BD41005', 0, NULL, NULL, 2, NULL, 0, 'Keep warm until serving time. Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E26A4850A033F4899AEE7949C315F11', X'F07FFEEEF6C4B840972F6F037AAF90E8', X'F07FFEEEF6C4B840972F6F037AAF90E8', 0, NULL, NULL, 2, NULL, 0, 'Cut peppers in half lengthwise leaving part of the stem on each piece.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBB9C35671B59D4FBED338D913B323D3', X'F07FFEEEF6C4B840972F6F037AAF90E8', X'F07FFEEEF6C4B840972F6F037AAF90E8', 1, NULL, NULL, 2, NULL, 0, 'Scrape out/remove the seeds and white parts inside the jalapenos');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29BCBB9F4A26FD4385B4C38AE69E59B4', X'6B88901C2F788D4C8FB9F37DC7DE036F', X'6B88901C2F788D4C8FB9F37DC7DE036F', 0, NULL, NULL, 2, NULL, 0, 'Use storebought or homemade pimento cheese to stuff peppers full and slightly heaped up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7E3422FCC6B8E4B91D088080F9890A2', X'7AEE4AFBFFADD64EB817336A5DF92A83', X'7AEE4AFBFFADD64EB817336A5DF92A83', 0, NULL, NULL, 2, NULL, 0, 'Cut the bacon slices in half and wrap a half piece around each pepper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'560FF419A45C73429AB7AB63B9094F8A', X'7AEE4AFBFFADD64EB817336A5DF92A83', X'7AEE4AFBFFADD64EB817336A5DF92A83', 1, NULL, NULL, 2, NULL, 0, 'Use a toothpick to secure if needed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53378D8706567A44ABD2A4AE13710293', X'360BE88FA933984DB6A43B13AE013C78', X'360BE88FA933984DB6A43B13AE013C78', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 225-240℉ using indirect heat and your favorite hardwood for smoke. We recommend maple or cherry.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'854B8D4F6B680E4F8809E6B9F875BB54', X'360BE88FA933984DB6A43B13AE013C78', X'360BE88FA933984DB6A43B13AE013C78', 1, NULL, NULL, 2, NULL, 0, 'If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BBA0996B2835D408A7E7E704A0CEBD1', X'360BE88FA933984DB6A43B13AE013C78', X'360BE88FA933984DB6A43B13AE013C78', 2, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, place the peppers on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7967A2081AD1ED49A0A20F2CD4ED2930', X'457764A6C3F3AE44B39BB4199EFDCF36', X'457764A6C3F3AE44B39BB4199EFDCF36', 0, NULL, NULL, 2, NULL, 0, 'Cook for about 3 hours or until the bacon is crisp and peppers are soft. Remove and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF3FAB731DC53A4DBB03CC8E454DE7DC', X'457764A6C3F3AE44B39BB4199EFDCF36', X'457764A6C3F3AE44B39BB4199EFDCF36', 1, NULL, NULL, 2, NULL, 0, 'Make plenty!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7992B7A509AC1E4387F6A1F3A44606EB', X'A33BE2AE7152644DA0491948FBEC70C7', X'A33BE2AE7152644DA0491948FBEC70C7', 0, NULL, NULL, 2, NULL, 0, 'Trim the fat cap from the top side of the beef ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC2DF96DAA8454C938174AB02669827', X'E15F22B1C24F3D42B158D63080A821E1', X'E15F22B1C24F3D42B158D63080A821E1', 0, NULL, NULL, 2, NULL, 0, 'Apply a generous coat of Jeff''s Texas style rub to the top side of the beef ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AC267AAE7F0AC44A05FD04C0641140D', X'E15F22B1C24F3D42B158D63080A821E1', X'E15F22B1C24F3D42B158D63080A821E1', 1, NULL, NULL, 2, NULL, 0, 'After applying the rub, I let it sit for about 20 minutes while you go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'618448441AD84F4AB2EC9DFBC5C1AADA', X'EDC1D0CEE538AB4EA2996A94E15C4DF7', X'EDC1D0CEE538AB4EA2996A94E15C4DF7', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEE429DB075A454387359715EB8A507F', X'EDC1D0CEE538AB4EA2996A94E15C4DF7', X'EDC1D0CEE538AB4EA2996A94E15C4DF7', 1, NULL, NULL, 2, NULL, 0, 'If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57AB717855154F4E9B1E441A00D61D00', X'EDC1D0CEE538AB4EA2996A94E15C4DF7', X'EDC1D0CEE538AB4EA2996A94E15C4DF7', 2, NULL, NULL, 2, NULL, 0, 'Leave them alone while they cook for the first 4 hours or until the ribs reach about 160 degrees in the thickest part between the bones.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E573FE41F61D04EB7255F29F3D0C42C', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', 0, NULL, NULL, 2, NULL, 0, 'After 4 hours of cooking or when they reach 160°F in the thickest part of the meat between the bones, wrap the meat in foil or place them in a disposable foil pan with about 8 ounces of beef broth. If you use a pan, cover the top tightly with foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C679872D856D104DA7057B0C9885E2B2', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', 1, NULL, NULL, 2, NULL, 0, 'Place the wrapped, covered ribs back into the smoker and crank up the heat to 275°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADB55C7393B02F43BE7D29CDCD5B2C90', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', 2, NULL, NULL, 2, NULL, 0, 'The goal temperature is 201-203°F which will take about 1 to 1.5 hours to reach after they are covered while cooking at 275 degrees.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'433574E956792D4FB2CD7789B2B6023A', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', X'C5B456C0FEA8A24EB1B4FCCDAF45AB54', 3, NULL, NULL, 2, NULL, 0, 'Also check them with a probe to ensure they are very tender and there is no resistance.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08DFFF1DDAEA0A4A9059F25871DB663C', X'3A5E46E9157BDD41BABBA1075E9CEF69', X'3A5E46E9157BDD41BABBA1075E9CEF69', 0, NULL, NULL, 2, NULL, 0, 'After removing them from the smoker, place the entire pan into a cooler for at least an hour. 2 to 3 hours would not be a bad thing if you get them done way ahead of dinner time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B292C6F0BB95F418B11AAA24133BA09', X'C2FE9E1D62517F4A83F75B553BD9BB44', X'C2FE9E1D62517F4A83F75B553BD9BB44', 0, NULL, NULL, 2, NULL, 0, 'Slice the ribs right between the bones and serve ''em up to your guests.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'951D5034A2328744B02C04A2B45F365B', X'FA84B30086EECB42AA98E1B7E0B45117', X'FA84B30086EECB42AA98E1B7E0B45117', 0, NULL, NULL, 2, NULL, 0, 'Place the rib section bone side up and pry up the membrane with your fingers or a sharp implement such as a butter knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'304DB13BAEDD3A4F8E422810CFADF9EC', X'FA84B30086EECB42AA98E1B7E0B45117', X'FA84B30086EECB42AA98E1B7E0B45117', 1, NULL, NULL, 2, NULL, 0, 'Grab ahold of it with a paper towel for better grip and pull firmly to remove it completely.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37506F2D04C721418D5AF207221C932F', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', 0, NULL, NULL, 2, NULL, 0, 'Apply the Texas style rub first then apply the original rub over the top of that.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F40E8B258502CC4CAFE09F4BB156C18E', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', 1, NULL, NULL, 2, NULL, 0, 'First place the ribs bone side up and apply the Texas style rub all over and don''t forget the sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69BA5C7A9826EA489ED0D7C7A94C03C9', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', 2, NULL, NULL, 2, NULL, 0, 'Next apply a coat of the original rub in the same way.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58E55946E3D13B48A8AD9E3DB931EDE5', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', 3, NULL, NULL, 2, NULL, 0, 'Now flip them over and repeat the Texas style rub and the original rub on the meaty side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F65416B0136E24E8C7DE129E9C6FAB3', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', X'5FBD88F44EFB2C4D80A66A1A4DE8132A', 4, NULL, NULL, 2, NULL, 0, 'If you are doing this in advance (highly recommended) you can now place them in the fridge to dry brine and they will be good to go in as little as 4 hours although 8-10 hours is even better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B410EEAD7F93E14FB215CD31E7E4E7A4', X'F6E3F5E866E5F241B36AA79B6F42898C', X'F6E3F5E866E5F241B36AA79B6F42898C', 0, NULL, NULL, 2, NULL, 0, 'I recommend you cook them for 1 hour at the lowest setting or the specialized smoke setting. I use the Camp Chef Woodwind and I used “lo smoke”.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'165C6BA033483A4C935363788D81110E', X'F6E3F5E866E5F241B36AA79B6F42898C', X'F6E3F5E866E5F241B36AA79B6F42898C', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready place the ribs on the grate or you can use a pan and rack like I did to keep the smoker clean and eliminate some of the cleanup.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A585D85CB965A448DB292F8ED6C9FCB', X'F6E3F5E866E5F241B36AA79B6F42898C', X'F6E3F5E866E5F241B36AA79B6F42898C', 2, NULL, NULL, 2, NULL, 0, 'After the initial 1 hour, turn them up to 225°F. (On the Woodwind, I turned them up to Hi Smoke which maintains an average of 220°F)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BA5A73C687EE348B48EFF71E8D9A78A', X'F6E3F5E866E5F241B36AA79B6F42898C', X'F6E3F5E866E5F241B36AA79B6F42898C', 3, NULL, NULL, 2, NULL, 0, 'Maintain the 220-225°F temperature in your smoker for 6.5 to 7 hours or until the ribs are tender. You can also check them with a temperature probe if you prefer– they are done when they reach about 200°F in the meat between the bones. Make sure the probe does not touch the bone.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E54CE38F36CB74399533B9375EB23BC', X'F6E3F5E866E5F241B36AA79B6F42898C', X'F6E3F5E866E5F241B36AA79B6F42898C', 4, NULL, NULL, 2, NULL, 0, 'Tip: Mist the ribs with apple juice every 30 minutes or as often as possible while they are cooking using a food grade plastic spray bottle.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8FF42E79FF2094181F4F7A5A8FE11ED', X'FC5E072014DECD46BB3A3647F594FB81', X'FC5E072014DECD46BB3A3647F594FB81', 0, NULL, NULL, 2, NULL, 0, 'Preheat your smoker to 225 using indirect heat. If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92A88B2DC4C33243A93026C9E1A75710', X'FC5E072014DECD46BB3A3647F594FB81', X'FC5E072014DECD46BB3A3647F594FB81', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready place the ribs on the grate or you can use a pan and rack like I did to keep the smoker clean and eliminate some of the cleanup.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9710C8CF8F093B449D9F40DC2C03C3E4', X'FC5E072014DECD46BB3A3647F594FB81', X'FC5E072014DECD46BB3A3647F594FB81', 2, NULL, NULL, 2, NULL, 0, 'Maintain 225 to 240°F temperature in your smoker for 6 to 7 hours or until the ribs are tender. You can also check them with a temperature probe if you prefer– they are done when they reach about 200°F in the meat between the bones. Make sure the probe does not touch the bone.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F951F05610BE646A3053ECD0A19C9BA', X'FC5E072014DECD46BB3A3647F594FB81', X'FC5E072014DECD46BB3A3647F594FB81', 3, NULL, NULL, 2, NULL, 0, 'Tip: Mist the ribs with apple juice every 30 minutes or as often as possible while they are cooking using a food grade plastic spray bottle.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED8E19B9F7B1CC479CB24D9C1F479817', X'3E6CF0170D948B4BA7C6AF17066ADC1F', X'3E6CF0170D948B4BA7C6AF17066ADC1F', 0, NULL, NULL, 2, NULL, 0, 'When the ribs are finished cooking, let them rest for about 10 minutes under tented foil then slice them between the bones and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61B2AB6608F4A4458B8EE8225E26C15E', X'0A305E623EA9E44090672B09259A6D10', X'0A305E623EA9E44090672B09259A6D10', 0, NULL, NULL, 2, NULL, 0, 'Flip the ribs to bone side up, use a knife, spoon or just your finger to start pulling up on that plastic-like membrane that covers the bones.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D6DF6A8C22EAC469A84A4C0417F56CD', X'0A305E623EA9E44090672B09259A6D10', X'0A305E623EA9E44090672B09259A6D10', 1, NULL, NULL, 2, NULL, 0, 'Tip: use a paper towel or catfish pliers for better grip.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD6F9DB3C37AD040A1EADC583B5A439B', X'4BB5E816CACEDA459B50921081245E18', X'4BB5E816CACEDA459B50921081245E18', 0, NULL, NULL, 2, NULL, 0, 'To do this make sure the ribs are bone side up so you can see the bones better and slice off the first bone to your left, right against the bone.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14AD3323BEBA9C42B5D65EF2B13E426F', X'4BB5E816CACEDA459B50921081245E18', X'4BB5E816CACEDA459B50921081245E18', 1, NULL, NULL, 2, NULL, 0, 'Now slice as close against the third bone from left as you can leaving maximum meat on the bone in the center');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79434D2B7FB4294F9CDE1881E90434F7', X'4BB5E816CACEDA459B50921081245E18', X'4BB5E816CACEDA459B50921081245E18', 2, NULL, NULL, 2, NULL, 0, 'Doing it this way, you get a bone in the center with maximum meat on both sides of the bone.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF6F21AB3A35542A7550E3A522C577D', X'4BB5E816CACEDA459B50921081245E18', X'4BB5E816CACEDA459B50921081245E18', 3, NULL, NULL, 2, NULL, 0, 'Continue these same cuts until you end up with about 6 center ribs with lots of meat on both sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25AA6EA09246AF4F84696343DD5D888F', X'4BB5E816CACEDA459B50921081245E18', X'4BB5E816CACEDA459B50921081245E18', 4, NULL, NULL, 2, NULL, 0, 'You''ll end up with about 7 bone fragments with very little meat on them. Those can be saved for a soup, pot of beans, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECDF58E832C826478B186ABBD6AC1E40', X'74EC48BD3792D44C8BA041CD5F7E5D4A', X'74EC48BD3792D44C8BA041CD5F7E5D4A', 0, NULL, NULL, 2, NULL, 0, 'Lay the extra wide ribs down in a foil pan–');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'326E6785FC4C4B4B901889EB7BCE27B4', X'74EC48BD3792D44C8BA041CD5F7E5D4A', X'74EC48BD3792D44C8BA041CD5F7E5D4A', 1, NULL, NULL, 2, NULL, 0, 'A little sauce on each one first– Get your hands dirty and rub it in to the top, sides and bottom of each one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE6CD26CB22E094F8F66ED859E2148C2', X'74EC48BD3792D44C8BA041CD5F7E5D4A', X'74EC48BD3792D44C8BA041CD5F7E5D4A', 2, NULL, NULL, 2, NULL, 0, 'Now apply ample amounts of Jeff''s original rub on all sides.. pat it in a little to make it stay before you flip them over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DFEF357A9FF264C8AAA2AA4325EBB79', X'E21710F104360B4CA8DF9F029ABBF674', X'E21710F104360B4CA8DF9F029ABBF674', 0, NULL, NULL, 2, NULL, 0, 'Place the pan of ribs in the fridge for at least 4 hours but overnight is MUCH better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4A0B8A79691C44D9DEED0C0B92CF47E', X'305564D7B7977743B0065341F478318E', X'305564D7B7977743B0065341F478318E', 0, NULL, NULL, 2, NULL, 0, 'Use whatever smoker you have or want to use whether it''s charcoal, splits, gas, electric, you get the idea. Run about 225°F no matter what smoker you use.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5718097FDCEE57409D2C42134A5FF6A8', X'305564D7B7977743B0065341F478318E', X'305564D7B7977743B0065341F478318E', 1, NULL, NULL, 2, NULL, 0, 'If your smoker uses a water pan, fill it up for good measure.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15984C5BC175644F9F215CA53DA0F7D8', X'305564D7B7977743B0065341F478318E', X'305564D7B7977743B0065341F478318E', 2, NULL, NULL, 2, NULL, 0, 'I used pecan but hickory or almost any smoking wood that you have available will work great for these ribs and give them an amazing flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8859E97D5567C49B31FF8CC1D1B0614', X'305564D7B7977743B0065341F478318E', X'305564D7B7977743B0065341F478318E', 3, NULL, NULL, 2, NULL, 0, 'Once your smoker is preheated and ready to cook, let''s get the ribs in there.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56100E429347EA468563CB20DF06B36B', X'151DB049996BDA40A340F739B841874B', X'151DB049996BDA40A340F739B841874B', 0, NULL, NULL, 2, NULL, 0, 'Place the pan of ribs into the smoker or you can place them directly onto the grates if you prefer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A22B777F0FDFB441911B2F4811493618', X'151DB049996BDA40A340F739B841874B', X'151DB049996BDA40A340F739B841874B', 1, NULL, NULL, 2, NULL, 0, 'Let them cook for about 3 hours first to form a really good crust. Check them at this point and if they look like the rub on the top is nice and dry, feel free to spritz them with some apple juice, Pepsi, whatever you have. Something with a little sugar in it is best.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B232A0DB129EC4293689AAB80E1EB79', X'5B541BC6833E504988139D0A513A3CDA', X'5B541BC6833E504988139D0A513A3CDA', 0, NULL, NULL, 2, NULL, 0, 'At hour 4, it''s time to put some sauce on them, of course you should use Jeff''s original barbecue sauce but if you have another favorite, well, whatever');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DB6C8A95C442A4C881131C6FE372DC2', X'5B541BC6833E504988139D0A513A3CDA', X'5B541BC6833E504988139D0A513A3CDA', 1, NULL, NULL, 2, NULL, 0, 'Coat them good and spread it out over the top and sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EA5DD85E983474891F7577752ABD33E', X'5B541BC6833E504988139D0A513A3CDA', X'5B541BC6833E504988139D0A513A3CDA', 2, NULL, NULL, 2, NULL, 0, 'Now crank up the heat. I have a Camp Chef Woodwind so I pushed it on up to 425°F to get a good finish on them. If you also have a smoker that can cook hot like that, then I recommend you do the same otherwise as hot as you can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7890A960E277B6488CF9B8AB4220958F', X'5B541BC6833E504988139D0A513A3CDA', X'5B541BC6833E504988139D0A513A3CDA', 3, NULL, NULL, 2, NULL, 0, 'Tip: Another option is to bring them in at this point and put them into the oven preheated to 425°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA951F2E2E994E4E8565F3D5BDBCD05C', X'5B541BC6833E504988139D0A513A3CDA', X'5B541BC6833E504988139D0A513A3CDA', 4, NULL, NULL, 2, NULL, 0, 'At 425°F, it took mine an additional 40 minutes to be get the color I wanted on the ribs and to get some char on the sauce.. they turned out beautiful if you ask me!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65C83EC26D7E6641AF71F6157A9F4F7D', X'23B04BC023032843A3085A6C67F35FF9', X'23B04BC023032843A3085A6C67F35FF9', 0, NULL, NULL, 2, NULL, 0, 'Serve them just as soon as they are finished and watch the smiling faces and enjoy the quietness as everyone stops talking to eat.. that''s how you know the food is amazing');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDF7A08A1DAAD14593856AA5E2CB1AC8', X'0C12B7218EEC314BB7C35B8D1000F73F', X'0C12B7218EEC314BB7C35B8D1000F73F', 0, NULL, NULL, 2, NULL, 0, 'Use ½ teaspoon per pound of meat when dry brining meat such as this chuck roast. This chuck was 3 lbs so I can use about 1.5 teaspoons of salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E5005EB05504F42903B90B3B48F6B4A', X'0C12B7218EEC314BB7C35B8D1000F73F', X'0C12B7218EEC314BB7C35B8D1000F73F', 1, NULL, NULL, 2, NULL, 0, 'I made the decision to use 1 teaspoon of coarse kosher salt and some of my Texas style rub which has a small amount of salt as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E482A3DB99747E4E88A647C89D7E8BD3', X'0C12B7218EEC314BB7C35B8D1000F73F', X'0C12B7218EEC314BB7C35B8D1000F73F', 2, NULL, NULL, 2, NULL, 0, 'First the salt..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB5BEB028661D4195C82124D6DAAB80', X'0C12B7218EEC314BB7C35B8D1000F73F', X'0C12B7218EEC314BB7C35B8D1000F73F', 3, NULL, NULL, 2, NULL, 0, 'Then the Texas style rub..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCD06E4E20029841BC8641314D01B1C3', X'0C12B7218EEC314BB7C35B8D1000F73F', X'0C12B7218EEC314BB7C35B8D1000F73F', 4, NULL, NULL, 2, NULL, 0, 'Now place that pan with the chuck roast in the fridge overnight or for at least 6 hours to let the salt and seasoning to do its magic!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4477EC51B091604C9E06FFECB64C63A1', X'AE8AA583BC9464449C5107E096E1207C', X'AE8AA583BC9464449C5107E096E1207C', 0, NULL, NULL, 2, NULL, 0, 'Just before the brine time is over, set up your smoker for cooking indirect at about 225-240°F and if your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12AEF693E15BD3469404B7E19F4CD971', X'AE8AA583BC9464449C5107E096E1207C', X'AE8AA583BC9464449C5107E096E1207C', 1, NULL, NULL, 2, NULL, 0, 'You will notice that I used my new 20×36 by Lone Star Grillz.. not only was it super easy to maintain my temperature but the quality and craftsmanship on this thing is phenomenal!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF467D38BD16AB44B68B6358E173DEEC', X'AE8AA583BC9464449C5107E096E1207C', X'AE8AA583BC9464449C5107E096E1207C', 2, NULL, NULL, 2, NULL, 0, 'If you''re in the market for a patio sized offset smoker.. this one is perfectly sized and of course they have other sizes as well. You can check them out at lonestargrillz.com');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3E60BC290E19149A25CAD8F09740CBF', X'AE8AA583BC9464449C5107E096E1207C', X'AE8AA583BC9464449C5107E096E1207C', 3, NULL, NULL, 2, NULL, 0, 'You''ll hear me talking more about this smoker in future newsletters and videos.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE3D936AE1D9444F8C1D654BC6B19461', X'27C4F9E18F95AA478BB75F56BFF218B0', X'27C4F9E18F95AA478BB75F56BFF218B0', 0, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready and the dry brining is done, place the meat in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'638A0654013623419A7BBE9C65683356', X'27C4F9E18F95AA478BB75F56BFF218B0', X'27C4F9E18F95AA478BB75F56BFF218B0', 1, NULL, NULL, 2, NULL, 0, 'You will notice that I like to use a pan with a rack to cut down on smoker cleaning while still allowing the smoke to get to every side of the meat. The one I use was purchased on Amazon but anything similar will work just fine. Mine is a full size stainless steel pan with fitted stainless steel rack and it came with (2) sets of these.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F7C40796B5AF6458AFF43471D102615', X'27C4F9E18F95AA478BB75F56BFF218B0', X'27C4F9E18F95AA478BB75F56BFF218B0', 2, NULL, NULL, 2, NULL, 0, 'Maintain 225 to 240°F for the entire cook time. I used a mix of cherry and hickory but use what you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE4C8B73318AD14A8189F2FCA7611C99', X'7E93D870D4DF6346AD39BFF318426F13', X'7E93D870D4DF6346AD39BFF318426F13', 0, NULL, NULL, 2, NULL, 0, 'Let the chuck roast smoke along until it reaches about 145-150°F and then move it to a foil pan filled with a can of beef broth and a half can of cream of mushroom soup.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E244A458FDA3F74091DD8625523FDA01', X'7E93D870D4DF6346AD39BFF318426F13', X'7E93D870D4DF6346AD39BFF318426F13', 1, NULL, NULL, 2, NULL, 0, 'I mixed the ingredients into a small bowl before adding it to the pan..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEA3398A417B3C44917C4F411163C014', X'7E93D870D4DF6346AD39BFF318426F13', X'7E93D870D4DF6346AD39BFF318426F13', 2, NULL, NULL, 2, NULL, 0, 'Place the meat down into the foil pan with the liquid and let it continue cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3A92F466E335049BC94043D9C35527F', X'811389F3667C144E934AF212B49E22F6', X'811389F3667C144E934AF212B49E22F6', 0, NULL, NULL, 2, NULL, 0, 'When the meat reaches 175°F, you''ll want to wrap it up or just place a piece of foil tightly over the foil pan. This will allow the liquid to create steam and braise the meat to make it more tender.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74E6D45B0ED01348B0E96E0A15A10324', X'811389F3667C144E934AF212B49E22F6', X'811389F3667C144E934AF212B49E22F6', 1, NULL, NULL, 2, NULL, 0, 'Let it stay wrapped and cooking until it is fall apart tender. Mine ended up going all the way to 208°F and I figure that you''ll probably have a similar experience. Fortunately, these have plenty of fat to keep them moist even when you cook them to these extremes to tenderize them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A94921C421D6242B60AAAE47FC04BD4', X'BC5DDA3E652C474FB56E41F91772435A', X'BC5DDA3E652C474FB56E41F91772435A', 0, NULL, NULL, 2, NULL, 0, 'When the meat is tender, best determined by pushing a probe or other sharp object into the meat and feeling for no resistance, remove the pan from the smoker and let it sit, still covered, and rest for up to an hour or two but at least 10-15 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28284FC9D449B148A848992CA883850B', X'BC5DDA3E652C474FB56E41F91772435A', X'BC5DDA3E652C474FB56E41F91772435A', 1, NULL, NULL, 2, NULL, 0, 'If you have some help, let them pull the meat into pieces while you grill up the onions and toast the bread. Otherwise, you can go ahead and pull the chuck roast while the onions and bread are on the griddle.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA9E9BE0AC82C64EBA2A5C7DA9C4D66D', X'0D350634553A054EB30423CF8E9C8E2B', X'0D350634553A054EB30423CF8E9C8E2B', 0, NULL, NULL, 2, NULL, 0, 'If you have a griddle such as Camp Chef or Blackstone, that will work great. This new smoker came with a griddle over the top of the firebox and boy it worked great! It was right around 365 °F when I started and that worked perfectly for what I was doing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92DDE43C2BC6BB498CFC91C69D663733', X'0D350634553A054EB30423CF8E9C8E2B', X'0D350634553A054EB30423CF8E9C8E2B', 1, NULL, NULL, 2, NULL, 0, 'If you want to grill some onions, use half slices or even the whole rings if you want to. Pour the onions on the griddle with about a tablespoon of olive oil and about a tablespoon of my Texas style rub on top. Mix it all in and just stir it around here and there until they get really soft and wimpy. Delicious!!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'426E79333AF84F4FAD5133779A497A55', X'0D350634553A054EB30423CF8E9C8E2B', X'0D350634553A054EB30423CF8E9C8E2B', 2, NULL, NULL, 2, NULL, 0, 'To grill the bread, just butter both sides and grill both sides until it gets a nice crisp surface.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB3E56180D36C74CB780EC50275CC182', X'0D350634553A054EB30423CF8E9C8E2B', X'0D350634553A054EB30423CF8E9C8E2B', 3, NULL, NULL, 2, NULL, 0, 'I went ahead and mixed the meat with some of the onions on the griddle to warm it all up for the sandwich.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37453DAEC5024D43852ABBD355E7C28A', X'C98DCA4D2F1EBF4CAB8847BB5ACE1F3D', X'C98DCA4D2F1EBF4CAB8847BB5ACE1F3D', 0, NULL, NULL, 2, NULL, 0, 'Pile the meat and grills onions on the toast and make yourself a sandwich that will make you smile!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BEE3FF02185A1488595EE24B5A07D75', X'ED69F537C2BA53408238BDBD066D2D5F', X'ED69F537C2BA53408238BDBD066D2D5F', 0, NULL, NULL, 2, NULL, 0, 'Rinse the brisket well under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'731A332F1E42C8439512879C252F4A96', X'A51EE18E13EB1B4E8FCAF373E24468F5', X'A51EE18E13EB1B4E8FCAF373E24468F5', 0, NULL, NULL, 2, NULL, 0, 'Cut a small, thin slice of the brisket and fry it in a pan until tender. Taste it to determine if it is too salty.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F2C36FD7F08474BA982144F64EC753F', X'A51EE18E13EB1B4E8FCAF373E24468F5', X'A51EE18E13EB1B4E8FCAF373E24468F5', 1, NULL, NULL, 2, NULL, 0, 'If too salty, soak in a pan of cold water in the fridge for 3-4 hours to remove some of the salt. Repeat this process of frying, tasting, soaking until it is perfect. Note: This recipe is a relatively mild concentration and usually does not require any soaking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6F2FFB05244F342B66CA2F7EC85E169', X'99714FABA75B6448B26796AB493BD113', X'99714FABA75B6448B26796AB493BD113', 0, NULL, NULL, 2, NULL, 0, 'Pour enough coriander seeds into a mortar and pestle to obtain 1 TBS of crushed coriander. Mix 1 TBS of crushed coriander with 3 TBS of Jeff''s Texas style rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF212FBC7A1871499A829E696016EE04', X'99714FABA75B6448B26796AB493BD113', X'99714FABA75B6448B26796AB493BD113', 1, NULL, NULL, 2, NULL, 0, 'Apply a light to medium coat of the rub to the top side of the brisket and on the sides as much as possible. Press down all over with a fork to make it stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BE0A6CE7B5B064DBB1E448E7AD6BF9A', X'31D60315B2D3904C9F9EF4F86959E328', X'31D60315B2D3904C9F9EF4F86959E328', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 225 degrees using indirect heat. Once it''s ready, place the brisket in the smoker and let it cook until it reaches 165 degrees F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF90AE4BF66E5A48A104CC65F334E281', X'31D60315B2D3904C9F9EF4F86959E328', X'31D60315B2D3904C9F9EF4F86959E328', 1, NULL, NULL, 2, NULL, 0, 'Place brisket in pan and cover with foil or wrap the brisket in unlined, unwaxed butcher paper or foil and continue cooking until the brisket reaches about 200 degrees.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C86F6EE943EE9943A7938E89241632E0', X'31D60315B2D3904C9F9EF4F86959E328', X'31D60315B2D3904C9F9EF4F86959E328', 2, NULL, NULL, 2, NULL, 0, 'The brisket is finished cooking when a probe inserted in the thickest part gives no resistance.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69C7D4E896C2BB44B8D429D21CA55DD5', X'8A4DE5092BB4194ABE51D45D54854E87', X'8A4DE5092BB4194ABE51D45D54854E87', 0, NULL, NULL, 2, NULL, 0, 'Rest the brisket covered for at least 1 hour then slice across the grain and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63F60DB4FD331346BD07FECE4A8A1E15', X'6BDF630E0681B540AFC116AAF56B0C32', X'6BDF630E0681B540AFC116AAF56B0C32', 0, NULL, NULL, 2, NULL, 0, 'Trim all of the fat from the larger point end and to at least 1/4 inch or less on the flat end. If the brisket has plenty of fat marbling, you can remove all of the fat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9511220960E7FC4286F692C8820E5E4C', X'F44582BBBFFAA3439848BF44623B4963', X'F44582BBBFFAA3439848BF44623B4963', 0, NULL, NULL, 2, NULL, 0, 'Add the curing salt and the coarse kosher salt to the water and stir until it becomes clear (about 2 minutes)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25E03121A754424684571011850AA77F', X'F44582BBBFFAA3439848BF44623B4963', X'F44582BBBFFAA3439848BF44623B4963', 1, NULL, NULL, 2, NULL, 0, 'Add the brown sugar and stir until dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80A7D5A099684742B99DB50AF34D534A', X'38B403F88B8A974A97FDD9582CF2A080', X'38B403F88B8A974A97FDD9582CF2A080', 0, NULL, NULL, 2, NULL, 0, 'Heat an iron skillet over high heat and place pickling spices in the pan to toast. Keep the spices moving for about a minute or until you smell their aroma.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'835171C89CE0A9478851F569905769CD', X'38B403F88B8A974A97FDD9582CF2A080', X'38B403F88B8A974A97FDD9582CF2A080', 1, NULL, NULL, 2, NULL, 0, 'Transfer toasted pickling spices to the curing brine and stir.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E939E1AECCB98448AA1043651C4FCBBA', X'134A4E8606E94E47AC75F755A25C6A57', X'134A4E8606E94E47AC75F755A25C6A57', 0, NULL, NULL, 2, NULL, 0, 'Place the meat into a large food-safe plastic, glass or non-reactive container. Pour brine over the meat to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6C828EA68E798438D957FCB63320BE8', X'134A4E8606E94E47AC75F755A25C6A57', X'134A4E8606E94E47AC75F755A25C6A57', 1, NULL, NULL, 2, NULL, 0, 'If necessary, weigh the meat down with a plate and/or a zip top bag filled with water to keep the meat submerged.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F48DDA1A1A4B7046B98F6549C8FC95A0', X'134A4E8606E94E47AC75F755A25C6A57', X'134A4E8606E94E47AC75F755A25C6A57', 2, NULL, NULL, 2, NULL, 0, 'Cover the container and place in the fridge.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020A4A18CA4D1045AAC4F089C7FADB1A', X'2FE181CA53733440AC0D9DE147C05059', X'2FE181CA53733440AC0D9DE147C05059', 0, NULL, NULL, 2, NULL, 0, 'Keep the meat in the fridge for 6-10 days flipping the brisket every day for more even brining/curing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'655C2E4ABEA35043A5FE6FE059A0815D', X'FB404976FBBD2D48B06CB67B1B8418E6', X'FB404976FBBD2D48B06CB67B1B8418E6', 0, NULL, NULL, 2, NULL, 0, 'Measure out each of the snacks into a large foil pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE0CD74332160441BF67F7D61AE93489', X'FF364B4A0E3A0241908DB1D46D3137F1', X'FF364B4A0E3A0241908DB1D46D3137F1', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter then add the hot sauce and 2 TBS of Jeff''s original rub and mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95AA438AE87C054BBCC89A696D133C64', X'59910B8B84C77347B3F1F9A552C76A1C', X'59910B8B84C77347B3F1F9A552C76A1C', 0, NULL, NULL, 2, NULL, 0, 'Drizzle the butter mixture over the snack mix and stir well to coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'921563D63FA6CC47B8D05D5AC900F249', X'DD380A09E4C8CE438A349E93DE5FD675', X'DD380A09E4C8CE438A349E93DE5FD675', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker to maintain indirect heat of 225°F. If you are using a pellet smoker, I recommend going with 180°F or the low smoke setting for better smoke flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D43027167FC38409ECFE4FE957DE60D', X'DD380A09E4C8CE438A349E93DE5FD675', X'DD380A09E4C8CE438A349E93DE5FD675', 1, NULL, NULL, 2, NULL, 0, 'Once the smoke is going, place the pan(s) of snacks into the smoker and close the lid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A637E3CD1353840817C949205FAB36D', X'DD380A09E4C8CE438A349E93DE5FD675', X'DD380A09E4C8CE438A349E93DE5FD675', 2, NULL, NULL, 2, NULL, 0, 'Stir the mix occasionally for more uniform smoke flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FCC887FC9439E4B87F44254F368F862', X'DD380A09E4C8CE438A349E93DE5FD675', X'DD380A09E4C8CE438A349E93DE5FD675', 3, NULL, NULL, 2, NULL, 0, 'Smoke the snack mix for about an hour or until the snacks are as crisp and smoky as you like. Note: In a pellet smoker, I recommend 180°F for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5317B4A5484E0D4181F375CC929CAAD2', X'DD380A09E4C8CE438A349E93DE5FD675', X'DD380A09E4C8CE438A349E93DE5FD675', 4, NULL, NULL, 2, NULL, 0, 'Remove the pans from the smoker and bring them into the house to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'247053BC22981347869DE73C436ED4EE', X'164D5BBAA2389C4585448A8792D275B3', X'164D5BBAA2389C4585448A8792D275B3', 0, NULL, NULL, 2, NULL, 0, 'Serve the snack mix right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A96AE3D0A5AFCA41835D79B77DCAAEDE', X'164D5BBAA2389C4585448A8792D275B3', X'164D5BBAA2389C4585448A8792D275B3', 1, NULL, NULL, 2, NULL, 0, 'If you are not serving them right away: Allow them to cool completely on the counter then store them in zipper bags.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0F28E867CB0D04992A51E53AEF4A123', X'7CE46D27B2F0BA489C6247326FD9D603', X'7CE46D27B2F0BA489C6247326FD9D603', 0, NULL, NULL, 2, NULL, 0, 'Clean and deseed the yellow bell pepper and the jalapenos then dice them up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86B2E1CAF198FC49AC0269B411078C58', X'7CE46D27B2F0BA489C6247326FD9D603', X'7CE46D27B2F0BA489C6247326FD9D603', 1, NULL, NULL, 2, NULL, 0, 'Dice up a small yellow onion.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BE943ED493C3C49A5388DA75FECE57D', X'FFD101E212A40449998767A6B8147FE0', X'FFD101E212A40449998767A6B8147FE0', 0, NULL, NULL, 2, NULL, 0, 'Pour 2 TBS of vegetable or olive oil into a large chili pot over medium heat then add all of the diced vegetables. Stir occasionally until the peppers and onion soften and begin to char.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6123D419929BD4586DA98CB6E210C80', X'E12CCB1D2567024BB6426CDA162A51C5', X'E12CCB1D2567024BB6426CDA162A51C5', 0, NULL, NULL, 2, NULL, 0, 'Cube 2 lbs of brisket burnt ends into 1/2 inch pieces then add to the pot once the vegetables are sauteed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F590EAF0DCC244DA085378902CFE5CD', X'E12CCB1D2567024BB6426CDA162A51C5', X'E12CCB1D2567024BB6426CDA162A51C5', 1, NULL, NULL, 2, NULL, 0, 'Let the vegetables and meat cook together for about 10 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56C9FE51D5C5B34D986B6996D5C69A6A', X'533671F59FA5EF428B844B9A3CDF35A1', X'533671F59FA5EF428B844B9A3CDF35A1', 0, NULL, NULL, 2, NULL, 0, 'Add Ro-tel, tomato paste, water and spices and stir well to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADAEF9B1248234438A6B254B29B22452', X'4318E251DA6B8548BA42FE0924B6C0A1', X'4318E251DA6B8548BA42FE0924B6C0A1', 0, NULL, NULL, 2, NULL, 0, 'While still on medium heat, cover the pot and bring the chili to a boil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B652C5280BF5C243ACE910B6C3EE57CC', X'4318E251DA6B8548BA42FE0924B6C0A1', X'4318E251DA6B8548BA42FE0924B6C0A1', 1, NULL, NULL, 2, NULL, 0, 'Once it boils, turn the heat down to low and remove the lid. Let the chili continue to cook on low for about 2 hours or until it obtains the thickness you are looking for.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D157908B7B3F4940850A99EBB33BB981', X'18A73834163269468AAA5CF4332F0E23', X'18A73834163269468AAA5CF4332F0E23', 0, NULL, NULL, 2, NULL, 0, 'Pour 1 gallon of cold water into large pitcher. Add salt and sugar and stir until dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD6B881E876A2C4883E0A2BA25876EF5', X'18A73834163269468AAA5CF4332F0E23', X'18A73834163269468AAA5CF4332F0E23', 1, NULL, NULL, 2, NULL, 0, 'Place chicken into non-reactive container and pour brine over chicken to cover. Refrigerate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EBF31BB3ADA9F4DBBA4BE7B39B60899', X'18A73834163269468AAA5CF4332F0E23', X'18A73834163269468AAA5CF4332F0E23', 2, NULL, NULL, 2, NULL, 0, 'After 4 hours, remove the chicken, rinse and pat dry. Discard brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CCD04476F474341883FD56407AA5E3E', X'911220A28080BD48A2D661FE794570D4', X'911220A28080BD48A2D661FE794570D4', 0, NULL, NULL, 2, NULL, 0, 'Cut along both sides of backbone to remove. Press chicken open and place skin side down on cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CE098D24D3475438A1096D1383DAEA0', X'911220A28080BD48A2D661FE794570D4', X'911220A28080BD48A2D661FE794570D4', 1, NULL, NULL, 2, NULL, 0, 'Mix together 1 cup of mayonnaise and 1/4 cup of Jeff''s original rub. Place 1/2 of mixture on meat side of chicken. Spread to coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7E148F8A8C56242A0B92B264BC43136', X'911220A28080BD48A2D661FE794570D4', X'911220A28080BD48A2D661FE794570D4', 2, NULL, NULL, 2, NULL, 0, 'Flip chicken to skin side up and apply other half of mayo/rub mixture to skin side spreading it all over the breast, wings and legs to coat. Push some of it under the skin wherever you can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'598CBF098817BE44956934EF2B213BD3', X'AA1987AA4B39774285EAAB046DBDCEBE', X'AA1987AA4B39774285EAAB046DBDCEBE', 0, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking indirect at 275 degrees using pecan wood for smoke. If your smoker uses a water pan, leave it dry.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E558E3287F0B142A87258F98A1D55D1', X'AA1987AA4B39774285EAAB046DBDCEBE', X'AA1987AA4B39774285EAAB046DBDCEBE', 1, NULL, NULL, 2, NULL, 0, 'Place chicken meat side down- cook chicken until internal temperature reaches 165 degrees F in the thickest part of the breast and thigh.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88DA1F5606EEE84ABA3A2A5F46E52FC9', X'AA1987AA4B39774285EAAB046DBDCEBE', X'AA1987AA4B39774285EAAB046DBDCEBE', 2, NULL, NULL, 2, NULL, 0, 'While the chicken is cooking, mix together 1 stick of melted butter and 2 TBS of Jeff''s original rub. Brush this all over the skin side of the chicken 2 to 3 times during the cooking process or anytime it starts to look dry.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD80E8F71902EB47886DA7E75CCD5F6E', X'317CD1F4B8B97449AFFA0C860E3EA29F', X'317CD1F4B8B97449AFFA0C860E3EA29F', 0, NULL, NULL, 2, NULL, 0, 'Once the chicken is finished cooking, remove it to the counter and let it rest for 15 minutes with foil tented over the top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFFF71F2D39C124C851DBF3939C6CDFD', X'317CD1F4B8B97449AFFA0C860E3EA29F', X'317CD1F4B8B97449AFFA0C860E3EA29F', 1, NULL, NULL, 2, NULL, 0, 'Carve the chicken and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED91A1DB976E9B4A9A37571D898C27E0', X'5CEFC8DB8B0D3342879002A54C52A957', X'5CEFC8DB8B0D3342879002A54C52A957', 0, NULL, NULL, 2, NULL, 0, 'Open the bag of Cheetos® Puffs and pour them into a large sheet pan. Something like a Weber grill pan would also work well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFB97177C3B3DC4180BB6FE9342EF94F', X'5CEFC8DB8B0D3342879002A54C52A957', X'5CEFC8DB8B0D3342879002A54C52A957', 1, NULL, NULL, 2, NULL, 0, 'Pour the little puffs of goodness into the pan(s) making sure the smoke can get to all of them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C5FD3CB0264374FB19E837D0AD00DBA', X'3E9B39C109F5FC4F802DC08DA57E8014', X'3E9B39C109F5FC4F802DC08DA57E8014', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker to maintain indirect heat of around 180°F or lower if possible. You do NOT need heat, only smoke, however, a little heat will not hurt them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C6727FEA0577C499FB1E2F58EB2FBC6', X'3E9B39C109F5FC4F802DC08DA57E8014', X'3E9B39C109F5FC4F802DC08DA57E8014', 1, NULL, NULL, 2, NULL, 0, 'Once the smoke is going, place the pan(s) of Puffs into the smoker and close the lid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DB98B8F0DBE724687EB1F5C1DCCD8E9', X'3E9B39C109F5FC4F802DC08DA57E8014', X'3E9B39C109F5FC4F802DC08DA57E8014', 2, NULL, NULL, 2, NULL, 0, 'Leave them alone for 30 minutes and try to not raise the lid until they are finished for maximum smoke flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26235F1C66B2C142BDCB88A5E8F3A9CD', X'3E9B39C109F5FC4F802DC08DA57E8014', X'3E9B39C109F5FC4F802DC08DA57E8014', 3, NULL, NULL, 2, NULL, 0, 'When the time has expired, remove them from the smoker and bring them into the house to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'167BDA9B7C6C26478DC4027AFB97B52E', X'F10CBEAC828B144AAD4A002416A73850', X'F10CBEAC828B144AAD4A002416A73850', 0, NULL, NULL, 2, NULL, 0, 'These can be devoured in any way that you see fit.. enjoy the smoke flavor!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FE815E83E9E70498F66E03E8DCCD80F', X'F10CBEAC828B144AAD4A002416A73850', X'F10CBEAC828B144AAD4A002416A73850', 1, NULL, NULL, 2, NULL, 0, 'Once they are cooled down, you can store them in zipper bags.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B708C2F23DFD534798CC596A2D138FBA', X'30518890BBB24F489908FCFD0829947F', X'30518890BBB24F489908FCFD0829947F', 0, NULL, NULL, 2, NULL, 0, 'Open the bag of potato chips and pour them into one or more Weber grill pans or a large sheet pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5738DED20DFEC646948A01F3CFD116EC', X'30518890BBB24F489908FCFD0829947F', X'30518890BBB24F489908FCFD0829947F', 1, NULL, NULL, 2, NULL, 0, 'If you want to put some of my Jeff''s original rub on them, spray them with the cooking oil so the rub will stick better then apply the rub to them generously on one side only.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BC5BD1B631FE64E8AD9E56A2C2F7881', X'30518890BBB24F489908FCFD0829947F', X'30518890BBB24F489908FCFD0829947F', 2, NULL, NULL, 2, NULL, 0, 'The potato chips are now ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D648B8FBC825CF438B54B4617C3CFFEE', X'9E484C8E4CD70A44B57475FACF26AFF6', X'9E484C8E4CD70A44B57475FACF26AFF6', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker to maintain indirect heat of around 180°F or lower if possible. You do NOT need heat, only smoke, however, a little heat will not hurt them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D50B902CA3266042B5EC0CFFF23F4571', X'9E484C8E4CD70A44B57475FACF26AFF6', X'9E484C8E4CD70A44B57475FACF26AFF6', 1, NULL, NULL, 2, NULL, 0, 'Once the smoke is going, place the pan(s) of chips into the smoker and close the lid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A63008C11344C04DB08C4EB77FC63650', X'9E484C8E4CD70A44B57475FACF26AFF6', X'9E484C8E4CD70A44B57475FACF26AFF6', 2, NULL, NULL, 2, NULL, 0, 'Leave them alone for 45 minutes and try to not raise the lid until they are finished for maximum smoke flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0D8BF78080E4C4AAF94808277B298A4', X'9E484C8E4CD70A44B57475FACF26AFF6', X'9E484C8E4CD70A44B57475FACF26AFF6', 3, NULL, NULL, 2, NULL, 0, 'When the time has expired, remove them from the smoker and bring them into the house to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'791F9EDE0918E54E812E24217B44ACCC', X'6BE8553A1979414EA84A68375E000789', X'6BE8553A1979414EA84A68375E000789', 0, NULL, NULL, 2, NULL, 0, 'The smoked potato chips are delicious warm so you can eat them right away. Once they are cooled down, place them into zipper bags for storage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23812CBF1FE5ED42887979A6C1879111', X'F1943179D6B8FA4E86AA88A17B618567', X'F1943179D6B8FA4E86AA88A17B618567', 0, NULL, NULL, 2, NULL, 0, 'To get started, I laid down a bed of rosemary sprigs in my pan. Then the steaks are laid right on top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E72D614DAE85B4485ABECF949232F6A', X'F1943179D6B8FA4E86AA88A17B618567', X'F1943179D6B8FA4E86AA88A17B618567', 1, NULL, NULL, 2, NULL, 0, 'Coat each of the steaks with about 1/4 tsp of coarse kosher salt');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A9D079D8BDBD24B96CAAA51A2966714', X'F1943179D6B8FA4E86AA88A17B618567', X'F1943179D6B8FA4E86AA88A17B618567', 2, NULL, NULL, 2, NULL, 0, 'Place the entire pan of steaks in the fridge for about 2 to 3 hours. When the steaks are done brining, there is no need to rinse.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D8041C95356FB44A267510C8B0D042C', X'4A187E82A799D94A97D4E191A0A7B314', X'4A187E82A799D94A97D4E191A0A7B314', 0, NULL, NULL, 2, NULL, 0, 'After brining, give them a good application of the Texas style rub. They are ready to be smoked.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'636EB83583B6764EA633675959E533A6', X'2FE12618634388409FE77D3044CF949B', X'2FE12618634388409FE77D3044CF949B', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for indirect heat at about 225°F. If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA5E600FB89A8647A6F0DA4F856F7DB2', X'2FE12618634388409FE77D3044CF949B', X'2FE12618634388409FE77D3044CF949B', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the pan of steaks right on the grate and let the smoking commence.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E7DFD32D9B48545AA694D590FF0FB09', X'A4953DCE5A45954BAF004C23A099EFC2', X'A4953DCE5A45954BAF004C23A099EFC2', 0, NULL, NULL, 2, NULL, 0, 'Once the steaks reach about 120°F, increase the heat in your smoker to about 450°F or you can move the pan to your oven broiler to give the steaks some sear on the outside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200609F4FB2ED34CAA6A1C1F91D0C9E0', X'A4953DCE5A45954BAF004C23A099EFC2', X'A4953DCE5A45954BAF004C23A099EFC2', 1, NULL, NULL, 2, NULL, 0, 'When the reach 130°F or a good medium rare, they can be removed from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDC6EA5F6EF6F64999D3E95D2DAD6163', X'BCE4244EC0C8B445BB92B39DE4FCAF20', X'BCE4244EC0C8B445BB92B39DE4FCAF20', 0, NULL, NULL, 2, NULL, 0, 'Rest the steaks for 15 minutes with foil tented over the top of the pan before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CE0A34FB41AAD45B0176D96DD461A7D', X'BCE4244EC0C8B445BB92B39DE4FCAF20', X'BCE4244EC0C8B445BB92B39DE4FCAF20', 1, NULL, NULL, 2, NULL, 0, 'Just before serving, brush some melted butter with original rub over the top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1CF765E756B0744B0BB1E30F101B792', X'BCE4244EC0C8B445BB92B39DE4FCAF20', X'BCE4244EC0C8B445BB92B39DE4FCAF20', 2, NULL, NULL, 2, NULL, 0, 'Rub Butter is made using 1/2 stick of melted butter with 1 tablespoon of Jeff''s original rub. Stir while using to keep it mixed up well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'829B8EF629ED0D4B924D0BB294DDF355', X'BCE4244EC0C8B445BB92B39DE4FCAF20', X'BCE4244EC0C8B445BB92B39DE4FCAF20', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy one of the best steaks you''ve ever tasted!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D52ACF3569E0DB499B990360C1014F47', X'1F27BFEE420E844C86A29C388BAD7310', X'1F27BFEE420E844C86A29C388BAD7310', 0, NULL, NULL, 2, NULL, 0, 'First let''s cut off that annoying long piece of extra skin!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD809D284E828641A2385DD4C1A61BB5', X'1F27BFEE420E844C86A29C388BAD7310', X'1F27BFEE420E844C86A29C388BAD7310', 1, NULL, NULL, 2, NULL, 0, 'Cut along both side of the backbone to remove it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17F8A3DBF42A214A892707C2DBB26AEC', X'1F27BFEE420E844C86A29C388BAD7310', X'1F27BFEE420E844C86A29C388BAD7310', 2, NULL, NULL, 2, NULL, 0, 'Flip the bird over and press down to flatten it out.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66EA74BCB4837C4EA11C17E2B526227E', X'A870F6AD13FB9545B14790BFF4A603C6', X'A870F6AD13FB9545B14790BFF4A603C6', 0, NULL, NULL, 2, NULL, 0, 'With meat side facing up, sprinkle about 1.5 to 2 teaspoons of coarse kosher salt evenly all over. Leave it in this position for about 5 or 10 minutes or until you see the salt starting to dissolve');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8300FB2009BDB249A936354FDAD00BC2', X'A870F6AD13FB9545B14790BFF4A603C6', X'A870F6AD13FB9545B14790BFF4A603C6', 1, NULL, NULL, 2, NULL, 0, 'Now flip the bird over the skin side up and place it on a rack inside of a cookie sheet or pan. I used a Bradley rack as usual but a cooling rack or even a Weber grill pan would work great.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B846B5CE1B17DA43B3B256A5A24B3E26', X'A870F6AD13FB9545B14790BFF4A603C6', X'A870F6AD13FB9545B14790BFF4A603C6', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle another 1.5 to 2 teaspoons of coarse kosher salt evenly all over the wings, legs and breast..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36E36D33E35881409ED00244F574814C', X'A870F6AD13FB9545B14790BFF4A603C6', X'A870F6AD13FB9545B14790BFF4A603C6', 3, NULL, NULL, 2, NULL, 0, 'Now place the whole pan with the duck into the fridge, uncovered overnight or for 8 to 12 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5376EAD1B032A4C9EF8FDB2754CF362', X'A870F6AD13FB9545B14790BFF4A603C6', X'A870F6AD13FB9545B14790BFF4A603C6', 4, NULL, NULL, 2, NULL, 0, 'There is no need to rinse the duck after brining.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7814E835ABEFB4581B11DAC1AEAB309', X'36EB0C4A8516964583E7BC0E020688F7', X'36EB0C4A8516964583E7BC0E020688F7', 0, NULL, NULL, 2, NULL, 0, 'Apply my original rub all over the duck, both sides. Then apply a light sprinkling of the Texas rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C2BBA476AAB0E48983D8A466E24C63B', X'B6640AB0CF08FE42A0227EE763D7F31D', X'B6640AB0CF08FE42A0227EE763D7F31D', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking with indirect heat at about 225°F and if your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69D8B73FECA6F64780F5A7D475D764CE', X'B6640AB0CF08FE42A0227EE763D7F31D', X'B6640AB0CF08FE42A0227EE763D7F31D', 1, NULL, NULL, 2, NULL, 0, 'Use pecan and cherry wood (or a mix) for smoke');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'165429800F02F2449F7A351C7948FF81', X'B6640AB0CF08FE42A0227EE763D7F31D', X'B6640AB0CF08FE42A0227EE763D7F31D', 2, NULL, NULL, 2, NULL, 0, 'Once your smoker is ready, place the whole pan into the smoker, with the duck skin side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F99AF0087DD12458D866F5D9455FB6A', X'B6640AB0CF08FE42A0227EE763D7F31D', X'B6640AB0CF08FE42A0227EE763D7F31D', 3, NULL, NULL, 2, NULL, 0, 'Let the duck cook at 225°F for 2 hours for some good smoke flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED9422E2B6063D46BE10AABF41746654', X'B6640AB0CF08FE42A0227EE763D7F31D', X'B6640AB0CF08FE42A0227EE763D7F31D', 4, NULL, NULL, 2, NULL, 0, 'Several times during the cook brush the duck with a mixture of 1 stick of butter and 2 TBS of Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E77429EA5FF17E4FA5C0E1BD401825B4', X'97222FD011AF4140A646FD9A3FFF5B6D', X'97222FD011AF4140A646FD9A3FFF5B6D', 0, NULL, NULL, 2, NULL, 0, 'Using kitchen shears, make a few snips in the skin over the breast. These are drain holes so the fat can bubble out as it renders.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C08DF32B293774D9393AF7FBDA15E05', X'97222FD011AF4140A646FD9A3FFF5B6D', X'97222FD011AF4140A646FD9A3FFF5B6D', 1, NULL, NULL, 2, NULL, 0, 'If you have a smoker that will cook at higher temperatures, I recommend turning up the heat to 325°F for about an hour or until it reaches 150°F in the thickest part of the breast. Then you can flip it over and finish it off over a direct flames on the grill or you can leave it skin side up and use the broiler in your oven.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41FB6C7F249ADF4D9EC5368B52D2D7B3', X'97222FD011AF4140A646FD9A3FFF5B6D', X'97222FD011AF4140A646FD9A3FFF5B6D', 2, NULL, NULL, 2, NULL, 0, 'If your smoker will not cook this hot, consider moving the pan with the duck to the oven at 325°F for about an hour or until it reaches 150°F in the thickest part of the breast. Then you can finish using the broiler in your oven.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DE4EF3701AA404F938825B6549DA222', X'36EE81F1D03C074CB414CCFDDFC8E44C', X'36EE81F1D03C074CB414CCFDDFC8E44C', 0, NULL, NULL, 2, NULL, 0, 'When the duck is finished cooking, remove it from the heat and tent some foil over the top for about 15 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB39BCCDF91154F853E35DC3C4DB5AA', X'36EE81F1D03C074CB414CCFDDFC8E44C', X'36EE81F1D03C074CB414CCFDDFC8E44C', 1, NULL, NULL, 2, NULL, 0, 'Remove the wings and legs then slowly and carefully remove the breast meat by following right along the breast bone with a very sharp knife. Slice the breast into cutlets and call dinner.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F62E1791319EE0448277139000163404', X'18A6F84D33F6E14B9120D45F8F13706F', X'18A6F84D33F6E14B9120D45F8F13706F', 0, NULL, NULL, 2, NULL, 0, 'For cranberry sauce, Add1 cup of orange juice, 1 cup of granulated sugar and 12 ounces of fresh cranberries to a small sauce pan over medium high heat and bring to a boil. Once it boils reduce the heat to low and let it simmer until it thickens to your liking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D187D00370BB94B8C2959B1D45123DC', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', 0, NULL, NULL, 2, NULL, 0, 'Sit the ham into a shallow pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05DC26181419794892E5B66E5AC24125', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', 1, NULL, NULL, 2, NULL, 0, 'Peel back each slice and douse it with maple syrup.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'198BC91C5F6DC54CA96B0232931D9413', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', 2, NULL, NULL, 2, NULL, 0, 'Now, go back through and peel back every slice giving it a good application of my original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'141A5A0D0EFDE04CAA55858DD65B2181', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', 3, NULL, NULL, 2, NULL, 0, 'Don''t forget to get that very front piece and the sides and back of the ham as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E0A8F7658BDC48B82C5A35D06E7C4E', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', 4, NULL, NULL, 2, NULL, 0, 'Pin the very front slice in place with some toothpicks around the perimeter so it doesn''t fall over during cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9542758C5E164468527B79646D2AC1C', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', X'56EF1DFA53DA874CACF8F0DDFE2F3BBD', 5, NULL, NULL, 2, NULL, 0, 'The ham is now ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C5C8CDB269C3F4195BE68D42B46BAA9', X'F959A9FF2B79B343B6E9037F1AD950AB', X'F959A9FF2B79B343B6E9037F1AD950AB', 0, NULL, NULL, 2, NULL, 0, 'Setup you smoker for indirect cooking at about 225°F and my recommendation is to use pecan wood for smoke if you have that available. Otherwise, any smoking wood will work.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00DB6C0CDBFA6B4F9FA1B2D61A727B0B', X'F959A9FF2B79B343B6E9037F1AD950AB', X'F959A9FF2B79B343B6E9037F1AD950AB', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9977492FE5886243B2E717BB57A46F14', X'F959A9FF2B79B343B6E9037F1AD950AB', X'F959A9FF2B79B343B6E9037F1AD950AB', 2, NULL, NULL, 2, NULL, 0, 'Place the pan with the ham on the smoker grate and let it go for about 3 to 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D458B92D0BBE1478BFBB169C4CAB197', X'F959A9FF2B79B343B6E9037F1AD950AB', X'F959A9FF2B79B343B6E9037F1AD950AB', 3, NULL, NULL, 2, NULL, 0, 'Make up a batch of ham glaze using ½ cup of maple syrup and 2 TBS of Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'296CC5C1E875804A908BD6BD614EB73D', X'F959A9FF2B79B343B6E9037F1AD950AB', X'F959A9FF2B79B343B6E9037F1AD950AB', 4, NULL, NULL, 2, NULL, 0, 'About once every hour, glaze the outside of the ham with this stuff. You don''t need to peel back the slices, just the outside will do.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB99C8AD65850140B1C90833D908FCA6', X'242394FA2D978243950414DEF944934E', X'242394FA2D978243950414DEF944934E', 0, NULL, NULL, 2, NULL, 0, 'When the ham starts to get some nice crispiness to the edges or when you can no longer stand the amazing smell without taking a bite, call it done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F407F46D8C12B43BE735C54C53D749F', X'242394FA2D978243950414DEF944934E', X'242394FA2D978243950414DEF944934E', 1, NULL, NULL, 2, NULL, 0, 'Remove the toothpicks and tear the slices off into a serving plate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F04371DD3D1D074B89623D4E7745CCE4', X'242394FA2D978243950414DEF944934E', X'242394FA2D978243950414DEF944934E', 2, NULL, NULL, 2, NULL, 0, 'All those sweet and smoky juices in the pan can now be poured over the top of the slices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2FD911DA7C0B045941F9BD1268BE526', X'242394FA2D978243950414DEF944934E', X'242394FA2D978243950414DEF944934E', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45DC0A3F7086724FBD3B5E387A3B40B3', X'14AFABB234FEC94C9F6D5034D49B776C', X'14AFABB234FEC94C9F6D5034D49B776C', 0, NULL, NULL, 2, NULL, 0, 'Make a brine mixture using 1 gallon of water, 1 cup of coarse kosher salt and 1 cup of brown sugar (light or dark).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29083BF9121C614C96EA29610F20FD5F', X'14AFABB234FEC94C9F6D5034D49B776C', X'14AFABB234FEC94C9F6D5034D49B776C', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey legs in a plastic or glass container and pour the brine mixture over them to cover. Chill in the fridge for 8 to 10 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39AF9C06FD8E094DBE8E438DBEBB5408', X'14AFABB234FEC94C9F6D5034D49B776C', X'14AFABB234FEC94C9F6D5034D49B776C', 2, NULL, NULL, 2, NULL, 0, 'Rinse turkey legs when they are finished brining.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54E4419A9CD014409E9ADD3262A2F183', X'1C61AEA9B8450A428228817343800689', X'1C61AEA9B8450A428228817343800689', 0, NULL, NULL, 2, NULL, 0, 'Make together 1 cup of mayonnaise and 1/4 cup of rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E54A4B4D4D556642A74260FACAE30ABD', X'1C61AEA9B8450A428228817343800689', X'1C61AEA9B8450A428228817343800689', 1, NULL, NULL, 2, NULL, 0, 'Apply mixture to all of the turkey legs getting it under the skin wherever possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84838170BDEB2043B5A7FA6AAD619BDA', X'265DF5E4B376014287F4CB51EA9598DC', X'265DF5E4B376014287F4CB51EA9598DC', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at 225-250 degrees F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36D20A011326EF4397CD9F26BF200504', X'265DF5E4B376014287F4CB51EA9598DC', X'265DF5E4B376014287F4CB51EA9598DC', 1, NULL, NULL, 2, NULL, 0, 'Place turkey legs on smoker grate to cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E217E74371B6E84692AF6B511CA913BF', X'27A0988935DC9A4FA5431B5CA6691CD8', X'27A0988935DC9A4FA5431B5CA6691CD8', 0, NULL, NULL, 2, NULL, 0, 'When the turkey reaches about 160 degrees F, glaze the turkey with a mixture of 1 cup maple syrup, 1 stick of butter and 1/4 cup Jeff''s origiinal rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93EB7233DDFB344186B09ED2C2D064BE', X'27A0988935DC9A4FA5431B5CA6691CD8', X'27A0988935DC9A4FA5431B5CA6691CD8', 1, NULL, NULL, 2, NULL, 0, 'Glaze again in about 15 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BD440D0152E67458680672E1FDCCF9C', X'0A0316BD64436E46A2F5B30BF69E9365', X'0A0316BD64436E46A2F5B30BF69E9365', 0, NULL, NULL, 2, NULL, 0, 'When the turkey reaches 180 degrees in the thickest part, it is finished and can be removed from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8179AC8DDD967A4CB17B9487BCC4AC5A', X'0A0316BD64436E46A2F5B30BF69E9365', X'0A0316BD64436E46A2F5B30BF69E9365', 1, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF3122B565A1094AB653AAFA987E8353', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 0, NULL, NULL, 2, NULL, 0, 'Cut a `T` into each jalapeno pepper by cutting halfway through the pepper just below the stem then cutting a slit from the top of the `T` to the tip of the pepper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4236C8D95A2EF4AAD5E2DBBCABEB595', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 1, NULL, NULL, 2, NULL, 0, 'Carefully pry open the pepper and deseed and devein the peppers with a thin, sharp knife then give them a good rinse under cold water to remove any remaining seeds.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC839E70AAA524AACA4FBB6EAEAD1EE', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 2, NULL, NULL, 2, NULL, 0, 'Make the stuffing mixture using 2 cups of pulled brisket, 16 ounces of softened cream cheese, 2 cups of shredded smoked gouda, and 1/2 cup of Jeff''s original rub. Mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7737415958046499D5C49EAAA0D787A', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 3, NULL, NULL, 2, NULL, 0, 'Stuff the peppers with the stuffing mixture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62147BDC5AABDA428B4EA294BF26C877', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 4, NULL, NULL, 2, NULL, 0, 'Stretch thick slices of bacon then wrap a single piece around each pepper. Secure with a toothpick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6CADE1BD55FE7448ED623AB6619AC31', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 5, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at 225 degrees with indirect heat. Use pecan wood for smoke and if your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C0E1920B2098446890C7F0619712DE1', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 6, NULL, NULL, 2, NULL, 0, 'Cook peppers directly on the grate for about 3 hours or until the bacon has a good bite-through and the peppers are soft to your liking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C70B9BDB67636345A4ADF688978BDE78', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 7, NULL, NULL, 2, NULL, 0, 'When the peppers are ready to eat, give them a good glazing with thinned barbecue sauce and crank up the heat to 350 degrees for 15 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5179AD1E747DFA42A2CF552177A28474', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', X'E7C9EE9A0AC8A44FB4C0F325C12C3A6E', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3FA7DBC39E915419A7E1A2F83A1BD60', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 0, NULL, NULL, 2, NULL, 0, 'Trim the fat cap to 1/4 inch and remove any hard fat on the brisket.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'998D9EFC2D92D346B8F95BC0B038D8F3', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 1, NULL, NULL, 2, NULL, 0, 'Score the fat cap in a crosshatch pattern at 1-inch intervals.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B2F38C4DFC9C24A8ADB4C9699CE0795', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 2, NULL, NULL, 2, NULL, 0, 'Place the brisket in a large foil pan fat cap up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'994B80B3E0EBBF489FB2D56A1659C867', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 3, NULL, NULL, 2, NULL, 0, 'Douse the top with Worcestershire sauce and apply Jeff''s Texas style rub generously.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EAB9BE766816540B35A58C641DD1199', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 4, NULL, NULL, 2, NULL, 0, 'Flip the brisket to fat side down');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A66B36C6C652734C8DBE31AF88361CE4', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 5, NULL, NULL, 2, NULL, 0, 'Douse the new top side with Worcestershire sauce and apply Jeff''s Texas style rub generously.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13A313D0AECF1C40BB05085511989012', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 6, NULL, NULL, 2, NULL, 0, 'Cover with foil and place the pan of brisket in the fridge overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBD0A5A2B5C88349A4A3F1BFB328C9E6', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 7, NULL, NULL, 2, NULL, 0, 'Setup the smoker for cooking at 225 degrees using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B01AAFB5576244EAE1F40B827DC048A', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 8, NULL, NULL, 2, NULL, 0, 'When ready, place the pan of brisket on the smoker and let it cook for 6-7 hours or until it reaches 165 degrees F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F462BCFD5A72AF40BD03362DF58EA49A', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 9, NULL, NULL, 2, NULL, 0, 'Douse the brisket with Worcestershire every 90 minutes until it reaches 165 degrees F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E675405A5CD8FE45BCBA71617F6EAE85', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 10, NULL, NULL, 2, NULL, 0, 'At 165 degrees, place another equally sized pan upside down on top of the pan with the brisket sealing the meat inside. You can also just cover with foil if desired.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0783E83A39C9DC488DE7B971F68FC572', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 11, NULL, NULL, 2, NULL, 0, 'Continue cooking the brisket until it reaches ~200 degrees and a probe inserted in the meat shows no resistance.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7854E4DAE8E9642A53942D0098A94E8', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 12, NULL, NULL, 2, NULL, 0, 'Remove the brisket from heat and wrap in unlined/uncoated butcher paper or foil. Place in a empty cooler to rest for 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61FD8B008788F446BFB62488A06FECBD', X'5F3A0ACCB6BC684F84FF50971F6365EE', X'5F3A0ACCB6BC684F84FF50971F6365EE', 13, NULL, NULL, 2, NULL, 0, 'After resting, remove brisket from cooler and slice 1/4 inch thick across the grain.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A514C4945DE47940BE54858C7301C4DC', X'99EE9EE038BD944E8FCD188AA45021E3', X'99EE9EE038BD944E8FCD188AA45021E3', 0, NULL, NULL, 2, NULL, 0, 'Place the breadcrumbs into a separate bowl and pour the buttermilk over the top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB91769F33F9DF48838E1689008357D9', X'99EE9EE038BD944E8FCD188AA45021E3', X'99EE9EE038BD944E8FCD188AA45021E3', 1, NULL, NULL, 2, NULL, 0, 'Add onions, Texas style rub, Worcestershire and chopped parsley.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FE9991612052E489E45F91BD8094026', X'99EE9EE038BD944E8FCD188AA45021E3', X'99EE9EE038BD944E8FCD188AA45021E3', 2, NULL, NULL, 2, NULL, 0, 'Give it a good mix and then leave it sitting so the ingredients could get to know each other for about 10 minutes while you get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D017479916CB041AA9CE5D410D08212', X'99EE9EE038BD944E8FCD188AA45021E3', X'99EE9EE038BD944E8FCD188AA45021E3', 3, NULL, NULL, 2, NULL, 0, 'Break the meat up into chunks and add that to a large bowl with the bread crumb mixture on top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60E27F12F7E3644991726CD9A1A569F8', X'99EE9EE038BD944E8FCD188AA45021E3', X'99EE9EE038BD944E8FCD188AA45021E3', 4, NULL, NULL, 2, NULL, 0, 'Blend that carefully with your hands making sure to only mix as much as absolutely necessary. Over-mixing is not good for meatballs and tends to make them tough.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E189EB7C21A2C94E8B46E030D7BE4F51', X'13CE95FE8DDD9642A82A961A182F5162', X'13CE95FE8DDD9642A82A961A182F5162', 0, NULL, NULL, 2, NULL, 0, 'To make the meatballs more uniform in size, you can either weigh them or just use a scoop so you get the same amount of mixture for each one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F5ACC72C1340C4795F6370861142430', X'13CE95FE8DDD9642A82A961A182F5162', X'13CE95FE8DDD9642A82A961A182F5162', 1, NULL, NULL, 2, NULL, 0, 'Roll the mixture between your palms to form as many uniform meatballs as you need.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CB4E983272B504A95D36620A5AA0C58', X'58B93F16AD2F504AB1FC10230109CD9C', X'58B93F16AD2F504AB1FC10230109CD9C', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 225 to 240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D871581A3E4FE8459BCBF8F2783DDE62', X'58B93F16AD2F504AB1FC10230109CD9C', X'58B93F16AD2F504AB1FC10230109CD9C', 1, NULL, NULL, 2, NULL, 0, 'I recommend oak or cherry smoke or a mixture of these if you have it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E754B076BB1B2C46BAA8F0E9058E97A2', X'58B93F16AD2F504AB1FC10230109CD9C', X'58B93F16AD2F504AB1FC10230109CD9C', 2, NULL, NULL, 2, NULL, 0, 'Lastly, if your smoker utilizes a water pan, fill it up and use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D04A988B50A64481C89C02033BA31D', X'03664F731D29F74EB680B3F33ADAAD61', X'03664F731D29F74EB680B3F33ADAAD61', 0, NULL, NULL, 2, NULL, 0, 'Once the smoker is preheated and ready to go, place the meatballs on the grate or you can leave them on the pan if you are using something like a Bradley rack or a Weber grill pan like I do.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38DF1F2AA34E734DA0479F53A1CF1AFB', X'03664F731D29F74EB680B3F33ADAAD61', X'03664F731D29F74EB680B3F33ADAAD61', 1, NULL, NULL, 2, NULL, 0, 'Keep a close eye on these meatballs since they''ll most likely be done in an hour or less depending on the size that you make them and the temperature that you run in your smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0B960F382B02B489898A96E35B1E06E', X'03664F731D29F74EB680B3F33ADAAD61', X'03664F731D29F74EB680B3F33ADAAD61', 2, NULL, NULL, 2, NULL, 0, 'Use a handheld thermometer that reads fast for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CA88A286CAED3449BD0E86FBFDCDF2A', X'03664F731D29F74EB680B3F33ADAAD61', X'03664F731D29F74EB680B3F33ADAAD61', 3, NULL, NULL, 2, NULL, 0, 'The meatballs are done when they reach an internal temperature of 160°F however, when they reach about 150°F, you have a decision to make– keep reading!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EF637C93C13F7459C153A10A0F90AC6', X'2DE3AC195796F942BE26B1E9437DD144', X'2DE3AC195796F942BE26B1E9437DD144', 0, NULL, NULL, 2, NULL, 0, 'If you want to glaze the meatballs with sauce, then read on. Otherwise skip to step 6');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDAFBF7040328A41B6A8656A3A32130B', X'2DE3AC195796F942BE26B1E9437DD144', X'2DE3AC195796F942BE26B1E9437DD144', 1, NULL, NULL, 2, NULL, 0, 'Pour a cup of my barbecue sauce into a bowl and mix in ½ cup of Worcestershire sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FF753C6305EAC4B8E0A4FE5BAD24B80', X'2DE3AC195796F942BE26B1E9437DD144', X'2DE3AC195796F942BE26B1E9437DD144', 2, NULL, NULL, 2, NULL, 0, 'Glaze the meatballs with this sauce mixture then let them finish cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7C1FDAF6438A7438ABB067E84FF1545', X'7EA42CAB5A457047A313B2A14BF0D6C7', X'7EA42CAB5A457047A313B2A14BF0D6C7', 0, NULL, NULL, 2, NULL, 0, 'When the meatballs reach 160°F they are finished and should be removed from the heat and served immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45F57B508FAA8C4FAE365810AC241090', X'7EA42CAB5A457047A313B2A14BF0D6C7', X'7EA42CAB5A457047A313B2A14BF0D6C7', 1, NULL, NULL, 2, NULL, 0, 'Add a little chopped parsley on top for garnish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6BB9609BBDA1F4F97D9CFEC88C05816', X'76F5BA22B5866F4282DBB59840FCA1B3', X'76F5BA22B5866F4282DBB59840FCA1B3', 0, NULL, NULL, 2, NULL, 0, 'If you are cooking the tri-tip low and slow, then there is no reason to leave the fat cap intact. Remove it with a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEDA440077A4D94B8D63583DB32C16D5', X'ACD14A6D2F374341A8A4A5325686F0D7', X'ACD14A6D2F374341A8A4A5325686F0D7', 0, NULL, NULL, 2, NULL, 0, 'Apply coarse kosher salt to both sides of the meat at a rate of about 1/2 teaspoon per lb. 1 teaspoon of coarse kosher salt covers a 2 lb tri-tip.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13099701FF2A504483E4B5919DE71AB9', X'ACD14A6D2F374341A8A4A5325686F0D7', X'ACD14A6D2F374341A8A4A5325686F0D7', 1, NULL, NULL, 2, NULL, 0, 'Place meat in a lidded container and into the fridge for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09FB5B11BE37D942879E34C1B3A1F822', X'ACD14A6D2F374341A8A4A5325686F0D7', X'ACD14A6D2F374341A8A4A5325686F0D7', 2, NULL, NULL, 2, NULL, 0, 'It is not necessary to rinse the salt from the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E517976D1312C746ACCB15717F4DC104', X'443F225E75A86A458AF5E71BF425BFC3', X'443F225E75A86A458AF5E71BF425BFC3', 0, NULL, NULL, 2, NULL, 0, 'Apply a light coat of Jeff''s Texas style rub to the top side of the tri-tip.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A1DBAAE4CC74449D81EA3F3949B786', X'443F225E75A86A458AF5E71BF425BFC3', X'443F225E75A86A458AF5E71BF425BFC3', 1, NULL, NULL, 2, NULL, 0, 'Set the meat aside while you go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8A899F92F5A664AB0D2DB8254D27141', X'FF7940CF87C510499DA06B05399AE6C9', X'FF7940CF87C510499DA06B05399AE6C9', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 225°F with indirect heat if possible. If your smoker uses a water pan fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9322D622132A2C4FB20E838429564F86', X'FF7940CF87C510499DA06B05399AE6C9', X'FF7940CF87C510499DA06B05399AE6C9', 1, NULL, NULL, 2, NULL, 0, 'Let the smoker preheat for about 30 minutes before proceeding to the next step.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD5A257035C7094D8BB95ADDD6E82DC3', X'D2379BEE21485944BD3E2DD19E7B39C8', X'D2379BEE21485944BD3E2DD19E7B39C8', 0, NULL, NULL, 2, NULL, 0, 'Lay the tri-tip right on the smoker grate or you can use something like a Weber grill pan or Bradley rack to make it easy to move it to and from the kitchen.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F87FD3AD758D204E846EE59D6A77BC44', X'D2379BEE21485944BD3E2DD19E7B39C8', X'D2379BEE21485944BD3E2DD19E7B39C8', 1, NULL, NULL, 2, NULL, 0, 'Use oak and/or pecan for smoke if you have it and keep that smoke going the entire time if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DA57FE6FC4C624BB403FC0B5B42B89B', X'D2379BEE21485944BD3E2DD19E7B39C8', X'D2379BEE21485944BD3E2DD19E7B39C8', 2, NULL, NULL, 2, NULL, 0, 'Keep a CLOSE eye on the temperature since that is the ONLY way to truly know when it''s perfect.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B607073D4A29D54FAC2555688E575E81', X'CCB6CC4126A42442A95027B9B8984C0D', X'CCB6CC4126A42442A95027B9B8984C0D', 0, NULL, NULL, 2, NULL, 0, 'When the meat reaches about 110 to 115°F is a great time to remove it from the smoker and place it over a fire, some hot coals or under the broiler of your oven to sear the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13F6C2B43DE0C041823F432EB3701E03', X'CCB6CC4126A42442A95027B9B8984C0D', X'CCB6CC4126A42442A95027B9B8984C0D', 1, NULL, NULL, 2, NULL, 0, 'The idea here is to brown the outside of the meat not just to make sear marks. This browning improves the flavor greatly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B963404A1D4E664D91DCEA0BED93B4E9', X'CCB6CC4126A42442A95027B9B8984C0D', X'CCB6CC4126A42442A95027B9B8984C0D', 2, NULL, NULL, 2, NULL, 0, 'If the meat has not reached 130°F when you are done searing, place the meat back in the smoker to finish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CFF5FAD5CED5B4F9309EA729F21C976', X'E5A37396B07A8D469FEFDFABBE8511F0', X'E5A37396B07A8D469FEFDFABBE8511F0', 0, NULL, NULL, 2, NULL, 0, 'When finished, remove the tri-tip from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A702BE0F2042324283E881DD61E1736E', X'E5A37396B07A8D469FEFDFABBE8511F0', X'E5A37396B07A8D469FEFDFABBE8511F0', 1, NULL, NULL, 2, NULL, 0, 'For best results, loosely wrap it in foil for a little rest time. I usually allow about 10-15 minutes before slicing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F99BA62D926344861C3D341BC821A0', X'E5A37396B07A8D469FEFDFABBE8511F0', X'E5A37396B07A8D469FEFDFABBE8511F0', 2, NULL, NULL, 2, NULL, 0, 'Slice it up into pieces about the thickness of a pencil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9AE7EFDE0158C4A8E10560D4A226D51', X'E5A37396B07A8D469FEFDFABBE8511F0', X'E5A37396B07A8D469FEFDFABBE8511F0', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6F24689EA904A4091BAF397C21DBE7E', X'B23C2AA67643A84FAE379798D5B4E833', X'B23C2AA67643A84FAE379798D5B4E833', 0, NULL, NULL, 2, NULL, 0, 'Use a very sharp knife to remove skin/rind if required.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'883E615C0EE67647B26AA935D81BE1C0', X'8A047D92C53FB34F82951047C60BE5EE', X'8A047D92C53FB34F82951047C60BE5EE', 0, NULL, NULL, 2, NULL, 0, 'With the pork belly laying flat in front of you, fat cap side up, use a very sharp knife to make cuts (back to front) about ¼ inch deep and about 1 inch apart.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE6ABBEEB9A5E744A0ED6CDCD6FEC316', X'8A047D92C53FB34F82951047C60BE5EE', X'8A047D92C53FB34F82951047C60BE5EE', 1, NULL, NULL, 2, NULL, 0, 'Make the same cuts from side to side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23747C451FD95D478C9BA25960B5D15A', X'4C79BC9AF7A2484EBBD44D9C934BC35C', X'4C79BC9AF7A2484EBBD44D9C934BC35C', 0, NULL, NULL, 2, NULL, 0, 'Apply barbecue sauce or yellow mustard all over the fat cap to help the rub to stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DFDADFB2428174EAB33C0839304E8BD', X'4C79BC9AF7A2484EBBD44D9C934BC35C', X'4C79BC9AF7A2484EBBD44D9C934BC35C', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle rub generously onto the fat cap.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7644EF3E35EBA4AA09A66FEE9D21A50', X'637F83EFC185EE4AAE4C3142AF7209A8', X'637F83EFC185EE4AAE4C3142AF7209A8', 0, NULL, NULL, 2, NULL, 0, 'Flip the pork belly over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0934C410C50FDB40B856730E19F8796C', X'637F83EFC185EE4AAE4C3142AF7209A8', X'637F83EFC185EE4AAE4C3142AF7209A8', 1, NULL, NULL, 2, NULL, 0, 'Apply the same barbecue sauce or mustard and rub on the meaty side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8106303C7AE49E4E941BE5F3ED3EB11F', X'525CA75B8A1BFC4A8C66043D329E7E91', X'525CA75B8A1BFC4A8C66043D329E7E91', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 225°F using indirect heat and if your smoker has a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C412649611EA54CBE1A13DC5C71EA1E', X'525CA75B8A1BFC4A8C66043D329E7E91', X'525CA75B8A1BFC4A8C66043D329E7E91', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, go get the meat and get it on the grates.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C86463B7DFF0C49A24E8CC4DDB210A5', X'9BC6F1F099A5F042BB2EC628BA9B74DB', X'9BC6F1F099A5F042BB2EC628BA9B74DB', 0, NULL, NULL, 2, NULL, 0, 'Place the pork belly on the smoker grate fat side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D107FA803AF1CE4C947EA621F1D6C458', X'9BC6F1F099A5F042BB2EC628BA9B74DB', X'9BC6F1F099A5F042BB2EC628BA9B74DB', 1, NULL, NULL, 2, NULL, 0, 'Let it cook for about 4 hours or until it reaches 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C49A45F824959940819D4C6DA4C436B2', X'D369C600ED818B49B6DEA50A561DF4F3', X'D369C600ED818B49B6DEA50A561DF4F3', 0, NULL, NULL, 2, NULL, 0, 'Wrap the meat in foil, butcher paper or you can place it down in a foil pan and cover that with foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C7DE5E4EB67444DB8C29C4CBA79590E', X'D369C600ED818B49B6DEA50A561DF4F3', X'D369C600ED818B49B6DEA50A561DF4F3', 1, NULL, NULL, 2, NULL, 0, 'After wrapping or covering, place it back on to the grate for another ~1.5 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9ED0D661DF3E14F964C9B94E42A6A57', X'CB9CF49BAE5CC543AE09F613BE042FAD', X'CB9CF49BAE5CC543AE09F613BE042FAD', 0, NULL, NULL, 2, NULL, 0, 'Let the pork belly reach about 195 to 200°F before calling them done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0221E94329732A4691353658A4B4E3CB', X'CB9CF49BAE5CC543AE09F613BE042FAD', X'CB9CF49BAE5CC543AE09F613BE042FAD', 1, NULL, NULL, 2, NULL, 0, 'Let the meat rest with foil tented over it for at least 15 minutes before serving. An hour is better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'622F672DCC1ADF45AB64AEEB1255C623', X'CB9CF49BAE5CC543AE09F613BE042FAD', X'CB9CF49BAE5CC543AE09F613BE042FAD', 2, NULL, NULL, 2, NULL, 0, 'Slice about ¼ inch thick and serve it up!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34511E22B0F8A84A8F7F51B6A473A5E9', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 0, NULL, NULL, 2, NULL, 0, 'Remove membrane from bone side of ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4803028FD9961D4990B3ECDE87EC61F8', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 1, NULL, NULL, 2, NULL, 0, 'Remove flap of meat from bone side using a sharp knife');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDDCB9D217336446B498227741CC7ACF', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 2, NULL, NULL, 2, NULL, 0, 'Apply Jeff''s original rub to bone side of ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'327702B8B244794E89F498B31E13E2F6', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 3, NULL, NULL, 2, NULL, 0, 'Flip ribs over to meaty side up');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26704B74B1D5544689A387BEA59AFA6C', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 4, NULL, NULL, 2, NULL, 0, 'Apply Jeff''s original rub all over');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBB05461ABC6004A82BE000ACE1DB5B1', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 5, NULL, NULL, 2, NULL, 0, 'Smoke cook ribs at 225 degrees for 6 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA99B5B985E17549BB2C092504ECC415', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 6, NULL, NULL, 2, NULL, 0, 'Just before ribs are done, place jar of raspberry spreadable fruit in sauce pan over medium low heat and stir until smooth.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68BB1C41EDD3134E86B9AC7320F79B62', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 7, NULL, NULL, 2, NULL, 0, 'Apply generous coat of the rasperry glaze onto meaty side of ribs about 15 minutes before they are finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28A72364524E244BA106B926176920E2', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 8, NULL, NULL, 2, NULL, 0, 'Remove ribs from heat when they reach 194 degrees F in the meaty area between the bones.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C56E50FD18E89F4E9DB7DCA791979626', X'913E6D83362C8648B15A4CEFFCCBA083', X'913E6D83362C8648B15A4CEFFCCBA083', 9, NULL, NULL, 2, NULL, 0, 'Slice ribs between the bones and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'117FEA9994E03F4783564997AD568FE0', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 0, NULL, NULL, 2, NULL, 0, 'Apply yellow mustard all over the outside of the meat to help the rub to stick better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BDE71613573BD40A0BB03B6EE490355', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 1, NULL, NULL, 2, NULL, 0, 'Pour ¼ cup of Jeff''s original rub on top of the pork butt and massage it in to create a paste. Cover the top and sides with the rub/mustard paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC03A2234B4C844F8E36E31A1530C9E3', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 2, NULL, NULL, 2, NULL, 0, 'Cover/wrap the pork butt and place it in the fridge overnight if possible');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1294E968A224CE4095AD84F386CF6831', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 3, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at about 300°F with indirect heat. Once it''s ready, it''s time to cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5CC73673AE23848863282ED9BF850F9', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 4, NULL, NULL, 2, NULL, 0, 'Place pork butt on grate fat side down and close lid/door.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8536DCF6D3C9764FBD759A427CC67A0B', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 5, NULL, NULL, 2, NULL, 0, 'When the pork butt reaches 165°F or when the color is perfect (about 3 hours), remove meat from smoker and wrap in a double layer of heavy duty foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F14EEF874ABF34799654D2D607D21D0', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 6, NULL, NULL, 2, NULL, 0, 'Place pork butt back onto 300 degree smoker and continue to cook for 2 hours or until the internal temperature of the meat reaches 205°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AEA332EF3E6074FB57AC3FC25E5196B', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 7, NULL, NULL, 2, NULL, 0, 'Remove the meat from the heat and let it rest for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F8381A5DB526D449385F4589872FB4D', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 8, NULL, NULL, 2, NULL, 0, 'Pull the meat into chunks, pieces discarding any clumps of fat that did not render during the cooking process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CECD5369FC293249BB3C2CB5705486FA', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 9, NULL, NULL, 2, NULL, 0, 'Serve pork on slider buns with slaw, pickles, Jeff''s barbecue sauce, onions, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEA0EAF4AEC2DB4E9FE75D1DDC7A666A', X'37173B2E22A5554282E6DC23E7D5E432', X'37173B2E22A5554282E6DC23E7D5E432', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F37A512B8920E54187AE70D70EB36E24', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle approximately 1/8 teaspoon of kosher salt on the top of each steak and place tray of steaks in the fridge for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2CD4C07B2E4A947A493557E7D7FEF88', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 1, NULL, NULL, 2, NULL, 0, 'Remove steaks from fridge (no rinsing required) when they are finished brining.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C77C36AF8DF490419DDBCE552D86A00C', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 2, NULL, NULL, 2, NULL, 0, 'Wrap a piece of bacon around the outside edge of each steak and secure with a toothpick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63F4D9C5206BC74A8631D14C5AC77EAF', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 3, NULL, NULL, 2, NULL, 0, 'Apply a light coat of olive oil and a light sprinkling of Jeff''s Texas style rub to each steak.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE3A23ACFFDEB43959B2B7B8AEFA6A8', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 4, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at about 225°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'301A4C21AF94E5488185D9ECD5B11E37', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 5, NULL, NULL, 2, NULL, 0, 'Place the meat in the smoker for approximately 1 hour or until the steaks reach an internal temperature of 130°F (medium rare).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B231B5C0017EC4E85088BCA5D6021E1', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 6, NULL, NULL, 2, NULL, 0, 'Let the steaks cool for about 10-15 minutes then sear the top and bottom of each steak on a very hot grill or in a screaming hot skillet. Leave them in the skillet only long enough to brown the meat. Do not let them burn.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'855BA9CBF383154E9A2A4E0FDD18909B', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 7, NULL, NULL, 2, NULL, 0, 'As an option, you can use a handheld torch to crisp the bacon on the outside of each steak as 1 hour isn''t quite long enough to do that in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'393F65294F9B6C419578188619E3C2D9', X'57A50782BF1D4D44B707A5FF9868B0D9', X'57A50782BF1D4D44B707A5FF9868B0D9', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76FA03B470A7204DA7260C40B464BC20', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 0, NULL, NULL, 2, NULL, 0, 'Remove skin on bone side of the ribs if desired');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DEE6A7C9CADF947A4A5126039C41D98', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle about 1 to 1.5 teaspoons of kosher salt on the meaty side of each rack of ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56E0514EBF6EBB48BE63CFA35B9409B8', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 2, NULL, NULL, 2, NULL, 0, 'Place the ribs in the fridge for at least 2 hours but overnight is also good.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68A8A0342DD0CC4897422255E3264DDC', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 3, NULL, NULL, 2, NULL, 0, 'Do not rinse. Apply about 2 TBS of Jeff''s Texas style rub to the top meaty side of each rack of ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12F1B4AA233BDA47B47794B323964781', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 4, NULL, NULL, 2, NULL, 0, 'Set up the Pit Barrel Cooker according to manufacturers instruction and when it''s ready, hang the racks() of ribs and replace the lid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3088EB78895B04CA3A47CB2FFB0E123', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 5, NULL, NULL, 2, NULL, 0, 'Check the temperature of the ribs after about 2 hours and when they reach 190 °F, apply about ¼ cup of sauce all over (if desired)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7392D8B605C766489A4D543637B3CCAA', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 6, NULL, NULL, 2, NULL, 0, 'The ribs are finished cooking when they reach 195 °F internal temperature and can be removed from the cooker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7D844CC8C80754790362C2CEF898694', X'5C0BC405432B7646B400B2CFB857B2C1', X'5C0BC405432B7646B400B2CFB857B2C1', 7, NULL, NULL, 2, NULL, 0, 'Rest for about 10 minutes then slice between the bones and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEAB54A527656D468F00103BF8CCA92C', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 0, NULL, NULL, 2, NULL, 0, 'Separate meat from large clumps of fat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32515DD034F7474D9F60B80A95E4BBE8', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 1, NULL, NULL, 2, NULL, 0, 'Cut meat into pieces of consistent size. I recommend about 1 x 1 inch.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EED6403037DFA84AA1BDB476E8146F94', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 2, NULL, NULL, 2, NULL, 0, 'Place meat into a bag and pour ½ cup of Jeff''s original rub over the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83B5356B956C0F408A6D7405CAEA20C0', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 3, NULL, NULL, 2, NULL, 0, 'Roll, knead, and shake the bag to coat the meat with the rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4FC3F8315EDE34BAA9E8431A268DF90', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 4, NULL, NULL, 2, NULL, 0, 'Place the bag of seasoned meat into the fridge for at least 2 hours or overnight to let the flavor absorb better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12218B414F05C648A557B41A8D8EDF5C', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 5, NULL, NULL, 2, NULL, 0, 'Thread the meat onto skewers. I recommend 4-6 pieces per skewer depending on how large the pieces are.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'793E1851A77AF547B4D6706DBC85ADAB', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 6, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking indirect at about 225-240°F. If the smoker uses a water pan, fill it up. Use pecan, cherry and/or maple for wood smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66CC010DE509A042A1B26A2B51042739', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 7, NULL, NULL, 2, NULL, 0, 'Place the meat on the grate and smoke cook for about 2 hours and 10 minutes or until they reach an internal temperature of 185-190°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'662AC99590D29C4ABCAF7D0A576C4439', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 8, NULL, NULL, 2, NULL, 0, 'Optional: Brush with barbecue sauce and place the skewers over a hot grill or under the oven broiler for a minute or two per side to get a good sear on them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40A6E87CCABB2E41978C798221CC475F', X'791A8E9C2ACAE840BECC6F915AE16EF1', X'791A8E9C2ACAE840BECC6F915AE16EF1', 9, NULL, NULL, 2, NULL, 0, 'Serve to your guests.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22E925336A04DA4E846E833883581AF6', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 0, NULL, NULL, 2, NULL, 0, 'Dry brine the turkey by sprinkling kosher salt evenly over the breast, legs, wings and back of the turkey. Total salt usage should be about 2 TBS. Place the turkey in the fridge uncovered for about 8 hours or overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EEF4672AC5F8348A58976B84B09A293', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 1, NULL, NULL, 2, NULL, 0, 'Mix together 1 cup of mayonnaise with about ½ bottle of Jeff''s original rub (½ cup).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA5A252C23C4DB418EAE630D42FC327E', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 2, NULL, NULL, 2, NULL, 0, 'Make sure the turkey is nice and dry then apply the mayonnaise/rub mixture all over the outside of the turkey. Get some under the skin wherever possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5B3B502056E134A91EDF6E5D4002881', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 3, NULL, NULL, 2, NULL, 0, 'Set up Pit Barrel Cooker according to directions in manual. If using a regular smoker, set it up to cook at about 240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9C78AF244AB2F4AB26A6CC48FA77CA5', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 4, NULL, NULL, 2, NULL, 0, 'Place the turkey in the Pit Barrel using hooks or the special turkey hanger. You can also just set it on the included grate if you prefer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2668E2033271E34C9990268441FC19F9', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 5, NULL, NULL, 2, NULL, 0, 'Let the turkey cook in the PBC for approximately 4 hours or until a thermometer reads 165°F in the thickest part of the breast or thigh. If you are cooking at a lower temperature such as 240°F using a normal smoker, the time will be closer to 6 or 7 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8602E108B288D48B0C06687BEED8F69', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 6, NULL, NULL, 2, NULL, 0, 'When the turkey reaches the finish temperature, remove it from the cooker/smoker and let it rest under foil for about 20 minutes before carving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C984FA2714D7140B411D8020A6096E8', X'4D2F851F86BA4149B47DFB36D3E9C4CE', X'4D2F851F86BA4149B47DFB36D3E9C4CE', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EA248C1DA2B2647BAB4BF9FCF798D26', X'46277C0BF576B94FBD55C507E48CDB5D', X'46277C0BF576B94FBD55C507E48CDB5D', 0, NULL, NULL, 2, NULL, 0, 'Make a brine by mixing a gallon of cold water, 1 cup of kosher salt and 1 cup of brown sugar. Mix until dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A5C2A560FB1F445AF874BE8383B99CD', X'46277C0BF576B94FBD55C507E48CDB5D', X'46277C0BF576B94FBD55C507E48CDB5D', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken into a large plastic container and pour the brine over the chicken to cover. Place contain in the fridge for 3 hours. Rinse chicken with cold water when complete');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42F00E84900D2B4EB26258F5D375E1D9', X'46277C0BF576B94FBD55C507E48CDB5D', X'46277C0BF576B94FBD55C507E48CDB5D', 2, NULL, NULL, 2, NULL, 0, 'Pat the chicken dry with a paper towel and put back into brining container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2884139EA3A639489600C4E106DF6BF2', X'46277C0BF576B94FBD55C507E48CDB5D', X'46277C0BF576B94FBD55C507E48CDB5D', 3, NULL, NULL, 2, NULL, 0, 'Cover chicken with about 1 cup of Jeff''s original rub and stir to coat or place lid on container and shake to coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9B4EE39510D9444BAC665D9F87207F1', X'46277C0BF576B94FBD55C507E48CDB5D', X'46277C0BF576B94FBD55C507E48CDB5D', 4, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at about 180°F using indirect heat. If you have a water pan, leave it dry.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3755AF56992E3E46A5ACB94E972FE81A', X'46277C0BF576B94FBD55C507E48CDB5D', X'46277C0BF576B94FBD55C507E48CDB5D', 5, NULL, NULL, 2, NULL, 0, 'Place chicken on smoker grate and smoke cook at 180°F for 45-60 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'155108AC4C5558489721C3B9D026BEEA', X'46277C0BF576B94FBD55C507E48CDB5D', X'46277C0BF576B94FBD55C507E48CDB5D', 6, NULL, NULL, 2, NULL, 0, 'Increase smoker temperature to 350-400°F if possible and continue cooking wings for about 30 minutes or until they reach 180°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A7265EAE1AD8648BA1B9B0661686AA2', X'435E78B2C2265A40A2ED0C71E134BE70', X'435E78B2C2265A40A2ED0C71E134BE70', 0, NULL, NULL, 2, NULL, 0, 'I recommend a boneless pork butt. If you purchase a bone-in, you will need to remove the bone with a sharp knife prior to cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C76222FC8BB69440964ED11D6F0F98AF', X'435E78B2C2265A40A2ED0C71E134BE70', X'435E78B2C2265A40A2ED0C71E134BE70', 1, NULL, NULL, 2, NULL, 0, 'To create a nice round shape for slicing, tie the pork butt up along the length and from end to end.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'605354624C58754796F1339F046F3538', X'435E78B2C2265A40A2ED0C71E134BE70', X'435E78B2C2265A40A2ED0C71E134BE70', 2, NULL, NULL, 2, NULL, 0, 'Add a thin coat of mustard all over and then apply a heavy coat of Jeff''s original rub. The pork butt is ready to smoke or it can be placed in the fridge overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8587F736C1FF8847B10F4A4F9B8037C2', X'D03795A83C58104E86F42D17E2658620', X'D03795A83C58104E86F42D17E2658620', 0, NULL, NULL, 2, NULL, 0, 'Set up the smoker for cooking at about 250°F using indirect heat and if you smoker has a water pan, fill it up. If you are using a gas, charcoal, or electric smoker, you need to have enough smoking wood to last about 4 hours. I used an equal mix of apple and cherry but you can use whatever smoking wood you have available. Once the smoker is ready, place the pork butt on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAC170B4A973E14D85FF9A1196790E79', X'D03795A83C58104E86F42D17E2658620', X'D03795A83C58104E86F42D17E2658620', 1, NULL, NULL, 2, NULL, 0, 'Once the meat reaches 175°F (about 8 hours), remove it from the smoker and set the pan on the counter for about an hour to rest and cool down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F2CCEBD36E33F4EBC959774741D7C2F', X'64E2C887D40AC7448C88543E4BD65957', X'64E2C887D40AC7448C88543E4BD65957', 0, NULL, NULL, 2, NULL, 0, 'Let the roast cool for about an hour then place it in the fridge for about 5-6 hours or overnight to firm up for slicing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73DA7C5F3C82074CBB9135F66782D1EC', X'64E2C887D40AC7448C88543E4BD65957', X'64E2C887D40AC7448C88543E4BD65957', 1, NULL, NULL, 2, NULL, 0, 'Slice the smoked pork butt with an electric slicer or a very sharp knife into pieces that are about 1/8 inch thick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FFA7411CDC3D5479CA00110D0AD320F', X'E316D31FF65C20408F888A4F0B84EAD6', X'E316D31FF65C20408F888A4F0B84EAD6', 0, NULL, NULL, 2, NULL, 0, 'Make the campfire sauce using 1 cup of Jeff''s barbecue sauce and 1 cup of mayonnaise. Mix thoroughly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'407D9B5355D42F46A409A369AC86199D', X'E316D31FF65C20408F888A4F0B84EAD6', X'E316D31FF65C20408F888A4F0B84EAD6', 1, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of softened butter onto the tops and bottoms of each slider bun and toast under the broiler until golden brown. Watch carefully and do not burn.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CF49A6482BFA1449AD47602BE353F95', X'E316D31FF65C20408F888A4F0B84EAD6', X'E316D31FF65C20408F888A4F0B84EAD6', 2, NULL, NULL, 2, NULL, 0, 'Warm the sliced meat in a 275°F oven covered with foil for about 15 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DBC8B62C23B604E8EEA013BE61DB2FB', X'00C2FA9413FA5846B54A72B52FE9CD4E', X'00C2FA9413FA5846B54A72B52FE9CD4E', 0, NULL, NULL, 2, NULL, 0, 'Place a dollop of campfire sauce on the bottom, stack 3-4 layers of meat on top of that, a spoonful of slaw, more campfire sauce on top with a toasted bun.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94409402DABAC54FBEE07B951C49EC02', X'00C2FA9413FA5846B54A72B52FE9CD4E', X'00C2FA9413FA5846B54A72B52FE9CD4E', 1, NULL, NULL, 2, NULL, 0, 'Serve immediately!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D3FC18FCA65E44924AF48C823ED0C4', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 0, NULL, NULL, 2, NULL, 0, 'Cut the link of smoked sausage into ½ inch pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'619DBF94CC553C4E9A20C1771E842258', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 1, NULL, NULL, 2, NULL, 0, 'Cut the slab of bacon in half.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4532787B956B24FA69178D96AC6FCFB', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 2, NULL, NULL, 2, NULL, 0, 'Combine the cream cheese with 3 TBS of Jeff''s original rub and spoon into a quart sized zip to bag. Snip off a small corner at the bottom for piping the cream cheese into the `cups`.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E7BD9D3E53C34459755DE2CB6E6DF15', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 3, NULL, NULL, 2, NULL, 0, 'Wrap a half slice of bacon around a piece of smoked sausage and secure all the way through with a toothpick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AB7845CCB7120488D992D1FC66AF66E', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 4, NULL, NULL, 2, NULL, 0, 'Place a cube of cheddar cheese into the bacon cup');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02D68BC043393A4BB7324E67F44CB0BD', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 5, NULL, NULL, 2, NULL, 0, 'Pipe cream cheese filling into the cup until it''s just below the top of the bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE540AD668CDB14D871E7D18DF5150A3', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 6, NULL, NULL, 2, NULL, 0, 'Spoon in about ½ teaspoon of diced onions and sprinkle more of Jeff''s original rub on top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'591A6B6D06A41A41AE4E9D6F9AACAB27', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 7, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 250°F with indirect heat and apple wood for smoke. If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F051D1BAB9F2CE4C889D36BF5077AE50', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 8, NULL, NULL, 2, NULL, 0, 'Place the pig shots on a Weber grill pan, Bradley rack or even a cooling rack and place that into the smoker for about 90 minutes or until the bacon has a good eating texture and the cheese is melted.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DEDD54B9ED03440A23316AB2E39548C', X'1FE9FA1C7154B940828035FFAAB850D0', X'1FE9FA1C7154B940828035FFAAB850D0', 9, NULL, NULL, 2, NULL, 0, 'Remove the pig shots from the smoker and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70518C0B90E339499B993690D4B8607A', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 0, NULL, NULL, 2, NULL, 0, 'Rub the pork butt with thin coat of yellow mustard. You can omit this step but it helps the rub to stick better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87E007BC8F48CB409B75E727A324FCE4', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 1, NULL, NULL, 2, NULL, 0, 'Apply Jeff''s original rub onto all sides of the roast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235F41BB94300E4A803D1F261D0A6560', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 2, NULL, NULL, 2, NULL, 0, 'Set the meat aside while you get the smoker ready to cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A372D96F3EE2499C6DCABA55F1086D', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 3, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-250°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97C1A56D7A99E44690EE4CD2FCF58CC0', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 4, NULL, NULL, 2, NULL, 0, 'If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29F179C008666743BED7468FA1714468', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 5, NULL, NULL, 2, NULL, 0, 'Place the meat directly on the smoker grate and close the lid. If you are using a pellet grill/smoker, I recommend starting out in the special `Smoke` setting for about 1 hour then adjust to 225-250°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E49200258FD41447AFD16DCAB14AE95C', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 6, NULL, NULL, 2, NULL, 0, 'When the meat reaches 160°F, it''s a great idea to place it into a foil pan to catch the drippings and to help it to cook faster.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA9467F88A650D4DA57827D252A5A5C1', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 7, NULL, NULL, 2, NULL, 0, 'Use a thermometer such as the ThermoWorks Smoke to monitor the internal temperature of the roast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84AA1875F51C94B93684C5B5D493F82', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 8, NULL, NULL, 2, NULL, 0, 'When the meat reaches 205-207 °F it is done and perfectly tender.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC4A108121FD14097D0328471869F69', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 9, NULL, NULL, 2, NULL, 0, 'Allow it to rest under a tent of foil for about an hour before proceeding.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BA0D2F85F2B534FBAEA668A515C1063', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 10, NULL, NULL, 2, NULL, 0, 'Pull the meat into pieces using a couple of forks and discard any fat and gristle that you find.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71880C164182324D9172D226D2AFC826', X'1FA51A479460E4458998FD19E7B41C9F', X'1FA51A479460E4458998FD19E7B41C9F', 11, NULL, NULL, 2, NULL, 0, 'Serve with Jeff''s barbecue sauce on the side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96C4D896F8C65F4DB0C2C356A2CB85CA', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 0, NULL, NULL, 2, NULL, 0, 'Lay the tri-tip on a cutting board or in a pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1D85B0F8A763D479B8A7BA6AF213FE9', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle coarse kosher salt onto the top of the meat at a coverage of about 1 teaspoon per lb.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3651E3F6852B0E46896CDBC35728844C', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 2, NULL, NULL, 2, NULL, 0, 'Place the meat in the fridge for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E46344F96C27C543BD2017011CF09D76', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 3, NULL, NULL, 2, NULL, 0, 'Flip the meat over and dry brine the bottom side with the same coverage of coarse kosher salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAB988CD13A696458B483564706609E8', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 4, NULL, NULL, 2, NULL, 0, 'No need to rinse.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0C1152D664CAE4DB34F6321EECEE325', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 5, NULL, NULL, 2, NULL, 0, 'Season the top and sides of the tri-tip with Jeff''s Texas style rub (purchase recipes here).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67AD0F7E17DF444FB56DF75607B8CF75', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 6, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F using a blend of hickory, maple and apple or whatever smoking wood you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6DD1E4C0842424EAA3DE0749E661C8D', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 7, NULL, NULL, 2, NULL, 0, 'Place the tri-tip on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A175136E5FECA4FA68DAAAD92A0C8CA', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 8, NULL, NULL, 2, NULL, 0, 'Combine butter and rub ingredients and keep the mop warm stirring occasionally while you use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70BC8C94AECDB941B6F221A60018FF69', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 9, NULL, NULL, 2, NULL, 0, 'Mop the meat with this mixture about every 20-30 minutes while it cooks to keep the meat moist.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71D620506FFF4740A696279A174BC560', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 10, NULL, NULL, 2, NULL, 0, 'Let the meat cook for about 2 hours with smoke or until it reaches about 130°F internal temperature as measured by a digital remote meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE61FCC405D7F47A203BD0DA1835ACD', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 11, NULL, NULL, 2, NULL, 0, 'Let the meat rest under foil for about 10 to 15 minutes then slice against the grain at about ¼ inch thick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'262CB37886085B4BA966D1F6C536E029', X'BE9A70E6B55F844AA499F939BE649FCD', X'BE9A70E6B55F844AA499F939BE649FCD', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADE27D881CD8AA42862513CB24D150ED', X'4B032C3788CE4F46978255AC3859BAC8', X'4B032C3788CE4F46978255AC3859BAC8', 0, NULL, NULL, 2, NULL, 0, 'Make the brine in a large 1 gallon pitcher by adding the water, kosher salt and brown sugar.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D711018A502FA41A5B4EE3373C1520E', X'4B032C3788CE4F46978255AC3859BAC8', X'4B032C3788CE4F46978255AC3859BAC8', 1, NULL, NULL, 2, NULL, 0, 'Mix well until all ingredients are dissolved and the liquid becomes a clear amber color.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E7617D96594EC41A9D0A0B87C519CF2', X'4B032C3788CE4F46978255AC3859BAC8', X'4B032C3788CE4F46978255AC3859BAC8', 2, NULL, NULL, 2, NULL, 0, 'Place the chicken(s) into a zip top bag or other plastic or glass container and pour the brine over the chicken to cover. Place the container with the chicken into the fridge for about 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4EC19D2802B1E4BB0841265255672F2', X'4B032C3788CE4F46978255AC3859BAC8', X'4B032C3788CE4F46978255AC3859BAC8', 3, NULL, NULL, 2, NULL, 0, 'At the end of the brining time, rinse the chicken under cold water and pat dry.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ECAA543E69607428E63638EFD0AABE0', X'D9EA61E0790E7B4AACD0D28F011085A4', X'D9EA61E0790E7B4AACD0D28F011085A4', 0, NULL, NULL, 2, NULL, 0, 'Place the chickens over cans of beer, root beer or other soft drink and use the legs to support some of the weight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8855362C06156E49A5D37E8C166C7733', X'D9EA61E0790E7B4AACD0D28F011085A4', X'D9EA61E0790E7B4AACD0D28F011085A4', 1, NULL, NULL, 2, NULL, 0, 'Mix 1 cup of mayonnaise with 3 tablespoons of Jeff''s original rub and apply this mixture all over the outside of the chickens.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E0C323602675948B09CB2CAF36404A4', X'433B255204FE7F4B9AAA51C033E1F6BF', X'433B255204FE7F4B9AAA51C033E1F6BF', 0, NULL, NULL, 2, NULL, 0, 'Preheat the smoker to 275°F and once it''s ready place the chickens on the grate and cook until the internal temperature reaches 165 °F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'495B0B39D2FFC248869256709765CAE5', X'433B255204FE7F4B9AAA51C033E1F6BF', X'433B255204FE7F4B9AAA51C033E1F6BF', 1, NULL, NULL, 2, NULL, 0, 'Rest the chicken for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2DB17DB4DD5254A9A1F17B0BC9CF2A2', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 0, NULL, NULL, 2, NULL, 0, 'Place the corned beef brisket into a pan, fat side down, for ease of cleanup.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'505273D018303D44BC0978C720DB5379', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 1, NULL, NULL, 2, NULL, 0, 'Slather a thin coat of Dijon mustard on the top and sides of the brisket.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93834069EB3A3C468F223BDF0D2BEC2E', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 2, NULL, NULL, 2, NULL, 0, 'Apply Jeff''s original rub (get the recipes here) generously all over the top and sides of the brisket and let it continue to sit until it get''s a `wet` look. (about 10-15 minutes)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'656FC4FB91B2AE489F337E68FEC407F9', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 3, NULL, NULL, 2, NULL, 0, 'Flip the brisket over to fat cap up and repeat the mustard and rub on the fatty side of the brisket.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BAFE16789F0B14794141CC839F2A1CF', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 4, NULL, NULL, 2, NULL, 0, 'Let the brisket sit while you go get the smoker ready for cooking at 240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'203D5587B0A22F4E8D59AA4C39D7F048', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 5, NULL, NULL, 2, NULL, 0, 'Place the pan of brisket on the smoker grate and let it cook until it reaches about 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D41A9ED41AFCC4781DD48B930F2F1D6', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 6, NULL, NULL, 2, NULL, 0, 'Cover the top of the pan with a piece of foil and let it continue cooking with just heat until the meat reaches a temperature of 195-200°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F152FC2EB47F6242856841FCED2C7714', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 7, NULL, NULL, 2, NULL, 0, 'Use a skewer or toothpick to check for tenderness. It should have little to no resistance when inserted.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1702C5995FD1AC44934FE180A312214C', X'7DD2CCE7256F664584EADC854806C077', X'7DD2CCE7256F664584EADC854806C077', 8, NULL, NULL, 2, NULL, 0, 'Let the meat rest with a piece of foil loosely tented over the top for approximately 30 minutes before slicing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7003946F158D544A34CA98941C343EC', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 0, NULL, NULL, 2, NULL, 0, 'Lay the strips of pork belly on a flat sheet pan for dry brining.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'123BB83BD878EC4EAC3D720978E577BF', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle salt on the top side of the meat at about 10% coverage then place in the fridge for 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B484776684701247AA6B4157FE5B341B', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 2, NULL, NULL, 2, NULL, 0, 'Flip the meat over, salt the other side in the same way and place it back in the fridge for another hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'829D47E57B29484894AF959402E61457', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 3, NULL, NULL, 2, NULL, 0, 'After the 2nd hour, the meat is ready to be seasoned with the original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E131F59104C37B4B8A52588F77C82E9E', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 4, NULL, NULL, 2, NULL, 0, 'Season both sides liberally with Jeff''s original rub waiting in between top side and bottom side for the rub to acquire that `wet'' look before flipping them over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71C20CC9580F804CB64BDC353E67D421', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 5, NULL, NULL, 2, NULL, 0, 'Smoke the pork belly slices at 240°F for approximately 90 minutes or until they reach an internal temperature of 200-205 °F as measured by an instant-read hand-held digital thermometer such as the Thermapen or ThermoPop.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'630CA5337659794ABB5CEB29D670EC55', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 6, NULL, NULL, 2, NULL, 0, 'When finished, remove them from the smoker and let them cool for about an hour. You can also place them in the fridge to cool for longer if you are making them ahead of time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63E021B9D0C07C4081B940706BBB9B92', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 7, NULL, NULL, 2, NULL, 0, 'Just before serving, heat a skillet or electric griddle to medium heat and cook both sides of the pork belly slices until they are seared but not blackened.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'760D31A20A8A184BA04F541F3C5B457C', X'84066088993D774C91D916A7ABE5649E', X'84066088993D774C91D916A7ABE5649E', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2908F0D40AFB464F8A8CA712D18481D9', X'3AE425C937F60C48B0DAB0A39FF5A1F0', X'3AE425C937F60C48B0DAB0A39FF5A1F0', 0, NULL, NULL, 2, NULL, 0, 'Make the cream cheese mixture by combining the softened cream cheese, ½ cup grated cheese, 1 tablespoon of Jeff''s original rub and and 2 tablespoons of chopped jalapeños into a small to medium sized mixing bowl. Mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB4F5AE2E1367E438E7117BC4784EC67', X'3AE425C937F60C48B0DAB0A39FF5A1F0', X'3AE425C937F60C48B0DAB0A39FF5A1F0', 1, NULL, NULL, 2, NULL, 0, 'Wrap the half pieces of bacon around the sausage slices and secure with a toothpick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4F35F24D396034192C6BE59FCFACEE8', X'3AE425C937F60C48B0DAB0A39FF5A1F0', X'3AE425C937F60C48B0DAB0A39FF5A1F0', 2, NULL, NULL, 2, NULL, 0, 'Fill the `cups` with the cream cheese but not all the way to the top. The bacon will shrink as it cooks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FA734FBADC7424BB63D399C1CF307EB', X'3AE425C937F60C48B0DAB0A39FF5A1F0', X'3AE425C937F60C48B0DAB0A39FF5A1F0', 3, NULL, NULL, 2, NULL, 0, 'Setup your smoker for indirect cooking at about 250-275°F or as close to this temperature as you can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07FAF0DFA8BEC14C84B3E92EF3E05993', X'3AE425C937F60C48B0DAB0A39FF5A1F0', X'3AE425C937F60C48B0DAB0A39FF5A1F0', 4, NULL, NULL, 2, NULL, 0, 'Place the pig shots on the smoker grate and smoke cook for approximately 90 minutes or until the bacon is cooked through and has good `bite-through` or texture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C0AA4ED2745441958C4EAD7E1B2B25', X'3AE425C937F60C48B0DAB0A39FF5A1F0', X'3AE425C937F60C48B0DAB0A39FF5A1F0', 5, NULL, NULL, 2, NULL, 0, 'When the appetizers are finished cooking, you can glaze with barbecue sauce if you like. I used my original barbecue sauce thinned with a little apple juice. Let the pig shots continue in the heat for about 10 minutes after cooking to allow the sauce to set.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60FDB2064657ED4E8763777076438565', X'3AE425C937F60C48B0DAB0A39FF5A1F0', X'3AE425C937F60C48B0DAB0A39FF5A1F0', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16CE9AFA1EE3604CA3C422C804C4F6F8', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 0, NULL, NULL, 2, NULL, 0, 'Thaw the cornish hens in the fridge for 1-2 days or in a sink of cold water, changing the water every 30 minutes until they are completely thawed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C536B174372CB149BCAD6964B04B99B1', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 1, NULL, NULL, 2, NULL, 0, 'Cut along both sides of the backbone to to remove the backbone and open up the chickens like a book.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'357DDF36CA8E0146AF6073EBB22A0090', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 2, NULL, NULL, 2, NULL, 0, 'To dry brine the chickens, sprinkle about ½ teaspoon of kosher salt on the inside cavity of the birds and place them in the fridge for one hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'510891BB36CACE4A9A3F9051F11D1D05', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 3, NULL, NULL, 2, NULL, 0, 'Remove from the fridge and flip over to skin side up. Sprinkle with about ¾ to 1 teaspoon of coarse kosher salt and place back into the fridge overnight or for 8-16 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDA7B2049C995741B8948FD358302A79', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 4, NULL, NULL, 2, NULL, 0, 'There is no need to rinse the chickens after dry brining.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE8460364F043B4EB7B1B285E849C19D', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 5, NULL, NULL, 2, NULL, 0, 'Make a mayo/rub/herb mixture by mixing (1) cup of mayonnaise, (4) heaping TBS of Jeff''s original rub, (2) TBS chopped parsley and (1) TBS chopped thyme.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C727468D5650784D9C28A50B728919C1', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 6, NULL, NULL, 2, NULL, 0, 'Place equal portions or a heaping TBS of the mixture onto the chicken and spread it all over the top and sides with your hands.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A170A92F38A41048A2E26BCE4BB99AE6', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 7, NULL, NULL, 2, NULL, 0, 'The chicken is now ready to cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E78262109107834F89683D6D47F29ED9', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 8, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 250-275°F and once it''s ready, place the chicken directly on the grates, skin side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66D78F495F554249B2F1486956B66777', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 9, NULL, NULL, 2, NULL, 0, 'Cook for approximately 1 hour or until it reaches 165°F in the thickest part of the breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D2298C57661D4FBC5947526BA97E49', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 10, NULL, NULL, 2, NULL, 0, 'About 10 minutes before the chicken is finished cooking, mix 1 TBS of Jeff''s original rub into a stick (¼ lb) of melted butter and brush that onto the tops of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39D1D33D87671D4C9EF56EA9776E3C55', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 11, NULL, NULL, 2, NULL, 0, 'Once the chicken is finished cooking, remove it from the smoker and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF16AA8CEA53C941AA07304194DBCB7A', X'249C5D400A07C140A4DC50E8DE7EF409', X'249C5D400A07C140A4DC50E8DE7EF409', 12, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEFE3563EAC9D844801E8DE160C49C2D', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 0, NULL, NULL, 2, NULL, 0, 'Trim the fat from the tri-tip using a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5540793124B06740991617C9A691FDF5', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 1, NULL, NULL, 2, NULL, 0, 'To make the brine, pour 4-5 cups of coffee into a ½ gallon pitcher or large bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'632C76F4E739834ABF5E2022D1FBEF7F', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 2, NULL, NULL, 2, NULL, 0, 'Add the kosher salt and stir until it is dissolved. Add the cocoa and red pepper flakes and stir well again to make sure the cocoa is dissolved into the liquid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53D9D2B5117000459C991909B651366A', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 3, NULL, NULL, 2, NULL, 0, 'If the coffee is still warm, it will need to be cooled before use.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8FD8B8939820B4C85C47E5F20886154', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 4, NULL, NULL, 2, NULL, 0, 'Place the meat into a shallow container or even a 1- gallon sized ziptop bag and pour the brine over the meat to cover. Place in the fridge for about 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA470E1961A3742BA789A8977F2CA39', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 5, NULL, NULL, 2, NULL, 0, 'Remove the meat from the fridge after brining. Set the meat aside and discard the brining liquid. There is no need to rinse the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6720FD5BC675C4FA7D0BD1DF95640B1', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 6, NULL, NULL, 2, NULL, 0, 'Season the top side of the meat and the edges with Jeff''s Texas style rub, flip it over and season the bottom side as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'638380D009C4874392F3155C7E6619E2', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 7, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F using indirect heat. If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5243B9578C51D94089F07E7948258885', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 8, NULL, NULL, 2, NULL, 0, 'Once your smoker is heated to 225-240°F and holding steady, Place the meat in the smoker and let it cook for approximately 2 hours with at least 1 hour of smoke action.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0DEDB5227F85E458E4FD36E22821ED0', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 9, NULL, NULL, 2, NULL, 0, 'At about the 110-115 degree mark, melt a stick of butter (¼ lb) and stir in 1 heaping TBS of the Texas style rub. Baste the meat liberally with this mixture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EC1499E348D4F47AC408F047C2EF84F', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 10, NULL, NULL, 2, NULL, 0, 'When the meat reaches 137°F or a perfect medium rare, remove it from the smoker and let it rest with foil tented over the top on the cabinet for about 10-15 minutes before slicing or cutting into it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9D619FC04EAA941A67206406F04E548', X'D802667D91123C43B055398EA71512AA', X'D802667D91123C43B055398EA71512AA', 11, NULL, NULL, 2, NULL, 0, 'Slice the meat pencil thick across the grain and serve immediately with plenty of vegetables and sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1FFDA68B01B7742A21A36AD7719D0DB', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 0, NULL, NULL, 2, NULL, 0, 'Clean and deseed the jalapenos then cut them into ¼ inch strips. Cut the block of cream cheese into 8 equally sized pieces. Set aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A0535D369EA3242B38A78AABD14A405', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 1, NULL, NULL, 2, NULL, 0, 'Clean the fat from the boneless, skinless chicken thighs. Lay them flat on the cutting board and season liberally on the inside with Jeff''s original rub (purchase recipes here).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5B46E01D67AB24BBBD1FD2D8F6BBAA4', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 2, NULL, NULL, 2, NULL, 0, 'Lay a piece of the cream cheese and 2-3 pieces of the jalapeno strips in the center of each thigh and fold over to close.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1EB275EA14A334782C2530901A11141', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 3, NULL, NULL, 2, NULL, 0, 'Wrap a piece of stretched bacon around each chicken thigh to hold it closed. Place the stuffed and wrapped thighs into the holes of a muffin pan to help them hold their shape while they cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'846CF37C0C871E4BA1D5AC79059A7181', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 4, NULL, NULL, 2, NULL, 0, 'Season the top of the thighs with Jeff''s original rub (purchase recipes here) and lay a small pat of butter on top of each.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6EFAA0C6BBC0A43B75C7A20C45F8540', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 5, NULL, NULL, 2, NULL, 0, 'Preheat the smoker to 275°F using indirect heat and hickory or pecan for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B5042C9633CBD4A99B28EEB612B9D26', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 6, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the pan of chicken onto the grate of the smoker and let them cook for about 40 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'488A6E74069D2947BEA349EF54A01987', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 7, NULL, NULL, 2, NULL, 0, 'After 35-40 minutes, cover the pan with a piece of foil to tenderize the chicken and let them continue cooking for about 20-25 minutes or until they reach 170-175°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9069841F187BE4180CF191A022F5236', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 8, NULL, NULL, 2, NULL, 0, 'When they are finished, remove from the pan and lay them on a cooling rack, cookie sheet or Weber grill pan. Coat with Jeff''s barbecue sauce (purchase recipes here) and continue cooking for about 10 minutes to let the sauce set.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BDCC7C2C6CD464989DA8C99A4FB42D8', X'6E8588F6CE1F8F4F8FEC9146309E168C', X'6E8588F6CE1F8F4F8FEC9146309E168C', 9, NULL, NULL, 2, NULL, 0, 'Remove from the smoker and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F564E6EA00F045AD85A470ABD9380C', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle coarse kosher salt onto top sirloin steaks and place into the fridge for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E72E7FDFF4B937459CC6635A8EA43BA1', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 1, NULL, NULL, 2, NULL, 0, 'Remove from fridge, flip them over and repeat salting on the other side. Leave steaks out to warm to room temperature for 1 hour. No need to rinse.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A4184F40D835245BE7F45D352F868EB', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 2, NULL, NULL, 2, NULL, 0, 'Dry steaks well with paper towel then sear on all sides in heavy skillet over medium high heat with just a couple of TBS of vegetable oil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'670A5AE6A3DB984BBBFF9EE09BCCE2EA', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 3, NULL, NULL, 2, NULL, 0, 'Let the steaks cool for a few minutes then coat with Jeff''s Texas style rub on all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45419833C8441641B82CF4B93A097C20', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 4, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at 230°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79F2E2375DBADF49A8D6A90BDE12311C', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 5, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, place the steaks on the grate and let them smoke cook with oak or whatever smoking wood you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DBA1EA218CA8244B54BE10AE08C2EEC', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 6, NULL, NULL, 2, NULL, 0, 'At about 30 minutes in, baste the steaks with a concoction of ½ cup low sodium soy sauce, ½ cup Worcestershire, and ¼ cup of olive oil. Stir the ingredients constantly as you use it to keep them from separating.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D20101EABC8CBE48B8CDCA799A3B6DDF', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 7, NULL, NULL, 2, NULL, 0, 'When the steaks reach 137°F as measured with a meat thermometer, remove them from the smoker and let them rest for 4-7 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F5470ADD4D01C4699FB9C05B4BA729C', X'848EF4FBC4AD2B46ACF927E5E462EE00', X'848EF4FBC4AD2B46ACF927E5E462EE00', 8, NULL, NULL, 2, NULL, 0, 'They can be sliced into ¼ inch slices or served as whole steaks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'966BD4FB29A98544BFD866C86B636EF1', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 0, NULL, NULL, 2, NULL, 0, 'If meatballs are frozen, place them in the fridge for a few hours or up to a day to thaw.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9682BF5E9E31B44A935078D6ABBDA0BF', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 1, NULL, NULL, 2, NULL, 0, 'Wrap half pieces of bacon around each meatball and secure with a toothpick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BD6ED29634B4042BD4BC4067EAD705C', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 2, NULL, NULL, 2, NULL, 0, 'Place the meatballs on a Bradley rack, Weber grill pan or a cooling rack to make them easy to carry to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2101915177A9584CA7A8DB3ACDD38D33', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 3, NULL, NULL, 2, NULL, 0, 'Season on all sides with a low salt barbecue rub such as my original rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A8B65B778E7240A380364EBA2FCE99', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 4, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 240°F using indirect heat and hickory or pecan for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF00B49D450DD94EA6EAAF06B181E4CA', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 5, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, fill it up with hot water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67A92BD182581B4DB3AC91D9900FF7F5', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 6, NULL, NULL, 2, NULL, 0, 'When the smoker is ready to go place the wrapped meatballs on the smoker grate and let them cook for 2 hours or until they reach an internal temperature of 160 and the bacon is firm.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB107D9DDEF94E4B8809423972DED209', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 7, NULL, NULL, 2, NULL, 0, 'When the meatballs are finished or nearly finished cooking, glaze the outside of the meatballs with barbecue sauce thinned with beer, juice, melted butter, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A444D5C3B31162428E25A2BA4C866097', X'29753A14C0BC3841AB82AE424F306E15', X'29753A14C0BC3841AB82AE424F306E15', 8, NULL, NULL, 2, NULL, 0, 'Let the glaze set for about 10 minutes then bring them in and serve right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08BFD618B8058947B5129BF359EFE3E8', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 0, NULL, NULL, 2, NULL, 0, 'Rinse pork tenderloins with cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAD4DDBCAEA78B42A961E91E31E69582', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 1, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to remove any extra fat and silver skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEEEF78E63373C479D99E89F2C860FDC', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 2, NULL, NULL, 2, NULL, 0, 'Coat the outside of each tenderloin with a tablespoon of mayonnaise.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F04B7AB291ABFB45A64B0CB3C19F7866', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 3, NULL, NULL, 2, NULL, 0, 'Generously sprinkle Jeff''s original rub on to all sides of the pork tenderloins.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BE2F98CFEA2ED4990D13366EDEA0363', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 4, NULL, NULL, 2, NULL, 0, 'Prepare your smoker for cooking at 225-240°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'886D3AE4D6F7ED46879463488EF63B03', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 5, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, fill it up with water or liquid of your choice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FCCE1E4C5CE9848A569A0CB7EB0419C', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 6, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, place the pork tenderloins on the smoker grate and close the door/lid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A982AF23B0F61948B9567ED175033597', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 7, NULL, NULL, 2, NULL, 0, 'Prepare the maple barbecue glaze by mixing 1 cup of maple syrup to 2 heaping tablespoons of Jeff''s original rub. Stir well to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC7661CC4C59BD448BA41A671014C01D', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 8, NULL, NULL, 2, NULL, 0, 'Baste the meat with the glaze every 30 minutes until it is finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72332F4E88453B43A1BA63554D8202F8', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 9, NULL, NULL, 2, NULL, 0, 'When the pork reaches 145°F as measured with a digital meat thermometer, it is perfectly done and should be removed from the smoker right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5666D0AA696CC445945794CCCDA68223', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 10, NULL, NULL, 2, NULL, 0, 'Allow the meat to rest under foil for about 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24185E19090ACD48B9483F3CF90D619F', X'FC2E41050F5A044B89B672559A0DCDD4', X'FC2E41050F5A044B89B672559A0DCDD4', 11, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DFD2616744CA14EAD38F314DD7E099A', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 0, NULL, NULL, 2, NULL, 0, 'Remove the rind from the pork belly using a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5184470F5C5404E835FD863706B2E35', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 1, NULL, NULL, 2, NULL, 0, 'Place the pork belly in the freezer for 30 minutes to allow the fat to get really solid so it will cut more easily.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'122F765E3207DA4CA18A2DA3ED3E9B03', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 2, NULL, NULL, 2, NULL, 0, 'With the pork belly laying in front of you on a cutting board, slice the pork belly into pieces that are about 1.5 inches wide. Further cut the slices into pieces that are 1.5 inches wide giving you cubes of meat that are 1.5 x 1.5 inches square.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D6B1A2F5B15B54CB418B9DCD7C58B49', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 3, NULL, NULL, 2, NULL, 0, 'Place ⅓ cup of Jeff''s original rub into a zip top back and then place 2 lbs of meat into the bag. Shake and massage bag to coat the meat with the rub. Pour the coated pieces of meat out onto a cutting board or bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A96784639A9BD43A3ACFE75F928DD37', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 4, NULL, NULL, 2, NULL, 0, 'Arrange the pieces of meat onto a cooling rack, Bradley rack or Weber grill pan to make them easy to move into and out of the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'186FB15BB2E5D2468F4F01E9F127E30B', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 5, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240 °F using indirect heat. If your smoker has a water pan, fill it up. When the smoker is ready, place the racks of meat onto the smoker grate and quickly close the lid/door.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'695621F1AB9C0F4FB9231714563E2796', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 6, NULL, NULL, 2, NULL, 0, 'Let the meat cook and smoke until a crust forms and the bark on the outside of the meat starts to turn to a light mahogany.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0355C32769D2439BAEF4946246339E', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 7, NULL, NULL, 2, NULL, 0, 'Place the meat into a foil pan and coat with about ¼ to ⅓ cup of honey and ¼ to ½ cup of Jeff''s barbecue sauce per 2 lbs of meat. Optional, you can omit the barbecue sauce and add 2-3 heaping tablespoons of hot pepper jelly with the honey. Mix the glaze with the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33CBFE59DB36B34BB41B502795943302', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 8, NULL, NULL, 2, NULL, 0, 'Cover the pan with foil and place back into he smoker for an additional 60-90 minutes or until the meat reaches 200°F. (no smoke required).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF08866082EBCC46992AFFA0E5A0C507', X'FAECDAD28D8E014DA33F5818D70C5989', X'FAECDAD28D8E014DA33F5818D70C5989', 9, NULL, NULL, 2, NULL, 0, 'When the pork belly burnt ends are finished cooking, remove the foil and let them cool for a few minutes before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98DE20079C7E324EB807B94026179952', X'147289481AED9549AD098A911FC1CA43', X'147289481AED9549AD098A911FC1CA43', 0, NULL, NULL, 2, NULL, 0, 'Trim fat and silver skin from the top of the beef short ribs. Sprinkle about 1 to 1.5 tsp of coarse kosher salt onto the top of the beef ribs to and place them in the fridge for 4 hours. When 4 hours is up, there is no need to rinse. Generously sprinkle my Texas style rub onto the top and sides of the ribs and let them sit while you go get the smoker ready. Fire up the smoker and let it preheat to 240°F using indirect heat. If your smoker uses a water pan, fill it up. Place the seasoned beef short ribs into the smoker on a shallow pan. Make the basting sauce using 2 cups of root beer, 1 cup of low sodium soy sauce and 1 cup of Worcestershire. Pour the basting sauce over the meat and let it run down into the pan. Continue basting the meat with the sauce from the pan using a turkey baster every 30-45 minutes while it cooks in the smoker. When the beef short ribs are fork tender they are finished cooking. This is usually at 190-195°F but can be lower or higher than that. Let the meat rest under foil for about 10 minutes before slicing and serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24D49A0BE7FFA54AB54AE61E85D40AB8', X'F62E8E4023B39743A94CBE132AC7E14C', X'F62E8E4023B39743A94CBE132AC7E14C', 0, NULL, NULL, 2, NULL, 0, 'Using ⅓ lb of beef, form balls in your hands. Smash them into patties that are about the same size as your buns. Use your thumb to dimple the center of the burger patty. Place the burgers in the fridge until ready to use them. Once ready to use, sprinkle the top side of the burgers generously with Jeff''s Texas style rub recipe. Preheat smoker to 225 using indirect heat. Place burgers on smoker grate and smoke for about 1 hour or until they reach 150°F in the center. While the burgers are smoking, prepare all of the vegetables, grill the onions if desired and grill the buns. Set up a very hot gas or charcoal grill and when the burgers reach 150°F, sear both sides for about 1 minute. Just before the burgers are done, lay a piece of cheese over the meat so it can melt. Build the burgers or let each person make their own.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E31C01599002EF439553C904E5791FD3', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 0, NULL, NULL, 2, NULL, 0, 'Rinse chicken drumsticks with cold water and pat dry with a paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7FA0508BF76ED429FE2F1875EFF3C87', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 1, NULL, NULL, 2, NULL, 0, 'Place chicken on a wire rack or sheet pan and place in fridge for several hours to help dry the skin. This helps to ensure a better texture in the finished skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D13329E1A423364FAA4A3D2C7C74C4D3', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 2, NULL, NULL, 2, NULL, 0, 'While the chicken is drying, mix up the Alabama white sauce using 2 cups of mayonnaise, 1/2 cup of apple cider vinegar, 1/4 cup of finely ground black pepper and 1 teaspoon of cayenne pepper. Mix until smooth and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D616DD2B682C584D8CE40DBA6E8E2F57', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 3, NULL, NULL, 2, NULL, 0, 'Once you are ready to season the chicken, brush some of the Alabama white sauce onto the chicken then coat the outside of the chicken with Jeff''s original rub. This can be done the night before or 8-10 hours in advance if desired.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E292BA404A096B409A94F050829CDFEF', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 4, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at 225-240°F using indirect heat. If your smoker has a water pan, leave it dry if possible to reduce humidity and help the chicken skin to end up with a better texture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E588BB599D9FB479CDDD2DDEE686176', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 5, NULL, NULL, 2, NULL, 0, 'Place the chicken on the smoker grate and let it smoke with a mix of maple, cherry and hickory or whatever smoking wood you have available. Leave the chicken alone for about 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BEA653B8863E54FA7A1A873A840AD68', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 6, NULL, NULL, 2, NULL, 0, 'After 30 minutes you can crank the heat up to about 275°F if your smoker is capable of higher heat otherwise, maintain normal smoking temperatures.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7981AB440A72744E9EC6B5A3FED537EF', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 7, NULL, NULL, 2, NULL, 0, 'We will also begin brushing the Alabama white sauce on the chicken every 15 minutes until it is finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A23742C0179074439CD5330251675CC9', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 8, NULL, NULL, 2, NULL, 0, 'When the chicken reaches 180°F as measured by an accurate meat thermometer, it is finished and should be removed from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A46858378A50DE4EBF20D1F8CB499988', X'C8B4A1EA82687C4681687FBC68B515B7', X'C8B4A1EA82687C4681687FBC68B515B7', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with more white sauce for dipping.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67841E2E011FD9419530DE60444D5AD3', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 0, NULL, NULL, 2, NULL, 0, 'Wash the jalapeños');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8BA6E1554652842BF11DF18EF747496', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 1, NULL, NULL, 2, NULL, 0, 'Cut off the stems and remove the seeds and veins');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D7FFD4A8C489044A8A9CE41A4E22353', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 2, NULL, NULL, 2, NULL, 0, 'Cut the peppers in half lengthwise so they become boats.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69F79CB75D37BD4185731565AFAD2540', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 3, NULL, NULL, 2, NULL, 0, 'Combine 16 oz of cream cheese, 2 cups of shredded extra sharp cheddar and ½ cup of Jeff''s original rub into a large bowl. Mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99BD65675DD52C488377821BADCBF031', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 4, NULL, NULL, 2, NULL, 0, 'Stuff the mixture into the boats just slightly heaped up over the edges.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD8CACBFBFB87E4C9B47885C09299559', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 5, NULL, NULL, 2, NULL, 0, 'Cut the bacon slices in half and wrap a half slice of bacon around each stuffed pepper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F89325A8C9187C43BFC1FD2795097658', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 6, NULL, NULL, 2, NULL, 0, 'Place the stuffed, bacon wrapped jalapeños onto a Bradley rack for easy transport into and out of the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B424C2D4E0782F4F9B2ACF9EC6D6C269', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 7, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 230°F and enough wood for about 2 hours of smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1537E67A4289D43A827464FDB227D47', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 8, NULL, NULL, 2, NULL, 0, 'Smoke cook the stuffed jalapeños for 3 hours or until the peppers are wimpy and the bacon is done and has good bite thru.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8892AF50F377B144B6B8663C6D8E9A5D', X'19A7A2792460D243B8D7888A60C1FB37', X'19A7A2792460D243B8D7888A60C1FB37', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9817FC322499614EB1ECDE001C815516', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 0, NULL, NULL, 2, NULL, 0, 'Remove the turkey roast from it''s plastic net and packaging but leave it in the cotton net. Rinse the meat, net and all, under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0250DBBE18475D4AB1953BADFF1F1DBF', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 1, NULL, NULL, 2, NULL, 0, 'Set the roast aside and make a brine solution of ½ gallon of water, ½ cup coarse kosher salt and ½ cup brown sugar. Mix well until everything is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0539A632BC7AD84AA8B5B6121D78C2FE', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 2, NULL, NULL, 2, NULL, 0, 'Place the netted roast down into the brine and place it in the fridge for 4 hours. After 4 hours, remove the meat from the brine and rinse well under cold water. Pat dry with a paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'708E0D4C0B6D2244BF1F28AD1544CA16', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 3, NULL, NULL, 2, NULL, 0, 'Pour olive or vegetable oil all over the roast making sure it is well coated. Sprinkle Jeff''s original rub onto all sides of the roast-- be generous.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF38ACA01416E046AB22370338842AAE', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 4, NULL, NULL, 2, NULL, 0, 'Place 3-4 pieces of bacon into the net if you are planning to hang it in your smoker or place the roast on a rack with the bacon draped over it if you plan to cook it on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D4D5B19F1F7DB4AA881B5EB3E711A39', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 5, NULL, NULL, 2, NULL, 0, 'If hanging on a Bradley rack, push the net through one of the holes and thread a chopstick through to hold it in place. You can also use a spoon handle or other similar object.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD36D142E876544690A83B820CF67D5F', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 6, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B36BFD40A46C0941BC941358EA0A34F2', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 7, NULL, NULL, 2, NULL, 0, 'Place the meat in the smoker and close the lid/door. Keep the smoke going for about 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB18D15EE59444889F7052F5BD9F222', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 8, NULL, NULL, 2, NULL, 0, 'The turkey roast should take about 5-6 hours to reach 160°F at which point it should be removed and allowed to rest for 10-15 minutes with foil tented over it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DA2F4FC2F14B547816F7E4DD9362E0B', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 9, NULL, NULL, 2, NULL, 0, 'Once rested, cut the net and carefully remove it from the roast. Slice the meat as thick as you like and serve immediately with mashed potatoes, corn, peas, gravy and all of the fixins.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C672B2B3AACC7843B7A1D0D384C85744', X'D147000C7E93624887ACAA80310E63F8', X'D147000C7E93624887ACAA80310E63F8', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CF94313F3E7874CB7278DFB7BEECDEB', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 0, NULL, NULL, 2, NULL, 0, 'Remove ribs from package and rinse under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B37D916D38E3347B6DB54570C44BAF5', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 1, NULL, NULL, 2, NULL, 0, 'Cut brisket bone and square ends to make them St. Louis style spare ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE5B8923ED31F64093A48E1448307F84', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 2, NULL, NULL, 2, NULL, 0, 'Remove membrane.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8FB2E15075D4E40B02005687CB69CFB', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 3, NULL, NULL, 2, NULL, 0, 'Generously coat bone side of ribs with Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C686B0603E472D4A8704A15913F7B115', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 4, NULL, NULL, 2, NULL, 0, 'Wait about 10 minutes for the rub to take on a `wet` look. This means the rub has mixed with the rib juices and will stick to the ribs without falling off when you turn them over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268A7538924F4D4E976C5EF4A41480EB', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 5, NULL, NULL, 2, NULL, 0, 'Flip the ribs over to meaty side up and coat the top and sides with generous amounts of Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76E3F850EE732243915EB9DB65D4FD90', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 6, NULL, NULL, 2, NULL, 0, 'With the ribs on a cookie sheet, place them in the fridge overnight to allow the flavors in the rub to seep into the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'863536460EF54E4A9E887F9FBF4365ED', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 7, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 230 °F with indirect heat. If your smoker has a water pan, fill it up with hot water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CEFD0D23748484A894E1022FF603D42', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 8, NULL, NULL, 2, NULL, 0, 'Follow this cooking schedule:');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39C0D46FC735984DB5C43A820FD19521', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 9, NULL, NULL, 2, NULL, 0, '-4 hrs @ 230°F - laid on grate with pecan/cherry smoke');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'221C261C5A76DF4D98525BC057A1CD01', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 10, NULL, NULL, 2, NULL, 0, '-2 hrs @ 230°F - wrapped (no smoke required)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73958B903DD1AF468BF1CDE789114730', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 11, NULL, NULL, 2, NULL, 0, '-2hrs @ 200°F - wrapped (no smoke required)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D8880621EA093499352358E55B9D93B', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 12, NULL, NULL, 2, NULL, 0, '-1.5 hrs @ 170°F - wrapped (no smoke required)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08BCAF43A0737145BFEBBECAF42F39B7', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 13, NULL, NULL, 2, NULL, 0, 'Remove the ribs from the foil and pull the bones out of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4E6C4FE73C8674DBAA9AC0CA5828F75', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 14, NULL, NULL, 2, NULL, 0, 'Slice the meat into ribs that are about 1-inch wide.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53B3E2FD6DDC6A43AB86B2B6CAA2E49A', X'E795150F7075164E86CFA501F5AC60F0', X'E795150F7075164E86CFA501F5AC60F0', 15, NULL, NULL, 2, NULL, 0, 'Serve right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CC8A5B91120BD4DAC04279DBA777212', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 0, NULL, NULL, 2, NULL, 0, 'Place the chicken wings on a cooling rack over a sheet pan and pat dry with a paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D680181178D68749B907949D626C3EE0', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 1, NULL, NULL, 2, NULL, 0, 'Set the chicken in the fridge to allow the skin to dry for about 4 hours or optimally overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E036B641A81F4EB3F5495645000901', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 2, NULL, NULL, 2, NULL, 0, 'Apply a coat of mayonnaise all over the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B013B52ABF8D845A01D422DAD5F581E', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 3, NULL, NULL, 2, NULL, 0, 'Season the chicken wings liberally with Jeff''s original rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB2F244CDD6A7A4BA46292BABB13B8F5', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 4, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 225-240°F using indirect heat and hickory wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F83098D277440B499D739A22172474DA', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 5, NULL, NULL, 2, NULL, 0, 'Place the chicken wings directly on the smoker grate and let them cook for about 1.5 hours or until they reach 165°F in the thickest part of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'214F280E0E54F74FBE278CE32A380102', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 6, NULL, NULL, 2, NULL, 0, 'Make a glaze of ½ cup Smucker''s apricot simplyfruit® or any apricot spreadable fruit and ½ cup honey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'098F5178C1634C47A68185047DFC1DFF', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 7, NULL, NULL, 2, NULL, 0, 'Heat the ingredients in a small saucepan to help the mixture to combine more easily.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F0EBCF6E78C9044B381C111389E7BA9', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 8, NULL, NULL, 2, NULL, 0, 'Once the glaze is finished, brush it liberally onto the chicken wings.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6ACBB225151074DA384FBB26216BBB5', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 9, NULL, NULL, 2, NULL, 0, 'Let the wings continue cooking until they reach about 175-180°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C79E6344B50E24CB381CA6F2DF821B7', X'8AFE9D3B7C611740B43646254737A78B', X'8AFE9D3B7C611740B43646254737A78B', 10, NULL, NULL, 2, NULL, 0, 'Serve the wings immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'961D06AF71F8D74AB84C7C9168C8A8EA', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 0, NULL, NULL, 2, NULL, 0, 'Place lamb loin chops on a cooling rack or cookie sheet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4D6E2C3535F024EB932D1BAC5887DD7', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 1, NULL, NULL, 2, NULL, 0, 'To dry brine, sprinkle salt generously on the top of the chops.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF236A7A70DED48A0B5522BB2A03CD6', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 2, NULL, NULL, 2, NULL, 0, 'Place the chops in the fridge for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A07BF0654CB72459A8A456ECD39F3C9', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 3, NULL, NULL, 2, NULL, 0, 'Remove the chops from the fridge, no rinsing is necessary.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A497EC337F4E194CBA5510CC7E49037A', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 4, NULL, NULL, 2, NULL, 0, 'Make an infusion of rosemary and olive oil by pouring ¼ cup of olive oil over 1 tablespoon of chopped rosemary. Let the infusion sit for about an hour before you use it if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A382370A0B6AD24A97602923A0B151C9', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 5, NULL, NULL, 2, NULL, 0, 'Brush the olive oil/rosemary on the top and sides of the lamb chops.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD39BCC247D99643AC7C2DA29F58B39D', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle the top, bottom and sides of the chops with Jeff''s original rub. Be as generous as you like.. it''s low in salt and big on flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4856AE4EAD3E69439B80ED475E2417EF', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 7, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8D5F12C77EB6244B3FB0B4133E423B3', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 8, NULL, NULL, 2, NULL, 0, 'Use a mix of pecan and apple for smoke for great results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8CDA934CA94DC4CA34541A7E233473E', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 9, NULL, NULL, 2, NULL, 0, 'Monitor the internal temperature of the chops and when they reach 138 °F (medium rare) they are finished and should be removed from the smoker right away. This should take about 50 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBEDB48149CAE44BA8652BEA2BA14BB9', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 10, NULL, NULL, 2, NULL, 0, 'Let the chops rest on the counter with foil tented over them for about 5-7 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9597BAB9F88404DBF61F0275A0748B2', X'725B204F657E6A4F9D561746461C164C', X'725B204F657E6A4F9D561746461C164C', 11, NULL, NULL, 2, NULL, 0, 'Serve with a couple of Easter worthy sides and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'446C259E2C5C194F86852DC1FF3B0766', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle coarse kosher salt onto the top side of the tomahawk steaks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D8B880ED480AB4283245FE4B81B8D97', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 1, NULL, NULL, 2, NULL, 0, 'Set the steaks on a cooling rack over a large pan in the fridge for 2 hours to let the salt melt and soak in.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CFB348DA158B54780243BBA9CCB365D', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 2, NULL, NULL, 2, NULL, 0, 'Flip the steaks over and repeat the salt and fridge time for 2 hours on the reverse side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F354BA02F2235141B30C71F4A7F603E1', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 3, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto the top and sides of the steak and season liberally with Jeff''s Texas style rub. Repeat the oil and seasoning on the reverse side of the steaks and they are ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14512B0354B53D4A959D1A76614034D0', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 4, NULL, NULL, 2, NULL, 0, 'Preheat the smoker to 225°F and once it''s ready, place the steaks directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CB8B34431BA5241B39F01A5BC4230B1', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 5, NULL, NULL, 2, NULL, 0, 'Let the steaks cook for about 1.5 hours or until they reach 100-110°F in the center.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFF803E108A2E4489647D8C6470BD9F0', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 6, NULL, NULL, 2, NULL, 0, 'At this point, sear the steaks on a hot grill, over live coals or under the broiler of your oven. The idea is to get a good browning on the outside of the steaks for flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'394566961FE2714D86E352F0317176DA', X'2249AE76CCD370499D788893AC2BB255', X'2249AE76CCD370499D788893AC2BB255', 7, NULL, NULL, 2, NULL, 0, 'Let the steaks rest under tented foil for 10 minutes then slice ¼ inch thick and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7BC20708AF38A449B93AED98AEE68F2', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 0, NULL, NULL, 2, NULL, 0, 'Put the country style ribs in a foil pan');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4337C9815B96848BFF4F9754011381A', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 1, NULL, NULL, 2, NULL, 0, 'Spray the top of the ribs with Cherry Dr Pepper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA2B94A40E02D48BD2B4498D75EB732', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 2, NULL, NULL, 2, NULL, 0, 'Generously sprinkle the original rub on the top of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6FA99F637513141A8E08675A4A6AD3C', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 3, NULL, NULL, 2, NULL, 0, 'Turn the meat ¼ turn and repeat the Dr Pepper and rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6230E3BEB951BA4A9E28CB32A36AB987', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 4, NULL, NULL, 2, NULL, 0, 'Repeat the process 2 more times so that all 4 sides of the country style ribs are coated with rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B77FE46A3E087A4A998126D3F71D1245', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 5, NULL, NULL, 2, NULL, 0, 'Set up the smoker for cooking at about 240°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07EBD32F3AC995428A2DC919A247EC3B', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 6, NULL, NULL, 2, NULL, 0, 'Place the meat directly on the smoker grate and close the lid/door.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52C6FE4613570F49BFF87B9683196D75', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 7, NULL, NULL, 2, NULL, 0, 'Let the meat smoke with mesquite or hickory wood for about 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41506DD6448E934F93E3283E759C5E1D', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 8, NULL, NULL, 2, NULL, 0, 'If you have the ability to crank up the heat on the smoker, take it up to about 275 and let the country style ribs cook until they reach an internal temperature of 185°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CE84DE350FA224C8A1AED4B878E50DF', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 9, NULL, NULL, 2, NULL, 0, 'Mix equal parts Cherry Dr Pepper with Jeff''s original barbecue sauce and stir well to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D79EA56AEBEB5949A7DF49FA43D8CE4F', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 10, NULL, NULL, 2, NULL, 0, 'Brush the sauce onto the ribs and leave them for about 10 minutes to let the sauce caramelize.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB5010DD0FF74C479A81EDAB1FFEFF3A', X'89CFAF63353D4346985F5D31BB86EE2B', X'89CFAF63353D4346985F5D31BB86EE2B', 11, NULL, NULL, 2, NULL, 0, 'Remove the meat from the smoker and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C0B194CE4F456448FA7ECD7CAAC6435', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 0, NULL, NULL, 2, NULL, 0, 'Remove the corned beef brisket from the package.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C21E07F9FC0604F80C86EA374F3C833', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 1, NULL, NULL, 2, NULL, 0, 'To remove extra salt, soak the meat in cold water in the fridge, changing out the water every 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D982D1747DB9C442A35799A5F74191E1', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 2, NULL, NULL, 2, NULL, 0, 'Three to four hours should be plenty of soak time to remove the extra salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9241363E167CB041A8D8E792FE81FB9A', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 3, NULL, NULL, 2, NULL, 0, 'Remove any excess fat on the non fat cap side of the brisket.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C4D75CD59DDF0459325BF286908A53F', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 4, NULL, NULL, 2, NULL, 0, 'Coat the entire corned beef brisket with horseradish mustard then apply Jeff''s original rub liberally to all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5DE39917472194BA0D2180264885C40', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 5, NULL, NULL, 2, NULL, 0, 'Pat the rub down as it is applied rather than massaging it in.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'238D76260D6527438C737BE9EABEA930', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 6, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at 225°F with hickory smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39059F3A25E6054688F4FB24B9839B37', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 7, NULL, NULL, 2, NULL, 0, 'Place the corned beef brisket on the smoker grate and smoke cook for 2-3 hours or until it reaches 130-140°F in the center of the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF891457E40FDD4B90CB6601FEE64E38', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 8, NULL, NULL, 2, NULL, 0, 'Make a mustard sauce by mixing equal parts of Jeff''s original barbecue sauce with horseradish mustard such as Dijon or Grey Poupon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'234DDAA3FCDC4A4097159D1B8F2ED1E7', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 9, NULL, NULL, 2, NULL, 0, 'Create a pad of mustard in the bottom of a pan and lay the meat on the pad fat cap side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF7AFE44930F05448A5F06C27ECB5758', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 10, NULL, NULL, 2, NULL, 0, 'Brush more of the mustard sauce onto the fat cap and cover tightly with foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AA240F5E797F14E8634925BC83E3066', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 11, NULL, NULL, 2, NULL, 0, 'Place the meat back into the smoker at 225°F and continue cooking until it reaches 185°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'301722B4285E4F4085FA525127E42824', X'D7659B10EB133F49B438BDDDC52D1703', X'D7659B10EB133F49B438BDDDC52D1703', 12, NULL, NULL, 2, NULL, 0, 'Let the meat rest under foil for 30 minutes then slice pencil thick and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86C8F2EA2C5D3944953D619B067287DE', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 0, NULL, NULL, 2, NULL, 0, 'Make the buttermilk brine by mixing 1 quart of buttermilk with ¼ cup of coarse kosher salt. Stir in salt until dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5BC4E9AAAAE7F45AAAB70FEF948EFF7', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s original rub generously on one side of the chicken tenderloins and stir them around to coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A17DF13D9FCA8428813E5E8554BEDC5', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 2, NULL, NULL, 2, NULL, 0, 'Pour brine over chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72A92434F5A41640BA52FB4A38E1ED80', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 3, NULL, NULL, 2, NULL, 0, 'Place the chicken in the fridge for 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D914727F7832FE4D901BAB601E9655C4', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 4, NULL, NULL, 2, NULL, 0, 'Remove the chicken from the fridge and rinse the chicken well under cold water. Discard the brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EECFE3078C403A4AA6C1F2BCE528C8BD', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 5, NULL, NULL, 2, NULL, 0, 'Lay the chicken pieces on a flat surface and sprinkle Jeff''s rub on both sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDC797293064BA42B3B37790FB2D23FB', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 6, NULL, NULL, 2, NULL, 0, 'Place the chicken in the smoker and smoke for about 1 hour or until they reach 165 °F as measured by a digital meat thermometer such as the Thermapen.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23C175DE929A7A49B9D388A3DB41ABF1', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 7, NULL, NULL, 2, NULL, 0, 'Cut or shred the chicken into small pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1798E362D001594FAD1E71F9B375E71B', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 8, NULL, NULL, 2, NULL, 0, 'Add 1-2 TBS of Jeff''s original barbecue sauce to 1 cup of shredded chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F53D3B5B048A4192528D213E1EE3D0', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 9, NULL, NULL, 2, NULL, 0, 'Place the Tostitos® Scoops!® on a cookie sheet and fill with a small amount of chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BF6A9914D93C04493A4E6A283F204FB', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 10, NULL, NULL, 2, NULL, 0, 'Cover the top of the chicken with shredded cheese and place under the broiler for a minute or two to melt the cheese. Make sure it does not burn.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92F89D4F88B7B244B7ECE2EFCBE222C6', X'514304E50C735B4DBDD4C1637EF76C88', X'514304E50C735B4DBDD4C1637EF76C88', 11, NULL, NULL, 2, NULL, 0, 'Add sour cream, guacamole, jalapeños, chopped tomatoes, salsa, etc. on top of each chip and serve right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1814AD0C16282A44A5CBA1B0BC59513F', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 0, NULL, NULL, 2, NULL, 0, 'Peel and devein shrimp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1983C3DC3489849ADF11FF234A66F11', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 1, NULL, NULL, 2, NULL, 0, 'Place shrimp into a foil pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC9ED47275D7B448F82A47A6683B308', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 2, NULL, NULL, 2, NULL, 0, 'Add ¼ cup of chopped parsley to the top of the shrimp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B00F3126C779E42AB5761E1D100137D', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 3, NULL, NULL, 2, NULL, 0, 'Add ½ chopped poblano or 1 chopped jalapeno to the top of the shrimp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55E2A6C4A664A447B0559E7B03A3CDF6', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 4, NULL, NULL, 2, NULL, 0, 'Cut the lemon and orange in half and squeeze the juice from the halves over the shrimp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'239B60B1ED7C9D46B2E975738F9B4BA7', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle about 2 TBS of Jeff''s original rub onto the shrimp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B3275AEDA5DC944A5504EA5F87DFAE0', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 6, NULL, NULL, 2, NULL, 0, 'Turn the shrimp over or just toss them with a spoon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55B18830B35EF04DA7DA21152FAD53A2', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle another 2 TBS of Jeff''s original rub onto the top of the shrimp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B82E9B5237CA54C87747E4CCFC3B6DC', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 8, NULL, NULL, 2, NULL, 0, 'Give the shrimp another toss and place the pan into the fridge for 2 hours to marinate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CC781F5E720C146A8CF20C6D865A297', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 9, NULL, NULL, 2, NULL, 0, 'Melt 2 sticks of butter and pour it over the shrimp. If you need more to cover better, melt another stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'903BD86949FE49418082B123C45762A2', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 10, NULL, NULL, 2, NULL, 0, 'Set up the smoker for cooking at about 220°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1E8CFC2C380FC4A973B984679C7327D', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 11, NULL, NULL, 2, NULL, 0, 'Keep a light hickory or pecan smoke going for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'735F08A6D92CA44FA167D50903728772', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 12, NULL, NULL, 2, NULL, 0, 'Check the shrimp after 40 minutes to make sure they aren''t getting done to quick. When shrimp are done, the flesh will be slightly opaque (no longer transparent) and the shell will be pink to bright pink. The bite should be crisp, not rubbery.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CDC8385BA45CB4DBAB67B6C2D6D35F0', X'0CF895C1E4CF60498EF42363859827A2', X'0CF895C1E4CF60498EF42363859827A2', 13, NULL, NULL, 2, NULL, 0, 'Serve immediately when finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6F67841BF5C7D41BA51D1DD735F25DC', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 0, NULL, NULL, 2, NULL, 0, 'To make the brine for this chicken, combine ½ cup of kosher salt into ½ gallon of water and stir until the mixture is clear. Set this aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA97037454A7D741A24753312DC67080', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken legs into a large plastic zip top bag and pour the brine mixture over the chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D5A44AB7B63F64AB28F8CB1D7B2F556', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 2, NULL, NULL, 2, NULL, 0, 'Place the bag of chicken into a mixing bowl and into the fridge for 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35E94B86907D994FB357D15D75DDA743', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 3, NULL, NULL, 2, NULL, 0, 'Once the chicken is finished brining, rinse each piece under cold water and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0972C3039B7315448E7D81DCEB3F48CA', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 4, NULL, NULL, 2, NULL, 0, 'Further dry the chicken skin by placing it on a paper towel lined sheet pan and into the fridge for a couple of hours. This helps the skin to end up a little more crisp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EABFF55F444C2408761B3DE409FE006', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 5, NULL, NULL, 2, NULL, 0, 'Season the outside of the chicken with Jeff''s original rub and it is ready to smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A5909D02933984AA625E653EEDC4F9B', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 6, NULL, NULL, 2, NULL, 0, 'Setup the smoker or grill for cooking at about 275°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F62122ED7955244294C21C195D82D00F', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 7, NULL, NULL, 2, NULL, 0, 'Place the chicken on the grate and let it cook for about an hour or until it registers 180°F in the thickest part of the chicken. Note: chicken is safe to eat at 165°F however, chicken legs are often more tender if you cook them a little longer than what is required.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79FBAA220886D84493BFD580707B90CD', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 8, NULL, NULL, 2, NULL, 0, 'Make mustard sauce by mixing equal parts yellow mustard with Jeff''s original barbecue sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E43424B19BE2C4C96CF018E4A72C587', X'23CE74A2253E2348AED8A453182210CB', X'23CE74A2253E2348AED8A453182210CB', 9, NULL, NULL, 2, NULL, 0, 'Brush on sauce during the last 10 minutes if desired');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE308AE7815603448ED5C298C23065C5', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 0, NULL, NULL, 2, NULL, 0, 'Tie up the pork loin using butchers twine to give it a round even shape.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B0732D4AED3804F8180F749062E5461', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 1, NULL, NULL, 2, NULL, 0, 'Cut `X''s` into the fat cap between the strings.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87534BE71999BD42801F3DDEE63D90FF', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 2, NULL, NULL, 2, NULL, 0, 'Coat the outside of the pork loin with Smucker''s Simply Fruit® Strawberry spreadable fruit.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B73BDB6A22C4E4419B39E25D8D58E17E', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 3, NULL, NULL, 2, NULL, 0, 'Apply Jeff''s original rub to the entire outside of the pork loin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49E68A14A0FAF649AABED12B9DBF5027', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 4, NULL, NULL, 2, NULL, 0, 'Set up the smoker for cooking at 225-240 °F using indirect heat and cherry wood for smoke (any other smoking wood will also work).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FFEC9CC140F5C4E8B455DD4BE528C15', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 5, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, place the pork loin on the smoker grate and let cooking commence.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5801C9173664BB4B86626169B2545F37', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 6, NULL, NULL, 2, NULL, 0, 'Let the pork loin smoke cook until it reaches 145°F in the center as indicated by a digital probe meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E3F4DFBAA606E4D9638C0770E73AF82', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 7, NULL, NULL, 2, NULL, 0, 'Lay the smoked pork loin on the counter with a piece of foil tented over it and allow to rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08DFBC7A6DE4FE4F9763D9893FE94964', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 8, NULL, NULL, 2, NULL, 0, 'Clip the strings and slice ½ to ¾ thick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BBD2B69EA9949429F168A5E8F86056B', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 9, NULL, NULL, 2, NULL, 0, 'Make the strawberry balsamic glaze while the meat is resting.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9246AB947E99CC44BCBF08F45D0EBF21', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 10, NULL, NULL, 2, NULL, 0, 'To make the glaze, place 10 oz of Smucker''s Simply Fruit® Strawberry spreadable fruit and 1/3 cup balsamic vinegar into a small pan over low heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71577AE2A23AC043BC5CD08F32925BCB', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 11, NULL, NULL, 2, NULL, 0, 'As the ingredients get warm, stir to combine. When it''s warm through, it''s ready to use.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02F53DBE19B267448D4276B146DA0B43', X'3A78A144C676F340A0C5FA169ECD7E88', X'3A78A144C676F340A0C5FA169ECD7E88', 12, NULL, NULL, 2, NULL, 0, 'Drizzle the strawberry balsamic glaze over the pork loin slices and serve right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D77FEECE72CFA948ADD1433433FC7C07', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 0, NULL, NULL, 2, NULL, 0, 'Place the ribs on a cutting board or into a pan bone side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F265CA7FDC66064C8C6FD37296130B5F', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 1, NULL, NULL, 2, NULL, 0, 'Remove the flap of meat called the `skirt` using a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C62C7D37C26CE40AF3DBEE22576AC7A', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 2, NULL, NULL, 2, NULL, 0, 'Remove the brisket bone at the top edge of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC22033AD0C1234683E73652514F796A', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 3, NULL, NULL, 2, NULL, 0, 'Remove the plastic-like membrane using a paper towel or catfish pliers for better grip.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'512EE043D448754AA841E86248B04B39', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 4, NULL, NULL, 2, NULL, 0, 'Apply a coat of yellow mustard to the bone side of the ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB058AA7274EDF47A7704C15534FA12A', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 5, NULL, NULL, 2, NULL, 0, 'Generously apply Jeff''s original rub to the bone side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39946B2FE8E1BC42A5E7799A37D10ECA', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 6, NULL, NULL, 2, NULL, 0, 'Leave the ribs sitting until the rub gets a `wet` look showing that it has absorbed moisture from the mustard and the meat juices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA1CBCA6CD38DC4D91F8E09D6928D9CA', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 7, NULL, NULL, 2, NULL, 0, 'Flip the spare ribs over to meaty side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'829CB7C563CBA24990C745519AE63DD3', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 8, NULL, NULL, 2, NULL, 0, 'Apply a coat of yellow mustard to the meaty side of the ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87482029CDF5BE40A2497982DE2D533B', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 9, NULL, NULL, 2, NULL, 0, 'Generously apply Jeff''s original rub to the meaty side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3044F95F7D6E974494F303FC411E3288', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 10, NULL, NULL, 2, NULL, 0, 'Leave the ribs sitting until the rub gets a `wet` look showing that it has absorbed moisture from the mustard and the meat juices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75EF74F279D9EC489D224E6D829348ED', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 11, NULL, NULL, 2, NULL, 0, 'The ribs are now ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA9F53483624A54ABC53B09BDAC8A5CC', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 12, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240 °F using indirect heat and cherry wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F59FEB10DF4A0A4E96A684ADDECF163E', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 13, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the ribs directly on the smoker grate and cook for 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34136053731B5C48B75E958A454C0E5A', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 14, NULL, NULL, 2, NULL, 0, 'At the end of 3 hours, wrap the spare ribs with 2 layers of heavy duty foil to allow the ribs to steam and tenderize.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE4C42BDA72AA8448F11A70356E35166', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 15, NULL, NULL, 2, NULL, 0, 'At the end of 2 hours wrapped in foil, unwrap them once again and place them back into the smoker at 225-240°F to firm up the crust and finish cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F53E4B50599E054198E67DE0B21F6C2F', X'3278BCE7DAA65842886C190C9A243D93', X'3278BCE7DAA65842886C190C9A243D93', 16, NULL, NULL, 2, NULL, 0, 'You can also place them on a 300°F grill for about 5 minutes each side. This is a great time to brush on sauce if you like.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0699F7664F346B4BA361B15BE5C9BB12', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 0, NULL, NULL, 2, NULL, 0, 'Cut the root end and stem end off the onion.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E281EC568DF10A4EAFFFB8F3EA7DCBD2', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 1, NULL, NULL, 2, NULL, 0, 'Peel the skin from the outer layer of the onion.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C950C131266D54F80501324EF2C260C', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 2, NULL, NULL, 2, NULL, 0, 'Cut the onion into ½ inch slices and remove the centers to only leave about 2 layers.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5A4CD02966F564B9BC606BBC746A812', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 3, NULL, NULL, 2, NULL, 0, 'Brush barbecue sauce onto the top of the onion slices then sprinkle with Jeffs'' original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87CF17B971048B4C824BE9D7D12082F8', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 4, NULL, NULL, 2, NULL, 0, 'Flip the onion slices over and repeat the sauce and rub on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86DDBAA52DB7F445A9A9B82856DE50D6', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 5, NULL, NULL, 2, NULL, 0, 'Wrap strips of thin bacon around the edges of the onion slices. Expect to use about 2-3 slices each.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B08249FF3039B44B940087A864A1472', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 6, NULL, NULL, 2, NULL, 0, 'Lay the bacon wrapped onion rings onto a Bradley rack or cooling rack and sprinkle with a little more rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76CE30DB0B132B428DAEEEE1EE2BE300', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 7, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 240°F with indirect heat using pecan wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3F25F84F13B1A4D869C09FB91162300', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 8, NULL, NULL, 2, NULL, 0, 'Smoke cook the onion rings for about 2 hours or until the bacon is crisp and nicely browned.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5232E801AA03D428EAB9DF0DE7DAD87', X'699755264B123142AA63D38B7F7959C2', X'699755264B123142AA63D38B7F7959C2', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately when finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66D8FECA76A23E4DA6CCE51EF16B732A', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 0, NULL, NULL, 2, NULL, 0, 'Place the two chuck roasts on a plate or other container and evenly sprinkle about a teaspoon of kosher salt on the top of each.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D68BEACC6878A44BD440943BC49A38F', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 1, NULL, NULL, 2, NULL, 0, 'Place the roasts in the fridge for two hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'955C489BB9623A468A9A7A125D9F30D8', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 2, NULL, NULL, 2, NULL, 0, 'Remove the roasts from the fridge, flip them over and apply salt in the same manner to the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'819D64D9DFD4684B95C3B68EFBD22538', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 3, NULL, NULL, 2, NULL, 0, 'Place the chuck roasts back into the fridge for 4 hours to finish the dry brining process. No rinsing required.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AADC82C1827542458AD0DE80D04B6C97', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 4, NULL, NULL, 2, NULL, 0, 'Apply a layer of mustard to one of the chuck roasts and then a generous application of Jeff''s original rub (top, bottom and sides).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46FA79C56E33AE4E8BC73E83F81887D6', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 5, NULL, NULL, 2, NULL, 0, 'Apply a coat of olive oil to one of the chuck roasts and then a generous application of Jeff''s Texas style rub (top, bottom and sides).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'660B0D932255094F99C5744D6B94F94A', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 6, NULL, NULL, 2, NULL, 0, 'Place the roasts into a smoker preheated to 225-240°F and smoke cook with pecan wood until the meat has reached an internal temperature of 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'180C8155BD844D4FA88A37572B3B9D6A', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 7, NULL, NULL, 2, NULL, 0, 'Wrap the roasts in brown butcher paper and place them back into the smoker to continue cooking until they reach a finished temperature of 195-200°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E260E4F4534B447A1845F2A72777A2E', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 8, NULL, NULL, 2, NULL, 0, 'Let the meat rest for 2 hours, still wrapped, in an empty cooler or an oven/smoker heated to about 170°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03CD59E4D5249E4EB06AB8B931A10F66', X'3F6CD7CA63037D4881D86B15D710D3DB', X'3F6CD7CA63037D4881D86B15D710D3DB', 9, NULL, NULL, 2, NULL, 0, 'When the resting period is over, the meat can be unwrapped and sliced, pulled or chopped depending on how you want to serve it to your guests.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A2B513E067DF545B4940AD614F32D12', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', 0, NULL, NULL, 2, NULL, 0, 'Scrub the potatoes under cool water to remove any dirt, grime, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1CB9C0EA2A22A40A7D355B6F6D7EE27', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', 1, NULL, NULL, 2, NULL, 0, 'Dry them with a clean paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2135435C6F85614686E7A0D8F547F148', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', 2, NULL, NULL, 2, NULL, 0, 'I was always taught to poke potatoes before baking them to let the steam escape and prevent exploding.. there is a lot of skepticism regarding this being really necessary but I figure it''s easy to do and life is too short for taking these kind of chances. It''s your potatoes.. so, you assume the risk;-)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEEB405D26859D4A8783CFE0E3E29FB8', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', 3, NULL, NULL, 2, NULL, 0, 'Set them on a cooling rack, Bradley rack or Weber grill pan as this makes it really easy to transport them to and from the smoker but you can use anything you like.. even a large bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36F85DE6861B04B93C9F33C8B7FB65A', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', 4, NULL, NULL, 2, NULL, 0, 'Brush on some olive oil.. Then some kosher salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF41F650C327B64595227EC4D9A780AB', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', X'B55EAE2AC2FFB74AA47A28BB0A92F54C', 5, NULL, NULL, 2, NULL, 0, 'Voila! They are ready for the smoker!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85EEDA26CB1BD1459088E53D814951A5', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 0, NULL, NULL, 2, NULL, 0, 'At 225°F, you are looking at 2.5 to 3 hours however if you crank the heat up to about 275-300, you can get these done in about 1 hour 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F38788EE2AF5F468C828C4C94E0FCE3', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 1, NULL, NULL, 2, NULL, 0, 'Place the potatoes directly on the rack or leave them on the cooling rack and set the whole thing in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E09A728C61C40B4596AA0ECFCC54849F', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 2, NULL, NULL, 2, NULL, 0, 'Let them cook with smoke flowing until they are soft just like you like them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C1E526A3AC12408B08AD9BFA993204', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 3, NULL, NULL, 2, NULL, 0, 'If you like them al dente, then you can remove them a little sooner or if you like them really soft (like I do) then leave them longer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50567E79970FE04A983DED0AF67AEAB6', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 4, NULL, NULL, 2, NULL, 0, 'If they get done before the other food, keep them hot for several hours in the oven at 170°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561F85AC62A7C5419386F8C1D8F9B6E4', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 5, NULL, NULL, 2, NULL, 0, 'You can eat them savory or sweet or try both and see which you like better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35A99590433A294C834D8FB0583E4645', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 6, NULL, NULL, 2, NULL, 0, 'A little butter with my original rub is really good or if you prefer, butter and plenty of brown sugar is good enough to be dessert.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35E3767DC19F0B4792BC17B308701C16', X'2410BC32C9546F4E9787F335CD356746', X'2410BC32C9546F4E9787F335CD356746', 7, NULL, NULL, 2, NULL, 0, 'I paired these sweet potatoes with some citrus rosemary cornish hens and the combination was a hit!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'007FC46D801BC941A87348DBCB705A54', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 0, NULL, NULL, 2, NULL, 0, 'Make the marinade by adding 3 cups of olive oil to a medium sized mixing bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DE61DD5E00EF743896B3CEC0D0DB533', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 1, NULL, NULL, 2, NULL, 0, 'Zest about ⅓ cup each of the oranges, lemons and limes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D527D6088D5306439865253D09464D4A', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 2, NULL, NULL, 2, NULL, 0, 'Squeeze the oranges, lemons and limes to get about 1 full cup of juice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C0FB9F513F0E648A5B598AA66473B10', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 3, NULL, NULL, 2, NULL, 0, 'Add the zest, juice, chopped rosemary, minced garlic, salt and pepper to the olive oil and stir well to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8F3D332271E2E47868F45E9E7342299', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 4, NULL, NULL, 2, NULL, 0, 'Halve the cornish hens by removing the backbone and the keel bone along the breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17693368D18CF642815DAE4477D643ED', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 5, NULL, NULL, 2, NULL, 0, 'Add the half chickens to a lidded container and pour the marinade over the chicken to cover. Place the chicken in the fridge for 12 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B485FE2596E2F4D99FCE4BDF8BFE7F9', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 6, NULL, NULL, 2, NULL, 0, 'Do not rinse when finished. Sprinkle a thin layer of Jeff''s Texas style rub onto the skin side of the chicken halves.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'251EA18BB5612D4DB8A13F3D61047F92', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 7, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 275°F if possible using orange wood, or any citrus/fruit wood or even pecan, oak or hickory if that is all you have.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1920420D9ADFD44BA6B1B2B65A5C3067', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 8, NULL, NULL, 2, NULL, 0, 'Smoke cook the cornish hens skin side up for about 1 hour and 15 minutes at 275°F or until they reach 165°F in the thickest part as measured by a meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14AF8F3C1ADED446BF6A92E769864F1D', X'AC0FC8F69C390E499BD995C4CED5F64C', X'AC0FC8F69C390E499BD995C4CED5F64C', 9, NULL, NULL, 2, NULL, 0, 'When the chicken is done, it can be served immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99456EEF95F2D54F9BDBC5635F714B8F', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 0, NULL, NULL, 2, NULL, 0, 'Remove fish from package and lay it on a cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'492F2E95DD3C794584C23E2C1D9EBA47', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 1, NULL, NULL, 2, NULL, 0, 'Cut the fish into 8 equal servings.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'607083A3088E8040A7BD67438D808621', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 2, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, remove the skin from the flesh.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'614C266DF4578C4588CC61E6EDFB0438', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 3, NULL, NULL, 2, NULL, 0, 'Rinse the fish under cold water to remove any scales.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91C5E55D7F40DE4EA0B9BD2405AEF864', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 4, NULL, NULL, 2, NULL, 0, 'Pat dry with paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E2BA501EB740E49B330BE07B61F5711', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 5, NULL, NULL, 2, NULL, 0, 'Make dry brine mixture using 2 cups dark brown sugar, ¼ cup of kosher salt and 2 TBS of coarse ground pepper. Mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92F778DBE522BF4784E5E1AAAEB40F30', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 6, NULL, NULL, 2, NULL, 0, 'Create a thin layer of the dry brine mixture in a lidded plastic or glass container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9013F74B92D2E40A53CDFE2DAFD3DAB', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 7, NULL, NULL, 2, NULL, 0, 'Lay the dry pieces of fish onto the thin layer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59B74167F46DB7408F666870B9A770F2', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 8, NULL, NULL, 2, NULL, 0, 'Create a thin layer of the dry brine mixture on the top and sides of the pieces of fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6CD6B9E7DD8104C9F312A66CF2579FA', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 9, NULL, NULL, 2, NULL, 0, 'Pat it into place with your hand.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F0158699EF9F4428CA4C31FCF26CC4A', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 10, NULL, NULL, 2, NULL, 0, 'Put on the lid and place container into fridge for 6-8 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC5CE30C9FC12A41BD512C5DA3B57460', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 11, NULL, NULL, 2, NULL, 0, 'Rinse the fish well under cold water and pat completely dry with a paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AC84CD663817242A790743A68691D86', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 12, NULL, NULL, 2, NULL, 0, 'Place the fish onto a cooling rack and into the fridge to dry for 2-3 hours or until the outside of the fish is tacky to the touch and has a shiny appearance.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9BA284BF9F3CD448D8EF113092D02DA', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 13, NULL, NULL, 2, NULL, 0, 'Sprinkle a very thin layer of Jeff''s original rub onto the top of the salmon to season.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED1DDDD915AD5148955A16B00E7A4DF2', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 14, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 180°F with indirect heat using cherry wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C50A646E7E9B5941B354115CB2BE6D3E', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 15, NULL, NULL, 2, NULL, 0, 'Let the fish cook for about 1.5 hours or until the fish reaches 125-145°F in the thickest part. (while USDA recommend 145 °F, most chefs recommend 125 for a more tender and moist salmon).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DBA42C0DE49C94C8C8E6F20ABA72756', X'0F5B9C09173A6141BBF523F41A05E763', X'0F5B9C09173A6141BBF523F41A05E763', 16, NULL, NULL, 2, NULL, 0, 'Remove from the smoker and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F457912689A1BA419DFD09A830DDF29C', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 0, NULL, NULL, 2, NULL, 0, 'Place the beef short ribs into a foil pan or plastic container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04A7103747AF2643A5FD6E4EBF7E16EF', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle coarse grained kosher salt onto the top of the beef short ribs. Coverage should be about 10-15% and should leave at least 1/8 to ¼ inch between the grains on average.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'004E7B86EABC9241BBCBD9980316F6C4', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 2, NULL, NULL, 2, NULL, 0, 'Place the beef short ribs in the fridge uncovered for at least 4 hours or overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B15DA630CC7EB84E9F6C14B5C1B4216C', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 3, NULL, NULL, 2, NULL, 0, 'When they are done brining, there is no need to rinse them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7159EA24429D97488C9EB250B2764055', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 4, NULL, NULL, 2, NULL, 0, 'If necessary, move the beef short ribs to a foil pan that is about 2 to 3 inches deep.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25F08534EF5C524A968686149D014549', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 5, NULL, NULL, 2, NULL, 0, 'Pour about ½ inch of cranberry juice or juice cocktail into the bottom of the foil pan. Pour the juice over the beef ribs so that they get wetted in the process. This will help the rub to stick better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92A09A964F125546857B047F58C7F276', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s original rub generously onto the top of each beef short rib and set aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26316CD4F134E6429A15D154C709495A', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 7, NULL, NULL, 2, NULL, 0, 'Prepare the smoker for cooking at 225-240°F using indirect heat and cherry wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AAB2FCA1A1C634981793E9C62F26B07', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 8, NULL, NULL, 2, NULL, 0, 'Place the pan of ribs into the smoker and let them smoke cook uninterrupted for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D6C235C893AF342846DE490699E0310', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 9, NULL, NULL, 2, NULL, 0, 'At the end of 2 hours, cover the pan with foil to let the braising really take place. This braising process should last for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41418EAC26C2ED4EBD76FE004C90C4B5', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 10, NULL, NULL, 2, NULL, 0, 'Uncover the short ribs at the end of 2 hours and spoon some of the tasty juices up onto the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'055822476FC1AF4E9E3B20343D11DA49', X'89F446815495E14BA062446C2D3DBE5D', X'89F446815495E14BA062446C2D3DBE5D', 11, NULL, NULL, 2, NULL, 0, 'Garnish with some chopped parsley and rosemary and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F580E3EAD5FB114F91D4D4A3903B25B9', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 0, NULL, NULL, 2, NULL, 0, 'Remove the membrane using a sharp knife, your fingers, the end of a spoon, etc. to get under the membrane. Grasp the membrane with a paper towel for better grip or you can use catfish pliers.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A74CCAE995C7CD44A23E91B17733A7CF', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 1, NULL, NULL, 2, NULL, 0, 'Place the ribs boney side up on the table or cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B178155249B2F340B3E6DB2827AE2F1D', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle about 1 tsp of kosher salt evenly on the boney side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CF4A069E7CB034E9894A613162C4B5B', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 3, NULL, NULL, 2, NULL, 0, 'Place the ribs in the fridge for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5453524B1D87FD44AA874ADBDABAB78F', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 4, NULL, NULL, 2, NULL, 0, 'Remove the ribs from the fridge, flip them over to meaty side up and sprinkle a tsp of kosher salt evenly on the meaty (top) side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FBA300A1E84164892EE75FA842DC4FC', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 5, NULL, NULL, 2, NULL, 0, 'Place the ribs back into the fridge for at least 4 hours or overnight is okay.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37178C8849705341A4EFCF05BD5EEBEF', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 6, NULL, NULL, 2, NULL, 0, 'There is no need to rinse the ribs after this dry brining process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F03CCF06FF8C3440A5C8DC17E22C0382', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 7, NULL, NULL, 2, NULL, 0, 'Season the outside of the ribs generously with my original rub on the top meaty side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F12D49908564C544B8D62C046E711210', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 8, NULL, NULL, 2, NULL, 0, 'Let the ribs sit there just a few minutes until they get that `wet` look. This is the rub mixing with the natural juices in the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6A03D5A93F2EC4B862CFB1BF43E22A1', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 9, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 230°F if possible. Be sure to use a tried and true thermometer to make sure that your smoker is running at the temperature that it says it is.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29A56CCD4F5872428524135547CDD4AD', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 10, NULL, NULL, 2, NULL, 0, 'Once your smoker is heated up and ready to smoke, it’s time to get those ribs on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF795F32AA1BE14AAF880C24AE7518F3', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 11, NULL, NULL, 2, NULL, 0, 'Place the ribs bone side down if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'327043C490246448BD72D502F973995E', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 12, NULL, NULL, 2, NULL, 0, 'Let the ribs cook at about 230°F and with smoke for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDE1E847564DD44C92FB94C7A2D166AC', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 13, NULL, NULL, 2, NULL, 0, 'At the end of 2 hours, wrap the ribs with foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71522A4AFD52394BB08F3B540F161F27', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 14, NULL, NULL, 2, NULL, 0, 'Do this carefully so as to not tear the foil. If you do happen to tear it, just double wrap it with another piece.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81F85AFD9B6F1E4D99E627474D38041A', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 15, NULL, NULL, 2, NULL, 0, 'Place the wrapped ribs back into the 230°F smoker and you can even stack them all together if you need to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'051885189A995746B5CA64D90AC721A3', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 16, NULL, NULL, 2, NULL, 0, 'Let them cook in this wrapped state for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAF8DB6DE589AC4CA527A2F3F78C2462', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 17, NULL, NULL, 2, NULL, 0, 'With a total of 4 hours past, you now want to unwrap them again.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47F77CFB11913041B35BFB58131EB196', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 18, NULL, NULL, 2, NULL, 0, 'They will be very soft on the outside and very tender on the inside, and this last step is to firm up the outside just a little before serving them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EEA5DDCB6A0CA42AE2F0526014022B2', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 19, NULL, NULL, 2, NULL, 0, 'Remove the foil and place them back into the 230°F smoker for 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A39A04957F562046A26A4D301F7203B5', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 20, NULL, NULL, 2, NULL, 0, 'Paint the ribs with sauce several times over the course of this last hour to make them nice and sticky.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1052BC56FF41D4DBCA1AB2525FFC940', X'1ABFF58895F597449EC878303B02EFFD', X'1ABFF58895F597449EC878303B02EFFD', 21, NULL, NULL, 2, NULL, 0, 'Slice up the ribs and serve them');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C97591C636BF7D4F869234B285FBF313', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 0, NULL, NULL, 2, NULL, 0, 'Remove the turkey breast from the packaging but leave the net intact as this is what holds it''s shape while it cooks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B48C558CC7381429EB54925125C0514', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 1, NULL, NULL, 2, NULL, 0, 'To a medium pot add 1 quart of water, 1 TBS of course ground black pepper, 1 TBS of red pepper flakes, 2 long sprigs of thyme, 2 long sprigs of rosemary and 8-10 garlic cloves smashed with the side of a knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E43ACE4EC2E37047B732C66B9F26B5DD', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil then remove it from the heat and let it steep covered for 20-30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D10F8BBE9F16B4E976AB3488D886562', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 3, NULL, NULL, 2, NULL, 0, 'Place the mixture in the fridge for several hours to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DAE83903B595D458B039DAFF569E0E8', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 4, NULL, NULL, 2, NULL, 0, 'To make the brine, add 3 quarts of cold water to a gallon sized pitcher and 1 cup of kosher salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'703584328507F2439BE14679E9174220', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 5, NULL, NULL, 2, NULL, 0, 'Stir the mixture until the salt is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFE20CF3D63C6343A85F6C7664F55B69', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 6, NULL, NULL, 2, NULL, 0, 'Add the cooled mixture from the fridge into the brine and stir to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DCA0785F7C4544F94D00E7B561F0B60', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 7, NULL, NULL, 2, NULL, 0, 'Place the turkey breasts into a brining container and pour the brine over the turkey to cover and then into the fridge for 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EE399BA7F12AB458FFC0EFFE5971221', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 8, NULL, NULL, 2, NULL, 0, 'After brining is complete, rinse the turkey breasts under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA076A459A8E63479B9917DB49E06957', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 9, NULL, NULL, 2, NULL, 0, 'With the net still intact, brush on a little cooking oil and season the outside of the turkey breasts liberally with Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D18319D32F627E48B9508466C393CB85', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 10, NULL, NULL, 2, NULL, 0, 'Set the seasoned turkey onto Weber grill pans, Bradley racks or regular cooling racks and they are ready to go into the smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44FDB7CEB175BA46AD5FE7FE0B5A7B67', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 11, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at 225-240 °F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8735547602360A4BA0D8ADB5668D75F4', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 12, NULL, NULL, 2, NULL, 0, 'When the smoker is preheated, place the pan with the turkey breasts into the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C737BBFEFFFE24EABA9379A749D9154', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 13, NULL, NULL, 2, NULL, 0, 'Let the turkey cook until it reaches an internal temperature of 162°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A1A2C72B9747A4DBB5E1ED986588D03', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 14, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the smoker and let it rest under loose foil for about 10-15 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'581B93D3D640B84AAEA01CB274B8233D', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 15, NULL, NULL, 2, NULL, 0, 'Remove the net from the turkey breasts being careful to not disturb the crust more than necessary.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53343899E9B64D438ADD13A33C84A7A6', X'BD4BB2609121A042B1645F6BC17A2B01', X'BD4BB2609121A042B1645F6BC17A2B01', 16, NULL, NULL, 2, NULL, 0, 'Slice and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3326354EE850654BB811FC3DD7E2A041', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 0, NULL, NULL, 2, NULL, 0, 'To a medium pot add 1 quart of water, 1 TBS of course ground black pepper, 1 TBS of red pepper flakes, 2 long sprigs of thyme, 2 long sprigs of rosemary and 8-10 garlic cloves smashed with the side of a knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D48DAB88689EF44A857CFFC71B370944', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil then remove it from the heat and let it steep covered for 20-30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'186542616C92E34783D10184E0747C1A', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 2, NULL, NULL, 2, NULL, 0, 'Place the mixture in the fridge for several hours to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B5C9A859848F2458D0D00A728233096', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 3, NULL, NULL, 2, NULL, 0, 'To make the brine, add 3 quarts of cold water to a gallon sized pitcher and 1 cup of kosher salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A8E6307531944E843DD96803E8D76A', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 4, NULL, NULL, 2, NULL, 0, 'Stir the mixture until the salt is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2941200C824764DB38DC6B5640E8A45', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 5, NULL, NULL, 2, NULL, 0, 'Add the cooled mixture from the fridge into the brine and stir to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B00DBAEDBBA3B84F9963797777C8C58E', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 6, NULL, NULL, 2, NULL, 0, 'Place the turkey tenderloins into a plastic bowl or zip top bag and pour the brine over them to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D57B006312D24498AAB37B2C393BDB1', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 7, NULL, NULL, 2, NULL, 0, 'Place the container with the brining turkey into the fridge for at least 4 hours or overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8CAEC8675A8E941B53D603DB8F7E2D0', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 8, NULL, NULL, 2, NULL, 0, 'When the brining is complete, rinse the turkey under cold water to remove any residual salt and discard the brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'411FC133468C8044853966D39435C971', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 9, NULL, NULL, 2, NULL, 0, 'Season the turkey with Jeff''s original rub then wrap the entire tenderloin with bacon. This will take approximately 4-6 pieces of bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'456445C6E42DAA47B66C5FFD0C363314', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 10, NULL, NULL, 2, NULL, 0, 'Season the outside of the bacon wrap with Jeff''s original rub then place the wrapped tenderloins onto a Weber grill pan, Bradley rack or cooling rack and it is ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10DF6166AD1AC649A4FA79EAC57E1971', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 11, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 240°F using indirect heat and cherry wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F732FD3C598770448AD241BD0D188E22', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 12, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, place the meat into the smoker and close the lid or door.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'592B22B6A796C04EB86DFDBA85DCFB1D', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 13, NULL, NULL, 2, NULL, 0, 'Let the turkey smoke cook until it has reached 165°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF526E5055795A4B8E65FDA951265674', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 14, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the heat and allow it to rest under a loose piece of foil for about 10 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE88569422108A4B85066216F3243CD5', X'09325D33DF523146847C791B5FB48E46', X'09325D33DF523146847C791B5FB48E46', 15, NULL, NULL, 2, NULL, 0, 'Slice and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FD5F2B388106D479E8C0BA53005351A', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 0, NULL, NULL, 2, NULL, 0, 'Remove chicken skin using a paper towel for better grip.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'325BACC5CF9453498061D34D18569850', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 1, NULL, NULL, 2, NULL, 0, 'Place chicken legs into a zip top bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A732B1EA991D2D45A5CFB588C1741149', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 2, NULL, NULL, 2, NULL, 0, 'Place 2/3 cup of Dijon mustard into the bag and massage to cover all of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D79F27E03987BF49AD328C67DD07E2D1', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 3, NULL, NULL, 2, NULL, 0, 'Place ½ cup of Jeff''s original rub into the bag and massage/roll bag to coat the chicken evenly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE2C676212C7D429BBE077EC7EA3091', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 4, NULL, NULL, 2, NULL, 0, 'Place the bag in the fridge overnight if desired or proceed with wrapping in bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D03D0C603AFB94495D4071FA53C7C93', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 5, NULL, NULL, 2, NULL, 0, 'Lay a strip of bacon on the cutting board');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24C76ACE4C295643998E083C23BB7EE3', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 6, NULL, NULL, 2, NULL, 0, 'Place a piece of chicken about 4 inches from the end of the bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEE9358111969149A58D525B538EDC5C', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 7, NULL, NULL, 2, NULL, 0, 'Wrap the short end over the top of the bacon then proceed to wrap the long end around the rest of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44A694AAC6985D4C94933ED15C1BA4F0', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 8, NULL, NULL, 2, NULL, 0, 'Repeat this process on all of the chicken legs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0887E5B93A7934AA88C660633E55376', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 9, NULL, NULL, 2, NULL, 0, 'Place the wrapped chicken legs onto a Bradley rack or similar.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9406933F29EB6F4EAF99C40E75ECF78C', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 10, NULL, NULL, 2, NULL, 0, 'Preheat the smoker to 250°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020553F45C166B4BB3EA6E6E8B65D98E', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 11, NULL, NULL, 2, NULL, 0, 'To smoke cook the chicken, lay the chicken on the grate and close the lid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDAFAB225C48F347A1891C9B9D511231', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 12, NULL, NULL, 2, NULL, 0, 'When the chicken reaches 145°F it can be brushed with Dr Pepper glaze made by mixing ½ cup of my barbecue sauce, ½ cup of Dr Pepper and 2 TBS of honey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AAA2F8F4A75DC49A8137B84F71D0C3D', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 13, NULL, NULL, 2, NULL, 0, 'Multiple coats can be applied if you want the chicken to be extra sticky.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AFF50A0589C06418DDD78A1C4A8BFF0', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 14, NULL, NULL, 2, NULL, 0, 'When the chicken reaches 175°F it is finished and should be removed from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5509FE8ED9D75E43A0811BED2D9E5869', X'FADC332E96A4A447AAB636DDF0B37632', X'FADC332E96A4A447AAB636DDF0B37632', 15, NULL, NULL, 2, NULL, 0, 'Serve immediately with warm sauce on the side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7DEC8D3022BC64E950CA2E3DAD9CBD0', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 0, NULL, NULL, 2, NULL, 0, 'Lay the meat onto a cookie sheet and press it out flat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C05D7B3C713194C9DF6C1FE04F425C9', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 1, NULL, NULL, 2, NULL, 0, 'Evenly layer all of the ingredients except for the barbecue sauce on top of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0104F03A42B5124C9E7AB4A4B915DC8A', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 2, NULL, NULL, 2, NULL, 0, 'Roll the meat and ingredients up and press it gently into a loaf shape.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47D86EBAE184C3498BA025AE49A63E25', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 3, NULL, NULL, 2, NULL, 0, 'Place the meatloaf `dough` into a loaf pan or a loaf shaped bowl and into the fridge for at least an hour or longer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8F4FA170A80944E8D6B4C8B03C48512', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 4, NULL, NULL, 2, NULL, 0, 'Get the smoker ready by preheating it to 225-240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC2A5759B4207245B334837AA2BDD892', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 5, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, remove the meatloaf from the bowl and onto a cooling rack or Weber grill pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3656FB9E466E4448475C587241835C9', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 6, NULL, NULL, 2, NULL, 0, 'Place the rack on top of another pan so the grease can drain away and be caught by the pan. This prevents the meatloaf from sitting in grease and getting soggy.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'334A6EA58B51FD4F85B39C0274688EEA', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 7, NULL, NULL, 2, NULL, 0, 'Use pecan, hickory or cherry wood for smoke and let the meatloaf cook for 3-4 hours or until it reaches 160 in the center as measured by a digital meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FB420A9CABCD14C9D5C3C53733C9905', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 8, NULL, NULL, 2, NULL, 0, 'Top the meatloaf with barbecue sauce about 30 minutes before it is finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B81BE1CA2ABC4488A46A921AA1BDF54', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 9, NULL, NULL, 2, NULL, 0, 'Let the meatloaf rest for 7-10 minutes then slice into ½ to ¾ inch servings.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A05302422CB1F41A2F72F0925FE77C1', X'8196531FA8A24D47BBBD6608DA12FC78', X'8196531FA8A24D47BBBD6608DA12FC78', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A11B7AD4A555F409C954603871808EC', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 0, NULL, NULL, 2, NULL, 0, 'Place brisket fat cap down inside of a large foil pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67B0BBEF9DA0B24BA3E74B711E5721BA', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 1, NULL, NULL, 2, NULL, 0, 'Coat the top of the brisket with molasses then sprinkle 4-6 TBS of Jeff''s original rub onto the top and sides of the brisket.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC1A8EEAB9E764C98864F405A492335', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 2, NULL, NULL, 2, NULL, 0, 'Give the rub a couple of minutes to stick then flip the brisket over to fat cap up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0630EE2EDB7B6648AD9CBCE29F02510D', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 3, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to score the fat cap down to the meat using a crosshatch pattern with about 1-inch intervals.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D14FBEE1BC3F2479AC10AD1829C069A', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 4, NULL, NULL, 2, NULL, 0, 'Coat the scored fat cap of the brisket with molasses then pour 4-6 TBS of Jeff''s original rub and rub it in. The idea is for the molasses and rub to get down into the score marks in the fat cap.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FDEFD750CCCFB458C9D24867AC7BF94', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 5, NULL, NULL, 2, NULL, 0, 'The brisket is now ready to cook in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88BADCED2577654DAEC963EB11869E0B', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 6, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 240-250°F with indirect heat using a mix of pecan and cherry wood for smoke or whatever smoking wood you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C85FF9F6FCD64F45949AD12B191A170F', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 7, NULL, NULL, 2, NULL, 0, 'Let the brisket smoke cook for about 5 hours or until it reaches 160°F in the thickest part of the flat area.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A54B75BF6BCFB54FA63BD0241B91B9B1', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 8, NULL, NULL, 2, NULL, 0, 'At this point you can wrap the brisket with foil or USDA approved natural brown (or pink) paper and continue the cooking process in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D75929E07A59D547B241D6F25CAEAB02', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 9, NULL, NULL, 2, NULL, 0, 'The brisket is done when it reaches about 200°F. You can test the brisket for perfect tenderness by inserting a probe or skewer into the flat area. It should have very little resistance and feel like it''s going into room temperature butter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAD84BE03A53A2449C4A3FECC1F5A027', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 10, NULL, NULL, 2, NULL, 0, 'Once done, remove the brisket from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8843B61AD70DB4A8EDE7DD97E26E37E', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 11, NULL, NULL, 2, NULL, 0, 'It should be wrapped in foil at this point and allowed to rest for 1.5 to 2 hours in a empty, dry cooler.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'741D56CD59B8F14DB1C03164049CB8B0', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 12, NULL, NULL, 2, NULL, 0, 'After resting, remove the brisket from the foil and slice across the grain into ¼ inch pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CF5610680A8304DA08FD4B089CD3C5F', X'D235E8E10F3BDA46B828ECDF7128E790', X'D235E8E10F3BDA46B828ECDF7128E790', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B25E8657C65A8B4BB0AF21A421FDBFC2', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 0, NULL, NULL, 2, NULL, 0, 'Make a brine by adding 1 cup of kosher salt to 1 gallon of water. Mix until the salt has dissolved and the water returns to clear.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E8483E6713A9B40827945654129B756', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 1, NULL, NULL, 2, NULL, 0, 'Place the cornish hens in a large bowl or container and pour the brine over them to cover. Put the container into the fridge for 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F93CAC8DADAD6F418E2F9C65AD5892F8', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 2, NULL, NULL, 2, NULL, 0, 'Once the brining process is complete, rinse the birds to remove any residual salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A04274B296A6448B8C93C89FDF2704F', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 3, NULL, NULL, 2, NULL, 0, 'Use kitchen shears to cut along both sides of the backbone.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECF5C8E729D8E04C96597AA250A93C39', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 4, NULL, NULL, 2, NULL, 0, 'Press the carcass open to make them lay flat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF9B2E142FD7A14FA824E5146F8D842D', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 5, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto the inside of the hen then sprinkle a generous amount of Jeff''s Texas style rub onto the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749DE980C6E6C241B9097035B6967074', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 6, NULL, NULL, 2, NULL, 0, 'Flip the bird over and repeat this process of oil and rub on the skin side of the bird.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60CADA1182BBE04F83F3838220D4D3EB', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 7, NULL, NULL, 2, NULL, 0, 'Prepare the smoker for cooking at about 240°F with indirect heat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAE80784D6CE1441AA77CA07F7735661', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 8, NULL, NULL, 2, NULL, 0, 'Apply smoke for at least 1 hour using a mixture of pecan and maple or your own favorite smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E47DB6C2C8DB14EB749AF3FE8305163', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 9, NULL, NULL, 2, NULL, 0, 'Use a digital thermometer to monitor the temperature of the hens while they cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0B9B92BFFA4F145990E02942366C6FE', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 10, NULL, NULL, 2, NULL, 0, 'When they reach 165°F in the thickest part of the breast and thigh, they are finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDE32CE58342EB4D9ED7B6E7AC84201D', X'1F0A669D837D5B41B24900602BA3FE40', X'1F0A669D837D5B41B24900602BA3FE40', 11, NULL, NULL, 2, NULL, 0, 'Remove from the heat and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3E1D1D5EE797F44841D388CF0CAB753', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 0, NULL, NULL, 2, NULL, 0, 'Lay the pork steaks on a cookie sheet or pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31F126A07A06A84992209C738B9E41CD', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle about 1 tsp of Mortons kosher salt on the top side of the pork steaks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1A250D771FF9246AB11C9C5811DEA61', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 2, NULL, NULL, 2, NULL, 0, 'Place the steaks in the fridge uncovered for 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6D2AC402192FF4A82051D594EA6AB5D', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 3, NULL, NULL, 2, NULL, 0, 'Rinse the steaks under cold water to remove any residual salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A58E00F4AC4F14C9E686F9391074065', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 4, NULL, NULL, 2, NULL, 0, 'Season both sides of the meat with Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F317110FEB6E73428CBE1F63B3E02AB5', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 5, NULL, NULL, 2, NULL, 0, 'Set up the smoker for cooking at about 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69F98C8BF5D1B14A87D99A4E80A020DF', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 6, NULL, NULL, 2, NULL, 0, 'Use the water pan if you have one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA39F6E8C56DF14C99FBDAA4FF6C8D1C', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 7, NULL, NULL, 2, NULL, 0, 'Cook the steaks on the smoker for about 2.5 hours or until they reach 185°F in the center.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE3B2CF88C50B44BB0FCDAFB4EA20DAA', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 8, NULL, NULL, 2, NULL, 0, 'About 30 minutes before they are finished, you can sauce them if you like.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D1F78F470CA724EBD76147D0D8C0A61', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 9, NULL, NULL, 2, NULL, 0, 'Brush sauce onto top side, wait about 15 minutes then flip over and do the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4945600F3108840A454C9D6B307F7BF', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', X'7FDC48BAD2CA6D4A9BF42BD37FB562E0', 10, NULL, NULL, 2, NULL, 0, 'Rest under loose foil for about 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D7C5C8EE596A5499180DF50E0B977E7', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 0, NULL, NULL, 2, NULL, 0, 'Dry brine the steaks by sprinkling about 1 tsp of kosher salt on the top. If they are extra thick, do the bottom as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F35CA4D9D951644A54542C70380185B', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 1, NULL, NULL, 2, NULL, 0, 'Place the steaks on a plate or open container in the fridge for 2-4 hours. The thicker the steak, the longer it takes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA7A0D22F0510C4A969E9FD99B08CF4E', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 2, NULL, NULL, 2, NULL, 0, 'When the brining is complete, rinse the steaks and set aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'405819F5CE1E014CBB5DFAF52731C817', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 3, NULL, NULL, 2, NULL, 0, 'Season the steaks, top and bottom with Jeff''s Texas style rub and let them come to room temperature while you get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5E2C732F97FE74693634AEC3A847D98', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 4, NULL, NULL, 2, NULL, 0, 'Prepare the smoker for cooking at 225°F with indirect heat and cherry wood for smoke. Pecan or oak are also great choices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'244CE084A97C00439CD86EA4989AFBF1', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 5, NULL, NULL, 2, NULL, 0, 'Place the steaks on the smoker grate for about 1.5 to 2 hours or until they reach 120°F in the center as measured by an accurate digital thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCBDFF31F621FE4381B4965B24731229', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 6, NULL, NULL, 2, NULL, 0, 'Remove the steaks from the heat and let them rest with foil tented over them for about 10 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B48AABBEB5ADA4C89585A8DC45EF921', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 7, NULL, NULL, 2, NULL, 0, 'Meantime, heat an iron skillet over high heat and once water will spit in the pan, it is ready to use.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFFFF8131EA42542B652951AA56C74E1', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 8, NULL, NULL, 2, NULL, 0, 'Pour a little vegetable oil in the center and place the steak in the pan rotating it to spread the oil out.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96C45A846EB91E47AC5586B7DD8FB219', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 9, NULL, NULL, 2, NULL, 0, 'Sear each side for about a minute.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D37589BF86C2C04589EB8D131525202E', X'AE6D1C6AEE78CD449A739637293C00D8', X'AE6D1C6AEE78CD449A739637293C00D8', 10, NULL, NULL, 2, NULL, 0, 'Let the steaks rest for about 5 minutes, no foil this time and then serve to your guests.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B2407C450BD4B4CB21557409B312EF1', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 0, NULL, NULL, 2, NULL, 0, 'Rinse the ribs with cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5188D510735FC4C82337F3D6A1FACE1', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 1, NULL, NULL, 2, NULL, 0, 'Remove the membrane using a paper towel for better grip.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8628A957252FF042A654F66BD166A60C', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 2, NULL, NULL, 2, NULL, 0, 'Apply a good coat of molasses to the bone side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8F27C9F3268F64C87548990314A531A', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s original rub over the layer of molasses and let them sit until they begin to look `wet`.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8319A8013D9B034CA7F64E17B9AAB144', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 4, NULL, NULL, 2, NULL, 0, 'Flip the rack of ribs over and repeat the molasses and rub on the meaty side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3179D2ACA07B95489CC99316F43E7720', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 5, NULL, NULL, 2, NULL, 0, 'Place the ribs on the smoker grate and smoke cook them for 2 hours at 225°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'027761A236C47D4D9346EFCE86FF0DC0', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 6, NULL, NULL, 2, NULL, 0, 'Use cherry, hickory, maple and apple for smoke. A mix or any combination of these woods will work great.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'364331232B03BB40B4C98E744E608906', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 7, NULL, NULL, 2, NULL, 0, 'Wrap the rack of ribs in heavy duty foil and put it back on the smoker grate to cook another 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F12EFE093AAB4DB587C119F7EA5A3D', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 8, NULL, NULL, 2, NULL, 0, 'Place the ribs on the cutting board bone side up and cut them apart so you end up with 12-13 ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD66C888F8A004EB47CD7778A74C50B', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 9, NULL, NULL, 2, NULL, 0, 'Wrap each rib in a piece of stretched bacon. Secure with a toothpick if you need to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD6F55F4BF48614FBDDFC5A609220C87', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 10, NULL, NULL, 2, NULL, 0, 'Place the ribs on a cookie sheet and put them into an oven preheated to 450°F for 15-20 minutes or until the bacon is done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1753440C6FE6F049ADBBE37DDBBE129E', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 11, NULL, NULL, 2, NULL, 0, 'You can also perform the last step in the smoker or grill and especially if you are able to increase the heat enough to cook the bacon to a crisp finish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'037BB92EB380B846BCD77D094E450CFA', X'7873E8292B9C184FB607BE45741EA1A1', X'7873E8292B9C184FB607BE45741EA1A1', 12, NULL, NULL, 2, NULL, 0, 'Serve the bacon wrapped ribs immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6655AE81FBA1D4983A844086FCDF38F', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 0, NULL, NULL, 2, NULL, 0, 'Remove beef ribs from packaging and rinse under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6A01CC6424D5D428C9A7B5AEF6DB5D9', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 1, NULL, NULL, 2, NULL, 0, 'Remove excess clumps of fat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02398973B0BAE941AD46C2B05FA8062D', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 2, NULL, NULL, 2, NULL, 0, 'Peel membrane or skin from bone side of ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'580CB11D7541D244979C2D56665E7883', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 3, NULL, NULL, 2, NULL, 0, 'Brush several tablespoons of barbecue sauce and/or mustard onto the meaty side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC07D546B12A7D4FA8BDE11E8FDD8044', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle about 2 tablespoons of Jeff''s rub onto the top of the ribs and rub it in to the top and sides creating a paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E5F99D7C94FF345B737EDA81FB9F54F', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 5, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A82667971568734ABBC903CDAE4B3EDA', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 6, NULL, NULL, 2, NULL, 0, 'Use a mix of cherry and oak or whatever smoking wood you have available for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A60DCEA2B4C86841AB9EEF047B9294DA', X'3B2B225332059B44B59307B14EC0AEB2', X'3B2B225332059B44B59307B14EC0AEB2', 7, NULL, NULL, 2, NULL, 0, 'Place the ribs on the smoker grate and let them cook for 4-5 hours or until they are tender to your liking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'206B7014E11749478B824FE5E12C94FB', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 0, NULL, NULL, 2, NULL, 0, 'Rinse fish under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38B7CAAB89F7EC488C4B9D8C3DDCC994', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 1, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto the top and sides of the fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3059573ACA247644A9F8F2BAE0E12402', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 2, NULL, NULL, 2, NULL, 0, 'Apply my Texas style rub to the top of the fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77D9730B5FF51C4B825C242019E58928', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 3, NULL, NULL, 2, NULL, 0, 'Place into a lidded container or ziptop bag and place it in the fridge if the smoker is not ready or if you prepped it ahead of time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9190CC92A158F49AA60A40012742A4F', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 4, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F with indirect heat and a light tasting wood such as alder and/or apple.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3C6A5ABC8FCFD44AFCDD6E7771A0D00', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 5, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, place the fish on a piece of parchment paper and on to the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F00327C67D07604D9579241F8D754BB0', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 6, NULL, NULL, 2, NULL, 0, 'Smoke cook the fish for about 2 hours or until the internal temperature reads 140°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2879066F23792C48BE304C7DF77FDB65', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 7, NULL, NULL, 2, NULL, 0, 'Remove the fish from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94E4396A9225AA429A0D1C6C91CE7FF4', X'6296A7561D8FD849B83F8D984A21A723', X'6296A7561D8FD849B83F8D984A21A723', 8, NULL, NULL, 2, NULL, 0, 'Slice and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4950816703B3A84BA97BBC5057817078', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 0, NULL, NULL, 2, NULL, 0, 'Add all ingredients to large bowl and combine with your hands but do not over mix.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3F21C251D8B96429A0E39916928FD80', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 1, NULL, NULL, 2, NULL, 0, 'Form meatballs between the palms of your hands.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15915D67F72E3D4E9788ED6A542707B4', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 2, NULL, NULL, 2, NULL, 0, 'Flatten meatball and place a ¼ inch cube of cream cheese into the center of the meatball.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63DC76DE3806E4489E31F7FD76B571D3', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 3, NULL, NULL, 2, NULL, 0, 'Wrap meat back around cream cheese and roll between your palms again to form into a meatball.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EFA3510A1110B4397613FE7EFA17B44', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 4, NULL, NULL, 2, NULL, 0, 'Place stuffed meatballs onto a Bradley rack or other pan for easy transport to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0686322EC73B7A45A69DD0082FB57F4D', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 5, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking indirect at about 225-240°F using pecan or other smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C31ED37C38A1B4E99F7AFFB332461D4', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 6, NULL, NULL, 2, NULL, 0, 'Place meatballs into smoker and cook until they reach an internal temperature of 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E167D73B7370244CB60B52175EBCD5CA', X'3BCFDA84FEDDF142BF21FD474C25F3A0', X'3BCFDA84FEDDF142BF21FD474C25F3A0', 7, NULL, NULL, 2, NULL, 0, 'Serve meatballs immediately while they are still hot.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14E1C078D6688C4DA5B02286B1192398', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 0, NULL, NULL, 2, NULL, 0, 'Cut pork loin into thirds or purchase 3 pieces of pork loin');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255B96CD984C244EB15E97673F9A51A7', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 1, NULL, NULL, 2, NULL, 0, 'Remove the fat and silver skin using a sharp knife');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7DD67132BF16E488CE5F60580D28637', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 2, NULL, NULL, 2, NULL, 0, 'Prepare the first pork loin by applying 2 TBS of peach butter, 2 TBS of honey and about 2 TBS of Jeff''s original rub onto the meat and massaging it into a paste on all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78F57A19EE7A944FBF20A25AE8C14C46', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 3, NULL, NULL, 2, NULL, 0, 'Prepare the second pork loin by applying 2 TBS of olive oil, 2 TBS of Jeff''s Texas style rub and 2-3 TBS of minced garlic onto the meat. Massage the ingredients together onto all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D0B3F5754045148B96F60C3159C8AB2', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 4, NULL, NULL, 2, NULL, 0, 'Prepare the third pork loin by applying 2 TBS of olive oil, 2 TBS of Jeff''s Texas style rub and the chopped herbs onto the top of the meat. Massage the ingredients together onto all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E7F925FB2CDA9409CA8F7750191F159', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 5, NULL, NULL, 2, NULL, 0, 'Place the individual pork loins into gallon sized zip top bags and place in the fridge to marinate overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09FA088F92633C4D8F5FBA34FDD13F1A', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 6, NULL, NULL, 2, NULL, 0, 'The next morning, tie the pork loins about 4-5 times each to make them round and place them on a Weber grill pan or Bradley rack.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BB1241A25E2AA408C6C1316571DA6FD', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 7, NULL, NULL, 2, NULL, 0, 'Preheat the smoker to 225-240°F and when it is ready, smoke the pork loins with cherry wood for about 4 hours or until they reach an internal temperature of 145°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F99BB02ECA372A4C814F081B1A9A9234', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 8, NULL, NULL, 2, NULL, 0, 'Tent foil over the pork loins for about 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4923E616FB0D1E4FAB35B5E442D8990B', X'49739FF03B2631409204F27B8E83B98E', X'49739FF03B2631409204F27B8E83B98E', 9, NULL, NULL, 2, NULL, 0, 'Slice the pork loins and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6ACDFAD46408A4D91AD3F268C6C7DF4', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 0, NULL, NULL, 2, NULL, 0, 'Place chuck roast into a lidded container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE6FC127ED523045AFE5AA7E47696BDF', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 1, NULL, NULL, 2, NULL, 0, 'Pour 4-6 ounces of Worcestershire over the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B39CF248E072A7438797D566C3AD483A', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle some of Jeff''s original rub onto the top of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E76D634A7A8CAA42A2DC498213813B4F', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 3, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to make cuts through the top into the center of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875B354A4AF47C4787E05FEF3419CD23', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 4, NULL, NULL, 2, NULL, 0, 'Drizzle on more Worcestershire so it can seep down into the holes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0084D691810A9242ADDCCA233165CB49', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 5, NULL, NULL, 2, NULL, 0, 'Place a lid on the container and let the meat marinate overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6FEE09637EA7047B50A78016727E07E', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 6, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 240 °F using indirect heat and cherry wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C82467BA1D81C14A99E727B256691E62', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 7, NULL, NULL, 2, NULL, 0, 'Place chuck roast directly on smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EE6C424AB214C44BFB073D129A0D79F', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 8, NULL, NULL, 2, NULL, 0, 'Let the meat smoke cook for about 8 hours or until it reads 190°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7824FA0B5E9BC4AB897E2B475838F70', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 9, NULL, NULL, 2, NULL, 0, 'Cut the chuck roast into bite size cubes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FDEB7D9A9959B4DB65F9EACB92737AF', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 10, NULL, NULL, 2, NULL, 0, 'Place the cubes in a foil pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C94A1FBE0E32F44A3FB5CB76A3D54B0', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 11, NULL, NULL, 2, NULL, 0, 'Add Jeff''s original sauce and original rub onto the meat making sure it is well coated.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE814C074098374480FD30B8495CEBBC', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 12, NULL, NULL, 2, NULL, 0, 'Place the meat back into the smoker at 240 °F for about 2 hours stirring every 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BAD7FEE98F7DC47A616113F492088C0', X'0951656D3BCAF241B1145F689175142F', X'0951656D3BCAF241B1145F689175142F', 13, NULL, NULL, 2, NULL, 0, 'Note: you can also place the meat on the grill over high heat or in the oven on broil stirring every few minutes to get it done much quicker. When the color is right and the sauce/rub is caramelized, they are done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC5762449C48084FBC478FFA8C4DEF6F', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 0, NULL, NULL, 2, NULL, 0, 'Remove skin from thighs, snip knuckles from bones and remove any excess fat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13A9A1D5692066418F448A136397B293', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 1, NULL, NULL, 2, NULL, 0, 'Add Jeff''s original rub to chicken thighs on top and bottom. Refrigerate overnight if you like or get right on with it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05BD8F2D36B0FD4088FA9B21834EF737', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 2, NULL, NULL, 2, NULL, 0, 'Replace skin on to chicken thighs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D0ECAA12FC1254CAEC2D651C5BC7484', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 3, NULL, NULL, 2, NULL, 0, 'Put chicken skin side down into special, pre-drilled muffin pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA6EEA1B71400D4C98E7FF8F2057F64A', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 4, NULL, NULL, 2, NULL, 0, 'Place the muffin pan onto a cookie sheet filled with ¼ inch of chicken broth and smoke cook at 275 for 1.5 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'216D8B28860C8A41AACD0A6F9A50A92E', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 5, NULL, NULL, 2, NULL, 0, 'Flip chicken over in muffin pan to skin side up, add some seasoning and smoke cook at 275 for another 45 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEFA7C58881DAE47A944E338CAD8E76A', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 6, NULL, NULL, 2, NULL, 0, 'Remove chicken from muffin pan and place skin side up onto cookie sheet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'460FA1D4C6131D4291DBF248E010FC39', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 7, NULL, NULL, 2, NULL, 0, 'Sauce top and sides and smoke cook at 275 for an additional 30 minutes to let the sauce caramelize or until the internal temperature reaches 175℉.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51A6AB55A3311F48904B9A0CA1BC0974', X'DBDA21A76430B54A8893F459528A49BA', X'DBDA21A76430B54A8893F459528A49BA', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F43FA50B1571646A223B7EBA235F57F', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 0, NULL, NULL, 2, NULL, 0, 'Cut top from pineapple.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19687AABE4A8604EB831349738C0D090', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 1, NULL, NULL, 2, NULL, 0, 'Remove skin and eyes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'807393B23E4E1548A4329D147CE06436', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 2, NULL, NULL, 2, NULL, 0, 'Core out inside leaving about ¾ inch wall on sides and bottom.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A9109E256BE4C4CA3BB752B936BA08F', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 3, NULL, NULL, 2, NULL, 0, 'Fill with smoked country style ribs or baby back rib meat seasoned with rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D0FA6F88580444A5400308FF69FCB3', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 4, NULL, NULL, 2, NULL, 0, 'Replace top and secure with long toothpicks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5087BF4DAD7F9F4B82DD2043B82C4D0A', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 5, NULL, NULL, 2, NULL, 0, 'Make a bacon weave large enough to wrap completely around pineapple.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E4A0AB69317704E9E8DC8442B2DF050', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 6, NULL, NULL, 2, NULL, 0, 'Wrap in bacon weave -or-');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0131275A7F5EA34EBF467C03C0348CA0', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 7, NULL, NULL, 2, NULL, 0, 'Wrap stretched bacon around the pineapple.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF372A819DADC2458916AB5692E70269', X'B4578C8C2EDD2B40B19018546A0F7DC4', X'B4578C8C2EDD2B40B19018546A0F7DC4', 8, NULL, NULL, 2, NULL, 0, 'Smoke at 240°F for about 4 hours or until bacon is crisp.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8F88CF0701BFB4D9DD6DA9AE5FCE3E5', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 0, NULL, NULL, 2, NULL, 0, 'Coat beef with Jeff''s Texas style rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB14E9FBCE45D54FB89A286AB432D62E', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 1, NULL, NULL, 2, NULL, 0, 'Add more coarse kosher salt to the meat to help with the dry brining process. See picture in web version of recipe to estimate proper coverage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D565B648B26994882C76100EA7847BD', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 2, NULL, NULL, 2, NULL, 0, 'Place top round into lidded container and place into fridge overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B7E61B3E8E1704386009DF0E265CB47', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 3, NULL, NULL, 2, NULL, 0, 'When the brining process is complete, remove the top round from the fridge and set aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'022A48EE2DC5AB47A370A3F0569747B4', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 4, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at about 225-250°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F851E3685273F54EAAF59A9F9D655878', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 5, NULL, NULL, 2, NULL, 0, 'Place the meat on the smoker grate and let it cook for 4-5 hours or until it reaches medium rare (130°F)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04A138A5E4B0B542AAC06E8157C76D3D', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 6, NULL, NULL, 2, NULL, 0, 'Use oak smoke for best results but any smoking wood will work fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'880E0A079CC8E24FA86D77F84D8CD709', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 7, NULL, NULL, 2, NULL, 0, 'Add smoke for at least 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE3C22F4D4B3D24E839319990789FC03', X'06E6F426B29BDB41B30C14B6C10067FC', X'06E6F426B29BDB41B30C14B6C10067FC', 8, NULL, NULL, 2, NULL, 0, 'When the meat is finished cooking, let it rest for about 10 minutes then slice into steaks or strips.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82289163B8B95946B49B17D588CDCB6D', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 0, NULL, NULL, 2, NULL, 0, 'Make a bacon weave with 8 horizontal strips and 8 vertical strips on a large piece of foil. It should end up being about 12 inches by 12 inches.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1B23EA999FF0249BBDB3B0D69A1B30B', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 1, NULL, NULL, 2, NULL, 0, 'Season the top of the bacon weave with Jeff''s original rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A39EFD62217C74CAB53DF1AD5B22A76', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 2, NULL, NULL, 2, NULL, 0, 'Place the bacon weave in the fridge to firm up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B00039A95793914BBD7985BE80782036', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 3, NULL, NULL, 2, NULL, 0, 'Place 2 lbs of ground sausage into a zip top bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AAAD0178ADB9C4DB76D661496A141CA', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 4, NULL, NULL, 2, NULL, 0, 'Flatten the sausage inside the bag until is of equal thickness.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9DA5BF4964E2D48823F533CB7788E63', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 5, NULL, NULL, 2, NULL, 0, 'Use scissors to cut along the seams and remove the top layer of the plastic bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72CB5E031032D24FACC2BBBE69D09BDE', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 6, NULL, NULL, 2, NULL, 0, 'Season the top of the sausage with Jeff''s original rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8A42517671EEE41A8ECE3FD9009AA3C', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 7, NULL, NULL, 2, NULL, 0, 'Lay the flattened sausage onto the bacon weave with the top edge about 3 to 4 inches from the top edge of the bacon weave.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'846FD695FD139B44BEAC859058EEC745', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 8, NULL, NULL, 2, NULL, 0, 'Place ½ lb of diced ham and 1.5 cups of sharp cheddar cheese onto the flattened sausage. Leave about 2 inches at the top and bottom and 1 inch on the sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA3D2E366F49884AA02C95F2B58DE5C6', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 9, NULL, NULL, 2, NULL, 0, 'Season the ham and cheese with Jeff''s original rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FA438512DFF5F4BB35EAE28B8970DD1', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 10, NULL, NULL, 2, NULL, 0, 'Using the plastic like a sushi mat, roll the sausage and stuffing into a roll and onto the bacon weave.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD700E6D40C56C4A8673A5E0BD84BD82', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 11, NULL, NULL, 2, NULL, 0, 'Seal the ends of the sausage roll with more sausage to keep the cheese and ham from falling out during the cooking process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8286E99A6EA8C4A99A425F328235770', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 12, NULL, NULL, 2, NULL, 0, 'Using the foil like a sushi mat, wrap the bacon weave around the sausage roll.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C62A6E0278C114AB894D0F4AE1A9B57', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 13, NULL, NULL, 2, NULL, 0, 'Wrap the roll tightly in foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D95C0746AA94D64DAF6587B7BA6A1A0A', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 14, NULL, NULL, 2, NULL, 0, 'Place the wrapped sausage roll in the fridge to firm up for at least an hour or overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94F86301AFFA5346A1DF80EB4EB0CC50', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 15, NULL, NULL, 2, NULL, 0, 'Season the outside of the bacon with Jeff''s original rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52544C3828A69D4CBDB653124CDB2D0D', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 16, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at 225-240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0F52C37B7F1F14D94BD0277CE3B9F9B', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 17, NULL, NULL, 2, NULL, 0, 'Place bacon wrapped sausage roll onto smoker grate or use a baking sheet, Weber grill pan or Bradley rack to make it easier.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFABB31058242F4BA799FEB6E5668ACA', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 18, NULL, NULL, 2, NULL, 0, 'Smoke cook for 3 hours or until the inside of the meat reaches 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FE9FC3CC172D048AE35C8D3AE8B9CDE', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 19, NULL, NULL, 2, NULL, 0, 'Glaze the outside with Jeff''s original barbecue sauce if desired and let it continue cooking for about 10 minutes to glaze the sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B389954CCA94D74994A25878583C6ADE', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 20, NULL, NULL, 2, NULL, 0, 'Remove from the smoker and let it rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87D4CEB5EEF0F74C8F0DA5AB8972F8D7', X'D04C04A69C64D541BB2412802528DEE3', X'D04C04A69C64D541BB2412802528DEE3', 21, NULL, NULL, 2, NULL, 0, 'Slice and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3ABD41A4E892348B82518C9414E415D', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 0, NULL, NULL, 2, NULL, 0, 'Cut along both sides of the backbone to remove it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C884B0645E5248418C838E27718C9746', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 1, NULL, NULL, 2, NULL, 0, 'Once the backbone is removed, press the chicken open so that it lays open like a book.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4A79657116C1D48B127C640496BBBD6', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 2, NULL, NULL, 2, NULL, 0, 'Create a brine from 1 gallon of cold water and 1 cup of coarse kosher salt and mix together until the water returns to clear.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E8406D9A957A9429EF526EE48FD0DE7', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 3, NULL, NULL, 2, NULL, 0, 'Place the chicken into a lidded container or zip top bag and pour the brine over the chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'311EB4477C81FD48AA8831CF3A7698F3', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate the chicken for 4-6 hours while it brines.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A86D0354D61DC944873FB3B3D4FC7204', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 5, NULL, NULL, 2, NULL, 0, 'While the chicken is brining, make the bacon butter mixture by adding 1 stick of butter, 3 TBS of chopped chives, 1 TBS of Jeff''s original rub and 4 slices of uncooked, chopped bacon to a small mixing bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEDEBD67E9583F4EA7CAA325AC0C76A4', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 6, NULL, NULL, 2, NULL, 0, 'Mix the bacon butter ingredients together until it is well combined.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'692D99D26C42204092F0908F78732A06', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 7, NULL, NULL, 2, NULL, 0, 'When the chicken is finished brining, discard the brine and rinse the chicken under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E31D7AEC3205D4490DD1CA7FDB5F764', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 8, NULL, NULL, 2, NULL, 0, 'Set the chicken breast sides up on the cutting board and work the skin around the breast loose with your hands.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9F8F04C86F68C439BBB12E531849E3A', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 9, NULL, NULL, 2, NULL, 0, 'Stuff the bacon butter mixture under the skin of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83FF4364AD9D944885E1E2CC979895FB', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 10, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto the outside of the skin and season generously with Jeff''s Texas style rub recipe or a salt/pepper mixture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E595CFC0F098644B65D9B5F8E0AEC09', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 11, NULL, NULL, 2, NULL, 0, 'Place the chicken into the smoker breast side up and cook at 225-325°F until it reaches 165°F in the thickest part. Some smokers will not cook beyond 225-240°F but you can get the chicken done faster and get a better bite through on the skin from higher temperatures if your smoker will do it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B68B602EEB6E0546B562ED84EE5D200F', X'EBBF204EEF07634ABA48306B7608B03A', X'EBBF204EEF07634ABA48306B7608B03A', 12, NULL, NULL, 2, NULL, 0, 'Let the chicken rest for about 10 minutes before carving and serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37BF55E677570A408AFA21C5E17E4339', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 0, NULL, NULL, 2, NULL, 0, 'Place pork butt into foil plan fat side down');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4394C35A8E12A4AB4E24CB84EA67F61', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 1, NULL, NULL, 2, NULL, 0, 'Apply mustard all over the top and sides of the pork butt spreading it out with your hands.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B6419184F1DCC449EB5D705390537FE', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 2, NULL, NULL, 2, NULL, 0, 'Add about ¼ cup of Jeff''s original rub to the top of the pork butt and massage it into the top and sides of the meat allowing it to mix with the mustard and create a paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE36166AEE3344598D61C6F58664238', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 3, NULL, NULL, 2, NULL, 0, 'Set the pork butt aside and go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9F8BFEA7718284DBE06ACE8F2D89C0E', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 4, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05C433AB51096C4D8B7BF63721DBB3D4', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 5, NULL, NULL, 2, NULL, 0, 'I recommend using pecan or hickory but any smoking wood will work.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'172BDCE998465641BDEF27D9B2B10980', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 6, NULL, NULL, 2, NULL, 0, 'Place the pork butt onto the smoker grate with a pan on a lower rack or you can leave the pork butt in the pan and set the pan and meat on the smoker grate. This is to contain the rendered juices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB4C2247DD99124390AA55100B20DFEA', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 7, NULL, NULL, 2, NULL, 0, 'Let the pork butt cook for about 12-14 hours or until it reaches an internal temperature of 205°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B881D4F206600498A08482778100279', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 8, NULL, NULL, 2, NULL, 0, 'Remove the pork butt from the heat and let it rest with some foil tented over it for about 30-45 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB815182F781E42A01FD243E5205B97', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 9, NULL, NULL, 2, NULL, 0, 'Pull the meat apart into small pieces and chunks removing any fat clumps or pieces as you go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF7EA4C2CB049C47AF2C8D572C375B40', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 10, NULL, NULL, 2, NULL, 0, 'A normal pork butt will yield about 4 lbs of finished pulled pork. To this add 6-12 cleaned, seeded and diced jalapeños, about 20 oz of crushed pineapple and another ¼ cup of Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E5810BC7B3AC346B65B3311570AFD1C', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 11, NULL, NULL, 2, NULL, 0, 'Mix the meat, rub, jalapeños and pineapple together.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADA5F582B23AC64595501E7BD1E48C6F', X'A2BF5AA54808034E877B2575B2A2B842', X'A2BF5AA54808034E877B2575B2A2B842', 12, NULL, NULL, 2, NULL, 0, 'Use on tacos, nachos, sliders, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F45F6008D2E1544A375BF90EEFFCCE2', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 0, NULL, NULL, 2, NULL, 0, 'I purchased shrimp that still had the shell so I spent a little time peeling. Not my favorite job but it''s worth it in the end.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDEDCF4D557182458EBFA3A0B88B75A9', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 1, NULL, NULL, 2, NULL, 0, 'If your shrimp are frozen, pour them into a colander and run cold water over them for a couple of minutes to thaw.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92FB7AFA47188B4C955A60520505DDAF', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 2, NULL, NULL, 2, NULL, 0, 'Peel the shrimp and leave the tail on if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84E44F179BD02346819B9502A224AC73', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 3, NULL, NULL, 2, NULL, 0, 'Place the peeled shrimp in a bowl and set aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69021981084A854A8D221D9D6E3A873B', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 4, NULL, NULL, 2, NULL, 0, 'Pour an ample amount of my Texas style rub over the shrimp and stir them around to coat. If you have time, place the bowl in the fridge for an hour to give it time to get `happy`.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'411925A5684B6C47A2AAD621AF073C2E', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 5, NULL, NULL, 2, NULL, 0, 'I tried this multiple ways and what seemed to work best for me was to make a ball of sausage about 1-inch in diameter then press it out flat and thin in my hand.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D93D369FE852E34E9C69CFAD34DED104', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 6, NULL, NULL, 2, NULL, 0, 'I then laid the shrimp onto the sausage and wrapped the sausage around the body of the shrimp. Note: This is quick and easy if the shrimp are dry and the sausage is cold.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2FE6E9C2175CD4B934195719C18167A', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle some of my original rub onto the sausage and lay the completed appetizers on a Weber grill pan or Bradley rack.They are ready to smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'648FAA2150B5B24FBE596CB42DB13B11', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 8, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 240°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A22CC29A58D6AC44B27CF95C311E2A2B', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 9, NULL, NULL, 2, NULL, 0, 'If your smoker utilizes a water pan, fill it with hot water to create a moist environment.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C2DD60E9173874B9E09AD47BD8CFB17', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 10, NULL, NULL, 2, NULL, 0, 'For smoke, I chose some cherry but almost any light tasting wood will work such as pecan, apple or any fruit wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2402A88F6C17654F863A45CF134BDDDA', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 11, NULL, NULL, 2, NULL, 0, 'When your smoker is ready to go, place the shrimp appetizers on the smoker grate and let them cook for about 45 minutes with light smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A84EC5AC9C8C2E48B5AC790E0C71EF8B', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 12, NULL, NULL, 2, NULL, 0, 'You may want to check the temperature at the center of the sausage with a Thermapen at around 30 minutes to make sure everything is going as it should.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C776CF5448073C40BD41ED9F490BB5B8', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 13, NULL, NULL, 2, NULL, 0, 'The sausage needs to reach 160°F for it to be safe at which point the shrimp inside will be just perfect.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01622F2A9C701146A1E08A2CFBA09A33', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 14, NULL, NULL, 2, NULL, 0, 'When the sausage wrapped shrimp appetizers are finished cooking, remove them from the smoker right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54D7B65E573991459D54881E77F60132', X'2DBE35F4BED7D044B021E2AC1DE33BA4', X'2DBE35F4BED7D044B021E2AC1DE33BA4', 15, NULL, NULL, 2, NULL, 0, 'Serve these up with some warm barbecue sauce on the side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'176ADEBF08FE034CABEA899F7B5D503D', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 0, NULL, NULL, 2, NULL, 0, 'Fill a large container such as a pitcher or bowl with 40 ounces (1 quart + 1 cup) cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A43C82D076FFEE44B6455FEF253B8692', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 1, NULL, NULL, 2, NULL, 0, 'Add two 12-0z bottles of beer');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ACD439739DF13418B7CF7FF8127D597', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 2, NULL, NULL, 2, NULL, 0, 'Stir in ½ cup of kosher salt and continue to stir until salt is diluted into the water and the water becomes clear.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0CCE8E7359E874FA6F594D554D1E071', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 3, NULL, NULL, 2, NULL, 0, 'Stir in ½ cup brown sugar making sure it is melted into the water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7233FE21FD6ABC438A8142AA8BCDC492', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 4, NULL, NULL, 2, NULL, 0, 'Add the drumsticks to a lidded container for brining.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'992573C12A00564F8E19E8811A48A218', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 5, NULL, NULL, 2, NULL, 0, 'If you see any large clumps of fat or extra skin, now is a great time to go ahead and remove it with kitchen shears or a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A45876E01B9AC3479113D3E5CAC7E6C7', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 6, NULL, NULL, 2, NULL, 0, 'Once all of the chicken is in the container, pour the brine over the chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35370F89F2D753468936478BDFF6C861', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 7, NULL, NULL, 2, NULL, 0, 'Cover with the lid, plastic wrap, etc. and place the brining chicken into the fridge for 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E11A0BCF617514FB4371D48B781BEE4', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 8, NULL, NULL, 2, NULL, 0, 'At the end of the brining time, discard the liquid brine and rinse each piece of chicken to remove any residual salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B10603405BF824B93D024CFA6B194C5', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 9, NULL, NULL, 2, NULL, 0, 'Lay the chicken on several layers of paper towels to drain before proceeding to the next step.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A20CD900BEA7E14BA104225A341B840F', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 10, NULL, NULL, 2, NULL, 0, 'Drizzle olive oil or vegetable oil onto the chicken and use a basting brush to spread it out.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22F729207B4AC445B47D0F5FAE9F24AF', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 11, NULL, NULL, 2, NULL, 0, 'Move the chicken around to make sure it is well coated.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00FED42A86B24C4685752845F71A085E', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 12, NULL, NULL, 2, NULL, 0, 'Season generously with Jeff''s Texas style rub then roll each piece over and season the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0065E679C07F904E907EAD1C01FD9B55', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 13, NULL, NULL, 2, NULL, 0, 'Make sure the Texas style rub recipe is on all sides of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F8BBA1FDC7FEB4BA3C32AB0013555B3', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 14, NULL, NULL, 2, NULL, 0, 'Chop 2-3 tablespoons of parsley and chives and sprinkle it all over the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B766890BA2B834D9AD72EE9FD08DDAB', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 15, NULL, NULL, 2, NULL, 0, 'Setup your smoker or grill for cooking at 275-300°F with indirect heat. You can cook them lower at around 225°F but the skin will not have a good bite through.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'753B585142B09E418DAD3499E4549522', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 16, NULL, NULL, 2, NULL, 0, 'If you normally use a water pan, I recommend leaving it dry. When cooking chicken legs, thighs or quarters, I seem to get better results in a smoking environment with lower humidity.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3071AFA7F46E7747B56F53D2B40B2B35', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 17, NULL, NULL, 2, NULL, 0, 'Keep the smoke going for the entire time for great flavor. I recommend cherry, apple or pecan if you have any of these.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD89990F11E01E439237311A8A386012', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 18, NULL, NULL, 2, NULL, 0, 'Use a quick read thermometer to check the temperature after about 45 minutes or you can use a thermometer with a leave-in probe to keep you aware of the temperature throughout the entire process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'144507528D78DC46BAA78A1610329193', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 19, NULL, NULL, 2, NULL, 0, 'When the drumsticks reach 160°F, spoon some beer barbecue sauce onto the drumsticks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04E098E4F2DFE34E85DB4B377B96C559', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 20, NULL, NULL, 2, NULL, 0, 'Beer barbecue sauce is simply about a cup of my barbecue sauce mixed with equal parts of beer or until it gets to the consistency that you like.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7E8958BCECFF04B8DE50AF495F1F4AE', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 21, NULL, NULL, 2, NULL, 0, 'I say `spoon it` simply because we do not want to disturb or brush off the seasoning that we applied. A turkey baster would also work well to pick up some of the sauce and apply it to the drumsticks in a way that does not wash away the seasoning.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D4B5B1056C13749A763FAF88F9CC478', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 22, NULL, NULL, 2, NULL, 0, 'The chicken is done at 165°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'872D2A3164564A41BFAABCF88204AAEB', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 23, NULL, NULL, 2, NULL, 0, 'When cooking chicken, go high with the heat if you can or smoke it for half the time and then use the oven or grill to finish if that''s what you have to do.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'969120129825FB419BD62630BF5DF0E7', X'12BFAAB94C8DDC409A7A4D11123D9F7C', X'12BFAAB94C8DDC409A7A4D11123D9F7C', 24, NULL, NULL, 2, NULL, 0, 'Once the chicken is done, serve it up right away while it''s still hot!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7002150D899D9741B52AE7BBB1669EAF', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 0, NULL, NULL, 2, NULL, 0, 'Remove all of the netting and wrapping from the ham and lay the ham on a cutting board or preferably down into a large foil pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90DD0D2FD7D02044A03241C01289E508', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 1, NULL, NULL, 2, NULL, 0, 'Pull the slices apart one by one and drizzle honey down in there.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8493D2C6D31C0E4CAB070CB12382DFF7', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 2, NULL, NULL, 2, NULL, 0, 'Add more honey to the top of the ham and the flat or front area. Spread it out with a brush or your hands.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CA1AA55FF139748BE944BA04986B653', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 3, NULL, NULL, 2, NULL, 0, 'Pull back the slices once again and sprinkle a bit of rub down in between each one. Oh so good!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A602C9958EC8454A976EAF3A35B23967', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 4, NULL, NULL, 2, NULL, 0, 'Now apply rub all over the outside of the ham including the flat area in the front.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'667E889F0E07A547B209271D1D86D63C', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 5, NULL, NULL, 2, NULL, 0, 'To finish things off, drizzle more honey all over the top,sides and front of the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B1D45826DF0B1438C0D91564CB72A74', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 6, NULL, NULL, 2, NULL, 0, 'Tip: if the slices start to fall down in the front, use toothpicks to pin them up. I figured this out about halfway through the smoking process');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6312E4D6F96C6E4DAF8FFBF456546FA9', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 7, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF83CD44FCCCC1439BF3927B05212B3F', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 8, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, fill it with hot water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4CBBBE544DA7649BE82AC85D8ABB369', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 9, NULL, NULL, 2, NULL, 0, 'We are going to apply light apple smoke for the full 3 hours that the ham is in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4643E3AECA814446B7C9E4F6A1F04020', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 10, NULL, NULL, 2, NULL, 0, 'Most store bought hams do well if you smoke them at 230 to 240°F for about 3 hours. These are already cooked and the process is only to add more smoke flavor and to add seasoning and perhaps crisp up the outside here and there. If you want to smoke for less time, that is perfectly acceptable but results may vary.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E141D65AAA43EC48A341F508338C120B', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 11, NULL, NULL, 2, NULL, 0, 'When the smoker is preheated, place the pan with the ham into the smoker. The pan will catch drippings and keep your smoker cleaner.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E543F19D6038E4A93F31795F930EC4C', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 12, NULL, NULL, 2, NULL, 0, 'Note: Some smokers such as the Bradley advise strongly against using aluminum pans in their smokers. I recommend that you follow the manufacturers advice for the safety and well-being of your smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B20B2232400194BA7F7ECB4DFE41632', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 13, NULL, NULL, 2, NULL, 0, 'After about 2 hours, you can opt to make the pineapple/root beer glaze and pour it over the ham. This is optional but recommended.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4051C0059CEFBD4AB14B5AB44CC0D848', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 14, NULL, NULL, 2, NULL, 0, 'Make the glaze using 1 cup of root beer, ¼ cup of brown sugar, 1 TBS of butter, 2 TBS of Jeff''s original rub and ½ cup crushed pineapple (undrained).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1800501297F4B4E96F21D46391659A8', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 15, NULL, NULL, 2, NULL, 0, 'Add the ingredients into a sauce pan over medium-high heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F586E7395B33A44992D375BA0AC6E943', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 16, NULL, NULL, 2, NULL, 0, 'Bring to a boil and let it reduce for 6-8 minutes to thicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F88252E518ACFA4AADD61E9DCBFAA1D2', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 17, NULL, NULL, 2, NULL, 0, 'When it''s finished, gently pour it all over the top of the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8E380D931AD8E4E8BD950BEDCA15452', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 18, NULL, NULL, 2, NULL, 0, 'Alternative glazing method if you did not use a pan in the smoker: Remove the ham from the smoker about 15 minutes early, place it in a oven safe pan or dish and pour the pineapple/root beer glaze all over the top of the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97A7ACC72D625A45B87A574474CF56E4', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 19, NULL, NULL, 2, NULL, 0, 'Place into an oven preheated to 275°F for about 10 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88F9C335A57FED41ABD1106DFE12E708', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 20, NULL, NULL, 2, NULL, 0, 'After 3 hours of smoking/cooking time, the ham is finished and can be removed from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58D0D45FE8F16F41B9333A37E56BF3E6', X'F4833A63066CCC498C349D5B0BB8C924', X'F4833A63066CCC498C349D5B0BB8C924', 21, NULL, NULL, 2, NULL, 0, 'Remove the ham from the pan onto a serving platter and call dinner.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FA9479134226E46B31693491CB830B6', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 0, NULL, NULL, 2, NULL, 0, 'I used a simple buttermilk brine on the lamb shoulder chops as follows: 1 quart buttermilk, ¼ cup kosher salt (coarse), 1 cup (8 ounces) cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F03859D23F784244819E191A5BE4C231', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 1, NULL, NULL, 2, NULL, 0, 'Pour the buttermilk and water into a large pitcher. Add the salt and stir for about 30 seconds to make sure the salt is dissolved into the liquid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F038FD2DB086B141A0546CE4D07897B1', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 2, NULL, NULL, 2, NULL, 0, 'Note: This was enough to cover about 4 chops. If you are making more, you may need to double the recipe or adjust the recipe as needed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869A8F4BA1BE9944964451DA0AE4C433', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 3, NULL, NULL, 2, NULL, 0, 'Place the lamb shoulder chops into a bowl or zip top bag');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1540149B2439BC4BAA5564C36CF76CFD', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 4, NULL, NULL, 2, NULL, 0, 'Pour the buttermilk brine over the top of them to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56D8485018E59F42AB431434887478C0', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 5, NULL, NULL, 2, NULL, 0, 'Place the chops in the fridge for 3-4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6394031CB72BDA458DF2FFBE38DEA529', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 6, NULL, NULL, 2, NULL, 0, 'When the brining time has finished, discard the buttermilk brine and rinse the meat with cold water to remove any residual salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64D34741AD494C4D8DEF57CFF057CBC3', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 7, NULL, NULL, 2, NULL, 0, 'Drizzle some olive oil onto the top side of the chops and use a brush to spread it out.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC3BB1AF28F4624AA3F50C21A23B8081', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 8, NULL, NULL, 2, NULL, 0, 'Generously sprinkle Jeff''s Texas style rub onto the top side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73593871DA4FF74A8051D0000BF11032', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 9, NULL, NULL, 2, NULL, 0, 'Flip the meat over and do the olive oil and Texas style rub again on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E64EF32C453A0246893EB5E8E7ACC8C2', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 10, NULL, NULL, 2, NULL, 0, 'Let the lamb chops sit there while you go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'915F489F6A1CA4409AB9854369E44CFD', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 11, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at at about 240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E6CF320C4587F469E5A123115040FAC', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 12, NULL, NULL, 2, NULL, 0, 'If you smoker has a water pan, fill it with hot water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B73A5292F796DA46B5755B41D81CEDFB', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 13, NULL, NULL, 2, NULL, 0, 'Make sure you have enough smoking wood available for about 30 minutes of light smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E068BE1975EEB41A7FD5078DDA4D853', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 14, NULL, NULL, 2, NULL, 0, 'Once the smoker is preheated and ready to go, place the lamb shoulder chops on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6B46437ACC22D40B8D6E2E118FCDCEA', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 15, NULL, NULL, 2, NULL, 0, 'Watch them carefully as these may cook fast and especially if they are on the thin side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA022C9BCC0C6941B728BB4CF532322D', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 16, NULL, NULL, 2, NULL, 0, 'When they reach about 110-115°F it''s time to move them to a very hot grill or under the broiler of your oven to finish up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD16A4543AE1B545A71C6A8CD4589F8A', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 17, NULL, NULL, 2, NULL, 0, 'Tip: Use a fast reading thermometer such as the Thermapen to cook these perfectly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0638348CBFFE944E8BACF4863B9F40CE', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 18, NULL, NULL, 2, NULL, 0, 'Place the meat on the grill or under the oven broiler and watch them very carefully.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09F492540DA77443945E1EE82DD4CE09', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 19, NULL, NULL, 2, NULL, 0, 'When they reach just a little above medium rare, they are done. I like to take these to about 140-145°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6266F7BB5ECCCD498FCED9E9FED8B4A5', X'CFB146B429D2E4498F267DF70D414EA9', X'CFB146B429D2E4498F267DF70D414EA9', 20, NULL, NULL, 2, NULL, 0, 'When they are finished, serve them right away for best results. If you have to hold them for a few minutes, tent some foil over them during the wait.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE1972DD7202CA4398CE66CD378828B4', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 0, NULL, NULL, 2, NULL, 0, 'Make the steak marinade by placing all of the following ingredients into a bowl and stirring to combine: ¾ cup lemon lime soda (Sprite, 7-Up, etc.), ¼ cup soy sauce (low sodium is best), 3 TBS apple cider vinegar, 2 TBS Worcestershire sauce, 2 TBS Jeff''s original rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C495FFF90A3B14F9A08122E27CA9B83', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 1, NULL, NULL, 2, NULL, 0, 'Lay the steaks onto a cutting board and cut into strips then into pieces that are about 1-inch wide.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BC86A46F58F34458CB73690D9127CB4', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 2, NULL, NULL, 2, NULL, 0, 'Put the steak pieces into a zip top bag and pour the steak marinade over the pieces to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A156D082DCF4F442BD36817A160F28CC', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 3, NULL, NULL, 2, NULL, 0, 'Seal the bag and place in the fridge for 4 hours or overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAEF1AC3AA13144FA1663CD206D37B14', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 4, NULL, NULL, 2, NULL, 0, 'While the meat is marinating, cut up the onions and squash so they are sized to fit onto a skewer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B3CE4F9B85A174782876DF25C008EB1', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 5, NULL, NULL, 2, NULL, 0, 'Place the squash into a bowl and coat generously with my Texas style rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0A3A934FED6FB44B90119A34B359A9D', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 6, NULL, NULL, 2, NULL, 0, 'When the meat is finished marinating, take it out of the fridge and discard the liquid in the bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FF1A8958D5E37429DA6AA8106CBD1EE', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 7, NULL, NULL, 2, NULL, 0, 'Pour the pieces of meat into a plate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC64259506AB045B5DC4D51EFFB47B4', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 8, NULL, NULL, 2, NULL, 0, 'Thread the meat and vegetables in alternating patterns onto long skewers and place onto a grilling pan or Bradley rack.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A29FCE6DE14E4C4D8F495938095F9863', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 9, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 230°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A32ED2B0DC2FC4D81F49C26A91D8F6F', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 10, NULL, NULL, 2, NULL, 0, 'Smoke cook the skewers for about 45-60 minutes or until the meat reaches 140°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB3533ED3ECB054BBF62A3C18DEA0984', X'1A58CB5B00F5E445AD55C33D47280750', X'1A58CB5B00F5E445AD55C33D47280750', 11, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFA570EFA7E7A244A8033F4F407DC852', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 0, NULL, NULL, 2, NULL, 0, 'Soak corned beef in water to remove excess salt for at least 4 hours (overnight is better).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EC980D905087D4695A1EF7C8BA3EFF7', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 1, NULL, NULL, 2, NULL, 0, 'Dry meat with paper towels and place into a foil pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C1F69596AAC06499B8C3CDD8567C8C3', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 2, NULL, NULL, 2, NULL, 0, 'Cover meat with Dijon mustard and Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1C7B0A39104304AAE0E08CE96D09E57', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 3, NULL, NULL, 2, NULL, 0, 'Massage the rub/mustard to cover the entire brisket flat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'818E4B9D3ECCBB4FB06E92D50B0679C0', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 4, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at about 240°F with a mix of hickory and peach or other smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AACD5BF96B41D4AB192B58B8B1138AB', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 5, NULL, NULL, 2, NULL, 0, 'Once smoker is ready, place pan with brisket onto grate and keep the heat and smoke going for at least 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC96DB6B0B18BA4B90B7323F48919A3B', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 6, NULL, NULL, 2, NULL, 0, 'After 4 hours the brisket can continue to cook with just heat for an additional 2-4 hours or until it reaches 195°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'166FA24F1E8C9544B71D3C9B3CAB246F', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 7, NULL, NULL, 2, NULL, 0, 'Baste the meat with a mixture of ¼ cup Dijon and ¼ cup of Jeff''s barbecue sauce when the meat reaches 180°F and again at 190°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98567CB6BF426141B787CB365ECF0432', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 8, NULL, NULL, 2, NULL, 0, 'When the meat reaches 195°F and is tender to your liking, remove from the heat and allow it to rest for at meat 30 minutes tented with foil over it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'105D39E53021344D8CCC627D861154BE', X'2C5CF3087AA792498B91FF58A7ABA065', X'2C5CF3087AA792498B91FF58A7ABA065', 9, NULL, NULL, 2, NULL, 0, 'Slice the meat across the grain into ½ inch pieces and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C32BB24EB9AED499ABA92DCE11E8371', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 0, NULL, NULL, 2, NULL, 0, 'Rinse pork chops under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C6604590174BD4D8182840D33C28D3E', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 1, NULL, NULL, 2, NULL, 0, 'Make a brine solution using ½ gallon of cold water and ½ cup of coarse kosher salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69299C020DD87C4FB539162A8BF2890F', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 2, NULL, NULL, 2, NULL, 0, 'Mix the water and salt together until the salt is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CE6D3BEC7C6004CBDF92118A816FE8D', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 3, NULL, NULL, 2, NULL, 0, 'Place the pork chops in a lidded container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A74E886883D7E41918B9042D8145A2F', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 4, NULL, NULL, 2, NULL, 0, 'Pour the brine solution over the chops until they are covered.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'520C14DDBE54F94882F090D219E288A9', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 5, NULL, NULL, 2, NULL, 0, 'Cover with a lid or plastic wrap and place in the fridge.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B12AC5ECF3F54E4FB878FF4F9A1D2F15', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 6, NULL, NULL, 2, NULL, 0, 'Brine for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF3C8EC9FDA2D949A31E9304A2759C31', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 7, NULL, NULL, 2, NULL, 0, 'Remove from brine and rinse to remove any extra salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D86530AEA4074B8F07F4C9966FEAAD', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 8, NULL, NULL, 2, NULL, 0, 'Sauté about a 1/3 cup mixture of celery, onions and bell peppers with 1 TBS of cooking oil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9811203056F91E49B3BD8818A1085214', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 9, NULL, NULL, 2, NULL, 0, 'Remove vegetables and brown 1 lb of breakfast sausage in the same pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CA93CB44958A64FBFD58A1A3507E3B6', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 10, NULL, NULL, 2, NULL, 0, 'Add the vegetables to the sausage to combine and remove from heat to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50B771844405284B858FDA9E43B2C7FB', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 11, NULL, NULL, 2, NULL, 0, 'Season pork chops with Jeff''s Texas style rub recipe inside the pocket and on the outside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00F1CD514755AD48B6F65D5FC51A8F0D', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 12, NULL, NULL, 2, NULL, 0, 'Stuff the chops with the sausage mixture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4798AAAE0E6771418A2EE60307966807', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 13, NULL, NULL, 2, NULL, 0, 'Leave the pork chops sitting pocket side up leaning against the side of a foil pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF5595C5E4F5AB4E94078DE38353EDE4', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 14, NULL, NULL, 2, NULL, 0, 'Place the pan of pork chops on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79DBE78124E5634BB0D15637DB5B5709', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 15, NULL, NULL, 2, NULL, 0, 'Smoke cook the meat for about 2 hours or until they reach 145°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCCB8E174BF0F047A3DDEE1DDB03C68B', X'059548FAFA716746869449B58E849719', X'059548FAFA716746869449B58E849719', 16, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1183FFF67AB32D498385943B16B8CFA7', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 0, NULL, NULL, 2, NULL, 0, 'Remove meat from wrapper');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98516F121AEEBB419E8F9AB05DDE5806', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 1, NULL, NULL, 2, NULL, 0, 'Inject cherry Dr Pepper into pork butt about every 2 inches.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5061AA1DE5ED3449DB26E65A188EE4B', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 2, NULL, NULL, 2, NULL, 0, 'Generously apply Jeff''s original rub to all sides of pork butt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1BE2ED660E1E246B8DD72260234962F', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 3, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C803955420AAA941947784ECCE3944C5', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 4, NULL, NULL, 2, NULL, 0, 'Place pork butt into smoker fat cap down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFE2A6ED1B74FC48BAB3EB3816D67485', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 5, NULL, NULL, 2, NULL, 0, 'When internal temperature of meat reaches 160°F, it can be wrapped in foil, if desired, to speed up the process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D19116C1A6C4E44FB013C4CA1E93F0AE', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 6, NULL, NULL, 2, NULL, 0, 'Let the internal temperature of the meat come to about 205°F before calling it done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F71CA1C6CD3624A9A39480BF6878D7D', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 7, NULL, NULL, 2, NULL, 0, 'Let the meat cool uncovered on the counter for about an hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0ABE5E843E28747A39E48605BDACB17', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 8, NULL, NULL, 2, NULL, 0, 'Remove the bone from the meat and discard.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64285D07D4C6A84F9C9892EA70D024F3', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 9, NULL, NULL, 2, NULL, 0, 'Pull the meat apart into sections removing any clumps of fat as you go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF91BBD03AAFC241B9478C01803FF5A5', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 10, NULL, NULL, 2, NULL, 0, 'Further pull the meat into smaller pieces discarding any fat or gristle that you find.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'680A0B05AC0B6244B154D67AACDCC7AF', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 11, NULL, NULL, 2, NULL, 0, 'Season the finished pulled pork again with Jeff''s original rub and stir around to disperse.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD4084309DAAFF41A27C975F67677C54', X'D0F4A7A3DC4609439D5D784C21A033F1', X'D0F4A7A3DC4609439D5D784C21A033F1', 12, NULL, NULL, 2, NULL, 0, 'The pulled pork is now ready to use as you wish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D81AD2DEF02EC34C92243B112F8B1F82', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 0, NULL, NULL, 2, NULL, 0, 'Make buttermilk brine by combining ½ gallon buttermilk with ½ cup coarse kosher salt. Stir for 2 minutes or until dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'898A0543A16F864BB91FE610F2F1B529', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 1, NULL, NULL, 2, NULL, 0, 'Place chicken into brine-safe lidded container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC56113E717FB642A411AD0555081872', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 2, NULL, NULL, 2, NULL, 0, 'Pour buttermilk brine over chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59C2CADA5A88C24093C4E0AEF8B7A387', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 3, NULL, NULL, 2, NULL, 0, 'Place container with chicken into fridge for 4 hours during brining.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55AD0B1F83F3CD408C959A26262F5569', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 4, NULL, NULL, 2, NULL, 0, 'Rinse chicken well to remove any residual salt and discard brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F390AB418ACE549B15A611824CAEA7D', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 5, NULL, NULL, 2, NULL, 0, 'Lay chicken thighs flat on cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F01F86774D3DC14BB9063CF63409F8D1', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 6, NULL, NULL, 2, NULL, 0, 'Season liberally with Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3955A8CC490884A9DF97E17CC60A8EA', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 7, NULL, NULL, 2, NULL, 0, 'Place ½ white cheddar cheese stick and 1 teaspoon of diced jalapeño onto chicken thigh');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CEEE775E88CA441A2F54E86FA452B5D', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 8, NULL, NULL, 2, NULL, 0, 'Fold chicken thigh over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93C903F27EE7974E8955F7ABE514783E', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 9, NULL, NULL, 2, NULL, 0, 'Wrap stretched piece of bacon around chicken thigh to hold it closed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE4018059ACF3843B7FA3258360E69DC', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 10, NULL, NULL, 2, NULL, 0, 'Wrap another piece of bacon from end to end to keep the cheese from coming out once it melts.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'626D0EB71F894F43801003776BBBB1A5', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 11, NULL, NULL, 2, NULL, 0, 'Smoke chicken thighs for about 1.5 hours at 250°F using apple or other smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A1684CE9701DB4692142000D0C1547C', X'85E93DDC73494A49BBDC0DE207FB6A67', X'85E93DDC73494A49BBDC0DE207FB6A67', 12, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F269B8F3FC740547BDA387D0A5EC9245', X'B533BBACC3C8FA40946977EA37934A01', X'B533BBACC3C8FA40946977EA37934A01', 0, NULL, NULL, 2, NULL, 0, 'Cut the meat into 1-inch cubes or pieces');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14E2944EB4EE9B458B44EC4A11893DFC', X'35F79F9A950E424CA8D74E48C9C90396', X'35F79F9A950E424CA8D74E48C9C90396', 0, NULL, NULL, 2, NULL, 0, 'Generously apply Jeff''s original rub all over the meat making sure to coat all sides');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FD428E7653BD34AA6986E00EE60686F', X'35F79F9A950E424CA8D74E48C9C90396', X'35F79F9A950E424CA8D74E48C9C90396', 1, NULL, NULL, 2, NULL, 0, 'Cover container with lid or plastic wrap.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25E8C208DD5A9B4D9B9AA73538CB9DB6', X'35F79F9A950E424CA8D74E48C9C90396', X'35F79F9A950E424CA8D74E48C9C90396', 2, NULL, NULL, 2, NULL, 0, 'Place meat in fridge for at least 4 hours but overnight is better to allow the meat to marinate and take on the flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61AED320C99BC44BBA3AD8DE337DD5CB', X'528B81AE02AB1F4CB485DA00C6F7BA02', X'528B81AE02AB1F4CB485DA00C6F7BA02', 0, NULL, NULL, 2, NULL, 0, 'Clean and deseed jalapeños.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9638D68916F5044990BD3EC555FAD19', X'528B81AE02AB1F4CB485DA00C6F7BA02', X'528B81AE02AB1F4CB485DA00C6F7BA02', 1, NULL, NULL, 2, NULL, 0, 'Cut peppers in half then cut into pieces about ¾ inch square');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFAA39AB3025234CA0E0954585EF0B5B', X'3162AC88EF7EE642ABC7F1D7EB31D2C2', X'3162AC88EF7EE642ABC7F1D7EB31D2C2', 0, NULL, NULL, 2, NULL, 0, 'Cut the bacon in half');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28C599230F36B842A09F56383DC11CCE', X'3162AC88EF7EE642ABC7F1D7EB31D2C2', X'3162AC88EF7EE642ABC7F1D7EB31D2C2', 1, NULL, NULL, 2, NULL, 0, 'Lay the half-strips of bacon into a shallow baking sheet and cook in 375°F oven for about 6 minutes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0429800D938C634CB049958055CCD968', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', 0, NULL, NULL, 2, NULL, 0, 'Cut long skewers to the length you want or about 4 inches long.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A509D71856BEB4DA8CE73812F70E0EF', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', 1, NULL, NULL, 2, NULL, 0, 'Wrap a single piece of meat in a half-slice of partially pre-cooked bacon');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD155E7B36BEE448B7AECAF61F35FFCB', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', 2, NULL, NULL, 2, NULL, 0, 'Push pointed end of skewers through a piece of pepper then into the bacon wrapped piece of meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C86A97EC76F51429B1D793874442153', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', X'0E1ACCD68E2CBD4A957B67DB6E61EE3F', 3, NULL, NULL, 2, NULL, 0, 'Continue with all meat until completed');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CDEFEBF5DC49B4E86A72C2071FF9A7F', X'26E7E0232CAA064896F6CDD1A841DF95', X'26E7E0232CAA064896F6CDD1A841DF95', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7B1F3C9DEC1F14D855DC3CAC5102D72', X'26E7E0232CAA064896F6CDD1A841DF95', X'26E7E0232CAA064896F6CDD1A841DF95', 1, NULL, NULL, 2, NULL, 0, 'Place the pops on the smoker grate and close the door');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFEE13DBCCB5AF428A14296CFC665522', X'26E7E0232CAA064896F6CDD1A841DF95', X'26E7E0232CAA064896F6CDD1A841DF95', 2, NULL, NULL, 2, NULL, 0, 'Once the pops are almost done, drizzle sauce on the top (optional)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BE82D2B1EB9274498F693B6F7DEDEFA', X'26E7E0232CAA064896F6CDD1A841DF95', X'26E7E0232CAA064896F6CDD1A841DF95', 3, NULL, NULL, 2, NULL, 0, 'Pops are done when they reach 145°F in the center (tenderloin) or 165 °F in the center for pork country style rib meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A334E518B5A4A4F939704F753904965', X'90E8F5B8F5AD4D48AE5BE17109312256', X'90E8F5B8F5AD4D48AE5BE17109312256', 0, NULL, NULL, 2, NULL, 0, 'Add 1 cup of coarse kosher salt to 1 gallon of cold water. Stir until water returns to clear and salt is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5351B8DC63DFE49AAD4EEC1CE01B789', X'90E8F5B8F5AD4D48AE5BE17109312256', X'90E8F5B8F5AD4D48AE5BE17109312256', 1, NULL, NULL, 2, NULL, 0, 'Add ¾ cup brown sugar and stir until dissolved into brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4027CAC5F428214B89BCA81A8DDC8C46', X'90E8F5B8F5AD4D48AE5BE17109312256', X'90E8F5B8F5AD4D48AE5BE17109312256', 2, NULL, NULL, 2, NULL, 0, 'Place chicken pieces into zip top bag and pour brine solution over chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4D78DE2E854D645A4AC7F967BFE519E', X'90E8F5B8F5AD4D48AE5BE17109312256', X'90E8F5B8F5AD4D48AE5BE17109312256', 3, NULL, NULL, 2, NULL, 0, 'Seal bag or cover brining container and put into the fridge for about 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'404A93C78282E749A97791E2BE258A4F', X'90E8F5B8F5AD4D48AE5BE17109312256', X'90E8F5B8F5AD4D48AE5BE17109312256', 4, NULL, NULL, 2, NULL, 0, 'Remove brined chicken from fridge and rinse well under cold water to remove residual salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6448FD48639964EAC0EB54985E8D631', X'90E8F5B8F5AD4D48AE5BE17109312256', X'90E8F5B8F5AD4D48AE5BE17109312256', 5, NULL, NULL, 2, NULL, 0, 'Pat the chicken dry with a paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D09646677C828E409D68A8AACEBA8F2E', X'90E8F5B8F5AD4D48AE5BE17109312256', X'90E8F5B8F5AD4D48AE5BE17109312256', 6, NULL, NULL, 2, NULL, 0, 'For better skin, place the chicken back into the fridge on a cooling rack, bradley rack, etc. over a baking sheet (to catch drips) for about 4 hours to dry and tighten the skin before applying glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04E7B62ABE004D499E47FA73BD035B14', X'6055F1C6A1043546B686906AE2C6CF47', X'6055F1C6A1043546B686906AE2C6CF47', 0, NULL, NULL, 2, NULL, 0, 'Make the glaze while the chicken is brining');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE60EC2702403D44B8CC4D59E9A25634', X'6055F1C6A1043546B686906AE2C6CF47', X'6055F1C6A1043546B686906AE2C6CF47', 1, NULL, NULL, 2, NULL, 0, 'Into a small mixing bowl combine ½ cup of real mayonnaise, 2 TBS of Jeff''s original rub, 1 TBS minced garlic and 2 TBS chopped chives');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19BF98E6317FF247B0B27C3F096FE154', X'0B60027F87001245BC637B5FF24F863E', X'0B60027F87001245BC637B5FF24F863E', 0, NULL, NULL, 2, NULL, 0, 'Place a heaping TBS of the mayo glaze onto each chicken quarter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FACDBAD39A92294587C00C479BBE57A8', X'0B60027F87001245BC637B5FF24F863E', X'0B60027F87001245BC637B5FF24F863E', 1, NULL, NULL, 2, NULL, 0, 'Use your hands to massage the glaze all over the chicken getting it under the skin wherever possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DAD6FB1B8AF764087133F1ED0E00585', X'0B60027F87001245BC637B5FF24F863E', X'0B60027F87001245BC637B5FF24F863E', 2, NULL, NULL, 2, NULL, 0, 'Chicken is now ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86845B1965ABCB4BA0F089576DA8BF3C', X'2F7B987AF4621840A5D39A82D50D444A', X'2F7B987AF4621840A5D39A82D50D444A', 0, NULL, NULL, 2, NULL, 0, 'Set up the smoker for cooking at 275°F using pecan or other smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EC686250B219040AF657A52D03EC8DE', X'2F7B987AF4621840A5D39A82D50D444A', X'2F7B987AF4621840A5D39A82D50D444A', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the chicken directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'123EEF5D0E6DEF4CA4AB2A35CF9D9920', X'2F7B987AF4621840A5D39A82D50D444A', X'2F7B987AF4621840A5D39A82D50D444A', 2, NULL, NULL, 2, NULL, 0, 'Let the chicken cook for 1 to 1.5 hours or until it reaches 165°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0F80E88105B6F4B936D97213FC72B5B', X'2F7B987AF4621840A5D39A82D50D444A', X'2F7B987AF4621840A5D39A82D50D444A', 3, NULL, NULL, 2, NULL, 0, 'For extra good chicken skin, remove the chicken before it is completely done (about 145-150°F) and place it on a very hot grill or under the oven broiler for a few minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F167C3A8D6F60746824A686DB7DCC422', X'2F7B987AF4621840A5D39A82D50D444A', X'2F7B987AF4621840A5D39A82D50D444A', 4, NULL, NULL, 2, NULL, 0, 'Be sure it reaches a final temperature of 165°F before calling it done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3343241025E53B45A9DDF534D050437E', X'10E0BE46B9A2394096BF88079DF44BD6', X'10E0BE46B9A2394096BF88079DF44BD6', 0, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC32D8104B83F4CBF67427ADA03C7AE', X'65FFAEC0214F824A8E6B1AC99A5A40FF', X'65FFAEC0214F824A8E6B1AC99A5A40FF', 0, NULL, NULL, 2, NULL, 0, 'Lay the brisket fat side up on the cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46FEEAA32CECC244B9B60AB0EBE8C4A5', X'65FFAEC0214F824A8E6B1AC99A5A40FF', X'65FFAEC0214F824A8E6B1AC99A5A40FF', 1, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut a crosshatch pattern through the fat down to the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FAF2131ED29C54C87FA6665080B001B', X'65FFAEC0214F824A8E6B1AC99A5A40FF', X'65FFAEC0214F824A8E6B1AC99A5A40FF', 2, NULL, NULL, 2, NULL, 0, 'Make the cuts about 1 inch apart.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'804451EC5CF4C54790432199D1254796', X'936C2B787928D34AA8285682AB42B80F', X'936C2B787928D34AA8285682AB42B80F', 0, NULL, NULL, 2, NULL, 0, 'Flip the brisket over so that it is fat side down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86EBFFF058A39D4FAF087D3CE6FBDD05', X'936C2B787928D34AA8285682AB42B80F', X'936C2B787928D34AA8285682AB42B80F', 1, NULL, NULL, 2, NULL, 0, 'Take notice of the direction the meat fibers are running.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0818E9371270FD41BDEE9F22ADB2DA9B', X'936C2B787928D34AA8285682AB42B80F', X'936C2B787928D34AA8285682AB42B80F', 2, NULL, NULL, 2, NULL, 0, 'On the corner edge, make a cut halfway through the meat across those meat fibers.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'153248065825B943B7F78BF85AC1D2C1', X'936C2B787928D34AA8285682AB42B80F', X'936C2B787928D34AA8285682AB42B80F', 3, NULL, NULL, 2, NULL, 0, 'This is to mark the grain direction so you will know how to slice it once it''s finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D30427D7D9BF248BA7A552714827330', X'D2D29CC59E111448B47369A85DF8B571', X'D2D29CC59E111448B47369A85DF8B571', 0, NULL, NULL, 2, NULL, 0, 'Apply a thin coat of spicy mustard to the top and sides of the brisket.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C8A362E5D0C354CB79ACE43064BDD0B', X'D2D29CC59E111448B47369A85DF8B571', X'D2D29CC59E111448B47369A85DF8B571', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s original rub or the Texas style rub onto the area coated with mustard.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B23623604181F04FB466A7EF2CE181E8', X'D2D29CC59E111448B47369A85DF8B571', X'D2D29CC59E111448B47369A85DF8B571', 2, NULL, NULL, 2, NULL, 0, 'Flip the brisket over and repeat the mustard and rub on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F60D04766A874BB1F1B8F8136E1266', X'4908C870D7A34D479E6B58AB7833EC5D', X'4908C870D7A34D479E6B58AB7833EC5D', 0, NULL, NULL, 2, NULL, 0, 'Setup and preheat smoker at about 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9D102D31B25104983D91BE79A1E9724', X'4908C870D7A34D479E6B58AB7833EC5D', X'4908C870D7A34D479E6B58AB7833EC5D', 1, NULL, NULL, 2, NULL, 0, 'Place the brisket on the smoker grate fat side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B09F65E8804FE468777E04BDE1FAB61', X'4908C870D7A34D479E6B58AB7833EC5D', X'4908C870D7A34D479E6B58AB7833EC5D', 2, NULL, NULL, 2, NULL, 0, 'Allow the brisket to cook for 18-22 hours or until it reaches 195-205°F in the center of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E4A3FFC743F924280060616FEE80423', X'91B9D13E2F4DA640816480C10052ADB5', X'91B9D13E2F4DA640816480C10052ADB5', 0, NULL, NULL, 2, NULL, 0, 'Once the brisket is finished cooking, place it in the foil lined bottom of an empty cooler.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AF9FB0E50D98E45A84B2725E9D4A318', X'91B9D13E2F4DA640816480C10052ADB5', X'91B9D13E2F4DA640816480C10052ADB5', 1, NULL, NULL, 2, NULL, 0, 'Cover top with foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99F62CF55A4B4E4CB222176D3B0A756B', X'91B9D13E2F4DA640816480C10052ADB5', X'91B9D13E2F4DA640816480C10052ADB5', 2, NULL, NULL, 2, NULL, 0, 'Place towels and/or newspaper over the brisket to help insulate it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D4AC2FFF4B42842AED3898CF0CE15C8', X'91B9D13E2F4DA640816480C10052ADB5', X'91B9D13E2F4DA640816480C10052ADB5', 3, NULL, NULL, 2, NULL, 0, 'Leave in cooler for 2-4 hours to tenderize.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E1097E8FBCE4948BA882C89E82C4B49', X'221C56A3EB35E94C834A40DD684B004F', X'221C56A3EB35E94C834A40DD684B004F', 0, NULL, NULL, 2, NULL, 0, 'Remove brisket from cooler and place on cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A25F60AA3C2EC04FBAA541E68479729F', X'221C56A3EB35E94C834A40DD684B004F', X'221C56A3EB35E94C834A40DD684B004F', 1, NULL, NULL, 2, NULL, 0, 'Slice into pencil thick slices across the grain using the grain marker cut into the corner as a guide.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B3089B44C41E947AE75BDA685E8B612', X'221C56A3EB35E94C834A40DD684B004F', X'221C56A3EB35E94C834A40DD684B004F', 2, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'851C766E8E748A4FB924285D4B26A3B2', X'E403E95557AA1A489CC44F1554106872', X'E403E95557AA1A489CC44F1554106872', 0, NULL, NULL, 2, NULL, 0, 'Ask the butcher to cut off the bones and then tie it all back up for you. You can also ask him to `french` the bones for you to make it look real nice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'349183F9AF407043B0CA62BBA46A36D5', X'AD0CBC9DC73FB74C9C5A463A46CDB27F', X'AD0CBC9DC73FB74C9C5A463A46CDB27F', 0, NULL, NULL, 2, NULL, 0, 'Apply a good coating of oil to the entire outside surface of the prime rib to help the seasoning to stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D6E1F364EB91D46A358B77E8445A72B', X'AD0CBC9DC73FB74C9C5A463A46CDB27F', X'AD0CBC9DC73FB74C9C5A463A46CDB27F', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s Texas style rub onto the meat on all sides. Don''t forget the ends.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FADDABB88FA034B85040E1704933C80', X'44ABA16CDB454A4F88FAA1269E9927F8', X'44ABA16CDB454A4F88FAA1269E9927F8', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'491926A4B16C354CB2422973C4AB849D', X'44ABA16CDB454A4F88FAA1269E9927F8', X'44ABA16CDB454A4F88FAA1269E9927F8', 1, NULL, NULL, 2, NULL, 0, 'Apply light smoke for the entire time if possible or for at least 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6FB7E7FBE64FC44BD0C78CFB647FAC1', X'44ABA16CDB454A4F88FAA1269E9927F8', X'44ABA16CDB454A4F88FAA1269E9927F8', 2, NULL, NULL, 2, NULL, 0, 'Monitor the temperature and when the temperature reaches about 130°F, remove it from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5909FFD43FF6F46A130E8A3C53D0054', X'44ABA16CDB454A4F88FAA1269E9927F8', X'44ABA16CDB454A4F88FAA1269E9927F8', 3, NULL, NULL, 2, NULL, 0, 'Tent foil over the top of the meat and let it rest for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E43862243862642BD34672C31732874', X'07B1A4F7991F194A949E91ADAB7741A0', X'07B1A4F7991F194A949E91ADAB7741A0', 0, NULL, NULL, 2, NULL, 0, 'Cut the strings that are holding the roast and bones together.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23BC6EEFE9E75841AA83886BEF5F1FA3', X'07B1A4F7991F194A949E91ADAB7741A0', X'07B1A4F7991F194A949E91ADAB7741A0', 1, NULL, NULL, 2, NULL, 0, 'Remove the rack of bones and set them aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57F36A2723C0124C9B7EA68474FC41FB', X'07B1A4F7991F194A949E91ADAB7741A0', X'07B1A4F7991F194A949E91ADAB7741A0', 2, NULL, NULL, 2, NULL, 0, 'Slice the roast into ½ to ¾ inch slices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEE48A07CE029A419B2611BABF071763', X'07B1A4F7991F194A949E91ADAB7741A0', X'07B1A4F7991F194A949E91ADAB7741A0', 3, NULL, NULL, 2, NULL, 0, 'Serve to your guests and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'990796B8F59B264C93A918512912DCF1', X'765B1C9DCCA94541AB5E6C1A241AD941', X'765B1C9DCCA94541AB5E6C1A241AD941', 0, NULL, NULL, 2, NULL, 0, 'Place the ham on a cutting board or down in a pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3A89EF61CE3894985657E9D44DDB1E2', X'765B1C9DCCA94541AB5E6C1A241AD941', X'765B1C9DCCA94541AB5E6C1A241AD941', 1, NULL, NULL, 2, NULL, 0, 'Squeeze on some regular yellow mustard and rub it over the entire surface area of the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E29718AF6E5924D821CE9C445C4FF27', X'0F65A28C58CD8A4BBA2FEAC831E4B110', X'0F65A28C58CD8A4BBA2FEAC831E4B110', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle about a half cup of Jeff''s rub onto the top of the ham and rub it over the mustard creating a paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C2ECDC5719D5440B943C690D6DE73D6', X'0F65A28C58CD8A4BBA2FEAC831E4B110', X'0F65A28C58CD8A4BBA2FEAC831E4B110', 1, NULL, NULL, 2, NULL, 0, 'Be sure to cover the entire surface of the ham with rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BE99437FED6484B9CC045D84DE552B0', X'0F65A28C58CD8A4BBA2FEAC831E4B110', X'0F65A28C58CD8A4BBA2FEAC831E4B110', 2, NULL, NULL, 2, NULL, 0, 'If you need more rub, don''t be afraid to use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50BE8915FFC8FB47844188B7A9C8CF50', X'0F65A28C58CD8A4BBA2FEAC831E4B110', X'0F65A28C58CD8A4BBA2FEAC831E4B110', 3, NULL, NULL, 2, NULL, 0, 'When the ham is ready to cook, place it on a Bradley rack or a cookie sheet to make it easy to transport to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6FE48E96393C9429B5D0570DDAABD85', X'07E686961F78DC4DA4EF43DF26B5ABE9', X'07E686961F78DC4DA4EF43DF26B5ABE9', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F. We aren''t trying to cook the ham, just bring it to a nice eating temperature of about 140°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D48694D85D4C5F4EB1922C3C77B79886', X'07E686961F78DC4DA4EF43DF26B5ABE9', X'07E686961F78DC4DA4EF43DF26B5ABE9', 1, NULL, NULL, 2, NULL, 0, 'When the smoker is ready, place the ham directly on the grate or if you are using a Bradley rack, you can lay the rack directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7B57507260CD8489309B699B76F90C4', X'07E686961F78DC4DA4EF43DF26B5ABE9', X'07E686961F78DC4DA4EF43DF26B5ABE9', 2, NULL, NULL, 2, NULL, 0, 'Let the ham cook for about 3 hours or until it reaches an internal temperature of 140°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C295E0F4D5E7D24FB27207D92EF567BD', X'07E686961F78DC4DA4EF43DF26B5ABE9', X'07E686961F78DC4DA4EF43DF26B5ABE9', 3, NULL, NULL, 2, NULL, 0, 'Keep the smoke going the entire time if you are using an electric, gas or charcoal smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25BE5432CB8F5A4B94B36019EC9A5B48', X'7873695DC3C0754580E50CB5A17FD07F', X'7873695DC3C0754580E50CB5A17FD07F', 0, NULL, NULL, 2, NULL, 0, 'When the ham is finished, it can be served immediately or wrapped in foil and kept warm until dinner if necessary.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3F5684DFC26CF45B347F7210D4FA432', X'285E9A944E66484F869F782D00FA632B', X'285E9A944E66484F869F782D00FA632B', 0, NULL, NULL, 2, NULL, 0, 'Remove the membrane from the boney side of the ribs. Lift it up using a butter knife or similar then use a paper towel or catfish pliers to get a good grip and pull it clean off.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'842F8F8169BAB440B8C992940BE9212B', X'C8AFC2952A8D2E4292DD9A17C2CB3151', X'C8AFC2952A8D2E4292DD9A17C2CB3151', 0, NULL, NULL, 2, NULL, 0, 'Coat both sides of the ribs with maple syrup and Jeff''s original rub (recipe). Brush on the maple syrup then sprinkle on the rub to cover well. Allow the rub to sit on the first side until it starts looking wet then flip it over and repeat on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A41BC2AF089BE4F8E46A6B89C52A98D', X'5A9D40A95A32D242AA033780CE74BED0', X'5A9D40A95A32D242AA033780CE74BED0', 0, NULL, NULL, 2, NULL, 0, 'Place seasoned ribs into a lidded container or wrap them in plastic wrap and place into fridge overnight to enhance the flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F593A44E0585EC448291A1926E62030C', X'20CB89096A03D349BBFDE1F081AD8E7D', X'20CB89096A03D349BBFDE1F081AD8E7D', 0, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at about 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE06DB1A60FF5646A8C9A849FEA13DDC', X'20CB89096A03D349BBFDE1F081AD8E7D', X'20CB89096A03D349BBFDE1F081AD8E7D', 1, NULL, NULL, 2, NULL, 0, 'Have enough smoking wood on hand to maintain light smoke for about 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1A353FF29E6264A85313B6379D585B5', X'20CB89096A03D349BBFDE1F081AD8E7D', X'20CB89096A03D349BBFDE1F081AD8E7D', 2, NULL, NULL, 2, NULL, 0, 'Once smoker is ready, place ribs directly on smoker grate and close door/lid of smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41D16DE5BB09CA469DEFB5926615905C', X'20CB89096A03D349BBFDE1F081AD8E7D', X'20CB89096A03D349BBFDE1F081AD8E7D', 3, NULL, NULL, 2, NULL, 0, 'When the ribs have been cooking for about 3 hours, baste them with the maple barbecue glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76E77B5BD6648A4EAEFC7D390EB8EFB6', X'9011989A4FF90F4F85BDF5D820320DB9', X'9011989A4FF90F4F85BDF5D820320DB9', 0, NULL, NULL, 2, NULL, 0, '-¼ cup of maple syrup');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1173AF773606C4A8FD9051740D8486E', X'9011989A4FF90F4F85BDF5D820320DB9', X'9011989A4FF90F4F85BDF5D820320DB9', 1, NULL, NULL, 2, NULL, 0, '-2 TBS of Jeff''s original rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B185C52FD870984FA906A4F8AE1397D6', X'9011989A4FF90F4F85BDF5D820320DB9', X'9011989A4FF90F4F85BDF5D820320DB9', 2, NULL, NULL, 2, NULL, 0, 'Mix ingredients together and stir well to combine. Continue to stir during use to maintain proper mixture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FA44A0C6E3A5546B5477A8FF619DAB4', X'4BFB79A6178A3546B70C8A8CD2C45670', X'4BFB79A6178A3546B70C8A8CD2C45670', 0, NULL, NULL, 2, NULL, 0, 'Let the ribs continue to cook/smoke for about 1.5 hours then glaze them again with the maple barbecue glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'456F1C574B25594AB77632BB8CD460B9', X'4BFB79A6178A3546B70C8A8CD2C45670', X'4BFB79A6178A3546B70C8A8CD2C45670', 1, NULL, NULL, 2, NULL, 0, 'Total time is about 5 hours. If the ribs are extra meaty, they may require additional cooking time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FAA16C7BB53844D9D43F9170DCA9119', X'4BFB79A6178A3546B70C8A8CD2C45670', X'4BFB79A6178A3546B70C8A8CD2C45670', 2, NULL, NULL, 2, NULL, 0, 'The ribs are finished when they are tender.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35EF806AFB9F2442A2CCF8CD77524D7A', X'4BFB79A6178A3546B70C8A8CD2C45670', X'4BFB79A6178A3546B70C8A8CD2C45670', 3, NULL, NULL, 2, NULL, 0, 'Check for tenderness by lifting up one end. The ribs will bend in half when they are fully done, tender and ready to eat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E89629D035B7524E8852F97D935ED9FB', X'4BFB79A6178A3546B70C8A8CD2C45670', X'4BFB79A6178A3546B70C8A8CD2C45670', 4, NULL, NULL, 2, NULL, 0, 'You can also pull apart on 2 adjacent rib bones to test the tenderness and looseness of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB6FC52BEEF27F4BACFB808B90FBE096', X'C13B352370008D44BEACE623C1C93EE7', X'C13B352370008D44BEACE623C1C93EE7', 0, NULL, NULL, 2, NULL, 0, 'Place the ribs boney side up on the cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7D30377C414434CBA7800DFA2D3332A', X'C13B352370008D44BEACE623C1C93EE7', X'C13B352370008D44BEACE623C1C93EE7', 1, NULL, NULL, 2, NULL, 0, 'Slice between the bones using a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DB289352681D047AEEC2FF10ABDAC76', X'C13B352370008D44BEACE623C1C93EE7', X'C13B352370008D44BEACE623C1C93EE7', 2, NULL, NULL, 2, NULL, 0, 'Serve');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1A92B80F81864B8BB24D5F20E849D0', X'4289519F4FA9A84B88187371EAC4E7AA', X'4289519F4FA9A84B88187371EAC4E7AA', 0, NULL, NULL, 2, NULL, 0, 'Ok, they don''t actually have to be perfect cubes. Pieces, whether square or oblong that are about 1 inch wide will work perfectly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EB39DFC751268448914E784290AD6E5', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 0, NULL, NULL, 2, NULL, 0, 'There''s two ways you can go about adding the rub:');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E17F16306EF6418BE5F73B6EDA4431', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 1, NULL, NULL, 2, NULL, 0, 'Bag Method (best if you want to season them the night before)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E40C6303110DF439A1999954508CC1A', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 2, NULL, NULL, 2, NULL, 0, 'Place all of the pieces into a bag');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D48D718F67067D4196FD51E8FEB4E195', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 3, NULL, NULL, 2, NULL, 0, 'Add 2-3 TBS of vegetable oil and ¼ cup of rub to the bag');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB9B8AFC2B62BF498DBF38C8EE56833C', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 4, NULL, NULL, 2, NULL, 0, 'Seal bag and shake, roll, massage bag to coat the pieces of meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90E272FF31152E4593349A0CB108BAA1', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 5, NULL, NULL, 2, NULL, 0, 'Place in fridge until ready to use');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9F034CF28FEEA48ACC248EB3D0F9E19', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 6, NULL, NULL, 2, NULL, 0, 'Fast Method (the method I used)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3FC1D2D844C4B44A3A91D0AA6508355', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 7, NULL, NULL, 2, NULL, 0, 'Place the meat onto a cooling rack or Bradley rack in a single layer making sure to leave a little space between each one so the smoke and heat can get to them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B640D4D18EBD248A4B41A9E92D10878', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 8, NULL, NULL, 2, NULL, 0, 'Spray top and sides with vegetable oil');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A48BFC379EBC54EAD4753F677D8EA42', X'DBADCDF0ACE5134F99D030864E1D8A52', X'DBADCDF0ACE5134F99D030864E1D8A52', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s original rub onto meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF026D7CC4E6BA4AB50A6463981EA911', X'3E7D2899DE24954EB78AD1170108F9A7', X'3E7D2899DE24954EB78AD1170108F9A7', 0, NULL, NULL, 2, NULL, 0, 'If you used the bag method, remove the meat from the fridge and place the pieces onto a cooling rack or Bradley rack in a single layer making sure to leave a little space between each one so the smoke and heat can get to them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'274D7E1ECA62BF4C9A30F74CF2C03B44', X'3E7D2899DE24954EB78AD1170108F9A7', X'3E7D2899DE24954EB78AD1170108F9A7', 1, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 225°F with pecan smoke or other favorite smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8666F62CA4433341A7CF4FC6ACFA6452', X'3E7D2899DE24954EB78AD1170108F9A7', X'3E7D2899DE24954EB78AD1170108F9A7', 2, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F4AF0450C72F84A9191EEBD493A32C4', X'3E7D2899DE24954EB78AD1170108F9A7', X'3E7D2899DE24954EB78AD1170108F9A7', 3, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the racks of meat into the smoker and let them cook until they reach about 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B680F8C6B0BCF489A35299058EBD799', X'3E7D2899DE24954EB78AD1170108F9A7', X'3E7D2899DE24954EB78AD1170108F9A7', 4, NULL, NULL, 2, NULL, 0, 'This will take 3-4 hours depending on how cold the meat is when it goes in and how well the smoker maintains the goal temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C20FA3D0B384FE4C9CF5D58D0AD0CC99', X'3E7D2899DE24954EB78AD1170108F9A7', X'3E7D2899DE24954EB78AD1170108F9A7', 5, NULL, NULL, 2, NULL, 0, 'During this time, the most important thing is the smoke flavor so keep a light smoke going constantly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4574736E9AC59488E5121FD6551BCE3', X'9CF1613EDF3B5F458AB461564E920C49', X'9CF1613EDF3B5F458AB461564E920C49', 0, NULL, NULL, 2, NULL, 0, 'When the meat reaches about 160°F it is time to start creating the delicious bark on the outside of the pork burnt ends.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADF98B90AA49304AB2473C7BB39243D1', X'9CF1613EDF3B5F458AB461564E920C49', X'9CF1613EDF3B5F458AB461564E920C49', 1, NULL, NULL, 2, NULL, 0, 'Place all of the pieces of meat into foil pans.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'709FF59F3338644FBB15A2F7352E4C14', X'9CF1613EDF3B5F458AB461564E920C49', X'9CF1613EDF3B5F458AB461564E920C49', 2, NULL, NULL, 2, NULL, 0, 'Keep it to a single layer rather than piling it on top of each other. You may need to use multiple pans.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF269912C3B0C2488BFD1434668D6730', X'9CF1613EDF3B5F458AB461564E920C49', X'9CF1613EDF3B5F458AB461564E920C49', 3, NULL, NULL, 2, NULL, 0, 'Add sauce to the top of the meat and stir it around. This is for flavor, bark creation and to help the rub to stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19B0EA16E35C20458A6401F842379E0B', X'9CF1613EDF3B5F458AB461564E920C49', X'9CF1613EDF3B5F458AB461564E920C49', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle my rub generously to the top of the meat and once again stir the meat to coat well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D4369959625534CA426FA7303FE245D', X'ACB791B56EED8B4A93F69D7B7F93CEEF', X'ACB791B56EED8B4A93F69D7B7F93CEEF', 0, NULL, NULL, 2, NULL, 0, 'Place the pans of meat into the smoker and continue to keep the smoke and heat going as before for 1-2 more hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA19CCC12CB69347AC6E48910117F050', X'ACB791B56EED8B4A93F69D7B7F93CEEF', X'ACB791B56EED8B4A93F69D7B7F93CEEF', 1, NULL, NULL, 2, NULL, 0, 'If you have a smoker that is capable of higher temperatures, bark can be best achieved at 275°F or even higher. The grill will also work for this.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42D9E6D7F9A3E64E83BFA629D9992404', X'ACB791B56EED8B4A93F69D7B7F93CEEF', X'ACB791B56EED8B4A93F69D7B7F93CEEF', 2, NULL, NULL, 2, NULL, 0, 'The meat will need to be stirred every 30 minutes or so. More often at higher temperatures.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'290FF5441995D94EA77D5D4320D086D5', X'ACB791B56EED8B4A93F69D7B7F93CEEF', X'ACB791B56EED8B4A93F69D7B7F93CEEF', 3, NULL, NULL, 2, NULL, 0, 'When the meat reaches 185°F and has a nice bark formation all over, it is done and ready to eat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A691FE71565802499E454B605F726D1E', X'C20B655D3EA184448CE93C44596A52C8', X'C20B655D3EA184448CE93C44596A52C8', 0, NULL, NULL, 2, NULL, 0, 'Burnt ends are great appetizers eaten with a toothpick or a pile of burnt ends next to mashed potatoes and barbecue beans is a real crowd pleaser.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC0BD8B6AC0F8D4A9886013D195AA65D', X'9BC36EC00268D042A362BE50315098FF', X'9BC36EC00268D042A362BE50315098FF', 0, NULL, NULL, 2, NULL, 0, 'Wash the jalapeños..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EC6385E3C62B144AFDF2FD370E1F00E', X'9BC36EC00268D042A362BE50315098FF', X'9BC36EC00268D042A362BE50315098FF', 1, NULL, NULL, 2, NULL, 0, 'Cut off the stem end');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3611A75426129E41B0E4FF597E35E7C8', X'9BC36EC00268D042A362BE50315098FF', X'9BC36EC00268D042A362BE50315098FF', 2, NULL, NULL, 2, NULL, 0, 'Use a thin blade knife or even an oyster shucker to scrape the inside of the peppers and remove the seeds and veins.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE5C5CC8C780E84AB793BA93D1C7567C', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', 0, NULL, NULL, 2, NULL, 0, 'If you do not like any `hotness` in your jalapeños, consider soaking them in a citrus flavored soda for several hours to remove some of the capsaicin (the chemical in peppers that make them taste hot)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6BE586124D488429D4D7AD6BDF0AFD0', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', 1, NULL, NULL, 2, NULL, 0, 'After cleaning them, place them in a bowl and pour citrus soda over them to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2B705C571C3F742AA370F53188CED8C', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', 2, NULL, NULL, 2, NULL, 0, 'Soak them in this for 3-4 hours or even longer depending on how mild you want them to be.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF458DF8A599514B903D99DB95E8F453', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', 3, NULL, NULL, 2, NULL, 0, 'I recommend tasting of a small bit of the stem end that you cut off to see how hot they are before deciding whether they need to be soaked or not. Some jalapeños are extremely mild while others can be a little less tame.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6B9E9A2699EE046827A2911B7307F55', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', X'E3EDCFFE5ED36A48BE4ACFA3247D3237', 4, NULL, NULL, 2, NULL, 0, 'When the peppers are finished soaking, remove them from the soda. Save the soda if you wish to flavor brines, soups, whatever needs a little jalapeño flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63A5FA0DF9720D41BB3DE8952B00E956', X'D45B552412EC9B428CFE8188E2EB108D', X'D45B552412EC9B428CFE8188E2EB108D', 0, NULL, NULL, 2, NULL, 0, 'Hopefully you left the cream cheese on the countertop for an hour or so to soften. This will make it easier to work with.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADD28B67D4C96546B19D193B4430BD4B', X'D45B552412EC9B428CFE8188E2EB108D', X'D45B552412EC9B428CFE8188E2EB108D', 1, NULL, NULL, 2, NULL, 0, 'Turkey Cranberry Cream Cheese Stuffing: 8 oz of cream cheese, 1/2 cup of shredded cheddar, 2TBS cranberry relish, ¼ cup of Jeff''s original rub, ¾ to 1 cup turkey pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BA7B9B37EFF1E4FA948D52EB79E9A60', X'D45B552412EC9B428CFE8188E2EB108D', X'D45B552412EC9B428CFE8188E2EB108D', 2, NULL, NULL, 2, NULL, 0, 'Place all of the ingredients into a mixing bowl and use a spoon to fold all of the ingredients together so that it is well combined.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97BD9102B25D374B80C702916B81904F', X'0515D8AA0338FF4F980EFC2DCA01D4CF', X'0515D8AA0338FF4F980EFC2DCA01D4CF', 0, NULL, NULL, 2, NULL, 0, 'Use a spoon or your fingers to press the stuffing into the open end of the jalapeño peppers making sure they are completely filled and running over the edge.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'662C4EFF655D5441924CCE740BF182C8', X'55D49398E31F1F4088CA7C209785F29A', X'55D49398E31F1F4088CA7C209785F29A', 0, NULL, NULL, 2, NULL, 0, 'Lay a piece of bacon on the cutting board');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89210847F8AA6B4ABA7511A8186B7781', X'55D49398E31F1F4088CA7C209785F29A', X'55D49398E31F1F4088CA7C209785F29A', 1, NULL, NULL, 2, NULL, 0, 'Place one of the stuffed peppers onto one end of the pepper about 1 inch from the end.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B641633E56A6645B7E3E1C16E5F7407', X'55D49398E31F1F4088CA7C209785F29A', X'55D49398E31F1F4088CA7C209785F29A', 2, NULL, NULL, 2, NULL, 0, 'Wrap the short end of the bacon over the top of the pepper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'037DBE7EE30D514C9850B5F4204E14ED', X'55D49398E31F1F4088CA7C209785F29A', X'55D49398E31F1F4088CA7C209785F29A', 3, NULL, NULL, 2, NULL, 0, 'Wrap the long end of the bacon over the top of the pepper and all the way around again to seal the pepper and to use up the piece of bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A53854518169CA448AFBB45875A1CAAA', X'55D49398E31F1F4088CA7C209785F29A', X'55D49398E31F1F4088CA7C209785F29A', 4, NULL, NULL, 2, NULL, 0, 'Note: it is ok to cut the peppers in half so that you have boats. This makes it easier to stuff and wrap but allows the cheese to leak out a little more and gives you less jalapeño flavor.. your choice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD57D49A2D71BA488A5C99C2B9444ECF', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', 0, NULL, NULL, 2, NULL, 0, 'Once all of the peppers are stuffed and wrapped, place them on a Bradley rack or on a cookie sheet for easy transport out to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'740A9AD1D4C1FB4FAFC16364C6E31239', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', 1, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240 °F for approximately 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9621B945E604214E98334F9E44521803', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', 2, NULL, NULL, 2, NULL, 0, 'Use the water pan if you have one in your smoker and be sure to have enough dry wood chips, chunks or pellets for at least 1.5 to 2 hours. It is also ok to continue the smoke throughout the entire process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E558906B59EF974984F1BD39EBC935D4', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', 3, NULL, NULL, 2, NULL, 0, 'If you are not using a Bradley rack, place the peppers on the grate making sure they are not able to fall through.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F445E5F829549C41946A833EB6841BC6', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', X'CB3A06D61E2D3F4BB86EA3F03FA73CEB', 4, NULL, NULL, 2, NULL, 0, 'Cook smoke the peppers for about 3 hours or until they peppers are nice and soft and the bacon is done cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38E2D4277800B64F9696A37B0C68A730', X'81AAB3A55EA6074FB56D480372FD8F46', X'81AAB3A55EA6074FB56D480372FD8F46', 0, NULL, NULL, 2, NULL, 0, 'Serve the turkey and cranberry stuffed abt''s right away while the cheese is still oozing and hot.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0B2687DD83EE644887624B97A3EDC8C', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', 0, NULL, NULL, 2, NULL, 0, 'Remove the turkey from it''s packaging.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97AA4623AFE2FC40A27E11BF4D7174B3', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', 1, NULL, NULL, 2, NULL, 0, 'Remove giblets, neck, pop-up timer and any plastic or hardware that is holding the legs together.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'917E58465EAC8540AA4B06D3BB2F3C80', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', 2, NULL, NULL, 2, NULL, 0, 'Place the turkey on your cutting board with the backbone facing up and neck end toward you.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D823830D05031C49A702B7EC3C181D7B', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', 3, NULL, NULL, 2, NULL, 0, 'Using a pair of heavy duty kitchen shears, cut along both sides of the backbone to completely remove it from the turkey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51833E166523CA468E22A47BD1F68123', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', X'0B6A7F70881E9E4DAFCD4CDAF1892ACC', 4, NULL, NULL, 2, NULL, 0, 'Turn the turkey breast side up and press down with both hands to flatten it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'093547468D610745B7E2FE415E0FE45B', X'32BE5BD124DFE94EAACFCCF948105503', X'32BE5BD124DFE94EAACFCCF948105503', 0, NULL, NULL, 2, NULL, 0, 'Make a simple turkey brine using 1 gallon of cold water, 1 cup of coarse kosher salt and ¾ cup of dark brown sugar. (the sugar is optional but I think it adds a lot to the brine).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44F7D13E7F8ED942980CA4F6F77F6CFC', X'32BE5BD124DFE94EAACFCCF948105503', X'32BE5BD124DFE94EAACFCCF948105503', 1, NULL, NULL, 2, NULL, 0, 'Pour the salt into the water and stir until it becomes clear again. Add the sugar and stir until it dissolves. Double or triple the brine recipe as required.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48F9380ED548334FAEE1F9AFA90FA94D', X'32BE5BD124DFE94EAACFCCF948105503', X'32BE5BD124DFE94EAACFCCF948105503', 2, NULL, NULL, 2, NULL, 0, 'Place the turkey in a plastic, glass or stainless steel container and pour the brine over the bird to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D20261C88FF2D644B09D6CBA1AB7E2B7', X'32BE5BD124DFE94EAACFCCF948105503', X'32BE5BD124DFE94EAACFCCF948105503', 3, NULL, NULL, 2, NULL, 0, 'Place a lid or cover on the container with the bird and the brine and place it in the refrigerator overnight or 8-12 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95090F948765CD40B7E697642DC88192', X'6D4721C18E7FBF4C865425C9BAD1CDB7', X'6D4721C18E7FBF4C865425C9BAD1CDB7', 0, NULL, NULL, 2, NULL, 0, 'When the turkey is finished brining, discard the brine and rinse the turkey really well under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0A378A8036A9B468FF550111B0D8E99', X'6D4721C18E7FBF4C865425C9BAD1CDB7', X'6D4721C18E7FBF4C865425C9BAD1CDB7', 1, NULL, NULL, 2, NULL, 0, 'Dry the inside and outside of the turkey with clean paper towels.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B86C373D18364A875BA046374B6E0D', X'6D4721C18E7FBF4C865425C9BAD1CDB7', X'6D4721C18E7FBF4C865425C9BAD1CDB7', 2, NULL, NULL, 2, NULL, 0, 'Place the turkey in the fridge uncovered for about 2 hours to allow the skin to dry further.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'708BF30F09CF7444877EFFFDAEE0C07B', X'6D4721C18E7FBF4C865425C9BAD1CDB7', X'6D4721C18E7FBF4C865425C9BAD1CDB7', 3, NULL, NULL, 2, NULL, 0, 'While the drying process is occurring, make up the herb butter (recipe below)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D625C2D241E8254EBFDBB13EC95B5D40', X'3689EC1FCB9E4A4AA4BC37F91E2D70F6', X'3689EC1FCB9E4A4AA4BC37F91E2D70F6', 0, NULL, NULL, 2, NULL, 0, 'Combine 1 stick of softened butter, 1/4 cup chopped parsley, 1/4 cup chopped thyme, 1/4 cup chopped chives, 1/4 cup onion, 5 garlic cloves, 2 TBS Jeff''s Texas Rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B040F5ECC8DBE440938F9E51FE45D043', X'3689EC1FCB9E4A4AA4BC37F91E2D70F6', X'3689EC1FCB9E4A4AA4BC37F91E2D70F6', 1, NULL, NULL, 2, NULL, 0, 'If made ahead, allow it to soften to help with the application.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C055A7DBD6A94D88832F8D199D9681', X'D3FAFC585C432B4AB20A58692A5D5E48', X'D3FAFC585C432B4AB20A58692A5D5E48', 0, NULL, NULL, 2, NULL, 0, 'Split the butter into (2) parts and put half of it under the skin and half of it on the outside of the skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BF06DC559F6BA4595CD89D5B14518BE', X'D3FAFC585C432B4AB20A58692A5D5E48', X'D3FAFC585C432B4AB20A58692A5D5E48', 1, NULL, NULL, 2, NULL, 0, 'Work your hands between the skin and the meat tearing the membrane loose as you go. Do this carefully and patiently and you will be able to completely loosen the skin from the breast meat and even the thighs and legs. Leave the skin attached around the edges (except for where your hand entered) to help hold in the butter and herbs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97AEECE88311894D8EB0CAE7132AE2D3', X'D3FAFC585C432B4AB20A58692A5D5E48', X'D3FAFC585C432B4AB20A58692A5D5E48', 2, NULL, NULL, 2, NULL, 0, 'Once the skin is loose enough, stuff some of the herbed butter in the breast area, the thighs, and the legs. Pat it down from the outside to spread it out a little more.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9198556C02AC443ADCA3439DFE32D38', X'D3FAFC585C432B4AB20A58692A5D5E48', X'D3FAFC585C432B4AB20A58692A5D5E48', 3, NULL, NULL, 2, NULL, 0, 'Apply the remaining herbed butter on the outside of the skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2305F421E407344B8E66B36B38D27882', X'AE4F7298F6704A468D99B7BC7A8B58D2', X'AE4F7298F6704A468D99B7BC7A8B58D2', 0, NULL, NULL, 2, NULL, 0, 'Season the entire turkey skin side and meat side with my Texas rub for perfect seasoning throughout.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADB9CDB79BAA414BA95521BBD405D101', X'E42B3303342B4A4CA87D37B5243A69EF', X'E42B3303342B4A4CA87D37B5243A69EF', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DECAE1DF259D144A6C43E2CCC81FD33', X'E42B3303342B4A4CA87D37B5243A69EF', X'E42B3303342B4A4CA87D37B5243A69EF', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, it''s a great idea to use as this helps to reduce the drying effect of the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D01DCCAC6672494D98CD1CF8A7F6C6E5', X'E42B3303342B4A4CA87D37B5243A69EF', X'E42B3303342B4A4CA87D37B5243A69EF', 2, NULL, NULL, 2, NULL, 0, 'Make sure you have enough smoking wood to last for at least 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7389C176242B2943A02CC210B0B0490B', X'E42B3303342B4A4CA87D37B5243A69EF', X'E42B3303342B4A4CA87D37B5243A69EF', 3, NULL, NULL, 2, NULL, 0, 'Place the spatchcocked turkey skin side up and let it smoke cook for about 4 hours at 240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B820EA7E969524F818D8DF4FA0FB797', X'E42B3303342B4A4CA87D37B5243A69EF', X'E42B3303342B4A4CA87D37B5243A69EF', 4, NULL, NULL, 2, NULL, 0, 'Monitor the temperature of the breast at it''s thickest part and when it reaches about 158-160 it''s time to move it from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'964796001FE21645BF778C58E4F5F99D', X'90A1EB3770FD5D429974A1F39A546DA5', X'90A1EB3770FD5D429974A1F39A546DA5', 0, NULL, NULL, 2, NULL, 0, 'Quickly tent some foil over the bird and leave it for about 10-15 minutes to rest before carving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9140A9577B9F543BD04404A3F08DDA3', X'90A1EB3770FD5D429974A1F39A546DA5', X'90A1EB3770FD5D429974A1F39A546DA5', 1, NULL, NULL, 2, NULL, 0, 'To carve, remove the leg quarters then separate the thigh from the leg. Remove the wings then remove the breast meat in one big section by cutting along the carcass as you pull back on the breast meat. Slice the breast meat into pieces and you are ready to eat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4510D9AA57DE54E8463B0439EA4C4E2', X'2FDBC0190D05B544B04E2CFE9A38E44E', X'2FDBC0190D05B544B04E2CFE9A38E44E', 0, NULL, NULL, 2, NULL, 0, 'Make about ½ gallon of brine or whatever is required to cover the turkey cutlets.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07B2506605366A42A8CA432EF1060AB6', X'2FDBC0190D05B544B04E2CFE9A38E44E', X'2FDBC0190D05B544B04E2CFE9A38E44E', 1, NULL, NULL, 2, NULL, 0, 'Basic Brine: ½ gallon of cold water, ½ cup of coarse kosher salt, ½ cup dark brown sugar (optional)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D6081AE2891A348846421BAB23128E6', X'2FDBC0190D05B544B04E2CFE9A38E44E', X'2FDBC0190D05B544B04E2CFE9A38E44E', 2, NULL, NULL, 2, NULL, 0, 'To this recipe you can add fruit juice, beer, wine, hot sauce, marinade, seasoning, almost anything and the flavor will make it''s way deep into the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'584EC6C9009DC14FA69D756A44374BBA', X'2FDBC0190D05B544B04E2CFE9A38E44E', X'2FDBC0190D05B544B04E2CFE9A38E44E', 3, NULL, NULL, 2, NULL, 0, 'To make the brine: Start by adding ½ gallon of water to a large pitcher. Pour in the salt and stir until the salt is dissolved into the water. Add the sugar and stir once again until it is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E47C0C2FEE773479AE04D297AF2C733', X'2FDBC0190D05B544B04E2CFE9A38E44E', X'2FDBC0190D05B544B04E2CFE9A38E44E', 4, NULL, NULL, 2, NULL, 0, 'Add any other ingredients you like and give it one last stir to combine. The brining solution is now ready to use.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43F0F20D1C188040869BA3860BA7E7EC', X'2FDBC0190D05B544B04E2CFE9A38E44E', X'2FDBC0190D05B544B04E2CFE9A38E44E', 5, NULL, NULL, 2, NULL, 0, 'Place the turkey cutlets into a bowl or even a zip top bag and pour enough brine over the meat to cover. Seal it up and place in the fridge for 2-4 hours for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'012C6FBC00D1BB47833C3B0E9357269C', X'2FDBC0190D05B544B04E2CFE9A38E44E', X'2FDBC0190D05B544B04E2CFE9A38E44E', 6, NULL, NULL, 2, NULL, 0, 'Once the brining is complete, you will want to discard the brining solution and rinse the turkey cutlets with cold water to remove any excess salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B79A01C8D0ABFC40A98114BE569F7B72', X'BBA04EC5AE794D46A71F1D2F30E0BE4C', X'BBA04EC5AE794D46A71F1D2F30E0BE4C', 0, NULL, NULL, 2, NULL, 0, 'Lay the turkey cutlets on a cutting board or into a foil pan and coat the top and bottom sides with my original rub (purchase recipes here).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4617A78E0F2F9649B6FDD2E927CE266B', X'BBA04EC5AE794D46A71F1D2F30E0BE4C', X'BBA04EC5AE794D46A71F1D2F30E0BE4C', 1, NULL, NULL, 2, NULL, 0, 'Place about a tablespoon of diced jalapeño and a tablespoon of shredded cheddar onto one half of the cutlet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F49A1FAD9C5EA349B1E98B3867FE32CF', X'BBA04EC5AE794D46A71F1D2F30E0BE4C', X'BBA04EC5AE794D46A71F1D2F30E0BE4C', 2, NULL, NULL, 2, NULL, 0, 'Fold the cutlet over to finish the stuffing process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF5A6AB8F66B864D90D9D65FFF187B9A', X'587F421882EA364B89B47407217BF66A', X'587F421882EA364B89B47407217BF66A', 0, NULL, NULL, 2, NULL, 0, 'Pick up the stuffed turkey carefully with one hand and wrap bacon around the stuffed turkey cutlet so as to hold in the cheese and the jalapeños.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A6D7D16354ADA4FBCFE15D79FCA9D7A', X'587F421882EA364B89B47407217BF66A', X'587F421882EA364B89B47407217BF66A', 1, NULL, NULL, 2, NULL, 0, 'I wrapped mine tight with 2 pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB6E6AC5A7E0974496D7769A5031BF17', X'587F421882EA364B89B47407217BF66A', X'587F421882EA364B89B47407217BF66A', 2, NULL, NULL, 2, NULL, 0, 'Give them another good sprinkle of rub (purchase recipes here) on the outside and they are ready to cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CEBAE29E8E6D54B98CBFFB48CB83641', X'139E672ADFC5E44D9D660C522D7AAC2E', X'139E672ADFC5E44D9D660C522D7AAC2E', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker/cooker for cooking at about 240°F with indirect heat for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F4829AB5A678C48B8680CC07F8C3B4B', X'139E672ADFC5E44D9D660C522D7AAC2E', X'139E672ADFC5E44D9D660C522D7AAC2E', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E51DA32EB04F1438A8108331BBC69E6', X'139E672ADFC5E44D9D660C522D7AAC2E', X'139E672ADFC5E44D9D660C522D7AAC2E', 2, NULL, NULL, 2, NULL, 0, 'Make sure you have enough dry smoking wood for at least 1 hour but it is ok to keep a light smoke going for the entire cooking time of 2 hours or so.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACA860E6760C7B45A08382BD7156B89D', X'139E672ADFC5E44D9D660C522D7AAC2E', X'139E672ADFC5E44D9D660C522D7AAC2E', 3, NULL, NULL, 2, NULL, 0, 'Once the smoker is preheated and everything is ready to go, the cooking smoking process can commence.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'849E8F90AB352C4681BCFADFF00383DD', X'1E6C3DCEF50A074596449C35CFDC875A', X'1E6C3DCEF50A074596449C35CFDC875A', 0, NULL, NULL, 2, NULL, 0, 'Bradley racks or something similar is a great way to transport these and almost anything you cook to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8542DE1127F6DF49B2DBE6D817A9F608', X'1E6C3DCEF50A074596449C35CFDC875A', X'1E6C3DCEF50A074596449C35CFDC875A', 1, NULL, NULL, 2, NULL, 0, 'Place the rack in the smoker or you can lay the wrapped turkey bombs directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E184E7421D9D8D4CAE6E8E2DF70366F8', X'1E6C3DCEF50A074596449C35CFDC875A', X'1E6C3DCEF50A074596449C35CFDC875A', 2, NULL, NULL, 2, NULL, 0, 'Maintain 225-240°F for the entire time and keep a light smoke flowing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D931FA8A4C59E44936B866147D76B83', X'1E6C3DCEF50A074596449C35CFDC875A', X'1E6C3DCEF50A074596449C35CFDC875A', 3, NULL, NULL, 2, NULL, 0, 'Monitor the temperature of the turkey by using a leave in digital meat thermometer such as the Maverick ET-733 or Maverick ET-735.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE8A078F7B9B3E4C94BB75D3B516F5B8', X'1E6C3DCEF50A074596449C35CFDC875A', X'1E6C3DCEF50A074596449C35CFDC875A', 4, NULL, NULL, 2, NULL, 0, 'You can also check the temperature now and then using a Thermapen. Since these read so quickly, you can open the door/lid, check the temperature and close the door/lid in less than 8 seconds without a significant loss of heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CAE1ED6CC81A546B35D3A36ABD7290B', X'1E6C3DCEF50A074596449C35CFDC875A', X'1E6C3DCEF50A074596449C35CFDC875A', 5, NULL, NULL, 2, NULL, 0, 'When they reach 160°F, they are perfectly done and ready to be taken out of the smoker. It is not uncommon for me to remove them a degree or two early since I know the temperature will continue to rise even after they come out of the smoker due to residual heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53E7DF83C4D8444582BD24EBA37CFD0D', X'1E6C3DCEF50A074596449C35CFDC875A', X'1E6C3DCEF50A074596449C35CFDC875A', 6, NULL, NULL, 2, NULL, 0, 'Let them rest covered loosely with foil for about 5 minutes before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19F0D0F2D4491C4CB84F8806AE86A70E', X'280C677B44890E4D95E065316ABC9719', X'280C677B44890E4D95E065316ABC9719', 0, NULL, NULL, 2, NULL, 0, 'After the resting is complete, serve a smoked jalapeño cheddar turkey bomb to each diner with plenty of tasty sides and you''ll have a meal to remember.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A310CA317FAF8C4DA7715E5581BB9D64', X'DD67270F091E764CBA3F6E53B94F5BF9', X'DD67270F091E764CBA3F6E53B94F5BF9', 0, NULL, NULL, 2, NULL, 0, 'Unlike most of my instructions, we are going to get the smoker going first since the bacon needs to go on right from the package without any additional preparation.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'506CA9AB9AA15244B072A6E58C3F8E64', X'DD67270F091E764CBA3F6E53B94F5BF9', X'DD67270F091E764CBA3F6E53B94F5BF9', 1, NULL, NULL, 2, NULL, 0, 'We will cook the bacon for about 45-60 minutes before adding the glaze to it which magically transforms it into smoked pig candy.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B90C249F26D17343ACAFC4825527A712', X'DD67270F091E764CBA3F6E53B94F5BF9', X'DD67270F091E764CBA3F6E53B94F5BF9', 2, NULL, NULL, 2, NULL, 0, 'I used a small cabinet style electric smoker for this but you can use whatever smoker you want as long as it will maintain about 240-250°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9AFA336974263439C2A27AE3E01AFBA', X'DD67270F091E764CBA3F6E53B94F5BF9', X'DD67270F091E764CBA3F6E53B94F5BF9', 3, NULL, NULL, 2, NULL, 0, 'While the smoker is coming up to temperature or preheating, go inside and get the bacon out of the package and ready to cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29F4DDA7BBAD0B43887FF6737DCB332A', X'4617C6DFD9B26A4CAC300D9CAEE41D27', X'4617C6DFD9B26A4CAC300D9CAEE41D27', 0, NULL, NULL, 2, NULL, 0, 'I placed my bacon onto Bradley racks to make it easy but you can also just lay the bacon right onto the smoker grate if you want.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9F17019CBD985478D92D65D9FE38A5C', X'4617C6DFD9B26A4CAC300D9CAEE41D27', X'4617C6DFD9B26A4CAC300D9CAEE41D27', 1, NULL, NULL, 2, NULL, 0, 'I do not recommend using a cookie sheet since it''s important that the smoke can get to the bacon from all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'105ECB2B73EF124E810048DB22D2D5E2', X'4617C6DFD9B26A4CAC300D9CAEE41D27', X'4617C6DFD9B26A4CAC300D9CAEE41D27', 2, NULL, NULL, 2, NULL, 0, 'I laid the racks of bacon right on the smoker grates and then placed a throw-away foil pan under the racks of a bacon on a lower shelf to catch the bacon drippings and the glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E5079C6BC4FFB4EB5EA045E526B4EED', X'4617C6DFD9B26A4CAC300D9CAEE41D27', X'4617C6DFD9B26A4CAC300D9CAEE41D27', 3, NULL, NULL, 2, NULL, 0, 'Note: I like to reduce cleanup as much as possible.. not sure if that means I''m working smart or if I''m really lazy but cleanup is not my favorite part of this process and I work hard to reduce and even eliminate it wherever I can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80C5C1A4BA76F64B826B56C6985A02A0', X'4617C6DFD9B26A4CAC300D9CAEE41D27', X'4617C6DFD9B26A4CAC300D9CAEE41D27', 4, NULL, NULL, 2, NULL, 0, 'The bacon will cook this way for about 45 minutes.. if you are running a little less than 250°F you will want to give it a few extra minutes and leave it in there about 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74FC648F4B903B4490DCECECCB732111', X'4617C6DFD9B26A4CAC300D9CAEE41D27', X'4617C6DFD9B26A4CAC300D9CAEE41D27', 5, NULL, NULL, 2, NULL, 0, 'While the bacon does it''s thing in the smoker, go inside and make the candy glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1397EAD78DE0E4EBB9EBDD9E12A3167', X'12791513174200439C853EAE22A9BF89', X'12791513174200439C853EAE22A9BF89', 0, NULL, NULL, 2, NULL, 0, 'Add the following ingredients to a small mixing bowl: ½ cup brown sugar (I used the dark), ¼ cup maple syrup, ¼ cup bourbon, ½ cup Jeff''s original rub, 2 TBS honey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E795D7E3700CA24BB49A36B09D502DD4', X'12791513174200439C853EAE22A9BF89', X'12791513174200439C853EAE22A9BF89', 1, NULL, NULL, 2, NULL, 0, 'Mix it up real well and if you want it to be a little more thin, you can add an extra tablespoon or two of bourbon (can''t go wrong there).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E588E09E90590B4AA68DFE8EC2286660', X'12791513174200439C853EAE22A9BF89', X'12791513174200439C853EAE22A9BF89', 2, NULL, NULL, 2, NULL, 0, 'Note: if you do not want to use bourbon for some reason, you can use Dr. Pepper in the same quantity and leave out the honey since there is additional sweetness in the soda.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70366881957FC9499BC5052ACC341951', X'F456372052D58943858AB4D45A82F800', X'F456372052D58943858AB4D45A82F800', 0, NULL, NULL, 2, NULL, 0, 'Once the bacon has been cooking for about 45-60 minutes, it is time to start brushing on the glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B4B2B33E456D47A3EC9F4E896E5B27', X'F456372052D58943858AB4D45A82F800', X'F456372052D58943858AB4D45A82F800', 1, NULL, NULL, 2, NULL, 0, 'Using a long handled silicone brush, open the door and quickly brush a layer of glaze onto the top side of the bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C611A1F749AFD4FA95DAE21657F73DE', X'F456372052D58943858AB4D45A82F800', X'F456372052D58943858AB4D45A82F800', 2, NULL, NULL, 2, NULL, 0, 'Flip the bacon over and glaze the bottom side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0372B2C57507E54A8D23F8E5F57D24BA', X'F456372052D58943858AB4D45A82F800', X'F456372052D58943858AB4D45A82F800', 3, NULL, NULL, 2, NULL, 0, 'Close the smoker door and set a timer for 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C8AEFC42B73F045A449F9866CEB6B7D', X'354231EA1EBC374686AB3B85A9475090', X'354231EA1EBC374686AB3B85A9475090', 0, NULL, NULL, 2, NULL, 0, 'Once again using a long handled silicone brush, open the smoker door or lid and quickly brush a layer of glaze onto the top side of the bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5635EBC82F16134E92EEEEAA6FF98102', X'354231EA1EBC374686AB3B85A9475090', X'354231EA1EBC374686AB3B85A9475090', 1, NULL, NULL, 2, NULL, 0, 'Flip the bacon over and glaze the bottom side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F89EC91E580B164D943881A837EFEE1D', X'354231EA1EBC374686AB3B85A9475090', X'354231EA1EBC374686AB3B85A9475090', 2, NULL, NULL, 2, NULL, 0, 'Close the smoker door and once again set the timer for 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE742AF41BF4504AABED9E55B2499AEF', X'FE02C4FE7291CA4D9D4ED57E6DE3E3DF', X'FE02C4FE7291CA4D9D4ED57E6DE3E3DF', 0, NULL, NULL, 2, NULL, 0, 'Very quickly remove the bacon from the grates or the bradley racks and place it onto wax paper to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87B0CF613AD06B41A9C898622B0000D5', X'FE02C4FE7291CA4D9D4ED57E6DE3E3DF', X'FE02C4FE7291CA4D9D4ED57E6DE3E3DF', 1, NULL, NULL, 2, NULL, 0, 'As it cools the bacon and glaze hardens and it becomes more and more difficult to remove from the racks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9526E9DB037A7947B0741A8782C406DA', X'FE02C4FE7291CA4D9D4ED57E6DE3E3DF', X'FE02C4FE7291CA4D9D4ED57E6DE3E3DF', 2, NULL, NULL, 2, NULL, 0, 'I was not quick enough.. be faster than me and you should be ok.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'401533B2387B6743905BDD88D6C215AC', X'5B03721A4A99F74A844BC179147C1099', X'5B03721A4A99F74A844BC179147C1099', 0, NULL, NULL, 2, NULL, 0, 'If you are able to get them off in whole strips then you can decide if you like them better that way. If you have issues with them breaking up, it''s still ok to serve them as pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46DC7BC156114C459C5B911B3ED4BFD9', X'5B03721A4A99F74A844BC179147C1099', X'5B03721A4A99F74A844BC179147C1099', 1, NULL, NULL, 2, NULL, 0, 'And.. be sure to save some of the smaller pieces for that big bowl of ice cream I know you''ve been contemplating!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2337226330089B4C913DDB6F5126C076', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', 0, NULL, NULL, 2, NULL, 0, 'Remove the pork sirloin from it''s packaging and give it a good rinse under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDED4572D182164F8949185A522D5A79', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', 1, NULL, NULL, 2, NULL, 0, 'Place the roast in a large zip top bag');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB1A8F762F6494ABF834C46EC47709F', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', 2, NULL, NULL, 2, NULL, 0, 'Pour enough teriyaki sauce over the meat to fully coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8FFE6F5EAA70A47AE8931D37930A21D', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', 3, NULL, NULL, 2, NULL, 0, 'Press the air from the bag and zip it closed to create a vacuum and help the marinade to stay close to the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9CDF68F9E721F429DC768E6F20D9C9C', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', 4, NULL, NULL, 2, NULL, 0, 'Put the bagged meat in the fridge for at least 4 hours and up to overnight or 8 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5149C0BD5200BC42B8212E19E96E1520', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', 5, NULL, NULL, 2, NULL, 0, 'When the marinate time is over, remove the pork sirloin from the bag to season it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6AC8DED228AA14B9E782F09ECCB5F30', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', X'5C4A8ED5EF2A924BBB1A1DFDA7C406E1', 6, NULL, NULL, 2, NULL, 0, 'Do not rinse the marinade off.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADB9EA8B0245964392F2A4600446677B', X'86BDF40040FAB44988AE633F9510EA19', X'86BDF40040FAB44988AE633F9510EA19', 0, NULL, NULL, 2, NULL, 0, 'I like to place the meat in a foil pan to contain the mess');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'615F8D7786FBBE4EBCB4974021636E4C', X'86BDF40040FAB44988AE633F9510EA19', X'86BDF40040FAB44988AE633F9510EA19', 1, NULL, NULL, 2, NULL, 0, 'Coat the entire surface of the pork sirloin roast with my original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF9F46CC8A94C34FBECC4C9D05181647', X'86BDF40040FAB44988AE633F9510EA19', X'86BDF40040FAB44988AE633F9510EA19', 2, NULL, NULL, 2, NULL, 0, 'Be generous!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B537AC2870A73542BA1283A0FE897AF5', X'86BDF40040FAB44988AE633F9510EA19', X'86BDF40040FAB44988AE633F9510EA19', 3, NULL, NULL, 2, NULL, 0, 'Leave the rubbed meat laying while you go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D081718C4B074393725F9791B733BF', X'065B3FB89BEE31478491B16602F2D06F', X'065B3FB89BEE31478491B16602F2D06F', 0, NULL, NULL, 2, NULL, 0, 'Prepare the smoker for a 2 hour cook at about 225-240°F with enough smoking wood for at least 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA80770014617341BEAB2164547B48F9', X'065B3FB89BEE31478491B16602F2D06F', X'065B3FB89BEE31478491B16602F2D06F', 1, NULL, NULL, 2, NULL, 0, 'You can also use a grill as long as you set it up for cooking indirect.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F75FB5A5380B54A8213F06C44329E4E', X'065B3FB89BEE31478491B16602F2D06F', X'065B3FB89BEE31478491B16602F2D06F', 2, NULL, NULL, 2, NULL, 0, 'Note: to cook indirect on a typical 3 or 4 burner gas grill, only light one or two of the burners on one side. Place the meat on the opposite side over unlit burners.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5B6CE02F613DB4394254EDC2056E2E6', X'065B3FB89BEE31478491B16602F2D06F', X'065B3FB89BEE31478491B16602F2D06F', 3, NULL, NULL, 2, NULL, 0, 'On a charcoal grill, place coals on both side of the grill and place a metal pan of water in the center between the coals. The meat goes directly over the water pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86CFE48132D736499B62EDA1481EB302', X'065B3FB89BEE31478491B16602F2D06F', X'065B3FB89BEE31478491B16602F2D06F', 4, NULL, NULL, 2, NULL, 0, 'Smoke will come from wood chips wrapped in foil and placed over the coals or over the lit burners. Replace wood chips as needed to keep the smoke going for at least 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'761D41CCD26CC94081FB1257D5776120', X'0685654AF68F324590D2581609821DAC', X'0685654AF68F324590D2581609821DAC', 0, NULL, NULL, 2, NULL, 0, 'Once the smoker or grill is ready, place the meat directly on the grate or you can use a Bradley rack for easy transport to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFB3F15894FA744DB0AF105C34FDAF7D', X'0685654AF68F324590D2581609821DAC', X'0685654AF68F324590D2581609821DAC', 1, NULL, NULL, 2, NULL, 0, 'Cook the meat at 225-240°F for about 2 hours or until it reaches an internal temperature of 145°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A727961CCC71074981E2250B3AE336F9', X'0685654AF68F324590D2581609821DAC', X'0685654AF68F324590D2581609821DAC', 2, NULL, NULL, 2, NULL, 0, 'Note: For years now we have been told to cook pork to 160°F to make sure it is safe. Recently, this safe temperature was reduced to 145°F. I do understand that some folks do not deal well with change (yours truly) but if you can manage it, you will find that pork is so much better at this lower finished temperature. It ends up a lot more moist and flavorful due to it not being cooked to death.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D290491C492D8E47BA7EB0A37E12D18A', X'0685654AF68F324590D2581609821DAC', X'0685654AF68F324590D2581609821DAC', 3, NULL, NULL, 2, NULL, 0, 'For more teriyaki flavor, you can mix 2 parts teriyaki sauce with my barbecue sauce to make a uniquely delicious flavored sauce for this smoked pork sirloin. Apply the sauce a couple of times during the last 30 minutes in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B7E3C6B05EEFC418CDB81FBE099F347', X'BDEE4E01BE493645ADBA660BD9E5A65E', X'BDEE4E01BE493645ADBA660BD9E5A65E', 0, NULL, NULL, 2, NULL, 0, 'After removing the meat from the smoker, tent a piece of foil loosely over the meat and let it rest about 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B77ECE6198FCC4449E2E36069EBA6F88', X'BDEE4E01BE493645ADBA660BD9E5A65E', X'BDEE4E01BE493645ADBA660BD9E5A65E', 1, NULL, NULL, 2, NULL, 0, 'After resting, slice it up and serve it immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5AE57C7272B874BA33DCAE94D24A9E1', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 0, NULL, NULL, 2, NULL, 0, 'Place the chicken wing sections into a large plastic or glass bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AC52585C784F845A74B0D20916DAC4C', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 1, NULL, NULL, 2, NULL, 0, 'Drizzle Franks original RedHot® sauce over the top of all the wings');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87DBA67F11426343BCC5458883F087A3', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 2, NULL, NULL, 2, NULL, 0, 'Stir them around to fully coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A5E75BB752D349AD7134A7CAB2AA68', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s original rub also called Jeff''s naked rib rub onto the top of the wing sections (be generous).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8CC0374BA00584AB7709A9C8558199B', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 4, NULL, NULL, 2, NULL, 0, 'Stir the wings around to get the rub all over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'378179CF65957C4D8BB9BB18FA2CED72', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 5, NULL, NULL, 2, NULL, 0, 'Repeat the rub and the stirring to make sure you have plenty of rub on the chicken wings.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83C42734FFEDEA49A59A1500273B4C41', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 6, NULL, NULL, 2, NULL, 0, 'Cover the container with a lid, plastic wrap or foil and place them in the fridge for about 4 hours to marinate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0098AE428847034683709D25D6600159', X'6122B6B04A0B6F499F38F4B06D618915', X'6122B6B04A0B6F499F38F4B06D618915', 7, NULL, NULL, 2, NULL, 0, 'When the marinating is finished, place the wing sections on a bradley rack or cookie sheet for easy transport out to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC629DB7E8BB92478B41E4559A86D001', X'1710262BBE34564A8358E6FB3ECC726E', X'1710262BBE34564A8358E6FB3ECC726E', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 250°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D34E5C727C1A45AAF70E541A3C4999', X'1710262BBE34564A8358E6FB3ECC726E', X'1710262BBE34564A8358E6FB3ECC726E', 1, NULL, NULL, 2, NULL, 0, 'You will be keeping this heat going for about 1 to 1.5 hours so you''ll want enough smoking wood for about 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4D6F73E5292C349BCB67B4D9602A458', X'1710262BBE34564A8358E6FB3ECC726E', X'1710262BBE34564A8358E6FB3ECC726E', 2, NULL, NULL, 2, NULL, 0, 'Once the smoker is maintaining the proper temperature, you can proceed with the cooking process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7184413C3887F4F86E7583FE9ED6DAB', X'53D5391A1B9AA54FA141120E93FAD02F', X'53D5391A1B9AA54FA141120E93FAD02F', 0, NULL, NULL, 2, NULL, 0, 'If you used a Bradley rack, you can just place the Bradley rack right on the smoker grate and close the door or lid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D701A55F90B9894780DB6B4091090EC0', X'53D5391A1B9AA54FA141120E93FAD02F', X'53D5391A1B9AA54FA141120E93FAD02F', 1, NULL, NULL, 2, NULL, 0, 'Otherwise, place the chicken wing sections on the smoker grate with a little space between them to allow the heat and smoke to get to them easily.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8226EEAA04EE54AB48AD41180C41D3D', X'53D5391A1B9AA54FA141120E93FAD02F', X'53D5391A1B9AA54FA141120E93FAD02F', 2, NULL, NULL, 2, NULL, 0, 'Keep a light pecan smoke going for at least 1 hour. You can also use another wood if you do not have pecan or if you have another favorite smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BBC3E50FD3E9A41A66DCB6F05103525', X'53D5391A1B9AA54FA141120E93FAD02F', X'53D5391A1B9AA54FA141120E93FAD02F', 3, NULL, NULL, 2, NULL, 0, 'A Thermapen is really useful for checking things like chicken wings. It''s quick and you can check a lot of wings in a very short period of time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'932C300B6926CD4DBBEC5382E3B3A8E5', X'53D5391A1B9AA54FA141120E93FAD02F', X'53D5391A1B9AA54FA141120E93FAD02F', 4, NULL, NULL, 2, NULL, 0, 'A Thermapen is a really nice hand held thermometer that checks the temperature of meat in around 2-3 seconds by simply poking the meat with the probe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5797BB87EE81F4DB8BEB0625BA2DBDE', X'53D5391A1B9AA54FA141120E93FAD02F', X'53D5391A1B9AA54FA141120E93FAD02F', 5, NULL, NULL, 2, NULL, 0, 'You can use a different thermometer if you have one, just make sure it is accurate so you don''t undercook the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B092404E2ADD6541A775B75FE1E1311F', X'53D5391A1B9AA54FA141120E93FAD02F', X'53D5391A1B9AA54FA141120E93FAD02F', 6, NULL, NULL, 2, NULL, 0, 'When the chicken reaches an internal temperature of 175°F, it is time to super heat and crisp the skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2F4EE0313E43244966572EF8E672977', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 0, NULL, NULL, 2, NULL, 0, 'This process can be done on the grill or under the oven broiler--');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2B3B22D12A6DF4FB5439F4CD955E7B8', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 1, NULL, NULL, 2, NULL, 0, 'I highly recommend using the broiler feature on your home oven for this to get a more consistent heating. Turn it on `High` and it should be ready within just a minute or two.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95C6C78BBC544F4BBFDA0330239BCE55', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 2, NULL, NULL, 2, NULL, 0, 'Important: Transfer the chicken to a cookie sheet to prevent the grease from dripping down onto your oven floor and starting a fire.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A22EFA002358B64CA8358BB5E1E32F60', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 3, NULL, NULL, 2, NULL, 0, 'Electric (or Gas) broiler at top of oven:');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C86C394F1B46EC45B14F2BC36D33E6FA', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 4, NULL, NULL, 2, NULL, 0, 'Place the pan about 10-12 inches under the broiler.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94323CF738AA464896992738D3334388', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 5, NULL, NULL, 2, NULL, 0, 'After a minute or so the skin will start to sizzle/bubble and get dark, and when they reach a nice brown color, it is time to flip them over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B61F28C59E6DD5438D43F6B2DBF36E4A', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 6, NULL, NULL, 2, NULL, 0, 'Flip the chicken pieces over and do the same on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CB4238D60542745851AA5A38E3D75FF', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 7, NULL, NULL, 2, NULL, 0, 'Gas broiler in a drawer under the oven:');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129C0E51CDA67E45982BD39F066DE6DE', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 8, NULL, NULL, 2, NULL, 0, 'Place the pan of chicken under the broiler and leave the drawer pulled out so you can watch it the entire time. This type of broiler usually has the food closer to the heat so it will get done really fast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93DA0999DF9B864187B6FABA61C7DDD6', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 9, NULL, NULL, 2, NULL, 0, 'Very quickly the skin will start to sizzle/bubble and get dark, and when they reach a nice brown color, it is time to flip them over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BDD2213E7E62D43B8E03D17BA78E8E3', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 10, NULL, NULL, 2, NULL, 0, 'Pull the pan out of the broiler, flip the chicken pieces over and repeat the broiling process to crisp the skin on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EEEC5423C6CD64FB09BA8F1B2293BA3', X'550F670C6860404E8DED322FAD4EAC47', X'550F670C6860404E8DED322FAD4EAC47', 11, NULL, NULL, 2, NULL, 0, 'Note: The time will vary depending on how far the chicken is from the broiler element but it is imperative that you do not walk away during this process. Turn on the oven light, if you have one and watch them so they do not burn. When you see the sizzling of the skin, let that go on for about 15 seconds then proceed to flip them over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'637400400316034DA61E649AE0185CD7', X'FB3B7A72083EBB44BA844EA506B27285', X'FB3B7A72083EBB44BA844EA506B27285', 0, NULL, NULL, 2, NULL, 0, 'When the smoked chicken wings come out off the grill or out from the broiler, serve them immediately for best results. There''s nothing quite as good as smoked chicken wings still hot and burning your fingers.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0887F72C8C35544A9427937B218D372', X'018D173FEC4B044BB5025207125FE153', X'018D173FEC4B044BB5025207125FE153', 0, NULL, NULL, 2, NULL, 0, 'The whole rack and even the pieces already cut up will usually have a layer of fat on top of the meat. I choose to remove this as there is plenty of fat within the meat and I like for the rub to make direct contact with the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65123EBF4D0A1C4EB8EE84974A423099', X'018D173FEC4B044BB5025207125FE153', X'018D173FEC4B044BB5025207125FE153', 1, NULL, NULL, 2, NULL, 0, 'I asked the butcher to do this for me but it''s pretty easy to do with a sharp knife. Be sure to remove any silverskin you find as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'414BB2B26A61154EA767DDA3EEF95EFE', X'018D173FEC4B044BB5025207125FE153', X'018D173FEC4B044BB5025207125FE153', 2, NULL, NULL, 2, NULL, 0, 'Some of the fat runs deep into the meat and so you just have to trim it down level with the meat and stop, otherwise, you''d be cutting so far into the meat, the rack would separate into pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B757FB7F0172EB40BAB16423086E44EC', X'018D173FEC4B044BB5025207125FE153', X'018D173FEC4B044BB5025207125FE153', 3, NULL, NULL, 2, NULL, 0, 'Do the best you can and let the heat melt the rest of it away as it keeps the beef short ribs juicy.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47AD414217DF544D966922C6CB0DAE4F', X'018D173FEC4B044BB5025207125FE153', X'018D173FEC4B044BB5025207125FE153', 4, NULL, NULL, 2, NULL, 0, 'Flip the rack of beef ribs over to the membrane side and either remove the membrane or score it really good with a sharp knife. I got a small corner loose then grabbed it with a paper towel to pull it clean off.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A8CE839D9F0AC4D9B2CD6396AD5B842', X'2DED953E87EE8B45AE265932886617D3', X'2DED953E87EE8B45AE265932886617D3', 0, NULL, NULL, 2, NULL, 0, 'With the fat and silverskin removed, place the rack of ribs into a plastic or glass container and pour enough Worcestershire sauce over the meat to wet it real good.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4556ECA418BCEB4EB26301B3F261C7F4', X'2DED953E87EE8B45AE265932886617D3', X'2DED953E87EE8B45AE265932886617D3', 1, NULL, NULL, 2, NULL, 0, 'Add several tablespoons of minced garlic to the top and use a spoon to spread it over the meat. I used minced garlic in a bottle as it works just fine for things like this.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A25D404D949DF49BB2C169BC1913639', X'2DED953E87EE8B45AE265932886617D3', X'2DED953E87EE8B45AE265932886617D3', 2, NULL, NULL, 2, NULL, 0, 'Finally, sprinkle a good layer of my Texas rub (purchase recipes here) onto the top meat side and let it sit there for a few minutes and mingle with the other ingredients.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4BA0428B5FCBC4D8D27AEB7D7C7F8E9', X'2DED953E87EE8B45AE265932886617D3', X'2DED953E87EE8B45AE265932886617D3', 3, NULL, NULL, 2, NULL, 0, 'Once it looks nice and wet, flip the rack of beef short ribs over to top side down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC1419361F0E314E94EDC6E032C721AA', X'2DED953E87EE8B45AE265932886617D3', X'2DED953E87EE8B45AE265932886617D3', 4, NULL, NULL, 2, NULL, 0, 'Pour a little more Worcestershire sauce down in the bottom of the container to make sure the meat is making contact with the sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E5C6A00D47A914B8D34B2031223CE89', X'2DED953E87EE8B45AE265932886617D3', X'2DED953E87EE8B45AE265932886617D3', 5, NULL, NULL, 2, NULL, 0, 'Place a lid or cover on the container and put it it the fridge for at least 3 hours but up to 8 hours is perfectly fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CBF33D49F5CE94B85556F6A10B4A293', X'6CC5B68F8C172C48AB38385B623C8671', X'6CC5B68F8C172C48AB38385B623C8671', 0, NULL, NULL, 2, NULL, 0, 'While the meat is finishing up the marinating time, go ahead and get the smoker ready to go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F07187E0C2A70418DCE9A2B8B48F609', X'6CC5B68F8C172C48AB38385B623C8671', X'6CC5B68F8C172C48AB38385B623C8671', 1, NULL, NULL, 2, NULL, 0, 'You''ll want to set it up for cooking at about 250°F with about 3-4 hours worth of smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39599A76BAAFE945B5A5E31B01F6773A', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 0, NULL, NULL, 2, NULL, 0, 'I like to place the rack of ribs down in a foil pan to contain the juices while it cooks and especially if I plan to cover with foil later.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76185DB3B24D494982F9EEDA0039A18E', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 1, NULL, NULL, 2, NULL, 0, 'Bone side down, meat side up is the proper configuration.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E49F926333CE04DB50AA08FBE5605CD', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 2, NULL, NULL, 2, NULL, 0, 'Place the pan into a 250°F smoker and maintain light smoke for at least 3-4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6FE425701EE9E4E87652DDCD8180F68', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 3, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, I recommend using it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A62F70417278BF499C12F4A3B4E67440', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 4, NULL, NULL, 2, NULL, 0, 'Use a leave-in digital probe meat thermometer so you can monitor the temperature. The probe should go into meat only, without touching bone.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37897BECB4496F4BA69E5FC18E8BDFDC', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 5, NULL, NULL, 2, NULL, 0, 'The beef short ribs are cooked to about 200°F before calling them done. I took mine to 203°F and inserting a sharp skewer into the meat was giving me almost no resistance at all.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09AE396DD3767440885F89B50F183418', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 6, NULL, NULL, 2, NULL, 0, 'You can expect the beef short ribs to take 7-9 hours to reach 200°F. Be sure to use the Temperature NOT the time to tell you when they are done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2BAD12EE0AE1D488EFCE4CC7BE0123F', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 7, NULL, NULL, 2, NULL, 0, 'If you want to cover them at 160°F internal meat temperature it will help to speed them along but the bark formation will not be as good. I left mine uncovered down in the pan for the entire time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D9C6428FECB5D4082A62F31677D658B', X'FC680A78B0339C47B2C7C79F3948B70C', X'FC680A78B0339C47B2C7C79F3948B70C', 8, NULL, NULL, 2, NULL, 0, 'Once the ribs were finished cooking, I removed them from the smoker to the countertop and placed another foil pan on top to hold in a little heat while the meat rested for about 30 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D476CFEB4EED3C4A8E420BACEC78B966', X'6CED8DE0B929D54D93F7FE41435EBE2A', X'6CED8DE0B929D54D93F7FE41435EBE2A', 0, NULL, NULL, 2, NULL, 0, 'Cut the rack of ribs into individual bones and let folks dig in.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FD398CF3182D14A8D150B9D6C36408B', X'084548DDCB0CCB44837DFD49295732F5', X'084548DDCB0CCB44837DFD49295732F5', 0, NULL, NULL, 2, NULL, 0, 'These often come packaged in sets of (2) and it rarely says so on the package for some reason. A good way to tell is by the weight-- most tenderloins will weigh around 1 lb each so a 2 lb package probably has (2) tenderloins. If it says 1.17 lbs, there''s probably only one in there.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAB25F174DB11F45BE315C2D8A3FA4B2', X'084548DDCB0CCB44837DFD49295732F5', X'084548DDCB0CCB44837DFD49295732F5', 1, NULL, NULL, 2, NULL, 0, 'Remove these from the package and give them a good rinse under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C5F7FD27676F4A880C9679A0DE3993', X'084548DDCB0CCB44837DFD49295732F5', X'084548DDCB0CCB44837DFD49295732F5', 2, NULL, NULL, 2, NULL, 0, 'Pat dry with a paper towel and lay them on the cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09B229365B86274CA19456A8356047B5', X'74ECDE29F453094F861F3D56820DD8AB', X'74ECDE29F453094F861F3D56820DD8AB', 0, NULL, NULL, 2, NULL, 0, 'Use a very sharp knife to remove the silverskin that will often be along one side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9F77AB2E9E255409F904315BEE11D1B', X'74ECDE29F453094F861F3D56820DD8AB', X'74ECDE29F453094F861F3D56820DD8AB', 1, NULL, NULL, 2, NULL, 0, 'Slip the knife under one end and just follow it to the end. If you miss a part of it, just give it another go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'714DEE9E010F2F428775407F5ED9F886', X'74ECDE29F453094F861F3D56820DD8AB', X'74ECDE29F453094F861F3D56820DD8AB', 2, NULL, NULL, 2, NULL, 0, 'While you have the knife in hand, it''s a good idea to remove any excess fat as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'065789D20C052540A178C0D1BEE0B596', X'3FB5EB09A6101E4599362557085EA559', X'3FB5EB09A6101E4599362557085EA559', 0, NULL, NULL, 2, NULL, 0, 'I normally use my original rub on all pork but I wanted to try out the Texas style recipe on these pork tenderloins so I did one with the original recipe and one with the Texas style recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1478FDC2A2AF540B27BE459DCB88492', X'3FB5EB09A6101E4599362557085EA559', X'3FB5EB09A6101E4599362557085EA559', 1, NULL, NULL, 2, NULL, 0, 'As an afterthought, I also decided to use a different sticking base for each one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BBC9E03F14060469FB736E140CB941A', X'3FB5EB09A6101E4599362557085EA559', X'3FB5EB09A6101E4599362557085EA559', 2, NULL, NULL, 2, NULL, 0, 'Mayonnaise contains oil and egg and makes a great base for applying rub and helping it to stick. I often use this on chicken but felt it would marry better with the Texas style rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC1BCBD927C46E4DAAC6408CD47B5A54', X'3FB5EB09A6101E4599362557085EA559', X'3FB5EB09A6101E4599362557085EA559', 3, NULL, NULL, 2, NULL, 0, 'I used my normal mustard base with the original rub recipe on the other pork tenderloin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F82F25E719BC0848AA2F7493979DFF95', X'3FB5EB09A6101E4599362557085EA559', X'3FB5EB09A6101E4599362557085EA559', 4, NULL, NULL, 2, NULL, 0, 'Make sure to get the rub applied well to all sides and the ends.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C442B87FAF76734BB2F3D0070F8D5820', X'289615D5BECD3C4DB8291C5D0F2DB35D', X'289615D5BECD3C4DB8291C5D0F2DB35D', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5679071A5BBF234295852A9E07B92F53', X'289615D5BECD3C4DB8291C5D0F2DB35D', X'289615D5BECD3C4DB8291C5D0F2DB35D', 1, NULL, NULL, 2, NULL, 0, 'If your smoker came with a water pan, use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBA13435E846824F801488BF728A69E6', X'289615D5BECD3C4DB8291C5D0F2DB35D', X'289615D5BECD3C4DB8291C5D0F2DB35D', 2, NULL, NULL, 2, NULL, 0, 'When your smoker is up to temperature and holding steady, it''s time to cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8379B9F8E245B54B98F17DDCB492A896', X'D00B8604D7A8434B9639BF5EC9BFF2CF', X'D00B8604D7A8434B9639BF5EC9BFF2CF', 0, NULL, NULL, 2, NULL, 0, 'I placed the tenderloins on a Bradley rack to make it easy but you can also just place them directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DB7E1A2E9016B4BB5D011850ACD0CE9', X'D00B8604D7A8434B9639BF5EC9BFF2CF', X'D00B8604D7A8434B9639BF5EC9BFF2CF', 1, NULL, NULL, 2, NULL, 0, 'Keep the smoke going for at least 30 minutes but it''s ok to keep a light smoke going for the entire time as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA70865E33ACCD4AA3F1AE42BE0719AA', X'D00B8604D7A8434B9639BF5EC9BFF2CF', X'D00B8604D7A8434B9639BF5EC9BFF2CF', 2, NULL, NULL, 2, NULL, 0, 'I highly recommend the use of a remote digital probe meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BD3DEEE0C8FDD4EA1BCAEFE85F95EF0', X'D00B8604D7A8434B9639BF5EC9BFF2CF', X'D00B8604D7A8434B9639BF5EC9BFF2CF', 3, NULL, NULL, 2, NULL, 0, 'Is it vitally important that the pork tenderloin be removed from the smoker at 145°F. This is the safe temperature for pork.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B0E701E999FD04C99410B1A0EE7DB81', X'8F8181F388DDE1408E3065AD954F7768', X'8F8181F388DDE1408E3065AD954F7768', 0, NULL, NULL, 2, NULL, 0, 'Place a foil pan or a piece of foil over the tenderloins for about 10 minutes to let them rest.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8546E00E0655147BD97DC6DC2D03CF9', X'8F8181F388DDE1408E3065AD954F7768', X'8F8181F388DDE1408E3065AD954F7768', 1, NULL, NULL, 2, NULL, 0, 'Once rested, slice the pork into pieces and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C9388A3F72DF48940A4539873D0C69', X'C9C16074A816A94E87BC2DED1ED66547', X'C9C16074A816A94E87BC2DED1ED66547', 0, NULL, NULL, 2, NULL, 0, 'Most of the smoked sausage that I purchase locally comes (2) per package. Lay the links parallel with sides touching to speed up the cutting and cut them into ¾ to 1-inch long pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B4E8C63ACA0474F8B5DE8ED09A9A1F7', X'C9C16074A816A94E87BC2DED1ED66547', X'C9C16074A816A94E87BC2DED1ED66547', 1, NULL, NULL, 2, NULL, 0, 'Use a knife to cut an `X` in the top side of each piece of sausage down about 1/2 to 2/3 of the way through. Make sure to NOT cut all the way through.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4402119221CF9488FA0F882CF05C175', X'B05837C30E8D734E84C066B89136E398', X'B05837C30E8D734E84C066B89136E398', 0, NULL, NULL, 2, NULL, 0, 'Leave the cream cheese on the cabinet for about an hour prior to use so it can soften and be easier to work with.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F3F0DD9F989DA4BB1247952845B2302', X'B05837C30E8D734E84C066B89136E398', X'B05837C30E8D734E84C066B89136E398', 1, NULL, NULL, 2, NULL, 0, 'Place the 8 oz of cream cheese into a medium sized mixing bowl. Add a good handful of grated cheddar (about a cup if you want to measure) and 3-4 TBS of Jeff''s rub (purchase recipes here).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48E1BF49630BF348A0C79B99CF04AA72', X'B05837C30E8D734E84C066B89136E398', X'B05837C30E8D734E84C066B89136E398', 2, NULL, NULL, 2, NULL, 0, 'Combine all of the ingredients. A fork works well for this. Please note: I halved the recipe to make about 20 of these and had some left over.. the full recipe will make enough for about 40-50 depending on how much you use in each one');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E3ECC9C206A4444A766D602D5BFD1FD', X'CB8BD292936ECB42990EA6805314F27C', X'CB8BD292936ECB42990EA6805314F27C', 0, NULL, NULL, 2, NULL, 0, 'If you have an icing tool or bag, that will work well for operations like this. Otherwise, you can just put the cheese mixture into a small ziptop bag and cut off a small corner to make a stuffing tool that will work great.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60747ECF74E9874996EB90DE2CF33E8C', X'CB8BD292936ECB42990EA6805314F27C', X'CB8BD292936ECB42990EA6805314F27C', 1, NULL, NULL, 2, NULL, 0, 'Use one hand or a helper to open up the `X` in the sausage. Put the cut corner of the stuffing tool into the opening and squeeze until it oozes out of the sides and top of the sausage pieces. Be as generous as you like. You can''t get too much of this stuff.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64EC8EA93DF873408786F09E448ACDE6', X'403215461228BD4487A29A20EE42A2AE', X'403215461228BD4487A29A20EE42A2AE', 0, NULL, NULL, 2, NULL, 0, 'Cut off the stem of the peppers and carefully clean out the seeds and ribs of the peppers if you want to. The peppers will be hotter if you leave some of the seeds in there.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'396AB75924B45D429AFAFFCC9F03CD91', X'403215461228BD4487A29A20EE42A2AE', X'403215461228BD4487A29A20EE42A2AE', 1, NULL, NULL, 2, NULL, 0, 'I recommend using gloves any time you deal with hot peppers in any way.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C769ADC7C266FC488B45C0FDCD4A8E6B', X'403215461228BD4487A29A20EE42A2AE', X'403215461228BD4487A29A20EE42A2AE', 2, NULL, NULL, 2, NULL, 0, 'Once cleaned, cut the peppers into slices that are about 1/8-inch thick. Thicker is fine if you want more of the pepper flavor to come through.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489A249E9103A345BE017151F7ABEB8E', X'403215461228BD4487A29A20EE42A2AE', X'403215461228BD4487A29A20EE42A2AE', 3, NULL, NULL, 2, NULL, 0, 'Lay a pepper slice on top of each piece of stuffed sausage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD545496A95E2049A2FECE876B225C51', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', 0, NULL, NULL, 2, NULL, 0, 'Cut pieces of bacon in half and use ½ slice for each piece of sausage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84CAB033F1D5B34A8FB9018E2142C8CC', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', 1, NULL, NULL, 2, NULL, 0, 'Wrap the bacon across the top and bottom of the stuffed sausage pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C66B07C793D5A46909A85767A1F0982', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', 2, NULL, NULL, 2, NULL, 0, 'Try to end up with the ends of the bacon under the sausage to help hold it. Tip: Bacon can easily be stretched to adjust the length.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EB3B3DAD23DCB4CAC08D21775355524', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', X'5AF98E29F20DFD4DB12FDC8A7D9468B7', 3, NULL, NULL, 2, NULL, 0, 'No toothpick is needed in most cases as the bacon is sticky and will usually hold fine with no further pinning required.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D98FE4822A50AD479EA02C7D2E4641F0', X'50E6B8B552814F4082F9406FB61B3FC5', X'50E6B8B552814F4082F9406FB61B3FC5', 0, NULL, NULL, 2, NULL, 0, 'Make up a batch of Jeff''s rub (purchase recipes here) and put some of it into a cheese shaker or other spice dispenser with large holes. Sprinkle the rub onto the tops of the stuffed and wrapped sausage pieces. Be generous with the rub as it is low on salt and high on flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A74F9EF230845144AA29138952C2659B', X'50E6B8B552814F4082F9406FB61B3FC5', X'50E6B8B552814F4082F9406FB61B3FC5', 1, NULL, NULL, 2, NULL, 0, 'If you haven''t already, place the wrapped and stuffed sausage poppers onto a Bradley rack or a cookie sheet to aid in transporting them out to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84265318CA6CA94A88BA4F4347FB5EA2', X'2B746491B5ADF14A9A0BDFF9E12FAFB8', X'2B746491B5ADF14A9A0BDFF9E12FAFB8', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker, grill or other cooking device for 250°F. If your smoker will not get that hot, then 220 or 225 °F will work but please note that it will take longer and the bacon will not end up as firm as it would otherwise.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3527C5137FA53742B6A04C4501556660', X'2B746491B5ADF14A9A0BDFF9E12FAFB8', X'2B746491B5ADF14A9A0BDFF9E12FAFB8', 1, NULL, NULL, 2, NULL, 0, 'Use the water pan if you have one and apply smoke for at least 1 hour if you are using a charcoal, gas or electric smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FE022C9EF966E46A7EDE9F468C28BB0', X'2B746491B5ADF14A9A0BDFF9E12FAFB8', X'2B746491B5ADF14A9A0BDFF9E12FAFB8', 2, NULL, NULL, 2, NULL, 0, 'Leave the sausage pieces in the smoker until the bacon starts to get done. At that point you can paint on some good barbecue sauce (purchase recipes here) if you want to add another layer of flavor. I think it definitely adds to the `goodness` but use your own judgement there or do some with and some without to get a fair comparison from your guests.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0415A4AE0DE55B49A110E07214C79935', X'DC9C04ED8A0A9B4AAFC59395F147B1DA', X'DC9C04ED8A0A9B4AAFC59395F147B1DA', 0, NULL, NULL, 2, NULL, 0, 'These will go fast once you take them into the house so, eat a couple before you announce `done` and then rearrange them on the serving platter so no one will notice that a couple are missing');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63E420DD88CED4488FBB843789FA4B57', X'D8C627404016E34CAB0EDF2BC781B514', X'D8C627404016E34CAB0EDF2BC781B514', 0, NULL, NULL, 2, NULL, 0, 'For this recipe I purchased a whole boneless pork butt but after having sliced it up myself, I highly recommend that you just buy a package or two of pork steaks to make it a lot easier. I do not mind slicing it up, but I am all about convenience.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'650B5B0690CC8744A28B46FE0CAD4EED', X'D8C627404016E34CAB0EDF2BC781B514', X'D8C627404016E34CAB0EDF2BC781B514', 1, NULL, NULL, 2, NULL, 0, 'If you do decide to use a pork butt, I recommend some freezer time for the butt to stiffen it up a little bit. You don''t want it frozen but just at that point where it''s solid enough to cut good slices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AE0BE4394EDF9419BF7116DA7B73AD6', X'D8C627404016E34CAB0EDF2BC781B514', X'D8C627404016E34CAB0EDF2BC781B514', 2, NULL, NULL, 2, NULL, 0, 'A meat slice would also work well for this if you have one or have access to one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75C70E68D0F55B4D9FB244C6E3AE6123', X'29621F4107260E439DD42AB3EE3043C5', X'29621F4107260E439DD42AB3EE3043C5', 0, NULL, NULL, 2, NULL, 0, 'Remove as much of the fat on the outside as you can, you don''t need the fat cap for these. Use a sharp knife and you don''t have to spend a lot of time with it. Just remove what you can and move along.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4F634251D184741B8ACF8259F14E6B7', X'29621F4107260E439DD42AB3EE3043C5', X'29621F4107260E439DD42AB3EE3043C5', 1, NULL, NULL, 2, NULL, 0, 'Cut the pork butt into ¼ inch slices then into strips that are about 2 inches wide. If you are using pork steaks, you can probably get 2 to 3 strips from each steak.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6036BC1C95679544B88FCCA42F390F36', X'921715A12AE0E94EB9D128EB3F382542', X'921715A12AE0E94EB9D128EB3F382542', 0, NULL, NULL, 2, NULL, 0, 'Layer the strips of meat into a bowl and sprinkle my rub onto each layer. The rub will draw some of the moisture out of the meat and it will become a thick marinade that will flavor the strips of pork very deeply.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0167CEB457D54744B3C5B0A3D54F929F', X'921715A12AE0E94EB9D128EB3F382542', X'921715A12AE0E94EB9D128EB3F382542', 1, NULL, NULL, 2, NULL, 0, 'Once all of the meat is layered into the bowl, sprinkle some extra rub on top and cover the bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E71252890DE441BE5C87095149F980', X'921715A12AE0E94EB9D128EB3F382542', X'921715A12AE0E94EB9D128EB3F382542', 2, NULL, NULL, 2, NULL, 0, 'Place the bowl in the fridge overnight to marinate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5587DC0C1311ED43BCC0A88DE85B58D9', X'921715A12AE0E94EB9D128EB3F382542', X'921715A12AE0E94EB9D128EB3F382542', 3, NULL, NULL, 2, NULL, 0, 'Note, you can also use a zip top bag if that is more convenient. Place all of the meat into the bag and then pour enough rub into the bag to coat the meat really well. ½ cup is probably enough but you will have to make sure that all of the meat is coated well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F6AC37AAB497143AE637CE6BE001F4D', X'921715A12AE0E94EB9D128EB3F382542', X'921715A12AE0E94EB9D128EB3F382542', 4, NULL, NULL, 2, NULL, 0, 'Massage the closed bag to combine the meat and the rub then place in the fridge overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64E49045C0E610459563DAA5E18D6876', X'242B7069909B6349AB6E598CF2341762', X'242B7069909B6349AB6E598CF2341762', 0, NULL, NULL, 2, NULL, 0, 'Do not rinse the meat when it is finished marinating.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0AB53DE631C7A428FEB1A017B6EACC1', X'242B7069909B6349AB6E598CF2341762', X'242B7069909B6349AB6E598CF2341762', 1, NULL, NULL, 2, NULL, 0, 'Use long wooden skewers that are about 12-inches long if possible');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B55B6B75478BCC4AA4813A369F602608', X'242B7069909B6349AB6E598CF2341762', X'242B7069909B6349AB6E598CF2341762', 2, NULL, NULL, 2, NULL, 0, 'Push the skewer through the meat making sure to keep it inside the meat if possible. If it seems to be hard to push through, try twisting the skewer as you push.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'288D712853153741BD12352E5F224E64', X'242B7069909B6349AB6E598CF2341762', X'242B7069909B6349AB6E598CF2341762', 3, NULL, NULL, 2, NULL, 0, 'As the sticks are loaded, place them into a Bradley rack with room between each one so the smoke and heat can have ample access.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA904EBFA0AE1640B66211AD5BD8B933', X'C27AC7BE28F11048ACDBBA4A6ABD5584', X'C27AC7BE28F11048ACDBBA4A6ABD5584', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 225-240°F for about 2 hours and apply smoke for at least 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7CA1B99CD71B642B6A282A73EEB9DE9', X'C27AC7BE28F11048ACDBBA4A6ABD5584', X'C27AC7BE28F11048ACDBBA4A6ABD5584', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, fill it ¾ full with water to create a nice humid environment inside the smoker to reduce drying.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F02EBDB1B9859640A5FA6BDB66085D02', X'C27AC7BE28F11048ACDBBA4A6ABD5584', X'C27AC7BE28F11048ACDBBA4A6ABD5584', 2, NULL, NULL, 2, NULL, 0, 'Place the skewers directly on oiled smoker racks or you can use the Bradley racks if you have them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D815A071DD15DF4A88792BCF6E989515', X'C27AC7BE28F11048ACDBBA4A6ABD5584', X'C27AC7BE28F11048ACDBBA4A6ABD5584', 3, NULL, NULL, 2, NULL, 0, 'Once the meat reaches about 170°F, coat both sides with my barbecue sauce. It is ok to crank up the heat at this point if your smoker has this ability. I used a propane smoker for this session and turned the control knob to high to help caramelize the sauce and to get a little charring on the outside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87D1A49135D51646B8611C407FDF5DE5', X'C27AC7BE28F11048ACDBBA4A6ABD5584', X'C27AC7BE28F11048ACDBBA4A6ABD5584', 4, NULL, NULL, 2, NULL, 0, 'You could also transfer them to a hot grill for this operation. If using this option, let the meat reach 180°F in the smoker before removing them to the grill and adding sauce and high heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C178FF7D58AE946A0B0899D29D406F9', X'C27AC7BE28F11048ACDBBA4A6ABD5584', X'C27AC7BE28F11048ACDBBA4A6ABD5584', 5, NULL, NULL, 2, NULL, 0, 'If you plan to use the grill to finish the meat, you will want to soak the skewers in water for a few hours before loading them with meat. This will help to prevent them from catching fire in the high heat. You should also keep a spray bottle of water close by the grill to extinguish any unexpected flare ups.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B99820D815BE24DA23CB682F78C9075', X'C27AC7BE28F11048ACDBBA4A6ABD5584', X'C27AC7BE28F11048ACDBBA4A6ABD5584', 6, NULL, NULL, 2, NULL, 0, 'If they seem to be drying out and/or if you are not able to use a water pan in your smoker, the skewers of meat could be placed into a large roasting pan and covered in foil to finish once they get about 1 hour of good smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C518F7129B885948993A3E7881025F54', X'A130708C73694B489C5F0B41914A0CFE', X'A130708C73694B489C5F0B41914A0CFE', 0, NULL, NULL, 2, NULL, 0, 'Let folks start grabbing these as soon as theyjjjj are finished and while they are really hot and delicious. Be sure to provide some sauce on the side for those who like to dip. I found that I did not need sauce after the fact as there was plenty caramelized onto the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A3E935141B045448BD15113FCBA7DBB', X'109E1DDE378099479600E70C5B973E47', X'109E1DDE378099479600E70C5B973E47', 0, NULL, NULL, 2, NULL, 0, 'Take two slices of loaf bread or the top and bottom half of a bun and tear it into small pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6FEC62702FC9F4EA2A0E221B75CB684', X'109E1DDE378099479600E70C5B973E47', X'109E1DDE378099479600E70C5B973E47', 1, NULL, NULL, 2, NULL, 0, 'With the bread in a small bowl, pour ½ cup of buttermilk over the bread. The bread will soak up the buttermilk and this will be part of what makes the meatloaf so moist.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6CF79BC4A6EE24EB35E03B120D60CDD', X'109E1DDE378099479600E70C5B973E47', X'109E1DDE378099479600E70C5B973E47', 2, NULL, NULL, 2, NULL, 0, 'Once the milk is soaked in, crack a couple of large eggs onto the bread mixture. Stir gently to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B3A6105160A20468334BCC2A2728994', X'109E1DDE378099479600E70C5B973E47', X'109E1DDE378099479600E70C5B973E47', 3, NULL, NULL, 2, NULL, 0, 'Set aside');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37C8BF04B2D26149971B0865856DB500', X'A97529FADAF66E4C8D7F2819DCA19749', X'A97529FADAF66E4C8D7F2819DCA19749', 0, NULL, NULL, 2, NULL, 0, 'Dice bell peppers, onion and celery so that you end up with about ½ cup of each.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3CB2F8DD03B764DB73756496DCEB701', X'A97529FADAF66E4C8D7F2819DCA19749', X'A97529FADAF66E4C8D7F2819DCA19749', 1, NULL, NULL, 2, NULL, 0, 'If you have extra, place them in a zip top bag and place them in the freezer for later.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8ED51D4BEDF73E49A57C763C524B9930', X'A97529FADAF66E4C8D7F2819DCA19749', X'A97529FADAF66E4C8D7F2819DCA19749', 2, NULL, NULL, 2, NULL, 0, 'Put 2 TBS of olive oil into a skillet over medium heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C01E9CFFB8BDDB46B4F6EB5D58AA4F3B', X'A97529FADAF66E4C8D7F2819DCA19749', X'A97529FADAF66E4C8D7F2819DCA19749', 3, NULL, NULL, 2, NULL, 0, 'Once the pan is hot enough, pour in the onions, peppers and celery and stir to mix with the oil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269ABCF4964AB645A761C2F2A3CA3496', X'A97529FADAF66E4C8D7F2819DCA19749', X'A97529FADAF66E4C8D7F2819DCA19749', 4, NULL, NULL, 2, NULL, 0, 'Let them cook for about 10 minutes or until the vegetables start to get slightly soft.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7821E6D49807A04FB37ED2C2B97D2CAA', X'A97529FADAF66E4C8D7F2819DCA19749', X'A97529FADAF66E4C8D7F2819DCA19749', 5, NULL, NULL, 2, NULL, 0, 'Once the veggies are finished cooking, remove them from the pan and into a plate or bowl to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B880996FB2578947B5AFE41A8F064A72', X'A97529FADAF66E4C8D7F2819DCA19749', X'A97529FADAF66E4C8D7F2819DCA19749', 6, NULL, NULL, 2, NULL, 0, 'Add ½ cup of Jeff''s barbecue sauce and 3 TBS of Jeff''s rub to the sautéed vegetables. Stir to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26FD11139159C14883DEAD8150A135CD', X'4B3E6C00367F7342859E3E4B2D698EF4', X'4B3E6C00367F7342859E3E4B2D698EF4', 0, NULL, NULL, 2, NULL, 0, 'Place the 1 pound of ground chuck and 1 pound of ground sausage into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C46729D9D398204BA73E674E248D6F2B', X'4B3E6C00367F7342859E3E4B2D698EF4', X'4B3E6C00367F7342859E3E4B2D698EF4', 1, NULL, NULL, 2, NULL, 0, 'Add the bread, buttermilk and egg mixture to the top of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0515C216773DA4393C56C973732D861', X'4B3E6C00367F7342859E3E4B2D698EF4', X'4B3E6C00367F7342859E3E4B2D698EF4', 2, NULL, NULL, 2, NULL, 0, 'Add the vegetable, sauce and rub mixture to the top of the meat as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C132CCD80F562E4F9AD073D94B7A3392', X'4B3E6C00367F7342859E3E4B2D698EF4', X'4B3E6C00367F7342859E3E4B2D698EF4', 3, NULL, NULL, 2, NULL, 0, 'Add 1 teaspoon of salt to the top of the mixture and gently combine the ingredients together.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96C80DFA3BD23441BEDB75A9DB610173', X'4B3E6C00367F7342859E3E4B2D698EF4', X'4B3E6C00367F7342859E3E4B2D698EF4', 4, NULL, NULL, 2, NULL, 0, 'Do not overmix. Do just enough to combine and stop. Overworking the meatloaf mixture will make it tough.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93DBF1FE85FEC1478FCAFFA52B975C4A', X'E3BA7CB807638E44947F6A737834A666', X'E3BA7CB807638E44947F6A737834A666', 0, NULL, NULL, 2, NULL, 0, 'Pour or scoop the mixture onto a cookie sheet, food grade butcher paper, etc. and form into a loaf with your hands.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6F0458B87C48246A5179880C0475C7E', X'E3BA7CB807638E44947F6A737834A666', X'E3BA7CB807638E44947F6A737834A666', 1, NULL, NULL, 2, NULL, 0, 'You can also place the mixture into a loaf pan to form it and then dump it out onto the pan, rack or tray that you will use to cook it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F581F148BA204C4193845FEA1BC609E5', X'E3BA7CB807638E44947F6A737834A666', X'E3BA7CB807638E44947F6A737834A666', 2, NULL, NULL, 2, NULL, 0, 'You will notice that this mixture may be a little wetter than what you are used to using but that''s ok. It will form and hold together just fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'741166B8B3BFD940BED622C29D9DF496', X'E3BA7CB807638E44947F6A737834A666', X'E3BA7CB807638E44947F6A737834A666', 3, NULL, NULL, 2, NULL, 0, 'I recommend about 2 inches thick in the shape of a rectangle but you can get creative with this if you like.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C43D51AD8F2CA4890FB6FEFB9D84CED', X'E3BA7CB807638E44947F6A737834A666', X'E3BA7CB807638E44947F6A737834A666', 4, NULL, NULL, 2, NULL, 0, 'If using a Bradley rack, I recommend placing a piece of wax paper under the meat so it will be easier to remove once done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3F519EF9EBC424AA7ED67425EA77634', X'92C27F877725E1479A1D4B18B90555C9', X'92C27F877725E1479A1D4B18B90555C9', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F using indirect heat with hickory smoke or whatever smoking wood you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6391F5357CD82D49ACDD6540DD1F59D9', X'92C27F877725E1479A1D4B18B90555C9', X'92C27F877725E1479A1D4B18B90555C9', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, I recommend that you use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CDEA395E5536449947B509B0B7A5CE9', X'92C27F877725E1479A1D4B18B90555C9', X'92C27F877725E1479A1D4B18B90555C9', 2, NULL, NULL, 2, NULL, 0, 'Once your smoker is maintaining the proper temperature, the cooking can commence.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BCFF027A0E09F47804C6B672DEA1164', X'92C27F877725E1479A1D4B18B90555C9', X'92C27F877725E1479A1D4B18B90555C9', 3, NULL, NULL, 2, NULL, 0, 'Place the meatloaf in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8CEA197F1563641BC1A5C63C76622BE', X'92C27F877725E1479A1D4B18B90555C9', X'92C27F877725E1479A1D4B18B90555C9', 4, NULL, NULL, 2, NULL, 0, 'Let the loaf cook for 3-4 hours or until it reaches about 155°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75B75FEE01FB544DB642F0B6DBF11848', X'4CBF1F8392585F46BA0CF6DA04AE886A', X'4CBF1F8392585F46BA0CF6DA04AE886A', 0, NULL, NULL, 2, NULL, 0, 'At about 155°F, brush the top and sides of the meatloaf with plenty of Jeff''s barbecue sauce. Give it about 25-30 minutes to caramelize then remove from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C33775AD0797E4E8E67971D182F684B', X'4CBF1F8392585F46BA0CF6DA04AE886A', X'4CBF1F8392585F46BA0CF6DA04AE886A', 1, NULL, NULL, 2, NULL, 0, 'Finish temperature for meatloaf is actually 160°F but knowing that it will continue to cook and rise in temperature even after being removed from the smoker, it is ok to remove it a few degrees early.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D43DF3F66E5D7C4CA7ACE68CA7187B32', X'F4386F2D0F6E56459FE158CD518AC379', X'F4386F2D0F6E56459FE158CD518AC379', 0, NULL, NULL, 2, NULL, 0, 'Once the meat is brought into the house, tent some foil over the top and let it rest for 15-20 minutes before slicing and serving. This resting period gives the juices in the meat time to redistribute throughout the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A90BA9784581348975527E70AA6CC7C', X'0AD649952D36C94589CEAF22D6263847', X'0AD649952D36C94589CEAF22D6263847', 0, NULL, NULL, 2, NULL, 0, 'Remove ribs from package and rinse well under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E50293DE8759B34BA1415FE6B158BE17', X'0AD649952D36C94589CEAF22D6263847', X'0AD649952D36C94589CEAF22D6263847', 1, NULL, NULL, 2, NULL, 0, 'Pat dry with paper towels and lay on cutting board');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C25E2E5C77AC484298B4E99459BAFC03', X'25A6A3EE5ECD7D4A93A86E8B0BF2C1A9', X'25A6A3EE5ECD7D4A93A86E8B0BF2C1A9', 0, NULL, NULL, 2, NULL, 0, 'If the ribs have a membrane (thick plastic-like skin on bone side), it’s a good idea to remove it if you can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22A1B5CBC18A4A48A0CC0EF8008818CB', X'45F43B209F942942975E742674E5BEF7', X'45F43B209F942942975E742674E5BEF7', 0, NULL, NULL, 2, NULL, 0, 'Baby backs: Use a very sharp knife and slice right between the bones.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E878314234DB43B01F45AC8C6BC1FA', X'45F43B209F942942975E742674E5BEF7', X'45F43B209F942942975E742674E5BEF7', 1, NULL, NULL, 2, NULL, 0, 'Spare ribs: For best results, trim these up St. Louis style. This just means you remove the brisket bone that runs along the leading edge and square them up a little by removing the extra pieces of meat on the ends. Then slice them up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D333316E6896244D8C08BA14A4D157ED', X'C61898F9A16C084AA7FE1F0C365724F9', X'C61898F9A16C084AA7FE1F0C365724F9', 0, NULL, NULL, 2, NULL, 0, 'These will do well on any smoker or even a grill as long as you can maintain temperatures between 225-240°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FF53CE09906CF41BC8BAC3A81590249', X'C61898F9A16C084AA7FE1F0C365724F9', X'C61898F9A16C084AA7FE1F0C365724F9', 1, NULL, NULL, 2, NULL, 0, 'Make sure you have enough smoking wood for about 3-4 hours of smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4D4202D3D0E6E4CA44510BB0BD531B0', X'C61898F9A16C084AA7FE1F0C365724F9', X'C61898F9A16C084AA7FE1F0C365724F9', 2, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, I recommend using it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1289128DB5F4AE478299C27FBB58F356', X'C61898F9A16C084AA7FE1F0C365724F9', X'C61898F9A16C084AA7FE1F0C365724F9', 3, NULL, NULL, 2, NULL, 0, 'Once the smoker is set up and holding the proper temperature, you can proceed with cooking the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDB61A307D8A0E45B529DD77D680C12F', X'7A3AF74755F79942ADC58BE31F408CCB', X'7A3AF74755F79942ADC58BE31F408CCB', 0, NULL, NULL, 2, NULL, 0, 'Smoke them for about 2 hours at 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'639917A7E81CF348B6482C29FCB7763E', X'BCC2E45ED57456438C7B3AA5039A219F', X'BCC2E45ED57456438C7B3AA5039A219F', 0, NULL, NULL, 2, NULL, 0, 'Toss them into a foil pan. Sprinkle 1/4 cup of my original rub on them for the first layer of flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B425640CA26C7A469B6CE401B6712381', X'BCC2E45ED57456438C7B3AA5039A219F', X'BCC2E45ED57456438C7B3AA5039A219F', 1, NULL, NULL, 2, NULL, 0, 'Cover the foil pan of rubbed ribs with foil. Make sure it’s tight around the edges to keep the steam inside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C60F00B31D05E4BB14B0A1A23A6F461', X'BCC2E45ED57456438C7B3AA5039A219F', X'BCC2E45ED57456438C7B3AA5039A219F', 2, NULL, NULL, 2, NULL, 0, 'Leave them this way for 1 to 1.5 hours depending on how tender you want them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6DF8B48D8CEE54C91BD524A848AF06C', X'BCC2E45ED57456438C7B3AA5039A219F', X'BCC2E45ED57456438C7B3AA5039A219F', 3, NULL, NULL, 2, NULL, 0, 'When this step is finished, simply remove the foil cover from the top of the pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'804B9ADFBDEA21489B23F99778882749', X'1B8E728B096DF04E950FFC1CCA0D146E', X'1B8E728B096DF04E950FFC1CCA0D146E', 0, NULL, NULL, 2, NULL, 0, 'Apply about 1 cup of my barbecue sauce all over the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7006EBA1F7DEA445A4C04D70BCED4B49', X'1B8E728B096DF04E950FFC1CCA0D146E', X'1B8E728B096DF04E950FFC1CCA0D146E', 1, NULL, NULL, 2, NULL, 0, 'Toss the ribs around in the pan to make sure they are well coated with the sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ECD1AC2C93813449F69C5FD0B05C609', X'1B8E728B096DF04E950FFC1CCA0D146E', X'1B8E728B096DF04E950FFC1CCA0D146E', 2, NULL, NULL, 2, NULL, 0, 'Now sprinkle about 1/2 cup of rub all over the tops of the ribs then shake the pan lightly to coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA29733D7C77F84A9A01CC68D9618FB3', X'38CEDAE38A9E604C901379A528B0CC53', X'38CEDAE38A9E604C901379A528B0CC53', 0, NULL, NULL, 2, NULL, 0, 'With the top left uncovered, continue to cook the ribs for an additional 1.5 to 2 hours or until they are at the consistency that you want.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A02CF3FD526D5040B8E16277E55EB027', X'3F311F27F9F5AF4B9E5DDEE6F80C1956', X'3F311F27F9F5AF4B9E5DDEE6F80C1956', 0, NULL, NULL, 2, NULL, 0, 'When they are done cooking, you can cover them with foil, and hold them on low heat for an hour or serve them right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E2001DF2A9F24E9E321278E5527F2C', X'3F311F27F9F5AF4B9E5DDEE6F80C1956', X'3F311F27F9F5AF4B9E5DDEE6F80C1956', 1, NULL, NULL, 2, NULL, 0, 'Dump into a serving container or let folks grab them right out of the pan.. your choice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14583788FAFC0E489914A4613E04E6B2', X'6928CB932D20ED4894526B0099E29B93', X'6928CB932D20ED4894526B0099E29B93', 0, NULL, NULL, 2, NULL, 0, 'To brine the chicken breasts make a solution of: 1 quart of cold water, ¼ cup of coarse kosher salt and ¼ cup of brown sugar.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4012C058F140684CA3B6BE4B2FDAA8B7', X'6928CB932D20ED4894526B0099E29B93', X'6928CB932D20ED4894526B0099E29B93', 1, NULL, NULL, 2, NULL, 0, 'Stir well until everything is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914F51C5878BD44AA55CCA44D9ACF8E9', X'6928CB932D20ED4894526B0099E29B93', X'6928CB932D20ED4894526B0099E29B93', 2, NULL, NULL, 2, NULL, 0, 'Place the chicken breasts into a zip top bag or other plastic or glass container and pour the brine over the chicken to cover. Place in the fridge for about 2 hours to brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7239C7D7810C404D8C6F6BA5A7158C8D', X'6928CB932D20ED4894526B0099E29B93', X'6928CB932D20ED4894526B0099E29B93', 3, NULL, NULL, 2, NULL, 0, 'Once the brining is complete, remove the chicken from the brine and rinse well under cold water. Pat dry and set aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15D463C9C186EB4C86F1A690B84DEEC5', X'283808EDB2C2AD46B697E98F90E0838A', X'283808EDB2C2AD46B697E98F90E0838A', 0, NULL, NULL, 2, NULL, 0, 'Leave the cream cheese out on the table for about an hour to soften.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E1DE1CA44316941855E5B2AB4988BA1', X'283808EDB2C2AD46B697E98F90E0838A', X'283808EDB2C2AD46B697E98F90E0838A', 1, NULL, NULL, 2, NULL, 0, 'Place a block of cream cheese or about 8 oz into a small to medium sized mixing bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88DE45A049D3A94D8F713A99FDCA2171', X'283808EDB2C2AD46B697E98F90E0838A', X'283808EDB2C2AD46B697E98F90E0838A', 2, NULL, NULL, 2, NULL, 0, 'Deseed, clean and dice 2-3 jalapeño peppers depending on how well you like jalapeño and how spicy the jalapeños are.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E011943381FFD54CB74FEC778A11F015', X'283808EDB2C2AD46B697E98F90E0838A', X'283808EDB2C2AD46B697E98F90E0838A', 3, NULL, NULL, 2, NULL, 0, 'Put the diced peppers into the bowl with the cream cheese and stir to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD07B37E8290284FBC0D03081D98F747', X'2DCE329583A04F4D891AA5C3ABBC243B', X'2DCE329583A04F4D891AA5C3ABBC243B', 0, NULL, NULL, 2, NULL, 0, 'Brush a little olive oil onto the chicken breasts then season the top side with Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A04966476D0DB04281F4EE9796E3D696', X'2DCE329583A04F4D891AA5C3ABBC243B', X'2DCE329583A04F4D891AA5C3ABBC243B', 1, NULL, NULL, 2, NULL, 0, 'Add a heaping tablespoon of the cream cheese/jalapeno mixture on top of the rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99734667E76C9943911E08A63ED26A68', X'2DCE329583A04F4D891AA5C3ABBC243B', X'2DCE329583A04F4D891AA5C3ABBC243B', 2, NULL, NULL, 2, NULL, 0, 'Fold the chicken over on top of itself. To make it easier to fold, you can make a shallow cut at about the halfway point of the chicken breast widthwise.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5A3732016ED464D80335940F881180A', X'2DCE329583A04F4D891AA5C3ABBC243B', X'2DCE329583A04F4D891AA5C3ABBC243B', 3, NULL, NULL, 2, NULL, 0, 'Once the chicken is folded coat the entire outside of the chicken breast with a little olive oil and season generously with Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'239B4190CEC4714491C31ABEDF45A60B', X'5FE484CBFC9E3C408B697874C3A08D99', X'5FE484CBFC9E3C408B697874C3A08D99', 0, NULL, NULL, 2, NULL, 0, 'Lay 2 thin slices of bacon across the stuffed chicken breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E5066674F242145A624796FC262D227', X'5FE484CBFC9E3C408B697874C3A08D99', X'5FE484CBFC9E3C408B697874C3A08D99', 1, NULL, NULL, 2, NULL, 0, 'Lift the chicken up and hold it with one hand while you wrap the bacon `artistically` around the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550115E3EAA1B144A225428C4C2EB467', X'B598D283AEF33A4A90164DF7A18EF1A0', X'B598D283AEF33A4A90164DF7A18EF1A0', 0, NULL, NULL, 2, NULL, 0, 'Place the stuffed chicken on a Bradley rack, cookie sheet or something similar to make it easy to carry them out to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50177F1BA70F6D4F95705CBE9A3974B4', X'B598D283AEF33A4A90164DF7A18EF1A0', X'B598D283AEF33A4A90164DF7A18EF1A0', 1, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking with indirect heat at around 240°F if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C56327990A278B4A90A08AD4F70DD0D5', X'B598D283AEF33A4A90164DF7A18EF1A0', X'B598D283AEF33A4A90164DF7A18EF1A0', 2, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, it''s a good idea to use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E640620FBE5AF4CB8559D9EF85D8CAA', X'B598D283AEF33A4A90164DF7A18EF1A0', X'B598D283AEF33A4A90164DF7A18EF1A0', 3, NULL, NULL, 2, NULL, 0, 'I used pecan but you can use any smoking wood that you desire and have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B171F75E996F74B8642F2C77D1D62E9', X'B598D283AEF33A4A90164DF7A18EF1A0', X'B598D283AEF33A4A90164DF7A18EF1A0', 4, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the stuffed chicken directly on the smoker grate and insert a probe from a digital probe meat thermometer (if you have one) so you will know the exact time it is finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38E7B82EAEA99F4FBCF737CC6FD62F73', X'B598D283AEF33A4A90164DF7A18EF1A0', X'B598D283AEF33A4A90164DF7A18EF1A0', 5, NULL, NULL, 2, NULL, 0, 'You can also use a super fast meat thermometer such as the Thermapen but this requires you to open the door several times and you stand the chance of overcooking the chicken if it happens to get done sooner than you expect.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F59239AC9A41847BBF67B00361DB6C8', X'471D4A01416B0F4CB0501CB1DD4A9E58', X'471D4A01416B0F4CB0501CB1DD4A9E58', 0, NULL, NULL, 2, NULL, 0, 'When the chicken is done cooking, remove it from the smoker and let it rest for a couple of minutes before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFC33E5A1A778841982AF2202CFBB68D', X'F22903DFF3F36B44BBD560A0DB204764', X'F22903DFF3F36B44BBD560A0DB204764', 0, NULL, NULL, 2, NULL, 0, 'Give the ribs a good rinse to remove bone fragments and anything else that shouldn’t be there. Pat them dry with a paper towel and lay on the cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F757B2797F201A4B905884A41E448186', X'84C992962E2B194C8400DB141F4B5C50', X'84C992962E2B194C8400DB141F4B5C50', 0, NULL, NULL, 2, NULL, 0, 'I prefer to remove the membrane but it is certainly not worth a lot of time, effort and frustration. If you have trouble with it, you can skip this step and still have some of the best smoked ribs in town.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BB30A36F67A9446801D54096A591570', X'84C992962E2B194C8400DB141F4B5C50', X'84C992962E2B194C8400DB141F4B5C50', 1, NULL, NULL, 2, NULL, 0, 'I have smoked ribs with the membrane and without the membrane and as far as tenderness, moisture and flavor, there’s not a big difference in my opinion either way.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDDE022B65097343BE0A284724E0E53A', X'84C992962E2B194C8400DB141F4B5C50', X'84C992962E2B194C8400DB141F4B5C50', 2, NULL, NULL, 2, NULL, 0, 'Most baby backs will not have a lot of fat but if they do, spend a few minutes removing any of it that is easy to get to with a sharp knife. I had to remove a little bit on these but I did not obsess about it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B0B3BCDF8A97A40BFC8CC763CF6F1DC', X'84C992962E2B194C8400DB141F4B5C50', X'84C992962E2B194C8400DB141F4B5C50', 3, NULL, NULL, 2, NULL, 0, 'Any extra flaps of meat sticking out? Slice it off and set it aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FD00DC3A304DA46AB2EA354FB5BE047', X'81BA96611AF1D54F94A8C609652B0593', X'81BA96611AF1D54F94A8C609652B0593', 0, NULL, NULL, 2, NULL, 0, 'After you have spend minimal time doing a little prep work to the ribs, get that batch of my rib rub that you mixed up earlier and sprinkle a good coating on the top of the ribs. Just enough so that you can no longer see the meat under the rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F97607A9A7FAD24E9216BEB183C767CF', X'81BA96611AF1D54F94A8C609652B0593', X'81BA96611AF1D54F94A8C609652B0593', 1, NULL, NULL, 2, NULL, 0, 'Go take a break for about 10 minutes and wait for the few little granules of salt in my rub to start pulling the juice to the surface.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E517BF05099F6741838459E53928433F', X'81BA96611AF1D54F94A8C609652B0593', X'81BA96611AF1D54F94A8C609652B0593', 2, NULL, NULL, 2, NULL, 0, 'After a few minutes, the top surface of the ribs will start to look wet. This is the moisture in the ribs being pulled to the surface where it will mix with the rub and create a paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'612ABFCA640D96409FD6CF0522CB1EFC', X'81BA96611AF1D54F94A8C609652B0593', X'81BA96611AF1D54F94A8C609652B0593', 3, NULL, NULL, 2, NULL, 0, 'After about 10-15 minutes of waiting, I like to give it another good sprinkle of the rub for good measure and because my rub recipe is high on flavor and LOW on salt, this is not a problem ?');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD54AC4200A12540A2BEA8858036CB54', X'81BA96611AF1D54F94A8C609652B0593', X'81BA96611AF1D54F94A8C609652B0593', 4, NULL, NULL, 2, NULL, 0, 'After this second sprinkle, go get the smoker ready.. those ribs are ready for their destiny!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'186D2DE1C6474144BE51FEF9E1216615', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49C06614EB4D2C488DB883462DA6F325', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 1, NULL, NULL, 2, NULL, 0, 'I used pecan smoke for these but use whatever smoking wood that you like and have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73B6FD327E97EA468BF356B28EFED3C1', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 2, NULL, NULL, 2, NULL, 0, 'Once your smoker is preheated and you have selected the wood you will use and it is producing a little smoke, you are ready to place the pork ribs in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F36C715757CF1C4D826379F1AE0582C4', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 3, NULL, NULL, 2, NULL, 0, 'Place the rack(s) of ribs directly on the smoker grate boney side down, meaty side up and quickly close the door or lid to your smoker. Leave them this way for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A81EC1922537442A9C72E95B9364FF2', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 4, NULL, NULL, 2, NULL, 0, 'After 2 hours, either wrap them in foil or place them into a very large foil pan covered with foil over the top. Both options work equally well but the foil pan is easier and a little more leak proof. Leave them in this configuration for 2 hours in the smoker at 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'246B14CA7DF67743A08BFBE9BD6831E5', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 5, NULL, NULL, 2, NULL, 0, 'Please note: This foil stage is where the ribs get super tender. If you do not like super tender ribs then you will need to adjust the amount of time the ribs are in the foil. Some folks only leave them in the foil for 1 hour to create ribs that are less tender.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2D543CA22A3CB4690520B1E4A335CF7', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 6, NULL, NULL, 2, NULL, 0, 'It is now time to unwrap the ribs from the foil or remove them from the foil pan and place them back on the smoker grate at 225-240°F for about 1 hour. This is also a great time to start painting some sauce on them if you are going for sticky ribs. If you are using my rub, you won’t need sauce but you may still want some and that is perfectly fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1264BD022ECC7146825D6FB121A7753B', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 7, NULL, NULL, 2, NULL, 0, 'This 3-step method of cooking pork ribs is known as the 2-2-1 method for smoked baby backs. (for spare ribs it’s 3-2-1 with an extra hour at the beginning)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF74441291C9734E86D112A1A3E3DFE2', X'6D6F3E22867D5B45948D6E8E0A899FC6', X'6D6F3E22867D5B45948D6E8E0A899FC6', 8, NULL, NULL, 2, NULL, 0, 'If you need some metrics for making sure that the ribs are actually ready to eat: Pork is safe at 145°F internally but pork ribs are perfectly tender at 195°F. Feel free to check that with a Thermapen or other digital meat thermometer in that meaty area right between the bones, The bones should be showing about ½ inch or more due to the meat pulling back, and the rack of ribs should bend nearly in half when held up at one end with a pair of tongs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9C7A8C184C7244DA7B23651900664CD', X'0AECC5997304D14CB9529B1D847D8E6D', X'0AECC5997304D14CB9529B1D847D8E6D', 0, NULL, NULL, 2, NULL, 0, 'Here’s the easy part.. remove the ribs from the smoker, turn the rack upside down on the cutting board and slice between the bones with a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E9DACFD805C6F488E53F8D83BF13850', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 0, NULL, NULL, 2, NULL, 0, 'Place the ribeye steaks on a cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'501C7B936A1B7547BB8FB00B14C4F8DF', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 1, NULL, NULL, 2, NULL, 0, 'Note: This one was just over 2 lbs and was about 3 inches thick. Any size and/or thickness will work but thicker is better in my opinion.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3395434D743C5545AE7BDB103EA216CD', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 2, NULL, NULL, 2, NULL, 0, 'Drizzle olive oil onto them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0C12FDA29A29141B1305FDEE52AA927', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 3, NULL, NULL, 2, NULL, 0, 'Use a basting brush to coat the entire piece of meat with the oil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F59885AA03D037419ADD4678BE9B1D9F', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle a generous amount of Jeff’s Texas style rub recipe onto the top, sides and bottom of the steaks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC4227DEC669B94FA51DE8AE4C7BC74B', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 5, NULL, NULL, 2, NULL, 0, 'Note: the Original rub recipe (Jeff’s Naked Rib Rub) is also very good on steak and I have used it many times with excellent results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8567A177A04AB14FB9847D2C2BF3757C', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 6, NULL, NULL, 2, NULL, 0, 'Let the steaks sit on the counter for a few minutes while you get the smoker ready. You will notice that within a few minutes, the rub begins to draw some of the moisture out of the meat. The moisture then mixes with the rub ingredients and it creates a paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96FBE8E3FEA60143B4DD42079AA2BC30', X'603B619C22B07B469359E3FF26C6862A', X'603B619C22B07B469359E3FF26C6862A', 7, NULL, NULL, 2, NULL, 0, 'Place the steaks on a Bradley rack or a cookie sheet to make it easy to transport them to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36C362BE798508479740BF503306AB6E', X'06E33987CCF95E4FBABEA09EE0A02820', X'06E33987CCF95E4FBABEA09EE0A02820', 0, NULL, NULL, 2, NULL, 0, 'I used the Landmann propane smoker for this cook but any smoker will work great as long as it can maintain about 220°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B9D9D532D87ED4DAC8C8C144D203690', X'06E33987CCF95E4FBABEA09EE0A02820', X'06E33987CCF95E4FBABEA09EE0A02820', 1, NULL, NULL, 2, NULL, 0, 'Set it up for indirect cooking and use the water pan if your smoker was designed with one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF77D84DA5F29144AEDF2222AF52C506', X'06E33987CCF95E4FBABEA09EE0A02820', X'06E33987CCF95E4FBABEA09EE0A02820', 2, NULL, NULL, 2, NULL, 0, 'I recommend cherry wood for smoke but any smoking wood will work great for these.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA13DE3DFFA7B840AC914FC6BCA20FAF', X'06E33987CCF95E4FBABEA09EE0A02820', X'06E33987CCF95E4FBABEA09EE0A02820', 3, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the steak right on the smoker grate or if you used the Bradley rack, you can just place the rack right on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE8D060BB55E845AE2120B68FA43B72', X'06E33987CCF95E4FBABEA09EE0A02820', X'06E33987CCF95E4FBABEA09EE0A02820', 4, NULL, NULL, 2, NULL, 0, 'Keep the heat and smoke going for about any hour or until the internal temperature of the steak reads about 110-115°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D14571D5C82F24092DF747705F262AC', X'C1C81C0EB3951E408D2883309C83BC06', X'C1C81C0EB3951E408D2883309C83BC06', 0, NULL, NULL, 2, NULL, 0, 'Once the steaks reach an internal temperature of 110-115°F, place them on an already preheated gas or charcoal grill. The grill needs to be very hot.. the hotter the better in my opinion.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'177505357C114E4EA3984692D267446A', X'C1C81C0EB3951E408D2883309C83BC06', X'C1C81C0EB3951E408D2883309C83BC06', 1, NULL, NULL, 2, NULL, 0, 'Keep a close eye on the steaks and do not leave them alone at all. Place them on the hot grill for about 2 minutes then rotate them 90° to get nice looking grill marks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FC43980102FBB4885C1CED4571BCB5C', X'C1C81C0EB3951E408D2883309C83BC06', X'C1C81C0EB3951E408D2883309C83BC06', 2, NULL, NULL, 2, NULL, 0, 'In 2 more minutes or so, flip them over and do the same thing on that side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6485438FDAE724BB6334D42F2510145', X'C1C81C0EB3951E408D2883309C83BC06', X'C1C81C0EB3951E408D2883309C83BC06', 3, NULL, NULL, 2, NULL, 0, 'The amount of time it takes to get a good char is completely dependent on the temperature of the grill so it may take more or less than 2 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01DBE76696E1F14B82CC235A25FD087E', X'C1C81C0EB3951E408D2883309C83BC06', X'C1C81C0EB3951E408D2883309C83BC06', 4, NULL, NULL, 2, NULL, 0, 'As an alternative, you can bring them into the house and place them under the broiler of the oven for this charring step. You will not get the coveted grill marks but you will get a great char and color and because the heat is working on the top of the steak instead of the bottom, you’ll be able to easily see when it’s had enough and is ready to be flipped over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A9236D5DF3F344089320282D17AA571', X'C1C81C0EB3951E408D2883309C83BC06', X'C1C81C0EB3951E408D2883309C83BC06', 5, NULL, NULL, 2, NULL, 0, 'Leave the smoker going and if the charring is complete but the steaks are not to your desired doneness, you can easily place them right back in the smoker until the desired temperature is acquired.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'708F31261DDB574B8A7FEC6C22652A1C', X'DEBC31C7495F72478C1C1B1571FA42C1', X'DEBC31C7495F72478C1C1B1571FA42C1', 0, NULL, NULL, 2, NULL, 0, 'When the ribeye steaks have reached your desired level of doneness (I recommend medium rare or 135°F for best flavor, tenderness and juiciness), lay them on the cutting board and cover loosely with a foil pan or piece of foil to allow them to rest for 10 minutes. During the resting period, the juices in the steak will settle down and redistribute throughout the meat. The meat will also raise a few degrees in temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F1738FCCEDEF7438D16E57F9BFA6111', X'DEBC31C7495F72478C1C1B1571FA42C1', X'DEBC31C7495F72478C1C1B1571FA42C1', 1, NULL, NULL, 2, NULL, 0, 'Cut or carve the steak(s) as desired and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEE28DC2133B6A4BB935A3C0F3374671', X'DEBC31C7495F72478C1C1B1571FA42C1', X'DEBC31C7495F72478C1C1B1571FA42C1', 2, NULL, NULL, 2, NULL, 0, 'You can obviously cook them to a higher internal temperature if that’s the way you like to eat steak ?');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1733295C286FE046B7C625C95B0D9575', X'DEBC31C7495F72478C1C1B1571FA42C1', X'DEBC31C7495F72478C1C1B1571FA42C1', 3, NULL, NULL, 2, NULL, 0, 'Enjoy the perfectly cooked smoked bone-in ribeye steaks!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFD794AC4BA2C64FBCB2C2F351553912', X'71B3E5A04C6AF147A26F65A3455071EC', X'71B3E5A04C6AF147A26F65A3455071EC', 0, NULL, NULL, 2, NULL, 0, 'Add 1 cup of coarse kosher salt to 1 gallon of water and stir until the salt is dissolved and the water returns to clear. Add 1 cup of brown sugar and stir to mix it into the water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CA6BA6E2979FB4BBDD0EF9351719A3A', X'71B3E5A04C6AF147A26F65A3455071EC', X'71B3E5A04C6AF147A26F65A3455071EC', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken into a food safe plastic or glass bowl and pour the brine over the chicken to cover. You can also use a large zip top bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65ABC88D97BB424A94E33FDADE37F8D0', X'71B3E5A04C6AF147A26F65A3455071EC', X'71B3E5A04C6AF147A26F65A3455071EC', 2, NULL, NULL, 2, NULL, 0, 'Close or cover the container and place it in the fridge for about 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE31370512DC0F449EAFD3C6F2E54D94', X'71B3E5A04C6AF147A26F65A3455071EC', X'71B3E5A04C6AF147A26F65A3455071EC', 3, NULL, NULL, 2, NULL, 0, 'After the brining has finished, rinse the outside of the chicken with cold water to remove any residual salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9839D247A56DC409A2B722AF05F2A9F', X'E1A6EA3870052349902CC4FE5D387BB2', X'E1A6EA3870052349902CC4FE5D387BB2', 0, NULL, NULL, 2, NULL, 0, 'Pour maple syrup onto the chicken and use a brush to coat the chicken all over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11BA782EEC4E5E41BD3BA6C09D642191', X'E1A6EA3870052349902CC4FE5D387BB2', X'E1A6EA3870052349902CC4FE5D387BB2', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff’s original rub all over the top, sides and bottom of the chicken making sure to get the rub down in all of the hard to reach spots. It is also a good idea to get some of the maple syrup and rub down under the skin wherever you can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F58450BE417774D8FB7756AE57C84DB', X'95DC9D9912CC5745A0EAA32610E0331C', X'95DC9D9912CC5745A0EAA32610E0331C', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 250°F if possible. Some smokers will not cook this hot so a lower temperature will work but it will require a longer cook time for the chicken to get done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD1BD35877A14D49AC5D12F7814866A3', X'95DC9D9912CC5745A0EAA32610E0331C', X'95DC9D9912CC5745A0EAA32610E0331C', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the chicken on the grate breast side down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C98BB995274FF24B8DEA9C675EBE9C1F', X'95DC9D9912CC5745A0EAA32610E0331C', X'95DC9D9912CC5745A0EAA32610E0331C', 2, NULL, NULL, 2, NULL, 0, 'In gas, electric, charcoal and non-conventional cookers, apply smoke (I recommend pecan for this recipe) for the entire time the chicken is cooking. The skin does a great job of protecting the meat from the smoke and just enough gets through to make it deliciously smoky.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C036A9647DF8847A305C75133E316BF', X'95DC9D9912CC5745A0EAA32610E0331C', X'95DC9D9912CC5745A0EAA32610E0331C', 3, NULL, NULL, 2, NULL, 0, 'My 4 lb chicken took about 2 hours and 10 minutes to reach 165°F. Times will vary from cooker to cooker so be sure to let the internal temperature of the chicken decide when it’s done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38AF64874238CE4BB6ABB742B4046430', X'95DC9D9912CC5745A0EAA32610E0331C', X'95DC9D9912CC5745A0EAA32610E0331C', 4, NULL, NULL, 2, NULL, 0, 'Use a digital probe meat thermometer in the thickest part of the thigh or breast to monitor the meat temperature while it cooks. You can also check the temperature with a super fast Thermapan when you baste. With the Thermapen, you can get an accurate reading in around 2-3 seconds.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD401F41826ED148BE326D9018DBC587', X'95DC9D9912CC5745A0EAA32610E0331C', X'95DC9D9912CC5745A0EAA32610E0331C', 5, NULL, NULL, 2, NULL, 0, 'Make the Maple Barbecue Mop Sauce by mixing 4 oz of maple syrup with 2 heaping tablespoons of Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4D739D919408B4480DB65CED2590A10', X'95DC9D9912CC5745A0EAA32610E0331C', X'95DC9D9912CC5745A0EAA32610E0331C', 6, NULL, NULL, 2, NULL, 0, 'After the first hour, begin to apply the maple barbecue mop sauce every 20 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'074678300835D64A82C0CC3A81E3BEBB', X'CB8F07B082611F49A1EE2DAC31B89AE0', X'CB8F07B082611F49A1EE2DAC31B89AE0', 0, NULL, NULL, 2, NULL, 0, 'Once the chicken reaches 165°F it is done cooking and should be removed from the smoker. Allow it to rest for 10 minutes or so before carving to allow the juices to cool down a little and redistribute throughout the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6BBE3B2780B0C4AA669683816CD4260', X'398540D7350C66408FE32DE7940AF844', X'398540D7350C66408FE32DE7940AF844', 0, NULL, NULL, 2, NULL, 0, 'Cook bacon until it is crispy. You will need 2 slices per pork chop.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'005F9DBE8019624E80137B066783076F', X'398540D7350C66408FE32DE7940AF844', X'398540D7350C66408FE32DE7940AF844', 1, NULL, NULL, 2, NULL, 0, 'Make extra so you’ll have some to nibble on.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AA4E67F6B6773429437109E050EFED2', X'398540D7350C66408FE32DE7940AF844', X'398540D7350C66408FE32DE7940AF844', 2, NULL, NULL, 2, NULL, 0, 'Let it cool then crumble into small to medium sized pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F41E30044A2BD742861A5BD715796A2C', X'398540D7350C66408FE32DE7940AF844', X'398540D7350C66408FE32DE7940AF844', 3, NULL, NULL, 2, NULL, 0, 'Buy already shredded cheese or you can buy a block of cheese and shred it yourself.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'078FBF4FB81917488A19A98E039458AF', X'398540D7350C66408FE32DE7940AF844', X'398540D7350C66408FE32DE7940AF844', 4, NULL, NULL, 2, NULL, 0, 'I shredded about a cup of cheese so I had leftovers. You’ll only need about 2 TBS per pork chop.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEE9A97E2A75B94D9D664E325DD27491', X'398540D7350C66408FE32DE7940AF844', X'398540D7350C66408FE32DE7940AF844', 5, NULL, NULL, 2, NULL, 0, 'Mix 2 TBS of shredded cheese and 2 slices of crumbled bacon for each pork chop you plan to smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB9443DB7ABAA040B6DF38473402D9A0', X'2465AE87D6FC4648943E55BF7759E753', X'2465AE87D6FC4648943E55BF7759E753', 0, NULL, NULL, 2, NULL, 0, 'I purchased pork chops that were already butterflied and ready to go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11DED50C455E5840BA8A2078C6B12FBE', X'2465AE87D6FC4648943E55BF7759E753', X'2465AE87D6FC4648943E55BF7759E753', 1, NULL, NULL, 2, NULL, 0, 'First, we’ll season the inside just a little with my original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08C2019AB9C6244582D735D16A33CA52', X'2465AE87D6FC4648943E55BF7759E753', X'2465AE87D6FC4648943E55BF7759E753', 2, NULL, NULL, 2, NULL, 0, 'Add a little spicy brown mustard to help the rub to stick');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE802473C9822440885F45513DEA71BD', X'2465AE87D6FC4648943E55BF7759E753', X'2465AE87D6FC4648943E55BF7759E753', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle my rub on the top');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86BC3E382538264B943A6AB3DF995587', X'2465AE87D6FC4648943E55BF7759E753', X'2465AE87D6FC4648943E55BF7759E753', 4, NULL, NULL, 2, NULL, 0, 'Place some of the cheese/bacon mixture to the top of the pork chop (one of the sides only)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BDDB33751ED2549B2A176522CCC88D0', X'2465AE87D6FC4648943E55BF7759E753', X'2465AE87D6FC4648943E55BF7759E753', 5, NULL, NULL, 2, NULL, 0, 'Fold the butterflied pork chop over on top of itself');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'958CA1BAB5E87E4AB5A26AEA6659E550', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 0, NULL, NULL, 2, NULL, 0, 'Apply some spicy brown mustard to the outside of the stuffed pork chops');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0FEE8D86DC49D488EABF692DB95A879', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 1, NULL, NULL, 2, NULL, 0, 'Rub the mustard all over the top and bottom of the pork chop then sprinkle Jeff’s original rub on both sides generously.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE197BAB790944428FB37CBFF3D2F112', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 2, NULL, NULL, 2, NULL, 0, 'Cut (3) 24 inch pieces of butchers twine for each pork chop to tie it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B221373A3EF4E440AC3705B4C6B0C319', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 3, NULL, NULL, 2, NULL, 0, 'Lay the pieces of twine parallel and at about 2 inch intervals.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489C3289E207F946B6D4DE9ED70A2C82', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 4, NULL, NULL, 2, NULL, 0, 'Lay the pork chop on top of the twine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F770929DABFCF40BD04090CD64A6DA2', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 5, NULL, NULL, 2, NULL, 0, 'Loop each piece of twine over the stuffed pork chop, pull it tight and tie a knot that will hold.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B380E38E0895F84189CF82EB12EC06C1', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 6, NULL, NULL, 2, NULL, 0, 'Trim the extra string with scissors or a sharp knife.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9A3CACD869D044E8F6D3B52C011DD99', X'549056FF65D30648B0A61E4ACAA4338C', X'549056FF65D30648B0A61E4ACAA4338C', 7, NULL, NULL, 2, NULL, 0, 'Place the tied up, stuffed pork chops on a bradley rack or cookie sheet and they are ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD8021B4B3A14F40BD08CE7E24966B25', X'83B6BD70FFBAC74F8460C70C3A443712', X'83B6BD70FFBAC74F8460C70C3A443712', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking indirectly at about 225-240°F using cherry or your favorite smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2A68A2EA667D04AB2B4BA2A1653DC99', X'83B6BD70FFBAC74F8460C70C3A443712', X'83B6BD70FFBAC74F8460C70C3A443712', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the stuffed pork chops directly on the grate or if you use Bradley racks, you can just place the rack onto the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'801C813EADA40346881FFA293F175C40', X'83B6BD70FFBAC74F8460C70C3A443712', X'83B6BD70FFBAC74F8460C70C3A443712', 2, NULL, NULL, 2, NULL, 0, 'Place a digital probe meat thermometer into one of the pork chops so you’ll know when it is perfectly done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BCBDC4DC5451C428971F8D4FC7CD1AC', X'83B6BD70FFBAC74F8460C70C3A443712', X'83B6BD70FFBAC74F8460C70C3A443712', 3, NULL, NULL, 2, NULL, 0, 'You can expect the pork chops to take from 1.5 to 2 hours depending on actual thickness, how often you open the lid, weather conditions, and how cold the meat was when it went into the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F44F61A0F386084C8D14A421ED6119D6', X'83B6BD70FFBAC74F8460C70C3A443712', X'83B6BD70FFBAC74F8460C70C3A443712', 4, NULL, NULL, 2, NULL, 0, 'If you are using a smoker that is not fueled by wood, you should apply smoke for at least an hour. I usually keep a light smoke going for the entire time to replicate the real wood smoker experience.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2925B0652E713D4691D7BD16612ADBF0', X'83B6BD70FFBAC74F8460C70C3A443712', X'83B6BD70FFBAC74F8460C70C3A443712', 5, NULL, NULL, 2, NULL, 0, 'Ultimately, the pork chops are done and ready to eat when they reach 145°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E52F493A3DB18D40801E31DB626424F3', X'D0B27ADB07732A45B8924EEAED84FEC3', X'D0B27ADB07732A45B8924EEAED84FEC3', 0, NULL, NULL, 2, NULL, 0, 'Serve the pork chops as they come off the smoker if possible so your eaters will get to experience the dish at it’s very best and while the cheese is still very melty.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7608DC3E775A314E99E1132EB39CFC2E', X'D0B27ADB07732A45B8924EEAED84FEC3', X'D0B27ADB07732A45B8924EEAED84FEC3', 1, NULL, NULL, 2, NULL, 0, 'Don’t forget to cut and remove the butchers twine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CBD5744425F874E910D71071038C0FC', X'210FBFB046CD114DA4BFA85A0CF51604', X'210FBFB046CD114DA4BFA85A0CF51604', 0, NULL, NULL, 2, NULL, 0, 'Beef short ribs tend to have a lot of fat. Remove the fat and silver skin. A very sharp knife makes this job a lot faster and easier.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'199779BAE644A646AF37FB16E4B12E09', X'8734D3E815B69C489927EFBC7FE55B00', X'8734D3E815B69C489927EFBC7FE55B00', 0, NULL, NULL, 2, NULL, 0, 'Place the beef short ribs in a plastic zip top bag and squirt about ¼ cup of spicy brown mustard down in the bag with about ¼ cup of Jeff’s Texas style rub recipe');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376CD829C588FA468EB41310E6BAC4D1', X'8734D3E815B69C489927EFBC7FE55B00', X'8734D3E815B69C489927EFBC7FE55B00', 1, NULL, NULL, 2, NULL, 0, 'Massage and roll the bag to coat all of the ribs with mustard and rub paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDF54DA75D3B5E4D87F7A7983017EB62', X'CA3026024DA67F4F832914E2B0A9D5B2', X'CA3026024DA67F4F832914E2B0A9D5B2', 0, NULL, NULL, 2, NULL, 0, 'Once the mustard and rub are applied, let them sit in the fridge overnight to really soak up the flavor of the rub. You can also use the ribs right away or as soon as you want to. Any time at all with the mustard and rub will do them good.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8FC903110BF7544961E7161A0FFBBA1', X'CA3026024DA67F4F832914E2B0A9D5B2', X'CA3026024DA67F4F832914E2B0A9D5B2', 1, NULL, NULL, 2, NULL, 0, 'Do NOT rinse the ribs once they are finished marinating.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76D2AD3AF2FB32489C13245CFA46ADBB', X'CA3026024DA67F4F832914E2B0A9D5B2', X'CA3026024DA67F4F832914E2B0A9D5B2', 2, NULL, NULL, 2, NULL, 0, 'They are now ready to cook. I recommend placing them down in a disposable foil pan and leaving them there during the entire cooking process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2147AEE2255D741B53F13A11F44B96C', X'D830368FA5C6564B998E0EF50CECF77C', X'D830368FA5C6564B998E0EF50CECF77C', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for indirect heat at about 225-240°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8989867B5720E44B9E6E21AC2156745', X'D830368FA5C6564B998E0EF50CECF77C', X'D830368FA5C6564B998E0EF50CECF77C', 1, NULL, NULL, 2, NULL, 0, 'Leave the ribs in the pan for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F75EC8EEA39C443A1CFA08E7A1FFA81', X'D830368FA5C6564B998E0EF50CECF77C', X'D830368FA5C6564B998E0EF50CECF77C', 2, NULL, NULL, 2, NULL, 0, 'Apply cherry smoke for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A050E4E079BD4408196F5117CC0BF7F', X'D51475058E6E704486B4000D685D9FB1', X'D51475058E6E704486B4000D685D9FB1', 0, NULL, NULL, 2, NULL, 0, 'After applying smoke for about 2 hours, pour about 1 cup of red wine (I used Rosa Regale but any similar red sweet wine will work great)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'206ECC9BE36C7C4A857F3F2DB6B3B78D', X'D51475058E6E704486B4000D685D9FB1', X'D51475058E6E704486B4000D685D9FB1', 1, NULL, NULL, 2, NULL, 0, 'Cover the pan tightly with foil and let it cook for 3 hours at 225-240°F. During this time, the wine and beef juices will create steam and the beef will get a good braising inside the covered pan serving to super tenderize the beef.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'901E84F55C806944849A785EF75CA9A7', X'D51475058E6E704486B4000D685D9FB1', X'D51475058E6E704486B4000D685D9FB1', 2, NULL, NULL, 2, NULL, 0, 'Note: Don’t want to use wine for some reason? You can use sparkling red grape juice or even regular red grape juice to get a similar effect and flavor without the alcohol content.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ECA487E20459F46A14DAC3C3DF52398', X'9519B4645269514B80B21A201BEF0045', X'9519B4645269514B80B21A201BEF0045', 0, NULL, NULL, 2, NULL, 0, 'After the braising process, uncover the ribs and turn up the heat to 300 °F. If your smoker won’t heat to that high, use the grill or even the oven to finish them off.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE8F16B65E708A4EAE5BD311C9FCF704', X'9519B4645269514B80B21A201BEF0045', X'9519B4645269514B80B21A201BEF0045', 1, NULL, NULL, 2, NULL, 0, 'Mix 1 part Jeff’s sauce with 2 parts of the red wine (for example: 1/3 cup Jeff’s sauce + 2/3 cup red wine) and brush the ribs with this mixture.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AD16D59B3F4AF46B0D01A97EB63A84B', X'9519B4645269514B80B21A201BEF0045', X'9519B4645269514B80B21A201BEF0045', 2, NULL, NULL, 2, NULL, 0, 'Brush the ribs with this mixture 2-3 times during the next hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B5E7C3C5BC374083D3187ECE51F32B', X'9519B4645269514B80B21A201BEF0045', X'9519B4645269514B80B21A201BEF0045', 3, NULL, NULL, 2, NULL, 0, 'Use a Thermapen or other accurate digital meat thermometer to test the temperature of the ribs. When they reach 190-200°F and are fork tender, they are done. “Tender” is the best measurement of when these beef short ribs are done. Another visual indicator of doneness is the meat pulling back from the bone.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C16CBFAA5229A409194F7B995AACE0F', X'8C36E47AE788BB4B85FBC19B92A1859C', X'8C36E47AE788BB4B85FBC19B92A1859C', 0, NULL, NULL, 2, NULL, 0, 'Once the beef short ribs are finished cooking, serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EDE0731E84CFB4CB602F8160B7069E7', X'DC7CE3359AC86E43ADF51102B42E0CD8', X'DC7CE3359AC86E43ADF51102B42E0CD8', 0, NULL, NULL, 2, NULL, 0, 'Add 1 cup of coarse kosher salt to 1/2 gallon of cold water..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'825FFB5A2D0BEF4E81F0F8CB9F2AE65D', X'DC7CE3359AC86E43ADF51102B42E0CD8', X'DC7CE3359AC86E43ADF51102B42E0CD8', 1, NULL, NULL, 2, NULL, 0, 'Stir in the salt until it dissolves and the water becomes clear.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91682D60289A3F4FBE8A1F435715AC77', X'DC7CE3359AC86E43ADF51102B42E0CD8', X'DC7CE3359AC86E43ADF51102B42E0CD8', 2, NULL, NULL, 2, NULL, 0, 'Add ¼ cup of Jeff''s rub and 4 bottles of beer (12 oz)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7557F8854FF8864DA5D736D9CA944C05', X'DC7CE3359AC86E43ADF51102B42E0CD8', X'DC7CE3359AC86E43ADF51102B42E0CD8', 3, NULL, NULL, 2, NULL, 0, 'Stir to combine. It may get a little foamy..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CA920E2F9FE7641BE2B62D18606684A', X'DC7CE3359AC86E43ADF51102B42E0CD8', X'DC7CE3359AC86E43ADF51102B42E0CD8', 4, NULL, NULL, 2, NULL, 0, 'The brine is ready to use');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B22C8A1C02E644A93B219FA6C7A82C4', X'B96DF7F0C98FF143865C7F252259A73B', X'B96DF7F0C98FF143865C7F252259A73B', 0, NULL, NULL, 2, NULL, 0, 'Rinse chicken pieces under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3341E9F47B9A504C89E9E4EADFF149CB', X'B96DF7F0C98FF143865C7F252259A73B', X'B96DF7F0C98FF143865C7F252259A73B', 1, NULL, NULL, 2, NULL, 0, 'Place chicken into a plastic or glass container');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8A5C1002916E24DBB1258A9F1C9E689', X'B96DF7F0C98FF143865C7F252259A73B', X'B96DF7F0C98FF143865C7F252259A73B', 2, NULL, NULL, 2, NULL, 0, 'Pour the brine over the chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6800C51BD9ACD94FB45C87C5D23095F8', X'B96DF7F0C98FF143865C7F252259A73B', X'B96DF7F0C98FF143865C7F252259A73B', 3, NULL, NULL, 2, NULL, 0, 'Add some ice on top to make sure it is nice and cold and place the bowl in the fridge for about 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F86C47903FCA1F4FBE5D95E91E96CA88', X'B96DF7F0C98FF143865C7F252259A73B', X'B96DF7F0C98FF143865C7F252259A73B', 4, NULL, NULL, 2, NULL, 0, 'When the brining is finished, rinse the chicken really good to remove any extra salt residue from the surface.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C635DAEF5EF5ED4A9AB61D42EBBC55FD', X'15C336F2F8501C4CACFDB3DD2E994B97', X'15C336F2F8501C4CACFDB3DD2E994B97', 0, NULL, NULL, 2, NULL, 0, 'Place the chicken pieces into a large ziptop bag and pour ¼ cup of Jeff''s rub into the bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5E913BE273D16429BE9ACA33CB39EB0', X'15C336F2F8501C4CACFDB3DD2E994B97', X'15C336F2F8501C4CACFDB3DD2E994B97', 1, NULL, NULL, 2, NULL, 0, 'Zip the bag closed and roll or massage the bag to coat all of the chicken with the rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA929976068C1741949A3BBF2B426811', X'15C336F2F8501C4CACFDB3DD2E994B97', X'15C336F2F8501C4CACFDB3DD2E994B97', 2, NULL, NULL, 2, NULL, 0, 'Once the chicken is well coated, place the chicken pieces onto a Bradley rack or pan. It is now ready to cook in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68E6ED46E5ABF547A6536F1C2C507D86', X'D41791C631E0E144B30E7794C5483B3D', X'D41791C631E0E144B30E7794C5483B3D', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 275°F if possible. If your smoker will not heat to that temperature, then normal smoking temperatures (225-240°F) will work fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BD9A86700F29D47A8F82EA24B725122', X'D41791C631E0E144B30E7794C5483B3D', X'D41791C631E0E144B30E7794C5483B3D', 1, NULL, NULL, 2, NULL, 0, 'Use indirect heat (most smokers are designed for this by default).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D371187B8C222047BF2AF8EF717A1E1E', X'D41791C631E0E144B30E7794C5483B3D', X'D41791C631E0E144B30E7794C5483B3D', 2, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the chicken on the smoker grate and let it smoke cook until it reaches 165°F in the the thickest part of each piece. This should take about 1 hour but watch the temperature carefully as the times will vary from smoker to smoker and depending on how often you open the lid, how cold the chicken entered the smoker and even weather.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE8F36AD12736C4E96CC39EC15D8B1C4', X'D41791C631E0E144B30E7794C5483B3D', X'D41791C631E0E144B30E7794C5483B3D', 3, NULL, NULL, 2, NULL, 0, 'About 15 minutes before the chicken is finished, brush on the beer barbecue glaze (recipe below)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8CB2972069D054BB4A235A67390F1AA', X'BDCC5BED3ACD8140A81164B14239D051', X'BDCC5BED3ACD8140A81164B14239D051', 0, NULL, NULL, 2, NULL, 0, '2 TBS Jeff''s barbecue sauce');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E01120D8D6BEF41BB6330E286415499', X'BDCC5BED3ACD8140A81164B14239D051', X'BDCC5BED3ACD8140A81164B14239D051', 1, NULL, NULL, 2, NULL, 0, '1/4 cup beer');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F0537E673FC9D4B969E101149B4D260', X'BDCC5BED3ACD8140A81164B14239D051', X'BDCC5BED3ACD8140A81164B14239D051', 2, NULL, NULL, 2, NULL, 0, 'Stir well to combine. You can make it more thick or thin by changing the ratio of sauce to beer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F209AC2B29A4D499D6FFACC3386DDED', X'C2AA7822E6AE5743804D593C97C1EB08', X'C2AA7822E6AE5743804D593C97C1EB08', 0, NULL, NULL, 2, NULL, 0, 'Check each piece using a quick reading thermometer such as a Thermapen to make sure a safe temperature of 165°F has been obtained.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'249260385FA428438E929C6FB31398DA', X'C2AA7822E6AE5743804D593C97C1EB08', X'C2AA7822E6AE5743804D593C97C1EB08', 1, NULL, NULL, 2, NULL, 0, 'Serve with extra beer barbecue sauce for dipping if you like.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CFB4E2CDB875446B777DC2D9F563805', X'DC827FCBB9647D41BB8B18E8163ECAFD', X'DC827FCBB9647D41BB8B18E8163ECAFD', 0, NULL, NULL, 2, NULL, 0, 'This chicken was extremely juicy, even the white breast meat and you could distinctively taste the beer flavor all the way through the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41C826E79C6B8344B9E8131F477EAAC0', X'DC827FCBB9647D41BB8B18E8163ECAFD', X'DC827FCBB9647D41BB8B18E8163ECAFD', 1, NULL, NULL, 2, NULL, 0, 'Use any kind of beer you like.. the cheap stuff will work perfectly. I usually use Shiner Bock when I use beer in recipes and it works great.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'913D8DACA35EFE4CA504E6DBABC9C795', X'DC827FCBB9647D41BB8B18E8163ECAFD', X'DC827FCBB9647D41BB8B18E8163ECAFD', 2, NULL, NULL, 2, NULL, 0, 'Yes, you can use something besides beer if you like and it will still be super juicy and flavorful. Some things that work well are:');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F44B8ACDE4B83A42A59D2E2762A31BEA', X'DC827FCBB9647D41BB8B18E8163ECAFD', X'DC827FCBB9647D41BB8B18E8163ECAFD', 3, NULL, NULL, 2, NULL, 0, 'Cherry Dr Pepper, Dr Pepper, Root beer, fruit nectar, apple cider, sparkling grape juice, buttermilk, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D618A16355BC4DA9BF1D219B8A0889', X'DC827FCBB9647D41BB8B18E8163ECAFD', X'DC827FCBB9647D41BB8B18E8163ECAFD', 4, NULL, NULL, 2, NULL, 0, 'If you are not able to smoke at the higher temperature I recommended, it will take longer for the chicken to get done. At normal smoking temperatures of 225-240°F, the chicken pieces will take around 2 hours to reach 165°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48E251C5C75AA546BFAA7B314C9986A1', X'4B1DB4DE2F0024408E637F4B33DFC0D7', X'4B1DB4DE2F0024408E637F4B33DFC0D7', 0, NULL, NULL, 2, NULL, 0, 'Place ground chuck (80/20 or 85/15) into mixing bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C816513EE69E794EBEE3A60594CB683D', X'4B1DB4DE2F0024408E637F4B33DFC0D7', X'4B1DB4DE2F0024408E637F4B33DFC0D7', 1, NULL, NULL, 2, NULL, 0, 'Add ¼ cup of Jeff’s Texas style rub recipe (purchase recipes here).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05FDB26123631D43961369D2B9AF16D3', X'4B1DB4DE2F0024408E637F4B33DFC0D7', X'4B1DB4DE2F0024408E637F4B33DFC0D7', 2, NULL, NULL, 2, NULL, 0, 'Combine but don’t overwork the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE74A31E4AB57B41B5559A2BDA8A12D7', X'B33E391950C4AF44AA99A48AEE36AB60', X'B33E391950C4AF44AA99A48AEE36AB60', 0, NULL, NULL, 2, NULL, 0, 'Use a scale if you have one or just estimate to divide meat into 8 oz (½ lb) balls.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1726C4E4C9C66D4994588A80922DC858', X'B33E391950C4AF44AA99A48AEE36AB60', X'B33E391950C4AF44AA99A48AEE36AB60', 1, NULL, NULL, 2, NULL, 0, 'Form the balls with your hands but it doesn’t have to be perfect.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4DFCC00875498459BCF445B198A3CC0', X'8573A365D1143E4C89FE1E80A9843672', X'8573A365D1143E4C89FE1E80A9843672', 0, NULL, NULL, 2, NULL, 0, 'Use a cleaned, unopened 12 oz beverage (beer, soda, etc.)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'161D57CE5D9B814E8B61F17DC8846565', X'8573A365D1143E4C89FE1E80A9843672', X'8573A365D1143E4C89FE1E80A9843672', 1, NULL, NULL, 2, NULL, 0, 'Press the can into the center of the ball of meat to form the pocket.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'284114A737C5DE4EA454BB8F6FF50A6D', X'8573A365D1143E4C89FE1E80A9843672', X'8573A365D1143E4C89FE1E80A9843672', 2, NULL, NULL, 2, NULL, 0, 'The meat will break a little around the edges. Use your hands to form the meat around the can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'882E84D72C8B8F4CBC753D17CE2FBDC5', X'6E0E30878A1B6B468743A445C2C9D54F', X'6E0E30878A1B6B468743A445C2C9D54F', 0, NULL, NULL, 2, NULL, 0, 'Wrap a thin slice of bacon around the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10DF06B03564834CBE47BD1C0223448E', X'6E0E30878A1B6B468743A445C2C9D54F', X'6E0E30878A1B6B468743A445C2C9D54F', 1, NULL, NULL, 2, NULL, 0, 'Hold the meat with one hand to maintain it’s shape while you remove the can gently.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C1E1D1E0CB53E45BA7B6C98B13C0033', X'A184D8AF49635145820D983E7E6914FB', X'A184D8AF49635145820D983E7E6914FB', 0, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of Jeff’s Original rub recipe into the pocket for great flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04E8F883E4E8974C8D87D885C3B9646E', X'A184D8AF49635145820D983E7E6914FB', X'A184D8AF49635145820D983E7E6914FB', 1, NULL, NULL, 2, NULL, 0, 'Stuff with pulled pork..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26DD0BB69C69B04F89F7010E70D09A8C', X'A184D8AF49635145820D983E7E6914FB', X'A184D8AF49635145820D983E7E6914FB', 2, NULL, NULL, 2, NULL, 0, 'Add more of my original rub to the top of the pulled pork..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01A43C046006E2479270BEB8404EE885', X'A184D8AF49635145820D983E7E6914FB', X'A184D8AF49635145820D983E7E6914FB', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle grated cheese on top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7019A704F735149AB553DB54E82E8E7', X'A184D8AF49635145820D983E7E6914FB', X'A184D8AF49635145820D983E7E6914FB', 4, NULL, NULL, 2, NULL, 0, 'I used sharp cheddar but you can use a different kind if you prefer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'335F325ADD6F1647A8CBF106091BC3EF', X'A184D8AF49635145820D983E7E6914FB', X'A184D8AF49635145820D983E7E6914FB', 5, NULL, NULL, 2, NULL, 0, 'The burgers are now stuffed and ready to smoke!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1732F8753CD1C241BD38499DCF8C4FE0', X'33821A192F038F46A151182702E159C0', X'33821A192F038F46A151182702E159C0', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking with indirect heat at 225-240°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF7A7086D5041C42B3AD0D572F5DAF14', X'33821A192F038F46A151182702E159C0', X'33821A192F038F46A151182702E159C0', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A391E7D43D776E448902939EACA77780', X'33821A192F038F46A151182702E159C0', X'33821A192F038F46A151182702E159C0', 2, NULL, NULL, 2, NULL, 0, 'I like to use mesquite for these and especially to get a good smoked flavor in a relatively short period of time. Use your own favorite smoking wood if desired.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E11D91F4B008E48B0FC9E509AE80B95', X'33821A192F038F46A151182702E159C0', X'33821A192F038F46A151182702E159C0', 3, NULL, NULL, 2, NULL, 0, 'Place the burgers on the grate and allow them to smoke cook for about 1.5 hours or until the burger reaches 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'747B30230CAB054D9D6B62220181ACFD', X'33821A192F038F46A151182702E159C0', X'33821A192F038F46A151182702E159C0', 4, NULL, NULL, 2, NULL, 0, 'About 25-30 minutes before they are finished, brush with Jeff’s sauce (purchase recipes here) if desired.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8019F2BE64DCB4ABB1F12F942503B5C', X'25AB41A54C0A7A45BE9A2F67D4AA2DA6', X'25AB41A54C0A7A45BE9A2F67D4AA2DA6', 0, NULL, NULL, 2, NULL, 0, 'When the burgers are finished cooking, remove them from the smoker grate and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F19441D0BFC6484292A5ABFABBF320BC', X'25AB41A54C0A7A45BE9A2F67D4AA2DA6', X'25AB41A54C0A7A45BE9A2F67D4AA2DA6', 1, NULL, NULL, 2, NULL, 0, 'They can be eaten as an entree or placed between buns with condiments, tomato, onions, lettuce, etc for one of the best hamburgers you’ve ever eaten.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5B973578449384B8FF82F3897C1AB13', X'086F1D66B8B88840950D31742BAFC8A7', X'086F1D66B8B88840950D31742BAFC8A7', 0, NULL, NULL, 2, NULL, 0, 'Remove the pork butt from the packaging and rinse it under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D164E234B80074D85D4FC6A8C98EE42', X'086F1D66B8B88840950D31742BAFC8A7', X'086F1D66B8B88840950D31742BAFC8A7', 1, NULL, NULL, 2, NULL, 0, 'Pat dry with a paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'635955B2C944714D9B08D6D50253AB92', X'086F1D66B8B88840950D31742BAFC8A7', X'086F1D66B8B88840950D31742BAFC8A7', 2, NULL, NULL, 2, NULL, 0, 'I recommend placing it in a pan during the seasoning process to make cleanup really easy.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8A6ED83EC2C4C4D94F60566689B70BA', X'FDC00C3E89AE0B46B92461A4970A58D6', X'FDC00C3E89AE0B46B92461A4970A58D6', 0, NULL, NULL, 2, NULL, 0, 'Normally I use yellow mustard to help the rub to stick since it works so well however, you can use almost anything and to prove that, I am using zesty Italian dressing on this pork butt and it works like a charm.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEEF5A6CB61F674CA7290407D259D15D', X'FDC00C3E89AE0B46B92461A4970A58D6', X'FDC00C3E89AE0B46B92461A4970A58D6', 1, NULL, NULL, 2, NULL, 0, 'Pour the dressing over the top of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9AF9598C16DAA45AEB0B305846B06F8', X'FDC00C3E89AE0B46B92461A4970A58D6', X'FDC00C3E89AE0B46B92461A4970A58D6', 2, NULL, NULL, 2, NULL, 0, 'Use a basting brush to make sure the meat is coated well with the dressing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7350033201172049A382FACAEFFDB92C', X'BC1EDE92184EA94FA89517E01D830463', X'BC1EDE92184EA94FA89517E01D830463', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle my Jeff’s naked rib rub onto all sides of the meat making sure you have good coverage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17EEC84EB8278B4BB74C984BE26E6B1C', X'BC1EDE92184EA94FA89517E01D830463', X'BC1EDE92184EA94FA89517E01D830463', 1, NULL, NULL, 2, NULL, 0, 'Be generous to create a nice crispy bark on the outside during the cooking process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87153AEF76C1C14A938DF1ACEE58BE6A', X'BC1EDE92184EA94FA89517E01D830463', X'BC1EDE92184EA94FA89517E01D830463', 2, NULL, NULL, 2, NULL, 0, 'It is now ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2CDB7BC2180DE42BFB35D92D0B4E62B', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for indirect heat maintaining about 225-240°F for about 14-16 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF7E028A1360DA488797B9B620330674', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 1, NULL, NULL, 2, NULL, 0, 'When you are ready, place the pork butt directly on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66EBA4905FA24C4D99053BE267C8D5A6', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 2, NULL, NULL, 2, NULL, 0, 'I like to place a pan on a lower rack if I can to catch the juices that drip down. If your smoker is configured to allow for this, it’s a great idea.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3471B539068F7E46B6628F7024FCF692', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 3, NULL, NULL, 2, NULL, 0, 'I used apple wood to impart a really wonderful flavor into the pork but you can use almost any smoking wood that you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E5463B5D19EFB46B569C6ECE748CFC8', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 4, NULL, NULL, 2, NULL, 0, 'I recommend keeping the smoke flowing for at least 6 to 8 hours if you are using charcoal, electric or gas smokers.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F8921F53EEEDE44B3807C02658D042D', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 5, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'646FE0E1884E5340BC1A015FDEA4687F', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 6, NULL, NULL, 2, NULL, 0, 'Let the pork butt smoke out in the open grate for the entire time or you can place it in a pan once it reaches about 160°F covered with foil to help it get done a little faster.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C749D4EC4182B4D8BBD9B7C3B355425', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 7, NULL, NULL, 2, NULL, 0, 'I opted to not cover this one but left it directly on the grate with a pan under it for the entire time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A87A46DE207C34DAB73238DD3332C75', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 8, NULL, NULL, 2, NULL, 0, 'When it reaches 205°F, it should be fall apart tender and can be removed from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AAC1322EFA6C642ABC31F504F300A62', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 9, NULL, NULL, 2, NULL, 0, 'Those not familiar with pork butts always wonder why it’s so dark. The rub gets darker and darker as it cooks and this is what you usually want when you are making pulled pork.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C264E4318ACE734DB63F234A0E1A5B29', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 10, NULL, NULL, 2, NULL, 0, 'If you wrap the meat at 160°F it will not end up quite as dark however it will also be soft on the outside rather than crispy.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3649DE1AC8A6E24EBDC4DC86AB1748AB', X'84C71F600F97F0459D6183414244E74A', X'84C71F600F97F0459D6183414244E74A', 11, NULL, NULL, 2, NULL, 0, 'Let it sit with foil tented over it for about an hour to cool before attempting to pull/shred the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94AD0C52F344A247A8FF6797731228B3', X'28C4ED216544594F952132367B704B4A', X'28C4ED216544594F952132367B704B4A', 0, NULL, NULL, 2, NULL, 0, 'When you are ready, slide the bone out of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2790EA853BD6D46AB1090CE4EED3E9F', X'28C4ED216544594F952132367B704B4A', X'28C4ED216544594F952132367B704B4A', 1, NULL, NULL, 2, NULL, 0, 'Separate the meat into pieces removing any pieces of fat as you go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B93C0AAC484E64DB53291B7EA81E14E', X'28C4ED216544594F952132367B704B4A', X'28C4ED216544594F952132367B704B4A', 2, NULL, NULL, 2, NULL, 0, 'I generally use my hands only but some folks like to use large forks or special tools for this process such as bear paws.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EFD3A59FC991A4F984F92D4C1EA6B93', X'1C7677567A1B8641A168994D0983F7AE', X'1C7677567A1B8641A168994D0983F7AE', 0, NULL, NULL, 2, NULL, 0, 'You can serve the pulled pork piled high on a bun as a traditional pulled pork sandwich or use your imagination and let it run wild.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D1C5D1B51DD5F42B16339B6B78CCE5C', X'33ECE8685B97164B8CEA0D0674A8DA5F', X'33ECE8685B97164B8CEA0D0674A8DA5F', 0, NULL, NULL, 2, NULL, 0, 'While holding the sausage, hot link, hotdog, etc., run the skewer lengthwise through the center stopping just before it comes out the other end.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BEE146C270F8247A6678830494B30C0', X'F5ECEEE6D4094547BD9E04B918C795BD', X'F5ECEEE6D4094547BD9E04B918C795BD', 0, NULL, NULL, 2, NULL, 0, 'Put some mustard and rub into (2) separate plates');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E2A5BB89BD99341B8A982E8EBC9B021', X'F5ECEEE6D4094547BD9E04B918C795BD', X'F5ECEEE6D4094547BD9E04B918C795BD', 1, NULL, NULL, 2, NULL, 0, 'Roll the hot links in mustard');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9276A65B65EC15408A13E6E12BA57572', X'F5ECEEE6D4094547BD9E04B918C795BD', X'F5ECEEE6D4094547BD9E04B918C795BD', 2, NULL, NULL, 2, NULL, 0, 'Now roll the hot links in the rub making sure to coat well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9896DCA8C32A5249B01EE19F8C1FB055', X'36BE73D3A3F4F445AAB5887981B4C047', X'36BE73D3A3F4F445AAB5887981B4C047', 0, NULL, NULL, 2, NULL, 0, 'Lay a strip of bacon onto the work space then place the rub coated hot link perpendicular onto the bacon about 2 inches from one of the ends.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'032CBD907D6BC240AD45B1F7E7860034', X'36BE73D3A3F4F445AAB5887981B4C047', X'36BE73D3A3F4F445AAB5887981B4C047', 1, NULL, NULL, 2, NULL, 0, 'Flip the end of the bacon over the top of the hot link to get it started..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8910C8B3BFF8DB46A9397117CE19E132', X'36BE73D3A3F4F445AAB5887981B4C047', X'36BE73D3A3F4F445AAB5887981B4C047', 2, NULL, NULL, 2, NULL, 0, 'Roll the hot link to wrap the bacon around itself. Allow the bacon to overlap a bit with each round and keep it tight while it wraps.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269877275ADCD14CA0A7172BDC8913B9', X'36BE73D3A3F4F445AAB5887981B4C047', X'36BE73D3A3F4F445AAB5887981B4C047', 3, NULL, NULL, 2, NULL, 0, 'Perform the same task on all of the hot links, sausages, brats, or hotdogs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E414EE3CBE711E4282806881E1A4FA39', X'7677CB0B846A894CB841CC9D8D1985D3', X'7677CB0B846A894CB841CC9D8D1985D3', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking with indirect heat at about 240°F for 2-3 hours. I used cherry wood for smoke which is really great on these but you can use your favorite wood or whatever smoking wood you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D97901AF21F4DC468185319469938656', X'7677CB0B846A894CB841CC9D8D1985D3', X'7677CB0B846A894CB841CC9D8D1985D3', 1, NULL, NULL, 2, NULL, 0, 'Most things will be perfectly smoked in about 2 hours but we want the bacon to crisp up as well. Leaving it in the smoker for an extra hour will help or you can place them on a hot grill for a few minutes after a 2-hour smoking process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A603EB6D91E194AB9879D7B9CEE1DD3', X'7677CB0B846A894CB841CC9D8D1985D3', X'7677CB0B846A894CB841CC9D8D1985D3', 2, NULL, NULL, 2, NULL, 0, 'About 30 minutes before they are finished, paint them generously with my barbecue sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79FCD3546F8775419DF5446C50A954E5', X'7677CB0B846A894CB841CC9D8D1985D3', X'7677CB0B846A894CB841CC9D8D1985D3', 3, NULL, NULL, 2, NULL, 0, 'When the bacon is firm to crisp, remove the smoked bacon wrapped hot links from the smoker and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40C069AC2205F84EB307C32EA92A3312', X'F4741B2D0B9E194E9BA38E299CE6E238', X'F4741B2D0B9E194E9BA38E299CE6E238', 0, NULL, NULL, 2, NULL, 0, 'Remove the country style ribs from the package and rinse them under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD1E2CE8B203194F8C2F2D5190CFB4A1', X'F4741B2D0B9E194E9BA38E299CE6E238', X'F4741B2D0B9E194E9BA38E299CE6E238', 1, NULL, NULL, 2, NULL, 0, 'Pat them dry with a paper towel and lay them on the cutting board or in a large pan to contain the mess you’ll make by seasoning them ?');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'043B379E4F425C48ABC4A08B6C1C7693', X'CE3530E0CA67454CA6683731AEE7419F', X'CE3530E0CA67454CA6683731AEE7419F', 0, NULL, NULL, 2, NULL, 0, 'It’s pork.. very good pork, I might add and as most of you know, that calls for a nice sticky base of yellow mustard to help the rub to stick to the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D9A8674F0ACCB45B1D885B8D68733B1', X'CE3530E0CA67454CA6683731AEE7419F', X'CE3530E0CA67454CA6683731AEE7419F', 1, NULL, NULL, 2, NULL, 0, 'Go ahead and be generous.. it won’t taste like mustard and it won’t be yellow when it’s all said and done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0422FB987C67284BA25B07F2406C6EEB', X'CE3530E0CA67454CA6683731AEE7419F', X'CE3530E0CA67454CA6683731AEE7419F', 2, NULL, NULL, 2, NULL, 0, 'Rub it all over with you hands or if you’re squeamish, I suppose you could use a basting brush.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'878F5BA8173E964DB32C72F19F67307E', X'CE3530E0CA67454CA6683731AEE7419F', X'CE3530E0CA67454CA6683731AEE7419F', 3, NULL, NULL, 2, NULL, 0, 'As long as the mustard is completely covering the meat, doesn’t matter how it gets there (within reason of course).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6251D70323EE254FB4438CC236C9ACC0', X'CE3530E0CA67454CA6683731AEE7419F', X'CE3530E0CA67454CA6683731AEE7419F', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle my Jeff''s original rub (purchase recipes here) onto the meat and make sure you have full coverage on all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35CC7BB67D8EB64CB3926854973A758B', X'CE3530E0CA67454CA6683731AEE7419F', X'CE3530E0CA67454CA6683731AEE7419F', 5, NULL, NULL, 2, NULL, 0, 'I like to rub it in a little so it turns into a paste with the mustard.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA65ED5368E2040B14845F5B660DC7F', X'30D35152D2BA67488E14E434F9AF0576', X'30D35152D2BA67488E14E434F9AF0576', 0, NULL, NULL, 2, NULL, 0, 'If you have Bradley racks like I do, lay them on the rack with just a slight bit of space between each one to allow the smoke to get in there a little bit. Cooling racks also work well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54880A8DA80B7D4097ACA0CD56AD9C05', X'30D35152D2BA67488E14E434F9AF0576', X'30D35152D2BA67488E14E434F9AF0576', 1, NULL, NULL, 2, NULL, 0, 'I usually leave as much room as possible but I had other things to cook and so I had to give them a little less breathing room than usual.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'530301E2965F75459EB625B4F07F8413', X'0F78450548A28A4F9972A74C81DD50E4', X'0F78450548A28A4F9972A74C81DD50E4', 0, NULL, NULL, 2, NULL, 0, 'Light up your smoker whether that means actually lighting the fire or just plugging it in and dialing in some settings. Do what it takes to get your smoker going at about 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA4C11A1C7ED3943BB206B240EA90EDF', X'0F78450548A28A4F9972A74C81DD50E4', X'0F78450548A28A4F9972A74C81DD50E4', 1, NULL, NULL, 2, NULL, 0, 'Make sure the heat is indirect and use the water pan if your smoker has one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AFB4456B47D924AA30708E7FA9F3CEF', X'0F78450548A28A4F9972A74C81DD50E4', X'0F78450548A28A4F9972A74C81DD50E4', 2, NULL, NULL, 2, NULL, 0, 'I recommend a mix of mesquite and cherry wood for smoke if you can find it. If not, just use one of your other favorites that you have available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82AC9F7547B41140B3707E6573006251', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 0, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the pork country style ribs directly on the grate or use the Bradley rack (described above) directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E6B30A76B94AC41900D1D028E3CA29E', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 1, NULL, NULL, 2, NULL, 0, 'Keep the heat at about 225°F if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'646023F1A5C8354A93C9FA625193E616', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 2, NULL, NULL, 2, NULL, 0, 'Keep the smoke going for at least 2 hours but longer is fine and even recommended as long as the smoke is nice and thin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36AA0BBB02FB8045B3C9363B32B5F9D4', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 3, NULL, NULL, 2, NULL, 0, 'If you have a digital meat thermometer such as the Maverick ET-733 or the Maverick ET-735 (new bluetooth model), place the probe so that the end is about in the center of one of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E58B7E3DE7CCFF4BAC6ED1769133A97A', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 4, NULL, NULL, 2, NULL, 0, 'You can also use the super fast Thermapen to get a quick check on the temperature when you are adding wood or some other task that requires you to have the door open.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F1BD12E3BBD9649A9F125A3F4F89C05', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 5, NULL, NULL, 2, NULL, 0, 'You can expect these ribs to take somewhere between 3-4 hours if you maintain 225-240°F but other factors do play a part such as:');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C071DD30FEAC546959EF78A74CBFC42', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 6, NULL, NULL, 2, NULL, 0, 'Temperature of the meat when it goes into the smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C01ED6FA99B4D46A1B82F4D06860E29', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 7, NULL, NULL, 2, NULL, 0, 'How often you open the smoker door');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CD762BB38F69844B1A0151EB0EFDC01', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 8, NULL, NULL, 2, NULL, 0, 'How well you or the smoker maintains the set temperature');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'012686E6404E6E43B1922463DD0D1522', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 9, NULL, NULL, 2, NULL, 0, 'Wind, rain, ambient temperature, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'098135A725358E42A4C393B30AE03903', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 10, NULL, NULL, 2, NULL, 0, 'When the meat gets about 30 minutes away from being done, you can sauce them up if you want. I like sauce on these but they are also very good with just the dry rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E020E44A0A58149A2ABA83C10DB0366', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 11, NULL, NULL, 2, NULL, 0, 'Another option is to put them into a foil pan, covered with foil once they reach about 150-160°F to help them get done faster and to help them end up more tender.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B03DA0A87B9E340BD1D48C6885B3B5B', X'C832C306D159CC40A64D69FC914002E8', X'C832C306D159CC40A64D69FC914002E8', 12, NULL, NULL, 2, NULL, 0, 'They are done and tender at 180-185°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DB77BC4EECFFC40BBA2E9B0C530D2FE', X'EC7B06CE0E721A48B0E7C4479AFFCD66', X'EC7B06CE0E721A48B0E7C4479AFFCD66', 0, NULL, NULL, 2, NULL, 0, 'You do not have to let these rest for a few minutes but in my opinion, about 10 minutes of rest time before serving does them good.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859BED33461C5F47B32A873809C5907B', X'98CB8C7F86C0A84A98D702C1FC5EF0EF', X'98CB8C7F86C0A84A98D702C1FC5EF0EF', 0, NULL, NULL, 2, NULL, 0, 'Add 1 cup of salt to 1 gallon of cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F828C2BFAC3D5F49A5CD10B7AC8E9958', X'98CB8C7F86C0A84A98D702C1FC5EF0EF', X'98CB8C7F86C0A84A98D702C1FC5EF0EF', 1, NULL, NULL, 2, NULL, 0, 'Stir until the salt is dissolved');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11FBFFFF9F0C8D49A2382548F5AA1A17', X'98CB8C7F86C0A84A98D702C1FC5EF0EF', X'98CB8C7F86C0A84A98D702C1FC5EF0EF', 2, NULL, NULL, 2, NULL, 0, 'Add 1 cup of brown sugar and stir until sugar is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBE25D118299B04B8345E0E9F54E3130', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', 0, NULL, NULL, 2, NULL, 0, 'Rinse cornish hens under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A810EEE5F983AA4891B7F0045362D086', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', 1, NULL, NULL, 2, NULL, 0, 'Place birds in food safe container such as a bowl or zip top bag');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9043F1CEFF261A4D89360D0301EAE6A9', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', 2, NULL, NULL, 2, NULL, 0, 'Pour brine over birds to cover');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82AE459519F0F6439C6703E4436D36AB', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', 3, NULL, NULL, 2, NULL, 0, 'Place brining container with cornish hens into fridge for 1.5 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A6183C3F492B1438C4011B3C9C161B6', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', 4, NULL, NULL, 2, NULL, 0, 'When brining time is elapsed, rinse birds under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'270A6942FC8D344C94B93A88F8925770', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', X'3CD13CB50517FF4AA7DF2F8D7F4A4163', 5, NULL, NULL, 2, NULL, 0, 'Pat dry with paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9B5B1D1E8E6F14C800BF406E65E69A6', X'68D9B29251B92B46B635FA8382F73AE9', X'68D9B29251B92B46B635FA8382F73AE9', 0, NULL, NULL, 2, NULL, 0, 'Apply mayonnaise liberally to cornish hens');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCE80190313D254996114D70903D0F92', X'68D9B29251B92B46B635FA8382F73AE9', X'68D9B29251B92B46B635FA8382F73AE9', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle rub on top, bottom and sides of chickens');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC1AAA2132C9BD459ECABAEC65E6643E', X'70F3B15CE029E34F8057A8A2C635B88F', X'70F3B15CE029E34F8057A8A2C635B88F', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at 230 to 260°F. If you can maintain the higher temperature, the hens will only take about 1.5 hours. At the lower end, they will take about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D1283661CD1814FB359C655A23316C6', X'70F3B15CE029E34F8057A8A2C635B88F', X'70F3B15CE029E34F8057A8A2C635B88F', 1, NULL, NULL, 2, NULL, 0, 'Once smoker is ready, place birds on smoker grate breast side down');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8122CBA2611D044E8AB575F0D01AC21D', X'70F3B15CE029E34F8057A8A2C635B88F', X'70F3B15CE029E34F8057A8A2C635B88F', 2, NULL, NULL, 2, NULL, 0, 'Continue cooking until they reach 165°F as read by a digital meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F83632B8761F294F8A689D5C6AC94283', X'F7A240CC7864EC438B5AF646DBF0A022', X'F7A240CC7864EC438B5AF646DBF0A022', 0, NULL, NULL, 2, NULL, 0, 'Remove the chickens from the grate when they are done and place one cornish hen on each person''s plate with sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'069F9D74370A934E836E88CC969F5C3F', X'456C007F60E70B41AD6F4AF52E6FDA96', X'456C007F60E70B41AD6F4AF52E6FDA96', 0, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, remove the fat cap from the roast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C727E108E5E59C418AF96617A4C1F167', X'5A2C50FCD5A993408D1777F2B178AD1E', X'5A2C50FCD5A993408D1777F2B178AD1E', 0, NULL, NULL, 2, NULL, 0, 'Rub olive or vegetable oil all over the top, bottom and sides of the tri-tip roast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5A427CF91DC1F45AF6946249C7F1667', X'5A2C50FCD5A993408D1777F2B178AD1E', X'5A2C50FCD5A993408D1777F2B178AD1E', 1, NULL, NULL, 2, NULL, 0, 'Apply Jeff''s Texas style rub generously all over the roast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6159349946ECF4FBCE4BC5398DEEF75', X'538589B5EEF0A74A9E6BE64C25872135', X'538589B5EEF0A74A9E6BE64C25872135', 0, NULL, NULL, 2, NULL, 0, 'Place the ti-tip directly on the grate of the smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DEC75F52006B64CA9FE640636F60C72', X'538589B5EEF0A74A9E6BE64C25872135', X'538589B5EEF0A74A9E6BE64C25872135', 1, NULL, NULL, 2, NULL, 0, 'Using indirect heat, smoke cook the roast for about 2 hours or until it reaches medium rare (140°F)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C96749C80E11D4EA50F27A7D07F5FFA', X'538589B5EEF0A74A9E6BE64C25872135', X'538589B5EEF0A74A9E6BE64C25872135', 2, NULL, NULL, 2, NULL, 0, 'Remove from smoker and allow trip tip to rest under foil for about 20 minutes once it has finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE1585166A52B459A5FC08A4A78A4EE', X'3B8EFF10D471A445AC59281EAD98385B', X'3B8EFF10D471A445AC59281EAD98385B', 0, NULL, NULL, 2, NULL, 0, 'Slice the smoked tri-tip roast thin for sandwiches or into ¼ inch slices and then strips for placing atop a salad.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'739D741BC499A54CB94B79495920D023', X'A7A6E43843C725498997BFC76A8310DE', X'A7A6E43843C725498997BFC76A8310DE', 0, NULL, NULL, 2, NULL, 0, 'Remove the ribs from the package');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C414CE174C5B0849A332F82B6896BCC7', X'A7A6E43843C725498997BFC76A8310DE', X'A7A6E43843C725498997BFC76A8310DE', 1, NULL, NULL, 2, NULL, 0, 'Rinse under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'534B86874542CF4AA09FADFA1B00B3C8', X'A7A6E43843C725498997BFC76A8310DE', X'A7A6E43843C725498997BFC76A8310DE', 2, NULL, NULL, 2, NULL, 0, 'Allow to drain on paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE3DFC233052DA48832E9559E0BFCBAE', X'08AD17E0534A594FAEE1997421B5F315', X'08AD17E0534A594FAEE1997421B5F315', 0, NULL, NULL, 2, NULL, 0, 'Remove skirt, brisket bone, and end piece to square them up and create the St. Louis style spare ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E165CFAB5CBA47499873E0496460CC37', X'931F8CB9B9F52F47995DABB54103FFB7', X'931F8CB9B9F52F47995DABB54103FFB7', 0, NULL, NULL, 2, NULL, 0, 'Apply mustard to bone side of ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B00D12EE3F4C7041B33E9F56E3DF5C2D', X'931F8CB9B9F52F47995DABB54103FFB7', X'931F8CB9B9F52F47995DABB54103FFB7', 1, NULL, NULL, 2, NULL, 0, 'Pour on about ¼ cup of rib rub and spread to cover over meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B93D67203193F141B66653846A59121C', X'931F8CB9B9F52F47995DABB54103FFB7', X'931F8CB9B9F52F47995DABB54103FFB7', 2, NULL, NULL, 2, NULL, 0, 'Repeat mustard and rub on meaty side of ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7706C699A3DBE74E9C8D7FFD0CE0A4A3', X'E86794BDEBE7A5459EC79F5015DC2C0C', X'E86794BDEBE7A5459EC79F5015DC2C0C', 0, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at 230 °F using indirect heat with hickory or other favorite smoking wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DEBB33FAB09DC4B8A0FF9F3D6B3F52F', X'E86794BDEBE7A5459EC79F5015DC2C0C', X'E86794BDEBE7A5459EC79F5015DC2C0C', 1, NULL, NULL, 2, NULL, 0, 'Place ribs directly on smoker grate bone side down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'979935847645AF48A9CDFB6DDB7B4E47', X'E86794BDEBE7A5459EC79F5015DC2C0C', X'E86794BDEBE7A5459EC79F5015DC2C0C', 2, NULL, NULL, 2, NULL, 0, 'Smoke for 3 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4280DEC3C7D6246A7D733B72780707E', X'E86794BDEBE7A5459EC79F5015DC2C0C', X'E86794BDEBE7A5459EC79F5015DC2C0C', 3, NULL, NULL, 2, NULL, 0, 'After 3 hours, wrap in foil and place back into smoker for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'477299DD42502C4C9AD88DF7BB9B6F18', X'E86794BDEBE7A5459EC79F5015DC2C0C', X'E86794BDEBE7A5459EC79F5015DC2C0C', 4, NULL, NULL, 2, NULL, 0, 'After 2 hours, unwrap and place back into smoker for a final hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FAD1B3704EA2D47A017EA0331D9B367', X'E86794BDEBE7A5459EC79F5015DC2C0C', X'E86794BDEBE7A5459EC79F5015DC2C0C', 5, NULL, NULL, 2, NULL, 0, 'Brush on sauce if desired during last hour of cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDB450CA44CC424883A8ABD23042C376', X'075B60EEB0DF724EABE0B4D2B53E4D5F', X'075B60EEB0DF724EABE0B4D2B53E4D5F', 0, NULL, NULL, 2, NULL, 0, 'Slice the ribs between the bones and serve with warm sauce on the side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13A4BB78ECB67742AFBAA30D9972CF73', X'B3BC17FE54A2DB4ABD3862DC3667551D', X'B3BC17FE54A2DB4ABD3862DC3667551D', 0, NULL, NULL, 2, NULL, 0, 'Lay the thighs out on the cutting board and remove any visible fat with a sharp knife or kitchen scissors.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0D3B374888FEE468373E98722F66CB6', X'B7A8403B916EED48AA104D0AB28896F8', X'B7A8403B916EED48AA104D0AB28896F8', 0, NULL, NULL, 2, NULL, 0, 'I tried to cut the chicken into pieces that are bite size but that is a very subjective term. Whatever you visualize as bite size, cut them that size. I got about 6-8 bites from each thigh.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F73F21A02839544AA81CF9C19DB0B3B', X'9DF39244A0750646AD2DA497ED095A70', X'9DF39244A0750646AD2DA497ED095A70', 0, NULL, NULL, 2, NULL, 0, 'Put the chicken pieces into a zip top bag and pour about ¼ to ½ cup of olive or vegetable oil over the top of them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB405DE41CD010419847CE167F400952', X'9DF39244A0750646AD2DA497ED095A70', X'9DF39244A0750646AD2DA497ED095A70', 1, NULL, NULL, 2, NULL, 0, 'Massage the bag to make sure all of the chicken is coated with the oil as this helps the rub to stick to the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68E97CBE649D5D41B3F33D7CA9B4D498', X'9DF39244A0750646AD2DA497ED095A70', X'9DF39244A0750646AD2DA497ED095A70', 2, NULL, NULL, 2, NULL, 0, 'Add ½ cup of Jeff’s rub to the bag and, once again, massage the bag really well to coat all of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2648CCA37E45A449AAEAFD16A484C656', X'9DF39244A0750646AD2DA497ED095A70', X'9DF39244A0750646AD2DA497ED095A70', 3, NULL, NULL, 2, NULL, 0, 'Place the bag of rubbed chicken in the fridge for at least an hour but overnight would not hurt anything.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EB3A7D0E9B08C42A3936B4A7D2A5069', X'651242BAEDD7BF47AD8CBEA1630B1DD8', X'651242BAEDD7BF47AD8CBEA1630B1DD8', 0, NULL, NULL, 2, NULL, 0, 'When the chicken pieces are finished marinating in the rub, pull them out of the fridge. They will smell really good but avoid eating them raw and definitely do not lick your fingers');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BD8E484BA8A74489096CC922B966A67', X'651242BAEDD7BF47AD8CBEA1630B1DD8', X'651242BAEDD7BF47AD8CBEA1630B1DD8', 1, NULL, NULL, 2, NULL, 0, 'I don’t like to be stingy with the bacon so I cut mine in half. You may be able to cut it into thirds if you wish as long as it will fit around your bite size pieces of chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0254444F0238654993F00D6181004D62', X'651242BAEDD7BF47AD8CBEA1630B1DD8', X'651242BAEDD7BF47AD8CBEA1630B1DD8', 2, NULL, NULL, 2, NULL, 0, 'Lay a piece of the chicken on a piece of the bacon and roll ‘er up!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AB42141F5DBC7459249A7879E381905', X'651242BAEDD7BF47AD8CBEA1630B1DD8', X'651242BAEDD7BF47AD8CBEA1630B1DD8', 3, NULL, NULL, 2, NULL, 0, 'Push a skewer through the bacon wrapped chicken piece..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'683081D6444BEC49B46A23CFF7C66ADB', X'651242BAEDD7BF47AD8CBEA1630B1DD8', X'651242BAEDD7BF47AD8CBEA1630B1DD8', 4, NULL, NULL, 2, NULL, 0, 'Do that about 5 more times on the same skewer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A8EDA4C1B27964082A05579DA5D0DF3', X'651242BAEDD7BF47AD8CBEA1630B1DD8', X'651242BAEDD7BF47AD8CBEA1630B1DD8', 5, NULL, NULL, 2, NULL, 0, 'Make as many skewers as you can with the chicken that you cut up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FAB3ECC6CC2B149A06CF0A63C39AE67', X'3738E8142907D441AC11B53738B72E06', X'3738E8142907D441AC11B53738B72E06', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 250°F if possible. Cooking these a little hotter will help the bacon to crisp up and chicken can definitely handle the higher heat just fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CCDE1995CF4DC42A6E4302E3B475AEF', X'3738E8142907D441AC11B53738B72E06', X'3738E8142907D441AC11B53738B72E06', 1, NULL, NULL, 2, NULL, 0, 'I used pecan just because it’s my favorite and it goes really well with a lot of things.. poultry is one of them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24B19571F84B5D44AC1B4389421A1E27', X'3738E8142907D441AC11B53738B72E06', X'3738E8142907D441AC11B53738B72E06', 2, NULL, NULL, 2, NULL, 0, 'As usual, to make it really simple, I loaded the finished skewers onto bradley racks. These racks are about 11 x 15 and if they will fit on your smoker grate, they are very, very handy to have.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBC636861C779849BC37C758AE0D221E', X'3738E8142907D441AC11B53738B72E06', X'3738E8142907D441AC11B53738B72E06', 3, NULL, NULL, 2, NULL, 0, 'Set the bradley rack on the smoker grate or you can place the chicken skewers directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19D3942FA5004341B69A12FFA97E862D', X'3738E8142907D441AC11B53738B72E06', X'3738E8142907D441AC11B53738B72E06', 4, NULL, NULL, 2, NULL, 0, 'Smoke cook at 250°F for about 1.5 hours, sauce them real good then let them go another 30 minutes before taking them out of the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92E6B09D17652445AE2B3393DD0C3915', X'3738E8142907D441AC11B53738B72E06', X'3738E8142907D441AC11B53738B72E06', 5, NULL, NULL, 2, NULL, 0, 'Use an instant read thermometer such as the Thermapen (reads in around 2-3 seconds) to check each piece and make sure it has safely reached 165°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C758F16748A9D4195E44D4ACCF33F82', X'7651B6157AD9BD49876D94DDE7DE0272', X'7651B6157AD9BD49876D94DDE7DE0272', 0, NULL, NULL, 2, NULL, 0, 'Pull them off the sticks to serve as appetizers or serve an entire skewer to each person as an entree with home fries or even some baked beans.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481650FBA401A54190E31ED198FB5EE2', X'151CE6788530164D9CA3CE86E17A2AC6', X'151CE6788530164D9CA3CE86E17A2AC6', 0, NULL, NULL, 2, NULL, 0, 'Lay the ham on a cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BAD41D74087494085AB941DCBDEA5E5', X'151CE6788530164D9CA3CE86E17A2AC6', X'151CE6788530164D9CA3CE86E17A2AC6', 1, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, score the meat about ½ inch deep along the horizontal and vertical axis of the ham. Perfection is not required here.. just a way for the rub and smoke to get a little more access.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C3DD3338A37314D8B81B44D7E14C0C0', X'151CE6788530164D9CA3CE86E17A2AC6', X'151CE6788530164D9CA3CE86E17A2AC6', 2, NULL, NULL, 2, NULL, 0, 'Note: Spiral cut hams will not need to be scored.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF3764BCE320924487658960438361DC', X'468D8044AD6C3B40920D3BB515CB4673', X'468D8044AD6C3B40920D3BB515CB4673', 0, NULL, NULL, 2, NULL, 0, 'Apply some spicy brown mustard to the ham to help the rub to stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F8DFCE8C0BB1B43BEB2253891B8632B', X'468D8044AD6C3B40920D3BB515CB4673', X'468D8044AD6C3B40920D3BB515CB4673', 1, NULL, NULL, 2, NULL, 0, 'Generously pour on some of Jeff’s original rub and massage it in. My rub with the spicy brown mustard creates this really nice crust that is, in my opinion, the best part of the ham. Don’t be skimpy with the rub;-)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'925205AA105AF14AA7722DCF38BAD4EA', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 0, NULL, NULL, 2, NULL, 0, 'In times past I have just said to smoke the ham for 3 hours and that works to get it up to a good eating temperature but I feel that the ham is actually better if you take it on up to about 140-145°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7CC680FAE4B734A917FCB5A20694FA0', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 1, NULL, NULL, 2, NULL, 0, 'Note: Smaller hams will take less time, larger hams will take more time. If you have limited time, consider smoking 2 smaller hams rather than 1 large one to speed up the process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5DEF8617DB1CF44ABF06AFA84161C33', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 2, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F and make sure that you have about 3-4 hours worth of good smoking wood. I used pecan but hickory or almost any fruit wood would also work really good on this.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D93BEF08144ED4EBCE33E47D3C6B565', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 3, NULL, NULL, 2, NULL, 0, 'Once the smoker is heated up and holding steady, place the ham on a Bradley rack and place the rack on the smoker grate or you can just place the ham directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E6D542A981CB24DAE0BB11D46692E40', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 4, NULL, NULL, 2, NULL, 0, 'Please note, you can use almost any type of ham including the spiral cut variety.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'495E43AD32C9304FBC1829C51E8B4223', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 5, NULL, NULL, 2, NULL, 0, 'Once the ham is about 1 hour from being done, baste it twice, 15 minutes between each basting, with my Dr. Pepper and Honey Barbecue Glaze');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E2A324A8845FF4E97FB5AED3FFC6853', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 6, NULL, NULL, 2, NULL, 0, 'To make the Dr. Pepper and Honey Barbecue Glaze: 1/3 cup of Jeff’s Barbecue Sauce, ½ cup of Dr. Pepper (Not diet), ¼ cup of Honey');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D097991E03A7014580AB6E38621AEC2D', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 7, NULL, NULL, 2, NULL, 0, 'Add all ingredients to a small bowl and mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67CA7CD41E7E4A4BAD4EF078AD86CA77', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', X'D0A1A5FBACA8D34DBBD7308AD9A246D5', 8, NULL, NULL, 2, NULL, 0, 'Apply to ham with a basting brush making sure to get it down into the score marks and all over the top and sides of the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE540B856B0CC741B6984146CB6A818B', X'9C3A0EF49315FC43B1FB19C26BE5300B', X'9C3A0EF49315FC43B1FB19C26BE5300B', 0, NULL, NULL, 2, NULL, 0, 'When the ham reaches 140 to 145°F in the center, pull it out of the smoker and set it on the cabinet for about 15 minutes to rest.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE88F273F043EF4D8A809ECB12DD0AAD', X'9C3A0EF49315FC43B1FB19C26BE5300B', X'9C3A0EF49315FC43B1FB19C26BE5300B', 1, NULL, NULL, 2, NULL, 0, 'Slice, serve and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9BB999C9B9EBA4CAC20BF60CF9A5165', X'E49EE3DB4EBD504ABE8123DA444D89C0', X'E49EE3DB4EBD504ABE8123DA444D89C0', 0, NULL, NULL, 2, NULL, 0, 'If you are doing sausage with bologna, you will want to have about 4 sausage links and about 2 lbs of bologna.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EBA95A984F5FA478F87B26D6DB90B11', X'E49EE3DB4EBD504ABE8123DA444D89C0', X'E49EE3DB4EBD504ABE8123DA444D89C0', 1, NULL, NULL, 2, NULL, 0, 'If using sausage only, you''ll want to use an extra 3-4 links.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8CC20088DAF79449054D0C977B2BB10', X'E49EE3DB4EBD504ABE8123DA444D89C0', X'E49EE3DB4EBD504ABE8123DA444D89C0', 2, NULL, NULL, 2, NULL, 0, 'Cut the sausage up into pieces that are about 1-2 inches long.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09F36B72E4B50A48AAF499F2D4B25808', X'E49EE3DB4EBD504ABE8123DA444D89C0', X'E49EE3DB4EBD504ABE8123DA444D89C0', 3, NULL, NULL, 2, NULL, 0, 'Cut the bologna up into chunks as well. Don''t forget to remove the wrapper');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C58204A0B7B00E4585FCBF321777E78C', X'E49EE3DB4EBD504ABE8123DA444D89C0', X'E49EE3DB4EBD504ABE8123DA444D89C0', 4, NULL, NULL, 2, NULL, 0, 'Remove the stem, core and seeds from the peppers and slice them up into strips as shown.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95A3BD7FB78F5D46BB10F8D2447F58A3', X'E49EE3DB4EBD504ABE8123DA444D89C0', X'E49EE3DB4EBD504ABE8123DA444D89C0', 5, NULL, NULL, 2, NULL, 0, 'Slice the onion into pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC12C141FF2E1C499DB9D54777ADA734', X'7BFCC3282CE3AE4095D8B689CFBA0645', X'7BFCC3282CE3AE4095D8B689CFBA0645', 0, NULL, NULL, 2, NULL, 0, 'Place the meat and vegetables into a large ziptop bag with ½ cup of oil and 1 cup of Jeff''s rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB46AE9AD61D384A8A13F14F605E599A', X'7BFCC3282CE3AE4095D8B689CFBA0645', X'7BFCC3282CE3AE4095D8B689CFBA0645', 1, NULL, NULL, 2, NULL, 0, 'Place the bag into the fridge overnight if possible to let all of the flavors meld together or at least for a couple of hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE3AA8EA9EC1B24C964AED3185A7DA74', X'7BFCC3282CE3AE4095D8B689CFBA0645', X'7BFCC3282CE3AE4095D8B689CFBA0645', 2, NULL, NULL, 2, NULL, 0, 'When the marinating is finished, pour the contents into a 9x13 or whatever container you are using.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'954D21298D423B4F99F5F1D4056780B2', X'3D531687C2B0354CA41C7E8009237612', X'3D531687C2B0354CA41C7E8009237612', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for indirect cooking at about 225°F for 2 hours or until the vegetables are sufficiently tender to your liking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0C33AF74171704E80B1775FF2225D87', X'3D531687C2B0354CA41C7E8009237612', X'3D531687C2B0354CA41C7E8009237612', 1, NULL, NULL, 2, NULL, 0, 'Apply smoke from cherry, apple or your favorite smoking wood for at least 1 hour or you can keep it going for the full 2 hours for great flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA2849BA8140D7449D15F3A08A14DF3F', X'3D531687C2B0354CA41C7E8009237612', X'3D531687C2B0354CA41C7E8009237612', 2, NULL, NULL, 2, NULL, 0, 'Stir the meat and vegetables 3-4 times during the process to make sure the smoke has access to everything.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F67C40134C92D4448F98AC4FE84905F4', X'3D531687C2B0354CA41C7E8009237612', X'3D531687C2B0354CA41C7E8009237612', 3, NULL, NULL, 2, NULL, 0, 'When the vegetables are tender to your liking and the meat has had time to get sufficiently hot and smokey, the dish is complete.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'908B76CDC4B7E245A4F526C4E711AC07', X'B3FBB806641A1F4980CC24259336125F', X'B3FBB806641A1F4980CC24259336125F', 0, NULL, NULL, 2, NULL, 0, 'Serve over rice or on top of buns');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'466F17F87FF2DD4993E04CEE6EF9A1DB', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 0, NULL, NULL, 2, NULL, 0, 'Rinse the corned beef brisket under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CB12099E098354C83AE6E6DF2C2054F', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 1, NULL, NULL, 2, NULL, 0, 'Lay the meat in a pan (recommended) so you can season it on all sides without making a huge mess.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'668ABC2AA3BDCA418DF5A4C8D58A175C', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 2, NULL, NULL, 2, NULL, 0, 'Brush vegetable or olive oil onto all sides of the meat to help the rub to stick');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6813EA66CC5E7418BC76F25EAD9D821', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 3, NULL, NULL, 2, NULL, 0, 'Apply a generous portion of my Texas style rub to all sides of the corned beef brisket');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79D452638367E942A13E1EB0D73146C3', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 4, NULL, NULL, 2, NULL, 0, 'My corned beef had a seasoning packet which included seeds of fennel, coriander, mustard and caraway along with allspice, cinnamon, pepper, dried chili and pieces of bay leaf. I decided to go ahead and use it on the very top of the beef to enhance the color and flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66352D59CE074A42932B266747E9FE11', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 5, NULL, NULL, 2, NULL, 0, 'If your’s does not have a seasoning packet, you can add on some common pickling spices (available in a small can where you purchase spices)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00B1EB5DC75988468F9AA41B9BD97E2A', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 6, NULL, NULL, 2, NULL, 0, 'You can season the night before if you wish or you can perform this task right before you are ready to smoke the corned beef. Your choice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F927EDEFCB6EB949B69BC72016E46C8D', X'3A263CCCCA0F93479B1C9FEA7A9D4171', X'3A263CCCCA0F93479B1C9FEA7A9D4171', 7, NULL, NULL, 2, NULL, 0, 'Once seasoned, the corned beef brisket is placed on a rack to make it very easy to transport the beef to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF1E9900BD3F6498B4332A6D5E59A7D', X'4070D3617948D34383E5A159C1BCF390', X'4070D3617948D34383E5A159C1BCF390', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking indirect at 225-240°F using cherry wood if you have it. Any there smoking wood will work fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08E8E99F80252F418856AE05B7463E5E', X'4070D3617948D34383E5A159C1BCF390', X'4070D3617948D34383E5A159C1BCF390', 1, NULL, NULL, 2, NULL, 0, 'If you have a water pan, use it. A little humidity in the smoker goes a long way toward helping the meat to not dry out as bad in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53DA9F3E44BB6644B85E277C65E82E64', X'4070D3617948D34383E5A159C1BCF390', X'4070D3617948D34383E5A159C1BCF390', 2, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready to go, place the corned beef brisket flat in the smoker on a rack or directly on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'889BE89678DFB247925829B16750D313', X'4070D3617948D34383E5A159C1BCF390', X'4070D3617948D34383E5A159C1BCF390', 3, NULL, NULL, 2, NULL, 0, 'Monitor the temperature of the brisket using a digital probe meat thermometer so you will know exactly when it’s done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A6F120AD428C947AE92B352F259CD87', X'4070D3617948D34383E5A159C1BCF390', X'4070D3617948D34383E5A159C1BCF390', 4, NULL, NULL, 2, NULL, 0, 'The meat is safe to eat when it reaches 160°F but is usually cooked to 190°F or higher to allow it to get more tender.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADB8FF3F1D9384478DF9BEC7787CF10D', X'4070D3617948D34383E5A159C1BCF390', X'4070D3617948D34383E5A159C1BCF390', 5, NULL, NULL, 2, NULL, 0, 'You can expect this to take from 6-10 hours depending on the thickness of the corned beef brisket that you are using. The thicker is is, the longer it will take to reach it’s done temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48C1411FDF2A784EB8FB955A5A886BB0', X'167AEAB3478C284DB09CD438CEAB74DC', X'167AEAB3478C284DB09CD438CEAB74DC', 0, NULL, NULL, 2, NULL, 0, 'When the meat is done cooking, it can be held for 3-4 hours (if needed) wrapped in foil until dinner time as long as it is kept at or above 140°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E9485046F976B4593B107756CA2E14E', X'167AEAB3478C284DB09CD438CEAB74DC', X'167AEAB3478C284DB09CD438CEAB74DC', 1, NULL, NULL, 2, NULL, 0, 'To serve, slice the meat up into ¼ inch pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E6CD2EB5E148C47A69D798621CB985E', X'919468DD7B91F3478F5E75C3A4FBC142', X'919468DD7B91F3478F5E75C3A4FBC142', 0, NULL, NULL, 2, NULL, 0, 'Place a single layer of the wings in a disposable pan');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D61C5E421A21FE4197EB6C62A6BBC982', X'919468DD7B91F3478F5E75C3A4FBC142', X'919468DD7B91F3478F5E75C3A4FBC142', 1, NULL, NULL, 2, NULL, 0, 'Drizzle with olive or vegetable oil and brush it on to coat all sides thoroughly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19312656A449AC468096EF89E9E5CD56', X'919468DD7B91F3478F5E75C3A4FBC142', X'919468DD7B91F3478F5E75C3A4FBC142', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle a generous amount of Jeff’s original rub onto the wings then flip them over and apply rub to the other side as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F37CCB631D6824E91DB4E3714740C62', X'919468DD7B91F3478F5E75C3A4FBC142', X'919468DD7B91F3478F5E75C3A4FBC142', 3, NULL, NULL, 2, NULL, 0, 'Let the rub “settle in” and when it starts getting that wet look, place the wings in a ziptop bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DF30F43626DC643BBC6FC9C69075545', X'919468DD7B91F3478F5E75C3A4FBC142', X'919468DD7B91F3478F5E75C3A4FBC142', 4, NULL, NULL, 2, NULL, 0, 'Place them in the fridge for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0252727068B8D74AA96BEE703C4E82FF', X'78AA9A6596FCAB4B84BE10274A1F5FE1', X'78AA9A6596FCAB4B84BE10274A1F5FE1', 0, NULL, NULL, 2, NULL, 0, 'Make the brine: Pour the quart of buttermilk into a pitcher and add in ¼ cup of salt. Stir for about 20 seconds to make sure the salt is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EADFD2B61017A4F949381E088783705', X'78AA9A6596FCAB4B84BE10274A1F5FE1', X'78AA9A6596FCAB4B84BE10274A1F5FE1', 1, NULL, NULL, 2, NULL, 0, 'Pour the brine into the bag with the marinated chicken making sure that all of the chicken is submerged in the brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'717F0F62E955234697CD433E28620E5F', X'78AA9A6596FCAB4B84BE10274A1F5FE1', X'78AA9A6596FCAB4B84BE10274A1F5FE1', 2, NULL, NULL, 2, NULL, 0, 'Place the bag of chicken back into the fridge for another 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AD1F93FB510CE4F94E87B6593BF6F70', X'A94F62A0F0546943B9447892BBA8B020', X'A94F62A0F0546943B9447892BBA8B020', 0, NULL, NULL, 2, NULL, 0, 'After 2 hours of brining, remove the chicken wings from the fridge and pour them into a disposable aluminum pan for seasoning.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A46628AF597FB1438993A47E13B0CCAD', X'A94F62A0F0546943B9447892BBA8B020', X'A94F62A0F0546943B9447892BBA8B020', 1, NULL, NULL, 2, NULL, 0, 'The wings do NOT need to be rinsed off. The buttermilk and rub together adds a lot of flavor and because my rub is low on salt, the salt in the brining process is perfectly fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4187C3099C6BD0449E9A0C7075320C67', X'A94F62A0F0546943B9447892BBA8B020', X'A94F62A0F0546943B9447892BBA8B020', 2, NULL, NULL, 2, NULL, 0, 'Add more rub to the chicken wings by placing the wings in a single layer and reapplying the rub just like you did in the beginning.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32F2D580EFAADF45B8135ED8F66098BB', X'A94F62A0F0546943B9447892BBA8B020', X'A94F62A0F0546943B9447892BBA8B020', 3, NULL, NULL, 2, NULL, 0, 'Flip all of the wings over and rub the other side as well..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CE564BA994CE543A92E4A832EF793BF', X'A94F62A0F0546943B9447892BBA8B020', X'A94F62A0F0546943B9447892BBA8B020', 4, NULL, NULL, 2, NULL, 0, 'Leave the wings sitting for a few minutes while you get the smoker ready!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'884AEA7C926CCB4F8484D217F55CA761', X'5FE3BA863234184581A1CE44641845C8', X'5FE3BA863234184581A1CE44641845C8', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 250°F if possible. Lower temperatures will work if your smoker will not get up to 250°F as long as you know that it will take them a little longer to reach their finish temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFB9DCC94E84F24ABF1FEDD89DD7BF64', X'5FE3BA863234184581A1CE44641845C8', X'5FE3BA863234184581A1CE44641845C8', 1, NULL, NULL, 2, NULL, 0, 'I recommend using hickory on these or even mesquite if you want to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA5FB78D04D26E469F63FB123DE28F9C', X'5FE3BA863234184581A1CE44641845C8', X'5FE3BA863234184581A1CE44641845C8', 2, NULL, NULL, 2, NULL, 0, 'Place the chicken wings directly on the grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E472A0A6F6A944D9A3B8FD81405A65E', X'5FE3BA863234184581A1CE44641845C8', X'5FE3BA863234184581A1CE44641845C8', 3, NULL, NULL, 2, NULL, 0, 'You can expect the chicken wings to reach 165-170°F in about 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5FC9A49822EA14A98DE5636009D4DCF', X'5FE3BA863234184581A1CE44641845C8', X'5FE3BA863234184581A1CE44641845C8', 4, NULL, NULL, 2, NULL, 0, 'Use a good quality instant read thermometer such as the Thermapen to test the temperature and make sure all of them are perfectly and safely done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D79B9593587A544AB89842B271D7615', X'FE3DDF05E2BF5C4D9063A8720DC39613', X'FE3DDF05E2BF5C4D9063A8720DC39613', 0, NULL, NULL, 2, NULL, 0, 'Just before the wings are finished cooking, turn on the broiler in your oven so it’s ready to caramelize the sauce just as soon as they are finished in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1433FA9E1F33942B57F8F35B643C6ED', X'FE3DDF05E2BF5C4D9063A8720DC39613', X'FE3DDF05E2BF5C4D9063A8720DC39613', 1, NULL, NULL, 2, NULL, 0, 'You will also need to get the sauce ready by mixing 2 parts Jeff’s sauce with 1 part honey. To make a cup of the honey barbecue sauce, mix ⅔ cup of Jeff’s barbecue sauce with ⅓ cup of honey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81119A7F5E16584DA99B96FB9519C65B', X'FE3DDF05E2BF5C4D9063A8720DC39613', X'FE3DDF05E2BF5C4D9063A8720DC39613', 2, NULL, NULL, 2, NULL, 0, 'Bring the finished wings into the house and brush the sauce onto the top of the chicken wings as generously as you dare.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88796FCF86210A46899823CA4553BAEB', X'FE3DDF05E2BF5C4D9063A8720DC39613', X'FE3DDF05E2BF5C4D9063A8720DC39613', 3, NULL, NULL, 2, NULL, 0, 'Place them under the broiler for about a minute or until the sauce starts to bubble furiously (WATCH THEM CAREFULLY).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF89304753B18F4597152BC432DF4C9C', X'FE3DDF05E2BF5C4D9063A8720DC39613', X'FE3DDF05E2BF5C4D9063A8720DC39613', 4, NULL, NULL, 2, NULL, 0, 'Remove them, flip them over, sauce the bottom side and broil that side for about a minute once again.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCB0A7C720EE364C83EAB7D77A4FDDEA', X'58E591359469404182C72613F9BA83C9', X'58E591359469404182C72613F9BA83C9', 0, NULL, NULL, 2, NULL, 0, 'Holler, “done!” and watch them evaporate from before your eyes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C95FDC6C6294754B87BAEE63603D4F1B', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 0, NULL, NULL, 2, NULL, 0, 'Rinse the chuck roast under cold water and dry with a paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D4CC615E873724D846AAF2094151923', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 1, NULL, NULL, 2, NULL, 0, 'Make the Bourbon Beef Marinade: 2 TBS Jeff’s original rub, 1 cup beef broth, ¼ cup Bourbon, ¼ cup Worcestershire sauce, 2 TBS lemon juice');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13ED02A2DA82E64A8FFFB4491BDFAC37', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 2, NULL, NULL, 2, NULL, 0, 'Add all ingredients to a pint jar and mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAC658D781B8BF43A6CE3A3C98494A3D', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 3, NULL, NULL, 2, NULL, 0, 'Place the meat down in a large pan and cover the pan with stretch wrap. This prevents huge messes while injecting by keeping the marinade contained inside the enclosed area.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84CFCDE3E6055D4989FC4124096F9EDF', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 4, NULL, NULL, 2, NULL, 0, 'I recommend an injector with a large needle so the rub does not get stuck. Many injectors have very small openings in the needle and the coarse rub will clog easily.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43E4CBFE1C215D4698935448D41DCCFE', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 5, NULL, NULL, 2, NULL, 0, 'Stir the marinade before each fill of the injector.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29E52F3080681043A980573501F40E3F', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 6, NULL, NULL, 2, NULL, 0, 'Once the injector is full of marinade, press the end of the needle into the meat at a 45 degree angle and depress the plunger to push the marinade into the meat. You will see it plump up. Once it starts running out, it’s time to move to a new location.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'048C6FB842AF214487CBDA59BE2B9A03', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 7, NULL, NULL, 2, NULL, 0, 'I usually inject about every inch.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200FC0B0825BE843968E1E8820D58568', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 8, NULL, NULL, 2, NULL, 0, 'Use about half of the marinade inside the meat and then pour the rest over the top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F006D1E69BEB694E9D39BD2E20734BF6', X'F3023686DE68F348B3135E7EB7431566', X'F3023686DE68F348B3135E7EB7431566', 9, NULL, NULL, 2, NULL, 0, 'Cover the foil pan and place in the fridge for about 8 hours to marinate. Flip the meat over after 4 hours so both sides are able to sit in the marinade for a while.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E531054E302AB40B3600DDB679FD244', X'0C0B13EB583BB4468F00153FB9FC9D03', X'0C0B13EB583BB4468F00153FB9FC9D03', 0, NULL, NULL, 2, NULL, 0, 'After marinating the meat, the only thing left to do is to add my rub to the outside. This will create a nice crust and give you that perfectly seasoned bite you are looking for. (With the meat already wet from the marinade, there is no need to add mustard or oil to help the rub to stick.)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55DAEE1F4AB1B54493780B30A9EBE986', X'0C0B13EB583BB4468F00153FB9FC9D03', X'0C0B13EB583BB4468F00153FB9FC9D03', 1, NULL, NULL, 2, NULL, 0, 'If possible, place the chuck roast on a Bradley rack and sit that right on top of the pan. This allows the smoke to have full access to the meat and the juices from the roast can drip down into the pan keeping the smoker clean and giving us some nice, tasty meat juice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F42CADF79A43384BA31DB31DD09F8AF7', X'0C0B13EB583BB4468F00153FB9FC9D03', X'0C0B13EB583BB4468F00153FB9FC9D03', 2, NULL, NULL, 2, NULL, 0, 'My original rub is perfect on this chuck roast. Use the Texas rub once it’s finished being pulled/chopped if you need more saltiness.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDBE7B732D1E974499F1CBD942D4DF7F', X'4F5337BD8173CF48B5E9ECEAFA6B6950', X'4F5337BD8173CF48B5E9ECEAFA6B6950', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F with enough wood to last at least 4 hours or longer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A25F3DFE95CD5D4DA5C67E716BD2D108', X'4F5337BD8173CF48B5E9ECEAFA6B6950', X'4F5337BD8173CF48B5E9ECEAFA6B6950', 1, NULL, NULL, 2, NULL, 0, 'I used a mix of mesquite and apple but you can use another favorite if you desire.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34DC6D01AE654F469CB38E27B5FDD660', X'4F5337BD8173CF48B5E9ECEAFA6B6950', X'4F5337BD8173CF48B5E9ECEAFA6B6950', 2, NULL, NULL, 2, NULL, 0, 'Place the chuck roast directly on the grate or if you have the Bradley racks I mentioned earlier, you can use the same configuration as I did.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6402F22C52A3C74B9C9B381A95CEDE5A', X'4F5337BD8173CF48B5E9ECEAFA6B6950', X'4F5337BD8173CF48B5E9ECEAFA6B6950', 3, NULL, NULL, 2, NULL, 0, 'If you have a vertical smoker with multiple racks, you can also just place the pan on a lower grate to allow the juices to drip down into the pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA30AE9BD6494D4E8630B210C24319BD', X'B5B6E6FEE9549F43A3952F865AC61A28', X'B5B6E6FEE9549F43A3952F865AC61A28', 0, NULL, NULL, 2, NULL, 0, 'While the chuck roast smokes away, you can cut up your peppers and onions and get them ready to go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52A52BD23D6EBE46B66F801672E0CBD6', X'3A23E1C7DBC7E34EA40D4F36EF16879C', X'3A23E1C7DBC7E34EA40D4F36EF16879C', 0, NULL, NULL, 2, NULL, 0, 'When the roast reaches about 150°F, pour the vegetables into the drip pan and place the roast right on top');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C202533A9CE89D4CB6761D98CD6435B5', X'3A23E1C7DBC7E34EA40D4F36EF16879C', X'3A23E1C7DBC7E34EA40D4F36EF16879C', 1, NULL, NULL, 2, NULL, 0, 'If you need more juice, just add about a cup of beef broth.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02C75545DC9FDA478C901058F872A8D1', X'3A23E1C7DBC7E34EA40D4F36EF16879C', X'3A23E1C7DBC7E34EA40D4F36EF16879C', 2, NULL, NULL, 2, NULL, 0, 'Cover the top of the pan tightly with foil and place the pan back into the smoker to finish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFA8965893AAA741A81C2F5540CA4DEE', X'3A23E1C7DBC7E34EA40D4F36EF16879C', X'3A23E1C7DBC7E34EA40D4F36EF16879C', 3, NULL, NULL, 2, NULL, 0, 'Be sure to use a digital probe meat thermometer to keep tabs on the internal temperature of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E029E43E4F2803478B54E9386BE2DDE9', X'3A23E1C7DBC7E34EA40D4F36EF16879C', X'3A23E1C7DBC7E34EA40D4F36EF16879C', 4, NULL, NULL, 2, NULL, 0, 'When the roast reaches about 210°F, it is done cooking and is ready to be pulled/chopped and served.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9432AA3C83BA2E448DF825177C87E5A9', X'3A23E1C7DBC7E34EA40D4F36EF16879C', X'3A23E1C7DBC7E34EA40D4F36EF16879C', 5, NULL, NULL, 2, NULL, 0, 'I recommend letting it rest uncovered for about 20 minutes before processing it further.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F10B00696C3704AA7065809606F9D62', X'0FF98C01556C0D4F8CD7B91C05533109', X'0FF98C01556C0D4F8CD7B91C05533109', 0, NULL, NULL, 2, NULL, 0, 'The chuck roast can be served in any number of ways, similar to brisket. We chose to serve the meat and vegetables piled high on wheat hoagie buns topped with parmesan cheese and my Texas rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C878EE8DDDD1746AE7BF8ACE6A9922C', X'F1A378B4FDE7D04E860449D2E21FFF06', X'F1A378B4FDE7D04E860449D2E21FFF06', 0, NULL, NULL, 2, NULL, 0, 'Make enough brine to cover the number of fish you are smoking. Brine is easy and cheap to make so I usually just make a gallon unless I know for sure that I don’t need that much.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CB2C429B2FD614E91BC4278E7BBAF5B', X'F1A378B4FDE7D04E860449D2E21FFF06', X'F1A378B4FDE7D04E860449D2E21FFF06', 1, NULL, NULL, 2, NULL, 0, 'Make my basic brine recipe using 1 gallon of water, 1 cup of coarse kosher salt, 1 cup of brown sugar (Dark is best, light will work), ¼ cup of lemon juice (2 lemons squeezed, approx.)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'284A3C1A033FBD4BA13AB9FBE73B31F7', X'F1A378B4FDE7D04E860449D2E21FFF06', X'F1A378B4FDE7D04E860449D2E21FFF06', 2, NULL, NULL, 2, NULL, 0, 'Add the coarse kosher salt and lemon juice to the gallon of water in a large pitcher and mix until the salt is dissolved then add the brown sugar and mix thoroughly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'947F0B183D79334C8B9EF1EC3A7567A5', X'F1A378B4FDE7D04E860449D2E21FFF06', X'F1A378B4FDE7D04E860449D2E21FFF06', 3, NULL, NULL, 2, NULL, 0, 'Place the trout into a lidded bowl or a zip top bag');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C2591B31EA3A94585E70A6340BC35AE', X'F1A378B4FDE7D04E860449D2E21FFF06', X'F1A378B4FDE7D04E860449D2E21FFF06', 4, NULL, NULL, 2, NULL, 0, 'Pour enough brine over the fish to cover completely.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3241792C3C25D54EA120848F40FDF3BD', X'F1A378B4FDE7D04E860449D2E21FFF06', X'F1A378B4FDE7D04E860449D2E21FFF06', 5, NULL, NULL, 2, NULL, 0, 'Seal the bowl or bag and place it in the fridge for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'604C2A8559D95246AD129A7E24272106', X'F1A378B4FDE7D04E860449D2E21FFF06', X'F1A378B4FDE7D04E860449D2E21FFF06', 6, NULL, NULL, 2, NULL, 0, 'Once the fish are done brining, rinse them under cold water to remove any residual salt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52078D3113532C4C9CD1D6F3BAA2F452', X'FDDA6419C3695F4C8CCB7094309161D2', X'FDDA6419C3695F4C8CCB7094309161D2', 0, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto the entire inside flesh of the fish. This makes the rub stick to the fish really well and helps to keep the fish moist while it cooks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'871F43BA50AACF46B5D76FDFE4044C63', X'FDDA6419C3695F4C8CCB7094309161D2', X'FDDA6419C3695F4C8CCB7094309161D2', 1, NULL, NULL, 2, NULL, 0, 'Hold the trout open and sprinkle a good hefty portion of my Texas style rub into the inside of the fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FF56D114D174046847D3E987E8319D6', X'FDDA6419C3695F4C8CCB7094309161D2', X'FDDA6419C3695F4C8CCB7094309161D2', 2, NULL, NULL, 2, NULL, 0, 'Stuff the inside of the fish with equal portions of the oregano and chives.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18831D33D8EF2E49852717BC8349FEE2', X'FDDA6419C3695F4C8CCB7094309161D2', X'FDDA6419C3695F4C8CCB7094309161D2', 3, NULL, NULL, 2, NULL, 0, 'Place about 3 slices of lemon into each fish');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03D125AE78B7C745BDF8E46AFA9C3AD0', X'FDDA6419C3695F4C8CCB7094309161D2', X'FDDA6419C3695F4C8CCB7094309161D2', 4, NULL, NULL, 2, NULL, 0, 'The fish are now ready to smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AC13EB7DFE1E948865FCAEC644C6874', X'FAFDBFFBD2538F43B46387AA4263086B', X'FAFDBFFBD2538F43B46387AA4263086B', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker or grill with indirect heat maintaining 225°F and a light flavored wood such as alder or apple.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C74913E6C09F584EBC7031610009A57D', X'FAFDBFFBD2538F43B46387AA4263086B', X'FAFDBFFBD2538F43B46387AA4263086B', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready, place the fish directly on the oiled grate. Note: To make sure the trout do not stick to the rack or grate, you can place each fish on a piece of parchment paper cut to the basic footprint of the fish. Usually about 3 inches wide and 10-12 inches in length will work great for “good eating size” fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7774CDD6A8B9D41B59BCF4167832C06', X'FAFDBFFBD2538F43B46387AA4263086B', X'FAFDBFFBD2538F43B46387AA4263086B', 2, NULL, NULL, 2, NULL, 0, 'Cook the fish until it reaches an internal temperature of 145°F. At this point the meat of the fish will flake easily and it will be perfectly done. This will usually take about an hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AD428E629F04D498393935F1F9B0464', X'02807B67AD9A2240B0EB5FFEE710FED2', X'02807B67AD9A2240B0EB5FFEE710FED2', 0, NULL, NULL, 2, NULL, 0, 'Once the trout is smoked and ready, remove it from the smoker right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F739D52A4A50B45A9EA8FC59F071F49', X'02807B67AD9A2240B0EB5FFEE710FED2', X'02807B67AD9A2240B0EB5FFEE710FED2', 1, NULL, NULL, 2, NULL, 0, 'Serve the trout immediately for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1652EB53B1F864F82E7796BFE573CED', X'DAB9683787D99741B10F51CB3A68C1CC', X'DAB9683787D99741B10F51CB3A68C1CC', 0, NULL, NULL, 2, NULL, 0, 'Place the oysters in a colander and run cold water over them to remove grit and pieces of shell');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A58BC6DF749064487E9D5DF70717CC6', X'DAB9683787D99741B10F51CB3A68C1CC', X'DAB9683787D99741B10F51CB3A68C1CC', 1, NULL, NULL, 2, NULL, 0, 'Clean the shells while you are at it for good presentation');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3DD3A8E683FC3418BB8EF20983267E4', X'DAB9683787D99741B10F51CB3A68C1CC', X'DAB9683787D99741B10F51CB3A68C1CC', 2, NULL, NULL, 2, NULL, 0, 'Dry the oysters carefully with a paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB910487E3EA1439D0560807CDD0FF1', X'A825F90EB15C9D4A8141136CF58BB95C', X'A825F90EB15C9D4A8141136CF58BB95C', 0, NULL, NULL, 2, NULL, 0, 'Lay the oysters on the cutting board and sprinkle Jeff''s rub on them lightly');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FF5D4F6CBB1FB43AB1EE91F539F628E', X'A825F90EB15C9D4A8141136CF58BB95C', X'A825F90EB15C9D4A8141136CF58BB95C', 1, NULL, NULL, 2, NULL, 0, 'Flip them over and season the other side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E58C8A3B105464A802BF3A5E0A3E44D', X'3A837469FA30AB488EEF4B25194331B8', X'3A837469FA30AB488EEF4B25194331B8', 0, NULL, NULL, 2, NULL, 0, 'Cut bacon into thirds');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A80870E86A44E34C82F893FC97E96966', X'3A837469FA30AB488EEF4B25194331B8', X'3A837469FA30AB488EEF4B25194331B8', 1, NULL, NULL, 2, NULL, 0, 'Place an oyster on one end of the bacon');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24509C8949822B4D8E913AD3478CCDB4', X'3A837469FA30AB488EEF4B25194331B8', X'3A837469FA30AB488EEF4B25194331B8', 2, NULL, NULL, 2, NULL, 0, 'Wrap the bacon around the oyster allowing it to overlap by ½ inch');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7836E58077AD8F4CACF42B86B3A44E69', X'3A837469FA30AB488EEF4B25194331B8', X'3A837469FA30AB488EEF4B25194331B8', 3, NULL, NULL, 2, NULL, 0, 'Use scissors to cut off any extra bacon');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C837227ABBC3048B54818971918B960', X'3A837469FA30AB488EEF4B25194331B8', X'3A837469FA30AB488EEF4B25194331B8', 4, NULL, NULL, 2, NULL, 0, 'Secure with a toothpick');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561359D45E1E75489995C1A51000E8AF', X'3A837469FA30AB488EEF4B25194331B8', X'3A837469FA30AB488EEF4B25194331B8', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle more of Jeff''s rub onto each wrapped oyster');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEFC456FEF5257499FF3503A657333F2', X'CE2054FA1DB33E41B24EC8AE4A19D86F', X'CE2054FA1DB33E41B24EC8AE4A19D86F', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at 225°F with indirect heat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9B5560AFB4C8E4E9BF6A0DB0589864F', X'CE2054FA1DB33E41B24EC8AE4A19D86F', X'CE2054FA1DB33E41B24EC8AE4A19D86F', 1, NULL, NULL, 2, NULL, 0, 'Once smoker is ready, place oysters onto smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CD3EBCE8FFA5341A182398EBC1866B2', X'CE2054FA1DB33E41B24EC8AE4A19D86F', X'CE2054FA1DB33E41B24EC8AE4A19D86F', 2, NULL, NULL, 2, NULL, 0, 'Allow the oysters to cook for about 1 hour and 45 minutes or until the bacon is browned and beginning to crisp');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7DAD026BB115F4F9DF0F83469B4950A', X'CE2054FA1DB33E41B24EC8AE4A19D86F', X'CE2054FA1DB33E41B24EC8AE4A19D86F', 3, NULL, NULL, 2, NULL, 0, 'Remove the oysters from the smoker just as soon as they are finished cooking');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7C55737BB037A4296F684727D8FB3EE', X'82D84198B3BD4440B46297CCFDB413D7', X'82D84198B3BD4440B46297CCFDB413D7', 0, NULL, NULL, 2, NULL, 0, 'Serve immediately with hot sauce or a 50/50 mix of Louisiana Hot Sauce and Jeff''s Barbecue sauce and watch them disappear.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'589941134487254BA54DA0743FDB4CB7', X'7FCD0640D0061A4F899DEB7B980A0197', X'7FCD0640D0061A4F899DEB7B980A0197', 0, NULL, NULL, 2, NULL, 0, 'Place the ground chuck, ground pork (or sausage), egg, parmesan, jalapeño, onion, grated cheddar, bread crumbs, rub, barbecue sauce, Worcestershire and yogurt into a large mixing bowl and get in there with your clean bare hands to combine everything thoroughly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BE4D2029AF13548B3B949DE19345E16', X'5255666FD48D934C8DC3E0311D2B1C03', X'5255666FD48D934C8DC3E0311D2B1C03', 0, NULL, NULL, 2, NULL, 0, 'Use your hands to form meatballs that are about 1.5 inch in diameter. I used an ice cream scoop to make sure I was getting the same amount every time then rolled it between my cupped palms to make it round.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA25FDCC8F6FC4E99D4584B36D0A2BF', X'6D2546F2EA92B341A554B37CCC6675E8', X'6D2546F2EA92B341A554B37CCC6675E8', 0, NULL, NULL, 2, NULL, 0, 'Press the ¼ inch cubes of cheese right down into the center of each meatball.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A79249D5DB868D42B1A221EECE9D67C7', X'6D2546F2EA92B341A554B37CCC6675E8', X'6D2546F2EA92B341A554B37CCC6675E8', 1, NULL, NULL, 2, NULL, 0, 'Roll the meatball between cupped palms again to reform them into round shapes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B9F4B97E65EFE46B77EB18D58554743', X'6D2546F2EA92B341A554B37CCC6675E8', X'6D2546F2EA92B341A554B37CCC6675E8', 2, NULL, NULL, 2, NULL, 0, 'Optional: Transfer the meatballs to a Bradley rack or cookie sheet to help transport them to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'463E847AD9ABB74295AC8DAE1C698BC9', X'3738110190DD2347800B4FB72C317E4D', X'3738110190DD2347800B4FB72C317E4D', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F using apple wood if you have it. Other fruit woods will work as well as pecan, oak, or almost any smoking wood that you have and like.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEE000F6A5516B428591A1AFC5903692', X'3738110190DD2347800B4FB72C317E4D', X'3738110190DD2347800B4FB72C317E4D', 1, NULL, NULL, 2, NULL, 0, 'Once ready, place the cheesy meatballs (or the entire Bradley rack if you are using them), directly onto the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8C8A6D0DF3CC94FB65DE655D1A72BE3', X'3738110190DD2347800B4FB72C317E4D', X'3738110190DD2347800B4FB72C317E4D', 2, NULL, NULL, 2, NULL, 0, 'Keep the smoke going the entire time and do your best to maintain 225°F throughout.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'292724EDE180DB438E1B06AD7DA47A8B', X'5791FE86D7A24C41B8BAA8D5E749097B', X'5791FE86D7A24C41B8BAA8D5E749097B', 0, NULL, NULL, 2, NULL, 0, 'I chose to serve the sauce on the side but I was asked by more than one person why I did not sauce them. I''m just saying.. you might consider it;)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FE31F6F8AD0704E898D5CDB23CA4AF6', X'5791FE86D7A24C41B8BAA8D5E749097B', X'5791FE86D7A24C41B8BAA8D5E749097B', 1, NULL, NULL, 2, NULL, 0, 'If you choose to sauce them, do so at about the 30 and 45 minute mark. This will give the sauce time to glaze over so they are not too sticky.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A035CF6FDB8DD34DB41F20F2EE3976C6', X'04F257C105501342B839F3E4BB634DDE', X'04F257C105501342B839F3E4BB634DDE', 0, NULL, NULL, 2, NULL, 0, 'Check the temperature of the meatballs and when they reach 160 °F, they are done. This should take about an hour, but you might check them sooner than this to get a feel for how fast they are getting done. The amount of time required will be determined by the temperature of the meatballs when they go into the smoker, how often you open the door and let the heat escape, the diameter of the meatballs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D024F89185A0D4D8C3C231164121698', X'40528C539180F640AB03453FF2D7B648', X'40528C539180F640AB03453FF2D7B648', 0, NULL, NULL, 2, NULL, 0, 'Serving the cheesy smoked meatballs simply means that you bring them into the house (after having hid a few for yourself).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B38AC90EF1954EB932AE055419C206', X'68DAB9E380771A4595A9B728DBC651BA', X'68DAB9E380771A4595A9B728DBC651BA', 0, NULL, NULL, 2, NULL, 0, 'Wrap in half slices of bacon');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71553EE0B12A624581631E53CC3577AA', X'68DAB9E380771A4595A9B728DBC651BA', X'68DAB9E380771A4595A9B728DBC651BA', 1, NULL, NULL, 2, NULL, 0, 'Use these for meatball subs (delicious)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31CD84A2D8E205408E0364614648D44C', X'68DAB9E380771A4595A9B728DBC651BA', X'68DAB9E380771A4595A9B728DBC651BA', 2, NULL, NULL, 2, NULL, 0, 'Use ground turkey or ground chicken for healthier choices');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC63005F256E424ABDBF193BE7256C26', X'68DAB9E380771A4595A9B728DBC651BA', X'68DAB9E380771A4595A9B728DBC651BA', 3, NULL, NULL, 2, NULL, 0, 'Use over pasta (spaghetti, linguini, etc.)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869347047ED6AF4A95FBE9C9F041D79E', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', 0, NULL, NULL, 2, NULL, 0, 'Make a basic brine from 1 gallon of water, 1 cup of kosher salt and ¾ cup of brown sugar.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F790A497BE641F449C52E35967DE61C2', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', 1, NULL, NULL, 2, NULL, 0, 'Add the salt to the water and stir until dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA29153C3097C740AE2B35B8B5CF468C', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', 2, NULL, NULL, 2, NULL, 0, 'Add the brown sugar and stir once again until it is dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92F0466D4BC74B4E8FC0ABA9505C822F', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', 3, NULL, NULL, 2, NULL, 0, 'Put the chicken quarters down into a large zip top bag and then pour the brine into the bag over the chicken to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13F6C2D297F3E644A863E365FB57B099', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', 4, NULL, NULL, 2, NULL, 0, 'Brine in the refrigerator or in temperature of 33 to 39°F for 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0240F7FD760634B970432BC31497579', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', X'04BEA78BDED4894AAEAD11CFA3B3B0A5', 5, NULL, NULL, 2, NULL, 0, 'When it’s done brining, give the chicken quarters a good rinse and you are ready to proceed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20676465474EDE4E8CBA04E449B003EF', X'F640EC50D301EF4690AF9031C082CE24', X'F640EC50D301EF4690AF9031C082CE24', 0, NULL, NULL, 2, NULL, 0, 'Brush on some olive oil to help the rub to stick');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'968E7609F2A3CB4B94D6637119ABFC9A', X'F640EC50D301EF4690AF9031C082CE24', X'F640EC50D301EF4690AF9031C082CE24', 1, NULL, NULL, 2, NULL, 0, 'Apply a tablespoon or two of my rub to the top of the chicken and spread it all over with your hands so that it mixes with the oil and creates a nice paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F7D8D57A427C4BBAEEFAE0F246CCFF', X'F640EC50D301EF4690AF9031C082CE24', X'F640EC50D301EF4690AF9031C082CE24', 2, NULL, NULL, 2, NULL, 0, 'Make sure to get oil and rub up under the skin a little as well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53D09D982B9EBE49A19B6A8DD9A98EE1', X'F640EC50D301EF4690AF9031C082CE24', X'F640EC50D301EF4690AF9031C082CE24', 3, NULL, NULL, 2, NULL, 0, 'Move the seasoned chicken quarters to a Bradley rack or similar device and it’s ready to go into the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3530288EF472C6409C64AAE33B5C00FB', X'82079F15F077FA4CB2004537E74A1CDE', X'82079F15F077FA4CB2004537E74A1CDE', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker for cooking at about 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5163FF0D4D9CC643A174B4DD1E7039ED', X'82079F15F077FA4CB2004537E74A1CDE', X'82079F15F077FA4CB2004537E74A1CDE', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken directly on the grate leaving about 1 inch between them to allow the smoke full access to the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34C150C1ADACDF42B80A493692ED8B58', X'82079F15F077FA4CB2004537E74A1CDE', X'82079F15F077FA4CB2004537E74A1CDE', 2, NULL, NULL, 2, NULL, 0, 'Leave the chicken quarters in the smoker using indirect heat for about 1 hour during which time it will reach about 140°F internally.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CCC778E9C38BA40A0A11B46133B2425', X'82079F15F077FA4CB2004537E74A1CDE', X'82079F15F077FA4CB2004537E74A1CDE', 3, NULL, NULL, 2, NULL, 0, 'Use dry pecan, cherry or another favorite wood for smoke the entire time it is in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC122DB377126A4FB969B3624B704756', X'82079F15F077FA4CB2004537E74A1CDE', X'82079F15F077FA4CB2004537E74A1CDE', 4, NULL, NULL, 2, NULL, 0, 'Be sure to use a thermometer such as the Maverick ET-733 to monitor the temperature of the chicken while it cooks in the smoker and grill.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B31625093C4E5E4A80E6A51621AF055A', X'1D49F6AA531CBD44A2321B034E8476A6', X'1D49F6AA531CBD44A2321B034E8476A6', 0, NULL, NULL, 2, NULL, 0, 'Heat the grill to very high heat just before the chicken is finished smoking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129097D081348740870E3327198AF522', X'1D49F6AA531CBD44A2321B034E8476A6', X'1D49F6AA531CBD44A2321B034E8476A6', 1, NULL, NULL, 2, NULL, 0, 'Once an hour of smoking has passed, place the chicken on the grill over direct heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D3BEC09424C124DAA28DEE2493E625C', X'1D49F6AA531CBD44A2321B034E8476A6', X'1D49F6AA531CBD44A2321B034E8476A6', 2, NULL, NULL, 2, NULL, 0, 'Brush on a little of my sauce while it finishes cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'953D319095351C49BCE87FD5E47B0B60', X'1D49F6AA531CBD44A2321B034E8476A6', X'1D49F6AA531CBD44A2321B034E8476A6', 3, NULL, NULL, 2, NULL, 0, 'Watch the chicken carefully to make sure it does not burn.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B9C2F3B8CA0AB41B77BDF74E56927EC', X'1D49F6AA531CBD44A2321B034E8476A6', X'1D49F6AA531CBD44A2321B034E8476A6', 4, NULL, NULL, 2, NULL, 0, 'When the chicken quarters reach 165-170 °F, they are finished cooking and are ready to eat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CDAA943B1A39B408923831ECE1C1C7B', X'E09E308D1CF83042B90BFDD543124638', X'E09E308D1CF83042B90BFDD543124638', 0, NULL, NULL, 2, NULL, 0, 'Serve the chicken quarters immediately when they are finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C32667C1D6148F4B95B8B99CA00E4F56', X'2F2DDB4017DA6C4ABF6B4221FFE6D7B7', X'2F2DDB4017DA6C4ABF6B4221FFE6D7B7', 0, NULL, NULL, 2, NULL, 0, 'Last week, I showed you how to smoke a brisket from start to finish and those instructions are archived HERE.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B0042514000E643B4DB4A6FA9F32270', X'2F2DDB4017DA6C4ABF6B4221FFE6D7B7', X'2F2DDB4017DA6C4ABF6B4221FFE6D7B7', 1, NULL, NULL, 2, NULL, 0, 'When it gets finished, separate the flat part of the brisket from the thicker, more fatty end called the point. The point is used to make the burnt ends.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB8C74FCBA2404CB59F7C31466C8638', X'25063CA3E47B9647B1AE007681FBB8E4', X'25063CA3E47B9647B1AE007681FBB8E4', 0, NULL, NULL, 2, NULL, 0, 'Brisket is really expensive right now and you may find a chuck roast to be a little easier on the wallet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9685094E48B544AA41F9ED7AF5CBFB9', X'25063CA3E47B9647B1AE007681FBB8E4', X'25063CA3E47B9647B1AE007681FBB8E4', 1, NULL, NULL, 2, NULL, 0, 'Smoke the chuck roast at 225°F until it reaches 160°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2FBAA547B4FF347BA02F258A759FC4B', X'25063CA3E47B9647B1AE007681FBB8E4', X'25063CA3E47B9647B1AE007681FBB8E4', 2, NULL, NULL, 2, NULL, 0, 'Wrap it in foil and continue cooking it until it reaches about 200 °F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'879228065C56ED429BADE583616053EB', X'317B3A9F89A8A84D8B706F901EDEBBBE', X'317B3A9F89A8A84D8B706F901EDEBBBE', 0, NULL, NULL, 2, NULL, 0, 'Once you remove the flat end, you will notice that there is a thick vein of fat that runs through the middle of the brisket. I run my knife along the bottom of this to separate them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F54E48C4C47DE149ACAAC09C3D889DE7', X'317B3A9F89A8A84D8B706F901EDEBBBE', X'317B3A9F89A8A84D8B706F901EDEBBBE', 1, NULL, NULL, 2, NULL, 0, 'Slice/remove the fat from the top of the pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EBAB0FB81748B4CB5E51BA603BBDDE0', X'4C19CFBC0969304B81A6501835F60822', X'4C19CFBC0969304B81A6501835F60822', 0, NULL, NULL, 2, NULL, 0, 'Slice the meat into pieces that are about 1 to 1-1/2 inch thick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4E7D6F96EE6164EA390E32920393B7A', X'4C19CFBC0969304B81A6501835F60822', X'4C19CFBC0969304B81A6501835F60822', 1, NULL, NULL, 2, NULL, 0, 'Further cut the thick slices into pieces about 1 inch wide to make cubes of meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9CCFCEE710B8E4FBB728125B65DAC99', X'4C19CFBC0969304B81A6501835F60822', X'4C19CFBC0969304B81A6501835F60822', 2, NULL, NULL, 2, NULL, 0, 'Add some sauce to each one');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22FF32AB32F9084D969076ECD7BC5599', X'4C19CFBC0969304B81A6501835F60822', X'4C19CFBC0969304B81A6501835F60822', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on some rub generously then roll them around to coat well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D2D7A8261F9854B91BCF5484F04D319', X'4C19CFBC0969304B81A6501835F60822', X'4C19CFBC0969304B81A6501835F60822', 4, NULL, NULL, 2, NULL, 0, 'If you want more rub, you can also place some rub in a plate and roll the sauced brisket pieces in the rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43AE7DDA7819A848980D440FC4ABC1B8', X'4C19CFBC0969304B81A6501835F60822', X'4C19CFBC0969304B81A6501835F60822', 5, NULL, NULL, 2, NULL, 0, 'The rub and sauce together is what makes that nice caramelized coating on the outside that tastes so good.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B998A34EC4A7634D957391E6A7073E78', X'1A98173B4DF67940A62C547CAAE25477', X'1A98173B4DF67940A62C547CAAE25477', 0, NULL, NULL, 2, NULL, 0, 'Now that the little tasty morsels of goodness are covered in a nice layer of sauce and rub, you can cut pieces of thin bacon in half and wrap each one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'469333839A621A4FBA0840B3F21EADFE', X'FD3498A00AB8C94B888FADD1E0D8259F', X'FD3498A00AB8C94B888FADD1E0D8259F', 0, NULL, NULL, 2, NULL, 0, 'Setup whatever smoker or grill you have available for cooking at 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'146D1F62B8783941B56E8788B5D0EAF5', X'FD3498A00AB8C94B888FADD1E0D8259F', X'FD3498A00AB8C94B888FADD1E0D8259F', 1, NULL, NULL, 2, NULL, 0, 'With the bacon wrapped version we are making in this recipe, I recommend using a Bradley rack or something similar to give them open access to the heat and smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC2270AEC76A5D44978F9CCF6B310978', X'FD3498A00AB8C94B888FADD1E0D8259F', X'FD3498A00AB8C94B888FADD1E0D8259F', 2, NULL, NULL, 2, NULL, 0, 'You can also crank up the heat to help the bacon to crisp up a little bit. Using thin bacon helps a lot.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F5F4276F9A4C54BB4D32644380387EE', X'FD3498A00AB8C94B888FADD1E0D8259F', X'FD3498A00AB8C94B888FADD1E0D8259F', 3, NULL, NULL, 2, NULL, 0, 'Brush on a little sauce if desired about 30 minutes before removing them from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C01B709D0F257F49812016155A2E64DA', X'FD3498A00AB8C94B888FADD1E0D8259F', X'FD3498A00AB8C94B888FADD1E0D8259F', 4, NULL, NULL, 2, NULL, 0, 'Serve ''em up and Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0011E7202DBD934FBBBA2D0ED58C5762', X'477579DBB12532429BF23401A0CF4F95', X'477579DBB12532429BF23401A0CF4F95', 0, NULL, NULL, 2, NULL, 0, 'Score the fat cap down to meat in a crosshatch pattern - 1 inch increments');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'414A91FC1D4D424ABFD5F76C366FB4FE', X'477579DBB12532429BF23401A0CF4F95', X'477579DBB12532429BF23401A0CF4F95', 1, NULL, NULL, 2, NULL, 0, 'Massage about 2 TBS of mustard and about 3-4 TBS of Jeff''s rub onto the top and bottom side of the brisket');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355393ED701D0E4A829F7FDA7A22ED5C', X'D7C02C263EA8C94E894ABD4EF78ACAB6', X'D7C02C263EA8C94E894ABD4EF78ACAB6', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EA200EC3B5F934992C582BE2B587827', X'D7C02C263EA8C94E894ABD4EF78ACAB6', X'D7C02C263EA8C94E894ABD4EF78ACAB6', 1, NULL, NULL, 2, NULL, 0, 'Place brisket fat side up on smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BEDFA08D420704E8B02435BF53AD6D6', X'D7C02C263EA8C94E894ABD4EF78ACAB6', X'D7C02C263EA8C94E894ABD4EF78ACAB6', 2, NULL, NULL, 2, NULL, 0, 'Place pan under brisket if possible to catch juices');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08874E3C96872B4AB01700BA6D0514D2', X'D7C02C263EA8C94E894ABD4EF78ACAB6', X'D7C02C263EA8C94E894ABD4EF78ACAB6', 3, NULL, NULL, 2, NULL, 0, 'Apply smoke for at least 6 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57FC85C23480BC4A926204CD00F98338', X'D7C02C263EA8C94E894ABD4EF78ACAB6', X'D7C02C263EA8C94E894ABD4EF78ACAB6', 4, NULL, NULL, 2, NULL, 0, 'Smoke cook brisket for about 16 to 18 hours or until it reaches 200°F in the flat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A68AC82D07833A42AA11A888517040C1', X'D7C02C263EA8C94E894ABD4EF78ACAB6', X'D7C02C263EA8C94E894ABD4EF78ACAB6', 5, NULL, NULL, 2, NULL, 0, 'Remove brisket from smoker when finished and cool for 30 minutes on counter');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD7BB41DD8987D4E8A4B9794609362C6', X'D14D2A42D988914FB146706000CEFBD6', X'D14D2A42D988914FB146706000CEFBD6', 0, NULL, NULL, 2, NULL, 0, 'After cooling, separate flat from point');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEFD0BDF62E9F141A48C1B0E728E9829', X'D14D2A42D988914FB146706000CEFBD6', X'D14D2A42D988914FB146706000CEFBD6', 1, NULL, NULL, 2, NULL, 0, 'Remove fat cap from top of flat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9093172E9F9C624587DB0C0185E795C7', X'D14D2A42D988914FB146706000CEFBD6', X'D14D2A42D988914FB146706000CEFBD6', 2, NULL, NULL, 2, NULL, 0, 'Chop remainder of flat and put meat into a foil pan');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BEE9D58BD7D5540AEEB0CF61BBB79A4', X'D14D2A42D988914FB146706000CEFBD6', X'D14D2A42D988914FB146706000CEFBD6', 3, NULL, NULL, 2, NULL, 0, 'Add half of defatted juices back into chopped meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EC932C1401E2841B51BA5B5CDE69A41', X'D14D2A42D988914FB146706000CEFBD6', X'D14D2A42D988914FB146706000CEFBD6', 4, NULL, NULL, 2, NULL, 0, 'Place pan of chopped brisket back into smoker for 30 minutes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26906260E7476A48A3444EA5BF144AB5', X'35A974613A880E4B825BF16F38B6E64B', X'35A974613A880E4B825BF16F38B6E64B', 0, NULL, NULL, 2, NULL, 0, 'Add more of the defatted juices if needed and season with Jeff''s Texas style rub to taste');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D54CC3011113374D9D7876A28E862A24', X'35A974613A880E4B825BF16F38B6E64B', X'35A974613A880E4B825BF16F38B6E64B', 1, NULL, NULL, 2, NULL, 0, 'Serve immediately with warm barbecue sauce on the side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0714D8B1875CE54F8A48DFB32850F48A', X'DC92B7AF9B4C56409C6AEF46E85AF11D', X'DC92B7AF9B4C56409C6AEF46E85AF11D', 0, NULL, NULL, 2, NULL, 0, 'Lay the turkey breast on the cutting board and butterfly it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC3B3A07518EE944A53179CA4C8D02FE', X'DC92B7AF9B4C56409C6AEF46E85AF11D', X'DC92B7AF9B4C56409C6AEF46E85AF11D', 1, NULL, NULL, 2, NULL, 0, 'Lay plastic wrap on top of the turkey breast and pound the meat to flatten it evenly to about ¼ inch thick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ED5F51C36948E409EDBB74774A348A8', X'755187547C5CD54ABC5D8FA8D03CB85A', X'755187547C5CD54ABC5D8FA8D03CB85A', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle a little of my rub on the inside surface area of the turkey breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B25A642780A134C8A5A5E313B0C094A', X'755187547C5CD54ABC5D8FA8D03CB85A', X'755187547C5CD54ABC5D8FA8D03CB85A', 1, NULL, NULL, 2, NULL, 0, 'Spoon some of the uncooked stuffing onto the top of the seasoned meat. Leave about ½ inch around the edges and about 2 inches on the end away from you.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A70BC5B2EA66F0429C6A57F735C60FA5', X'755187547C5CD54ABC5D8FA8D03CB85A', X'755187547C5CD54ABC5D8FA8D03CB85A', 2, NULL, NULL, 2, NULL, 0, 'Add some dried sweetened cranberries and chopped pecans to the top of the stuffing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C8DA3B811E589488C4F40FD55914720', X'106BCAA506DEBF49BD082730AD1814E4', X'106BCAA506DEBF49BD082730AD1814E4', 0, NULL, NULL, 2, NULL, 0, 'Roll up the turkey breast as tightly as you can.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BBF0C376E924E4DBEFDBACFA7945A14', X'106BCAA506DEBF49BD082730AD1814E4', X'106BCAA506DEBF49BD082730AD1814E4', 1, NULL, NULL, 2, NULL, 0, 'Use 4 pieces of cooking twine to tie the stuffed and rolled turkey breast into place.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28FF5F95A8F579489C489E186A95FB90', X'C911618A7BE76644866F3606CDC53387', X'C911618A7BE76644866F3606CDC53387', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle the Texas style rub on the outside of the turkey breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5444AFCDB2EB7441BCC8852DD1D22484', X'C911618A7BE76644866F3606CDC53387', X'C911618A7BE76644866F3606CDC53387', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey breast(s) on a Bradley rack or cookie sheet to make it easy to transport to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E5C056D3A69274B95B93C5F1C57A855', X'1508738FE441034C8FA14863D135A51F', X'1508738FE441034C8FA14863D135A51F', 0, NULL, NULL, 2, NULL, 0, 'Set up whatever smoker you have available for cooking at about 225°F using indirect heat. If your smoker came with a water pan, use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'957A70E3304FB84CAD1C42BD65E9059E', X'1508738FE441034C8FA14863D135A51F', X'1508738FE441034C8FA14863D135A51F', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is heated and ready, place the stuffed, rolled turkey breasts directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'648A3A5B4688904C8740C8A8F569665A', X'1508738FE441034C8FA14863D135A51F', X'1508738FE441034C8FA14863D135A51F', 2, NULL, NULL, 2, NULL, 0, 'Use pecan wood if you have it or any fruit wood will work really well with this.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD3977841D56A449A8CDE2628E7FFF40', X'1508738FE441034C8FA14863D135A51F', X'1508738FE441034C8FA14863D135A51F', 3, NULL, NULL, 2, NULL, 0, 'I recommend keeping the smoke going for at least 2 hours if possible. You can also keep the smoke going the entire time as long you keep the smoke thin and light.. what we call thin blue smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD1BC1C41135540BD24BB5EAD2AA405', X'1508738FE441034C8FA14863D135A51F', X'1508738FE441034C8FA14863D135A51F', 4, NULL, NULL, 2, NULL, 0, 'About once every hour, baste the turkey with a little melted butter to keep the outside of the meat moist so it does not dry out.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82E30A1EB7A8684DADF65E6E0EF956D6', X'4957F868C90C474AB1904CF8D1156B14', X'4957F868C90C474AB1904CF8D1156B14', 0, NULL, NULL, 2, NULL, 0, 'Use a digital probe meat thermometer to monitor the temperature of the meat while it cooks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E316194004B934EA3EFBE66FBD5753B', X'4957F868C90C474AB1904CF8D1156B14', X'4957F868C90C474AB1904CF8D1156B14', 1, NULL, NULL, 2, NULL, 0, 'When it hits 165°F in the center, it is perfectly done and should be removed from the smoker right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61339D0AC51FF64D96CCFF49217F6C9A', X'4957F868C90C474AB1904CF8D1156B14', X'4957F868C90C474AB1904CF8D1156B14', 2, NULL, NULL, 2, NULL, 0, 'Let it rest on the cabinet for 10 minutes then remove the strings.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C850D68DD55AC94DA8EFE85A0D1CAB36', X'4957F868C90C474AB1904CF8D1156B14', X'4957F868C90C474AB1904CF8D1156B14', 3, NULL, NULL, 2, NULL, 0, 'Slice it into ½ to ¾ inch slices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41A5B0AAC23E224786BF6609DF3E0326', X'4957F868C90C474AB1904CF8D1156B14', X'4957F868C90C474AB1904CF8D1156B14', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately with all of your other favorite fixings and sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8595F933E49D2B409C8E05B09EA94BF9', X'EAEFB9782BEFC348B21FDCDF2EFA233F', X'EAEFB9782BEFC348B21FDCDF2EFA233F', 0, NULL, NULL, 2, NULL, 0, 'Remove the pork tenderloins from the package, give them a rinse under cold water and pat them dry with a paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B18EF297208834FA5F74260B7DD4E49', X'EAEFB9782BEFC348B21FDCDF2EFA233F', X'EAEFB9782BEFC348B21FDCDF2EFA233F', 1, NULL, NULL, 2, NULL, 0, 'Lay them on the cutting board and do your best to remove any silver skin, extra fat, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE8CF1775474DF4CBE43B96C116371BA', X'13F3D769758E024BABC5F8144B3684D7', X'13F3D769758E024BABC5F8144B3684D7', 0, NULL, NULL, 2, NULL, 0, 'As usual, I like to use the old trusty yellow mustard base on this pork to help that rub to stick really well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B74B61B4B6253D44BE5913D52EF5C47A', X'13F3D769758E024BABC5F8144B3684D7', X'13F3D769758E024BABC5F8144B3684D7', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle on a few tablespoons of rub and massage it into the meat all over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABF6DEB480FAD64F9FB79E7F3F1A4D2F', X'13F3D769758E024BABC5F8144B3684D7', X'13F3D769758E024BABC5F8144B3684D7', 2, NULL, NULL, 2, NULL, 0, 'If you are doing these the night before (recommended) simply put them in a big ziploc bag and place in the fridge overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C2866E130FA674181E4D3C8064C37B5', X'13F3D769758E024BABC5F8144B3684D7', X'13F3D769758E024BABC5F8144B3684D7', 3, NULL, NULL, 2, NULL, 0, 'If you are ready to smoke, you can lay them on a Bradley rack with a little space between them and you are good to go.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'977AD29FE3E78B43A4F25074496A5ADB', X'8D709BB2600CB24380ED9AB643AAE213', X'8D709BB2600CB24380ED9AB643AAE213', 0, NULL, NULL, 2, NULL, 0, 'You can use whatever smoker or grill you have access to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DBDFCCA06575B44A1B6406DF2D96C36', X'8D709BB2600CB24380ED9AB643AAE213', X'8D709BB2600CB24380ED9AB643AAE213', 1, NULL, NULL, 2, NULL, 0, 'Set it up for cooking at about 225°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FE2E50BE89BDA49BDD800C6F4E1C33C', X'382ED2C903066241868835936DBC3E1F', X'382ED2C903066241868835936DBC3E1F', 0, NULL, NULL, 2, NULL, 0, 'Place the pork tenderloins directly on the grate or place the Bradley rack with the meat on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EA7880D1B347D4C978DBAE711CE1477', X'382ED2C903066241868835936DBC3E1F', X'382ED2C903066241868835936DBC3E1F', 1, NULL, NULL, 2, NULL, 0, 'You can probably expect these to take around 1.5 to 2 hours to reach the perfect safely done temperature for pork at 145°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'575B5C1A727D5045A347382C8DEDE9AC', X'382ED2C903066241868835936DBC3E1F', X'382ED2C903066241868835936DBC3E1F', 2, NULL, NULL, 2, NULL, 0, 'Keep the smoke going for the entire time if you can or for at least an hour to get some good smoke flavor on the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84E9A42BB204064DB606B3DFC63C08B2', X'382ED2C903066241868835936DBC3E1F', X'382ED2C903066241868835936DBC3E1F', 3, NULL, NULL, 2, NULL, 0, 'I used cherry to go along with the cherry compote but any fruit wood or even pecan would be really good on these.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'647D8046324F9D4E9F343571590ACE98', X'382ED2C903066241868835936DBC3E1F', X'382ED2C903066241868835936DBC3E1F', 4, NULL, NULL, 2, NULL, 0, 'When the pork tenderloins reach about 110°F or about an hour into the cook, brush some of the cherry bourbon compote onto the top of each one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52D6CFD0FB864F479300AAC6DB06984D', X'382ED2C903066241868835936DBC3E1F', X'382ED2C903066241868835936DBC3E1F', 5, NULL, NULL, 2, NULL, 0, 'Wait about 20 minutes and repeat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57851A6CF482564F80C784C3EDA6ED12', X'382ED2C903066241868835936DBC3E1F', X'382ED2C903066241868835936DBC3E1F', 6, NULL, NULL, 2, NULL, 0, 'Be sure to use a tested digital thermometer to monitor the temperature while they cook. You are looking for about 145°F but I often remove them a couple of degrees early if I plan to wrap them in foil and hold them for 30-60 minutes until dinner time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65D788E5F62AA4499989E5053264DB9E', X'CC73C50F87C67C4BA6865EF212B8D819', X'CC73C50F87C67C4BA6865EF212B8D819', 0, NULL, NULL, 2, NULL, 0, 'Into a medium sauce pan, place ¼ cup Bourbon, ¼ cup brown sugar, ½ cup water, and a 12-13 oz jar of cherry preserves.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60E584835269104D9280722C46780C0E', X'CC73C50F87C67C4BA6865EF212B8D819', X'CC73C50F87C67C4BA6865EF212B8D819', 1, NULL, NULL, 2, NULL, 0, 'Using medium heat, bring the mixture to a rolling boil then reduce heat slightly to simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF5826B3F030594DB5430280413B13DD', X'CC73C50F87C67C4BA6865EF212B8D819', X'CC73C50F87C67C4BA6865EF212B8D819', 2, NULL, NULL, 2, NULL, 0, 'The compote will reduce by about 1/3 by the time it is finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED5AC5DF37C6DA41AEB942D2B1C69D4F', X'CC73C50F87C67C4BA6865EF212B8D819', X'CC73C50F87C67C4BA6865EF212B8D819', 3, NULL, NULL, 2, NULL, 0, 'When 10 minutes have elapsed, remove the compote from the heat and allow it to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6161F5FAD04C514DA20E8ACA9B40C416', X'4340BECC42D9E84891642154C578CE15', X'4340BECC42D9E84891642154C578CE15', 0, NULL, NULL, 2, NULL, 0, 'Remove the pork tenderloins from the smoker and bring them into the house.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2952FA0F613BB24882C2D93B21234347', X'4340BECC42D9E84891642154C578CE15', X'4340BECC42D9E84891642154C578CE15', 1, NULL, NULL, 2, NULL, 0, 'If necessary, wrap them in foil and then in a thick towel on the counter to hold them for 30-60 minutes until dinner time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F26D2C3E31F1E040BFB78AEB5FD9B9CB', X'4340BECC42D9E84891642154C578CE15', X'4340BECC42D9E84891642154C578CE15', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, remove from the foil, slice and serve about 1/3 to ½ tenderloin per person');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CB3CA845CDAAF4FBD5210A54F6246CA', X'6D961B43095F2547B44DAD1E61E93888', X'6D961B43095F2547B44DAD1E61E93888', 0, NULL, NULL, 2, NULL, 0, 'Ask the butcher to semi-detach the bones, to tie it up and to french the bones. They are usually happy to offer these services.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A6EB197CC34C14B9A65B0509184E2E9', X'6D961B43095F2547B44DAD1E61E93888', X'6D961B43095F2547B44DAD1E61E93888', 1, NULL, NULL, 2, NULL, 0, 'Note: Figure on 1 bone for every 2 people eating.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51F5395B5A0C7542B329AAB7128F8F89', X'90EFB5F33AB2434CA7DECC0F107C01C7', X'90EFB5F33AB2434CA7DECC0F107C01C7', 0, NULL, NULL, 2, NULL, 0, 'Lay the prime rib on the cutting board');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E2C05C118C32443953503CCB071702B', X'90EFB5F33AB2434CA7DECC0F107C01C7', X'90EFB5F33AB2434CA7DECC0F107C01C7', 1, NULL, NULL, 2, NULL, 0, 'Brush on a little olive oil or cooking oil to help the rub to stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF42182123EFAF4EADB598FFE6147B21', X'90EFB5F33AB2434CA7DECC0F107C01C7', X'90EFB5F33AB2434CA7DECC0F107C01C7', 2, NULL, NULL, 2, NULL, 0, 'Mix up a batch of the Texas style rub recipe and use it generously all over the prime rib.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF50FDF87915E6468636CD14DBF24018', X'7A979FDE1D85674A8416B59560625B06', X'7A979FDE1D85674A8416B59560625B06', 0, NULL, NULL, 2, NULL, 0, 'You can use any smoker to smoke this prime rib but I recommend that you use one that allows you to easily control the temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07BCE386E7478349BB31754045F09194', X'7A979FDE1D85674A8416B59560625B06', X'7A979FDE1D85674A8416B59560625B06', 1, NULL, NULL, 2, NULL, 0, 'Set up your smoker to maintain about 225°F for about 5-6 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1827F7B6330EE148B2A5C762372C311A', X'1E51C49C53CEA045813DBCEDE57751D6', X'1E51C49C53CEA045813DBCEDE57751D6', 0, NULL, NULL, 2, NULL, 0, 'Once the smoker is ready to go, place the prime rib roast directly on the grate, bones down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B35449F5849944498E8C20E73B33999', X'1E51C49C53CEA045813DBCEDE57751D6', X'1E51C49C53CEA045813DBCEDE57751D6', 1, NULL, NULL, 2, NULL, 0, 'I used dry mesquite wood for flavor but you can use pecan, or almost any fruit wood such as cherry for great results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE361041E6851C4EB98D8F8DC6A1544E', X'1E51C49C53CEA045813DBCEDE57751D6', X'1E51C49C53CEA045813DBCEDE57751D6', 2, NULL, NULL, 2, NULL, 0, 'Keep the smoke going the entire time if you desire or you can go for about 3 hours and that should be plenty.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A1BA395A5D376409AC89B5B7CDC1FE6', X'1E51C49C53CEA045813DBCEDE57751D6', X'1E51C49C53CEA045813DBCEDE57751D6', 3, NULL, NULL, 2, NULL, 0, 'Use a digital probe meat thermometer to monitor the temperature and be sure to test it in boiling water beforehand to make sure it is reading within a degree or two of 212°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BC3193DB72CB8459D0935BEFA96A867', X'1E51C49C53CEA045813DBCEDE57751D6', X'1E51C49C53CEA045813DBCEDE57751D6', 4, NULL, NULL, 2, NULL, 0, 'I recommend cooking the prime rib to medium rare or about 135-140°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E08F9C5B95240E40AD83026D26531CF6', X'48C058B867D34548AD93E42FBA7612D3', X'48C058B867D34548AD93E42FBA7612D3', 0, NULL, NULL, 2, NULL, 0, 'Once the smoked prime rib hits that magic number of 138 °F or whatever you deem it to be, remove it quickly from the smoker and move it to the house. There is no need to wait to cut it but you can hold it for 30 minutes or so by tenting foil over the top of the roast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FDBA21387805348983B5FE9AB95DEDE', X'48C058B867D34548AD93E42FBA7612D3', X'48C058B867D34548AD93E42FBA7612D3', 1, NULL, NULL, 2, NULL, 0, 'When ready to slice it up, clip the strings, remove the bones and then slice it into pieces about ¾ inch thick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5330049B4926EA4EBF0F4EC3A1682551', X'73878212889C72448C1B093042B02955', X'73878212889C72448C1B093042B02955', 0, NULL, NULL, 2, NULL, 0, 'One stick or ¼ lb. of (real) salted butter, room temperature, 1-½ TBS of Worcestershire sauce, and 1 tsp of the Texas style rub recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6BA590E836EA445B07C76998BDB2CC1', X'73878212889C72448C1B093042B02955', X'73878212889C72448C1B093042B02955', 1, NULL, NULL, 2, NULL, 0, 'Mix/fold everything together until all ingredients are combined really well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'342DCB48A5EDCE4F832B7D57F74599E7', X'27809FC1129CF2418E9D6D3FF4C4DA0C', X'27809FC1129CF2418E9D6D3FF4C4DA0C', 0, NULL, NULL, 2, NULL, 0, 'How to reheat slices of the prime rib? Very carefully! You do not want to overheat the meat which will cook it and take it beyond medium rare. I sometimes just set it on the counter to let it come up to room temperature and just eat it that way to preserve all of that original good flavor. You can also wrap it up in a piece of foil with just a teaspoon of water or beef broth in the bottom of the foil. Place it into a 225 degree oven for about 30 minutes or until it gets as warm as you like it. Be sure to not let it exceed 140°F or it will no longer be medium rare.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50617A873E9BE14684F3D52CBB329FC7', X'1DDC4675CA041E4DBDC1546062FEEF66', X'1DDC4675CA041E4DBDC1546062FEEF66', 0, NULL, NULL, 2, NULL, 0, 'Remove the membrane.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15A914A5B443674B95F348A86FE8633A', X'1DDC4675CA041E4DBDC1546062FEEF66', X'1DDC4675CA041E4DBDC1546062FEEF66', 1, NULL, NULL, 2, NULL, 0, 'Apply some vegetable oil or yellow mustard to the ribs to help the rub to stick real nice to the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7C8D26A12CC654A95943CC2AFBD80F8', X'BF65F71038B5B24DA6730FA2A7A47D3C', X'BF65F71038B5B24DA6730FA2A7A47D3C', 0, NULL, NULL, 2, NULL, 0, 'Use a 50:50 mix of cinnamon and white sugar');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'087E81087CCEC447842494AADB60B997', X'BF65F71038B5B24DA6730FA2A7A47D3C', X'BF65F71038B5B24DA6730FA2A7A47D3C', 1, NULL, NULL, 2, NULL, 0, 'After you add the vegetable oil, just sprinkle a very light dusting of the cinnamon sugar on both sides of the ribs (it doesn’t take a lot).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A49C1C30B3A3FD4DB85458850FDE6AEC', X'BF65F71038B5B24DA6730FA2A7A47D3C', X'BF65F71038B5B24DA6730FA2A7A47D3C', 2, NULL, NULL, 2, NULL, 0, 'For an alternative to the extra layer of cinnamon, I had a reader tell me that he mixes in 1 TBS of cinnamon per batch of my rub for a nice flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72AB6518D0F3B4B8E70C7551A27688E', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle my rub on the bone side first.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA4D690C98F8734499AC2426D3ACB7F8', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', 1, NULL, NULL, 2, NULL, 0, 'Let the ribs sit there just a few minutes to let the rub absorb the oil and create a paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C45F5F905CD2434BB199FB900E67DE68', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', 2, NULL, NULL, 2, NULL, 0, 'Flip ribs over to meaty side up and repeat oil and rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B397C0F0281714EB291DAECFBA8603D', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', X'A44E22AFBC5A6C4ABEA007AA3AED57C4', 3, NULL, NULL, 2, NULL, 0, 'Let the ribs sit there a little longer while you go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75932B41CFD7BE4CACE34A3E4A82710E', X'211A030C596A2B40B5F8F3516598C14E', X'211A030C596A2B40B5F8F3516598C14E', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 240°F if possible. Be sure to use a tried and true thermometer to make sure that your smoker is running at the temperature that it says it is.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3596DF2F3944C34F88F9DB08C910D494', X'211A030C596A2B40B5F8F3516598C14E', X'211A030C596A2B40B5F8F3516598C14E', 1, NULL, NULL, 2, NULL, 0, 'Once your smoker is heated up and ready to smoke, it’s time to get those ribs on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0736CE3B0951494C8674CFF16FBAFA4C', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 0, NULL, NULL, 2, NULL, 0, 'Step 1');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F12DD23273F754CA62A17DD82E84CC5', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 1, NULL, NULL, 2, NULL, 0, 'Place the ribs bone side down if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4435028E8B514C4BA47750EDE00E9527', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 2, NULL, NULL, 2, NULL, 0, 'Let the ribs cook at about 240°F and with smoke for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E717AAE85696104EB5478FFF39C0BFC2', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 3, NULL, NULL, 2, NULL, 0, 'Step 2');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6877B1452AD19A45ACAAF5D739CA190B', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 4, NULL, NULL, 2, NULL, 0, 'At the end of 2 hours, wrap the ribs with foil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D45B07220B83AF46B5C7DC5BB62FA1C9', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 5, NULL, NULL, 2, NULL, 0, 'Do this carefully so as to not tear the foil. If you do happen to tear it, just double wrap it with another piece.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F229407128C23643B3A4DB994798B0FD', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 6, NULL, NULL, 2, NULL, 0, 'Place the wrapped ribs back into the 240°F smoker and you can even stack them all together if you need to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FD45B07B4D71D4C94FAEC1E5D1B9CDE', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 7, NULL, NULL, 2, NULL, 0, 'Let them cook in this wrapped state for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CF8CAD809D1F34989EC3BA3E836D1CE', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 8, NULL, NULL, 2, NULL, 0, 'Step 3');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8A0D02E3476684C8333F14778730CB2', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 9, NULL, NULL, 2, NULL, 0, 'With a total of 4 hours past, you now want to unwrap them again.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914AE8BBBA8DFB498120DC8ECFF4E9F5', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 10, NULL, NULL, 2, NULL, 0, 'They will be very soft on the outside and very tender on the inside, and this last step is to firm up the outside just a little before serving them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39C5EC2CA14D6B4399AFE4B48E3841CA', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 11, NULL, NULL, 2, NULL, 0, 'Remove the foil and place them back into the 240°F smoker for 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE4C5ED09FEB394D808F8BB1DA435937', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 12, NULL, NULL, 2, NULL, 0, 'Alternative for Step 3');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1171C1F658A4D4EABEE7D483AD1492F', X'1910393AB5C0894E891B13A5F8100DD7', X'1910393AB5C0894E891B13A5F8100DD7', 13, NULL, NULL, 2, NULL, 0, 'Throw them on a very hot grill and let the high heat give them a good char. Paint on some barbecue sauce if you like sticky ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88192C452AAC2D4880C3660D15DB853C', X'CC01A677FCE3954D913BC66EA8B7C7F5', X'CC01A677FCE3954D913BC66EA8B7C7F5', 0, NULL, NULL, 2, NULL, 0, 'Slice up the ribs and serve them');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'734073AD5C767B4A8173CC129454BF21', X'89FF97C278EB6745BDCF836CA9BFC2F7', X'89FF97C278EB6745BDCF836CA9BFC2F7', 0, NULL, NULL, 2, NULL, 0, 'What if I want to do spare ribs or St. Louis style spares? No problem. Follow the same basic process but add an extra hour to the first step. It will end up being 3-2-1.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFBC3A5ED0911C4286A63BAD6AF63F59', X'89FF97C278EB6745BDCF836CA9BFC2F7', X'89FF97C278EB6745BDCF836CA9BFC2F7', 1, NULL, NULL, 2, NULL, 0, 'Should I put liquid down in the foil during step 2? You can and many do but I found of late that I get better results with less steam. By leaving out the liquid, there is less steam and the rub stays intact better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'941E501E28C6484EBF210EE674197035', X'89FF97C278EB6745BDCF836CA9BFC2F7', X'89FF97C278EB6745BDCF836CA9BFC2F7', 2, NULL, NULL, 2, NULL, 0, 'Should I brine my ribs? I have liquid brined the ribs for about 6 hours and the results were very good. Dry brining using 1 TBS of kosher salt for the entire rack makes the meat too salty and “hammy” for my liking so I don’t recommend it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B91D4240E4AA41BEB77ECE1E76719A', X'89FF97C278EB6745BDCF836CA9BFC2F7', X'89FF97C278EB6745BDCF836CA9BFC2F7', 3, NULL, NULL, 2, NULL, 0, 'Not enough room. Is it ok to use a rib rack? If you have to use a rib rack due to space constraints, that is fine but I feel that the flat, bone side down is the best position since that allows the juices to pool on top of the meat and keeps the ribs more moist while they cook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D82DCA0B5AA38B40BC91B36F86475603', X'89FF97C278EB6745BDCF836CA9BFC2F7', X'89FF97C278EB6745BDCF836CA9BFC2F7', 4, NULL, NULL, 2, NULL, 0, 'Do I need to use a water pan? If your smoker came new designed to be used with a water pan, it is always a good idea to use it unless you are drying meat/making jerky.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9937B2D193BB0741B24B2D4EC044708F', X'8786A9D7949B5C4F8864777B7C976BEE', X'8786A9D7949B5C4F8864777B7C976BEE', 0, NULL, NULL, 2, NULL, 0, 'You can purchase the ham steaks already sliced into ½ inch pieces or you can do it yourself. I recommend purchasing them already sliced or asking your butcher to do it for you. Their meal slicer will make quick work of it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE0C1AE4B73DE0479BFBC23E54B6500C', X'229648840C393C4D91D30F23D13B072F', X'229648840C393C4D91D30F23D13B072F', 0, NULL, NULL, 2, NULL, 0, 'Lay the ham steaks in a Bradley rack or even a cookie sheet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'510E2E70B843AA4F9783218791857E7E', X'229648840C393C4D91D30F23D13B072F', X'229648840C393C4D91D30F23D13B072F', 1, NULL, NULL, 2, NULL, 0, 'Brush some cooking oil on the top side to help the rub to stick. You can also use yellow mustard if you are so inclined.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EE2AF7F22249542B431607A57FA5D0C', X'229648840C393C4D91D30F23D13B072F', X'229648840C393C4D91D30F23D13B072F', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle some of Jeff’s rub on the top side of the ham steaks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B868FE113090A9409013B52BA75EA8CB', X'229648840C393C4D91D30F23D13B072F', X'229648840C393C4D91D30F23D13B072F', 3, NULL, NULL, 2, NULL, 0, 'The ham steaks are now ready to go into the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3874FE5581C6944195F68548EB6AC00A', X'ECB7A80460B5424D90CE436436B7587B', X'ECB7A80460B5424D90CE436436B7587B', 0, NULL, NULL, 2, NULL, 0, 'Setup the smoker for cooking at about 225°F for about 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33272270CA5CB44D8DCCF5180E205E3F', X'ECB7A80460B5424D90CE436436B7587B', X'ECB7A80460B5424D90CE436436B7587B', 1, NULL, NULL, 2, NULL, 0, 'Place the Bradley racks with the ham steaks onto the smoker grate or lay the ham steaks onto the smoker grate (rub side up).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68154F37BF9D2C40A14A855C51D7DAB2', X'ECB7A80460B5424D90CE436436B7587B', X'ECB7A80460B5424D90CE436436B7587B', 2, NULL, NULL, 2, NULL, 0, 'Let the ham steaks cook smoke for about 30 minutes then spoon on some of the cranberry glaze (instructions below) and spread it out all over the top of the ham steaks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388CAE2E9CBDC043A46AFA0CD550B9C2', X'3D379ED447C96C48962C50C1E8EF6BB9', X'3D379ED447C96C48962C50C1E8EF6BB9', 0, NULL, NULL, 2, NULL, 0, 'Ingredients: 12 oz package of cranberries (fresh or frozen), 1 cup sugar, 1 cup water, 1-½ teaspoons orange peel (grated)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47C21856D282014F909D899414504628', X'3D379ED447C96C48962C50C1E8EF6BB9', X'3D379ED447C96C48962C50C1E8EF6BB9', 1, NULL, NULL, 2, NULL, 0, 'Place cranberries and sugar in a food processor or blender. Chop cranberries coarsely.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A68735C75F1D64D851744942A4B6C13', X'3D379ED447C96C48962C50C1E8EF6BB9', X'3D379ED447C96C48962C50C1E8EF6BB9', 2, NULL, NULL, 2, NULL, 0, 'Put sugar/cranberry mixture into a medium saucepan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E18B2B77DDA6E47A219FA4EDCF63029', X'3D379ED447C96C48962C50C1E8EF6BB9', X'3D379ED447C96C48962C50C1E8EF6BB9', 3, NULL, NULL, 2, NULL, 0, 'Add 1 cup of water and grated orange peel and bring to a boil stirring occasionally.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93222A8B0BDB3D46A436FB08908672DA', X'3D379ED447C96C48962C50C1E8EF6BB9', X'3D379ED447C96C48962C50C1E8EF6BB9', 4, NULL, NULL, 2, NULL, 0, 'Reduce heat and simmer stirring occasionally until mixture is reduced to about 2-1/3 cups (about 10-12 minutes).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6E9801F7D3C254CA541B3171EB9394F', X'3D379ED447C96C48962C50C1E8EF6BB9', X'3D379ED447C96C48962C50C1E8EF6BB9', 5, NULL, NULL, 2, NULL, 0, 'Allow glaze to cool slightly before using.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'395EC2E959DD154B9C48FF7836ECBBCD', X'3D379ED447C96C48962C50C1E8EF6BB9', X'3D379ED447C96C48962C50C1E8EF6BB9', 6, NULL, NULL, 2, NULL, 0, '(optional) I wanted the glaze to not be coarse and chunky so I put it back into the food processor until it was smooth.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'965C1846FC36734A8B1FC9648F3D11D6', X'A96E57B1CA82DD428DFE9AA1EDEEA126', X'A96E57B1CA82DD428DFE9AA1EDEEA126', 0, NULL, NULL, 2, NULL, 0, 'Continue cooking/smoking ham for an additional 30 minutes after you put the glaze on the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD0FCE582EF5854B8E5F1A5330AB2E6A', X'A96E57B1CA82DD428DFE9AA1EDEEA126', X'A96E57B1CA82DD428DFE9AA1EDEEA126', 1, NULL, NULL, 2, NULL, 0, 'Since the ham is already cooked, we are only adding additional smoke flavor and heating them to a good eating temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'766A228DBE303E4E99CEBBCDD2BAC882', X'A96E57B1CA82DD428DFE9AA1EDEEA126', X'A96E57B1CA82DD428DFE9AA1EDEEA126', 2, NULL, NULL, 2, NULL, 0, 'When 1 hour has expired, the double smoked ham steaks should be appropriately hot and nicely smoked.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36C5695C571D054DA124CCFB13D8BFCD', X'A96E57B1CA82DD428DFE9AA1EDEEA126', X'A96E57B1CA82DD428DFE9AA1EDEEA126', 3, NULL, NULL, 2, NULL, 0, 'Place the double smoked ham steaks under the broiler for a couple of minutes to caramelize the glaze just a little. Watch it carefully so that it does not burn.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'584B05A39A49DC4898241F5E15071994', X'F95B4A8F917345408A581432DA798C4F', X'F95B4A8F917345408A581432DA798C4F', 0, NULL, NULL, 2, NULL, 0, 'Brush my barbecue sauce on the ham about 30 minutes into the cook time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3DFEFB80F8C5340A784516139559B55', X'F95B4A8F917345408A581432DA798C4F', X'F95B4A8F917345408A581432DA798C4F', 1, NULL, NULL, 2, NULL, 0, 'Use your favorite jelly or jam on the ham about 30 minutes into the cook time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77FBA7EC6DFCEB4581EA0A92C37A02C8', X'F95B4A8F917345408A581432DA798C4F', X'F95B4A8F917345408A581432DA798C4F', 2, NULL, NULL, 2, NULL, 0, 'Honey and/or maple syrup is always great as a glaze on ham');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA55779040FAB74AA5CD61F1EE1A28BA', X'F95B4A8F917345408A581432DA798C4F', X'F95B4A8F917345408A581432DA798C4F', 3, NULL, NULL, 2, NULL, 0, 'Serve the ham right away or keep it covered and warm until dinner.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C30FCB48C278E4CBB66CC47EF91BA0F', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 0, NULL, NULL, 2, NULL, 0, 'First, make the brine');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C065EE5FA844B2409A785DEA3D51314F', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 1, NULL, NULL, 2, NULL, 0, 'Maple Turkey Brine Recipe: 1 gallons water, 1 cup of kosher salt (coarse), 12 oz of real maple syrup, 2 TBS of Jeff’s Rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDDB801794F5AD44B7AE1586181A248B', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 2, NULL, NULL, 2, NULL, 0, 'Place a quart of the water in a pot over medium heat and add the maple syrup and rub to the water to help it melt and mix together better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'201ED2856786B14592D4198D68B064CE', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 3, NULL, NULL, 2, NULL, 0, 'Stir for about 3-4 minutes then remove from the heat and let it cool to room temperature..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A949A25F8B79B47A8AA3D1D5468E17A', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 4, NULL, NULL, 2, NULL, 0, 'Put the 3 quarts of cool, unheated water into a gallon sized pitcher and mix in the salt. Stir until the salt has completely dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37C35E2F9F08E04B995F1557A49B990F', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 5, NULL, NULL, 2, NULL, 0, 'Add the heated mixture to the salted water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09CAC22758BDA54A96F17C23F56F423E', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 6, NULL, NULL, 2, NULL, 0, 'You need a food safe plastic/glass or other non-reactive container large enough to hold the turkey and enough brine to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E956880A04F3E4AAC39E37AF6E92F74', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 7, NULL, NULL, 2, NULL, 0, 'Remove the turkey from it’s packaging and remove any “gifts” that are stuffed down inside of the cavity.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EA30CED93E27F4AB42FAEAC34FD899E', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 8, NULL, NULL, 2, NULL, 0, 'Place the turkey in the brining container');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'605BBFBF6FCB8142AA7F87E2CF2812A4', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 9, NULL, NULL, 2, NULL, 0, 'Pour enough brine over the turkey to cover it. If the turkey tries to float, put a heavy plate on top of it to hold it under the water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD33AB1F4AF86A4480C3EC49262C31FD', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 10, NULL, NULL, 2, NULL, 0, 'I recommend using a smaller container if possible and placing the container in the fridge for optimum cooling. Add a bag or two of ice to help keep the water at less than 40°F if it will not fit in the fridge.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC3EAA42504D3641A5DBF6B1386B7931', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 11, NULL, NULL, 2, NULL, 0, 'Leave the turkey in the brine for 10-12 hours or overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63D5B54FEB7CFC488E03217224C86BE2', X'E0D9BC36CCB93B46B08E663A8A1109F6', X'E0D9BC36CCB93B46B08E663A8A1109F6', 12, NULL, NULL, 2, NULL, 0, 'When the turkey is finished brining, rinse it well under cold water to remove any residual salt on the surface of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FD3C51665DB634BA16C8FDDEF5CA68E', X'98CEC9EA6D5B4547B091BBAAA6255C99', X'98CEC9EA6D5B4547B091BBAAA6255C99', 0, NULL, NULL, 2, NULL, 0, 'Since we are going with a maple theme this year, it only seems fitting to use maple syrup as our base to help the rub to stick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5291E846F3ED9E4DB8AFAA9D373B45D2', X'98CEC9EA6D5B4547B091BBAAA6255C99', X'98CEC9EA6D5B4547B091BBAAA6255C99', 1, NULL, NULL, 2, NULL, 0, 'I placed the turkey in a baking pan to catch the mess.. yep, I don’t like cleanup!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DD1DF195D3BDA4C87ED3D319CD319B5', X'98CEC9EA6D5B4547B091BBAAA6255C99', X'98CEC9EA6D5B4547B091BBAAA6255C99', 2, NULL, NULL, 2, NULL, 0, 'Pour the maple syrup all over the turkey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05852201379C47438D17619CC6FC4E31', X'98CEC9EA6D5B4547B091BBAAA6255C99', X'98CEC9EA6D5B4547B091BBAAA6255C99', 3, NULL, NULL, 2, NULL, 0, 'Use a silicon brush or your hands to get it all over.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'070971CA9F19AA499FDA551A5EB2BA13', X'98CEC9EA6D5B4547B091BBAAA6255C99', X'98CEC9EA6D5B4547B091BBAAA6255C99', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff’s rub all over the turkey');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C13F1531ECDD42B86E68DE5961A084', X'98CEC9EA6D5B4547B091BBAAA6255C99', X'98CEC9EA6D5B4547B091BBAAA6255C99', 5, NULL, NULL, 2, NULL, 0, 'For a little extra flavor, work your hands down under the skin of the turkey and try to get some maple syrup and rub in there.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15EF97534DAC58488B45C10315E5552D', X'98CEC9EA6D5B4547B091BBAAA6255C99', X'98CEC9EA6D5B4547B091BBAAA6255C99', 6, NULL, NULL, 2, NULL, 0, 'At this point the turkey is ready to go in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81A5AF56F0D64D4E84AB2E42BC96A3B4', X'8AF85BD61F2D064EA00841D3A0DAD9E8', X'8AF85BD61F2D064EA00841D3A0DAD9E8', 0, NULL, NULL, 2, NULL, 0, 'You can use practically any smoker (even the grill) to cook the Thanksgiving turkey this year as long as you can maintain a range between about 220 and 250°F using indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B6D1156386B834FB6A3C1C2E1DCB1AC', X'8AF85BD61F2D064EA00841D3A0DAD9E8', X'8AF85BD61F2D064EA00841D3A0DAD9E8', 1, NULL, NULL, 2, NULL, 0, 'Preheat the smoker/grill and once it is maintaining about 240°F , you are ready to smoke!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F593CE56AACF4F4DAC4B266F0538748C', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 0, NULL, NULL, 2, NULL, 0, 'Place the turkey directly on the grate breast side up, or if you want to keep the smoker a little cleaner, you can leave the turkey on the baking sheet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49B81C59F2225B4FAFB766704CCA84C5', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 1, NULL, NULL, 2, NULL, 0, 'If you are using a smoker that has multiple levels, you can place the turkey on a Bradley rack to make it easy to transport it to the smoker and then back into the house once it’s finished. Multiple levels allow you to place a pan on a lower rack to catch the syrup and turkey juices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87D658F1BD03F74082896DCE2BBF7298', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 2, NULL, NULL, 2, NULL, 0, 'I used pecan wood for this turkey but almost any fruit wood or a mix of pecan and fruit wood such as apple, plum, or cherry, would be great.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C125055B8D0E1741B695A4F4D9763B5B', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 3, NULL, NULL, 2, NULL, 0, 'Let the turkey smoke for about 2 hours without touching it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'215C0B4DE066E74CB5015CE469CBB83D', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 4, NULL, NULL, 2, NULL, 0, 'At the 3 hour mark begin mopping the bird with the maple barbecue mop sauce every hour');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE2332E5CFC6404AA1F3DCD8D1ECB9E6', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 5, NULL, NULL, 2, NULL, 0, 'Maple Barbecue Mop Sauce: 6 oz maple syrup, 2 heaping TBS of Jeff’s original rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4D0A8DC539A06488717D42120EE69AB', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 6, NULL, NULL, 2, NULL, 0, 'Heat the maple syrup in the microwave then add the rub. Mix well then continuously mix while using.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A704E9558F83BF448FFA1366AEB0DBE4', X'C477F764BD43904A887C0BAA3E4ED0C5', X'C477F764BD43904A887C0BAA3E4ED0C5', 7, NULL, NULL, 2, NULL, 0, 'Use a silicon brush or basting mop to apply the mop sauce to the top, sides, legs and wings of the turkey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D9D0FC0B25E2C43B556DA2E73CD5736', X'991EF458138BAF49B121ABFCBBD37D5F', X'991EF458138BAF49B121ABFCBBD37D5F', 0, NULL, NULL, 2, NULL, 0, 'I highly recommend using a good digital probe meat thermometer to monitor the temperature of the turkey while it cooks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42BABFA79FADA4489EE95F03B6A2EEFB', X'991EF458138BAF49B121ABFCBBD37D5F', X'991EF458138BAF49B121ABFCBBD37D5F', 1, NULL, NULL, 2, NULL, 0, 'Turkey breast which is white meat cooks a little differently than the darker meat found in the legs and wings and it is important to find the happy medium between getting the two meats perfectly done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D5E0A671194364CB2C7120EB7074D42', X'991EF458138BAF49B121ABFCBBD37D5F', X'991EF458138BAF49B121ABFCBBD37D5F', 2, NULL, NULL, 2, NULL, 0, 'I usually go with the breast temperature and as long as both the white breast meat and the legs are at least 165°F , I call the turkey done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'672FB09D1D22F448B24C4B38B127162C', X'991EF458138BAF49B121ABFCBBD37D5F', X'991EF458138BAF49B121ABFCBBD37D5F', 3, NULL, NULL, 2, NULL, 0, 'I recommend 165°F simply because I believe that’s the optimum temperature based on my experience.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9D94883C14EE04A87B1375AEC7ECBDB', X'B2B801782EED134A87A72E78F828F863', X'B2B801782EED134A87A72E78F828F863', 0, NULL, NULL, 2, NULL, 0, 'I recommend allowing the turkey to rest after it is done cooking. During this time, the juices settle back down in the meat and are less prone to spurt out when you cut into it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49491341B542824AABAFB67FB9A10F2B', X'B2B801782EED134A87A72E78F828F863', X'B2B801782EED134A87A72E78F828F863', 1, NULL, NULL, 2, NULL, 0, 'When I go out to get the finished turkey, I usually take a large roasting pan lined with foil and a lid. The turkey goes from the smoker into the roasting pan and the lid is quickly placed on top to preserve the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE801CADE1A0A41B92785D40F407BD6', X'B2B801782EED134A87A72E78F828F863', X'B2B801782EED134A87A72E78F828F863', 2, NULL, NULL, 2, NULL, 0, 'When I get it into the house, I allow it to sit on the cabinet for about 20-30 minutes before removing/carving the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B20799DE6316E84DAA79665276353882', X'6AEABF9744E08F44A06BDA929E18DDE8', X'6AEABF9744E08F44A06BDA929E18DDE8', 0, NULL, NULL, 2, NULL, 0, 'I start by removing the legs and wings.This gives me good access to cut slices of the breast meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E15BCC92415A146850A96118C3D5A16', X'6AEABF9744E08F44A06BDA929E18DDE8', X'6AEABF9744E08F44A06BDA929E18DDE8', 1, NULL, NULL, 2, NULL, 0, 'I recommend placing the dark meat and white meat in separate piles so folks can get what they want without having to dig around for it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41654C429D7C9145A315D1C3A0203A4F', X'5A7B8229AC911241859BB8937C3958EA', X'5A7B8229AC911241859BB8937C3958EA', 0, NULL, NULL, 2, NULL, 0, 'Why breast side up? I sometimes cook the turkey breast side down for a short time then flip it over for the rest of the time, breast side up. This is not as important in smokers where the heat is really even and the heat is not moving from the bottom of the smoker to the top. In my experience, breast side up keeps the delicate breast meat the furthest from the heat and helps it to get done without any unintended blasts of heat. This also gives me the best access to mop and baste the meat with the maple barbecue mop sauce. All in all, I don’t think either way is a deal breaker. If you’ve had success with breast side down the entire time, then go for it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F570234DC3CFA419DE7FF2D59B0904F', X'5A7B8229AC911241859BB8937C3958EA', X'5A7B8229AC911241859BB8937C3958EA', 1, NULL, NULL, 2, NULL, 0, 'Can I stuff the turkey before smoking it? No. Smoking turkey at low temperatures makes it unsafe to block heat flow into the cavity of the turkey. Wait until the turkey is done then stuff it with the already cooked stuffing for table presentation. It is ok to place a few pieces of onion, apple, carrot, etc into the cavity as long as heat can travel freely into the cavity.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'046B96872D1F0641A39340015D31A5D5', X'5A7B8229AC911241859BB8937C3958EA', X'5A7B8229AC911241859BB8937C3958EA', 2, NULL, NULL, 2, NULL, 0, 'How do I reheat the turkey if I cook it ahead of time? It just so happened that I had to reheat the turkey used in this newsletter. It was finished about 4 hours before we were ready to eat so I placed it in a roasting pan with a good fitting lid and put the entire pan in the fridge. About one hour before dinner time, I put the lidded roasting pan with the turkey into the oven preheated to 350°F. Just before closing the oven door, I basted it once again with some leftover maple barbecue mop sauce. After about 45 minutes the turkey was up to 120 °F internal temperature so I turned it down to 250 °F where it stayed for another 30 minutes until we were ready to carve it. It was a good eating temperature and was just as juicy as it ever was.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36DA398260CAE143ADB5D7375E79867A', X'5A7B8229AC911241859BB8937C3958EA', X'5A7B8229AC911241859BB8937C3958EA', 3, NULL, NULL, 2, NULL, 0, 'Can I make the turkey ahead of time? Obviously, the best smoked turkey is going to be right out of the smoker the same day but if you have scheduling issues and must cook it ahead of time, you can certainly do that and it will still be the best turkey most folks have ever had. Ideally, you can make it less than 2-3 days before you need it and you can leave it in the fridge and simply reheat it the way that I did in the previous question. If it will be more than 3 days, it must be placed in the freezer. Thawing in the fridge is the best option and will take about 1 day for every 5 pounds of turkey. Once the turkey is thawed, place it in a pan with a lid. Add about 1 cup of water to the bottom of the pan. Make extra maple barbecue mop sauce and baste the turkey liberally just before heating it. Preheat the oven to 350 °F and place the pan of turkey, lid intact, into the oven for about 45 minutes to 1 hour or until it reaches a good eating temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25099BB9F5B5864B9C226C30877A7D6B', X'5A7B8229AC911241859BB8937C3958EA', X'5A7B8229AC911241859BB8937C3958EA', 4, NULL, NULL, 2, NULL, 0, 'How much turkey do I need to smoke? For the bone to meat ratio in smoking size turkeys around 12 lb, you should figure about 2 uncooked lbs per person which will give you a good size helping per person with a few leftovers. For a 12 person thanksgiving dinner, I would make (2) 12 lb turkeys.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'760DAC25543CD840A717A282E89D2936', X'A5BB5A355CD2334E8BB1BB81463E8745', X'A5BB5A355CD2334E8BB1BB81463E8745', 0, NULL, NULL, 2, NULL, 0, 'Ingredients: ½ gallon water, 1 quart buttermilk, ¾ cup coarse kosher salt, and ½ cup dark brown sugar (light will work).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5130522F6D507418101DF89A0C2712C', X'A5BB5A355CD2334E8BB1BB81463E8745', X'A5BB5A355CD2334E8BB1BB81463E8745', 1, NULL, NULL, 2, NULL, 0, 'Place the water into a gallon sized pitcher.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E496CFFF94B92F47ACB5D037319FD629', X'A5BB5A355CD2334E8BB1BB81463E8745', X'A5BB5A355CD2334E8BB1BB81463E8745', 2, NULL, NULL, 2, NULL, 0, 'Add the salt and stir until the salt dissolves');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3752A9944E06244A9DA7D0C22A172BCA', X'A5BB5A355CD2334E8BB1BB81463E8745', X'A5BB5A355CD2334E8BB1BB81463E8745', 3, NULL, NULL, 2, NULL, 0, 'Add the buttermilk and brown sugar and stir again for about 30 seconds to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09A79EF13E8AE244AE2204B93B40F7B0', X'A5BB5A355CD2334E8BB1BB81463E8745', X'A5BB5A355CD2334E8BB1BB81463E8745', 4, NULL, NULL, 2, NULL, 0, 'Set the brine aside while you get the turkey legs out of the package.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C9CCA3B7355C419EDB15620D572D8E', X'1F91FC4133E25C4DBF3439378FFF3526', X'1F91FC4133E25C4DBF3439378FFF3526', 0, NULL, NULL, 2, NULL, 0, 'Remove the turkey legs from the package and place them into a ziptop bag or a lidded plastic/glass container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAE7F0A33F81EB42A13165E2CAB054A0', X'1F91FC4133E25C4DBF3439378FFF3526', X'1F91FC4133E25C4DBF3439378FFF3526', 1, NULL, NULL, 2, NULL, 0, 'Pour enough brine over the turkey legs to cover them completely.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D3F367B6884DE4FB87501688A46270C', X'1F91FC4133E25C4DBF3439378FFF3526', X'1F91FC4133E25C4DBF3439378FFF3526', 2, NULL, NULL, 2, NULL, 0, 'Place a lid on the container then refrigerate the brining container for about 10 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78A068EAC24C264D83B44BB68B9316C1', X'1F91FC4133E25C4DBF3439378FFF3526', X'1F91FC4133E25C4DBF3439378FFF3526', 3, NULL, NULL, 2, NULL, 0, 'When the turkey legs are finished brining, remove them from the brine and rinse them really well under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90959B9B2837384A83DB6ECCC7C8FD32', X'D7993E53757D804EA8CBCC9A1E6B5754', X'D7993E53757D804EA8CBCC9A1E6B5754', 0, NULL, NULL, 2, NULL, 0, 'Drizzle olive oil over the drumsticks then spread it all over with your hands or a brush.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9001ACD9B90D04193965810BD0F1235', X'D7993E53757D804EA8CBCC9A1E6B5754', X'D7993E53757D804EA8CBCC9A1E6B5754', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff’s rub on all sides of the turkey drumsticks');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04220A404FC6E745B236ED00307DABD8', X'3007CB16EE214E45909248D89DE4FB85', X'3007CB16EE214E45909248D89DE4FB85', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225-240°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'458DBE040EBA4A47AAA6BAD1BD51C279', X'3007CB16EE214E45909248D89DE4FB85', X'3007CB16EE214E45909248D89DE4FB85', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BC9D00249E71F488DFAC6E6E21EA854', X'B324ABDF11ED5B44B0F8895ED59AC791', X'B324ABDF11ED5B44B0F8895ED59AC791', 0, NULL, NULL, 2, NULL, 0, 'Once the smoker is going and maintaining the proper temperature, place the turkey legs on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8B136D07D463F4E91AE298AE5CAAB33', X'B324ABDF11ED5B44B0F8895ED59AC791', X'B324ABDF11ED5B44B0F8895ED59AC791', 1, NULL, NULL, 2, NULL, 0, 'It is best to monitor the temperature using a digital probe meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76CA7A1B756E3F47BED6F728C385640C', X'B324ABDF11ED5B44B0F8895ED59AC791', X'B324ABDF11ED5B44B0F8895ED59AC791', 2, NULL, NULL, 2, NULL, 0, 'Once the turkey legs reach 165 °F in the thickest part, they are done and should be removed from the heat right away.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C52B39906CB041974B899FD501AC3C', X'6C893E7CCEFBAD4D9E182A22C3AE17F0', X'6C893E7CCEFBAD4D9E182A22C3AE17F0', 0, NULL, NULL, 2, NULL, 0, 'Immediately wrap the smoked turkey legs in foil individually and let them sit and rest for about 10 minutes before serving them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D4DC472D44E0141B497819938F1E9C8', X'7309D30810F79941B31EFA6C9A393726', X'7309D30810F79941B31EFA6C9A393726', 0, NULL, NULL, 2, NULL, 0, 'Where can I find the giant turkey legs? Unfortunately, I have not been successful at finding the really big ones like you see at state fairs and amusement parks. The folks who do have them, do not give up their suppliers so, for the purpose of use lowly backyarders, I think we are going to have to stick with the smaller ones found at our local supermarkets.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F96A93F0CE77F4997CC9B616EA10F49', X'7309D30810F79941B31EFA6C9A393726', X'7309D30810F79941B31EFA6C9A393726', 1, NULL, NULL, 2, NULL, 0, 'Should I dry the skin on the turkey legs? Some folks dry the skin (in the fridge) after brining them and before smoking them. This is to make the skin end up more crispy. I don’t normally administer this step but it does not hurt anything if you want to try it out. Simply brine the legs as usual, then pat them dry and place them on a rack. Let them air dry in the fridge for several hours before putting my rub on them and smoking them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6AFBBD6C3886942AF0A279A1DAF11B6', X'7309D30810F79941B31EFA6C9A393726', X'7309D30810F79941B31EFA6C9A393726', 2, NULL, NULL, 2, NULL, 0, 'Can these turkey legs be injected instead of brined? Of course you can, but, in my opinion, the flavor is not the same. When I eat a turkey leg, I am looking for a slightly salty and highly flavorful taste. In my opinion, injecting just does not do the job like brining does. The next time you do a batch, inject one of them and brine the rest. You will be able to see the difference and make a decision as to whether injected smoked turkey legs are for you.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CC4420388677645B8FECFB9B4FC826B', X'7309D30810F79941B31EFA6C9A393726', X'7309D30810F79941B31EFA6C9A393726', 3, NULL, NULL, 2, NULL, 0, 'Can the turkey legs be smoked at a hotter temperature? Turkey, like most other poultry, does not benefit greatly from low and slow cooking other than the fact that it gets more time in the smoke. If you need to smoke them faster, you can certainly do that just be aware that if you take it past about 260°F, the sugar in the rub will burn and char. I tend to do them lower to give them more smoke flavor and to make sure my rub maintains it’s great flavor without burning.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A637891E4831884CA91136744C0488AC', X'C7DE27BAAD55EC4DB5CA834F93638ED4', X'C7DE27BAAD55EC4DB5CA834F93638ED4', 0, NULL, NULL, 2, NULL, 0, 'Remove the beef from it’s package and give them a good rinse under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCEE8B17584A1140879C8497BCEB17DA', X'C7DE27BAAD55EC4DB5CA834F93638ED4', X'C7DE27BAAD55EC4DB5CA834F93638ED4', 1, NULL, NULL, 2, NULL, 0, 'Place the meat in a ziptop bag or other plastic/glass lidded container');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A7092F34C3E7141A0B1955C3FFD548D', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 0, NULL, NULL, 2, NULL, 0, '-1/2 cup orange juice');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8925A791C5C0D148902DE7579CDB31A4', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 1, NULL, NULL, 2, NULL, 0, '-1/2 cup worcestershire');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAD000DA76D133478C3997FBED9D7062', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 2, NULL, NULL, 2, NULL, 0, '-¼ cup soy sauce');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA9A5669C5B21A4FAF7BFEFF8C63CF85', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 3, NULL, NULL, 2, NULL, 0, '-4 cloves, minced garlic');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45DF2AB19D19664F86C33EED2612A4AF', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 4, NULL, NULL, 2, NULL, 0, '-2 TBS Jeff’s rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B3A004BCBA53A4D8A0DA9E9CE41AC68', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 5, NULL, NULL, 2, NULL, 0, '-2 TBS molasses');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B38883D91B391745B351D45B4F06E4C3', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 6, NULL, NULL, 2, NULL, 0, '-1 tsp cayenne');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E091446DBE5B43A8FD9F21D2904965', X'1FC339932E9FEE439921FCF20F6A8D69', X'1FC339932E9FEE439921FCF20F6A8D69', 7, NULL, NULL, 2, NULL, 0, 'Mix well and pour over beef country style ribs to cover');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA52E4AB8516404CADEDADDF521B380F', X'F034EEDE24D5C44E9264C2C0CC8E03AE', X'F034EEDE24D5C44E9264C2C0CC8E03AE', 0, NULL, NULL, 2, NULL, 0, 'Remove the meat from the marinade (do not rinse).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'806DD129815A5444AE7CEE7DCFE6F82E', X'F034EEDE24D5C44E9264C2C0CC8E03AE', X'F034EEDE24D5C44E9264C2C0CC8E03AE', 1, NULL, NULL, 2, NULL, 0, 'Coat the meat with yellow mustard');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9108D492065E1E4D97A1BEA10A32155E', X'F034EEDE24D5C44E9264C2C0CC8E03AE', X'F034EEDE24D5C44E9264C2C0CC8E03AE', 2, NULL, NULL, 2, NULL, 0, 'Pour some rub onto the meat and massage it into all sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'906CA92B06FC534E9938AD91A9B6D529', X'F034EEDE24D5C44E9264C2C0CC8E03AE', X'F034EEDE24D5C44E9264C2C0CC8E03AE', 3, NULL, NULL, 2, NULL, 0, 'Lay the seasoned meat onto a bradley rack or cookie sheet for easy transport out to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F11D010E32CE444A30001C35A5268BB', X'E0AF574D85951841A2BFF7315DBE9610', X'E0AF574D85951841A2BFF7315DBE9610', 0, NULL, NULL, 2, NULL, 0, 'Prepare your smoker for cooking at about 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F731CF557D23E14687C650E649270CFF', X'E0AF574D85951841A2BFF7315DBE9610', X'E0AF574D85951841A2BFF7315DBE9610', 1, NULL, NULL, 2, NULL, 0, 'Fill the water pan with water if your smoker has one and have enough smoking wood to last about 2 hours. I use and recommend pecan if you have it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F027809678BFFC4F9958A268622D4F31', X'E0AF574D85951841A2BFF7315DBE9610', X'E0AF574D85951841A2BFF7315DBE9610', 2, NULL, NULL, 2, NULL, 0, 'Let the smoker preheat to 225 °F before placing the meat in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE68503B10901846937C15233193F90E', X'07378A14DCFDAF41A838C5A6F667E9F8', X'07378A14DCFDAF41A838C5A6F667E9F8', 0, NULL, NULL, 2, NULL, 0, 'I have been smoking this cut for long enough to know that every location and butcher does them a little different. Interestingly enough, they all tend to look similar but some of them are cut from the chuck roast while others are cut from more tender parts. For this reason, I recommend you monitor the internal temperature of these and check the tenderness once they reach about 135°F which is a good medium rare. If they happen to be tender at 135 then they are ready to eat. If they are still tough as shoe leather, then you likely have some made from the chuck roast and they will need to be cooked to about 185 or so. From my research, it seems that most of them are chuck and will take 4-5 hours of smoky heat to tenderize them but I did want to caution you to make sure.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98EE66D42265B44C84FCE136D50EDEF5', X'6E28DF7A9951E84CBF80C73571D79979', X'6E28DF7A9951E84CBF80C73571D79979', 0, NULL, NULL, 2, NULL, 0, 'Place the Bradley rack on the smoker grate or place the meat directly on the smoker grate and let the smoking begin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BF15413F8823340B66D31488CF56665', X'6E28DF7A9951E84CBF80C73571D79979', X'6E28DF7A9951E84CBF80C73571D79979', 1, NULL, NULL, 2, NULL, 0, 'Be sure to use a digital probe meat thermometer to monitor the temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54BE54625C7B6A4F98C9008D3CBD55F7', X'6E28DF7A9951E84CBF80C73571D79979', X'6E28DF7A9951E84CBF80C73571D79979', 2, NULL, NULL, 2, NULL, 0, 'When the meat reaches about 135 °F (about 1 to 1.5 hours), check it for tenderness by sticking a toothpick or thermometer probe into it. If it feels tender, remove a piece of the meat and cut/taste it to see where you are as described above in the “Important Note“.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BCE9C979B1D4043A67228C47BBB8833', X'6E28DF7A9951E84CBF80C73571D79979', X'6E28DF7A9951E84CBF80C73571D79979', 3, NULL, NULL, 2, NULL, 0, 'If it is done, then all is well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C9BADE57A862C48ABEBB9821A6B1116', X'6E28DF7A9951E84CBF80C73571D79979', X'6E28DF7A9951E84CBF80C73571D79979', 4, NULL, NULL, 2, NULL, 0, 'If it is not done, continue to smoke cook the meat until it is tender to your liking. This can take as many as 4-5 hours in some cases.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'886A2F663B5EF14485C7D242EBF3CDB4', X'6E28DF7A9951E84CBF80C73571D79979', X'6E28DF7A9951E84CBF80C73571D79979', 5, NULL, NULL, 2, NULL, 0, 'I recommend wrapping them in foil for a couple of hours to help them tenderize more.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92282C179602D34C9428768CD4553657', X'C6E8E63888534E4EBB46009E18FE1CA8', X'C6E8E63888534E4EBB46009E18FE1CA8', 0, NULL, NULL, 2, NULL, 0, 'Once my beef ribs were done cooking in the smoker, I immediately placed them on a very hot grill to get a little sear on the outside and, in my opinion, it was worth it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48ECBE2A2522024C9274AE793B9F8765', X'C6E8E63888534E4EBB46009E18FE1CA8', X'C6E8E63888534E4EBB46009E18FE1CA8', 1, NULL, NULL, 2, NULL, 0, 'Brush some of my sauce on the outside and let them cook for about 4 minutes on each side putting them at the 2 o’clock position first for about 2 minutes then at the 10 o’clock position for a couple of minutes to get perfect 90 degree sear marks on the outside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD9492C6ABCDB044A348C6CE3408F39E', X'C6E8E63888534E4EBB46009E18FE1CA8', X'C6E8E63888534E4EBB46009E18FE1CA8', 2, NULL, NULL, 2, NULL, 0, 'Flip them over and repeat the 2 and 10 position to get the good looking and tasty sear on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABBB2FA139DBAF478C37AB371C805E0A', X'E1210D0A7E6D114AB38590C678D86270', X'E1210D0A7E6D114AB38590C678D86270', 0, NULL, NULL, 2, NULL, 0, 'Serve the beef CSR ribs immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B9B9C7E8C13E8468E5AAD40594DA40D', X'CBB8884905EDD440B6797B7FF0BEED08', X'CBB8884905EDD440B6797B7FF0BEED08', 0, NULL, NULL, 2, NULL, 0, 'Do I have to marinate the meat? No. The marinating step is completely optional. The ribs will be good even if you don’t but I do highly recommend that you do if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C5D62F2098ACF4BB5365AA4B4A7D3DB', X'CBB8884905EDD440B6797B7FF0BEED08', X'CBB8884905EDD440B6797B7FF0BEED08', 1, NULL, NULL, 2, NULL, 0, 'If it becomes obvious that the meat will need longer cooking times, how long and when to wrap in foil? When the meat reaches about 160°F is a great time to wrap the meat in foil or optionally, place it in a foil pan covered with foil. Leave the meat wrapped in the foil until it reaches about 185°F or when you determine that it is tender to your liking. Then place the meat on the grill (as described above) for a few minutes to add some great flavor and color to the outside. This is also a great time to brush with some sauce if you so desire.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59CB9A32C58D5D4C8F849984EDBA2925', X'CBB8884905EDD440B6797B7FF0BEED08', X'CBB8884905EDD440B6797B7FF0BEED08', 2, NULL, NULL, 2, NULL, 0, 'How do you recommend serving the meat? I have been known to just give each person an entire “rib” but I also like to slice it thin and place it on top of a vegetable salad.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A2FD5ECB0AB914896AF32E657596316', X'312A4399FDC6B94CA052CA39B6412390', X'312A4399FDC6B94CA052CA39B6412390', 0, NULL, NULL, 2, NULL, 0, 'Peel the bacon apart');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00A4AB7653FFB54C90ECC1D019E178BC', X'312A4399FDC6B94CA052CA39B6412390', X'312A4399FDC6B94CA052CA39B6412390', 1, NULL, NULL, 2, NULL, 0, 'Layer it into a lidded container and drizzle maple syrup and rub between each layer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F18F413E52C4834EB9E3611AF745969B', X'312A4399FDC6B94CA052CA39B6412390', X'312A4399FDC6B94CA052CA39B6412390', 2, NULL, NULL, 2, NULL, 0, 'Spread the syrup out with a brush before sprinkling on the rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58620B3A7418E043BD30709F79ED0CD4', X'73DD802E3475904FA08B1B6A11D819ED', X'73DD802E3475904FA08B1B6A11D819ED', 0, NULL, NULL, 2, NULL, 0, 'Once all of the bacon is layered into the container with maple syrup and rub, cover it and place it in the fridge overnight to marinate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'282C18AE947A4242A8FBF35F9A9E932F', X'47E86FE5FBA950429653DAD78DDE5609', X'47E86FE5FBA950429653DAD78DDE5609', 0, NULL, NULL, 2, NULL, 0, 'To smoke the bacon and turn it into bacon candy, lay it on a jerky rack or onto bradley racks. Be sure to leave a little space between each piece to allow the smoke easy access and to make sure they don’t stick together.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBBF1B835D042049B8A101754ED6ACC3', X'47E86FE5FBA950429653DAD78DDE5609', X'47E86FE5FBA950429653DAD78DDE5609', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle more rub and brown sugar onto the bacon just before it goes into the smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D96B40ADF149B64585A0895B1022A1C2', X'25731DE7AC7F734E9D721A3004E1FE25', X'25731DE7AC7F734E9D721A3004E1FE25', 0, NULL, NULL, 2, NULL, 0, 'Prepare your smoker for cooking at about 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B73B09DC36747F449B8EF5D57A393FCF', X'25731DE7AC7F734E9D721A3004E1FE25', X'25731DE7AC7F734E9D721A3004E1FE25', 1, NULL, NULL, 2, NULL, 0, 'Let the smoker heat up to 225°F before placing the bacon in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CCFDC9ED940814BAB4FEAA1D3362104', X'25731DE7AC7F734E9D721A3004E1FE25', X'25731DE7AC7F734E9D721A3004E1FE25', 2, NULL, NULL, 2, NULL, 0, 'If you need further help with your smoker, try one of these links:');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76613E5E9464F242A97E22F68F054ACC', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', 0, NULL, NULL, 2, NULL, 0, 'Place the bacon into the smoker and let it smoke away for about 1 hour over indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'090857D78E304C4D90E9D5494FB5403B', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', 1, NULL, NULL, 2, NULL, 0, 'At the end of an hour, take it out of the smoker and flip the bacon pieces over to the other side. Once again, make sure the pieces do not touch.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0857DE1AE068F48A4BCD126DD06A017', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', 2, NULL, NULL, 2, NULL, 0, 'Brush on more maple syrup and brown sugar');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3240D67F3E24934DB92A30F452CA8685', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', 3, NULL, NULL, 2, NULL, 0, 'Smoke for an additional hour');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46794FB0FE41C345B82893F09126CEA6', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', 4, NULL, NULL, 2, NULL, 0, 'Remove to parchment paper right away and let it cool for about 30 minutes before eating it for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0101DFF18CA6E42A5892101914E7138', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', X'E3226CB0FC4A7D4BB4ADECF1E73464C8', 5, NULL, NULL, 2, NULL, 0, 'Break the bacon up into 3rds or halves and let the frenzy begin!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32E07B4CC1A9D24EB87153653503780B', X'5B531CCE4950A64BAA3275CB051AC856', X'5B531CCE4950A64BAA3275CB051AC856', 0, NULL, NULL, 2, NULL, 0, 'Remove the beef back ribs from the packaging and give them a good rinse under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'965906A9726039439BAE20F5E700A133', X'5B531CCE4950A64BAA3275CB051AC856', X'5B531CCE4950A64BAA3275CB051AC856', 1, NULL, NULL, 2, NULL, 0, 'Lay the ribs on the cutting board and remove any large clumps of fat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14AA1C24F4F7A24B884292367E3F87C7', X'5B531CCE4950A64BAA3275CB051AC856', X'5B531CCE4950A64BAA3275CB051AC856', 2, NULL, NULL, 2, NULL, 0, 'Turn the rack of beef ribs over and remove the membrane on the boney side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11E299012907B545B695677B8BC5E2D3', X'5B531CCE4950A64BAA3275CB051AC856', X'5B531CCE4950A64BAA3275CB051AC856', 3, NULL, NULL, 2, NULL, 0, 'Get ahold of the thick layer of plastic-like membrane by prying up on it with your finger, a butter knife or whatever works best for you.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'924C0C59480E5A479025BFECC0B984E2', X'5B531CCE4950A64BAA3275CB051AC856', X'5B531CCE4950A64BAA3275CB051AC856', 4, NULL, NULL, 2, NULL, 0, 'Once you have it started, get ahold of it with a paper towel and pull it off.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01FFB55D7754DE4AB517596EDD5086C1', X'5B531CCE4950A64BAA3275CB051AC856', X'5B531CCE4950A64BAA3275CB051AC856', 5, NULL, NULL, 2, NULL, 0, 'I don’t usually season the boney side of beef ribs.. just doesn’t seem to be justified but feel free to do so if you wish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C35BD9398F7F147A3875AF6F4C317CB', X'5B531CCE4950A64BAA3275CB051AC856', X'5B531CCE4950A64BAA3275CB051AC856', 6, NULL, NULL, 2, NULL, 0, 'Flip them over to meat side up and proceed with the seasoning process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'842C30DA59F83E459FE3B47AA3165244', X'31429508DEF42E4C98A438DAF756B797', X'31429508DEF42E4C98A438DAF756B797', 0, NULL, NULL, 2, NULL, 0, 'Please note that you can apply the mustard and rub right before the ribs go into the smoker and it will be fine but if you have time to do this the night before and let them marinate in the rub for several hours, the ribs will be even better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'962ACF02FCD6674AB15B80F83716B36D', X'31429508DEF42E4C98A438DAF756B797', X'31429508DEF42E4C98A438DAF756B797', 1, NULL, NULL, 2, NULL, 0, 'Apply some yellow mustard to the meat side of the beef ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA982659A83A03409721FDD3B1198F5E', X'31429508DEF42E4C98A438DAF756B797', X'31429508DEF42E4C98A438DAF756B797', 2, NULL, NULL, 2, NULL, 0, 'Rub the mustard into the ribs with your hands and don’t forget the sides and any crevices down between the rib bones.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8D83256F0FB8B4185774B11143643F4', X'31429508DEF42E4C98A438DAF756B797', X'31429508DEF42E4C98A438DAF756B797', 3, NULL, NULL, 2, NULL, 0, 'Put a tablespoon or two of Jeff’s original rub onto the meaty side of the ribs and massage it in with the mustard to create a paste that is extremely flavorful. This paste not only tastes amazing, but it creates the perfect crust on the outside of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4630DFFAAA47204883DE51F97EF4EC18', X'31429508DEF42E4C98A438DAF756B797', X'31429508DEF42E4C98A438DAF756B797', 4, NULL, NULL, 2, NULL, 0, 'Wrap the ribs in plastic wrap and place them in the fridge overnight or for at least 4 hours to allow some of the flavor of the rub to get into the top layer of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD2F4DF970704C4E9868333D05EF2A54', X'31429508DEF42E4C98A438DAF756B797', X'31429508DEF42E4C98A438DAF756B797', 5, NULL, NULL, 2, NULL, 0, 'Note: I recommend placing the wrapped ribs into a cookie sheet or pan to prevent any runaway leaks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E46B74764178D94FA735E75382351ED2', X'31429508DEF42E4C98A438DAF756B797', X'31429508DEF42E4C98A438DAF756B797', 6, NULL, NULL, 2, NULL, 0, 'The next morning, remove the ribs from the fridge, unwrap them from the plastic and lay them in a Bradley rack or cookie sheet in preparation for transporting them out to the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E8FCC2F05219648BDDD37E4FBE19097', X'BE2316C951EA3541898CC060F55F2CB4', X'BE2316C951EA3541898CC060F55F2CB4', 0, NULL, NULL, 2, NULL, 0, 'Prepare your smoker for cooking at about 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB6A4CEE46A372428CC3B97C4BD9C87E', X'BE2316C951EA3541898CC060F55F2CB4', X'BE2316C951EA3541898CC060F55F2CB4', 1, NULL, NULL, 2, NULL, 0, 'If your smoker has one, fill the water pan with water .');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF631A3312C1EF4AA70F07B1BB5BEC4E', X'BE2316C951EA3541898CC060F55F2CB4', X'BE2316C951EA3541898CC060F55F2CB4', 2, NULL, NULL, 2, NULL, 0, 'Let the smoker heat up to 225 °F before placing the meat in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ED546B2B9D0A74E9C1D5E28C7240BC5', X'EFB2D95C1C746949A51276CCE900FABD', X'EFB2D95C1C746949A51276CCE900FABD', 0, NULL, NULL, 2, NULL, 0, 'Place the Bradley rack in the smoker or lay the beef back ribs directly on the smoker grate bone side down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED94C61F08E211438DBCF79F0E415490', X'EFB2D95C1C746949A51276CCE900FABD', X'EFB2D95C1C746949A51276CCE900FABD', 1, NULL, NULL, 2, NULL, 0, 'I recommend a good hearty smoke like mesquite, hickory or pecan but you can use any smoking wood that you have available and they will be fine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB50207F0FE894A8B1F2AD4EB279586', X'EFB2D95C1C746949A51276CCE900FABD', X'EFB2D95C1C746949A51276CCE900FABD', 2, NULL, NULL, 2, NULL, 0, 'I like to apply smoke for the entire time to replicate the flavor of a wood burning smoker but if you do not like a more subtle smoke flavor, it is ok to smoke them for less time and finish with just heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'775933E5097CCD43942B9B904D1D1777', X'EFB2D95C1C746949A51276CCE900FABD', X'EFB2D95C1C746949A51276CCE900FABD', 3, NULL, NULL, 2, NULL, 0, 'Let the beef ribs cook until they are tender. (This usually happens at around 185-190 °F and can take anywhere from 4-6 hours or even more depending on variables such as weather, how often you raise/open the lid, and the starting meat temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9901A6AFEE5C184995383AD3394305C2', X'D8AFD517B991144ABCFE20ECBB7724BF', X'D8AFD517B991144ABCFE20ECBB7724BF', 0, NULL, NULL, 2, NULL, 0, 'Let the smoked beef back ribs rest for about 10 minutes, then slice them up and serve to your family and guests.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81168DA51963084DB0FDC0ED8B80B750', X'9A194C065D717A4CBF2E7033F83EFB5C', X'9A194C065D717A4CBF2E7033F83EFB5C', 0, NULL, NULL, 2, NULL, 0, 'Put ¼ cup of kosher salt and 1 tablespoon of Jeff’s rub (purchase recipes here) in a plastic, glass or other non-reactive container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D12948A0A616E2458F870D1ED473D3D0', X'9A194C065D717A4CBF2E7033F83EFB5C', X'9A194C065D717A4CBF2E7033F83EFB5C', 1, NULL, NULL, 2, NULL, 0, 'Pour 1 quart of apple juice over the salt and rub and mix well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10FDBD3BEBDE9D4E8290D4C20B4F2E7E', X'9A194C065D717A4CBF2E7033F83EFB5C', X'9A194C065D717A4CBF2E7033F83EFB5C', 2, NULL, NULL, 2, NULL, 0, 'Note: The salt will dissolve, the rub will not.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'448D07AC6ADB9F4B91A26CB8449015F2', X'9A194C065D717A4CBF2E7033F83EFB5C', X'9A194C065D717A4CBF2E7033F83EFB5C', 3, NULL, NULL, 2, NULL, 0, 'Set the brine aside while you get the tenderloins ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1499BD85F6DB5F4A8BD082B190BEA593', X'18FA83FF5E1831478159AB975CEC5F11', X'18FA83FF5E1831478159AB975CEC5F11', 0, NULL, NULL, 2, NULL, 0, 'Most packages of pork tenderloins include (2) of them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97FA9228A0D0B14AB3753F935121851F', X'18FA83FF5E1831478159AB975CEC5F11', X'18FA83FF5E1831478159AB975CEC5F11', 1, NULL, NULL, 2, NULL, 0, 'Remove them from the package, rinse them with cold water and pat them dry with a paper towel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5772BA589EB14E44B3CF619F67505FFB', X'18FA83FF5E1831478159AB975CEC5F11', X'18FA83FF5E1831478159AB975CEC5F11', 2, NULL, NULL, 2, NULL, 0, 'To help make sure they cook evenly, trim off the tails and anything that looks irregular so that the ends are square and the thickness is consistent from end to end.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3A4C8CDD65A244093BC46CD128B5E76', X'18FA83FF5E1831478159AB975CEC5F11', X'18FA83FF5E1831478159AB975CEC5F11', 3, NULL, NULL, 2, NULL, 0, 'The pieces trimmed off are seasoned like everything else and cooked separately. Chef’s treats!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87F25FB15C2DA043B0A9BC3372A5CF5E', X'18FA83FF5E1831478159AB975CEC5F11', X'18FA83FF5E1831478159AB975CEC5F11', 4, NULL, NULL, 2, NULL, 0, 'The tenderloins are then placed into a plastic bowl, zip top bag or other brining container.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E80527B2A99E924DB97F828411027A60', X'18FA83FF5E1831478159AB975CEC5F11', X'18FA83FF5E1831478159AB975CEC5F11', 5, NULL, NULL, 2, NULL, 0, 'Cover the tenderloins with the apple juice brine that you made earlier then place a lid on the container and put it in the fridge for at least 4 hours but I recommend overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2831AC7C5196704EA9CDF03BFD5E6858', X'18FA83FF5E1831478159AB975CEC5F11', X'18FA83FF5E1831478159AB975CEC5F11', 6, NULL, NULL, 2, NULL, 0, 'Once the pork tenderloins are finished brining, remove them from the brine and rinse them really well under cold water. This removes any excess salt that might have accumulated on the outside of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6215385ACF59D48B940EF43E09F6B21', X'B8992BFEA7261F4FAF8331D6BCC33977', X'B8992BFEA7261F4FAF8331D6BCC33977', 0, NULL, NULL, 2, NULL, 0, 'Apply a little yellow mustard and about a teaspoon of rub per tenderloin. Rub it all in together to create a nice paste on the outside of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A71C1F081BC8DA4992FE10F6F8A47D4B', X'5A6D73DC6EBF2947BAE660D91E64CCEB', X'5A6D73DC6EBF2947BAE660D91E64CCEB', 0, NULL, NULL, 2, NULL, 0, 'This is optional but I do highly recommend it as it affects the finished product in a big way.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DC30F1AB0B28445BF91E946229B020A', X'5A6D73DC6EBF2947BAE660D91E64CCEB', X'5A6D73DC6EBF2947BAE660D91E64CCEB', 1, NULL, NULL, 2, NULL, 0, 'You will notice that the tenderloins are sort of oblong and flat on the top and bottom.. we want them to be round so that they will cook more evenly and make better looking slices on the plate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20735B17CC572948904C7D5DFA265A37', X'5A6D73DC6EBF2947BAE660D91E64CCEB', X'5A6D73DC6EBF2947BAE660D91E64CCEB', 2, NULL, NULL, 2, NULL, 0, 'This is easy to do using butchers twine about every 2 inches along the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC7987349439E04DB8A9C3319778D92F', X'5A6D73DC6EBF2947BAE660D91E64CCEB', X'5A6D73DC6EBF2947BAE660D91E64CCEB', 3, NULL, NULL, 2, NULL, 0, 'Lay the pork tenderloins on a smoking plank but you can also use a Bradley rack or you can place them directly on the smoker grate if you want to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA895F1B48FB394F98D3D91982B8E965', X'5A6D73DC6EBF2947BAE660D91E64CCEB', X'5A6D73DC6EBF2947BAE660D91E64CCEB', 4, NULL, NULL, 2, NULL, 0, 'The plank does not infuse as much smoke into the meat as it does on a grill due to the lower temperature but it does make a great presentation.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF43DABE590B21488D3D64A9CF69CC96', X'5A6D73DC6EBF2947BAE660D91E64CCEB', X'5A6D73DC6EBF2947BAE660D91E64CCEB', 5, NULL, NULL, 2, NULL, 0, 'Leave the tied up tenderloins on the counter while you go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0CDC61DDD07A64CAD2EB8EBD0415E6B', X'15FA0A49DB5A7F4DAE8E156F790174C9', X'15FA0A49DB5A7F4DAE8E156F790174C9', 0, NULL, NULL, 2, NULL, 0, 'Prepare your smoker for cooking at about 225°F with indirect heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'928211F60C173F4BBFC32B80944C9F9B', X'15FA0A49DB5A7F4DAE8E156F790174C9', X'15FA0A49DB5A7F4DAE8E156F790174C9', 1, NULL, NULL, 2, NULL, 0, 'Fill the water pan with water if your smoker has one and have enough smoking wood (apple recommended) to last about 1.5 to 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EC017181740D94A87865C7CF06CDBDA', X'15FA0A49DB5A7F4DAE8E156F790174C9', X'15FA0A49DB5A7F4DAE8E156F790174C9', 2, NULL, NULL, 2, NULL, 0, 'Let the smoker preheat to 225 °F before placing the meat in the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18321ECACEE20D42885FA8D72E000BF8', X'6B13E9D647AF074F81BE3061DBFA4347', X'6B13E9D647AF074F81BE3061DBFA4347', 0, NULL, NULL, 2, NULL, 0, 'Place the pork tenderloins in the smoker directly on the grate or use a plank or Bradley rack as mentioned earlier.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E65D93C01F0F0444AE1CD00E9650F696', X'6B13E9D647AF074F81BE3061DBFA4347', X'6B13E9D647AF074F81BE3061DBFA4347', 1, NULL, NULL, 2, NULL, 0, 'Keep the smoke going the entire time if possible and let the pork cook until it reaches an internal temperature of 145 °F in the center of the meat as measured by a digital probe meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5AB0639460B954981AA8EB91D245FC6', X'6B13E9D647AF074F81BE3061DBFA4347', X'6B13E9D647AF074F81BE3061DBFA4347', 2, NULL, NULL, 2, NULL, 0, 'Note: spray the outside surface of the meat with apple juice about every 30 minutes but do it quickly to minimize heat loss. If your smoker has a difficult time recovering when opened, you should probably skip this step.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5F63B68973B0F44BF31F63705975F5B', X'6B13E9D647AF074F81BE3061DBFA4347', X'6B13E9D647AF074F81BE3061DBFA4347', 3, NULL, NULL, 2, NULL, 0, 'Serve it Up');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D438A6108A070545BAE3DEAFEDC33684', X'6B13E9D647AF074F81BE3061DBFA4347', X'6B13E9D647AF074F81BE3061DBFA4347', 4, NULL, NULL, 2, NULL, 0, 'When the pork reaches 145 °F, remove it from the smoker and let it sit for about 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C78B4DC32741443B72A8FC144B01C21', X'6B13E9D647AF074F81BE3061DBFA4347', X'6B13E9D647AF074F81BE3061DBFA4347', 5, NULL, NULL, 2, NULL, 0, 'Be sure to remove the butcher’s twine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85D7CB4969626F40908CB13E62126E02', X'F53EB3FB011F3D47B87BB0A9D9BE4133', X'F53EB3FB011F3D47B87BB0A9D9BE4133', 0, NULL, NULL, 2, NULL, 0, 'Make a batch of apple barbecue sauce by mixing equal parts of Jeff’s barbecue sauce with apple juice.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F16B0CFC274AB4E988C65BBEB214366', X'F53EB3FB011F3D47B87BB0A9D9BE4133', X'F53EB3FB011F3D47B87BB0A9D9BE4133', 1, NULL, NULL, 2, NULL, 0, 'I used 1/3 cup of apple juice and 1/3 cup of barbecue sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70BB4D754A724D48A8E626D75F9035F7', X'A50B03132D95E74A829C70D2A7E2A923', X'A50B03132D95E74A829C70D2A7E2A923', 0, NULL, NULL, 2, NULL, 0, 'These are great as an entree with a couple of vegetables but they also work well as a slider.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859A59D481FBA845B966FB0762706F8D', X'A50B03132D95E74A829C70D2A7E2A923', X'A50B03132D95E74A829C70D2A7E2A923', 1, NULL, NULL, 2, NULL, 0, 'Use apple sauce instead of mustard as a base for the rub to switch things up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AFE488D54A48442BB7641E48AA45EA4', X'A50B03132D95E74A829C70D2A7E2A923', X'A50B03132D95E74A829C70D2A7E2A923', 2, NULL, NULL, 2, NULL, 0, 'Do not overcook the pork. The USDA now says that 145 °F is completely safe for whole cuts of pork (unground).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3B8E241521B744A87F974176DE8BB2C', X'A50B03132D95E74A829C70D2A7E2A923', X'A50B03132D95E74A829C70D2A7E2A923', 3, NULL, NULL, 2, NULL, 0, 'Since the pork is brined, be sure to use a rub like mine that has very little salt. If you use a store-bought rub it will probably be way too salty.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01667E6A358EE04CAF05901CF3CCBED3', X'D7A8182A4263834E81F328EA470DF2CA', X'D7A8182A4263834E81F328EA470DF2CA', 0, NULL, NULL, 2, NULL, 0, 'Place the 2 TBS of butter into a large pan or skillet over medium heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EA950D161675E4388BE6F1BFABE773C', X'D7A8182A4263834E81F328EA470DF2CA', X'D7A8182A4263834E81F328EA470DF2CA', 1, NULL, NULL, 2, NULL, 0, 'Let the butter melt.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F3ED34E64D63B42BEED7D1AEE5E2448', X'D7A8182A4263834E81F328EA470DF2CA', X'D7A8182A4263834E81F328EA470DF2CA', 2, NULL, NULL, 2, NULL, 0, 'Add the green onions, celery, carrots, onions, and garlic to the melted butter. Let it cook for about 4-6 minutes stirring occasionally.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99390368A095AE48B1D9E8F9293476D8', X'D7A8182A4263834E81F328EA470DF2CA', X'D7A8182A4263834E81F328EA470DF2CA', 3, NULL, NULL, 2, NULL, 0, 'Once the carrots get slightly soft, (won’t take long if you grated them) add the 1/3 cup of Jeff’s barbecue sauce to the vegetables and mix together well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2282C978A38724FA41A7F0015D6D6AB', X'D7A8182A4263834E81F328EA470DF2CA', X'D7A8182A4263834E81F328EA470DF2CA', 4, NULL, NULL, 2, NULL, 0, 'Note: After about 4 minutes, if the carrots are not soft, cover the vegetables with a lid for a couple of minutes to help speed things along.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'403AC8A81E371446A78AAC6CA2817A69', X'D7A8182A4263834E81F328EA470DF2CA', X'D7A8182A4263834E81F328EA470DF2CA', 5, NULL, NULL, 2, NULL, 0, 'Set the vegetable/sauce mixture aside to cool for about 10-15 minutes. It will need to be room temperature or less before adding it to the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9CD6125ABF92D4999A4B7C0251E2ECF', X'0B152DF048C2E84B9A23311DE3479BE8', X'0B152DF048C2E84B9A23311DE3479BE8', 0, NULL, NULL, 2, NULL, 0, 'Place 1.5 lbs of ground chuck (80/20) and ¾ lb of ground breakfast sausage (Hot) into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0BF057949F17543BA2DACEE438BE94D', X'0B152DF048C2E84B9A23311DE3479BE8', X'0B152DF048C2E84B9A23311DE3479BE8', 1, NULL, NULL, 2, NULL, 0, 'Add the slightly beaten eggs, bread crumbs, chopped parsley, Jeff’s rub, and sautéed vegetables to the mixing bowl and mix the meat and other ingredients together until combined.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0508F4DDB977E740A0330CE08516555B', X'0B152DF048C2E84B9A23311DE3479BE8', X'0B152DF048C2E84B9A23311DE3479BE8', 2, NULL, NULL, 2, NULL, 0, 'After it’s mixed well, form into a loaf and set it out on the cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72F71B8BF84B9942B848FAD872C1E51C', X'DFB8B1846B48D54B8ECEEBF8F230BEFB', X'DFB8B1846B48D54B8ECEEBF8F230BEFB', 0, NULL, NULL, 2, NULL, 0, 'Separate the loaf into 6 equal portions and place each portion into an area of the large size muffin pan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9446F912680C984EA3BABF01E8FE6052', X'DFB8B1846B48D54B8ECEEBF8F230BEFB', X'DFB8B1846B48D54B8ECEEBF8F230BEFB', 1, NULL, NULL, 2, NULL, 0, 'Press the meat into place and flatten the top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB6FDBD329863B4A8E1E24D09753FF9F', X'DFB8B1846B48D54B8ECEEBF8F230BEFB', X'DFB8B1846B48D54B8ECEEBF8F230BEFB', 2, NULL, NULL, 2, NULL, 0, 'Cover the top of the muffin pan with plastic wrap and place in the fridge for at least 2 hours or overnight to firm it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89E3432D990AFE47B73A18BF38B38992', X'180474691653D34CAFDB80852A06F296', X'180474691653D34CAFDB80852A06F296', 0, NULL, NULL, 2, NULL, 0, 'After 2 hours or the next morning, remove the muffins from the muffin pan and arrange them on a Bradley rack or cookie sheet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEED1FC5FF8D38408693F4770F4B599D', X'180474691653D34CAFDB80852A06F296', X'180474691653D34CAFDB80852A06F296', 1, NULL, NULL, 2, NULL, 0, 'Note: if you have trouble getting them out, use a butter knife along the sides to help loosen them up. You could also line each hole with foil or wax paper to make it easier to get the meatloaf out.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88A5D3ED4F6A6346A212D2DFA8577D67', X'7741EB159C66584A8C8857FC762B136F', X'7741EB159C66584A8C8857FC762B136F', 0, NULL, NULL, 2, NULL, 0, 'Setup your smoker or cooker for cooking indirect at about 225°F using your electric, charcoal, gas or wood smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0635983B9946749A314BC656445D74E', X'7741EB159C66584A8C8857FC762B136F', X'7741EB159C66584A8C8857FC762B136F', 1, NULL, NULL, 2, NULL, 0, 'If your smoker came with a water pan, be sure to use it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D15F76FC494E794EB681D0D17D907AE0', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', 0, NULL, NULL, 2, NULL, 0, 'Place the meatloaf muffins into the smoker directly on the smoker grate or to make it easy, use the Bradley rack and just lay the entire rack on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4642055C0F5454FAC088B0199DC5CBB', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', 1, NULL, NULL, 2, NULL, 0, 'Maintain 225 °F of the entire time and keep the smoke going for at least 1 hour but for better smoke flavor, just keep the smoke going for the entire time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9087318BEC410B4B97C0F93352679F34', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', 2, NULL, NULL, 2, NULL, 0, 'About 30 minutes before the meatloaf is finished or at about 150°F, brush a layer of Jeff’s barbecue sauce (purchase recipes here) all over the top and sides of the meatloaf muffins.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D04D6412FB872419EA22D538E9D7FA9', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', X'6BBFD1274317BF4F9DBFC4A7FE7C4651', 3, NULL, NULL, 2, NULL, 0, 'The meatloaf is done when it reaches 160 °F in the center.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D69DC4CA25A8214986ADC57AB9B70817', X'B60133BE5E0B6E4ABA57E57135F43843', X'B60133BE5E0B6E4ABA57E57135F43843', 0, NULL, NULL, 2, NULL, 0, 'When the meatloaf is done cooking, serve immediately with mashed potatoes, corn, mixes vegetables, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'848EE6ACECAE0844A02B0233504A6AD0', X'AE9DE78E13E5A74CA9485ECEAB2E2C40', X'AE9DE78E13E5A74CA9485ECEAB2E2C40', 0, NULL, NULL, 2, NULL, 0, 'Lay the chicken on a piece of plastic wrap then cover it with another piece of plastic wrap. This is to keep the mess to a minimum.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481670977BBD664EB09DFE00051E549B', X'AE9DE78E13E5A74CA9485ECEAB2E2C40', X'AE9DE78E13E5A74CA9485ECEAB2E2C40', 1, NULL, NULL, 2, NULL, 0, 'Use a meat hammer or other heavy object to pound the chicken breast a little bit.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF5C1A0D34E80242B1437B19B2491E7F', X'01DF61696274AF4BA9C2E272FF934DC3', X'01DF61696274AF4BA9C2E272FF934DC3', 0, NULL, NULL, 2, NULL, 0, 'Brush or spoon a little olive oil onto the chicken breasts and sprinkle a generous amount of Jeff’s Rub onto the top and bottom of the chicken breasts.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33BEA17540B38D479F51245C59E93B99', X'563FFFEB09F66F4789EBB72BED3DC551', X'563FFFEB09F66F4789EBB72BED3DC551', 0, NULL, NULL, 2, NULL, 0, 'Once seasoned, place the chicken breasts into a ziptop bag, press out the air and place the bag into the fridge overnight or for at least 4 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB8C073F95C7DD49939E72F370F924CD', X'563FFFEB09F66F4789EBB72BED3DC551', X'563FFFEB09F66F4789EBB72BED3DC551', 1, NULL, NULL, 2, NULL, 0, 'After 4 hours or the next morning, remove the chicken breasts from the fridge and place them on a Bradley rack or cookie sheet to make it easy to transport them out to the grill or smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44EBA7F531C2194C88A7A98677A38ADA', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 0, NULL, NULL, 2, NULL, 0, 'Light 1 or 2 of the burners on a gas grill and leave 1 or 2 of the burners unlit (3-4 burner gas grills)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82B3BF5C4B4BCA4A8F518B42E484E57A', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 1, NULL, NULL, 2, NULL, 0, 'Place wood chips or chunks wrapped in foil over the flame to create smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77340874690CF344895D9B46FCE3EF53', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 2, NULL, NULL, 2, NULL, 0, 'Maintain 225°F by manipulating the setting of the burners on the hot side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C711D80CC098D44782738B15E900F7AE', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 3, NULL, NULL, 2, NULL, 0, 'Place the chicken breasts on the cool side of the grill for about 45 minutes or until they reach about 125°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81A34C92B7C4414FB976BD1628325FF3', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 4, NULL, NULL, 2, NULL, 0, 'Sauce both sides of the chicken with my beer bbq sauce recipe (½ cup of Jeff''s barbecue sauce and ½ cup of beer mixed together well)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5757E157EF3474439797C4CAFB46A11C', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 5, NULL, NULL, 2, NULL, 0, 'Keep the smoke going for the entire time to ensure great smoked flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77FAF01EBDD20642B5EB25A42F4DA1F1', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 6, NULL, NULL, 2, NULL, 0, 'After 45 minutes or 125°F move the chicken breast to the hot side of the grill.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DD6C5DD376173478D30AE676C4F3DF4', X'DBDFC5413AC2F9488C3BE79612E97F30', X'DBDFC5413AC2F9488C3BE79612E97F30', 7, NULL, NULL, 2, NULL, 0, 'Turn the grill to high heat and cook for an additional 7 minutes on each side or until the chicken reaches 165 °F in the center as measured by a meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A08AC6BAD67F6F4497D49A4DD668FC55', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 0, NULL, NULL, 2, NULL, 0, 'For a charcoal grill, you use the same basic concept, using charcoal.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF3C7507B212647B45B9D14410C12F2', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 1, NULL, NULL, 2, NULL, 0, 'Place an aluminum pan in the center of the grill filled with water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23F805869941664CA473A205CD2D1BCA', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 2, NULL, NULL, 2, NULL, 0, 'Pile the charcoal on both sides to create hot zones on the sides with a cooler center.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2A4AC23F38C2748932264BF484BA63F', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 3, NULL, NULL, 2, NULL, 0, 'You can also just pile all of the charcoal on one side of the grill with a water pan on the other side and get the same effect.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA2F39CB3354814CA6FB85A02F46D783', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 4, NULL, NULL, 2, NULL, 0, 'Lay wood chunks on the charcoal or very near the charcoal to get some smoking action.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6528D178679CBE45BB1C99103515B6AE', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 5, NULL, NULL, 2, NULL, 0, 'Place the chicken breasts on the cool zone of the grill for about 45 minutes or until they reach about 125°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC1FFE64B4A61E4A9C3D471B164D62B7', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 6, NULL, NULL, 2, NULL, 0, 'Sauce both sides of the chicken with my beer bbq sauce recipe (½ cup of Jeff''s barbecue sauce and ½ cup of beer mixed together well)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BA1E71D3C839840B166E9265DBBA0E5', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 7, NULL, NULL, 2, NULL, 0, 'Keep the smoke going for the entire time to ensure great smoked flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24E6E8A1CD667E499F39DCB06922155D', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 8, NULL, NULL, 2, NULL, 0, 'After 45 minutes or 125°F move the chicken breast to the hot zone of the charcoal grill.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7B6D91352F59B4688ACF5B68299AAAD', X'0C9BC00559ECF34096CB743321396778', X'0C9BC00559ECF34096CB743321396778', 9, NULL, NULL, 2, NULL, 0, 'Cook for an additional 5-7 minutes on each side or until the chicken reaches 165 °F in the center as measured by a meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BD777C543CC3C46B41E3214C3C840C6', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', 0, NULL, NULL, 2, NULL, 0, 'Place the chicken breasts directly on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2A3417212B5964FAE3E276C6DCBA110', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', 1, NULL, NULL, 2, NULL, 0, 'Smoke the chicken for about 1.5 hours (depending on thickness) or until it reaches 165°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5641179C2DC7B4469614EC8356D4A4DC', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', 2, NULL, NULL, 2, NULL, 0, 'About 20 minutes before it gets finished, sauce both sides of the chicken with my beer bbq sauce recipe (½ cup of Jeff''s barbecue sauce and ½ cup of beer mixed together well)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24B3FE12AC318247B5EB75BACCC0C582', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', X'BE27DBCDDD8D8E4CBD1AE9C02C3E477B', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E85EDEC63A229B4AAB48870BA23F7220', X'8E566FCDC10BE34E95D338C09324CC5D', X'8E566FCDC10BE34E95D338C09324CC5D', 0, NULL, NULL, 2, NULL, 0, 'I found these ribs already trimmed and membrane removed and they were at a great price. If you have regular spare ribs, you will need to remove the membrane and trim off the bone on leading edge so that you are only left with actual ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EE1E072D2BD244185F69427C7A1B8BA', X'4B547017EBA02A40A463D49AB7B34E1A', X'4B547017EBA02A40A463D49AB7B34E1A', 0, NULL, NULL, 2, NULL, 0, 'Brush some oil onto each side of the ribs to help the rub to stick. I often use mustard, honey, and an assortment of other things as well to serve this purpose.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A77257B92B5C646B7945F6177C58DD2', X'4B547017EBA02A40A463D49AB7B34E1A', X'4B547017EBA02A40A463D49AB7B34E1A', 1, NULL, NULL, 2, NULL, 0, 'Apply Jeff’s rub (purchase recipes here) to the ribs generously.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6019BA159DB5A14C9F36D629CAE6B92B', X'4B547017EBA02A40A463D49AB7B34E1A', X'4B547017EBA02A40A463D49AB7B34E1A', 2, NULL, NULL, 2, NULL, 0, 'Wait until it has that “wet” look, flip the ribs over and apply oil and rub to the meaty side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46E023E4FE68D740A65C24394278686F', X'4B547017EBA02A40A463D49AB7B34E1A', X'4B547017EBA02A40A463D49AB7B34E1A', 3, NULL, NULL, 2, NULL, 0, 'Once both sides are oiled and rubbed with Jeff’s rub, my original naked rib rub recipe, wrap the ribs in plastic wrap and allow to marinate for 8-10 hours in the fridge.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEB2F75C952D2E46AEE7031F344BBD05', X'4B547017EBA02A40A463D49AB7B34E1A', X'4B547017EBA02A40A463D49AB7B34E1A', 4, NULL, NULL, 2, NULL, 0, 'The next morning, pull the ribs out of the fridge and remove the plastic.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D333BE19269E446973C9409E2ECB908', X'458D419795866A48BE53AEFC2DBB55B6', X'458D419795866A48BE53AEFC2DBB55B6', 0, NULL, NULL, 2, NULL, 0, 'Make the butter injection right before you pull the ribs out of the fridge.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73B9BBAAA7730E498AD75A8D5FE40972', X'458D419795866A48BE53AEFC2DBB55B6', X'458D419795866A48BE53AEFC2DBB55B6', 1, NULL, NULL, 2, NULL, 0, 'Run about a cup of my rub through the coffee grinder to powder it. This helps it to not get stuck in the small holes in the injector.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914BAE633632214EBFF5CDBEEC9B1EA5', X'458D419795866A48BE53AEFC2DBB55B6', X'458D419795866A48BE53AEFC2DBB55B6', 2, NULL, NULL, 2, NULL, 0, 'Mix a heaping tablespoon of the powdered rub into a stick (¼ lb) of melted butter and mix thoroughly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BDC6888CC2C35419FA78B9C3A2C7F26', X'664F3363C78F2949AFD216D2F9221C80', X'664F3363C78F2949AFD216D2F9221C80', 0, NULL, NULL, 2, NULL, 0, 'Lay the marinated ribs on a piece of plastic wrap then cover the ribs with a piece of plastic wrap. This prevents the butter from becoming a geyser that can shoot across the kitchen suddenly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'098F0E6A8B38DD4F97C8708C0A466DBC', X'664F3363C78F2949AFD216D2F9221C80', X'664F3363C78F2949AFD216D2F9221C80', 1, NULL, NULL, 2, NULL, 0, 'Fill the injector with hot melted spicy butter and push the needle into the meat between each bone so that the tip is about 1 inch into the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F244A790BBEC63478E9BA7855BFA1CE3', X'664F3363C78F2949AFD216D2F9221C80', X'664F3363C78F2949AFD216D2F9221C80', 2, NULL, NULL, 2, NULL, 0, 'Depress the plunger as you move the needle left and right at different angles to ensure that the butter is getting in there real good.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ABCAB5512F9994994DE50FD0F3CA7A9', X'664F3363C78F2949AFD216D2F9221C80', X'664F3363C78F2949AFD216D2F9221C80', 3, NULL, NULL, 2, NULL, 0, 'Slowly pull it out as you continue to depress the plunger.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DF0DAF45BF5B344BE39D2A4DCE568ED', X'664F3363C78F2949AFD216D2F9221C80', X'664F3363C78F2949AFD216D2F9221C80', 4, NULL, NULL, 2, NULL, 0, 'Keep injecting butter until you see it seeping out somewhere.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AFC379CD5D1704CB25F9876843B9049', X'664F3363C78F2949AFD216D2F9221C80', X'664F3363C78F2949AFD216D2F9221C80', 5, NULL, NULL, 2, NULL, 0, 'Flip the ribs around and do the same on the opposite edge at about 1 inch into the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF1E97D15A7E6C488D16F73D1AEE1BBD', X'664F3363C78F2949AFD216D2F9221C80', X'664F3363C78F2949AFD216D2F9221C80', 6, NULL, NULL, 2, NULL, 0, 'If you need more butter, make another batch so you don’t have to scrimp on it. Give it all it will take!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B43D34DB33DDF498CFD7B5B5E411A69', X'ED93EC075380994BB1154F40A8D7B884', X'ED93EC075380994BB1154F40A8D7B884', 0, NULL, NULL, 2, NULL, 0, 'Spread the butter that seeped out all over the top of the ribs and sprinkle on a little more of my rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6E947130093B54686AEC736F8A2B898', X'EEB4B362449E8A4398F3168A235B8EDB', X'EEB4B362449E8A4398F3168A235B8EDB', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat your smoker to between 225 and 240°F and make sure you have at least 3-4 hours of smoking wood available.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255FF0167D8A6943A4601B8AF4B49AF4', X'DF25E190FD2ECA4F9A68B91F0421FC57', X'DF25E190FD2ECA4F9A68B91F0421FC57', 0, NULL, NULL, 2, NULL, 0, 'I wanted to use a Bradley rack (I love those things in case you haven’t noticed) so I cut the rack of spare ribs in half as you’ll notice above. This makes them very easy to maneuver, flip over, wrap, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6DED17EF91DAE43A845F6ACFEA223EE', X'DF25E190FD2ECA4F9A68B91F0421FC57', X'DF25E190FD2ECA4F9A68B91F0421FC57', 1, NULL, NULL, 2, NULL, 0, 'Place the ribs on the smoker grate bone side down and let the smoking begin!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4200FA4A8EEC2418D41A4E7372D1878', X'D9AF2617E53C8C418A0020A6678597F9', X'D9AF2617E53C8C418A0020A6678597F9', 0, NULL, NULL, 2, NULL, 0, 'Place the ribs on the smoker bone side down, as mentioned above, and let them smoke their little porky hearts out for about 4 hours at 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'378D7818CE8EF845900EE837C62B637F', X'2510BA4A8EF1AD4F932C217D5B8C36AA', X'2510BA4A8EF1AD4F932C217D5B8C36AA', 0, NULL, NULL, 2, NULL, 0, 'Wrap the ribs in a double layer of heavy duty foil, not real tight but tight enough so they can steam real nice inside and find their tender side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B80B3122880F0E449828ADAA8D73581E', X'2510BA4A8EF1AD4F932C217D5B8C36AA', X'2510BA4A8EF1AD4F932C217D5B8C36AA', 1, NULL, NULL, 2, NULL, 0, 'Place the wrapped ribs back into the smoker for 1 hour at the same exact temperature.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DD8C760E6BD654A8A295F18BC40F18B', X'CF68B5845ADF9F4297CC23D595DA099A', X'CF68B5845ADF9F4297CC23D595DA099A', 0, NULL, NULL, 2, NULL, 0, 'After the ribs have tenderized for 1 hour, remove the foil and place the ribs back into the smoker, just like in step one, for one additional hour. This additional hour, resets the crust, brings a little more color to the outside of the ribs and can even give them an extra bit of smoke should you choose to add smoke during this time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42ED0C26A7CA6A4C9DA6BE84736E4F85', X'9F440C65240C3745BFEF87EE022AE098', X'9F440C65240C3745BFEF87EE022AE098', 0, NULL, NULL, 2, NULL, 0, 'When the ribs are finished, bring them in, let them rest for about 10 minutes then slice them up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA0769683029834CAE54E78694283EA2', X'9F440C65240C3745BFEF87EE022AE098', X'9F440C65240C3745BFEF87EE022AE098', 1, NULL, NULL, 2, NULL, 0, 'I recommend placing them bone side up to slice since that allows you to see the direction of the bones making it easy to slice between them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6100E48A7CC71941A8F6B5A2F88D2FEB', X'AA9C6D483FF4BD43BB449C4C6CE18F5D', X'AA9C6D483FF4BD43BB449C4C6CE18F5D', 0, NULL, NULL, 2, NULL, 0, 'Lay bacon into a flat cookie sheet then place another cookie sheet on top of that to hold the bacon flat while it cooks.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E7C070530A8B5459710E99DADDE5562', X'AA9C6D483FF4BD43BB449C4C6CE18F5D', X'AA9C6D483FF4BD43BB449C4C6CE18F5D', 1, NULL, NULL, 2, NULL, 0, 'Cook bacon until is about 75% done and is still flexible enough to wrap around the salmon filets.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'091B22326143464BA92BC973176DF6C0', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 0, NULL, NULL, 2, NULL, 0, 'Lay the salmon filet on your cutting board.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC907E8DB9BA8D4EB06D41B19DAE335B', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 1, NULL, NULL, 2, NULL, 0, 'Pull out any pin bones that you find using a clean pair of needle nosed pliers or tweezers.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7501ABB4B0A7EC49969A2F718DCDC464', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 2, NULL, NULL, 2, NULL, 0, 'To find the pin bones, simply run your fingers along the fish and you''ll feel the little bones sticking up just above the flesh. They are usually located right along the side of the thickest part of the fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AFE2F006747F242B47B2FE209E7F7C2', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 3, NULL, NULL, 2, NULL, 0, 'Cut the fish into strips width wise. Use a piece of bacon to measure how wide it needs to be.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32D76276A2E5AF4C9381D75052E8718E', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 4, NULL, NULL, 2, NULL, 0, 'Once the strips are cut, use a sharp knife to remove the skin from each piece.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E7A014109C29446A09A485469EE66D2', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 5, NULL, NULL, 2, NULL, 0, 'Roll (2) pieces in a plate of Jeff''s rub to coat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1CE0234C91814C97124EF93149089F', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 6, NULL, NULL, 2, NULL, 0, 'Lay the pieces on top of each other. Thick part on top of the thin part of the fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97CE2CB495CD364E903274A2170BCC47', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 7, NULL, NULL, 2, NULL, 0, 'Roll it together to form a circle.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0AB31893096964D965BD9E595C1C74E', X'B17328F828D5F84EB0F7882283535F84', X'B17328F828D5F84EB0F7882283535F84', 8, NULL, NULL, 2, NULL, 0, 'While holding it securely, wrap a piece of bacon around it and secure with a toothpick or a piece of butchers twine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B56F75C6BE14B4A9E6BC6373751A0D1', X'082CF4AF72B6754DB43596EFBDAB474B', X'082CF4AF72B6754DB43596EFBDAB474B', 0, NULL, NULL, 2, NULL, 0, 'Set up your electric, charcoal, gas or wood smoker for about an hours worth of cooking at 225-240°F and a nice fruit wood or something that is not too strong such as alder, cherry, apple, pecan, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2C6858917F8E34CB6742B194B7BA5C3', X'082CF4AF72B6754DB43596EFBDAB474B', X'082CF4AF72B6754DB43596EFBDAB474B', 1, NULL, NULL, 2, NULL, 0, 'Place the salmon filets on the smoker grate and let them smoke cook for about 1 hour or until they reach 145 °F in the center.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A08E7C20F81C884A9142474AB7FF3BE7', X'C1BE45BB5FA6D24F853DAF908655EAB7', X'C1BE45BB5FA6D24F853DAF908655EAB7', 0, NULL, NULL, 2, NULL, 0, 'Just as soon as the salmon is finished cooking, serve them immediately for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD5B918CE19EF5428AD373F16A86A5B5', X'7B4B82781FAA224695C393BD86687D31', X'7B4B82781FAA224695C393BD86687D31', 0, NULL, NULL, 2, NULL, 0, 'Get a good hold on skin at the larger thigh end and pull it toward the leg and it will come clean off.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1883C7ACECAE14EB23D416AE5B9C25A', X'9D0097978A0F2F49B51E50568A65167F', X'9D0097978A0F2F49B51E50568A65167F', 0, NULL, NULL, 2, NULL, 0, 'Fill a pot with your potatoes and carrots and cover them with water. Bring the water to a boil and let it boil for about 5 minutes for small carrots and potatoes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F8498AECD98444E8815695CFD10DD1A', X'9D0097978A0F2F49B51E50568A65167F', X'9D0097978A0F2F49B51E50568A65167F', 1, NULL, NULL, 2, NULL, 0, 'Pour the potatoes and carrots into a colander and run cold water over them to stop the cooking process.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A61BFEC618E7B74B9DD82C5989794535', X'D3320AC1631C804C9261254F0E854810', X'D3320AC1631C804C9261254F0E854810', 0, NULL, NULL, 2, NULL, 0, 'Place the skinned chicken onto the center of a piece of foil that is about 18x18 inches square.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD8554A320E8F646A046C5C7B35BD6DB', X'D3320AC1631C804C9261254F0E854810', X'D3320AC1631C804C9261254F0E854810', 1, NULL, NULL, 2, NULL, 0, 'Pile some sausage pieces, potatoes, carrots, a quarter of an onion and a half ear of corn around the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16D4FF80D4AE7A4B9F22B3C9C9361C4C', X'D3320AC1631C804C9261254F0E854810', X'D3320AC1631C804C9261254F0E854810', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle some of my Texas style rub (recipe included when you purchase the recipes for my original rub and sauce) onto the chicken, sausage and vegetables.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'132DDD114D682D488FF7A3055B1F9448', X'D3320AC1631C804C9261254F0E854810', X'D3320AC1631C804C9261254F0E854810', 3, NULL, NULL, 2, NULL, 0, 'Now sprinkle about 2-3 tablespoons of Jeff''s original rub onto the top of everything for amazing flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'842C8EB644F4C24B9F214A68E3CB9F81', X'D3320AC1631C804C9261254F0E854810', X'D3320AC1631C804C9261254F0E854810', 4, NULL, NULL, 2, NULL, 0, 'Add a tablespoon of butter on top..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F0CE0FD3308B244BADFC51B2EC4D99F', X'D3320AC1631C804C9261254F0E854810', X'D3320AC1631C804C9261254F0E854810', 5, NULL, NULL, 2, NULL, 0, 'Roll up the edges of the foil around the meat and vegetables to form a bowl shape.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC571B52A4C7F438AE42FF8C82FF166', X'D3320AC1631C804C9261254F0E854810', X'D3320AC1631C804C9261254F0E854810', 6, NULL, NULL, 2, NULL, 0, 'Be sure to leave it open for now to allow the smoke in');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9E408317BBFB04FBA518C63F13A32CF', X'9D56B8FD8B586E458FD65D5C2480F9B2', X'9D56B8FD8B586E458FD65D5C2480F9B2', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 240°F although it would not be unheard of to take it on up to 275°F or so if you need to get them done faster. Chicken handles high heat quite well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA37F78E0EE99D46899D320619AE0BD8', X'9D56B8FD8B586E458FD65D5C2480F9B2', X'9D56B8FD8B586E458FD65D5C2480F9B2', 1, NULL, NULL, 2, NULL, 0, 'Be sure to have enough Pecan or other smoking wood for about 1 hour of smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85D2720666E36A4A96828758DF5A1552', X'9D56B8FD8B586E458FD65D5C2480F9B2', X'9D56B8FD8B586E458FD65D5C2480F9B2', 2, NULL, NULL, 2, NULL, 0, 'Place the foil `bowls` of chicken, sausage and vegetables into the smoker and let them smoke away for about 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'706FB080D9A3604384968FF3DA22B3D2', X'9D56B8FD8B586E458FD65D5C2480F9B2', X'9D56B8FD8B586E458FD65D5C2480F9B2', 3, NULL, NULL, 2, NULL, 0, 'After one hour, pull the foil together at the top and pinch it closed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC012C82F5D012408C7D7709DA336F43', X'9D56B8FD8B586E458FD65D5C2480F9B2', X'9D56B8FD8B586E458FD65D5C2480F9B2', 4, NULL, NULL, 2, NULL, 0, 'Place a probe from a digital probe meat thermometer into one of the pieces of chicken before closing it up so you will know when it is done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79F04312C551784FAFB93DD8D50FE291', X'9D56B8FD8B586E458FD65D5C2480F9B2', X'9D56B8FD8B586E458FD65D5C2480F9B2', 5, NULL, NULL, 2, NULL, 0, 'I shoot for 170°F on these.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8C4BC4F13CAE643B4FEF0B16794D5FD', X'059A80D422D31E4CAC5C03E95003F952', X'059A80D422D31E4CAC5C03E95003F952', 0, NULL, NULL, 2, NULL, 0, 'Have plates already setting on the table with a single paper towel on the plate (to protect the plate from grease from the smoker grates)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26CB7A30EA3CF7428001476404CAA09A', X'059A80D422D31E4CAC5C03E95003F952', X'059A80D422D31E4CAC5C03E95003F952', 1, NULL, NULL, 2, NULL, 0, 'When the chicken is done, lay a foil packet on each plate and call Dinner!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ED5D9B294B8EF47AEA4CD4C76C755AA', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 0, NULL, NULL, 2, NULL, 0, 'Separate the ground chuck into pieces that are about ¼ lb each and, without overworking the meat, make a ball out of one of those ¼ lb pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AD1411C42FB43408DE2AE170CD491E2', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 1, NULL, NULL, 2, NULL, 0, 'Lay the ball on a piece of wax paper and carefully form it into a patty that is about 4-5 inches in diameter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7774726A03A74EAB82C912E37FA84E', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 2, NULL, NULL, 2, NULL, 0, 'Lay 2 hotdogs on top of the burger patty with a little space between them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37328A16F21A04BB7A78978F47E75E9', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle shredded cheese on top of the hot dogs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B937285FC93EE4292291BBD4406F772', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 4, NULL, NULL, 2, NULL, 0, 'Add a nice healthy portion of my rub for amazing flavor');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'319533A9F498274EB2F5CD50237245F2', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 5, NULL, NULL, 2, NULL, 0, 'Top it with another patty made exactly like the first one');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1089F7BACD4D8F44AD3F632785DF251C', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 6, NULL, NULL, 2, NULL, 0, 'Pinch the edges together to seal in all the goodness');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFF373C76C8F5342AE839E00B39E58EE', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 7, NULL, NULL, 2, NULL, 0, 'Season the top of the burgers with Jeff''s rub or a mixture of Pepper and salt');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A470092699BFE543ADCD4EE731E5628A', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 8, NULL, NULL, 2, NULL, 0, 'Place the burgers onto a Bradley rack and you are ready to smoke ''em!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79DE774276F8C041AB57ABE86502DC83', X'B3B1AD8E773D8D41B547A5364562986B', X'B3B1AD8E773D8D41B547A5364562986B', 9, NULL, NULL, 2, NULL, 0, 'If you want more smoked burger dogs, double, triple or even quadruple the recipe.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D824FEFA131F2240A83699F7292D8F25', X'1EB331AAD8DFDF4A8607E0688D16BE35', X'1EB331AAD8DFDF4A8607E0688D16BE35', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225 °F and be sure to have enough smoking wood to keep the smoke going for at least an hour but I recommend letting the smoke go the entire time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E787CDC1DEF84CA5D0F74420FCF411', X'1EB331AAD8DFDF4A8607E0688D16BE35', X'1EB331AAD8DFDF4A8607E0688D16BE35', 1, NULL, NULL, 2, NULL, 0, 'Place the burger dogs directly on the grate or if you used a Bradley rack, place the rack on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB0E634C7985504881904F3BAE86A619', X'1EB331AAD8DFDF4A8607E0688D16BE35', X'1EB331AAD8DFDF4A8607E0688D16BE35', 2, NULL, NULL, 2, NULL, 0, 'Let the burgers cook until they reach an internal temperature of 160°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2961CBADBB26746BE7B10FAEA083376', X'54BDA4FA3DC22D4B85E56512B0FC07A2', X'54BDA4FA3DC22D4B85E56512B0FC07A2', 0, NULL, NULL, 2, NULL, 0, 'Put the burger dogs on a toasted bun heaped with lettuce, tomatoes, onions, bacon and your favorite condiments.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F528C5A40FE7414AA1ED8373D2AD8EB1', X'54BDA4FA3DC22D4B85E56512B0FC07A2', X'54BDA4FA3DC22D4B85E56512B0FC07A2', 1, NULL, NULL, 2, NULL, 0, 'Eat and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55644B7FE4357D4B8BB810C20C3C0C0D', X'021B1C3CC4B141458F44834DB77B9841', X'021B1C3CC4B141458F44834DB77B9841', 0, NULL, NULL, 2, NULL, 0, 'Lay the roast on the cutting board skin side up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BEB76BC4CFEA341895C549720D7CA7E', X'021B1C3CC4B141458F44834DB77B9841', X'021B1C3CC4B141458F44834DB77B9841', 1, NULL, NULL, 2, NULL, 0, 'Make a cut through the skin right down the center as shown.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1156AB569A760489D40C8669112126F', X'021B1C3CC4B141458F44834DB77B9841', X'021B1C3CC4B141458F44834DB77B9841', 2, NULL, NULL, 2, NULL, 0, 'While pulling up and away from the center, run the blade of a sharp knife along the attached skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'214B5E91745561419E8C70B43114083B', X'021B1C3CC4B141458F44834DB77B9841', X'021B1C3CC4B141458F44834DB77B9841', 3, NULL, NULL, 2, NULL, 0, 'Turn the roast over to finish removing the skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E5A894CC1C5444AAA4CAE5B225D4655', X'8DF738AC1E41ED4CB1F26EB810035F65', X'8DF738AC1E41ED4CB1F26EB810035F65', 0, NULL, NULL, 2, NULL, 0, 'Spread mustard all over the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E0272A1F5B4344EAE02221D44606AA3', X'8DF738AC1E41ED4CB1F26EB810035F65', X'8DF738AC1E41ED4CB1F26EB810035F65', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle about ¼ cup of rub all over the top of the roast then rub it in so that it mixes with the mustard.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEE0A451DB624743AB358AFA54620739', X'8DF738AC1E41ED4CB1F26EB810035F65', X'8DF738AC1E41ED4CB1F26EB810035F65', 2, NULL, NULL, 2, NULL, 0, 'Flip the roast over and get the bottom side the same way.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45D7028FDB47EC418C76058AF97437CC', X'419BFFFE21A1884D934DBA477705F8FB', X'419BFFFE21A1884D934DBA477705F8FB', 0, NULL, NULL, 2, NULL, 0, 'Set up the smoker for cooking at 225 °F and make sure you have plenty of smoking wood for the long haul. If you are using an electric, charcoal or gas smoker, I recommend having enough wood chips, chunks or splits for about 6-8 hours of smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73DEA435EB8E9D4AAA8F80A354C5C3FA', X'419BFFFE21A1884D934DBA477705F8FB', X'419BFFFE21A1884D934DBA477705F8FB', 1, NULL, NULL, 2, NULL, 0, 'Once the smoker is running steady at the prescribed temperature, it is time to put the meat on the smoker grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF538DCB74840540864E11B68867A2D8', X'E22CFA1ABD06124EA2EE229D4279107B', X'E22CFA1ABD06124EA2EE229D4279107B', 0, NULL, NULL, 2, NULL, 0, 'Place the pork butt directly on the smoker grate fat side down.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C8A532834E84942A466C4988A11A959', X'E22CFA1ABD06124EA2EE229D4279107B', X'E22CFA1ABD06124EA2EE229D4279107B', 1, NULL, NULL, 2, NULL, 0, 'Keep the cherry wood smoke going for 6-8 hours if possible.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FC4591DA600D246B6608DEEF890C798', X'E22CFA1ABD06124EA2EE229D4279107B', X'E22CFA1ABD06124EA2EE229D4279107B', 2, NULL, NULL, 2, NULL, 0, 'I recommend using a meat thermometer for the most failsafe way to tell when the meat is done. Once you’ve cooked a few and you want to start experimenting with the guess work, go for it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F34323D066D06488D4D8C0D6C667B87', X'E22CFA1ABD06124EA2EE229D4279107B', X'E22CFA1ABD06124EA2EE229D4279107B', 3, NULL, NULL, 2, NULL, 0, 'I like to leave the pork butt open and on the smoker grate for the entire time but if you want to speed things up somewhat, you can wrap it in foil once it reaches 160°F. Once you wrap it, no more smoke is required and it can even be moved to the kitchen oven at 225°F if you want to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'521A8D164F46A14FADBCC7C230D29CB4', X'E22CFA1ABD06124EA2EE229D4279107B', X'E22CFA1ABD06124EA2EE229D4279107B', 4, NULL, NULL, 2, NULL, 0, 'When the pork butt reaches 200-205 in the very center, it is done.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94EDECC583E9E94B819CAA15DE353A41', X'267684B8B4E90D43802505C8E01C614C', X'267684B8B4E90D43802505C8E01C614C', 0, NULL, NULL, 2, NULL, 0, 'Place the wrapped bundle into an empty ice cooler and fill in any remaining space with more towels, small blankets or pillows.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'260C5FA5828077419E2B4135A06F6A95', X'267684B8B4E90D43802505C8E01C614C', X'267684B8B4E90D43802505C8E01C614C', 1, NULL, NULL, 2, NULL, 0, 'Hold for up to 4h ours if desired or it can be used immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8C21D0F59E0C845B8C7C4543D17DFF9', X'FB61DF52C519D944A8B356CA11285670', X'FB61DF52C519D944A8B356CA11285670', 0, NULL, NULL, 2, NULL, 0, 'Use a couple of forks to pull or shred the meat removing any clumps of fat that you find.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC3A06C1259A834981B10CD89E3E0056', X'EC2614413C81814095E63EA1B597CA72', X'EC2614413C81814095E63EA1B597CA72', 0, NULL, NULL, 2, NULL, 0, 'Rinse the chicken wings under cold water then pat them dry with a paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EEAFB53D880D04E8114756661D352F8', X'EC2614413C81814095E63EA1B597CA72', X'EC2614413C81814095E63EA1B597CA72', 1, NULL, NULL, 2, NULL, 0, 'Put the raw chicken wings into a large ziptop bag and pour in about ¼ cup of olive oil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869BE20AE5CD8445A328CB612E38A9A4', X'EC2614413C81814095E63EA1B597CA72', X'EC2614413C81814095E63EA1B597CA72', 2, NULL, NULL, 2, NULL, 0, 'Add about ½ cup of Jeff''s rub (per 3 lbs of chicken wings) to the bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D692BBBAC4A231469277CF1FCAFF3E01', X'EC2614413C81814095E63EA1B597CA72', X'EC2614413C81814095E63EA1B597CA72', 3, NULL, NULL, 2, NULL, 0, 'Close the bag and shake, rattle, and roll the bag to allow the rub to coat all of the chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22E334536904D843A148136AD5BBA663', X'ABE4E3038E668B43A0082CFBC8BE0197', X'ABE4E3038E668B43A0082CFBC8BE0197', 0, NULL, NULL, 2, NULL, 0, 'Place the bag of wings into a pan of some sort to ensure that they do not leak and then into the fridge to keep them cold.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A744DA468723848BAAC58BA277833DD', X'ABE4E3038E668B43A0082CFBC8BE0197', X'ABE4E3038E668B43A0082CFBC8BE0197', 1, NULL, NULL, 2, NULL, 0, 'Allow them to marinate in the fridge overnight or for 6-8 hours for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF638ACCCEE7F84DAA2EE20F03F449CD', X'ABE4E3038E668B43A0082CFBC8BE0197', X'ABE4E3038E668B43A0082CFBC8BE0197', 2, NULL, NULL, 2, NULL, 0, 'The next morning or when you are ready to smoke the chicken wings, remove the bag of wings from the fridge and pour them into a pan so you can easily place them onto the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12578C74A6FF6F42A061AABFAFB7B2DB', X'ADB8DE29D16BE74094B71AFE42F0F4CE', X'ADB8DE29D16BE74094B71AFE42F0F4CE', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 225 °F with pecan wood if possible (it’s awesome on these smoked chicken wings!).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35468B4B1B5B47429111EFB02503191E', X'ADB8DE29D16BE74094B71AFE42F0F4CE', X'ADB8DE29D16BE74094B71AFE42F0F4CE', 1, NULL, NULL, 2, NULL, 0, 'Smoke the wings with indirect heat and leave the water pan dry to give the skin a better texture with less humidity.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52EF6658897F1F4EA596194B59DB3A8E', X'ADB8DE29D16BE74094B71AFE42F0F4CE', X'ADB8DE29D16BE74094B71AFE42F0F4CE', 2, NULL, NULL, 2, NULL, 0, 'Use pecan wood if you have it and maintain 225 -230 °F for about 1.5 to 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB01CCF5006D5B4E893A80C69001FBC1', X'ADB8DE29D16BE74094B71AFE42F0F4CE', X'ADB8DE29D16BE74094B71AFE42F0F4CE', 3, NULL, NULL, 2, NULL, 0, 'About 30 minutes before the wings are done cooking, brush the honey and beer barbecue sauce (recipe below) onto the tops of the chicken wings.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AC0AAD7C4FAE746842FF377B4651801', X'ADB8DE29D16BE74094B71AFE42F0F4CE', X'ADB8DE29D16BE74094B71AFE42F0F4CE', 4, NULL, NULL, 2, NULL, 0, 'Let them cook for another 10 minutes then flip them over and repeat on the other side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A02445A23CADE64FA0D873D945066210', X'ADB8DE29D16BE74094B71AFE42F0F4CE', X'ADB8DE29D16BE74094B71AFE42F0F4CE', 5, NULL, NULL, 2, NULL, 0, 'Cook the chicken until it measures 165°F as measured by a tested digital probe meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2692640681519C4A81E201D7BBAEF1B5', X'C18F70C2406B8F4D9E098288516F9BEB', X'C18F70C2406B8F4D9E098288516F9BEB', 0, NULL, NULL, 2, NULL, 0, 'Combine 1 cup of Jeff’s barbecue sauce, 2 TBS of honey and ½ cup of beer until it is well mixed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05329268B46B67438FFB833CDBE47797', X'C18F70C2406B8F4D9E098288516F9BEB', X'C18F70C2406B8F4D9E098288516F9BEB', 1, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'503D79E632159842B15578AB4D73DF6A', X'1669D0FC9EE54845AC711603E0843473', X'1669D0FC9EE54845AC711603E0843473', 0, NULL, NULL, 2, NULL, 0, 'Lay the ribs bone side up on a flat surface.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FECE8CCC2AE824080E299531AD9B7F6', X'1669D0FC9EE54845AC711603E0843473', X'1669D0FC9EE54845AC711603E0843473', 1, NULL, NULL, 2, NULL, 0, 'Work your fingers or other semi-sharp object under the membrane in the center of the rack of ribs and pull upward. With a little practice, the entire membrane will pull off in one piece.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4885A0A868A8D4488027F76E3EB2798A', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', 0, NULL, NULL, 2, NULL, 0, 'Coat both sides of the ribs with yellow mustard.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E8F32E7E4E71F4E8BB43B33AEA73543', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', 1, NULL, NULL, 2, NULL, 0, 'Apply the mustard to the bone side first, then generously apply the original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4E5B88AE933A24E84EF504F9FA3ABEC', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', 2, NULL, NULL, 2, NULL, 0, 'Wait about 5 minutes or so for the rub to start looking wet.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B78724A9B9483F4A9074C2A453BB9F85', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', 3, NULL, NULL, 2, NULL, 0, 'Turn the ribs over and apply mustard and then rub to the meaty side of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F7D15E2BDEB242844F162444381F2B', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', 4, NULL, NULL, 2, NULL, 0, 'Then apply the rub very generously to the top of the ribs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4592952AA890D4D9C2C083C4767D67F', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', X'0AF33BCDBF5FE84AB0D66FA4275D8B9A', 5, NULL, NULL, 2, NULL, 0, 'Let them sit for about 5-10 minutes while the rub mixes with the mustard and rib juices to form a really nice paste.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86C319A7380B724DA5DBCC3F18A8C047', X'1772CD1F09427E4994652956C8EAEB1B', X'1772CD1F09427E4994652956C8EAEB1B', 0, NULL, NULL, 2, NULL, 0, 'Once they are seasoned well, lay the ribs on a piece of plastic wrap and wrap them up nice and tight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABCFF606AA51DB4F83E6CCE577A54342', X'1772CD1F09427E4994652956C8EAEB1B', X'1772CD1F09427E4994652956C8EAEB1B', 1, NULL, NULL, 2, NULL, 0, 'Place the wrapped ribs in a pan (to prevent possible leaks) and put them in the fridge overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72E57BD43D65634FB693CC29103DFD5E', X'1772CD1F09427E4994652956C8EAEB1B', X'1772CD1F09427E4994652956C8EAEB1B', 2, NULL, NULL, 2, NULL, 0, 'The next morning, remove the ribs from the fridge and unwrap them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'616DCCAA45C2884AAB07FA116542D7D5', X'1772CD1F09427E4994652956C8EAEB1B', X'1772CD1F09427E4994652956C8EAEB1B', 3, NULL, NULL, 2, NULL, 0, 'Leave them on the counter for a few minutes while you go get the smoker ready.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5B77DDAE1770146A722FE9BA1BBE5EB', X'442D0FD34CD5184D98BA97065218DD17', X'442D0FD34CD5184D98BA97065218DD17', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F46C32A014730F4AAA880573AF3F0FD2', X'442D0FD34CD5184D98BA97065218DD17', X'442D0FD34CD5184D98BA97065218DD17', 1, NULL, NULL, 2, NULL, 0, 'Make sure to have enough smoking wood to last about 2-3 hours. Mesquite mixed with equal parts of apple is a great combination but any smoking wood will work.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39CC4DC582D06B42941DA5FEE176C2FF', X'442D0FD34CD5184D98BA97065218DD17', X'442D0FD34CD5184D98BA97065218DD17', 2, NULL, NULL, 2, NULL, 0, 'Once your smoker is set up and ready to go, the ribs are ready to go into the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA2947DF75B2254D9E7B2ECDD867C5FF', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 0, NULL, NULL, 2, NULL, 0, 'Place the ribs directly on the smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96D117340205454BB157D1C758946170', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 1, NULL, NULL, 2, NULL, 0, 'Let them smoke for about 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FB9616FC7AB3A499366A279B32C4384', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 2, NULL, NULL, 2, NULL, 0, 'After 2 hours, remove them from the smoker with a spatula or something that does not disturb the rub and place them on a long sheet of heavy duty foil (I use a 30-inch piece).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D04FD61B662CC44A61FFB6614ABCE1B', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle extra rub on the top of the ribs and wrap them up individually.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AAA213A1F93D84B92B9AF754BDA6E43', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 4, NULL, NULL, 2, NULL, 0, 'Place the wrapped ribs back into the smoker at 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9BD3FD391CCA245BD670B2B4A4973F2', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 5, NULL, NULL, 2, NULL, 0, 'After 2 hours, remove the wrapped ribs from the smoker and take them carefully from the foil. Be careful when you open them up, they will be very hot and the steam and rib juices in the bottom of the foil will be very hot.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'305C25045671DD409B3501EF199A8CFA', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle them once again with more rub on the top side and place them back onto the smoker grate for 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'222EE82C406EF74CB9615AC03A61F22A', X'CF0174E460BBAA4195E27A51C209A30D', X'CF0174E460BBAA4195E27A51C209A30D', 7, NULL, NULL, 2, NULL, 0, 'After the single hour, they are done and ready to come into the house');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'708BC9AA0F6C7B409F10DA2EDB534B0B', X'32E74BAF20EF184282AACF642D6D9780', X'32E74BAF20EF184282AACF642D6D9780', 0, NULL, NULL, 2, NULL, 0, 'Lay the racks of ribs bone side up on the cutting board and very carefully twist the bones while pulling upward to separate them from the meat. Making a cut along both sides of the bone helps if they are a little stubborn.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6531765A8E3CE149A01E130D9EC347A1', X'32E74BAF20EF184282AACF642D6D9780', X'32E74BAF20EF184282AACF642D6D9780', 1, NULL, NULL, 2, NULL, 0, 'Note: there are sometimes some small bone pieces in the more narrow tip of the baby backs. Be sure to check for those.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDB26948BE607E4782CA17900B230815', X'08B9F30D64D6374592AE74CCDF4BCC7B', X'08B9F30D64D6374592AE74CCDF4BCC7B', 0, NULL, NULL, 2, NULL, 0, 'Brush a little melted butter onto the inside of the bread and give it a good toasting in the oven or grill.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ADAAC11AD8ED143AFE8FFE06E624FDE', X'08B9F30D64D6374592AE74CCDF4BCC7B', X'08B9F30D64D6374592AE74CCDF4BCC7B', 1, NULL, NULL, 2, NULL, 0, 'Lay the entire rack of ribs onto the top of the bread using a long wide knife or something similar underneath to keep the super tender ribs from breaking into pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C80530FBCD000C4299678C79C5E8E147', X'08B9F30D64D6374592AE74CCDF4BCC7B', X'08B9F30D64D6374592AE74CCDF4BCC7B', 2, NULL, NULL, 2, NULL, 0, 'Top the ribs with Jeff’s BBQ sauce to add yet another layer of flavor.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84BB84DECF25A948BE309D0E190FB590', X'08B9F30D64D6374592AE74CCDF4BCC7B', X'08B9F30D64D6374592AE74CCDF4BCC7B', 3, NULL, NULL, 2, NULL, 0, 'Top the sandwich');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34C14A058CE4F2428DBEB0FE2FD2DE4D', X'08B9F30D64D6374592AE74CCDF4BCC7B', X'08B9F30D64D6374592AE74CCDF4BCC7B', 4, NULL, NULL, 2, NULL, 0, 'Serve Immediately');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C31919F5EE3AB74DA584E823A36674FB', X'8697A549F1655447865BCE92381435E2', X'8697A549F1655447865BCE92381435E2', 0, NULL, NULL, 2, NULL, 0, 'If the shrimp are frozen, place them in a colander and run cold water over them for a few minutes until they are thawed.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36EE4EFE7B2744E887665AB236946A9', X'8697A549F1655447865BCE92381435E2', X'8697A549F1655447865BCE92381435E2', 1, NULL, NULL, 2, NULL, 0, 'Place the shrimp on a cookie sheet or flat surface and sprinkle my rub all over them.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D0DB833A4494646BB4999AC1315A6E9', X'8010B59EEBD49C46A6A69F00790921EE', X'8010B59EEBD49C46A6A69F00790921EE', 0, NULL, NULL, 2, NULL, 0, 'Cut up the smoked sausage into circles that are about ½ inch thick');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6FBDCA542BC934EBF21CD15CE66432A', X'E36A89EAB239FD408D2055F504D9BF5D', X'E36A89EAB239FD408D2055F504D9BF5D', 0, NULL, NULL, 2, NULL, 0, 'Place the shrimp onto a flat surface and lay a sausage round right in the center of the natural “C” the body of the shrimp makes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53591ADE3C97BB468F3F9076768EE539', X'E36A89EAB239FD408D2055F504D9BF5D', X'E36A89EAB239FD408D2055F504D9BF5D', 1, NULL, NULL, 2, NULL, 0, 'Push a toothpick through the shrimp, through the sausage and through the shrimp again to hold it all together nicely.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D13EDF776997424C98D062D51AB18833', X'E36A89EAB239FD408D2055F504D9BF5D', X'E36A89EAB239FD408D2055F504D9BF5D', 2, NULL, NULL, 2, NULL, 0, 'Repeat this process on all of the shrimp and sausage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2272A71611507A4DABF2565504AFEC0D', X'F6148F6E9B351B4E9FB346F15BF123B8', X'F6148F6E9B351B4E9FB346F15BF123B8', 0, NULL, NULL, 2, NULL, 0, 'Setup the smoker for cooking low and slow at about 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F281F65FA556D4D9B2D13715A00EB49', X'F6148F6E9B351B4E9FB346F15BF123B8', X'F6148F6E9B351B4E9FB346F15BF123B8', 1, NULL, NULL, 2, NULL, 0, 'Place the shrimp and sausage appetizers directly onto the smoker grate for about 30 minutes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC363FDA3523EE4EB2003C0B68A8F412', X'F6148F6E9B351B4E9FB346F15BF123B8', X'F6148F6E9B351B4E9FB346F15BF123B8', 2, NULL, NULL, 2, NULL, 0, 'They will turn from grey to a pinkish color and the meat changes from translucent to opaque when they are done cooking');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C524B8D0987774994C01B3BFE69805A', X'F6148F6E9B351B4E9FB346F15BF123B8', X'F6148F6E9B351B4E9FB346F15BF123B8', 3, NULL, NULL, 2, NULL, 0, 'Use mesquite so you can get plenty of good robust smoke flavor on the shrimp and sausage in a very short period of time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F85C605B220CD41B6C17EEE233FC60A', X'717C874D02CF9B45990556B85F142876', X'717C874D02CF9B45990556B85F142876', 0, NULL, NULL, 2, NULL, 0, 'When you think they are done, grab one and pop it into your mouth. Give it your nod of approval and get them into the house for everyone else to try.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A803F99C975214ABBC5B28435147EF0', X'717C874D02CF9B45990556B85F142876', X'717C874D02CF9B45990556B85F142876', 1, NULL, NULL, 2, NULL, 0, 'If they need a little longer, let them go another 4-5 minutes then try another one.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBE3F9F2E8CE9241B1EEFDAAD946BD98', X'717C874D02CF9B45990556B85F142876', X'717C874D02CF9B45990556B85F142876', 2, NULL, NULL, 2, NULL, 0, 'Serve immediately!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88D2C856EE08834C813903A8F111E0E0', X'F2EEB8FB8DF9454F8A8C682F163EE0CE', X'F2EEB8FB8DF9454F8A8C682F163EE0CE', 0, NULL, NULL, 2, NULL, 0, 'Place 2 lbs of ground beef (80/20), 2 eggs, ¾ cup of diced green bell pepper, 1 cup of onion, ½ cup of quick oats, ¼ cup worcestershire, 2 eggs, 1/3 cup of Jeff''s rub, and 1/3 cup of Jeff''s sauce into a large mixing bowl');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05B7CFA64943224E900E3B0EC1609174', X'F2EEB8FB8DF9454F8A8C682F163EE0CE', X'F2EEB8FB8DF9454F8A8C682F163EE0CE', 1, NULL, NULL, 2, NULL, 0, 'Combine the ingredients together well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'727016F0EB55844AA018971ADB14035C', X'1315638A75AEDD45AC5E7A20F5432D50', X'1315638A75AEDD45AC5E7A20F5432D50', 0, NULL, NULL, 2, NULL, 0, 'This is something that is almost impossible to write out in a way that can be understood without images. You are looking for a weave that is 3 strips of bacon wide. Use half strips weaved into those 3 steps to create the `weave`.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1942E6C149F8E498654F1225FA64AA1', X'1315638A75AEDD45AC5E7A20F5432D50', X'1315638A75AEDD45AC5E7A20F5432D50', 1, NULL, NULL, 2, NULL, 0, 'Create the weave on one end of a piece of wax or parchment paper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C71BB22F6363A419A13D2A3A70F6034', X'CFB4445A7AA96443B1A58A0491DF02A0', X'CFB4445A7AA96443B1A58A0491DF02A0', 0, NULL, NULL, 2, NULL, 0, 'With the meatloaf that has been mixed together, form (2) logs that are as long as your bacon weave and about 2 inches in diameter');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF59CDACD96720478A8A6432CFC161D2', X'CFB4445A7AA96443B1A58A0491DF02A0', X'CFB4445A7AA96443B1A58A0491DF02A0', 1, NULL, NULL, 2, NULL, 0, 'Form the logs on the other end of the wax paper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B315FCD141C0B4A987421B6A3A51527', X'FF771D16E60A934EB3728903B80BC0C9', X'FF771D16E60A934EB3728903B80BC0C9', 0, NULL, NULL, 2, NULL, 0, 'Use the wax paper to help you roll/place the log onto the edge of the bacon weave.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F0713F5D9994F4EB98BE151DB74FFEA', X'FF771D16E60A934EB3728903B80BC0C9', X'FF771D16E60A934EB3728903B80BC0C9', 1, NULL, NULL, 2, NULL, 0, 'Continue to roll the meatloaf log until the bacon weave is wrapped around it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24E792B4068CEA4EB230FB5C58CAC139', X'031F1EC283BB0C4495BD7029CDB62000', X'031F1EC283BB0C4495BD7029CDB62000', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s rub generously onto the outside of the wrapped meatloaf logs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D70E1A7385D0774491EECF9D47143EF6', X'AE2F598E6E55224392F548C00B2659C9', X'AE2F598E6E55224392F548C00B2659C9', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at about 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F5AEDC2AEF499469DC0BD99FFFA6961', X'AE2F598E6E55224392F548C00B2659C9', X'AE2F598E6E55224392F548C00B2659C9', 1, NULL, NULL, 2, NULL, 0, 'Place the wrapped meatloaf logs directly onto the smoker grate or use a bradley rack.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A784A63D01770646A7EA30C1FB7A6343', X'AE2F598E6E55224392F548C00B2659C9', X'AE2F598E6E55224392F548C00B2659C9', 2, NULL, NULL, 2, NULL, 0, 'Smoke cook for about 2.5 hours or until the internal temperature of the meat reads 165°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8ECB1EB9789C747BDE484D0B1A1F5ED', X'76A9CCB65BECC444B35B64FD8AFC8603', X'76A9CCB65BECC444B35B64FD8AFC8603', 0, NULL, NULL, 2, NULL, 0, 'Remove the smoked meatloaf logs from the smoker once they are finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'406B0494BC696844BB01F473A0F87F56', X'76A9CCB65BECC444B35B64FD8AFC8603', X'76A9CCB65BECC444B35B64FD8AFC8603', 1, NULL, NULL, 2, NULL, 0, 'Slice and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7604620DFFC094EA35811DAF403E352', X'3CF93F29528E8B4E99ECD7BC91F3A369', X'3CF93F29528E8B4E99ECD7BC91F3A369', 0, NULL, NULL, 2, NULL, 0, 'Rinse chicken legs under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E58C06A93AEC3344BB48870F606C725B', X'3CF93F29528E8B4E99ECD7BC91F3A369', X'3CF93F29528E8B4E99ECD7BC91F3A369', 1, NULL, NULL, 2, NULL, 0, 'Lay one of the chicken legs on the cutting board or hard surface.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE3FD98F37AA394D83A8EC6D39611B63', X'3CF93F29528E8B4E99ECD7BC91F3A369', X'3CF93F29528E8B4E99ECD7BC91F3A369', 2, NULL, NULL, 2, NULL, 0, 'Make a cut through skin, meat and tendons all the way around the chicken leg, just below the knuckle');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F47794EDDAE1B40B7E7944DE259F2D3', X'3CF93F29528E8B4E99ECD7BC91F3A369', X'3CF93F29528E8B4E99ECD7BC91F3A369', 3, NULL, NULL, 2, NULL, 0, 'Push meat all to one end leaving at least half of the bone on the knuckle end as a handle.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD97EF631942474CAC997B7DDCC07F23', X'3CF93F29528E8B4E99ECD7BC91F3A369', X'3CF93F29528E8B4E99ECD7BC91F3A369', 4, NULL, NULL, 2, NULL, 0, 'Snip off any extruding tendons, meat, fat, etc. to clean it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526C39884D051B438FE8D400AC1D4ECF', X'3CF93F29528E8B4E99ECD7BC91F3A369', X'3CF93F29528E8B4E99ECD7BC91F3A369', 5, NULL, NULL, 2, NULL, 0, 'Repeat process on all chicken legs.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7923E38BF8835418F1F037AACD4E3F1', X'D91118B64726514B9C112805BF44284B', X'D91118B64726514B9C112805BF44284B', 0, NULL, NULL, 2, NULL, 0, 'Apply mayonnaise to outside of chicken to create a stick surface for the seasoning to stick to.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DA931A5C6B79942BA6CE62FD93FDAE6', X'D91118B64726514B9C112805BF44284B', X'D91118B64726514B9C112805BF44284B', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s rub onto each chicken leg making sure to coat all sides thoroughly.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CAFF80F9F19FC4F859C5F946FBBF152', X'D91118B64726514B9C112805BF44284B', X'D91118B64726514B9C112805BF44284B', 2, NULL, NULL, 2, NULL, 0, 'Place all of the seasoned chicken into a lidded bowl or ziptop bag and place it in the fridge for 2 hours minimum. You can also marinate overnight if desired.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D462A68BCFA3E4F893EE32E655501B6', X'D91118B64726514B9C112805BF44284B', X'D91118B64726514B9C112805BF44284B', 3, NULL, NULL, 2, NULL, 0, 'After marinating time is complete, remove chicken from fridge.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6ACDA39734ABA41878F542AE70C365F', X'6A77B1A21CFB41488C8EB38B86C5B83E', X'6A77B1A21CFB41488C8EB38B86C5B83E', 0, NULL, NULL, 2, NULL, 0, 'To prevent the handles from getting too dark or burnt, cut a square 4-inch pieces of foil for each chicken leg.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B185E1A22E080E4692CC695886DBAF29', X'6A77B1A21CFB41488C8EB38B86C5B83E', X'6A77B1A21CFB41488C8EB38B86C5B83E', 1, NULL, NULL, 2, NULL, 0, 'Wrap the foil around the handle (bone) of each chicken leg.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09C035690396344886121FF0076B8D5C', X'27A4233145CC644EA36BF28D1613AFA6', X'27A4233145CC644EA36BF28D1613AFA6', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 250°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'710D033FD937A4498408C33B4ECE52EB', X'27A4233145CC644EA36BF28D1613AFA6', X'27A4233145CC644EA36BF28D1613AFA6', 1, NULL, NULL, 2, NULL, 0, 'Place chicken directly on smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'716E8202549DF6409308F6BCC9D7B729', X'27A4233145CC644EA36BF28D1613AFA6', X'27A4233145CC644EA36BF28D1613AFA6', 2, NULL, NULL, 2, NULL, 0, 'Let chicken cook for about 2 hours or until it reaches 175°F in the thickest part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9424994C3BFA90468C0537007F25A062', X'27A4233145CC644EA36BF28D1613AFA6', X'27A4233145CC644EA36BF28D1613AFA6', 3, NULL, NULL, 2, NULL, 0, 'If you want to add sauce to the chicken (highly recommended) do so during the last 30 minutes of the cooking time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'773061E704D4404287CC30614C8E9A52', X'0A4E7B6F8AAF9442ADD3975375ED1DED', X'0A4E7B6F8AAF9442ADD3975375ED1DED', 0, NULL, NULL, 2, NULL, 0, 'Remove foil from chicken and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B2967AE85E58B419BBE594E2A6A2983', X'7589B61331F2E5488DBE33C89D8E886D', X'7589B61331F2E5488DBE33C89D8E886D', 0, NULL, NULL, 2, NULL, 0, 'Drizzle honey on country style ribs or pork steaks and spread onto meat using hands or a basting brush.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40E219DEB652BE4D87E176DC6F8F2929', X'7589B61331F2E5488DBE33C89D8E886D', X'7589B61331F2E5488DBE33C89D8E886D', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s rub onto all sides of the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF27DD736AB3ED4A9BC245BE346086D6', X'86E5180FB3CDFE4A9068510450E103C5', X'86E5180FB3CDFE4A9068510450E103C5', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at about 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11522C9555FB1A48A1FB0EC313069599', X'86E5180FB3CDFE4A9068510450E103C5', X'86E5180FB3CDFE4A9068510450E103C5', 1, NULL, NULL, 2, NULL, 0, 'Place meat directly on smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10BF72EDA1BDDC4CACBEDEF51C8DAC72', X'86E5180FB3CDFE4A9068510450E103C5', X'86E5180FB3CDFE4A9068510450E103C5', 2, NULL, NULL, 2, NULL, 0, 'Let the meat cook until it reaches 175-180°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1EF57236076F84AA4D4C257504632EC', X'86E5180FB3CDFE4A9068510450E103C5', X'86E5180FB3CDFE4A9068510450E103C5', 3, NULL, NULL, 2, NULL, 0, 'About an hour before the meat is finished, brush some of Jeff''s sauce onto the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCF6A232C14EA9408555F8668DE1557B', X'86E5180FB3CDFE4A9068510450E103C5', X'86E5180FB3CDFE4A9068510450E103C5', 4, NULL, NULL, 2, NULL, 0, 'When the meat is finished cooking, serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35518BB5018E1449A3735FC1436AA662', X'C565CFD58F3B384E9E6C5FD56D47028A', X'C565CFD58F3B384E9E6C5FD56D47028A', 0, NULL, NULL, 2, NULL, 0, 'Remove shells from shrimp');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC479DEE491F2F4DBA211D20CDEC7339', X'C565CFD58F3B384E9E6C5FD56D47028A', X'C565CFD58F3B384E9E6C5FD56D47028A', 1, NULL, NULL, 2, NULL, 0, 'Put the shrimp down into a ziptop bag with about ¼ cup of Jeff''s rub and shake/roll to coat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97FAA5043E8E704A99F2EBCBCFCF96F4', X'C565CFD58F3B384E9E6C5FD56D47028A', X'C565CFD58F3B384E9E6C5FD56D47028A', 2, NULL, NULL, 2, NULL, 0, 'Place bag of shrimp in the fridge for 30-60 minutes to marinate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'482945E2F87D564993B62862AC82E3FA', X'09999E9E54708E4BB86EBF0EAEAA9CB2', X'09999E9E54708E4BB86EBF0EAEAA9CB2', 0, NULL, NULL, 2, NULL, 0, 'Cut peppers in half lengthwise');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D3E185A61FF9443804872336E482653', X'09999E9E54708E4BB86EBF0EAEAA9CB2', X'09999E9E54708E4BB86EBF0EAEAA9CB2', 1, NULL, NULL, 2, NULL, 0, 'Remove seeds and veins completely');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'396D89EE59C9D74C8DD925ACBA08829B', X'E1E3F7D37579E04CB898B63C9FDC2967', X'E1E3F7D37579E04CB898B63C9FDC2967', 0, NULL, NULL, 2, NULL, 0, 'Mix 8 ounces of cream cheese with 1 cup of shredded cheddar and 3 TBS of Jeff''s rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'102CCACE6F611C4B8636106E8D8A730D', X'E1E3F7D37579E04CB898B63C9FDC2967', X'E1E3F7D37579E04CB898B63C9FDC2967', 1, NULL, NULL, 2, NULL, 0, 'Mix well to combine ingredients');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489B4F362EB3C348BD24592FF64D0C69', X'F3434184F477B74791A5A4F1ACD2DBE6', X'F3434184F477B74791A5A4F1ACD2DBE6', 0, NULL, NULL, 2, NULL, 0, 'Lay marinated shrimp into pepper half');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7CC7D495793F04697B991FC6DBF4973', X'F3434184F477B74791A5A4F1ACD2DBE6', X'F3434184F477B74791A5A4F1ACD2DBE6', 1, NULL, NULL, 2, NULL, 0, 'Make partial cuts into the body of the shrimp to help it lay flat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2C2C9E7ABA2BC4DA96D60A1E89C0A61', X'F3434184F477B74791A5A4F1ACD2DBE6', X'F3434184F477B74791A5A4F1ACD2DBE6', 2, NULL, NULL, 2, NULL, 0, 'Stuff cream cheese mixture over the top and sides of the shrimp to fill pepper half');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77219B65B383364DAAD8628D2059769E', X'F3434184F477B74791A5A4F1ACD2DBE6', X'F3434184F477B74791A5A4F1ACD2DBE6', 3, NULL, NULL, 2, NULL, 0, 'Wrap half strips of bacon around stuffed pepper halves');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EFAE3C108DCA64D80050843E5AD78F1', X'23190DCF1F97D24EA351832E7255D31B', X'23190DCF1F97D24EA351832E7255D31B', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at about 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E0AA66EB48B63408CE6F9507847947E', X'23190DCF1F97D24EA351832E7255D31B', X'23190DCF1F97D24EA351832E7255D31B', 1, NULL, NULL, 2, NULL, 0, 'Place stuffed peppers directly on smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C60E1FD487D8743BE800173826810F1', X'23190DCF1F97D24EA351832E7255D31B', X'23190DCF1F97D24EA351832E7255D31B', 2, NULL, NULL, 2, NULL, 0, 'Smoke with pecan wood (or other smoking wood) for about 3 hours or until peppers are soft');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'236E3F94897A5946B5ACBC046BE84DFB', X'23190DCF1F97D24EA351832E7255D31B', X'23190DCF1F97D24EA351832E7255D31B', 3, NULL, NULL, 2, NULL, 0, 'For crispy bacon, place peppers under oven broiler for a few minutes watching them closely');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'045527D8C1DF1C47A78722064B7CF0E3', X'23190DCF1F97D24EA351832E7255D31B', X'23190DCF1F97D24EA351832E7255D31B', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64772E9A83C3B04BBCDFC701CCE26F67', X'3C067A0510501E40BE1DB04D94FC9B0E', X'3C067A0510501E40BE1DB04D94FC9B0E', 0, NULL, NULL, 2, NULL, 0, 'Remove membrane from bone side of ribs using a paper towel to get a good grip');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66213FD5F2A2B841A5F52551B3F18CEF', X'3C067A0510501E40BE1DB04D94FC9B0E', X'3C067A0510501E40BE1DB04D94FC9B0E', 1, NULL, NULL, 2, NULL, 0, 'Apply a thin coat of yellow mustard to the bone side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C531253FA1C9944EADD93CC0E10C2152', X'3C067A0510501E40BE1DB04D94FC9B0E', X'3C067A0510501E40BE1DB04D94FC9B0E', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle enough of Jeff''s rub onto he ribs to cover the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A953696604765945A2028D8343B071DF', X'3C067A0510501E40BE1DB04D94FC9B0E', X'3C067A0510501E40BE1DB04D94FC9B0E', 3, NULL, NULL, 2, NULL, 0, 'Once the rub gets that typical `wet` look, flip them over and repeat the mustard and rub on the meaty side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6B745BE0C0DD845BE1F3244627376E4', X'E74ECCE02274C34BA9232251BF572017', X'E74ECCE02274C34BA9232251BF572017', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for smoking at about 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4756E132100E294CA508C6FA53301F19', X'E74ECCE02274C34BA9232251BF572017', X'E74ECCE02274C34BA9232251BF572017', 1, NULL, NULL, 2, NULL, 0, 'Place the ribs directly on the smoker grate or you can use a rib rack if you need to fit more');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'002E9C5F8D97E34F8E568313A131DF71', X'E74ECCE02274C34BA9232251BF572017', X'E74ECCE02274C34BA9232251BF572017', 2, NULL, NULL, 2, NULL, 0, 'Let the ribs smoke cook until they are as tender as you like them. (this is normally about 5 hours for baby back ribs)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9317BB20B337354091697B672AD3C18A', X'E74ECCE02274C34BA9232251BF572017', X'E74ECCE02274C34BA9232251BF572017', 3, NULL, NULL, 2, NULL, 0, 'Sauce the ribs about 30 minutes before they are finished for best results.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DEA26864C0779419EBF91108475144C', X'E74ECCE02274C34BA9232251BF572017', X'E74ECCE02274C34BA9232251BF572017', 4, NULL, NULL, 2, NULL, 0, 'When the ribs are finished cooking, remove them from the smoker, slice ''em up and serve.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5AF5C835D0B55469419EF01DF24E96A', X'C410EA6BBAE25947810D57227CE4D524', X'C410EA6BBAE25947810D57227CE4D524', 0, NULL, NULL, 2, NULL, 0, 'Rinse the piece of meat under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9823B6482DD8F4F80871D80F72BFBE0', X'C410EA6BBAE25947810D57227CE4D524', X'C410EA6BBAE25947810D57227CE4D524', 1, NULL, NULL, 2, NULL, 0, 'Apply a thin coat of yellow mustard to the chuck roast');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87CDAA38CC92454196C2AB276F0582E4', X'C410EA6BBAE25947810D57227CE4D524', X'C410EA6BBAE25947810D57227CE4D524', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle on a coat of Jeff''s rub onto all sides of the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'774BE73BCCD99F408E04D5B928EB2464', X'BAE6C9B4D53441489039CDBE6EEA9C9E', X'BAE6C9B4D53441489039CDBE6EEA9C9E', 0, NULL, NULL, 2, NULL, 0, 'Prepare smoker for cooking at about 235°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09445AD4C4DDF74BBE1919C1D95CACB9', X'BAE6C9B4D53441489039CDBE6EEA9C9E', X'BAE6C9B4D53441489039CDBE6EEA9C9E', 1, NULL, NULL, 2, NULL, 0, 'Place meat directly onto smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'541DE46EDFBAA34EA751A6E4DFD19ED5', X'BAE6C9B4D53441489039CDBE6EEA9C9E', X'BAE6C9B4D53441489039CDBE6EEA9C9E', 2, NULL, NULL, 2, NULL, 0, 'Cook for 12-20 hours or until the thickest part of the meat measures 195°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'702A5E08FE526242A8D8508F645F57C7', X'BAE6C9B4D53441489039CDBE6EEA9C9E', X'BAE6C9B4D53441489039CDBE6EEA9C9E', 3, NULL, NULL, 2, NULL, 0, 'Apply smoke for at least 6 hours if you are using an electric, gas or charcoal smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8250B261293E6419D8D1E2AAC0E2419', X'BAE6C9B4D53441489039CDBE6EEA9C9E', X'BAE6C9B4D53441489039CDBE6EEA9C9E', 4, NULL, NULL, 2, NULL, 0, 'The meat can be wrapped in foil once it reaches 160 °F to hold in moisture and to speed up the process');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB96C1EDE2D29C4A86BBA7A6EBAF101E', X'AB134B7015A7444A9971FD1147DBD953', X'AB134B7015A7444A9971FD1147DBD953', 0, NULL, NULL, 2, NULL, 0, 'Once the meat reaches 195 °F, wrap the meat in foil (if you haven''t already) then in a thick towel and place it an empty ice cooler to rest for at least 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75F874EBAD1E744EB0314AD2BBA872B6', X'AB134B7015A7444A9971FD1147DBD953', X'AB134B7015A7444A9971FD1147DBD953', 1, NULL, NULL, 2, NULL, 0, 'Fill in any remaining space in the cooler with more towels or blankets.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1AB8195C808D0408ABA6FDA2032535E', X'2D82636399A6C84C9CD58E59BBD2BAF4', X'2D82636399A6C84C9CD58E59BBD2BAF4', 0, NULL, NULL, 2, NULL, 0, 'Once the meat has rested, open the foil and allow it to cool for a few minutes then using heat resistant gloves, pull the meat into pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA0286C3C2DBEC4B90A02CFCF5DC15C0', X'2D82636399A6C84C9CD58E59BBD2BAF4', X'2D82636399A6C84C9CD58E59BBD2BAF4', 1, NULL, NULL, 2, NULL, 0, 'Remove any fat and/or gelatinous substance you come across');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12FB164CBF92C64285E420AEF8E6DE4B', X'2D82636399A6C84C9CD58E59BBD2BAF4', X'2D82636399A6C84C9CD58E59BBD2BAF4', 2, NULL, NULL, 2, NULL, 0, 'Further pull or chop the meat into the size that you want.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6732CE5B0FEA844E8D52D5E4057EDBD7', X'2D82636399A6C84C9CD58E59BBD2BAF4', X'2D82636399A6C84C9CD58E59BBD2BAF4', 3, NULL, NULL, 2, NULL, 0, 'Apply more rub and stir into the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7F198D8C4925549913332DEAE81D20D', X'2D82636399A6C84C9CD58E59BBD2BAF4', X'2D82636399A6C84C9CD58E59BBD2BAF4', 4, NULL, NULL, 2, NULL, 0, 'The meat is now ready to serve');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44B44793C3D12F4787B04C4D7122E6BE', X'4F3889914F09F949B4B3C487BD56291C', X'4F3889914F09F949B4B3C487BD56291C', 0, NULL, NULL, 2, NULL, 0, 'Remove backbone by cutting along both sides of the backbone with kitchen shears');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2926FFD6F14CD4A91081C8355AF821B', X'4F3889914F09F949B4B3C487BD56291C', X'4F3889914F09F949B4B3C487BD56291C', 1, NULL, NULL, 2, NULL, 0, 'Remove keel bone');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F03C00E8660E284293F68382BD6ABD44', X'4F3889914F09F949B4B3C487BD56291C', X'4F3889914F09F949B4B3C487BD56291C', 2, NULL, NULL, 2, NULL, 0, 'Cut through center of chicken to divide into two halves');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29A2CEC866AE3544890CF8EC9C0E9A78', X'4F3889914F09F949B4B3C487BD56291C', X'4F3889914F09F949B4B3C487BD56291C', 3, NULL, NULL, 2, NULL, 0, 'Watch video at http://youtu.be/PYVaWI3LaG0 for full instructions');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C23F1FA3CD5F4947ACE4000D3BEC8D3B', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', 0, NULL, NULL, 2, NULL, 0, 'Place chickens into plastic or glass bowl with a lid');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50D5A30DBA93E54BB85B905A064B14A2', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', 1, NULL, NULL, 2, NULL, 0, 'Mix 1 cup of koshers salt, 1 gallon of water and ¾ cup of brown sugar and stir until well dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875F84E995F5574B87AA0290C7F19DF6', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', 2, NULL, NULL, 2, NULL, 0, 'Pour brine mixture over chicken until covered and refrigerate for 3 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB8EAE8D38BAAD4DA6FD8854D4712AB9', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', X'88BFE1DB9B278E4CB2D43D21AE97E9D6', 3, NULL, NULL, 2, NULL, 0, 'After 3 hours, rinse chicken well under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F408C71B6946F8498A8DE8DA41F7D36D', X'B88DC6A80042554CBCA1A08BC2D02702', X'B88DC6A80042554CBCA1A08BC2D02702', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker for cooking at about 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2DC344ACC2F1C4C812CFE337280B532', X'B88DC6A80042554CBCA1A08BC2D02702', X'B88DC6A80042554CBCA1A08BC2D02702', 1, NULL, NULL, 2, NULL, 0, 'Place chicken directly on smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDAFDF70FF4BF7458FEDB1A5A5DEAE8C', X'B88DC6A80042554CBCA1A08BC2D02702', X'B88DC6A80042554CBCA1A08BC2D02702', 2, NULL, NULL, 2, NULL, 0, 'Smoke chicken for 3 hours or until a meat thermometer inserted in the leg or thigh measures 165°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F4E43CC97C85749A1D169E4086813F0', X'B88DC6A80042554CBCA1A08BC2D02702', X'B88DC6A80042554CBCA1A08BC2D02702', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B814D2B58ABAD47B31D45967A5AFB9B', X'601A1EC1DC9DFF42850E27A3471BF409', X'601A1EC1DC9DFF42850E27A3471BF409', 0, NULL, NULL, 2, NULL, 0, 'Cut salmon in half lengthwise separating the thick part from the thinner part.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D81BA0F42A81EE4D84D68A95A6D31821', X'601A1EC1DC9DFF42850E27A3471BF409', X'601A1EC1DC9DFF42850E27A3471BF409', 1, NULL, NULL, 2, NULL, 0, 'Further separate lengthwise into strips that are 1.5 to 2 inches wide');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CF7A8BAA69D794A9C4B5BEBBF6A37E2', X'601A1EC1DC9DFF42850E27A3471BF409', X'601A1EC1DC9DFF42850E27A3471BF409', 2, NULL, NULL, 2, NULL, 0, 'Make cuts every 2 inches or so vertically through the fish stopping when you reach the skin');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95797BB65753BB4EAAF2CC1EC2E6A08B', X'601A1EC1DC9DFF42850E27A3471BF409', X'601A1EC1DC9DFF42850E27A3471BF409', 3, NULL, NULL, 2, NULL, 0, 'Slide the knife blade between the fish and the skin to separate the skin from the fish');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'738CDEF35B0FAF49BB069701A0DEFE02', X'601A1EC1DC9DFF42850E27A3471BF409', X'601A1EC1DC9DFF42850E27A3471BF409', 4, NULL, NULL, 2, NULL, 0, 'Place the pieces of salmon onto skewers making sure to put pieces of similar thickness on the same skewer');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29121F93CE6E544A86F5A6F7B2D97AC3', X'601A1EC1DC9DFF42850E27A3471BF409', X'601A1EC1DC9DFF42850E27A3471BF409', 5, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto the salmon to help the rub to stick');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ACF12A0FB64D643A5B02C9B9FC825FF', X'601A1EC1DC9DFF42850E27A3471BF409', X'601A1EC1DC9DFF42850E27A3471BF409', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s rub onto the salmon to add a nice layer of flavor and seasoning to the fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2997CDD28172404E89F846439D01355B', X'87DA85C905090D429EF2A36A86D192BC', X'87DA85C905090D429EF2A36A86D192BC', 0, NULL, NULL, 2, NULL, 0, 'Place the salmon skewers onto oiled smoker grates');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'087F5582E6064142958F82904B905A4B', X'87DA85C905090D429EF2A36A86D192BC', X'87DA85C905090D429EF2A36A86D192BC', 1, NULL, NULL, 2, NULL, 0, 'Smoke fish at 225°F for about 1 to 1.5 hours or until it reaches 135°F* in the center.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E95C0A5FDD34347916805178ED8A72A', X'87DA85C905090D429EF2A36A86D192BC', X'87DA85C905090D429EF2A36A86D192BC', 2, NULL, NULL, 2, NULL, 0, 'Use a thermometer to check the temperature of the fish after about 45 minutes or sooner if the fish is a little on the thin side.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D69522411334A4FA367ECBC1303B6B8', X'09C24A7F27BCA5458A7AE0090B2EF996', X'09C24A7F27BCA5458A7AE0090B2EF996', 0, NULL, NULL, 2, NULL, 0, 'When the fish has reached 135°F*, remove it from the smoker and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB9A9149B42D6940948F909F9350F67E', X'F4247D29076D6B47808AE8D6863DDF05', X'F4247D29076D6B47808AE8D6863DDF05', 0, NULL, NULL, 2, NULL, 0, 'Place scallops in ziptop bag with olive oil and 1 TBS of Jeff''s original rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D19B4B9D617F2048A82141B1CF876DF7', X'F4247D29076D6B47808AE8D6863DDF05', X'F4247D29076D6B47808AE8D6863DDF05', 1, NULL, NULL, 2, NULL, 0, 'Seal bag then roll and massage bag gently to fully coat scallops');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'490E168ECE10EC418E1D08ED6AE63868', X'F4247D29076D6B47808AE8D6863DDF05', X'F4247D29076D6B47808AE8D6863DDF05', 2, NULL, NULL, 2, NULL, 0, 'Place bag in fridge for 1 hour to marinate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2600219F40840F40B301F66F12A65EE7', X'B7C801CD0F82C649B9D3BECE74F322FB', X'B7C801CD0F82C649B9D3BECE74F322FB', 0, NULL, NULL, 2, NULL, 0, 'Place scallop onto one end of a thin slice of bacon');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D050B8B1E331B4199AC59DEC98B74EA', X'B7C801CD0F82C649B9D3BECE74F322FB', X'B7C801CD0F82C649B9D3BECE74F322FB', 1, NULL, NULL, 2, NULL, 0, 'Wrap bacon around scallop allowing an overlap of about ½ inch');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A14649B4C075E245A901F1AB7D1A7E7F', X'B7C801CD0F82C649B9D3BECE74F322FB', X'B7C801CD0F82C649B9D3BECE74F322FB', 2, NULL, NULL, 2, NULL, 0, 'Cut bacon to fit perfectly');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'802BAF866EB8ED48A498EC499328BD45', X'B7C801CD0F82C649B9D3BECE74F322FB', X'B7C801CD0F82C649B9D3BECE74F322FB', 3, NULL, NULL, 2, NULL, 0, 'Push toothpick through bacon overlap and all the way through scallop to secure');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11F7FBF965D726418EF54600420D3F91', X'B7C801CD0F82C649B9D3BECE74F322FB', X'B7C801CD0F82C649B9D3BECE74F322FB', 4, NULL, NULL, 2, NULL, 0, 'Repeat on all scallops');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89A623B38280C54BAC68D26DA20068BF', X'B7C801CD0F82C649B9D3BECE74F322FB', X'B7C801CD0F82C649B9D3BECE74F322FB', 5, NULL, NULL, 2, NULL, 0, 'Place bacon wrapped and secured scallops onto bradley rack or cookie sheet');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C18A3807CC7614E8E1593F1B31636A4', X'B7C801CD0F82C649B9D3BECE74F322FB', X'B7C801CD0F82C649B9D3BECE74F322FB', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s Texas style rub onto the outside of the wrapped scallops.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0359A6B0E0C534089496DED60E01F9D', X'93F78DDFEA436F439F7E8720ED7BEECF', X'93F78DDFEA436F439F7E8720ED7BEECF', 0, NULL, NULL, 2, NULL, 0, 'Place scallops directly onto smoker grate and cook for about 1 hour');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'295C7952547447428E9D0190FC534538', X'93F78DDFEA436F439F7E8720ED7BEECF', X'93F78DDFEA436F439F7E8720ED7BEECF', 1, NULL, NULL, 2, NULL, 0, 'Scallops are done when they reach 145°F in the center');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B2A24FFAC1C35499E8CFEAD13D01E8E', X'93F78DDFEA436F439F7E8720ED7BEECF', X'93F78DDFEA436F439F7E8720ED7BEECF', 2, NULL, NULL, 2, NULL, 0, 'Apply smoke for at least 20 minutes or up to 1 hour if desired');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC192F264958144BA23153010057CCB8', X'93F78DDFEA436F439F7E8720ED7BEECF', X'93F78DDFEA436F439F7E8720ED7BEECF', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB3D27770F761F419C6F8E1EF61867E5', X'B9216D51008C7F47BD43521A81F7A172', X'B9216D51008C7F47BD43521A81F7A172', 0, NULL, NULL, 2, NULL, 0, 'Rinse ribs under cool water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0A5A6619B98EC45A586CC4E837C44CA', X'B9216D51008C7F47BD43521A81F7A172', X'B9216D51008C7F47BD43521A81F7A172', 1, NULL, NULL, 2, NULL, 0, 'Remove any large areas of fat on meaty side of ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE9E5F9D1DF83A4F9DE0083BA32D81A9', X'B9216D51008C7F47BD43521A81F7A172', X'B9216D51008C7F47BD43521A81F7A172', 2, NULL, NULL, 2, NULL, 0, 'Flip the ribs over and remove the membrane from the bone side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9790CEDEE4661542969EF68E2494F4C5', X'B9216D51008C7F47BD43521A81F7A172', X'B9216D51008C7F47BD43521A81F7A172', 3, NULL, NULL, 2, NULL, 0, 'Wet the ribs with apple cider vinegar');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E72E669E113EAE44B1C5B00066F51790', X'B9216D51008C7F47BD43521A81F7A172', X'B9216D51008C7F47BD43521A81F7A172', 4, NULL, NULL, 2, NULL, 0, 'Apply a generous coat of Jeff''s rub to the bone side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4021BDB219954644BB423A55FAACF1DC', X'B9216D51008C7F47BD43521A81F7A172', X'B9216D51008C7F47BD43521A81F7A172', 5, NULL, NULL, 2, NULL, 0, 'Turn the ribs over and apply the vinegar and rub to the meaty side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF38428BD1FBC24AA5F4A55EE7FD2D33', X'B9216D51008C7F47BD43521A81F7A172', X'B9216D51008C7F47BD43521A81F7A172', 6, NULL, NULL, 2, NULL, 0, 'Cut the rack of ribs in half');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6622473E9CED974DA3859F28EB3ACB51', X'903E6A9660B8FC4783371C0DD3A81D32', X'903E6A9660B8FC4783371C0DD3A81D32', 0, NULL, NULL, 2, NULL, 0, 'Slice two onions into ¼ inch slices and separate the rings');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FB47A7A4922D144B1F5CB993EB59199', X'903E6A9660B8FC4783371C0DD3A81D32', X'903E6A9660B8FC4783371C0DD3A81D32', 1, NULL, NULL, 2, NULL, 0, 'Place the rings and 8-10 garlic cloves in a large foil pan');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCDC479177623F449094D8B872742DAD', X'903E6A9660B8FC4783371C0DD3A81D32', X'903E6A9660B8FC4783371C0DD3A81D32', 2, NULL, NULL, 2, NULL, 0, 'Pour enough cola to fill the pan to about ½ full');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EF6B836880CBB49B40BCFA9C2BA2281', X'903E6A9660B8FC4783371C0DD3A81D32', X'903E6A9660B8FC4783371C0DD3A81D32', 3, NULL, NULL, 2, NULL, 0, 'Lay one of the rib halves on top of the onions and garlic');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C80CA314D06A434BB1B307BB9485537B', X'903E6A9660B8FC4783371C0DD3A81D32', X'903E6A9660B8FC4783371C0DD3A81D32', 4, NULL, NULL, 2, NULL, 0, 'Prepare a 2nd pan in the same manner for the other half of the ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58FAA6E28C1CF340BFC7295371D76F80', X'8D5B07E515DD934E9A13CBAC2F1C4A7A', X'8D5B07E515DD934E9A13CBAC2F1C4A7A', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker to cook at about 250°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6BE33D896914C4F98CC722346C3DB0A', X'8D5B07E515DD934E9A13CBAC2F1C4A7A', X'8D5B07E515DD934E9A13CBAC2F1C4A7A', 1, NULL, NULL, 2, NULL, 0, 'Place the pan(s) of ribs onto the smoker grates');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B23FEA1BA3384147A9D8B14BB1FC768D', X'8D5B07E515DD934E9A13CBAC2F1C4A7A', X'8D5B07E515DD934E9A13CBAC2F1C4A7A', 2, NULL, NULL, 2, NULL, 0, 'Cook for 5-6 hours or until they ribs are as tender as you like them');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D269BEFC8D754899ADB3526947D846', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 0, NULL, NULL, 2, NULL, 0, 'Remove chicken from package and give them a good rinse');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8502807366BD0D42BF7D222705BE6838', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 1, NULL, NULL, 2, NULL, 0, 'Pat them dry and lay them on a flat surface such as a cutting board or plate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB1D30D545E06F439EC738D19AA09D93', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 2, NULL, NULL, 2, NULL, 0, 'Apply a light coat of Jeff''s rub on the top side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3D7237B0F231F4594D1BFAF35620A8A', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 3, NULL, NULL, 2, NULL, 0, 'Flip them over and lay (2) stalks of asparagus on the chicken');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABB0763E4FD20640B3135000560DEA83', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 4, NULL, NULL, 2, NULL, 0, 'Fold the chicken over the top of the asparagus');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D0405AF080F0E4A91DC6EDDAE4E350B', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 5, NULL, NULL, 2, NULL, 0, 'Hold chicken in place while you wrap a long piece of thin sliced bacon around it');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F39BDC261AD0C14682E28204BCFF42F6', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 6, NULL, NULL, 2, NULL, 0, 'Insert a toothpick through the bacon and chicken to keep everything in place');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3A72B7D1B2873499E97D162FDE242C7', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle more of my rub onto the outside of the bacon wrapped chicken breast');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F01C4F54604805489389EBCC67429AF7', X'D15F0EBCD292B14184D07A04C71E79A9', X'D15F0EBCD292B14184D07A04C71E79A9', 8, NULL, NULL, 2, NULL, 0, 'Place prepared chicken breasts into a lidded container and into the fridge overnight or at least 4 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F99FABD5B97D5419814961DB1EC4E3A', X'2AFCCE6F52AD2148881A33C20C68686E', X'2AFCCE6F52AD2148881A33C20C68686E', 0, NULL, NULL, 2, NULL, 0, 'Set up smoker with indirect heat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'556D03EFE5AE58419A7F4D56F9C506FE', X'2AFCCE6F52AD2148881A33C20C68686E', X'2AFCCE6F52AD2148881A33C20C68686E', 1, NULL, NULL, 2, NULL, 0, 'Preheat to 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9AB2F12D793B54EB596E8BFB6857F55', X'2AFCCE6F52AD2148881A33C20C68686E', X'2AFCCE6F52AD2148881A33C20C68686E', 2, NULL, NULL, 2, NULL, 0, 'When smoker is holding a steady temperature place chicken breasts directly on the grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EA09EC215298642A392B1D46E1E57D5', X'2AFCCE6F52AD2148881A33C20C68686E', X'2AFCCE6F52AD2148881A33C20C68686E', 3, NULL, NULL, 2, NULL, 0, 'Smoke for about 1.5 hours or until they reach 165°F in the thickest part');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3B03D1BEFA60C43B2ABE4BA17786AA2', X'2AFCCE6F52AD2148881A33C20C68686E', X'2AFCCE6F52AD2148881A33C20C68686E', 4, NULL, NULL, 2, NULL, 0, 'Apply hickory/apple or your favorite wood smoke for at least 1 hour');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D328CBE4F48F7A4289E743D036E31737', X'915501210CB7A54EB5C5121CD67EFCCB', X'915501210CB7A54EB5C5121CD67EFCCB', 0, NULL, NULL, 2, NULL, 0, 'Add the following ingredients to a food processor and mix until well combined: About a cup of fresh thyme sprigs, 1-½ sprigs of fresh rosemary, 4 cloves of garlic, 2 heaping tablespoons of Jeff''s original rub, ½ cup of olive oil.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED532E252B38B84D9F8CE381D43BB509', X'F8C54AFC3743424FBAB782A52FC37E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', 0, NULL, NULL, 2, NULL, 0, 'Trim off all exterior fat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B492301EDD02924F941C0A0A5FFB1B98', X'F8C54AFC3743424FBAB782A52FC37E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', 1, NULL, NULL, 2, NULL, 0, 'Make diagonal cuts into the meat about ¼ inch deep and about 1 inch apart all over the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D4C3D7616FBA84B8A2898B9688E9384', X'F8C54AFC3743424FBAB782A52FC37E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', 2, NULL, NULL, 2, NULL, 0, 'Add a good sprinkling of Jeff''s rub all over the meat and work it in good.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9811E6BF90CF63468A5778EACA49AE92', X'F8C54AFC3743424FBAB782A52FC37E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', 3, NULL, NULL, 2, NULL, 0, 'Add half of the herb rub to the top of the pork loin and work it into the top and sides of the meat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA5C68A6D262C94C8C7C8545E8027E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', 4, NULL, NULL, 2, NULL, 0, 'Flip the meat over and apply the other half of the herb rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB4798B4A334D47BE790EA462303C58', X'F8C54AFC3743424FBAB782A52FC37E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', 5, NULL, NULL, 2, NULL, 0, 'Place the loin in a lidded container and place in the fridge overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C80F87A10BF05641A6B4A58C977177AE', X'F8C54AFC3743424FBAB782A52FC37E7E', X'F8C54AFC3743424FBAB782A52FC37E7E', 6, NULL, NULL, 2, NULL, 0, 'About 30 minutes before ready to smoke, remove meat from fridge and tie it up using kitchen twine to make it nice and round instead of oblong.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B1BF259B60FEE4892CD806D730B12EF', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 225°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3901C4EE5981524E888DEEE288594100', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', 1, NULL, NULL, 2, NULL, 0, 'Place the pork loin directly on the grate and let it cook for 3-4 hours or until it reaches 145°F n the center of the roast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'007FF197F830414791D7AE2D84D018CE', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', 2, NULL, NULL, 2, NULL, 0, 'Remove from the smoker and carve into ½ inch slices.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B9C35510B844C4E8E1C0D4958577B09', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', X'EB4E4B56697F4A4DB6BFFCEA5D0E48FE', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE7F648762DDE149A6A920A2A7DA8F94', X'21E9E44AAE35624DBBEC18F722D82DBD', X'21E9E44AAE35624DBBEC18F722D82DBD', 0, NULL, NULL, 2, NULL, 0, 'Make patties that are about ½ inch thick and 4 to 5 inches in diameter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7217512A41BE442BA034E4F1AE89AC0', X'21E9E44AAE35624DBBEC18F722D82DBD', X'21E9E44AAE35624DBBEC18F722D82DBD', 1, NULL, NULL, 2, NULL, 0, 'Lay the patties flat on a plate or flat surface');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BE4D68E0EBC6A4BBABADC5EA567C4B6', X'21E9E44AAE35624DBBEC18F722D82DBD', X'21E9E44AAE35624DBBEC18F722D82DBD', 2, NULL, NULL, 2, NULL, 0, 'Pour on a little Worcestershire sauce to wet the surface of the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'930125D8CB318A44B308DED78EB02291', X'21E9E44AAE35624DBBEC18F722D82DBD', X'21E9E44AAE35624DBBEC18F722D82DBD', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle rub on the top of the burger patties.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CA27FA2CB5D2748A92BBA2C78B725F2', X'21E9E44AAE35624DBBEC18F722D82DBD', X'21E9E44AAE35624DBBEC18F722D82DBD', 4, NULL, NULL, 2, NULL, 0, 'Wait about 10 minutes for the `wet` look then flip them over and repeat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E94865F6153BB409ED6311DF8C1B2A8', X'28413D980A06434E98519E9EA9016A50', X'28413D980A06434E98519E9EA9016A50', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7EFCB515D33674EB4D8366A4EDAB082', X'28413D980A06434E98519E9EA9016A50', X'28413D980A06434E98519E9EA9016A50', 1, NULL, NULL, 2, NULL, 0, 'Place burgers directly on grate over indirect heat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1BEB779FD357B48A193672A00284903', X'28413D980A06434E98519E9EA9016A50', X'28413D980A06434E98519E9EA9016A50', 2, NULL, NULL, 2, NULL, 0, 'Smoke cook for 60-90 minutes applying hickory or other wood smoke the entire time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C825739B3CEC1049B33A016A91C31D94', X'28413D980A06434E98519E9EA9016A50', X'28413D980A06434E98519E9EA9016A50', 3, NULL, NULL, 2, NULL, 0, 'The burgers are finished when they reach 160 °F in the center or your desired level of doneness.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEBCADC38A3EFC4C856B0FC5F0E9635C', X'9984248BD3238646BBBF0A9D0B3DC2F2', X'9984248BD3238646BBBF0A9D0B3DC2F2', 0, NULL, NULL, 2, NULL, 0, 'Remove hotdogs from package');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B83ED0E656BB404E8EF147BAB4CA160C', X'9984248BD3238646BBBF0A9D0B3DC2F2', X'9984248BD3238646BBBF0A9D0B3DC2F2', 1, NULL, NULL, 2, NULL, 0, 'Roll hotdogs in Jeff''s rub making sure to not miss the ends');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9677BEABF42384B92943893E8303C62', X'A78F1A30ADFD174991437FABC670FE84', X'A78F1A30ADFD174991437FABC670FE84', 0, NULL, NULL, 2, NULL, 0, 'Prepare smoker for cooking at 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DBE7B19837FC54AB428FFD8CF2A8648', X'A78F1A30ADFD174991437FABC670FE84', X'A78F1A30ADFD174991437FABC670FE84', 1, NULL, NULL, 2, NULL, 0, 'Once smoker is ready, place hotdogs directly on smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'847B0F8A1B2C26488241F5ABFB11E7B1', X'A78F1A30ADFD174991437FABC670FE84', X'A78F1A30ADFD174991437FABC670FE84', 2, NULL, NULL, 2, NULL, 0, 'Apply hickory or other wood smoke the entire time');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'098B8F116E3D9F43BDDA09F1495009C9', X'A78F1A30ADFD174991437FABC670FE84', X'A78F1A30ADFD174991437FABC670FE84', 3, NULL, NULL, 2, NULL, 0, 'Let the hotdogs cook for about an hour or until they are heated through');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8731463A5B0804595CF5A041B93F47D', X'A78F1A30ADFD174991437FABC670FE84', X'A78F1A30ADFD174991437FABC670FE84', 4, NULL, NULL, 2, NULL, 0, 'Remove from smoker and serve immediately');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A27589FB7DB644C8C515CDEBA96C229', X'3893F1A0EC9B464BA942A0E2872CD60A', X'3893F1A0EC9B464BA942A0E2872CD60A', 0, NULL, NULL, 2, NULL, 0, 'Apply a coat of regular yellow mustard to all sides of the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A824EE68320ED439B8D482F26C584E2', X'3893F1A0EC9B464BA942A0E2872CD60A', X'3893F1A0EC9B464BA942A0E2872CD60A', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle a generous amount of Jeff''s original rub onto all sides of the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'362E4A7E7FDD6C4A8D1B3D6E32457648', X'3893F1A0EC9B464BA942A0E2872CD60A', X'3893F1A0EC9B464BA942A0E2872CD60A', 2, NULL, NULL, 2, NULL, 0, 'Wait for about 20-30 minutes until the meat starts to get a `wet` look');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD051D55B69DE2458C1D4433AF15E33D', X'5284B419A90F4048A156A46B577CCBA9', X'5284B419A90F4048A156A46B577CCBA9', 0, NULL, NULL, 2, NULL, 0, 'Place meat directly on grate or use a rack');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34807327EDFD424EA0049F1F0D218CAC', X'5284B419A90F4048A156A46B577CCBA9', X'5284B419A90F4048A156A46B577CCBA9', 1, NULL, NULL, 2, NULL, 0, 'Smoke for 4-5 hours at 225-240°F or until the meat reaches 170°F in the center');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9CA82FC8A5AED448DB8F94F15581064', X'5284B419A90F4048A156A46B577CCBA9', X'5284B419A90F4048A156A46B577CCBA9', 2, NULL, NULL, 2, NULL, 0, 'Bring the meat into the house and let it cool for about 30 minutes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D88E129FA9A9244C920C801377D8AC29', X'5284B419A90F4048A156A46B577CCBA9', X'5284B419A90F4048A156A46B577CCBA9', 3, NULL, NULL, 2, NULL, 0, 'Once cool, cut the meat into 1-inch by 1-inch cubes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A69F558B2B28BA4CB861E7BA5F961003', X'03933C367FBD894D82467AEB04D749B4', X'03933C367FBD894D82467AEB04D749B4', 0, NULL, NULL, 2, NULL, 0, 'Mix 1 stick of melted butter with 1 TBS of your favorite hot sauce (I used Tabasco with Habanero)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B47285F73751D846B74AFAFDD4974DCD', X'03933C367FBD894D82467AEB04D749B4', X'03933C367FBD894D82467AEB04D749B4', 1, NULL, NULL, 2, NULL, 0, 'Use an injector to inject each piece with butter until the butter runs out of the meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'434CC8E97047A34CA4A2BFF589593ED5', X'03933C367FBD894D82467AEB04D749B4', X'03933C367FBD894D82467AEB04D749B4', 2, NULL, NULL, 2, NULL, 0, 'Place injected pieces into an open foil pan');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18EA94E405EF4547B5978E53BF22B811', X'03933C367FBD894D82467AEB04D749B4', X'03933C367FBD894D82467AEB04D749B4', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on more rub to create a nice bark all over');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B14CC754BF5444DB6330FEECBB8A211', X'2D41AFD32698134BA6CBD7F26A984F6C', X'2D41AFD32698134BA6CBD7F26A984F6C', 0, NULL, NULL, 2, NULL, 0, 'Place the pan of injected pieces back into the smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEE341131282CC47AFD0E6372BCEEC61', X'2D41AFD32698134BA6CBD7F26A984F6C', X'2D41AFD32698134BA6CBD7F26A984F6C', 1, NULL, NULL, 2, NULL, 0, 'Continue to smoke cook for about 2 hours or until they get tender to your liking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDBBBA48E128134E98E28975738BB7B3', X'2D41AFD32698134BA6CBD7F26A984F6C', X'2D41AFD32698134BA6CBD7F26A984F6C', 2, NULL, NULL, 2, NULL, 0, 'Stir occasionally while they cook');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'973A614BEFBF4E4A8C1FB6348837EE24', X'5AAEC575661068428CE62CB02159D571', X'5AAEC575661068428CE62CB02159D571', 0, NULL, NULL, 2, NULL, 0, 'Serve immediately on toothpicks as an appetizer, piled onto a bun or as an entree with several vegetables or sides.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B490A1A2932ABD46B9DA73BE4F3C95DA', X'C1E761F328C12B4EA51AE308246F87F2', X'C1E761F328C12B4EA51AE308246F87F2', 0, NULL, NULL, 2, NULL, 0, 'If you purchased a slab, remove the membrane from the boney side of the ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4297B43CA581544489FD55049CEBCB55', X'C1E761F328C12B4EA51AE308246F87F2', X'C1E761F328C12B4EA51AE308246F87F2', 1, NULL, NULL, 2, NULL, 0, 'Cut them up into individual ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91AD63F50A1E36448BA1239D3E43697F', X'307D25E400E1FD48ABA097E356F2E08D', X'307D25E400E1FD48ABA097E356F2E08D', 0, NULL, NULL, 2, NULL, 0, 'Place the ribs into a bowl or container');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E2F0937B9AEFD499B7C3767A8200B82', X'307D25E400E1FD48ABA097E356F2E08D', X'307D25E400E1FD48ABA097E356F2E08D', 1, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto the meat and sprinkle rub onto the top and sides of the ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B6C88D3E432564A8C3EDA852A8B5139', X'307D25E400E1FD48ABA097E356F2E08D', X'307D25E400E1FD48ABA097E356F2E08D', 2, NULL, NULL, 2, NULL, 0, 'Cover the container and place the ribs in the fridge for 2 hours to dry brine');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43FDE75B03D18942B7F02BE65C52C894', X'307D25E400E1FD48ABA097E356F2E08D', X'307D25E400E1FD48ABA097E356F2E08D', 3, NULL, NULL, 2, NULL, 0, 'After 2 hours, remove the ribs from the fridge and allow them to sit on the counter for about 30 minutes (do not rinse off the rub)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87ED7C95F27E5F4D81CD72AA2621F6E6', X'2ECEE523CFEB934D9F8FB3795C5275AC', X'2ECEE523CFEB934D9F8FB3795C5275AC', 0, NULL, NULL, 2, NULL, 0, 'Place the ribs bone side down directly on the smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3E91D59E1761B478F042141F60AFBC7', X'2ECEE523CFEB934D9F8FB3795C5275AC', X'2ECEE523CFEB934D9F8FB3795C5275AC', 1, NULL, NULL, 2, NULL, 0, 'Smoke at 225 °F for 4-5 hours or until they get as tender as you like them');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'214A16DC6B69EB4D8EB38695A3E2CF83', X'ABDAA5F8C9FEA749898C9594FBD38996', X'ABDAA5F8C9FEA749898C9594FBD38996', 0, NULL, NULL, 2, NULL, 0, 'Serve immediately with a couple of sides such as savory potatoes and sautéed green beans');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E60618A12C949479D42668D83C8073F', X'6B9AB63D36651949A71B3BEC69A06C8E', X'6B9AB63D36651949A71B3BEC69A06C8E', 0, NULL, NULL, 2, NULL, 0, 'Remove any extra, loose fat from meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC57488EF9AC284C9413645A2F763C30', X'6B9AB63D36651949A71B3BEC69A06C8E', X'6B9AB63D36651949A71B3BEC69A06C8E', 1, NULL, NULL, 2, NULL, 0, 'Place meat in bowl and cover with buttermilk');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97B1B9B5AF82EA478CE2CAB0516F08B5', X'6B9AB63D36651949A71B3BEC69A06C8E', X'6B9AB63D36651949A71B3BEC69A06C8E', 2, NULL, NULL, 2, NULL, 0, 'Cover and place in fridge for 4 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCF8BEB86BD05643BEBC7F44AFFD2F99', X'6B9AB63D36651949A71B3BEC69A06C8E', X'6B9AB63D36651949A71B3BEC69A06C8E', 3, NULL, NULL, 2, NULL, 0, 'Remove from fridge and drain buttermilk.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AA7DF0559830F4BBA058A6B077F0F8B', X'6B9AB63D36651949A71B3BEC69A06C8E', X'6B9AB63D36651949A71B3BEC69A06C8E', 4, NULL, NULL, 2, NULL, 0, 'Discard buttermilk');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72055AE9022974789A2384A790D83EA', X'CE437A4F9A27284E9069E2D6644233E7', X'CE437A4F9A27284E9069E2D6644233E7', 0, NULL, NULL, 2, NULL, 0, 'Place single layer of meat into container');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC078061F81637459E41E143D5EBC51C', X'CE437A4F9A27284E9069E2D6644233E7', X'CE437A4F9A27284E9069E2D6644233E7', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle a generous coat of rub onto meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'160782E0B349CB4385E92D02169D3CC5', X'CE437A4F9A27284E9069E2D6644233E7', X'CE437A4F9A27284E9069E2D6644233E7', 2, NULL, NULL, 2, NULL, 0, 'Place another layer on top of the first');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1396D69F3BC78748B84725213D5AFCC4', X'CE437A4F9A27284E9069E2D6644233E7', X'CE437A4F9A27284E9069E2D6644233E7', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle a generous coat of rub onto 2nd layer of meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3628DEBF2ED56B44B2C1AB39907A06DF', X'CE437A4F9A27284E9069E2D6644233E7', X'CE437A4F9A27284E9069E2D6644233E7', 4, NULL, NULL, 2, NULL, 0, 'Repeat layers until all meat is coated with rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3D2395E296EF542AAD1C1BF0FF50ADA', X'CE437A4F9A27284E9069E2D6644233E7', X'CE437A4F9A27284E9069E2D6644233E7', 5, NULL, NULL, 2, NULL, 0, 'Cover and place in fridge for 2 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8376414C0ECE4541B4E5E2E65811F10B', X'CE437A4F9A27284E9069E2D6644233E7', X'CE437A4F9A27284E9069E2D6644233E7', 6, NULL, NULL, 2, NULL, 0, 'Remove from fridge');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3655340AA132043B3CD1ECA911EF7B5', X'4EC521C12A0BB64790364745A7B7B4FE', X'4EC521C12A0BB64790364745A7B7B4FE', 0, NULL, NULL, 2, NULL, 0, 'Stretch a piece of bacon until it is about 2x it''s normal length');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E9497113B99C43800944E97F38012C', X'4EC521C12A0BB64790364745A7B7B4FE', X'4EC521C12A0BB64790364745A7B7B4FE', 1, NULL, NULL, 2, NULL, 0, 'Wrap stretched piece of bacon around thigh');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1683EC87AAF7A241BBF8E027E4D4DB9B', X'4EC521C12A0BB64790364745A7B7B4FE', X'4EC521C12A0BB64790364745A7B7B4FE', 2, NULL, NULL, 2, NULL, 0, 'Lay on cookie sheet or Bradley rack');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82339EA3521BD74AAE66B8379E9BC97C', X'A7C7EA8882DAA24FA42A581B1BE03226', X'A7C7EA8882DAA24FA42A581B1BE03226', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 240°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DA310C170803540AE706A640DEF8DD5', X'A7C7EA8882DAA24FA42A581B1BE03226', X'A7C7EA8882DAA24FA42A581B1BE03226', 1, NULL, NULL, 2, NULL, 0, 'Smoke cook with cherry wood for about 2 hours or until center of meat reads 165°F using a digital meat thermometer or Thermapen');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'631C3A2E025B1C45B13D9F870D932857', X'A7C7EA8882DAA24FA42A581B1BE03226', X'A7C7EA8882DAA24FA42A581B1BE03226', 2, NULL, NULL, 2, NULL, 0, 'Serve immediately');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A289E499CF692A44AB516B145F12F64D', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 0, NULL, NULL, 2, NULL, 0, 'Make up a batch of my rub using my very own personal recipe (purchase recipe)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BDB9511E542FD4FA05FDF52FC3B0762', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 1, NULL, NULL, 2, NULL, 0, 'Apply the rub liberally all over a pork butt (be very generous with it)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A855B411A50E1040BEE21B26B6A0818E', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 2, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 225-240°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF05803C64F03F4195DB414732C8F69F', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 3, NULL, NULL, 2, NULL, 0, 'Place the meat fat side down directly on the smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4949C7B81C58074982FD5A5AAF0A5A15', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 4, NULL, NULL, 2, NULL, 0, 'Smoke cook for about 6 hours or until it reaches 160°F in the center');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91BE5117804D6A45B2B73DEED8BD6565', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 5, NULL, NULL, 2, NULL, 0, 'Wrap meat in foil (or place in foil pan covered tightly in foil) and continue to cook until the internal temperature reaches 205°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67408D6027DA0C4F86937CAA46AA2907', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 6, NULL, NULL, 2, NULL, 0, 'Remove foiled meat from smoker (carefully) and place on counter to rest for about an hour. (open foil slightly to allow some heat to escape)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D8FDECA9502E54D82BF6C2B8EF43BB1', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 7, NULL, NULL, 2, NULL, 0, 'Remove bone then pull meat into it’s natural sections');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED3405DEBB01534B86E6238EC7DC0B29', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 8, NULL, NULL, 2, NULL, 0, 'Spend a few minutes picking out any clumps of fat, gelatin-like substance, membrane, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10D7E0E57A21A2458936B202E236922F', X'B84BFC4D187C6D43AFE3C59BFC541FC7', X'B84BFC4D187C6D43AFE3C59BFC541FC7', 9, NULL, NULL, 2, NULL, 0, 'Pull the meat into small chunks and pieces using your hands or a couple of forks');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A242EE58C1E647A5D40429112570C4', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', 0, NULL, NULL, 2, NULL, 0, '(4)oz cream cheese');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ED00113C1D55B40A46990EA57929007', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', 1, NULL, NULL, 2, NULL, 0, '½ cup pulled pork (make sure it has cooled well)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D91CFB6724B734C8B49EDC0F482CA04', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', 2, NULL, NULL, 2, NULL, 0, '(1)TBS Jeff’s Rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4C87013E292EB4DB71020728E47770F', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', 3, NULL, NULL, 2, NULL, 0, '½ TBS Lemon Juice');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EDCF49D3359DF4CB54612FBC0AFFADC', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', 4, NULL, NULL, 2, NULL, 0, '(1/8) cup (2 TBS) finely chopped onions');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54D6A9BE059442478254D3A3BFBA4E91', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', X'3354D6BF090DBB4CBCFDBE4B9729DBCF', 5, NULL, NULL, 2, NULL, 0, '(1/8) cup (2 TBS) finely chopped red jalapeños (green will work if you can’t find the red ones)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EF7936920B7034EAD4794E81E55A79E', X'F61147D87DCC3A4D8C68E1C1CC28C647', X'F61147D87DCC3A4D8C68E1C1CC28C647', 0, NULL, NULL, 2, NULL, 0, 'Add all ingredients to a medium sized mixing bowl and fold together until combined.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBC72895C4206445B61DC2377989A0EF', X'1076E86682998C4F856C10377D3E3749', X'1076E86682998C4F856C10377D3E3749', 0, NULL, NULL, 2, NULL, 0, '(4) oz cream cheese');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18B76C1153E2084AA17E5C84B47EBCD2', X'1076E86682998C4F856C10377D3E3749', X'1076E86682998C4F856C10377D3E3749', 1, NULL, NULL, 2, NULL, 0, '(4) oz (¼ of a chub) ground breakfast sausage (like Jimmy Dean’s), Hot');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEA716B48E196A4C94FB5166A7F39056', X'1076E86682998C4F856C10377D3E3749', X'1076E86682998C4F856C10377D3E3749', 2, NULL, NULL, 2, NULL, 0, '(1)TBS Jeff’s Rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB7CD1C5E4FADA47A628B2D3A9CCEBDC', X'1076E86682998C4F856C10377D3E3749', X'1076E86682998C4F856C10377D3E3749', 3, NULL, NULL, 2, NULL, 0, '½ cup finely chopped onions');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E24064481A9A2540B7D6E2952EC1CE55', X'1076E86682998C4F856C10377D3E3749', X'1076E86682998C4F856C10377D3E3749', 4, NULL, NULL, 2, NULL, 0, '¼ cup finely chopped green jalapeños');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'574A10D95726CA4E900E31CA7C1A1D82', X'1076E86682998C4F856C10377D3E3749', X'1076E86682998C4F856C10377D3E3749', 5, NULL, NULL, 2, NULL, 0, '½ TBS lemon juice');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B10ACB8C6B74A4584083B370E6376AB', X'1076E86682998C4F856C10377D3E3749', X'1076E86682998C4F856C10377D3E3749', 6, NULL, NULL, 2, NULL, 0, '¼ cup of hand grated parmesan cheese');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0F615B31C258D45A4D76B21B50CFD60', X'8032D806C45FF84999603BB3872E234C', X'8032D806C45FF84999603BB3872E234C', 0, NULL, NULL, 2, NULL, 0, 'Cook the sausage in a frying pan over medium heat until well browned then remove to cool.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF7E1D909F5D654BA47D35B5BA849172', X'8032D806C45FF84999603BB3872E234C', X'8032D806C45FF84999603BB3872E234C', 1, NULL, NULL, 2, NULL, 0, 'Put onions and jalapeños into the same frying pan without adjusting the heat and cook for about 2-3 minutes stirring frequently.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'923ABC993DDCB54C8ECEECAB74CFA3DC', X'8032D806C45FF84999603BB3872E234C', X'8032D806C45FF84999603BB3872E234C', 2, NULL, NULL, 2, NULL, 0, 'Once the sausage, onions and jalapeños are cooled, add all ingredients to a medium sized mixing bowl and fold together until combined.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7418FBCF5456B64B9B47A0CD92F6628E', X'A5A8BD8DE7BC96489392AC81CC7D580D', X'A5A8BD8DE7BC96489392AC81CC7D580D', 0, NULL, NULL, 2, NULL, 0, 'Remove stems from mushrooms');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'544152C47C75B7468EE80211C7345CB6', X'A5A8BD8DE7BC96489392AC81CC7D580D', X'A5A8BD8DE7BC96489392AC81CC7D580D', 1, NULL, NULL, 2, NULL, 0, 'Wash mushrooms under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAC043D1736A074E890631BD033279FB', X'A5A8BD8DE7BC96489392AC81CC7D580D', X'A5A8BD8DE7BC96489392AC81CC7D580D', 2, NULL, NULL, 2, NULL, 0, 'Use a teaspoon to fill each mushroom with desired filling');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CD8A0034560DE4DA3F3BE44894350BB', X'A5A8BD8DE7BC96489392AC81CC7D580D', X'A5A8BD8DE7BC96489392AC81CC7D580D', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle some extra rub on the mushrooms for good measure');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DB9E9117BCD164E87D461A246C0A805', X'37D84ACA7F6A2C44BC4092980DF88334', X'37D84ACA7F6A2C44BC4092980DF88334', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94BC45270F5CA843B2E23EBC636053E3', X'37D84ACA7F6A2C44BC4092980DF88334', X'37D84ACA7F6A2C44BC4092980DF88334', 1, NULL, NULL, 2, NULL, 0, 'Place the mushrooms directly on the smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'099A014742FE6948A810CFC9798C1F8C', X'37D84ACA7F6A2C44BC4092980DF88334', X'37D84ACA7F6A2C44BC4092980DF88334', 2, NULL, NULL, 2, NULL, 0, 'Smoke for 45-60 minutes or until they are warm in the center and slightly browned on top');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'379C9F2153278C499F3BAE832A7FC4C5', X'37D84ACA7F6A2C44BC4092980DF88334', X'37D84ACA7F6A2C44BC4092980DF88334', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7A2106D1FD4248B6750A0F72A4D8B1', X'16D66E285426C84789F15C21291BA03E', X'16D66E285426C84789F15C21291BA03E', 0, NULL, NULL, 2, NULL, 0, 'Put all ingredients except ice in a large pot and bring to a boil stirring occasionally.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14DFA3A91B6E92449B17E13A1B376CA2', X'16D66E285426C84789F15C21291BA03E', X'16D66E285426C84789F15C21291BA03E', 1, NULL, NULL, 2, NULL, 0, 'Cover and let sit for about 10 minutes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7C91B2901B24E48864F30D050A2BABF', X'16D66E285426C84789F15C21291BA03E', X'16D66E285426C84789F15C21291BA03E', 2, NULL, NULL, 2, NULL, 0, 'Add ice cubes and stir to cool down. Finish cooling in fridge if necessary.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B92EF8C427DEA4439BD26185072DBFF9', X'BDD77787C5083C4A8B5CBEE41FC1B494', X'BDD77787C5083C4A8B5CBEE41FC1B494', 0, NULL, NULL, 2, NULL, 0, 'Place the chickens in a zip top bag or other brining container');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29DCC6B588D6724BA394672B454F6596', X'BDD77787C5083C4A8B5CBEE41FC1B494', X'BDD77787C5083C4A8B5CBEE41FC1B494', 1, NULL, NULL, 2, NULL, 0, 'Pour cold brine over birds to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88022C35B0DFF4488088A25C8871094A', X'BDD77787C5083C4A8B5CBEE41FC1B494', X'BDD77787C5083C4A8B5CBEE41FC1B494', 2, NULL, NULL, 2, NULL, 0, 'Place brining container/bag in the fridge for 4 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16BFF82FE9964E4293E2E9E8ED122D1C', X'BDD77787C5083C4A8B5CBEE41FC1B494', X'BDD77787C5083C4A8B5CBEE41FC1B494', 3, NULL, NULL, 2, NULL, 0, 'After 4 hours, rinse cornish hens under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7AB53C5CBB57A448A1C5B7A7FF7BBA9', X'993F173D2BBE4340BA6D4E5CE0EE0FCC', X'993F173D2BBE4340BA6D4E5CE0EE0FCC', 0, NULL, NULL, 2, NULL, 0, 'Apply some spicy brown mustard to the outside of the hens');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9B163D4FDBBAD408989F6C77CFBBE5E', X'993F173D2BBE4340BA6D4E5CE0EE0FCC', X'993F173D2BBE4340BA6D4E5CE0EE0FCC', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s rub generously onto hens');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'010AB6C6D0118544B5842E602E6F77B9', X'85323408CE66BF489244854D81600C01', X'85323408CE66BF489244854D81600C01', 0, NULL, NULL, 2, NULL, 0, 'Place meat breast side up directly on smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0272E94E1D34D40A339C65DE949DD56', X'85323408CE66BF489244854D81600C01', X'85323408CE66BF489244854D81600C01', 1, NULL, NULL, 2, NULL, 0, 'Smoke cook at 250°F for about 2 hrs or until they reach 165°F in the thickest part of the breast or thigh');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15D0657D8029004D9E7B9EBBD02704BF', X'85323408CE66BF489244854D81600C01', X'85323408CE66BF489244854D81600C01', 2, NULL, NULL, 2, NULL, 0, 'Brush on beer barbecue sauce (recipe below) about 30 minutes before chickens are done cooking. (reserve some for dipping later)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33D96F01D1ED9489816E9B111C2314B', X'CCDE4E829374DA4F82B64D3E06FE1517', X'CCDE4E829374DA4F82B64D3E06FE1517', 0, NULL, NULL, 2, NULL, 0, 'Serve immediately giving each person their own cornish hen.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CA4DE145C6C9E4590AED8FB79FCE0F3', X'324DC798A1224C4EA3C3D7B23F4BB45A', X'324DC798A1224C4EA3C3D7B23F4BB45A', 0, NULL, NULL, 2, NULL, 0, 'Add all of the beer barbecue sauce ingredients into a small saucepan.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E22EC181A828C741A874FAF60B5928BD', X'324DC798A1224C4EA3C3D7B23F4BB45A', X'324DC798A1224C4EA3C3D7B23F4BB45A', 1, NULL, NULL, 2, NULL, 0, 'Stir to combine and simmer over very low heat until ready to use.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ACC5750EC38F94E948E76AA212C5CC6', X'D2CDDF18D4B4984FAE22AA09E991EE65', X'D2CDDF18D4B4984FAE22AA09E991EE65', 0, NULL, NULL, 2, NULL, 0, 'Remove from packaging and rinse well under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A32DA54FAD5F814C90F3F1921867687A', X'D2CDDF18D4B4984FAE22AA09E991EE65', X'D2CDDF18D4B4984FAE22AA09E991EE65', 1, NULL, NULL, 2, NULL, 0, 'Pat dry with paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'498743CA714CBE4086647352658E4B38', X'D2CDDF18D4B4984FAE22AA09E991EE65', X'D2CDDF18D4B4984FAE22AA09E991EE65', 2, NULL, NULL, 2, NULL, 0, 'Brush some maple syrup to add flavor and help the rub to stick');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98E476DCCD1C18428A9CFAAA4E034C14', X'D2CDDF18D4B4984FAE22AA09E991EE65', X'D2CDDF18D4B4984FAE22AA09E991EE65', 3, NULL, NULL, 2, NULL, 0, 'Apply Jeff''s rub all over the pork tenderloins');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AEBACC9B2F9AC4E9FC1A7978149EA9A', X'56E0947ACE9AD24982C4EF3899570758', X'56E0947ACE9AD24982C4EF3899570758', 0, NULL, NULL, 2, NULL, 0, 'Get the smoker going and let it level out at 225°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4E49B1522974048BF255376BF3D426E', X'56E0947ACE9AD24982C4EF3899570758', X'56E0947ACE9AD24982C4EF3899570758', 1, NULL, NULL, 2, NULL, 0, 'Place the meat directly on the smoker grate');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3038600D178224DA18B66EF8DEA8402', X'56E0947ACE9AD24982C4EF3899570758', X'56E0947ACE9AD24982C4EF3899570758', 2, NULL, NULL, 2, NULL, 0, 'Use peach wood or other fruity wood to apply smoke for entire session');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF80FD8B36FC1F4B914710FFC69E2E2C', X'56E0947ACE9AD24982C4EF3899570758', X'56E0947ACE9AD24982C4EF3899570758', 3, NULL, NULL, 2, NULL, 0, 'Meat is done when a trusted meat thermometer reads 145°F in the thickest part');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5A37F852D97254BAEEF3BA0F2048AE4', X'2693B8C069456C40A58DC8F8A6A9EC85', X'2693B8C069456C40A58DC8F8A6A9EC85', 0, NULL, NULL, 2, NULL, 0, 'Allow the meat to rest on the counter under foil for 25-30 minutes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED068BB0BC708945BA68F3F8E5370E8C', X'2693B8C069456C40A58DC8F8A6A9EC85', X'2693B8C069456C40A58DC8F8A6A9EC85', 1, NULL, NULL, 2, NULL, 0, 'Slice into ¼ inch medallions and serve immediately.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C4C605C21939246BF59FFEA7DBF3C1C', X'CCC0906D0FF33A4E9D774957B3367770', X'CCC0906D0FF33A4E9D774957B3367770', 0, NULL, NULL, 2, NULL, 0, '½ cup peach jam');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0AAD3065F646B49AE4B143AE536836C', X'CCC0906D0FF33A4E9D774957B3367770', X'CCC0906D0FF33A4E9D774957B3367770', 1, NULL, NULL, 2, NULL, 0, '½ cup Jeff''s barbecue sauce');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20086FCE77EE5A448FD3BAEA3C04768A', X'52BF1B9A8B7E76439DCB76E1C897EF2E', X'52BF1B9A8B7E76439DCB76E1C897EF2E', 0, NULL, NULL, 2, NULL, 0, 'Put jam into a sauce pan over medium heat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DED801E74E63F54893967A2E73216CD3', X'52BF1B9A8B7E76439DCB76E1C897EF2E', X'52BF1B9A8B7E76439DCB76E1C897EF2E', 1, NULL, NULL, 2, NULL, 0, 'Once the jam starts to melt, add the barbecue sauce to the pan');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ABC47332A2F4B4995AE27CD319CF243', X'52BF1B9A8B7E76439DCB76E1C897EF2E', X'52BF1B9A8B7E76439DCB76E1C897EF2E', 2, NULL, NULL, 2, NULL, 0, 'Stir until it is well mixed and heated through');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95657451EE658942A35B73EA0CCB471B', X'3AB5306757D17347B8F9B3C77DE98118', X'3AB5306757D17347B8F9B3C77DE98118', 0, NULL, NULL, 2, NULL, 0, 'Rinse the brisket flat with cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09BF3CBBD424E840945E8AF79E36744F', X'3AB5306757D17347B8F9B3C77DE98118', X'3AB5306757D17347B8F9B3C77DE98118', 1, NULL, NULL, 2, NULL, 0, 'Pat the brisket dry with a paper towel and set aside');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6743D926F35182459F0399D5A06A0D04', X'3AB5306757D17347B8F9B3C77DE98118', X'3AB5306757D17347B8F9B3C77DE98118', 2, NULL, NULL, 2, NULL, 0, 'Mix 1 quart of water, 2 TBS of Tabasco habanero sauce and ¼ cup of kosher salt');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A84984ADD126C479674CE6FCCAD51B5', X'3AB5306757D17347B8F9B3C77DE98118', X'3AB5306757D17347B8F9B3C77DE98118', 3, NULL, NULL, 2, NULL, 0, 'Stir until salt is dissolved');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A62B68DB0FEE3B4296F664AC0087E4C0', X'3AB5306757D17347B8F9B3C77DE98118', X'3AB5306757D17347B8F9B3C77DE98118', 4, NULL, NULL, 2, NULL, 0, 'Place brisket is a gallon sized zip top bag or suitable brining container');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FA52E9B20F42E40878CEF87CBBC5885', X'3AB5306757D17347B8F9B3C77DE98118', X'3AB5306757D17347B8F9B3C77DE98118', 5, NULL, NULL, 2, NULL, 0, 'Pour orange juice brine over brisket until it is covered');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B82FB669BBD3B4496971D5FC8BA2162', X'3AB5306757D17347B8F9B3C77DE98118', X'3AB5306757D17347B8F9B3C77DE98118', 6, NULL, NULL, 2, NULL, 0, 'Seal bag and place in fridge overnight');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75FD5D8F58962145A0FCFF88EAB825C9', X'42228F69C52B144D94022E14A36D036C', X'42228F69C52B144D94022E14A36D036C', 0, NULL, NULL, 2, NULL, 0, 'Using cold water, rinse brisket with cold water to remove extra salt');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'455E7E498CB38347AC22396697A5DDFC', X'42228F69C52B144D94022E14A36D036C', X'42228F69C52B144D94022E14A36D036C', 1, NULL, NULL, 2, NULL, 0, 'Pat brisket dry with a paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EE47D2FBC35A64A9E9C5C81909E721F', X'42228F69C52B144D94022E14A36D036C', X'42228F69C52B144D94022E14A36D036C', 2, NULL, NULL, 2, NULL, 0, 'Apply a thin coat of yellow mustard to the outside of the brisket');
