INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2A22DED4C0DFE42AE251925CF162A6A', X'2B28FDB3F1273040A40A7BC00F390289', X'2B28FDB3F1273040A40A7BC00F390289', 0, NULL, NULL, 2, NULL, 0, 'Place brisket directly on smoker grate fat side down');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E48EE50BC2BC4B90D531BBEA2CAAC9', X'2B28FDB3F1273040A40A7BC00F390289', X'2B28FDB3F1273040A40A7BC00F390289', 1, NULL, NULL, 2, NULL, 0, 'Smoke at 240°F for 8-10 hours or until very tender');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1A80474397CE34F87C5405E60CBD810', X'2B28FDB3F1273040A40A7BC00F390289', X'2B28FDB3F1273040A40A7BC00F390289', 2, NULL, NULL, 2, NULL, 0, 'Wrap in foil when meat temperature reaches 160°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0090951261A53340A2D8ECE5D6608B6E', X'AF9B5B5CB350FA4EA68BE1D60377984E', X'AF9B5B5CB350FA4EA68BE1D60377984E', 0, NULL, NULL, 2, NULL, 0, 'Leave brisket in foil once it finishes and let it rest on counter for about 1 hour');
