INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93CA6B7B60B7174795572864390AF6A2', X'AAF5E52327E8A64D920D8F994A5FAA13', X'AAF5E52327E8A64D920D8F994A5FAA13', 4, NULL, NULL, 2, NULL, 0, 'Once your selected fillings are made and ready, set them aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B39BAD4B4F501C48946D81B4DFFC9E07', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', 0, NULL, NULL, 2, NULL, 0, 'Divide 1 pound of sausage into 10 equal sized balls');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'075B6472C3E6F8408DEDE405D1F7BC0D', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', 1, NULL, NULL, 2, NULL, 0, 'Flatten a ball of sausage onto your hand forming a boat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D87129B41E90441BF03460768A38B57', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', 2, NULL, NULL, 2, NULL, 0, 'Place about a TBS of filling into the boat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7DD292F888C9449974C996900D4C408', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', 3, NULL, NULL, 2, NULL, 0, 'Fold the ends of the boat together and pinch edges to seal');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29D8F9D9A4FFE74596E91661D077408A', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', 4, NULL, NULL, 2, NULL, 0, 'Form into shape by rolling the sausage in your hand');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34947F227769F44E90579EB7EAF58109', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', 5, NULL, NULL, 2, NULL, 0, 'Wrap an entire piece of bacon around the filled sausage');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC11060493607340AE482996650428BB', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', X'DBBE76E8AE4D0D4C8462F5F59ECD7714', 6, NULL, NULL, 2, NULL, 0, 'Roll the bacon wrapped sausages in a plate of rub');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'997AF8EE6E519A4E87D815FFF05DF064', X'186132F106D83449B890DDE4B9BB1CCB', X'186132F106D83449B890DDE4B9BB1CCB', 0, NULL, NULL, 2, NULL, 0, 'Place directly on the grate of your smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AAF78D4B6AB1C4C829EF9E0F79F9D1D', X'186132F106D83449B890DDE4B9BB1CCB', X'186132F106D83449B890DDE4B9BB1CCB', 1, NULL, NULL, 2, NULL, 0, 'Smoke at 225°F to 275°F for about 1 hour or until they reach 160°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5B1ACD47AED3744AD627C7C4F803820', X'186132F106D83449B890DDE4B9BB1CCB', X'186132F106D83449B890DDE4B9BB1CCB', 2, NULL, NULL, 2, NULL, 0, 'Serve immediately');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE5E1E82810519499DE151D65560DAB9', X'9915BA95CD5F9842B4E04768A6523F15', X'9915BA95CD5F9842B4E04768A6523F15', 0, NULL, NULL, 2, NULL, 0, 'Rinse steaks under cold water and pat dry with a paper towel');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F6A882BD649E341AAFF444296475DE8', X'9915BA95CD5F9842B4E04768A6523F15', X'9915BA95CD5F9842B4E04768A6523F15', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle about 1/4 tsp of coarse kosher salt onto the top of each steak and place in the fridge uncovered for 2-4 hours. No need to rinse when finished.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EA079C5DAE0DD428CD86A802881DE2B', X'9915BA95CD5F9842B4E04768A6523F15', X'9915BA95CD5F9842B4E04768A6523F15', 2, NULL, NULL, 2, NULL, 0, 'Coat steaks with olive oil');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F7E1B99714E154C942303FC57458170', X'9915BA95CD5F9842B4E04768A6523F15', X'9915BA95CD5F9842B4E04768A6523F15', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle Jeff''s original rub generously on both sides to cover meat');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85B1C8E88F62474AAD1853F85C42B3F8', X'9915BA95CD5F9842B4E04768A6523F15', X'9915BA95CD5F9842B4E04768A6523F15', 4, NULL, NULL, 2, NULL, 0, 'Place steaks in glass or plastic bowl with lid');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB1605F55DD5F943B828DA52E8AEDCD0', X'9915BA95CD5F9842B4E04768A6523F15', X'9915BA95CD5F9842B4E04768A6523F15', 5, NULL, NULL, 2, NULL, 0, 'Marinate in fridge for 6-8 hours or overnight');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E61389B8F96946AFABAF6909F9B71C', X'62FB3BCC052AC0409397FF3AA38BBA4A', X'62FB3BCC052AC0409397FF3AA38BBA4A', 0, NULL, NULL, 2, NULL, 0, 'Place steaks directly on the grate of smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD5893B9EA220C41B21C8C9CC2C92448', X'62FB3BCC052AC0409397FF3AA38BBA4A', X'62FB3BCC052AC0409397FF3AA38BBA4A', 1, NULL, NULL, 2, NULL, 0, 'Smoke at 225°F for about 45 minutes or until they reach 130℉ or desired doneness');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1D00D71C0A1E4458E8F012FCD0DAFF4', X'62FB3BCC052AC0409397FF3AA38BBA4A', X'62FB3BCC052AC0409397FF3AA38BBA4A', 2, NULL, NULL, 2, NULL, 0, 'Let steaks rest for 15-20 minutes before serving');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A7830DE1E18AE4487EEFDB35674DB0A', X'62FB3BCC052AC0409397FF3AA38BBA4A', X'62FB3BCC052AC0409397FF3AA38BBA4A', 3, NULL, NULL, 2, NULL, 0, 'Serve with smoked twice baked potatoes');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB661C65EC98A545A399F472B679EBE8', X'BBEF89D0C32B104FB81543E8D1721601', X'BBEF89D0C32B104FB81543E8D1721601', 0, NULL, NULL, 2, NULL, 0, 'Cut a slit along the top of the tail from the front all the way back to where the tail fin begins.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2135072442C9194E929C440FD0C378B1', X'BBEF89D0C32B104FB81543E8D1721601', X'BBEF89D0C32B104FB81543E8D1721601', 1, NULL, NULL, 2, NULL, 0, 'Lay the lobsters shell side up on a rack or grill pan and they are ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C5BA8DE4DD90F499273D162BF79A375', X'B10DB22F1EFFFA45A6D48C02DA40C17A', X'B10DB22F1EFFFA45A6D48C02DA40C17A', 0, NULL, NULL, 2, NULL, 0, 'If the shrimp are frozen, place them in a colander and run cold water over them for several minutes.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D462F3A08C11B843B7FB811C4BEAA7CC', X'B10DB22F1EFFFA45A6D48C02DA40C17A', X'B10DB22F1EFFFA45A6D48C02DA40C17A', 1, NULL, NULL, 2, NULL, 0, 'Remove shells and tails and place them in a bowl for seasoning');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE1FB7F50D6DFA4793F395104B34DA70', X'B10DB22F1EFFFA45A6D48C02DA40C17A', X'B10DB22F1EFFFA45A6D48C02DA40C17A', 2, NULL, NULL, 2, NULL, 0, 'Drizzle about ½ cup of olive oil over the shrimp and stir to coat. Then sprinkle on about a tablespoon of my original rub (Purchase formula here | Purchase bottled rub).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC1E8D4875E3D74490CC1D2D514AC937', X'B10DB22F1EFFFA45A6D48C02DA40C17A', X'B10DB22F1EFFFA45A6D48C02DA40C17A', 3, NULL, NULL, 2, NULL, 0, 'Stir to coat well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEBB8A7F4E6B2C42BB7B4D219945FCE8', X'B10DB22F1EFFFA45A6D48C02DA40C17A', X'B10DB22F1EFFFA45A6D48C02DA40C17A', 4, NULL, NULL, 2, NULL, 0, 'These shrimp were fairly small and I wanted to use my Bradley rack so I quickly skewered them with extra long (4-inch) toothpicks to make sure they could not fall through the holes. A Weber grill pan or even a simple cooling rack also works well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6D5D5771DABD34DAD3CD57A8643EB42', X'B10DB22F1EFFFA45A6D48C02DA40C17A', X'B10DB22F1EFFFA45A6D48C02DA40C17A', 5, NULL, NULL, 2, NULL, 0, 'About 3 per toothpick.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'128DD9486B156847AAA6001D224752DA', X'B10DB22F1EFFFA45A6D48C02DA40C17A', X'B10DB22F1EFFFA45A6D48C02DA40C17A', 6, NULL, NULL, 2, NULL, 0, 'The shrimp are now ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'113303698BE3E945A0FC3C72CF8BB896', X'7850AD64D40AE446A1ADC8D034D24FFA', X'7850AD64D40AE446A1ADC8D034D24FFA', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking indirect at about 225°F with pecan and/or apple wood.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914D9FAB72AF594181DA9E7C2520525E', X'6C2291CE0C273E4D8C2F1F7B76B737E6', X'6C2291CE0C273E4D8C2F1F7B76B737E6', 0, NULL, NULL, 2, NULL, 0, 'Place the lobster tails in the smoker first since it will take about 45-60 minutes. After about 30 minutes, place the shrimp in the smoker. This should get the lobster and the shrimp done at about the same time.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED1DF678615CF140A46E3C8C67120AE9', X'6C2291CE0C273E4D8C2F1F7B76B737E6', X'6C2291CE0C273E4D8C2F1F7B76B737E6', 1, NULL, NULL, 2, NULL, 0, 'While the lobsters are smoking, spread the tail open and drizzle with a mixture containing 1 stick of butter and 2 TBS of Jeff''s original rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06D97A56D91ED444A08EEB1540C20087', X'6C2291CE0C273E4D8C2F1F7B76B737E6', X'6C2291CE0C273E4D8C2F1F7B76B737E6', 2, NULL, NULL, 2, NULL, 0, '4-5 oz lobster tails will take about 45-60 minutes and will be bright red/orange and about 140°F in the center when they are finished cooking.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6E07A28CBEAF843A81C4DB0CE99B01F', X'6C2291CE0C273E4D8C2F1F7B76B737E6', X'6C2291CE0C273E4D8C2F1F7B76B737E6', 3, NULL, NULL, 2, NULL, 0, 'Medium shrimp (41-50/lb) will take about 20-25 minutes to finish cooking and will turn from translucent to opaque when they are finished. Do not overcook.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A222EC9140A33A4F835A74F1347A037A', X'E093AC3EB2600240B971990A2BEFC505', X'E093AC3EB2600240B971990A2BEFC505', 0, NULL, NULL, 2, NULL, 0, 'Place 3/4 cups of the shredded cheese in a large bowl; set aside the remaining cheese for later.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E0F6354427C84DBC0AC971EBB54B9D', X'E093AC3EB2600240B971990A2BEFC505', X'E093AC3EB2600240B971990A2BEFC505', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, heat the butter and cream over low heat until the butter is melted (about 8 minutes). Do not boil or turn up the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4EDD30D7D8E7348AA65481AA3339EBC', X'E093AC3EB2600240B971990A2BEFC505', X'E093AC3EB2600240B971990A2BEFC505', 2, NULL, NULL, 2, NULL, 0, 'Pour the heated cream and butter over the parmesan and stir to melt. Pour over cooked, drained pasta and toss to coat with sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43B30EFC903E7B4D9979C3FFE9643EBD', X'E093AC3EB2600240B971990A2BEFC505', X'E093AC3EB2600240B971990A2BEFC505', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining parmesan and toss gently. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9B59564F9422245953A021911402EAB', X'02CC2671A4F78B4F893D696D84B14D2D', X'02CC2671A4F78B4F893D696D84B14D2D', 0, NULL, NULL, 2, NULL, 0, 'Remove lobster meat from shell and slice each into 6-8 medallions.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1DA0C1C88D5394DA34BEC27260A9EFB', X'02CC2671A4F78B4F893D696D84B14D2D', X'02CC2671A4F78B4F893D696D84B14D2D', 1, NULL, NULL, 2, NULL, 0, 'Place fettuccine onto plate with 7-8 shrimp and lobster medallions. Serve immediately!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08CB67165DAA4D47817C3AB4F6CB05C4', X'68091EFC4EC70A4C97DA849A70CCF35C', X'68091EFC4EC70A4C97DA849A70CCF35C', 0, NULL, NULL, 2, NULL, 0, 'Chop 18 fresh cherries very fine and set aside');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'586B34AC25CBEC4DA1CAEAA4952B6E74', X'68091EFC4EC70A4C97DA849A70CCF35C', X'68091EFC4EC70A4C97DA849A70CCF35C', 1, NULL, NULL, 2, NULL, 0, 'Chop two cleaned/deseeded habaneros very fine and set aside (only use one habanero if you don''t want them to be very hot)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'490E9A96644A3045824515E37BBD3A57', X'8131F88F56FE534F8430C2F39C60AEC4', X'8131F88F56FE534F8430C2F39C60AEC4', 0, NULL, NULL, 2, NULL, 0, 'Fold in bread crumbs, rub, blue cheese (optional), eggs, cherries and habanero to ground chicken');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEA46D57421D094F9E0F23F27EBABD36', X'7F5A9EBDA146364F91A83C4525703C85', X'7F5A9EBDA146364F91A83C4525703C85', 0, NULL, NULL, 2, NULL, 0, 'Form 1-inch cherry bombs from the chicken mixture');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FA175CD09181B43B1D00C4564E9A7A2', X'7F5A9EBDA146364F91A83C4525703C85', X'7F5A9EBDA146364F91A83C4525703C85', 1, NULL, NULL, 2, NULL, 0, 'Pour some rub in a plate and roll the 1-inch balls in the rub to coat lightly');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91283E609B7CCC4C8455F05804E85E50', X'7F5A9EBDA146364F91A83C4525703C85', X'7F5A9EBDA146364F91A83C4525703C85', 2, NULL, NULL, 2, NULL, 0, 'Insert a toothpick into the center of the cherry bombs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DFB7AB710464144A39BBE06800EF64F', X'6437C78E12486F49AB53F69B018E3AB2', X'6437C78E12486F49AB53F69B018E3AB2', 0, NULL, NULL, 2, NULL, 0, 'Smoke the chicken cherry bombs at 225°F for about 1 hour or until they reach 165°F in the center');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DCB64F790BC7849AF7A61A7CAA29423', X'4854D92E27DC5447880250A7F8B86DA8', X'4854D92E27DC5447880250A7F8B86DA8', 0, NULL, NULL, 2, NULL, 0, 'Mix up some special sauce for the cherry bombs using one part Jeff''s sauce and one part Franks Original RedHot sauce');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AC4ADB57775BF47AF853C9B4324C51B', X'C96BDE5A4322FF4AB8DBC95A5D0D2C44', X'C96BDE5A4322FF4AB8DBC95A5D0D2C44', 0, NULL, NULL, 2, NULL, 0, 'Drizzle the sauce on the smoked chicken cherry bombs or serve on the side for dipping');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'589C230F0EB5EB448BE5B97A44E2B2DB', X'91763DF80050FE45B8C2914F439B686C', X'91763DF80050FE45B8C2914F439B686C', 0, NULL, NULL, 2, NULL, 0, 'Rinse salmon with cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D70E6EEB522394F85FAD29DFB644B2A', X'91763DF80050FE45B8C2914F439B686C', X'91763DF80050FE45B8C2914F439B686C', 1, NULL, NULL, 2, NULL, 0, 'On a cutting board, slice across the fish into 1 inch wide pieces');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CF9B6897E2A5C49ABB89727AEFED8D6', X'91763DF80050FE45B8C2914F439B686C', X'91763DF80050FE45B8C2914F439B686C', 2, NULL, NULL, 2, NULL, 0, 'Remove skin using a sharp knife');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'249494738D4BD3439994965CB43BEB8F', X'91763DF80050FE45B8C2914F439B686C', X'91763DF80050FE45B8C2914F439B686C', 3, NULL, NULL, 2, NULL, 0, 'Rinse fish under cold water to remove scales');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CA8DCD44C800443931FBBEF73E96347', X'5EE58C911FF03C45B863DA45A0403CBB', X'5EE58C911FF03C45B863DA45A0403CBB', 0, NULL, NULL, 2, NULL, 0, 'Place a layer of rub into a plastic or glass bowl.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A78214452979CD45B06394613D9387B7', X'5EE58C911FF03C45B863DA45A0403CBB', X'5EE58C911FF03C45B863DA45A0403CBB', 1, NULL, NULL, 2, NULL, 0, 'Place the fish onto the rub.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9688F3A59CA89143B5FE510B053A7A7F', X'5EE58C911FF03C45B863DA45A0403CBB', X'5EE58C911FF03C45B863DA45A0403CBB', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle a layer of rub onto the top of the fish pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA27AF7643701C4C9D8573DEFE4760C3', X'5EE58C911FF03C45B863DA45A0403CBB', X'5EE58C911FF03C45B863DA45A0403CBB', 3, NULL, NULL, 2, NULL, 0, 'If you need to double-stack the fish, place another layer of rub then more fish.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6BF4F7681D994F86AF411BBB5227AF', X'5EE58C911FF03C45B863DA45A0403CBB', X'5EE58C911FF03C45B863DA45A0403CBB', 4, NULL, NULL, 2, NULL, 0, 'Finish this layer with a layer of rub on top.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38835138B497F94BBCB460B509349E96', X'5EE58C911FF03C45B863DA45A0403CBB', X'5EE58C911FF03C45B863DA45A0403CBB', 5, NULL, NULL, 2, NULL, 0, 'Place a lid on the container and place in the fridge for 8-12 hours or overnight.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39F1CEC37A3C9B4A9D35234B941F2898', X'5EE58C911FF03C45B863DA45A0403CBB', X'5EE58C911FF03C45B863DA45A0403CBB', 6, NULL, NULL, 2, NULL, 0, 'Rinse the rub from the fish using cold water and lay on paper towel to drain.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE7CD1F30A8520448A18C40615D78946', X'868C9E7AF708BA42ACE5529850E7C814', X'868C9E7AF708BA42ACE5529850E7C814', 0, NULL, NULL, 2, NULL, 0, 'Place the salmon on a Bradley rack or similar with at least ½ inch between pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD20C4F1EDC04449B861EAA10C928413', X'868C9E7AF708BA42ACE5529850E7C814', X'868C9E7AF708BA42ACE5529850E7C814', 1, NULL, NULL, 2, NULL, 0, 'Place rack in fridge for 3-4 hours to allow the fish to dry and form a pellicle.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23CF2424BB726C4FBBEF2CABC4D479DC', X'A70F6C36BAEE6B448F16876E8CEF8C84', X'A70F6C36BAEE6B448F16876E8CEF8C84', 0, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at about 120°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A6460643D633C4AB9F9EB7E0F808676', X'A70F6C36BAEE6B448F16876E8CEF8C84', X'A70F6C36BAEE6B448F16876E8CEF8C84', 1, NULL, NULL, 2, NULL, 0, 'Place salmon pieces directly on smoker grate for 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EF21B2A7DEDA3439126F66B461506D2', X'A70F6C36BAEE6B448F16876E8CEF8C84', X'A70F6C36BAEE6B448F16876E8CEF8C84', 2, NULL, NULL, 2, NULL, 0, 'Increase heat to 140°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4566E162FC64B4DAA46308D909C08B8', X'A70F6C36BAEE6B448F16876E8CEF8C84', X'A70F6C36BAEE6B448F16876E8CEF8C84', 3, NULL, NULL, 2, NULL, 0, 'Continue to cook salmon for 2 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E590381C503C445814FA21F7D221AA4', X'A70F6C36BAEE6B448F16876E8CEF8C84', X'A70F6C36BAEE6B448F16876E8CEF8C84', 4, NULL, NULL, 2, NULL, 0, 'Increase heat to 160°F');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E306AA8EA6F0C8449409C30642882C67', X'A70F6C36BAEE6B448F16876E8CEF8C84', X'A70F6C36BAEE6B448F16876E8CEF8C84', 5, NULL, NULL, 2, NULL, 0, 'Continue to cook salmon until it has reached the desired dryness/texture');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8485A228DA97744194C7F7ED2ADC4B29', X'0800B07C2C02A54EBAE0C988CE4E4F7F', X'0800B07C2C02A54EBAE0C988CE4E4F7F', 0, NULL, NULL, 2, NULL, 0, 'Mix together ¾ cup of maple syrup with 2 TBS of Jeff''s rub to create a glaze.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B740E94B14EB264A84224954FD87923B', X'0800B07C2C02A54EBAE0C988CE4E4F7F', X'0800B07C2C02A54EBAE0C988CE4E4F7F', 1, NULL, NULL, 2, NULL, 0, 'Brush the glaze on the salmon pieces every hour while they are in the smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78643FDB4785A340A339C99D9D6617C2', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 0, NULL, NULL, 2, NULL, 0, 'Rinse ribs under cold water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70FAD1232A072240815E5948565B6D55', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 1, NULL, NULL, 2, NULL, 0, 'Trim the ribs St. Louis style if desired (instructions above)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67FEB8082F14E444B34ABC2901346742', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 2, NULL, NULL, 2, NULL, 0, 'Remove membrane and flap meat on bone side of ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03E3FE0691EA4E4996582A88487A2267', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 3, NULL, NULL, 2, NULL, 0, 'Apply a coat of yellow mustard then a heavy coat of rub to the bone side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3D6E478401CAB4993C76050D94F78C3', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 4, NULL, NULL, 2, NULL, 0, 'Flip the ribs over and repeat the mustard/rub application on meaty side');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35F20395691CF64690C4EAB4B304E3B5', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 5, NULL, NULL, 2, NULL, 0, 'Prepare smoker for cooking at 225°F with pecan + fruit wood');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F656660941BF3547A5DB29AE39513510', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 6, NULL, NULL, 2, NULL, 0, 'Place ribs bone side down directly on smoker grate and cook for 3 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E7AC5182395BD488DF160CCBD59BC24', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 7, NULL, NULL, 2, NULL, 0, 'Make a bed of brown sugar, rub and butter onto 36 x 18 sheet of foil');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF652658AB576348A3DED604463AC0A0', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 8, NULL, NULL, 2, NULL, 0, 'Lay rack of ribs meat side down onto this bed of ingredients');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E0E40C71B4FEE499C64395A603F1A7E', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 9, NULL, NULL, 2, NULL, 0, 'Wrap foil around ribs carefully then wrap with another equally sized piece');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE3DA58F6CD004AB470FA5C2CD712AB', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 10, NULL, NULL, 2, NULL, 0, 'Cook at 225°F for 2 hours');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'632DF637EDDA14489B359A6CBBF12D64', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 11, NULL, NULL, 2, NULL, 0, 'Remove ribs from foil carefully saving the juice that has collected');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'463AE863DDECF740910C027AE0E56D14', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 12, NULL, NULL, 2, NULL, 0, 'Place ribs, bone side down back onto smoker grate for 1 hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7218033D3BA6904193B00D6E92D7B984', X'14EA2562158DCF40996F13B364F5CDAB', X'14EA2562158DCF40996F13B364F5CDAB', 13, NULL, NULL, 2, NULL, 0, 'Rest for 10 minutes then slice and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9BB9116874EC94FB14709AA3993AE7F', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 0, NULL, NULL, 2, NULL, 0, 'Make the rum sauce using 1/3 cup of rum, 1/3 cup of dark (or light) brown sugar, and 1/4 lb butter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DB119B739243642A53F0A1D7F014D87', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 1, NULL, NULL, 2, NULL, 0, 'Pour the rum into a small sauce pan over low heat. Add the butter and let it melt while you stir it around. Add in the brown sugar and let it dissolve while you continue to stir. Once everything is melted and dissolved and warm, remove from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47766F5D8BBAF84B8A39774466776AA2', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 2, NULL, NULL, 2, NULL, 0, 'Do not let the sauce boil or you will end up making candy.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A380A55421B234F9FAEFA341707625C', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 3, NULL, NULL, 2, NULL, 0, 'If you''d rather use a maple sauce instead of rum, use 1/3 cup of maple syrup, 2 TB of dark (or light) brown sugar and 1/4 lb of butter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A561903FE64E247866AC1DF0433AF2E', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 4, NULL, NULL, 2, NULL, 0, 'Pour the maple syrup into a small sauce pan over low heat. Add the butter and let it melt while you stir it around. Add in the brown sugar and let it dissolve while you continue to stir. Once everything is melted and dissolved and warm, remove from the heat.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'164CB47BC554784084B642E36491F87E', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 5, NULL, NULL, 2, NULL, 0, 'Note: Do not let the sauce boil or you will end up making candy.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51ADE1879148A845AE7AFB5EC2777E4C', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 6, NULL, NULL, 2, NULL, 0, 'With the ham wrapped in plastic (to contain the mess), fill the injector with the warm sauce and inject as much as you can at 1 inch intervals. Some of it will leak out but much of it stays in there and flavors the inside of the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C67B5C869374774BA678624E73F07041', X'53E64897974D4A40A21F584291CEC19B', X'53E64897974D4A40A21F584291CEC19B', 7, NULL, NULL, 2, NULL, 0, 'When the injecting is finished, remove the plastic.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3507596F2A9554CB42FD5CD42C21C24', X'18DD22DA2BDAA445B512CEEE7101744F', X'18DD22DA2BDAA445B512CEEE7101744F', 0, NULL, NULL, 2, NULL, 0, 'Brush a little of the rum or maple sauce onto the ham to make it nice and sticky then sprinkle Jeff''s original rub all over the outside of the ham.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EB359B686CC324CB603B7D9072B1D5D', X'18DD22DA2BDAA445B512CEEE7101744F', X'18DD22DA2BDAA445B512CEEE7101744F', 1, NULL, NULL, 2, NULL, 0, 'The ham is now ready for the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DEB880AD327CF47ACB559BDDBC45C0A', X'63068563E8AC334C9D44E8132D78ADEA', X'63068563E8AC334C9D44E8132D78ADEA', 0, NULL, NULL, 2, NULL, 0, 'Preheat your smoker to about 225°F and when it''s holding steady, place the ham directly on the grate.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB9FA3B1E9AAAC419432BB9B03F83A6C', X'63068563E8AC334C9D44E8132D78ADEA', X'63068563E8AC334C9D44E8132D78ADEA', 1, NULL, NULL, 2, NULL, 0, 'Apply heat and smoke for about 3 hours or until the internal temperature of the ham reaches a good eating temperature (I prefer about 140°F).');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78A7BE730EDD5B4E9B27D20DF3535505', X'66853B30E9F2064992F77605DEEC0C05', X'66853B30E9F2064992F77605DEEC0C05', 0, NULL, NULL, 2, NULL, 0, 'Let the ham rest for about 15-20 minutes then slice it up into 1/4 inch thick pieces piled onto a nice platter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'670577B0EB54A04FB36DD448EE83DCF3', X'66853B30E9F2064992F77605DEEC0C05', X'66853B30E9F2064992F77605DEEC0C05', 1, NULL, NULL, 2, NULL, 0, 'If you have extra sauce, consider brushing or drizzling some onto the pieces that are sliced.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44DDCC6B8C7FE54C8CA4F317B5B33842', X'66853B30E9F2064992F77605DEEC0C05', X'66853B30E9F2064992F77605DEEC0C05', 2, NULL, NULL, 2, NULL, 0, 'Disclaimer: Not all of the alcohol cooks out of the rum sauce during the brief warming period and during the time in the smoker. Rum adds a lot of nice flavor but there is still trace amounts of alcohol present when it is being served. The maple sauce is an excellent alternative.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92E04C010BAAC748B0762B3CF2DD6B26', X'689049DD7044DD4B98CA78B4EB690A53', X'689049DD7044DD4B98CA78B4EB690A53', 0, NULL, NULL, 2, NULL, 0, 'Make a basic brine by mixing 2 gallons of cold water, 2 cups coarse kosher salt, 2 cups brown sugar (light or dark) and 1/4 cup Jeff''s original rub until the salt and brown sugar are dissolved.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47F8B80F983F5E46A36F63D9B5C00E68', X'689049DD7044DD4B98CA78B4EB690A53', X'689049DD7044DD4B98CA78B4EB690A53', 1, NULL, NULL, 2, NULL, 0, 'Put the turkey into a very large ziptop bag, a clean empty cooler or a large plastic food-safe bucket and pour the brine over the turkey to cover.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4ED72506C88DC4A8E9AA0C21032691C', X'689049DD7044DD4B98CA78B4EB690A53', X'689049DD7044DD4B98CA78B4EB690A53', 2, NULL, NULL, 2, NULL, 0, 'Let the turkey brine for 10-12 hours or overnight in the fridge making sure the temperature of the brine stays between 33 and 39 degrees F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'342BAC4BE2C74F4387A08DD5EAB629C1', X'689049DD7044DD4B98CA78B4EB690A53', X'689049DD7044DD4B98CA78B4EB690A53', 3, NULL, NULL, 2, NULL, 0, 'Once the turkey is finished brining, discard the brine and rinse the turkey under cold water.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED84C9A790C4E84BB0350673BBE60C06', X'689049DD7044DD4B98CA78B4EB690A53', X'689049DD7044DD4B98CA78B4EB690A53', 4, NULL, NULL, 2, NULL, 0, 'While the turkey is brining, make the bacon butter by adding 5 strips of bacon (cut into 1/2 inch pieces), 1/4 cup of Jeff''s original rub, 1 stick of butter and 3 garlic cloves to a food processor. Run the food processor until the mixture is smooth.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'712E260365810F498303572D3151D9A6', X'738C76B23F8B5945B72A39D16313F57B', X'738C76B23F8B5945B72A39D16313F57B', 0, NULL, NULL, 2, NULL, 0, 'With your hands, work the bacon butter under the skin of the turkey as much as possible then rub it all over the outside of the turkey as well. Be generous with it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F82B1BB619584AB89CDB68D4707BD3', X'B172010090006F4DB3FBB7BFD9BC191B', X'B172010090006F4DB3FBB7BFD9BC191B', 0, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking between a range of 225 and 240 degrees F. If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCE4DEB43FC81F438357FA9CB61B7792', X'B172010090006F4DB3FBB7BFD9BC191B', X'B172010090006F4DB3FBB7BFD9BC191B', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey in the smoker breast side down and let it smoke cook with cherry wood for about 7 hours or until it reaches 165 degrees in the thickest part of the breast and thigh.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1812C7F2DC76DA489F086D6032715C25', X'B172010090006F4DB3FBB7BFD9BC191B', X'B172010090006F4DB3FBB7BFD9BC191B', 2, NULL, NULL, 2, NULL, 0, 'You can brush a mixture of melted butter onto the outside of the turkey a couple of times during the last hour or so for a better finish color.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7B29D6340380C4FB112CF0A12B28608', X'4183AD87C8ED534BAF584F77B156D681', X'4183AD87C8ED534BAF584F77B156D681', 0, NULL, NULL, 2, NULL, 0, 'When the turkey hit 165°F remove it from the smoker and set it on the counter.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85EDB1067F66614691D93BBD78890B5D', X'4183AD87C8ED534BAF584F77B156D681', X'4183AD87C8ED534BAF584F77B156D681', 1, NULL, NULL, 2, NULL, 0, 'Let it rest for about 20-30 minutes if possible before carving to allow the juices to redistribute throughout the turkey.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19078DB978689448A6C4E836708E60A0', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 0, NULL, NULL, 2, NULL, 0, 'Remove giblets, neck, etc from cavity of chicken');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A308F48476A7F249AB2C4B7FC79BF1D8', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 1, NULL, NULL, 2, NULL, 0, 'Brine chicken if desired using 1 gallon of water, 1 cup of kosher salt and 1 cup of brown sugar mixed well until ingredients are dissolved. Pour brine over chicken to cover and into fridge for at least 4 hours (8-12 hours is better)');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C64F8BC8F3FAF429546DF0523240052', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 2, NULL, NULL, 2, NULL, 0, 'Use hands to carefully separate skin from body of chicken wherever possible and apply rub under skin.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E0089200BACCB4E9370330BD20CAD78', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 3, NULL, NULL, 2, NULL, 0, 'Open a can of beer, root beer or Dr Pepper and dispose/drink half of it. Insert about 2 TBS of original rub into can of liquid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72D6A4428D54A4681FBE06CD88F4982', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 4, NULL, NULL, 2, NULL, 0, 'Place cavity of chicken onto cans using the legs as props to hold it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45CEEBA1ABFB544D8FC8B0D2FEE48846', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 5, NULL, NULL, 2, NULL, 0, 'Prepare smoker for cooking at 230-250°F with indirect heat and several chunks of hickory wood to keep the smoke flowing for at least 2 hours.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E662F01AC0296C4082F5DCA436E3E6A4', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 6, NULL, NULL, 2, NULL, 0, 'Place the chicken in the smoker and let it cook for about 3.5 hours or until the thickest part of the thigh and/or breast reaches 165°F.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B565D6E2EF661E4D83C96B1577C69613', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 7, NULL, NULL, 2, NULL, 0, 'While the chicken cooks, mix ¾ cup olive/vegetable oil + 2 TBS of original rub and mop this mixture onto the outside of the chicken 2 to 3 times during the last hour.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'372E4A19A4B4F14083B427026D968421', X'200AA3F5AEEE414B8F37873594AD1D45', X'200AA3F5AEEE414B8F37873594AD1D45', 8, NULL, NULL, 2, NULL, 0, 'Let the chicken rest for about 15 minutes before carving. Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E87947C42241145BE64C12EF5003839', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 0, NULL, NULL, 2, NULL, 0, 'Make the brine by mixing the coffee, brown sugar and kosher salt together. Stir until the ingredients are dissolved. Add the rub and stir to combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4902B4AD2F3A954DBDE11DB62AE51B2B', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 1, NULL, NULL, 2, NULL, 0, 'Place the brine solution in the fridge, preferably overnight, to get cold.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D277F36DD207424E925D215BC29A41E7', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 2, NULL, NULL, 2, NULL, 0, 'Prep the ribs for brining by removing the membrane on the boney side of the ribs');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E1EFFB5E13C694DBDE80755CAE2F0C6', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 3, NULL, NULL, 2, NULL, 0, 'Place the ribs in a plastic container or a large zip top bag then pour the brine solution over the top of the ribs to cover. Refrigerate for 6 hours then rinse with cold water and discard brine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84A3891555A0BC45AF4717BE4BB5539E', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 4, NULL, NULL, 2, NULL, 0, 'Rub yellow mustard on the bone side of the ribs then sprinkle Jeff''s original rub generously onto the ribs to cover. Flip the ribs over and repeat the mustard and rub on the meaty side. Lay aside while you set up the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDAAB4F66F108F43AEAE728E16B80617', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 5, NULL, NULL, 2, NULL, 0, 'Prepare the smoker for cooking at 225-240°F with indirect heat. If your smoker uses a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF3BAC25A3F0EF4382D98A59DEFACEED', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 6, NULL, NULL, 2, NULL, 0, 'Stage 1: Place the ribs directly on the grate and let them smoke cook for 2 hours using apple wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B603AF5EA8E094FA3FC9A15FF756CC5', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 7, NULL, NULL, 2, NULL, 0, 'Stage 2: Remove the ribs from the smoker and wrap with 18-inch wide heavy duty foil. Place them back into the smoker and cook for 2 hours wrapped. No smoke required during this stage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66FD61AC65756046B6C23EACF8FAE7A4', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 8, NULL, NULL, 2, NULL, 0, 'Stage 3: Remove the ribs from the foil and place them back into the smoker, directly on the grates. Smoke cook for 1 hour using apple wood for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DCE9BF91CC0964187634A58AB263CAE', X'0357E3F5BB250A4ABE2FE905BD6F24D1', X'0357E3F5BB250A4ABE2FE905BD6F24D1', 9, NULL, NULL, 2, NULL, 0, 'Remove ribs from the smoker when they reach 195 degrees or when they have reached the level of tenderness that you desire. Let the ribs rest for about 10-15 minutes before slicing.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D758FBBC5FE0C9448E4D87FC2549309C', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 0, NULL, NULL, 2, NULL, 0, 'Place the ground beef, sausage, bread crumbs, jalapeños, onions, buttermilk, eggs and Jeffs'' original rub into a large bowl and combine.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F20E1CCFC4C81F418F1A20E281BA1FE5', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 1, NULL, NULL, 2, NULL, 0, 'Remove the meatloaf mixture and form it into a loaf that is rectangular in shape and about 2 to 3 inches thick on top of a wood plank.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'867377CA8E80D74A80E6A81AC5305C57', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 2, NULL, NULL, 2, NULL, 0, 'This can be made in advance if you wish and put into the fridge until you are ready to smoke it.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'124828AEE71D954A9BD5DF4BF8325165', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 3, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 225-240°F with indirect heat. If your smoker has a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0342844FA100C54CA13E6F3F657837D0', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 4, NULL, NULL, 2, NULL, 0, 'Place the plank with the meatloaf on the smoker grate and cook with smoke until the internal temperature of the meatloaf reads 160°F as measured by a digital probe meat thermometer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F923160D31AD1E49A1A37BB3F3AE7B68', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 5, NULL, NULL, 2, NULL, 0, 'About 30 minutes before the meatloaf is finished cooking, add a layer of Jeff''s original barbecue sauce to the top and sides of the meatloaf.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'578626FEB75AD74586455229953291FF', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 6, NULL, NULL, 2, NULL, 0, 'When the meatloaf is finished cooking, remove it from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'640E4CD079F2A74C9A795C002F5270DD', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 7, NULL, NULL, 2, NULL, 0, 'Let it rest for about 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F4546931D15B04EAAA75FC4A05C765F', X'DB0325E7F881D84DA19444611A26EAC8', X'DB0325E7F881D84DA19444611A26EAC8', 8, NULL, NULL, 2, NULL, 0, 'Slice into ½ inch pieces and enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'894540071C7F0E4FB63DFEC136207E43', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 0, NULL, NULL, 2, NULL, 0, '1/2 gallon water');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBFA5D41ABB1CB4E943F48B50F281AA3', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 1, NULL, NULL, 2, NULL, 0, '1/2 cup kosher salt');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB9C66A193689C458CB14C891A3FE420', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 2, NULL, NULL, 2, NULL, 0, '1/2 cup dark brown sugar');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEF1A4475AE5E64992962A5624958894', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 3, NULL, NULL, 2, NULL, 0, '3 TBS Louisiana hot sauce');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53B32F7F8D2FF944844C4FFC432607DD', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 4, NULL, NULL, 2, NULL, 0, 'Add all of the ingredients to the water and stir until everything is dissolved and mixed in well.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CD7C273DA303F489BEDFAEE05737F7D', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 5, NULL, NULL, 2, NULL, 0, 'Using gallon sized zip top bags, put 12 thighs in each bag and pour enough brine into the bag to cover. I used about ½ of the brine in each bag and it was plenty.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C37D6FE5C62F434CA5045B363EAD3F64', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 6, NULL, NULL, 2, NULL, 0, 'Seal the bags pressing out all of the air and let them sit in the fridge during the brining process. I recommend sitting the bags down in bowls to prevent the risk of leakage.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B3B9AAA8E608D438425C80FC5829BA6', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 7, NULL, NULL, 2, NULL, 0, 'After 3 hours, remove the bags from the fridge and discard the brine liquid.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C57196FF8AF274FB77BFC2CF2B42457', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 8, NULL, NULL, 2, NULL, 0, 'Place 12 chicken thighs into each bowl and coat well with yellow mustard.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'435491423383BE41B4ACA5C5F8ADEBE4', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 9, NULL, NULL, 2, NULL, 0, 'Pour ¼ cup of Jeff''s original rub into each bowl and stir well to coat chicken.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3073DD3AA16BC42AFC203329B65441D', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 10, NULL, NULL, 2, NULL, 0, 'The chicken can now be placed onto Bradley racks, Weber grill pans or cooling racks for east transport to and from the smoker.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA8DF71F0BB5D43B91B48E5CD51A324', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 11, NULL, NULL, 2, NULL, 0, 'Setup smoker for cooking at 225-240°F with indirect heat using mesquite, pecan or whatever wood you have available for smoke.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF02F810B1930F40AAC78F5637D330F5', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 12, NULL, NULL, 2, NULL, 0, 'If your smoker has a water pan, fill it up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'553E8D1E622BE94CAEB1677205480BD4', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 13, NULL, NULL, 2, NULL, 0, 'Cook the chicken until it reaches 175°F as measured by a digital meat thermometer such as a thermapen or a ThermoPop.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2153AEFEF32D844B9B5F395C29F3586E', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 14, NULL, NULL, 2, NULL, 0, 'When the chicken is finished cooking, de-bone it (unless it''s boneless), then pull the meat into pieces.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5E0A16C89108A4FB8339725C19E5B86', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 15, NULL, NULL, 2, NULL, 0, 'Coat the meat with an ample amount of Jeff''s barbecue sauce.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD62291F760EF74CB117CB149DF24C1F', X'73D3B40EE68CD9479489DA81C70CB96B', X'73D3B40EE68CD9479489DA81C70CB96B', 16, NULL, NULL, 2, NULL, 0, 'Make sandwiches with the pulled chicken topping it with coleslaw, pickles and onions.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D69775D9FE78FF438D32C2B5D9113023', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 0, NULL, NULL, 2, NULL, 0, 'Place chicken breast in ziploc back or between 2 pieces of wax paper.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E9E6B29855BDC4DAE1B03BE6C9BB1DB', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 1, NULL, NULL, 2, NULL, 0, 'Pound lightly with a meat mallet until desired thickness is acquired.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DABA77AEDE9AC41BD02F2D650A60B28', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 2, NULL, NULL, 2, NULL, 0, 'Apply light coat of my original rub to both sides of chicken breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94974A233B65844E9CBCCF0685F1B16B', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 3, NULL, NULL, 2, NULL, 0, 'Lay ham then swiss cheese onto top of chicken breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0257A8ABEF94594FA77576F7811E35CA', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 4, NULL, NULL, 2, NULL, 0, 'Roll up chicken breast, ham and cheese like a pinwheel.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D5E978DCC08B0429A7345628E35434C', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 5, NULL, NULL, 2, NULL, 0, 'Lay down 2 pieces of bacon parallel and about 1 inch apart');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6E3EF4AB49A6F46A9087A26BDDC93A3', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 6, NULL, NULL, 2, NULL, 0, 'Place rolled, stuffed chicken breast across bacon about 3 inches from end.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6B458B82F99D74997E41867D9D18F60', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 7, NULL, NULL, 2, NULL, 0, 'Pull short ends of bacon up over the chicken breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F696696EFB3A14288B58A0B42A9E762', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 8, NULL, NULL, 2, NULL, 0, 'Roll stuffed chicken breast across bacon until about 4 inches from end.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'414A0A2192F14149A021FD8E1EAF2E5D', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 9, NULL, NULL, 2, NULL, 0, 'Pull bacon pieces up over the center of stuffed chicken breast.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D99A0CBC7795394CAC71248A5ECC8B2A', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 10, NULL, NULL, 2, NULL, 0, 'Overlap ends of bacon and apply a little more rub to outside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C91C7C54FF39894581295EC0E9D98E8D', X'480ED66C07B19B4D8EC6E84049A0AFF0', X'480ED66C07B19B4D8EC6E84049A0AFF0', 11, NULL, NULL, 2, NULL, 0, 'Push toothpick through bacon and chicken to secure.');
