INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A9119A0FB20B43992816C37981B029', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', 7, NULL, NULL, 2, NULL, 0, 'Place into smoker');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'290E6A84DA7CBA46AAC7F2020ABCD783', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', 8, NULL, NULL, 2, NULL, 0, 'Fry or Bake bacon for making `crumbles`');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73AB116EC77D8A4AB66B4FAAB79C39DD', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', 9, NULL, NULL, 2, NULL, 0, 'Top meat loafs with Jeff''s barbecue sauce and bacon crumbles about 30 minutes before done');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'217FF5862616524698878DE6CB1827D1', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', 10, NULL, NULL, 2, NULL, 0, 'Smoke for 3 hours or until they reach 160 degrees internal temperature');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1DB7521322F874C97992DDD25B37EF8', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', 11, NULL, NULL, 2, NULL, 0, 'Serve with side of grilled peppers and onions');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2178762FC3CEF4AB4EF5E6BC2063DCD', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', X'D88CF7BF7C2D5E4A9C1B0B89571F56D9', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35BBD5A6D5A2B44889250AC4F7F0D364', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 0, NULL, NULL, 2, NULL, 0, 'Start by prepping your vegetables and doing any slicing, cleaning, chopping ,etc..');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3516B4F227FB44987AF04806B738E1B', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 1, NULL, NULL, 2, NULL, 0, 'Make a bacon weave on a piece of wax or parchment paper that is 7 slices by 6 slices. Weave them so they interlock and it forms a blanket of bacon.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DDF0FA3A6EDFF46BA292A330E2279DB', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle some rub on the top of the bacon weave and set aside.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5FA315CAAEF0449A06F3E06CE9425E4', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 3, NULL, NULL, 2, NULL, 0, 'Place 1 lb of bacon into a gallon-size zip top bag and press it out flat. Snip off a small piece of the corner of the bag to avoid air bubbles.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7B99FA32DE194AB7DEFDD9DD25AC30', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 4, NULL, NULL, 2, NULL, 0, 'Use a sharp knife or scissors to cut and remove the top side of the zip top bag.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECA16009B4ED954FA7D396C8DB5540CE', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 5, NULL, NULL, 2, NULL, 0, 'Flip the flattened sausage over onto a piece of wax or parchment paper.. you can also just leave it on the plastic if you prefer.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9587EF32454B8644B2D347040A5A5D7F', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 6, NULL, NULL, 2, NULL, 0, 'Add the stuffing by layering on spinach, cheese, onions, peppers, egg slices, etc.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0300C624AAC3D6418349F41DD4855ECB', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 7, NULL, NULL, 2, NULL, 0, 'Use the plastic or was paper under the flattened sausage to help you roll it up. Do not roll up the plastic, continually pull it away as the sausage rolls.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30E810A93054E04AA1384E54BDFAF3D3', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 8, NULL, NULL, 2, NULL, 0, 'Move the stuffed, rolled up sausage onto the edge of the longest side of the bacon weave.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E23765D18A42B4993211B0BA7B2C982', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 9, NULL, NULL, 2, NULL, 0, 'Use the wax or parchment paper to help you roll the weave over the sausage roll making sure to continually pull the paper away as the weave is rolled up.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82DCD98852EB154E802C1A8544F655C0', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 10, NULL, NULL, 2, NULL, 0, 'Set up your smoker for cooking at 225-240°F with indirect heat. I prefer to leave the water pan dry for these to encourage the bacon to crisp up better.');
INSERT INTO `VWR INTERNAL Smokingmeat howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34248D784D1EDD438A1B1D189A790994', X'D272EDDF69D8794EABB312B785187280', X'D272EDDF69D8794EABB312B785187280', 11, NULL, NULL, 2, NULL, 0, 'Smoke cook for 2.5 to 3 hours or until the internal temperature reaches 160°F.');
