INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E469FDA4D2C854DA145938A0D83B887', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 0, NULL, NULL, 2, NULL, 0, 'Bash the lemongrass firmly with a knife handle or wooden spoon then finely chop it. Slice the cucumber and radish and put in a bowl. Finely slice the carrot, red cabbage and spring onions and place in a separate bowl');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8333501DDE6B4342A9BDB7EB7A021ED0', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 1, NULL, NULL, 2, NULL, 0, 'Mix rice vinegar, sugar, soy sauce and two tbsp of oil in a bowl before dividing the mixture into two separate bowls. In the first bowl add teriyaki sauce');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92E38DF4CA44924CB332415BC43A31F7', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 2, NULL, NULL, 2, NULL, 0, 'In the second bowl add grated ginger and garlic. Marinate your salmon in this mixture and add a generous pinch of salt and pepper, then leave to sit');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01701A2EF0DE8649A4FB2EB485C4AE5D', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 3, NULL, NULL, 2, NULL, 0, 'Heat up the mixture in your second bowl (in which you added the teriyaki sauce) in a non-stick saucepan until it has thickened slightly and has become sticky');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B751AC60EFE37D4E9B1F0FF5733E07AD', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix the sliced cucumber and radishes with 1 tsp of rice wine vinegar, a pinch of salt and sugar. Mix then set aside to pickle');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19C3451E487E384EB7EBAF01A2965930', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 5, NULL, NULL, 2, NULL, 0, 'Heat oil in a non-stick frying pan on a medium to high heat. Add 2 salmon fillets skin side down and fry until each side is coloured and the skin is crispy. Then set aside while you repeat with the other two');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'855AAEDA12FB7F4C888CCD3B59FCDD01', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 6, NULL, NULL, 2, NULL, 0, 'In the same pan, add Limited Edition Japanese Teriyaki rice and lemongrass. Fry until hot and slightly crispy. This should take a few minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81F6BAE9E3C97B40A696E0EACDFD3A43', X'6E0A80B2E969A6479329AC6C9EEBDFFF', X'6E0A80B2E969A6479329AC6C9EEBDFFF', 7, NULL, NULL, 2, NULL, 0, 'To assemble your bento bowl, add the rice at the bottom then place salmon on top with the pickles and sliced rainbow vegetables. Before serving add a drizzle of the sticky dressing');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859D9CB21C9B6749B88E0D0543CFA6C1', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 0, NULL, NULL, 2, NULL, 0, 'In a small saucepan, cover eggs with water, bring to the boil and then simmer for 6 minutes. Once cooked, add eggs to ice cold water so they can cool');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5650CF6A3541F445AF6A5F7E45326F94', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 1, NULL, NULL, 2, NULL, 0, 'Cover the haddock and 1 kaffir lime leaf in some water in a saucepan. Poach for 5 minutes on a simmering heat');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0D614F7B841DF4CBF3EBB8A1F38B228', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 2, NULL, NULL, 2, NULL, 0, 'Remove the fish and leaf from the pan and leave to the side. You can flake your fish at the end');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C025FEF2B080441955A139331F388EB', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 3, NULL, NULL, 2, NULL, 0, 'Add a tbsp of oil over a medium heat in a large saucepan and saute the finely diced onion. Then add the mustard and cumin seeds and heat for a few more minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8C18B87F6375478421B0DA07C30E71', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 4, NULL, NULL, 2, NULL, 0, 'Stir in the peel, grated ginger and garlic and saute for a minute before adding curry powder, kaffir lime leaf, a squeeze of lemon juice, zest, salt, pepper and a tbsp of oil');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E0BC2035B24834EAB0DD29B1E048DB5', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 5, NULL, NULL, 2, NULL, 0, 'Stir through the Tilda Coconut rice and add 2 tbsp of water to loosen up the mixture');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A82AD6C390F38E4AAD6BA18DC24BE664', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 6, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from the heat, flake the fish and season with a tbsp of oil, salt and pepper. Then de-shell the two eggs and quarter them');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1068D88037CD748B61B3D96A5F7A082', X'FCD83A59E7C3AD439AF1678881FA99CE', X'FCD83A59E7C3AD439AF1678881FA99CE', 7, NULL, NULL, 2, NULL, 0, 'Serve up the rice and add the quartered eggs. Then sprinkle on some chopped coriander');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ED70365E7ACD347B5A182F94AFF2F7E', X'33803F3DF8E7F44CA91C3F4B92FBF369', X'33803F3DF8E7F44CA91C3F4B92FBF369', 0, NULL, NULL, 2, NULL, 0, 'Cook the potatoes, cauliflower and carrots in boiling water for 10-15 minutes until soft. Drain and cool');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8AC4DA0B4A20843BF267FAD6DA77E72', X'33803F3DF8E7F44CA91C3F4B92FBF369', X'33803F3DF8E7F44CA91C3F4B92FBF369', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large pan and add the onions, garlic and ginger and fry for about 5 minutes to soften');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0707ECB17D0CBC4B9FB86AC20F4438A1', X'33803F3DF8E7F44CA91C3F4B92FBF369', X'33803F3DF8E7F44CA91C3F4B92FBF369', 2, NULL, NULL, 2, NULL, 0, 'Add the Rogan Josh paste and black onion seeds and heat for a couple of minutes');
