INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A03DC9AB31F4449AFF00817044CA7AB', X'977F04FBFC924747A50167FA0B9F0B1E', X'977F04FBFC924747A50167FA0B9F0B1E', 2, NULL, NULL, 2, NULL, 0, 'Carefully remove the lemongrass from the sauce and blend until smooth before returning the sauce to the pan. Add the beef strips to the hot sauce and simmer gently for a further 2-3 minutes until the meat is cooked and tender');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68F0532F87447F468BCD4BB4296B8958', X'977F04FBFC924747A50167FA0B9F0B1E', X'977F04FBFC924747A50167FA0B9F0B1E', 3, NULL, NULL, 2, NULL, 0, 'Heat the Tilda Coconut rice according to pack instructions. Steam the Pak Choi for 2 minutes until wilted');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88C1D2656372894B8E27ABCF938AEC7E', X'977F04FBFC924747A50167FA0B9F0B1E', X'977F04FBFC924747A50167FA0B9F0B1E', 4, NULL, NULL, 2, NULL, 0, 'Top the hot rice with the beef strips and pour over the curry sauce. Serve with the steamed pak choi along-side. Garnish with lime wedges, red chilli and toasted coconut shavings (if using)');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD1C8127A9F39B459EC303D8590EDEC1', X'B3BA2414FC22C249AED6E15CF3084E37', X'B3BA2414FC22C249AED6E15CF3084E37', 0, NULL, NULL, 2, NULL, 0, 'Preheat the grill to high');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8417EEA88CC84B49881445858666877E', X'B3BA2414FC22C249AED6E15CF3084E37', X'B3BA2414FC22C249AED6E15CF3084E37', 1, NULL, NULL, 2, NULL, 0, 'String a cube of the lamb on a skewer, then thread on a spring onion and repeat until you have 4 lamb cubes and 3 spring onions per skewer . Brush with olive oil and place onto an oven tray and under the grill. Cook the lamb for 4 minutes, turn the skewers over, then repeat for a final 4 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97607FBCA3AD2940BEF9C1ADC2F55EE0', X'B3BA2414FC22C249AED6E15CF3084E37', X'B3BA2414FC22C249AED6E15CF3084E37', 2, NULL, NULL, 2, NULL, 0, 'Whilst the lamb is cooking, combine the cumin, garlic granules and chilli powder to make the spice mix');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE02A015BD31CA419B28DA54F4C5B76C', X'B3BA2414FC22C249AED6E15CF3084E37', X'B3BA2414FC22C249AED6E15CF3084E37', 3, NULL, NULL, 2, NULL, 0, 'Once the meat is ready, remove from the grill and sprinkle generously with the spice mix on all sides and rest under a sheet of kitchen foil to keep warm');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E2752965E8A2A418D376A1A8AE130BF', X'B3BA2414FC22C249AED6E15CF3084E37', X'B3BA2414FC22C249AED6E15CF3084E37', 4, NULL, NULL, 2, NULL, 0, 'Blitz the parsley and mint in a food processor until finely chopped. Tip into a large bowl and add the ground allspice, cloves, cherry tomatoes, lemon juice and a tablespoon of olive oil. Season generously with salt and pepper');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'137F5CBDBB01BB4BB644B34E8A1E1D9C', X'B3BA2414FC22C249AED6E15CF3084E37', X'B3BA2414FC22C249AED6E15CF3084E37', 5, NULL, NULL, 2, NULL, 0, 'Cook the Tilda Wholegrain Basmati & Wild rice according to pack instructions. Toss the parsley mixture with the hot rice and lay the warm lamb skewers on top of the tabbouleh and scatter over the pomegranate seeds');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4787445BAD867F489AA6023EEC848E95', X'09B883ACFA99AF44B327318CBDE09EBD', X'09B883ACFA99AF44B327318CBDE09EBD', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 240C, 220C fan or gas 7. Line an oven tray with baking parchment');
