INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44A52253DC5CF4ABD90DDF42094D697', X'5A750BD52E68BA4FB3CE846E6164ED28', X'5A750BD52E68BA4FB3CE846E6164ED28', 1, NULL, NULL, 2, NULL, 0, 'Caramelise one half in a frying pan on a low medium heat, until dark brown but not burned. This will take about 20 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD3C903AF817A8418A5697A1188613AB', X'5A750BD52E68BA4FB3CE846E6164ED28', X'5A750BD52E68BA4FB3CE846E6164ED28', 2, NULL, NULL, 2, NULL, 0, 'Once done, pour the onions in to the rice and cook the rice according to packet instructions, with no deviation from water amount etc');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C469960271208844A41F4EFC26B0E78D', X'5A750BD52E68BA4FB3CE846E6164ED28', X'5A750BD52E68BA4FB3CE846E6164ED28', 3, NULL, NULL, 2, NULL, 0, 'While the rice is cooking, shallow fry the rest of the onions in batches until crispy');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8ECAA739D24E8B4E8AEB196DF94EA2C7', X'5A750BD52E68BA4FB3CE846E6164ED28', X'5A750BD52E68BA4FB3CE846E6164ED28', 4, NULL, NULL, 2, NULL, 0, 'Combine the rice cooked with onions once done, with the can of lentils in equal amounts');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355703C859A59E41AF5E777B98198729', X'5A750BD52E68BA4FB3CE846E6164ED28', X'5A750BD52E68BA4FB3CE846E6164ED28', 5, NULL, NULL, 2, NULL, 0, 'Garnish with parsley and pomegranate seeds, and serve with yoghurt on the side');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8490746ECEDFA4EA958D4355F9C97E9', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 0, NULL, NULL, 2, NULL, 0, 'Butter can burn very easily, so make sure throughout recipe a low to medium heat is used.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'946B8240FEB3884A8115E776076644FD', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 1, NULL, NULL, 2, NULL, 0, 'Rinse the Basmati rice in water till the water runs clear, then soak rice for half an hour. Once soaked, drain and put to the side.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'854D7DF3CB3C234EAD2CFAC14FA48240', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 2, NULL, NULL, 2, NULL, 0, 'Slice the onions and put to one side.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C966D4306DB6347BCCF855AA7DFEBAF', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 3, NULL, NULL, 2, NULL, 0, 'On a medium to low heat melt the butter in a large saucepan.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5E9EDADD2AE146BB6030B04CE1ED0E', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 4, NULL, NULL, 2, NULL, 0, 'Add the nuts to the butter and cook till golden. Remove with a slotted spoon and use at the end.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81DE7F51512767419CD53AD9C55527A9', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 5, NULL, NULL, 2, NULL, 0, 'Add the whole cardamom pods and sliced onions in the butter. Add your curry powder, bay leaves, turmeric and chilli powder. Cook for a couple of minutes until fragrant and the onions are tender.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9538E3883100A5419F49CF98D462E523', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 6, NULL, NULL, 2, NULL, 0, 'Now add the soaked rice and stir altogether for 1 minute, making sure the rice does not stick to the bottom of the pan but is glossy and buttery.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E1795CEC9CF4B4587C970CAAB738909', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 7, NULL, NULL, 2, NULL, 0, 'Cover with water. Don’t forget to add a generous pinch of salt.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'870F09E91D46524B8EFF70EBC127BB1A', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 8, NULL, NULL, 2, NULL, 0, 'Get the pan to a gentle simmer and then cover for 7 mins.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2748C71D0BBB946BFB90980CC9C7937', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 9, NULL, NULL, 2, NULL, 0, 'Once cooked and all the water is absorbed add a tablespoon of butter on top and put the lid back on for a couple of minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F66CC8458493A946B3A1438E60F24DEE', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 10, NULL, NULL, 2, NULL, 0, 'In a frying pan cook your fried egg at a high heat in butter for the ultimate crisped edges.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3777495606D6A64C92F920D35DB67DA3', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 11, NULL, NULL, 2, NULL, 0, 'Remove lid sand fluff the rice with a fork then stir in the buttered pistachios.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC4CAADC50DC4D45AA0F2B41CA23EE66', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 12, NULL, NULL, 2, NULL, 0, 'Squeeze in your lemon juice and season.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30654657073F1E49957639D4314956D3', X'C7945CBBA2859F49941EFF31A3171B38', X'C7945CBBA2859F49941EFF31A3171B38', 13, NULL, NULL, 2, NULL, 0, 'Serve up your fragrant curried rice with a sunny side egg on top and some chopped mint.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD9284BDD7B6D44DB1DEB8610A4F0148', X'910E3FB261F6534D810B08506125364B', X'910E3FB261F6534D810B08506125364B', 0, NULL, NULL, 2, NULL, 0, 'Heat a deep pan with a glug of olive oil / coconut oil, then add the finely chopped onion, chilli and ginger & fry for 6-7 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D26D04026F72446BA0A1773E51741C4', X'910E3FB261F6534D810B08506125364B', X'910E3FB261F6534D810B08506125364B', 1, NULL, NULL, 2, NULL, 0, 'Add the finely chopped garlic cloves and fry for a further 1-2 minutes until cooked before adding the tinned coconut milk, birdseye chillies, bay leaves and spices');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C0ED0CEC93D5F4E9E08B134AA9F5CB8', X'910E3FB261F6534D810B08506125364B', X'910E3FB261F6534D810B08506125364B', 2, NULL, NULL, 2, NULL, 0, 'Mix the plant-based milk with the lemon juice and leave to curdle before adding to the pan and allow to sit on a low heat for around 10 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17106A834F8B2B4C8A1E0B7257BD1A84', X'910E3FB261F6534D810B08506125364B', X'910E3FB261F6534D810B08506125364B', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile in a pot add 150g Tilda Pure Original Basmati with 300ml water, large pinch of salt and allow to simmer with the lid on for around 10-15 minutes (it should absorb the water and be perfectly cooked and fluffy)');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42323AA271F5F04499A2DC7AD23BF676', X'910E3FB261F6534D810B08506125364B', X'910E3FB261F6534D810B08506125364B', 4, NULL, NULL, 2, NULL, 0, 'Taste test the curry adding a little more salt & pepper if needed then serve up!');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F1CB194829D104B8E4D2D6F9198EB01', X'94DA12657C234544BB6B19FB759D216C', X'94DA12657C234544BB6B19FB759D216C', 0, NULL, NULL, 2, NULL, 0, 'Mix together the sesame oil, ginger, honey, Chinese five spice and soy sauce. Coat the chicken with the honey.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB2552955B84F4CBF782C6BB0FECD6D', X'94DA12657C234544BB6B19FB759D216C', X'94DA12657C234544BB6B19FB759D216C', 1, NULL, NULL, 2, NULL, 0, 'Add the chicken to a hot griddle pan or wok and fry until cooked and a lovely golden brown colour. Set aside and keep warm.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68F36E33D758B54AA423589C02DDEBDC', X'94DA12657C234544BB6B19FB759D216C', X'94DA12657C234544BB6B19FB759D216C', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in pan and gently fry the spring onions until soft. Heat the rice in the microwave for 1 minute and then add to the spring onions.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B3BA6E99B5E1940AF4F728A965688BB', X'94DA12657C234544BB6B19FB759D216C', X'94DA12657C234544BB6B19FB759D216C', 3, NULL, NULL, 2, NULL, 0, 'To serve heap the rice mixture into a large bowl and top with the chicken. Drizzle any leftover juices over the chicken and finish with a sprinkle of toasted sesame seeds (if using).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA9D69BD2BB1EB4FA1C897C52A51D3D9', X'B55A6D9039981141B469D956ED58B718', X'B55A6D9039981141B469D956ED58B718', 0, NULL, NULL, 2, NULL, 0, 'Blanch the broccoli in lightly salted boiling water for 2 minutes, drain then set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF3933905154C144B3A52148E9791962', X'B55A6D9039981141B469D956ED58B718', X'B55A6D9039981141B469D956ED58B718', 1, NULL, NULL, 2, NULL, 0, 'Mix together the hoisin sauce, Chinese five spice powder, garlic, ginger and pork in a large bowl and leave to marinade for at least 30 minutes (if you have time you can make this the night before).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6775DC42453D74F91017ACF5142FEC9', X'B55A6D9039981141B469D956ED58B718', X'B55A6D9039981141B469D956ED58B718', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large non-stick wok or frying pan and stir-fry the onion for 2 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43FC72121C75E34ABC9C0B412AFE77E2', X'B55A6D9039981141B469D956ED58B718', X'B55A6D9039981141B469D956ED58B718', 3, NULL, NULL, 2, NULL, 0, 'Lift the meat from the marinade, shaking off the excess. Add the meat to the wok and stir-fry for 4 minutes. Keep the marinade aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D498C431313C664FB862F2ECE18AF62B', X'B55A6D9039981141B469D956ED58B718', X'B55A6D9039981141B469D956ED58B718', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the rice according to pack instructions.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A80C9DDF40A954A96AEDE7E77AF70C9', X'B55A6D9039981141B469D956ED58B718', X'B55A6D9039981141B469D956ED58B718', 5, NULL, NULL, 2, NULL, 0, 'Add the broccoli, spring onion and reserved marinade to the pork mixture and stir-fry for 2 minutes until piping hot. Serve spooned over the rice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6B87AD3EFCE0F4199DC2ACD269D8832', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 0, NULL, NULL, 2, NULL, 0, 'Place the chilli and spring onion into a bowl of ice cold water and set aside while cooking the curry.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62108BC3DE722B44A29BD4AA009E22CA', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 1, NULL, NULL, 2, NULL, 0, 'Dry fry the peanuts in a small pan for 1-2 minutes until toasted. Set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44B22DA59BA2214793D538A82AF386D7', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 2, NULL, NULL, 2, NULL, 0, 'Place coconut milk, chicken stock, cinnamon stick and curry paste into a large saucepan and bring to the boil.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A46CC409285D9E4DA79C4605487EE039', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 3, NULL, NULL, 2, NULL, 0, 'Stir in the chicken and potatoes , cover and cook over a low heat for 20 minutes or until the chicken and potatoes are tender and cooked through.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A74D53F802FC744CA35F1582F43BC659', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 4, NULL, NULL, 2, NULL, 0, 'Discard the cinnamon stick. Stir in half the coriander, lime zest and juice and season to taste.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B0F094E831A349AB237B4A415C2EED', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 5, NULL, NULL, 2, NULL, 0, 'Cook the rice according to pack instructions then arrange into individual serving dishes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29B88697FDAD0747916B21C223538830', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 6, NULL, NULL, 2, NULL, 0, 'Spoon over the Massaman curry. Scatter over the peanuts and remaining coriander. Drain the spring onions and scatter over the curry to serve.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D58B052E868F0F4F873FE535D4ABB286', X'F076D7B24810EA4AB7AC8813EC3E66F1', X'F076D7B24810EA4AB7AC8813EC3E66F1', 7, NULL, NULL, 2, NULL, 0, 'Serve with lime wedges to be squeezed over to taste.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97CD9A156008714D96631590C7832C44', X'242F0F4C6527364FB2B5402771114A6D', X'242F0F4C6527364FB2B5402771114A6D', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180/Gas mark 5.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A3267766562F4DA85F976987A4DEE6', X'242F0F4C6527364FB2B5402771114A6D', X'242F0F4C6527364FB2B5402771114A6D', 1, NULL, NULL, 2, NULL, 0, 'Cut a generous square of tin foil, brush the oil over the fish and place on the foil.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DAB27CB00806A418F59BCA1BCBEF1B4', X'242F0F4C6527364FB2B5402771114A6D', X'242F0F4C6527364FB2B5402771114A6D', 2, NULL, NULL, 2, NULL, 0, 'Add the other ingredients (except the rice).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09A1EF43DB876646BC5058B07BE78286', X'242F0F4C6527364FB2B5402771114A6D', X'242F0F4C6527364FB2B5402771114A6D', 3, NULL, NULL, 2, NULL, 0, 'Draw up the corners to make a loose parcel and leave a small hole at the top.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A6EDF3A7367FB43A8C72B22BB0BC717', X'F610D33F7496884DBF75F83DB5DF5BF3', X'F610D33F7496884DBF75F83DB5DF5BF3', 0, NULL, NULL, 2, NULL, 0, 'Place the frozen peas in a pan of boiling water and cook for 4-5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D73D5E094BDFB42933DCA508AD22ABB', X'F610D33F7496884DBF75F83DB5DF5BF3', X'F610D33F7496884DBF75F83DB5DF5BF3', 1, NULL, NULL, 2, NULL, 0, 'Drain and set aside.');
