INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB55F0B2CBCB2E4597BB027D7B8A9EFB', X'0E7867418ED25F44BBAE6F41A3D102D5', X'0E7867418ED25F44BBAE6F41A3D102D5', 1, NULL, NULL, 2, NULL, 0, 'Heat Tilda Pure Basmati rice according to pouch instructions and place into a serving bowl.');
