INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB31016F0E4C842B45C7FB23AF71B68', X'5DDCE6C35FACF94280B789A6FAB07348', X'5DDCE6C35FACF94280B789A6FAB07348', 0, NULL, NULL, 2, NULL, 0, 'Add the coconut oil, garlic, ginger, red chilli, red onion and broccoli to a large frying pan or wok. Cook over a medium heat, stirring gently, until the onion begins to soften, about 3-4 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E41EC26B43C6234598DE43525D775C85', X'5DDCE6C35FACF94280B789A6FAB07348', X'5DDCE6C35FACF94280B789A6FAB07348', 1, NULL, NULL, 2, NULL, 0, 'Add the coconut Super Grains and edamame beans and continue to cook for a further 2-3 minutes until heated through.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66617B0CACA3914FB39AD631E475CE4D', X'5DDCE6C35FACF94280B789A6FAB07348', X'5DDCE6C35FACF94280B789A6FAB07348', 2, NULL, NULL, 2, NULL, 0, 'Add the grated carrot and stir to combine.');
