INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C14841ED3C36C4689E1476A0A75BAF9', X'FF56C706AA259E44AA67E04BE33324B4', X'FF56C706AA259E44AA67E04BE33324B4', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200°C, gas mark 6. Use a non stick 21cm oven proof frying pan.');
