INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFA4DD5BC5BFAD42849A4AE89F5BE864', X'FF56C706AA259E44AA67E04BE33324B4', X'FF56C706AA259E44AA67E04BE33324B4', 4, NULL, NULL, 2, NULL, 0, 'Heat the rice according to pack instructions. Mix the rice with the tomatoes, add the peas, eggs and half of the cheese. Season to taste.');
