INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27DBA268645FD5469B9B39EC758C7869', X'B668E212EAC879419FD6844EEDF8D4A0', X'B668E212EAC879419FD6844EEDF8D4A0', 2, NULL, NULL, 2, NULL, 0, 'Cool slightly and mix in the mincemeat.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E750453D56AB74DB7F7B4978EC9A13E', X'B668E212EAC879419FD6844EEDF8D4A0', X'B668E212EAC879419FD6844EEDF8D4A0', 3, NULL, NULL, 2, NULL, 0, 'Cut all the filo sheets into 4 squares.');
