INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB4543E758492A4AAAD33F19E5A6E37A', X'2DA212864E012F4C809A22EEBA56A172', X'2DA212864E012F4C809A22EEBA56A172', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large frying pan and fry the onion until soft.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAC8E1377ED80E4B81BB4C66E11358E9', X'2DA212864E012F4C809A22EEBA56A172', X'2DA212864E012F4C809A22EEBA56A172', 1, NULL, NULL, 2, NULL, 0, 'Add the curry paste and the haddock for 3 - 4 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C0D41951BC85442BF4EBC9E04B6DEE6', X'2DA212864E012F4C809A22EEBA56A172', X'2DA212864E012F4C809A22EEBA56A172', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the rice according to the pack instructions then add to the pan with the peas and heat through.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B4FB779126E7D46891718B043F69595', X'2DA212864E012F4C809A22EEBA56A172', X'2DA212864E012F4C809A22EEBA56A172', 3, NULL, NULL, 2, NULL, 0, 'Stir in the parsley and season to taste.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26F3BFC4903A6B43A82B4EF4EDB75C96', X'2DA212864E012F4C809A22EEBA56A172', X'2DA212864E012F4C809A22EEBA56A172', 4, NULL, NULL, 2, NULL, 0, 'Serve piping hot garnished with the eggs and a squeeze of lemon juice.');
