INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'464D8812C895C240BBCC2F0A75766A9B', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 0, NULL, NULL, 2, NULL, 0, 'To make koftas: Pre-heat oil for deep frying');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9A147838C41FF468F1C82F347482C1E', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, mix together paneer, lauki and potato');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'487A71E4BC473B439C82C074E718FEF1', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 2, NULL, NULL, 2, NULL, 0, 'Add red chilli, salt, cumin powder and garam masala together with gram flour, mix well using your hands to combine the mixture');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9406C3003CC6B548BC36DCDBE4CFA300', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 3, NULL, NULL, 2, NULL, 0, 'Once mixture is combined well together, moisten your hands using the oil and make balls from the mixture (about 4cm diameter). Use the oil to moisten hands as needed');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6FAE7C0E2B06F4FA6D1E10DD0ECB538', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 4, NULL, NULL, 2, NULL, 0, 'Deep fry the kofta balls until golden brown, your oil should be on about 175°C, this should take about 3-4 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E94C849381F65449DC29F15C99B2137', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 5, NULL, NULL, 2, NULL, 0, 'Remove the koftas from oil onto a tray and leave to one side');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E805E4752BB4B441865365AAB608E2F2', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 6, NULL, NULL, 2, NULL, 0, 'To make the sauce: Heat vegetable oil in a saucepan');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E54F3830C401A643B3CE8E419A4CCBD9', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 7, NULL, NULL, 2, NULL, 0, 'Add the cumin seeds and once the cumin starts to sizzle add onions and cook for 4-5 minutes until onions are very brown and caramelised');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17F407B89ECACD4CA68748970765E29F', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 8, NULL, NULL, 2, NULL, 0, 'Add ginger and garlic paste and green chilli paste followed by the puréed tomatoes and cook for a few seconds');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FE4A5283F3D9C4F9959A527A482F285', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 9, NULL, NULL, 2, NULL, 0, 'Add in the turmeric, red chilli powder and salt and mix well');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AD275FA674F254AB6C14702D0EF3521', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 10, NULL, NULL, 2, NULL, 0, 'Cook the masala sauce on a medium heat with the lid on for 4-5 minutes (keep mixing every minute or so)');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAE7F229C655DB4E94FECA33EA9F8BCE', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 11, NULL, NULL, 2, NULL, 0, 'Once the oil starts to seep out the edges and the sauce is sizzling, the masala is cooked perfectly, add in the water and bring the gravy to a boil');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE7EB7D7144BAE4FAFD9EC7305533A05', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 12, NULL, NULL, 2, NULL, 0, 'Add green cardamom powder and double cream to the sauce together with the fenugreek leaves (crushed), garam masala, fresh coriander and anardana powder, cook for 1 minute or so');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F9060283C93E64D9C77AA237CD16B5B', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 13, NULL, NULL, 2, NULL, 0, 'Add in the fried kofta balls and shake the pan to coat the balls evenly with the sauce');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25CD7555CBE4254F9E965B10109B2F69', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 14, NULL, NULL, 2, NULL, 0, 'Mix well together and cook for a final 2 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD8824068336BD44928C390F9B83FF01', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 15, NULL, NULL, 2, NULL, 0, 'Remove from the heat and garnish with fresh coriander and a swirl of double cream');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B8795C9A0819F4198D6651A15FE6EC9', X'132886B73D5D9248B94E012FF433D28C', X'132886B73D5D9248B94E012FF433D28C', 16, NULL, NULL, 2, NULL, 0, 'Serve with Tilda Pure Basmati Rice and relish. Cook rice as per pack instructions.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9124CF570C563E439451460A6D554261', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a saucepan, add cumin seeds, cinnamon sticks, black cardamom, peppercorn and cloves. Cook until the spices begin to sizzle');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A7F42E4CDFCB24FA0B2BE6BB6CA0320', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 1, NULL, NULL, 2, NULL, 0, 'Add onions and cook for 1-2 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A77C652618B884CA8768E894E5CFF67', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 2, NULL, NULL, 2, NULL, 0, 'Add ginger and garlic paste, cook for 2 minutes before adding mixed vegetables and red chilli powder to pan');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EEDB26B85E90640B75928CA20DFA097', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 3, NULL, NULL, 2, NULL, 0, 'Cook the veg for 1 minute or so before adding water, salt and garam masala to the pan and bring to the boil (test the water for seasoning, it should be a little over seasoned as the rice will require this for flavouring)');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A536EE128A13A7438654A764BDFC35AD', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 4, NULL, NULL, 2, NULL, 0, 'Once the water is boiling, drain the soaked rice and add to the pan');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CEE0E4537DBC04AAD89AE173CE43DEA', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 5, NULL, NULL, 2, NULL, 0, 'Cook the rice on a medium heat (stirring gently occasionally so the rice grains do not break), this should take 5-7 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54B243B08AE96041BEAE4B2D7FE5BCD1', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 6, NULL, NULL, 2, NULL, 0, 'Once the water has almost been soaked up by the rice, completely lower the heat and cover the pilau with two small sheets on foil (inside of the pan) to completely cover the rice and also the top of the pan. Put a lid on the saucepan and steam cook on a low heat for 2 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A1208E96059594E84120000DED2E7C5', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 7, NULL, NULL, 2, NULL, 0, 'Switch off the heat and steam the pilau without any heat for 15 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27965FDDF622974481865BF294EB10A1', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 8, NULL, NULL, 2, NULL, 0, 'Uncover the pilau, lifting the foil and then using a spoon gently lift the rice from 4 sides of the saucepan using a wooden spoon to fluff the rice and shake it so it opens up, each grain should be separate and the rice fluffy and delicious');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B26E8EDC048A448A7892743316E6898', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 9, NULL, NULL, 2, NULL, 0, 'Garnish with dried fried onions, pomegranate pearls and fresh coriander');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E5E0CFB7B41D24094CE06B9750D6ECE', X'20F0EE7A24337B4FAFBE6D292B3FE19E', X'20F0EE7A24337B4FAFBE6D292B3FE19E', 10, NULL, NULL, 2, NULL, 0, 'Serve with a raita or plain yoghurt');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97AAA14D8AD42D4A99427C79742C6CC2', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 0, NULL, NULL, 2, NULL, 0, 'Fill a pan with water and place on high heat.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A09D370CF1908D4381DB0F95620AC41A', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 1, NULL, NULL, 2, NULL, 0, 'Add ¼ cup salt and bring to a boil.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A0B505DF8BAE43B81B57D00CDC1875', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 2, NULL, NULL, 2, NULL, 0, 'Add rice and cook for 6-8mins. Once the rice has parboiled, remove from hob and drain.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85595A41483C1A45B915BCEBD1F50FF9', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 3, NULL, NULL, 2, NULL, 0, 'While the rice is cooling, add ¾ cup barberries to a pan with a small knob of butter. Allow the barberries to plump up slightly and cook on low-medium heat for about 5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2357311864C4847B0A7184B04B1C205', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together egg yolks, yoghurt and crushed saffron.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADA5316D62F480469E2E5FF0B6E495AC', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 5, NULL, NULL, 2, NULL, 0, 'Add the parboiled rice to the bowl and mix well so all the rice is covered in yoghurt mixture.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4FCDE3BA6641043AA6E98B2F674A7E0', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 6, NULL, NULL, 2, NULL, 0, 'Grease a baking dish and add half the amount of rice mixture.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7671BD2573338D478EF87A055841A9C7', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle barberries on the top half of the rice (save some to decorate the top too).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCA7D083DCA412479B458963A169BDFC', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 8, NULL, NULL, 2, NULL, 0, 'Add remaining half of rice and use and gently pat down the top layer evenly.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBF370A7E5E5E34993519648F347AEBE', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 9, NULL, NULL, 2, NULL, 0, 'Cover the dish with foil and place in oven for 50mins- 1hr.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8C0F5E3B49F0442AA721BFA83489E89', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 10, NULL, NULL, 2, NULL, 0, 'Remove from oven and flip dish over to remove rice cake on a plate.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'060701AB1D9AA5479A68BA86B05729CC', X'85C879E9B7D7CB4CB1A14918698267FA', X'85C879E9B7D7CB4CB1A14918698267FA', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle barberries and crushed pistachios on top of rice cake to decorate.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69C49FCCFEBAEE4BB12FBAF9931A6D14', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 200 °C');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD76A94143153E499700B8B6C3F2CA5F', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 1, NULL, NULL, 2, NULL, 0, 'Begin by marinating the chicken in tandoori masala. Set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95164B6499EAE14DA9B1F0B124E977B2', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 2, NULL, NULL, 2, NULL, 0, 'Take a large casserole dish and add in the chicken stock, salt, garlic paste, green chilli, cream and milk. Mix together until everything is well combined');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3175CD0A530F284EBD6A861084F53AA0', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 3, NULL, NULL, 2, NULL, 0, 'Add in the Tilda Pure basmati rice and mix again.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'653FDECDB6A1C141B4A29009C4D39C25', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 4, NULL, NULL, 2, NULL, 0, 'To the top, layer on enough fried onions to cover the dish.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A1BB51D6FD7947956CFC8F50558A99', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 5, NULL, NULL, 2, NULL, 0, 'Carefully add the chicken breasts to the top.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95B7B50508EBF643AF6B7065B24680CC', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 6, NULL, NULL, 2, NULL, 0, 'Bake in the oven for 1 hour.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55B54A103786B648BAAABA55F15890DC', X'B56F8B190901854EAF555852C3033897', X'B56F8B190901854EAF555852C3033897', 7, NULL, NULL, 2, NULL, 0, 'Remove from the oven and leave to rest for 5-10mins before eating.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A6118B32AA7C2478EBCD5E323FF6334', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 0, NULL, NULL, 2, NULL, 0, 'First make the marinade. Put the onions into a bowl and rub them gently under running cold water. Drain well in a colander, then dry in a salad spinner or with kitchen paper.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47B7AAF15B790447B695405B0A45E20B', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a deep pan. When hot, fry the onions in 3 separate batches until pale golden. Using a slotted spoon, transfer them to a colander, where they will continue to brown in their own heat. The oil can be reused another time if cooled and strained.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'934C6D10B8CE8D45BA668F660663C3AB', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 2, NULL, NULL, 2, NULL, 0, 'Put all but 2 tablespoons of the fried onions into a blender, add the remaining marinade ingredients and whiz to a purée. You might need to do this in batches so that the machine can create a smooth paste and the motor doesn’t overheat. Transfer the mixture to a large flameproof casserole dish.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2560AA33C0DDA148BCF0BED76306D296', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 3, NULL, NULL, 2, NULL, 0, 'Trim any excess skin from the chicken pieces, then place the meat in the marinade. Cover with a tight-fitting lid and set aside in the refrigerator for at least 1 hour.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA5D658DAEC2F34B807B8DC6715EC1F3', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180ºC/350ºF/Gas Mark 4. Transfer the casserole dish to the oven and cook for about 1 hour, until the chicken is tender.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14657F4C12AD894A87C273EA70CA9723', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the potatoes in a pan of boiling salted water for about 8–10 minutes, until tender. Drain and set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE66BBEFB79EA243A52481A3AF14E61F', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 6, NULL, NULL, 2, NULL, 0, 'Put the measured water into a large saucepan with the bay leaves and some salt and bring to the boil. Add the rice and boil for roughly 6 minutes, until al dente. Drain well, reserving the water to make soup if you wish. Fork through the rice to loosen the grains.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53561DE20D77D54A93D7F5C6EA03F109', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 7, NULL, NULL, 2, NULL, 0, 'Heat the saffron threads in a warm small frying pan over a gentle heat until they are crisp. Add 2–3 tablespoons of water and set aside to rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'319864F17760A941B22BDC77472E2874', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 8, NULL, NULL, 2, NULL, 0, 'Pour the saffron and its liquid over the rice while still in the colander and mix lightly to partially colour the grains.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CE3FF7D8C87174BA34604874F56E6BB', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 9, NULL, NULL, 2, NULL, 0, 'When the chicken is ready, the sauce might look too thin or a bit oily, but don’t worry about that. Heat it, uncovered, for a few minutes to reduce it if you wish, but a generous amount of sauce is needed to finish the dish. Taste and adjust the salt if necessary, then add the fresh mint and coriander. Transfer the chicken to a plate and pour the sauce into a jug. Lower the oven temperature to 150ºC/300ºF/Gas Mark 2.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C26BA7022F1CCA428DA8490F1FC88AB4', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 10, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large saucepan. Return some of the sauce to the empty casserole dish and cover with one-third of the rice. Arrange the chicken pieces and a few chunks of potato on top, then cover with another third of the rice. Pour over some more sauce, dot with the remaining potatoes and cover with the remaining rice. Press down gently. Drizzle the remaining sauce over the rice, sprinkle with the reserved onions and the melted butter and cover tightly.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'713111333CB4AA4E8F87B4A25366FFFA', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 11, NULL, NULL, 2, NULL, 0, 'Place on the middle shelf of the oven for up to 40 minutes to heat through fully. If the ingredients have become cold during the process of putting the dish together, they might need longer to heat through. In this case, lower the temperature to 120ºC/250ºF/Gas Mark ½ and continue heating until completely hot.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D417FA95097C2F448E69ABD6F78D96F9', X'97B94910F99737438C7E5433201F7240', X'97B94910F99737438C7E5433201F7240', 12, NULL, NULL, 2, NULL, 0, 'Serve the biryani with Mixed Raita or a daal, allowing people to help themselves straight from the dish if you like.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7147CF269DD6B24CA4EC4EF120B58102', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 0, NULL, NULL, 2, NULL, 0, 'Make the cured egg yolks by mixing the tamari and mirin together in a sealable container. Gently add the egg yolks, cover and place in the fridge for 4–6 hours');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2456911AE61E254482A4EF70CABD1C19', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 1, NULL, NULL, 2, NULL, 0, 'Place the cabbage in a bowl and drizzle over the rice vinegar, sesame oil and maple syrup. Stir to ensure the cabbage is completely coated and set aside to pickle while you prepare the rest of the dish');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F6CAA94B2B3C34BB5A7AC825561186D', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 2, NULL, NULL, 2, NULL, 0, 'Place the brown rice blend into a large saucepan of boiling water set over a medium heat and cook for 25 minutes. Drain well, cover and set aside for 3 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43A9CDB1EEF49F4A9B7DF9801DDAFD61', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 3, NULL, NULL, 2, NULL, 0, 'While the rice is cooking, make the donburi sauce by adding the soy sauce, mirin, maple syrup, ginger and garlic to a small bowl, stirring to combine');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DDDF955F2E3D74EA2F4657136686556', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 4, NULL, NULL, 2, NULL, 0, 'Pour the olive oil to a large frying pan set over a low heat. Add the chopped onion and cook gently for 5 minutes until soft and translucent');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C8AE8ABD5DEFC488CDF5F0DA3B8A55F', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 5, NULL, NULL, 2, NULL, 0, 'Turn the heat up to medium and add the chicken pieces. Fry for 5 minutes, stirring regularly, then add the mushrooms. Fry for a further 3 minutes, or until the chicken and mushrooms are cooked');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268DC59EE0A8374BB913C695A3636706', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 6, NULL, NULL, 2, NULL, 0, 'Add the donburi sauce to the pan and cook for a further 2 minutes, or until the sauce has thickened and reduced slightly');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'485D0FCAC0EAC94DB2A55D1D94ECADD7', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 7, NULL, NULL, 2, NULL, 0, 'Divide the cooked rice & quinoa between four bowls, then add the chicken doburi, pickled cabbage and avocado. Using a spoon, carefully remove the cured egg yolks from the curing liquor and gently place on top of the rice');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6204D04A0ED224F9885C22AC1C67E05', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 8, NULL, NULL, 2, NULL, 0, 'Garnish with chopped spring onions, micro herbs and a sprinkle of black sesame seeds and serve immediately');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F79EE2D1E3AD114FA34BA10DD92F50B9', X'D42ADA01ED54D140BDB95BD9A9412D8B', X'D42ADA01ED54D140BDB95BD9A9412D8B', 9, NULL, NULL, 2, NULL, 0, 'You can use Tilda Pure Basmati as an alternative.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'078AD6E7B3E2104FAE62A2AE2EB5E756', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 0, NULL, NULL, 2, NULL, 0, 'Wash the basmati rice in several changes of cold running water until the water runs clear, then place in a bowl and soak for 2 hours in more fresh cold water. If you do not have time to soak the rice for 2 hours, then do soak it for as long as possible – I always see a difference in the length of grain of rice after I soak the rice. In addition to this, the grains are less likely to stick as they have absorbed water while soaking and therefore cook faster');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5C1FA9D58296949987D9ADDE334E3B8', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 1, NULL, NULL, 2, NULL, 0, 'When ready to cook, drain the soaked basmati rice and set aside. In a heavy-based pan that has a lid, heat 1 tablespoon of the ghee over a medium-high heat');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8BCE2FDDC853442A6ABA6C7D934AED3', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 2, NULL, NULL, 2, NULL, 0, 'When the ghee is hot, add the cardamom pods, cassia bark, cloves and bay leaf to the pan. After a few seconds, when the spices begin to sizzle and pop, using a slotted spoon, remove them from the pan to a plate and set aside');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D5BD546A47E9459416E45B8C3F2E16', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 3, NULL, NULL, 2, NULL, 0, 'Using the same pan the spices were fried in, fry the cashew nuts and stir until lightly brown, then remove with a slotted spoon. Lower the heat and add the raisins (you need to be quick as you do not want them to burst; remove them the moment they start to swell up)');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCAC7C2AB4268846B54735362E27C2DE', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 4, NULL, NULL, 2, NULL, 0, 'Finally add the onion to the pan and fry gently, stirring occasionally, until golden brown and caramelized. Using a slotted spoon, remove the onion from the pan and place on a plate to drain. Spread the onions across the plate so they crisp as they cool');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6F93FF18035A34E8F91E78806DFA671', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 5, NULL, NULL, 2, NULL, 0, 'Put the kettle on to boil. Add the soaked, drained basmati rice to the pan with the remainder of the ghee. Add the fried spices, bay leaf and half the caramelised onions. Stir for 1 minute to coat the rice in the ghee and spice mixture, then add the salt and cover the rice with 600ml boiling water from the kettle');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABEC6A14997310419DE53E739E5CAC19', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 6, NULL, NULL, 2, NULL, 0, 'Cook, uncovered, over a medium-high heat until the water has been almost absorbed by the rice (about 4 minutes)');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51FFCFBEE988A44BA37BF5E7A5D29305', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 7, NULL, NULL, 2, NULL, 0, 'Once most of the water has been absorbed, cover the pan with a lid, lower the heat and simmer for a further 5 minutes. Once the rice is cooked, add the fried cashew nuts and raisins and gently mix using a fork to lift and separate the grains');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8605320C1AED6458DF319548C7CBD3A', X'8844260842E6C24DB5FF88CA3D06D11C', X'8844260842E6C24DB5FF88CA3D06D11C', 8, NULL, NULL, 2, NULL, 0, 'Cover again and leave for a further 5 minutes before serving. Garnish with the remaining browned onions and raisins');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4F29F726E2F4045A28795C1C1D51AC4', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 0, NULL, NULL, 2, NULL, 0, 'Put the star anise, coriander, cloves, peppercorns, cumin, cardamom, fennel and cinnamon stick into a spice grinder, food processor or pestle and mortar to grind them, and then set the spice mix to one side.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'667BC11DAE6A584CBC511958A4BAE070', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 1, NULL, NULL, 2, NULL, 0, 'Rinse your basmati rice in at least three changes of water to wash out the starch and leave to soak in a bowl of cold water on one side for 30 mins. Next, chop your onions in half, peel them and then slice into fine rings. Pop the ginger and garlic into a food processor, or finely grate the ginger and crush the garlic.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5415F14593223546967238D81A663CC0', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 2, NULL, NULL, 2, NULL, 0, 'Put a tablespoon of oil into a large, heavy-bottomed, lidded frying pan on a high heat. When you are sure that the pan is very hot, sear the lamb in batches (so that you don’t crowd the pan), turning them until they are golden brown. Take the meat out with a slotted spoon and set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D838C29F331B04E8402B89169D4164B', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 3, NULL, NULL, 2, NULL, 0, 'Add another 2 tablespoons of oil to the same pan and fry the onions for 8 to 10 minutes, until they are soft and golden. Take out half of the onions, transfer them to a bowl and set to one side. Then put the garlic and ginger into the pan. Stir-fry for a couple of minutes and add the spices that you have ground, together with the turmeric and chilli powder. Keep stirring to ensure these don’t burn at the bottom of the pan.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D4954E4435B794C94D7BA0E756A3E64', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 4, NULL, NULL, 2, NULL, 0, 'After a minute or so, add the lamb, yoghurt and tomato tin blitzed, stir well to ensure it is all mixed together, and add 400ml of warm water. Bring to the boil, then turn the heat down to low, pop the lid on and leave to simmer for around 1½ hours, or until lamb is tender.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5EE2F9171C8D945AFD2297FD8FF266D', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 5, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180°C/350°F/gas 4.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E93B8DBB93742439256DEC8CC93318C', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, you’ll need to cook the rice and prepare the fried onions for layering. Drain the rice and transfer to a saucepan. Boil a large pan of water add the rice so that there is 4 times as much water as the rice, and bring to the boil. Boil for 8 minutes, until it’s just cooked but still has a bite. Drain and set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B93A88F6CF84FA4B9CA68B53C4BC39D2', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 7, NULL, NULL, 2, NULL, 0, 'Transfer the onions from the bowl to a small frying pan and continue to fry for another 10 minutes, or until the onions are caramelized, then take off the heat. Check on the lamb: as soon as it is falling apart, season with salt to taste and take off the heat. Seal the pot with a tight lid or foil.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4711A0B7DF62D4B890C1CEECC091C74', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 8, NULL, NULL, 2, NULL, 0, 'Take an ovenproof pot which will snugly accommodate the rice and lamb, such as a casserole dish. Remove half of the meat from the lamb curry using a slotted spoon (to leave the sauce behind) and place it at the bottom of the pot. Top the meat with half of the rice and half of the onions and repeat with the rest of the meat, rice and onions. Keep the sauce of the curry aside to serve with the biryani. Bake the biryani for 20 mins.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C33A73E3161D84084996B35181A24BD', X'2A1E124DC5AD144A885AB958E4B7D181', X'2A1E124DC5AD144A885AB958E4B7D181', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the rice with the rose water, saffron, Toasted cashews and sliced pistachio.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBCAFCF81FC7524E93DC996C99583F9B', X'07F1C2F698F2ED44A443F745BBC1CF7F', X'07F1C2F698F2ED44A443F745BBC1CF7F', 0, NULL, NULL, 2, NULL, 0, 'Mix the saffron threads into a 4 tsps of hot water, Set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5B559CDDA351F46BA9BD34DFE8F1F34', X'07F1C2F698F2ED44A443F745BBC1CF7F', X'07F1C2F698F2ED44A443F745BBC1CF7F', 1, NULL, NULL, 2, NULL, 0, 'Soak the rice for 30 minutes in cold water. Rinse the rice thoroughly when you drain it and set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D9C6FAB76F0334DBFF9D174E2604EDC', X'07F1C2F698F2ED44A443F745BBC1CF7F', X'07F1C2F698F2ED44A443F745BBC1CF7F', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large skillet and saute the fennel and cumin seeds until they are fragrant, just a minute or two. Add the cinnamon, cardamom and allspice and stir to combine. Add the onion and saute over low heat until the onion is softened, about 10 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AD4B06CAFF9EB41A052AD62580CB6B2', X'07F1C2F698F2ED44A443F745BBC1CF7F', X'07F1C2F698F2ED44A443F745BBC1CF7F', 3, NULL, NULL, 2, NULL, 0, 'Add in the rice and saute it for a minute or 2 with the spices and onion.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7E651CD8C65CB43B4B2F9830900DC68', X'07F1C2F698F2ED44A443F745BBC1CF7F', X'07F1C2F698F2ED44A443F745BBC1CF7F', 4, NULL, NULL, 2, NULL, 0, 'To 1 litre of water add the bay leaves and lemon rind. Stir to combine, season with salt and fresh cracked black pepper.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FB5B65EC4A83146B7744CBA19DDFFC7', X'07F1C2F698F2ED44A443F745BBC1CF7F', X'07F1C2F698F2ED44A443F745BBC1CF7F', 5, NULL, NULL, 2, NULL, 0, 'Add in the fruit and nuts, stir to combine, and cover the skillet with a tight fitting lid. Cook on low to medium heat for about 8 minutes, then turn off the heat and let it sit, covered to let it steam dry. Add the saffron water in swirls and let this sit for another 10 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A39BD4E79C7F0441B73612CE1A2BFD81', X'07F1C2F698F2ED44A443F745BBC1CF7F', X'07F1C2F698F2ED44A443F745BBC1CF7F', 6, NULL, NULL, 2, NULL, 0, 'When the rice is done, fluff it, remove the bay leaves, and spoon onto a large platter. Scatter the pomegranate seeds on top to garnish.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'349AD9EBF3AB1E42BCD42A366964CC79', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 0, NULL, NULL, 2, NULL, 0, 'Rinse your basmati rice in at least three changes of water to wash out the starch and leave to soak in a bowl of cold water on one side for 30 mins.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9E134F1C873CF4C8300EBB908D5F86B', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 1, NULL, NULL, 2, NULL, 0, 'Next, chop your onions in half, peel them and then slice into fine rings and dry them off in a tablespoon of oil until golden brown and set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B9812B6C3EBC84B8683E3BFB03E4FAA', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 2, NULL, NULL, 2, NULL, 0, 'Heat a non-stick pan. Add the remaining green cardamoms, cloves, black cardamom and cinnamon along with bay leaf and caraway seeds and roast. Add onions, carrot, French beans, cauliflower florets and green peas.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A9210882DDE0F4AB1F8B6AA9C7221D2', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle salt, cover and cook on medium heat for two minutes. Add ginger-garlic paste mixed with a little water and stir. Cover and cook for two minutes. Add turmeric powder, red chilli powder and coriander powder and cook. Whisk yogurt with rose water and saffron. Add a little water or milk and whisk well. Add tomato puree to the vegetables along with half teaspoon Garam masala powder and mix well. Simmer for two minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54C7C2C39192A546A3603949BBF364EF', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180°C/350°F/gas 4.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA5F01EAB88F1A4CB6C890CC35C9BB5B', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a separate pan you need to cook the rice and prepare the fried onions for layering. Drain the rice and transfer to a saucepan. Pour enough cold water over the rice so that it is covered by 2cm, and bring to the boil. Boil for 8 minutes, until it’s just cooked but still has a bite. Drain and set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC2BF28DF3DFB542ACFAFBB6EF83F129', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 6, NULL, NULL, 2, NULL, 0, 'Take an ovenproof pot which will snugly accommodate the rice and veg curry, such as a casserole dish. Remove half of the veg from the veg curry using a slotted spoon (to leave the sauce behind) and place it at the bottom of the pot. Add a little of chopped mint and coriander at each layer. Top the veg with half of the rice and half of the onions and repeat with the rest of the veg, rice and onions. Keep the sauce of the curry aside to serve with the biryani. Bake the biryani for 20 mins.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20FB1BCE05D4AA48A44CE30040AE3E0D', X'8D6386A3B987EB49A7651E0EA475D11B', X'8D6386A3B987EB49A7651E0EA475D11B', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the rice with the rose water, saffron, Toasted cashews and sliced pistachio.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E969B5A822F254283011BEC7649DB4C', X'D1E5CEC0668FA041B2DD930AA564503B', X'D1E5CEC0668FA041B2DD930AA564503B', 0, NULL, NULL, 2, NULL, 0, 'To begin, toast the cumin and coriander seeds in a dry pan then grind them with the cardamom in a pestle and mortar or spice grinder');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'432110345E94D949BE056188824CD2B5', X'D1E5CEC0668FA041B2DD930AA564503B', X'D1E5CEC0668FA041B2DD930AA564503B', 1, NULL, NULL, 2, NULL, 0, 'In a wide heavy-based pan, soften the onions in some olive oil, then add the ras el hanout and other ground spices to the pan. Cook out for a couple of minutes, stirring so they don''t burn');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BC5588B10D9F04E9AD99118EBE9C808', X'D1E5CEC0668FA041B2DD930AA564503B', X'D1E5CEC0668FA041B2DD930AA564503B', 2, NULL, NULL, 2, NULL, 0, 'Remove the onions from the pan, turn up the heat and add the lamb, stirring occasionally until browned all over. Once browned, reduce the heat, add the stock and 500ml water and pop the onions back into the pan. Cook with a lid on over a very low heat for an hour');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'163F9F090520B94A88E6CA1D94BDF80A', X'D1E5CEC0668FA041B2DD930AA564503B', X'D1E5CEC0668FA041B2DD930AA564503B', 3, NULL, NULL, 2, NULL, 0, 'Add the rice, dried cherries, some salt and pepper, and another 250ml of boiling water and cook for another 40–45 minutes, or until the rice is cooked');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DB8A3B4A3FC634399F3C8406C88AFF8', X'D1E5CEC0668FA041B2DD930AA564503B', X'D1E5CEC0668FA041B2DD930AA564503B', 4, NULL, NULL, 2, NULL, 0, 'When the rice is almost ready, place the spinach in the pan on top of the tagine and put the lid back on to wilt the spinach for a minute. Once wilted, stir it into the dish. Finally, stir in the tarragon and serve');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA08A7103790FD469B127EF632947AE9', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 0, NULL, NULL, 2, NULL, 0, 'To begin, make the marinade. Finely mince the garlic and coriander stems. Dissolve the sugar in the warm water, then combine all the ingredients for the marinade in a bowl, stirring to mix well. Rub all over the chicken and let the chicken sit in the marinade in a large ziplock bag overnight. Turn the bag occasionally so that all sides stay coated with the marinade');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129A776A0E7B414DB1DC23F074C85B26', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 1, NULL, NULL, 2, NULL, 0, 'On the day of cooking, preheat the oven to 160°C/gas mark 3');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C009925521A234482894FE51C619D04', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 2, NULL, NULL, 2, NULL, 0, 'Take the chicken out of the fridge and let it come back up to room temperature. Pat with paper towels to remove the marinade and place on a roasting tray');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBA5B382C83A794CA171F37B4D9E5E5F', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 3, NULL, NULL, 2, NULL, 0, 'Drizzle lightly with oil, place on the oven’s middle rack and roast for 2 hours, basting it regularly with its own juices. Turn the oven up to 220°C/gas mark 8 and let the chicken cook for another 15–20 minutes, or until golden brown');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D383497E866B94EAE0090EB3719AD45', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 4, NULL, NULL, 2, NULL, 0, 'While the chicken is in the oven, prepare the other elements of the dish. Wash and drain the rice. Cover with cold water and leave to soak. Blitz all the ingredients for the green sauce together, adding just enough oil to make a smooth runny sauce, sort of like a pesto. Season to taste with salt. Keep the sauce covered in the refrigerator so it doesn’t lose its vibrant green');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE9517F1315A5B458035071D951803C2', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 5, NULL, NULL, 2, NULL, 0, 'When the chicken is ready, remove it from the oven, lifting it from the tin onto a chopping board, and cover loosely with a foil tent');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1566FE9D0A136A43AEB792BD2C6C4666', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 6, NULL, NULL, 2, NULL, 0, 'While the chicken is resting, drain and tip the rice into a rice cooker. Pour the chicken roasting juices into a measuring jug and top up with enough water so you have 300ml liquid altogether. Add to the rice and let the rice cooker do its magic');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15AFA2750EFFB1448EA970FDA09BF561', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 7, NULL, NULL, 2, NULL, 0, 'For those without a rice cooker, bring the rice and roast chicken juices to a boil in a saucepan, then cover and turn the heat down to the lowest setting. Let the rice cook, undisturbed, for about 10–15 minutes or until all the liquid has been absorbed. Turn the heat off, give the rice a stir, and put the lid back on, letting it finish cooking by steaming in the pan’s residual heat, for another 5 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4146DE10081F4043BB7B4F63768E6A14', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 8, NULL, NULL, 2, NULL, 0, 'When ready to serve, carve and chop the chicken into generous bite-sized pieces before serving with the fragrant rice and green sauce');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F00756E94EACFF4FB4476BC7BC2DC14B', X'A3DCE8E110BF964EBD715C709BF6D719', X'A3DCE8E110BF964EBD715C709BF6D719', 9, NULL, NULL, 2, NULL, 0, 'You can use Tilda Pure Basmati Rice as an alternative.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F389737A4CABC04292304E4FCD98C218', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 0, NULL, NULL, 2, NULL, 0, 'To begin, lightly dust the duck breasts with the Thai 7-Spice and leave to marinate for an hour in the fridge');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A70102C83973454BADDB2EC740AF9899', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the curry paste by simply placing all the ingredients (apart from the coriander leaves) in a blender. Blitz to a rough and ready paste. Place in a bowl or jar and cover (you may have some left over, so a jar might be preferable');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'241CB7F203E33A4CBAA81596D2D077D4', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 2, NULL, NULL, 2, NULL, 0, 'For the crispy shallots, pour the oil into a saucepan and place over a high heat. When the hot is oil (not smoking though) throw in the shallot rounds. They will bubble up immediately and the key here is to keep a close eye on proceedings. As they bubble, the water will evaporate and the bubbling will slowly begin to die down after about 8 minutes or so, when the shallots will begin to become golden and crisp. Don’t let them go too far, or they will burn. Scoop out with a slotted spoon and allow to drain and cool on kitchen paper. These can now be stored in an airtight container');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38D5BFB6B4522547BA927CF78C17A80A', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 3, NULL, NULL, 2, NULL, 0, 'Remove the duck breasts from the fridge and allow to come up to room temperature');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11E300117481C448B66E274A92EFB536', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180°C/gas mark 4Place the duck breasts skin-side down in a cold frying pan then place over a medium to high heat. This method helps to slowly render the fat out of the duck, crisping up the skin beautifully. After 5 minutes, flip the breasts and place in the oven to finish cooking – 10–12 minutes is good for medium, 15 minutes for well done. Once the duck breasts are ready, leave to rest in a warm spot for another 10 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD90F95487582443851764A5D69A70D9', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 5, NULL, NULL, 2, NULL, 0, 'While the duck is cooking, prepare the rice by heating a wide saucepan or wok on the hob over a high heat. Add a generous splash of oil and then 4 tablespoons of the red curry paste. Or a bit more if you like things spicy. Less if you don’t. Stir briskly for a good couple of minutes, to cook the paste out');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34FD45A0611F894F954245A0241854C2', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 6, NULL, NULL, 2, NULL, 0, 'Add the rice and stir to toast for 1 minute. Add the coconut milk and 100ml of water and cover with a lid. Cook until the rice is tender and the liquid has been absorbed, then stir through the white parts of the spring onions. Remove from the heat and stir through the diced mango');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA31CF3A1FDE8E49A94B803B2F0803EC', X'B41ABCAFEE6D874A9E5CBF3EA815F057', X'B41ABCAFEE6D874A9E5CBF3EA815F057', 7, NULL, NULL, 2, NULL, 0, 'To serve, slice the rested duck breasts evenly. Divide the rice between plates, place the duck breasts on top and garnish with the crispy shallots. Chop some of the reserved coriander leaves, sprinkle over and serve');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F54B9944C31F124EB28D77B024E945A0', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 0, NULL, NULL, 2, NULL, 0, 'For the salted caramel sauce, heat the granulated sugar in a sauce pan over a medium heat, stirring constantly with a rubber spatula.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'659912E43B097943A314FCC30B7348F1', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 1, NULL, NULL, 2, NULL, 0, 'The sugar will form clumps and eventually melt into a thick brown, amber coloured liquid as you continue to stir. Be careful not to burn.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5821DBED4A4CF549B1E00B29F441FA05', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 2, NULL, NULL, 2, NULL, 0, 'Once the sugar has completely melted, immediately add the butter. Be careful as the caramel will bubble rapidly when the butter is added.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D13F559FCCA4A34D805B25848EE66484', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 3, NULL, NULL, 2, NULL, 0, 'Stir until the butter has completely melted (approx. 2-3 mins).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A37029387787FA429C555C6BD3F5FD64', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 4, NULL, NULL, 2, NULL, 0, 'Very slowly drizzle in the cream while stirring – the mixture will bubble when the cream is added.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ED246AE16C28844BBC02CA05DB4E2D2', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 5, NULL, NULL, 2, NULL, 0, 'Allow the mixture to boil for one minute.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C03157DC8C478E4AA5575EDF72AEAD1D', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 6, NULL, NULL, 2, NULL, 0, 'Remove from the heat and stir in the salt.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BB1192F9D989241B45A613806D5BB56', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 7, NULL, NULL, 2, NULL, 0, 'Allow to cool before using.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90D4F94611BAB84499E24E8CC0B6D3ED', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 8, NULL, NULL, 2, NULL, 0, 'For the Rice Pudding, place the rice in a large saucepan with 200ml cold water. Bring to the boil, reduce to a low heat, cover and simmer very gently for 12 minutes or until the water has absorbed.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE2271C76D863A4FBE82D96E098AC3E9', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 9, NULL, NULL, 2, NULL, 0, 'Pour the milk into a separate saucepan, scrape the seeds from the vanilla pod and add along with the sugar. Heat to simmering point over a low heat.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C5C86E4FD1A1A4596656113805A4339', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 10, NULL, NULL, 2, NULL, 0, 'Once the rice has absorbed all 200ml water add to the milk mixture, rise to a medium heat for 12-15 minutes, until the rice is tender and the milk has thickened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66C337C3A2293A458434728C1FEB5616', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 11, NULL, NULL, 2, NULL, 0, 'In a small bowl beat together the egg yolk and cream, pour into the rice mixture slowly stirring throughout, reduce to a low heat for a further 6-8 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD9ECF278D1E724D9CED5DEF828BE856', X'358FDDBB760AC642BB1C07ACEC55ABDF', X'358FDDBB760AC642BB1C07ACEC55ABDF', 12, NULL, NULL, 2, NULL, 0, 'Take the pan off the heat, spoon into individual dessert bowls or glasses and finish with the salted caramel sauce.');
