INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4336DD58A18412498324D8360B634181', X'83AE5E730A835E47AF5588250D0E0AE3', X'83AE5E730A835E47AF5588250D0E0AE3', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining oil to a new pan and cook the chicken on a high heat, turning to seal the edges. Pour over the sauce, bring to the boil, cover, reduce to a simmer and cook for 30 minutes until the sauce has thickened and the chicken is cooked through. Add more water if needed during cooking');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'814A755E970C1D4ABC68D5229193006A', X'83AE5E730A835E47AF5588250D0E0AE3', X'83AE5E730A835E47AF5588250D0E0AE3', 4, NULL, NULL, 2, NULL, 0, 'Cook the rice according to pack instruction');
