INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26C9C8AD916DA9408E3687CBEAE44B9A', X'4A2979AFC4DD23459016622DF3FBDD91', X'4A2979AFC4DD23459016622DF3FBDD91', 0, NULL, NULL, 2, NULL, 0, 'Blanch the rice in a large pan of boiling water for 2 minutes then drain.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE95D5718318994497EC122E395732E9', X'4A2979AFC4DD23459016622DF3FBDD91', X'4A2979AFC4DD23459016622DF3FBDD91', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 150 C/Gas mark 3. Grease and line a 25cm round deep cake tin.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D60648E9DF452A4186FBE16D21779E54', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 0, NULL, NULL, 2, NULL, 0, 'Toast the coconut in a pan over a low heat, stirring until it turns golden brown');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0037E84F0C417F4EA7FB689B3DE86DD5', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 1, NULL, NULL, 2, NULL, 0, 'Remove the coconut to a plate and into the same pan add the cinnamon sticks, dried chillies, peppercorns, star anise and fennel seeds. Roast in the pan for 3-4 minutes then add the ground coriander and chilli powder and stir for a minute. Add 100ml of water to form a paste and cook for 2 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2165734C2CE03F4C8AD8B7637BC18CE5', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 2, NULL, NULL, 2, NULL, 0, 'Remove the cinnamon sticks, star anise and whole chillies and keep for later. Add the toasted coconut, turmeric and salt to the pan. Add everything to a spice grinder or pestle and mortar and grind to a smooth paste.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6DE023F923B17498844DBB7807CEAF1', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil in the same pan and slowly cook the onions until they are soft – this will take about 10 minutes. Add the garlic and ginger and cook for a couple of minutes then turn up the heat and add the curry leaves and stir for a minute.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4088C9AE5AA18340B97362E808FCBEEF', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 4, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and the spice paste, return the cinnamon stick, star anise and chillie to the pan and cook for 5 minutes, stirring until you end up with a smooth paste and the oil starts to separate out.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7304C4DA61B0FA498CA0BBBDDE206C11', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 5, NULL, NULL, 2, NULL, 0, 'Remove half the mixture and freeze or place in an airtight container which will keep in the fridge for a week or so.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A864E3C93201994488F04AAB79DCAE81', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 6, NULL, NULL, 2, NULL, 0, 'Add the chicken and cook, covered, for 10 minutes. Add 200ml chicken stock remove the lid and cook for a further 5-10 minutes until the chicken is cooked through.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'917A987608757548AFB5016FF332F2B7', X'D12FFC7DDDC81F4DA8F6D22CDB244345', X'D12FFC7DDDC81F4DA8F6D22CDB244345', 7, NULL, NULL, 2, NULL, 0, 'Remove the star anise, cinnamon and chilli, stir through the lemon juice and green chilli (if using) and scatter with the fresh coriander.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6817CB23F4871428712FF28FA5C42A5', X'883912785D8D5747B4C10E060FE0ACCB', X'883912785D8D5747B4C10E060FE0ACCB', 0, NULL, NULL, 2, NULL, 0, 'Sift the cornflour and plain flour together in a large bowl');
