INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC28CB6EA60D9E43832FAFCED6381FEF', X'883912785D8D5747B4C10E060FE0ACCB', X'883912785D8D5747B4C10E060FE0ACCB', 6, NULL, NULL, 2, NULL, 0, 'Deep fry for 2-3 minutes, until golden and crisp');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9B531F68FCEDD4E934CACB04EE5BBCC', X'883912785D8D5747B4C10E060FE0ACCB', X'883912785D8D5747B4C10E060FE0ACCB', 7, NULL, NULL, 2, NULL, 0, 'Remove with slotted spoon and drain on kitchen paper');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF6414BF981634DBC6C497AEAFF54BA', X'883912785D8D5747B4C10E060FE0ACCB', X'883912785D8D5747B4C10E060FE0ACCB', 8, NULL, NULL, 2, NULL, 0, 'Heat the rice according to pack instruction and serve with the prawns and sauce of your choice');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AD0D08CE7CB2A4EA764C34CFDB638B8', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 0, NULL, NULL, 2, NULL, 0, 'To prepare the rice, add the basmati to a pan of cold water and allow to soak for 5 minutes. Drain carefully, then top up with more cold water to cover. Give the pan a swirl to rinse the grains before carefully draining again. Repeat this process a couple more times to fully wash the rice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E23E6F47E9825645BCFAB43391B019DE', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 1, NULL, NULL, 2, NULL, 0, 'Pre heat the oven to 220C/gas mark 6.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'495286A6F7CA3945BCC1393E3F24914E', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 2, NULL, NULL, 2, NULL, 0, 'In a large ovenproof pan, add the butter and allow to melt over a gentle heat. Add the chopped shallots and sweat down until softened, then stir in the pistachios, cranberries and thyme. Cook for 2 minutes before adding the washed rice and miixing together until evenly coated in butter.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2888F4968D76A04C8BD6D1B5C61F2632', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 3, NULL, NULL, 2, NULL, 0, 'Stir in the stock and increase the heat to high. Allow the stock to come to the boil before adding a little salt to season and covering the pan with a fitted lid. Transfer the pan to the oven and cook for 15 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E947A234510F9459D6BA1F92E41E992', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 4, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from the oven and allow the rice to sit and steam for 5-10 minutes with the lid still on.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F44D2AB6772E9940A8BAD08062AF84FC', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 5, NULL, NULL, 2, NULL, 0, 'While the rice steams, heat a griddle pan with a little oil over a high heat and add the turkey steaks. Fry for 2 minutes, then turn over and fry for a further 2 minutes on the other side.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'369DE8649C1B9E43BB883DAEA34F6856', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 6, NULL, NULL, 2, NULL, 0, 'Add the butter and baste the steaks for a few more minutes, before taking off the heat and leaving to rest for 2 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76B9756FC6197A469966B5DCDA4252E0', X'B8C95BF3B422C744A69786F4A9E1778B', X'B8C95BF3B422C744A69786F4A9E1778B', 7, NULL, NULL, 2, NULL, 0, 'Fluff up the pilaf with a fork before serving, dividing the rice between plates and topping with the sliced turkey and a garnish of parsley cress.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85BA9B478AFD2B4CA4A3E1D00011A912', X'822A13DC7CF02841BECFDE74289C2005', X'822A13DC7CF02841BECFDE74289C2005', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 180°C.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'717DFA94323A6C448B532929BBDA1C66', X'822A13DC7CF02841BECFDE74289C2005', X'822A13DC7CF02841BECFDE74289C2005', 1, NULL, NULL, 2, NULL, 0, 'Seal shanks with onions, thyme, bay, and cinnamon on a medium to high heat. Add 3 cloves of garlic (whole) and cook for 10 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9AE068035C5CB4CB347D21149204367', X'822A13DC7CF02841BECFDE74289C2005', X'822A13DC7CF02841BECFDE74289C2005', 2, NULL, NULL, 2, NULL, 0, 'Add wine and reduce for 2 minutes then add 1/2 the stock, cover and place in the oven to cook for 1 hour.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FE6E3976348914BA87F109451222094', X'822A13DC7CF02841BECFDE74289C2005', X'822A13DC7CF02841BECFDE74289C2005', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile soak rice for 1 hour and add to dish with remaining stock and cook for 40 minutes covered');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6B51587BDF78E4EB6776F69F7443AF7', X'822A13DC7CF02841BECFDE74289C2005', X'822A13DC7CF02841BECFDE74289C2005', 4, NULL, NULL, 2, NULL, 0, 'Heat the redcurrant jelly and mustard with a couple of tablespoons of water in a saucepan.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'790EF90EBD0D22429ECD592940A7319B', X'822A13DC7CF02841BECFDE74289C2005', X'822A13DC7CF02841BECFDE74289C2005', 5, NULL, NULL, 2, NULL, 0, 'Scatter with fresh parsley, mint and season as required. Pour over the redcurrant and mustard mixture and serve!');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58DE1A016045294D8315C655A8252C62', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200°C/Gas mark 6.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED7374E2F8A35A48BA43722B2D9C8F1C', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a flameproof casserole dish, add the onion and cook for 5 minutes over a medium heat until softened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC65BBE2C0E49043AAF022E8BD19C741', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, turmeric, ras el hanout and coriander stalks and cook, stirring, for a couple of minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14212D605140F54EB1A08E3C5EDF52F8', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 3, NULL, NULL, 2, NULL, 0, 'Add the turkey, butternut squash, chickpeas and halved apricots to the casserole, then pour over the tomatoes and stock.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FB3AA23C56AD747AE447F97E8B7B681', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 4, NULL, NULL, 2, NULL, 0, 'Season well with salt and pepper and bring to the boil. Put the lid on and transfer to the oven.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FC194AC13B39A4FA4F41797ABDBB7C8', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 5, NULL, NULL, 2, NULL, 0, 'After 30 minutes, turn down the over to 150°C/Gas mark 4, stir the tagine and return to the oven, uncovered, for a further 30 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC5C031191514045BF065C9E2B1C306C', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile empty the almonds onto a baking tray and bake for 6-8 minutes until golden, then finely slice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7725F6CDA9541643864D7ECE15BBC61F', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 7, NULL, NULL, 2, NULL, 0, 'Taste and adjust the seasoning.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB3085E215CAC94C8CD5EFB85B3959BB', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 8, NULL, NULL, 2, NULL, 0, 'Stir in the almonds and pomegranate seeds then sprinkle over the lemon zest and coriander leaves.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECCC5F76E15194429102A7160AD8A6E7', X'21875A6FD5C2734B9A8B9C07ACB00AD0', X'21875A6FD5C2734B9A8B9C07ACB00AD0', 9, NULL, NULL, 2, NULL, 0, 'Serve with Tilda Wholegrain Basmati Rice and yoghurt.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0D42E8F77830C4499E15D1B60FA6C50', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 0, NULL, NULL, 2, NULL, 0, 'For the rice stage, rinse the rice three times in cold water and drain well');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D33B2D4258085841A8AA27A895D5AA01', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 1, NULL, NULL, 2, NULL, 0, 'Place the ﬁsh stock in a large saucepan, bring to the boil and add the bay leaves, turmeric and mixed herbs');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C15CF13820E9FA49BEAD7D4436767FEE', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 2, NULL, NULL, 2, NULL, 0, 'Add the rice, cover, and cook over a very low heat for 16 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'423683241B0A9B4581EC0A8BFAFD8E00', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 3, NULL, NULL, 2, NULL, 0, 'Add the frozen peas and cook for a further 4 minutes, until the stock is absorbed and the rice is cooked. Remove the bay leaves.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEE18595B73E924D9E723869759B1302', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, for the chicken and prawn stage, heat the oil in a paella pan or large non-stick frying pan over a medium heat.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B90AE069C0E6748B7E04E819F946BC5', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 5, NULL, NULL, 2, NULL, 0, 'Add the onion, garlic and chilli ﬂakes and stir-fry for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2C1FEE7F4E6044980C451AFE2349935', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 6, NULL, NULL, 2, NULL, 0, 'Add the red pepper and stir-fry for 4-5 minutes, then add the chicken and stir-fry for a further 6-7 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68F22903A533CB47B8FC452E02B3CAF4', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 7, NULL, NULL, 2, NULL, 0, 'Stir in the prawns (and mussels and scallops if using and heat through gently for 4-5 minutes. Ensure the scallops are thoroughly cooked.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'008E5044FC6D82459879048CA0E034EA', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 8, NULL, NULL, 2, NULL, 0, 'Add the seafood mixture to the rice and stir gently to combine.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02D044F104631547A66BCB2D01780AE8', X'30042AC930F3624794ACE2B71770C89F', X'30042AC930F3624794ACE2B71770C89F', 9, NULL, NULL, 2, NULL, 0, 'Add the lemon juice and serve immediately.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C179AD4F02ECB48A1DD5B3B5D66E565', X'607E11C81DBFB64D8DA6AB614CBF3972', X'607E11C81DBFB64D8DA6AB614CBF3972', 0, NULL, NULL, 2, NULL, 0, 'Place the rice in a large saucepan with 200ml cold water. Bring to the boil, reduce heat to low, then cover and simmer very gently for 12 minutes or until the water is absorbed.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6E1DAB1AC4AF24EAA7A8F2DA869248A', X'607E11C81DBFB64D8DA6AB614CBF3972', X'607E11C81DBFB64D8DA6AB614CBF3972', 1, NULL, NULL, 2, NULL, 0, 'Pour the milk into a separate saucepan and add the cardamom pod. Scrape the vanilla seeds into the milk with a sharp knife. Heat to simmering point over a low heat and pour over the rice. Add the sugar and cook over a medium heat for 12-15 minutes, until the rice is tender and the milk has thickened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C1A4DB4EBF56145A5347235DE17F9D8', X'607E11C81DBFB64D8DA6AB614CBF3972', X'607E11C81DBFB64D8DA6AB614CBF3972', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, beat together the egg yolk, cream and lemon zest. Stir in a large spoonful of the rice mixture, mix well, then add to the remaining rice mixture. Cook over a low heat for 6-8 minutes, stirring all the time, until the pudding is thick and creamy.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2368E9CB37EF7E43B7D8DE79554B586E', X'607E11C81DBFB64D8DA6AB614CBF3972', X'607E11C81DBFB64D8DA6AB614CBF3972', 3, NULL, NULL, 2, NULL, 0, 'Take the pan off the heat, remove the cardamom pod and stir in the butter. Spoon into individual dessert bowls or glasses, garnish with the extra lemon zest and serve warm with chocolate biscuits if desired.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC08264304F81A408791929C8621181E', X'A09D0BD70375E4438DCDEE45E2AAC010', X'A09D0BD70375E4438DCDEE45E2AAC010', 0, NULL, NULL, 2, NULL, 0, 'Boil the rice in a large saucepan with the coconut milk and stock for 15-20 minutes, uncovered, stirring occasionally.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C9A1B465331034887BDF71DB171E05F', X'A09D0BD70375E4438DCDEE45E2AAC010', X'A09D0BD70375E4438DCDEE45E2AAC010', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, in another pan cook the cauliflower in boiling water for 5 minutes, drain.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'695F99C0B92AF744A76B54E023605C92', X'A09D0BD70375E4438DCDEE45E2AAC010', X'A09D0BD70375E4438DCDEE45E2AAC010', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a frying pan and fry 1 tsp mustard seeds, until they pop, add the onion and chilli flakes and continue to fry for 3-4 minutes. Add the curry powder, cauliflower, peas and 50ml water and fry for 2-3 minutes. Season to taste.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C408B2CBFFC1C48B08DC556392F0E12', X'A09D0BD70375E4438DCDEE45E2AAC010', X'A09D0BD70375E4438DCDEE45E2AAC010', 3, NULL, NULL, 2, NULL, 0, 'Stir the almonds, sultanas, coriander and remaining mustard seeds into the rice, and serve with the spicy cauliflower');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'153E4F75D3D91C4885BF61AC889AA8ED', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 0, NULL, NULL, 2, NULL, 0, 'Add the rice to a heavy-bottomed pan with the milk, vanilla, zest and sugar. Grate in a little fresh nutmeg and add a pinch of cinnamon (adding more or less to taste) and stir to combine. Place over a gentle heat and allow to simmer gently for 15 minutes, or until the rice is tender');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7F335700246E74BAFA6523E07537E8F', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 1, NULL, NULL, 2, NULL, 0, 'Taking the pan off the heat, add in the white chocolate and stir until melted through the rice. Squeeze out the excess water from the gelatin leaves and mix into the pan so that they dissolve into the rice');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99D032AB72AB594B8F75C2483903809F', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 2, NULL, NULL, 2, NULL, 0, 'Leave the rice to cool until it reaches 30°C, then gently fold through the whipped cream. Distribute the mixture between ten 6cm round moulds or ramekins and chill for a minimum of 4 hours');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58E32625FE795C42BF9CC47737F19FBA', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 3, NULL, NULL, 2, NULL, 0, 'While the parfaits chill, make the stock syrup which will be used for poaching the fruits. Add the water and sugar to a large pan, stir together then bring to the boil. Once the sugar has fully dissolved, remove from the heat and allow to cool');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B297319E7030E4C8C267DE0E74448E5', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 4, NULL, NULL, 2, NULL, 0, 'Poaching the fruit separately will help maintain their unique flavours. Add the prunes to one pan with 250ml of the stock syrup (or to just cover the prunes) and the tea bag, then simmer until soft. Chill in the fridge until needed');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEBCBA33F452D14EA65577B9DFC24ACE', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 5, NULL, NULL, 2, NULL, 0, 'In another pan, add the apricots, another 250ml of stock syrup and the split vanilla pod. Simmer until soft, then reserve in the fridge');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BDA3BA4845EA04EAD21D7E7EEE42BB9', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 6, NULL, NULL, 2, NULL, 0, 'For the figs, add them to a pan with 250ml of the stock syrup and the fresh thyme, simmering until tender before chilling until needed');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A2042B14A9D5F47A2AD49D3343BCA2C', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 7, NULL, NULL, 2, NULL, 0, 'Add the pears to a pan with any remaining stock syrup, the sweet wine, cloves and cinnamon stick. Simmer until softened, then allow to cool in the poaching liquor. Dice into 5mm cubes and chill until needed');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB38157244D3594C83A87B2697481DE6', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', X'73FA9AC2B1CB5F40BF3FE98ED538FF42', 8, NULL, NULL, 2, NULL, 0, 'To serve, carefully turn the parfaits out from the moulds on to serving plates. Dust liberally with icing sugar and use a blowtorch to caramelise this slightly. Garnish with sprigs of fresh mint and serve alongside the poached fruits');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E74391CADA26046822BAABAC9F83B7A', X'88A19DE7EC08E842B650FBE04506B9C7', X'88A19DE7EC08E842B650FBE04506B9C7', 0, NULL, NULL, 2, NULL, 0, 'Rinse the rice three times in cold water and drain well.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFD8534C67EB084AAA309DBDF0DE2FA4', X'88A19DE7EC08E842B650FBE04506B9C7', X'88A19DE7EC08E842B650FBE04506B9C7', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken stock in a large saucepan, bring to the boil and add the remaining rice ingredients.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24D739ADDEB7E54DA24A3E08A2A8B61E', X'88A19DE7EC08E842B650FBE04506B9C7', X'88A19DE7EC08E842B650FBE04506B9C7', 2, NULL, NULL, 2, NULL, 0, 'Add the rice, cover, and cook over a very low heat for 20 minutes, stirring occasionally until the stock is absorbed and the rice is cooked. Remove the bay leaves.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E25B394519E46F42838DA2FDE2E3CB94', X'88A19DE7EC08E842B650FBE04506B9C7', X'88A19DE7EC08E842B650FBE04506B9C7', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large non-stick frying pan over a high heat. Add the turkey and Cajun spice mix and stir-fry for 5-6 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C61217DB74883A48A6B60B40927D4DD6', X'88A19DE7EC08E842B650FBE04506B9C7', X'88A19DE7EC08E842B650FBE04506B9C7', 4, NULL, NULL, 2, NULL, 0, 'Add the onion and stir-fry for a further 3-4 minutes, then turn down the heat to low and add the peppers, celery, spring onions and garlic and stir-fry gently for 4-5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F28F9CEA20A2CE41BC8503AADC065C1A', X'88A19DE7EC08E842B650FBE04506B9C7', X'88A19DE7EC08E842B650FBE04506B9C7', 5, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and parsley to the meat and vegetables in the pan. Stir to combine, then mix gently into the cooked rice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'100F15132DBA1B4CA7AF632640B6F3C4', X'88A19DE7EC08E842B650FBE04506B9C7', X'88A19DE7EC08E842B650FBE04506B9C7', 6, NULL, NULL, 2, NULL, 0, 'Season to taste and serve immediately with a crisp, green salad.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92CAB771B4361F44AF2E327D55F9A606', X'AA117D32EC60184EBCBCFA2E9EA98AA7', X'AA117D32EC60184EBCBCFA2E9EA98AA7', 0, NULL, NULL, 2, NULL, 0, 'Rinse the rice is cold water until the water runs clears – set aside in a sieve to allow as much water as possible to drain off.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F1250CAF869B94A8D8D44D1DD6D2532', X'DF500C16DA8CA94CA01FBD8355094A35', X'DF500C16DA8CA94CA01FBD8355094A35', 0, NULL, NULL, 2, NULL, 0, 'Cook the rice for 10mins, drain and cool immediately');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5B04D3C2CCAAF49B5BC6E4ED2B39FF7', X'DF500C16DA8CA94CA01FBD8355094A35', X'DF500C16DA8CA94CA01FBD8355094A35', 1, NULL, NULL, 2, NULL, 0, 'Scramble the eggs with the stir fry oil in a pan, season well and keep to one side');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'912A8720D1D03441A8B754C90570D107', X'CEB13F880E027841B3F9519F7AD9A9B6', X'CEB13F880E027841B3F9519F7AD9A9B6', 0, NULL, NULL, 2, NULL, 0, 'Score the skin of the duck breast');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC372F0922A2940B4EA4A129A545D95', X'CEB13F880E027841B3F9519F7AD9A9B6', X'CEB13F880E027841B3F9519F7AD9A9B6', 1, NULL, NULL, 2, NULL, 0, 'Combine the remaining ingredients except for the spring onions to make a marinade');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1C55B21D861AD438DFBF6E9FC69182C', X'CEB13F880E027841B3F9519F7AD9A9B6', X'CEB13F880E027841B3F9519F7AD9A9B6', 2, NULL, NULL, 2, NULL, 0, 'Combine with the duck breasts and leave to marinade in the fridge for 2-3 hours or overnight if time allows');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFC3BB18480538499B0ADCF4C7078D10', X'3AE29C95B6B4BF439C83DD55CD2A2217', X'3AE29C95B6B4BF439C83DD55CD2A2217', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 160°c, Gas mark 3');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B639EBE2225EC542A322E91174C31CD0', X'3AE29C95B6B4BF439C83DD55CD2A2217', X'3AE29C95B6B4BF439C83DD55CD2A2217', 1, NULL, NULL, 2, NULL, 0, 'Roughly chop or crush the nuts with the back of a knife');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCA3BD58A94BC04DBC607C36213A8323', X'3AE29C95B6B4BF439C83DD55CD2A2217', X'3AE29C95B6B4BF439C83DD55CD2A2217', 2, NULL, NULL, 2, NULL, 0, 'Bring the water to the boil and add 180g of the sugar. Continue to boil until the syrup reaches a nutty brown colour');
