INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79245532B864CD4D9497714FD216CA7F', X'3AE29C95B6B4BF439C83DD55CD2A2217', X'3AE29C95B6B4BF439C83DD55CD2A2217', 4, NULL, NULL, 2, NULL, 0, 'Bring the milk, vanilla essence, rice and the remaining sugar to the boil. Turn down to a simmer and cook until all the milk has been absorbed. Leave to cool slightly');
