INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD29E9B54CCCB347B7345B2E171C80F0', X'3AE29C95B6B4BF439C83DD55CD2A2217', X'3AE29C95B6B4BF439C83DD55CD2A2217', 7, NULL, NULL, 2, NULL, 0, 'Leave brulees to cool right down and then sprinkle with crushed praline or decorate with shards of broken praline');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C01F60C0B8353046BC09C6CAAB0CDA3E', X'A7D7A7B6E580DD44B2439258EF31560C', X'A7D7A7B6E580DD44B2439258EF31560C', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large pan. Add the garlic and shallots and fry gently until soft for 3 -5 minutes. Add the mushrooms and paprika and cook for a further 2-3 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E3AD6C3A599914DA48638A99EB26E8A', X'A7D7A7B6E580DD44B2439258EF31560C', X'A7D7A7B6E580DD44B2439258EF31560C', 1, NULL, NULL, 2, NULL, 0, 'Add the stock or wine and heat for a minutes. Add the spinach, mushroom ketchup and crème fraiche. Simmer for a few minutes to reduce');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32DF82C464C64C4DA21F171A64677565', X'A7D7A7B6E580DD44B2439258EF31560C', X'A7D7A7B6E580DD44B2439258EF31560C', 2, NULL, NULL, 2, NULL, 0, 'Heat the Tilda Wholegrain & Wild Steamed Basmati in the microwave');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1029D899D3A0FC4199FDEE54231211D6', X'A7D7A7B6E580DD44B2439258EF31560C', X'A7D7A7B6E580DD44B2439258EF31560C', 3, NULL, NULL, 2, NULL, 0, 'Add the parsley to the Mushroom Stroganoff. Season with salt and pepper and serve on a bed of the rice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEDDB27772B0D342BC0E6891B17398F4', X'2A7E78D1C71FC14F95D73F3EC2351364', X'2A7E78D1C71FC14F95D73F3EC2351364', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 180°c, Gas Mark 6');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D61132B9D00D014385AD8BB41F72FF95', X'2A7E78D1C71FC14F95D73F3EC2351364', X'2A7E78D1C71FC14F95D73F3EC2351364', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken, breast side down, and starting at the thigh end make a cut right through the chicken. Turn the chicken over and press down firmly to open the chicken up. Place in an ovenproof dish');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6F904FE53CC034295BA7C8986EA036B', X'2A7E78D1C71FC14F95D73F3EC2351364', X'2A7E78D1C71FC14F95D73F3EC2351364', 2, NULL, NULL, 2, NULL, 0, 'Mix together all the marinade ingredients and spread over the chicken');
