INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'616CC1E1EB56DC4098334379553DC798', X'2A7E78D1C71FC14F95D73F3EC2351364', X'2A7E78D1C71FC14F95D73F3EC2351364', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile make the Asparagus and Coconut Rice. Blanch the Asparagus Tips in boiling water for about 3 mins and cool immediately in cold water.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F65C95D958718A42BA478C3C4ED1A2BF', X'2A7E78D1C71FC14F95D73F3EC2351364', X'2A7E78D1C71FC14F95D73F3EC2351364', 5, NULL, NULL, 2, NULL, 0, 'In a large frying pan or wok heat the coconut oil. Add the spinach and heat through until wilted');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55AC46F5C2FEB840B16A79201E8E293C', X'2A7E78D1C71FC14F95D73F3EC2351364', X'2A7E78D1C71FC14F95D73F3EC2351364', 6, NULL, NULL, 2, NULL, 0, 'Heat the Tilda Coconut Steamed Basmati Rice in the microwave and stir into the pan');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'001F756916586D468150A9965F8D452C', X'2A7E78D1C71FC14F95D73F3EC2351364', X'2A7E78D1C71FC14F95D73F3EC2351364', 7, NULL, NULL, 2, NULL, 0, 'Add the asparagus and a squeeze of lemon juice Serve with slices of the Lemon & Chili Chicken');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED94E70B3346EB44A68A4CCD638216F8', X'5D9CECF29EDDC840A1A7B5630A5341CF', X'5D9CECF29EDDC840A1A7B5630A5341CF', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 180°c, gas mark 6.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0488609C57C8B6418367D2F32D41557D', X'5D9CECF29EDDC840A1A7B5630A5341CF', X'5D9CECF29EDDC840A1A7B5630A5341CF', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large pan. Add the garlic, shallots and fennel and fry gently until soft for about 3-5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09EF78B15C3A7749A2A15209D952F7DF', X'5D9CECF29EDDC840A1A7B5630A5341CF', X'5D9CECF29EDDC840A1A7B5630A5341CF', 2, NULL, NULL, 2, NULL, 0, 'Add the stock or wine, passatta and red pepper. Fry for about 5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDEA715ABEB4D6449A35E14CA0102E7B', X'5D9CECF29EDDC840A1A7B5630A5341CF', X'5D9CECF29EDDC840A1A7B5630A5341CF', 3, NULL, NULL, 2, NULL, 0, 'Add the fish and seafood mix and transfer to an ovenproof dish or tagine. Lay the clams on top. Season to taste.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8102DFD16167C54FB38244DC0DBD586D', X'5D9CECF29EDDC840A1A7B5630A5341CF', X'5D9CECF29EDDC840A1A7B5630A5341CF', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook for about 20 minutes or until the clam shells have opened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'778FEDFFB1582D498EFF34CC554D3F04', X'5D9CECF29EDDC840A1A7B5630A5341CF', X'5D9CECF29EDDC840A1A7B5630A5341CF', 5, NULL, NULL, 2, NULL, 0, 'Serve with heated Tilda Lemon & Herb rice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0D3F8F475FD98429244B35AA7F5FBF9', X'5B5BE554EFE1914890404460AC192F1A', X'5B5BE554EFE1914890404460AC192F1A', 0, NULL, NULL, 2, NULL, 0, 'In a pot boiling with boiling water add the Brown Basmati & Wild Rice and simmer for 25 minutes and then drain. Whilst this is cooking');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'665F36A46951F349A60D8ACCF9462FBD', X'5B5BE554EFE1914890404460AC192F1A', X'5B5BE554EFE1914890404460AC192F1A', 1, NULL, NULL, 2, NULL, 0, 'Roast 100g of the beetroot with honey in a hot oven for 20 minutes. Leave to cool and then chop roughly');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'238F318E24945D41B0C66122C3F646EA', X'48CE6FB2E64D314CA0B452D12162B78F', X'48CE6FB2E64D314CA0B452D12162B78F', 0, NULL, NULL, 2, NULL, 0, 'Make the minted pea puree by blitzing together 100g of the peas and the mint until smooth');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF99404BFCA9F49A6AEFC789B85D307', X'6E4E66BE0808D04EBCA1AD4EEC1F2FDC', X'6E4E66BE0808D04EBCA1AD4EEC1F2FDC', 0, NULL, NULL, 2, NULL, 0, 'Soak the dried mushrooms in 150g hot water for 10 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D54D20CB55BDB9449F7D9740510EB0A5', X'6E4E66BE0808D04EBCA1AD4EEC1F2FDC', X'6E4E66BE0808D04EBCA1AD4EEC1F2FDC', 1, NULL, NULL, 2, NULL, 0, 'Drain and squeeze the water from the mushrooms. Reserve the liquor');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7960D7389F92FF47BB851B3DE980DFFA', X'FA688FF667A44A42A2F82149F475D3F4', X'FA688FF667A44A42A2F82149F475D3F4', 0, NULL, NULL, 2, NULL, 0, 'To toast almonds, add dry to a pan and gently heat, keeping the flakes moving to prevent burning.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'857D0157316C2A47AC62630AB80223E2', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 0, NULL, NULL, 2, NULL, 0, 'If using raw chickpeas, please soak them in adequate water in a deep bowl or pan over night or a minimum of six hours at least. The longer they are soaked the more they will swell and cook faster. However prolonged soaking will ferment and thereby spoil the chickpeas. Boil them in the same water adding more if required with a little salt until tender. Set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76C1C0B698E9F4459B17F0ECC97DB124', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a deep pot or pan, which has a tight fitting lid.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4E1D3F4BDDA3344BAA2A0CE2616C326', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 2, NULL, NULL, 2, NULL, 0, 'When the oil forms a haze add the cinnamon bark, cloves & red chilli. As soon as you see the cloves swell add the cumin and sauté for a minute or so.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBCE65CAEF4ED24FA98A50961ED44B85', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic & a half-minute later the onions and sauté until the garlic is almost on the point of browning.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E20DDEB51503641975DFCA535F826C1', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 4, NULL, NULL, 2, NULL, 0, 'Now lower the heat & add the rice, the salt and stir for a minute or two, turning well and levelling out each time so that the rice gets an all round heat.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28ACFD39297A4F488DBBC464A586D13C', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 5, NULL, NULL, 2, NULL, 0, 'Add how water or stock up to maybe 3/4th of an inch or 2cms above the height of the rice. This is if you feel you are comfortable not using one and half times.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBC5B8280545504280F876714998DE85', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 6, NULL, NULL, 2, NULL, 0, 'This is simpler and works very well.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6BB0EDB2C22514F8F71B472B36A1FB1', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 7, NULL, NULL, 2, NULL, 0, 'Stir for a few seconds and allow to rest covered.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD7D211F48F89D4EB93CB0711608499E', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 8, NULL, NULL, 2, NULL, 0, 'Put the heat to a minimum.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DB50B3484DA1442BBB11FD5E1238737', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 9, NULL, NULL, 2, NULL, 0, 'Stir again every minute or so until most of the water is absorbed and you can detect water along the sides but not in plentiful.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC1B8FBC38D2C408E1062843288D2EC', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 10, NULL, NULL, 2, NULL, 0, 'Add either canned chickpeas drained or boiled chickpeas also drained. You may like to use the water along with the water when first adding to the rice. This will add a lot more flavour but it only discolours the pulao.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DCB0639ACF53844A4216FFF0E4BBDAB', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 11, NULL, NULL, 2, NULL, 0, 'After each stir cover the pot but ensure that all the sides are clean of grains when you are about to cover.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCA8410C9493BF4CA19491B1AF4847E6', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 12, NULL, NULL, 2, NULL, 0, 'Check every one or two minutes, give the rice a gentle stir from the bottom up but very gently.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25590726D50E2246AD5EC64014F96FE9', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 13, NULL, NULL, 2, NULL, 0, 'Check your seasoning and whether or not the rice is cooked. Approximately 30-40 minutes on a slow fire is often adequate.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAB442CE4369B2439399A898F63A2A86', X'323A22604FE429499493DED56EC2D2CB', X'323A22604FE429499493DED56EC2D2CB', 14, NULL, NULL, 2, NULL, 0, 'When serving, top with browned onions and sprinkle some freshly chopped coriander.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6400680B41BA241A3847043EDAEE352', X'C8B4AD467A60854D8151C94202A0645D', X'C8B4AD467A60854D8151C94202A0645D', 0, NULL, NULL, 2, NULL, 0, 'Rinse and soak the rice and beans together in some water for about 30 minutes. Drain well.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E71301BFFF84F40871CD86634372BA4', X'C8B4AD467A60854D8151C94202A0645D', X'C8B4AD467A60854D8151C94202A0645D', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 140°C/275°F/Gas Mark 1.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90F34996F51F814EAACED4D1F9748F6D', X'C8B4AD467A60854D8151C94202A0645D', X'C8B4AD467A60854D8151C94202A0645D', 2, NULL, NULL, 2, NULL, 0, 'Over a medium heat, warm the oil or ghee in a casserole dish that has a lid and that can be placed in the oven. Once the oil is hot, add the cumin seeds and in 30 seconds, once they change colour slightly, add the ginger, garlic and green chilli and sauté until the garlic changes colour, about 2 minutes. Add the onion and sauté well until the onion turns pale, about 3–4 minutes, then add the chopped tomato and the turmeric. Sauté until the tomato turns to pulp and almost dries out, about 8–10 minutes, then add the water and salt.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CBEC94980444F4080448115BCF9EE90', X'C8B4AD467A60854D8151C94202A0645D', X'C8B4AD467A60854D8151C94202A0645D', 3, NULL, NULL, 2, NULL, 0, 'When the water comes to a boil, add the rice and daal mix and stir regularly until the water starts to be absorbed. When this happens, clean down the sides of the dish with a flat spatula, add some seasoning, cover the dish and place in the oven.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16C4D5F199B52E438BE894BDCE7568A0', X'C8B4AD467A60854D8151C94202A0645D', X'C8B4AD467A60854D8151C94202A0645D', 4, NULL, NULL, 2, NULL, 0, 'Let cook for 10–12 minutes, then turn off the oven and leave the dish inside for another 30 minutes or so. Remove, stir gently and check the seasoning. Sometimes, depending on the rice, the khichdi can become dry and lumpy. If this has happened, add some water and stir to get a porridge-like consistency.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8F9AB40E58C914994BC8771994E9807', X'C8B4AD467A60854D8151C94202A0645D', X'C8B4AD467A60854D8151C94202A0645D', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle with some coriander, if you like, and serve.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'465B2080D194FE4E8F040B9C8677C7E6', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 0, NULL, NULL, 2, NULL, 0, 'Cook the rice according to the packet instructions. Once cooked, drain the rice and set aside to steam-dry');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D4B67AB58AD0A4B978047B4E99FACB9', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 1, NULL, NULL, 2, NULL, 0, 'While the rice cooks, heat 1 teaspoon of sesame oil in a medium to large saucepan over a moderate heat. Once hot, add the diced onion and finely chopped celery and sweat for 8 minutes until they have softened, become translucent and started to slightly caramelise');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB529156183E3743BCB930A1256D2A3B', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, ginger and chilli and sauté for 2 minutes. Add the sliced carrot and continue to stir-fry for 5 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF59E1E642D56745823505B6151D0912', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, drain the silken tofu in a sieve or a colander');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E71C487D683EB442B2889289BE204ED7', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 4, NULL, NULL, 2, NULL, 0, 'After 5 minutes, add the peas and baby corn and stir well. Stir-fry for 4 minutes to heat through');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E6EA85433574D47BD905EF2D62D8353', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked and drained rice, stir well to combine and then make a well in the centre of the saucepan. Turn the heat up high and add your tofu to the well – it should sizzle immediately. Sprinkle the turmeric and the kala namak over the tofu');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B623461079D1E34B82DBEBD778C1A499', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 6, NULL, NULL, 2, NULL, 0, 'Hold your nerve here with the heat; after two minutes start to stir and break up the tofu and incorporate this into the rice and veggies. Continue to stir-fry the fried rice for 6 minutes, or longer if you prefer it drier and crispier');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4280D360DCEF2B4C9CD6CEAD608867AD', X'756A15140946E7489248D8C02E4370EC', X'756A15140946E7489248D8C02E4370EC', 7, NULL, NULL, 2, NULL, 0, 'Serve in warmed bowls, sprinkled with sliced spring onions, fresh lime wedges or a squeeze of lime juice, toasted sesame seeds and 2 tablespoons of kimchi per bowl');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D674A0ABD1CF540A0F7D1D685EC2D0B', X'06052A8C08B3B349B77EA276644F37A9', X'06052A8C08B3B349B77EA276644F37A9', 0, NULL, NULL, 2, NULL, 0, 'Dice the mixed peppers and slice the onions. Cut the feta into small cubes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'804020076D6F93448EE53E5F3C539878', X'06052A8C08B3B349B77EA276644F37A9', X'06052A8C08B3B349B77EA276644F37A9', 1, NULL, NULL, 2, NULL, 0, 'Heat Tilda Pure Basmati rice according to pouch instructions and place into a serving bowl.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA2E9B9171A7B04E9E4D56227FABE5AE', X'06052A8C08B3B349B77EA276644F37A9', X'06052A8C08B3B349B77EA276644F37A9', 2, NULL, NULL, 2, NULL, 0, 'Mix in the olives, peppers, onions and feta, as well as the spice mix, herbs and chickpeas.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'984AB5F7EC758F45B3673889DD135CC1', X'06052A8C08B3B349B77EA276644F37A9', X'06052A8C08B3B349B77EA276644F37A9', 3, NULL, NULL, 2, NULL, 0, 'Squeeze over the lemon before serving.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0CE0BF90BFEE34D9773EC0199005FD2', X'5C3047075D771246B9F67D8D990A7D73', X'5C3047075D771246B9F67D8D990A7D73', 0, NULL, NULL, 2, NULL, 0, 'Place the spices, crushed chillies and sea salt in a small bowl and combine.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41EF9B4DCE1E9949BAEF87CAFF9464FF', X'5C3047075D771246B9F67D8D990A7D73', X'5C3047075D771246B9F67D8D990A7D73', 1, NULL, NULL, 2, NULL, 0, 'Put the chicken breasts in a large freezer bag along with the spice mix, garlic, lime juice and 1 tsp of olive oil. Seal the bag then shake and rub to combine the meat and spices (you can do this anytime you like before and keep in the fridge).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C971DCEC1DAF184CB8D64933300FB972', X'5C3047075D771246B9F67D8D990A7D73', X'5C3047075D771246B9F67D8D990A7D73', 2, NULL, NULL, 2, NULL, 0, 'To make the salsa, place the mango, tomatoes, coriander, lime juice, chilli and sea salt in a medium-sized bowl and combine then place in the fridge to keep cool.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6A443D0DC1DE74A951B11C77649061A', X'5C3047075D771246B9F67D8D990A7D73', X'5C3047075D771246B9F67D8D990A7D73', 3, NULL, NULL, 2, NULL, 0, 'Place the freezer bag on a firm surface and bash the chicken breasts with a rolling pin or back of a serving spoon and flatten until roughly 1cm thick.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03146831DB41F040BDADCA4C336372B1', X'5C3047075D771246B9F67D8D990A7D73', X'5C3047075D771246B9F67D8D990A7D73', 4, NULL, NULL, 2, NULL, 0, 'Set a griddle or large non-stick frying pan over a medium heat and heat the remaining 2 tsp of oil. Once hot, place the chicken breasts in the pan and cook for about 5 minutes either side until cooked through. Once cooked, remove the chicken from the pan and set aside on a plate.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30191AEAFE9D4A45AEA9ADDC143A4EBF', X'5C3047075D771246B9F67D8D990A7D73', X'5C3047075D771246B9F67D8D990A7D73', 5, NULL, NULL, 2, NULL, 0, 'Open the Tilda Rice packet and place in the microwave and cook for 3 minutes.');
