INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E1557EBAA09774EB581BE3A474A1B32', X'827ABA9BD5C4CA42B11E016C88DF8379', X'827ABA9BD5C4CA42B11E016C88DF8379', 8, NULL, NULL, 2, NULL, 0, 'Serve with warmed pittas, the Yoghurt Dip and the Tomato and Onion Salad');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7568BCA569008F4BB06A21FB5A29B2F5', X'DD2F9350DD55F543B46032EEDC4F6068', X'DD2F9350DD55F543B46032EEDC4F6068', 0, NULL, NULL, 2, NULL, 0, 'Heat the rice according to the pack instructions. Mix the mango, avocado, pistachios, pomegranate seeds and salad onions together and stir into the rice mix.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3389922D062574D9CE08AFA7CC38209', X'DD2F9350DD55F543B46032EEDC4F6068', X'DD2F9350DD55F543B46032EEDC4F6068', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the oil and vinegar and toss the salad. Serve in a large bowl, with raita on the side if you wish.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DFEAF565953EF40B35D9E9E915ABD1E', X'DD2F9350DD55F543B46032EEDC4F6068', X'DD2F9350DD55F543B46032EEDC4F6068', 2, NULL, NULL, 2, NULL, 0, 'TIP For a quick raita, stir together Greek yoghurt, grated cucumber and torn mint.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D7D3BFA0CA2E4E95E11693FB4B2066', X'34AC2F0A8213DE498CC5F87214E47717', X'34AC2F0A8213DE498CC5F87214E47717', 0, NULL, NULL, 2, NULL, 0, 'Combine the cooked brown rice, milk and cinnamon in a pan. Bring to the boil, then reduce heat to low and simmer for 20 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47B3A4104D3538498202312BA4FE174D', X'34AC2F0A8213DE498CC5F87214E47717', X'34AC2F0A8213DE498CC5F87214E47717', 1, NULL, NULL, 2, NULL, 0, 'Beat the egg in a small bowl');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F3960BBA4917A41A1540A775BD7DCBC', X'34AC2F0A8213DE498CC5F87214E47717', X'34AC2F0A8213DE498CC5F87214E47717', 2, NULL, NULL, 2, NULL, 0, 'Temper the egg by whisking in some of the hot rice, a tablespoon at a time until you have incorporated about six tablespoons');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57934EC7752F8247A2130294BD7F39A1', X'34AC2F0A8213DE498CC5F87214E47717', X'34AC2F0A8213DE498CC5F87214E47717', 3, NULL, NULL, 2, NULL, 0, 'Stir the egg mixture into the rice along with the vanilla and seeds and continue cooking over low heat for one to two minutes to thicken');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEC059E7D69982499F7A3CD470428542', X'34AC2F0A8213DE498CC5F87214E47717', X'34AC2F0A8213DE498CC5F87214E47717', 4, NULL, NULL, 2, NULL, 0, 'Stir in the sliced banana and blueberries and drizzle with honey just before serving');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEB3122A99832247B77C1BE9A303C0EC', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 0, NULL, NULL, 2, NULL, 0, 'Place the Brown Basmati rice into a saucepan, cover generously with cold water and add a pinch of salt. Bring up to the boil then reduce to a medium heat and simmer for 20 minutes, or until the rice is tender. Drain thoroughly and spread the rice out across a baking tray to cool.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB70FC2712231841833B899C85DDD2FD', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180C/gas mark 4.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5D53818C94BF94BBDBF957F274B2F3F', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, place the salmon into a roasting tray, season generously and place half of the butter on top of the salmon. Cover the tray with foil and cook in the oven for 8 minutes until just firm, then remove the tin foil and leave to cool. Flake the salmon into a bowl, taking care to remove any fish bones.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EB3BDEBE8298946BBC7CFB814AB2F25', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 3, NULL, NULL, 2, NULL, 0, 'Place a frying pan over a low heat and gently melt the remaining butter. Add the shallots and garlic and cook, without colouring, for 3 minutes until softened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C80827FCCE82E4FA4573DEEEC99F5AB', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 4, NULL, NULL, 2, NULL, 0, 'Add the sliced mushrooms to the pan and cook until tender, then remove from the heat and stir in the chopped herbs and lemon zest. Spread the mushroom mixture out across a baking tray to cool.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D48A5C31100E7479C4856F679B595EC', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 5, NULL, NULL, 2, NULL, 0, 'Roll out the puff pastry on a lightly floured surface until 4mm thick. Cut out two 11cm discs and two 15cm discs using pastry cutters. Place the discs carefully onto a lined baking tray and chill in the fridge for 15 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F63ECA0983F70D4AAEF9E87FFF890BD0', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the filling. Mix the cook salmon, rice, mushrooms and boiled eggs together in a large bowl, seasoning to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56D9B565681E5D429E4E0648D04CBEAB', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 7, NULL, NULL, 2, NULL, 0, 'Line a baking tray with baking parchment and place the two smaller discs of pastry on the tray. Carefully place a mound of the salmon and rice mixture into the centre of each disc, using a 9cm pastry cutter as a guide for more consistency if desired.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C657D28067E5B48887FA28335C79730', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 8, NULL, NULL, 2, NULL, 0, 'Brush the edge of each pastry base with the egg yolk and carefully place the larger discs over the top, gently pressing down the edges to seal. Brush over the pastry top with the egg yolk to glaze and place in the fridge to chill for 20 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B0D1A1023FB6540AF6CAB8DF7A0D82A', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 9, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200C/gas mark 6.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'163A39471522B344B6F46877CCEB231D', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 10, NULL, NULL, 2, NULL, 0, 'Use a small knife to gently score semi circular patterns down the sides to decorate, trimming the bases with a fluted cutter if desired. Pierce a steam hole in the top of each pie and bake in the oven for 20 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBEA13B84A472F4584C6AF0803404DE5', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 11, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the dill sauce. Sweat the shallot and garlic in half the butter over a medium heat, then add the fish stock and continue to cook until reduced by half, then stir in the cream and bring to the boil. Once boiling remove the pan from the heat and whisk in the remaining butter. Pass the sauce through a fine sieve and stir in the dill, seasoning to taste if necessary.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6541BD51C7F5084D8F33DB22D01F8773', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 12, NULL, NULL, 2, NULL, 0, 'For the vegetables, place the carrots in a pan and add enough water to just cover. Add half the butter and the sugar, then simmer until the carrots are tender and the water has reduced to a syrup. Add half the pear vinegar and cook for a further 20 seconds, then drain and transfer to a warmed serving dish.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E60F75F0BCC3646B6689702DB2F8AFF', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 13, NULL, NULL, 2, NULL, 0, 'Repeat the process with the leeks, adding them to a pan with just enough water to cover. Add the remaining butter to the pan and cook on a high heat until the leeks are tender, then add the remaining pear vinegar. Reduce until glazed and drain, adding to the serving dish with the carrots.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D26015F686DFF4489E252881866D8B8', X'8E5E0EBF0D8D6740AA93231BAFA692B7', X'8E5E0EBF0D8D6740AA93231BAFA692B7', 14, NULL, NULL, 2, NULL, 0, 'Remove the pies from the oven and serve immediately with the glazed vegetables and dill sauce on the side.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FEA370D91F48845B3261D781CECD0C8', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 0, NULL, NULL, 2, NULL, 0, 'For the rice stage, rinse the rice three times in cold water and drain well');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBEC3E713F477F41AF1EF051B2176B5D', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 1, NULL, NULL, 2, NULL, 0, 'Place the ﬁsh stock in a large saucepan, bring to the boil and add the bay leaves, turmeric and mixed herbs');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB607E525D5C34DAEAB63BFA1238324', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 2, NULL, NULL, 2, NULL, 0, 'Add the rice, cover, and cook over a very low heat for 16 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D9A4D6F58D6AC4B8B8119F67FA3B12D', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 3, NULL, NULL, 2, NULL, 0, 'Add the frozen peas and cook for a further 4 minutes, until the stock is absorbed and the rice is cooked. Remove the bay leaves.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A48D464AC111A48B04FD8D13E8DA022', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, for the chicken and prawn stage, heat the oil in a paella pan or large non-stick frying pan over a medium heat.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDABBA8B41B5BB40848DE068B0F149CA', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 5, NULL, NULL, 2, NULL, 0, 'Add the onion, garlic and chilli ﬂakes and stir-fry for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F566B8CA9AA70144BA9E60AAEF5025D0', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 6, NULL, NULL, 2, NULL, 0, 'Add the red pepper and stir-fry for 4-5 minutes, then add the chicken and stir-fry for a further 6-7 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E3834E31B92914E8125CA1FE8FDDCDC', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 7, NULL, NULL, 2, NULL, 0, 'Stir in the prawns (and mussels and scallops if using and heat through gently for 4-5 minutes. Ensure the scallops are thoroughly cooked.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'746D37DA2C31B741B7C9C517254744D8', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 8, NULL, NULL, 2, NULL, 0, 'Add the seafood mixture to the rice and stir gently to combine.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D6D0C9E9CF6E448A6D5AE01A461A7B0', X'DCF5D428037562489E7CA4BA27FFE121', X'DCF5D428037562489E7CA4BA27FFE121', 9, NULL, NULL, 2, NULL, 0, 'Add the lemon juice and serve immediately.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17DC8B01D741774995027CCCD8C70312', X'8646423789F4C04EAF321C1A2BA1DEE3', X'8646423789F4C04EAF321C1A2BA1DEE3', 0, NULL, NULL, 2, NULL, 0, 'Rinse the rice is cold water until the water runs clears – set aside in a sieve to allow as much water as possible to drain off.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2399900ADFD729438CD59C51C67D40A6', X'009021B2DE78004D9BC8C56F85C9DD67', X'009021B2DE78004D9BC8C56F85C9DD67', 0, NULL, NULL, 2, NULL, 0, 'Bend the turkey mince, breadcrumbs, black pepper, salt, herbs and chilli paste together in a bowl. Form into 12 mini meatballs. Refrigerate if time.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B5D6B68014EE94B95C4C8B6FAF92838', X'CE587C9F78F66242A7C43F5E24E68011', X'CE587C9F78F66242A7C43F5E24E68011', 0, NULL, NULL, 2, NULL, 0, 'Chop courgettes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'184996C11C4DB94DB954852E8DD18E2D', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', 0, NULL, NULL, 2, NULL, 0, 'Bring large pan water to the boil and add the vinegar. Poach the eggs: stir the boiling water to form a whirlpool and crack the eggs into the water one or two at a time. Poach for 3-4 minutes, remove with a slotted spoon and set aside');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AF1BB2650BF0141B303494D31DD2450', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile heat the olive oil in large pan with a lid. Add the asparagus, coat in the oil, add the stock or water and a tsp of the butter, season to taste. Cover with lid and cook gently for 3-4 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DFD242DAF911A49B26044C449748475', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', 2, NULL, NULL, 2, NULL, 0, 'Remove the asparagus from the pan and set aside. Simmer the cooking liquid until halved, add the tsp remaining butter and a dash balsamic vinegar to taste');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E941C5BA312064781E08BA0BBDBCAB4', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', 3, NULL, NULL, 2, NULL, 0, 'Heat the rice according to pack instruction and divide between 4 plates');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D92660EB087D245B091716035FD0A8F', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', X'FE6D11E8D7ADC142A9D07836C1D3AEC4', 4, NULL, NULL, 2, NULL, 0, 'Top with 3 asparagus spears and a poached egg, drizzle with some of the sauce and serve');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'496E4C6A7CC8FD42A7BA81C21F527F49', X'26512E3AAC6B7A4EBD540EE443F09B9E', X'26512E3AAC6B7A4EBD540EE443F09B9E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 180°c, gas mark 6.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE0122227273AA49815595C0927AAEC8', X'26512E3AAC6B7A4EBD540EE443F09B9E', X'26512E3AAC6B7A4EBD540EE443F09B9E', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large pan. Add the garlic, shallots and fennel and fry gently until soft for about 3-5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AA79A488762BA458D2C8287BCCB81BF', X'26512E3AAC6B7A4EBD540EE443F09B9E', X'26512E3AAC6B7A4EBD540EE443F09B9E', 2, NULL, NULL, 2, NULL, 0, 'Add the stock or wine, passatta and red pepper. Fry for about 5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'244DF2AE5AFA88418DAD8F445F48BA2A', X'26512E3AAC6B7A4EBD540EE443F09B9E', X'26512E3AAC6B7A4EBD540EE443F09B9E', 3, NULL, NULL, 2, NULL, 0, 'Add the fish and seafood mix and transfer to an ovenproof dish or tagine. Lay the clams on top. Season to taste.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'399F1D6B379A4241B8823AF024CB0A24', X'26512E3AAC6B7A4EBD540EE443F09B9E', X'26512E3AAC6B7A4EBD540EE443F09B9E', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook for about 20 minutes or until the clam shells have opened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24F7A8EEFC05BB4EAD98BCF5A999B175', X'26512E3AAC6B7A4EBD540EE443F09B9E', X'26512E3AAC6B7A4EBD540EE443F09B9E', 5, NULL, NULL, 2, NULL, 0, 'Serve with heated Tilda Lemon & Herb rice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'219D25AC5F96AD4DBF0371CC3E22C41C', X'719E34E344E8104C8DB4654F8B9A5806', X'719E34E344E8104C8DB4654F8B9A5806', 0, NULL, NULL, 2, NULL, 0, 'Cook the rice as per the instructions on the pack');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'522D381C0ADB5F49ABB08CAEA8915331', X'719E34E344E8104C8DB4654F8B9A5806', X'719E34E344E8104C8DB4654F8B9A5806', 1, NULL, NULL, 2, NULL, 0, 'At the same time, heat the oil then gently fry the chilli, cumin and halloumi for 2 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D127F43B48F1429F90A87C056BEA3D', X'719E34E344E8104C8DB4654F8B9A5806', X'719E34E344E8104C8DB4654F8B9A5806', 2, NULL, NULL, 2, NULL, 0, 'Add the sun blushed tomatoes, olives and chickpeas and heat through for a further 3 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CC845E9EC7AF548ADDF46A01C216BF7', X'719E34E344E8104C8DB4654F8B9A5806', X'719E34E344E8104C8DB4654F8B9A5806', 3, NULL, NULL, 2, NULL, 0, 'Stir in cooked Basmati rice, rocket, coriander, mint and lemon');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE31D19576EFA44AE058F4AB48954B7', X'719E34E344E8104C8DB4654F8B9A5806', X'719E34E344E8104C8DB4654F8B9A5806', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with greek yogurt drizzled over and warm flat bread');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69E36DFE9AF51441AC39A873E1AA85C1', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 0, NULL, NULL, 2, NULL, 0, 'Begin by charring the peppers. Use metal tongs to hold the peppers directly over an open flame on a gas stove. Carefully roast the pepper for about a minute on each side before turning and until all sides are blackened. Once blackened, pile all peppers onto a sheet of foil and wrap tightly so that the peppers continue to steam for 5 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7B184367A39714BAE9F13205A346368', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 1, NULL, NULL, 2, NULL, 0, 'Once cool enough to handle, peel and discard the black skins, stems and seeds and finely slice. Mix into a bowl with the halved olives, a pinch of sea salt and a little lemon zest. Set aside to serve later');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F53CAEABF5FD9E43A13564D7E982FDF5', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 2, NULL, NULL, 2, NULL, 0, 'To cook the rice, wash in a colander with cold water and set aside. Carefully melt the butter over a low heat in a medium-sized saucepan, the crush the grains of paradise in a pestle and mortar and add to the melted butter, along with the grated ginger and garlic');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22616A197E3C3847AAF087EA2CED8D5D', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 3, NULL, NULL, 2, NULL, 0, 'Cook for 2 minutes before adding the rice to the spiced butter. Water will sizzle in hot oil, so drain the rice properly');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACBE42267995B440BE0C8E817E482D20', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 4, NULL, NULL, 2, NULL, 0, 'Pour in 400ml of vegetable stock, 200ml of water and a teaspoon of sea salt. Bring the rice to the boil then turn down the heat, cover and simmer for 10 minutes. Once cooked, fluff with a fork');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'594AE72797FBA34CA7F570A3AC31EFF3', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 5, NULL, NULL, 2, NULL, 0, 'To make the yassa, heat two tablespoons of oil in a sauté pan and add the onions. Sprinkle with a pinch or two of sea salt to extract moisture from the onions and cook for 10 minutes over a low-medium heat, stirring occasionally until softened and translucent. Once cooked, add the garlic, ginger and fragrant smoked paprika. Stir and cook for two minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D435E820338E446830B452AFCCF48F9', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 6, NULL, NULL, 2, NULL, 0, 'Mix the lemon juice with the remaining lemon zest and Dijon mustard. Add to the sauté pan with fish stock, stir well and add the finely chopped (or pierced) scotch bonnet. Cook uncovered for 10 minutes to slightly thicken the sauce');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69DB35B4354E114FB1FB73E5049E2A24', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 7, NULL, NULL, 2, NULL, 0, 'Add sea salt and then carefully lower the fish into the sauce, with a few sprigs of lemon thyme and freshly ground grains of paradise. Cook covered on medium heat for 10 minutes. If using fish with skin, take out the fish after 5 minutes and grill in a preheated oven for the final 5 minutes to crisp it up');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B783D0CE72F464088F6EAB8D85F2214', X'ED7057F2D318A541A6EDE24F02A4FE2C', X'ED7057F2D318A541A6EDE24F02A4FE2C', 8, NULL, NULL, 2, NULL, 0, 'Serve the fish with the rice and pepper salad for a sweet, vibrant and lip-smacking West African feast!');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A4944E24F41124F816E5B9C633DC90A', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 0, NULL, NULL, 2, NULL, 0, 'Fill a pan with water and place on high heat.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84AC398E196A949A619F9D7111879F9', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 1, NULL, NULL, 2, NULL, 0, 'Add ¼ cup salt and bring to a boil.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12521B70C2D0D5499B9222C288C981B4', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 2, NULL, NULL, 2, NULL, 0, 'Add rice and cook for 6-8mins. Once the rice has parboiled, remove from hob and drain.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'181BCFB060CAD349846A69F88F1AF341', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 3, NULL, NULL, 2, NULL, 0, 'While the rice is cooling, add ¾ cup barberries to a pan with a small knob of butter. Allow the barberries to plump up slightly and cook on low-medium heat for about 5 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3C10A8B20D9C047B26C20B7B04BB486', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together egg yolks, yoghurt and crushed saffron.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'557D0693080D1A46A658F284995F20A8', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 5, NULL, NULL, 2, NULL, 0, 'Add the parboiled rice to the bowl and mix well so all the rice is covered in yoghurt mixture.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6380FFADCE8C824F9C9E178A1CB715A4', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 6, NULL, NULL, 2, NULL, 0, 'Grease a baking dish and add half the amount of rice mixture.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C274D6B6C1CE543BC12D9F75305EC6A', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle barberries on the top half of the rice (save some to decorate the top too).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F30559A4373D84EA2C1FC154F131668', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 8, NULL, NULL, 2, NULL, 0, 'Add remaining half of rice and use and gently pat down the top layer evenly.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E89AE721133A1349B72606E4E6B80DA3', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 9, NULL, NULL, 2, NULL, 0, 'Cover the dish with foil and place in oven for 50mins- 1hr.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A5435FCCCF18042B83CD295E4FB2E43', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 10, NULL, NULL, 2, NULL, 0, 'Remove from oven and flip dish over to remove rice cake on a plate.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32DA7782F72B4B46841EE8D524CE638A', X'BA8A70CE63F29249BA04C91DAD29C5F3', X'BA8A70CE63F29249BA04C91DAD29C5F3', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle barberries and crushed pistachios on top of rice cake to decorate.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16E6A3C79712A94BABA7EA28574C7245', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 0, NULL, NULL, 2, NULL, 0, 'Take a saucepan or a casserole large enough to hold all the tomatoes at one time as well as the rice when cooked');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21C08BAC8C3A784B9FDC05FC8D7F1A26', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 1, NULL, NULL, 2, NULL, 0, 'Wash and halve or quarter the tomatoes and add to the pot');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2963859AE0505547B261510B2C7C0AFC', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 2, NULL, NULL, 2, NULL, 0, 'Place on the cooker on a high heat & add approximately 500ml of hot or boiling water and boil for eight to ten minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9CAC1B0D4059649AD38D25F01E2D181', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile in a mortar and pestle make a paste of the ginger & garlic and add to the tomatoes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29FEEF36FF193F469E7A4E6255B0A6A5', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 4, NULL, NULL, 2, NULL, 0, 'Either puree the tomatoes or pass through a strainer. A stick blender with puree it fine even with the skins');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BBE59F00422614F9CF59B13C6BF8868', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil in a small pan or kadhai or wok and gently brown the sliced onions.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'319DE6316CEB374796B61B86A0F9B324', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile wash and drain the rice in a strainer');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B8D069054A26D42B71D0D2BF1E63DF8', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 7, NULL, NULL, 2, NULL, 0, 'Remove half on a kitchen towel and to the pan add the peppercorns, cloves.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76F68150E58C17469C0C3E8E1142E330', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 8, NULL, NULL, 2, NULL, 0, 'Add the rice and sauté the rice with the onions and the spices for two to three minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CA3A7CE0420874FBDE3281AE3B60B0B', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 9, NULL, NULL, 2, NULL, 0, 'Add all of this into the pureed tomato with the ginger and garlic purees and return to the fire.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EA4BA669E6DF54EBA9255309EBA0D9F', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 10, NULL, NULL, 2, NULL, 0, 'Add another 4-500ml of hot water, salt and cover the pot lightly');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFFB51F916DE7D43BA8ED1C48CD50342', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 11, NULL, NULL, 2, NULL, 0, 'As soon as boiling commences stir well and see if anything is stuck at the bottom.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56BE26D8D93F3B4890C7AA707A319FFE', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 12, NULL, NULL, 2, NULL, 0, 'Scrape the edges with a spatula to clean the sides, cover the pot now tightly and reduce the heat');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E7980A50C0D4C4BB1269E5FC074B537', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 13, NULL, NULL, 2, NULL, 0, 'Let the rice cook on a low heat now for fifteen to twenty minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2E3F21ED5F1B347A9DC3AFBA2E3DED9', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 14, NULL, NULL, 2, NULL, 0, 'Stir occasionally if only to prevent sticking at the bottom.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB89C275F25144E8B980CAEB21C4A0F', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 15, NULL, NULL, 2, NULL, 0, 'When rice is cooked either remove and serve with the browned onions and coriander sprinkled on the top or blend the fried onions and chopped coriander into the rice.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D47D0612FC04F47A70C0BE8E869CB9D', X'7E1D94A57A8504438E6D3123A5CE3CDB', X'7E1D94A57A8504438E6D3123A5CE3CDB', 16, NULL, NULL, 2, NULL, 0, 'Tomato rice will also taste good cold served with yoghurt.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83B69174A2E457469C5A56BA74663297', X'9744D3415EC8B44CBD55ED78FD8E4E54', X'9744D3415EC8B44CBD55ED78FD8E4E54', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large pan and sauté the shallots and garlic for 4–5 minutes. Add the pumpkin, thyme and flaked almonds and sauté for a further 8–10 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DEE63C16AAF4A4C9EE5086347D6C33E', X'9744D3415EC8B44CBD55ED78FD8E4E54', X'9744D3415EC8B44CBD55ED78FD8E4E54', 1, NULL, NULL, 2, NULL, 0, 'Add the rice and mix well before pouring in the vegetable stock. At this point you can continue to cook the dish in the pan, covered, for 20–25 minutes, or bake as suggested below');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C543A97DE5A904BA9D6F144D191EE12', X'9744D3415EC8B44CBD55ED78FD8E4E54', X'9744D3415EC8B44CBD55ED78FD8E4E54', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180°C/gas mark 4');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CFDDEDE8D497C4A9999C57D17DB4B94', X'9744D3415EC8B44CBD55ED78FD8E4E54', X'9744D3415EC8B44CBD55ED78FD8E4E54', 3, NULL, NULL, 2, NULL, 0, 'To bake, spoon the mixture into a large gratin dish and scatter over ¾ of the grated cheese, cover with foil and bake the oven for 20 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAA202158733164B8A8DDEF12F3BA9AA', X'9744D3415EC8B44CBD55ED78FD8E4E54', X'9744D3415EC8B44CBD55ED78FD8E4E54', 4, NULL, NULL, 2, NULL, 0, 'After 20 minutes, remove the foil and bake for a further 20–25 minutes until the cheese is golden brown and the rice is cooked');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47F0C196D2FA2F45AFCD992C92B99A78', X'9744D3415EC8B44CBD55ED78FD8E4E54', X'9744D3415EC8B44CBD55ED78FD8E4E54', 5, NULL, NULL, 2, NULL, 0, 'Serve with the remaining grated cheese, salad and crusty bread or seasonal greens');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CAEB7DB2ED0764381DBC55725A19BC1', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 0, NULL, NULL, 2, NULL, 0, 'Cook the rice according to the packet instructions. Once cooked, drain the rice and set aside to steam-dry');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A200425CB00394B8001BF010B87FA2A', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 1, NULL, NULL, 2, NULL, 0, 'While the rice cooks, heat 1 teaspoon of sesame oil in a medium to large saucepan over a moderate heat. Once hot, add the diced onion and finely chopped celery and sweat for 8 minutes until they have softened, become translucent and started to slightly caramelise');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00D4791F66003841B1A55BB59FED214A', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, ginger and chilli and sauté for 2 minutes. Add the sliced carrot and continue to stir-fry for 5 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63277092DFA07E43BA8CE87A905E4F0E', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, drain the silken tofu in a sieve or a colander');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BB915227308C644B23255473EA82D8E', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 4, NULL, NULL, 2, NULL, 0, 'After 5 minutes, add the peas and baby corn and stir well. Stir-fry for 4 minutes to heat through');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'000CFE5C4A73CB419BFE8F3BA3F1CFF8', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked and drained rice, stir well to combine and then make a well in the centre of the saucepan. Turn the heat up high and add your tofu to the well – it should sizzle immediately. Sprinkle the turmeric and the kala namak over the tofu');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73EF9BDFA525E74C98267B4E68D79193', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 6, NULL, NULL, 2, NULL, 0, 'Hold your nerve here with the heat; after two minutes start to stir and break up the tofu and incorporate this into the rice and veggies. Continue to stir-fry the fried rice for 6 minutes, or longer if you prefer it drier and crispier');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA98DBB297652748AAB6E9A7CE527415', X'0978AD90C3B1A14D9AE00D59AFADCF07', X'0978AD90C3B1A14D9AE00D59AFADCF07', 7, NULL, NULL, 2, NULL, 0, 'Serve in warmed bowls, sprinkled with sliced spring onions, fresh lime wedges or a squeeze of lime juice, toasted sesame seeds and 2 tablespoons of kimchi per bowl');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F442B07CD3BA745BF615FC221048A5C', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 0, NULL, NULL, 2, NULL, 0, 'First make the marinade. Put the onions into a bowl and rub them gently under running cold water. Drain well in a colander, then dry in a salad spinner or with kitchen paper.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78754167E8FF7A4394C9CDD5002BF292', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a deep pan. When hot, fry the onions in 3 separate batches until pale golden. Using a slotted spoon, transfer them to a colander, where they will continue to brown in their own heat. The oil can be reused another time if cooled and strained.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F80AD4CABE8C8140AE6D7F7DA16C5DB7', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 2, NULL, NULL, 2, NULL, 0, 'Put all but 2 tablespoons of the fried onions into a blender, add the remaining marinade ingredients and whiz to a purée. You might need to do this in batches so that the machine can create a smooth paste and the motor doesn’t overheat. Transfer the mixture to a large flameproof casserole dish.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4951DE783B6CE848AE4459AAF8B67449', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 3, NULL, NULL, 2, NULL, 0, 'Trim any excess skin from the chicken pieces, then place the meat in the marinade. Cover with a tight-fitting lid and set aside in the refrigerator for at least 1 hour.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D51698192324EB46934E5A864F87066E', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180ºC/350ºF/Gas Mark 4. Transfer the casserole dish to the oven and cook for about 1 hour, until the chicken is tender.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B5B8C002E6FC4D9077BA526F5F5B0D', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the potatoes in a pan of boiling salted water for about 8–10 minutes, until tender. Drain and set aside.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F4B7C4183BA794DA2C1EFAA3F2F4F37', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 6, NULL, NULL, 2, NULL, 0, 'Put the measured water into a large saucepan with the bay leaves and some salt and bring to the boil. Add the rice and boil for roughly 6 minutes, until al dente. Drain well, reserving the water to make soup if you wish. Fork through the rice to loosen the grains.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FCCE98689BBBF419485826E20969BB4', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 7, NULL, NULL, 2, NULL, 0, 'Heat the saffron threads in a warm small frying pan over a gentle heat until they are crisp. Add 2–3 tablespoons of water and set aside to rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C19845F08236AD429BDE66D88F1266F6', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 8, NULL, NULL, 2, NULL, 0, 'Pour the saffron and its liquid over the rice while still in the colander and mix lightly to partially colour the grains.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7063E4FF1570747B295521F46D39A46', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 9, NULL, NULL, 2, NULL, 0, 'When the chicken is ready, the sauce might look too thin or a bit oily, but don’t worry about that. Heat it, uncovered, for a few minutes to reduce it if you wish, but a generous amount of sauce is needed to finish the dish. Taste and adjust the salt if necessary, then add the fresh mint and coriander. Transfer the chicken to a plate and pour the sauce into a jug. Lower the oven temperature to 150ºC/300ºF/Gas Mark 2.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C91A5A2FA189A745A32E4C7FFCD3222C', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 10, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large saucepan. Return some of the sauce to the empty casserole dish and cover with one-third of the rice. Arrange the chicken pieces and a few chunks of potato on top, then cover with another third of the rice. Pour over some more sauce, dot with the remaining potatoes and cover with the remaining rice. Press down gently. Drizzle the remaining sauce over the rice, sprinkle with the reserved onions and the melted butter and cover tightly.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E249746301CFD94A8B741686B26C6174', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 11, NULL, NULL, 2, NULL, 0, 'Place on the middle shelf of the oven for up to 40 minutes to heat through fully. If the ingredients have become cold during the process of putting the dish together, they might need longer to heat through. In this case, lower the temperature to 120ºC/250ºF/Gas Mark ½ and continue heating until completely hot.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'198ECAF1F8DD634793553652D2C700A0', X'C2A629F5BB04FE4C9F03BB9871312A10', X'C2A629F5BB04FE4C9F03BB9871312A10', 12, NULL, NULL, 2, NULL, 0, 'Serve the biryani with Mixed Raita or a daal, allowing people to help themselves straight from the dish if you like.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DF1FC5BAD92C043B47D655B2DDA98F6', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 0, NULL, NULL, 2, NULL, 0, 'Prepare a broth for the chicken and coconut rice by heating a tablespoon of coconut oil in a wide saucepan. Add the sliced onion and cook over a medium heat until softened, then add the garlic and ginger and cook for 2 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6BB473A4454F842AACBCB3378251069', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 1, NULL, NULL, 2, NULL, 0, 'Lightly toast the whole seeds in a hot dry frying pan for a minute or until darker in colour, then crush with a pestle and mortar and add to the onions. Add the curry powder and stir');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48501B3191E8274C955F9D4C7B204B31', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 2, NULL, NULL, 2, NULL, 0, 'Throw in the bay leaf, coconut milk, crushed whole uda pod and sea salt. Stir well, bring the broth to the boil and add the chicken thighs. Reduce heat to low, cover and poach for 5 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19C0AE8F0995C7478410F5D85E9EA867', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 3, NULL, NULL, 2, NULL, 0, 'Remove the uda pod and poached chicken from the broth with a slotted spoon and place in a colander to drain');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51C4C1BE9A81A44E82ED628DED8A60AE', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 4, NULL, NULL, 2, NULL, 0, 'For the coconut rice, rinse the rice, drain well and add to the coconut broth. Cook tightly covered over a medium heat for 5 minutes. Add the chopped carrots and green beans, then cook for a further 5 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09826E66530449418F824C13FC89BC33', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 5, NULL, NULL, 2, NULL, 0, 'Check the rice for doneness and, if needed, add 100–200ml of water at a time. Continue to cook until the rice is tender. Once cooked, sprinkle with chopped spring onions and fluff the rice with a fork');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7459AE47237B0A42A7CBA46FA77B3094', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 6, NULL, NULL, 2, NULL, 0, 'For the suya, dry roast the peanuts in a hot frying pan until golden then crush into a fine powder using a pestle and mortar or food processor. Transfer to a muslin cloth and squeeze out as much oil as possible, until you are left with a cakey peanut residue');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2286667646956D43A64FB4ABEB1CED19', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 7, NULL, NULL, 2, NULL, 0, 'Crush the uda pods as finely as possible, separating and discarding the coarse bark. Mix the peanuts and crushed uda pods with the garlic, ginger and chilli powder, then season with sea salt. Save a tablespoon or two of the suya spices for garnish, and use the rest to coat both sides of the poached chicken thighs');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A296A484F871FB4C9BFD51BFD52D9AB2', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 8, NULL, NULL, 2, NULL, 0, 'Heat a grill or griddle pan until smoking hot. Brush the chicken on both sides with coconut oil and grill for 5 minutes on each side or until cooked through. Once cooked, remove the chicken from the grill and leave to rest. Place the beef tomatoes and onions on the grill until charred');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5415B7023FD2A74DA8ADDFA02433E506', X'7D7F7CBEAA3E6047B529F64AA37361CC', X'7D7F7CBEAA3E6047B529F64AA37361CC', 9, NULL, NULL, 2, NULL, 0, 'Slice the grilled chicken into thin strips and serve with the coconut rice, char-grilled onions and tomatoes and a sprinkling of the reserved suya spices, just like you would get on the bustling streets of Lagos');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B522BFC04EB6747861D5F8F32F189A5', X'BA1AAD443B570341895304BD278D848D', X'BA1AAD443B570341895304BD278D848D', 0, NULL, NULL, 2, NULL, 0, 'To make the ketchup, place the apples, sugar, ajwain seeds and vinegar in a saucepan and set over a medium heat. Bring to the boil and cook the apples until they are very soft.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B91278322A6649BC73842CD21A9BCE', X'BA1AAD443B570341895304BD278D848D', X'BA1AAD443B570341895304BD278D848D', 1, NULL, NULL, 2, NULL, 0, 'Drain the apples, reserving the liquid in the pan. Place the apple mixture into a blender and blitz until smooth. Add the lemon juice and enough of the reserved cooking liquid to reach a thick ketchup-like consistency. Store any extra ketchup in an airtight container in the fridge for a week or so.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43D754E4C2669C4EB4E79AB884712B82', X'CC93BDC86DAD7B488E0F4CF14A84148A', X'CC93BDC86DAD7B488E0F4CF14A84148A', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 180C');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA1CBA381A52214EB26F68DECE460167', X'CC93BDC86DAD7B488E0F4CF14A84148A', X'CC93BDC86DAD7B488E0F4CF14A84148A', 1, NULL, NULL, 2, NULL, 0, 'Coat the diced butternut squash in a little oil and a sprinkle of cumin seeds and chilli. Bake for 25 minutes until soft and charred. Remove and cool.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1631F112876894E97A77AAF27B59E95', X'CC93BDC86DAD7B488E0F4CF14A84148A', X'CC93BDC86DAD7B488E0F4CF14A84148A', 2, NULL, NULL, 2, NULL, 0, 'Coat the kale in a sprinkle of oil with some cumin and cook in the oven for 10 minutes until crisp – do not let it brown too much. Remove and leave to cool.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F41522A0D3792D4397055CE9F8AEE9BE', X'36A29909B065394C844CD9C9ABA4326F', X'36A29909B065394C844CD9C9ABA4326F', 0, NULL, NULL, 2, NULL, 0, 'Dissolve the sugar and the vinegar in a bowl.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3CEFD2A940B344CAA34F77317529BB7', X'36A29909B065394C844CD9C9ABA4326F', X'36A29909B065394C844CD9C9ABA4326F', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a heavy pan and add all the spices until they sizzle & become fragrant. This should only take a minute.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC2BE335F6E9A64689338CE3F36D5AE6', X'36A29909B065394C844CD9C9ABA4326F', X'36A29909B065394C844CD9C9ABA4326F', 2, NULL, NULL, 2, NULL, 0, 'Very gently, pour the vinegar and sugar mixture into the pan with the spices and stir. Bring this to a simmer.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0B1E1476764E6429867C7EE4BB6ED20', X'36A29909B065394C844CD9C9ABA4326F', X'36A29909B065394C844CD9C9ABA4326F', 3, NULL, NULL, 2, NULL, 0, 'Add in the tomatoes and cook on a very low heat until they have softened and have gone pulpy (about 45 minutes to 1 hour).');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82BB63622366CE479C0E8C39E9894517', X'36A29909B065394C844CD9C9ABA4326F', X'36A29909B065394C844CD9C9ABA4326F', 4, NULL, NULL, 2, NULL, 0, 'Season with salt and chilli powder then stir to keep it from sticking. If you want to remove any of the tomato skins then do so with some tongs.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF6C3862548D5048B4887A6914137951', X'36A29909B065394C844CD9C9ABA4326F', X'36A29909B065394C844CD9C9ABA4326F', 5, NULL, NULL, 2, NULL, 0, 'Put the chutney into a sterilised glass jar and leave to cool.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C9C132209FE7D4588051F4C9CC4DCEC', X'36A29909B065394C844CD9C9ABA4326F', X'36A29909B065394C844CD9C9ABA4326F', 6, NULL, NULL, 2, NULL, 0, 'Once cooled seal the jar. The chutney will keep for 4-6 weeks. Refrigerate once opened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'046AE2703D654B41A0FB65A6A6CAE67C', X'C60256006F318F49A06632928EE65B55', X'C60256006F318F49A06632928EE65B55', 0, NULL, NULL, 2, NULL, 0, 'In a wide, heavy bottom sauce pan, add the rice, full fat milk and coconut milk, and heat over medium heat. Keep stirring it and bring to the boil.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A918C4195460E441ADB82ABC09B14F42', X'C60256006F318F49A06632928EE65B55', X'C60256006F318F49A06632928EE65B55', 1, NULL, NULL, 2, NULL, 0, 'Simmer and stir in the maple syrup and the crushed cardamom seeds.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'416BC4FF0E9AFF4396D710EF150BD865', X'C60256006F318F49A06632928EE65B55', X'C60256006F318F49A06632928EE65B55', 2, NULL, NULL, 2, NULL, 0, 'This needs to cook gently for about 30 minutes. Make sure you stir the rice so it doesn’t stick. If it feels too dry just add a little more milk.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9338EDCDE9FCC4482AA572F88CD24A1', X'C60256006F318F49A06632928EE65B55', X'C60256006F318F49A06632928EE65B55', 3, NULL, NULL, 2, NULL, 0, 'Test the rice by squeezing it between your fingers. Once it''s soft and cooked through the pudding should have a lovely creamy consistency, remove it from the heat and cover with a lid.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E567C4A2AFE7594198ED9D5B8AE05E80', X'C60256006F318F49A06632928EE65B55', X'C60256006F318F49A06632928EE65B55', 4, NULL, NULL, 2, NULL, 0, 'In a frying pan heat the coconut oil and add cashew nuts to toast slightly, add the pistachios and raisins and fry for a few seconds.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E50EAFFA9A7925448D8C5461A3D762A8', X'C60256006F318F49A06632928EE65B55', X'C60256006F318F49A06632928EE65B55', 5, NULL, NULL, 2, NULL, 0, 'Pour the fried nuts along with the butter over the Payasam with a sprinkle of the coconut and pomegranate seeds, and serve warm.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6D8223E0061784DA9D682AC0714A7CB', X'4C1AD23068B1974AB2A147F0C7CC8F0E', X'4C1AD23068B1974AB2A147F0C7CC8F0E', 0, NULL, NULL, 2, NULL, 0, 'To begin, heat oil in a pan. Add the cinnamon, cloves, cardamom pods, chillies and peppercorns and fry for 1 minute. Add the onion and fry until golden-brown.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56FE0CB8A811CA4DA53459FB4AD947C5', X'4C1AD23068B1974AB2A147F0C7CC8F0E', X'4C1AD23068B1974AB2A147F0C7CC8F0E', 1, NULL, NULL, 2, NULL, 0, 'Stir in the tomatoes, a generous pinch of salt and the turmeric. Cook for a further 7–10 minutes. Add the mussels and allow to cook through.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34F4552434B5A84887FD9D57A6B83A59', X'4C1AD23068B1974AB2A147F0C7CC8F0E', X'4C1AD23068B1974AB2A147F0C7CC8F0E', 2, NULL, NULL, 2, NULL, 0, 'Add the rice and water to the pan and leave to cook with a lid on until the rice is tender. Serve immediately with some fresh coriander.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE34668A11187B43BA57446B9D6D2EAC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 0, NULL, NULL, 2, NULL, 0, 'Put a medium sauce pan on a medium heat, add the oil and the garlic and fry till fragrant.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3268E7866A14644BEA82400C78058D4', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 1, NULL, NULL, 2, NULL, 0, 'Add the tomato and lemongrass base.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'117D5D99B27C1B409D44443469C9E4DA', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 2, NULL, NULL, 2, NULL, 0, 'Add 2 tbsp. water and stir, simmer for 2 mins.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2308A41E39173443AA1D0BFF272B159D', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 3, NULL, NULL, 2, NULL, 0, 'Add 1 tbsp. soy sauce and ½ a tsp. sugar and mix well.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD1EA16BF41C6747918ACDC8258B4B12', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 4, NULL, NULL, 2, NULL, 0, 'Add the chopped aubergine, add 150 ml of water simmer for a minute, add the rest of the fish sauce and sugar.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EEEC03B84F5894788DEFBB09246BCE6', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 5, NULL, NULL, 2, NULL, 0, 'Mix well and add the turmeric, pepper and the lemongrass stalk. Simmer for about 12mins or until the aubergine is cooked. Add the spring onion and stir in. Cook for a further minute.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92733DF088CF9C41A72FE076000FD19E', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 6, NULL, NULL, 2, NULL, 0, 'Serve in a warm dish or clay pot and garnish with lots of chopped herbs with white rice on the side.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52C6064EE70C204BA1051FA0B89CCC4B', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 7, NULL, NULL, 2, NULL, 0, 'Scale and fillet the bream, pin bone and lightly score the fish fillets.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD4C078D082FE94F807C5A7E1CA543DE', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 8, NULL, NULL, 2, NULL, 0, 'Finely chop the chilli and garlic');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5351646A58F9E4286170200B01387A5', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 9, NULL, NULL, 2, NULL, 0, 'Place the chilli, sugar, vinegar, fish sauce and water in a pan and bring to the boil, reduce the heat and simmer until the mix starts to thicken, add the garlic and continue to cook until the mix is thickened.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'755D97B4056A514C9D6279846DAB8EC8', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 10, NULL, NULL, 2, NULL, 0, 'Peel and julienne the green mango. Toss in a little lime juice with a pinch of salt.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9785C56ADDDBD4BB2876D8945CA157D', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 11, NULL, NULL, 2, NULL, 0, 'Heat a frying pan until hot, season the fish skin with a little salt and cook skin side down until crisp and golden, drizzle the skin with the glaze and then flash the fish under the grill to finish, serve on a bed of the mango with some of the mango scattered over the top, garnish with fresh Vietnamese mint and coriander.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BFCFA4C95504A40A8CAC7CFCF482FB7', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 12, NULL, NULL, 2, NULL, 0, 'Place the rice and coconut in a small saucepan and pour in the oil and salt, stir together. Alternatively heat up Tilda Coconut rice for 2 minutes in the microwave and serve.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E4C707558DED8428315DE88399DE096', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 13, NULL, NULL, 2, NULL, 0, 'Cover the rice by 1 cm with boiling water and then bring to the boil.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6025100CBFACB49BC5A876071BC8A95', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 14, NULL, NULL, 2, NULL, 0, 'Reduce the heat and simmer the rice with a lid on until all the water has been absorbed.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'114CC9EAFB66CA4FA75BFC9FB608F929', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 15, NULL, NULL, 2, NULL, 0, 'Lightly fluff the rice, stir in the coconut milk and then place a clean tea towel over the top of the pan and recover with the lid; this will allow the rice to steam.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4692F9394C74FE45914188629F70D29F', X'28379CEB5E65CA41A2D9FD9FFBF019FC', X'28379CEB5E65CA41A2D9FD9FFBF019FC', 16, NULL, NULL, 2, NULL, 0, 'Fluff the rice again before serving.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67FB43B5ED645B40898F6F9CC6E00403', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', 0, NULL, NULL, 2, NULL, 0, 'For the dough, place the flour into a large bowl and stir the hot water gradually into it, mixing all the time, until the water is incorporated.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D1496916D820D419F9A3847E6551330', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', 1, NULL, NULL, 2, NULL, 0, 'Tip the dough mixture onto a clean work surface and knead it with your hands, dusting the dough with a little flour if it''s sticky. Continue kneading until smooth - this should take about eight minutes, then allow to rest for 20 minutes.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AAC98DFA5DAA644A82BA27CDD832B84', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', 2, NULL, NULL, 2, NULL, 0, 'For the stuffing, while the dough is resting, combine the stuffing ingredients in a large bowl and mix them together thoroughly.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E9A609F34AFCE46936EFE78F3D0BCC0', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', 3, NULL, NULL, 2, NULL, 0, 'Shape the dough into a roll about 20cm long and about 2.5cm in diameter, then with a sharp knife, slice the roll into 16 equal-sized pieces and roll each piece into a small ball. With a rolling pin, roll each ball into a small, round, flat ''pancake'' - about 9cm in diameter.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BCED4915CEDE64A89ED26E1BF7F629F', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', 4, NULL, NULL, 2, NULL, 0, 'Place about two teaspoons of filling in the centre of each ''pancake'' and moisten the edges with water. Fold the dough in half and pinch together with your fingers.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'595EF6333080A548A864E88AAF0FDB3C', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', 5, NULL, NULL, 2, NULL, 0, 'Pleat around the edge, pinching with your fingers to seal well. The dumplings should look like a small Cornish pasty with a flat base and rounded top.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72E57794FDC714468AD2386B873415C0', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', X'F8B3873BE6FB6D46BDCB0333EA60CB9E', 6, NULL, NULL, 2, NULL, 0, 'To cook, steam in a basket for about 8 minutes. Alternatively, they can be fried. For the dipping sauce, combine all the ingredients in a small bowl.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84A490E484BBA4DB1D21FA1666EDE80', X'5545FA32AC37B14AA4ED724CDFD1DCCB', X'5545FA32AC37B14AA4ED724CDFD1DCCB', 0, NULL, NULL, 2, NULL, 0, 'Slice the red onions, chillies and runner beans, and chop the garlic finely');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1520CC3BE21E5C43A32C04C7EE6052E3', X'404B17A823F55B46BCBC559B08472132', X'404B17A823F55B46BCBC559B08472132', 0, NULL, NULL, 2, NULL, 0, 'Add sliced red onion, broccoli florets and grated sweet potato to a pan and saute');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63F9266EACFDE840BE577A3F16ED325E', X'404B17A823F55B46BCBC559B08472132', X'404B17A823F55B46BCBC559B08472132', 1, NULL, NULL, 2, NULL, 0, 'Add chopped garlic and ginger to the pan with a spoonful of black mustard seeds, 1 tbsp of curry powder and some thinly sliced chilli');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4950B99609F4A49880E1CAD1C2774D7', X'404B17A823F55B46BCBC559B08472132', X'404B17A823F55B46BCBC559B08472132', 2, NULL, NULL, 2, NULL, 0, 'Pour in a cup of coconut milk, stir well and leave to simmer');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DEB9377423C8D4BB586BB7D6953F846', X'404B17A823F55B46BCBC559B08472132', X'404B17A823F55B46BCBC559B08472132', 3, NULL, NULL, 2, NULL, 0, 'Add cod fillets, stir and leave to cook through');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CEA97E2867A4C418AA13016502061FE', X'404B17A823F55B46BCBC559B08472132', X'404B17A823F55B46BCBC559B08472132', 4, NULL, NULL, 2, NULL, 0, 'Microwave Tilda coconut rice for two minutes, plate up with the rice and enjoy the delicious taste of Southern India');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A55F035241C1344AABD524F440D9AF9B', X'0E0D52DA92C06740A7420D1F738F94E4', X'0E0D52DA92C06740A7420D1F738F94E4', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan, and add the diced onion. Cook over a medium heat for a few minutes, until the onion is soft and translucent.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E32B8FFD92F02945AB50622140C1D9A6', X'0E0D52DA92C06740A7420D1F738F94E4', X'0E0D52DA92C06740A7420D1F738F94E4', 1, NULL, NULL, 2, NULL, 0, 'Peel the sweet potatoes, and cut into 2cm dice. Add to the pan, along with the vegetable stock. Cover the pan with a lid, and bring the mixture to a simmer.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F63B708A228C6C468E6E368108996C1C', X'0E0D52DA92C06740A7420D1F738F94E4', X'0E0D52DA92C06740A7420D1F738F94E4', 2, NULL, NULL, 2, NULL, 0, 'Cook for around 25 minutes until the sweet potato is very soft - you should be able to squash a piece with the back of your spoon.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0791D79371F25446A61BDE40398938E1', X'0E0D52DA92C06740A7420D1F738F94E4', X'0E0D52DA92C06740A7420D1F738F94E4', 3, NULL, NULL, 2, NULL, 0, 'Using an immersion blender, blend the soup until it’s fairly smooth. Add the red Thai curry paste and coconut milk, and mix well.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D93CFD6B2B7B0C40A05BCE7604521377', X'0E0D52DA92C06740A7420D1F738F94E4', X'0E0D52DA92C06740A7420D1F738F94E4', 4, NULL, NULL, 2, NULL, 0, 'Next add the Super Grains Sweet Potato, Chilli & Coconut, and cook for just a few more minutes until the grains are completely cooked.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235167755AD09843832986D280967CDC', X'0E0D52DA92C06740A7420D1F738F94E4', X'0E0D52DA92C06740A7420D1F738F94E4', 5, NULL, NULL, 2, NULL, 0, 'Serve the soup with plenty of garnish - fresh coriander, sliced spring onions and sliced mild chillies are all good options.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01355F37FD0E334AB0D250E2DAC3E055', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200°C/gas mark 6');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B554A0580E78543A4F35E03EE6A0536', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 1, NULL, NULL, 2, NULL, 0, 'To begin, soak the dried chilli in a bowl of boiling water for 20 minutes using 300ml water – the quantity is important as this is the water you''ll use later to cook your rice. You may need to weigh your chilli down with a spoon if it keeps floating to the surface');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41F1EAB3926A9946AA35D5F2BBCBBD6A', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 2, NULL, NULL, 2, NULL, 0, 'Mix the plantain with the paprika and oil. Lightly season, spread out on a tray and bake in the oven for 15–20 minutes, turning over halfway through. They should be soft when cooked and just starting to crisp up');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB10630251B3FA49BB6D8F82E14485C6', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 3, NULL, NULL, 2, NULL, 0, 'Take the chilli out of the water and pat dry with some kitchen paper. At this stage, you can keep it whole or you can split it in half. If you like things spicy, go for the latter');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE597C645E30774E9E58A3574BD0D305', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 4, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan over a medium heat and add the chilli, stirring around for a minute or two. Add the garlic, cinnamon, cumin and lime peel. Stir around for another minute before adding the rice and the oregano. Once more, stir for a minute to ensure that all the flavours are mixing in with the rice');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42619ECC88DD73449F745A1A3CF0DF08', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 5, NULL, NULL, 2, NULL, 0, 'Finally, add the water used to soak the chilli and bring to a boil. Reduce to a bare simmer, cover and leave to cook for 12 minutes. When the 12 minutes is up, turn the heat off but leave covered for at least another 10 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F768D379FBBA46B855F6D66C77A168', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the mixed beans by placing a saucepan over a medium heat and adding a splash of oil. Add the herbs, stirring through for a minute, then add the beans. Reduce the heat and keep an eye on things, stirring every now and then. You want the beans to break down a touch and thicken. If things begin to look too dry, add a splash of water');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'899CCDE94D075E4182B46C46A82A1B98', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 7, NULL, NULL, 2, NULL, 0, 'When the rice is ready, take off the lid and remove all the larger aromatics – the chilli, garlic and cinnamon stick – all of which should be on the top of the rice. Fluff up with a fork and season with lime juice and salt');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0507D829C57748837C3AAA9847F885', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 8, NULL, NULL, 2, NULL, 0, 'To serve, lay everything out in bowls and plates and let everyone dig in for themselves. Layer up with rice, plantain and beans and garnish with sour cream and salsa. Finish with a scattering of coriander (and maybe some more lime juice) and then roll and wrap up both ends to create a burrito');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55347CCCD8116E46A51EB7A5C6A45FC9', X'CD4BC91FA843614588F5EADE12C1CD0C', X'CD4BC91FA843614588F5EADE12C1CD0C', 9, NULL, NULL, 2, NULL, 0, 'You can use Tilda Pure Basmati Rice as an alternative.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F10F800158DBBF47865BE5BF1BF73FC1', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 0, NULL, NULL, 2, NULL, 0, 'To begin, make the marinade. Finely mince the garlic and coriander stems. Dissolve the sugar in the warm water, then combine all the ingredients for the marinade in a bowl, stirring to mix well. Rub all over the chicken and let the chicken sit in the marinade in a large ziplock bag overnight. Turn the bag occasionally so that all sides stay coated with the marinade');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91053083A3CE6948A2560E77C8226E68', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 1, NULL, NULL, 2, NULL, 0, 'On the day of cooking, preheat the oven to 160°C/gas mark 3');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53CDD1C5A06453468A8CB51467BC0995', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 2, NULL, NULL, 2, NULL, 0, 'Take the chicken out of the fridge and let it come back up to room temperature. Pat with paper towels to remove the marinade and place on a roasting tray');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'180D9551F866B140900DCE6274FAF1B1', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 3, NULL, NULL, 2, NULL, 0, 'Drizzle lightly with oil, place on the oven’s middle rack and roast for 2 hours, basting it regularly with its own juices. Turn the oven up to 220°C/gas mark 8 and let the chicken cook for another 15–20 minutes, or until golden brown');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3E1B8EF76CEF64F9D6ED4F2D67466AE', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 4, NULL, NULL, 2, NULL, 0, 'While the chicken is in the oven, prepare the other elements of the dish. Wash and drain the rice. Cover with cold water and leave to soak. Blitz all the ingredients for the green sauce together, adding just enough oil to make a smooth runny sauce, sort of like a pesto. Season to taste with salt. Keep the sauce covered in the refrigerator so it doesn’t lose its vibrant green');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E50B1FD819040E41B815D204E2897376', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 5, NULL, NULL, 2, NULL, 0, 'When the chicken is ready, remove it from the oven, lifting it from the tin onto a chopping board, and cover loosely with a foil tent');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81E4A65945CB3641A1A9396685C44029', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 6, NULL, NULL, 2, NULL, 0, 'While the chicken is resting, drain and tip the rice into a rice cooker. Pour the chicken roasting juices into a measuring jug and top up with enough water so you have 300ml liquid altogether. Add to the rice and let the rice cooker do its magic');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E81DDB12910744E860F9A59DE0C200B', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 7, NULL, NULL, 2, NULL, 0, 'For those without a rice cooker, bring the rice and roast chicken juices to a boil in a saucepan, then cover and turn the heat down to the lowest setting. Let the rice cook, undisturbed, for about 10–15 minutes or until all the liquid has been absorbed. Turn the heat off, give the rice a stir, and put the lid back on, letting it finish cooking by steaming in the pan’s residual heat, for another 5 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9090DF139A042144972EB706D03186A7', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 8, NULL, NULL, 2, NULL, 0, 'When ready to serve, carve and chop the chicken into generous bite-sized pieces before serving with the fragrant rice and green sauce');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'553D63E6C625674ABD0DD4744CCBF7EC', X'39F8F2493C80284C96B7DD32C2E0A2AD', X'39F8F2493C80284C96B7DD32C2E0A2AD', 9, NULL, NULL, 2, NULL, 0, 'You can use Tilda Pure Basmati Rice as an alternative.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376E428022F3F2459453B8151864C5FD', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 0, NULL, NULL, 2, NULL, 0, 'To begin, heat the oil in a large saucepan over a medium heat. Sauté the bay leaves for 10 seconds and add sliced onions. Fry until deep golden brown');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA3E78E20F0DC41A465F1A01B219C90', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 1, NULL, NULL, 2, NULL, 0, 'While the onions are cooking, place a large pan of water to boil. Wash and soak the rice in a large bowl of lukewarm water. Place the fish stock in a saucepan over medium heat, reduce by half and set aside');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'005B847ADD69534988B3753AA0EBD263', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 2, NULL, NULL, 2, NULL, 0, 'Grind the ingredients for the herb paste in a food processor and set aside');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB09B216F0C88C448228242CC89A3B5E', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 3, NULL, NULL, 2, NULL, 0, 'When the onions are browned, add the turmeric, fry for a minute and add the herb paste. Fry for a further 2–3 minutes, add the yoghurt and stir well to combine. Cook for a further 5 minutes and remove from heat');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECD69FB51B567A4EA94AEB60B6E3930C', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 4, NULL, NULL, 2, NULL, 0, 'Add the cinnamon, cardamom, cloves and salt to the pan of boiling water, simmer for 5 minutes then remove the spices and discard. Drain the soaked rice and add to the boiling water, stirring gently');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AC23CB7C1315A46BBF4A61C30F18794', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the pan containing the herb mixture and add the scallops, prawns and crab meat. Sauté for a minute, stirring gently. Add the reduced fish stock and remove a few prawns and scallops for garnish');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A13A883BDEC99348A7246234406AA5D6', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 6, NULL, NULL, 2, NULL, 0, 'Check the rice – once it is three quarters cooked, drain in a colander and add to the pan containing the herbs and seafood. Stir gently to mix the herbs and seafood into the rice. Check the seasoning, add the lemon juice and leave to simmer for 10 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39AD13C354B8AA4D98C6240BB02D385D', X'92624B0856974A48B859CCA7A7F001C8', X'92624B0856974A48B859CCA7A7F001C8', 7, NULL, NULL, 2, NULL, 0, 'To serve, divide the biryani between bowls and garnish with a few of the reserved king prawns and scallops. Add a sprinkling of toasted pine nuts, lemon zest and dill fronds. Serve hot');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8D790E0376B144E8FD8B9F2B0547359', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 0, NULL, NULL, 2, NULL, 0, 'To begin, lightly dust the duck breasts with the Thai 7-Spice and leave to marinate for an hour in the fridge');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61B5E14FBF033443B90B621A6CA495C4', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the curry paste by simply placing all the ingredients (apart from the coriander leaves) in a blender. Blitz to a rough and ready paste. Place in a bowl or jar and cover (you may have some left over, so a jar might be preferable');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE784BEDA0599D499715E0B0953B4C52', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 2, NULL, NULL, 2, NULL, 0, 'For the crispy shallots, pour the oil into a saucepan and place over a high heat. When the hot is oil (not smoking though) throw in the shallot rounds. They will bubble up immediately and the key here is to keep a close eye on proceedings. As they bubble, the water will evaporate and the bubbling will slowly begin to die down after about 8 minutes or so, when the shallots will begin to become golden and crisp. Don’t let them go too far, or they will burn. Scoop out with a slotted spoon and allow to drain and cool on kitchen paper. These can now be stored in an airtight container');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'420CA82036C4FE408520228E35493CF8', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 3, NULL, NULL, 2, NULL, 0, 'Remove the duck breasts from the fridge and allow to come up to room temperature');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C818FD68B735E46BFE7D3BDCA745963', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180°C/gas mark 4Place the duck breasts skin-side down in a cold frying pan then place over a medium to high heat. This method helps to slowly render the fat out of the duck, crisping up the skin beautifully. After 5 minutes, flip the breasts and place in the oven to finish cooking – 10–12 minutes is good for medium, 15 minutes for well done. Once the duck breasts are ready, leave to rest in a warm spot for another 10 minutes');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84A3E868C6BBAE4988FF76520DF67F0A', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 5, NULL, NULL, 2, NULL, 0, 'While the duck is cooking, prepare the rice by heating a wide saucepan or wok on the hob over a high heat. Add a generous splash of oil and then 4 tablespoons of the red curry paste. Or a bit more if you like things spicy. Less if you don’t. Stir briskly for a good couple of minutes, to cook the paste out');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7823090C6F9BFE4BAC572C14762A2A27', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 6, NULL, NULL, 2, NULL, 0, 'Add the rice and stir to toast for 1 minute. Add the coconut milk and 100ml of water and cover with a lid. Cook until the rice is tender and the liquid has been absorbed, then stir through the white parts of the spring onions. Remove from the heat and stir through the diced mango');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'717C809EC804B248950EC8A8EC0060E4', X'7AB0498879EE3B4ABB49A40C512C6A70', X'7AB0498879EE3B4ABB49A40C512C6A70', 7, NULL, NULL, 2, NULL, 0, 'To serve, slice the rested duck breasts evenly. Divide the rice between plates, place the duck breasts on top and garnish with the crispy shallots. Chop some of the reserved coriander leaves, sprinkle over and serve');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81C5959EF19C840BF6E9946DBC285CF', X'D9A3CD467C38E24A80A2972BB7486CA7', X'D9A3CD467C38E24A80A2972BB7486CA7', 0, NULL, NULL, 2, NULL, 0, 'Dry fry the cumin and mustard seeds until they start to pop. Remove from heat and grind in pestle and mortar.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7BA81963C5E6548A752CE6F0D155390', X'D9A3CD467C38E24A80A2972BB7486CA7', X'D9A3CD467C38E24A80A2972BB7486CA7', 1, NULL, NULL, 2, NULL, 0, 'Fry the onion, garlic, ginger and chilli until it softens and turns brown.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D554A8FDCF4E4699A187B5E6146BFA', X'D9A3CD467C38E24A80A2972BB7486CA7', X'D9A3CD467C38E24A80A2972BB7486CA7', 2, NULL, NULL, 2, NULL, 0, 'Add the dry spices (or curry powder) to the cumin and mustard and mix in the tomato purée to make a paste. Add this to the pan and continue to fry for 1-2 mins.');
INSERT INTO `VWR INTERNAL Tilda recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77A2B68DF423A241BFF09D99D3B91FD4', X'D9A3CD467C38E24A80A2972BB7486CA7', X'D9A3CD467C38E24A80A2972BB7486CA7', 3, NULL, NULL, 2, NULL, 0, 'Pour in the water and simmer on a medium heat for a further 20-25 mins and add the leftover turkey.');
