INSERT INTO `VWR INTERNAL Umamigirl recipeCategory` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'02F9134AA2BE4E4E9B0A14C8B1352AC0', X'D2F1A18EBC32C04AA7890CAD4734AE27', X'D2F1A18EBC32C04AA7890CAD4734AE27', 39, NULL, NULL, 2, NULL, 0);
INSERT INTO `VWR INTERNAL Umamigirl recipeCategory` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'754707C4B0FFCE45BB809BF17BBBF349', X'D2F1A18EBC32C04AA7890CAD4734AE27', X'D2F1A18EBC32C04AA7890CAD4734AE27', 40, NULL, NULL, 2, NULL, 0);
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B9ED9503CF7334E992AFA8496FDCF03', X'85D2E3AA84F5C84FBD7F59515B1EF129', X'85D2E3AA84F5C84FBD7F59515B1EF129', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D7E355E0027E34B8DE3ED9364480384', X'85D2E3AA84F5C84FBD7F59515B1EF129', X'85D2E3AA84F5C84FBD7F59515B1EF129', 1, NULL, NULL, 2, NULL, 0, 'Chop the bacon into bite-sized pieces and place in a 12-inch nonstick frying pan set over medium-high heat. Cook, stirring frequently, until brown and crisp-tender. Remove bacon to paper towels to drain and pour off all but about two tablespoons fat from pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EF075C0971C964892844FEF9259F60F', X'85D2E3AA84F5C84FBD7F59515B1EF129', X'85D2E3AA84F5C84FBD7F59515B1EF129', 2, NULL, NULL, 2, NULL, 0, 'Add broccolini to pan and stir to coat with fat. Cook uncovered for a minute or two, until bright green. Add two tablespoons water to pan and cover. Cook until water is evaporated and broccolini is just tender, a few minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAE978A030CA3B4989FB975B7D77FE30', X'85D2E3AA84F5C84FBD7F59515B1EF129', X'85D2E3AA84F5C84FBD7F59515B1EF129', 3, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a mixing bowl, pour in cream, and beat well with a fork. Pour egg mixture into pan. Sprinkle evenly with cheese. Transfer pan to oven and bake until eggs are set, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBCF93CDC956D74EA2176AC87E3AAD98', X'85D2E3AA84F5C84FBD7F59515B1EF129', X'85D2E3AA84F5C84FBD7F59515B1EF129', 4, NULL, NULL, 2, NULL, 0, 'Cool slightly and then top with broccoli sprouts, tomato pieces, pine nuts, and a drizzle of balsamic glaze. Sprinkle with salt and pepper. Cut into wedges to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'462B4BD830BAE94898A15A588158F309', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 0, NULL, NULL, 2, NULL, 0, 'Have egg and milk at room temperature for optimum pancake fluffiness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E4183F9D87F9C4D815DEBFE2FF13CC8', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 1, NULL, NULL, 2, NULL, 0, 'Shred the zucchini on the large holes of a box grater or in a food processor with the shredding disk. Place the shredded zucchini in a colander in the sink and sprinkle with the salt. Toss to combine. Let drain for 30 minutes, then pick up by the handful and squeeze out as much moisture as possible. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A51AF9D6245894FB721D524FC84A42D', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 2, NULL, NULL, 2, NULL, 0, 'Into a large mixing bowl, sift together the flour, sugar, baking powder, cinnamon, and nutmeg.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB7B1D5625CE0A4388597476C2CDCF03', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 3, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the milk, egg, and vanilla. Then whisk in the butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B511FFDD654CDB4EA7665871FF82A5CA', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 4, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients over the dry ingredients and stir together just until combined. Some lumps are fine. Don''t overmix. Then gently fold in the zucchini.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'446F231EE8A4734A8484E2105E4D81E2', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 5, NULL, NULL, 2, NULL, 0, 'Let batter rest for five minutes before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A2A38E8AA1BBC4B882A2D8DE9B7AFB0', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 6, NULL, NULL, 2, NULL, 0, 'Set a griddle or heavy, nonstick pan over medium heat. Coat lightly with safflower oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E7315013F76174895F5787EFB7D2539', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 7, NULL, NULL, 2, NULL, 0, 'When griddle is hot, scoop batter by the approximate 1/4-cupful onto the skillet, leaving plenty of room between pancakes. Cook until the edges of the underside are browned and the top is covered with bubbles that have begun to burst and stay hollow. Flip and cook until the underside is browned. Repeat with remaining batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E78966D8F88D7742AF4BADA0E9C65D51', X'FD07F9241DC5734091DFFD49767B2C86', X'FD07F9241DC5734091DFFD49767B2C86', 8, NULL, NULL, 2, NULL, 0, 'Pancakes are best served right as they come off the griddle, though you can keep them warm in a 200°F oven while you cook the rest if you insist.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89DA67BE78FCCA49A40FCD8ABC89B732', X'D13FC87ECABF6142B0C3CD5334299C35', X'D13FC87ECABF6142B0C3CD5334299C35', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl (or right in the bowl of your stand mixer, if you like), stir together the sourdough starter, water, and sugar with a fork to break up the starter. It doesn''t have to be perfectly incorporated into the water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C11438772707942B3A1DE351B1A3874', X'D13FC87ECABF6142B0C3CD5334299C35', X'D13FC87ECABF6142B0C3CD5334299C35', 1, NULL, NULL, 2, NULL, 0, 'Add the flour and the salt. Mix until there''s no loose flour. I find it''s easiest to start mixing with the fork and then switch to using my hand as the dough stiffens and gets less sticky.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'772395B55E5E434D8FBDB843471A56F5', X'D13FC87ECABF6142B0C3CD5334299C35', X'D13FC87ECABF6142B0C3CD5334299C35', 2, NULL, NULL, 2, NULL, 0, 'Knead the dough for eight minutes. You can do this by hand, but it''s much easier in a stand mixer since this dough is quite stiff. In a stand mixer, use the dough hook on Speed 2.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB745CFD9F26C4419EE5036FE0FB0735', X'D13FC87ECABF6142B0C3CD5334299C35', X'D13FC87ECABF6142B0C3CD5334299C35', 3, NULL, NULL, 2, NULL, 0, 'Clean the bowl and oil it. Use your hands to work the dough into a nice ball and place it in the bowl. Turn it over a few times to coat with oil. Cover with a damp kitchen towel, a plate that''s big enough to sit on the rim of the bowl, or plastic wrap.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6282ED9C048A1B489C5EAE5186DFE1DC', X'D13FC87ECABF6142B0C3CD5334299C35', X'D13FC87ECABF6142B0C3CD5334299C35', 4, NULL, NULL, 2, NULL, 0, 'Let the dough rise for eight hours. At this point, you can make the bagels or stash the dough in the fridge overnight.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A6018E5C09FBE458C4D911D9B6A0C21', X'9A6F380A4A574E4D98C3DA91CD9A37DA', X'9A6F380A4A574E4D98C3DA91CD9A37DA', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, use a fork to beat together the eggs, milk, salt, and pepper. Mix until the eggs are an even light yellow color, but don''t go out of your way to incorporate any more air than you need to. (That''s one reason to use a fork instead of a whisk to mix.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90E306DEACED4D4BB036FFA9953AEFE7', X'9A6F380A4A574E4D98C3DA91CD9A37DA', X'9A6F380A4A574E4D98C3DA91CD9A37DA', 1, NULL, NULL, 2, NULL, 0, 'Set a small, good-quality nonstick pan over low heat. For four to eight eggs, an 8-inch pan works well. It''s easier to control the heat when you have a bit of depth to the eggs rather than a very shallow layer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C023BF7E67A5A438CEDA44C1DBAFB9C', X'9A6F380A4A574E4D98C3DA91CD9A37DA', X'9A6F380A4A574E4D98C3DA91CD9A37DA', 2, NULL, NULL, 2, NULL, 0, 'Add the butter to the pan and swipe it around with the spatula to lightly coat the whole pan. When there''s a light layer of melted butter on the pan but most of the butter is still solid, pour in the egg mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A23CEC5416BFD428BAA9835A789A79F', X'9A6F380A4A574E4D98C3DA91CD9A37DA', X'9A6F380A4A574E4D98C3DA91CD9A37DA', 3, NULL, NULL, 2, NULL, 0, 'From now until approximately INFINITY, stir the eggs constantly with a wooden spoon or silicone spatula. This will take about 15-20 minutes total, depending on the heat of the low setting on your stove. (For reference: On my professional gas range, I use the low setting rather than the even lower simmer. On the little electric burner that I use to make videos, I started on the low setting and ended up turning it up just a touch to somewhere between low and medium-low.) For the first four to five minutes, it will seem like nothing is happening. The eggs will remain liquid with no curds forming. But slowly bringing the eggs up to temperature will make all the difference in the final process, so don''t rush it. Just keep stirring constantly, running your spatula around the entire bottom and sides of the pan. The color will go from brighter to darker yellow over these first few minutes, but you might not notice since it will be very gradual. If nothing at all has happened after about five or six minutes, raise the heat very slightly and note that you can start on that slightly higher setting next time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53478F9586EA9B4B801588773DE64553', X'9A6F380A4A574E4D98C3DA91CD9A37DA', X'9A6F380A4A574E4D98C3DA91CD9A37DA', 4, NULL, NULL, 2, NULL, 0, 'At some point, very small curds will start to form. Just keep doing the same thing you''ve been doing — stirring until death do us part — while the eggs begin to thicken into a silky, custardy scramble. Keep going until the eggs are just thick enough to stay for a moment on the side of the pan that you push them toward. You can watch the video for visual cues.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB8ABC48F5F3BD47AC2D88FC6D58A99B', X'9A6F380A4A574E4D98C3DA91CD9A37DA', X'9A6F380A4A574E4D98C3DA91CD9A37DA', 5, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in the cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16D52AC70DD35546AC1D966493822D5F', X'9A6F380A4A574E4D98C3DA91CD9A37DA', X'9A6F380A4A574E4D98C3DA91CD9A37DA', 6, NULL, NULL, 2, NULL, 0, 'Slide the eggs on to serving plates (or into small bowls if you like) and serve. We like to garnish ours with flaky sea salt, more freshly ground black pepper, and maybe a sprinkle of minced chives, ramps, or parsley if we''re in the mood.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D497217F25C034CA0E0BB8DDAC08E71', X'062D86A598C0FA4C9E1D12346BEAFF92', X'062D86A598C0FA4C9E1D12346BEAFF92', 0, NULL, NULL, 2, NULL, 0, 'Crack eggs into a small mixing bowl and pour in cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB54935BC4FFB42963FBCFFE5AA0CB9', X'062D86A598C0FA4C9E1D12346BEAFF92', X'062D86A598C0FA4C9E1D12346BEAFF92', 1, NULL, NULL, 2, NULL, 0, 'With a fork, beat the eggs until the contents of the bowl are an even, creamy yellow, with no visible bits of yolk or white.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7A6FAE9CAD9804C92ECE7F4945E90B2', X'062D86A598C0FA4C9E1D12346BEAFF92', X'062D86A598C0FA4C9E1D12346BEAFF92', 2, NULL, NULL, 2, NULL, 0, 'Set a heavy 8- or 10-inch nonstick pan over medium-low heat. Add the butter. When it''s melted and foamy, pour in the eggs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BC83DE2E4B5B7479CE9B910ADBFB7EF', X'062D86A598C0FA4C9E1D12346BEAFF92', X'062D86A598C0FA4C9E1D12346BEAFF92', 3, NULL, NULL, 2, NULL, 0, 'Let the eggs sit undisturbed just until you can see around the edges that they''ve set on the underside. Then, in a few gentle motions, use a silicone spatula to pull the edges of the eggs into the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EB5A92C1544F64A8B091620ED484798', X'062D86A598C0FA4C9E1D12346BEAFF92', X'062D86A598C0FA4C9E1D12346BEAFF92', 4, NULL, NULL, 2, NULL, 0, 'Repeat this process — letting the underside set, and then pulling the edges to the center — once or twice more as needed. You can lift the pan up off the heat a bit to control the temperature so the eggs set without browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CC7064535936548A0541E791637635E', X'062D86A598C0FA4C9E1D12346BEAFF92', X'062D86A598C0FA4C9E1D12346BEAFF92', 5, NULL, NULL, 2, NULL, 0, 'Just before you think the eggs are perfectly cooked, remove the pan from the heat. They''ll keep cooking a bit with their residual heat and end up just right.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287108078658194E871954C36BAE2CA6', X'062D86A598C0FA4C9E1D12346BEAFF92', X'062D86A598C0FA4C9E1D12346BEAFF92', 6, NULL, NULL, 2, NULL, 0, 'Slide the eggs onto a plate and serve right away, sprinkled with salt and freshly ground black pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F47CAE43B76574A91C4F6CA2227BD89', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 375°F with a rack in the center. Grab a 12-inch frying pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F883329093BBE418DB5C5C75342BEC3', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, beat the eggs together with the liquid (if using) and a little bit of salt and pepper until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E749FA91057F214AB96CBA90A9E26B9C', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', 2, NULL, NULL, 2, NULL, 0, 'Chop meat, if using, into bite-sized pieces. The meat should be cooked by the time it goes into the frittata, so if it''s raw when you''re starting, cook it in the frying pan now. If it leaves more than about a tablespoon of fat in the pan drain the fat before proceeding. At this point you can leave the meat in the pan or remove it and add it back later.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5021E1901029164FAECFBA5B83B8BEE5', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', 3, NULL, NULL, 2, NULL, 0, 'Chop vegetables into bite-sized pieces. Just like the meat, most vegetables should be cooked by the time they go into a frittata. If they aren''t yet cooked, give them a sauté in the frying pan at this point.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4706A59DCF3E24DA23C7AEF0D3DC67C', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', 4, NULL, NULL, 2, NULL, 0, 'With the cooked meats and vegetables in the pan and the pan set over medium heat on the stove, pour in the beaten egg mixture and sprinkle with cheese. Let the frittata cook undisturbed until the bottom is set, about a minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DA1F3789B43514089E50426C129D1FA', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', 5, NULL, NULL, 2, NULL, 0, 'Transfer the pan to the oven and bake until eggs are just set, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D992DEE52ABF2F4C9B588560D4D83EF3', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', X'A1DD8D7A9B01DC4AAAB5544E359C3F92', 6, NULL, NULL, 2, NULL, 0, 'That''s it! Cut your frittata into wedges, and it''s ready to eat warm or at room temperature. Leftovers keep well, tightly wrapped in a cold fridge, for a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFD6101E5D29E24B97325D0BCBD003B3', X'9DC3E389F94D0E49BDFE440553592EC7', X'9DC3E389F94D0E49BDFE440553592EC7', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil. A 7 1/4 quart Dutch oven fits a steamer basket perfectly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31F5E051E9303A4A8543A68B702B0D8E', X'9DC3E389F94D0E49BDFE440553592EC7', X'9DC3E389F94D0E49BDFE440553592EC7', 1, NULL, NULL, 2, NULL, 0, 'Arrange eggs in a steamer basket. Lower basket into water and cover pot. Set timer for 12 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40D09DE4177B0A40B7811EB0BC7A1E84', X'9DC3E389F94D0E49BDFE440553592EC7', X'9DC3E389F94D0E49BDFE440553592EC7', 2, NULL, NULL, 2, NULL, 0, 'As soon as water begins to bubble vigorously again, reduce heat to low to maintain a bare simmer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DE0B76C82F9DE40B57C398B4514C103', X'9DC3E389F94D0E49BDFE440553592EC7', X'9DC3E389F94D0E49BDFE440553592EC7', 3, NULL, NULL, 2, NULL, 0, 'Fill a large bowl with ice water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EACB0CD785FAB49B77B317260A538AF', X'9DC3E389F94D0E49BDFE440553592EC7', X'9DC3E389F94D0E49BDFE440553592EC7', 4, NULL, NULL, 2, NULL, 0, 'When timer rings, pull steamer out of pot and tip eggs into ice water. Cool for 15 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99B943BDB6D4254DBEFE5A4139DAC441', X'9DC3E389F94D0E49BDFE440553592EC7', X'9DC3E389F94D0E49BDFE440553592EC7', 5, NULL, NULL, 2, NULL, 0, 'Peel carefully and give a quick rinse under running water to remove any remaining bits of shell.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DEE2047103A5848A82CECA9154123F7', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 0, NULL, NULL, 2, NULL, 0, 'Have eggs and milk at room temperature for optimum fluffiness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5918BBE53ACBB4EB0F6A1B9CAF0F9EA', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 1, NULL, NULL, 2, NULL, 0, 'Preheat waffle iron. (If yours has multiple doneness settings, try about 3/4 of the way from light to dark.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FBDE79773ED08448F72C3B2299D2159', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 2, NULL, NULL, 2, NULL, 0, 'Sift together flour, sugar, baking powder, and salt into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'234319A85D44B74B91B68119325B1B91', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 3, NULL, NULL, 2, NULL, 0, 'Separate egg whites from yolks. Place whites into a medium mixing bowl and set aside for the moment. Add yolks to milk. (We like to use a 2-cup glass measuring cup to measure the milk and then mix together the wet ingredients right in the measuring cup.) Add vanilla to milk mixture and whisk together until well combined. Pour in slightly cooled melted butter and whisk to thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB055BFD69EC4F468922F03AD99881D1', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 4, NULL, NULL, 2, NULL, 0, 'Pour wet ingredients over dry ingredients and stir together until just combined. Some lumps of flour are fine. Don’t overmix. Let batter rest while you whip the egg whites.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6263A54AAC1E584DBAFA896D2B9D4230', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 5, NULL, NULL, 2, NULL, 0, 'Whip egg whites with an electric mixer to the stiff peak stage. This means they are white and shiny, and if you turn off the mixer and pull it out of the egg whites, the little point at the top of the whites stands straight up. There is such a thing as overbeating egg whites, so stop once you reach this stage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93D6C63DE679654EBEBFD267D3733248', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 6, NULL, NULL, 2, NULL, 0, 'Scrape egg whites into the bowl with the batter and use a silicone spatula to gently fold the whites into the batter. (See note.) Leave some streaks of whipped egg white throughout the batter. Don’t overmix or the egg whites will deflate too much.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F20CBD40948493459DA4E3994717F5A3', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 7, NULL, NULL, 2, NULL, 0, 'Spray waffle iron with nonstick cooking spray (or brush very lightly with vegetable oil or melted butter).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE9F76A8F9C14C4780692B4D381B23BD', X'54F083A554160A459820E427D1BF68AC', X'54F083A554160A459820E427D1BF68AC', 8, NULL, NULL, 2, NULL, 0, 'Add batter to waffle iron according to manufacturer’s instructions and cook until waffles are golden brown. Serve immediately, or keep warm in a 200°F oven while you finish cooking. You can place waffles directly on a clean oven rack or place them on a baking rack set into a rimmed baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE3122E646D04249B9BDE923F863709F', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Place crust (in foil pie plate) onto a rimmed baking sheet and defrost on countertop for 10 minutes. Prick crust all over the bottom and sides with a fork. (See note.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D35B9A8D7FB5D64D8BD4AC976FA310E8', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 1, NULL, NULL, 2, NULL, 0, 'Bake crust for 10 minutes, then let cool for 10 minutes. (If directions for “blind baking” on your crust’s package vary substantially, you can follow those, but this method should work for most frozen crusts.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE46A5892C583D4593F810EFDD9A7929', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 2, NULL, NULL, 2, NULL, 0, 'Reduce oven temperature to 325°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EFC1B0C90EE0741866289FCE8F0C1F2', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 3, NULL, NULL, 2, NULL, 0, 'To cook the spinach, place it and two tablespoons water in a microwave-safe, lidded container and cook on high until just hot, about four minutes depending on your microwave.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EED95EC44BFE70498712C2188AD916F1', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 4, NULL, NULL, 2, NULL, 0, 'When cool enough to handle, place spinach in a fine-mesh strainer and press out all the liquid you can with a fork or spoon, or with your fingers.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5168B5C77444404BB6E08B9F45D063D6', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 5, NULL, NULL, 2, NULL, 0, 'While crust bakes and spinach cooks, warm olive oil in a medium-sized frying pan over medium heat. Add onion and cook, stirring occasionally, until softened, about five minutes. Off the heat, add drained spinach and 1/4 teaspoon salt to pan and use a fork to mix the spinach and onion together thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D421BF2B7D5FD040A055D56CEBABB7EB', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 6, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat eggs well with a fork. Add cream, milk, remaining 1/2 teaspoon salt, nutmeg, cayenne, and black pepper and mix until well-combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98891F1FA6BC074B84C4F73F0D4694EE', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 7, NULL, NULL, 2, NULL, 0, 'Gently spread spinach mixture and cheese evenly over bottom of crust.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA2BD3EB185D24AB7C6C81305280A2A', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 8, NULL, NULL, 2, NULL, 0, 'Open oven and pull out rack enough to set the partially filled crust (still on rimmed baking sheet) onto the rack. Carefully pour the egg mixture into the crust. It will be very full. Gently slide rack back into place.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'302AF6A871D39B489E55890C8296B4B5', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 9, NULL, NULL, 2, NULL, 0, 'Bake until filling is just set and still slightly jiggly but not sloshy in the center, 30-40 minutes. The center will firm up as it cools, and pulling the quiche out at this stage will prevent it from getting tough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B172A0BE7CA3E444B9B3EA334B65D6B0', X'2DD27AE5521786498896F02671D804ED', X'2DD27AE5521786498896F02671D804ED', 10, NULL, NULL, 2, NULL, 0, 'Let cool for at least 10 minutes before serving, preferably longer. Quiche is even better the next day.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6DFB7595A0C294FAA986A5E9DDC6069', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Place crust (in foil pie plate) onto a rimmed baking sheet and defrost on countertop for 10 minutes. Prick crust all over the bottom and sides with a fork. (See note.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5D0E5B34DB21F4B8D29633B0E5848A6', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 1, NULL, NULL, 2, NULL, 0, 'Bake crust for 10 minutes, then let cool for 10 minutes. (If directions for “blind baking” on your crust’s package vary substantially, you can follow those, but this method should work for most frozen crusts.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDC3277C0778754EBE31102B1762183A', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 2, NULL, NULL, 2, NULL, 0, 'Reduce oven temperature to 325°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'130DA598AE8AA8489637C725B5B05DEA', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 3, NULL, NULL, 2, NULL, 0, 'While crust bakes, set a medium-sized frying pan over medium heat. Add bacon and cook, stirring frequently, until browned and crisped to your liking. Remove bacon from pan and drain on paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF2C34F18A1D8D428C2D0993AD640204', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 4, NULL, NULL, 2, NULL, 0, 'Pour off all but about one tablespoon bacon fat from pan and replace pan over medium heat. Add onion and cook, stirring occasionally, until softened, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9E9BF72BB59994FBFE37F2ED66C40AE', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 5, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat eggs well with a fork. Add cream, milk, salt, nutmeg, cayenne, and black pepper and mix until well-combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C6BD6210E186C448D8B72BF25D6424F', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 6, NULL, NULL, 2, NULL, 0, 'Gently spread onion, bacon, and cheese evenly over bottom of crust.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65CA5F9931C10F4A9F26B321F64ED32A', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 7, NULL, NULL, 2, NULL, 0, 'Open oven and pull out rack enough to set the partially filled crust (still on rimmed baking sheet) onto the rack. Carefully pour the egg mixture into the crust. It will be very full. Gently slide rack back into place.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9561119CA652094F997FF37225D18A24', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 8, NULL, NULL, 2, NULL, 0, 'Bake until filling is just set and still ever so slightly jiggly (but not sloshy) in the center, 30-40 minutes. The center will firm up as it cools, and pulling the quiche out at this stage will prevent it from getting tough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02473E3C5A707E4790AC8B48B2595E50', X'438CD46EADDD954688A85E1E4F12F988', X'438CD46EADDD954688A85E1E4F12F988', 9, NULL, NULL, 2, NULL, 0, 'Let cool for at least 10 minutes before serving, preferably longer. Quiche is even better the next day.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07D95B4898058848BA86921E4A70EA10', X'4169873452E5304D9D4B023CAC689EFC', X'4169873452E5304D9D4B023CAC689EFC', 0, NULL, NULL, 2, NULL, 0, 'Prepare one or two batches of classic deviled eggs, depending on the size of your party and what else you''re serving. Figure on one to two whole eggs per person depending on circumstances. Set them out on a serving tray near the beginning of the buffet line.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75FF056A15405B41830551565FBE1FD9', X'4169873452E5304D9D4B023CAC689EFC', X'4169873452E5304D9D4B023CAC689EFC', 1, NULL, NULL, 2, NULL, 0, 'Set out toppings in small bowls. Be thoughtful about grouping ingredients together so as not to give your guests analysis paralysis, and also about setting out serving utensils where needed. Sauces like sriracha can stay right in the bottle, as long as it''s been reasonably well maintained.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD10F29CA18FBF4A9EAECA9CE04D3CA7', X'4169873452E5304D9D4B023CAC689EFC', X'4169873452E5304D9D4B023CAC689EFC', 2, NULL, NULL, 2, NULL, 0, 'Place a stack of small plates and a stack of napkins at the start of the buffet line. Depending on your toppings, you may want to include forks as well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DC44288A71A7B4DB3C6FE2516CB7B3C', X'6512D9CFA0AF0243A0588755EE85E0C7', X'6512D9CFA0AF0243A0588755EE85E0C7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'367105B4224B9C4D84881F3EB56D102E', X'6512D9CFA0AF0243A0588755EE85E0C7', X'6512D9CFA0AF0243A0588755EE85E0C7', 1, NULL, NULL, 2, NULL, 0, 'Line a rimmed half-sheet pan with aluminum foil if you like. (We sometimes do and sometimes don''t. It helps a bit with cleanup but isn''t strictly necessary.) Arrange bacon in a single layer. If you have to overlap the slices just a little, it''s okay — they''ll shrink.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'249AE029EC44CA42ABF7FC63175FC1B9', X'6512D9CFA0AF0243A0588755EE85E0C7', X'6512D9CFA0AF0243A0588755EE85E0C7', 2, NULL, NULL, 2, NULL, 0, 'Bake for 15-20 minutes, more or less, until crisped to your liking. Watch carefully toward the end. Cooking time will vary depending on thickness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8AFCB35DF824F4597A4B6891E920F32', X'6512D9CFA0AF0243A0588755EE85E0C7', X'6512D9CFA0AF0243A0588755EE85E0C7', 3, NULL, NULL, 2, NULL, 0, 'Drain on paper towels and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCBED00BE9ED894AA6A3D6F1A186BE7A', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 0, NULL, NULL, 2, NULL, 0, 'Have egg and milk at room temperature for optimum pancake fluffiness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0FC9C226EE3A3469F0BD03A3F172184', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 1, NULL, NULL, 2, NULL, 0, 'Into a large mixing bowl, sift together the flour, baking powder, sugar, and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6418A4DCAF381148A79B57140D34DC64', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 2, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the milk, egg, and vanilla. Then whisk in the butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEA675654BA33D4AA579AF5BADCB8E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 3, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients over the dry ingredients and stir together just until combined. Some lumps are fine. Don''t overmix.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FE6AAF0A5371A42A30B5719DF6E678A', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 4, NULL, NULL, 2, NULL, 0, 'Let batter rest for five minutes before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB67FBFC682A3F4ABB046C96009D73E1', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 5, NULL, NULL, 2, NULL, 0, 'Set a griddle or heavy, nonstick pan over medium heat. Coat lightly with safflower oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42927F16545A004F95B7C58A1AC899F6', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 6, NULL, NULL, 2, NULL, 0, 'When griddle is hot, scoop batter by the approximate 1/4-cupful onto the skillet, leaving plenty of room between pancakes. Cook until the edges of the underside are browned and the top is covered with bubbles that have begun to burst and stay hollow. Flip and cook until the underside is browned. Repeat with remaining batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'245BF9EFD6B58F4CB5BCD808B8920B1C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', X'B8E5FFDC656F4D419E0DDC0FCC104E4C', 7, NULL, NULL, 2, NULL, 0, 'Pancakes are best served right as they come off the griddle, though you can keep them warm in a 200°F oven while you cook the rest if you insist.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'362F2A65847DB549975182613EF518AD', X'2E512584E75D5349B87CF5EA0F5052FA', X'2E512584E75D5349B87CF5EA0F5052FA', 0, NULL, NULL, 2, NULL, 0, 'Preheat waffle iron.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3BCA0BAD7B1484A89FD9CBDD92A5943', X'2E512584E75D5349B87CF5EA0F5052FA', X'2E512584E75D5349B87CF5EA0F5052FA', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the all-purpose flour, white whole wheat flour, baking powder, baking soda, salt, cinnamon, ginger, nutmeg, and cloves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'918C4F10AD4E1F408A54F6E8917F5C89', X'2E512584E75D5349B87CF5EA0F5052FA', X'2E512584E75D5349B87CF5EA0F5052FA', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the eggs, milk, sour cream, melted butter, and molasses.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BCAF7110CA285468C342B3994D4EA05', X'2E512584E75D5349B87CF5EA0F5052FA', X'2E512584E75D5349B87CF5EA0F5052FA', 3, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients into the dry ingredients and mix together until just combined. A few lumps are okay, just try to avoid any substantial pockets of unmixed dry ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CFB798F16290C4AA5EAA628A1CCC2C4', X'2E512584E75D5349B87CF5EA0F5052FA', X'2E512584E75D5349B87CF5EA0F5052FA', 4, NULL, NULL, 2, NULL, 0, 'Spray waffle iron lightly with nonstick cooking spray. Pour batter into waffle iron and cook according to manufacturer''s instructions. In our waffle iron (recommended below), we use a scant 1/2 cup batter for each waffle and set the iron to medium-dark for these waffles.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F08E3B53021DE44694917D83B074841C', X'2E512584E75D5349B87CF5EA0F5052FA', X'2E512584E75D5349B87CF5EA0F5052FA', 5, NULL, NULL, 2, NULL, 0, 'Serve with a dusting of powdered sugar, maple syrup, and/or lightly sweetened whipped cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'705C7A94D894A84B8ACF085F54BDAD8A', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', 0, NULL, NULL, 2, NULL, 0, 'Heat one tablespoon of the olive oil over medium heat in a nonstick or well-seasoned frying pan. Add the onion and cook, stirring occasionally, until starting to soften, about 5 minutes. Add the garlic and cook for one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80AA87E4E1EE4C4FA628EF4F45C07E87', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', 1, NULL, NULL, 2, NULL, 0, 'Add the spinach, in batches if necessary, and cook until wilted. If there''s a lot of liquid in the pan at this stage, you may wish to pour it off. (Sometimes there is, sometimes there isn''t -- depends on the spinach.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'490F7E85678F464C8F87751ACD4DA6CC', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', 2, NULL, NULL, 2, NULL, 0, 'Add the nutmeg, cayenne, heavy cream, cream cheese, and some salt and pepper. Stir to combine well. Simmer until the sauce is reduced enough to mostly cling to the spinach. Taste for salt and pepper and divide between two shallow bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B82D5BF63BEFBE44B9FDE0C08F86B181', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', X'6A7C3EC3C148CB47ABBE0F6A5EAFF0F8', 3, NULL, NULL, 2, NULL, 0, 'Wipe out the frying pan. Add the remaining tablespoon of olive oil. Fry the eggs in the pan to your liking, and slide two eggs into each bowl. Cut the toast into strips (`soldiers,` as the English call them) for dipping into the yolk and cream, and garnish each bowl with a few. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71473119611B004BA2A2212A660F9749', X'6FDFD55045A62A40B9A49EC9BD01108B', X'6FDFD55045A62A40B9A49EC9BD01108B', 0, NULL, NULL, 2, NULL, 0, 'Warm olive oil in a 12-inch nonstick pan over medium-high heat. Add shallot, pepper and tomatoes and cook, stirring occasionally, until peppers have softened and tomatoes have burst, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15A4CD0A4FF4204AAE753D295F81FDE1', X'6FDFD55045A62A40B9A49EC9BD01108B', X'6FDFD55045A62A40B9A49EC9BD01108B', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, crack eggs into a bowl and scramble.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'647D279EBEA18E468BA663DA344A4AF1', X'6FDFD55045A62A40B9A49EC9BD01108B', X'6FDFD55045A62A40B9A49EC9BD01108B', 2, NULL, NULL, 2, NULL, 0, 'Stir spinach into pan and cook until wilted, about 2 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57D7EED388E5454C9185F696FA965D90', X'6FDFD55045A62A40B9A49EC9BD01108B', X'6FDFD55045A62A40B9A49EC9BD01108B', 3, NULL, NULL, 2, NULL, 0, 'Pour in eggs and sprinkle with McCormick breakfast seasoning. Cook, stirring frequently, until eggs are almost set.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E1DF3FCF33154F9A849E8B7391A1BC', X'6FDFD55045A62A40B9A49EC9BD01108B', X'6FDFD55045A62A40B9A49EC9BD01108B', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle in feta and olives and continue cooking until eggs are set to your liking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEE2AE88CF04F841ACA94E63F7103BAB', X'6FDFD55045A62A40B9A49EC9BD01108B', X'6FDFD55045A62A40B9A49EC9BD01108B', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle with freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D21E7638837A4C83CBD8D25E5DA09E', X'1EEFCC720A105747892F8BB60EA9CC14', X'1EEFCC720A105747892F8BB60EA9CC14', 0, NULL, NULL, 2, NULL, 0, 'Fill a small pot about halfway with water and bring to a boil. Carefully lower the eggs into the pot one at a time. (I like to use a small strainer or silicone tongs, but you can also use a spoon.) Set a timer for seven minutes. Try to keep the water at a brisk simmer without jostling the eggs around more than necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'346C41A92E0C1F439BE02A447511DE41', X'1EEFCC720A105747892F8BB60EA9CC14', X'1EEFCC720A105747892F8BB60EA9CC14', 1, NULL, NULL, 2, NULL, 0, 'While the eggs boil, fill a medium bowl about halfway with lots of ice and cold water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB859CBEE413114293415C03024FF63A', X'1EEFCC720A105747892F8BB60EA9CC14', X'1EEFCC720A105747892F8BB60EA9CC14', 2, NULL, NULL, 2, NULL, 0, 'When the timer rings, take the eggs out right away, place them in the ice water, and let sit until they''re cool enough to peel. They should be nice and easy to peel. Use right away or store, peeled and covered, in the fridge for a day or two. You can reheat peeled eggs in the microwave for 30 seconds or so or with a minute''s dip in hot water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31FD090FCCB31D44869E7CC177D40238', X'A107DC4A86D3EA41A97E71E4F9B79E94', X'A107DC4A86D3EA41A97E71E4F9B79E94', 0, NULL, NULL, 2, NULL, 0, 'In a 12-inch nonstick skillet, heat the olive oil on medium-high. Add shallot and garlic and cook for a few seconds. Add spinach by the handful as it fits in the pan, sprinkle with salt and stir frequently until wilted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB532455A9888146B34C6EE5195ADDF4', X'A107DC4A86D3EA41A97E71E4F9B79E94', X'A107DC4A86D3EA41A97E71E4F9B79E94', 1, NULL, NULL, 2, NULL, 0, 'To poach the eggs, fill a wide sauté pan halfway with water. (A pan like this is ideal.) Add vinegar and bring water to a brisk simmer, then reduce heat so the water is virtually still. Crack each egg into a small bowl and tip carefully into the water, submerging the bowl a bit as you pour. I like to add the eggs in a clockwise circle starting near the handle so I remember in which order to remove them for even cooking. Cook undisturbed for four minutes, until whites are set and yolks are runny. Remove eggs from pan with a strainer spoon (something like this). Gently place eggs on a paper towel-lined plate and dab the tops to remove excess water. Cut away any scraggly whites if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E47C39095733774295924BDC4326FAE5', X'A107DC4A86D3EA41A97E71E4F9B79E94', X'A107DC4A86D3EA41A97E71E4F9B79E94', 2, NULL, NULL, 2, NULL, 0, 'While the eggs poach, toast the bread and make the hollandaise.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E64D6DF16CB1459F91ADE9AD29E5A8', X'A107DC4A86D3EA41A97E71E4F9B79E94', X'A107DC4A86D3EA41A97E71E4F9B79E94', 3, NULL, NULL, 2, NULL, 0, 'To serve, on each of four plates, top a piece of toast with some spinach, making a little bed for the eggs. Add two poached eggs. Spoon some Hollandaise sauce overtop. Finish with freshly ground black pepper and some parsley if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBEF235862E8304C851B684DB8694358', X'058C3E6277682545846CA9035DA8A893', X'058C3E6277682545846CA9035DA8A893', 0, NULL, NULL, 2, NULL, 0, 'Split the English muffins and toast to your liking. (Lightly toasted, gently crisp on the outside and tender inside if you''re looking for a recommendation.) Divide among 4 plates. Drape a slice of prosciutto over each English muffin half while it''s still hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'283BEDF067987F4A93F75CAE76432F5A', X'058C3E6277682545846CA9035DA8A893', X'058C3E6277682545846CA9035DA8A893', 1, NULL, NULL, 2, NULL, 0, 'To poach the eggs, fill a wide sauté pan halfway with water. (A pan like this is ideal.) Add vinegar and bring water to a brisk simmer, then reduce heat so the water is virtually still. Crack each egg into a small bowl and tip carefully into the water, submerging the bowl a bit as you pour. I like to add the eggs in a clockwise circle starting near the handle so I remember in which order to remove them for even cooking. Cook undisturbed for four minutes, until whites are set and yolks are runny. Remove eggs from pan with a strainer spoon (something like this). Gently place eggs on a paper towel-lined plate and dab the tops to remove excess water. Cut away any scraggly whites if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2656D048EA9E524A96AA411D7A7E4F28', X'058C3E6277682545846CA9035DA8A893', X'058C3E6277682545846CA9035DA8A893', 2, NULL, NULL, 2, NULL, 0, 'While the eggs poach, make the hollandaise.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'673D5F7E2804064C9E5469414640C0E6', X'058C3E6277682545846CA9035DA8A893', X'058C3E6277682545846CA9035DA8A893', 3, NULL, NULL, 2, NULL, 0, 'To serve, place a poached egg over each prosciutto-draped English muffin half and spoon some Hollandaise sauce overtop. Finish with freshly ground black pepper and some parsley if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB7051F39E083147B8FBB187B650FB33', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BBA4CFE6B8E8B4F8897E0633A2EE15C', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 1, NULL, NULL, 2, NULL, 0, 'Cut the baguette into six sections and halve each section lengthwise.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'295261951C716147B8C098EDCDD95CB3', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 2, NULL, NULL, 2, NULL, 0, 'Line a sheet pan with parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E5A420DC53F1C4598E95CE6EC93C1A5', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 3, NULL, NULL, 2, NULL, 0, 'Place sections, cut sides up, in a closely spaced single layer on the pan, leaving some room for the apricots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'248BB4AA411432469652F5390F582EA1', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together two tablespoons of the apricot preserves and the dried apricots until the apricots are evenly coated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05A53324B8E0004A95E0760EB58B44EA', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 5, NULL, NULL, 2, NULL, 0, 'Place apricots in a single layer in the remaining space on the sheet pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD773E1DB4454408B5AD7916252E334', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 6, NULL, NULL, 2, NULL, 0, 'Bake for about 15 minutes, until bread is lightly toasted and apricots are a bit sticky and jammy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E474FDE0B5545478FB8DD55079EECD8', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 7, NULL, NULL, 2, NULL, 0, 'Spread each baguette piece with some of the remaining apricot preserves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B4762CB6B1E034F81458FF9DFFCC650', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 8, NULL, NULL, 2, NULL, 0, 'Drape with a slice of prosciutto and top with a slice of brie and a couple of jammy apricots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D866382AA37A264F8DE03C033BA48AD0', X'121FA622799B1F478BEE9F75D7E1B499', X'121FA622799B1F478BEE9F75D7E1B499', 9, NULL, NULL, 2, NULL, 0, 'Garnish with thyme and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8592907929854342BA9FC31D450FBCFC', X'EB6DD3F3147E88409C64B3092B5D706C', X'EB6DD3F3147E88409C64B3092B5D706C', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil over medium-high heat in a 10-inch nonstick skillet. Add peppers and onions along with a good pinch of salt. Cook for a few minutes, until softened to your liking. Meanwhile, scramble the eggs in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A047737143F9148A4FEE8C909748CCC', X'EB6DD3F3147E88409C64B3092B5D706C', X'EB6DD3F3147E88409C64B3092B5D706C', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low. Pour eggs into the skillet and cook, pulling to the center with a silicone spatula as the set underneath, until just cooked through. Divide between two plates and top with cheese, cilantro, hot sauce to taste as well as more salt if you like and some freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6077C46E467F448A53C1E872E62C93A', X'CF6F959ABCCB744686E4422DCD8D1518', X'CF6F959ABCCB744686E4422DCD8D1518', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, stir together the flour, corn meal, cornstarch, baking powder, baking soda, salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9203FDAB43F4E4995FF7A373A2E3C9C', X'CF6F959ABCCB744686E4422DCD8D1518', X'CF6F959ABCCB744686E4422DCD8D1518', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl or glass measuring cup, whisk together the buttermilk and eggs. Pour into bowl with dry ingredients and mix gently but thoroughly. Pour in the melted butter and stir to incorporate. Then stir in the cheddar and chives.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C1D4F095CFD3846B46DE301BE061228', X'CF6F959ABCCB744686E4422DCD8D1518', X'CF6F959ABCCB744686E4422DCD8D1518', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 200°F with a rack in the center. Preheat waffle iron to medium or medium-dark setting. (Waffle irons vary tremendously, so you may have to experiment a little.) Pour in as much batter as your waffle iron''s instructions recommend and cook until golden. Gently remove each waffle from iron and place directly on oven rack. The waffles will still be slightly soft when they come out of the iron but will crisp up on the outside and stay tender on the inside while they rest in the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43DB2861EC50694CB804ED1F37CFDC12', X'CF6F959ABCCB744686E4422DCD8D1518', X'CF6F959ABCCB744686E4422DCD8D1518', 3, NULL, NULL, 2, NULL, 0, 'When all waffles are cooked and have had a couple of minutes in the oven, serve with fried eggs on top and a dollop of sour cream if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ED6E5F2AB7F974FA722BE1EE4FFFEAC', X'37366404DEE8994C91DE3451557C4476', X'37366404DEE8994C91DE3451557C4476', 0, NULL, NULL, 2, NULL, 0, 'Fit a food processor with the slicing disk. Feed Brussels sprouts through the feed tube to shred. (If you don''t have a food processor, you can carefully...and tediously...use a mandoline or slice sprouts with a knife.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B5E9C21F46444B9F6D48ED6F8B3E01', X'37366404DEE8994C91DE3451557C4476', X'37366404DEE8994C91DE3451557C4476', 1, NULL, NULL, 2, NULL, 0, 'Dice the bacon and place in a 12-inch skillet set over medium heat. Cook, stirring occasionally, until bacon is just about cooked to your liking. Spoon out and discard all but 2 tablespoons of fat from the pan. Add shallot and cook for two minutes or so, until becoming translucent. Stir in shredded Brussels sprouts and cook, stirring from time to time, until wilted and just tender. Off the heat, add 1 tablespoon of the vinegar and some freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D095281EBA627A4F8FD8A703F5B1F90B', X'37366404DEE8994C91DE3451557C4476', X'37366404DEE8994C91DE3451557C4476', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a pan of water to the gentlest simmer with the remaining two tablespoons of vinegar. Carefully crack eggs into the water, leaving plenty of space in between, and cook for 4 minutes. Divide hash between two shallow bowls and top with eggs. Sprinkle with salt and pepper and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DC746A9D733764E90BCBC9DF592CEB2', X'1EDD6A048A9B1B4E86A2859A74C92A41', X'1EDD6A048A9B1B4E86A2859A74C92A41', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Cut potatoes into cubes of about 3/4-inch. Place in a 12-inch cast iron skillet. Toss with the olive oil, salt, a few good grinds of pepper, the garlic, rosemary and thyme. Roast for 30 minutes, stirring after 20.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFE2BF3CFE1FAF4999C51C8986B95372', X'1EDD6A048A9B1B4E86A2859A74C92A41', X'1EDD6A048A9B1B4E86A2859A74C92A41', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, remove stems from kale and tear leaves into bite-sized pieces. After 30 minutes, stir kale into pan and roast 10 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5F8DF9670BCFA43823E70EE16EEE6DF', X'1EDD6A048A9B1B4E86A2859A74C92A41', X'1EDD6A048A9B1B4E86A2859A74C92A41', 2, NULL, NULL, 2, NULL, 0, 'Remove rosemary and thyme sprigs. Sprinkle feta over skillet contents, then crack in eggs. Bake for about 15 minutes, checking after 10, until eggs are set to your liking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F90B3678B5234243943DE1B64D651C82', X'1EDD6A048A9B1B4E86A2859A74C92A41', X'1EDD6A048A9B1B4E86A2859A74C92A41', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle with parsley and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30893CA0B54BE44ABCEDF2CD03E13EFE', X'31E3F2EE2AC916429C9509700BFA0CC8', X'31E3F2EE2AC916429C9509700BFA0CC8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DEB18FF3C48BF468C8DD047103F5233', X'31E3F2EE2AC916429C9509700BFA0CC8', X'31E3F2EE2AC916429C9509700BFA0CC8', 1, NULL, NULL, 2, NULL, 0, 'Warm the olive oil in a 10-inch nonstick skillet over medium heat. Add shallot, garlic and a good pinch of salt and cook, stirring constantly, for a minute, until very fragrant. Add spinach and cook, stirring, until wilted and dramatically reduced in volume but still bright green, about two minutes more. Off the heat, stir in the quinoa and feta and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABA0CDCB8D76274DBC7747B15CEA86C6', X'31E3F2EE2AC916429C9509700BFA0CC8', X'31E3F2EE2AC916429C9509700BFA0CC8', 2, NULL, NULL, 2, NULL, 0, 'Cut the butter into 12 pieces and place one into each muffin cup. Place muffin tin in oven for 5 minutes to melt butter. Meanwhile, scramble the eggs in a medium bowl with the cream, 1/4 teaspoon salt and a few good grinds black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE5D5E36317D4B4E9C6DA5E241771009', X'31E3F2EE2AC916429C9509700BFA0CC8', X'31E3F2EE2AC916429C9509700BFA0CC8', 3, NULL, NULL, 2, NULL, 0, 'Working quickly and carefully, brush melted butter to coat the bottoms and sides of the muffin cups. Pour eggs evenly into muffin cups, filling each about halfway. Spoon a roughly even amount of spinach-quinoa mixture into each muffin cup. Bake for 20-25 minutes, until eggs are set. Cool slightly before gently removing from pan. Serve immediately or cool completely on a rack before storing tightly covered in the fridge for up to a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12A5AFC406F088419816F2656F1B4F56', X'3161F3E9BC4E9240BC26441E037BE159', X'3161F3E9BC4E9240BC26441E037BE159', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil over medium heat in a heavy 12-inch frying pan with tall sides and a lid (which you''ll use later). Add onion and red bell pepper along with a good pinch of the salt and some freshly ground black pepper and cook, stirring occasionally, for about 5 minutes, until somewhat tender. Add garlic, cumin, paprika and pepper flakes and cook, stirring frequently, two minutes more. Add tomatoes and remaining salt and simmer until slightly thickened, about 15 minutes. Stir in feta.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2937FCE63FDEC44385F09FFA7D443E64', X'3161F3E9BC4E9240BC26441E037BE159', X'3161F3E9BC4E9240BC26441E037BE159', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low and, working one at a time, use a spoon to make a small well for each egg and crack egg into it. Cover pan and simmer for 10-15 minutes, until whites are set. (See note.) Remove from heat and sprinkle with parsley and more salt and pepper. Serve with crusty bread or pita.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F55CE84BBB81E4A8A524FCE29B6C45E', X'A79C4535DD044141BE0A60778547338B', X'A79C4535DD044141BE0A60778547338B', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in the braiser or skillet over medium-high heat. Add the onion, peppers, and shiitakes all at once, along with 1/2 teaspoon of the salt. Cook, stirring from time to time, for about 10 minutes, until the vegetables have softened, browned a bit, and decreased in volume by at least half. Remove from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8727165BB789E4392EDC9F79B012B34', X'A79C4535DD044141BE0A60778547338B', X'A79C4535DD044141BE0A60778547338B', 1, NULL, NULL, 2, NULL, 0, 'While the other vegetables cook, place the defrosted broccoli on a thick layer of paper towels and cover with another thick layer. When the veggies in the skillet have cooked as noted above, press down gently on the paper towels to remove moisture from the broccoli. The add broccoli to the skillet and stir to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12F89BD8A41DB149B10A4E55C732DBF5', X'A79C4535DD044141BE0A60778547338B', X'A79C4535DD044141BE0A60778547338B', 2, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a large mixing bowl and beat well with a fork. Stir in milk, nutmeg, cayenne, and a few good grinds black pepper. Add bread cubes and stir to moisten. Then stir in all the vegetables, half the gruyere and half the parmesan or pecorino. Pour the whole mixture back into the braiser or into the baking pan and sprinkle with the remaining cheese. Cover and refrigerate overnight or for at least a few hours before proceeding. Remove from fridge about an hour before baking if possible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E403C8C429B3D2438EC9225076EC560F', X'A79C4535DD044141BE0A60778547338B', X'A79C4535DD044141BE0A60778547338B', 3, NULL, NULL, 2, NULL, 0, 'An hour before serving time, preheat oven to 375°F with a rack in the center. Bake uncovered for about 40 minutes, until puffed and golden. Serve hot or even at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A08C3E540BD896439007F4B0868977BB', X'A367A18F5A9F6C4C95FC57A98BE3EAD0', X'A367A18F5A9F6C4C95FC57A98BE3EAD0', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil over medium-high heat in a nonstick frying pan. Add the pepper, onion and tortilla strips and cook, stirring occasionally, until vegetables are tender and everything is browned in spots, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B45439B18EEE0B459B8E786E701B9269', X'A367A18F5A9F6C4C95FC57A98BE3EAD0', X'A367A18F5A9F6C4C95FC57A98BE3EAD0', 1, NULL, NULL, 2, NULL, 0, 'Beat eggs in a small bowl. Pour into skillet, add cheese and salsa, and give it all a quick scramble, lifting from heat as necessary to cook gently, until the eggs are set.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D65C0E169D61F47954B098D60C62D1D', X'A367A18F5A9F6C4C95FC57A98BE3EAD0', X'A367A18F5A9F6C4C95FC57A98BE3EAD0', 2, NULL, NULL, 2, NULL, 0, 'Place migas on a plate and garnish with cilantro and hot sauce to taste. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B8B323C6EF88488F06391958B20375', X'D6367556D2E5944CB9ABBBCB5D76A10A', X'D6367556D2E5944CB9ABBBCB5D76A10A', 0, NULL, NULL, 2, NULL, 0, 'I mean...put ''er in a bowl and eat ''er, right?');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'824902AE7A122244904F58C6CDBEE183', X'D6367556D2E5944CB9ABBBCB5D76A10A', X'D6367556D2E5944CB9ABBBCB5D76A10A', 1, NULL, NULL, 2, NULL, 0, 'Bye.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA96EFAD97D37C4281FC2652ADB4AF96', X'AED0D6349A4B2E4BA0ED5E00BF66D931', X'AED0D6349A4B2E4BA0ED5E00BF66D931', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400°F with a rack in the center. In a 12-inch frying pan, heat the oil over medium-high heat. Add the onion along with a sprinkle of salt and cook, stirring occasionally, until softened, about 5 minutes. Add the shiitakes and cook until lightly browned and reduced in volume, about 5 minutes more. Add the capers and broccoli and cook until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E513370E3134C449BBCF018675868D31', X'AED0D6349A4B2E4BA0ED5E00BF66D931', X'AED0D6349A4B2E4BA0ED5E00BF66D931', 1, NULL, NULL, 2, NULL, 0, 'Beat the eggs and cream in a bowl and pour evenly over the vegetables in the frying pan. Sprinkle with the cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29065E9B386AB1458E5F9274761E52E1', X'AED0D6349A4B2E4BA0ED5E00BF66D931', X'AED0D6349A4B2E4BA0ED5E00BF66D931', 2, NULL, NULL, 2, NULL, 0, 'Transfer to oven and bake until set, about 15 minutes. Cut into wedges and serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC634F4189DA86409135D2E6C4904BA6', X'61B185F21C723548BD05BD02D1D9AFD6', X'61B185F21C723548BD05BD02D1D9AFD6', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the sugar, cocoa powder and salt. Add about half of the milk and whisk until the cocoa and sugar are incorporated and not lumpy. Whisk in the rest of the milk along with the eggs and vanilla.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB865006F3F73B4C95C68FCFE5BB7353', X'61B185F21C723548BD05BD02D1D9AFD6', X'61B185F21C723548BD05BD02D1D9AFD6', 1, NULL, NULL, 2, NULL, 0, 'Pour the mixture into a shallow baking dish. Add as many of the bread slices as will fit in a single layer. Let them sit and soak up the liquid for a minute or two, then flip and soak the other side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B49BA376F6FD54B97C23DD670C03728', X'61B185F21C723548BD05BD02D1D9AFD6', X'61B185F21C723548BD05BD02D1D9AFD6', 2, NULL, NULL, 2, NULL, 0, 'Melt a generous pat of butter in a large frying pan over medium heat. Add as many soaked bread slices as will fit without crowding. Cook for a few minutes until browned on the underside, then flip and continue cooking for a few minutes more, until browned and cooked through. (Adjust heat as necessary and keep a sharp eye out for burning since the sugar and cocoa in the egg mixture can go quickly from good to bad.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38EDA591CD7EE4458FCB62378A71B70F', X'61B185F21C723548BD05BD02D1D9AFD6', X'61B185F21C723548BD05BD02D1D9AFD6', 3, NULL, NULL, 2, NULL, 0, 'Repeat the soaking and cooking process with all 8 slices of bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE31BFFDEDD5CE4EA0454B60D3722341', X'61B185F21C723548BD05BD02D1D9AFD6', X'61B185F21C723548BD05BD02D1D9AFD6', 4, NULL, NULL, 2, NULL, 0, 'Serve topped with fresh berries and stone fruit, along with a dollop of whipped cream if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFA63D3784887D4BACF12625D8D89AB8', X'ACC6B8136A080945B1C6D3104D37D9EA', X'ACC6B8136A080945B1C6D3104D37D9EA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350° F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09F962D57CE901478697D1A4296EFA24', X'ACC6B8136A080945B1C6D3104D37D9EA', X'ACC6B8136A080945B1C6D3104D37D9EA', 1, NULL, NULL, 2, NULL, 0, 'Melt one tablespoon of the butter in a large, oven-safe nonstick frying pan set over medium-high heat. Add the sliced garlic scapes and a pinch of salt and cook, stirring once or twice, for two minutes. Add the sliced shiitakes and cook, stirring occasionally, until softened, about 5 minutes. Scrape skillet contents into a bowl and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C99BD1EF2D0534BB3F74D19C07BE1D8', X'ACC6B8136A080945B1C6D3104D37D9EA', X'ACC6B8136A080945B1C6D3104D37D9EA', 2, NULL, NULL, 2, NULL, 0, 'Melt another tablespoon of the butter in the same frying pan. Add the grated potatoes in a single layer and sprinkle with salt. Leave them alone for a few minutes to develop a bit of browning on the bottom, then stir or flip and cook a few minutes more to brown the other side. Add potatoes to bowl with scapes and shiitakes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC61223E35A9744970A03B1684E471A', X'ACC6B8136A080945B1C6D3104D37D9EA', X'ACC6B8136A080945B1C6D3104D37D9EA', 3, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a separate bowl, pour in cream, grind in some black pepper, and scramble.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B85ED2403726FA4680C36D3C11ADE47C', X'ACC6B8136A080945B1C6D3104D37D9EA', X'ACC6B8136A080945B1C6D3104D37D9EA', 4, NULL, NULL, 2, NULL, 0, 'Melt the final tablespoon of butter in the frying pan. Return potatoes, scapes and shiitakes to frying pan along with capers, distributing it all evenly around the pan. Pour in the eggs. Sprinkle cheese evenly overtop.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6D1A3E724C70044B11C6B37CC1EA4C2', X'ACC6B8136A080945B1C6D3104D37D9EA', X'ACC6B8136A080945B1C6D3104D37D9EA', 5, NULL, NULL, 2, NULL, 0, 'Cook for a minute on the stovetop, then transfer to oven. Bake until eggs are set, 15-20 minutes. Cool slightly before cutting into wedges to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A51951565BD894187B1589487CC97DE', X'548A20A181D5C24F8822FC9A037AFC1A', X'548A20A181D5C24F8822FC9A037AFC1A', 0, NULL, NULL, 2, NULL, 0, 'Spoon the tomato sauce into a ramekin with a lid and pop it into the toaster oven (or, fine, oven) while it preheats to 375° F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41F94D318CA0AE4B888563325BE0150B', X'548A20A181D5C24F8822FC9A037AFC1A', X'548A20A181D5C24F8822FC9A037AFC1A', 1, NULL, NULL, 2, NULL, 0, 'When preheated (or after about 10 minutes), crack the eggs gently into the ramekin. Sprinkle on the grated cheese and some pepper. Bake covered for 10 minutes, then uncovered until the whites are set, about 10 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'751D5B25056C2F48B5050046B6659DE6', X'548A20A181D5C24F8822FC9A037AFC1A', X'548A20A181D5C24F8822FC9A037AFC1A', 2, NULL, NULL, 2, NULL, 0, 'Place spinach in a bowl and drizzle with balsamic.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A9C666F352654BB95FE0FAA5FB8013', X'3D39E7F5F9D2914AAC741B7E602D97FA', X'3D39E7F5F9D2914AAC741B7E602D97FA', 0, NULL, NULL, 2, NULL, 0, 'Toast the bread to your liking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8870B9E455ACB4C924B502C05FF4289', X'3D39E7F5F9D2914AAC741B7E602D97FA', X'3D39E7F5F9D2914AAC741B7E602D97FA', 1, NULL, NULL, 2, NULL, 0, 'Scoop out the avocado half and mix it with the lime juice and a pinch of salt. Schmear it onto the toast.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E83CFFECBC86D6439587532A0889574B', X'3D39E7F5F9D2914AAC741B7E602D97FA', X'3D39E7F5F9D2914AAC741B7E602D97FA', 2, NULL, NULL, 2, NULL, 0, 'Heat the butter in a frying pan and fry the eggs as you like, sprinkling with smoked paprika and salt while cooking. Slide the eggs onto the toast.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E79E694722A414D8FEC4874EBBB2B13', X'3D39E7F5F9D2914AAC741B7E602D97FA', X'3D39E7F5F9D2914AAC741B7E602D97FA', 3, NULL, NULL, 2, NULL, 0, 'Top with the salsa and dig in.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C4E9D523B52934E8B924948898D65C1', X'2C6FDADF46A5734E855F30129B7EDE27', X'2C6FDADF46A5734E855F30129B7EDE27', 0, NULL, NULL, 2, NULL, 0, 'Using a clean spice or coffee grinder, pulse the flax seed until it is very finely ground. Place in a small bowl and stir in the water. Let sit for 15 minutes or so, until the flax has soaked up some of the water and turned a little bit gelatinous.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98C5F2C77C7785449AD85F8837AD8B24', X'2C6FDADF46A5734E855F30129B7EDE27', X'2C6FDADF46A5734E855F30129B7EDE27', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, stir together the flours, baking soda, baking powder, brown sugar, cinnamon, nutmeg and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65C4A0783B7BCA4992D26E85AB1C221C', X'2C6FDADF46A5734E855F30129B7EDE27', X'2C6FDADF46A5734E855F30129B7EDE27', 2, NULL, NULL, 2, NULL, 0, 'Mash the bananas and add them to the dry ingredients along with the flax mixture, milk and vanilla. Stir gently until combined. Then pour in the melted butter or oil and stir until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C457C9081F262F46989CE9523905A999', X'2C6FDADF46A5734E855F30129B7EDE27', X'2C6FDADF46A5734E855F30129B7EDE27', 3, NULL, NULL, 2, NULL, 0, 'Melt some vegan butter or coconut oil on a skillet over medium-high heat. Dollop batter onto the skillet a scant 1/4 cup at a time, leaving plenty of space between pancakes. Cook until tiny bubbles begin to pop on the tops of the pancakes and they are lightly browned on the underside. Flip and cook a minute or two more. Continue cooking pancakes in batches until batter is gone.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5DE45FF0C827B4AB25288669473093B', X'2C6FDADF46A5734E855F30129B7EDE27', X'2C6FDADF46A5734E855F30129B7EDE27', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately with maple syrup.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCA44FB6A8ACEB4885A1BD7214DC415E', X'AC1E8E4478B947498ECB455DF1521097', X'AC1E8E4478B947498ECB455DF1521097', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F or the broiler to high. Place a rack 6 to 12 inches from the heat source.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'112DD275062DCD4EA3C81D051E169194', X'AC1E8E4478B947498ECB455DF1521097', X'AC1E8E4478B947498ECB455DF1521097', 1, NULL, NULL, 2, NULL, 0, 'Warm the oil or butter in a large frying pan over medium-high heat. Add the onion along with a good sprinkle of salt and cook until starting to soften, about 5 minutes. Adjust the heat as necessary to prevent too much browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC8F6786A1EBA244815C42B25FFDF169', X'AC1E8E4478B947498ECB455DF1521097', X'AC1E8E4478B947498ECB455DF1521097', 2, NULL, NULL, 2, NULL, 0, 'Add the corn (straight from frozen is fine) and the capers and cook for a few minutes until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F4579AFD64A31409FF015BB87EC4D84', X'AC1E8E4478B947498ECB455DF1521097', X'AC1E8E4478B947498ECB455DF1521097', 3, NULL, NULL, 2, NULL, 0, 'Stir in the spinach a little at a time, adding more as it wilts. Sprinkle with a little more salt and cook until spinach is just wilted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4584B5773B28184E9312E816C3167DF7', X'AC1E8E4478B947498ECB455DF1521097', X'AC1E8E4478B947498ECB455DF1521097', 4, NULL, NULL, 2, NULL, 0, 'Scramble the eggs and pour them evenly into the pan. Place the cheddar on top.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7884E706493168469E3FC4B46EFFFF4D', X'AC1E8E4478B947498ECB455DF1521097', X'AC1E8E4478B947498ECB455DF1521097', 5, NULL, NULL, 2, NULL, 0, 'Transfer frittata to oven and bake or broil until set (or a little longer if you prefer a brown top and firmer eggs).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43A017F0FEA164685DF625F7AE5E61B', X'AC1E8E4478B947498ECB455DF1521097', X'AC1E8E4478B947498ECB455DF1521097', 6, NULL, NULL, 2, NULL, 0, 'Cut into wedges and serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C18502E2F160B7408248D317C262ED12', X'E2035FCF0032E341B0BDA44195B4ACDB', X'E2035FCF0032E341B0BDA44195B4ACDB', 0, NULL, NULL, 2, NULL, 0, 'Combine the oats, milk, water, maple syrup, vanilla, cinnamon, and nutmeg in a small pot. Bring to a boil over medium-high heat, then reduce to a gentle simmer. Cook, stirring occasionally, for a few minutes, until the porridge is soft and a little thinner than you''d like it. It will thicken quickly as it cools.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'482B2C8FD3A61E43858530DB373E8416', X'E2035FCF0032E341B0BDA44195B4ACDB', X'E2035FCF0032E341B0BDA44195B4ACDB', 1, NULL, NULL, 2, NULL, 0, 'To serve, divide oatmeal between two bowls and top with berries, a spoonful of peanut butter, and a sprinkling of flaxseed and coconut.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C5120A871453446BF0D55E24CF0D058', X'31013FC01162AB47AD37EB55553BA7E9', X'31013FC01162AB47AD37EB55553BA7E9', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot, combine the oats, water, cinnamon, and nutmeg.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9CE5567C7993143BEF1C6B684F99802', X'31013FC01162AB47AD37EB55553BA7E9', X'31013FC01162AB47AD37EB55553BA7E9', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59A26ED139CACB4D9ECEE3ACEC484464', X'31013FC01162AB47AD37EB55553BA7E9', X'31013FC01162AB47AD37EB55553BA7E9', 2, NULL, NULL, 2, NULL, 0, 'Cover and reduce heat to low or medium-low, to maintain a gentle simmer. Cook until water is absorbed and oats are tender but still chewy, 40 to 45 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8268353C82855A40851CC35007915BDA', X'31013FC01162AB47AD37EB55553BA7E9', X'31013FC01162AB47AD37EB55553BA7E9', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in maple syrup and flaxseeds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2930650F13C3D44898E5473A859681C5', X'31013FC01162AB47AD37EB55553BA7E9', X'31013FC01162AB47AD37EB55553BA7E9', 4, NULL, NULL, 2, NULL, 0, 'Spoon into bowls and top with fruit, nuts, and a splash of almond milk.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'726281A5006D5D49AE4C1DFF744367A2', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a 3- to 4-quart heavy pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD95B2972D10AC4DAFDFD6BCE0DF8E76', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, stirring occasionally.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'524A02FC262BF24E9A6606B7B7D30DF7', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', 2, NULL, NULL, 2, NULL, 0, 'Reduce the heat to maintain a simmer and cook, stirring occasionally, until the oatmeal is creamy and a little thinner than you like it (about ten minutes, give or take).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8AB106D533974429331D6EE57F5F7F3', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', X'8D7B6A8C2A795C4CB28F2E15EEBFA89D', 3, NULL, NULL, 2, NULL, 0, 'Cool for a few minutes and serve topped with fresh or dried fruit, chocolate chips, or nuts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5103D11EA24564386BA5111B8E5E2BA', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'350456DF60794240B0C983DF1D28FFE1', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', 1, NULL, NULL, 2, NULL, 0, 'Combine the oats, coconut, and almonds in a large bowl and mix well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85D23A12DE5FBE4CA76CECA13B3F94E5', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', 2, NULL, NULL, 2, NULL, 0, 'Add the oil and honey to the bowl, and mix gently until all ingredients are coated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2448DDCD31DF124BA16DB1E2C986BDDC', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', 3, NULL, NULL, 2, NULL, 0, 'Add the cinnamon, nutmeg and salt, and mix gently to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'795079D9448D5447B7F03010DE8760FD', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', 4, NULL, NULL, 2, NULL, 0, 'Spread the mixture onto two rimmed baking sheets (half-sheet pans) and bake for about 25 minutes, until golden brown. Watch carefully toward the end, since all ovens are different, and it can go from perfect to burned quickly. (If you have a convection oven, this is a great time to use it. The granola will brown more quickly, so watch carefully after about 10 to 15 minutes.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD5361358106E44A9E8174D43FAB10CD', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', X'DE0196337BD9DD42A6CD6BA28AF4AC7A', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle half the cherries over each pan, then cool completely before storing in an airtight container. Granola will keep for weeks stored this way at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4DC23C0F638634B874A28C64A494B3A', X'79EDF907B9337F4495E63829AC014011', X'79EDF907B9337F4495E63829AC014011', 0, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron skillet over medium heat for two minutes. Open the hamburger buns and brush each inside face with some of the melted butter. Place buns buttered side down (probably in two batches, depending on your skillet) and toast for a couple of minutes, until light golden brown on the underside. Place buns on serving plates.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7954812A420544D9BF10038439933C3', X'79EDF907B9337F4495E63829AC014011', X'79EDF907B9337F4495E63829AC014011', 1, NULL, NULL, 2, NULL, 0, 'Raise heat to medium-high. Divide beef into four hockey pucks and sprinkle both sides generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E35FD6EC122E0F478D1D1A597BC9F96D', X'79EDF907B9337F4495E63829AC014011', X'79EDF907B9337F4495E63829AC014011', 2, NULL, NULL, 2, NULL, 0, 'Place pucks onto skillet and use a sturdy metal spatula to firmly smash each puck into a patty that''s about 1/3-inch thick. After this, absolutely ignore the burgers for about three minutes, until the undersides are brown and crisp at the edges and the juices on the surface are hot and bubbly. Scrape up each patty and any brown bits with the spatula and flip it. Cook one to two minutes more. Place a slice of cheese on top of each burger at the very end of cooking to melt just enough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C68F4985B0F9E44ACA8781BD9E62AA0', X'79EDF907B9337F4495E63829AC014011', X'79EDF907B9337F4495E63829AC014011', 3, NULL, NULL, 2, NULL, 0, 'To make the sauce, stir together all sauce ingredients in a small bowl until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C5243F48FCA1E458C30EDE98EC278BD', X'79EDF907B9337F4495E63829AC014011', X'79EDF907B9337F4495E63829AC014011', 4, NULL, NULL, 2, NULL, 0, 'Transfer patties to buns. Layer each burger with two tomato slices and a piece of lettuce. Spoon 1/4 of the sauce onto each top bun half. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4BB59E7472D1B44819A320A31B0EEB1', X'E1F439A3DCF61F418E3EE8C5B5C475DF', X'E1F439A3DCF61F418E3EE8C5B5C475DF', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F. Line a half sheet pan with foil. To make the bacon weave, start by laying four bacon slices next to each other, long sides touching, in the middle of the pan. Then, one by one starting at the top, lay each of the remaining pieces crosswise. Weave the first piece over, under, over, under the original slices. Weave the second piece under, over, under, over, etc. (If you learned a different weaving method in elementary school, go for it.) Bake bacon until done to your liking -- 15 minutes plus depending on the thickness of the cut. Drain on paper towels, reserving the grease in the pan. When cooled enough, cut weave into four squares.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA3B543BC2F8B44AEC40248CC7EB873', X'E1F439A3DCF61F418E3EE8C5B5C475DF', X'E1F439A3DCF61F418E3EE8C5B5C475DF', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, form four burger patties from the ground beef. Use your thumb to create a nice big divot in the middle of each patty to ensure even cooking. Sprinkle generously with salt and pepper immediately before cooking. You can grill the burgers or cook in a cast-iron pan with a couple tablespoons of the bacon grease. To grill, prepare a hot side and a cooler side of the grill with a little oil on the grates. Start burgers over direct heat, flipping once, to form a nice crust on both sides. This should take about three minutes per side. Transfer burgers to indirect heat to cook through to your liking (125°F for medium-rare, 130°F for medium on an instant-read thermometer). For cast iron, preheat a 12-inch cast iron pan over high heat until very hot. Pour in two tablespoons of bacon grease, then set burgers into pan. Cook on high heat to form a crust on both sides, flipping once, then reduce heat to medium and continue cooking according to internal temperature instructions above. Let burgers rest for at least 5 minutes before continuing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7A00481DAD91C4EB19BD91FDFBAA052', X'E1F439A3DCF61F418E3EE8C5B5C475DF', X'E1F439A3DCF61F418E3EE8C5B5C475DF', 2, NULL, NULL, 2, NULL, 0, 'While burgers rest, add another two tablespoons of bacon grease into the hot pan, pile in the onions and sprinkle generously with salt and pepper. Cook, stirring frequently, until browned and wilted. Add marsala and cook until evaporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E41F2D924D2174487E82F9293B4239D', X'E1F439A3DCF61F418E3EE8C5B5C475DF', X'E1F439A3DCF61F418E3EE8C5B5C475DF', 3, NULL, NULL, 2, NULL, 0, 'To assemble the burgers, toast the buns for a minute or two if you like, then layer on some spinach leaves, a burger patty, bacon, onions, and some crumbled blue cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'357724278900474BAB56F8EBA39AF2D8', X'77A2D1950FA3D84C843C45EBA061EA07', X'77A2D1950FA3D84C843C45EBA061EA07', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot, combine the lentils, rice, salt, herbes de Provence and three cups of water. Bring to a boil, then cover and simmer until water is absorbed and rice and lentils are cooked, about 40 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CB37EC3F91DF149A6BDB3706A54A32B', X'77A2D1950FA3D84C843C45EBA061EA07', X'77A2D1950FA3D84C843C45EBA061EA07', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, warm the oil in a 12-inch frying pan over medium-high heat. Add the onion and celery and cook, stirring occasionally, until beginning to soften, about five minutes. Stir in garlic and cook one minute more. Add the shiitakes as they fit into the pan and cook, stirring frequently, until browned in spots and about halved in volume, 10 minutes or so.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBE9ED043ECF5C4B91D3ED358C4B3292', X'77A2D1950FA3D84C843C45EBA061EA07', X'77A2D1950FA3D84C843C45EBA061EA07', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F with two racks as near the center as possible. Line two baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CFCD61652489A498232E18258743467', X'77A2D1950FA3D84C843C45EBA061EA07', X'77A2D1950FA3D84C843C45EBA061EA07', 3, NULL, NULL, 2, NULL, 0, 'Into a full-size food processor fitted with the blade, place the walnuts and the mushroom mixture. Pulse until finely chopped. Stir in the cooked rice and lentils and pulse a few times to combine. Transfer mixture to a large mixing bowl. Add the ketchup, tamari, flaxseed, mustard, marmite and black pepper and mix together thoroughly. Stir in the cornmeal.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28FC0E798DA96F42A23D6509CF59C656', X'77A2D1950FA3D84C843C45EBA061EA07', X'77A2D1950FA3D84C843C45EBA061EA07', 4, NULL, NULL, 2, NULL, 0, 'Form into 10 patties and place on baking sheets.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16766370D82F414882F724CDC7841935', X'77A2D1950FA3D84C843C45EBA061EA07', X'77A2D1950FA3D84C843C45EBA061EA07', 5, NULL, NULL, 2, NULL, 0, 'Bake for 15 minutes, then flip each patty, rotate locations of pans and bake 15 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6C7898078ED304B93FA8E7BB7B9D9A3', X'77A2D1950FA3D84C843C45EBA061EA07', X'77A2D1950FA3D84C843C45EBA061EA07', 6, NULL, NULL, 2, NULL, 0, 'Serve ''er like a burger. (Sorry, I just had to.) These patties keep well in the fridge for up to a week and can be reheated in the microwave. Or you should be able to freeze cooked patties, though I haven''t tried this yet because they always disappear within the week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C03BFE41988C114FA9239C81BFF83AAE', X'6B1AB3191B3254468806C27C13C2AFFE', X'6B1AB3191B3254468806C27C13C2AFFE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F with a rack in the center. Line a baking sheet with parchment paper or a light layer of oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44FA80D9669D6E4AAA88E6576AC218A5', X'6B1AB3191B3254468806C27C13C2AFFE', X'6B1AB3191B3254468806C27C13C2AFFE', 1, NULL, NULL, 2, NULL, 0, 'Into food processor fitted with the blade, place the onion, garlic, sweet potato and walnuts. Process until finely chopped. Add the cooked rice and lentils and pulse until well combined. Transfer into a large mixing bowl. Add the ketchup, mustard, flax, salt, cayenne and black pepper and mix together thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C9B0C93700E5845AC14329260A6B29D', X'6B1AB3191B3254468806C27C13C2AFFE', X'6B1AB3191B3254468806C27C13C2AFFE', 2, NULL, NULL, 2, NULL, 0, 'Stir in the cornmeal. The mixture should be thick enough that you can easily form patties in your hands. If not, add a bit more cornmeal. Divide into eight patties and place on a the baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FC4928B4B8F3842A54EE01DEAC15B62', X'6B1AB3191B3254468806C27C13C2AFFE', X'6B1AB3191B3254468806C27C13C2AFFE', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes, then flip each patty and bake 15 minutes more. Serve as you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01EF6D1CC6693A4C94AF4E4661EC3ED1', X'B8CF3DDD7159FD43A2CDA90055705B5E', X'B8CF3DDD7159FD43A2CDA90055705B5E', 0, NULL, NULL, 2, NULL, 0, 'Peel the sweet potatoes and cut lengthwise into quarters. Peel and quarter the onion.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A50717458A8E4BA73FE1E730F0A337', X'B8CF3DDD7159FD43A2CDA90055705B5E', X'B8CF3DDD7159FD43A2CDA90055705B5E', 1, NULL, NULL, 2, NULL, 0, 'Set up a food processor with the grating blade. Grate the sweet potatoes in two batches and add them to a very large mixing bowl. (Alternatively, grate on a box grater.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFC6D81B259AF04F8C69A7DC5712DC0B', X'B8CF3DDD7159FD43A2CDA90055705B5E', X'B8CF3DDD7159FD43A2CDA90055705B5E', 2, NULL, NULL, 2, NULL, 0, 'Grate the onion and place on paper towels to absorb excess moisture. Then add to mixing bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'471017DED05EBD4290A655159AAD271B', X'B8CF3DDD7159FD43A2CDA90055705B5E', X'B8CF3DDD7159FD43A2CDA90055705B5E', 3, NULL, NULL, 2, NULL, 0, 'Mix in beaten eggs, scallions, flour, matzo meal, melted butter, salt, pepper, and baking powder.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8963D9AFA81994E903A328B1BCDFFF6', X'B8CF3DDD7159FD43A2CDA90055705B5E', X'B8CF3DDD7159FD43A2CDA90055705B5E', 4, NULL, NULL, 2, NULL, 0, 'Pour safflower oil into a large frying pan to a depth of about 1/8 inch. Heat oil over medium-high until it thins and becomes shimmery. Use a soup spoon to scoop little mounds of the sweet potato mixture into the skillet (3 tablespoons at a time makes nice, diminutive latkes). Gently flatten the pancakes. Don''t crowd the skillet — leave an inch or so between latkes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0483128DFAB44D4EB9BCE73A013C36DC', X'B8CF3DDD7159FD43A2CDA90055705B5E', X'B8CF3DDD7159FD43A2CDA90055705B5E', 5, NULL, NULL, 2, NULL, 0, 'Fry until the underside is crispy and golden brown, then flip and fry until the second side is crispy and golden brown, about 5 minutes total. Remove cooked latkes to paper towels to drain. Continue cooking, adding more oil as necessary and adjusting the heat to keep oil hot but not smoking, until you''ve used all of the sweet potato mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F35FE532B8348E4A8A91168400AFA548', X'B8CF3DDD7159FD43A2CDA90055705B5E', X'B8CF3DDD7159FD43A2CDA90055705B5E', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle with additional sliced scallions and some flaky sea salt and serve with sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BD1F068B8D4C346A7954CE2CA3C3A62', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 0, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and cut lengthwise into quarters. Contrary to what you might normally do, don''t put the peeled potatoes in a bowl of water, because you want to keep all the starch. Just leave them on the counter. Don''t worry if they brown a bit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E18585145947504D8F7D891216464E15', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 1, NULL, NULL, 2, NULL, 0, 'Peel and quarter the onion.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EC3EAE368D01344A7E952DC3A2C5564', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 2, NULL, NULL, 2, NULL, 0, 'Set up a food processor with the grating blade. Grate the potatoes and onions in two batches and add them to a strainer set over a large mixing bowl. Squeeze out as much moisture as you can with your hands, then let the mixture rest and drain for three minutes or so.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98D1D51B76EF4749933A38C1DE571547', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 3, NULL, NULL, 2, NULL, 0, 'Pour off all the liquid from the bowl, but leave the layer of starch that''s accumulated at the bottom. Add grated potatoes and onions to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E392506C72FA364BA1415D255F35B87C', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 4, NULL, NULL, 2, NULL, 0, 'Mix in beaten eggs, matzo meal, melted butter, salt, pepper, and baking powder.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3085DDFE8F2E1F41A200E5DFC97994E3', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 5, NULL, NULL, 2, NULL, 0, 'Pour safflower oil into a large frying pan to a depth of about 1/8 inch. Heat oil over medium-high until it thins and becomes shimmery. Use a soup spoon to scoop little mounds of the potato mixture into the skillet (3 tablespoons at a time makes nice, diminutive latkes). Gently flatten the pancakes. Don''t crowd the skillet — leave an inch or so between latkes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F80ACFDBC77334F8FBBB94269435F2F', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 6, NULL, NULL, 2, NULL, 0, 'Fry until the underside is crispy and golden brown, then flip and fry until the second side is crispy and golden brown, about 5 minutes total. Remove cooked latkes to paper towels to drain. Continue cooking, adding more oil as necessary and adjusting the heat to keep oil hot but not smoking, until you''ve used all of the potato mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'900A1B103FD1A5419E2AC9A84D476102', X'6F67B684FEC8B347B7612CE1BB0EBF94', X'6F67B684FEC8B347B7612CE1BB0EBF94', 7, NULL, NULL, 2, NULL, 0, 'Serve with applesauce, sour cream, and smoked salmon if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC39B92739D4441B1340FEDA935A8EF', X'B799150F8249B249B71B2B48F75764D3', X'B799150F8249B249B71B2B48F75764D3', 0, NULL, NULL, 2, NULL, 0, 'Shred the zucchini with the shredding disc of a food processor or on the large holes of a box grater. Place the shredded zucchini in a colander in the sink or over a bowl and sprinkle with the salt. Toss to combine. Let drain while you prepare the rest of the ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A83CF5B333124A4BA2E3DF3B9DC9DBA6', X'B799150F8249B249B71B2B48F75764D3', X'B799150F8249B249B71B2B48F75764D3', 1, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a large bowl and scramble lightly. Cut the kernels from the corn cobs and add the kernels to the bowl along with the diced onion, sliced scallions, chopped cilantro, flour, grated cheese and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F77036694BFC549B2F9A1CB84B3FCA0', X'B799150F8249B249B71B2B48F75764D3', X'B799150F8249B249B71B2B48F75764D3', 2, NULL, NULL, 2, NULL, 0, 'Pick up the shredded zucchini in small handfuls and squeeze out and discard as much liquid as you can. Add the zucchini to the bowl. Mix well to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B658425B5967B64985C89629A5F5C5C9', X'B799150F8249B249B71B2B48F75764D3', X'B799150F8249B249B71B2B48F75764D3', 3, NULL, NULL, 2, NULL, 0, 'Pour the oil into a large frying pan to a depth of about 1/4-inch. Heat the oil over medium-high heat until hot but not smoking. Drop batter by the 1/4-cupful into the pan and press lightly to form disc-shaped fritters. Cook in batches without crowding (about 3 or 4 at a time, depending on the size of your pan) until golden brown on the underside (about 2 minutes) and then flip and cook until golden brown on the second side. Remove to paper towels to drain. Add a bit more oil between batches if necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98C0F0A1B32D0040BE09DB70CE49C3A5', X'B799150F8249B249B71B2B48F75764D3', X'B799150F8249B249B71B2B48F75764D3', 4, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature with sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2C9554E4BADC54BA70FA9463B2F3199', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot, cover the potatoes with cold water by an inch or two, add the tablespoon of salt, and bring to a boil. Turn down the heat to maintain a brisk simmer and cook until potatoes are tender, about 15 minutes give or take depending on how big the pieces are. Drain potatoes in a colander and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'331271266791C3488BCCFED737B67A82', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 1, NULL, NULL, 2, NULL, 0, 'While the potatoes cook, prepare the shallots and cabbage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8AB7BB0B117AD4FB4CD184BD8ED6C47', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 2, NULL, NULL, 2, NULL, 0, 'Return the same pot to the stove and heat the butter and olive oil over medium-high heat. Add the shallots and cabbage and cook, stirring frequently (tongs are useful here), until cabbage is wilted and somewhat tender, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD4C4BDD924A324193CE179A21EF7F99', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 3, NULL, NULL, 2, NULL, 0, 'Add the milk, raise heat to high, and warm until gently simmering. Remove pot from heat. Return the potatoes to the pot, add the teaspoon of salt and the pepper, and mash well with a potato masher. Stir in the cheddar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'924DFF7D34BB1941BA2572C8F9ABED64', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 4, NULL, NULL, 2, NULL, 0, 'At this point the mixture will need to cool slightly — so either turn it out into a mixing bowl to speed the process or leave it in the pot to spare a dirty dish and wait a few minutes longer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FA678919A6BF64A88FDD106C082FCCB', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 5, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the potatoes and crack the eggs into it. Beat them well, then stir completely into the mix. Sprinkle the flour over the whole thing and mix in thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90BD1C7F554CA349877F038B96BF5F76', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 6, NULL, NULL, 2, NULL, 0, 'Heat a thin layer of oil over medium-high heat in a 12-inch nonstick skillet. When it shimmers, spoon about three tablespoons of the mixture into the pan to form a reasonably round cake. You should be able to fit 4 cakes in the skillet at a time without crowding. Cook undisturbed for a few minutes, until the underside is nicely browned. Flip gently with a spatula and cook until the second side is browned, a few minutes more. You may need to adjust the heat so the cakes cook through in the same amount of time they take to brown.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E645B9BB4D0B4642B7E3DB34F7C5B3BF', X'0898C3FA5532CB41A04C96A56D462AEE', X'0898C3FA5532CB41A04C96A56D462AEE', 7, NULL, NULL, 2, NULL, 0, 'Set on a paper towel-lined plate or in a 200°F oven while you cook the remaining batches. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E0A0BDECCCA714FAE3F29D6745C44CC', X'659235ABA9D0A24199054203B873CE1C', X'659235ABA9D0A24199054203B873CE1C', 0, NULL, NULL, 2, NULL, 0, 'Squeeze out as much water from the spinach as possible and place spinach in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96C62AA5BC3B5D479F846057172D344C', X'659235ABA9D0A24199054203B873CE1C', X'659235ABA9D0A24199054203B873CE1C', 1, NULL, NULL, 2, NULL, 0, 'Fill a medium pot halfway with water and bring to a boil over high heat. Add the fava beans and cook for two minutes. Drain. Place fava beans (including skins, if you like) in a food processor and pulse until a coarse puree forms. Add the puree to the mixing bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6B3EDDE6F68CB488DFE8AE37126D530', X'659235ABA9D0A24199054203B873CE1C', X'659235ABA9D0A24199054203B873CE1C', 2, NULL, NULL, 2, NULL, 0, 'Add the quinoa, garlic, salt, cumin, coriander, paprika, turmeric, and pepper to the bowl. Mix with a wooden spoon or with your hands to combine thoroughly. Taste for seasoning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78E8BE05732B3F4D82BE9894CA7216F4', X'659235ABA9D0A24199054203B873CE1C', X'659235ABA9D0A24199054203B873CE1C', 3, NULL, NULL, 2, NULL, 0, 'Form the mixture into small patties, 2 to 3 tablespoons each, and place on a baking sheet. Refrigerate for about 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F42D5E505CE249478C2190CDC8A81838', X'659235ABA9D0A24199054203B873CE1C', X'659235ABA9D0A24199054203B873CE1C', 4, NULL, NULL, 2, NULL, 0, 'Coat the bottom of a wide, heavy skillet with olive oil and warm over medium heat until it shimmers. Cook the cakes in batches, without crowding, until golden brown on the underside, and then flip and cook until the second side is golden as well. Place on a paper towel-lined plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1CA547C1F902C4889235029678F1BF9', X'659235ABA9D0A24199054203B873CE1C', X'659235ABA9D0A24199054203B873CE1C', 5, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature with lemon slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1DD56CEE57F834D9895F0B0550E32FB', X'C8801B56CC968C4DAAE6B7E411E7FC67', X'C8801B56CC968C4DAAE6B7E411E7FC67', 0, NULL, NULL, 2, NULL, 0, 'Place chickpeas in a large bowl and cover with water by at least 1 inch. Soak for 24 hours. Drain well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'343C4D7ABF1F854EB2B3392E904A5DC9', X'C8801B56CC968C4DAAE6B7E411E7FC67', X'C8801B56CC968C4DAAE6B7E411E7FC67', 1, NULL, NULL, 2, NULL, 0, 'Half at a time, pour chickpeas into a food processor fitted with the blade attachment. Process, stopping to scrape down the sides as necessary, until chickpeas begin to form a very coarse paste. It should just hold together when you squeeze it in your palm but still be made up of thousands of little chickpea bits. Scrape the chickpea paste back into the large bowl. Add the onion, garlic, cilantro, parsley, baking powder, coriander, cumin, cayenne, salt, and pepper. Mix well but gently with your hands (as you would for meatballs). Refrigerate until firm, about an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06727FA040F84A4E8CA80C7F19FB276A', X'C8801B56CC968C4DAAE6B7E411E7FC67', X'C8801B56CC968C4DAAE6B7E411E7FC67', 2, NULL, NULL, 2, NULL, 0, 'Using a measuring tablespoon if you wish, or just your hands, shape batter into 1-inch balls. The balls should stick together well, but try not to work the batter too much. Divide the balls between two half-sheet pans or cookie trays lined with parchment, and refrigerate for 15 minutes. Meanwhile, make a sauce, such as tahini (click and then scroll down for recipe) or yogurt sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF1E6C4C8131DD4AABEBCF7DCAF9752C', X'C8801B56CC968C4DAAE6B7E411E7FC67', X'C8801B56CC968C4DAAE6B7E411E7FC67', 3, NULL, NULL, 2, NULL, 0, 'Pour oil into a large, heavy pot to a depth of about 1 inch (I use a 5-1/2 quart Dutch oven). Insert a candy/deep fry thermometer and heat oil over medium heat until it reaches 350°F. Carefully drop balls one by one into the hot oil. Don''t crowd them—you''ll fry in batches of about 8 at a time, depending on the size of your pot. Fry until golden brown on all sides, about 3 minutes. Drain on paper towels. Keep warm in a 250°F oven if you like. Serve with pita, tahini or yogurt sauce, raw onions, cucumber, parsley, and tomato—or any or none of the above.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'362C8B4E57EC99489E19E12959A00B4F', X'DB2BC65DB66C44479B3CA4F927EB219E', X'DB2BC65DB66C44479B3CA4F927EB219E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center. Grease a standard 12-muffin tin or line with paper liners.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D2F6A669CE7D4FAB2D50817DB57582', X'DB2BC65DB66C44479B3CA4F927EB219E', X'DB2BC65DB66C44479B3CA4F927EB219E', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the cornmeal, flour, sugar, baking powder, baking soda and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFFEA57F32AE7A4388339034F7442748', X'DB2BC65DB66C44479B3CA4F927EB219E', X'DB2BC65DB66C44479B3CA4F927EB219E', 2, NULL, NULL, 2, NULL, 0, 'Measure the buttermilk into a 2-cup liquid measure, then crack in the eggs and beat to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E084BCE6D6FA14C9925B77E0AC0CBB0', X'DB2BC65DB66C44479B3CA4F927EB219E', X'DB2BC65DB66C44479B3CA4F927EB219E', 3, NULL, NULL, 2, NULL, 0, 'Pour the buttermilk mixture into the bowl with the dry ingredients and stir gently until there are no dry pockets.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D88C2BBC474D94490AC96B2C44D8EB6', X'DB2BC65DB66C44479B3CA4F927EB219E', X'DB2BC65DB66C44479B3CA4F927EB219E', 4, NULL, NULL, 2, NULL, 0, 'Pour in the melted butter and stir until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E976CBB9AAF8C4C84ACBA4CEC5BB689', X'DB2BC65DB66C44479B3CA4F927EB219E', X'DB2BC65DB66C44479B3CA4F927EB219E', 5, NULL, NULL, 2, NULL, 0, 'Divide batter among 12 muffin cups. I like to fill my 1/4-cup measure generously with batter and use it as a scoop. Bake 10-12 minutes, until a tester comes out clean. Remove to a rack to cool slightly before serving, or cool completely and keep for up to a day or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5190A1B47F5C9D4E86F8DC9F2454E513', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F with a rack in the center. Grease a medium (8-cup/9 x 5 x 3 inch) loaf pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776163494CE76C4DACDB0DC4C1FD80F6', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mash the bananas with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86537DAA812BF34CA432A95F9227B304', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 2, NULL, NULL, 2, NULL, 0, 'Mix in the melted butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E44A46258F8C942A8D021F3312ECD26', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 3, NULL, NULL, 2, NULL, 0, 'Crack the egg into the bowl near one of the sides, tilt the bowl slightly, and use the fork to lightly beat the egg.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC2FBCBEDB4B5469AFF906010E97B27', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 4, NULL, NULL, 2, NULL, 0, 'Add the brown sugar, vanilla, and coffee to the bowl and mix with the fork to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83AD56C908212D4197EA6FA81EB18D74', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the baking soda and salt over the batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7889FF179C4109489C8E90BD8FF9BD52', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 6, NULL, NULL, 2, NULL, 0, 'Add the flour and mix with the fork until just combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DFF32AB2496AB4698C79772751BDC26', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 7, NULL, NULL, 2, NULL, 0, 'Add the chocolate and walnuts and mix just until they''re well distributed in the batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'047AFA9D46BB2C4D8FBFF62F2575DC89', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 8, NULL, NULL, 2, NULL, 0, 'Scrape the batter into the loaf pan and bake for 45 minutes to one hour, until a tester inserted in the center comes out cleanish. It''s fine if it has some banana or chocolate on it, but lots of wet batter ain''t right.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6E108804B036541BABF95CC98C35D15', X'6C7953AC5BA2BC4A873ACD8110A68AAA', X'6C7953AC5BA2BC4A873ACD8110A68AAA', 9, NULL, NULL, 2, NULL, 0, 'Cool in the pan on a rack for ten minutes (before unmolding to cool completely, if you''re positively made of steel).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F423D4492B7D8E4A9F9DA9BE08B3739A', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center. Grease 16 two standard 12-muffin tins or line with paper liners.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D5FCFC9FE973F489D8185A0396AC56D', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the cherries and 1/4 cup of the sugar. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B28A66CFC935874F9E6F062A031AE1FF', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the cornmeal, flour, the remaining 1/4 cup of sugar, baking powder, baking soda and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'441AC1371FD80642A7B7CF54F76194BB', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 3, NULL, NULL, 2, NULL, 0, 'Measure the buttermilk into a 2-cup liquid measure, then crack in the eggs and beat to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7091B576438D514EA70A0A626312F426', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 4, NULL, NULL, 2, NULL, 0, 'Pour the buttermilk mixture into the bowl with the dry ingredients and stir gently until there are no dry pockets.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FCBB6D00EC575468A0912F5E6640AAC', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 5, NULL, NULL, 2, NULL, 0, 'Pour in the melted butter and stir until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A75E26830ECA44DAC5DBBD2D48F5781', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 6, NULL, NULL, 2, NULL, 0, 'Drain any liquid from the cherries and stir cherries into batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E41370D4DE705429566E18FBB52DC68', X'34ACF161325A4C4E97FBA701AE048029', X'34ACF161325A4C4E97FBA701AE048029', 7, NULL, NULL, 2, NULL, 0, 'Divide batter among 16 muffin cups. Bake 10-12 minutes, until a tester comes out clean. Remove from oven and let cool in pans for 5 minutes, then turn muffins out onto a wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38DDC3415D349145A034F328763BDD22', X'C0E99FCB4F8BA3448BE049513E63ADF4', X'C0E99FCB4F8BA3448BE049513E63ADF4', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F with a rack in the center. Grease three 1-pound loaf pans (8.5-by-4.25-by-2.75 inches) or 16 mini loaf pans with olive oil or spray with cooking spray. If you don’t have three pans, you can bake the bread in batches.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'757F2B8F8E0ACB419A533E9714758690', X'C0E99FCB4F8BA3448BE049513E63ADF4', X'C0E99FCB4F8BA3448BE049513E63ADF4', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, beat the eggs with a fork. Add the olive oil, water, sugar and pumpkin purée and whisk with a fork to combine thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FCAC001E70FBC4398857D5E3FCF6A0B', X'C0E99FCB4F8BA3448BE049513E63ADF4', X'C0E99FCB4F8BA3448BE049513E63ADF4', 2, NULL, NULL, 2, NULL, 0, 'Sift the flour together with the salt, nutmeg, cinnamon, cloves, and baking soda into the mixing bowl. Stir dry ingredients into wet ingredients until just combined. Stir in nuts, if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0012C210EB15C47918D66F5F2DC9A42', X'C0E99FCB4F8BA3448BE049513E63ADF4', X'C0E99FCB4F8BA3448BE049513E63ADF4', 3, NULL, NULL, 2, NULL, 0, 'Divide batter evenly among prepared pans, about three cups of batter (with nuts) per pan large loaf pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32375D4770163A43BE293B9A3625B92C', X'C0E99FCB4F8BA3448BE049513E63ADF4', X'C0E99FCB4F8BA3448BE049513E63ADF4', 4, NULL, NULL, 2, NULL, 0, 'For large pans, bake for 45 minutes to an hour, until a cake tester inserted into the center comes out clean. For mini loaf pans, bake 25 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE67F3EA6A32254D85033F8793FF0993', X'C0E99FCB4F8BA3448BE049513E63ADF4', X'C0E99FCB4F8BA3448BE049513E63ADF4', 5, NULL, NULL, 2, NULL, 0, 'Cool on a rack for 10 minutes before removing from pan to cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A63FD42C85BBA4B9604E8CF0FB05A3C', X'93FFD45F232D1247B0B940D62A68CB51', X'93FFD45F232D1247B0B940D62A68CB51', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350° F with a rack in the center. Grease a 9 x 5-inch (8-cup) loaf pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AEE327E06B1064BABABC54422493E39', X'93FFD45F232D1247B0B940D62A68CB51', X'93FFD45F232D1247B0B940D62A68CB51', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the flour, baking powder, baking soda, salt, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA6722F8214C3438FC1E037C9DA17C2', X'93FFD45F232D1247B0B940D62A68CB51', X'93FFD45F232D1247B0B940D62A68CB51', 2, NULL, NULL, 2, NULL, 0, 'Add the zucchini, cheeses, shallot, and herbs and toss to separate and coat with flour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F79FCA2E8E0B24458421458814944984', X'93FFD45F232D1247B0B940D62A68CB51', X'93FFD45F232D1247B0B940D62A68CB51', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl or glass measuring cup, whisk together the eggs, buttermilk, and melted butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21C663547BABFA4CB64AE5CD203A3445', X'93FFD45F232D1247B0B940D62A68CB51', X'93FFD45F232D1247B0B940D62A68CB51', 4, NULL, NULL, 2, NULL, 0, 'Pour wet ingredients into the bowl with the flour mixture and mix until just combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E79FCE1A7A5D164BBE9EEE063D94D1DB', X'93FFD45F232D1247B0B940D62A68CB51', X'93FFD45F232D1247B0B940D62A68CB51', 5, NULL, NULL, 2, NULL, 0, 'Bake for 55-60 minutes, until a cake tester inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8684BBC50852D48B2ADC9AD8DC9204D', X'93FFD45F232D1247B0B940D62A68CB51', X'93FFD45F232D1247B0B940D62A68CB51', 6, NULL, NULL, 2, NULL, 0, 'Let cool in the pan on a rack for 5 to 10 minutes before unmolding to cool completely on a rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EF2E49F8491D04FA7BA6E06D691F450', X'2752F691F3C64E4AB7A4D609994D10C7', X'2752F691F3C64E4AB7A4D609994D10C7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Line 18 standard muffin cups with paper liners or grease the pan well with oil or cooking spray, including the top.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFBE2737DFF7904E99F9DC8D95ABA830', X'2752F691F3C64E4AB7A4D609994D10C7', X'2752F691F3C64E4AB7A4D609994D10C7', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, stir together the 2 cups flour, baking powder, baking soda and 1/2 teaspoon salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'984E3B75B1CE1C4E85B63E827BB6B175', X'2752F691F3C64E4AB7A4D609994D10C7', X'2752F691F3C64E4AB7A4D609994D10C7', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the yogurt and vanilla extract.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B863933D40C0754DAAA4B6FA4908A694', X'2752F691F3C64E4AB7A4D609994D10C7', X'2752F691F3C64E4AB7A4D609994D10C7', 3, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer (or a large bowl), beat together the 4 tablespoons butter and the 1 cup sugar until lightened in color and texture, about 3 minutes. Add the eggs and beat to incorporate. Then add half the flour mixture and beat on low until just incorporated. Follow with half the yogurt mixture and then the remaining halves of the flour and yogurt, beating on low after each addition. Fold blueberries into the batter. Spoon by the scant 1/4-cupful into paper-lined muffin cups.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E68FF1738BCF2343B282DEFDC40319DF', X'2752F691F3C64E4AB7A4D609994D10C7', X'2752F691F3C64E4AB7A4D609994D10C7', 4, NULL, NULL, 2, NULL, 0, 'To make the crumble topping, stir together the flour, brown sugar, nuts, cinnamon and salt in a small bowl. Pour the melted butter overtop and stir to incorporate into the dry ingredients. Cover the surface of each muffin with topping and press lightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D524EB073A41DA40BF2F47385D5D4C3B', X'2752F691F3C64E4AB7A4D609994D10C7', X'2752F691F3C64E4AB7A4D609994D10C7', 5, NULL, NULL, 2, NULL, 0, 'Bake for about 24 minutes, until a tester comes out almost clean. Remove muffins to a wire rack to cool.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEDFA17E7275F942AF92F1EC427A26A6', X'9A96AAF3541C4F4C94CD7F5332A28E18', X'9A96AAF3541C4F4C94CD7F5332A28E18', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375° Fahrenheit with one rack about 1/3 of the way up from the oven''s bottom and another about 1/3 of the way down from the oven''s top. Place a total of 22 (or 24, if using walnuts) paper muffin cups in two standard 12-muffin tins.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E104493101C6CB41B4CD957DC069A61B', X'9A96AAF3541C4F4C94CD7F5332A28E18', X'9A96AAF3541C4F4C94CD7F5332A28E18', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the all-purpose flour, whole wheat flour, brown sugar, granulated sugar, baking soda, baking powder, salt, nutmeg, cinnamon, allspice, and cloves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02EBF7FF9A32B42A59361BDBD93390F', X'9A96AAF3541C4F4C94CD7F5332A28E18', X'9A96AAF3541C4F4C94CD7F5332A28E18', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the pumpkin puree, oil, maple syrup, and water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B07EE1A06ADA454DAC7CDD488C8AF643', X'9A96AAF3541C4F4C94CD7F5332A28E18', X'9A96AAF3541C4F4C94CD7F5332A28E18', 3, NULL, NULL, 2, NULL, 0, 'Add the wet ingredients to the dry ingredients and use a spatula to fold the ingredients together. Fold just until there are no pockets of flour that haven''t been incorporated into the batter, and then stop. Fold in the chopped walnuts, if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB5048DDE9D36549969FBA3D3B16BB34', X'9A96AAF3541C4F4C94CD7F5332A28E18', X'9A96AAF3541C4F4C94CD7F5332A28E18', 4, NULL, NULL, 2, NULL, 0, 'Use a medium ice cream scoop to fill each muffin cup a little more than halfway. Bake for 15 minutes with one muffin tin on each oven rack. Then switch the positions of the tins and bake for about 10 minutes more, until a tester inserted in the center of a muffin comes out clean. Cool muffins in tins on a wire rack for about 20 minutes, then remove muffins from tins and cool completely. Frost with cream cheese frosting (or a vegan frosting of your choice), if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3472814754A33A42AA7F370B72340257', X'AF173A34F2FF3543800CFF5E32CFB987', X'AF173A34F2FF3543800CFF5E32CFB987', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F with the top rack in the center. Lightly grease two popover or muffin pans.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A03916712DF64FAE874F501BBB52D5', X'AF173A34F2FF3543800CFF5E32CFB987', X'AF173A34F2FF3543800CFF5E32CFB987', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the eggs, milk, and water. Slowly drizzle in the melted butter while whisking. Add the flour and salt and mix until well combined but still slightly lumpy. Divide the batter among the pans, filling each well only about 1/3 full.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F0DC893743F0498354600BB9BB62A9', X'AF173A34F2FF3543800CFF5E32CFB987', X'AF173A34F2FF3543800CFF5E32CFB987', 2, NULL, NULL, 2, NULL, 0, 'Bake for 45 minutes without opening the oven. Then cut a small slit in the top of each popover to release the steam and bake for an additional 10 minutes. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C39D7CB53C36C649A08DDE1900BC439D', X'011E5150B22515418074D91A07E46E35', X'011E5150B22515418074D91A07E46E35', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375° F. Line two standard muffin pans with paper liners. In the bowl of a stand mixer fitted with the wire whisk, combine the bananas, yogurt, sweet potatoes, brown sugar and white sugar. Mix on high speed until lightened in color and texture, about 3 minutes. Add the butter, eggs and vanilla and mix until well-combined, stopping to scrape down the sides of the bowl when necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B86E27E4C929B945910C65AA90EA3AC8', X'011E5150B22515418074D91A07E46E35', X'011E5150B22515418074D91A07E46E35', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the all-purpose flour, white whole wheat flour, salt, baking soda, cinnamon and nutmeg. Add the dry ingredients to the wet ingredients and mix on low speed until just combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C32A35D0C473AC448A4D8B2917886A19', X'011E5150B22515418074D91A07E46E35', X'011E5150B22515418074D91A07E46E35', 2, NULL, NULL, 2, NULL, 0, 'Spoon the batter into the muffin cups, filling them a little more than halfway. Bake for 18-20 minutes, until a tester inserted into the center of a muffin comes out clean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDB0CA24315BE84DB095092EFDA0F0C3', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D740D3F4DE17424E8DC566149B8AE8A9', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 1, NULL, NULL, 2, NULL, 0, 'Spray a standard 12-muffin pan with baking spray or lightly oil the cups.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02089543A00FD645AE7F45594B302248', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 2, NULL, NULL, 2, NULL, 0, 'In a small microwave-safe bowl, warm the butter in the microwave for 1 ½ to 2 minutes on medium power until just melted. (Seriously, don’t use high power unless you like licking butter off the microwave door.) Alternatively, you can melt the butter over low heat in a small pot on the stove.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7341AC065D2E504482D9B93FB5D8E02C', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 3, NULL, NULL, 2, NULL, 0, 'Add the egg, yolk, milk and vanilla to the bowl with the melted butter and whisk thoroughly to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA488522C43FCE4996A55CF1BDCE197B', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the flour, sugar, baking powder and salt and mix thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F89AC8205961D243A5F0F4AE209084F5', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 5, NULL, NULL, 2, NULL, 0, 'Add the wet ingredients to the dry ingredients and mix gently until just combined. Do not overmix. The batter will be very thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BD523025DF667499D7DF803A2550FE6', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 6, NULL, NULL, 2, NULL, 0, 'Gently fold in the fruit, nuts or other additions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B69C3BC8D67864EB310B65D1F34DAB8', X'DFCA0E029D0E4F4285E8F8AE391848FE', X'DFCA0E029D0E4F4285E8F8AE391848FE', 7, NULL, NULL, 2, NULL, 0, 'Divide the batter among the 12 muffin cups. Bake for 18-22 minutes, until lightly browned on top. Cool on a rack for 10 minutes before removing muffins from the pan. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4BCD4C122AEE14887D77042FE941684', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl (or right in the bowl of a stand mixer, if you like), stir together the flour, yeast, salt, and sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C40882797FF2C408AFA7AE2E3291D49', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 1, NULL, NULL, 2, NULL, 0, 'Pour in the water and mix well (by hand or with a wooden spoon or silicone spatula) until all the flour is hydrated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'730115665DD6834A80AA6CC87AA6E958', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 2, NULL, NULL, 2, NULL, 0, 'Pour in olive oil and mix until incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EB4BBA72E99294D863505A7E562AB63', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 3, NULL, NULL, 2, NULL, 0, 'Knead dough for 10 minutes. I like to use a stand mixer fitted with the dough hook, but you can do it by hand if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36E20EF305DE741A8BCF571AF6DD4F5', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 4, NULL, NULL, 2, NULL, 0, 'Divide dough into two equal pieces and form each piece into a ball. Lightly oil and place into two separate sealed containers. Lidded glass food storage bowls or quart-sizes takeout containers works well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09B3B650B3A4524B95EBF15EF691D04D', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 5, NULL, NULL, 2, NULL, 0, 'Let dough rise in the fridge for at least 24 hours, up to 72 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D45A8F072CBAA468B19A6DD5B0A73C5', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 6, NULL, NULL, 2, NULL, 0, 'An hour before you you want to bake the pizzas, remove dough from refrigerator. Set a pizza stone or steel on a rack in the center of the oven and preheat oven to 500°F (260°C) or higher if your oven goes higher.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A04960CD702BC143BE8369CD2CD66F3E', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 7, NULL, NULL, 2, NULL, 0, 'When ready to bake, remove dough from container. If you''re a newer pizza maker and would like the extra security of a less sticky dough, feel free to flour it generously.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38C08EDA55B5BF49B28417CF1E03E2AC', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 8, NULL, NULL, 2, NULL, 0, 'Stretch the dough into a rough 14-inch round. You do not need to do a perfect job to end up with good pizza.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E0AD8E1B5BC9745AEFD0E5171C3E28A', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 9, NULL, NULL, 2, NULL, 0, 'Top the pizza judiciously. For cheese pizza, I use a not-obscene amount of thick tomato sauce, grated pecorino cheese, and low-moisture whole-milk mozzarella that I shred right before topping.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FAFBCE4576C9249A34E0B6408AE2826', X'5437EBA2D864F14686F4C237BF916B0A', X'5437EBA2D864F14686F4C237BF916B0A', 10, NULL, NULL, 2, NULL, 0, 'Slide the pizza into the oven and bake for 10 minutes, until cheese is melted and bubbly and crust is cooked and browned in spots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CA742C293DAC24AB594F8D325E47827', X'405F6195B2F52F4D8FA54438EC211B80', X'405F6195B2F52F4D8FA54438EC211B80', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 500°F with a pizza stone, upside down-sheet pan or 12-inch cast iron skillet on a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FD500EA9DA3154A9791CB34D765F5EA', X'405F6195B2F52F4D8FA54438EC211B80', X'405F6195B2F52F4D8FA54438EC211B80', 1, NULL, NULL, 2, NULL, 0, 'To make the pesto, fit a food processor with the blade. Place half the watercress in the bowl along with the spinach, olive oil, lemon juice, garlic, pecorino, salt and half the walnuts. Pulse until finely minced and well combined, stopping to scrape down the sides once or twice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE68200F6215A241B0F40E36DEE2218E', X'405F6195B2F52F4D8FA54438EC211B80', X'405F6195B2F52F4D8FA54438EC211B80', 2, NULL, NULL, 2, NULL, 0, 'Divide the dough into two equal portions and stretch each into a 12-inch circle. If you like, place each prepared dough round on a piece of parchment paper to make it easier to slide in and out of the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECCFAE171499E444BFA0CB8A9B5B67E3', X'405F6195B2F52F4D8FA54438EC211B80', X'405F6195B2F52F4D8FA54438EC211B80', 3, NULL, NULL, 2, NULL, 0, 'Spread half the pesto onto each piece of dough and top with the mozzarella. Bake for about 10 minutes, until browned and bubbly. Sprinkle with the remaining walnuts and the blue cheese and return to the oven for one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2091246A9BD0547AC82506C9A8ACBD1', X'405F6195B2F52F4D8FA54438EC211B80', X'405F6195B2F52F4D8FA54438EC211B80', 4, NULL, NULL, 2, NULL, 0, 'Drizzle pizzas with balsamic glaze and top with remaining watercress. Cut into slices and serve right away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C16A47478379B54A8F7CF17716AF53B6', X'44BE6F389A8C134EA8DF567D2512792D', X'44BE6F389A8C134EA8DF567D2512792D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 500°F with a rack in the center. If you have a pizza stone, preheat it on the center rack. (If not, you can use an overturned half sheet pan.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'764FA85CF348E94DA7065B9BDE1F3A95', X'44BE6F389A8C134EA8DF567D2512792D', X'44BE6F389A8C134EA8DF567D2512792D', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch nonstick frying pan, melt butter over medium heat. Add mushrooms, garlic and salt and raise heat to high. Toss mushrooms to coat with butter. Cook for 5 to 10 minutes, until they are beginning to brown in spots and have significantly reduced in volume. Add wine and cook until all the liquid is gone. Stir in heavy cream, asparagus and tarragon, reduce the heat to simmer briskly, and cook for an addition couple of minutes, until the cream has thickened to coat the vegetables without dripping.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8D691EB0A254A42851D95538F32E81D', X'44BE6F389A8C134EA8DF567D2512792D', X'44BE6F389A8C134EA8DF567D2512792D', 2, NULL, NULL, 2, NULL, 0, 'Rub pizza dough with olive oil and divide into two equal portions. Stretch each portion into a 12-inch round. I like to place each round on a piece of parchment to make transfer to and from the oven much easier. Top each with half the vegetable mixture, half the gruyere and half the pecorino. Bake for about 1o minutes, until dough is crisp on the underside and cheese is bubbly. (I bake one pizza at a time, putting the second one in the oven as we sit down to eat the first.) Sprinkle with parsley and pepper. Cut into slices and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCBDFB39D5BA514FA1DC08574139484D', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 500°F with a rack in the center. Preheat a pizza stone on the center rack if you have one.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E41A8AAE8220D3459423D2D1DCBEBF1C', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch skillet, cook bacon until crispy. Remove bacon from pan and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4B85D8ED00F894F80181D646B672A42', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 2, NULL, NULL, 2, NULL, 0, 'Drain off all but 1 tablespoon fat from pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E5B6E4D7A1BDE479553EC2D491F8D62', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 3, NULL, NULL, 2, NULL, 0, 'Return pan to medium heat and add onion and garlic. Cook, stirring occasionally, until beginning to soften, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B81BE719B535E4EB1B0D510C07710D9', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 4, NULL, NULL, 2, NULL, 0, 'Stir in cauliflower along with 1/4 cup water. Cover and cook until just tender. Sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D99120C52C10B34EA9616225E914DF1B', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, rub the olive oil over the pizza dough and stretch dough to cover a half sheet pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B2F3264B7319241900CED886C084BFD', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 6, NULL, NULL, 2, NULL, 0, 'Spread sauce over dough and sprinkle with mozzarella and rosemary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E9D9FD142702B47BBFE137F4CEA4D45', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 7, NULL, NULL, 2, NULL, 0, 'Spread cauliflower mixture evenly over pizza and sprinkle with pecorino.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8688F9B9E693B479B4E5FD800230CE4', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 8, NULL, NULL, 2, NULL, 0, 'Bake for 12-15 minutes, until crust is done and cauliflower has some nice charred spots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'218C5BD03004784384429CF19534CE56', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle pizza with reserved bacon and some flaky sea salt and freshly ground black pepper. Drizzle with balsamic glaze.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'464E5A8AE584364DBFD6E9B8A3C40619', X'EDEA954EC8D4184E957637B20CEA1118', X'EDEA954EC8D4184E957637B20CEA1118', 10, NULL, NULL, 2, NULL, 0, 'Cut into rectangles and serve right away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89B78191FD579944BE70ADC37C8E4610', X'B1151F36817EB446B20C04CF7D75DCEC', X'B1151F36817EB446B20C04CF7D75DCEC', 0, NULL, NULL, 2, NULL, 0, 'Place a pizza stone (or, in a pinch, an upside-down baking sheet) on a rack in the center of the oven. Preheat oven to 500°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9195E02F603CE448BC0E3A72DE0A1889', X'B1151F36817EB446B20C04CF7D75DCEC', X'B1151F36817EB446B20C04CF7D75DCEC', 1, NULL, NULL, 2, NULL, 0, 'Divide the pizza dough into two portions and stretch each into a 12-inch round. If you like, place each on a sheet of parchment just slightly larger than the dough. Top each dough round with 1/2 cup tomato sauce and spread well. Sprinkle each with half the mozzarella. Bake for about 10 minutes, until browned and bubbly. (I bake one at a time -- the first right before we''re ready to eat and the second while we eat the first.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04C48B37126BAD459172EA73BF04CF20', X'B1151F36817EB446B20C04CF7D75DCEC', X'B1151F36817EB446B20C04CF7D75DCEC', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, toss the arugula with the olive oil, lemon juice, flaky salt and plenty of freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F9EE99DDFBCBB4497590552D3775BCD', X'B1151F36817EB446B20C04CF7D75DCEC', X'B1151F36817EB446B20C04CF7D75DCEC', 3, NULL, NULL, 2, NULL, 0, 'When each pizza is done, tear the prosciutto slices in half and arrange on top of pizza. Heap arugula salad in the center, then shower with curls of Parmigiano cheese. Top with more black pepper if you like, and drizzle with balsamic glaze. Cut into slices and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A2840B3E7FA4D4084C9B1BD3FD9A7CD', X'EC5108526D4B9D4AA3C69B2575F925D6', X'EC5108526D4B9D4AA3C69B2575F925D6', 0, NULL, NULL, 2, NULL, 0, 'Warm olive oil over medium heat in a medium pot. Add garlic and cook until very fragrant, about a minute. Add broccoli along with 1/4 cup water, 1/4 teaspoon salt and the red pepper flakes. Stir, cover and adjust heat to maintain a gentle simmer. Cook until broccoli breaks down easily when pressed with a spatula or wooden spoon, about 30 minutes. Off the heat, stir in lemon juice and mash to a coarse puree.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53D563C1DC1E4A4481C864E254336C61', X'EC5108526D4B9D4AA3C69B2575F925D6', X'EC5108526D4B9D4AA3C69B2575F925D6', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 400°F with a rack in the center. Brush naan lightly with olive oil. Spread 1/4 of the ricotta on each naan. Top each naan with a scant 1/4 of the broccoli puree (use your judgment, there may be some left over), a sprinkle of the parmesan (reserve some to sprinkle when removed from oven) and 1/4 of the chopped olives.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'219843815AFC4744ADBA574AABB94B09', X'EC5108526D4B9D4AA3C69B2575F925D6', X'EC5108526D4B9D4AA3C69B2575F925D6', 2, NULL, NULL, 2, NULL, 0, 'Bake directly on the oven rack for about 10 minutes, until naan is crisp on the underside and ricotta is heated through. Sprinkle with black pepper and remaining parmesan, cut into wedges and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FABB8B145DFB1F4FA443833E1F8109BB', X'5DF29E90B21A8F45908584FB81A75CE5', X'5DF29E90B21A8F45908584FB81A75CE5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEB68966BB03A54591412BB20485D284', X'5DF29E90B21A8F45908584FB81A75CE5', X'5DF29E90B21A8F45908584FB81A75CE5', 1, NULL, NULL, 2, NULL, 0, 'Brush olive oil over the surface of the naan. In a small bowl, stir together the tomato paste and tomato sauce. Spread sauce over naan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3871EE26993D54BA3A4E11AF14EA35A', X'5DF29E90B21A8F45908584FB81A75CE5', X'5DF29E90B21A8F45908584FB81A75CE5', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle 2/3 of the pecorino over tomato sauce. Arrange mozzarella overtop.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D67721E67E48A746891F63F348D2CC83', X'5DF29E90B21A8F45908584FB81A75CE5', X'5DF29E90B21A8F45908584FB81A75CE5', 3, NULL, NULL, 2, NULL, 0, 'Place pizzas directly on oven rack and bake for 10 minutes or until cheese is melted and bubbly. Remove from oven and sprinkle with remaining pecorino, some pepper and basil if desired. Serve immediately, cut into slices of whatever size and shape you like you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D71677BED9C4647801A258F6C6502D3', X'756C634DD3B9724193CEB139C11220E3', X'756C634DD3B9724193CEB139C11220E3', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 550°F (or as high as your oven will go up to 550°F), with a pizza stone if you have one. Prepare your pizza pan or baking sheet by brushing with the olive oil and sprinkling evenly with the cornmeal. Roll or stretch the dough to cover the surface of the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23C16E17A84DC74385282C188ACF64E7', X'756C634DD3B9724193CEB139C11220E3', X'756C634DD3B9724193CEB139C11220E3', 1, NULL, NULL, 2, NULL, 0, 'Slice the zucchini as thinly as possible, on a mandoline if you have one or with a very sharp knife. Place zucchini slices in a bowl and toss with half the pesto. Spread the remaining pesto evenly over the dough and top with overlapping slices of zucchini. Sprinkle the feta and olives overtop. Bake until the dough is cooked through and the zucchini is tender, 8 to 15 minutes, depending on thickness. Slice and serve hot or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AF66EC79F5098458A705D8A302712AF', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 0, NULL, NULL, 2, NULL, 0, 'Warm the olive oil over medium heat in a 12-inch nonstick frying pan. Add the sliced onions along with a good pinch of salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B5313AB205F16459F7D92220B6FE163', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 1, NULL, NULL, 2, NULL, 0, 'Cook, stirring often, until the onions are soft, lightly brown, and reduced to about 1/3 of their raw volume. You may need to reduce the heat gradually as the onions shrink. This process will take about 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EB8D6DE35F51D4690F7E085129B530F', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small bowl, toss the strawberry slices with 1 teaspoon of the balsamic vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C36CF8B4CC3872458729F14C8B56425F', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 3, NULL, NULL, 2, NULL, 0, 'Wash the dandelion greens in several changes of water and dry thoroughly. Cut into bite-sized pieces if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38F5D9C06E80404ABE4DF46884AAED5A', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 4, NULL, NULL, 2, NULL, 0, 'When the onions are nearly done, add the remaining 1 tablespoon + 2 teaspoons balsamic vinegar to the pan and continue cooking until it has thickened to coat the onions, a minute or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C74A168978B854995910F3D5EEF1579', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 5, NULL, NULL, 2, NULL, 0, 'Remove the onions from the heat and stir in the Dijon mustard.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1049E2BCAF5A347A3F29A1E20EE5E95', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 6, NULL, NULL, 2, NULL, 0, 'In a salad bowl, combine the greens, onions and strawberries with all their juices, scraping down the onion pan with a rubber spatula to incorporate all the oil and vinegar into the salad.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DE58FC14F89B84295A0F01F1711AE5D', X'43BF09B5D871DC4EACCD24677D419DA4', X'43BF09B5D871DC4EACCD24677D419DA4', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle with salt and pepper and toss.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F820D8F5C7FC2949AEC5C7C7CC10A8C7', X'93F6BFC9652F5D4EB3202F399D7DBCB0', X'93F6BFC9652F5D4EB3202F399D7DBCB0', 0, NULL, NULL, 2, NULL, 0, 'Trim the fennel: Cut a thin slice off the root end of the bulb to remove any tough, dry parts. Cut off the fronds and green stems.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EF28FE3A6EAC3499C9AC5B0DFC3A68D', X'93F6BFC9652F5D4EB3202F399D7DBCB0', X'93F6BFC9652F5D4EB3202F399D7DBCB0', 1, NULL, NULL, 2, NULL, 0, 'Halve the fennel bulbs: You should now have just the white part of the fennel left. Cut each bulb in half from top to bottom.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33BA423C653EF247802F9476E140CD0E', X'93F6BFC9652F5D4EB3202F399D7DBCB0', X'93F6BFC9652F5D4EB3202F399D7DBCB0', 2, NULL, NULL, 2, NULL, 0, 'Use a mandoline slicer to thinly slice each half. If you don''t have a mandoline, you can use a chef''s knife or the slicing disk of a food processor. Cut as thinly as possible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C1BBC599035B7498A04FD503AB4D601', X'93F6BFC9652F5D4EB3202F399D7DBCB0', X'93F6BFC9652F5D4EB3202F399D7DBCB0', 3, NULL, NULL, 2, NULL, 0, 'Wash the fennel slices well and dry them well. We use a salad spinner for this.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AE7F1FA3A8FD045AB0623CF98240190', X'93F6BFC9652F5D4EB3202F399D7DBCB0', X'93F6BFC9652F5D4EB3202F399D7DBCB0', 4, NULL, NULL, 2, NULL, 0, 'Arrange the fennel on a serving plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B61C75DB15A9814B8DB1B0AE0712D9A9', X'93F6BFC9652F5D4EB3202F399D7DBCB0', X'93F6BFC9652F5D4EB3202F399D7DBCB0', 5, NULL, NULL, 2, NULL, 0, 'To dress the salad, sprinkle it with the oil, caper brine, lemon juice, minced shallots, minced capers, fennel fronds, lemon zest, and salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6A80303E79F074D89203F31D0BDA587', X'7D640C58C5DE264EA300AB1233A2E48C', X'7D640C58C5DE264EA300AB1233A2E48C', 0, NULL, NULL, 2, NULL, 0, 'Drain water or oil from tuna and place into a medium mixing bowl. Break up any large pieces of tuna with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB42C19C5A3B054E880419FC6C429F11', X'7D640C58C5DE264EA300AB1233A2E48C', X'7D640C58C5DE264EA300AB1233A2E48C', 1, NULL, NULL, 2, NULL, 0, 'Add celery, shallot, mustard, mayonnaise, salt, and pepper, along with any of the optional additions. Stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E7BEF13013AE458FC5279D532B678D', X'7D640C58C5DE264EA300AB1233A2E48C', X'7D640C58C5DE264EA300AB1233A2E48C', 2, NULL, NULL, 2, NULL, 0, 'Serve with salad or on a sandwich or tuna melt. For salads and sandwiches, I like to add a little freshly squeezed lemon juice right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4790012F1C3A99408B2F52F8FB247A85', X'666AB89E66D2C1418E06B4E0A23F3501', X'666AB89E66D2C1418E06B4E0A23F3501', 0, NULL, NULL, 2, NULL, 0, 'Trim the ends off the zucchini and cut into noodles according to the instructions on your spiralizer. Line two rimmed sheet pans with thick layers of paper towels. Spread half of the zucchini noodles onto each pan and sprinkle with the salt. Cover with another thick layer of paper towels and press gently. Leave noodles to dry while you prepare the rest of the dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FA3647DA11B3747BDAEC9511CFFCF01', X'666AB89E66D2C1418E06B4E0A23F3501', X'666AB89E66D2C1418E06B4E0A23F3501', 1, NULL, NULL, 2, NULL, 0, 'Warm olive oil over medium heat in a very large pan or wide, shallow pot. (A 5-quart Dutch oven, a 12-inch frying pan with high sides, or even a wok would all work well.) Add shallot, garlic, and capers, and cook, stirring frequently, for two minutes. Stir in tomatoes and cook, stirring occasionally, until most of them have burst.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E49B8C75BCB8C441A069A8D039D11B3A', X'666AB89E66D2C1418E06B4E0A23F3501', X'666AB89E66D2C1418E06B4E0A23F3501', 2, NULL, NULL, 2, NULL, 0, 'While tomatoes cook, cut the kernels from the corn cobs. Add corn kernels and chickpeas to pan and cook for about two minutes, until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235FCFFFDBC8E443A11A157B57DF7150', X'666AB89E66D2C1418E06B4E0A23F3501', X'666AB89E66D2C1418E06B4E0A23F3501', 3, NULL, NULL, 2, NULL, 0, 'Pat zucchini noodles dry with the paper towels and stir noodles into the vegetable mixture in the pan. (Tongs work really well if you have them.) Cook until noodles are crisp-tender, just a couple of minutes. The tomatoes and zucchini will create a bit of delicious sauciness in the bottom of the pan. Stir this to coat the noodles. Remove pan from heat and divide noodles and saucy vegetables among wide, shallow serving bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DC4A7FD3A03924D82CAC54B03B8AD10', X'666AB89E66D2C1418E06B4E0A23F3501', X'666AB89E66D2C1418E06B4E0A23F3501', 4, NULL, NULL, 2, NULL, 0, 'Cut slices of Bayonne Ham into long, thin strips, about four per slice. Divide strips among serving bowls. The residual heat from the noodles will warm the ham just enough to be beautifully tender and flavorful, without cooking it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EEFF56B4E20924291414D4D403843C6', X'666AB89E66D2C1418E06B4E0A23F3501', X'666AB89E66D2C1418E06B4E0A23F3501', 5, NULL, NULL, 2, NULL, 0, 'Garnish each bowl with some basil, freshly ground black pepper, and grated cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B6B844A52858048B3DD730AD9F0F109', X'1C4830B22027F241AE584E87B52FE1C9', X'1C4830B22027F241AE584E87B52FE1C9', 0, NULL, NULL, 2, NULL, 0, 'Slice the goat cheese into four rounds and place them in a small, shallow bowl in a single layer. Don''t worry if they''re not gorgeous, since you''ll be spreading them on the croutons later anyway.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BDE6AA5B09ABD47BD0E6D226EC8AFC8', X'1C4830B22027F241AE584E87B52FE1C9', X'1C4830B22027F241AE584E87B52FE1C9', 1, NULL, NULL, 2, NULL, 0, 'Pour 2 tablespoons of the olive oil over top and sprinkle evenly with the thyme and pepper. Ideally, you should marinate these in the fridge for a few hours, but if you don''t have the time, just set it aside at room temperature while you prepare the rest of the salad.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4372EE47763B0439413A04FE4A9ED3B', X'1C4830B22027F241AE584E87B52FE1C9', X'1C4830B22027F241AE584E87B52FE1C9', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven or a toaster oven to 400°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D3EF72897B7A54D8381ABE526AA174B', X'1C4830B22027F241AE584E87B52FE1C9', X'1C4830B22027F241AE584E87B52FE1C9', 3, NULL, NULL, 2, NULL, 0, 'Rub the baguette slices with the cut sides of the garlic clove, and brush them with the remaining teaspoon olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2436CEC5F7E2949A00EB7C13FF17079', X'1C4830B22027F241AE584E87B52FE1C9', X'1C4830B22027F241AE584E87B52FE1C9', 4, NULL, NULL, 2, NULL, 0, 'Bake in a small, shallow baking pan until lightly browned around the edges, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC303B29C9274449A2048F476BD1E3A8', X'1C4830B22027F241AE584E87B52FE1C9', X'1C4830B22027F241AE584E87B52FE1C9', 5, NULL, NULL, 2, NULL, 0, 'Let cool slightly. Then spread each crouton with one round of goat cheese; drizzle a bit of the marinade on top. Return to oven for about 5 minutes, until goat cheese is warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA9C9A24C4072E449209A71CDB8AEA3F', X'6E698788CD27B84F9B743EE688FA593C', X'6E698788CD27B84F9B743EE688FA593C', 0, NULL, NULL, 2, NULL, 0, 'Cut each slice of bacon crosswise into about eight pieces. (They''ll shrink to bite-sized as they cook.) Protip: we like to cut them with kitchen shears rather than dirtying a cutting board.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A036FE5CDF67354BBFDA634FA95B7A35', X'6E698788CD27B84F9B743EE688FA593C', X'6E698788CD27B84F9B743EE688FA593C', 1, NULL, NULL, 2, NULL, 0, 'In a 10-inch frying pan, cook bacon, stirring frequently, until just crisp.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A8F4C53A2F0D846ABCD3EE7FEE7CB25', X'6E698788CD27B84F9B743EE688FA593C', X'6E698788CD27B84F9B743EE688FA593C', 2, NULL, NULL, 2, NULL, 0, 'While the bacon cooks, make the dressing in the bottom of a large mixing bowl. Whisk together the minced shallot, mustard, a big pinch of fine sea salt, and the olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD828191196697489903D7B67468A2C1', X'6E698788CD27B84F9B743EE688FA593C', X'6E698788CD27B84F9B743EE688FA593C', 3, NULL, NULL, 2, NULL, 0, 'When bacon is cooked, drain on paper towels. Whisk two tablespoons of the hot bacon fat into the dressing and discard the rest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6A324B42C37DB4BA62D0232ABD01FE8', X'6E698788CD27B84F9B743EE688FA593C', X'6E698788CD27B84F9B743EE688FA593C', 4, NULL, NULL, 2, NULL, 0, 'To poach the eggs, fill a wide sauté pan halfway with water. (A pan like this is ideal.) Add vinegar and bring water to a brisk simmer, then reduce heat so the water is virtually still. Crack each egg into a small bowl and tip carefully into the water, submerging the bowl a bit as you pour. I like to add the eggs in a clockwise circle starting near the handle so I remember in which order to remove them for even cooking. Cook undisturbed for four minutes, until whites are set and yolks are runny. Remove eggs from pan with a strainer spoon (something like this). Gently place eggs on a paper towel-lined plate and dab the tops to remove excess water. Cut away any scraggly whites if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F687C133ECB854D967B8492749D0C14', X'6E698788CD27B84F9B743EE688FA593C', X'6E698788CD27B84F9B743EE688FA593C', 5, NULL, NULL, 2, NULL, 0, 'Tear the greens into bite-sized pieces, add to mixing bowl, and toss to coat with dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1D13BED0534C149946B6F0587AA4D5C', X'6E698788CD27B84F9B743EE688FA593C', X'6E698788CD27B84F9B743EE688FA593C', 6, NULL, NULL, 2, NULL, 0, 'Divide greens among four plates, top each with a poached egg, and sprinkle with some of the bacon. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65527460C761E14F81AD04F979C49C4E', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', 0, NULL, NULL, 2, NULL, 0, 'Prepare lentils according to our easy recipe for a perfect pot of lentils.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBF87D0F617EB04DBD4B207161580B69', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', 1, NULL, NULL, 2, NULL, 0, 'Trim ends from broccolini. Bring 1 inch of water and 1 teaspoon fine sea salt to a boil in a 12-inch lidded pan. Add broccolini and cook until it reaches your desired tenderness. We like it crisp-tender, about 5 minutes'' cooking time. Drain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'231F1C428041604F8861A7334415ACF8', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', 2, NULL, NULL, 2, NULL, 0, 'Dice halloumi into 1/2-inch pieces. Heat oil in another 12-inch pan over medium heat until shimmering. (Or dry and reuse the same pan if the broccolini is done). Add halloumi and cook, turning a few times, until golden brown on several sides and warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B28337C38FE942458983D552D2EA4D91', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', 3, NULL, NULL, 2, NULL, 0, 'Drain roasted red peppers.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B8AF3058AE78343A06E48B3B78879B2', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', X'DFEFB0489F6BE7439AA9CEB6B9A021CC', 4, NULL, NULL, 2, NULL, 0, 'Divide ingredients among four bowls, or let diners serve themselves, and drizzle everything with magic blender dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0079447A5498884BB39C18138A550EF6', X'FBFAEE06EDB9564D850A080E4CA2E408', X'FBFAEE06EDB9564D850A080E4CA2E408', 0, NULL, NULL, 2, NULL, 0, 'Place lettuce in a wide serving bowl (or plate salads individually in wide bowls like these or these). Halve or quarter tomatoes depending on size and place in a strip across serving bowl. Quarter the hard-boiled eggs lengthwise and arrange next to tomatoes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'139BD508CCB3C44D8A77307DADAC79E0', X'FBFAEE06EDB9564D850A080E4CA2E408', X'FBFAEE06EDB9564D850A080E4CA2E408', 1, NULL, NULL, 2, NULL, 0, 'Halve, pit, and slice avocados but leave slices in shells until ready to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'050695819983824ABA411D2F14E3D332', X'FBFAEE06EDB9564D850A080E4CA2E408', X'FBFAEE06EDB9564D850A080E4CA2E408', 2, NULL, NULL, 2, NULL, 0, 'Chop bacon into bite-sized pieces and place in a 10-inch nonstick frying pan over medium-high heat. Cook, stirring frequently, until browned and crisp. Remove bacon to paper towels to drain and pour off all but two tablespoons or so of the bacon fat from the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D1D93D1BB42EF44BF52069A6CEC2E9E', X'FBFAEE06EDB9564D850A080E4CA2E408', X'FBFAEE06EDB9564D850A080E4CA2E408', 3, NULL, NULL, 2, NULL, 0, 'Season chicken breasts well with salt and pepper and place in pan. Reduce heat to medium. Cook uncovered until nicely browned on the underside, about five minutes. Then flip, pour in about two tablespoons of water, and cover pan. Continue cooking until chicken breasts are just opaque all the way through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81F54236C64E6841A24254F759712B36', X'FBFAEE06EDB9564D850A080E4CA2E408', X'FBFAEE06EDB9564D850A080E4CA2E408', 4, NULL, NULL, 2, NULL, 0, 'Arrange bacon, avocado slices and blue cheese on salad. Dice chicken and place onto salad.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E25B8383005634E851CDA6C3389D597', X'FBFAEE06EDB9564D850A080E4CA2E408', X'FBFAEE06EDB9564D850A080E4CA2E408', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle some fine sea salt over both the eggs and the avocado, and then hit the whole salad with some freshly ground black pepper and flaky sea salt. Drizzle with dressing and serve with more dressing to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'771D43EA8B99E14A9B524E3E98308E7D', X'33F27EE67A845141AD9DFE4C9DF9B26A', X'33F27EE67A845141AD9DFE4C9DF9B26A', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 400° F with a 12-inch cast-iron skillet (or a rimmed sheet pan) on a rack in the center. When preheated, carefully toss potato cubes with two tablespoons of the olive oil and a generous pinch of salt in the skillet. Spread potatoes evenly over the pan surface to avoid crowding. Roast for about 20 minutes, turning with a spatula halfway through, until crisp on the outside and tender on the inside. Set aside to cool.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5BBDB9520E82640879C9D10B1FDEA09', X'33F27EE67A845141AD9DFE4C9DF9B26A', X'33F27EE67A845141AD9DFE4C9DF9B26A', 1, NULL, NULL, 2, NULL, 0, 'While potatoes roast, prepare corn (microwaving from frozen is fine!) Strip washed and dried kale from its stems and tear into small bite sized-pieces. Place in a large bowl. Pour in about 1/4 cup of dressing and, using your hands, massage dressing into kale. This process will tenderize the kale and reduce its volume by almost half. Add chickpeas and olives to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB0BFFCB39E3C0458323BF7AEA70AA2F', X'33F27EE67A845141AD9DFE4C9DF9B26A', X'33F27EE67A845141AD9DFE4C9DF9B26A', 2, NULL, NULL, 2, NULL, 0, 'Place a 12-inch nonstick frying pan over medium-high heat and warm the remaining tablespoon of olive oil. Add the halloumi cubes and cook, stirring occasionally, until lightly browned on most sides and warmed through. Add the cheese and potatoes to the salad bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE2BE952C552DD488667214660CE9D08', X'33F27EE67A845141AD9DFE4C9DF9B26A', X'33F27EE67A845141AD9DFE4C9DF9B26A', 3, NULL, NULL, 2, NULL, 0, 'Pour a little more vinaigrette onto the salad and toss all ingredients together gently. Sprinkle on the nutritional yeast and toss once more. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A18E8AD05907DD48B6248457CB18FFFC', X'A34E45BBDE36834C80E0E6A1D8AFA20C', X'A34E45BBDE36834C80E0E6A1D8AFA20C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center. Cut the sweet potatoes into 1/2-inch dice. Place on a sheet pan and toss with olive oil and some salt and pepper. Roast until lightly crisp on the outside and tender on the inside, 25-30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B8BAF9208A1EC469EC7AC9C692B14BD', X'A34E45BBDE36834C80E0E6A1D8AFA20C', X'A34E45BBDE36834C80E0E6A1D8AFA20C', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, place freekeh in a small pot along with three cups water and a big pinch of salt. Bring to a boil, then reduce heat and simmer, covered, for 20 minutes. Remove from heat and let rest for five minutes. Drain any remaining water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'291DEDD6398E3C43809A9666100EF3EE', X'A34E45BBDE36834C80E0E6A1D8AFA20C', X'A34E45BBDE36834C80E0E6A1D8AFA20C', 2, NULL, NULL, 2, NULL, 0, 'In a separate small pot, place lentils, herbs and a big pinch of salt. Cover with water by an inch. Bring to a boil, then reduce heat and simmer, uncovered, until tender, about 25 minutes. Drain remaining water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3B7148CED7B1343B4DC2F821C846100', X'A34E45BBDE36834C80E0E6A1D8AFA20C', X'A34E45BBDE36834C80E0E6A1D8AFA20C', 3, NULL, NULL, 2, NULL, 0, 'Strip kale from stems and break leaves into bite-sized pieces. Toss well with a few tablespoons of dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97E80ED6AA596648A33CB8BA60ACFA96', X'A34E45BBDE36834C80E0E6A1D8AFA20C', X'A34E45BBDE36834C80E0E6A1D8AFA20C', 4, NULL, NULL, 2, NULL, 0, 'Place some kale salad into each serving bowl. Arrange sweet potatoes, freekeh and lentils on top. Drizzle with extra dressing and sprinkle with nutritional yeast if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06C832EA6CB6D345AD97481C667757E8', X'4856509E69C7E040908C50D34092EC6F', X'4856509E69C7E040908C50D34092EC6F', 0, NULL, NULL, 2, NULL, 0, 'In a large zip-top bag or a 9x12 glass baking dish, stir together the vegetable oil, lime juice, sherry vinegar, garlic, shallot, chili powder, soy sauce and salt. Place steak in bag or dish and coat well with marinade. Marinate for two hours at room temperature or 8 hours in the fridge, flipping once if possible to ensure even coverage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C50A7FADEBBA0E47B1DC524A32F1FE5D', X'4856509E69C7E040908C50D34092EC6F', X'4856509E69C7E040908C50D34092EC6F', 1, NULL, NULL, 2, NULL, 0, 'Heat a gas grill to high (the hotter the better) or prepare a charcoal grill for direct heat. Remove steak from marinade and shake off any excess liquid, then grill each side for just a few minutes, until outside is nicely seared in spots and internal temperature reaches 130°F (check with an instant-read thermometer) for medium-rare. Skirt, flatiron and flank steak are best at medium-rare.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFF621DC0C333D46BDE610A127310B63', X'4856509E69C7E040908C50D34092EC6F', X'4856509E69C7E040908C50D34092EC6F', 2, NULL, NULL, 2, NULL, 0, 'Remove steak to a cutting board and let rest for 5 to 10 minutes. Place baguette slices on the grill in the coolest spot and toast very briefly, flipping once, to crisp the outside and get some grill marks. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE17A0A506B7D346A4432F99ADDC2285', X'4856509E69C7E040908C50D34092EC6F', X'4856509E69C7E040908C50D34092EC6F', 3, NULL, NULL, 2, NULL, 0, 'Arrange some greens, tomatoes, cucumber, red onion and avocado in each of four bowls. Slice the steak thinly against the grain and top each bowl with a generous serving. Drizzle salads with olive oil and balsamic glaze. Sprinkle with flaky salt and pepper and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACDBBBD9493BD34F8D4945B0AE44C24F', X'11D622668E6EE64E9A77B9E7C639436B', X'11D622668E6EE64E9A77B9E7C639436B', 0, NULL, NULL, 2, NULL, 0, 'Hard boil the eggs, peel when cool enough to handle, and set aside. You can do this well in advance if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81AE75C9ACC9FF4B97CA5609F35CBF62', X'11D622668E6EE64E9A77B9E7C639436B', X'11D622668E6EE64E9A77B9E7C639436B', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill to high (about 400-500°F). Place the salmon skin-side down on a double layer of aluminum foil. Sprinkle generously with salt and pepper and lay the lemon slices over the flesh. Grill salmon until done to your liking, about 8-10 minutes for just barely opaque in the center, depending on thickness. Place salmon in the center of a large serving platter with the lemon slices off to the side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F6B75ECE723164A8A4741C1F9759A80', X'11D622668E6EE64E9A77B9E7C639436B', X'11D622668E6EE64E9A77B9E7C639436B', 2, NULL, NULL, 2, NULL, 0, 'Rub asparagus with two tablespoons of the olive oil, sprinkle with salt and pepper, and grill until nicely charred, 2-3 minutes per side, flipping once. Add asparagus to platter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE57B42FADC0724C816EE3E4049125F1', X'11D622668E6EE64E9A77B9E7C639436B', X'11D622668E6EE64E9A77B9E7C639436B', 3, NULL, NULL, 2, NULL, 0, 'Trim tough ends from green beans and boil or steam until crisp-tender. If you like, plunge into a large bowl of ice water to stop cooking and preserve bright green color.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'337CCD862EFCA44DB32E8E2899584463', X'11D622668E6EE64E9A77B9E7C639436B', X'11D622668E6EE64E9A77B9E7C639436B', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the remaining olive oil, vinegar, mustard and plenty of salt and pepper. Whisk to emulsify.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1A31203A63B6A428E6E5FF474594AF6', X'11D622668E6EE64E9A77B9E7C639436B', X'11D622668E6EE64E9A77B9E7C639436B', 5, NULL, NULL, 2, NULL, 0, 'Add arugula and olives to platter. Halve hard-boiled eggs lengthwise and add them to the platter too.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'504B71C2ED43694EAA0D76F345F6506E', X'11D622668E6EE64E9A77B9E7C639436B', X'11D622668E6EE64E9A77B9E7C639436B', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle everything with a bit of flaky sea salt. Serve with dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E56E2BCB27A8A3439788387207AD2FBC', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, stir together the beans, celery, shallot, olives and parsley.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32A90AB5623E9843AF08A66AEABAD42E', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', 1, NULL, NULL, 2, NULL, 0, 'Add the oil, vinegar, lemon juice and zest, paprika if using, and salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C29082A63983B143AE4565C729605AC1', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', 2, NULL, NULL, 2, NULL, 0, 'Stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81C83E184C830641AE33BE49433BE7E4', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', X'0FA4FE01D0D6EE4DA3D0E77883762F0B', 3, NULL, NULL, 2, NULL, 0, 'Serve at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD18123C5D4A1F47870BC2EF729112A5', X'17B3C75ED422A0468B742278E701DB2F', X'17B3C75ED422A0468B742278E701DB2F', 0, NULL, NULL, 2, NULL, 0, 'Place the chickpeas in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADAD38C736B6054AB953EE3C5D15D68D', X'17B3C75ED422A0468B742278E701DB2F', X'17B3C75ED422A0468B742278E701DB2F', 1, NULL, NULL, 2, NULL, 0, 'In a small skillet, heat the oil until shimmering.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'083D849AE02E12428F35EF81E0337737', X'17B3C75ED422A0468B742278E701DB2F', X'17B3C75ED422A0468B742278E701DB2F', 2, NULL, NULL, 2, NULL, 0, 'Add the mustard seeds, partially cover the skillet and cook over medium-high heat until the mustard seeds almost stop popping, about a minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9927D8355763EE4DB39FF1924EDB155C', X'17B3C75ED422A0468B742278E701DB2F', X'17B3C75ED422A0468B742278E701DB2F', 3, NULL, NULL, 2, NULL, 0, 'Add the cumin and fennel seeds and cook for another minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FC400E95FE9B648BD92A7B5219B4F78', X'17B3C75ED422A0468B742278E701DB2F', X'17B3C75ED422A0468B742278E701DB2F', 4, NULL, NULL, 2, NULL, 0, 'Pour the hot oil and spices over the chickpeas.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F3A9D17E0CC704D9AF0B568D57BDFD8', X'17B3C75ED422A0468B742278E701DB2F', X'17B3C75ED422A0468B742278E701DB2F', 5, NULL, NULL, 2, NULL, 0, 'Stir in the yogurt, lemon zest and juice, scallions, cilantro and salt. Serve at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A561432685E1D4D855F7ABEE5A387B9', X'486D2F276E94064FB52EAAE25FFA43F7', X'486D2F276E94064FB52EAAE25FFA43F7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4CFFAEB7852B14092B6AF3641E85E56', X'486D2F276E94064FB52EAAE25FFA43F7', X'486D2F276E94064FB52EAAE25FFA43F7', 1, NULL, NULL, 2, NULL, 0, 'Toss the cauliflower florets and the chickpeas with the olive oil on a rimmed baking sheet. Sprinkle generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D890C1FF6E1BB44AB62515D7F879A7FE', X'486D2F276E94064FB52EAAE25FFA43F7', X'486D2F276E94064FB52EAAE25FFA43F7', 2, NULL, NULL, 2, NULL, 0, 'Roast, turning once halfway through, until the cauliflower is tender and browned in spots, about 35 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23B1321EEF93BF4DAB90A4E2A71DE291', X'486D2F276E94064FB52EAAE25FFA43F7', X'486D2F276E94064FB52EAAE25FFA43F7', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine the sour cream, mayonnaise, vinegar, and blue cheese in a small bowl. Stir all ingredients together with a fork, breaking up any large chunks of blue cheese as you go.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD11982615B0CF4AA60FEDB8ACD53FB4', X'486D2F276E94064FB52EAAE25FFA43F7', X'486D2F276E94064FB52EAAE25FFA43F7', 4, NULL, NULL, 2, NULL, 0, 'Mix together the melted butter and Frank''s Red Hot in a small bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB594F236F1EFE449BCCACE9AA189F86', X'486D2F276E94064FB52EAAE25FFA43F7', X'486D2F276E94064FB52EAAE25FFA43F7', 5, NULL, NULL, 2, NULL, 0, 'Pour over the cauliflower and chickpeas and toss to combine. Serve immediately, either on a platter with the dressing on the side or tossed with the greens and tomatoes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'992484B327C8E24BA83028D69A376BD4', X'45E64FD2D38C0049BA68EAE25EE17651', X'45E64FD2D38C0049BA68EAE25EE17651', 0, NULL, NULL, 2, NULL, 0, 'Fit a standard 12-cup food processor with the slicing disk. Feed Brussels sprouts through the feed tube to slice. Place sliced sprouts into a salad bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6B6D25855BAAC42BCBFF55A92BA308D', X'45E64FD2D38C0049BA68EAE25EE17651', X'45E64FD2D38C0049BA68EAE25EE17651', 1, NULL, NULL, 2, NULL, 0, 'Place pine nuts in a small, dry pan over medium heat. Cook for a few minutes, tossing once or twice, until fragrant and lightly browned. Add to salad bowl along with cheese, capers, pepper and dressing. Toss well and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30EB7DA805A8F2428404F63CEA962393', X'C1512087328F6C40B1CD25FEDDBBA901', X'C1512087328F6C40B1CD25FEDDBBA901', 0, NULL, NULL, 2, NULL, 0, 'Dry tofu well between several layers of paper towels. Cut into approximate 1/2-inch cubes and dry again. Place in a mixing bowl and sprinkle with cornstarch, salt and pepper. Toss gently to coat evenly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49B21220FFBFE946B1372BF8B2985104', X'C1512087328F6C40B1CD25FEDDBBA901', X'C1512087328F6C40B1CD25FEDDBBA901', 1, NULL, NULL, 2, NULL, 0, 'Warm 2 tablespoons of the oil over medium-high heat in a 12-inch nonstick skillet. Add tofu in a single layer and cook until golden brown on the bottom, then stir once in a while and continue cooking until most sides are golden. This will take about 10 minutes total. Slide tofu out onto a plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8DD4363E744EF49BFC4214629BE65CC', X'C1512087328F6C40B1CD25FEDDBBA901', X'C1512087328F6C40B1CD25FEDDBBA901', 2, NULL, NULL, 2, NULL, 0, 'In the same pan, heat the remaining tablespoon oil. Add bell pepper and mushrooms and cook for a few minutes, stirring only occasionally, until mushrooms have browned in spots and peppers are slightly tender. Add cabbage, tamari and vinegar and cook, stirring, until cabbage is tender and reduced in volume by at least half, which will take only a couple of minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0A1DA61BA4B6449AECFD733E0016772', X'C1512087328F6C40B1CD25FEDDBBA901', X'C1512087328F6C40B1CD25FEDDBBA901', 3, NULL, NULL, 2, NULL, 0, 'There will be some tasty liquid at the bottom of the pan. Depending on your sauce preferences, you can remove the vegetables from the skillet with tongs or a strainer to avoid the liquid, go with the flow and serve it as-is, or remove the vegetables and then simmer the liquid down into a thicker sauce. (The cornstarch from the tofu will aid this process.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9A5628D4342CC4D90122137CF7C6330', X'C1512087328F6C40B1CD25FEDDBBA901', X'C1512087328F6C40B1CD25FEDDBBA901', 4, NULL, NULL, 2, NULL, 0, 'Place vegetables on serving plates (alongside rice or other grains if you like) and top with reserved tofu. Sprinkle with chili sesame oil and cilantro as well as sauce if you like. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB15643C541B6D49BB890F1A2A767F48', X'6CF97ED8AD19954FB302EC9456B18FB9', X'6CF97ED8AD19954FB302EC9456B18FB9', 0, NULL, NULL, 2, NULL, 0, 'Combine rice, water and salt in a medium pot. Bring to a boil, then cover and simmer until tender but still nice and chewy, usually 30 to 40 minutes. (Check package instructions for timing, since brands may vary.) When possible, I like to do this a little bit ahead of time and leave the covered pot sitting on a cold stove for a while after cooking, since the rice steams a little more. It''s totally not necessary though.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19B38AC5A6903443A0466FE02B674F52', X'6CF97ED8AD19954FB302EC9456B18FB9', X'6CF97ED8AD19954FB302EC9456B18FB9', 1, NULL, NULL, 2, NULL, 0, 'Warm the oil over medium-high heat in a 12-inch nonstick skillet. Drain, rinse and pat dry chickpeas with paper towels. Add chickpeas to skillet in a single layer and leave undisturbed for a few minutes to brown. Toss once or twice and repeat to brown on another other side or two. Reduce heat to medium, sprinkle with onion powder, garlic powder and dill, and cook, stirring occasionally, for a few minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAB329A638F3CE4B9270011D5AEA362B', X'6CF97ED8AD19954FB302EC9456B18FB9', X'6CF97ED8AD19954FB302EC9456B18FB9', 2, NULL, NULL, 2, NULL, 0, 'Remove and discard kale stems and tear leaves into bite-sized pieces. In a large bowl, combine kale and about three tablespoons of the dressing. Toss well or massage dressing into kale with your hands (I like this method).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47744E7180831246BAA0BE5E7138BF67', X'6CF97ED8AD19954FB302EC9456B18FB9', X'6CF97ED8AD19954FB302EC9456B18FB9', 3, NULL, NULL, 2, NULL, 0, 'For each serving, arrange some kale salad, rice, chickpeas, peppers, cucumbers, snow or snap peas and avocado (if using) in little piles in a nice big bowl. Drizzle with dressing, sprinkle with pepitas, and serve. The bowls are great with warm rice and chickpeas but equally good at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DC62EDDA1607D40AFC094AB1FA90152', X'DC83B5641E7F5A4B8A359CC1C09E5624', X'DC83B5641E7F5A4B8A359CC1C09E5624', 0, NULL, NULL, 2, NULL, 0, 'Arrange the zoodles on a double layer of paper towels. Sprinkle with a little bit of salt. Cover with a double layer of paper towels and press gently. Leave while you make the sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3965179CE1D6A4BB545344DB16CF106', X'DC83B5641E7F5A4B8A359CC1C09E5624', X'DC83B5641E7F5A4B8A359CC1C09E5624', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch frying pan, heat the oil over medium-high heat. Add the onion, garlic and turkey and sprinkle with 1 teaspoon salt. Cook, stirring from time to time to break up the meat into bite-sized pieces, until turkey is cooked through and nicely browned in spots, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ACE5626BA0BE3489B7C239EC2F01D2B', X'DC83B5641E7F5A4B8A359CC1C09E5624', X'DC83B5641E7F5A4B8A359CC1C09E5624', 2, NULL, NULL, 2, NULL, 0, 'Add tomatoes, fennel, basil, thyme, oregano and red pepper flakes to pan and stir to incorporate. Reduce heat and simmer for about 10 minutes, until nice and thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F90356159E295408BBC8A3EF514E34E', X'DC83B5641E7F5A4B8A359CC1C09E5624', X'DC83B5641E7F5A4B8A359CC1C09E5624', 3, NULL, NULL, 2, NULL, 0, 'When ready to serve, give the zoodles a gentle squeeze between paper towels to remove as much moisture as possible. Warm the remaining teaspoon olive oil in a second frying pan. Add zoodles to frying pan and cook, tossing with tongs or a rubber spatula, for about a minute. They should retain a nice bite but not look raw.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5F940C98AF6664A800E5BEF61C371D4', X'DC83B5641E7F5A4B8A359CC1C09E5624', X'DC83B5641E7F5A4B8A359CC1C09E5624', 4, NULL, NULL, 2, NULL, 0, 'Divide zoodles among four bowls and top with sauce. Garnish with pepper, cheese and parsley, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCE6DC8BE15BF04BB67DF1D2CE3ED4F5', X'F1BAA535D52C4744B381EB6BB6FAB294', X'F1BAA535D52C4744B381EB6BB6FAB294', 0, NULL, NULL, 2, NULL, 0, 'Arrange the zoodles on a thick layer of paper towels. Sprinkle with a little bit of salt. Cover with another thick layer of paper towels and press gently. Leave while you make the sauce, longer if possible (20 minutes or so is ideal).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4E0015907C5864FA33D9E9C1C551940', X'F1BAA535D52C4744B381EB6BB6FAB294', X'F1BAA535D52C4744B381EB6BB6FAB294', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch frying pan, warm the butter and olive oil over medium-high heat. When butter melts, add shallot, garlic and capers and cook, stirring from time to time, until lightly browned, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'457D7E0DDB27D749A0D60D86F9D2DFF8', X'F1BAA535D52C4744B381EB6BB6FAB294', X'F1BAA535D52C4744B381EB6BB6FAB294', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, roll up the paper towels and give the zoodles a squeeze to remove as much moisture as possible. Add zoodles to frying pan and cook, tossing with tongs or a rubber spatula, for about a minute. They should retain a nice bite but not look raw. Turn off the heat and stir in the lemon juice, parsley and some pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'541C3B904C8F604FBC91340DCF45E94E', X'F1BAA535D52C4744B381EB6BB6FAB294', X'F1BAA535D52C4744B381EB6BB6FAB294', 3, NULL, NULL, 2, NULL, 0, 'Divide among four bowls, sprinkle with cheese, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B09279488679A34BA655DD26C42CE46A', X'0655F23E140F244EAE8DF7A19663D7A2', X'0655F23E140F244EAE8DF7A19663D7A2', 0, NULL, NULL, 2, NULL, 0, 'Quarter and core the radicchio. Cut each quarter into long, thin shreds. Place in a large mixing bowl and if you''re plating individual salads, toss with 1 teaspoon each olive oil and balsamic vinegar and a nice pinch of salt. Divide among four serving plates.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F82DC29A6CD848A101BFEC8F89F7B7', X'0655F23E140F244EAE8DF7A19663D7A2', X'0655F23E140F244EAE8DF7A19663D7A2', 1, NULL, NULL, 2, NULL, 0, 'Wash, trim and peel the beets. Slice them as thin as possible, then stack the slices and cut in half to make half-moons. A mandoline is great to use here if you have one. Place in the same large bowl and toss with another teaspoon each of oil and vinegar and a sprinkle of salt. Divide dressed slices among the serving plates and arrange as you like. Slice the avocado, squeeze lime juice overtop and arrange a few slices on each plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'599D85AE3AD7C240938D223FF5DFB528', X'0655F23E140F244EAE8DF7A19663D7A2', X'0655F23E140F244EAE8DF7A19663D7A2', 2, NULL, NULL, 2, NULL, 0, 'Slice the red onion as thin as you can and toss with remainder of vinegar. Sprinkle some onion slices over each plate. Slice the celery stalk into thin half-moons and divide among plates. Sprinkle walnuts over salads, along with feta if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ACCB02A4F931142A6B4F8A5F2B3A4E1', X'0655F23E140F244EAE8DF7A19663D7A2', X'0655F23E140F244EAE8DF7A19663D7A2', 3, NULL, NULL, 2, NULL, 0, 'Drizzle balsamic glaze over each salad along with the remaining teaspoon of olive oil. Sprinkle with flaky salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2B5DABCB182B84CA3A7BAC7433A9E51', X'7F7A101963DED740B7A098868A8B1DB7', X'7F7A101963DED740B7A098868A8B1DB7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'952A16AE7279374AB0B7A206812EBF0C', X'7F7A101963DED740B7A098868A8B1DB7', X'7F7A101963DED740B7A098868A8B1DB7', 1, NULL, NULL, 2, NULL, 0, 'Cut the squash into 1/4-inch thick rings (remove seedy centers with a biscuit cutter) or half-rings (halve squash first from stem to bottom and scoop out seeds with a spoon). Line a baking sheet with parchment paper, arrange squash slices on parchment, and brush tops with olive oil. Sprinkle with salt and pepper. Roast until lightly browned and tender, about 25 minutes total. Flip slices with a spatula halfway through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE276C2A2031EC469239F89DEA9CEEA2', X'7F7A101963DED740B7A098868A8B1DB7', X'7F7A101963DED740B7A098868A8B1DB7', 2, NULL, NULL, 2, NULL, 0, 'Strip the kale leaves from their stems and discard stems. Tear leaves into bite-sized pieces. Wash and dry thoroughly and place in a large salad bowl along with the onion and cabbage. Add two tablespoons olive oil, the cider vinegar and a good sprinkle of salt and pepper. Use your hands to massage the dressing into the veggies. This will ensure the kale and cabbage are nice and tender and the onions surrender a bit of their bite. Let sit while you prepare the rest of the salad.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'644F01D761F9364CBF624D15CC17CA07', X'7F7A101963DED740B7A098868A8B1DB7', X'7F7A101963DED740B7A098868A8B1DB7', 3, NULL, NULL, 2, NULL, 0, 'Set a small pan over medium heat and add walnuts and sugar. Keep a close eye on the pan as you cook, stirring occasionally at first and more frequently as you go along, to melt the sugar onto the nuts and lightly toast the nuts. Stop when nuts are nicely toasted, even if there''s still some unmelted sugar. They''ll be delicious either way.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F5453ABA8A07247AC77C87A94A6FC4F', X'7F7A101963DED740B7A098868A8B1DB7', X'7F7A101963DED740B7A098868A8B1DB7', 4, NULL, NULL, 2, NULL, 0, 'To serve the salad, gently toss in most of the walnuts and pomegranate arils. Arrange the squash slices overtop and sprinkle with a few more nuts and arils.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0BE01B1F358284D853B7552C1271951', X'303CC7FD7311E84E8ECF87F46CAEED4F', X'303CC7FD7311E84E8ECF87F46CAEED4F', 0, NULL, NULL, 2, NULL, 0, 'Cook the broccoli in the microwave according to package directions. To save time, you can use a bowl large enough to accommodate the broccoli and the corn and add the corn when the correct amount of cooking time remains. Drain vegetables and place in separate bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'304753FC47E0334080527350C06083BD', X'303CC7FD7311E84E8ECF87F46CAEED4F', X'303CC7FD7311E84E8ECF87F46CAEED4F', 1, NULL, NULL, 2, NULL, 0, 'While the vegetables cook, combine the shallot, vinegar, oil, mustard and salt in a small jar and shake to emulsify (or whisk together in a small bowl).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8203AD499B9F574E81CFFF762E2F918A', X'303CC7FD7311E84E8ECF87F46CAEED4F', X'303CC7FD7311E84E8ECF87F46CAEED4F', 2, NULL, NULL, 2, NULL, 0, 'We like to put all the ingredients in separate small serving bowls in the middle of the table, stick a spoon or fork in each, and let everyone assemble their own dinner. You could assemble each person''s serving beforehand if it''s easier to manage -- just arrange a bit of everything in its own little pile in each bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25BDFEEA3618964AB9E1E0C6046176C4', X'AE587F93D09FF64883CD8C98D6FC314F', X'AE587F93D09FF64883CD8C98D6FC314F', 0, NULL, NULL, 2, NULL, 0, 'Rinse the rice under running water until some of the milkiness of the runoff subsides. Place rice in a medium pot along with 2 1/2 cups cold water. Bring to a boil, then cover and reduce to a simmer. Cook for about 20 minutes, until water is absorbed. Remove from heat and let rest for a few minutes, then stir in seasoned rice vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B04495A1490404185883B679010D92A', X'AE587F93D09FF64883CD8C98D6FC314F', X'AE587F93D09FF64883CD8C98D6FC314F', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, dry the tofu with paper towels and cut into 1/2-inch cubes. Place in a bowl or large resealable plastic bag. Sprinkle with cornstarch, 1/2 teaspoon salt and 1/4 teaspoon pepper and toss to coat well. In a large nonstick frying pan, warm the vegetable oil over medium-high heat. Add tofu and cook until golden on one side, then flip and continue to cook until golden and crispy on most sides. This will take about 10 minutes total. Drain on paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5291A98F5050E2439A2228BEB3F8C072', X'AE587F93D09FF64883CD8C98D6FC314F', X'AE587F93D09FF64883CD8C98D6FC314F', 2, NULL, NULL, 2, NULL, 0, 'Place the edamame, avocado, cucumber, pomegranate seeds and nori into small serving bowls on the table along with gomasio and dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'609B668352457247A4F9E219C9848634', X'AE587F93D09FF64883CD8C98D6FC314F', X'AE587F93D09FF64883CD8C98D6FC314F', 3, NULL, NULL, 2, NULL, 0, 'To serve, give each person a bowl with some rice in it and let them arrange the rest of the ingredients on top as they like. Alternatively, you can pre-make each bowl if, let''s say, you don''t trust your kids to choose vegetables.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4AF0E47C30B3B47AD6D93D995423AD5', X'68B4EB67775D984C877DC61B47FC4F59', X'68B4EB67775D984C877DC61B47FC4F59', 0, NULL, NULL, 2, NULL, 0, 'We like to put all the ingredients in separate bowls in the middle of the table, stick a spoon or fork in each, and let everyone assemble their own taco bowl. You could make the bowls before serving if it''s easier to manage -- just arrange a bit of everything in its own little pile in each bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78286F18A014BD48BCF164647A2F9FFA', X'1E6D6D9384A47A469E4961987B7701AB', X'1E6D6D9384A47A469E4961987B7701AB', 0, NULL, NULL, 2, NULL, 0, 'Shuck the corn and cut the kernels off the cobs. Reserve the cobs to make soup (recipe coming soon).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B00FC84EE37E574B8949BA0EB5D7FEC8', X'1E6D6D9384A47A469E4961987B7701AB', X'1E6D6D9384A47A469E4961987B7701AB', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil over medium-high heat in a cast iron or other heavy skillet. When it shimmers, add the corn kernels and let sit for a few minutes to start browning. Stir and let sit again. Repeat this process until there is nice browning in lots of spots, 10 to 15 minutes total.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7551D66BB2F14D469D111AADABF0F787', X'1E6D6D9384A47A469E4961987B7701AB', X'1E6D6D9384A47A469E4961987B7701AB', 2, NULL, NULL, 2, NULL, 0, 'Add the minced garlic to the skillet and cook, stirring, for 30 seconds. Remove from heat and let cool for at least 10 minutes, until just warm. (Place in a bowl if you like to speed cooling.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'801A440CCC6C404AB3F6121BD2711084', X'1E6D6D9384A47A469E4961987B7701AB', X'1E6D6D9384A47A469E4961987B7701AB', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the mayonnaise, chili powder, and some salt and pepper. Stir into the corn. Add the scallions, cilantro, lime juice and cotija and toss. Taste for seasoning and add more of anything you like. Salud!');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'798F23B088F41248B251A086C685F637', X'43C8875438A9134581EEEC52532FEDB6', X'43C8875438A9134581EEEC52532FEDB6', 0, NULL, NULL, 2, NULL, 0, 'Cut the heirloom tomatoes and nectarines into wedges and halve the cherry tomatoes. Distribute among four plates or arrange on a single platter for sharing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B0E0D466496241BC1A95B369896428', X'43C8875438A9134581EEEC52532FEDB6', X'43C8875438A9134581EEEC52532FEDB6', 1, NULL, NULL, 2, NULL, 0, 'Tear the burrata into pieces and scatter over the salads, or place the whole ball in the middle of the platter and dig into it with a serving spoon if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F050D4430EE1C41865E0B2231D1ADEB', X'43C8875438A9134581EEEC52532FEDB6', X'43C8875438A9134581EEEC52532FEDB6', 2, NULL, NULL, 2, NULL, 0, 'Garnish with basil leaves, a drizzle of olive oil and balsamic and some flaky sea salt. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F59DFA49346AA4ABCB006A8B37F246C', X'CEFD38A97A136A4E9F1286C526B3DCCF', X'CEFD38A97A136A4E9F1286C526B3DCCF', 0, NULL, NULL, 2, NULL, 0, 'Remove the tough outer leaves from the cabbage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD979C1B80A0774889F9790182D6E71A', X'CEFD38A97A136A4E9F1286C526B3DCCF', X'CEFD38A97A136A4E9F1286C526B3DCCF', 1, NULL, NULL, 2, NULL, 0, 'Cut the head into quarters and remove the core from each piece.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40C588A28BA5B941A613DBC904077730', X'CEFD38A97A136A4E9F1286C526B3DCCF', X'CEFD38A97A136A4E9F1286C526B3DCCF', 2, NULL, NULL, 2, NULL, 0, 'Using a sharp chef''s knife or a mandoline, slice the cabbage into long, thin shreds and place in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2614D514E4FE6C47BB93F96062264EF7', X'CEFD38A97A136A4E9F1286C526B3DCCF', X'CEFD38A97A136A4E9F1286C526B3DCCF', 3, NULL, NULL, 2, NULL, 0, 'Slice the onion as thinly as possible and add to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40BAB1A5548D9B4D9245F430D1F53FB3', X'CEFD38A97A136A4E9F1286C526B3DCCF', X'CEFD38A97A136A4E9F1286C526B3DCCF', 4, NULL, NULL, 2, NULL, 0, 'Pour the vinegar and oil overtop and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D159F0A4A87E43A023F9E63207E8F3', X'CEFD38A97A136A4E9F1286C526B3DCCF', X'CEFD38A97A136A4E9F1286C526B3DCCF', 5, NULL, NULL, 2, NULL, 0, 'Toss together (hands work just fine!) until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6CEFC5CD8F3FD40946A864C56AA08A1', X'CEFD38A97A136A4E9F1286C526B3DCCF', X'CEFD38A97A136A4E9F1286C526B3DCCF', 6, NULL, NULL, 2, NULL, 0, 'Taste for seasoning, adjust as you like, and serve. It''s great right away and also good the next day.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BC40E1F1516B245B331C1EF38578719', X'1ACF9E61E85DE1418F12C497C9F8F442', X'1ACF9E61E85DE1418F12C497C9F8F442', 0, NULL, NULL, 2, NULL, 0, 'Tear the kale leaves from their stems and break into bite-sized pieces. Wash and dry thoroughly. (Discard stems or save for juicing or pesto.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'745A8ADCB64B3D4497C25E46BB739B8F', X'1ACF9E61E85DE1418F12C497C9F8F442', X'1ACF9E61E85DE1418F12C497C9F8F442', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the oil, lemon juice, salt and pepper over the kale and use your hands to massage the dressing into the leaves. Do this however you like -- you really can''t go wrong. In a minute or two, you''ll have a tender, delicious salad.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D86616CE91AA7640B26C6D02F0677E9F', X'1ACF9E61E85DE1418F12C497C9F8F442', X'1ACF9E61E85DE1418F12C497C9F8F442', 2, NULL, NULL, 2, NULL, 0, 'If you like, sprinkle with a generous amount of nutritional yeast for a pop of umami that beautifully complements the earthy kale, fruity olive oil and tangy lemon juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'595296104907004891DB9432EBF605CF', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', 0, NULL, NULL, 2, NULL, 0, 'Assemble all salad ingredients in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA114B2ECD567543B21C41497C30B3BD', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', 1, NULL, NULL, 2, NULL, 0, 'Whisk the dressing ingredients together in a small bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA17F73FF8968A4E9F7355873B71E304', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', 2, NULL, NULL, 2, NULL, 0, 'Pour the dressing over the salad and give it a gentle toss.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D133A29EB7B7564689EE3C5C021B53CC', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', X'A5CE34A7BCFF5146BB2B8301E0C70CF0', 3, NULL, NULL, 2, NULL, 0, 'Serve right away with some good bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'279236052BCA2D4A9B0EF8E3C362D63B', X'0606F591481C474B8F094B70FF5894D6', X'0606F591481C474B8F094B70FF5894D6', 0, NULL, NULL, 2, NULL, 0, 'Steam the cauliflower until crisp-tender and place in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A9111EFE60A5144A19F55043B74B989', X'0606F591481C474B8F094B70FF5894D6', X'0606F591481C474B8F094B70FF5894D6', 1, NULL, NULL, 2, NULL, 0, 'In a small pot, combine lentils, stock, herbes de Provence and a generous pinch of salt. Bring to a boil, then cover and simmer until liquid is almost absorbed and lentils are barely tender, about 20 minutes. Let rest, covered, for 10 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'276DFD2F4E74114AB1D1510A55F56057', X'0606F591481C474B8F094B70FF5894D6', X'0606F591481C474B8F094B70FF5894D6', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a large skillet over medium-high heat until it shimmers. Add the onion along with a generous sprinkle of salt and cook, stirring occasionally, until softened and browned in spots. Add garlic and capers and cook until very fragrant. Stir in cooked quinoa and heat through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D43EF254608F64BAC14208CE0366BB4', X'0606F591481C474B8F094B70FF5894D6', X'0606F591481C474B8F094B70FF5894D6', 3, NULL, NULL, 2, NULL, 0, 'Tip lentils and onion mixture into the bowl with the cauliflower. Sprinkle with the lemon juice, walnuts and pepper and toss gently to combine. Serve warm, with Pecorino to pass if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6DEA4C8BD508D40A2380D04B7A39F83', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 0, NULL, NULL, 2, NULL, 0, 'Rinse the quinoa very well, then place in a medium lidded pot with 1 1/2 cups water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6761AC684851E340A88048376587D852', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then cover and reduce heat to maintain a gentle simmer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57E5C977D850F54C8A8648B792BF66E2', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 2, NULL, NULL, 2, NULL, 0, 'Simmer for 15 minutes or until nearly all the water is absorbed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B69619F2B7D5C42886A492AA6C532CA', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and keep in covered pot to steam for 5 minutes, until all water is absorbed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'164AE9B667B59A4A8483B6EA16D39474', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 4, NULL, NULL, 2, NULL, 0, 'While the quinoa cooks, chop the vegetables and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526A606726B2C441B35E192AB0E2DD99', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the vinegar, soy sauce, mirin, and sesame oil with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'118E4B904553D44F835804D8F85DFDAA', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 6, NULL, NULL, 2, NULL, 0, 'Spoon the quinoa into a serving bowl while still warm and mix together thoroughly with the dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA0A0ACD6ACB4A49B3C83A2717DC85EC', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 7, NULL, NULL, 2, NULL, 0, 'After it cools a bit, stir in the chopped vegetables.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9A1E9EC1B9EB94197DB353A923DCCFA', X'0C097C85839C0D4487270DEC8E3CFC30', X'0C097C85839C0D4487270DEC8E3CFC30', 8, NULL, NULL, 2, NULL, 0, 'Serve cold or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F024BAA0153BD4982836E1214C7DC2B', X'775316F45C7AD9419309F33BEAF5217E', X'775316F45C7AD9419309F33BEAF5217E', 0, NULL, NULL, 2, NULL, 0, 'If using canned chickpeas, drain, rinse, and dry them thoroughly. In a large bowl, combine the chickpeas, shallots, and cilantro.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D39D584E41B7645874F4B14440D5AE4', X'775316F45C7AD9419309F33BEAF5217E', X'775316F45C7AD9419309F33BEAF5217E', 1, NULL, NULL, 2, NULL, 0, 'In a small pan, toast the sesame and cumin seeds over medium-low heat until the sesame seeds begin to turn golden brown and the seeds are very fragrant. Add the warm seeds to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F1B3F02EFF3964194C26D21F6F547A6', X'775316F45C7AD9419309F33BEAF5217E', X'775316F45C7AD9419309F33BEAF5217E', 2, NULL, NULL, 2, NULL, 0, 'In a blender, combine the lemon juice and zest, olive oil, garlic, salt, and pepper. Blend until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D2A862CF6D7F040905713E196EC6C0E', X'775316F45C7AD9419309F33BEAF5217E', X'775316F45C7AD9419309F33BEAF5217E', 3, NULL, NULL, 2, NULL, 0, 'Pour dressing over salad and toss to combine well. Serve immediately or store in the fridge for up to three days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'402E632CE54A86449D405A2CE3F5BCC5', X'700B496149AB734EA8EA649EA5A82EC8', X'700B496149AB734EA8EA649EA5A82EC8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°F. Cut the squash into quarters. Scoop out and discard the seeds and stringy pulp. Cut each squash quarter into 1/4-inch-thick wedges. Place squash wedges on a baking sheet and toss with 1 tablespoon of the olive oil and 1/4 teaspoon of the salt. Spread the wedges in a single layer in the pan. Roast for about 30 minutes, until tender and slightly browned. Set aside to cool slightly. (Alternatively, if you have a convection oven, it''s great to use here, at 400°F.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F97837681FDD1E4CAA270B4AB26D1CC0', X'700B496149AB734EA8EA649EA5A82EC8', X'700B496149AB734EA8EA649EA5A82EC8', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, place the quinoa in a 4-quart pot over medium-high heat. Cook, stirring, until lightly toasted, about two minutes. Add 2 cups of water and bring to a boil over high heat. Simmer, uncovered, until quinoa turns translucent and water is almost absorbed, about 10 minutes. Then stir in the baby spinach all at once, place a lid on the pot, and cook over low heat for 5 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36E04EDB23BEF14FB43FED4EF585DF2B', X'700B496149AB734EA8EA649EA5A82EC8', X'700B496149AB734EA8EA649EA5A82EC8', 2, NULL, NULL, 2, NULL, 0, 'To make the dressing, whisk together the remaining 2 tablespoons olive oil, the lime zest and juice, the remaining 1/2 teaspoon salt, and freshly ground black pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F011E38BB548F48B22F995FC1541318', X'700B496149AB734EA8EA649EA5A82EC8', X'700B496149AB734EA8EA649EA5A82EC8', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, place the roasted squash, quinoa-spinach mixture, chopped cilantro, and pumpkin seeds. Drizzle with the dressing and toss gently to coat. Serve warm or at room temperature. Keeps well in the fridge for up to three days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9502D593297C014C85EAADB2AB3AF496', X'5F3106B3667F9C4EB27A5ABB29D71F1C', X'5F3106B3667F9C4EB27A5ABB29D71F1C', 0, NULL, NULL, 2, NULL, 0, 'Bring two cups of water to a boil in a small pot. Add the rice and 1/4 teaspoon salt, cover, and cook at a gentle simmer for 40 minutes or until water is absorbed. Cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'393A73AC718C4A47A8BBF00137F05A8B', X'5F3106B3667F9C4EB27A5ABB29D71F1C', X'5F3106B3667F9C4EB27A5ABB29D71F1C', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 375°F with a rack in the middle. On a large rimmed baking sheet, place the diced eggplant, onion, and garlic. Sprinkle with two Tablespoons of the olive oil, the remaining 1/2 teaspoon salt, and the pepper. Roast for about 30 minutes, until tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBA48300E5336C46998DE4ED46AAEDAF', X'5F3106B3667F9C4EB27A5ABB29D71F1C', X'5F3106B3667F9C4EB27A5ABB29D71F1C', 2, NULL, NULL, 2, NULL, 0, 'Pour the rice, roasted eggplant mixture, olives, feta (if using), basil, and chickpeas (if using) into a large bowl. Sprinkle with the lemon juice and toss gently to combine. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3F2EC9D4F91734EA09549CE4D962B66', X'5E6D8E67E067E64A8FC4A13CFE852919', X'5E6D8E67E067E64A8FC4A13CFE852919', 0, NULL, NULL, 2, NULL, 0, 'Wash the greens in several changes of water and dry thoroughly. Strip the greens from their stems and tear into large bite-size pieces. If the stems are tender (yes for beets, chard, dandelion, mustard / no for kale and collards), chop them into 1-inch segments.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D16873A6E7EF445AE573DACCEC60CF3', X'5E6D8E67E067E64A8FC4A13CFE852919', X'5E6D8E67E067E64A8FC4A13CFE852919', 1, NULL, NULL, 2, NULL, 0, 'Start with a skillet that is wide enough to embarrass you at the thought of using it to cook lunch for one. Heat it on the stovetop over medium-high heat. Chop the bacon into 1-inch pieces and add to the heated skillet. Cook, stirring occasionally, until the bacon is just shy of as crisp as you would like it and has given off plenty of fat. If there is more than 1 to 2 tablespoons of fat in the pan, remove the rest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01AEA7352783AC48B26C8F50B9FC252B', X'5E6D8E67E067E64A8FC4A13CFE852919', X'5E6D8E67E067E64A8FC4A13CFE852919', 2, NULL, NULL, 2, NULL, 0, 'If you will be cooking your stems, add them to the skillet now, along with the garlic. If not, just add the garlic. Cook, stirring occasionally, for two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF1904B40E0614FBE3FA199FD899691', X'5E6D8E67E067E64A8FC4A13CFE852919', X'5E6D8E67E067E64A8FC4A13CFE852919', 3, NULL, NULL, 2, NULL, 0, 'Add the greens to the skillet and stir (preferably with tongs) to coat all the greens with a bit of bacon fat. For relatively tender greens (all but collards and kale): Cook uncovered until the greens are just shy of desired tenderness. Add the cider vinegar and cook for one minute more. For collards and kale: Cook uncovered for a few minutes, then add the stock or water and cider vinegar. Cover, lower the heat to maintain a simmer, and cook to desired tenderness, probably about 10 minutes. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'330546950EDABB40855EBF915542AB9D', X'5E6D8E67E067E64A8FC4A13CFE852919', X'5E6D8E67E067E64A8FC4A13CFE852919', 4, NULL, NULL, 2, NULL, 0, 'Place the greens in a bowl, crumble the feta cheese overtop, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DB20CADE6FAD94481EDFDA4C56208B2', X'39031C8C84F22A499F5EAEB4BA322CDE', X'39031C8C84F22A499F5EAEB4BA322CDE', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the garlic paste, lime zest and juice, orange juice, cumin, Sriracha, and olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB51BE4AB1901F4D906C0CEE676B7079', X'39031C8C84F22A499F5EAEB4BA322CDE', X'39031C8C84F22A499F5EAEB4BA322CDE', 1, NULL, NULL, 2, NULL, 0, 'Add the corn, bell pepper, beans, jicama, avocado, scallions, and cilantro, and toss gently to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0861E68A219D944FA8BB4931FE4E75D0', X'39031C8C84F22A499F5EAEB4BA322CDE', X'39031C8C84F22A499F5EAEB4BA322CDE', 2, NULL, NULL, 2, NULL, 0, 'Taste for seasoning and add additional salt, pepper, and Sriracha to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ECA4F464098AF4E965A2A439E76E201', X'39031C8C84F22A499F5EAEB4BA322CDE', X'39031C8C84F22A499F5EAEB4BA322CDE', 3, NULL, NULL, 2, NULL, 0, 'Serve as a light meal or a side dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'558F5E0C4226CF42991EDF6E61F9FD26', X'A21A0B68029E6A4998D5CABEFF8A2923', X'A21A0B68029E6A4998D5CABEFF8A2923', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven or toaster oven to 400° F. Drizzle one tablespoon of the olive oil over the breadcrumbs and toss, kneading slightly with your fingers, to distribute. Spread the breadcrumbs on a sheet pan or toaster oven pan and bake for 6 minutes (or a bit longer in the toaster oven) or until golden in spots, tossing once or twice if necessary. Cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDF16AAD2A1FF34A9245E0022FE5EA7C', X'A21A0B68029E6A4998D5CABEFF8A2923', X'A21A0B68029E6A4998D5CABEFF8A2923', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the remaining olive oil, vinegar, anchovies, shallot, and salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58A45AB8731AB2448DB09E9D0151FD6E', X'A21A0B68029E6A4998D5CABEFF8A2923', X'A21A0B68029E6A4998D5CABEFF8A2923', 2, NULL, NULL, 2, NULL, 0, 'Remove any wilted outer leaves from the radicchio. Wash, dry, quarter and core each head. Slice each quarter crosswise into 1/8-inch slices. Place in a salad bowl, tossing to separate slices. Toss in the breadcrumbs. Drizzle the vinaigrette over the salad and toss.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8425FD1862A9A0499D565AEB2064B754', X'A21A0B68029E6A4998D5CABEFF8A2923', X'A21A0B68029E6A4998D5CABEFF8A2923', 3, NULL, NULL, 2, NULL, 0, 'Either grate the egg on the finest holes of a box grater or press it through a mesh sieve. Toss gently into the salad. Taste and adjust seasonings if necessary. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F98891BB4EA4B54F8D63E77A1515CB28', X'39CEE8E9C190024586C42A738598DE80', X'39CEE8E9C190024586C42A738598DE80', 0, NULL, NULL, 2, NULL, 0, 'Wash kohlrabi well, trim ends, and peel thickly with a paring knife. Using the julienne blade of a mandoline or the large holes on a box grater, cut or shred the kohlrabi.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23106377B07BA042B7DF485C9D639D9E', X'39CEE8E9C190024586C42A738598DE80', X'39CEE8E9C190024586C42A738598DE80', 1, NULL, NULL, 2, NULL, 0, 'Crush the garlic with the side of a chef''s knife, remove the skin, and sprinkle with the salt. Smash the garlic and salt to a paste with the knife.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'870D384E982D5246923A52439F39BB88', X'39CEE8E9C190024586C42A738598DE80', X'39CEE8E9C190024586C42A738598DE80', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the mayonnaise, mustards, lemon juice, vinegar, garlic paste, tarragon and pepper. Whisk with a fork to combine. Add the julienned kohlrabi to the bowl and toss. Serve cold or at room temperature as an accompaniment to grilled fish, shellfish or meat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CC229965F930D4787A312E64E112CA5', X'3E06155351BD454F834A8ACF4B0E66A3', X'3E06155351BD454F834A8ACF4B0E66A3', 0, NULL, NULL, 2, NULL, 0, 'Trim and peel the beets. Cut beets and onion on the julienne blade of a mandoline slicer, or grate them on a box grater if you don''t have a mandoline.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FE07836256C664998F68F976EE7A181', X'3E06155351BD454F834A8ACF4B0E66A3', X'3E06155351BD454F834A8ACF4B0E66A3', 1, NULL, NULL, 2, NULL, 0, 'In a small, dry frying pan, toast the walnuts over medium heat for a few minutes, tossing occasionally, until fragrant and lightly browned in spots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'250421A1919A9745AA01A907379CFC7F', X'3E06155351BD454F834A8ACF4B0E66A3', X'3E06155351BD454F834A8ACF4B0E66A3', 2, NULL, NULL, 2, NULL, 0, 'Place beets, onion, walnuts, and orange zest in a medium mixing bowl. Add the oil, vinegar, orange juice, mustard, salt, and pepper, and toss to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36A1CB65B023354ABEFA88A346C63C74', X'3E06155351BD454F834A8ACF4B0E66A3', X'3E06155351BD454F834A8ACF4B0E66A3', 3, NULL, NULL, 2, NULL, 0, 'Plate salad in a large serving bowl or individual bowls and top with crumbled goat cheese and microgreens.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD1F5904E69AFC40847294349D186E25', X'F346964D94974344B4ABFC08B693BA79', X'F346964D94974344B4ABFC08B693BA79', 0, NULL, NULL, 2, NULL, 0, 'Cut the bacon slices in half and place in a cold frying pan. Set over medium heat and cook, turning occasionally, until crisped to your liking. Drain on paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD3F90EA03DAF48999CFEE1A23581F6', X'F346964D94974344B4ABFC08B693BA79', X'F346964D94974344B4ABFC08B693BA79', 1, NULL, NULL, 2, NULL, 0, 'If you like, lightly toast the bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6EC9703F3DFD745B103C4BA1BCD0EFA', X'F346964D94974344B4ABFC08B693BA79', X'F346964D94974344B4ABFC08B693BA79', 2, NULL, NULL, 2, NULL, 0, 'Layer the turkey slices onto one slice of the bread. Top with an even layer of bacon.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0B21FDDB61A9C46BECF4F86468D3E59', X'F346964D94974344B4ABFC08B693BA79', X'F346964D94974344B4ABFC08B693BA79', 3, NULL, NULL, 2, NULL, 0, 'Onto the other slice of bread, spread the mayo and then the sriracha. Sprinkle with scallions. Layer on the avocado slices. Sprinkle the avocado with the salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD65D6F02608AD4DB591C7A41BAEB07C', X'F346964D94974344B4ABFC08B693BA79', X'F346964D94974344B4ABFC08B693BA79', 4, NULL, NULL, 2, NULL, 0, 'Close the sandwich by tipping the avocado-topped slice of bread onto the turkey-topped slice. Cut in half and serve right away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776B4136F5C65F48A3E5BE3880363ADA', X'766CA9CD64212D47B63619D6BC93B2C4', X'766CA9CD64212D47B63619D6BC93B2C4', 0, NULL, NULL, 2, NULL, 0, 'In a 9x13-inch baking dish or other large, shallow dish, whisk together the marinade ingredients: olive oil, lemon juice, garlic, salt, dill, oregano, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'385B3CC103845E4F81449200BC456861', X'766CA9CD64212D47B63619D6BC93B2C4', X'766CA9CD64212D47B63619D6BC93B2C4', 1, NULL, NULL, 2, NULL, 0, 'Cut each chicken breast in half across the middle and pound with a mallet if necessary to make each piece about an inch thick and a little bigger than a hamburger bun. Or, if using chicken tenders, see prep note below.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE3261B303BA0F409D8DDDB0162E1A0C', X'766CA9CD64212D47B63619D6BC93B2C4', X'766CA9CD64212D47B63619D6BC93B2C4', 2, NULL, NULL, 2, NULL, 0, 'Place chicken pieces into the dish with the marinade and coat well., Leave to marinate at room temperature for 30 minutes or in the fridge for anywhere between 2 and 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E28421AD78ADA44A262582BA33D8286', X'766CA9CD64212D47B63619D6BC93B2C4', X'766CA9CD64212D47B63619D6BC93B2C4', 3, NULL, NULL, 2, NULL, 0, 'Cut bell peppers in half from stem end to tip and remove stems, ribs and seeds. Cut a slit of about an inch at both the top and bottom of each pepper half to help it relax into a flatter shape as it grills.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8274B091AA0D64EA750FBCB575908E9', X'766CA9CD64212D47B63619D6BC93B2C4', X'766CA9CD64212D47B63619D6BC93B2C4', 4, NULL, NULL, 2, NULL, 0, 'Cut halloumi into four slices, not super-thin or they might get too floppy on the grill.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9DB107E2BE9444BB0D8ED7823D79113', X'D191A505DF3ABD48AB7DD81638AB5BAC', X'D191A505DF3ABD48AB7DD81638AB5BAC', 0, NULL, NULL, 2, NULL, 0, 'Prep the collard greens. Cut off the part of the stem that extends below the leaf. Then shave down the remaining thick stem with a knife so that it''s not much thicker than the leaf.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D155D0EC24294049B78B0BA91C2602D6', X'D191A505DF3ABD48AB7DD81638AB5BAC', X'D191A505DF3ABD48AB7DD81638AB5BAC', 1, NULL, NULL, 2, NULL, 0, 'Blanch the greens: Fill a large pot halfway with water and bring it to a boil. Meanwhile, fill a large bowl halfway with ice and cold water. Add the collard greens to the pot and cook for one minute. Then immediately plunge into the ice water. When cold, remove and dry very well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'547EFD87EA77D84995DEC13E56A5BCC1', X'D191A505DF3ABD48AB7DD81638AB5BAC', X'D191A505DF3ABD48AB7DD81638AB5BAC', 2, NULL, NULL, 2, NULL, 0, 'Place leaves front-side-down on a work surface. (You''ll fill the back side of the leaf.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9991BAB39FD77847A4F01EB9B5267C1F', X'D191A505DF3ABD48AB7DD81638AB5BAC', X'D191A505DF3ABD48AB7DD81638AB5BAC', 3, NULL, NULL, 2, NULL, 0, 'On the bottom third of the leaf, centered from left to right, place about two tablespoons of hummus. Layer on the other ingredients, dividing equally among leaves. Be judicious with toppings, otherwise the leaves will be too hard to roll.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'841E0534FD50534680AAE3064539DDF2', X'D191A505DF3ABD48AB7DD81638AB5BAC', X'D191A505DF3ABD48AB7DD81638AB5BAC', 4, NULL, NULL, 2, NULL, 0, 'To fold, see the detailed instructions in the post and video. Fold up the bottom over the fillings. Then fold in the left side and the right side. Then roll the leaf away from you.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5E5D6B03695E2449F66B2220BCE3713', X'D191A505DF3ABD48AB7DD81638AB5BAC', X'D191A505DF3ABD48AB7DD81638AB5BAC', 5, NULL, NULL, 2, NULL, 0, 'Cut in half crosswise and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA53038E8B58144AE689B2211255E93', X'515972117427CA4487642DF01F5473F9', X'515972117427CA4487642DF01F5473F9', 0, NULL, NULL, 2, NULL, 0, 'Prepare a batch of sushi rice and let it cool to body temperature (or room temperature if you prefer) while you prepare the fillings.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'022E05D87E6F434EA2415BBFD80A53EF', X'747630BF939ADD49913769C2758F905E', X'747630BF939ADD49913769C2758F905E', 0, NULL, NULL, 2, NULL, 0, 'Using a mallet or rolling pin, pound chicken breast until it''s about 1/4-inch thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC43E814B7D03F48A8665BFB0A3EAE1B', X'747630BF939ADD49913769C2758F905E', X'747630BF939ADD49913769C2758F905E', 1, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron skillet or a grill to medium-high. Slice the baguette in half lengthwise. Cook cut sides of bread until nicely charred, then flip bread and cook a minute or two more to warm through. Place on a plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F99536832517D4479A8E1A1617809CA7', X'747630BF939ADD49913769C2758F905E', X'747630BF939ADD49913769C2758F905E', 2, NULL, NULL, 2, NULL, 0, 'Pour one tablespoon of the oil into the pan or use it to coat the chicken breast if grilling. Season chicken with salt and pepper. Cook, flipping once, until nicely browned and just cooked through, 2-3 minutes per side depending on thickness and cooking method.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD7607B0A23C88458033C5F4B4D6F99A', X'747630BF939ADD49913769C2758F905E', X'747630BF939ADD49913769C2758F905E', 3, NULL, NULL, 2, NULL, 0, 'Drizzle remaining oil and balsamic vinegar onto both cut sides of the bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AD60CA38CB84B4F8668D0B33EAD2BE5', X'747630BF939ADD49913769C2758F905E', X'747630BF939ADD49913769C2758F905E', 4, NULL, NULL, 2, NULL, 0, 'Onto the bottom piece of bread, layer the chicken, mozzarella, tomatoes (and a pinch of salt), prosciutto if using, and basil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D960FC6538F92045B0F91D165AEFA563', X'747630BF939ADD49913769C2758F905E', X'747630BF939ADD49913769C2758F905E', 5, NULL, NULL, 2, NULL, 0, 'Top with other half of bread and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34F1EC752676314CB65B69F8140914DC', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, use a fork to mix together the gruyere, cheddar, pecorino or parmesan, salt and pepper, and caramelized onions. You can gently tease the onions apart with the fork to make sure everything is mixed well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCEBE05B171C754E8241F4D9E145EFA2', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 1, NULL, NULL, 2, NULL, 0, 'Spread the mustard onto the inside faces of the bread slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'261ADA0AC94C594CBE8EE42EC7BC15F2', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 2, NULL, NULL, 2, NULL, 0, 'Spread the cheese and onion mixture evenly onto one slice of bread and top with the other slice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4084121957005F4B92B85B417EBD3D5C', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 3, NULL, NULL, 2, NULL, 0, 'In a small frying pan, melt the butter over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41292FB1116AD94D9264E76E3EE827D1', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 4, NULL, NULL, 2, NULL, 0, 'Place the sandwich into the pan and let the bottom slice of bread soak up some of the melted butter. Then carefully flip the sandwich and swipe it around the pan to mop up the remaining melted butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC03108DC442534583D9395B06C3400A', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 5, NULL, NULL, 2, NULL, 0, 'From this point on, the name of the game is to regulate the heat so that the cheese mixture melts in the same timeframe that the bread becomes golden-brown. You''ll have to use your judgment based on your own stovetop and pan, but this typically means keeping the burner on a medium-low setting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'229C354336BF5F4489F6C3AA94C61109', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 6, NULL, NULL, 2, NULL, 0, 'From time to time, use your spatula to press down on the grilled cheese to make sure it''s melting and browning evenly. During cooking (which should take a total of about five minutes), flip the sandwich at least once more to brown the top side. You''re welcome to flip it more if it helps you achieve even melting and browning, but it shouldn''t really be necessary. Toward the end of cooking, you can lower the heat and place a lid on the pan to aid in melting if you like. Don''t leave it on for more than a minute or so, to ensure that the bread remains crisp and doesn''t steam.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A63767B285614B8200780B518C3827', X'D8FCDB1975A76E4F96480BF62AAE5B9E', X'D8FCDB1975A76E4F96480BF62AAE5B9E', 7, NULL, NULL, 2, NULL, 0, 'When cheese is melted and bread is golden-brown, remove the grilled cheese to a cutting board or plate, cut in half and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40C00F06490DD447B37E47EE4B542D54', X'FCB4AB64D2553C4AB38115E852C6FFB1', X'FCB4AB64D2553C4AB38115E852C6FFB1', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450° F with two racks close to the middle.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62BBFC0FEE50B44BA6DF76D7903B22A', X'FCB4AB64D2553C4AB38115E852C6FFB1', X'FCB4AB64D2553C4AB38115E852C6FFB1', 1, NULL, NULL, 2, NULL, 0, 'Slice the eggplants 1/4-inch thick. Spray a baking sheet generously with cooking spray and arrange the eggplant slices in a single layer. Spray tops of slices with cooking spray, sprinkle generously with salt, and place in preheated oven. Bake for 30 minutes, flipping the slices at the halfway point.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F502A4C0984BB44D982D257E7A79A844', X'FCB4AB64D2553C4AB38115E852C6FFB1', X'FCB4AB64D2553C4AB38115E852C6FFB1', 2, NULL, NULL, 2, NULL, 0, 'While the eggplant begins to bake, prepare the portobellos. Remove stems and, using a small spoon, scrape the gills off the underside of the mushroom caps. Slice caps 1/4-inch thick. In a small bowl, whisk together olive oil, lemon juice, smoked paprika, and 1/2 teaspoon salt. On a second baking sheet, toss the portobello slices with the dressing. Arrange in a single layer and add to oven. Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'195BB508EDA40546A1822EC9BC843323', X'FCB4AB64D2553C4AB38115E852C6FFB1', X'FCB4AB64D2553C4AB38115E852C6FFB1', 3, NULL, NULL, 2, NULL, 0, 'Cut across the beltline of the boule and remove the top half. Use your hands to tear most of the soft insides out of both halves of the bread, leaving about 1/2 inch barrier before the crust. (Tear the soft parts into small chunks and freeze for use later as fresh breadcrumbs -- just give them a whirl in the food processor when you need them.) Spread the olive tapenade inside the bottom half of the bread and the pesto inside the top half. Spread giardiniera in a thick layer on top of pesto.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A7CD461DEF90C4EB82480B1388BB388', X'FCB4AB64D2553C4AB38115E852C6FFB1', X'FCB4AB64D2553C4AB38115E852C6FFB1', 4, NULL, NULL, 2, NULL, 0, 'Arrange the portobello pieces in a compact layer on the bottom of the bread. Layer on the roasted peppers, eggplant, and cheese. Replace the top half of the bread and press down firmly a few times to make it nice and compact. Let sit for at least an hour, or wrap and store for up to 12 hours if desired, then slice into wedges and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'083BB190A1D1F14484E7C1E8098BF18A', X'38A53CBEFD892A46AC5F9A602A03888C', X'38A53CBEFD892A46AC5F9A602A03888C', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour onto a plate and dredge each crab lightly in the flour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1E54D47C6646F4191EF33D572868611', X'38A53CBEFD892A46AC5F9A602A03888C', X'38A53CBEFD892A46AC5F9A602A03888C', 1, NULL, NULL, 2, NULL, 0, 'Heat butter and oil in a heavy 12-inch skillet over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0915DCEAD6E424DA920BE61EAB44141', X'38A53CBEFD892A46AC5F9A602A03888C', X'38A53CBEFD892A46AC5F9A602A03888C', 2, NULL, NULL, 2, NULL, 0, 'Add the crabs belly up in a single layer. Cook for four minutes on the first side, then flip and cook for three minutes on the other side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F903C60E71F07D439614A3C42FCF9B81', X'38A53CBEFD892A46AC5F9A602A03888C', X'38A53CBEFD892A46AC5F9A602A03888C', 3, NULL, NULL, 2, NULL, 0, 'Lightly toast brioche buns. I like to do this in the toaster oven, but you could do it right in the pan after removing the crabs if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'800FCCDC17FD304DADBFDC05355C5A18', X'38A53CBEFD892A46AC5F9A602A03888C', X'38A53CBEFD892A46AC5F9A602A03888C', 4, NULL, NULL, 2, NULL, 0, 'Spread some Kewpie mayo (or regular mayo if you insist) onto the inside of the top bun. Layer tomato, soft shell crab, and lettuce onto bottom bun.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A44BE222A3C3674D9AA8FBFBC9375BBF', X'38A53CBEFD892A46AC5F9A602A03888C', X'38A53CBEFD892A46AC5F9A602A03888C', 5, NULL, NULL, 2, NULL, 0, 'Close sandwich and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'443E38059950B441861C6B8FB1A71A95', X'15F01D831E7C9A41845DF6E0330A19FC', X'15F01D831E7C9A41845DF6E0330A19FC', 0, NULL, NULL, 2, NULL, 0, 'You guys know how to make a sandwich, but I''ll tell you anyway, okay?');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47A6D36BD6F5D34CB1B9A5A1CA81D0E9', X'15F01D831E7C9A41845DF6E0330A19FC', X'15F01D831E7C9A41845DF6E0330A19FC', 1, NULL, NULL, 2, NULL, 0, 'Spread some of the bean dip onto each slice of bread, reserving the rest for another use. I like to put most of it on the bottom slice and just enough on the top slice to give everything a fighting chance of sticking together.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4E212C83E1C6840A26FEE6634774E10', X'15F01D831E7C9A41845DF6E0330A19FC', X'15F01D831E7C9A41845DF6E0330A19FC', 2, NULL, NULL, 2, NULL, 0, 'Evenly layer the vegetables onto one of the slices in the order listed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22E377E6DE69634C955B433EEC73F8A9', X'15F01D831E7C9A41845DF6E0330A19FC', X'15F01D831E7C9A41845DF6E0330A19FC', 3, NULL, NULL, 2, NULL, 0, 'Top with the other slice of bread and press gently.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84DF43D22A11F4E89DD982D44A48548', X'15F01D831E7C9A41845DF6E0330A19FC', X'15F01D831E7C9A41845DF6E0330A19FC', 4, NULL, NULL, 2, NULL, 0, 'Slice in half if you like and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'311742528A59564A8E8BA109384A2E75', X'C757904134424D4F850D0234CD05AD21', X'C757904134424D4F850D0234CD05AD21', 0, NULL, NULL, 2, NULL, 0, 'Split each roll lengthwise, leaving attached at the bottom.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8359952E0AA52340A1C0B10B50FB8726', X'C757904134424D4F850D0234CD05AD21', X'C757904134424D4F850D0234CD05AD21', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together mayonnaise, minced pickle, lemon juice and black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AF1A889849CD24BAE268150BEB9657B', X'C757904134424D4F850D0234CD05AD21', X'C757904134424D4F850D0234CD05AD21', 2, NULL, NULL, 2, NULL, 0, 'Spread sauce over inside bun faces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E55B8A4060BD748B3BC2D4E9BD82ECB', X'C757904134424D4F850D0234CD05AD21', X'C757904134424D4F850D0234CD05AD21', 3, NULL, NULL, 2, NULL, 0, 'Place three warm baked chicken tenders in each roll and top with lettuce and hot sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73EA9C4273F8604D95FB84D68C75D8AF', X'C757904134424D4F850D0234CD05AD21', X'C757904134424D4F850D0234CD05AD21', 4, NULL, NULL, 2, NULL, 0, 'Serve right away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B0A14ABC26F0240B9837C2C0CD09D7D', X'EBEE749D3BE61B48B206BB0E2CC1889F', X'EBEE749D3BE61B48B206BB0E2CC1889F', 0, NULL, NULL, 2, NULL, 0, 'Cut each roll in half and butter the halves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8396AB07B9E824D8840E994F2B39BEF', X'EBEE749D3BE61B48B206BB0E2CC1889F', X'EBEE749D3BE61B48B206BB0E2CC1889F', 1, NULL, NULL, 2, NULL, 0, 'Lay a slice of prosciutto on each half.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'876E5870479C0E4E8982BCBB27EB1614', X'EBEE749D3BE61B48B206BB0E2CC1889F', X'EBEE749D3BE61B48B206BB0E2CC1889F', 2, NULL, NULL, 2, NULL, 0, 'Top the bottom half with a slice of frittata and some greens.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EFB703813E682498665983523A3FE99', X'EBEE749D3BE61B48B206BB0E2CC1889F', X'EBEE749D3BE61B48B206BB0E2CC1889F', 3, NULL, NULL, 2, NULL, 0, 'Close the sandwiches, wrap in foil or parchment if you''re picnicking, and serve at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8061656F67BD04291B8F18A528A35A4', X'048219EA1517574F95125AF430B678BF', X'048219EA1517574F95125AF430B678BF', 0, NULL, NULL, 2, NULL, 0, 'Place the cut vegetables into a shallow bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67C83D3364CA4A4A9A5AEB0A6C72497D', X'048219EA1517574F95125AF430B678BF', X'048219EA1517574F95125AF430B678BF', 1, NULL, NULL, 2, NULL, 0, 'Pour the 1/2 cup vinegar overtop and stir to coat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD46076119E04439A55BF041EDA1F2D', X'048219EA1517574F95125AF430B678BF', X'048219EA1517574F95125AF430B678BF', 2, NULL, NULL, 2, NULL, 0, 'Let sit until ready to assemble sandwiches, stirring occasionally.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6041E110E9B2144EB12AB367727FA6E2', X'048219EA1517574F95125AF430B678BF', X'048219EA1517574F95125AF430B678BF', 3, NULL, NULL, 2, NULL, 0, 'Before using, drain off excess vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DB28E9816ED244FAC93A259AE8BFFE0', X'BF5540CBFB2AE34091BFB4E990E9ADEE', X'BF5540CBFB2AE34091BFB4E990E9ADEE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Line a rimmed baking sheet with parchment or a silpat, or spray with cooking spray.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E93B8C55BFD65E4E970C695B81DFF71F', X'BF5540CBFB2AE34091BFB4E990E9ADEE', X'BF5540CBFB2AE34091BFB4E990E9ADEE', 1, NULL, NULL, 2, NULL, 0, 'Into a large bowl place the bread crumbs, milk, onion, garlic, parsley, cheese, fennel seed, oregano, salt, egg, and pepper. Mix together well with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AD2C76B1EE4734D99660B1DAEB734FC', X'BF5540CBFB2AE34091BFB4E990E9ADEE', X'BF5540CBFB2AE34091BFB4E990E9ADEE', 2, NULL, NULL, 2, NULL, 0, 'Add ground turkey and use fork to mix well but without overworking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB68F8E8CEDE74C91BBED16D883F83E', X'BF5540CBFB2AE34091BFB4E990E9ADEE', X'BF5540CBFB2AE34091BFB4E990E9ADEE', 3, NULL, NULL, 2, NULL, 0, 'Use two spoons or a small ice cream scoop to portion the mixture into 12 roughly equal parts. Then roll each meatball gently between your palms to make it spherical.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED76312B3F650C45BB64110DDC2A8089', X'BF5540CBFB2AE34091BFB4E990E9ADEE', X'BF5540CBFB2AE34091BFB4E990E9ADEE', 4, NULL, NULL, 2, NULL, 0, 'Space the meatballs evenly on the prepared baking sheet. Bake for 10 minutes, then flip with a spatula and bake 5 minutes more, until just cooked through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'273AFFC007201246B148CFA9C6E77613', X'BF5540CBFB2AE34091BFB4E990E9ADEE', X'BF5540CBFB2AE34091BFB4E990E9ADEE', 5, NULL, NULL, 2, NULL, 0, 'Heat tomato sauce in a medium pan, then add meatballs and simmer for a minute or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E392A1769750AF4197C0C910C57C8217', X'83B603AEC18FF04AAEDEB86C90EAEE4A', X'83B603AEC18FF04AAEDEB86C90EAEE4A', 0, NULL, NULL, 2, NULL, 0, 'Cut or tear the lobster meat into bite-sized pieces and place in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8910884757905E4990453933485F4EA3', X'83B603AEC18FF04AAEDEB86C90EAEE4A', X'83B603AEC18FF04AAEDEB86C90EAEE4A', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the mayonnaise, lemon juice, capers, chives and salt and pepper. Pour the dressing over the lobster and toss gently to coat all the meat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3D4796C3812E54ABC51A5EA867B0152', X'83B603AEC18FF04AAEDEB86C90EAEE4A', X'83B603AEC18FF04AAEDEB86C90EAEE4A', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter on a skillet over medium-high heat. Set the buns down on the skillet on one of their side edges. Cook for a minute or two until golden brown, then flip and cook the opposite side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F1C0EE84C3DBF49ADC1A567DCF1C934', X'83B603AEC18FF04AAEDEB86C90EAEE4A', X'83B603AEC18FF04AAEDEB86C90EAEE4A', 3, NULL, NULL, 2, NULL, 0, 'Divide the lobster meat among the buns and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50BD296DBA7AE04784BE326C9FDB1224', X'AA952F6F6B169D4D8F87B1A979B08A1A', X'AA952F6F6B169D4D8F87B1A979B08A1A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F with a rack in the center. Line a baking sheet with parchment paper or a light layer of oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED5178A1B60C2646A99942C8B9257B90', X'AA952F6F6B169D4D8F87B1A979B08A1A', X'AA952F6F6B169D4D8F87B1A979B08A1A', 1, NULL, NULL, 2, NULL, 0, 'Into food processor fitted with the blade, place the onion, garlic, sweet potato and walnuts. Process until finely chopped. Add the cooked rice and lentils and pulse until well combined. Transfer into a large mixing bowl. Add the ketchup, mustard, flax, salt, cayenne and black pepper and mix together thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82D2FD2B4A15B844BD192866424EB1DC', X'AA952F6F6B169D4D8F87B1A979B08A1A', X'AA952F6F6B169D4D8F87B1A979B08A1A', 2, NULL, NULL, 2, NULL, 0, 'Stir in the cornmeal. The mixture should be thick enough that you can easily form patties in your hands. If not, add a bit more cornmeal. Divide into eight patties and place on a the baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'637F0CDC88383C408245BB0A8856BE3E', X'AA952F6F6B169D4D8F87B1A979B08A1A', X'AA952F6F6B169D4D8F87B1A979B08A1A', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes, then flip each patty and bake 15 minutes more. Serve as you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C3CC0548876924999B3821454208155', X'C16EE4378B820546BE05529830D4B163', X'C16EE4378B820546BE05529830D4B163', 0, NULL, NULL, 2, NULL, 0, 'Spread some mustard on the inside of each slice of bread. Arrange one slice of American and half the cheddar in a single layer on each slice of bread, making sure to distribute the cheddar like a cheese boss who gets what she wants out of every bite. Lay the sauerkraut on the bottom slice and top with the top slice. Spread half the butter over the outside of the top slice of bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60004CBBEF56CE4CBA8433C281D6348B', X'C16EE4378B820546BE05529830D4B163', X'C16EE4378B820546BE05529830D4B163', 1, NULL, NULL, 2, NULL, 0, 'Place the sandwich buttered side down in a skillet over medium-high heat. Butter the outside of the top slice with the remaining butter. Cook, adjusting heat as necessary, until bread is golden brown and cheese is melted, flipping once halfway through. Cut and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79398029FDB71D438ACEDE174927A596', X'426F986A044C2641A0188F5A5F4D080B', X'426F986A044C2641A0188F5A5F4D080B', 0, NULL, NULL, 2, NULL, 0, 'To make the salmon filling, combine the salmon, mayonnaise, cream cheese, dill, horseradish, eggs, and pepper in a medium bowl and stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1702ED2642DB63439AF474776AE94101', X'426F986A044C2641A0188F5A5F4D080B', X'426F986A044C2641A0188F5A5F4D080B', 1, NULL, NULL, 2, NULL, 0, 'To make the shrimp filling, combine the shrimp, dill, mayonnaise, cream cheese, and pepper in a medium bowl and stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8CA3C497F9EF34DBD52AB62582BB9F0', X'426F986A044C2641A0188F5A5F4D080B', X'426F986A044C2641A0188F5A5F4D080B', 2, NULL, NULL, 2, NULL, 0, 'To assemble the Smörgåstårta, lay 1 1/2 bread slices on a cutting board to form a long rectangle. Top with half the salmon or herring filling. Layer another 1 1/2 bread slices on top, and top with half the shrimp filling. Repeat with the remaining bread and fillings, ending with a layer of bread. You will have half a slice of bread left over. Press gently on the sandwich from the top and sides to compress the layers a bit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97D834D4B5720428BB183106FB65DAD', X'426F986A044C2641A0188F5A5F4D080B', X'426F986A044C2641A0188F5A5F4D080B', 3, NULL, NULL, 2, NULL, 0, 'To `frost` the Smörgåstårta, spread the top and sides with a thin layer of cream cheese. (Stir in up to a couple of tablespoons of milk or cream if the cream cheese is too thick, since texture varies by brand.) Sprinkle with the chopped parsley, cut into slices, and serve. Can be refrigerated for up to a day, either before or after frosting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E08F27BF04654083C9465C84E001A5', X'B53909636ABECF438452D21F6A8F29F9', X'B53909636ABECF438452D21F6A8F29F9', 0, NULL, NULL, 2, NULL, 0, 'Peel the eggs and mash them well in a small bowl with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54FCFBE7EB1FF34A9CE43B0BEFDD6F02', X'B53909636ABECF438452D21F6A8F29F9', X'B53909636ABECF438452D21F6A8F29F9', 1, NULL, NULL, 2, NULL, 0, 'Add mayonnaise, salt, and pepper, and continue mashing until nicely combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4367801656A2547B14CE1CD3BE12438', X'B53909636ABECF438452D21F6A8F29F9', X'B53909636ABECF438452D21F6A8F29F9', 2, NULL, NULL, 2, NULL, 0, 'Spread mixture evenly over one slice of bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'517B0AE9A088AC40A2BC34267E9E1263', X'B53909636ABECF438452D21F6A8F29F9', X'B53909636ABECF438452D21F6A8F29F9', 3, NULL, NULL, 2, NULL, 0, 'Top with watercress and the other bread slice. Cut sandwich in half and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2A882627198FD4FBFD5DCE2BEFA8942', X'B8EF0CB67ABBAE41B5C59A8039D78F45', X'B8EF0CB67ABBAE41B5C59A8039D78F45', 0, NULL, NULL, 2, NULL, 0, 'Pan-fry the bacon until just crisp. Drain on paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10582A006328C042BD4033B473EC81B7', X'B8EF0CB67ABBAE41B5C59A8039D78F45', X'B8EF0CB67ABBAE41B5C59A8039D78F45', 1, NULL, NULL, 2, NULL, 0, 'Toast the bread lightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB9CEC7D4955FF4B94A3036FA6A4F87A', X'B8EF0CB67ABBAE41B5C59A8039D78F45', X'B8EF0CB67ABBAE41B5C59A8039D78F45', 2, NULL, NULL, 2, NULL, 0, 'Spread the mayonnaise on one slice of bread and the avocado on the other (or slice the avocado to layer on top).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E436191D7F68643BE63A303E25FB7A4', X'B8EF0CB67ABBAE41B5C59A8039D78F45', X'B8EF0CB67ABBAE41B5C59A8039D78F45', 3, NULL, NULL, 2, NULL, 0, 'Layer the bacon, tomato and lettuce or arugula on top of one slice of bread and top with the other slice of bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0B599349C941C4E9849E00FAB08309E', X'B8EF0CB67ABBAE41B5C59A8039D78F45', X'B8EF0CB67ABBAE41B5C59A8039D78F45', 4, NULL, NULL, 2, NULL, 0, 'Cut sandwich in half on the diagonal and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B91F380378D39B42B84DA00F78AF77D0', X'A8981B1206F5CA46A35CE654F8256E05', X'A8981B1206F5CA46A35CE654F8256E05', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in a medium pot over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B86853C64E964A9A707DBD64808CD3', X'A8981B1206F5CA46A35CE654F8256E05', X'A8981B1206F5CA46A35CE654F8256E05', 1, NULL, NULL, 2, NULL, 0, 'Add the minced shallot and cook for a minute or so, whisking once or twice. Place the turkey neck into the pot and continue cooking, turning occasionally, until lightly browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8601F94F0BEC1C4D8FCE50A4FB5EB089', X'A8981B1206F5CA46A35CE654F8256E05', X'A8981B1206F5CA46A35CE654F8256E05', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and cook, whisking, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B103D6044181A042A75A73075BC2E0BC', X'A8981B1206F5CA46A35CE654F8256E05', X'A8981B1206F5CA46A35CE654F8256E05', 3, NULL, NULL, 2, NULL, 0, 'Pour in the stock and whisk well. Whisk in soy sauce, marmite or vegemite, and a few grinds of pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'263793B2985B9044950354070C435F57', X'A8981B1206F5CA46A35CE654F8256E05', X'A8981B1206F5CA46A35CE654F8256E05', 4, NULL, NULL, 2, NULL, 0, 'Bring stock to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68BA2E1444B6C4439E33C074033D6B97', X'A8981B1206F5CA46A35CE654F8256E05', X'A8981B1206F5CA46A35CE654F8256E05', 5, NULL, NULL, 2, NULL, 0, 'Reduce heat and simmer for one hour. You can control the thickness by covering the pot for part of the cooking time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FB4AB6392AAC64DA9075C60852D6397', X'A8981B1206F5CA46A35CE654F8256E05', X'A8981B1206F5CA46A35CE654F8256E05', 6, NULL, NULL, 2, NULL, 0, 'Taste for seasoning and add salt and additional pepper if desired. Remove turkey neck before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70AD3BA107CDC3438C01A1F345FFFA2A', X'C38276319A6FF3448074CC7FADAEC5DD', X'C38276319A6FF3448074CC7FADAEC5DD', 0, NULL, NULL, 2, NULL, 0, 'Use a microplane to zest the orange.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40612F684E82004BB68AD13513AA2B59', X'C38276319A6FF3448074CC7FADAEC5DD', X'C38276319A6FF3448074CC7FADAEC5DD', 1, NULL, NULL, 2, NULL, 0, 'Cut the orange in half and juice it. You should end up with somewhere around 1/2 cup of orange juice. A little more or less is 100% fine. If it''s a lot less, add more juice from another orange or — if you don''t have one — add a little more apple cider than called for.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39FF8DEF4CF77D4B9B82B02E724AA4FD', X'1C1880C5F8A13842B02B300150873832', X'1C1880C5F8A13842B02B300150873832', 0, NULL, NULL, 2, NULL, 0, 'Place egg yolks, lemon juice, salt, Tobasco, and water in a 2-cup glass measuring cup or the tall container that came with your stick blender. In a small pot, heat butter until fully melted and very hot and bubbly. Transfer butter to the 1-cup measuring cup for easy pouring.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E15599EF45ED04B85C316752085996B', X'1C1880C5F8A13842B02B300150873832', X'1C1880C5F8A13842B02B300150873832', 1, NULL, NULL, 2, NULL, 0, 'Place the blender into the 2-cup measuring cup or the tall blender container, touching the bottom, and turn it on. Drizzle in the very hot butter in a slow, steady stream until it''s gone. This should take 30 seconds to a minute and leave you with a beautifully emulsified Hollandaise sauce. Taste for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D436995AD9C1034C8676C9D4BFC4BE42', X'1C1880C5F8A13842B02B300150873832', X'1C1880C5F8A13842B02B300150873832', 2, NULL, NULL, 2, NULL, 0, 'Serve over vegetables, fish, chicken, or steak or with Eggs Benedict or Florentine. And hey, maybe use those egg whites for a Pavlova, another great party trick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C14B672AD17845AD895F72B44EE739', X'B26F45EEA79A7F4E8A7460630BD82050', X'B26F45EEA79A7F4E8A7460630BD82050', 0, NULL, NULL, 2, NULL, 0, 'Shred zucchini or yellow squash on the large holes of a box grater or in a food processor with the shredding disc.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A90E62BBF08924EB6FC261DD288BA42', X'B26F45EEA79A7F4E8A7460630BD82050', X'B26F45EEA79A7F4E8A7460630BD82050', 1, NULL, NULL, 2, NULL, 0, 'Set a 10- or 12-inch nonstick skillet over medium heat. Melt butter (or heat olive oil). Add shallot, garlic, shredded squash, salt, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D3264390F12164AA4288EBCFA2CF05A', X'B26F45EEA79A7F4E8A7460630BD82050', X'B26F45EEA79A7F4E8A7460630BD82050', 2, NULL, NULL, 2, NULL, 0, 'Cook, stirring frequently, for about 20 minutes, until reduced to a soft, spreadable, silky consistency. You may need to lower the heat about halfway through to prevent browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC20F8826311C14DB7928CD05DE8AB5D', X'B26F45EEA79A7F4E8A7460630BD82050', X'B26F45EEA79A7F4E8A7460630BD82050', 3, NULL, NULL, 2, NULL, 0, 'Spread is ready to use right away, or let cool and transfer to an airtight container.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3243FEFDF0083247A9984AE46B279C71', X'91B59613D558F843807D884971E7AADD', X'91B59613D558F843807D884971E7AADD', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, stir together the vinegar, garlic, oregano, salt, red pepper flakes, and black pepper. Let sit for five minutes while the vinegar mellows the bite of the garlic.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B921F80591A134F836ACA84A0F7FEC1', X'91B59613D558F843807D884971E7AADD', X'91B59613D558F843807D884971E7AADD', 1, NULL, NULL, 2, NULL, 0, 'Stir in minced greens.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D8C48768305AC47823A8DD90956515F', X'91B59613D558F843807D884971E7AADD', X'91B59613D558F843807D884971E7AADD', 2, NULL, NULL, 2, NULL, 0, 'Drizzle in olive oil while stirring constantly. That''s it!');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FC86C98A20F474BA83CDBF93D9FC277', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 0, NULL, NULL, 2, NULL, 0, 'If you''ll be canning the jam, prepare the jars and hot water canner according to the instructions below.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'090C74DC5141E54296BF85E4E0FCFAD6', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 1, NULL, NULL, 2, NULL, 0, 'In a wide pot, stir together strawberries, rhubarb, butter, cardamom, pepper, and calcium water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD6CD357F012643B29F8E8A5D9F5081', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 2, NULL, NULL, 2, NULL, 0, 'Cook, stirring frequently, over medium heat until the fruit starts to release its juices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCD31B09FFBBB24B95621F12F82FF263', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 3, NULL, NULL, 2, NULL, 0, 'Raise heat to high and bring to a full boil. Then reduce heat again and simmer, stirring frequently to prevent burning, until fruit has softened completely and largely broken down, about 15 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E32B9845A0CAB345A1946DE655718299', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk the sugar and pectin together thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F0B7589F469DD4287F1E435FDB9DAE3', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 5, NULL, NULL, 2, NULL, 0, 'Add sugar mixture to pan and whisk constantly while mixture returns to a full boil. Simmer briskly for one to two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57799DFC36CFFB449CA7428A10FFCE16', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in vanilla.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD2DB7AD2BE00841BD6E3DE45A7EF88A', X'A7B9A2FC887EA54A93C49E6C3214892E', X'A7B9A2FC887EA54A93C49E6C3214892E', 7, NULL, NULL, 2, NULL, 0, 'Funnel into jars, leaving 1/2-inch headspace. Refrigerate once cool or proceed to canning instructions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E58CD4757A9F9144A35DA85422A7D1D6', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', 0, NULL, NULL, 2, NULL, 0, 'Toast the walnuts in a small, dry pan over medium heat until they are fragrant and a little darker brown than they started out (3 to 5 minutes).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'503E6413E954A040B025EFFAA19FC4F7', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', 1, NULL, NULL, 2, NULL, 0, 'Let cool slightly and pour into the bowl of a food processor fitted with the blade attachment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99D25B46DA9EE54D8B1FC36F33797DFB', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic scapes or cloves, basil, salt, pepper, and lemon juice and pulse just until a thick paste forms. Stop to press any stray or stubborn basil leaves into the whirling action of the blade as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BFA78EB883C8548BD9305915A12539C', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', X'E8E7FF1F98A54D4A9A046A2FAD7E3DE2', 3, NULL, NULL, 2, NULL, 0, 'With the motor running, pour the olive oil through the feed tube in a slow stream. Transfer pesto to a mixing bowl and stir in the grated cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'993DA538D3C89840BFBE4C4B2AFCBC8F', X'8A8B8B549DAB5D4C9A743B5715C37555', X'8A8B8B549DAB5D4C9A743B5715C37555', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a medium pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89538A1970310D4BA73C0DAEA63424A4', X'8A8B8B549DAB5D4C9A743B5715C37555', X'8A8B8B549DAB5D4C9A743B5715C37555', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, then reduce heat and simmer briskly, uncovered, until thickened, about 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50795AB340B9A941A8C24DEEB373EAC4', X'8A8B8B549DAB5D4C9A743B5715C37555', X'8A8B8B549DAB5D4C9A743B5715C37555', 2, NULL, NULL, 2, NULL, 0, 'Cool slightly (or completely if you like) before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1B2323BF5B5A147B657B5A3DD70057D', X'0096DC43CB334C448C2E8EBF099451F4', X'0096DC43CB334C448C2E8EBF099451F4', 0, NULL, NULL, 2, NULL, 0, 'Place the shallot, balsamic vinegar, and red wine vinegar into the jar and stir to ensure shallot is covered with vinegar. Let the shallot marinate for a few minutes (two to five is enough) to let the vinegar work its magic and take the bite out of the shallots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C254964B0F73C6408ABB2804612F46BA', X'0096DC43CB334C448C2E8EBF099451F4', X'0096DC43CB334C448C2E8EBF099451F4', 1, NULL, NULL, 2, NULL, 0, 'Add the salt, sugar, pepper, mustard, and olive oil to the jar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5A2AE701521974589F6E9B37EF7291B', X'0096DC43CB334C448C2E8EBF099451F4', X'0096DC43CB334C448C2E8EBF099451F4', 2, NULL, NULL, 2, NULL, 0, 'Close the lid and shake well until dressing is completely emulsified.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A1581EA4FF1BC4B84AB44AFAA03634E', X'0096DC43CB334C448C2E8EBF099451F4', X'0096DC43CB334C448C2E8EBF099451F4', 3, NULL, NULL, 2, NULL, 0, 'The dressing is ready to use right away and will keep well in a nice cold fridge for a week or two. If it solidifies in the fridge (as olive oil can do at colder temperatures), bring to room temperature before using. (You can pop the lidless jar into the microwave for about 10 seconds and give it a stir to hasten the process if you like.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'322FBD676CE315409C2107D57CA9D60A', X'A839DCA1CAAE5043B9F1DCD0C3700863', X'A839DCA1CAAE5043B9F1DCD0C3700863', 0, NULL, NULL, 2, NULL, 0, 'Peel and trim ends from onions. Halve onions from root to tip, then slice thinly from root to tip. See step-by-step photos and video for visual cues. Cutting from root to tip (as opposed to crosswise) is the way to ensure the onion slices will hold enough of their shape while caramelizing, rather than dissolving into mush.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF58600B5B3E534296CCA27E6D17ABBB', X'A839DCA1CAAE5043B9F1DCD0C3700863', X'A839DCA1CAAE5043B9F1DCD0C3700863', 1, NULL, NULL, 2, NULL, 0, 'Heat oil and melt butter together over medium heat in a 10-inch frying pan with a heavy bottom.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BAE5D981184154FA696E75716F57F9C', X'A839DCA1CAAE5043B9F1DCD0C3700863', X'A839DCA1CAAE5043B9F1DCD0C3700863', 2, NULL, NULL, 2, NULL, 0, 'Add onions and stir well to coat with oil and butter. Add rosemary, thyme, and salt, and stir again.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68171292253B8B4D9AE75FDB3FFF9FD6', X'A839DCA1CAAE5043B9F1DCD0C3700863', X'A839DCA1CAAE5043B9F1DCD0C3700863', 3, NULL, NULL, 2, NULL, 0, 'For the first 20 minutes or so, the onions will sweat a lot and reduce in volume substantially. Stir occasionally during this time to keep them cooking evenly, and adjust the heat as necessary to prevent burning. Don''t expect to see too much color on the onions during this time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03FA73760AC28B4DBF24C7361171E418', X'A839DCA1CAAE5043B9F1DCD0C3700863', X'A839DCA1CAAE5043B9F1DCD0C3700863', 4, NULL, NULL, 2, NULL, 0, 'During the next 20 to 40 minutes, the onions will really start to caramelize. Your job during this time is to stir the onions not too little but not too much. Allow them to get a little bit browned on the bottom without burning, and then scrape up the browned bits while stirring the onions with a metal spoon or spatula. Typically the heat should be in the medium to medium-low range, but your mileage may vary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99AC40A656F7E74FA7AC4604229A759C', X'A839DCA1CAAE5043B9F1DCD0C3700863', X'A839DCA1CAAE5043B9F1DCD0C3700863', 5, NULL, NULL, 2, NULL, 0, 'Over time you''ll learn exactly how dark you like your caramelized onions. Our favorite point to stop is when they''re a gorgeous mahogany brown and just on the cusp of jammy. At this stage they''re super sweet and savory and not at all burned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1DC75D212A54F48BEC7664480711075', X'A839DCA1CAAE5043B9F1DCD0C3700863', X'A839DCA1CAAE5043B9F1DCD0C3700863', 6, NULL, NULL, 2, NULL, 0, 'Remove any large herb stems before using these babies in whatever recipes you like (or just spooning over all your hopes and dreams).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8249245782EE8489F9D1454A2433BB4', X'8506E030AD6D564587D1578FECABB448', X'8506E030AD6D564587D1578FECABB448', 0, NULL, NULL, 2, NULL, 0, 'In a small lidded jar, shake all ingredients together (or whisk together in a bowl). Pass at the table with sushi bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8CD3C6F187F9B459D990FCBC65B9F49', X'E32A31A5AB8F584F8251C61992E473B9', X'E32A31A5AB8F584F8251C61992E473B9', 0, NULL, NULL, 2, NULL, 0, 'Stir all ingredients together and serve as a sauce or salad dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92A434842AFD0B4D92756E8735FCAA0D', X'E32A31A5AB8F584F8251C61992E473B9', X'E32A31A5AB8F584F8251C61992E473B9', 1, NULL, NULL, 2, NULL, 0, 'Store leftovers tightly sealed in the fridge for up to a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF05B14FA9FAF640B16F282226998506', X'1444FFC14FD93B41B06953D5795B7D9E', X'1444FFC14FD93B41B06953D5795B7D9E', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a blender and process on high until smooth and creamy. Keeps well in a jar the fridge for up to two weeks. If not pourable, bring to room temperature before using. (If you''re in a rush, 10-15 seconds in the microwave and a quick stir does the trick.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C1F253CD7594D41BBC07772699E47EF', X'DBA518186646CD4580D9778D3EFEAEDF', X'DBA518186646CD4580D9778D3EFEAEDF', 0, NULL, NULL, 2, NULL, 0, 'Use a mortar and pestle, the side of a chef''s knife, or a fork to mash the garlic to a paste. Stir well into the mayo. Spread onto any sandwich that could use a little kick. A good old summer tomato sandwich schmeared with this stuff is BOMB.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3352B4E434CDF4DA8E7138E6FB671C9', X'A33A4E8E36F01B41A8486B75CB18999F', X'A33A4E8E36F01B41A8486B75CB18999F', 0, NULL, NULL, 2, NULL, 0, 'Place the pine nuts in a small, dry pan over medium heat and toast until lightly browned in spots, stirring or tossing in the pan occasionally, about five minutes. Keep a close eye on them so they don''t burn. Let cool slightly and pour into a food processor fitted with the blade.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB8E062BF528ED4587779904AEF77320', X'A33A4E8E36F01B41A8486B75CB18999F', X'A33A4E8E36F01B41A8486B75CB18999F', 1, NULL, NULL, 2, NULL, 0, 'Trim any roots from the ramps and chop the bulbs, stems and leaves into bite-sized pieces. Add to food processor along with basil, spinach, salt and lemon juice. Pulse until finely chopped.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBCA389613FB294AB4A54C8414A2198F', X'A33A4E8E36F01B41A8486B75CB18999F', X'A33A4E8E36F01B41A8486B75CB18999F', 2, NULL, NULL, 2, NULL, 0, 'With the machine running, pour in the olive oil and process until oil is incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5BC9AD3EC596E459AC7A2591078D108', X'A33A4E8E36F01B41A8486B75CB18999F', X'A33A4E8E36F01B41A8486B75CB18999F', 3, NULL, NULL, 2, NULL, 0, 'With the machine off, stir in cheese with a spoon.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'314053F531929E46A8C528D4CCB3C98C', X'A33A4E8E36F01B41A8486B75CB18999F', X'A33A4E8E36F01B41A8486B75CB18999F', 4, NULL, NULL, 2, NULL, 0, 'Use immediately or store tightly covered in the fridge for up to a few days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D785AF9338FF5419FD9E3D9F904929B', X'3E48318157BBE64CBA1332CB843259A2', X'3E48318157BBE64CBA1332CB843259A2', 0, NULL, NULL, 2, NULL, 0, 'Pour the sugar into in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96E1021C04402C4A9DEECB68B8CC94A5', X'3E48318157BBE64CBA1332CB843259A2', X'3E48318157BBE64CBA1332CB843259A2', 1, NULL, NULL, 2, NULL, 0, 'Slit the vanilla beans lengthwise with a small, sharp knife and scrape out the seeds. Add the seeds to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C57D0B94F42D484792A44552F54D1D97', X'3E48318157BBE64CBA1332CB843259A2', X'3E48318157BBE64CBA1332CB843259A2', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle in the cinnamon, cardamom and nutmeg and stir well to distribute all the spices evenly. If there are any clumps, break them up and pass the mixture through a sieve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA2551FBFDB3884D9EF7924AF7E8FF58', X'3E48318157BBE64CBA1332CB843259A2', X'3E48318157BBE64CBA1332CB843259A2', 3, NULL, NULL, 2, NULL, 0, 'Divide spiced sugar among 12 small jars such as these wide-mouth half pint jars — about 6 heaping tablespoons per jar plus a little extra for good luck.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3324D772C9B9541B522BC325E4E9FBC', X'4F98B0937789134C9D5F371180708CB4', X'4F98B0937789134C9D5F371180708CB4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 200°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30992BF6B389224A85B497EB5D92105F', X'4F98B0937789134C9D5F371180708CB4', X'4F98B0937789134C9D5F371180708CB4', 1, NULL, NULL, 2, NULL, 0, 'Pick the rosemary needles and sage and thyme leaves from the stems and discard stems. Place the herbs in the bowl of a standard food processor (about 12-cup capacity) fitted with the blade. Add the garlic, lemon zest and salt. The bowl will be quite full, but don''t worry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75288E2883B344478CC70D4C7B05A0FB', X'4F98B0937789134C9D5F371180708CB4', X'4F98B0937789134C9D5F371180708CB4', 2, NULL, NULL, 2, NULL, 0, 'Process until the herbs are finely chopped and well-distributed throughout the salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8F13E6D1A3F394FBBA0C61E723C022A', X'4F98B0937789134C9D5F371180708CB4', X'4F98B0937789134C9D5F371180708CB4', 3, NULL, NULL, 2, NULL, 0, 'Spread the herb salt onto two rimmed baking sheets and place in the oven for 20 minutes. The salt will form a solid slab on each sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18A7DF21395E1343ACA8C99F09C5E17B', X'4F98B0937789134C9D5F371180708CB4', X'4F98B0937789134C9D5F371180708CB4', 4, NULL, NULL, 2, NULL, 0, 'Break up the slab with your hands, a fork or spoon, a potato masher, whatever you like. Then pass the mixture through a sieve into a large bowl until there are no clumps left.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E71DD0E1E33FF44994AC77719A8F6C1D', X'4F98B0937789134C9D5F371180708CB4', X'4F98B0937789134C9D5F371180708CB4', 5, NULL, NULL, 2, NULL, 0, 'Divide the herb salt among 12 small jars such as these wide-mouth half pint jars. Label and give!');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EB75FDBA70038438A4FA2816EF47BF8', X'B42E36FF81E856428EEF24B8D0DF8456', X'B42E36FF81E856428EEF24B8D0DF8456', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with two racks close to the center. Brush any dirt off shiitakes. Remove stems and slice caps very thinly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90AFC03ED9786B43A803B6C39DF6A028', X'B42E36FF81E856428EEF24B8D0DF8456', X'B42E36FF81E856428EEF24B8D0DF8456', 1, NULL, NULL, 2, NULL, 0, 'Place sliced shiitakes into a medium mixing bowl. Add the oil, smoked paprika, and salt. Toss thoroughly to coat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'984B44E0E7E7724188FCA685B818CE0C', X'B42E36FF81E856428EEF24B8D0DF8456', X'B42E36FF81E856428EEF24B8D0DF8456', 2, NULL, NULL, 2, NULL, 0, 'Divide shiitake slices between two rimmed cookie sheets. Spread in a single layer without crowding. Bake for about 20 minutes, until some bits are quite crispy and others a little softer. Let cool slightly before serving. I rarely have leftovers, but if you do, store tightly covered in the fridge for several days and pop in the toaster oven to re-crisp.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EF0DD7E91F4C14E9DF0560F32A8B16C', X'FEFE71331590464EA65E597C6BB213C3', X'FEFE71331590464EA65E597C6BB213C3', 0, NULL, NULL, 2, NULL, 0, 'Roughly chop cilantro. Combine all ingredients in a blender and blend until smooth, stopping to push cilantro down into the blades as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31C9B8EF19C829458297DC84BFC8D13A', X'9AF725C435961E4593641C62EC6B619F', X'9AF725C435961E4593641C62EC6B619F', 0, NULL, NULL, 2, NULL, 0, 'Pour the vegetable stock into a medium pot and drop in the shiitakes. Bring stock to a boil over high heat, then remove from heat and let mushrooms steep for 30 minutes. Pour stock and mushrooms into large spouted measuring cup or bowl, and wipe out the pot with a paper towel.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20DBD6D14204044AAEFECF5E53E78EF5', X'9AF725C435961E4593641C62EC6B619F', X'9AF725C435961E4593641C62EC6B619F', 1, NULL, NULL, 2, NULL, 0, 'Add the butter to the empty pot and melt over medium-high heat. If using the shallot, add and cook for a minute or so, whisking once or twice. Add the flour and cook, whisking, until it turns very lightly golden, about two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F94D446FC973E40B6D858DA5DEB872B', X'9AF725C435961E4593641C62EC6B619F', X'9AF725C435961E4593641C62EC6B619F', 2, NULL, NULL, 2, NULL, 0, 'Pour in the stock with the mushrooms in a stream, whisking the whole time. Add soy sauce, marmite, and a few grinds of pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4122EF3973A59F45A39B63E8A63209CE', X'9AF725C435961E4593641C62EC6B619F', X'9AF725C435961E4593641C62EC6B619F', 3, NULL, NULL, 2, NULL, 0, 'Bring stock back to a boil, then reduce heat and simmer, uncovered, until gravy is reduced by almost half, about 20 minutes. Taste for seasoning and add salt and additional pepper if desired. Remove mushrooms before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'024E356B9D62274791B7E37736B8CED9', X'B5E87D341604B14AAB83C533F4E50F41', X'B5E87D341604B14AAB83C533F4E50F41', 0, NULL, NULL, 2, NULL, 0, 'Peel the onions and halve from tip to root. Very thinly slice each half from tip to root.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'962C70106C3D7E45B617F4EC180ED406', X'B5E87D341604B14AAB83C533F4E50F41', X'B5E87D341604B14AAB83C533F4E50F41', 1, NULL, NULL, 2, NULL, 0, 'In a quart-size mason jar or medium bowl with a lid, combine the lime juice, red wine vinegar, white vinegar, sugar, and salt. Stir (or put a lid on the jar and shake gently) to completely dissolve sugar and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCE95770C2936048BC4F19E4B43FF736', X'B5E87D341604B14AAB83C533F4E50F41', X'B5E87D341604B14AAB83C533F4E50F41', 2, NULL, NULL, 2, NULL, 0, 'Add the sliced onions and the herbs to the jar or bowl. Stir and press a bit to submerge all the onions and herbs in the liquid. Top off with an extra splash of vinegar if necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F562AD7213513F459EA816E431111E2F', X'B5E87D341604B14AAB83C533F4E50F41', X'B5E87D341604B14AAB83C533F4E50F41', 3, NULL, NULL, 2, NULL, 0, 'Pickle these babies in the fridge for 24 hours or simply on the counter at room temperature for an hour or two before serving as a condiment for tacos, sandwiches, salads — you name it. They keep very well in the fridge for up to a month.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B1D76B591B79A4185DD26058DA70E5D', X'220BA7D2E010C4478B3EB62645221EF1', X'220BA7D2E010C4478B3EB62645221EF1', 0, NULL, NULL, 2, NULL, 0, 'Into the bowl of a food processor fitted with the blade, place the egg yolk, mustard, lemon juice, salt, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89F7F00806085F428DE8B2A656BF26D3', X'220BA7D2E010C4478B3EB62645221EF1', X'220BA7D2E010C4478B3EB62645221EF1', 1, NULL, NULL, 2, NULL, 0, 'Measure the oil into a liquid measuring cup with a spout. Turn on the food processor. Pour about half of the oil into the white plunger in the feed tube. The tiny hole at the bottom of the plunger will dribble the oil into the bowl of the processor, allowing the mayo to emulsify. When the oil level in the plunger gets low, pour in the remaining oil. The whole process should take about two to three minutes. When all the oil has dribbled into the bowl, stop the processor. Stir with a rubber spatula to incorporate any last bits of oil. Taste mayo for seasoning, adding more salt, pepper or lemon juice to taste. Store in an airtight container in the fridge for up to a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'581BA84BCA240442843271D17F21B718', X'BF7307F02B7D1A4F96C34EC1497D6D0A', X'BF7307F02B7D1A4F96C34EC1497D6D0A', 0, NULL, NULL, 2, NULL, 0, 'Quarter the apples and slice out the cores. Leave the skins on.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13EF1CDBDEB9C24C9C4F165C251E0932', X'BF7307F02B7D1A4F96C34EC1497D6D0A', X'BF7307F02B7D1A4F96C34EC1497D6D0A', 1, NULL, NULL, 2, NULL, 0, 'Add apples to a large pot (8 quart minimum).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D9AC360A0FD8F4F92A968BF885BDE34', X'BF7307F02B7D1A4F96C34EC1497D6D0A', X'BF7307F02B7D1A4F96C34EC1497D6D0A', 2, NULL, NULL, 2, NULL, 0, 'Add water and salt and bring to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'696B1B571CB14C47B1605180ED2CB269', X'BF7307F02B7D1A4F96C34EC1497D6D0A', X'BF7307F02B7D1A4F96C34EC1497D6D0A', 3, NULL, NULL, 2, NULL, 0, 'Cover and reduce the heat, adjusting to maintain a simmer. Cook until apples have softened completely and yield their shape easily when stirred.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60392A5E236C6C48B757FCDCF6123C92', X'BF7307F02B7D1A4F96C34EC1497D6D0A', X'BF7307F02B7D1A4F96C34EC1497D6D0A', 4, NULL, NULL, 2, NULL, 0, 'Set a food mill over a very large bowl and begin by pouring some of the apple mixture into the top of the mill. Turn the handle of the food mill clockwise to force the pulp through the holes. Reverse the direction of the blade occasionally to clear the mill.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'772EFA037C1FFC49A7BC3C6D920FF8E3', X'BF7307F02B7D1A4F96C34EC1497D6D0A', X'BF7307F02B7D1A4F96C34EC1497D6D0A', 5, NULL, NULL, 2, NULL, 0, 'Continue adding apples and rotating the blade until all the apples have gone through and only the peel remains in the top portion of the mill.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7366B2B008C742AAD4266C2FA45A94', X'BF7307F02B7D1A4F96C34EC1497D6D0A', X'BF7307F02B7D1A4F96C34EC1497D6D0A', 6, NULL, NULL, 2, NULL, 0, 'Add the sugar and cinnamon and stir thoroughly. When cool, divide into storage containers and refrigerate or freeze as desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE59EB31D15D5D4DA8B5B46F4F0AB43B', X'3873D2A81486684893006416D74999BA', X'3873D2A81486684893006416D74999BA', 0, NULL, NULL, 2, NULL, 0, 'Place peppercorns, mustard seeds, and coriander seeds into a small pan and set over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE1459D538D61A4E891378C76FE4B5E1', X'3873D2A81486684893006416D74999BA', X'3873D2A81486684893006416D74999BA', 1, NULL, NULL, 2, NULL, 0, 'Cook, stirring occasionally, until fragrant, adjusting heat as necessary to ensure spices don''t burn. Keep lid handy in case seeds pop. Cool slightly before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D4D2A9949A7344A86CF2CC2301CEE8D', X'3873D2A81486684893006416D74999BA', X'3873D2A81486684893006416D74999BA', 2, NULL, NULL, 2, NULL, 0, 'Pour pan contents into a mortar crush with pestle.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAA4D8D437C958469F584E3F6CBC9CAA', X'3873D2A81486684893006416D74999BA', X'3873D2A81486684893006416D74999BA', 3, NULL, NULL, 2, NULL, 0, 'Combine crushed seeds with other spices and mix thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45AA908F223D30448FFF19A1B87E7B42', X'3873D2A81486684893006416D74999BA', X'3873D2A81486684893006416D74999BA', 4, NULL, NULL, 2, NULL, 0, 'Store in a tightly sealed container for up to a few months.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9583BEC7F17414C9B1F6A4EFB830D25', X'62195E77BD909D4ABF6EDE1350ED745B', X'62195E77BD909D4ABF6EDE1350ED745B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Trim the bottoms from the asparagus, discarding as much as necessary to be left with only tender parts. Fill a large mixing bowl halfway with ice water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7F9D99166F5DD489B0C96FBEA36F95D', X'62195E77BD909D4ABF6EDE1350ED745B', X'62195E77BD909D4ABF6EDE1350ED745B', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch pan with a lid, bring an inch of water to a boil. Add asparagus, cover, and cook for two minutes. Remove asparagus from pan and plunge immediately into the bowl of ice water to set the bright green color.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C31FE813BBFB4E40BFE454A3F28076FF', X'62195E77BD909D4ABF6EDE1350ED745B', X'62195E77BD909D4ABF6EDE1350ED745B', 2, NULL, NULL, 2, NULL, 0, 'Dry asparagus well. Divide into 12 even bundles, with all tips facing the same direction.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55BBD029F631B242AC44E08CE1C9F1BF', X'62195E77BD909D4ABF6EDE1350ED745B', X'62195E77BD909D4ABF6EDE1350ED745B', 3, NULL, NULL, 2, NULL, 0, 'Wrap each bundle tightly with a slice of prosciutto. Line up bundles seam-side down on a parchment-lined baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E50215CFC7311A4ABEA477FDFB9DE867', X'62195E77BD909D4ABF6EDE1350ED745B', X'62195E77BD909D4ABF6EDE1350ED745B', 4, NULL, NULL, 2, NULL, 0, 'Melt the butter and stir in the garlic. Spoon some of the mixture over each bundle and brush it all over the surface of the asparagus and prosciutto. Sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FCDD76A1B4C4C4EA4D6FEFE794A7DFD', X'62195E77BD909D4ABF6EDE1350ED745B', X'62195E77BD909D4ABF6EDE1350ED745B', 5, NULL, NULL, 2, NULL, 0, 'Roast for 12 minutes, until beautifully fragrant and sizzling. Squeeze the lemon half all over the bundles before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3665818FDC76D84D95D0EB222D676354', X'B0C674C3163C9C46964748FC15D317EE', X'B0C674C3163C9C46964748FC15D317EE', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil. Add rice noodles and cook according to package instructions, usually just a few minutes (ours cook for 3).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F37B786BC9331A49BD257D5D9D5D2419', X'B0C674C3163C9C46964748FC15D317EE', X'B0C674C3163C9C46964748FC15D317EE', 1, NULL, NULL, 2, NULL, 0, 'While the noodles cook: Into a serving bowl, pour the safflower oil, toasted sesame oil, tamari, and sriracha. Whisk to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB328539815514BAA4AA89A55BC1BE4', X'B0C674C3163C9C46964748FC15D317EE', X'B0C674C3163C9C46964748FC15D317EE', 2, NULL, NULL, 2, NULL, 0, 'Drain noodles and add to sauce. You can rinse them before adding to the sauce if you like to reduce the starchiness, but I don''t bother. Toss with tongs to coat well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A4DFBB356A1CD47BEEC14DBD3F60CA8', X'B0C674C3163C9C46964748FC15D317EE', X'B0C674C3163C9C46964748FC15D317EE', 3, NULL, NULL, 2, NULL, 0, 'Add scallions and a big squeeze of fresh lime juice and toss. Serve warm or at room temperature with additional lime wedges.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26A2A1885C8F454AAA7E8AE675AE2469', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400° F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13C4DE2A187C61448DF625E48D93A809', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry the Brussels sprouts. Trim off and discard a little bit of the stem end from each sprout and slice each sprout in half lengthwise.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACCC79AA49575D42840789B5E1D96CC1', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the miso paste, tamari, hot water, and maple syrup.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9505DB801281F458F9182E85BFE29AC', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 3, NULL, NULL, 2, NULL, 0, 'Heat a 12-inch cast-iron skillet for a few minutes on medium-high. Pour in safflower oil and spread to coat bottom of pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF2C64234EB7164EB6FC5D2F42CFA908', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 4, NULL, NULL, 2, NULL, 0, 'Add Brussels sprouts and arrange cut-sides down. Cook without stirring until seared on the underside, 1 to 3 minutes depending on your stove.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03A62DF3AD2DB047AF268CB6D9FE6153', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 5, NULL, NULL, 2, NULL, 0, 'Remove pan from heat and pour miso glaze over sprouts. Stir to coat. Sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FFF4081EFE06845B16BC7D26A076095', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 6, NULL, NULL, 2, NULL, 0, 'Transfer pan to oven and roast for about 15 minutes, until cooked to your liking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4B6EBA08E9D86448E0420F0D84FC4B2', X'45B75C8CDB53014BBCA03530D7CEC6F1', X'45B75C8CDB53014BBCA03530D7CEC6F1', 7, NULL, NULL, 2, NULL, 0, 'Drizzle sprouts with sesame oil and toss well, scraping up any delicious browned bits from the bottom of the pan, and serve in a bowl or straight from the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA69FE36B89E0B478B657A77541D0420', X'43B21A83C3048049A3FA60FC19E8FAC4', X'43B21A83C3048049A3FA60FC19E8FAC4', 0, NULL, NULL, 2, NULL, 0, 'In a braiser or 12-inch lidded pan, bring 1/2-inch of water to a boil with a good pinch of salt. Add broccolini and cook until tender but crisp and vibrant green, about 5 minutes. Drain and plunge into a bowl of ice water if you like to stop cooking and preserve bright color. (You don''t have to do this, but it''s a cool and reliable trick.) Arrange broccolini on a platter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2857B42C666E4340BE80C665E0FB4270', X'43B21A83C3048049A3FA60FC19E8FAC4', X'43B21A83C3048049A3FA60FC19E8FAC4', 1, NULL, NULL, 2, NULL, 0, 'While the broccolini cooks, finely chop the parsley. Using a microplane, grate the garlic and then the zest of both lemons onto the parsley. Sprinkle 1/8 teaspoon salt overtop and then mix it all together well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7237FE2F0392554D975644A796438FE1', X'43B21A83C3048049A3FA60FC19E8FAC4', X'43B21A83C3048049A3FA60FC19E8FAC4', 2, NULL, NULL, 2, NULL, 0, 'Spoon gremolata over broccolini, drizzle with olive oil and the juice from 1/2 a lemon and sprinkle with flaky sea salt and pepper. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68457D4ED27CE64A8FEC1276499F90F9', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 0, NULL, NULL, 2, NULL, 0, 'To prepare the greens, cut out and discard the stems. Halve the leaves lengthwise and then cut crosswise into short strips. Wash the cut collards in several changes of water until perfectly clean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'953AD4241DF39149BDE1FC60C4B55C97', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 1, NULL, NULL, 2, NULL, 0, 'Set a 6-quart Instant Pot to the sauté function. Add bacon and cook, stirring frequently, until it''s rendered a few tablespoons of fat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66BC76E815D351469F55F973007C1936', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 2, NULL, NULL, 2, NULL, 0, 'Add onion and garlic and cook, stirring frequently, until beginning to soften (just a minute or two). Turn off the heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E2DD307B85A384089D565E975B2F974', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 3, NULL, NULL, 2, NULL, 0, 'Pour in the apple cider vinegar and stir, scraping up any browned bits from the bottom of the pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1954BD5A9BF2ED4C8B236076948C8568', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 4, NULL, NULL, 2, NULL, 0, 'Add collards, salt, red pepper flakes, paprika, and black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2A9E6DF8EDE0F439E7C99001F5854D4', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 5, NULL, NULL, 2, NULL, 0, 'Position the lid, set vent to sealing position, and set pot to manual, high pressure, 12 minutes. The pot will take a few minutes to come to pressure before starting to count down the cooking time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00DA79D28A21B443BCED3C2B8F4067F2', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 6, NULL, NULL, 2, NULL, 0, 'At the end of cooking, carefully perform an instant pressure release.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B7257ABEDEBFA4393FB3DF07D4B7677', X'099897E6692CDC40B92D3BA5CEA27765', X'099897E6692CDC40B92D3BA5CEA27765', 7, NULL, NULL, 2, NULL, 0, 'When steam has fully released, you can open the pot and give the greens a stir to coat them with the bit of silky liquid at the bottom of the pot. They''re ready to serve right away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C6D6D52F87F094AA21EE903ACDA3D67', X'2188AD294F66BD4EAEF920528F37A1AD', X'2188AD294F66BD4EAEF920528F37A1AD', 0, NULL, NULL, 2, NULL, 0, 'Wash collard greens. Carefully cut leaves from stems and discard stems.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAC252BF2F3CEC41B19F694DDC41BB4A', X'2188AD294F66BD4EAEF920528F37A1AD', X'2188AD294F66BD4EAEF920528F37A1AD', 1, NULL, NULL, 2, NULL, 0, 'Stack leaves one on top of the next. Roll leaves from stem end to tip into a cigar shape.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E0738F7E99EBF4498A9678EF7CCC4D2', X'2188AD294F66BD4EAEF920528F37A1AD', X'2188AD294F66BD4EAEF920528F37A1AD', 2, NULL, NULL, 2, NULL, 0, 'Cut cigar crosswise into strips 1/8-inch thick (as you would to chiffonade herbs, but a little thicker).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'555A3548C8704143930D8B8A2A5144AD', X'2188AD294F66BD4EAEF920528F37A1AD', X'2188AD294F66BD4EAEF920528F37A1AD', 3, NULL, NULL, 2, NULL, 0, 'Cut bacon crosswise into 1/4-inch batons. Place a 12-inch skillet over medium-high heat and cook bacon until crisp, stirring occasionally.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CEAB4F5789A7749A86E3FAE5470D697', X'2188AD294F66BD4EAEF920528F37A1AD', X'2188AD294F66BD4EAEF920528F37A1AD', 4, NULL, NULL, 2, NULL, 0, 'Add garlic and collard greens to pan and cook, stirring constantly until collards are just wilted and bright green, one to two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D2E1B1A9F20A14488D060EEE6119804', X'2188AD294F66BD4EAEF920528F37A1AD', X'2188AD294F66BD4EAEF920528F37A1AD', 5, NULL, NULL, 2, NULL, 0, 'Off the heat, add lemon juice and salt and pepper to taste. Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00222D0B9EDB0645A0C0A22099BAE05A', X'043D100B1DCE064C8482480850C70884', X'043D100B1DCE064C8482480850C70884', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center, or heat grill to medium-high.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16914D4EA8CDAB4CB3AD254D66B7FC69', X'043D100B1DCE064C8482480850C70884', X'043D100B1DCE064C8482480850C70884', 1, NULL, NULL, 2, NULL, 0, 'Wash, dry and trim off the woody bottom portion of the asparagus.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB87D4796B99947B43C273A708A8B37', X'9417CC039C0F6B4C87134FF76BC2ED6F', X'9417CC039C0F6B4C87134FF76BC2ED6F', 0, NULL, NULL, 2, NULL, 0, 'Peel, halve, and slice the onions from root to tip.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAE599BC6FAF9544962623C9EEA6629B', X'9417CC039C0F6B4C87134FF76BC2ED6F', X'9417CC039C0F6B4C87134FF76BC2ED6F', 1, NULL, NULL, 2, NULL, 0, 'Quarter the peppers, discard seeds and ribs, and slice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A26F1F005ECE2B4C915724248EBBCAC0', X'9417CC039C0F6B4C87134FF76BC2ED6F', X'9417CC039C0F6B4C87134FF76BC2ED6F', 2, NULL, NULL, 2, NULL, 0, 'Heat olive oil over medium-high heat in a 12-inch frying pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C7314A6357E89488D8CB44A9B864383', X'9417CC039C0F6B4C87134FF76BC2ED6F', X'9417CC039C0F6B4C87134FF76BC2ED6F', 3, NULL, NULL, 2, NULL, 0, 'Add peppers, onions, salt, and plenty of freshly ground pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3701C73771568F4F949CD813CC600CE2', X'9417CC039C0F6B4C87134FF76BC2ED6F', X'9417CC039C0F6B4C87134FF76BC2ED6F', 4, NULL, NULL, 2, NULL, 0, 'Cook, stirring from time to time, for 15 to 20 minutes, until peppers are as soft as you like and onions have softened and browned in spots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A01F1B5EF242448B1C22B110E463E68', X'D76A28B41410F24B8868F21136C6CBB2', X'D76A28B41410F24B8868F21136C6CBB2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EB3BD35869DB74DB8E661C111D7352F', X'D76A28B41410F24B8868F21136C6CBB2', X'D76A28B41410F24B8868F21136C6CBB2', 1, NULL, NULL, 2, NULL, 0, 'Warm the olive oil (or melt the vegan butter) over medium-high heat in a large frying pan. Add onion and celery and sprinkle with 1/4 teaspoon of the salt. Cook, stirring occasionally, until beginning to soften and turn translucent, about 5 minutes. Adjust heat as necessary to prevent browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F82165450A24E4FBD2832BFD4E54DFC', X'D76A28B41410F24B8868F21136C6CBB2', X'D76A28B41410F24B8868F21136C6CBB2', 2, NULL, NULL, 2, NULL, 0, 'Add apple, chestnuts, thyme, and sage, and continue cooking until apple has softened, about 5 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'519E1DE8649E2E4A899FFA67C44E364F', X'D76A28B41410F24B8868F21136C6CBB2', X'D76A28B41410F24B8868F21136C6CBB2', 3, NULL, NULL, 2, NULL, 0, 'Place bread cubes into a large mixing bowl and scrape the contents of the frying pan overtop. Pour in the broth. Sprinkle with remaining 1/4 teaspoon salt and a few generous grinds of pepper. Mix gently until everything is well-distributed, then let the mixture sit for a few minutes so the bread cubes absorb the wet ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F6002B2492E3245831D4D85D01C5054', X'D76A28B41410F24B8868F21136C6CBB2', X'D76A28B41410F24B8868F21136C6CBB2', 4, NULL, NULL, 2, NULL, 0, 'Transfer stuffing to a large baking dish such as an extra-deep 9x13-inch pan or a 5-quart braiser. Bake uncovered for 45 minutes, until crisp on top and tender inside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60946A92B442BD4D93C5519B1534CAB4', X'31BB40A18EB1D0468FBF0DE3A0565A09', X'31BB40A18EB1D0468FBF0DE3A0565A09', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, stir together sake, mirin, soy sauce, and sugar. Warm over medium heat, stirring occasionally, until sugar is dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1468F4455C38546837EBB8E5AC812B0', X'31BB40A18EB1D0468FBF0DE3A0565A09', X'31BB40A18EB1D0468FBF0DE3A0565A09', 1, NULL, NULL, 2, NULL, 0, 'Off the heat, add one heaping cup of ice to the pot to both dilute and chill the marinade. (Alternatively, you can place the ice in a 4-cup glass measuring cup or nonreactive bowl and pour the marinade overtop.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97D76670836EC94080B38CDDEBD6BE8B', X'31BB40A18EB1D0468FBF0DE3A0565A09', X'31BB40A18EB1D0468FBF0DE3A0565A09', 2, NULL, NULL, 2, NULL, 0, 'Place peeled seven-minute eggs into marinade, cover tightly, and refrigerate for about 24 hours, turning once or twice if you think of it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B55814A57A590F4B8ABB1232ED9789E3', X'31BB40A18EB1D0468FBF0DE3A0565A09', X'31BB40A18EB1D0468FBF0DE3A0565A09', 3, NULL, NULL, 2, NULL, 0, 'For less intense flavor, you can remove the eggs at any point after about eight hours. For more intense flavor you can let the eggs marinate a bit longer. However, the salt and sugar in the marinade will make the eggs tough eventually, so don''t leave them for more than about 36 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0E8B7FD6197B442BE2A3159C991F263', X'5A899D26E85F84428F7BE1F61A9E27D1', X'5A899D26E85F84428F7BE1F61A9E27D1', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a 12-inch frying pan — the bigger the better since raw spinach is huuuuuge — over medium heat. Add onion and garlic and cook, stirring occasionally, until softened but not browned, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FAC86DFCB4D5843A45E1218F066A94F', X'5A899D26E85F84428F7BE1F61A9E27D1', X'5A899D26E85F84428F7BE1F61A9E27D1', 1, NULL, NULL, 2, NULL, 0, 'Add spinach by the handful, letting it wilt just enough to make room before adding more. Cook, stirring almost constantly, just until all spinach is wilted. Stir in salt, nutmeg, red pepper flakes, heavy cream, cream cheese, and pepper to taste. Simmer briefly to heat through, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A1B73F09EF9C4BA70AB0F8F9D9C107', X'27D86EA9ACABE442B7F87B8F838A653A', X'27D86EA9ACABE442B7F87B8F838A653A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F. Place a rack in the center and a rack at the bottom with a rimmed baking sheet to catch any drips.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73BCBB96CDC57B428D441BFB1CF0634F', X'27D86EA9ACABE442B7F87B8F838A653A', X'27D86EA9ACABE442B7F87B8F838A653A', 1, NULL, NULL, 2, NULL, 0, 'Scrub potatoes clean and dry them well. Poke shallow holes all around each potato with the tines of a fork to let some steam escape during baking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'028AF4D03716424CB62C4AF8CE7C50C3', X'27D86EA9ACABE442B7F87B8F838A653A', X'27D86EA9ACABE442B7F87B8F838A653A', 2, NULL, NULL, 2, NULL, 0, 'Rub each potato with a little bit of oil and sprinkle with salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B738EB00A31165419AF556305E435B21', X'27D86EA9ACABE442B7F87B8F838A653A', X'27D86EA9ACABE442B7F87B8F838A653A', 3, NULL, NULL, 2, NULL, 0, 'Place potatoes directly onto middle oven rack and bake until very tender throughout. Baking time will vary with the size of your potatoes, but should be around an hour for medium-sized potatoes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4BF051BCC9DA649A784BA7BA47DFDA9', X'27D86EA9ACABE442B7F87B8F838A653A', X'27D86EA9ACABE442B7F87B8F838A653A', 4, NULL, NULL, 2, NULL, 0, 'Potatoes are done when they yield easily to a fork or give readily when you squeeze them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72EA1E1E1E5C7546BE93639619938F8B', X'673B97D80149F840A8C10AF1D7C1121B', X'673B97D80149F840A8C10AF1D7C1121B', 0, NULL, NULL, 2, NULL, 0, 'Trim the coarse end from each green bean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B509665560D8344891ADCCFACA66E7A', X'673B97D80149F840A8C10AF1D7C1121B', X'673B97D80149F840A8C10AF1D7C1121B', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, fill a wide, lidded pot or pan with 1 inch of water and two teaspoons of and bring to a boil over high heat. Add the trimmed beans, replace lid and cook until just tender, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8B6A7B707F7A7458DBCBD634A30D104', X'673B97D80149F840A8C10AF1D7C1121B', X'673B97D80149F840A8C10AF1D7C1121B', 2, NULL, NULL, 2, NULL, 0, 'While the water comes to a boil, place the minced shallot in a wide, shallow serving bowl large enough to accommodate the green beans and pour in the vinegar. Stir to combine. Wait five minutes for the vinegar to take the bite out of the shallots. Then whisk in the mustard, salt, pepper and olive oil until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5BB06E3B45F8A45A75E5344671BE3B7', X'673B97D80149F840A8C10AF1D7C1121B', X'673B97D80149F840A8C10AF1D7C1121B', 3, NULL, NULL, 2, NULL, 0, 'Drain green beans and add to the serving bowl. Toss to coat green beans with dressing and serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'487F8B11E407B24ABE52C83E5D4C4EB7', X'7F3996F74D84684888031F6D3C9514A2', X'7F3996F74D84684888031F6D3C9514A2', 0, NULL, NULL, 2, NULL, 0, 'Fill a very large pot halfway with cold water and two tablespoons of fine sea salt. Place potato pieces into the pot as you peel and chop them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75D75762A3A99341969268C632052C9F', X'7F3996F74D84684888031F6D3C9514A2', X'7F3996F74D84684888031F6D3C9514A2', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, then reduce heat and and simmer until tender, about 10 minutes. Drain potatoes in a colander.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53ACF63BAB26E24686E5C47C633CF28C', X'7F3996F74D84684888031F6D3C9514A2', X'7F3996F74D84684888031F6D3C9514A2', 2, NULL, NULL, 2, NULL, 0, 'Heat the butter and milk together in a small pot over medium-low heat until butter is melted. Either press the potatoes through a ricer or food mill back into the pot, or return them to the pot straightaway and use a masher to mash them until smooth. Pour in hot buttery milk in a slow stream, stirring to incorporate. Taste and adjust for salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FBE81D030D2A94983D9E4509B6E52B4', X'2D876B0E0CA37B4C89CFBCEA0724310D', X'2D876B0E0CA37B4C89CFBCEA0724310D', 0, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and cut into rough 1-inch cubes. Place in a medium pot and fill with water to cover by an inch. Add 1 tablespoon salt. Bring to a boil, then reduce heat and simmer until tender but not waterlogged, about 15 minutes. Drain and place back in pot. Mash with potato masher until reasonably smooth. (I prefer to mash before adding liquid so the potatoes don''t get gummy.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74348934D2A5E443957CD2622374C275', X'2D876B0E0CA37B4C89CFBCEA0724310D', X'2D876B0E0CA37B4C89CFBCEA0724310D', 1, NULL, NULL, 2, NULL, 0, 'Place milk and butter in a microwave-safe bowl or liquid measure and heat until just shy of boiling. Pour liquid into pot with potatoes, add remaining 1 teaspoon salt and some pepper, and stir together until creamy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EFA82494E673645B2F8C34372CB137E', X'1B12E5FB9D35614081081BFC013FAA5E', X'1B12E5FB9D35614081081BFC013FAA5E', 0, NULL, NULL, 2, NULL, 0, 'Cut potatoes into 1-inch pieces. Place in a medium pot with two tablespoons fine sea salt and cold water to cover by two inches. Bring to a boil, then cook until as tender as you like, 10-15 minutes after boiling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'644E7D59FB819044B4B5E8795633C132', X'1B12E5FB9D35614081081BFC013FAA5E', X'1B12E5FB9D35614081081BFC013FAA5E', 1, NULL, NULL, 2, NULL, 0, 'While potatoes boil, cook bacon until crispy, drain on paper towels and finely chop or crumble.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DA2A2EBE8D136448C60E73204BFFEBB', X'1B12E5FB9D35614081081BFC013FAA5E', X'1B12E5FB9D35614081081BFC013FAA5E', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together oil, vinegar, mustard, sugar, fine sea salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4E0A4082CA4AD49BAD4DC67CF2089E1', X'1B12E5FB9D35614081081BFC013FAA5E', X'1B12E5FB9D35614081081BFC013FAA5E', 3, NULL, NULL, 2, NULL, 0, 'When potatoes are done, drain and place into a large mixing bowl. Add dressing and toss to coat. Stir in bacon, herbs, and shallot. Place in a serving bowl and sprinkle with flaky salt if you like. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8A8185AD1CD2F41887AB92CA2C12283', X'A966B353B818F2459EBAE3C6F296980E', X'A966B353B818F2459EBAE3C6F296980E', 0, NULL, NULL, 2, NULL, 0, 'Fill a medium pot halfway with water, add 2 tablespoons salt, and bring to a boil. Add orzo, give it one big stir, and then cook undisturbed to al dente according to package instructions. Adjust heat as necessary to maintain a boil or very brisk simmer without letting the pot overflow.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9C33EE3B87CCB43ADA63325331FCC6F', X'A966B353B818F2459EBAE3C6F296980E', X'A966B353B818F2459EBAE3C6F296980E', 1, NULL, NULL, 2, NULL, 0, 'Drain pasta, place back into pot, and toss with butter and lemon juice until butter is melted. Stir in pecorino and plenty of freshly ground black pepper and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CF8061343941C498181C4D8BCB82BE6', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil over medium heat in a medium pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71D56443CAF8B6429096A5F6BFD71912', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 1, NULL, NULL, 2, NULL, 0, 'Add shallot, garlic and thyme and cook, stirring occasionally, for about two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40669CB45D73524290AD1DD2DEC51999', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 2, NULL, NULL, 2, NULL, 0, 'Stir in beans and stock. Simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC0274F02F40F744A8AE8138DCFBD4AA', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 3, NULL, NULL, 2, NULL, 0, 'Remove thyme sprigs and puree with an immersion blender until perfectly smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E686CDB821AF2545B1539D3033383C16', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 4, NULL, NULL, 2, NULL, 0, 'Stir in lemon juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF7C400301CB714986E8D36D0DE78FB7', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 5, NULL, NULL, 2, NULL, 0, 'In a small skillet over medium heat, melt butter and then cook until foaming subsides and butter solids turn brown at bottom of pan. Swirling the pan gently can help you see what’s going on under there.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A30BACC71813394FBE2101E4DDDD8A5A', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 6, NULL, NULL, 2, NULL, 0, 'When butter is golden brown and smells spectacularly nutty, remove pan from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FA3F36EE0ECC948834640F9E7C985AF', X'B2DA2B2315855E4491EF733090789DE6', X'B2DA2B2315855E4491EF733090789DE6', 7, NULL, NULL, 2, NULL, 0, 'Stir brown butter into bean puree.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB6864B118A3E747B3262A3A62847ADD', X'B5C32016671BB44D8F59D10EF8B493FC', X'B5C32016671BB44D8F59D10EF8B493FC', 0, NULL, NULL, 2, NULL, 0, 'Preheat a 12-inch cast iron skillet on the stovetop for five minutes over high heat. Meanwhile, wipe any dirt off the mushrooms with a paper towel and, if you like (and your mushrooms are whole), rinse under running water and spin dry. Remove stems from shiitakes and oyster mushrooms and trim a slice of the stem end off the baby bellas. Slice baby bellas and shiitakes about 1/4-inch thick and cut oyster mushrooms into small bite-sized pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE30570B06FD78428BB761D97283E905', X'B5C32016671BB44D8F59D10EF8B493FC', X'B5C32016671BB44D8F59D10EF8B493FC', 1, NULL, NULL, 2, NULL, 0, 'Add butter to pan. When melted and bubbly, add mushrooms and toss to coat. Cook, stirring only occasionally, for about 10 minutes, until well browned in plenty of spots. Stir in salt and pepper to taste and garnish with parsley if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B811F403409F45A515FBF871C7EAE1', X'C5A7EE2ABE9E9B4B9F79522B548D8E43', X'C5A7EE2ABE9E9B4B9F79522B548D8E43', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill to about 400°F. Place shucked corn on grates and grill until nicely browned but still tender, about 3 minutes per `side,` 10-ish minutes total. Place on a serving platter to cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B94029238952D84E80FBD1C49DB6B26C', X'C5A7EE2ABE9E9B4B9F79522B548D8E43', X'C5A7EE2ABE9E9B4B9F79522B548D8E43', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the mayonnaise, crema or sour cream, chili powder, garlic, lime juice and zest, cilantro and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC202E5AA4A8F94091855A0C6DA95006', X'C5A7EE2ABE9E9B4B9F79522B548D8E43', X'C5A7EE2ABE9E9B4B9F79522B548D8E43', 2, NULL, NULL, 2, NULL, 0, 'Spread sauce over corn with a spoon. Sprinkle with Cotija and with extra cilantro and chili powder. Garnish with lime wedges and serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'615272501AB7DE4496D3B0156709A34E', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', 0, NULL, NULL, 2, NULL, 0, 'Set heavy 12-inch skillet over medium heat. Pour in hazelnuts. Cook, shaking the pan occasionally, until skins have turned dark brown in spots and begun to split, about 15 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69135F1D03026747BC0015AAC7AD6F56', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', 1, NULL, NULL, 2, NULL, 0, 'While hot, carefully pour hazelnuts into center of clean dishtowel. Rub together to remove skins. When hazelnuts are cool enough to handle, finely chop and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F161F1D90866EF439BE7C2EF2FFD62E0', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', 2, NULL, NULL, 2, NULL, 0, 'Wipe out skillet to remove any hazelnut bits. Set over medium-high heat. Add butter and cook until bubbly and beginning to turn brown.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5B5B5D78036154B8C470928F06648B6', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', 3, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium. Add cauliflower, sage, chopped hazelnuts, and a few generous pinches of salt and pepper and cook, stirring occasionally, until cauliflower is tender and has caramelized in spots, 10 to 15 minutes. Depending on how well your pan retains heat, you may find the nuts are in danger of burning before the cauliflower becomes tender. If that''s the case, pour in a couple of tablespoons of water, cover the pan and reduce heat to low, cooking until cauliflower is tender, then proceed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'722B9F6DF517C74FBB12B6543446DEB5', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', X'9F6A0D248A9D6545B9FDF3EB1CAF0BA8', 4, NULL, NULL, 2, NULL, 0, 'Quarter and core pear and slice thinly. Add pear and parsley to cauliflower mixture, stir to distribute, and taste for seasoning. Serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12A429639F4BD645A255231DF1352259', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 0, NULL, NULL, 2, NULL, 0, 'In a medium-sized pot, heat the bacon fat or oil over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54D3B4A8F402FC4BA5D093327DA2D842', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 1, NULL, NULL, 2, NULL, 0, 'Add the diced onion and pepper along with the salt, and cook, stirring occasionally, until starting to soften, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0A5E097BAD5D9438C7443833691C001', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, tomato paste, cumin, and chili powder. Cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4D1E5209DB6254BB4EDC60F0A99E5A8', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 3, NULL, NULL, 2, NULL, 0, 'Add the beans along with 1/2 cup bean cooking water, broth, or plain water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C18B00136878E489D8645F04086568A', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1F3D616A7114948ADB15DE8750B6C68', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 5, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low and simmer for 15 minutes. Add up to 1/2 cup more water if the beans become too dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE595EDEE2808B4FA3D57EE5FE5F4CD0', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 6, NULL, NULL, 2, NULL, 0, 'Then, using a potato masher, the back of a wooden spoon, or an immersion blender, mash the beans until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99C3B3F4231D3C429652767CE54E605A', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 7, NULL, NULL, 2, NULL, 0, 'Stir in lime juice and taste for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3746BFE5C4DB6041AA60C158ED125270', X'9138EBDB71BF2746A1EF662C0F70A505', X'9138EBDB71BF2746A1EF662C0F70A505', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78448DF7328C3D43A31BBAFBD3DE7315', X'2C3AFE2B5A5A0F42ADC919C35A36F313', X'2C3AFE2B5A5A0F42ADC919C35A36F313', 0, NULL, NULL, 2, NULL, 0, 'Core and roughly chop the cauliflower and place in a large, heavy pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80EC6B982FF593429C95FC26584E9E82', X'2C3AFE2B5A5A0F42ADC919C35A36F313', X'2C3AFE2B5A5A0F42ADC919C35A36F313', 1, NULL, NULL, 2, NULL, 0, 'Add shallot, salt and vegetable broth. Cover and bring to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'579CB03D51ED794AA67738D733B5A61F', X'2C3AFE2B5A5A0F42ADC919C35A36F313', X'2C3AFE2B5A5A0F42ADC919C35A36F313', 2, NULL, NULL, 2, NULL, 0, 'Reduce heat to simmer and cook until very tender, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE5FF7E52FCCFD4EA45F14176E06EBB3', X'2C3AFE2B5A5A0F42ADC919C35A36F313', X'2C3AFE2B5A5A0F42ADC919C35A36F313', 3, NULL, NULL, 2, NULL, 0, 'Spoon the entire contents of the pot into a food processor or high-speed blender, or leave it in the pot if you''d like to use an immersion blender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E885FFAFF8DBA24F8F165BD78D9A9149', X'2C3AFE2B5A5A0F42ADC919C35A36F313', X'2C3AFE2B5A5A0F42ADC919C35A36F313', 4, NULL, NULL, 2, NULL, 0, 'Add sour cream, butter, gouda, parmesan, nutmeg and a few good grinds black pepper and');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AC4F8399EBA034686ECF89A1A082AAE', X'2C3AFE2B5A5A0F42ADC919C35A36F313', X'2C3AFE2B5A5A0F42ADC919C35A36F313', 5, NULL, NULL, 2, NULL, 0, 'Process until very smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'755851751A6D044DBE08BBDBFE639314', X'866EEBB8E5BF7247B2AEF019E08AF8AF', X'866EEBB8E5BF7247B2AEF019E08AF8AF', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with two racks close to the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFA70F2EE8E35947953E0F59AEBEC75D', X'866EEBB8E5BF7247B2AEF019E08AF8AF', X'866EEBB8E5BF7247B2AEF019E08AF8AF', 1, NULL, NULL, 2, NULL, 0, 'Peel and trim the carrots. Slice very thick carrots into quarters from root to tip, slice medium-sized carrots in half from root to tip, and leave any very thin carrots whole. The idea is to cut all pieces to a reasonably similar size so they will roast evenly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6EAEEDE6CCAF64C8F65E06F4D584A21', X'866EEBB8E5BF7247B2AEF019E08AF8AF', X'866EEBB8E5BF7247B2AEF019E08AF8AF', 2, NULL, NULL, 2, NULL, 0, 'Place the carrots on one baking sheet and toss with half the oil, then move half the carrots to a second baking sheet and arrange in a roomy single layer. Sprinkle with salt and pepper and roast until tender and lightly browned, about 20-25 minutes depending on size.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C7C46CC114D6C4DBB402AC8267D1525', X'866EEBB8E5BF7247B2AEF019E08AF8AF', X'866EEBB8E5BF7247B2AEF019E08AF8AF', 3, NULL, NULL, 2, NULL, 0, 'While the carrots roast, place the remaining 1/2 tablespoon of oil and the mustard and cumin seeds into your smallest pan. Warm over medium heat until the seeds start to pop vigorously, then remove from heat and let sit until ready to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35289EFFAA0C0C489E6A0A3BD549B824', X'866EEBB8E5BF7247B2AEF019E08AF8AF', X'866EEBB8E5BF7247B2AEF019E08AF8AF', 4, NULL, NULL, 2, NULL, 0, 'When ready to serve, arrange carrots on a platter, drizzle with the cumin and mustard seed oil, sprinkle with cilantro or parsley and some flaky sea salt if you have it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53A51910988F88458B0969F78D9DFC57', X'033F3B9706256C4BAB64973DCE7AB7AE', X'033F3B9706256C4BAB64973DCE7AB7AE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F. Line two half sheet pans with baking and cooling racks if you have them and parchment if you don''t (no worries).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF9079E2DBEEBF48A5F8B0FC2EC0F4B6', X'033F3B9706256C4BAB64973DCE7AB7AE', X'033F3B9706256C4BAB64973DCE7AB7AE', 1, NULL, NULL, 2, NULL, 0, 'Fill a large bowl halfway with ice water. Cut the potatoes into french fry shape using a mandoline or a knife. Add cut potatoes to ice water as you work and let sit for 15 minutes. Drain and dry well with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B32C9AD276171D478A3019187A442B6B', X'033F3B9706256C4BAB64973DCE7AB7AE', X'033F3B9706256C4BAB64973DCE7AB7AE', 2, NULL, NULL, 2, NULL, 0, 'Dry out the bowl and add potatoes, oil, and a few generous sprinkles of salt, probably more than you think. Toss gently until all potatoes are glistening.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D3B63BC76AAA94DB15EF588517C492B', X'033F3B9706256C4BAB64973DCE7AB7AE', X'033F3B9706256C4BAB64973DCE7AB7AE', 3, NULL, NULL, 2, NULL, 0, 'One by one, place the potatoes on the baking sheets in nice orderly rows so that they don''t touch. This is my least favorite thing ever and is super-tedious, but it''s worth it to get crispy, evenly cooked fries. You''ve come this far. Don''t drop the ball (or all the fries into one crappy pile) now.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCF2B1BA86594842B8410FB4BE97F82D', X'033F3B9706256C4BAB64973DCE7AB7AE', X'033F3B9706256C4BAB64973DCE7AB7AE', 4, NULL, NULL, 2, NULL, 0, 'Roast for 10 minutes, then rotate pans and their position in the oven and roast about 10 minutes more, depending on the size of the fries, until crisp on the outsides and tender inside. Sprinkle with more salt if you like and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'126FD684E4F08F4CBC339426D3E28C68', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 200°F. If you have a convection oven, go for it. You''ll need two racks, so, as they tell schoolchildren in England, be sensible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEE8D8B5BC088642B8DE6E4002B81504', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 1, NULL, NULL, 2, NULL, 0, 'Cut the baguettes into 3/4-inch cubes and distribute between two rimmed sheet pans without crowding. Bake for about an hour, peeking in and maybe shaking the pans occasionally, until the cubes are dry all the way through. You can do this up to a few days in advance if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1CD9BF40D1D37479F00E2C4BC25C8A4', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 2, NULL, NULL, 2, NULL, 0, 'While the bread is drying, slice the white and light green parts of the leeks into thin half moons and wash thoroughly in several changes of water in a colander. (Leeks can be very sandy.) Cut the celery into a nice small dice — you''ll want it small so it cooks as quickly as the leeks. Dust off the mushrooms with a damp paper towel and slice, removing any tough stems.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95DEDBE89D687C4FAA574B304914FEC4', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil over medium-low heat in a wide, reasonably nonstick frying pan. Add the leeks, celery, and salt, and cook, stirring from time to time, until starting to soften, about five minutes. If you suspect a hint of browning, reduce the heat. Add the mushrooms and cook until they''ve released their moisture and it''s mostly simmered away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16416840CD2B8F4D832394E6C4E7541C', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 4, NULL, NULL, 2, NULL, 0, 'Add the chestnuts, soy sauce, wine, thyme, sage, raise heat to medium-high, and cook for three minutes more. Remove from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB7CB7A521B26A49B867DAB5D18248E1', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 5, NULL, NULL, 2, NULL, 0, 'Remove bread cubes from oven and raise heat to 375°F. Move a rack to the middle. Tip the bread cubes into a large mixing bowl. Add the contents of the frying pan and the stock, pepper, and parsley or chives. Toss gently to combine and allow the bread to soak up all the liquid. Taste and adjust seasoning if you like. (It''s vegan...shh...so no food-safety worries.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE8CD6CAF60A74B85F4D43323ECC40D', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 6, NULL, NULL, 2, NULL, 0, 'Spoon the stuffing into a baking dish. Shallow 9x13 is good if you want more crisp bits, lidded 3-quart casserole is good if you like a higher proportion of custardy deliciousness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'209FC362327F9B42B4BCA4F7BFFC3106', X'A48E38854328DA4D984DC0588B98CB99', X'A48E38854328DA4D984DC0588B98CB99', 7, NULL, NULL, 2, NULL, 0, 'Bake, covered (with foil or the casserole lid) for 35 minutes. Then remove cover and bake 15 minutes more. Serve hot, with Umami Girl''s Best Vegetarian Gravy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE152C471F7AAB4CA689D2895C8623CC', X'C78B618CEFF3F142A23E65C6A8598628', X'C78B618CEFF3F142A23E65C6A8598628', 0, NULL, NULL, 2, NULL, 0, 'Bring about two inches of unsalted water to a boil in a medium pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61CE7890AF055740BBE7BEFFA4E8D4A2', X'C78B618CEFF3F142A23E65C6A8598628', X'C78B618CEFF3F142A23E65C6A8598628', 1, NULL, NULL, 2, NULL, 0, 'Rinse the samphire well and add it to the boiling water for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'865AC535E1805E4DA8C54AFACCCAF500', X'C78B618CEFF3F142A23E65C6A8598628', X'C78B618CEFF3F142A23E65C6A8598628', 2, NULL, NULL, 2, NULL, 0, 'Drain into a colander. (If you want the samphire to retain a bright green color and slightly crisper texture, you can shock it in a bowl of ice water and drain again.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8FAB4FD5E975A4099053688BAF06AA3', X'C78B618CEFF3F142A23E65C6A8598628', X'C78B618CEFF3F142A23E65C6A8598628', 3, NULL, NULL, 2, NULL, 0, 'Toss with the butter and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F1CE8900771AB419B4D6089D9304B9D', X'B23FA00B8F490D42A64704571D8AF51A', X'B23FA00B8F490D42A64704571D8AF51A', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F with the top rack in the center. Lightly grease two popover or muffin pans.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA94DEE8801A9F4F875C23763BD62FFC', X'B23FA00B8F490D42A64704571D8AF51A', X'B23FA00B8F490D42A64704571D8AF51A', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the eggs, milk, and water. Slowly drizzle in the melted butter while whisking. Add the flour and salt and mix until well combined but still slightly lumpy. Divide the batter among the pans, filling each well only about 1/3 full.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE8E8C17BD588A4F97BEF48810006F4F', X'B23FA00B8F490D42A64704571D8AF51A', X'B23FA00B8F490D42A64704571D8AF51A', 2, NULL, NULL, 2, NULL, 0, 'Bake for 45 minutes without opening the oven. Then cut a small slit in the top of each popover to release the steam and bake for an additional 10 minutes. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF9657995662E042B455A63C71F7CCA5', X'C9B444801BF5774A8EED701595022263', X'C9B444801BF5774A8EED701595022263', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325°F with a rack in the middle. Melt half the butter in a 12-inch skillet over medium-high heat. Add the onions and celery along with a generous sprinkle of salt and pepper and cook, stirring occasionally, until softened but not browned, 10 to 15 minutes. If necessary, reduce the heat to prevent browning. Transfer all the contents of the skillet to a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A725A35E5106246A6C044B3CE738FB6', X'C9B444801BF5774A8EED701595022263', X'C9B444801BF5774A8EED701595022263', 1, NULL, NULL, 2, NULL, 0, 'In the same skillet, melt the remaining butter. Add the diced apples and raise the heat to high. Cook, stirring occasionally, until the apples are browned in spots but not mushy, 5 to 10 minutes. Add all the contents of the skillet to the mixing bowl with the onion mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AA8CCA674357443A0CC40AB854324F8', X'C9B444801BF5774A8EED701595022263', X'C9B444801BF5774A8EED701595022263', 2, NULL, NULL, 2, NULL, 0, 'Add the cornbread, bread cubes, nuts, parsley, thyme, sage, and a generous sprinkle of salt and pepper to the bowl. Mix gently but well with your hands. Pour the beaten eggs and the stock over the contents of the bowl, and mix again with your hands to moisten all the bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C291DBFFB28714489F82319E4B81370', X'C9B444801BF5774A8EED701595022263', X'C9B444801BF5774A8EED701595022263', 3, NULL, NULL, 2, NULL, 0, 'Spoon the stuffing into a 9x13x2-inch baking dish. Bake, uncovered, for 45 minutes. Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB606CB14DF3E54AB13DEB66FF487A63', X'A6A3CBD037389A4D909EBC9041DC02E1', X'A6A3CBD037389A4D909EBC9041DC02E1', 0, NULL, NULL, 2, NULL, 0, 'Wash kale and shake to remove excess water, leaving some water clinging to leaves. Strip leaves from stems and discard stems. Cut leaves crosswise into bite-sized pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'209B58014847A14AA54C86F63AE33D7A', X'A6A3CBD037389A4D909EBC9041DC02E1', X'A6A3CBD037389A4D909EBC9041DC02E1', 1, NULL, NULL, 2, NULL, 0, 'In large Dutch oven, heat 1/4 cup olive oil over medium-high heat until shimmering. Add garlic and cook, stirring frequently, until fragrant, about 1 minute. Add stock and vinegar and raise heat to high. Begin adding kale by the handful, pausing to let it wilt as necessary, until all the kale is in the pot. Cover and reduce heat to medium-low. Cook, stirring occasionally, until kale is very tender, about 25 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'285B9BCF5B1AF947A4B3C443C4E317B6', X'A6A3CBD037389A4D909EBC9041DC02E1', X'A6A3CBD037389A4D909EBC9041DC02E1', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in medium skillet, heat remaining 1/4 cup olive oil over medium heat until shimmering. Add onions along with pinch salt and cook, stirring occasionally, until lightly browned and reduced to half their original volume, about 20 minutes. Add chopped walnuts and cook 5 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EA511C1B9D4C740858F43E6867284A0', X'A6A3CBD037389A4D909EBC9041DC02E1', X'A6A3CBD037389A4D909EBC9041DC02E1', 3, NULL, NULL, 2, NULL, 0, 'Stir onions, walnuts and blue cheese into kale. Season to taste with salt and pepper. Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA19E93B17033448AEA43243E0DF4841', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center. Wash and thoroughly dry the cauliflower and cut into large florets.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'833DB140237C874CA1317CBAE5DFC231', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the two tablespoons olive oil, chili powder, 1/4 teaspoon of the salt and some freshly ground black pepper. Place the cauliflower florets on a baking sheet and toss well with the oil mixture. Roast for 25 minutes, turning once with a spatula mid-roasting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2E56BE60D42FE48B362536A7AC2512B', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', 2, NULL, NULL, 2, NULL, 0, 'While the cauliflower roasts, combine the extra-virgin olive oil, lime juice, cilantro, garlic, and remaining 1/4 teaspoon salt in a blender. Blend until smooth, stopping to scrape down sides as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA2515FD3695BA4FA9BD3B94A1AFBB97', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', X'D85EF09F9D45C34B88D8FF5AE4DF9EDF', 3, NULL, NULL, 2, NULL, 0, 'Serve roasted cauliflower as an appetizer/snack with dipping sauce, or drizzle it with sauce and serve as a side dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62D65767278A824FBBEDA08451E9242B', X'7F858EA568B5DF408DAC29CF79B9CA8E', X'7F858EA568B5DF408DAC29CF79B9CA8E', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, combine the potato slices and vinegar. Bring to a boil, then lower the heat and simmer until fork-tender, about 8 minutes. Let cool in liquid for 30 minutes. Then drain well and pat potatoes dry with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11DC10786FF1E84F9AC729137A24A21C', X'7F858EA568B5DF408DAC29CF79B9CA8E', X'7F858EA568B5DF408DAC29CF79B9CA8E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the broiler with a rack about 6 inches below the heat source. Dump the potato slices onto a sheet pan, sprinkle very generously with olive oil, salt and pepper, and toss to coat. Arrange the potato slices in a single layer. Broil until lightly browned on top, about 7 minutes. Then flip the slices and broil until the underside is lightly browned, about 5 minutes more. Serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5684AD62DBAC544B9F05BC798DDC9D8', X'98F4819677F02048BE67871131288167', X'98F4819677F02048BE67871131288167', 0, NULL, NULL, 2, NULL, 0, 'To char the eggplants, either set them directly on a lit gas burner or grill, or place in the oven a few inches below the heat source with the broiler on high. Allow the skin to turn completely black and flaky on the first side, then turn the eggplants with long-handled tongs to begin charring on the next side. Continue turning the eggplants every few minutes until all sides are charred.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F17B674900C8E5419D5FC2D95E449F12', X'98F4819677F02048BE67871131288167', X'98F4819677F02048BE67871131288167', 1, NULL, NULL, 2, NULL, 0, 'When eggplants are cool enough to handle, remove the stems and peel away and discard as much of the skin as possible. You want to get rid of most of it, but don''t worry if there are little bits left on here and there.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AF3BFD5C2F1CE41B2ADAF7BCB3B4C49', X'98F4819677F02048BE67871131288167', X'98F4819677F02048BE67871131288167', 2, NULL, NULL, 2, NULL, 0, 'Add the eggplant flesh, tahini, lemon juice, garlic, salt, cumin, and pepper to a food processor fitted with the blade. Pulse to blend thoroughly. Taste and adjust seasoning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2133F0497B75E4884B6DB9BCE27BD79', X'98F4819677F02048BE67871131288167', X'98F4819677F02048BE67871131288167', 3, NULL, NULL, 2, NULL, 0, 'Scrape baba ghanoush onto a serving plate, drizzle with olive oil and sprinkle with parsley. Serve with pita wedges or crackers.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A3FBC1F0EE8FF4DACE08E3092F670A1', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 0, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and cut lengthwise into quarters. Contrary to what you might normally do, don''t put the peeled potatoes in a bowl of water, because you want to keep all the starch. Just leave them on the counter. Don''t worry if they brown a bit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30FB6A9FBDD87149A392078063B8689A', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 1, NULL, NULL, 2, NULL, 0, 'Peel and quarter the onion.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B684BAF2CED57448115205025423B4B', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 2, NULL, NULL, 2, NULL, 0, 'Set up a food processor with the grating blade. Grate the potatoes and onions in two batches and add them to a strainer set over a large mixing bowl. Squeeze out as much moisture as you can with your hands, then let the mixture rest and drain for three minutes or so.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40311EC18B9BFE4EBCDF0EE874E2CC60', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 3, NULL, NULL, 2, NULL, 0, 'Pour off all the liquid from the bowl, but leave the layer of starch that''s accumulated at the bottom. Add grated potatoes and onions to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E428EA7F9BF68749BB052EAE2652FDC8', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 4, NULL, NULL, 2, NULL, 0, 'Mix in beaten eggs, matzo meal, melted butter, salt, pepper, and baking powder.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1085614CDEA63048BFBCAD15994DDCF6', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 5, NULL, NULL, 2, NULL, 0, 'Pour safflower oil into a large frying pan to a depth of about 1/8 inch. Heat oil over medium-high until it thins and becomes shimmery. Use a soup spoon to scoop little mounds of the potato mixture into the skillet (3 tablespoons at a time makes nice, diminutive latkes). Gently flatten the pancakes. Don''t crowd the skillet — leave an inch or so between latkes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B877711FA1F524EA4722E5D7E215A0B', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 6, NULL, NULL, 2, NULL, 0, 'Fry until the underside is crispy and golden brown, then flip and fry until the second side is crispy and golden brown, about 5 minutes total. Remove cooked latkes to paper towels to drain. Continue cooking, adding more oil as necessary and adjusting the heat to keep oil hot but not smoking, until you''ve used all of the potato mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91DA99511CD52D489383C20E7681533D', X'328301B8AF933543ACE955FF0D879BFA', X'328301B8AF933543ACE955FF0D879BFA', 7, NULL, NULL, 2, NULL, 0, 'Serve with applesauce, sour cream, and smoked salmon if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B1F1954C71D4F40A2E899EED7C96247', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65910B21B46DE0409AAF92A5DF72F33C', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 1, NULL, NULL, 2, NULL, 0, 'Slice baguette on the bias into 1/4-inch thick slices and brush with two tablespoons of the olive oil. Bake until lightly toasted, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70B40C6201379946BFCDA5D1B531233A', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, warm butter and remaining two tablespoons olive oil over medium-high heat in a large pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27AEFBE0C7C49E4BB8C61153B6DFA7A8', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 3, NULL, NULL, 2, NULL, 0, 'Add sage and thyme and cook for a minute, stirring frequently, until fragrant. Butter will brown.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8569BB464FB6014DAFA26CE601C14BFE', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 4, NULL, NULL, 2, NULL, 0, 'Add squash, salt and pepper and cook, stirring only occasionally, until squash is lightly browned in lots of spots, 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'199AB3F119D2F345A797E81C2858F5BA', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 5, NULL, NULL, 2, NULL, 0, 'Stir in walnuts and transfer pan to oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01D43C52C069C84392A13068EEE9B646', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 6, NULL, NULL, 2, NULL, 0, 'Roast until squash is very tender and will fall apart when stirred, about 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235DD20FD9EBB44DB9F697B2EF6207BB', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 7, NULL, NULL, 2, NULL, 0, 'Remove from oven and stir in sherry vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'792069424282EA458B2AC160D4F8BE56', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 8, NULL, NULL, 2, NULL, 0, 'Spread some ricotta onto each toast and top with some butternut squash mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94438F4ACD6AE148BDEC095B4C8C4C07', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 9, NULL, NULL, 2, NULL, 0, 'Press a few pomegranate seeds into squash mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59BE2F5D9F4D594DA23B3D9ACA1C21B3', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 10, NULL, NULL, 2, NULL, 0, 'Drizzle with balsamic glaze and sprinkle with flaky salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F313806032EF842950657E10975CB51', X'EDD885CC2C1AFD45920F2C93EC83B4B2', X'EDD885CC2C1AFD45920F2C93EC83B4B2', 11, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59582FE78885654E91440D35C5EB6C37', X'E075BA11C6777949B39C84B17AB93026', X'E075BA11C6777949B39C84B17AB93026', 0, NULL, NULL, 2, NULL, 0, 'In a 12-inch cast iron skillet, warm the oil over medium heat until shimmering.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129ADEA2914599428271AAE321CE40A5', X'E075BA11C6777949B39C84B17AB93026', X'E075BA11C6777949B39C84B17AB93026', 1, NULL, NULL, 2, NULL, 0, 'Add peppers and cook, tossing frequently with tongs, until soft and blistered in many spots, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01063B85FDCA5F40AB8C288414DFAA8A', X'E075BA11C6777949B39C84B17AB93026', X'E075BA11C6777949B39C84B17AB93026', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle with lots of flaky sea salt and serve (with spicy mayo if you like).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'060155C22E53A24DAC5B24DCDDF0B84E', X'CF7997308027AA4F8BA410040F6943B4', X'CF7997308027AA4F8BA410040F6943B4', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a high-speed blender, with the liquids on the bottom.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30804026AC86CE488D9279ED93EED8D8', X'CF7997308027AA4F8BA410040F6943B4', X'CF7997308027AA4F8BA410040F6943B4', 1, NULL, NULL, 2, NULL, 0, 'Blend until completely smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'863365D007C6A346BA4579FF1F5B958D', X'CF7997308027AA4F8BA410040F6943B4', X'CF7997308027AA4F8BA410040F6943B4', 2, NULL, NULL, 2, NULL, 0, 'If you like, you can dress it up for a party by swirling it with the back of a spoon and garnishing with some olive oil, flaky sea salt, paprika, and thinly sliced scapes (or herbs of your choice).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4CDE14F4B21E4428C35151A8C5D5FD6', X'1BD5BCA4815AAF41B16715D5D7D4AE60', X'1BD5BCA4815AAF41B16715D5D7D4AE60', 0, NULL, NULL, 2, NULL, 0, 'Bake fries according to package instructions (generally in a single layer on a baking sheet for about 30 minutes at 425°F).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4CF80EF0720784BAD3F56F0DA6C5BAF', X'1BD5BCA4815AAF41B16715D5D7D4AE60', X'1BD5BCA4815AAF41B16715D5D7D4AE60', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, spoon sauces and truffle salt into small bowls and arrange on a big platter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2608D4B140CCCA4D8D011D39501C9EE8', X'1BD5BCA4815AAF41B16715D5D7D4AE60', X'1BD5BCA4815AAF41B16715D5D7D4AE60', 2, NULL, NULL, 2, NULL, 0, 'When fries are ready, arrange them on the platter. You can see this in action in the video.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A82397AC6348D845B1C80CA84A120B6E', X'1BD5BCA4815AAF41B16715D5D7D4AE60', X'1BD5BCA4815AAF41B16715D5D7D4AE60', 3, NULL, NULL, 2, NULL, 0, 'Garnish platter with cilantro leaves and lime wedges, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85A52F2A6356A049BC7473291788A911', X'B4649C682C4B6649B0C76FB546BC2B7F', X'B4649C682C4B6649B0C76FB546BC2B7F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F with a rack in the center. Place a baking rack on a rimmed half-sheet pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57C80E0DB80E3A41BF9790E371363445', X'B4649C682C4B6649B0C76FB546BC2B7F', X'B4649C682C4B6649B0C76FB546BC2B7F', 1, NULL, NULL, 2, NULL, 0, 'Cut each baked potato in half lengthwise. If there''s a side that''s flatter (which is often true for russet potatoes), cut so that the flatter sides will be on the bottom and the cut potatoes will be wide, shallow, and stable.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6E0456CFA591F449016349C69029E9A', X'B4649C682C4B6649B0C76FB546BC2B7F', X'B4649C682C4B6649B0C76FB546BC2B7F', 2, NULL, NULL, 2, NULL, 0, 'From each potato half, scoop out some of the flesh, leaving about 1/4 inch of flesh attached to the skins. (Reserve extra flesh for another use, such as Colcannon Cheddar Skillet Cakes or simply mashing up with a little milk and butter.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D85637985DAAB54084E26A2E2D02B878', X'B4649C682C4B6649B0C76FB546BC2B7F', X'B4649C682C4B6649B0C76FB546BC2B7F', 3, NULL, NULL, 2, NULL, 0, 'Brush tops and bottoms of potato halves with melted butter and sprinkle with fine sea salt. Place potato halves skin-side up on baking rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E5DB8F0EF097145904B872782B5CECA', X'B4649C682C4B6649B0C76FB546BC2B7F', X'B4649C682C4B6649B0C76FB546BC2B7F', 4, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes. Skins will be crisp and lightly browned and flesh will be very tender on the inside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAA2397470E15248B9147BD887E1FE24', X'B4649C682C4B6649B0C76FB546BC2B7F', X'B4649C682C4B6649B0C76FB546BC2B7F', 5, NULL, NULL, 2, NULL, 0, 'Flip potato halves right-side up and spoon some caramelized onions into each. Top with Gruyere. Return potatoes to oven for 5 minutes or so, until cheese is melted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CEB8788BED4B44597F8FBAA1A4E50A1', X'B4649C682C4B6649B0C76FB546BC2B7F', X'B4649C682C4B6649B0C76FB546BC2B7F', 6, NULL, NULL, 2, NULL, 0, 'Top each potato skin with a dollop of sour cream if you like, along with some minced chives and a bit of flaky sea salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A5E90114B7FFD4385F27AF86BFFDCCA', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 0, NULL, NULL, 2, NULL, 0, 'Arrange one or two small bowls for the chocolate dip onto the serving platter. (You''ll pour the dip in later.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A0F1C4D8A9050468087F37DD7035DF5', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 1, NULL, NULL, 2, NULL, 0, 'Gently rinse and dry all fruit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D918AEFDE2BA18409D6288D1212A93CA', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 2, NULL, NULL, 2, NULL, 0, 'To prepare the apples, quarter them, remove the core from each quarter by slicing it out diagonally, and then slice each quarter into four or five slices. Granny Smith apples don''t brown quickly, so you can do this in advance without worrying about it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B922C8BA91924ABD447F10B5EB49FF', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 3, NULL, NULL, 2, NULL, 0, 'To prepare the grapes, snip the stems to form little bundles of 5 to 10 grapes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BF8350E02E06B4C8CAB9F4A62530A72', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 4, NULL, NULL, 2, NULL, 0, 'To prepare the star fruit, trim off and discard a thin slice from each end, then slice the star fruit crosswise into about 8 star-shaped slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B40749C0EA7CF046B08DED9BCF05A567', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 5, NULL, NULL, 2, NULL, 0, 'To prepare the dragon fruit, trim off and discard a thin slice from the stem end. Quarter the dragon fruit lengthwise and then cut each quarter crosswise into slices. The pink skin is not edible, but it''s so beautiful that I like to leave it on. It''s very easy for eaters to peel away from the edible flesh and gives them something to hold onto while eating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D670EF54743AD340ABEA300DF73B2081', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 6, NULL, NULL, 2, NULL, 0, 'To prepare the kiwis, peel off the furry skin with a vegetable peeler or paring knife. Slice kiwis crosswise into rounds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B3A762C452A00448B03CB3806D4AC58', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 7, NULL, NULL, 2, NULL, 0, 'To prepare the watermelon, cut a baby watermelon in half crosswise and reserve half for another use. Place the half watermelon cut side down and cut it into four quarters. Slice each quarter crosswise into triangular slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ACD311B851F564F916D62CF5D05960E', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 8, NULL, NULL, 2, NULL, 0, 'To prepare the strawberries, either leave whole if they''re smaller or remove leaves and cut into halves or quarters if they''re large.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F2600261CEA849AE316485D223B122', X'16B35861DF1C414BB1A71B86D45A5B50', X'16B35861DF1C414BB1A71B86D45A5B50', 9, NULL, NULL, 2, NULL, 0, 'To prepare cape gooseberries, remove papery hulls and wash well to remove as much of sticky exterior coating as is reasonable. You may not be able to remove it all, and that''s okay.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB020A9030011A429953087F95B22F63', X'A4E02AFB1B3F444B882A15226BA72F96', X'A4E02AFB1B3F444B882A15226BA72F96', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, stir together sake, mirin, soy sauce, and sugar. Warm over medium heat, stirring occasionally, until sugar is dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8896596FE6CF414AB8C53EDAD5096E3E', X'A4E02AFB1B3F444B882A15226BA72F96', X'A4E02AFB1B3F444B882A15226BA72F96', 1, NULL, NULL, 2, NULL, 0, 'Off the heat, add one heaping cup of ice to the pot to both dilute and chill the marinade. (Alternatively, you can place the ice in a 4-cup glass measuring cup or nonreactive bowl and pour the marinade overtop.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3CD84940172944EA91D32E155C92483', X'A4E02AFB1B3F444B882A15226BA72F96', X'A4E02AFB1B3F444B882A15226BA72F96', 2, NULL, NULL, 2, NULL, 0, 'Place peeled seven-minute eggs into marinade, cover tightly, and refrigerate for about 24 hours, turning once or twice if you think of it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'581948168129184E9A4512205B6E66EE', X'A4E02AFB1B3F444B882A15226BA72F96', X'A4E02AFB1B3F444B882A15226BA72F96', 3, NULL, NULL, 2, NULL, 0, 'For less intense flavor, you can remove the eggs at any point after about eight hours. For more intense flavor you can let the eggs marinate a bit longer. However, the salt and sugar in the marinade will make the eggs tough eventually, so don''t leave them for more than about 36 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACE46907C1F3E24D8833F9621AB3831E', X'CFE1E5F437768E4B9FA8CE14F03FD51D', X'CFE1E5F437768E4B9FA8CE14F03FD51D', 0, NULL, NULL, 2, NULL, 0, 'To cook the spinach, place it and two tablespoons water in a microwave-safe, lidded container and cook on high until just hot, about four minutes depending on your microwave.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8127839FCFBF0A4F94ACD122FCFB528B', X'CFE1E5F437768E4B9FA8CE14F03FD51D', X'CFE1E5F437768E4B9FA8CE14F03FD51D', 1, NULL, NULL, 2, NULL, 0, 'When cool enough to handle, place spinach in a fine-mesh strainer and press out all the liquid you can with a fork or spoon, or with your fingers.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EABB247BBF4D3438330C240323E03D4', X'CFE1E5F437768E4B9FA8CE14F03FD51D', X'CFE1E5F437768E4B9FA8CE14F03FD51D', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, stir vegetable recipe mix into mayonnaise and sour cream. Stir in water chestnuts and then spinach, breaking up any clumps. We like to use a fork to mix in the spinach.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75400F99D4A220428709F368F3688354', X'CFE1E5F437768E4B9FA8CE14F03FD51D', X'CFE1E5F437768E4B9FA8CE14F03FD51D', 3, NULL, NULL, 2, NULL, 0, 'Chill, covered, for at least two hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1564329EE97414CBE1578C8501F954D', X'CFE1E5F437768E4B9FA8CE14F03FD51D', X'CFE1E5F437768E4B9FA8CE14F03FD51D', 4, NULL, NULL, 2, NULL, 0, 'Slice the top off of a sourdough boule and cut or tear out most of the soft insides, leaving about a 1/4- to 1/2-inch layer of bread inside the crust. You can cube the bread you removed and serve it for dipping, or pulse it in a food processor and use as fresh breadcrumbs in other recipes. (Fresh breadcrumbs freeze well.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F73DB840F83DB4895C9E08AEC8853F4', X'CFE1E5F437768E4B9FA8CE14F03FD51D', X'CFE1E5F437768E4B9FA8CE14F03FD51D', 5, NULL, NULL, 2, NULL, 0, 'Fill bread bowl with dip place, in the center of a serving platter, and surround with fresh veggies for dipping.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BCE5651D8B79F4B837F15F4D74EE776', X'E845F796BAB031459B69E3D28FDEC324', X'E845F796BAB031459B69E3D28FDEC324', 0, NULL, NULL, 2, NULL, 0, 'Prepare one or two batches of classic deviled eggs, depending on the size of your party and what else you''re serving. Figure on one to two whole eggs per person depending on circumstances. Set them out on a serving tray near the beginning of the buffet line.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB42DB21C87D5B44A2A5915BAF25FA0C', X'E845F796BAB031459B69E3D28FDEC324', X'E845F796BAB031459B69E3D28FDEC324', 1, NULL, NULL, 2, NULL, 0, 'Set out toppings in small bowls. Be thoughtful about grouping ingredients together so as not to give your guests analysis paralysis, and also about setting out serving utensils where needed. Sauces like sriracha can stay right in the bottle, as long as it''s been reasonably well maintained.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAD0340E8ED74344A42BDCF9DE3E0BEE', X'E845F796BAB031459B69E3D28FDEC324', X'E845F796BAB031459B69E3D28FDEC324', 2, NULL, NULL, 2, NULL, 0, 'Place a stack of small plates and a stack of napkins at the start of the buffet line. Depending on your toppings, you may want to include forks as well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFD40FE7429C9B47B4B955F72A77B5E7', X'EF9777E22472F744901FA92600367D1F', X'EF9777E22472F744901FA92600367D1F', 0, NULL, NULL, 2, NULL, 0, 'Slice each egg in half lengthwise and carefully remove yolks to a medium bowl.');
