INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D81EE6F98A634E41A8D36F3050AE3E08', X'EF9777E22472F744901FA92600367D1F', X'EF9777E22472F744901FA92600367D1F', 2, NULL, NULL, 2, NULL, 0, 'Chop any large pieces of vegetables in the giardiniera into bite-sized pieces. Then take about 1/4 of the giardiniera and mince it finely.');
