INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'475B3CD80C533546B2545E85F5DCCD25', X'8935F65E07BD0E479D17523B078945FB', X'8935F65E07BD0E479D17523B078945FB', 0, NULL, NULL, 2, NULL, 0, 'Slice each egg in half lengthwise and carefully remove yolks to a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B12A9E48A66D848913A3E36382528E6', X'8935F65E07BD0E479D17523B078945FB', X'8935F65E07BD0E479D17523B078945FB', 1, NULL, NULL, 2, NULL, 0, 'Mash yolks well with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE0A6E2A1CFCBE429CF0A02EDDEB4BC2', X'8935F65E07BD0E479D17523B078945FB', X'8935F65E07BD0E479D17523B078945FB', 2, NULL, NULL, 2, NULL, 0, 'Add mayonnaise, crème fraîche, mustard, salt, and pepper and continue mashing and blending with the fork until yolk mixture is creamy. Stir in shallot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5C57AA9301449469840EE50265CC3E2', X'8935F65E07BD0E479D17523B078945FB', X'8935F65E07BD0E479D17523B078945FB', 3, NULL, NULL, 2, NULL, 0, 'Using a piping bag fitted with a plain tip, a resealable plastic bag with one of the bottom corners snipped off, or a spoon, pipe or spoon the yolk mixture back into the egg halves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37D20262FE70A84EB9E5FCF5B93A1911', X'8935F65E07BD0E479D17523B078945FB', X'8935F65E07BD0E479D17523B078945FB', 4, NULL, NULL, 2, NULL, 0, 'In a small frying pan, melt the butter over medium heat. Add panko and cook, stirring frequently and watching carefully, until lightly browned. Let cool for a few minutes before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30E2DE327995544AAAF0EF08CCE81FB1', X'8935F65E07BD0E479D17523B078945FB', X'8935F65E07BD0E479D17523B078945FB', 5, NULL, NULL, 2, NULL, 0, 'Shortly before serving, garnish each deviled egg with a small piece of smoked salmon, a few capers, a sprinkle of chives, and a sprinkle of toasted panko.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15A5B21D01940B40B6514B532641413E', X'68A69AA7F7A4C24787B1F4535E7D853F', X'68A69AA7F7A4C24787B1F4535E7D853F', 0, NULL, NULL, 2, NULL, 0, 'Cook bacon according to package instructions until crisp. (We love to make bacon in the oven.) Finely crumble or chop two slices. Cut each of the remaining 8 slices into 3 equal pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C152CAF0BC6690419F7C1DA13337AC24', X'68A69AA7F7A4C24787B1F4535E7D853F', X'68A69AA7F7A4C24787B1F4535E7D853F', 1, NULL, NULL, 2, NULL, 0, 'Slice each egg in half lengthwise and carefully remove yolks to a medium bowl. Mash yolks well with a fork. Add mayonnaise, mustard, salt, and pepper and continue mashing and blending with the fork until yolk mixture is creamy. Stir in shallot, minced sun-dried tomato and crumbled bacon.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6816BF548E87D468E1299335D01211A', X'68A69AA7F7A4C24787B1F4535E7D853F', X'68A69AA7F7A4C24787B1F4535E7D853F', 2, NULL, NULL, 2, NULL, 0, 'Using a piping bag fitted with a plain tip or a star tip, a resealable plastic bag with one of the bottom corners snipped off, or a spoon, pipe or spoon the yolk mixture back into the egg halves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB54B7EE9CE1D4483EDD95AB0193A41', X'68A69AA7F7A4C24787B1F4535E7D853F', X'68A69AA7F7A4C24787B1F4535E7D853F', 3, NULL, NULL, 2, NULL, 0, 'To garnish each piece, arrange one cut piece of bacon, one small piece of sun-dried tomato and one parsley leaf in the yolk mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73A79FF4C47F234BBE1046E9B4DC8FEA', X'68A69AA7F7A4C24787B1F4535E7D853F', X'68A69AA7F7A4C24787B1F4535E7D853F', 4, NULL, NULL, 2, NULL, 0, 'Deviled eggs can be made up to a day or two in advance of serving and stored, tightly covered, in the fridge, but hold off on garnishing until right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D90D18025DD3C4EAE58493B38905CAA', X'14FFD70DCA789744AEE6AFBD15FEE247', X'14FFD70DCA789744AEE6AFBD15FEE247', 0, NULL, NULL, 2, NULL, 0, 'Slice each egg in half lengthwise and carefully remove yolks to a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'250D69278C25224DB5422C378DD0D25D', X'14FFD70DCA789744AEE6AFBD15FEE247', X'14FFD70DCA789744AEE6AFBD15FEE247', 1, NULL, NULL, 2, NULL, 0, 'Mash yolks well with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79380C0DA07C444AAB2A3174DD31FC90', X'14FFD70DCA789744AEE6AFBD15FEE247', X'14FFD70DCA789744AEE6AFBD15FEE247', 2, NULL, NULL, 2, NULL, 0, 'Add mayonnaise, mustard, salt, and pepper and continue mashing and blending with the fork until yolk mixture is creamy. Stir in shallot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32EA96001A9E3741AB713CF2F52D02AA', X'14FFD70DCA789744AEE6AFBD15FEE247', X'14FFD70DCA789744AEE6AFBD15FEE247', 3, NULL, NULL, 2, NULL, 0, 'Using a piping bag fitted with a plain tip, a resealable plastic bag with one of the bottom corners snipped off, or a spoon, pipe or spoon the yolk mixture back into the egg halves. Sprinkle with paprika and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4184025AB0782B49AE28169C29C3BBF4', X'C481FAB288DE7F4DB6781EC7A1176CB6', X'C481FAB288DE7F4DB6781EC7A1176CB6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F with a rack in the center. Place a baking rack on a rimmed half-sheet pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73730B08693F33489C40094E90D57AA5', X'C481FAB288DE7F4DB6781EC7A1176CB6', X'C481FAB288DE7F4DB6781EC7A1176CB6', 1, NULL, NULL, 2, NULL, 0, 'Cut each baked potato in half lengthwise. If there''s a side that''s flatter (which is often true for russet potatoes), cut so that the flatter sides will be on the bottom and the cut potatoes will be wide, shallow, and stable.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'881CD3F8D4A1AE43A2A801031ECE5A13', X'C481FAB288DE7F4DB6781EC7A1176CB6', X'C481FAB288DE7F4DB6781EC7A1176CB6', 2, NULL, NULL, 2, NULL, 0, 'From each potato half, scoop out some of the flesh, leaving about 1/4 inch of flesh attached to the skins. (Reserve extra flesh for another use, such as Colcannon Cheddar Skillet Cakes or simply mashing up with a little milk and butter.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67FD5C812E68E44A9B5966C92858AFB0', X'C481FAB288DE7F4DB6781EC7A1176CB6', X'C481FAB288DE7F4DB6781EC7A1176CB6', 3, NULL, NULL, 2, NULL, 0, 'Brush tops and bottoms of potato halves with melted butter and sprinkle with fine sea salt. Place potato halves skin-side up on baking rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA1D137620832D42BD1ACCF353F6E8F3', X'C481FAB288DE7F4DB6781EC7A1176CB6', X'C481FAB288DE7F4DB6781EC7A1176CB6', 4, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes. Skins will be crisp and lightly browned and flesh will be very tender on the inside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88A1F8FBCC792D40A87FD367B0EA1AB8', X'C481FAB288DE7F4DB6781EC7A1176CB6', X'C481FAB288DE7F4DB6781EC7A1176CB6', 5, NULL, NULL, 2, NULL, 0, 'Flip potato halves right-side up and sprinkle some minced shallot into each. Top with cheddar. Return potatoes to oven for 5 minutes or so, until cheese is melted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E61584CAF375024483E5B6C1DBADE30C', X'C481FAB288DE7F4DB6781EC7A1176CB6', X'C481FAB288DE7F4DB6781EC7A1176CB6', 6, NULL, NULL, 2, NULL, 0, 'Top each potato skin with a dollop of sour cream, about a tablespoon of crumbled bacon OR shiitake bacon bits, some minced chives, a shot of hot sauce, and a bit of flaky sea salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'415292108B3F3E4AB1A7D42DEF82CD70', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Line a rimmed baking sheet with parchment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32D3790208BD8F469AF68940FD9D1E7B', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', 1, NULL, NULL, 2, NULL, 0, 'Wearing latex gloves, slice each jalapeño in half lengthwise. Scoop out and discard seeds. The heat of jalapeños comes from the capsaicin in the white inner membranes. We usually remove all or most of this, too, so kids can enjoy this dish. If you want more heat, leave some or all of the white ribs intact.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91A0027D07EE70458195BD7A327E8E2E', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, stir together the cream cheese, shallot, and 1/2 teaspoon of the salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E8892BF8AEC2E47BEF3458F8AD6ECE8', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', 3, NULL, NULL, 2, NULL, 0, 'Spoon some of the mixture into each jalapeño half and smooth the top.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C11FF979233904599BC5EC3FFCA24E8', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', 4, NULL, NULL, 2, NULL, 0, 'Place stuffed jalapeños on lined baking sheet and sprinkle with grated cheddar. Place in oven for about 15 minutes, until peppers have softened and cheese is soft and bubbly. Then switch the oven to broil to brown the tops a bit. This will only take a minute or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A36AF5960B39EB48B85CB8789721D59A', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', 5, NULL, NULL, 2, NULL, 0, 'While the poppers bake, melt the butter in a small frying pan over medium heat. Add the panko and remaining 1/4 teaspoon salt and cook, stirring frequently and watching carefully, until lightly browned. (Panko tends to hang out at the pale stage for minutes up on minutes and then burn in a flash, so pay attention.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAA5C3518FCA0A43AD748D0B941BF561', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', X'C43A2A7C4F4D9D49BF61E86D500DA5DE', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle a little of the panko on top of each popper and serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C061B9EFE085045B37D3EF7DB379731', X'AF96C88865E0B04EA12D9993D79C9F3B', X'AF96C88865E0B04EA12D9993D79C9F3B', 0, NULL, NULL, 2, NULL, 0, 'Spread a tablespoon or two of bean dip onto each toast.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'085A233504D12B4A9979EBA25767C094', X'AF96C88865E0B04EA12D9993D79C9F3B', X'AF96C88865E0B04EA12D9993D79C9F3B', 1, NULL, NULL, 2, NULL, 0, 'Slice tomatoes 1/4-inch thick and cut each slice in half. Line up the flat side of the tomato with one of the edges of the toast (if you''re into that sort of thing).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD234E26C920F408498A03A81366059', X'AF96C88865E0B04EA12D9993D79C9F3B', X'AF96C88865E0B04EA12D9993D79C9F3B', 2, NULL, NULL, 2, NULL, 0, 'Garnish each piece with a bit of sun-dried tomato, herbs and pine nuts and sprinkle with flaky salt. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E51134A8909E0408A1CD0066ACD7DD2', X'F75BA5464083B541B5F603F2486B44D9', X'F75BA5464083B541B5F603F2486B44D9', 0, NULL, NULL, 2, NULL, 0, 'Into the bowl of a food processor fitted with the blade, add the beans, garlic, shallot, lemon juice, olive oil, salt, pepper, and half the sun dried tomato pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28F5EB873B1C444A9179E029289EB0C7', X'F75BA5464083B541B5F603F2486B44D9', X'F75BA5464083B541B5F603F2486B44D9', 1, NULL, NULL, 2, NULL, 0, 'Process dip until perfectly smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66C49795936A1F469ACFCD3FE0C57FCB', X'F75BA5464083B541B5F603F2486B44D9', X'F75BA5464083B541B5F603F2486B44D9', 2, NULL, NULL, 2, NULL, 0, 'With a spoon or spatula, stir in half the pesto until streaked throughout but not well mixed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FFC5FB392C44F45AB809C4E350883C5', X'F75BA5464083B541B5F603F2486B44D9', X'F75BA5464083B541B5F603F2486B44D9', 3, NULL, NULL, 2, NULL, 0, 'Scrape int');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F23A7A15903EC041963B3AC1A0D1C858', X'F75BA5464083B541B5F603F2486B44D9', X'F75BA5464083B541B5F603F2486B44D9', 4, NULL, NULL, 2, NULL, 0, 'o a wide, shallow serving bowl and garnish with remaining pesto, sun dried tomato, and pine nuts. Drizzle with olive oil and sprinkle with flaky sea salt if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC0E593A6C17244CAED6F7D38C63CF34', X'F75BA5464083B541B5F603F2486B44D9', X'F75BA5464083B541B5F603F2486B44D9', 5, NULL, NULL, 2, NULL, 0, 'Serve with raw veggies for dipping.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'730724828BFF684998475ACD5FFEB384', X'02F7B1628DED7148BCDBBC218F868AE9', X'02F7B1628DED7148BCDBBC218F868AE9', 0, NULL, NULL, 2, NULL, 0, 'Peel the eggs and slice in half lengthwise. Use a spoon to carefully scoop the yolks into a medium mixing bowl, and place whites on a serving platter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5A4779E76D5474FABB94921E6B95155', X'02F7B1628DED7148BCDBBC218F868AE9', X'02F7B1628DED7148BCDBBC218F868AE9', 1, NULL, NULL, 2, NULL, 0, 'Add the mayonnaise, mustard, salt and pepper to the bowl and mix well with a fork until the yolks are incorporated into the mayo and the mixture is smooth and even.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39762407E6C9E94EB24CA6DE95351BB6', X'02F7B1628DED7148BCDBBC218F868AE9', X'02F7B1628DED7148BCDBBC218F868AE9', 2, NULL, NULL, 2, NULL, 0, 'Use a fork to flake the tuna into the mixing bowl. Add shallot, olives, sun-dried tomato and parsley and mix with a fork until nice and creamy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C623CFEB8345554CAE769C9EC44E8EE7', X'02F7B1628DED7148BCDBBC218F868AE9', X'02F7B1628DED7148BCDBBC218F868AE9', 3, NULL, NULL, 2, NULL, 0, 'Using a piping bag fitted with a star tip, a resealable plastic bag with one of the bottom corners snipped off, or a spoon, pipe or spoon the yolk mixture back into the egg halves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2440BDBD528B9F43A7994EB822514E4A', X'7AC4876A11D79747BACF6F5CB4151D7A', X'7AC4876A11D79747BACF6F5CB4151D7A', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, stir together the flour and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'868EF7D232D1484BAD8A12AE23F38A56', X'7AC4876A11D79747BACF6F5CB4151D7A', X'7AC4876A11D79747BACF6F5CB4151D7A', 1, NULL, NULL, 2, NULL, 0, 'Pour in the boiling water a little at a time and mix with a spoon until incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9B38657D36864458F2D1537C08F31FF', X'7AC4876A11D79747BACF6F5CB4151D7A', X'7AC4876A11D79747BACF6F5CB4151D7A', 2, NULL, NULL, 2, NULL, 0, 'Pour in the cold water a little at a time, continuing to mix until you have a very wet, sticky dough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88F1E610CDF02D4C9FB8BBA636F1DE58', X'7AC4876A11D79747BACF6F5CB4151D7A', X'7AC4876A11D79747BACF6F5CB4151D7A', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle plenty of flour onto a work surface and your hands. Scrape dough from bowl onto work surface and knead, adding more flour as necessary to prevent sticking, until dough is smooth. (See video for visual cues here and throughout the recipe.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A6FAC9EC67B74CBDA46B3406FF7B03', X'7AC4876A11D79747BACF6F5CB4151D7A', X'7AC4876A11D79747BACF6F5CB4151D7A', 4, NULL, NULL, 2, NULL, 0, 'Place dough in a clean bowl, cover with a clean kitchen towel, and let rest while you make the filling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA779CD632CF5E41854E8B6BD1EBB6CB', X'EEA7E19DD778B04C95404E9687FA5688', X'EEA7E19DD778B04C95404E9687FA5688', 0, NULL, NULL, 2, NULL, 0, 'Peel and clean the shrimp, leaving tails on if you like. (Tails make a nice presentation and a useful handle, but you can serve the appetizer with cocktail forks or toothpicks instead.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42071217B04F65438E77FF17B903072A', X'EEA7E19DD778B04C95404E9687FA5688', X'EEA7E19DD778B04C95404E9687FA5688', 1, NULL, NULL, 2, NULL, 0, 'Cut each slice of prosciutto into two or three long strips. Wrap one strip around each shrimp. Place shrimp on a rimmed baking sheet lined with a piece of parchment paper that just fits.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A7F4939140DE74DAB46E5F753C5EAF0', X'EEA7E19DD778B04C95404E9687FA5688', X'EEA7E19DD778B04C95404E9687FA5688', 2, NULL, NULL, 2, NULL, 0, 'Melt butter in a small pot. Brush about half of the butter over the shrimp. Preheat broiler to high.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FC6F50ADC870A40949E73F873A4E647', X'EEA7E19DD778B04C95404E9687FA5688', X'EEA7E19DD778B04C95404E9687FA5688', 3, NULL, NULL, 2, NULL, 0, 'While broiler preheats, add garlic and sage to remaining butter. Cook on medium heat for a minute or two, until very fragrant.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B88AEB65F31FD246AAD20F25EF9CD2C6', X'EEA7E19DD778B04C95404E9687FA5688', X'EEA7E19DD778B04C95404E9687FA5688', 4, NULL, NULL, 2, NULL, 0, 'Broil shrimp for about 90 seconds, until shrimp are turning opaque and prosciutto begins to cook. Flip shrimp with tongs and broil about 60 seconds more, until just cooked through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C48899F950003418985CC389C1C41F3', X'EEA7E19DD778B04C95404E9687FA5688', X'EEA7E19DD778B04C95404E9687FA5688', 5, NULL, NULL, 2, NULL, 0, 'Transfer shrimp to a serving platter. Brush with garlic sage butter and sprinkle with black pepper. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55E84EFF15C6C744BA29B823CFE87E20', X'1417DE7BF119B741BDCA3D5243016B11', X'1417DE7BF119B741BDCA3D5243016B11', 0, NULL, NULL, 2, NULL, 0, 'Arrange arugula on a dinner plate and place burrata in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DD10E70100AC944B03AEE5A06B7A15F', X'1417DE7BF119B741BDCA3D5243016B11', X'1417DE7BF119B741BDCA3D5243016B11', 1, NULL, NULL, 2, NULL, 0, 'Arrange prosciutto, salami and tomatoes in whatever way makes you happy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B6BB18E536CFF47838BC506FDCF4863', X'1417DE7BF119B741BDCA3D5243016B11', X'1417DE7BF119B741BDCA3D5243016B11', 2, NULL, NULL, 2, NULL, 0, 'Drizzle it all with olive oil and balsamic glaze and sprinkle with salt. Cut into the burrata a bit so your guests won''t feel shy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F9A5E82BBA28C43B1191B03223B3AA6', X'1417DE7BF119B741BDCA3D5243016B11', X'1417DE7BF119B741BDCA3D5243016B11', 3, NULL, NULL, 2, NULL, 0, 'Serve with small plates and forks and some good bread if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E94503B0D173064AA3B53C45CDAC9F10', X'97D53117D246A2498BCAE702601A7BF2', X'97D53117D246A2498BCAE702601A7BF2', 0, NULL, NULL, 2, NULL, 0, 'Juice the limes into a medium mixing bowl. It might seem like there''s a lot of juice, but you won’t be sorry. Add the onion and stir to coat. Let sit while you prepare the avocados.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33CA6649DA90294489276B169FA14DF3', X'97D53117D246A2498BCAE702601A7BF2', X'97D53117D246A2498BCAE702601A7BF2', 1, NULL, NULL, 2, NULL, 0, 'Halve and pit each avocado. Dice flesh and scoop from skins into mixing bowl, tossing with lime juice as you go to prevent browning. Add salt and mash avocado to desired consistency. I like to leave a some pieces diced and mash some more thoroughly for a little textural interest. Add cilantro and jalapeño if using and stir to combine. Serve with tortilla chips and lots of good beer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE2E890206A2D4429AD02AC42E761DE3', X'99558D447E4C604E9BF095D6D9BC146E', X'99558D447E4C604E9BF095D6D9BC146E', 0, NULL, NULL, 2, NULL, 0, 'Spread a generous amount of butter onto each baguette slice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7B0024F9F3D7642987581DF3B6EAABE', X'99558D447E4C604E9BF095D6D9BC146E', X'99558D447E4C604E9BF095D6D9BC146E', 1, NULL, NULL, 2, NULL, 0, 'Top with a few radish slices and sprinkle with fleur de sel.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36ACA4D85B3F54AB934FBE4E6AE0FF6', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 0, NULL, NULL, 2, NULL, 0, 'Fill a large pot halfway with water. Add 1 tablespoon salt. Bring water to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'900F2E11B463574780E31AD997F59C79', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, fill a large bowl with ice water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9EEEAECB4BCB94987F50D692C769672', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 2, NULL, NULL, 2, NULL, 0, 'When water boils, add broccoli and cook for one to two minutes, until bright green and not raw. Remove with a strainer to ice water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C67AE8B4BB125A4DA0F11537D320D170', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 3, NULL, NULL, 2, NULL, 0, 'Repeat with asparagus.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'349FE462756B2A41A96F73C4E482582C', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 4, NULL, NULL, 2, NULL, 0, 'When vegetables are cool, blot dry and arrange on a large platter with endive and bread slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C09F178C768AFD4AB23D18DF38BB15C8', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 5, NULL, NULL, 2, NULL, 0, 'In a small pot, heat the olive oil and butter over medium heat until the butter starts to foam.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02E50146C6B98C4BAE717534B22460AE', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 6, NULL, NULL, 2, NULL, 0, 'Add garlic and anchovy and cook, mashing anchovy against bottom and sides of pot with a spoon, until most of the anchovy has dissolved into the sauce, about 3 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'362F1CC4209A2247AEE16F956A10FB5B', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat, season to taste with pepper, and pour into a small bowl (or serve directly from pot).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A242CB19D1B9C74F8E5C4908448443F4', X'540000092868AF4A83FF890B5A0C0A4C', X'540000092868AF4A83FF890B5A0C0A4C', 8, NULL, NULL, 2, NULL, 0, 'Serve warm, over a little Sterno burner or tea light if you like, with crudités and crusty bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F49BB54E8E7A1489A06C863F7B61589', X'59A1A1A2A901664EBC07C1E52AD0ACB3', X'59A1A1A2A901664EBC07C1E52AD0ACB3', 0, NULL, NULL, 2, NULL, 0, 'Bake tater tots according to package directions. In the last minute of baking, sprinkle on about 2/3 of the cheese so it melts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAE14D528EC64646A8562017E9DAC738', X'59A1A1A2A901664EBC07C1E52AD0ACB3', X'59A1A1A2A901664EBC07C1E52AD0ACB3', 1, NULL, NULL, 2, NULL, 0, 'Heap cheesy tots onto a serving plate. In a small bowl, mix together the sour cream, onion powder and salt. Spoon over tater tots. Top with tomatoes, olives, onion, cilantro, jalapeño and remaining cheese. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4F44526C414B049BA68E4458836766A', X'AD9117A55536C048B05909A5FD4FBAB5', X'AD9117A55536C048B05909A5FD4FBAB5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F562317FECC6A4D9421EE78D1083AAE', X'AD9117A55536C048B05909A5FD4FBAB5', X'AD9117A55536C048B05909A5FD4FBAB5', 1, NULL, NULL, 2, NULL, 0, 'Brush olive oil over the surface of the naan. In a small bowl, stir together the tomato paste and tomato sauce. Spread sauce over naan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AC5836FA77ADB4E8FAD93E78BF9ABB4', X'AD9117A55536C048B05909A5FD4FBAB5', X'AD9117A55536C048B05909A5FD4FBAB5', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle 2/3 of the pecorino over tomato sauce. Arrange mozzarella overtop.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5DD6D63D7F8F94F8A048E4E9B53F3DA', X'AD9117A55536C048B05909A5FD4FBAB5', X'AD9117A55536C048B05909A5FD4FBAB5', 3, NULL, NULL, 2, NULL, 0, 'Place pizzas directly on oven rack and bake for 10 minutes or until cheese is melted and bubbly. Remove from oven and sprinkle with remaining pecorino, some pepper and basil if desired. Serve immediately, cut into slices of whatever size and shape you like you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36D04599E6D5FB418783A098A4331057', X'1229F681698D8B4D900C1BC4EDC1BDF5', X'1229F681698D8B4D900C1BC4EDC1BDF5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A7637754FAE648BB8892313EE3A06B', X'1229F681698D8B4D900C1BC4EDC1BDF5', X'1229F681698D8B4D900C1BC4EDC1BDF5', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, stir together the cream cheese, mayonnaise, cheddar, 1/2 cup of the parmigiano, the jalapeños and the olives until well mixed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7C7D6AA9F1D55478C04284C96ECD864', X'1229F681698D8B4D900C1BC4EDC1BDF5', X'1229F681698D8B4D900C1BC4EDC1BDF5', 2, NULL, NULL, 2, NULL, 0, 'Transfer to a baking dish. In a small bowl, stir together the remaining 1/4 cup parmgiano reggiano and the panko. Sprinkle over the top of the dip.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC18D2AD8B5EF408B94D776EA73ED92', X'1229F681698D8B4D900C1BC4EDC1BDF5', X'1229F681698D8B4D900C1BC4EDC1BDF5', 3, NULL, NULL, 2, NULL, 0, 'Bake until hot and bubbly, about 20-25 minutes. Serve with tortilla chips or crackers.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F748A558BD47A64799434BDB06624D18', X'79ED0A0B6D2B07429892D0488D8C980A', X'79ED0A0B6D2B07429892D0488D8C980A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with two racks close to the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1FF0D255D67CF4494FD0C416AF48C6A', X'79ED0A0B6D2B07429892D0488D8C980A', X'79ED0A0B6D2B07429892D0488D8C980A', 1, NULL, NULL, 2, NULL, 0, 'On a piece of parchment, roll out the puff pastry sheet(s). If you''re using one 14-ounce sheet, roll it out into a 16-inch square. If you''re using two 8 1/2-ounce sheets, roll each out into a 12-inch by 14-inch rectangle.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8768C517D2EF3449D1F596A35E167B3', X'79ED0A0B6D2B07429892D0488D8C980A', X'79ED0A0B6D2B07429892D0488D8C980A', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the salt, cayenne, nutmeg and black pepper. Sprinkle over puff pastry. Next, sprinkle the shredded cheese over the puff pastry. It''s best if you vaguely aim for even distribution but inevitably don''t achieve it, which will make each bite a little different in a good way. Press down lightly with your hands to stick the toppings to the pastry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83163B07F8C2414C8871DAC50FB201DB', X'79ED0A0B6D2B07429892D0488D8C980A', X'79ED0A0B6D2B07429892D0488D8C980A', 3, NULL, NULL, 2, NULL, 0, 'Fold each sheet in half (if using the two puff pastry rectangles, fold over the longer side to make 12-inch by 7-inch rectangles), then roll out again until the short side reaches 12 inches. Slice each sheet into 1-inch by 12-inch strips.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'216CD27D2C24E049ABC41235A17DC668', X'79ED0A0B6D2B07429892D0488D8C980A', X'79ED0A0B6D2B07429892D0488D8C980A', 4, NULL, NULL, 2, NULL, 0, 'Line two baking sheets with parchment. Twist the two ends of each strip in opposite directions a few times and place twists on baking sheets with a little space in between. Brush with cream or egg wash.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FE9C9178AE1F64795C5D52475C02CF5', X'79ED0A0B6D2B07429892D0488D8C980A', X'79ED0A0B6D2B07429892D0488D8C980A', 5, NULL, NULL, 2, NULL, 0, 'Bake for about 15 minutes, until puffed and light brown. Rotate the pans and swap their oven positions halfway through. (Alternatively you can bake the pans one at a time in the center of the oven.) Cool slightly or completely, whichever you prefer, before serving. They''re best on the day you make them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B60C77F9C57DA24BB7DA84C150174F81', X'E33C5EE000DF67478620F20B1CE0DC10', X'E33C5EE000DF67478620F20B1CE0DC10', 0, NULL, NULL, 2, NULL, 0, 'Mix together the ketchup, horseradish, tabasco, gin, celery salt, salt and lemon juice. Chill until ready to serve. Stir in parsley right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07326119BE0DD54A8ADA65F8269C1911', X'E33C5EE000DF67478620F20B1CE0DC10', X'E33C5EE000DF67478620F20B1CE0DC10', 1, NULL, NULL, 2, NULL, 0, 'Peel and devein shrimp, leaving tails attached. Place peels into a large pot. Add water, 1 tablespoon of the salt, wine or vermouth, peppercorns, coriander seeds, bay leaves, onion, tarragon and lemon. Bring to a boil, then reduce heat and simmer uncovered for 20 minutes. Remove from heat and let sit 30 minutes more, then strain broth through a fine sieve into a fresh pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E15709EB9867104B855DFDDEB419064B', X'E33C5EE000DF67478620F20B1CE0DC10', X'E33C5EE000DF67478620F20B1CE0DC10', 2, NULL, NULL, 2, NULL, 0, 'Bring stock to a boil, then remove from heat. Add shrimp and cover. Leave for 3 minutes (still off the heat -- the shrimp `poach` in the residual heat and remain amazingly tender).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A05AC8365C2FE842A9D0D9D84699797C', X'E33C5EE000DF67478620F20B1CE0DC10', X'E33C5EE000DF67478620F20B1CE0DC10', 3, NULL, NULL, 2, NULL, 0, 'Fill a large bowl with ice water and the remaining tablespoon salt. Add shrimp and leave until just opaque, 3 minutes or a shade more if the shrimp are very big. Chill in fridge (I usually wrap in paper towels and put in a gallon zip top bag) until ready to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11F2D7A3D400D14CBD180168B8CE1EBE', X'B48037AE6FD2784EB0E3DF2361E7BA0D', X'B48037AE6FD2784EB0E3DF2361E7BA0D', 0, NULL, NULL, 2, NULL, 0, 'Defrost the puff pastry in the fridge overnight or on the counter for a couple of hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC5543B812F7DA4798CF21A8C00744F6', X'B48037AE6FD2784EB0E3DF2361E7BA0D', X'B48037AE6FD2784EB0E3DF2361E7BA0D', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Unfold the puff pastry and roll it out gently with a rolling pin until it grows by 2-3 inches horizontally and vertically. Cut it into quarters, and then cut each quarter into six equal rectangles. Tuck each rectangle in to a mini muffin cup and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40326A3E7387A242B6626894E0FAE17E', X'B48037AE6FD2784EB0E3DF2361E7BA0D', X'B48037AE6FD2784EB0E3DF2361E7BA0D', 2, NULL, NULL, 2, NULL, 0, 'Trim the root end and the dark green leaves from the leeks and discard. Slice the remaining white and light green parts in half lengthwise and then cut crosswise into thin half-moons. Place in a colander and rinse thoroughly, then shake off any excess water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E860BDF1382BA34A8FBA2E6A2835EEB4', X'B48037AE6FD2784EB0E3DF2361E7BA0D', X'B48037AE6FD2784EB0E3DF2361E7BA0D', 3, NULL, NULL, 2, NULL, 0, 'Melt the butter in a frying pan over medium heat. Add leeks, a generous pinch of salt and some freshly ground black pepper and cook, stirring from time to time to prevent browning, until tender and reduced in volume by half, 5-10 minutes. Stir in cream, nutmeg and thyme and continue to cook until cream is thick enough to coat leeks without leaving liquid in the pan, about 5 minutes more. Remove from heat, crumble feta overtop and stir to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D63972FD952E90409C4FF7E62DECEAAE', X'B48037AE6FD2784EB0E3DF2361E7BA0D', X'B48037AE6FD2784EB0E3DF2361E7BA0D', 4, NULL, NULL, 2, NULL, 0, 'Beat egg in a small bowl with one teaspoon water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A21EA4D289582C4A8DA8ECBC6554A1B0', X'B48037AE6FD2784EB0E3DF2361E7BA0D', X'B48037AE6FD2784EB0E3DF2361E7BA0D', 5, NULL, NULL, 2, NULL, 0, 'Spoon a small amount of leek filling into each prepared muffin cup. Brush with a little bit of egg wash. Bake for about 20 minutes, until pastry is nicely browned. Serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12E0A011E0E1064E8AE3F9A595F25031', X'357A70097F63614788B4C4995484E5DD', X'357A70097F63614788B4C4995484E5DD', 0, NULL, NULL, 2, NULL, 0, 'Scrub the oysters well under running water to remove all sand and dirt. To shuck each oyster, hold it in a kitchen towel with the cupped side of the shell facing down so you won''t lose the beautiful liquor when you open it. Insert the tip of an oyster knife firmly into the hinge and twist until it pops open. Then run the knife across the inside of the top shell. Watch the video linked in the notes section below to see this done.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B90EBB1EEDB2D544A1B113831BB89AE7', X'357A70097F63614788B4C4995484E5DD', X'357A70097F63614788B4C4995484E5DD', 1, NULL, NULL, 2, NULL, 0, 'Run the oyster knife between the meat and the bottom shell to fully release the meat from the shell so it can be slurped up later. Place shucked oysters on a rimmed baking pan lined with a big sheet of gently crumpled aluminum foil to prevent tipping. Discard top shells.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE390ACA253F4B40B93F0A17FEF50BC4', X'357A70097F63614788B4C4995484E5DD', X'357A70097F63614788B4C4995484E5DD', 2, NULL, NULL, 2, NULL, 0, 'In a small pot, melt the butter with the shallot over medium heat and simmer for 5 minutes. Remove from heat and stir in the basil, parsley and lemon zest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEBC427814083B4EB6E915D53145B48C', X'357A70097F63614788B4C4995484E5DD', X'357A70097F63614788B4C4995484E5DD', 3, NULL, NULL, 2, NULL, 0, 'Line the bottom of a heavy 12-inch frying pan with about 1/4 inch of Kosher salt. (I really prefer a cast iron skillet for its superior heat retention). Preheat the pan over medium-high heat for at least 5 minutes, until the salt starts to make some little popping noises. Then quickly arrange the shucked oysters in the pan and top each with a spoonful of the herb butter. Cover the pan with a lid (carefully use foil if you don''t have a lid that fits) and cook for three minutes, until the oysters are just opaque. They''ll continue to cook a bit from the residual heat in the pan. Sprinkle with a little bit of chopped prosciutto and a squeeze of lemon juice. Serve warm straight from the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9A54147FEC10144844F6080FC8809C6', X'429C17928DC69342A5B83DD4A72E0C80', X'429C17928DC69342A5B83DD4A72E0C80', 0, NULL, NULL, 2, NULL, 0, 'On a countertop or table, arrange a few nice-looking boards and three small bowls. Arrange cheeses, meats, fruits, olives, bread or crackers and other ingredients as if they just rolled out of fridge into their awesome, awesome life. Share with as many friends as you can find, or just a few if that sounds like more fun. Serve with plenty of wine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'109AC485AD60F54898B977B7F0313A0B', X'BD8F9C96B7464A459174B3B3808F691E', X'BD8F9C96B7464A459174B3B3808F691E', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, melt the butter over medium heat. Stir in the flour and let bubble for about a minute. Pour in the milk, whisk until smooth, then bring to a boil. Reduce heat and simmer about 5 minutes until quite thick. Off the heat, stir in sour cream and then grated cheese. If cheese doesn''t fully melt, you can help it along over low heat, stirring constantly. Stop when it''s just shy of fully melted and let the residual heat do the rest so the sauce doesn''t break. Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47A4D8B6B0A503428599E02627892715', X'BD8F9C96B7464A459174B3B3808F691E', X'BD8F9C96B7464A459174B3B3808F691E', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry the cilantro and trim off the very bottoms of the stems. In a high-speed blender or a food processor, combine cilantro (leaves, stems and all), olive oil, lime juice and salt. Process until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD398573D327AE4181F0C1114B72846A', X'BD8F9C96B7464A459174B3B3808F691E', X'BD8F9C96B7464A459174B3B3808F691E', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, pour the lime juice and a sprinkle of salt over the cabbage and massage a bit with your hands to tenderize cabbage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B05771B22DA7424799662A106278AE15', X'BD8F9C96B7464A459174B3B3808F691E', X'BD8F9C96B7464A459174B3B3808F691E', 3, NULL, NULL, 2, NULL, 0, 'Arrange the tortilla chips on a large platter or rimmed baking sheet. Heat the refried beans in the microwave until nice and hot, then dollop over tortilla chips. Pour the cheese sauce overtop, then sprinkle on the cabbage, hatch chilis, tomatoes and red onion. Finish by drizzling with cilantro sauce. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CDCDEE27BE70A459BF509DC9AB4F7CF', X'4DA03E7E52E69248AFD4F7B4A666BDC7', X'4DA03E7E52E69248AFD4F7B4A666BDC7', 0, NULL, NULL, 2, NULL, 0, 'Cook and drain edamame according to package directions. (This is a great time to use the microwave.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E728CD940C7FC45B0402B9DF9AD6218', X'4DA03E7E52E69248AFD4F7B4A666BDC7', X'4DA03E7E52E69248AFD4F7B4A666BDC7', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the salt and garlic powder.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1B16F9E1648DD4CBAFCD42ED6B1E255', X'4DA03E7E52E69248AFD4F7B4A666BDC7', X'4DA03E7E52E69248AFD4F7B4A666BDC7', 2, NULL, NULL, 2, NULL, 0, 'While edamame are still very hot, sprinkle with garlic salt and chili flakes. Toss to distribute evenly. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE1063B0C5E21444A14BD476225BBB72', X'69C96DAA8EBD414FA6EF2FEA562EA959', X'69C96DAA8EBD414FA6EF2FEA562EA959', 0, NULL, NULL, 2, NULL, 0, 'Pop the popcorn kernels with an air popper into a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD2B61CAB228B346BFD551876667E624', X'69C96DAA8EBD414FA6EF2FEA562EA959', X'69C96DAA8EBD414FA6EF2FEA562EA959', 1, NULL, NULL, 2, NULL, 0, 'Drizzle with the olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEF5CCDBEEA35343AC58A5249479805E', X'69C96DAA8EBD414FA6EF2FEA562EA959', X'69C96DAA8EBD414FA6EF2FEA562EA959', 2, NULL, NULL, 2, NULL, 0, 'With a mortar and pestle or spice grinder, grind the nutritional yeast and salt together until you have a fine powder. Sprinkle over popcorn and toss to coat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'849E8F4C26563C4A9522AD2F912EE1B0', X'02E9479ABCDCA644A0B68AFEAD3E5E9F', X'02E9479ABCDCA644A0B68AFEAD3E5E9F', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in the bowl of a food processor fitted with the blade.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8942689878FF2F4891A50E499D69094C', X'02E9479ABCDCA644A0B68AFEAD3E5E9F', X'02E9479ABCDCA644A0B68AFEAD3E5E9F', 1, NULL, NULL, 2, NULL, 0, 'Process until smooth and creamy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA7228DEA7C5A448B324EAF025D7696D', X'02E9479ABCDCA644A0B68AFEAD3E5E9F', X'02E9479ABCDCA644A0B68AFEAD3E5E9F', 2, NULL, NULL, 2, NULL, 0, 'Serve with chips, with vegetables, in a sandwich, on a bagel, from a spoon, yadda yadda.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B50F219D38DB2D47BBE538998857D9E0', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D7BABC637C8A04A87E0ED4DFA59B143', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 1, NULL, NULL, 2, NULL, 0, 'Pour chickpeas into a colander, discarding liquid, and rinse thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C25308BC15BCDC40A97F2AAD199F6F75', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 2, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking sheet with paper towels, and pour the chickpeas onto the baking sheet. Cover with additional paper towels and dry thoroughly. Discard paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D624B3652DA63D4F8194C3170C9AF653', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 3, NULL, NULL, 2, NULL, 0, 'Spread chickpeas evenly over the surface of the baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E756666F1183D498245C19CB6BB0B11', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 4, NULL, NULL, 2, NULL, 0, 'Roast on center rack, stirring occasionally, until chickpeas are just the slightest bit soft in the center. Start testing at the 20 minute mark.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E20ECED91B9AF46801610E04FA4F731', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 5, NULL, NULL, 2, NULL, 0, 'Remove baking sheet from the oven and toss the chickpeas thoroughly with the oil and either the soy sauce, lime juice, and salt or the paprikas and garlic salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'586A0C918877FF4FA22755C9FD6DF568', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 6, NULL, NULL, 2, NULL, 0, 'Roast about 10 minutes more, just until chickpeas are crisp all the way through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC804E7A15F6FC4189B185A7A51382E0', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 7, NULL, NULL, 2, NULL, 0, 'If you’re making the soy and lime variety, toss with lime zest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FC34FC5DDCDCD47A9A95E1311D85D48', X'0384AB042C62FD43A82FF0902C0E6956', X'0384AB042C62FD43A82FF0902C0E6956', 8, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature. Keeps in an airtight container at room temperature for a few days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAEB76768E5E5E49BB1B8EF3C6999F12', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer fitted with a dough hook, combine the water and sugar and stir to dissolve sugar. Sprinkle yeast overtop and set aside for 10 minutes in a warm spot in the kitchen. Yeast will be foamy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C3F254ABDADC84B9D608017EC304AA0', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 1, NULL, NULL, 2, NULL, 0, 'Add one cup of flour to the bowl and stir with a spoon or on low with the dough hook until incorporated. Then add salt and four additional cups of flour. Stir with a spoon or the dough hook until combined, about one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A4FAB76DD58CA4C852AF1C2C979E989', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 2, NULL, NULL, 2, NULL, 0, 'If kneading by hand, turn the dough out onto a clean work surface (no need to flour it), and knead until it loses most of its stickiness and becomes quite elastic, about ten minutes. (To knead, hold the dough in place with one hand and stretch it out in front of you with the heel of your other hand as shown in the photos above, then roll it back up on itself and repeat.) If using a stand mixer, turn machine on medium-low and let the dough hook knead for about 90 seconds, until the dough pulls away from the sides of the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB48107C2D9D0441A449CA2E2D281622', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining 1/2 cup of flour and knead by hand for about two minutes more or in stand mixer for one minute more, until smooth. (If using a stand mixer, turn out the dough and knead it a few times by hand at the end.) Wash and dry the bowl, then pour about a Tablespoon of the oil into it. Place the dough back in the bowl and roll it to coat lightly with the oil on all sides. Place a clean kitchen towel over the top of the bowl and set aside in a warm spot in the kitchen to rise. Dough is ready when it has approximately doubled in volume, which will take an hour or a little more depending on the temperature of your kitchen. Brush two large baking sheets lightly with some of the olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'615C6B3B16A49941B737BFD959906E85', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 4, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a clean work surface and punch it down a few times to release any trapped air. Divide the dough into sixteen equal pieces. Place fifteen of them back in the bowl and cover with a damp paper towel.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D101AE4758862E418E43B5FABF9331E3', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 5, NULL, NULL, 2, NULL, 0, 'Starting with one of the small pieces of dough, roll with your hands into an approximately 18-inch `snake,` and then twist into a pretzel shape. Set pretzel on baking sheet, leaving two inches of space on all sides. Repeat with remaining dough balls. Set pretzels aside for 15 minutes for their final rise.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8A1560A24225E49B0C4E283DA8797CC', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 6, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°F with one rack 1/3 of the way up from the oven floor and another 1/3 of the way down from the top. If you have a convection oven, it''s great to use here -- just set it to 425° instead. Fill a large, heavy pot with two inches of water, add baking soda, and bring to a boil over high heat. Lower heat to maintain a slow simmer. Place a sheet of parchment or waxed paper on the countertop next to the stove.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E29F946BCD620468869F311B50E205A', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 7, NULL, NULL, 2, NULL, 0, 'In batches of four to six depending on the size of your pot, gently place pretzels into the simmering water and poach for one to two minutes, until they are quite puffy and floating at the top of the pot. Remove to the parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B08C784E096155469D64D17961C08C86', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 8, NULL, NULL, 2, NULL, 0, 'Brush a little more oil onto the baking sheets. Gently blot any moisture off each pretzel with a paper towel and place back on the baking sheets. Brush each pretzel with a little olive oil and then sprinkle with sesame seeds and a bit of coarse salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFE051E4BF67734697F35C90A04B6F9B', X'30D819C0D385F14CAF7E7C6B40F6AF51', X'30D819C0D385F14CAF7E7C6B40F6AF51', 9, NULL, NULL, 2, NULL, 0, 'Place one baking sheet on the lower rack and one on the upper rack of the oven. Bake pretzels for eight minutes, then reverse the positions of the baking sheets and bake about seven minutes more, until pretzels are beautifully golden brown. Serve warm or at room temperature, preferably the same day they''re made.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53E35BAEA12C13499909CF02F3C71E73', X'DC4F43DF5EA77D4E89754272FDE995FA', X'DC4F43DF5EA77D4E89754272FDE995FA', 0, NULL, NULL, 2, NULL, 0, 'Slice each egg in half lengthwise and carefully remove yolks to a medium bowl. Mash yolks well with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E40171FCA47358409ED0CCCE52469A97', X'DC4F43DF5EA77D4E89754272FDE995FA', X'DC4F43DF5EA77D4E89754272FDE995FA', 1, NULL, NULL, 2, NULL, 0, 'Add mayonnaise, vinegar, mustard, salt, black pepper and cayenne and continue mashing and blending with the fork until yolk mixture is creamy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F00F124606194EA93ACF74C27211C6', X'DC4F43DF5EA77D4E89754272FDE995FA', X'DC4F43DF5EA77D4E89754272FDE995FA', 2, NULL, NULL, 2, NULL, 0, 'Stir in shallot and herbs. Taste for seasoning — it should be assertively flavored, since egg whites have virtually no taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'184F1ACA96C0684FB8D65DF81E041AC3', X'DC4F43DF5EA77D4E89754272FDE995FA', X'DC4F43DF5EA77D4E89754272FDE995FA', 3, NULL, NULL, 2, NULL, 0, 'Using a piping bag fitted with a star tip, a resealable plastic bag with one of the bottom corners snipped off, or a spoon, pipe or spoon the yolk mixture back into the egg halves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39EEB3B858322A45907106107CA27421', X'DC4F43DF5EA77D4E89754272FDE995FA', X'DC4F43DF5EA77D4E89754272FDE995FA', 4, NULL, NULL, 2, NULL, 0, 'Garnish with additional herbs, if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34738CF430ED48428F85C22BE99229D7', X'3A0C87BE04730340A93FE75E6AE5904C', X'3A0C87BE04730340A93FE75E6AE5904C', 0, NULL, NULL, 2, NULL, 0, 'In a 10-inch skillet, heat the olive oil over medium heat until shimmering.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B20D11508205A347BB4D99C3B1E8011C', X'3A0C87BE04730340A93FE75E6AE5904C', X'3A0C87BE04730340A93FE75E6AE5904C', 1, NULL, NULL, 2, NULL, 0, 'Add the pepitas and toss to coat with the oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'216E422FBB4A9A4399DB7A30CFD90850', X'3A0C87BE04730340A93FE75E6AE5904C', X'3A0C87BE04730340A93FE75E6AE5904C', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle on the paprika, salt, garlic powder, and cayenne pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'499FB3AD4555824EBC7B4F013D78A9CD', X'3A0C87BE04730340A93FE75E6AE5904C', X'3A0C87BE04730340A93FE75E6AE5904C', 3, NULL, NULL, 2, NULL, 0, 'Cook, tossing frequently, until many of the pepitas have turned light brown and made a popping sound.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'893A0F38EE5CE94B8F38D4E8325C834C', X'3A0C87BE04730340A93FE75E6AE5904C', X'3A0C87BE04730340A93FE75E6AE5904C', 4, NULL, NULL, 2, NULL, 0, 'Pour into a small bowl and serve as a bar snack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06EFC7637B061144B56493904649F8C6', X'EC9619EB179F2A46B277EE58132AAF3E', X'EC9619EB179F2A46B277EE58132AAF3E', 0, NULL, NULL, 2, NULL, 0, 'Preheat the broiler with a rack about 6 inches below the heat source. Place the strawberries, 1 teaspoon of the honey, and the mint leaves in a medium bowl and stir gently to combine. Let sit at room temperature while you prepare the rest of the components. Note: if your strawberries are not luxuriously ripe, you may need a bit more honey, so adjust according to your taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30A5BE1DFE081244B073C2BD026E456D', X'EC9619EB179F2A46B277EE58132AAF3E', X'EC9619EB179F2A46B277EE58132AAF3E', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the goat cheese, remaining 2 teaspoons honey, lemon juice, and 1/4 teaspoon salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEB501EC85BDB747949F43D129B95434', X'EC9619EB179F2A46B277EE58132AAF3E', X'EC9619EB179F2A46B277EE58132AAF3E', 2, NULL, NULL, 2, NULL, 0, 'Brush the baguette slices lightly with the olive oil. Place under the broiler until the tops are golden brown at the edges, 1 to 2 minutes. Flip the slices and brown the other side as well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95F70945C8B7244E81AC2F6BC7AFF9EF', X'EC9619EB179F2A46B277EE58132AAF3E', X'EC9619EB179F2A46B277EE58132AAF3E', 3, NULL, NULL, 2, NULL, 0, 'To assemble the bruschetta, turn the baguette slices so the olive oil side faces up. Spread some of the goat cheese mixture on each slice, and top with a few slices of strawberry. Arrange the hors d''oeuvres on a platter and drizzle a bit more honey over each piece. Sprinkle lightly with sea salt and freshly ground black pepper. Serve at once.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7386759E3555A49B9064A3A5554A3F9', X'13C6F8BFCA6B5949A44D55F70FB0CB36', X'13C6F8BFCA6B5949A44D55F70FB0CB36', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a food processor fitted with the blade.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DF9C0F997BB6B428F5462333B0351D2', X'13C6F8BFCA6B5949A44D55F70FB0CB36', X'13C6F8BFCA6B5949A44D55F70FB0CB36', 1, NULL, NULL, 2, NULL, 0, 'Blend until smooth, stopping to scrape down the sides as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBA8DA38D495A34F81007B2984AA02D7', X'13C6F8BFCA6B5949A44D55F70FB0CB36', X'13C6F8BFCA6B5949A44D55F70FB0CB36', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate for a couple of hours to let the flavors commune, if possible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FC97EE0A33C124CB1C7BF9E21334008', X'13C6F8BFCA6B5949A44D55F70FB0CB36', X'13C6F8BFCA6B5949A44D55F70FB0CB36', 3, NULL, NULL, 2, NULL, 0, 'Serve as a spread for crackers or a filling for sandwiches.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A81D682551F3C14F8EE54F37FAC19BBA', X'C1E6B58CEF7F3D4D8A856245DB86C79F', X'C1E6B58CEF7F3D4D8A856245DB86C79F', 0, NULL, NULL, 2, NULL, 0, 'Place the goat cheese in a small, shallow serving dish (or cut it in half and place it in two dishes).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB3A22FA968FE94D8177BBB18731C015', X'C1E6B58CEF7F3D4D8A856245DB86C79F', X'C1E6B58CEF7F3D4D8A856245DB86C79F', 1, NULL, NULL, 2, NULL, 0, 'Pour the olive oil overtop, and then sprinkle the goat cheese with the thyme, red pepper flakes, salt and black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD4AF75345DA544C90D24A6DBEF36C6D', X'C1E6B58CEF7F3D4D8A856245DB86C79F', X'C1E6B58CEF7F3D4D8A856245DB86C79F', 2, NULL, NULL, 2, NULL, 0, 'Leave to rest, covered with tented plastic wrap (use toothpicks to tent) in the refrigerator for at least 1/2 hour or up to a couple of days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8177E394A3D4E74AABB6EBC1A171DDBE', X'C1E6B58CEF7F3D4D8A856245DB86C79F', X'C1E6B58CEF7F3D4D8A856245DB86C79F', 3, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for 1/2 hour before serving as a spread for crackers or toasted slices of baguette.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB19588BD310B246B7BEEDA75D7FB365', X'18A4CCE530745B4EBAD75F6860AF6297', X'18A4CCE530745B4EBAD75F6860AF6297', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E3AFBC9E4911D428DC5BB54A3B4060F', X'18A4CCE530745B4EBAD75F6860AF6297', X'18A4CCE530745B4EBAD75F6860AF6297', 1, NULL, NULL, 2, NULL, 0, 'Slice baguette on the bias into 1/4-inch thick slices and brush with the olive oil. Place onto a baking sheet. Bake until lightly toasted, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'272FBB46A21A8A4584E70B1CB5900C33', X'18A4CCE530745B4EBAD75F6860AF6297', X'18A4CCE530745B4EBAD75F6860AF6297', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, melt the butter over medium heat in a wide frying pan. Add the shallot and garlic and cook, stirring once in a while, until beginning to soften, about 1 minute. Add the mushrooms along with a couple of good pinches of salt and some freshly ground black pepper, and stir to coat with butter. Cook, stirring occasionally, 5 to 8 minutes more, until the mushrooms have given off their liquid, browned slightly, and reduced to about half their original volume.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F54B8A1079632499885833B598C91AB', X'18A4CCE530745B4EBAD75F6860AF6297', X'18A4CCE530745B4EBAD75F6860AF6297', 3, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and pour in the brandy from a measuring cup. (Don''t pour directly from the bottle despite what you may have seen in the movies.) Very carefully light a long match and touch the surface of the mushroom mixture to ignite the brandy. Or, if you''re cooking with gas, you can tip the pan away from you into the burner''s flame until the brandy ignites. I find this method maybe a little too entertaining. Stand back until the flame dies out.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376ECC5540F2724487C03278E8BE2981', X'18A4CCE530745B4EBAD75F6860AF6297', X'18A4CCE530745B4EBAD75F6860AF6297', 4, NULL, NULL, 2, NULL, 0, 'Return the pan to medium heat. Add the cream and stir to combine. Simmer until the cream has thickened slightly, a minute or two. Turn off the heat and stir in the Parmesan cheese and parsley. Taste for seasoning and add additional salt and pepper if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88B407AC0DA0C445977470BCA5B5E326', X'18A4CCE530745B4EBAD75F6860AF6297', X'18A4CCE530745B4EBAD75F6860AF6297', 5, NULL, NULL, 2, NULL, 0, 'Spoon mushrooms onto toasted baguette slices and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'019808151ABF2E4CA2C554DB9C01E1AF', X'9A0CFACBF04E084780D96404D7A7A0BE', X'9A0CFACBF04E084780D96404D7A7A0BE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center. Wash and thoroughly dry the cauliflower and cut into large florets.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD8A6770AAE6704AAC6BD38000685170', X'9A0CFACBF04E084780D96404D7A7A0BE', X'9A0CFACBF04E084780D96404D7A7A0BE', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the two tablespoons olive oil, chili powder, 1/4 teaspoon of the salt and some freshly ground black pepper. Place the cauliflower florets on a baking sheet and toss well with the oil mixture. Roast for 25 minutes, turning once with a spatula mid-roasting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C51FA2F596F4EE4EADDDB3F5C88D9E87', X'9A0CFACBF04E084780D96404D7A7A0BE', X'9A0CFACBF04E084780D96404D7A7A0BE', 2, NULL, NULL, 2, NULL, 0, 'While the cauliflower roasts, combine the extra-virgin olive oil, lime juice, cilantro, garlic, and remaining 1/4 teaspoon salt in a blender. Blend until smooth, stopping to scrape down sides as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76288C5ED6A67E4EA18270E1F64F7EE9', X'9A0CFACBF04E084780D96404D7A7A0BE', X'9A0CFACBF04E084780D96404D7A7A0BE', 3, NULL, NULL, 2, NULL, 0, 'Serve roasted cauliflower as an appetizer/snack with dipping sauce, or drizzle it with sauce and serve as a side dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF8877926A82D747BB40299518D0E3E4', X'CBC5B3561855C34C95FF64E46594C9C2', X'CBC5B3561855C34C95FF64E46594C9C2', 0, NULL, NULL, 2, NULL, 0, 'Place half of cheese, along with onion, garlic puree, chives, mustard and cayenne, in the bowl of a food processor fitted with the metal blade. Process until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C8BC3924DCA534C81B45B3A2AE82AA8', X'CBC5B3561855C34C95FF64E46594C9C2', X'CBC5B3561855C34C95FF64E46594C9C2', 1, NULL, NULL, 2, NULL, 0, 'With the motor running, add the sherry and olive oil. Process to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EFF231B3819F74EBC17A479B7576CFB', X'CBC5B3561855C34C95FF64E46594C9C2', X'CBC5B3561855C34C95FF64E46594C9C2', 2, NULL, NULL, 2, NULL, 0, 'Add the remaining cheese and process until very thick and smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EF330D6565B1B479B5B97D2D452C8FE', X'CBC5B3561855C34C95FF64E46594C9C2', X'CBC5B3561855C34C95FF64E46594C9C2', 3, NULL, NULL, 2, NULL, 0, 'Add additional sherry 1 tablespoon at a time to thin, if necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AF8661F5AE1FF498BE55338EF845D3F', X'CBC5B3561855C34C95FF64E46594C9C2', X'CBC5B3561855C34C95FF64E46594C9C2', 4, NULL, NULL, 2, NULL, 0, 'Pack into 8-ounce crocks. Refrigerate for up to one month. Bring to room temperature before serving with crackers or toasts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6429B41BBFF214A9FD1E4F6E678D69C', X'6278DC1531F099449D5425AF07D8B189', X'6278DC1531F099449D5425AF07D8B189', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, combine the potato slices and vinegar. Bring to a boil, then lower the heat and simmer until fork-tender, about 8 minutes. Let cool in liquid for 30 minutes. Then drain well and pat potatoes dry with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B1666D9A1F5914B8EFEDC31895BAF8C', X'6278DC1531F099449D5425AF07D8B189', X'6278DC1531F099449D5425AF07D8B189', 1, NULL, NULL, 2, NULL, 0, 'Preheat the broiler with a rack about 6 inches below the heat source. Dump the potato slices onto a sheet pan, sprinkle very generously with olive oil, salt and pepper, and toss to coat. Arrange the potato slices in a single layer. Broil until lightly browned on top, about 7 minutes. Then flip the slices and broil until the underside is lightly browned, about 5 minutes more. Serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2D77AEECF912F4AA39BD6211C943865', X'FB24067D6CE99E4EB9703006C11CC0B1', X'FB24067D6CE99E4EB9703006C11CC0B1', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425° F. Brush a mini-muffin pan with oil or melted butter. Crack the egg into a medium bowl and beat lightly. Peel the potatoes and cut with the julienne blade of a mandoline. (Hold the potato so as to cut crosswise, not lengthwise, otherwise the pieces will be too long.) Mix the cut potatoes with the egg as you go so they don''t oxidize. Mix in a good pinch of salt and some freshly ground pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E03D9555D88A1441B1A1D2C031958E68', X'FB24067D6CE99E4EB9703006C11CC0B1', X'FB24067D6CE99E4EB9703006C11CC0B1', 1, NULL, NULL, 2, NULL, 0, 'Distribute the potato mixture among 24 mini-muffin molds to make a thinnish layer on the bottom and up the sides of each mold. Be careful to leave behind or drain off any accumulated liquid in the bowl rather than adding excess liquid to the molds. Bake for 25 minutes or until lightly browned and slightly crisp.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0D9D590939D734C9AF860C0160926B7', X'FB24067D6CE99E4EB9703006C11CC0B1', X'FB24067D6CE99E4EB9703006C11CC0B1', 2, NULL, NULL, 2, NULL, 0, 'Cool on a rack for 15 minutes before removing nests from pan, loosening each by running a paring knife carefully around the sides.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DFA2118DA5B99468530ECDFC25DF04D', X'FB24067D6CE99E4EB9703006C11CC0B1', X'FB24067D6CE99E4EB9703006C11CC0B1', 3, NULL, NULL, 2, NULL, 0, 'While the potato nests bake, melt the butter and olive oil in a wide frying pan over medium-high heat. Add the minced shallot and garlic. Sauté for one minute. Add the sliced mushrooms, along with a generous pinch or two of salt and some freshly ground black pepper. Sauté, stirring occasionally, until the mushrooms release any liquid and it cooks off. Add the Marsala and cook, stirring once or twice, until all liquid has evaporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C9AD949C2BAF940B4747BAEBEFB9E56', X'FB24067D6CE99E4EB9703006C11CC0B1', X'FB24067D6CE99E4EB9703006C11CC0B1', 4, NULL, NULL, 2, NULL, 0, 'Turn off the heat and stir in the crème fraîche, grated cheese and tarragon.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5C154E9A8B9C2479E83534AA7CADAD5', X'FB24067D6CE99E4EB9703006C11CC0B1', X'FB24067D6CE99E4EB9703006C11CC0B1', 5, NULL, NULL, 2, NULL, 0, 'Spoon a small amount of the mushroom mixture into each potato nest. Rewarm in a low oven for a few minutes before serving, if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6635D20DD117B94C8989B2B72E96103B', X'F6143EAB45A6614EBCA79E7B730F6026', X'F6143EAB45A6614EBCA79E7B730F6026', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEB79EDB01E5E647A287C972C0BCCEB7', X'F6143EAB45A6614EBCA79E7B730F6026', X'F6143EAB45A6614EBCA79E7B730F6026', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil a large frying pan over medium heat. Add the ramps or leeks, along with a good pinch of salt, and sauté for about 3 minutes, until the whites start to become translucent.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49A29D5760EDB444A5EB7D2F6C8CD6CE', X'F6143EAB45A6614EBCA79E7B730F6026', X'F6143EAB45A6614EBCA79E7B730F6026', 2, NULL, NULL, 2, NULL, 0, 'If using frozen spinach, cook according to package directions and drain thoroughly, squeezing out as much excess water as possible, before proceeding. Add the spinach, along with another pinch of salt, and sauté until wilted. Let cool slightly, and if there is any excess moisture, drain it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0C5C05C14C77040AFA159D9B5EE40BF', X'F6143EAB45A6614EBCA79E7B730F6026', X'F6143EAB45A6614EBCA79E7B730F6026', 3, NULL, NULL, 2, NULL, 0, 'Combine the cream cheese, sour cream, mayonnaise, salt, and pepper in a large bowl or the bowl of a stand mixer or food processor. Mix or pulse until well combined. Add the Parmigiano cheese, the chopped sundried tomatoes and the ramp-spinach mixture and mix or pulse to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31B8934C45D67C4AB50C80600A4A1280', X'F6143EAB45A6614EBCA79E7B730F6026', X'F6143EAB45A6614EBCA79E7B730F6026', 4, NULL, NULL, 2, NULL, 0, 'Spread the dip into a medium oven-proof dish and sprinkle with the Pepper Jack cheese. Bake for 25 minutes, until the cheese is melted and browned around the edges and the dip is starting to bubble a bit. Cool slightly and serve with chips or toasts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD48C508499D7549935CBF381C33EFB4', X'928C9AAE2A2AF1448EB78B81AA7EAD55', X'928C9AAE2A2AF1448EB78B81AA7EAD55', 0, NULL, NULL, 2, NULL, 0, 'To prepare the fava beans for cooking, remove them from their large outer pods. Bring a large pot of water to a rolling boil, and fill a large bowl with ice water. Blanch the beans in the boiling water for one to two minutes, until they slip fairly readily from their skins. Transfer the beans with a skimmer or slotted spoon to the ice water. When they’re cool, drain the water and remove the beans from their whitish skins.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7ED5EB33AA5046AEBC279BD53A02D9', X'928C9AAE2A2AF1448EB78B81AA7EAD55', X'928C9AAE2A2AF1448EB78B81AA7EAD55', 1, NULL, NULL, 2, NULL, 0, 'To make the puree, heat the ½ cup of olive oil in a medium pot over medium heat. Add the shelled, skinned beans, garlic, herbs, water, and a good couple of pinches of salt and pepper. Simmer, stirring occasionally, until the beans are very tender (about 15 minutes), adjusting the heat as necessary to prevent burning, and adding more water if necessary. Remove the pot from the heat, and remove the herb sprig. Mash the beans with the back of a spoon or pass the mixture through a food mill.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'746BBF380B1C604EB9072E2EE522221A', X'928C9AAE2A2AF1448EB78B81AA7EAD55', X'928C9AAE2A2AF1448EB78B81AA7EAD55', 2, NULL, NULL, 2, NULL, 0, 'Stir in the ¼ cup of extra virgin olive oil and a bit of water if necessary to achieve a nice, thick but spreadable consistency, along with a squeeze of lemon juice and a bit of grated zest, if desired. Season with salt and pepper to taste. Serve with toasted baguette slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10844A7BD5E0854EA91BC58DF19B04E7', X'6713A7BD28DC074CB55A6D6B61F45F60', X'6713A7BD28DC074CB55A6D6B61F45F60', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, warm the olive oil over medium-high heat. Add the shallot, leek, salt, and pepper and cook until beginning to soften, about two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B25B19EFF40CEE48B9398EAE4EF84022', X'6713A7BD28DC074CB55A6D6B61F45F60', X'6713A7BD28DC074CB55A6D6B61F45F60', 1, NULL, NULL, 2, NULL, 0, 'Add rice, stock, water, salt, pepper, and herbs de Provence. If you''re using white rice, add the carrot and celery now. If your rice cooks for longer, hold off until later.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB34EB89658FDE47836E1C3E462C2312', X'6713A7BD28DC074CB55A6D6B61F45F60', X'6713A7BD28DC074CB55A6D6B61F45F60', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then reduce heat, cover, and simmer for as long as suggested on the rice packaging. (For white rice, about 20 minutes. For brown and wild rice, about 45 minutes.) With 20 minutes left on the rice cooking time, add the carrot and celery.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95BC064228218341872DE137A77CCB6C', X'6713A7BD28DC074CB55A6D6B61F45F60', X'6713A7BD28DC074CB55A6D6B61F45F60', 3, NULL, NULL, 2, NULL, 0, 'While the soup simmers, make the beurre manié. In a small bowl, combine the room-temperature butter and the flour. Use a fork to mash it all together to form a thick paste. Keep going until it looks homogenous, with no dry flour remaining.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D37B604BD990BB4B82028BD57403BD81', X'6713A7BD28DC074CB55A6D6B61F45F60', X'6713A7BD28DC074CB55A6D6B61F45F60', 4, NULL, NULL, 2, NULL, 0, 'When rice is cooked, add turkey, cream (if using), and beurre manié. Stir very well until the beurre manié is fully incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D163DAA61F8F0344A6C148F0C0BA69F6', X'6713A7BD28DC074CB55A6D6B61F45F60', X'6713A7BD28DC074CB55A6D6B61F45F60', 5, NULL, NULL, 2, NULL, 0, 'Raise heat again to bring to a boil. Then reduce heat and simmer, uncovered, for 8 minutes to thicken the soup and cook out the flour. Ladle into bowls and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D65B38EDCEBA247BE9F1A3AE711E3C3', X'F17693C1BF0CA941B8BE7B5CFEEAC3CB', X'F17693C1BF0CA941B8BE7B5CFEEAC3CB', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except the lime juice in a pot and bring to a boil over high heat. Reduce heat and simmer until carrots and lentils are tender, about 40 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0F933E648B97A489BA36ED1CE595422', X'F17693C1BF0CA941B8BE7B5CFEEAC3CB', X'F17693C1BF0CA941B8BE7B5CFEEAC3CB', 1, NULL, NULL, 2, NULL, 0, 'Puree with an immersion blender or carefully in a regular blender until no carrot chunks remain but there''s still some nice texture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8EF0DFD05F22140A49560B11317E4B5', X'F17693C1BF0CA941B8BE7B5CFEEAC3CB', X'F17693C1BF0CA941B8BE7B5CFEEAC3CB', 2, NULL, NULL, 2, NULL, 0, 'Stir in the lime juice, ladle into bowls, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CF53C644652C04FBA6C827129BDE856', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 0, NULL, NULL, 2, NULL, 0, 'Into a large bowl, place the chopped tomatoes, pepper, cucumber, onion, garlic, salt, and bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA812B2D137FB342AC37F13646533C55', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 1, NULL, NULL, 2, NULL, 0, 'Stir thoroughly to distribute the salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33C103D3442EC248A6CDB8B1A63551CF', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 2, NULL, NULL, 2, NULL, 0, 'Let sit for 30 minutes, so that the salt infuses the vegetables and begins to draw out some of their moisture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC6BB3844CE1664B93AE9DB234CC8E62', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 3, NULL, NULL, 2, NULL, 0, 'Pour all contents of the bowl into a blender and process until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F49D93CF0EC5A47AE6A7766E9454C5C', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 4, NULL, NULL, 2, NULL, 0, 'With the blender running, stream in the olive oil and vinegar and continue blending until creamy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B90FF51497455845B2AA3728AD4035F4', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 5, NULL, NULL, 2, NULL, 0, 'Rinse and dry the original mixing bowl and set a fine-mesh strainer overtop. Pour the soup through the strainer into the bowl, stirring and pressing with a large spoon or silicone spatula.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E52A756E3A77B34FB0DB820CF0E6AE90', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 6, NULL, NULL, 2, NULL, 0, 'Chill gazpacho for at least 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED0F25125797034BB7301049F91603E7', X'F8268822229A3B4186C3F4D2367719F0', X'F8268822229A3B4186C3F4D2367719F0', 7, NULL, NULL, 2, NULL, 0, 'To garnish, if you like, drizzle with additional olive oil and/or sherry vinegar and top with finely diced tomato, pepper, and/or cucumber, and maybe a few leafy herbs (parsley, cilantro, and chives would work well).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25C015F55E2765489356333E8D2CC2BE', X'D1A9924B50C3404BA29F2A70ED4A569D', X'D1A9924B50C3404BA29F2A70ED4A569D', 0, NULL, NULL, 2, NULL, 0, 'Set a very large pot over medium-high heat. (We used a 9-quart Dutch oven.) When the pot is hot, add sausage and break up into small pieces. This will get easier as the sausage cooks. Cook, stirring and breaking up frequently, until browned. If there''s a lot of grease in the pot, carefully remove and discard all but about a tablespoon or two with a spoon. (There may not be.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1778D6C1C6750E4D9D7B2561E3532570', X'D1A9924B50C3404BA29F2A70ED4A569D', X'D1A9924B50C3404BA29F2A70ED4A569D', 1, NULL, NULL, 2, NULL, 0, 'Add onion, celery, garlic, rosemary, salt, chili flakes and plenty of freshly ground black pepper. Cook, stirring frequently, until vegetables are softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC2FA9933D99234AB8132B7A2903AACA', X'D1A9924B50C3404BA29F2A70ED4A569D', X'D1A9924B50C3404BA29F2A70ED4A569D', 2, NULL, NULL, 2, NULL, 0, 'Stir in potatoes and broth. Cover pot bring to a boil, then lower heat to maintain a brisk simmer and cook until potatoes are just tender, 5 to 10 minutes depending on size.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BCBB47A83167A4283B871A32473DB28', X'D1A9924B50C3404BA29F2A70ED4A569D', X'D1A9924B50C3404BA29F2A70ED4A569D', 3, NULL, NULL, 2, NULL, 0, 'Stir in kale and beans. Cover pot again and simmer until kale is tender, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3384740BC0325F4EA84F60862FC44FE8', X'D1A9924B50C3404BA29F2A70ED4A569D', X'D1A9924B50C3404BA29F2A70ED4A569D', 4, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in balsamic vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D650B9660C65343B325EEAC2AC5A3CA', X'D1A9924B50C3404BA29F2A70ED4A569D', X'D1A9924B50C3404BA29F2A70ED4A569D', 5, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve with grated cheese and freshly ground black pepper to pass at the table, if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09A8DF56325C4D488E507BAE84560F25', X'2971B073BEBFE44DB09AAF4EEC68F57A', X'2971B073BEBFE44DB09AAF4EEC68F57A', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients into a 6- or 8-quart Instant Pot. Pour in filtered water, stopping about a half inch below the max fill line.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21B5D69395B42244A80907B70D8FB20B', X'2971B073BEBFE44DB09AAF4EEC68F57A', X'2971B073BEBFE44DB09AAF4EEC68F57A', 1, NULL, NULL, 2, NULL, 0, 'Place lid on Instant Pot and set vent to sealing position.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79FCFDB5A4DE724D8EA7E3518FBA5CD3', X'2971B073BEBFE44DB09AAF4EEC68F57A', X'2971B073BEBFE44DB09AAF4EEC68F57A', 2, NULL, NULL, 2, NULL, 0, 'Set Instant Pot to manual (high pressure) for 120 minutes. (Protip: You can use the down arrow to make this faster since the minutes `wrap back around` to 120 after 0.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3942F3EE167DFD4EBE513B609088AE6B', X'2971B073BEBFE44DB09AAF4EEC68F57A', X'2971B073BEBFE44DB09AAF4EEC68F57A', 3, NULL, NULL, 2, NULL, 0, 'The pot will take a while to come up to pressure and then will begin counting down the cooking time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D2FE2F520B67145981D52DD104DBCE7', X'2971B073BEBFE44DB09AAF4EEC68F57A', X'2971B073BEBFE44DB09AAF4EEC68F57A', 4, NULL, NULL, 2, NULL, 0, 'When the cooking time ends, let the pressure release naturally. This may take 30 minutes or so.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99370FA6B2CFBB46912206876AC70A34', X'2971B073BEBFE44DB09AAF4EEC68F57A', X'2971B073BEBFE44DB09AAF4EEC68F57A', 5, NULL, NULL, 2, NULL, 0, 'Strain the broth through a fine-mesh sieve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2E68334821FF34A8FD4B4ACB8536B8D', X'2971B073BEBFE44DB09AAF4EEC68F57A', X'2971B073BEBFE44DB09AAF4EEC68F57A', 6, NULL, NULL, 2, NULL, 0, 'Store broth in airtight containers (such as mason jars) in a cold fridge for up to a week. Or freeze (in ice cube trays, mason jars, or other reusable containers) for up to 6 months. After chilling, a small layer of fat should rise to the top. You can skim this off if you like, and either use it for cooking or discard it. Or you can stir it back into the broth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1750AC4766264949BE9F4C41C5B3EECB', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 0, NULL, NULL, 2, NULL, 0, 'Char the poblanos by placing them directly on the flames of a gas burner, turning occasionally, until blackened on all sides. (If you don''t have gas burners, you can char them under the broiler or on a grill.) Place charred poblanos into a large bowl, top bowl with plastic wrap or a plate that fits tightly, and let the poblanos steam until they''re relatively cool. Then, using a damp paper towel or your fingers, peel off and discard the blackened skin.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'027388C3B329444C9F64D84EF6B49243', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 1, NULL, NULL, 2, NULL, 0, 'Dice peeled poblanos, removing inner ribs and seeds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'734932B4F3565E4C9EEC192153143D82', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 2, NULL, NULL, 2, NULL, 0, 'In a heavy 5-quart pot, melt the butter over medium-high heat. Add the onion and celery and cook, stirring occasionally, until slightly softened but not browned, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C115B6B07D12664D8299AD2E0FCBDE30', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 3, NULL, NULL, 2, NULL, 0, 'Stir in garlic and cook one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E50AC14FFDF2048ABE6E018A0457897', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 4, NULL, NULL, 2, NULL, 0, 'Add broth, raw chicken tenders, and potatoes to pot and raise heat to high until liquid comes to a boil. Then reduce heat to maintain a simmer and cook until chicken is just opaque throughout. (This should only take a few minutes.) Remove chicken to a plate or cutting board, cool slightly, and shred with two forks.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C720AFCC5CBAC4A890C43F4EA6BF583', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 5, NULL, NULL, 2, NULL, 0, 'Add salt, cumin, and chili powder, and continue simmering until potatoes are tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FD6A067C2BD434EB946DCACA1E453D1', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 6, NULL, NULL, 2, NULL, 0, 'Add corn and beans, along with reserved diced poblanos and shredded chicken, and simmer until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914450BA84726841B537F347DAAB9BE6', X'8E5A22250ECAB4408BE9F1F6690E17E9', X'8E5A22250ECAB4408BE9F1F6690E17E9', 7, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in half and half, lime juice, and cilantro.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A30D467A2D3B054CAA66E69CBF231D23', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 0, NULL, NULL, 2, NULL, 0, 'Trim ends from butternut squash. Peel, scoop out seeds, and dice into 1-inch pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26CC9111E83AF34F961A8B0B221A1659', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 1, NULL, NULL, 2, NULL, 0, 'Dice onion and apple.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C49DA8A61427C44E89AB7AC087A67D02', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 2, NULL, NULL, 2, NULL, 0, 'Instant Pot Instructions: Place all ingredients except cream in Instant Pot and close lid. Set vent to sealing position. Cook on high pressure for 12 minutes, then carefully release steam manually.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85392D76B6E5E3448885A4EEADD72845', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 3, NULL, NULL, 2, NULL, 0, 'Stovetop Instructions: In a large, heavy pot, melt the butter over medium heat. Add onion, garlic, and apple, and cook, stirring occasionally, until softened, about five minutes. Add butternut squash, broth, cider, maple syrup, bay leaves, salt, cinnamon, and nutmeg. Bring to a boil over high heat, then reduce heat, cover, and simmer until squash is very tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'281ADA5A294AD04AB5E08BE78BC8CDAE', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 4, NULL, NULL, 2, NULL, 0, 'Remove bay leaves and cinnamon sticks if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E438991E2D311D48A533511540D3CBA2', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 5, NULL, NULL, 2, NULL, 0, 'Stir in cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F83F24120973E24CA49AF33E7583F5A9', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 6, NULL, NULL, 2, NULL, 0, 'Puree soup with an immersion blender directly in Instant Pot/pot or in batches in a blender. (The Vitamix makes this soup super-creamy and luxurious, if you’re into that sort of thing.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDD5B308E7AAED45804E7A3D766E8C94', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 7, NULL, NULL, 2, NULL, 0, 'Grind in some black pepper and adjust for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2815B00A8E93514B8935CEEB7E5F4AFE', X'15C6DA290AB43446B3E4689545C84378', X'15C6DA290AB43446B3E4689545C84378', 8, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E95D0262DDBAC4A8AEA93ED4ADC4255', X'2CF707B237C04F489C71CCF36ACB6D5C', X'2CF707B237C04F489C71CCF36ACB6D5C', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot, add the chicken broth, tomatoes, taco seasoning, salt, and pepper, and stir to combine. Bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D25D1D419224547B219AC12EA29584D', X'2CF707B237C04F489C71CCF36ACB6D5C', X'2CF707B237C04F489C71CCF36ACB6D5C', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low. Add chicken pieces and lime juice and simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C286F9C532DC74DB6EEFB19F09A782C', X'2CF707B237C04F489C71CCF36ACB6D5C', X'2CF707B237C04F489C71CCF36ACB6D5C', 2, NULL, NULL, 2, NULL, 0, 'Adjust seasoning with salt, pepper, taco seasoning, or lime juice, as needed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2331E23C183CF46AC508DDBE461EC58', X'2CF707B237C04F489C71CCF36ACB6D5C', X'2CF707B237C04F489C71CCF36ACB6D5C', 3, NULL, NULL, 2, NULL, 0, 'Ladle into two bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'905751FA6306804AA92EA30089D90436', X'2CF707B237C04F489C71CCF36ACB6D5C', X'2CF707B237C04F489C71CCF36ACB6D5C', 4, NULL, NULL, 2, NULL, 0, 'Garnish with cilantro, avocado, and crumbled chips.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C7A989F88F6DC468916897D3EA25C3A', X'79952C7809954B419A641EB9C16651E8', X'79952C7809954B419A641EB9C16651E8', 0, NULL, NULL, 2, NULL, 0, 'In a heavy 5-quart pot, heat the olive oil over medium heat. Add the onions and cook, stirring occasionally, until softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60880797DD3F3C478729338E23FE941F', X'79952C7809954B419A641EB9C16651E8', X'79952C7809954B419A641EB9C16651E8', 1, NULL, NULL, 2, NULL, 0, 'While the onion cooks, combine the cashews and vegetable stock in a blender. Blend until completely smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E89696CFB59BD4780836D636B6C3568', X'79952C7809954B419A641EB9C16651E8', X'79952C7809954B419A641EB9C16651E8', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic to the pot and cook, stirring, for one minute. Add the tomatoes and their juices, the cashew mixture, the salt, if using, and the bouquet garni. Bring soup to the boil, then reduce heat and simmer, partially covered, for 20 minutes. Remove bouquet garni and stir in nutritional yeast.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF109335A5D60D4C92C57B91836B8B1D', X'79952C7809954B419A641EB9C16651E8', X'79952C7809954B419A641EB9C16651E8', 3, NULL, NULL, 2, NULL, 0, 'Using an immersion blender or in batches in the regular blender, puree the soup until it''s as smooth as you like it. (This step isn''t necessary if you like a more textured soup.) Taste for salt and add freshly ground pepper to taste. Serve hot. Keeps well in the fridge for up to 3 days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0791711F3326A040810BC3BE677AF996', X'E936C86E00AFC74D8782A2B190923C12', X'E936C86E00AFC74D8782A2B190923C12', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in a medium pot over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03A775DEA86A40449AE5E4E6A1D5C55F', X'E936C86E00AFC74D8782A2B190923C12', X'E936C86E00AFC74D8782A2B190923C12', 1, NULL, NULL, 2, NULL, 0, 'Trim roots from ramps and discard. Cut ramps to separate green leaves from stems.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC7BBF85CE1C1C47AD2AB1F2FE6BCD6D', X'E936C86E00AFC74D8782A2B190923C12', X'E936C86E00AFC74D8782A2B190923C12', 2, NULL, NULL, 2, NULL, 0, 'Add ramp stems and diced onion to pot and cook, stirring occasionally, until onion begins to soften, just a few minutes. (Alternatively, roughly chop garlic and add to pot with onion.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3A27CBCD6A4424FA631F1AAC76B5D74', X'E936C86E00AFC74D8782A2B190923C12', X'E936C86E00AFC74D8782A2B190923C12', 3, NULL, NULL, 2, NULL, 0, 'Trim woody ends from asparagus and discard, then chop asparagus into 1-inch pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC08A6F97533C446AFE228744B936C1D', X'E936C86E00AFC74D8782A2B190923C12', X'E936C86E00AFC74D8782A2B190923C12', 4, NULL, NULL, 2, NULL, 0, 'Add asparagus to pot along with ramp leaves, broth, and salt. Bring to a boil over high heat, then cover and reduce heat to maintain a gentle simmer until asparagus is tender, 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E1FF66F7082E34E80E80C4A6B60B57D', X'E936C86E00AFC74D8782A2B190923C12', X'E936C86E00AFC74D8782A2B190923C12', 5, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in cream, lemon juice, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'662AF4A54E566F47AB98E0AE44C14197', X'E936C86E00AFC74D8782A2B190923C12', X'E936C86E00AFC74D8782A2B190923C12', 6, NULL, NULL, 2, NULL, 0, 'Use an immersion blender or transfer soup to a blender (in batches if necessary) and process until perfectly smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACFB64040D997F4C9079B823C733F847', X'7EB88520E0C786429B117721BEC5F14A', X'7EB88520E0C786429B117721BEC5F14A', 0, NULL, NULL, 2, NULL, 0, 'Heat two tablespoons of the olive oil in a large pot over high heat. Remove sausage from casings (if you bought links) and crumble it into the pot. Cook, stirring occasionally, until sausage is browned and cooked through. Remove sausage to a plate and spoon out and discard all but about two tablespoons of fat from the pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB23DC305834F247BB17824330E3DDF4', X'7EB88520E0C786429B117721BEC5F14A', X'7EB88520E0C786429B117721BEC5F14A', 1, NULL, NULL, 2, NULL, 0, 'Lower heat to medium. Add onion, carrots and celery and stir to coat with fat. Cook for about 10 minutes, stirring occasionally, until carrots are fairly tender and veggies are lightly browned in spots. Add garlic and cook, stirring, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D597A27F33C5649B26F68D6566C4FE4', X'7EB88520E0C786429B117721BEC5F14A', X'7EB88520E0C786429B117721BEC5F14A', 2, NULL, NULL, 2, NULL, 0, 'Stir in stock and tomatoes, scraping up any browned bits from the bottom of the pot. Bring to a boil over high heat, then reduce heat to simmer for five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A0271C41778AE40945716EFAA4E1843', X'7EB88520E0C786429B117721BEC5F14A', X'7EB88520E0C786429B117721BEC5F14A', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook pasta shells to al dente in a separate pot of well salted water. Drain and toss with remaining tablespoon olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50BC5E386798804598622971DC0216E7', X'7EB88520E0C786429B117721BEC5F14A', X'7EB88520E0C786429B117721BEC5F14A', 4, NULL, NULL, 2, NULL, 0, 'Back in the big soup pot, add beans, reserved sausage and spinach. Simmer until spinach is wilted. Off the heat, stir in parsley and black pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'537B25BD4ACCA04AB4B7EEAF4E8A139C', X'7EB88520E0C786429B117721BEC5F14A', X'7EB88520E0C786429B117721BEC5F14A', 5, NULL, NULL, 2, NULL, 0, 'Ladle soup into bowls, stirring some pasta shells into each. Serve with cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F550959C657B7F45B537C8397180B09D', X'B6CF3D205D478B4FA3657080B2BB6C0F', X'B6CF3D205D478B4FA3657080B2BB6C0F', 0, NULL, NULL, 2, NULL, 0, 'Bring broth to a boil in a five-quart pot, covered, over high heat. Add orzo and simmer briskly, still covered, until al dente (a couple minutes shy of package directions). Meanwhile, in a medium bowl, beat the eggs well with a fork. Beat in lemon juice and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99FE53B28314CE49AA998EB8A6933E47', X'B6CF3D205D478B4FA3657080B2BB6C0F', X'B6CF3D205D478B4FA3657080B2BB6C0F', 1, NULL, NULL, 2, NULL, 0, 'When orzo is just cooked, ladle out about a cup of hot broth into a measuring cup with a spout. Slowly pour hot broth into bowl with eggs and lemon juice, whisking constantly with a fork. This process tempers the eggs so they don’t scramble when you add them to the pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C47151C836171C458795EBAF0F022677', X'B6CF3D205D478B4FA3657080B2BB6C0F', X'B6CF3D205D478B4FA3657080B2BB6C0F', 2, NULL, NULL, 2, NULL, 0, 'Stir chicken and spinach into soup pot and simmer for a minute or two until spinach is wilted. Reduce heat until broth is no longer bubbling. Stir egg mixture into soup and cook over low heat for a minute or two, until broth looks creamy and is slightly thickened. Add salt and pepper to taste and ladle soup into serving bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C87B6E4F14AF3419B24770123FC778C', X'B6CF3D205D478B4FA3657080B2BB6C0F', X'B6CF3D205D478B4FA3657080B2BB6C0F', 3, NULL, NULL, 2, NULL, 0, 'If you need to reheat leftovers, do so gently and without boiling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46790BE26F23AC4AA8065FD16610A6A5', X'2305541E36C6764E94E79D51BCEBA07D', X'2305541E36C6764E94E79D51BCEBA07D', 0, NULL, NULL, 2, NULL, 0, 'To a heavy 5-quart pot, add the potatoes, onions, carrots, water, salt, thyme, bay leaf and nutmeg. Bring to a boil over high heat. Cover and reduce heat to maintain a simmer. Simmer, stirring occasionally, until all vegetables are very tender, about an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82B68BB00710F84FB8A5EBBB0E4E18E9', X'2305541E36C6764E94E79D51BCEBA07D', X'2305541E36C6764E94E79D51BCEBA07D', 1, NULL, NULL, 2, NULL, 0, 'Remove thyme stems and bay leaf. Pass soup through a food mill with a medium disc attached. Almost all of the solids should go through. (Or use a stick blender for a less rustic texture.) Stir in cream or olive oil. Add pepper and additional salt, if desired, to taste. Return to pot and reheat over low heat before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'314D866DF82BD74A8F9BDED5B94A1A02', X'20BE9A3B4FF32143B8323A98154A753F', X'20BE9A3B4FF32143B8323A98154A753F', 0, NULL, NULL, 2, NULL, 0, 'In a large (about 7-quart) Dutch oven or other pot with a heavy bottom, warm the oil over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6696A6C6A4F7D4C94076CE55480AB3A', X'20BE9A3B4FF32143B8323A98154A753F', X'20BE9A3B4FF32143B8323A98154A753F', 1, NULL, NULL, 2, NULL, 0, 'Add onion, carrot and celery and cook, stirring only occasionally, for about 10 minutes, until lightly browned in spots and begging to get tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62749B30A078A940A27FF7D5958D66C6', X'20BE9A3B4FF32143B8323A98154A753F', X'20BE9A3B4FF32143B8323A98154A753F', 2, NULL, NULL, 2, NULL, 0, 'Stir in garlic and cook for a minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7332E209DD4FD8419F8717868C6EE074', X'20BE9A3B4FF32143B8323A98154A753F', X'20BE9A3B4FF32143B8323A98154A753F', 3, NULL, NULL, 2, NULL, 0, 'Add broth, herbes de Provence or thyme, bay leaves, and pepper (along with a teaspoon of fine sea salt if the broth and chickpeas are unsalted) and bring to a boil, then simmer, covered, for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0F72586DE0858459AAC6232AE0C36EA', X'20BE9A3B4FF32143B8323A98154A753F', X'20BE9A3B4FF32143B8323A98154A753F', 4, NULL, NULL, 2, NULL, 0, 'While broth simmers, bring a pot of well salted water to a boil and cook noodles to al dente according to package directions. Drain and toss with a little bit of oil to prevent sticking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'867118AE4E6AC24CA2815D89E261CA7C', X'20BE9A3B4FF32143B8323A98154A753F', X'20BE9A3B4FF32143B8323A98154A753F', 5, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in chickpeas, parsley, and lemon juice. Ladle soup into bowls, adding noodles to individual bowls right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A9C66C894CFE04889DE5DEFEA26611D', X'811E5E9F0236DA44BB689AE7FB2D483D', X'811E5E9F0236DA44BB689AE7FB2D483D', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large pot over medium-high heat. Add onion, carrots, celery and salt and stir to coat with oil. Cook for about 5 minutes, stirring occasionally, until beginning to soften. If you happen to get distracted and the veggies brown a little bit, it''ll add some nice depth of flavor.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'183E68D5F24AEE48939015B433AEFBDB', X'811E5E9F0236DA44BB689AE7FB2D483D', X'811E5E9F0236DA44BB689AE7FB2D483D', 1, NULL, NULL, 2, NULL, 0, 'Stir in stock, bring to a boil, then cover and reduce heat to simmer until veggies are tender. Remove lid and add spinach and tortellini. Cook according to tortellini package instructions (usually just simmering for a couple of minutes). Off the heat, stir in parsley and black pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AA80A9CB21B34DBA43A4C59C82740A', X'811E5E9F0236DA44BB689AE7FB2D483D', X'811E5E9F0236DA44BB689AE7FB2D483D', 2, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve with cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F604588E86FF24E8887BECC0743EADA', X'5AA5A593E47AFA4481D267F833579EC4', X'5AA5A593E47AFA4481D267F833579EC4', 0, NULL, NULL, 2, NULL, 0, 'Warm olive oil over medium-high heat in a large Dutch oven or other heavy pot with a lid. (You''ll use the lid later.) Add onion, celery, carrot and one teaspoon of salt and cook, stirring occasionally until lightly browned and somewhat tender, 5-10 minutes. Add garlic, tomato paste and marmite (if using) and cook, stirring, for a minute or two. Pour in broth, raise heat to high and bring to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'777622387C19C04E994920A8D0D9D027', X'5AA5A593E47AFA4481D267F833579EC4', X'5AA5A593E47AFA4481D267F833579EC4', 1, NULL, NULL, 2, NULL, 0, 'Add diced potatoes and remaining teaspoon salt. Cover and reduce heat to simmer. Cook until potatoes are almost tender, 15 minutes or so depending on the size of your dice. Add kale and beans and cook a further 10 minutes, until kale is tender and potatoes are just shy of falling apart.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82C84AA33091F84FBD2BBBA0CBC7D0BD', X'5AA5A593E47AFA4481D267F833579EC4', X'5AA5A593E47AFA4481D267F833579EC4', 2, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in vinegar, parsley and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB651E9E5F9FEE4A8346A305BE6A9F7D', X'5AA5A593E47AFA4481D267F833579EC4', X'5AA5A593E47AFA4481D267F833579EC4', 3, NULL, NULL, 2, NULL, 0, 'Serve hot with cheese to pass at the table if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D72F4C537DDA24490C28C83CDE6614C', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 0, NULL, NULL, 2, NULL, 0, 'Fill a large bowl halfway with cold water. Peel the potatoes and spiralize them with a medium-sized blade according to manufacturer instructions. Submerge spirals in water and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A453FBACBE06EC45A4D3B9BA77105EC5', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 1, NULL, NULL, 2, NULL, 0, 'In a medium pot, melt the butter over medium heat. Add the shallot, garlic and ginger and cook, stirring occasionally, until softened, about five minutes. Pour in the broth, raise heat to high, and bring to a boil. As soon as the broth boils, cover and reduce heat to maintain a bare simmer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE36E0A9CC6D64282096962E1360CDC', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the tamari, mirin and miso until smooth. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62014B0826B1D48A94AAF117CAB6F0E', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large pot of water to a boil, then reduce to simmer. Carefully lower the eggs into the water and simmer for seven minutes. Remove eggs with a slotted spoon and set aside to peel and halve when slightly cooled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1014D959FD04BF49962F4355DCB76786', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 4, NULL, NULL, 2, NULL, 0, 'Bring water back to a rolling boil. Add the bok choy and cook for two minutes. Remove to a large plate. Add snow peas and carrot matchsticks to pot and cook for 30 seconds. Remove to plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4A063DADADB5B4BBC5A1329647F7E34', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 5, NULL, NULL, 2, NULL, 0, 'Add two tablespoons salt to the boiling water. Cook potato spirals for about three minutes, until just tender. Divide potatoes among four bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C1448A514E694BB7F9329886E93F13', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 6, NULL, NULL, 2, NULL, 0, 'Remove broth from heat and whisk in the tamari mixture. Ladle some broth over the potato noodles in each bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7AEC08F4B5C5347B4FC5B1556E6A5D8', X'8FCE193EDA3BAB48B31854FBBB4E624E', X'8FCE193EDA3BAB48B31854FBBB4E624E', 7, NULL, NULL, 2, NULL, 0, 'Top each bowl with little piles of bok choy, snow peas, carrots, peppers, bean sprouts, cilantro and scallion, along with two egg halves. Sprinkle with gomasio and sesame oil. Serve immediately with chopsticks, a big spoon and a lime wedge on the side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E6FAB362AE7304A8845748BFBCB20EE', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 0, NULL, NULL, 2, NULL, 0, 'Combine the vegetable broth and dried shiitakes in a medium pot. Bring to a boil, then cover and remove from heat. Let mushrooms steep for at least 30 minutes, up to 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F98AEFAA005A9747BAE7485B90C76999', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 1, NULL, NULL, 2, NULL, 0, 'Remove mushrooms from pot and roughly chop, removing and discarding stems. Add mushrooms to a blender with one cup of the broth and puree until perfectly smooth. Add this mixture back to the stock pot along with the tamari. The broth can sit at this stage until shortly before serving, up to a few days in the fridge if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35C49171FFDFC9448741233D7F4EF6D9', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 2, NULL, NULL, 2, NULL, 0, 'In a large frying pan, heat one tablespoon of the vegetable oil over medium-high heat. Add the sliced onion and cook, stirring frequently, until softened and lightly browned in spots, about 5 minutes. Add the minced garlic and ginger and cook, stirring, two minutes more. Add this mixture to the stock pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D96424676DD735409E235F1B71A78E3F', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 3, NULL, NULL, 2, NULL, 0, 'Bring a large pot of unsalted water to a boil. Add the spinach and cook for about a minute, until just wilted. Remove from pot and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22AD4926A946814A89462A2174B80D34', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 4, NULL, NULL, 2, NULL, 0, 'Add eggs to pot, reduce heat to simmer, and cook for 7 minutes. Remove eggs to an ice bath and carefully peel when cool enough to touch.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10539709598BC043A6AAF2B732B9E371', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 5, NULL, NULL, 2, NULL, 0, 'Return water to boil, then add noodles and cook according to package directions. Drain and divide noodles among four large bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'597D167A4391CA4C8771843D4B5B4CD0', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 6, NULL, NULL, 2, NULL, 0, 'While noodles cook, add the remaining tablespoon of vegetable oil to the same frying pan used for the onion and heat over medium-high. Add the sliced shiitakes along with a couple of good pinches of salt. Cook, stirring occasionally, until reduced in volume and nicely browned in spots, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23444C57F47E924184FFC3DE97663615', X'6A1F8DBB284E864BA5189791454669F3', X'6A1F8DBB284E864BA5189791454669F3', 7, NULL, NULL, 2, NULL, 0, 'To assemble the ramen, bring the broth back up to a simmer and then remove from heat. Off the heat, whisk in the butter, miso paste and mirin. Ladle some broth over the noodles in each bowl, just shy of the level of the noodles. Arrange a little pile of spinach and of shiitakes in each bowl. Slice the tofu and arrange a few slices in each bowl. Cut each egg in half and place two halves in each bowl. Sprinkle with scallions, gomasio and a little toasted sesame chili oil, with extra garnishes to pass at the table. Serve with chopsticks and large spoons.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3CA5808B20CCF4D9296B314EDC16490', X'CE6379FF79013949802BAD7B6593061F', X'CE6379FF79013949802BAD7B6593061F', 0, NULL, NULL, 2, NULL, 0, 'Give the beans a quick rinse in a sieve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED9603BBF0C17A4F94A7FA8897EEC6D3', X'CE6379FF79013949802BAD7B6593061F', X'CE6379FF79013949802BAD7B6593061F', 1, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a large pot. Bring to a boil, then cover and simmer until beans are tender, about 90 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'206904CDE31AE84B98BDD6BFB26C463C', X'CE6379FF79013949802BAD7B6593061F', X'CE6379FF79013949802BAD7B6593061F', 2, NULL, NULL, 2, NULL, 0, 'Puree the soup about halfway with an immersion blender, or transfer half the soup to a regular blender and carefully puree it. Stir together the whole and pureed portions and ladle into bowls to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8393AD45B5E1144BADE55BEDA4E95331', X'20DC43B26F82ED4BAD3D4E213756D042', X'20DC43B26F82ED4BAD3D4E213756D042', 0, NULL, NULL, 2, NULL, 0, 'Rinse the split peas until the water runs clear. Place all ingredients in a large pot. Bring to a boil over high heat, then cover and simmer for about an hour and a half, until split peas are tender. That''s it!');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDA03561C9134F4B965DD627345E9C41', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 0, NULL, NULL, 2, NULL, 0, 'In a very large lidded pot (I used a 9-quart dutch oven), warm the olive oil over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E9DB9F36DB1134E8380964F9359AC53', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 1, NULL, NULL, 2, NULL, 0, 'Add the onions, celery and carrots and sprinkle the salt overtop. Cook, stirring occasionally, until the vegetables start to get tender and there’s plenty of nice browning on the bottom of the pot. Don''t rush this step, since the browned bits contribute to the soup''s deep flavor.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56CDC429A49C1B49B9308DDB068D11AF', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook, stirring a couple of times, for a minute or two, until you can really smell the garlic.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E06D94A64A19E748B86A68222C77C897', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 3, NULL, NULL, 2, NULL, 0, 'Add the tomato paste and cook, stirring, for a minute or two more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9223212CB5C3CE478A047194366271DB', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 4, NULL, NULL, 2, NULL, 0, 'Pour in the stock and diced tomatoes, thyme, oregano, red pepper flakes, and parmesan rind. Stir and scrape up all the browned bits from the bottom of the pot. (You won’t be able to see them, but you can feel the difference your spoon.) Bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BFF28937B6B76489805290418EF26F0', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 5, NULL, NULL, 2, NULL, 0, 'Add the potatoes, zucchini, green beans and canned beans. Reduce the heat to maintain a gentle simmer, cover, and cook for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'539DC6D5D680674996B474AF69F58A33', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 6, NULL, NULL, 2, NULL, 0, 'Add the kale and cook about 10 minutes more, until all the vegetables are tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51E110EBBAFEFF41A1185252DF03E85B', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 7, NULL, NULL, 2, NULL, 0, 'Off the heat, tear up the basil leaves and stir in.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D3C3260DBC7CA4181BD7C88D357D1F0', X'0508E32D16788C49B7C7F30139CBFB5F', X'0508E32D16788C49B7C7F30139CBFB5F', 8, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve, with grated cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7751E044BF4D84DA0793946C71C0201', X'6EB7559FBC175E4C876D0CD2925D7587', X'6EB7559FBC175E4C876D0CD2925D7587', 0, NULL, NULL, 2, NULL, 0, 'Soak cashews in a bowl of water overnight in the refrigerator to soften them up if you have time. If you''re in a rush, though, you can skip the soaking and the cream will still turn out fine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AC546F2B3E0A14DA9DA1E93342BF33D', X'6EB7559FBC175E4C876D0CD2925D7587', X'6EB7559FBC175E4C876D0CD2925D7587', 1, NULL, NULL, 2, NULL, 0, 'Drain cashews and rinse well. Place in a blender with cold water to cover by one inch and blend until perfectly smooth. If you''re using a high-speed blender, you''re all done. With a regular blender, you may need to strain the cream through cheesecloth to achieve perfect smoothness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'361949F1AED1444394573DB658A8B14F', X'0036F831B761B74599EFA97788DB962B', X'0036F831B761B74599EFA97788DB962B', 0, NULL, NULL, 2, NULL, 0, 'In a 5-quart heavy pot, heat the olive oil over medium heat. Add the onions and saute until translucent, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E012A7F9B0174A429D554093FC5E5F34', X'0036F831B761B74599EFA97788DB962B', X'0036F831B761B74599EFA97788DB962B', 1, NULL, NULL, 2, NULL, 0, 'Add the garlic, cumin, and garam masala and cook, stirring, one minute more. Add the sambal oelek, tomato paste, chopped tomatoes, stock or water, lentils, sugar, and cilantro, and stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C95CC1AA0B5444B8B30657D22100F4F', X'0036F831B761B74599EFA97788DB962B', X'0036F831B761B74599EFA97788DB962B', 2, NULL, NULL, 2, NULL, 0, 'Raise the heat to high to bring the soup to a boil, then lower the heat to maintain a simmer. Cook until lentils are very tender and flavors have blended, about 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5871480D23ADAA418502B40BAD921085', X'931FCF27F72DCF42A09177DA4D04A239', X'931FCF27F72DCF42A09177DA4D04A239', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot with a heavy bottom, heat the oil over medium-high heat.**');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B31420C954172F40B333501415108C88', X'931FCF27F72DCF42A09177DA4D04A239', X'931FCF27F72DCF42A09177DA4D04A239', 1, NULL, NULL, 2, NULL, 0, 'Add the onion and and cook, stirring occasionally until starting to soften, about 5 minutes. Reduce heat if necessary to prevent browning. ***');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85C739EBB1AE16499FFDD3E1B59AB8EB', X'931FCF27F72DCF42A09177DA4D04A239', X'931FCF27F72DCF42A09177DA4D04A239', 2, NULL, NULL, 2, NULL, 0, 'Add lentils and rice and stir to coat with oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'473EEC084319A94EA203785A65F8E391', X'931FCF27F72DCF42A09177DA4D04A239', X'931FCF27F72DCF42A09177DA4D04A239', 3, NULL, NULL, 2, NULL, 0, 'Add water, salt, cumin, and pepper. Bring to a boil, then cover and reduce heat to maintain a simmer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B66BE7530F1E824581C8DFDACF68286C', X'931FCF27F72DCF42A09177DA4D04A239', X'931FCF27F72DCF42A09177DA4D04A239', 4, NULL, NULL, 2, NULL, 0, 'Cook, lifting the lid occasionally to stir, until lentils and rice are completely tender and the soup is of uniform and moderately thick consistency, about an hour. Serve hot with lemon wedges to squeeze overtop.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1523164C97A79D4087EA022BACCD3E0A', X'858F2E594714794C8AD5BEDF6D0F041F', X'858F2E594714794C8AD5BEDF6D0F041F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1DB7894E6E953429CDECFF97D2823BA', X'858F2E594714794C8AD5BEDF6D0F041F', X'858F2E594714794C8AD5BEDF6D0F041F', 1, NULL, NULL, 2, NULL, 0, 'Split pumpkin in half and remove seeds with a large spoon. Brush pumpkin on cut surfaces with oil and place on a rimmed baking sheet, cut-side up.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72A3856F5E754645AB29A269B41616C6', X'858F2E594714794C8AD5BEDF6D0F041F', X'858F2E594714794C8AD5BEDF6D0F041F', 2, NULL, NULL, 2, NULL, 0, 'Roast until completely tender and a knife or cake tester inserted near the stem meets little to no resistance, about 1 hour. Remove from oven and allow to cool.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D8BF3FD3435E1459C01DBDCE7B9F212', X'858F2E594714794C8AD5BEDF6D0F041F', X'858F2E594714794C8AD5BEDF6D0F041F', 3, NULL, NULL, 2, NULL, 0, 'When cool enough to handle, scoop out flesh into a medium bowl and discard skin. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29B086E24C23514E88C3D05991D42E34', X'858F2E594714794C8AD5BEDF6D0F041F', X'858F2E594714794C8AD5BEDF6D0F041F', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, melt butter in a large, heavy pot over medium-high heat. Add onions and cook, stirring occasionally, until softened but not browned, about 4 minutes. Add pumpkin flesh, stock, salt, cinnamon, nutmeg, and cayenne. Bring to a boil, reduce to a simmer, and cook for 15 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F1F166C38A3F48A23DE1A0E57E17FD', X'858F2E594714794C8AD5BEDF6D0F041F', X'858F2E594714794C8AD5BEDF6D0F041F', 5, NULL, NULL, 2, NULL, 0, 'Remove pot oven from heat. Puree soup with immersion blender or carefully, in batches, in regular blender. For a smoother soup, strain through a fine-mesh strainer. Whisk in maple syrup and add cream. Season to taste with pepper, and it''s ready to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E321A695E0AD6448D9D2904A2BEF66D', X'76355B61A617434DA6A33073FF6BFCA9', X'76355B61A617434DA6A33073FF6BFCA9', 0, NULL, NULL, 2, NULL, 0, 'Trim away roots and dark green parts of leeks. Slice remaining white and light green parts in half lengthwise and wash thoroughly. Cut crosswise into 1/4-inch-thick half-moons. Heat olive oil and butter over medium heat in Dutch oven or heavy-bottomed pot. Add leeks and garlic and sprinkle with 1/2 teaspoon salt. Cook, stirring occasionally, until leeks are softened but not browned, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'512F23CC97793046BCF0903DB9471AA2', X'76355B61A617434DA6A33073FF6BFCA9', X'76355B61A617434DA6A33073FF6BFCA9', 1, NULL, NULL, 2, NULL, 0, 'While leeks are cooking, fill large bowl halfway with cold water. Peel potatoes, placing each in bowl of water immediately after peeling to prevent browning. Cut each potato in half lengthwise and slice into 1/2-inch-thick half-moons. Drain potato slices and add to pot along with stock and a few generous grinds of pepper. Raise heat to high and bring soup to a boil. Reduce to a simmer, cover, and cook until potatoes are very soft, about 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5A01C6AE1E0264D8984358F0FBC0107', X'76355B61A617434DA6A33073FF6BFCA9', X'76355B61A617434DA6A33073FF6BFCA9', 2, NULL, NULL, 2, NULL, 0, 'Puree soup with an immersion blender or in batches in standing blender. I like to use my Vitamix for a super-silky, luxurious texture. Season to taste with salt and pepper and serve garnished with chopped chives, dill, and flaky sea salt. Soup reheats well and will keep in refrigerator for up to a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FD0E205039C4B44AF588B8D51884FCF', X'25AC692E74806D4EB46BB34B3F3A3941', X'25AC692E74806D4EB46BB34B3F3A3941', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a large stockpot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AC942016BD90A449006CAC57ED84941', X'25AC692E74806D4EB46BB34B3F3A3941', X'25AC692E74806D4EB46BB34B3F3A3941', 1, NULL, NULL, 2, NULL, 0, 'Add 8 quarts water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27E6317F61325F40AA0B73ADB9B100FA', X'25AC692E74806D4EB46BB34B3F3A3941', X'25AC692E74806D4EB46BB34B3F3A3941', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, then reduce heat and simmer gently for 4 hours, stopping by from time to time to skim off and discard any gunk that collects at the top of the pot. (Gunk is the technical term.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5D9798D4758644FBE5D6DAF74B40F8E', X'25AC692E74806D4EB46BB34B3F3A3941', X'25AC692E74806D4EB46BB34B3F3A3941', 3, NULL, NULL, 2, NULL, 0, 'Remove from the heat and cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A532CBC43706464C807ED4DC776F60EC', X'25AC692E74806D4EB46BB34B3F3A3941', X'25AC692E74806D4EB46BB34B3F3A3941', 4, NULL, NULL, 2, NULL, 0, 'Strain the stock through a fine-mesh sieve into a very large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'069AD3D800AFA046A80FD40D4C8E71EC', X'25AC692E74806D4EB46BB34B3F3A3941', X'25AC692E74806D4EB46BB34B3F3A3941', 5, NULL, NULL, 2, NULL, 0, 'If you like, Chill the stock overnight and then skim off the fat on the surface, (I often don''t, to be perfectly honest.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A3217369DF31C48829A291163BAF2EB', X'25AC692E74806D4EB46BB34B3F3A3941', X'25AC692E74806D4EB46BB34B3F3A3941', 6, NULL, NULL, 2, NULL, 0, 'Keeps in airtight containers in a nice cold fridge for a week or in the freezer for six months at least.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F54A65FA74DADA4E95A71D2562189E51', X'0205E92115A3EC44822F9D85F6D09860', X'0205E92115A3EC44822F9D85F6D09860', 0, NULL, NULL, 2, NULL, 0, 'Place the kombu in a medium pot. You don’t need to rinse it, even if the package says you do. Pour in the water. Let sit for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E1984EE0E5D99419EF2EB1685BE9AB2', X'0205E92115A3EC44822F9D85F6D09860', X'0205E92115A3EC44822F9D85F6D09860', 1, NULL, NULL, 2, NULL, 0, 'Bring the water with the kombu in it to a boil. When it boils, turn down the heat and let it simmer very gently for 10 minutes. Then turn off the heat and add the bonito flakes. Let steep for 10 minutes, then strain the broth through cheesecloth or a coffee strainer into a clean pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'438DBDBC5945D2438705117881FBC471', X'EAE914BC34AE364B87A449EE914A485D', X'EAE914BC34AE364B87A449EE914A485D', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot over medium-high heat, bring the chicken stock (along with 1/2 teaspoon salt, if unsalted) to a gentle boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05BDC394D69BD24680FAE2173F5139AE', X'EAE914BC34AE364B87A449EE914A485D', X'EAE914BC34AE364B87A449EE914A485D', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a medium bowl, combine the eggs, semolina, Parmesan cheese, nutmeg and pepper. Whisk with a fork to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8392E5E5531074CA631B02BE213ABAF', X'EAE914BC34AE364B87A449EE914A485D', X'EAE914BC34AE364B87A449EE914A485D', 2, NULL, NULL, 2, NULL, 0, 'When the stock boils, add the spinach and cook until just beginning to wilt, about 30 seconds. Begin swirling the stock and spinach with a fork, then pour in the egg mixture in a slow stream. Continue swirling until the eggs have set into thin little shreds, and the semolina and cheese have fortified the broth, about 3 minutes. Ladle into bowls and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF22BD9C7422342BAD9B8E64416E10C', X'0D8353A75D49534681BB01E802B4E0AF', X'0D8353A75D49534681BB01E802B4E0AF', 0, NULL, NULL, 2, NULL, 0, 'In a 5 1/2-quart Dutch oven or similar heavy pot, warm the olive oil over medium-high heat. Add the onion, green pepper, carrot, celery and 1 teaspoon salt and cook, stirring only occasionally, until vegetables are beginning to be tender and browned, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9697532632956469AA84BFDE7A70C49', X'0D8353A75D49534681BB01E802B4E0AF', X'0D8353A75D49534681BB01E802B4E0AF', 1, NULL, NULL, 2, NULL, 0, 'Add garlic and cook, stirring frequently, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1FA5D6CFA8DCF42B9B4276E2A6DB969', X'0D8353A75D49534681BB01E802B4E0AF', X'0D8353A75D49534681BB01E802B4E0AF', 2, NULL, NULL, 2, NULL, 0, 'Add tomato paste, chili powder, cumin and paprika and cook, stirring, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19587FCDC8D1FF4B91E427DBE6F1658F', X'0D8353A75D49534681BB01E802B4E0AF', X'0D8353A75D49534681BB01E802B4E0AF', 3, NULL, NULL, 2, NULL, 0, 'Stir in broth and tomatoes (with all their juices). Bring to a boil, then reduce heat to simmer for 5 minutes. Sauce should be nice and thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADEBA7D84C09EA4E964B61FFCE4062E4', X'0D8353A75D49534681BB01E802B4E0AF', X'0D8353A75D49534681BB01E802B4E0AF', 4, NULL, NULL, 2, NULL, 0, 'Add beans and corn (straight from frozen is fine) and simmer until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96262DB587792344AF6297780B6026A2', X'0D8353A75D49534681BB01E802B4E0AF', X'0D8353A75D49534681BB01E802B4E0AF', 5, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in cilantro and lime juice. Serve with shredded cheese, avocado, red onion and sour cream if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92FD37A36D11864196C82B7E51B67C32', X'2D48CE7B4849744F9F38320EDFDEF5CC', X'2D48CE7B4849744F9F38320EDFDEF5CC', 0, NULL, NULL, 2, NULL, 0, 'Place all chili ingredients except lime juice into an Instant Pot and stir to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C375F479F6923147A7DCC853A7CF150C', X'2D48CE7B4849744F9F38320EDFDEF5CC', X'2D48CE7B4849744F9F38320EDFDEF5CC', 1, NULL, NULL, 2, NULL, 0, 'Cover, seal vent and cook on high pressure for 6 minutes, then release pressure manually. Stir in lime juice. Chili is ready to eat right away but, like all chili, will only get better over the next 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0969ADCCD3EADA489BF633C84439A756', X'2D48CE7B4849744F9F38320EDFDEF5CC', X'2D48CE7B4849744F9F38320EDFDEF5CC', 2, NULL, NULL, 2, NULL, 0, 'Ladle into serving bowls and garnish with lime wedges, cilantro, and vegan sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5BD599F60091E458DF20A6F22B0E96B', X'327DF60E06A9FD49B4FB69BA9E693948', X'327DF60E06A9FD49B4FB69BA9E693948', 0, NULL, NULL, 2, NULL, 0, 'Dry the beef cubes well with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE83E22519A8664B8548A65869CFBFA9', X'327DF60E06A9FD49B4FB69BA9E693948', X'327DF60E06A9FD49B4FB69BA9E693948', 1, NULL, NULL, 2, NULL, 0, 'Set a large Dutch oven or other heavy, oven-proof lidded pot over medium-high heat. Add chopped bacon and cook, stirring from time to time, until browned. Remove bacon from pot, leaving fat in pot. In batches to avoid crowding, add beef cubes and brown well on all sides. Remove beef from pot and pour off (or spoon out) all but 2 to 3 tablespoons of fat. Preheat oven to 350°F with a rack in the lower third.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'218118C84E424F46A7EBF8D10AA58CCD', X'327DF60E06A9FD49B4FB69BA9E693948', X'327DF60E06A9FD49B4FB69BA9E693948', 2, NULL, NULL, 2, NULL, 0, 'Add onions and carrots to pot. Cook, stirring from time to time, until lightly browned, about 10 minutes. Add garlic and cook, stirring, one minute more. Stir in tomato paste. Add beef and bacon back to pot. Pour in wine and enough beef broth to cover meat by about half an inch. Stir in thyme, bay leaves and salt. Cover pot with lid and place in oven. Cook for about two hours, until meat is very tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3F3AF4D0B132C4B95955441ECE1D1AF', X'327DF60E06A9FD49B4FB69BA9E693948', X'327DF60E06A9FD49B4FB69BA9E693948', 3, NULL, NULL, 2, NULL, 0, 'While stew cooks, set a frying pan over medium-high heat and pour in olive oil. When hot, add mushrooms and cook, stirring occasionally, until well browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550EBFF0540FBA4BAD7054CA1292916D', X'327DF60E06A9FD49B4FB69BA9E693948', X'327DF60E06A9FD49B4FB69BA9E693948', 4, NULL, NULL, 2, NULL, 0, 'When meat is very tender, carefully place pot on stovetop over medium heat. Add mushrooms and pearl onions. In a small bowl, mash together the butter and flour with a fork to form a paste. Stir paste into stew. Simmer for 10 minutes. Remove from heat. Serve immediately or bring to room temperature before refrigerating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2672363D6107DB48B932568E42D5F935', X'327DF60E06A9FD49B4FB69BA9E693948', X'327DF60E06A9FD49B4FB69BA9E693948', 5, NULL, NULL, 2, NULL, 0, 'Remove thyme stems and bay leaves and stir in parsley just before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7930EA887061C4FAF514DE863ADCD91', X'1A23E34B256A544C9344F6271218BA77', X'1A23E34B256A544C9344F6271218BA77', 0, NULL, NULL, 2, NULL, 0, 'Warm 1 tablespoon each of the olive oil and butter over medium-high heat in a 5 quart Dutch oven or other heavy lidded pot. Add mushrooms and cook, stirring just a few times, until nicely browned and reduced in volume by half. Remove mushrooms to a bowl and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'233202B66BFC9042ACFF4BF29CDF11A5', X'1A23E34B256A544C9344F6271218BA77', X'1A23E34B256A544C9344F6271218BA77', 1, NULL, NULL, 2, NULL, 0, 'In the same pot, heat the remaining butter and olive oil. Add onion, celery, carrots and 1/2 teaspoon of the salt and cook, stirring occasionally, until beginning to soften, about five minutes. Add the garlic and cook, stirring frequently, for a minute. Stir in the tomato paste and cook a minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF6CD19397C7F2409BB643752E4083C4', X'1A23E34B256A544C9344F6271218BA77', X'1A23E34B256A544C9344F6271218BA77', 2, NULL, NULL, 2, NULL, 0, 'Add the vegetable broth, remaining 1/2 teaspoon salt, soy sauce, marmite, bay leaf and thyme. Raise heat to high. Rinse and drain the lentils and add to pot. When broth comes to a boil, reduce heat, cover, and simmer for about 30 minutes, until lentils are nice and tender. Remove from heat and remove thyme stems and bay leaf.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56AFD8C9592CF64CB295AE2306C9E1AC', X'1A23E34B256A544C9344F6271218BA77', X'1A23E34B256A544C9344F6271218BA77', 3, NULL, NULL, 2, NULL, 0, 'If you have an immersion blender, pulse it 4 or 5 times to puree the stew just a little bit. (Alternatively, transfer about two cups of the stew to a blender and puree it, then return to pot, or simply forego this step if you prefer.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41F78298131C5442BFCE8C68DF6A73AF', X'1A23E34B256A544C9344F6271218BA77', X'1A23E34B256A544C9344F6271218BA77', 4, NULL, NULL, 2, NULL, 0, 'Return the mushrooms and any accumulated juices to the pot along with the spinach and cream. Simmer briskly, uncovered, until spinach is wilted, about two minutes. Off the heat, stir in vinegar. Let sit for 10 minutes, then ladle into bowls and serve with good bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64C8D0ABF0B5BC4B95EECB71D4C4F9C8', X'081DEB3D84D8894BAE73C459F27A892A', X'081DEB3D84D8894BAE73C459F27A892A', 0, NULL, NULL, 2, NULL, 0, 'If you are starting with raw chicken breasts: Preheat oven to 400°F with a rack in the center. Place chicken breasts in a small roasting pan and drizzle with olive oil. Sprinkle with 1/2 teaspoon chili powder, a bit of the salt, and pepper. Turn with tongs to coat evenly. Bake until cooked through, 20 minutes give or take. Set aside to cool. (If you are starting with cooked chicken or turkey, skip this step and simply add the full amount of chili powder to the pot in step 2 below.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E770907A1835304E9F2DA5E4B766931A', X'081DEB3D84D8894BAE73C459F27A892A', X'081DEB3D84D8894BAE73C459F27A892A', 1, NULL, NULL, 2, NULL, 0, 'While the chicken bakes, warm the remaining two tablespoons olive oil in a medium Dutch oven or other heavy pot over medium-high heat. Add the onion and pepper along with remaining salt and cook, stirring occasionally, until softened and a little bit browned, about 5 minutes. Add the garlic, reduce heat to medium, and cook, stirring frequently, until very fragrant, about a minute. Add flour, remaining 1 teaspoon chili powder and the cumin and stir to coat vegetables. Cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'196CB6AFD828304EB5426E1FE5A1518B', X'081DEB3D84D8894BAE73C459F27A892A', X'081DEB3D84D8894BAE73C459F27A892A', 2, NULL, NULL, 2, NULL, 0, 'Pour in the broth and diced chilis and raise heat to high. Stir a few times to incorporate flour, then bring to a boil. Reduce heat to simmer for about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776732D56554DD47A7BFB06769727055', X'081DEB3D84D8894BAE73C459F27A892A', X'081DEB3D84D8894BAE73C459F27A892A', 3, NULL, NULL, 2, NULL, 0, 'Using two forks, shred chicken. Add chicken, beans, and corn to pot along with any accumulated juices from the chicken roasting pan, and simmer for a few minutes more to warm through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F013AD3A7F4BE4E9825865284E31AF6', X'081DEB3D84D8894BAE73C459F27A892A', X'081DEB3D84D8894BAE73C459F27A892A', 4, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in cilantro, red onion and lime juice. To serve, ladle into bowls and pass cheese, sour cream and lime wedges at the table if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0B03C8A4E4772459403A131A54E67A0', X'EEF8176E9DAC7E478B65CF1428098AAB', X'EEF8176E9DAC7E478B65CF1428098AAB', 0, NULL, NULL, 2, NULL, 0, 'In a 12-inch, heavy nonstick skillet, heat one tablespoon each of the olive oil and butter over high heat. Add half the sliced mushrooms and cook without disturbing until golden brown on one side, about 3 minutes. Toss once and cook until some mushrooms start to brown on the other side (don''t stress at all about flipping all of them perfectly), about two minutes more. Scrape into a large bowl and repeat with a second tablespoon each of olive oil and butter and the other half of the mushrooms. Set mushrooms aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E0B4DBA8CA4874894B590A9145EDD5F', X'EEF8176E9DAC7E478B65CF1428098AAB', X'EEF8176E9DAC7E478B65CF1428098AAB', 1, NULL, NULL, 2, NULL, 0, 'In the same skillet, warm the remaining tablespoon olive oil over medium-high heat. Add onion and carrot along with 1/2 teaspoon salt and a few grinds black pepper and cook, stirring occasionally, for 10 minutes, Onions and carrots should be lightly browned. Stir in the garlic, thyme (stems and all) and sage and cook until very fragrant, a minute or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC4A7A55D0145849A55E02212441E20F', X'EEF8176E9DAC7E478B65CF1428098AAB', X'EEF8176E9DAC7E478B65CF1428098AAB', 2, NULL, NULL, 2, NULL, 0, 'Pour in the wine. Turn heat up to high and simmer briskly until reduced by half. Stir in tomato paste and broth, then add back the mushrooms and all the beautiful accumulated juice from the bowl. If your broth is low-sodium, add an additional 1/2 teaspoon salt. If not, wait and taste at the end. Bring to a boil, then simmer for 20 minutes. (This is a good time to start the mashed potatoes.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE8DD38A83B454409C5E7DD1AF3FE938', X'EEF8176E9DAC7E478B65CF1428098AAB', X'EEF8176E9DAC7E478B65CF1428098AAB', 3, NULL, NULL, 2, NULL, 0, 'With a fork, thoroughly mash together the remaining tablespoon of butter and the flour in a small bowl to form a paste. Stir paste into stew. Lower heat to simmer gently and cook 10 minutes more. Sauce should be thick and glossy. Taste for salt and pepper. Remove or avoid thyme stems. Serve spooned over mashed potatoes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30CE43C2601E0D4C8AFC88E9ED3600B9', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 0, NULL, NULL, 2, NULL, 0, 'In a large pot (I used a 5 1/2 quart Dutch oven), warm the olive oil over medium-high heat. Add the onion, bell pepper and salt and cook, stirring occasionally, until vegetables are starting to soften and there''s a touch of browning on the bottom of the pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D6C3FBFDB74264DA094F016EFBD3E94', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 1, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook until fragrant, about a minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45F9761F4F25994AA2ED6C43DF43961F', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 2, NULL, NULL, 2, NULL, 0, 'Add the cumin, chile powder, and smoked paprika and cook, stirring constantly, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'144DECDAF2F6BB4C9E3A479F2A4F7DD8', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 3, NULL, NULL, 2, NULL, 0, 'Stir in the vegetable broth, green chiles and oregano. Bring to a boil over high heat, then reduce to a simmer and add potatoes. Cook, covered, until potatoes are tender, about 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'076305B13012F14780A0DC573C063DB3', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 4, NULL, NULL, 2, NULL, 0, 'Uncover the pot. Add the corn, peas and beans and cook until heated through, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21069B6CDD70DC45B109C8A3D9109C83', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 5, NULL, NULL, 2, NULL, 0, 'Add the baby spinach and cook until wilted, a few minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C891662C2F6FB0499108AF305F0A6BC0', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 6, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in the cilantro, lime juice and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE3E9E3D834484EAE43EF770DA98B54', X'A770B67518D7E549A1329BDA2AB07BFD', X'A770B67518D7E549A1329BDA2AB07BFD', 7, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve garnished with sour cream if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9196B501F30A014DAB1AD19DE9C01B1A', X'C5E0732F26273D4ABC57E9657DABF526', X'C5E0732F26273D4ABC57E9657DABF526', 0, NULL, NULL, 2, NULL, 0, 'In large Dutch oven or other heavy pot, heat olive oil over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ED699CCF8CC6F4198F89F53F2D94691', X'C5E0732F26273D4ABC57E9657DABF526', X'C5E0732F26273D4ABC57E9657DABF526', 1, NULL, NULL, 2, NULL, 0, 'Add onions and carrots and cook, stirring occasionally until starting to soften, about 5 minutes. Add garlic and cook, stirring, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A9E4FCFBBF8B84E9788116CF364C7D3', X'C5E0732F26273D4ABC57E9657DABF526', X'C5E0732F26273D4ABC57E9657DABF526', 2, NULL, NULL, 2, NULL, 0, 'Add rice and stir to coat lightly with oil. Add tomato paste and cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6779701F6CA5F04F9A67BAD56B8C3319', X'C5E0732F26273D4ABC57E9657DABF526', X'C5E0732F26273D4ABC57E9657DABF526', 3, NULL, NULL, 2, NULL, 0, 'Add tomatoes, crushing each between your fingers. Then add stock, herbes de Provence, red pepper flakes, and cheese rind if using. Stir to combine. Bring to a boil over high heat, then cover tightly, reduce heat to maintain a simmer, and cook for 25 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB81094764E18E4DB01D0129A52B46FC', X'C5E0732F26273D4ABC57E9657DABF526', X'C5E0732F26273D4ABC57E9657DABF526', 4, NULL, NULL, 2, NULL, 0, 'Add kale and chickpeas, re-cover pot, raise heat to return liquid to boil, then reduce heat and simmer 15 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24235E632FAF494885CF0F4FC5B94957', X'C5E0732F26273D4ABC57E9657DABF526', X'C5E0732F26273D4ABC57E9657DABF526', 5, NULL, NULL, 2, NULL, 0, 'Serve hot, sprinkled with grated cheese if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A63AAD9CA514A849B2CE8EC2D9BB2AE6', X'F01D294F6A5B5949AED99CE2CF3BEB43', X'F01D294F6A5B5949AED99CE2CF3BEB43', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil over medium heat in a dutch oven. Add the onion and celery and saute for a few minutes without browning. (Dutch ovens retain heat like a charm, so you may need to turn it down once you get going.) Add the garlic, ginger and tomato paste and cook, stirring, for one minute. Add the zucchini and potatoes and let them hang out for a minute while you get the next ingredients ready.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F47F39D73079B49AE75D21429962D74', X'F01D294F6A5B5949AED99CE2CF3BEB43', X'F01D294F6A5B5949AED99CE2CF3BEB43', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk the coconut milk, curry paste, soy sauce, fish sauce, and brown sugar together to combine thoroughly. Pour the mixture into the pot. Cover and simmer until the zucchini and potatoes are just tender, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7708B8EAD37016428F21C5014DFCD4D2', X'F01D294F6A5B5949AED99CE2CF3BEB43', X'F01D294F6A5B5949AED99CE2CF3BEB43', 2, NULL, NULL, 2, NULL, 0, 'Add the carrots and tomatoes and cook a couple of minutes longer to warm through. Turn off the heat and stir in the cilantro, basil, lime zest and lime juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BABA25D7092EC94B88E8CFDB5AF54180', X'F01D294F6A5B5949AED99CE2CF3BEB43', X'F01D294F6A5B5949AED99CE2CF3BEB43', 3, NULL, NULL, 2, NULL, 0, 'Serve over rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC563F0C8FF26244A740735087D29AAE', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 0, NULL, NULL, 2, NULL, 0, 'Remove dough from refrigerator and preheat oven to 400° F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46A3355D7E5A4F4593108023918178CC', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry the chard leaves well. Remove stems and save for another use. Chop leaves into ribbons or large bite-sized pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'172BB1002BF9EC44A2E01CEC6F2BC992', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 2, NULL, NULL, 2, NULL, 0, 'Slice the leeks in half lengthwise and trim off the roots and dark green parts. Wash well, then cut into thin crosswise slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B220DD6FC34E104D87BD96972E9BD255', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 3, NULL, NULL, 2, NULL, 0, 'Finely chop the garlic.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73EB73CA5E59E14AAA7794B845C1DA9E', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 4, NULL, NULL, 2, NULL, 0, 'Remove the kernels from the corn cobs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'705D386BF68CF940A30040F7A9E07512', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large frying pan over medium heat. Add the leeks and garlic, along with a generous pinch of salt. Cook, stirring frequently, until leeks are softened, 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C5174AC7759564DBFF93EF0A1C9BE62', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 6, NULL, NULL, 2, NULL, 0, 'Add the chard by the handful as it fits in the pan and cook until wilted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F57EF46E860F29409F790DFB39503D12', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 7, NULL, NULL, 2, NULL, 0, 'Add the corn kernels and cook for two minutes more, stirring once or twice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEA92855B8D6FE48A115CD1AC9CC934E', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 8, NULL, NULL, 2, NULL, 0, 'Remove pan from heat. If there is any accumulated liquid at the bottom, drain it well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B7F0001008E764EA4B1B16D5F5508C4', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 9, NULL, NULL, 2, NULL, 0, 'Stir in vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90890478EC07D2448561AF03D19B03F8', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 10, NULL, NULL, 2, NULL, 0, 'By now the dough should be soft enough to roll out. Sprinkle a little bit of flour on each side of the dough and then replace the parchment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CB19EE54BB29A479E826235A25C26B7', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 11, NULL, NULL, 2, NULL, 0, 'Using a rolling pin and turning the dough a quarter-turn clockwise every so often, roll the dough out between the parchment into a 14-inch circle (which should be about the same size as the parchment sheets themselves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EC1A3F5E6EF8F48815C1C573CFD6090', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 12, NULL, NULL, 2, NULL, 0, 'Remove the top sheet of parchment and slide the dough (still on the bottom sheet of parchment) onto a pizza pan or baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C99C2EFED0237547AAB96D715E075317', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 13, NULL, NULL, 2, NULL, 0, 'Brush a little bit of olive oil over the surface of the dough, leaving a 1 1/2-inch ring around the outer edge.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3DC19764083884B90C64E0F98EECC1E', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 14, NULL, NULL, 2, NULL, 0, 'Sprinkle all but the outside ring of dough with half the Pecorino cheese and some freshly ground black pepper. Tear the Taleggio into small pieces, rind and all, and dot the surface of the dough with half of them, reserving the other half. Do the same with half the chopped olives.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FC5742492A2134BBA698A2765AE4851', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 15, NULL, NULL, 2, NULL, 0, 'Spread the chard mixture onto the dough, still leaving an outer ring. Top with the other half of the Taleggio, olives and Pecorino.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8F18AA81ACE1043806B9625B4681027', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 16, NULL, NULL, 2, NULL, 0, 'Fold the outer ring of dough over the chard mixture in 6 to 8 roughly equal sections, pressing gently at the pleats to stick them together.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60AB6D1EC7684F4B865827FE9276FB1B', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 17, NULL, NULL, 2, NULL, 0, 'In a small bowl, beat the egg with the splash of milk or cream to make an egg wash. Brush the dough with some of the egg wash.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5317E9425955D4698CE073910D4FAA8', X'8578F332BE515643AB9E323801005F57', X'8578F332BE515643AB9E323801005F57', 18, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes, rotating the pan halfway through to ensure even cooking. Slice into wedges and serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05F80A87440CDB4CA9118DE2ABD20E54', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Place crust (in foil pie plate) onto a rimmed baking sheet and defrost on countertop for 10 minutes. Prick crust all over the bottom and sides with a fork. (See note.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A93905F70426AD4A9C76078BA6FA9395', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 1, NULL, NULL, 2, NULL, 0, 'Bake crust for 10 minutes, then let cool for 10 minutes. (If directions for “blind baking” on your crust’s package vary substantially, you can follow those, but this method should work for most frozen crusts.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B26AB1312C83A94EBBAEE1F5584CA80F', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 2, NULL, NULL, 2, NULL, 0, 'Reduce oven temperature to 325°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'421957536BABCB44B36527C9764C666A', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 3, NULL, NULL, 2, NULL, 0, 'To cook the spinach, place it and two tablespoons water in a microwave-safe, lidded container and cook on high until just hot, about four minutes depending on your microwave.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F41CF4AE15CBB24589DAE692DC20B2FD', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 4, NULL, NULL, 2, NULL, 0, 'When cool enough to handle, place spinach in a fine-mesh strainer and press out all the liquid you can with a fork or spoon, or with your fingers.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE4DC4A098B799458DB794D34BD35B1C', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 5, NULL, NULL, 2, NULL, 0, 'While crust bakes and spinach cooks, warm olive oil in a medium-sized frying pan over medium heat. Add onion and cook, stirring occasionally, until softened, about five minutes. Off the heat, add drained spinach and 1/4 teaspoon salt to pan and use a fork to mix the spinach and onion together thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B598C1FA66CF07449B059EDC1772774D', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 6, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat eggs well with a fork. Add cream, milk, remaining 1/2 teaspoon salt, nutmeg, cayenne, and black pepper and mix until well-combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1661590A66A494492FD72752C60E9BD', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 7, NULL, NULL, 2, NULL, 0, 'Gently spread spinach mixture and cheese evenly over bottom of crust.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8123810AEB356458079221E5486E900', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 8, NULL, NULL, 2, NULL, 0, 'Open oven and pull out rack enough to set the partially filled crust (still on rimmed baking sheet) onto the rack. Carefully pour the egg mixture into the crust. It will be very full. Gently slide rack back into place.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFD5CCCDD52E5143A6EEE904B3214857', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 9, NULL, NULL, 2, NULL, 0, 'Bake until filling is just set and still slightly jiggly but not sloshy in the center, 30-40 minutes. The center will firm up as it cools, and pulling the quiche out at this stage will prevent it from getting tough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'639FD5006BB1D84880AC6FEBCA111175', X'2A851B1BA8E7324BBB520CE9D1386381', X'2A851B1BA8E7324BBB520CE9D1386381', 10, NULL, NULL, 2, NULL, 0, 'Let cool for at least 10 minutes before serving, preferably longer. Quiche is even better the next day.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB14FAEF9A216447B381EF2FE3962C07', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Place crust (in foil pie plate) onto a rimmed baking sheet and defrost on countertop for 10 minutes. Prick crust all over the bottom and sides with a fork. (See note.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCA643E8B8BDA34E8EEF7E46FE482C0D', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 1, NULL, NULL, 2, NULL, 0, 'Bake crust for 10 minutes, then let cool for 10 minutes. (If directions for “blind baking” on your crust’s package vary substantially, you can follow those, but this method should work for most frozen crusts.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'276668C68A1CFD44878AEBFB561CA7B1', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 2, NULL, NULL, 2, NULL, 0, 'Reduce oven temperature to 325°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF588FF03E906346A60D5F1F8C6184BB', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 3, NULL, NULL, 2, NULL, 0, 'While crust bakes, set a medium-sized frying pan over medium heat. Add bacon and cook, stirring frequently, until browned and crisped to your liking. Remove bacon from pan and drain on paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'572CFEFEDA3EC64FBC748F1DA4737591', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 4, NULL, NULL, 2, NULL, 0, 'Pour off all but about one tablespoon bacon fat from pan and replace pan over medium heat. Add onion and cook, stirring occasionally, until softened, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF18CB093C62B647B1740D15F8139B13', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 5, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat eggs well with a fork. Add cream, milk, salt, nutmeg, cayenne, and black pepper and mix until well-combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B220BF38546595499E8CD762050367B2', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 6, NULL, NULL, 2, NULL, 0, 'Gently spread onion, bacon, and cheese evenly over bottom of crust.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58801BA055676B4DB8660E03CACF9D50', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 7, NULL, NULL, 2, NULL, 0, 'Open oven and pull out rack enough to set the partially filled crust (still on rimmed baking sheet) onto the rack. Carefully pour the egg mixture into the crust. It will be very full. Gently slide rack back into place.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80673E9C4FEEFE43A7201F7AD44E4457', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 8, NULL, NULL, 2, NULL, 0, 'Bake until filling is just set and still ever so slightly jiggly (but not sloshy) in the center, 30-40 minutes. The center will firm up as it cools, and pulling the quiche out at this stage will prevent it from getting tough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44603F77B6B89B48B736921FBF5A335E', X'499C549AFA8D244999EAC372A2FD1E0F', X'499C549AFA8D244999EAC372A2FD1E0F', 9, NULL, NULL, 2, NULL, 0, 'Let cool for at least 10 minutes before serving, preferably longer. Quiche is even better the next day.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BFB1A77AA939D49903C7B73EE4A1755', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 0, NULL, NULL, 2, NULL, 0, 'Place the flours into the bowl of a food processor fitted with the blade.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97B92A5FC90EAA439C11CD446A78B6E9', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 1, NULL, NULL, 2, NULL, 0, 'Add the butter and pulse until the mixture resembles coarse sand with some larger pea-sized pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0421051F4DBAA2489ABCE2CEFE9DDC60', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 2, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons of the water and process for a moment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C27E0B13A6B1D46B10C2D2090A6A7D0', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 3, NULL, NULL, 2, NULL, 0, 'Test to see if the dough holds together when you squeeze a bit with your hand. If not, add the remaining water and process again.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12637A0B82E9AE41A1AC775A0540BC5F', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 4, NULL, NULL, 2, NULL, 0, 'Use your hands to quickly form the dough into a ball. Then flatten into a disk between two approximately 14-inch square pieces of parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48D2B380459C6F4B9482860C4720F6AB', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 5, NULL, NULL, 2, NULL, 0, 'Wrap in the parchment and refrigerate for at least an hour (up to 2 days) before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EC6BF07A571D14FB49765B73AA188E6', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 6, NULL, NULL, 2, NULL, 0, 'Remove dough from fridge about 20 minutes before you want to roll it out. Usually it''s fine to pull the dough from the fridge when you start to gather the ingredients for your tart filling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC337D3A8C02A3468DCF4D43EC53AEC3', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle a little bit of flour on each side of the dough and then replace the parchment. Using a rolling pin and turning the dough a quarter-turn clockwise every so often, roll the dough out between the parchment into a 14-inch circle (which should be about the same size as the parchment sheets themselves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0165AB3D26CDD24FB8EA5ACB14632196', X'636C17A7ABAB684481F4F9B7F9092243', X'636C17A7ABAB684481F4F9B7F9092243', 8, NULL, NULL, 2, NULL, 0, 'Remove the top sheet of parchment and slide the dough (still on the bottom sheet of parchment) onto a pizza pan or baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2D9A12C49CBD549BBEB4463B42554E9', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400° F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D814D0674A54A4F8AAD9C7CF5DEE4EC', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 1, NULL, NULL, 2, NULL, 0, 'Slice off and discard roots and dark green parts from leeks. Slice white and light green parts in half lengthwise and then cut each length crosswise into thin half-moons. Wash and dry very well in a salad spinner to remove all grit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94FAE6E795686240BA9229FEB50D7BFB', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 2, NULL, NULL, 2, NULL, 0, 'Melt butter over medium heat in a medium nonstick skillet. Add leeks and a good pinch of salt and cook, stirring frequently, until tender, about 5 minutes. Adjust heat if necessary to prevent browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'545C187C961B754EBEA0EB41DA8C2CC3', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 3, NULL, NULL, 2, NULL, 0, 'After rolling out the dough as directed in the savory tart dough recipe, brush it with the olive oil, leaving a 1 1/2-inch border around the outside. (In a few minutes you''ll fold this part over to form a ring of crust around the ingredients.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84E4019AEA341F42BA9FA6B4990C545C', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 4, NULL, NULL, 2, NULL, 0, 'Spread the ricotta evenly over the dough, still leaving a plain 1 1/2-inch border as you add all ingredients. Sprinkle half the pecorino evenly over ricotta. Lay asparagus over cheeses. (See note if your asparagus aren''t pencil-thin). Sprinkle with remaining pecorino.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBDF796273740F439A22E713CF6F530D', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 5, NULL, NULL, 2, NULL, 0, 'Working in 6 to 8 sections, fold the outer ring of dough over the filling to form a crust, pressing gently to adhere the sections together. I like to use the parchment paper to lift and press the dough rather than handling it directly, especially if it''s gotten a little soft. In any case, don''t worry about making it perfect. Rustic is charming, you hear?');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CF71B26BD8E6A49BFC2A8736B82E228', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 6, NULL, NULL, 2, NULL, 0, 'In a small bowl, beat together the egg with the milk or cream. Brush egg wash over outer ring of crust.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22AEC2B25E627F4FBFFB0E0720CE2F46', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 7, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes, until nicely browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B60E9DFA77C91A438938EF13C50B8F66', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 8, NULL, NULL, 2, NULL, 0, 'While tart is hot, cut or tear burrata into pieces and distribute over filling. Drizzle with balsamic glaze and sprinkle with flaky salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF469C348D0ED342AD5F23A81F17307C', X'6469731B098ED54391048152826BEE7B', X'6469731B098ED54391048152826BEE7B', 9, NULL, NULL, 2, NULL, 0, 'Cut into wedges and serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E28408BB0AED564C8D084075B947E972', X'B63A9037230E184FAC58F375A245234B', X'B63A9037230E184FAC58F375A245234B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F. Unfold the puff pastry onto a lightly floured cutting board. Using a 5-inch cookie cutter (or an overturned bowl and a small, sharp knife), cut three 5-inch circles from each sheet of dough. (If this isn''t possible, you can roll out the puff pastry gently with a floured rolling pin just until it''s big enough to accommodate the circles.) Place the rounds on a parchment-lined cookie sheet. Press a 3-inch cookie cutter into the center of each round, stopping 3/4 of the way down so that the outer and inner portions are still attached at the bottom. This will allow the outer ring to rise around the center. Pierce the inner section of each round all over with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BADDD575773BF3499DF1675C182E4A96', X'B63A9037230E184FAC58F375A245234B', X'B63A9037230E184FAC58F375A245234B', 1, NULL, NULL, 2, NULL, 0, 'Place the goat cheese, powdered sugar, lemon juice and salt in a small bowl. Microwave for 30 seconds or until soft but not melted. Stir to combine well, then divide and spread over the inner section of each round. Top with overlapping apple slices, making sure to stay within the inner section of each round.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15AE45BB63AEE049BAFC3E712E3E658D', X'B63A9037230E184FAC58F375A245234B', X'B63A9037230E184FAC58F375A245234B', 2, NULL, NULL, 2, NULL, 0, 'Combine the butter and honey in a small bowl and microwave for 30 seconds, or until the butter is almost melted. Stir to combine and complete the melting. (Be careful, as the honey gets hot surprisingly quickly.) Brush some honey butter over the entirety of each round. Dust lightly with ground allspice. Bake for 25 minutes, or until the apples are tender and the pastry is nicely browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA4FC7C3E1B6564183866D54FF4439FD', X'B63A9037230E184FAC58F375A245234B', X'B63A9037230E184FAC58F375A245234B', 3, NULL, NULL, 2, NULL, 0, 'Let the pastries cool slightly, then drizzle with additional honey if desired. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A73DF87B3121B4BA5FA4DDD752208AC', X'B2297278566BAF4FB13A1CECDA5FDF1E', X'B2297278566BAF4FB13A1CECDA5FDF1E', 0, NULL, NULL, 2, NULL, 0, 'Place the butter, powdered sugar and egg yolks in the bowl of a food processor fitted with the blade. Pulse until combined but still speckled with butter. Add the flour and run the machine just until the dough comes together when you pinch it between your fingers (see photo above). Turn dough out onto a large piece of parchment, knead a few times to bring it all together, and pat it into a disc shape. Wrap well in parchment and chill for about half an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0DB416B1B0CA419B81591635070CE1', X'B2297278566BAF4FB13A1CECDA5FDF1E', X'B2297278566BAF4FB13A1CECDA5FDF1E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Remove dough from the fridge and let it rest on the counter for 15 minutes. Sprinkle a bit of flour on your work surface, and roll out the dough with a rolling pin to an approximately 12-inch circle. Transfer the dough very carefully to a 10-inch tart pan, pressing on the dough lightly so it sits snugly against the bottom and sides of the pan. Prick the bottom of the shell all over with a fork. Place a piece of parchment paper over the shell, making sure to cover the edges. Spread plenty of dried beans over the parchment, covering the whole bottom of the tart shell. Bake for 15 minutes this way, then remove the parchment and beans and return the shell to the oven for an additional 10 minutes. Remove from the oven and let cool for at least 10 minutes before filling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11E1D9004C76F648B30D8BB4E5F95256', X'801681F6476173448DAF1D199032CA91', X'801681F6476173448DAF1D199032CA91', 0, NULL, NULL, 2, NULL, 0, 'Place the butter, powdered sugar and egg yolks in the bowl of a food processor fitted with the blade. Pulse until combined but still speckled with butter. Add the flour and run the machine just until the dough comes together when you pinch it between your fingers (see photo above). Turn dough out onto a large piece of parchment, knead a few times to bring it all together, and pat it into a disc shape. Wrap well in parchment and chill for about half an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E4057578FABA4BA5AA392495B9029D', X'801681F6476173448DAF1D199032CA91', X'801681F6476173448DAF1D199032CA91', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Remove dough from the fridge and let it rest on the counter for 15 minutes. Sprinkle a bit of flour on your work surface, and roll out the dough with a rolling pin to an approximately 12-inch circle. Transfer the dough very carefully to a 10-inch tart pan, pressing on the dough lightly so it sits snugly against the bottom and sides of the pan. Prick the bottom of the shell all over with a fork. Place a piece of parchment paper over the shell, making sure to cover the edges. Spread plenty of dried beans over the parchment, covering the whole bottom of the tart shell. Bake for 15 minutes this way, then remove the parchment and beans and return the shell to the oven for an additional 10 minutes. Remove from the oven and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'648679EBCE8ECC40889C2CEA14F4A706', X'3FD0A51DCC3EF44DABC87B043F3345D1', X'3FD0A51DCC3EF44DABC87B043F3345D1', 0, NULL, NULL, 2, NULL, 0, 'Place the flours and the salt in the bowl of a food processor fitted with the blade attachment and pulse to combine. Add the butter and pulse until the largest pieces are the size of small peas. Sprinkle four tablespoons of the ice water onto the dough. Pulse until the dough begins to hold together. Add the additional tablespoon of water and continue to pulse if the dough is otherwise too dry to hold together. Turn the dough out onto a piece of waxed paper and shape into a disc by pressing quickly and firmly with your hands, repairing any cracks as you go. Wrap the dough in the waxed paper and refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DE29A9FFFC9A74894D05BB5C86DFF09', X'3FD0A51DCC3EF44DABC87B043F3345D1', X'3FD0A51DCC3EF44DABC87B043F3345D1', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F. Roll out the dough into a 12-inch-diameter circle about 1/4-inch thick. Press into the bottom and sides of a fluted 10-inch tart pan with a removable bottom. Roll your rolling pin over the top of the pan to remove any excess dough. Prick the bottom of the dough all over with a fork, then line the dough with parchment paper and fill with dried beans. Place the tart pan on a baking sheet. Bake for 10 minutes, then remove the parchment and beans and bake for 7 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D06AF7353669C84994E4872311BCC8FA', X'3FD0A51DCC3EF44DABC87B043F3345D1', X'3FD0A51DCC3EF44DABC87B043F3345D1', 2, NULL, NULL, 2, NULL, 0, 'While the dough bakes, make the filling. Place the scapes, eggs, ricotta, lemon thyme, salt and pepper in a medium bowl. Beat with a fork until well combined. Pour the filling into the partially baked tart shell and return the whole thing to the oven for 30 minutes, until the filling is set. Cool to warm or room temperature before slicing and serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57EABDCD17B638409585E98553231A93', X'D8F5834769C6414DA6C75DBF8AFAA504', X'D8F5834769C6414DA6C75DBF8AFAA504', 0, NULL, NULL, 2, NULL, 0, 'Make the crust: Sift the flour, sugar and salt together into the bowl of a food processor fitted with the blade attachment. Add the butter and pulse until the dough has a sandy texture, with no large lumps of butter. Add the egg and water mixture and pulse until the dough begins to form a ball. Add ice water by the teaspoonful, pulsing after each addition, if the dough is too dry to come together. Turn the dough out onto a lightly floured work surface, divide into four equal pieces, and shape into four small discs. Wrap and refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05759A9DEE181C43A66D05C79C26C01E', X'D8F5834769C6414DA6C75DBF8AFAA504', X'D8F5834769C6414DA6C75DBF8AFAA504', 1, NULL, NULL, 2, NULL, 0, 'Roll out and bake the crust: Preheat the oven to 400°F. On a lightly floured work surface, roll out the dough discs one by one into 7-inch circles. Carefully press each circle into a 5-inch mini tart pan with a removable bottom. Place the pans on a baking sheet and refrigerate for 15 minutes. Then prick the bottoms of the tart shells all over with a fork, line with parchment, and fill with pie weights or dried beans. Bake for about 10 minutes, until edges look chalky and white. Remove the parchment and beans, lower the oven to 350°F, and bake until cooked through and golden brown, about 25 minutes more. Cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B33D46F05066A84288B4112BFE0A5990', X'D8F5834769C6414DA6C75DBF8AFAA504', X'D8F5834769C6414DA6C75DBF8AFAA504', 2, NULL, NULL, 2, NULL, 0, 'Make the filling and assemble the tarts: In the bowl of a stand mixer, beat together the cream cheese and muscovado sugar until smooth and lightened in color, about 3 minutes. Beat in the sour cream, lemon juice, and vanilla extract. Spread 1/4 of the filling into each tart shell. Top with berries, cut sides down. Sprinkle lightly with muscovado sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2A4291844D27646AC487C5B282B7D85', X'9418DAB228739848A23942487ADFBFD8', X'9418DAB228739848A23942487ADFBFD8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Combine the flour, butter and salt in the bowl of a food processor fitted with the blade. Pulse to blend until most of the mixture looks like coarse meal, with some larger pieces of butter about the size of peas.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A55845CD64C8DC4B80176BE4D4683D76', X'9418DAB228739848A23942487ADFBFD8', X'9418DAB228739848A23942487ADFBFD8', 1, NULL, NULL, 2, NULL, 0, 'With the motor running, add the ice water through the feed tube and blend very briefly to incorporate. Pick up a small handful of the dough and squeeze gently. If the dough holds together, turn it out onto a clean, dry work surface. If not, add more water by the teaspoonful and pulse just until it does. Do not overwork the dough, or it will get tough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6685562D18BDB4FBC3BCE22C0C19137', X'9418DAB228739848A23942487ADFBFD8', X'9418DAB228739848A23942487ADFBFD8', 2, NULL, NULL, 2, NULL, 0, 'Using the heel of your hand, smear the dough forward on the work surface in about 8 sections to distribute the butter. Working quickly, gather the dough into a disc shape, wrap in plastic wrap and refrigerate for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'636C566370D37341833A5CE29FCF047D', X'9418DAB228739848A23942487ADFBFD8', X'9418DAB228739848A23942487ADFBFD8', 3, NULL, NULL, 2, NULL, 0, 'Remove the dough from the fridge to rest for a few minutes. On a lightly floured work surface or two overlapping sheets of parchment paper, roll out the dough to a scant 1/4-inch thickness. It should make a circle big enough to fit the bottom and sides of the cake pan with substantial overhang. Place the cake pan on a baking sheet and line with parchment paper. Ease the dough into the pan, making sure it lines the bottom and sides without gaps. Cut away and reserve a few pieces from the overhang to patch any holes that arise during blind baking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E263EF406C239E43B40E4BC0F29A1DE5', X'9418DAB228739848A23942487ADFBFD8', X'9418DAB228739848A23942487ADFBFD8', 4, NULL, NULL, 2, NULL, 0, 'Line the dough with a sheet of parchment paper and fill with dried beans or pie weights. Bake for 30 minutes. Remove the beans and parchment, patch any holes with some of the reserved dough, and bake for about 15 minutes more, until lightly browned. If any holes remain, patch with the remaining reserved dough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EA50BE61CF7B84B943606897693767D', X'647A4C133DC64143B3412C8E731ABF80', X'647A4C133DC64143B3412C8E731ABF80', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the middle. Cut the squash in half lengthwise. Scoop out seeds and any stringy flesh in the middle.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96E68F47208C7B4F913D18FEF6AEBE41', X'647A4C133DC64143B3412C8E731ABF80', X'647A4C133DC64143B3412C8E731ABF80', 1, NULL, NULL, 2, NULL, 0, 'Place halves cut-sides down on a parchment- or silpat-lined rimmed baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BB1A9C8A09F9046A875944D417EE847', X'647A4C133DC64143B3412C8E731ABF80', X'647A4C133DC64143B3412C8E731ABF80', 2, NULL, NULL, 2, NULL, 0, 'Roast until just tender, about 40 minutes depending on size of squash.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E911D4AC9CEB04DA1D7F79F410C0A1E', X'647A4C133DC64143B3412C8E731ABF80', X'647A4C133DC64143B3412C8E731ABF80', 3, NULL, NULL, 2, NULL, 0, 'Once cool enough to handle, use a fork to shred the flesh of the squash, leaving a border of about 1/4 inch around the sides and bottom. Diners can shred and eat this bit at mealtime, but for now it will serve as a nice barrier for the filling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9CACF98CFC66945ADDF45D2AA2A1E93', X'647A4C133DC64143B3412C8E731ABF80', X'647A4C133DC64143B3412C8E731ABF80', 4, NULL, NULL, 2, NULL, 0, 'In a bowl, mix together the marinara sauce, tomato paste, half the parmesan, and plenty of freshly ground black pepper. Divide the mixture evenly between the squash halves and mix it thoroughly with the shredded flesh.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E204CFDC31788408260F1D5C752300E', X'647A4C133DC64143B3412C8E731ABF80', X'647A4C133DC64143B3412C8E731ABF80', 5, NULL, NULL, 2, NULL, 0, 'Top the boats with the mozzarella, the remaining parmesan, and some more pepper. Return to oven for 15 minutes, until filling is melted and bubbly. If you''d like a bit o browning on top, you can run the squash under the broiler for a minute or to before removing from the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D9AC25FAA043244B9E994833BA35015', X'647A4C133DC64143B3412C8E731ABF80', X'647A4C133DC64143B3412C8E731ABF80', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle with scallions or other garnish if you like, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EDF7408164FEC4884772FAFED1CB74A', X'22A4021ADB5C204586C68B5F9627D704', X'22A4021ADB5C204586C68B5F9627D704', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CBEC1D32533A54193160DFD719C8D12', X'22A4021ADB5C204586C68B5F9627D704', X'22A4021ADB5C204586C68B5F9627D704', 1, NULL, NULL, 2, NULL, 0, 'Warm the olive oil (or melt the vegan butter) over medium-high heat in a large frying pan. Add onion and celery and sprinkle with 1/4 teaspoon of the salt. Cook, stirring occasionally, until beginning to soften and turn translucent, about 5 minutes. Adjust heat as necessary to prevent browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D718190736A394CB4916999D358D235', X'22A4021ADB5C204586C68B5F9627D704', X'22A4021ADB5C204586C68B5F9627D704', 2, NULL, NULL, 2, NULL, 0, 'Add apple, chestnuts, thyme, and sage, and continue cooking until apple has softened, about 5 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB0791EEA3F5C3478E07B4073F7B98EB', X'22A4021ADB5C204586C68B5F9627D704', X'22A4021ADB5C204586C68B5F9627D704', 3, NULL, NULL, 2, NULL, 0, 'Place bread cubes into a large mixing bowl and scrape the contents of the frying pan overtop. Pour in the broth. Sprinkle with remaining 1/4 teaspoon salt and a few generous grinds of pepper. Mix gently until everything is well-distributed, then let the mixture sit for a few minutes so the bread cubes absorb the wet ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EBDF0CF0EC17A46B74EB90A72A4384D', X'22A4021ADB5C204586C68B5F9627D704', X'22A4021ADB5C204586C68B5F9627D704', 4, NULL, NULL, 2, NULL, 0, 'Transfer stuffing to a large baking dish such as an extra-deep 9x13-inch pan or a 5-quart braiser. Bake uncovered for 45 minutes, until crisp on top and tender inside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC8037F002F3A642866E6A626A19F8FF', X'9C8142BA83BA8C4D98B6E1085BE5C029', X'9C8142BA83BA8C4D98B6E1085BE5C029', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85158BA6D3058342A4F3EB8243248BB5', X'9C8142BA83BA8C4D98B6E1085BE5C029', X'9C8142BA83BA8C4D98B6E1085BE5C029', 1, NULL, NULL, 2, NULL, 0, 'Boil pasta shells in well salted water according to package instructions. Drain and toss with a teaspoon of the olive oil to prevent sticking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0716A0BBEB863E4BAB1E499CCF885BBC', X'9C8142BA83BA8C4D98B6E1085BE5C029', X'9C8142BA83BA8C4D98B6E1085BE5C029', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, warm the tablespoon of olive oil over medium-high heat in a 12-inch nonstick skillet. Add the onion, garlic and shiitakes along with a big pinch of salt and cook, stirring occasionally, until softened, 5-10 minutes. Add spinach and cook until wilted. Place spinach mixture into a large bowl and stir in ricotta, half the pecorino, the salt and a few good grinds of pepper. Crack the egg into the bowl, beat well with a fork and then stir into mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BAAE6E5A2CC9F4AB6AA908EB44BED53', X'9C8142BA83BA8C4D98B6E1085BE5C029', X'9C8142BA83BA8C4D98B6E1085BE5C029', 3, NULL, NULL, 2, NULL, 0, 'In a standard 9x13-inch baking dish, spread 1/2 cup or so of the tomato sauce. Fill each pasta shell with a couple of tablespoons of the vegetable-cheese mixture and place in a single, snug layer into the baking dish, continuing until all shells and filling are used. Pour remaining tomato sauce over shells and top with mozzarella and remaining pecorino. Bake for about half an hour, until hot and bubbly. Sprinkle with parsley and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC57EE2218D5B649AC5F0680A3B2A8A1', X'603B1718433ED241A815AC746ED4D047', X'603B1718433ED241A815AC746ED4D047', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Cut potatoes into cubes of about 3/4-inch. Place in a 12-inch cast iron skillet. Toss with the olive oil, salt, a few good grinds of pepper, the garlic, rosemary and thyme. Roast for 30 minutes, stirring after 20.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40C132CE5366364DBA7EB61A9F79F3ED', X'603B1718433ED241A815AC746ED4D047', X'603B1718433ED241A815AC746ED4D047', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, remove stems from kale and tear leaves into bite-sized pieces. After 30 minutes, stir kale into pan and roast 10 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32A0008E0B49C54598D2F567EC2D63C5', X'603B1718433ED241A815AC746ED4D047', X'603B1718433ED241A815AC746ED4D047', 2, NULL, NULL, 2, NULL, 0, 'Remove rosemary and thyme sprigs. Sprinkle feta over skillet contents, then crack in eggs. Bake for about 15 minutes, checking after 10, until eggs are set to your liking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68239F3C4C26AB4F84FFC7AF12DBDE62', X'603B1718433ED241A815AC746ED4D047', X'603B1718433ED241A815AC746ED4D047', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle with parsley and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8B605DF632D0A4FB9DF886C688DF23F', X'C8371997E98E464695D47EB9AC4EF2FE', X'C8371997E98E464695D47EB9AC4EF2FE', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in the braiser or skillet over medium-high heat. Add the onion, peppers, and shiitakes all at once, along with 1/2 teaspoon of the salt. Cook, stirring from time to time, for about 10 minutes, until the vegetables have softened, browned a bit, and decreased in volume by at least half. Remove from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFA13D484CA6724F8A43B208AD7115E1', X'C8371997E98E464695D47EB9AC4EF2FE', X'C8371997E98E464695D47EB9AC4EF2FE', 1, NULL, NULL, 2, NULL, 0, 'While the other vegetables cook, place the defrosted broccoli on a thick layer of paper towels and cover with another thick layer. When the veggies in the skillet have cooked as noted above, press down gently on the paper towels to remove moisture from the broccoli. The add broccoli to the skillet and stir to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89C556F8F2C75E40B45A460F6F47B7C2', X'C8371997E98E464695D47EB9AC4EF2FE', X'C8371997E98E464695D47EB9AC4EF2FE', 2, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a large mixing bowl and beat well with a fork. Stir in milk, nutmeg, cayenne, and a few good grinds black pepper. Add bread cubes and stir to moisten. Then stir in all the vegetables, half the gruyere and half the parmesan or pecorino. Pour the whole mixture back into the braiser or into the baking pan and sprinkle with the remaining cheese. Cover and refrigerate overnight or for at least a few hours before proceeding. Remove from fridge about an hour before baking if possible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7F31A1F74465D459ADB7F3730D3D6B0', X'C8371997E98E464695D47EB9AC4EF2FE', X'C8371997E98E464695D47EB9AC4EF2FE', 3, NULL, NULL, 2, NULL, 0, 'An hour before serving time, preheat oven to 375°F with a rack in the center. Bake uncovered for about 40 minutes, until puffed and golden. Serve hot or even at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DEF370757E6534591814D803A3D1B12', X'8A1347610028184598BE024CDEABD720', X'8A1347610028184598BE024CDEABD720', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Pierce the squash in several places with a small, sharp knife and place in a roasting pan. Bake for about an hour, until flesh is tender enough to insert a fork easily. When cool enough to handle, cut in half. Scoop out and discard seeds and stringy center. Then scoop the flesh out of the skin and into the bowl of a food processor fitted with the blade. Add broth and puree until smooth, stopping to scrape down sides as necessary. This process should yield somewhere in the neighborhood of 4 cups of squash puree, but don’t stress about it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C18379A93E4E443BC8AD63BA1CDE316', X'8A1347610028184598BE024CDEABD720', X'8A1347610028184598BE024CDEABD720', 1, NULL, NULL, 2, NULL, 0, 'In a medium pot with a heavy bottom, melt the butter over medium-high heat. Add the thyme and sage and cook for one minute. Whisk in flour and cook for another minute or so, until bubbling substantially. Whisk in milk all at once. Bring to a boil over high heat, then reduce heat and simmer, stirring frequently, for five minutes or so, until slightly thickened. Off the heat, whisk in 1 1/2 teaspoons of the salt along with the nutmeg and cayenne. Then whisk in the squash puree until it all becomes a smooth sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91ADA8652752F748BB03535CE4F1F5E0', X'8A1347610028184598BE024CDEABD720', X'8A1347610028184598BE024CDEABD720', 2, NULL, NULL, 2, NULL, 0, 'Warm the olive oil in a 12-inch nonstick skillet over medium heat. Add onion, mushrooms and garlic along with remaining 1/2 teaspoon salt and cook, stirring occasionally, until mushrooms have reduced in volume by about half. Add radicchio and cook, stirring frequently, until wilted. Off the heat, stir in the balsamic vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0490B084E8AEC24296A7B313D70D1DFA', X'8A1347610028184598BE024CDEABD720', X'8A1347610028184598BE024CDEABD720', 3, NULL, NULL, 2, NULL, 0, 'Lightly brush a 9x13-inch pan with oil. Place a thin layer of béchamel sauce at the bottom of the pan, followed by a layer of lasagna noodles. Top with half the mozzarella, half the mushroom mixture, 1/3 of remaining béchamel, and 1/4 cup of pecorino. Repeat with another layer of noodles, mozzarella, mushroom mixture, sauce and pecorino. Finally, add one more layer of noodles and top with remaining sauce and pecorino.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA91F17CFD55BC48B5DAC4831C37408C', X'8A1347610028184598BE024CDEABD720', X'8A1347610028184598BE024CDEABD720', 4, NULL, NULL, 2, NULL, 0, 'Bake for about 45 minutes, until lightly browned and bubbly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'521C5F2333C6BA478181F49752744AEF', X'19E68FAECE4B5E49B0BBC0C79C138250', X'19E68FAECE4B5E49B0BBC0C79C138250', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'104D86356DB1C44DBA0C3A7D96DAFF8B', X'19E68FAECE4B5E49B0BBC0C79C138250', X'19E68FAECE4B5E49B0BBC0C79C138250', 1, NULL, NULL, 2, NULL, 0, 'Peel the Yukon Gold and sweet potatoes and cut into 1-inch pieces. Place in a medium pot and cover by an inch with cold, salted water. Bring to a boil, then lower the heat and simmer until tender, about 15 minutes. Drain well, then return to pot and mash with 2 tablespoons of the olive oil and a few good grinds of pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4936656940FF86438B06437A0EF8FE04', X'19E68FAECE4B5E49B0BBC0C79C138250', X'19E68FAECE4B5E49B0BBC0C79C138250', 2, NULL, NULL, 2, NULL, 0, 'Heat one tablespoon of the olive oil in a wide skillet over medium heat. If you have a shallow Dutch oven or other attractive stovetop-to-oven cookware, you can use it from this point and bring it all the way to the table. Add the onion, carrot, celery, about 3/4 of the chopped garlic, the ground coriander and thyme sprigs. Cook, stirring from time to time, until softened, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'843785299A0B5940810A04C84DED016F', X'19E68FAECE4B5E49B0BBC0C79C138250', X'19E68FAECE4B5E49B0BBC0C79C138250', 3, NULL, NULL, 2, NULL, 0, 'While the onion mixture cooks, chop the mushrooms and sun-dried tomatoes together into a rough dice. Add to the pan along with the vinegar and cook, stirring occasionally, for 10 minutes. Add the wine, turn up the heat to bring to a boil for a minute. Add the stock, lentils and chickpeas (along with some more salt if those ingredients are unsalted) and simmer briskly until the stock creates a slightly thickened sauce, 5 to 10 minutes more. Remove the thyme stems, stir in the chopped parsley and taste for salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B6EF07FFF979B4D9CC93E8C4264EDBB', X'19E68FAECE4B5E49B0BBC0C79C138250', X'19E68FAECE4B5E49B0BBC0C79C138250', 4, NULL, NULL, 2, NULL, 0, 'Transfer to a baking dish (or don’t if you’ve used oven-proof cookware). Spread the mashed potatoes overtop, creating some texture with the back of a spoon.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC756AFC27D7294094D43348F18D0557', X'19E68FAECE4B5E49B0BBC0C79C138250', X'19E68FAECE4B5E49B0BBC0C79C138250', 5, NULL, NULL, 2, NULL, 0, 'Pick the rosemary needles off the stems into a small bowl. Mix in the bread crumbs, remaining chopped garlic, lemon zest and remaining tablespoon of olive oil. Sprinkle the seasoned crumbs over the mashed potatoes and slide the whole thing into the oven for 10 to 20 minutes, until hot and bubbly. Finish under the broiler for two minutes, until breadcrumbs are lightly golden.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA741C087DCBF74E88925525849872BD', X'457299EEB803C74CB9DA359B00EF423A', X'457299EEB803C74CB9DA359B00EF423A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Spray a 9- or 10-inch loaf pan with cooking spray and line with parchment that overhangs slightly on two sides.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A53A073B67F25841B91A50D17A900310', X'457299EEB803C74CB9DA359B00EF423A', X'457299EEB803C74CB9DA359B00EF423A', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a large skillet over medium-high heat. Add the onion and a sprinkle of the salt and cook, stirring occasionally, until softened, about 5 minutes. Add the minced mushrooms and another sprinkle of the salt, raise heat to high, and cook until they have released their juices and reabsorbed them, about 7 minutes. Add garlic, thyme, oregano, basil, tarragon and sage and cook 2 minutes more. Deglaze the pan with the wine, scraping up any browned bits, and cook until liquid is absorbed, about 1 minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D399EEC00E01484E93D3F4C3F2450409', X'457299EEB803C74CB9DA359B00EF423A', X'457299EEB803C74CB9DA359B00EF423A', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, beat together the eggs and cottage cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20B93C8417475E488F878B81874977C8', X'457299EEB803C74CB9DA359B00EF423A', X'457299EEB803C74CB9DA359B00EF423A', 3, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, toss together the brown rice, walnuts and cashews. Stir in the egg mixture, then add the mushroom mixture, cheeses, parsley, remaining salt and pepper. Mix well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FF74668B41CB34FA126ED7C2E6370CD', X'457299EEB803C74CB9DA359B00EF423A', X'457299EEB803C74CB9DA359B00EF423A', 4, NULL, NULL, 2, NULL, 0, 'Spoon mixture into loaf pan and smooth top. If desired, decorate with a few mushroom slices or walnut halves. Place loaf pan on a rimmed baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C289A33F1959A44BBFA722AF777D1B7', X'457299EEB803C74CB9DA359B00EF423A', X'457299EEB803C74CB9DA359B00EF423A', 5, NULL, NULL, 2, NULL, 0, 'Bake until firm, about 60 minutes. Let cool in the pan for 10 minutes before removing to a platter to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D6ECF852732640BBD6272EBABBA614', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 200°F. If you have a convection oven, go for it. You''ll need two racks, so, as they tell schoolchildren in England, be sensible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FCA2CE05D6CA04CBD8B93C17E507E2D', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 1, NULL, NULL, 2, NULL, 0, 'Cut the baguettes into 3/4-inch cubes and distribute between two rimmed sheet pans without crowding. Bake for about an hour, peeking in and maybe shaking the pans occasionally, until the cubes are dry all the way through. You can do this up to a few days in advance if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D703C6F410BCEF4EBFF7AEB38A604BC8', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 2, NULL, NULL, 2, NULL, 0, 'While the bread is drying, slice the white and light green parts of the leeks into thin half moons and wash thoroughly in several changes of water in a colander. (Leeks can be very sandy.) Cut the celery into a nice small dice — you''ll want it small so it cooks as quickly as the leeks. Dust off the mushrooms with a damp paper towel and slice, removing any tough stems.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20915BF3BC14154B888CA5DD72E904D2', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil over medium-low heat in a wide, reasonably nonstick frying pan. Add the leeks, celery, and salt, and cook, stirring from time to time, until starting to soften, about five minutes. If you suspect a hint of browning, reduce the heat. Add the mushrooms and cook until they''ve released their moisture and it''s mostly simmered away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DE31D3021EC684AA3F345D640AB9D9E', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 4, NULL, NULL, 2, NULL, 0, 'Add the chestnuts, soy sauce, wine, thyme, sage, raise heat to medium-high, and cook for three minutes more. Remove from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99B6ABFFC46EF64EAC7E2106503A31B3', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 5, NULL, NULL, 2, NULL, 0, 'Remove bread cubes from oven and raise heat to 375°F. Move a rack to the middle. Tip the bread cubes into a large mixing bowl. Add the contents of the frying pan and the stock, pepper, and parsley or chives. Toss gently to combine and allow the bread to soak up all the liquid. Taste and adjust seasoning if you like. (It''s vegan...shh...so no food-safety worries.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC1D7480565AE479ECC9301DC23A35F', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 6, NULL, NULL, 2, NULL, 0, 'Spoon the stuffing into a baking dish. Shallow 9x13 is good if you want more crisp bits, lidded 3-quart casserole is good if you like a higher proportion of custardy deliciousness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A6AB2A4F475D943B4318C4ACF5CC165', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', X'4D561C6B1EFD5B47A22BC1FF65B9FC37', 7, NULL, NULL, 2, NULL, 0, 'Bake, covered (with foil or the casserole lid) for 35 minutes. Then remove cover and bake 15 minutes more. Serve hot, with Umami Girl''s Best Vegetarian Gravy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B664BD07DA10B544B7171D2549698F07', X'29EA7257A180E04FB0E643D3AB476DA5', X'29EA7257A180E04FB0E643D3AB476DA5', 0, NULL, NULL, 2, NULL, 0, 'Butter the casserole dish or soufflé molds and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B65AE94B1FFEC141B7E15FCE931EB0E6', X'29EA7257A180E04FB0E643D3AB476DA5', X'29EA7257A180E04FB0E643D3AB476DA5', 1, NULL, NULL, 2, NULL, 0, 'Combine the milk, chopped herbs, and pepper in a medium pot with a heavy bottom. Set over medium-high heat until the milk just begins to simmer. Remove from heat and set aside to cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7891AC92E5B24845A4613E3E01D50D45', X'29EA7257A180E04FB0E643D3AB476DA5', X'29EA7257A180E04FB0E643D3AB476DA5', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, melt the butter in a wide skillet over medium heat. Then add the mushrooms and salt and cook, stirring occasionally, until tender, about 5 minutes. (If substituting leeks, cook, stirring occasionally, until tender, about 10 minutes. Reduce heat if necessary to prevent browning.) Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A69B31ACFCE57F45BA8C7288E4AB1D4F', X'29EA7257A180E04FB0E643D3AB476DA5', X'29EA7257A180E04FB0E643D3AB476DA5', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk the eggs, then whisk in the cooled milk mixture. Add the bread cubes, shredded cheese, and mushroom or leek mixture and stir until well combined. Set aside for 15 minutes, stirring occasionally, to let the bread absorb the liquid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FBF9665C9FF054C8960ED61C8D385F1', X'29EA7257A180E04FB0E643D3AB476DA5', X'29EA7257A180E04FB0E643D3AB476DA5', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat the oven to 375°F with a rack in the center (or one rung below center, if you have a teensy British oven).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'237784266FD14F4A8BDD4040E4A8C36D', X'29EA7257A180E04FB0E643D3AB476DA5', X'29EA7257A180E04FB0E643D3AB476DA5', 5, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into the casserole dish or soufflé molds. Bake until the top is golden brown, 30 to 35 minutes. Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A2AE9D6A8BE4E42ABD399DE3A91FB38', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375° F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DEEB0178E4EFC449C44D00836D4FF0D', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 1, NULL, NULL, 2, NULL, 0, 'Halve the acorn squash. Scoop out and discard the seeds. Place cut-side down on a rimmed baking sheet (lined with parchment for easy cleanup if you like) and roast until tender, about 40 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AE598090073D146B119D431E7FC5E48', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 2, NULL, NULL, 2, NULL, 0, 'Let cool slightly, then scoop out flesh into a medium bowl and mash with a fork. Stir in the ricotta cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39DE457FE5E4FE448567C8661317E5E6', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 3, NULL, NULL, 2, NULL, 0, 'While the squash is roasting, make the béchamel sauce. Melt the butter over medium heat in a medium pot. Add the onion, garlic, and sage and cook, stirring occasionally, until the onion begins to soften. Add the flour and cook, stirring constantly and scraping the bottom of the pot to prevent burning, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BA03765C11EF249BAE03B52D717E2BD', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 4, NULL, NULL, 2, NULL, 0, 'Add the milk along with the nutmeg, salt, and pepper. Raise the heat to high and bring to a boil, stirring constantly. Then reduce the heat to maintain a brisk simmer and cook, stirring occasionally, until the mixture has thickened enough to coat the back of a spoon, about 8 minutes. Remove from the heat and stir in one cup of the Parmigiano cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F99D674603555A459207744D04204649', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, lightly beat the eggs. Very slowly pour in about one cup of the sauce while whisking the eggs. (This tempers the eggs so they don''t scramble during the next step.) Then pour the tempered egg mixture into the pot with the sauce and stir to combine thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE33B330B1421843B841641FAE29237D', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 6, NULL, NULL, 2, NULL, 0, 'In a 9x13x2-inch pan, assemble the lasagna. Spread 1/4 of the sauce on the bottom of the pan. Lay five lasagna noodles on top, slightly overlapping them and breaking up the fifth noodle as necessary to cover the whole bottom. Top with half the squash mixture, followed by half of the shredded mozzarella.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06FC66DCA9A1B94FB927F5210D2EF359', X'C63AFFB152CF9E46AED37B524D7EAB7E', X'C63AFFB152CF9E46AED37B524D7EAB7E', 7, NULL, NULL, 2, NULL, 0, 'Repeat with a second layer of sauce, noodles, squash mixture, and mozzarella. Add a third layer of noodles and top with the remaining sauce. Sprinkle the remaining 1/2 cup Parmigiano cheese on top. Bake on the center rack until bubbly and slightly browned on top, about 35-40 minutes. Cool for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'999B5C1892F959488D790EEFF176998C', X'FDE3D955DE71C54B81F7509717CD23F6', X'FDE3D955DE71C54B81F7509717CD23F6', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375° Fahrenheit with a rack in the center. Grease the bottom and sides of the baking dish with 1 teaspoon olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8F668EB208A274B8B1D5E2DD169E80F', X'FDE3D955DE71C54B81F7509717CD23F6', X'FDE3D955DE71C54B81F7509717CD23F6', 1, NULL, NULL, 2, NULL, 0, 'Slice the chard leaves and stems crosswise into 1 ½-inch ribbons (for the leaves) and pieces (for the lower stems). Wash the chard and drain in a colander but do not dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'488D52099624C0498A088611FD223905', X'FDE3D955DE71C54B81F7509717CD23F6', X'FDE3D955DE71C54B81F7509717CD23F6', 2, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon of the olive oil over medium heat in your largest Dutch oven or wide, shallow pot with a tight-fitting lid. Add the onion and garlic and sauté for a few minutes, until softened but not browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1158D8DB7F41D24086C43ACBE1811E7F', X'FDE3D955DE71C54B81F7509717CD23F6', X'FDE3D955DE71C54B81F7509717CD23F6', 3, NULL, NULL, 2, NULL, 0, 'Add the chard, in batches if necessary, along with ½ teaspoon salt and a few grinds of black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'198F4C467C45DB4F8C643B7ABEC850E3', X'FDE3D955DE71C54B81F7509717CD23F6', X'FDE3D955DE71C54B81F7509717CD23F6', 4, NULL, NULL, 2, NULL, 0, 'Cover the pot tightly and steam the chard until it has wilted and reduced to less than half its original volume.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'430706E23C13C24D9C6C1251C42DC12E', X'FDE3D955DE71C54B81F7509717CD23F6', X'FDE3D955DE71C54B81F7509717CD23F6', 5, NULL, NULL, 2, NULL, 0, 'Remove the cover and boil for a few additional minutes to cook off as much liquid as possible. If there is still liquid in the pot after a few minutes, drain the chard in a colander set over a large bowl, pressing out the excess liquid. Reserve the liquid for another use – it makes a fabulous addition to broth-based soups.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81FF6BB79676BF4FBE3CD55662A6D21A', X'CB8FEE6F239CC54CB4C0D978350E48DA', X'CB8FEE6F239CC54CB4C0D978350E48DA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1DFA32C44C11343AB11E05220BF63C4', X'66848E934B153D42A6135334E1719E72', X'66848E934B153D42A6135334E1719E72', 0, NULL, NULL, 2, NULL, 0, 'Fill a cocktail shaker halfway with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63E35BFE1BE579478E076C495EBF3AFC', X'66848E934B153D42A6135334E1719E72', X'66848E934B153D42A6135334E1719E72', 1, NULL, NULL, 2, NULL, 0, 'Pour in gin, Cointreau, lime juice, and lemon juice, and dash in the bitters.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A2CE56B4407774BBD463046A2A2F5CB', X'66848E934B153D42A6135334E1719E72', X'66848E934B153D42A6135334E1719E72', 2, NULL, NULL, 2, NULL, 0, 'Shake well, until you’re not totally sure whether hell has frozen over.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49B6493E56522D49888DE602DA33BE81', X'66848E934B153D42A6135334E1719E72', X'66848E934B153D42A6135334E1719E72', 3, NULL, NULL, 2, NULL, 0, 'Strain into a cocktail or coupe glass.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB8506B6196BE045A89F1BBC08243E61', X'66848E934B153D42A6135334E1719E72', X'66848E934B153D42A6135334E1719E72', 4, NULL, NULL, 2, NULL, 0, 'Garnish with an orange twist, flaming if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A56416872B95E541B0523BC0209962B3', X'81FE637461791C42AA154D04B86DDB8B', X'81FE637461791C42AA154D04B86DDB8B', 0, NULL, NULL, 2, NULL, 0, 'Fill a mixing glass with plenty of ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'488082474C9D184EAA986748B0E3E6D2', X'81FE637461791C42AA154D04B86DDB8B', X'81FE637461791C42AA154D04B86DDB8B', 1, NULL, NULL, 2, NULL, 0, 'Pour in bourbon, Cynar, and syrup. Add three dashes of Angostura bitters.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8DAE47BBB277A40A6A661E68018E1AB', X'81FE637461791C42AA154D04B86DDB8B', X'81FE637461791C42AA154D04B86DDB8B', 2, NULL, NULL, 2, NULL, 0, 'Stir until very well-chilled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87D46F1DB5E0C143AE66F39001AFB3C3', X'81FE637461791C42AA154D04B86DDB8B', X'81FE637461791C42AA154D04B86DDB8B', 3, NULL, NULL, 2, NULL, 0, 'Strain into a coupe glass, garnish with star anise, and, serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7F2B4F36B63C143A738B1C77624AE34', X'98C2485A4C6365458571C82E09B38091', X'98C2485A4C6365458571C82E09B38091', 0, NULL, NULL, 2, NULL, 0, 'Place plenty of ice into a mixing glass.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'416494BA74B8FA4880F3C4889B7348A9', X'98C2485A4C6365458571C82E09B38091', X'98C2485A4C6365458571C82E09B38091', 1, NULL, NULL, 2, NULL, 0, 'Pour in the mezcal, vermouth, and Campari.');
