INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3680C78848816418241DA31CD0270F1', X'990846CC79CEB34FBF04C62D4F38BCFC', X'990846CC79CEB34FBF04C62D4F38BCFC', 0, NULL, NULL, 2, NULL, 0, 'Add plenty of ice to a mixing glass.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5C0A6CA27277941A41C519010EEE35E', X'990846CC79CEB34FBF04C62D4F38BCFC', X'990846CC79CEB34FBF04C62D4F38BCFC', 1, NULL, NULL, 2, NULL, 0, 'Pour in gin, vermouth, and olive brine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBAF35726C372447856F91EFDCA7CA7B', X'990846CC79CEB34FBF04C62D4F38BCFC', X'990846CC79CEB34FBF04C62D4F38BCFC', 2, NULL, NULL, 2, NULL, 0, 'Stir until very well chilled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74B11B57A6FFD24680B2DA4E083AD3A9', X'990846CC79CEB34FBF04C62D4F38BCFC', X'990846CC79CEB34FBF04C62D4F38BCFC', 3, NULL, NULL, 2, NULL, 0, 'Strain into a martini glass.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2A8C16ECE4FA6469CF83CBE2F567C75', X'990846CC79CEB34FBF04C62D4F38BCFC', X'990846CC79CEB34FBF04C62D4F38BCFC', 4, NULL, NULL, 2, NULL, 0, 'Garnish with three large, skewered olives and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129FE88FDC30854786CDA35363C3B9C1', X'0359560A127D0A439D642505DFB87A89', X'0359560A127D0A439D642505DFB87A89', 0, NULL, NULL, 2, NULL, 0, 'Fill a cocktail shaker with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3337D03573163A4DA4F22987EFE984DC', X'0359560A127D0A439D642505DFB87A89', X'0359560A127D0A439D642505DFB87A89', 1, NULL, NULL, 2, NULL, 0, 'Pour in tequila, Chartreuse, grapefruit juice, and lime juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26AAC9CBB27EB64CA5109BE36A772791', X'0359560A127D0A439D642505DFB87A89', X'0359560A127D0A439D642505DFB87A89', 2, NULL, NULL, 2, NULL, 0, 'Shake well and strain into an ice-filled old-fashioned glass.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9656018B2F8F1C40BE9EFB668572E67A', X'0359560A127D0A439D642505DFB87A89', X'0359560A127D0A439D642505DFB87A89', 3, NULL, NULL, 2, NULL, 0, 'Garnish with star anise and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CBABE8320CCE24C92433D2BCEBF78EC', X'46915D02D275834E824AD1375C089704', X'46915D02D275834E824AD1375C089704', 0, NULL, NULL, 2, NULL, 0, 'Fill a mixing glass halfway with ice. Pour in cocktail ingredients except garnish and stir. Strain into a coupe glass, garnish, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F675188EEC4104888E68D74BD15FEAC', X'9D0E109DA9518A408FECFCD621F344CF', X'9D0E109DA9518A408FECFCD621F344CF', 0, NULL, NULL, 2, NULL, 0, 'Fill a cocktail shaker with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5883B443BA196941B3C7CB70C1AC853D', X'9D0E109DA9518A408FECFCD621F344CF', X'9D0E109DA9518A408FECFCD621F344CF', 1, NULL, NULL, 2, NULL, 0, 'Pour in gin, lime juice, and simple syrup.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A46A68CAF48AA4D89ACFF88C853D978', X'9D0E109DA9518A408FECFCD621F344CF', X'9D0E109DA9518A408FECFCD621F344CF', 2, NULL, NULL, 2, NULL, 0, 'Shake well and strain into a coupe glass.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A56528155B09494F8B997E66A43FE0EF', X'9D0E109DA9518A408FECFCD621F344CF', X'9D0E109DA9518A408FECFCD621F344CF', 3, NULL, NULL, 2, NULL, 0, 'Garnish with cucumber or lime slice and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A3F736CB22628459E317503B56840E2', X'0ED9C5F0B9CE244FB84A0AD7975ADC67', X'0ED9C5F0B9CE244FB84A0AD7975ADC67', 0, NULL, NULL, 2, NULL, 0, 'Up to 6 hours before serving, make the batch. Pour chilled Campari, chilled coffee, simple syrup, and salt into a 2-quart pitcher and stir to mix. Seal well, covering with plastic wrap if needed, and refrigerate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75D0D50CD122F04781422E8381A5457E', X'0ED9C5F0B9CE244FB84A0AD7975ADC67', X'0ED9C5F0B9CE244FB84A0AD7975ADC67', 1, NULL, NULL, 2, NULL, 0, 'Up to 2 hours before serving, prepare grapefruit juice and stir into pitcher mix. Reseal and return to refrigerator if not serving immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31C835D995952C43A82A8CC64A488057', X'0ED9C5F0B9CE244FB84A0AD7975ADC67', X'0ED9C5F0B9CE244FB84A0AD7975ADC67', 2, NULL, NULL, 2, NULL, 0, 'To serve, whisk pitcher mixture well, or pour half of the mixture into a blender and blend for 20 seconds, then stir back into remaining pitcher mix. Fill 12-ounce highball glasses with ice and add 1/4 cup chilled tonic water to each glass. Top with pitcher mix and garnish each drink with a grapefruit twist.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9150ECDBF449C04EB1FA1EC1855C4679', X'A3A933A60CBC1A429D66A1298A0A29B3', X'A3A933A60CBC1A429D66A1298A0A29B3', 0, NULL, NULL, 2, NULL, 0, 'Fill a cocktail shaker halfway with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29217459F9B1A54AA96F23E4811B3055', X'A3A933A60CBC1A429D66A1298A0A29B3', X'A3A933A60CBC1A429D66A1298A0A29B3', 1, NULL, NULL, 2, NULL, 0, 'Add Lillet Blanc, vodka, and grapefruit juice and shake until chilled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F95B266BAC6DF0448537A273D0EBD7A5', X'A3A933A60CBC1A429D66A1298A0A29B3', X'A3A933A60CBC1A429D66A1298A0A29B3', 2, NULL, NULL, 2, NULL, 0, 'Strain into a cocktail glass, garnish with a slice of star fruit, and serve. You can also serve this cocktail with over ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAB0478FBB406B43A743CB439886DA75', X'BCA6E730042FEA4291FC99B161B9CCB0', X'BCA6E730042FEA4291FC99B161B9CCB0', 0, NULL, NULL, 2, NULL, 0, 'Combine the sugar and the water in a small pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72CCEC88E0CFC74BB4E050611C9786A7', X'BCA6E730042FEA4291FC99B161B9CCB0', X'BCA6E730042FEA4291FC99B161B9CCB0', 1, NULL, NULL, 2, NULL, 0, 'Cook over medium heat, stirring occasionally, until the sugar has dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'306BB5E219C4844B8A75BA56AE914A69', X'BCA6E730042FEA4291FC99B161B9CCB0', X'BCA6E730042FEA4291FC99B161B9CCB0', 2, NULL, NULL, 2, NULL, 0, 'Cool completely. Can be stored, covered, in the fridge for up to a month.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C21F25325FE9D248B7E89DE183B8AB49', X'922E8B70B9D6C34BB22AE200AAA25CD9', X'922E8B70B9D6C34BB22AE200AAA25CD9', 0, NULL, NULL, 2, NULL, 0, 'Place some ice in a tall glass. For their slow melt and simple, classy look I used these giant ice cubes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'990CE8B4DCCD3F41B404B58577DFC2E4', X'922E8B70B9D6C34BB22AE200AAA25CD9', X'922E8B70B9D6C34BB22AE200AAA25CD9', 1, NULL, NULL, 2, NULL, 0, 'Pour in Campari and vermouth, stir in soda water and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDBF8B2E70A38443924FD31C30E09F0F', X'3B1AE8186B9CF64E96C98142F282A4FC', X'3B1AE8186B9CF64E96C98142F282A4FC', 0, NULL, NULL, 2, NULL, 0, 'Pour ingredients into an ice-filled cocktail shaker.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BFC771D3288F144AF187E0C75AB1952', X'3B1AE8186B9CF64E96C98142F282A4FC', X'3B1AE8186B9CF64E96C98142F282A4FC', 1, NULL, NULL, 2, NULL, 0, 'Shake well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'582FD76C370B3D4F97BE997C20E61BEE', X'3B1AE8186B9CF64E96C98142F282A4FC', X'3B1AE8186B9CF64E96C98142F282A4FC', 2, NULL, NULL, 2, NULL, 0, 'Strain into a cocktail glass to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AF97BC541157B48B27B45FD1F243BD1', X'3B1AE8186B9CF64E96C98142F282A4FC', X'3B1AE8186B9CF64E96C98142F282A4FC', 3, NULL, NULL, 2, NULL, 0, 'Garnish with a Luxardo cherry or a lime twist.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98CC59152E7402419988833A4C441F17', X'1D0A20E89DCF2F428C46C94F953F9510', X'1D0A20E89DCF2F428C46C94F953F9510', 0, NULL, NULL, 2, NULL, 0, 'Heat a cast iron skillet over medium-high heat for a few minutes until very hot. Cut the lemon in half and then cut a 1/4-inch-thick round slice from one half. Place the lemon halves and the slice into the pan cut sides down and cook for about three to four minutes, until nicely charred. Remove lemon from pan and add thyme sprig. Cook for about a minute, until fragrant, then set aside to cool.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFEEA28914F93B4B8FCC072AD83B683F', X'1D0A20E89DCF2F428C46C94F953F9510', X'1D0A20E89DCF2F428C46C94F953F9510', 1, NULL, NULL, 2, NULL, 0, 'Squeeze juice from lemon halves through a fine-mesh sieve into a small bowl. Measure out 1 1/2 ounces juice, reserving any extra for another use. Stir honey into lemon juice until dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'182DF16397D99946ABABE316D0E9720C', X'1D0A20E89DCF2F428C46C94F953F9510', X'1D0A20E89DCF2F428C46C94F953F9510', 2, NULL, NULL, 2, NULL, 0, 'Fill a cocktail shaker with ice. Pour in lemon juice mixture, gin and Vermouth and shake well. Place a few ice cubes into a single old fashioned glass and pour drink into glass. Garnish with lemon slice and thyme sprig. (Bruise the thyme with your fingers a bit so its flavors will infuse into the drink.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1E05631FB69FC408FA2D46AFF4BFC2F', X'C4A7F5A9E227594392A1722D483A71B3', X'C4A7F5A9E227594392A1722D483A71B3', 0, NULL, NULL, 2, NULL, 0, 'Fill a copper mule cup most of the way with crushed ice. To crush your own ice, place regular ice cubes in a double layer of gallon-sized zip-top bags and smash ice with a wooden rolling pin or a mallet until crushed. (After a tough day you may find you start preferring your cocktail with homemade crushed ice.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF513177C1C1774B86239E898DF0FC89', X'C4A7F5A9E227594392A1722D483A71B3', X'C4A7F5A9E227594392A1722D483A71B3', 1, NULL, NULL, 2, NULL, 0, 'Stir in vodka, apple cider, ginger beer and lime juice. Garnish with apple slice and cinnamon stick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B257BCC875E4B84BA1837068462FA99C', X'5798BF979B20CD40B11289EB50A2B666', X'5798BF979B20CD40B11289EB50A2B666', 0, NULL, NULL, 2, NULL, 0, 'Place plenty of ice into a mixing glass.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0D2F279469A9740BF33D37DC5A700EA', X'5798BF979B20CD40B11289EB50A2B666', X'5798BF979B20CD40B11289EB50A2B666', 1, NULL, NULL, 2, NULL, 0, 'Pour in the gin, vermouth and Campari.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E9076E01B74814783AB080AF706D518', X'5798BF979B20CD40B11289EB50A2B666', X'5798BF979B20CD40B11289EB50A2B666', 2, NULL, NULL, 2, NULL, 0, 'Stir well until chilled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A099D696BD76124A94E8FD96BF54791D', X'5798BF979B20CD40B11289EB50A2B666', X'5798BF979B20CD40B11289EB50A2B666', 3, NULL, NULL, 2, NULL, 0, 'Pour into a single old-fashioned glass, garnish with twist of orange peel or a small slice of orange, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6C59F4DB7171C409283DFEC651EC489', X'3088D2D9528E894D89C2239AD8DCE752', X'3088D2D9528E894D89C2239AD8DCE752', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, combine the thyme, rosemary, sugar and water. Set over medium-high heat and simmer, stirring occasionally, until sugar is dissolved and liquid is just shy of boiling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3873A4203E4C5E48BC6D69F68CF53496', X'3088D2D9528E894D89C2239AD8DCE752', X'3088D2D9528E894D89C2239AD8DCE752', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and set aside for at least 30 minutes. The longer it sits, the stronger the herb flavor will be. Strain the liquid into a little cup or container and discard herbs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'535C812A362DB246947D790D1D2B477A', X'3088D2D9528E894D89C2239AD8DCE752', X'3088D2D9528E894D89C2239AD8DCE752', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, fill two medium glasses about 2/3 with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A7EF251B14106439F411140EF509816', X'3088D2D9528E894D89C2239AD8DCE752', X'3088D2D9528E894D89C2239AD8DCE752', 3, NULL, NULL, 2, NULL, 0, 'Juice the citrus into a liquid measuring cup with a spout and stir in two tablespoons of the herb syrup.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'603FC790A0A940459B7F90FFDFD5D43D', X'3088D2D9528E894D89C2239AD8DCE752', X'3088D2D9528E894D89C2239AD8DCE752', 4, NULL, NULL, 2, NULL, 0, 'Divide between glasses, garnish with herb sprigs and blood orange slices if you like, and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9A243FFD9546845B5383D21D6011BEC', X'AF0A504403BF984F86B2CF0E277E2169', X'AF0A504403BF984F86B2CF0E277E2169', 0, NULL, NULL, 2, NULL, 0, 'Pour the Campari into a champagne glass, fill the glass with Prosecco, stir gently and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DF49B71CE676040BF56425F591C1C4A', X'41A28FD995099948AB1AF2C3650BCDD9', X'41A28FD995099948AB1AF2C3650BCDD9', 0, NULL, NULL, 2, NULL, 0, 'Pour all ingredients into a 9x13-inch glass baking dishand stir.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5292E2E836C284FAAFD8AB73EA70F40', X'41A28FD995099948AB1AF2C3650BCDD9', X'41A28FD995099948AB1AF2C3650BCDD9', 1, NULL, NULL, 2, NULL, 0, 'Cover well and freeze overnight.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCEC3601E95C9F46A2178C97EEAC124B', X'41A28FD995099948AB1AF2C3650BCDD9', X'41A28FD995099948AB1AF2C3650BCDD9', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, use a fork to scrape into a slushie-like consistency. This will be easy since vodka doesn''t completely freeze.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C970138C4F99A44A84B74B6284AB24D', X'41A28FD995099948AB1AF2C3650BCDD9', X'41A28FD995099948AB1AF2C3650BCDD9', 3, NULL, NULL, 2, NULL, 0, 'Scoop into glasses and serve. It''s great frozen but also as it melts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FDDBBEE5E1DA84B92C98261046E8FE6', X'8DF5E5123BEAAA4C9FEE5BF23A485AF0', X'8DF5E5123BEAAA4C9FEE5BF23A485AF0', 0, NULL, NULL, 2, NULL, 0, 'In a cocktail shaker filled halfway with ice, shake clementine juice, lime juice and vodka until ice cold.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D400C3887DD2E84D8CBB84446FDE4790', X'8DF5E5123BEAAA4C9FEE5BF23A485AF0', X'8DF5E5123BEAAA4C9FEE5BF23A485AF0', 1, NULL, NULL, 2, NULL, 0, 'Strain into a glass (preferably a copper mug) filled about 2/3 full with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07992240C155944E838D6A23E1D94866', X'8DF5E5123BEAAA4C9FEE5BF23A485AF0', X'8DF5E5123BEAAA4C9FEE5BF23A485AF0', 2, NULL, NULL, 2, NULL, 0, 'Top with 4 ounces ginger beer and serve, garnished with clementine or lime if you like. (I like to give it a quick stir before garnishing, though I''m sure that''s illegal somehow.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49CB1B7F46E6D74CA60C2FDFA54F88EB', X'502970DA6F55A442A36B86E8D4D03DDD', X'502970DA6F55A442A36B86E8D4D03DDD', 0, NULL, NULL, 2, NULL, 0, 'Fill a pint-sized jar halfway with ice. Pour in champagne, gin and sour cherry syrup. Stir to combine. Serve right away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE9528AD865E5E44A9EC626C70B8ED91', X'A156DB9A28136F4EA7DC8BFFE7CCECCD', X'A156DB9A28136F4EA7DC8BFFE7CCECCD', 0, NULL, NULL, 2, NULL, 0, 'Fill a large wine glass (or a pint-sized mason jar, or whatever glass you like) halfway with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'576C0D8FD21DDD4695D983BF1DEAD2DA', X'A156DB9A28136F4EA7DC8BFFE7CCECCD', X'A156DB9A28136F4EA7DC8BFFE7CCECCD', 1, NULL, NULL, 2, NULL, 0, 'Pour in the Prosecco and then the Aperol. Top with soda water. Garnish with an orange slice if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FADDA60D1ABFF42B8FC1DF06544DEA1', X'DFC37EAF1E58C5488AF844D088AF71E7', X'DFC37EAF1E58C5488AF844D088AF71E7', 0, NULL, NULL, 2, NULL, 0, 'Fill a mason jar or cocktail shaker halfway with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01E4AA4A9A6659409C727290D30CD650', X'DFC37EAF1E58C5488AF844D088AF71E7', X'DFC37EAF1E58C5488AF844D088AF71E7', 1, NULL, NULL, 2, NULL, 0, 'Pour in the gin, vermouth, and fresh grapefruit juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A09B8DD4B9ED04CA3BB8DC4D67CE027', X'DFC37EAF1E58C5488AF844D088AF71E7', X'DFC37EAF1E58C5488AF844D088AF71E7', 2, NULL, NULL, 2, NULL, 0, 'Cover and shake, then divide between two ice-filled glasses. Garnish with a wisp of grapefruit rind and start sipping immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31649596392A9C4893ECE1DECA899148', X'E39890D87EED1248BF9601D0D270CFBE', X'E39890D87EED1248BF9601D0D270CFBE', 0, NULL, NULL, 2, NULL, 0, 'Combine the rum, lime juice and simple syrup in a cocktail shaker.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD875EB5DBC245468EB0F8861611A8CA', X'E39890D87EED1248BF9601D0D270CFBE', X'E39890D87EED1248BF9601D0D270CFBE', 1, NULL, NULL, 2, NULL, 0, 'Add a few ice cubes and shake.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E45FB8A4798B74EBF8179BDA051B44D', X'E39890D87EED1248BF9601D0D270CFBE', X'E39890D87EED1248BF9601D0D270CFBE', 2, NULL, NULL, 2, NULL, 0, 'Pour into an ice-filled glass and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D13CD346C286BB4193C7008140012571', X'FFB61E43E01B1A478DBF6C9FF81CF653', X'FFB61E43E01B1A478DBF6C9FF81CF653', 0, NULL, NULL, 2, NULL, 0, 'Into a 4-cup french press, liquid measure or jar, pour the ground coffee, brown sugar and cinnamon. Pour in half the water and stir well. Add remaining water and stir again.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3B0252470B2394EA76F24BB0C485A9A', X'FFB61E43E01B1A478DBF6C9FF81CF653', X'FFB61E43E01B1A478DBF6C9FF81CF653', 1, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature or in the fridge overnight, then press or strain through cheesecloth or a nut milk bag into a clean container.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'454A16CD0FC4514599093D4E6F4CAB29', X'FFB61E43E01B1A478DBF6C9FF81CF653', X'FFB61E43E01B1A478DBF6C9FF81CF653', 2, NULL, NULL, 2, NULL, 0, 'To serve, fill glass with ice and pour in coffee and milk to taste. Dilute with cold filtered water if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'768E817E12DD92419A6D9251E4C8411D', X'D2B554BB8614714891B6F14F5B1F3B60', X'D2B554BB8614714891B6F14F5B1F3B60', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in the jar of a blender and blend on high speed until perfectly smooth, about 30 seconds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2A915135C4C0748AE5C954584830A48', X'D2B554BB8614714891B6F14F5B1F3B60', X'D2B554BB8614714891B6F14F5B1F3B60', 1, NULL, NULL, 2, NULL, 0, 'Divide between two glasses and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A691D6FC15197B478A93E54CD66182C6', X'72E1F0CA3A7AF143A496A96571B1B279', X'72E1F0CA3A7AF143A496A96571B1B279', 0, NULL, NULL, 2, NULL, 0, 'Slice the rhubarb stalks crosswise into one-inch segments. Place rhubarb segments into a large pot along with the water. Bring to a boil over high heat, then cover and reduce heat to simmer for 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5768C23748196E43BDA5FAF44892FA70', X'72E1F0CA3A7AF143A496A96571B1B279', X'72E1F0CA3A7AF143A496A96571B1B279', 1, NULL, NULL, 2, NULL, 0, 'Strain liquid through a fine-mesh strainer into a pitcher or spouted bowl, pressing on the solids in the strainer to extract as much juice as possible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E6BCD1B5E138740B3B6EF2E9DF66472', X'72E1F0CA3A7AF143A496A96571B1B279', X'72E1F0CA3A7AF143A496A96571B1B279', 2, NULL, NULL, 2, NULL, 0, 'After 20 minutes, the juice will separate, leaving a yellowish sludge at the bottom. Decant the clear pink liquid into a clean bottle or pitcher, leaving the sludge behind. Chill for a few hours at least, and serve cold over ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF6C7C652B58EE429F8B5BD701262818', X'04DD3E719440CD4CAE8F029D3C084DEF', X'04DD3E719440CD4CAE8F029D3C084DEF', 0, NULL, NULL, 2, NULL, 0, 'Chop ingredients as necessary for your juicer, then feed through the juicer in the order listed.');
