INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F306DB7F1BEF84AA1F9C338FA9EA8E5', X'49CFA2380901E64A824F447CC1FD0D2A', X'49CFA2380901E64A824F447CC1FD0D2A', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a small pot. Set over medium-high heat to boil. Reduce heat to maintain a brisk simmer, stirring occasionally. Cook for 10-15 minutes, until liquid is red and slightly thickened. Strain liquid and discard (or eat!) cherries. Use when completely cooled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A48A8A063ADB93499F385277C2C13C4A', X'14C36AB39DB8064B8590522305B639DA', X'14C36AB39DB8064B8590522305B639DA', 0, NULL, NULL, 2, NULL, 0, 'Combine cherries, sugar and water in a small pot and set over medium-high heat to boil. Reduce heat to maintain a brisk simmer, stirring occasionally, until liquid has turned red and thickened slightly, 10-15 minutes. Pour through a fine mesh strainer to remove cherries. Chill syrup or let it cool on the counter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57E179920FBB8247B4176A4A8F1BAA5D', X'14C36AB39DB8064B8590522305B639DA', X'14C36AB39DB8064B8590522305B639DA', 1, NULL, NULL, 2, NULL, 0, 'Fill glasses with ice and pour in ginger ale or lemon-lime soda until glass is 3/4 full. Stir in 2 tablespoons sour cherry syrup (or more to taste). Deliver immediately into little hands, garnished with a fresh sour cherry or a maraschino cherry if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A65EFB46A773E24F8215964FA76C81E0', X'64BA9D68B570754B934CA257AC3740D7', X'64BA9D68B570754B934CA257AC3740D7', 0, NULL, NULL, 2, NULL, 0, 'Place the nuts in a medium bowl and cover completely with water. Soak nuts for eight hours, or overnight.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'368DB8BD6C3201419DCA744A46DA5197', X'64BA9D68B570754B934CA257AC3740D7', X'64BA9D68B570754B934CA257AC3740D7', 1, NULL, NULL, 2, NULL, 0, 'Drain and discard liquid and rinse nuts thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9B5FF9E71DECB4AA2B8BF9266C79A49', X'64BA9D68B570754B934CA257AC3740D7', X'64BA9D68B570754B934CA257AC3740D7', 2, NULL, NULL, 2, NULL, 0, 'Pour three cups of water into the blender and add the nuts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2344392EE427A40988ABB72B104CFC6', X'64BA9D68B570754B934CA257AC3740D7', X'64BA9D68B570754B934CA257AC3740D7', 3, NULL, NULL, 2, NULL, 0, 'Cover and blend for about two minutes, until very white and smooth. For cashews, there should be no remaining nut pulp and no need to strain. (See notes below.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79FF01C7BB3192438D4D1129C24C22D7', X'64BA9D68B570754B934CA257AC3740D7', X'64BA9D68B570754B934CA257AC3740D7', 4, NULL, NULL, 2, NULL, 0, 'For almonds, pour the milk through a fine-mesh strainer lined with two layers of cheesecloth (or through a nut milk or paint strainer bag). Press until no more liquid drains out. Then use the milk and reserve the almond pulp to add to baked goods, cereals, etc., if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A10E1368CC843846A998A994B5EA0847', X'64BA9D68B570754B934CA257AC3740D7', X'64BA9D68B570754B934CA257AC3740D7', 5, NULL, NULL, 2, NULL, 0, 'Milk keeps, tightly sealed, in the fridge for about three days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0198E20F5255934D84BFABB78C30ABEE', X'E857AD5200863445926C5FE5243E0E79', X'E857AD5200863445926C5FE5243E0E79', 0, NULL, NULL, 2, NULL, 0, 'Chop chocolate, pistachios, and raspberries separately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04EB38A8970FFC4A8AF6C391FA34FEE5', X'E857AD5200863445926C5FE5243E0E79', X'E857AD5200863445926C5FE5243E0E79', 1, NULL, NULL, 2, NULL, 0, 'Melt the chocolate in a double boiler or on half power in bursts of 30 to 60 seconds the microwave, stirring gently and frequently either way.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDB6694FE5AA264FB854BC2E1A97747E', X'E857AD5200863445926C5FE5243E0E79', X'E857AD5200863445926C5FE5243E0E79', 2, NULL, NULL, 2, NULL, 0, 'Pour melted chocolate onto a rimmed baking sheet lined with parchment and spread into an even layer. You can choose the thickness — I prefer something between 1/4 and 1/8 inch.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51241D04EB32D241BCFB6F25CDC2469A', X'E857AD5200863445926C5FE5243E0E79', X'E857AD5200863445926C5FE5243E0E79', 3, NULL, NULL, 2, NULL, 0, 'While chocolate is hot, sprinkle with chopped nuts and raspberries.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67DDA702229A9B4AA88A45637686C935', X'E857AD5200863445926C5FE5243E0E79', X'E857AD5200863445926C5FE5243E0E79', 4, NULL, NULL, 2, NULL, 0, 'Let rest until completely set, then break into shards.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0FEFD29342EB64386CCF78B6E97639F', X'317E2184710729448C0A707077467CA7', X'317E2184710729448C0A707077467CA7', 0, NULL, NULL, 2, NULL, 0, 'Fill a mixing glass with plenty of ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4100EE7CF4293041A8B7ECF2D1BADBBE', X'317E2184710729448C0A707077467CA7', X'317E2184710729448C0A707077467CA7', 1, NULL, NULL, 2, NULL, 0, 'Pour in bourbon, Cynar, and syrup. Add three dashes of Angostura bitters.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AE09E92789F574B891688F6901192F2', X'317E2184710729448C0A707077467CA7', X'317E2184710729448C0A707077467CA7', 2, NULL, NULL, 2, NULL, 0, 'Stir until very well-chilled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'877FAC39BF4E1E44AD7CFA18EF90BD84', X'317E2184710729448C0A707077467CA7', X'317E2184710729448C0A707077467CA7', 3, NULL, NULL, 2, NULL, 0, 'Strain into a coupe glass, garnish with star anise, and, serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C63E069A32BF9F47B52FF5DC8024640B', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F with the top rack in the lower third of the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E923390FA34DF49BBE55F2312AE2430', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 1, NULL, NULL, 2, NULL, 0, 'Remove the neck and the bag of innards and giblets from inside the cavities. Sometimes they’re both in the large main cavity, but sometimes the bag is in the smaller neck cavity around the other side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B32CAE03E77BB49A9AE7DDC76C7DE02', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 2, NULL, NULL, 2, NULL, 0, 'If there are any pin feathers still on the bird, pluck them out.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BC4B7AF02F4654EB8D62FDA47C81FB1', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 3, NULL, NULL, 2, NULL, 0, 'Place the turkey breast-side up into a large roasting pan. No racks or fancy positions necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB89B87B0E47EB4394789E6C0A0C01DB', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 4, NULL, NULL, 2, NULL, 0, 'Place butter into a small bowl. Strip one teaspoon of leaves from the thyme and add it to the bowl, reserving the rest. Add the salt, pepper, and lemon zest, and mix it all together thoroughly with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69EDDE9D2BEAD64CB6CD6E9344A99BB4', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 5, NULL, NULL, 2, NULL, 0, 'Use your fingers to carefully separate the turkey skin from the breast meat to create pockets. Tuck some of the compound butter into the pockets and distribute it between the skin and the breast meat by wriggling it in as best as possible. Spread the remaining compound butter all over the outside of the turkey.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D3683C6CBD339479D8630633036EFA2', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 6, NULL, NULL, 2, NULL, 0, 'Place the lemon and onion pieces into the large cavity of the bird along with a the remaining thyme sprigs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7478CB7FB2749B4384B4DBFCF620520B', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 7, NULL, NULL, 2, NULL, 0, 'To truss, tuck the tip of each wing under the bird and tie the tips of the drumsticks together with a small piece of twine if this isn’t already done.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'544A49197ACFA140A99BDB9553F5074E', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 8, NULL, NULL, 2, NULL, 0, 'Place turkey into oven and roast to your desired level of doneness. For me this means that an instant read thermometer inserted into the deepest part of the breast should read 155-160°F. Refer to the notes section below to learn more. A 10 pound turkey will probably need between 2 and 2 1/2 hours. A 14 pound turkey will probably need between 2 1/2 and 3 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B951A38DC3B816408F72866225552507', X'5AB8027D730CFB4FA35B53BA80DE1B05', X'5AB8027D730CFB4FA35B53BA80DE1B05', 9, NULL, NULL, 2, NULL, 0, 'Remove turkey from oven and let rest for at least 30 minutes before carving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9C104A634BDE548B143DE45E93000CC', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Spray a half-sheet pan with cooking spray, line it with a piece of parchment paper, and spray the paper too.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08CA9F5DCBF8C34C838ACD844DE1E8C4', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 1, NULL, NULL, 2, NULL, 0, 'Into a large bowl, sift together the flour, ginger, baking soda, cinnamon, cloves, nutmeg, and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A2A3BB15B27F649BA3E3E6C73A7878B', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer, beat the butter with the paddle attachment on medium-high speed until light and fluffy. Add brown sugar and beat until combined with the butter, stopping to scrape down the sides of the bowl with a spatula as needed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B49538775ACF8D46B3E24F5DD24737A5', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 3, NULL, NULL, 2, NULL, 0, 'Beat in the eggs one at a time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E7DF8DBEED5E94484E743CBA75D597C', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 4, NULL, NULL, 2, NULL, 0, 'Lower the mixing speed to medium-low and pour in the molasses. Beat until incorporated. Beat in hot water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E929B08672D34CA3E7D5A12F8EC70D', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 5, NULL, NULL, 2, NULL, 0, 'Lower mixing speed to low and gradually spoon in the flour mixture, along with the lemon zest. Mix just until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1812CC542B40DD4797B65929C6D4FD96', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 6, NULL, NULL, 2, NULL, 0, 'Pour batter onto prepared baking sheet and smooth with a spatula. Bake for 20-25 minutes, until a tester comes out clean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD83297A9F06EF499000D58BFA87832F', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 7, NULL, NULL, 2, NULL, 0, 'Let gingerbread cool in the pan set over a cooling rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'666C387A67B2F34F863498BBE4BEC11A', X'1076A63BE6B73F42A00BC2972BF04BA6', X'1076A63BE6B73F42A00BC2972BF04BA6', 8, NULL, NULL, 2, NULL, 0, 'Once cooled, remove cake from pan. (If you have a large cutting board, you can place it directly on top of the sheet pan and flip the whole thing over to release the gingerbread onto the cutting board. Peel off parchment paper and cut gingerbread into 1-inch squares.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF8E8B53086B8F43B569A9004041A23A', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 0, NULL, NULL, 2, NULL, 0, 'Arrange one or two small bowls for the chocolate dip onto the serving platter. (You''ll pour the dip in later.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'920EF43612D30249A51EF431C70DE1F1', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 1, NULL, NULL, 2, NULL, 0, 'Gently rinse and dry all fruit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EE862C7CC22DE43BD61D5342ECBD7E4', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 2, NULL, NULL, 2, NULL, 0, 'To prepare the apples, quarter them, remove the core from each quarter by slicing it out diagonally, and then slice each quarter into four or five slices. Granny Smith apples don''t brown quickly, so you can do this in advance without worrying about it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADF312553ECAE84BA1B224875846ED6A', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 3, NULL, NULL, 2, NULL, 0, 'To prepare the grapes, snip the stems to form little bundles of 5 to 10 grapes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21FC9B27D5EC4942925C9C208BCCC45C', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 4, NULL, NULL, 2, NULL, 0, 'To prepare the star fruit, trim off and discard a thin slice from each end, then slice the star fruit crosswise into about 8 star-shaped slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDB1A52ABB5D95449AE106A935762004', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 5, NULL, NULL, 2, NULL, 0, 'To prepare the dragon fruit, trim off and discard a thin slice from the stem end. Quarter the dragon fruit lengthwise and then cut each quarter crosswise into slices. The pink skin is not edible, but it''s so beautiful that I like to leave it on. It''s very easy for eaters to peel away from the edible flesh and gives them something to hold onto while eating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E740B4C40151FC43885F489393C30B19', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 6, NULL, NULL, 2, NULL, 0, 'To prepare the kiwis, peel off the furry skin with a vegetable peeler or paring knife. Slice kiwis crosswise into rounds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87B7A5049036F341B94A71320928FC96', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 7, NULL, NULL, 2, NULL, 0, 'To prepare the watermelon, cut a baby watermelon in half crosswise and reserve half for another use. Place the half watermelon cut side down and cut it into four quarters. Slice each quarter crosswise into triangular slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DEAFAE7FC46AA42B21518BD25931738', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 8, NULL, NULL, 2, NULL, 0, 'To prepare the strawberries, either leave whole if they''re smaller or remove leaves and cut into halves or quarters if they''re large.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52C787F8E818D14293486425ED6CB375', X'5583AD01D28CE341B9E7E57BDF6E472D', X'5583AD01D28CE341B9E7E57BDF6E472D', 9, NULL, NULL, 2, NULL, 0, 'To prepare cape gooseberries, remove papery hulls and wash well to remove as much of sticky exterior coating as is reasonable. You may not be able to remove it all, and that''s okay.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7900DA4D9C422449A978C12D6E1B5CEE', X'B7F13409B60C8C4A87F43FA3F208FEF1', X'B7F13409B60C8C4A87F43FA3F208FEF1', 0, NULL, NULL, 2, NULL, 0, 'Preheat waffle iron.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'208504A22A6556478571688428ED6D11', X'B7F13409B60C8C4A87F43FA3F208FEF1', X'B7F13409B60C8C4A87F43FA3F208FEF1', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the all-purpose flour, white whole wheat flour, baking powder, baking soda, salt, cinnamon, ginger, nutmeg, and cloves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'402D83533CF1C440A94EB049D8C7F0C0', X'B7F13409B60C8C4A87F43FA3F208FEF1', X'B7F13409B60C8C4A87F43FA3F208FEF1', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the eggs, milk, sour cream, melted butter, and molasses.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D47AAF2FDB78C3469D74570115C90C8F', X'B7F13409B60C8C4A87F43FA3F208FEF1', X'B7F13409B60C8C4A87F43FA3F208FEF1', 3, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients into the dry ingredients and mix together until just combined. A few lumps are okay, just try to avoid any substantial pockets of unmixed dry ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'357B74A89570E2498DE377EE1315111D', X'B7F13409B60C8C4A87F43FA3F208FEF1', X'B7F13409B60C8C4A87F43FA3F208FEF1', 4, NULL, NULL, 2, NULL, 0, 'Spray waffle iron lightly with nonstick cooking spray. Pour batter into waffle iron and cook according to manufacturer''s instructions. In our waffle iron (recommended below), we use a scant 1/2 cup batter for each waffle and set the iron to medium-dark for these waffles.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B31B078E53AFE64F80AB0CB0BC8E1D2D', X'B7F13409B60C8C4A87F43FA3F208FEF1', X'B7F13409B60C8C4A87F43FA3F208FEF1', 5, NULL, NULL, 2, NULL, 0, 'Serve with a dusting of powdered sugar, maple syrup, and/or lightly sweetened whipped cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A0835EA6142F245AD2C3891C27948B7', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 175°F with two racks close to the center. Line two cookie sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55177A4883D0DE4593F62BD4EE31E5F1', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 1, NULL, NULL, 2, NULL, 0, 'Place the egg whites in a large metal bowl or the bowl of a stand mixer. Beat on medium until very foamy and then on high until soft peaks form, meaning that if you lift the beaters out of the egg whites, the tip of the peak that forms folds softly back onto itself but doesn’t collapse entirely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'310FD4684F214A4A80F32A241575E919', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 2, NULL, NULL, 2, NULL, 0, 'With the mixer running at medium speed, gradually add half the sugar. Beat on high until white and glossy (it will look like marshmallow fluff).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F22EA0EEFF29D49A14824128507A0BE', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 3, NULL, NULL, 2, NULL, 0, 'Stop mixer and add remaining sugar all at once. Sprinkle cornstarch, vinegar, salt, and peppermint and chocolate extracts overtop. Beat again until mixture has enough structure to hold its shape when piped. This will take several minutes, maybe five to seven, depending on your mixer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1421AB5BA85A3D4D87763C11B003C0F9', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 4, NULL, NULL, 2, NULL, 0, 'Fit a large piping bag with a star tip. Using a skewer or a small paintbrush, paint three thin lines of red gel food dye onto the inside of the piping bag, starting from the tip and going about halfway up the bag. You don’t really have to do a good job of this at all for the effect to work, so don’t stress about it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E44C48629B7D4D8F5CC21DD1BDDD7F', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 5, NULL, NULL, 2, NULL, 0, 'Use a rubber spatula to transfer meringue from bowl into piping bag and twist the top to close.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CB4AD1AC2070143888BB67C4F558BBA', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 6, NULL, NULL, 2, NULL, 0, 'Pipe bite-sized meringues (about an inch and a half in diameter) onto parchment-lined cookie sheets, leaving an inch or so of space between them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1D23F8D4BBF0B42954C3F461685A404', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 7, NULL, NULL, 2, NULL, 0, 'Bake for about three hours, until dry and crisp.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E12BFB7D115F45B195181E6D761147', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 8, NULL, NULL, 2, NULL, 0, 'Transfer to a wire rack to cool completely. Keep the parchment-lined baking sheets available.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37BC8ED73EAE3B41A32EC2A6198C1583', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 9, NULL, NULL, 2, NULL, 0, 'When meringues are cool, melt chocolate chips. You can do this in a double-boiler or in the microwave in short bursts, stirring in between, until just melted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82A36118B14D7649A24E1A31969E688B', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 10, NULL, NULL, 2, NULL, 0, 'Dip the bottom of each kiss into the chocolate and set on parchment until dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D0D44A7E2D364418E76C22FD19D2AE0', X'F622C623B4683B45BB2901DF71FCC32D', X'F622C623B4683B45BB2901DF71FCC32D', 11, NULL, NULL, 2, NULL, 0, 'Once completely dry, kisses store well in an airtight container for a couple of weeks. Mason jars and cellophane gift bags work well for gifting!');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D4C33C268FAD443896494E1AC73A137', X'2A64FB29976901439A4142B16A797D56', X'2A64FB29976901439A4142B16A797D56', 0, NULL, NULL, 2, NULL, 0, 'Slice orange rinds into 1/2-inch-wide strips. You can do this over the course of several days if you like and leave rinds to air dry while you accumulate enough to dry in the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7035721F4C8CBF4898B9CD60B26176EB', X'2A64FB29976901439A4142B16A797D56', X'2A64FB29976901439A4142B16A797D56', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 300°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62C0E24F6FC7A84AA05AE0C4E1171B6C', X'2A64FB29976901439A4142B16A797D56', X'2A64FB29976901439A4142B16A797D56', 2, NULL, NULL, 2, NULL, 0, 'Arrange rinds in a single layer on a baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'461DB5143443014FAE2589F95C520D70', X'2A64FB29976901439A4142B16A797D56', X'2A64FB29976901439A4142B16A797D56', 3, NULL, NULL, 2, NULL, 0, 'Bake for about 15 minutes, until completely dried.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6E6717A05DB2F4789381D5A1E91A5DC', X'2A64FB29976901439A4142B16A797D56', X'2A64FB29976901439A4142B16A797D56', 4, NULL, NULL, 2, NULL, 0, 'Let cool completely before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCB8D74AB81C2C4BA1CC04AD5F11F363', X'663120200D74D74CAF2A2BC1718C0B53', X'663120200D74D74CAF2A2BC1718C0B53', 0, NULL, NULL, 2, NULL, 0, 'Place the water, sugar and egg whites in the bowl of a stand mixer or a large mixing bowl. Using the whisk attachment or an electric mixer, beat ingredients together, slowly at first to avoid a sugar snow shower. Keep going until the icing is smooth, glossy and the consistency of toothpaste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84A284AEC480D740AFCC27E0AEA298CB', X'663120200D74D74CAF2A2BC1718C0B53', X'663120200D74D74CAF2A2BC1718C0B53', 1, NULL, NULL, 2, NULL, 0, 'Transfer a small amount of the icing to a smaller bowl and mix in a few drops of the color you''d like to use to line your cookies. Spoon this icing into a disposable piping bag and snip off a tiny bit of the bag''s tip. Draw a line of icing as close as you''d like to the outer edge of your cookie, making sure to connect the line''s ends to each other so the flood icing won''t have any gaps to flow out.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DDC8B317C03DE44BF0E7C5F4533FFA5', X'663120200D74D74CAF2A2BC1718C0B53', X'663120200D74D74CAF2A2BC1718C0B53', 2, NULL, NULL, 2, NULL, 0, 'In another small bowl, mix some more of the icing with a few drops of the color you''d like to use to flood your cookies, along with two additional teaspoons of water. This icing should be thin enough to spread slowly and slightly across the surface of the cookies, but no thinner than that. You may need to experiment a little to find the consistency that''s best for your humidity and, ahem, skill level. Pour this icing into another piping bag or better yet a squeeze bottle.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0C2D257142A5B4284FAD220A5F4F552', X'663120200D74D74CAF2A2BC1718C0B53', X'663120200D74D74CAF2A2BC1718C0B53', 3, NULL, NULL, 2, NULL, 0, 'Squeeze some flood icing onto the surface of a cookie, filling in the entire surface inside the line. You can use a toothpick to fill any gaps that don''t fill themselves and gently rap the cookie onto the work surface if you''re brave and want a perfectly smooth finish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9B453C5B8901B42BD7C79B32D49010B', X'663120200D74D74CAF2A2BC1718C0B53', X'663120200D74D74CAF2A2BC1718C0B53', 4, NULL, NULL, 2, NULL, 0, 'To decorate the cookies further, add sprinkles or other decorations while the icing is still wet, or let the icing dry slightly and then mix other colors of the remaining thick icing and pipe additional designs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'978A2CC6D9E82E4F824EE66AE0FB167B', X'DBEB63CCC968C9459DF3A8AA1A8EFF88', X'DBEB63CCC968C9459DF3A8AA1A8EFF88', 0, NULL, NULL, 2, NULL, 0, 'Fill a very large pot halfway with cold water and two tablespoons of fine sea salt. Place potato pieces into the pot as you peel and chop them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2849E64D1DA7604DB1D5E774A9665C7E', X'DBEB63CCC968C9459DF3A8AA1A8EFF88', X'DBEB63CCC968C9459DF3A8AA1A8EFF88', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, then reduce heat and and simmer until tender, about 10 minutes. Drain potatoes in a colander.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9506B1AC848C6F40B7A799BFD96BCB8A', X'DBEB63CCC968C9459DF3A8AA1A8EFF88', X'DBEB63CCC968C9459DF3A8AA1A8EFF88', 2, NULL, NULL, 2, NULL, 0, 'Heat the butter and milk together in a small pot over medium-low heat until butter is melted. Either press the potatoes through a ricer or food mill back into the pot, or return them to the pot straightaway and use a masher to mash them until smooth. Pour in hot buttery milk in a slow stream, stirring to incorporate. Taste and adjust for salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'770F65DFBE8BE146AD1C3231423A9B84', X'BBC7FF6E0FD51A4397197D9C66D7D655', X'BBC7FF6E0FD51A4397197D9C66D7D655', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Line two standard muffin tins with 24 decorative paper liners.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE51ADB06718944280BBB14BDE479F25', X'BBC7FF6E0FD51A4397197D9C66D7D655', X'BBC7FF6E0FD51A4397197D9C66D7D655', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle, mix sugar, flour, cocoa, baking powder, baking soda and salt on low speed until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'601FA9A131433940A5C848D7B09B870B', X'BBC7FF6E0FD51A4397197D9C66D7D655', X'BBC7FF6E0FD51A4397197D9C66D7D655', 2, NULL, NULL, 2, NULL, 0, 'Add eggs, milk, oil and vanilla extract. Mix on medium speed until batter is smooth. Pour in boiling water and carefully mix on low until just incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA406A8E797B8F4DB8C80E650F4E18DE', X'BBC7FF6E0FD51A4397197D9C66D7D655', X'BBC7FF6E0FD51A4397197D9C66D7D655', 3, NULL, NULL, 2, NULL, 0, 'Divide batter among muffin cups. Bake cupcakes for about 18 minutes, until a tester comes out clean. Cool completely on a wire rack before frosting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9208A6E911376D4790AFEB62BF2E5B6C', X'C73F4F41798B7D45BE382EE21B5D9562', X'C73F4F41798B7D45BE382EE21B5D9562', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer fitted with the paddle, beat together the sugar, butter, baking powder, vanilla and salt until lightened and fluffy, about 5 minutes, scraping down bowl as necessary. Beat in egg until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1C99DD68243EC439564E4969D1FFFD4', X'C73F4F41798B7D45BE382EE21B5D9562', X'C73F4F41798B7D45BE382EE21B5D9562', 1, NULL, NULL, 2, NULL, 0, 'Into a medium bowl, sift together the cocoas and flour. Beat into wet ingredients all at once, just until well combined. Refrigerate dough for 30 minutes. (Dough will keep in fridge for 24 hours if you want to prepare it the day before.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D97B5049C218F4380FB09D1FE8F605D', X'C73F4F41798B7D45BE382EE21B5D9562', X'C73F4F41798B7D45BE382EE21B5D9562', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with two racks close to the center. Line two baking sheets with parchment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB2D90387A5974E82F91848FA39BBB9', X'C73F4F41798B7D45BE382EE21B5D9562', X'C73F4F41798B7D45BE382EE21B5D9562', 3, NULL, NULL, 2, NULL, 0, 'Divide dough into two discs. Sprinkle with cocoa. Roll out each disc until dough is 1/8` thick and cut into shapes with cookie cutters. Transfer cookies to baking sheets, leaving space between cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'665DFDC765A04543BEE59C0C1FB037CA', X'C73F4F41798B7D45BE382EE21B5D9562', X'C73F4F41798B7D45BE382EE21B5D9562', 4, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8 to 10 minutes, until edges begin to firm. Cool on pan for a couple of minutes and then completely on a rack before decorating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D87428340E1C447BCCA3CBAB388D309', X'ABF14B64AF55264E856F2C4D67D43130', X'ABF14B64AF55264E856F2C4D67D43130', 0, NULL, NULL, 2, NULL, 0, 'Peel and clean the shrimp, leaving tails on if you like. (Tails make a nice presentation and a useful handle, but you can serve the appetizer with cocktail forks or toothpicks instead.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3546B708663D44EAF1E79EF66968A71', X'ABF14B64AF55264E856F2C4D67D43130', X'ABF14B64AF55264E856F2C4D67D43130', 1, NULL, NULL, 2, NULL, 0, 'Cut each slice of prosciutto into two or three long strips. Wrap one strip around each shrimp. Place shrimp on a rimmed baking sheet lined with a piece of parchment paper that just fits.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F06145AE2B31C54792F7D2F14614F0E7', X'ABF14B64AF55264E856F2C4D67D43130', X'ABF14B64AF55264E856F2C4D67D43130', 2, NULL, NULL, 2, NULL, 0, 'Melt butter in a small pot. Brush about half of the butter over the shrimp. Preheat broiler to high.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED71B080845FF2438DC74F6410237108', X'ABF14B64AF55264E856F2C4D67D43130', X'ABF14B64AF55264E856F2C4D67D43130', 3, NULL, NULL, 2, NULL, 0, 'While broiler preheats, add garlic and sage to remaining butter. Cook on medium heat for a minute or two, until very fragrant.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA801BA276177B4CA8F6E5540534DABE', X'ABF14B64AF55264E856F2C4D67D43130', X'ABF14B64AF55264E856F2C4D67D43130', 4, NULL, NULL, 2, NULL, 0, 'Broil shrimp for about 90 seconds, until shrimp are turning opaque and prosciutto begins to cook. Flip shrimp with tongs and broil about 60 seconds more, until just cooked through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E91ABB2DE42D43BDE9B95C975ADCDB', X'ABF14B64AF55264E856F2C4D67D43130', X'ABF14B64AF55264E856F2C4D67D43130', 5, NULL, NULL, 2, NULL, 0, 'Transfer shrimp to a serving platter. Brush with garlic sage butter and sprinkle with black pepper. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD68E60E6191C646A91A0217CDD6677A', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 0, NULL, NULL, 2, NULL, 0, 'Fill a large pot halfway with water. Add 1 tablespoon salt. Bring water to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47AE8EAA7140DA43BAB257531534EA60', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, fill a large bowl with ice water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3DE1B52B6104649AA5E024F87EFE45A', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 2, NULL, NULL, 2, NULL, 0, 'When water boils, add broccoli and cook for one to two minutes, until bright green and not raw. Remove with a strainer to ice water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63B050FF93E784408BA60A1B348182AF', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 3, NULL, NULL, 2, NULL, 0, 'Repeat with asparagus.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C4ADD2CADA158489638172F8F14DA78', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 4, NULL, NULL, 2, NULL, 0, 'When vegetables are cool, blot dry and arrange on a large platter with endive and bread slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E24662B87193FC49A4AD8CF6FD389EF6', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 5, NULL, NULL, 2, NULL, 0, 'In a small pot, heat the olive oil and butter over medium heat until the butter starts to foam.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EC8072192F1AD439A3BCD56C4182CD2', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 6, NULL, NULL, 2, NULL, 0, 'Add garlic and anchovy and cook, mashing anchovy against bottom and sides of pot with a spoon, until most of the anchovy has dissolved into the sauce, about 3 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'982EC6DC8C98654A997438B0905FB41C', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat, season to taste with pepper, and pour into a small bowl (or serve directly from pot).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92A2F43F583395479794A7E86C4CE9A2', X'31F2F89FD58D9F4FBE48076CC75EC936', X'31F2F89FD58D9F4FBE48076CC75EC936', 8, NULL, NULL, 2, NULL, 0, 'Serve warm, over a little Sterno burner or tea light if you like, with crudités and crusty bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11FBC5F580D41548A93A2A38C672D18F', X'63CE439FFD2DBA4985A93C9073C4C890', X'63CE439FFD2DBA4985A93C9073C4C890', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with two racks close to the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C8CB9F23932F241852AB5503CB9AC6F', X'63CE439FFD2DBA4985A93C9073C4C890', X'63CE439FFD2DBA4985A93C9073C4C890', 1, NULL, NULL, 2, NULL, 0, 'On a piece of parchment, roll out the puff pastry sheet(s). If you''re using one 14-ounce sheet, roll it out into a 16-inch square. If you''re using two 8 1/2-ounce sheets, roll each out into a 12-inch by 14-inch rectangle.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EAD747E2A557A44A7591CB7A3FAB907', X'63CE439FFD2DBA4985A93C9073C4C890', X'63CE439FFD2DBA4985A93C9073C4C890', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the salt, cayenne, nutmeg and black pepper. Sprinkle over puff pastry. Next, sprinkle the shredded cheese over the puff pastry. It''s best if you vaguely aim for even distribution but inevitably don''t achieve it, which will make each bite a little different in a good way. Press down lightly with your hands to stick the toppings to the pastry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62CA8CEBE5A0C448D6DD9C4E0542520', X'63CE439FFD2DBA4985A93C9073C4C890', X'63CE439FFD2DBA4985A93C9073C4C890', 3, NULL, NULL, 2, NULL, 0, 'Fold each sheet in half (if using the two puff pastry rectangles, fold over the longer side to make 12-inch by 7-inch rectangles), then roll out again until the short side reaches 12 inches. Slice each sheet into 1-inch by 12-inch strips.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA1875CF3DF14E4F838E4C4C595A5387', X'63CE439FFD2DBA4985A93C9073C4C890', X'63CE439FFD2DBA4985A93C9073C4C890', 4, NULL, NULL, 2, NULL, 0, 'Line two baking sheets with parchment. Twist the two ends of each strip in opposite directions a few times and place twists on baking sheets with a little space in between. Brush with cream or egg wash.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'740F83AE1BFD724781ACAE3A9FF1D297', X'63CE439FFD2DBA4985A93C9073C4C890', X'63CE439FFD2DBA4985A93C9073C4C890', 5, NULL, NULL, 2, NULL, 0, 'Bake for about 15 minutes, until puffed and light brown. Rotate the pans and swap their oven positions halfway through. (Alternatively you can bake the pans one at a time in the center of the oven.) Cool slightly or completely, whichever you prefer, before serving. They''re best on the day you make them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5606CF56D9D9A4BB68381CA4AEC133A', X'4E29F4302A4BD549A3AB419B6DF67CBB', X'4E29F4302A4BD549A3AB419B6DF67CBB', 0, NULL, NULL, 2, NULL, 0, 'Pour the Campari into a champagne glass, fill the glass with Prosecco, stir gently and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B286394A973FC428AF91CBA2FEE83D0', X'22063A3B3B3D864A825595AFCCB8101A', X'22063A3B3B3D864A825595AFCCB8101A', 0, NULL, NULL, 2, NULL, 0, 'In a standard food processor fitted with the blade, pulse the Oreos until reduced to dust. Meanwhile, whip the cream cheese and peppermint extract. (I use the regular paddle attachment of my KitchenAid. You can also use a hand-held electric mixer or a spatula and a lot of elbow grease.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E7247475EDA294A845E6F8FD92FADAA', X'22063A3B3B3D864A825595AFCCB8101A', X'22063A3B3B3D864A825595AFCCB8101A', 1, NULL, NULL, 2, NULL, 0, 'Mix the cookie crumbs into the cream cheese until well combined. Chill for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A93F1343C853F649B2DA25A661B5EDEF', X'22063A3B3B3D864A825595AFCCB8101A', X'22063A3B3B3D864A825595AFCCB8101A', 2, NULL, NULL, 2, NULL, 0, 'Roll mixture into 30 1-inch balls and place on a parchment-lined baking sheet. Chill thoroughly. (I pop them in the freezer for an hour.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74E55B88D3CBB64CBE5E45EA06381D6B', X'22063A3B3B3D864A825595AFCCB8101A', X'22063A3B3B3D864A825595AFCCB8101A', 3, NULL, NULL, 2, NULL, 0, 'To temper the chocolate, roughly chop HALF of it and place in large microwave-safe bowl. Microwave in bursts (starting with one minute and following with 30 seconds), stirring in between, until chocolate is melted and reaches 115°F. Then place the entire remaining half (not chopped) into the bowl and stir until it is melted and the temperature of the melted chocolate is 90°. This can take a while, but it''s key. When the chocolate reaches 90°, use two forks to dip each truffle in the chocolate and roll to coat completely. Replace on parchment-lined baking sheet. I do about 5 at a time, stop to sprinkle quickly with crushed candy cane before they dry, and then continue. If you work quickly you shouldn''t have to reheat the chocolate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'792746D051637B4EA9BBB11C05E95649', X'EF62FF2AEC1C6640B1E299986ED662BE', X'EF62FF2AEC1C6640B1E299986ED662BE', 0, NULL, NULL, 2, NULL, 0, 'Mix together the ketchup, horseradish, tabasco, gin, celery salt, salt and lemon juice. Chill until ready to serve. Stir in parsley right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A91F5B99E6FDAF40BDAC877DD691894E', X'EF62FF2AEC1C6640B1E299986ED662BE', X'EF62FF2AEC1C6640B1E299986ED662BE', 1, NULL, NULL, 2, NULL, 0, 'Peel and devein shrimp, leaving tails attached. Place peels into a large pot. Add water, 1 tablespoon of the salt, wine or vermouth, peppercorns, coriander seeds, bay leaves, onion, tarragon and lemon. Bring to a boil, then reduce heat and simmer uncovered for 20 minutes. Remove from heat and let sit 30 minutes more, then strain broth through a fine sieve into a fresh pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24A371306F0CD44B95672A2C11D1188D', X'EF62FF2AEC1C6640B1E299986ED662BE', X'EF62FF2AEC1C6640B1E299986ED662BE', 2, NULL, NULL, 2, NULL, 0, 'Bring stock to a boil, then remove from heat. Add shrimp and cover. Leave for 3 minutes (still off the heat -- the shrimp `poach` in the residual heat and remain amazingly tender).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1652FB3876A5134196982D3CFB1F225E', X'EF62FF2AEC1C6640B1E299986ED662BE', X'EF62FF2AEC1C6640B1E299986ED662BE', 3, NULL, NULL, 2, NULL, 0, 'Fill a large bowl with ice water and the remaining tablespoon salt. Add shrimp and leave until just opaque, 3 minutes or a shade more if the shrimp are very big. Chill in fridge (I usually wrap in paper towels and put in a gallon zip top bag) until ready to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3001493820C344FAA4213AB4BB9E3D3', X'D5B1C76F147BAD40B5D30411E997FD1D', X'D5B1C76F147BAD40B5D30411E997FD1D', 0, NULL, NULL, 2, NULL, 0, 'Defrost the puff pastry in the fridge overnight or on the counter for a couple of hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F4BD5B1FDB80D4EB1F3480A2066427D', X'D5B1C76F147BAD40B5D30411E997FD1D', X'D5B1C76F147BAD40B5D30411E997FD1D', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Unfold the puff pastry and roll it out gently with a rolling pin until it grows by 2-3 inches horizontally and vertically. Cut it into quarters, and then cut each quarter into six equal rectangles. Tuck each rectangle in to a mini muffin cup and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'827D71F24A8C5A4CBCCDD5615F798BE5', X'D5B1C76F147BAD40B5D30411E997FD1D', X'D5B1C76F147BAD40B5D30411E997FD1D', 2, NULL, NULL, 2, NULL, 0, 'Trim the root end and the dark green leaves from the leeks and discard. Slice the remaining white and light green parts in half lengthwise and then cut crosswise into thin half-moons. Place in a colander and rinse thoroughly, then shake off any excess water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BC051C11F5DE04BBB19286AD8D12353', X'D5B1C76F147BAD40B5D30411E997FD1D', X'D5B1C76F147BAD40B5D30411E997FD1D', 3, NULL, NULL, 2, NULL, 0, 'Melt the butter in a frying pan over medium heat. Add leeks, a generous pinch of salt and some freshly ground black pepper and cook, stirring from time to time to prevent browning, until tender and reduced in volume by half, 5-10 minutes. Stir in cream, nutmeg and thyme and continue to cook until cream is thick enough to coat leeks without leaving liquid in the pan, about 5 minutes more. Remove from heat, crumble feta overtop and stir to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2D3432826C57C4AA85EF40EF1EDA9AA', X'D5B1C76F147BAD40B5D30411E997FD1D', X'D5B1C76F147BAD40B5D30411E997FD1D', 4, NULL, NULL, 2, NULL, 0, 'Beat egg in a small bowl with one teaspoon water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C76495F02DF24448CFC27635831AE3B', X'D5B1C76F147BAD40B5D30411E997FD1D', X'D5B1C76F147BAD40B5D30411E997FD1D', 5, NULL, NULL, 2, NULL, 0, 'Spoon a small amount of leek filling into each prepared muffin cup. Brush with a little bit of egg wash. Bake for about 20 minutes, until pastry is nicely browned. Serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21E970984AAFFA4994BEDCEAC3C0F4F1', X'318C1A6BC21524428306574CD57EE81E', X'318C1A6BC21524428306574CD57EE81E', 0, NULL, NULL, 2, NULL, 0, 'Scrub the oysters well under running water to remove all sand and dirt. To shuck each oyster, hold it in a kitchen towel with the cupped side of the shell facing down so you won''t lose the beautiful liquor when you open it. Insert the tip of an oyster knife firmly into the hinge and twist until it pops open. Then run the knife across the inside of the top shell. Watch the video linked in the notes section below to see this done.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB8703C437270B41B3D385B7CFD9B939', X'318C1A6BC21524428306574CD57EE81E', X'318C1A6BC21524428306574CD57EE81E', 1, NULL, NULL, 2, NULL, 0, 'Run the oyster knife between the meat and the bottom shell to fully release the meat from the shell so it can be slurped up later. Place shucked oysters on a rimmed baking pan lined with a big sheet of gently crumpled aluminum foil to prevent tipping. Discard top shells.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34190F3EB99FA24180BCF787A2799590', X'318C1A6BC21524428306574CD57EE81E', X'318C1A6BC21524428306574CD57EE81E', 2, NULL, NULL, 2, NULL, 0, 'In a small pot, melt the butter with the shallot over medium heat and simmer for 5 minutes. Remove from heat and stir in the basil, parsley and lemon zest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C55B7307A721D74BAE2EE1AC7CA5C161', X'318C1A6BC21524428306574CD57EE81E', X'318C1A6BC21524428306574CD57EE81E', 3, NULL, NULL, 2, NULL, 0, 'Line the bottom of a heavy 12-inch frying pan with about 1/4 inch of Kosher salt. (I really prefer a cast iron skillet for its superior heat retention). Preheat the pan over medium-high heat for at least 5 minutes, until the salt starts to make some little popping noises. Then quickly arrange the shucked oysters in the pan and top each with a spoonful of the herb butter. Cover the pan with a lid (carefully use foil if you don''t have a lid that fits) and cook for three minutes, until the oysters are just opaque. They''ll continue to cook a bit from the residual heat in the pan. Sprinkle with a little bit of chopped prosciutto and a squeeze of lemon juice. Serve warm straight from the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B944AF161CBB0429A8A909DB5A7891D', X'65083A4995F1204CAA734911F534131D', X'65083A4995F1204CAA734911F534131D', 0, NULL, NULL, 2, NULL, 0, 'Pour all ingredients into a 9x13-inch glass baking dishand stir.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F4587D2B936754C89739F95007F6400', X'65083A4995F1204CAA734911F534131D', X'65083A4995F1204CAA734911F534131D', 1, NULL, NULL, 2, NULL, 0, 'Cover well and freeze overnight.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5A59FFA71CC9E49BA6C38D2ECAD98CE', X'65083A4995F1204CAA734911F534131D', X'65083A4995F1204CAA734911F534131D', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, use a fork to scrape into a slushie-like consistency. This will be easy since vodka doesn''t completely freeze.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DFCA9DDEB326E46BA48EA6A60245ABE', X'65083A4995F1204CAA734911F534131D', X'65083A4995F1204CAA734911F534131D', 3, NULL, NULL, 2, NULL, 0, 'Scoop into glasses and serve. It''s great frozen but also as it melts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EABF749029EA58409038684535AE4EA5', X'9D163B29257E614F865B02B882272A03', X'9D163B29257E614F865B02B882272A03', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in the braiser or skillet over medium-high heat. Add the onion, peppers, and shiitakes all at once, along with 1/2 teaspoon of the salt. Cook, stirring from time to time, for about 10 minutes, until the vegetables have softened, browned a bit, and decreased in volume by at least half. Remove from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C5F90762598049A587A0BDA16264C5', X'9D163B29257E614F865B02B882272A03', X'9D163B29257E614F865B02B882272A03', 1, NULL, NULL, 2, NULL, 0, 'While the other vegetables cook, place the defrosted broccoli on a thick layer of paper towels and cover with another thick layer. When the veggies in the skillet have cooked as noted above, press down gently on the paper towels to remove moisture from the broccoli. The add broccoli to the skillet and stir to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F1FCD7DC266574ABCE26469EBBF62DD', X'9D163B29257E614F865B02B882272A03', X'9D163B29257E614F865B02B882272A03', 2, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a large mixing bowl and beat well with a fork. Stir in milk, nutmeg, cayenne, and a few good grinds black pepper. Add bread cubes and stir to moisten. Then stir in all the vegetables, half the gruyere and half the parmesan or pecorino. Pour the whole mixture back into the braiser or into the baking pan and sprinkle with the remaining cheese. Cover and refrigerate overnight or for at least a few hours before proceeding. Remove from fridge about an hour before baking if possible.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5914B26504544D4D91F8867998C84112', X'9D163B29257E614F865B02B882272A03', X'9D163B29257E614F865B02B882272A03', 3, NULL, NULL, 2, NULL, 0, 'An hour before serving time, preheat oven to 375°F with a rack in the center. Bake uncovered for about 40 minutes, until puffed and golden. Serve hot or even at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4955103B87A9D4683521FFC74D1A767', X'FB5D26EABA60B9449D13CF8D8FA5C15F', X'FB5D26EABA60B9449D13CF8D8FA5C15F', 0, NULL, NULL, 2, NULL, 0, 'Snip open the tea bag and pour the contents into a small microwave-safe bowl (or a small pot if you’d prefer to use the stovetop). Add the butter and warm on medium heat until melted. Set aside to let the tea steep in the butter for five minutes. (You''ll strain out the tea solids later.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B606F1D8E61314E903C11D074144C4D', X'FB5D26EABA60B9449D13CF8D8FA5C15F', X'FB5D26EABA60B9449D13CF8D8FA5C15F', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, stir together the flour, sugar, baking powder, cinnamon, ginger, nutmeg, cloves and allspice. Into the center of the bowl, crack the eggs and pour the buttermilk. Strain the butter into the bowl through a tea strainer or small fine-mesh sieve. Using a fork, beat the eggs a bit and then gently incorporate the dry ingredients into the wet to form a rough dough. Don’t overmix or donuts will be tough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89D0780421979844A4C2979995C4BE8B', X'FB5D26EABA60B9449D13CF8D8FA5C15F', X'FB5D26EABA60B9449D13CF8D8FA5C15F', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle a little flour on your work surface. (I sometimes like to roll out dough on a nice big piece of parchment — flour the parchment if you go that route.) Place the dough on the work surface and knead just a few times to bring it all together. Lightly flour the top of the dough and a rolling pin, and roll out to about 1/4-inch thickness. Using a 3 1/2-inch donut cutter with a 1-inch hole (or similarly sized cookie cutters, biscuit cutters or even an overturned water glass and shot glass) cut out as many donuts and holes as you can. Gather and re-roll the dough one time to make more donuts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11A830DA7B2AF74CA39DC1C8AA1272AC', X'FB5D26EABA60B9449D13CF8D8FA5C15F', X'FB5D26EABA60B9449D13CF8D8FA5C15F', 3, NULL, NULL, 2, NULL, 0, 'In a Dutch oven or similar heavy pot with high sides, heat the oil over medium-high heat until it reaches 350°F. During cooking, adjust the heat as necessary to maintain the temperature as best as possible. Fry donuts in batches, about three at a time depending on the size of your pot. It should take about 1 1/2 to 2 minutes per side until donuts are golden brown and cooked through. Some cracking on the top before you flip each donut is a good indication that the inside is nicely cooked. Cool donuts on a wire rack lined with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE26879E5CE4BE4F97B2EC344C9C1FF1', X'FB5D26EABA60B9449D13CF8D8FA5C15F', X'FB5D26EABA60B9449D13CF8D8FA5C15F', 4, NULL, NULL, 2, NULL, 0, 'In a wide, shallow bowl, stir together the powdered sugar, cinnamon and nutmeg. Pour in the tea and vanilla and stir until a nice smooth glaze forms. Dip the top of each donut into the glaze and return to rack. The glaze should dry within a few minutes. Serve donuts on the day you make them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43F33CDF4149FE4BB6EAA7994CA8AB26', X'743CA7383218244188DC7B735FB3B540', X'743CA7383218244188DC7B735FB3B540', 0, NULL, NULL, 2, NULL, 0, 'On a countertop or table, arrange a few nice-looking boards and three small bowls. Arrange cheeses, meats, fruits, olives, bread or crackers and other ingredients as if they just rolled out of fridge into their awesome, awesome life. Share with as many friends as you can find, or just a few if that sounds like more fun. Serve with plenty of wine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB3DD585327B0544AD0BD22DD568FA4F', X'C126E67847EDBF4F8C2934B0AB93B096', X'C126E67847EDBF4F8C2934B0AB93B096', 0, NULL, NULL, 2, NULL, 0, 'In a cocktail shaker filled halfway with ice, shake clementine juice, lime juice and vodka until ice cold.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A7A9A8D8F07CE40925F1E8242E700B8', X'C126E67847EDBF4F8C2934B0AB93B096', X'C126E67847EDBF4F8C2934B0AB93B096', 1, NULL, NULL, 2, NULL, 0, 'Strain into a glass (preferably a copper mug) filled about 2/3 full with ice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71488F66AEF7DC4DB0AA6ACC7F7308BB', X'C126E67847EDBF4F8C2934B0AB93B096', X'C126E67847EDBF4F8C2934B0AB93B096', 2, NULL, NULL, 2, NULL, 0, 'Top with 4 ounces ginger beer and serve, garnished with clementine or lime if you like. (I like to give it a quick stir before garnishing, though I''m sure that''s illegal somehow.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ECDEF1F9619BA4A865B1C4DEBB6EE26', X'155BC55022ECF5499726581CA5C66808', X'155BC55022ECF5499726581CA5C66808', 0, NULL, NULL, 2, NULL, 0, 'Fill each jar with about six heaping tablespoons of either herb salt or spiced sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FDFDC63CA7DD14BB9EC1E7412F8D8F9', X'155BC55022ECF5499726581CA5C66808', X'155BC55022ECF5499726581CA5C66808', 1, NULL, NULL, 2, NULL, 0, 'Print free printable labels, above, onto label sheets. It''s a good idea to test your printer first on a plain piece of paper and then hold that paper up to the light with a sheet of labels behind it to check for alignment. Make sure to print at 100%, not `fit to page.`');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20522BCEEA990948ADB8CC6D79672C16', X'155BC55022ECF5499726581CA5C66808', X'155BC55022ECF5499726581CA5C66808', 2, NULL, NULL, 2, NULL, 0, 'Adhere a label onto the flat insert on each jar lid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F591A0BCFF4DB4CBA4AA0C9AC594F2D', X'155BC55022ECF5499726581CA5C66808', X'155BC55022ECF5499726581CA5C66808', 3, NULL, NULL, 2, NULL, 0, 'Screw lids onto jars and gift away. Salt and sugar keep well for at least a year.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE094714A600354380F950D23EFC17BD', X'F104E5C8325D3C4A80F93F3DC47A5A8C', X'F104E5C8325D3C4A80F93F3DC47A5A8C', 0, NULL, NULL, 2, NULL, 0, 'Pour the sugar into in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'754DE946F9036549B495492FFD05BB24', X'F104E5C8325D3C4A80F93F3DC47A5A8C', X'F104E5C8325D3C4A80F93F3DC47A5A8C', 1, NULL, NULL, 2, NULL, 0, 'Slit the vanilla beans lengthwise with a small, sharp knife and scrape out the seeds. Add the seeds to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22A22B120A940B4C8E10E42E5B896A7D', X'F104E5C8325D3C4A80F93F3DC47A5A8C', X'F104E5C8325D3C4A80F93F3DC47A5A8C', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle in the cinnamon, cardamom and nutmeg and stir well to distribute all the spices evenly. If there are any clumps, break them up and pass the mixture through a sieve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36ADDEF278DC8240AEE0D01FC67F7574', X'F104E5C8325D3C4A80F93F3DC47A5A8C', X'F104E5C8325D3C4A80F93F3DC47A5A8C', 3, NULL, NULL, 2, NULL, 0, 'Divide spiced sugar among 12 small jars such as these wide-mouth half pint jars — about 6 heaping tablespoons per jar plus a little extra for good luck.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'398DE4B11A1C4745A4F3243902297EF0', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A475A4738645547AE2F99C6CD020346', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 1, NULL, NULL, 2, NULL, 0, 'Cut the butter into small pieces and place it in a large bowl (or the bowl of a stand mixer) with the sugar. Beat on medium speed until lightened in color and fluffy. Add the vanilla extract and beat to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E29E2AE3FE932C4288A0130F787B0710', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and stir in gently with a spatula or wooden spoon until incorporated. Gather dough into your hands and shape into two balls. You''ll need to give it a good squeeze to get it to hold together well. You can chill it for 20 minutes or so at this point or proceed directly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8D9C516AB0734AB33E7ABE708021BC', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 3, NULL, NULL, 2, NULL, 0, 'Dust work surface and rolling pin with flour and gently roll each dough ball into a thin disk, about 1/8 inch, but don''t stress.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC9086E45526134F8E3296DA876F3AE0', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 4, NULL, NULL, 2, NULL, 0, 'Using a 2 1/2-inch round cookie/biscuit cutter, cut as many circles as you can. You should get between 90 and 100 in total, after you re-roll any scraps and use them to make a few more cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E723934D80804C89593E48C323198F', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 5, NULL, NULL, 2, NULL, 0, 'Using a small snowflake cookie cutter (or any small festive shape you like), cut into the center of half the circles. Remove the snowflakes to bake separately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'790EF69D80CDAD4699EE2DC588667268', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 6, NULL, NULL, 2, NULL, 0, 'Transfer cookies to cookie sheets lined with parchment and chill in the fridge for 15 minutes to help cookies retain their shape in the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96370EC4604C154BB72A5B7E647B34DC', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 7, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes, until very lightly golden.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D0D19541614149837F3E29419696B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 8, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer cookies to a wire rack to cool completely. Cookies will keep well at this stage for a few days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B59C12C40AB24E4490DDD5847CB83570', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 9, NULL, NULL, 2, NULL, 0, 'The day you plan to present the cookies, spread a nice layer of jam over the flat (bottom) side of each of the intact circles. Heap a little bit of jam in the center and leave a little border around the edge.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1029AB6219E50945ACD5D8D03592B09F', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 10, NULL, NULL, 2, NULL, 0, 'Dust the tops of the snowflake-cutout circles, as well as the snowflakes themselves, with plenty of powdered sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43FEE9295861AD4CBB27700590B83FF8', X'782DDA1BF35FBE4DA7F13D02D104A1B3', X'782DDA1BF35FBE4DA7F13D02D104A1B3', 11, NULL, NULL, 2, NULL, 0, 'To assemble the cookies, gently press a cutout circle onto each jam-covered circle, which should help the jam fill in the snowflake shape and also spread to the outside edges of the cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E7677D8C57D2F45AD56EDFB3B815748', X'5D72EDE09AE5E246B78029DFB61B6EA2', X'5D72EDE09AE5E246B78029DFB61B6EA2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D79961AE83202543A767101B701820C7', X'5D72EDE09AE5E246B78029DFB61B6EA2', X'5D72EDE09AE5E246B78029DFB61B6EA2', 1, NULL, NULL, 2, NULL, 0, 'Cut the butter into small pieces and place it in a large bowl (or the bowl of a stand mixer) with the sugar. Beat on medium speed until lightened in color and fluffy. Add the vanilla extract and beat to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B757C5AE1E74D48A554E97EF0265126', X'5D72EDE09AE5E246B78029DFB61B6EA2', X'5D72EDE09AE5E246B78029DFB61B6EA2', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and stir in gently with a spatula or wooden spoon until incorporated. Gather dough into your hands and squeeze it together until it forms a cohesive dough. Shape into a ball.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95744AA0141D1543941AF202D5DFE975', X'5D72EDE09AE5E246B78029DFB61B6EA2', X'5D72EDE09AE5E246B78029DFB61B6EA2', 3, NULL, NULL, 2, NULL, 0, 'Dust work surface and rolling pin with flour and gently roll dough into a 1/4-inch thick disk. Cut into shapes using a cookie cutter. You can re-roll any scraps and use them to make a few more cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6FC6966E2D89A4696928F271FA48087', X'5D72EDE09AE5E246B78029DFB61B6EA2', X'5D72EDE09AE5E246B78029DFB61B6EA2', 4, NULL, NULL, 2, NULL, 0, 'Transfer cookies to a cookie sheet lined with parchment and chill in the fridge for 15 minutes to help cookies retain their shape in the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'323D7058CC13CD4C9CFB7B0B31768AB4', X'5D72EDE09AE5E246B78029DFB61B6EA2', X'5D72EDE09AE5E246B78029DFB61B6EA2', 5, NULL, NULL, 2, NULL, 0, 'Bake about 12 minutes, until very lightly golden.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2495A816A924E46B059981DFCFDEA82', X'5D72EDE09AE5E246B78029DFB61B6EA2', X'5D72EDE09AE5E246B78029DFB61B6EA2', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer cookies to a wire rack to cool. Decorate if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'623B01EC32C9214DBB01D60250545E04', X'650FBB79876DD24FBE1E019A7840FB23', X'650FBB79876DD24FBE1E019A7840FB23', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Spray a 9- or 10-inch loaf pan with cooking spray and line with parchment that overhangs slightly on two sides.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C143345FF83DFD4F881D2C91456B942C', X'650FBB79876DD24FBE1E019A7840FB23', X'650FBB79876DD24FBE1E019A7840FB23', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a large skillet over medium-high heat. Add the onion and a sprinkle of the salt and cook, stirring occasionally, until softened, about 5 minutes. Add the minced mushrooms and another sprinkle of the salt, raise heat to high, and cook until they have released their juices and reabsorbed them, about 7 minutes. Add garlic, thyme, oregano, basil, tarragon and sage and cook 2 minutes more. Deglaze the pan with the wine, scraping up any browned bits, and cook until liquid is absorbed, about 1 minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5166DADDCDF05498241CB8C4BB15A36', X'650FBB79876DD24FBE1E019A7840FB23', X'650FBB79876DD24FBE1E019A7840FB23', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, beat together the eggs and cottage cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A9B84D2F488D7418F7605D506FA6736', X'650FBB79876DD24FBE1E019A7840FB23', X'650FBB79876DD24FBE1E019A7840FB23', 3, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, toss together the brown rice, walnuts and cashews. Stir in the egg mixture, then add the mushroom mixture, cheeses, parsley, remaining salt and pepper. Mix well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'846EA2F06CB83A459D6ABD226EC5B4EC', X'650FBB79876DD24FBE1E019A7840FB23', X'650FBB79876DD24FBE1E019A7840FB23', 4, NULL, NULL, 2, NULL, 0, 'Spoon mixture into loaf pan and smooth top. If desired, decorate with a few mushroom slices or walnut halves. Place loaf pan on a rimmed baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84AE58B5C9C2454B9A046C914A709832', X'650FBB79876DD24FBE1E019A7840FB23', X'650FBB79876DD24FBE1E019A7840FB23', 5, NULL, NULL, 2, NULL, 0, 'Bake until firm, about 60 minutes. Let cool in the pan for 10 minutes before removing to a platter to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F8CAEF275271D4E99EB454B19EB771B', X'FBEE7B1C39C3D847BCFAF4B5B6D9698B', X'FBEE7B1C39C3D847BCFAF4B5B6D9698B', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, beat the butter with an electric mixer until lightened in color and texture, 1 to 2 minutes. Gradually add the sugar and continue to beat until the mixture is light, about 2 minutes more. Add the eggs and beat until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69658B4EDAC2B24A88B5F541978EC869', X'FBEE7B1C39C3D847BCFAF4B5B6D9698B', X'FBEE7B1C39C3D847BCFAF4B5B6D9698B', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, sift together 3 cups of the flour and the salt, cinnamon, and nutmeg. Add half the flour mixture to the bowl with the wet ingredients and stir to combine. Then add half the sherry and stir to incorporate. Repeat with remaining flour mixture and sherry. When all is well blended, add enough additional flour to stiffen the dough, about 1 cup. Wrap the dough in plastic wrap and refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'818C5D0035F12C4CBE81892158711FBF', X'FBEE7B1C39C3D847BCFAF4B5B6D9698B', X'FBEE7B1C39C3D847BCFAF4B5B6D9698B', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°F. Grease two baking sheets. Flour a work surface and roll out the dough to a mere 1/16 inch thick. Cut out cookies with a cookie cutter. Place on cookie sheets, leaving an inch of space between cookies. You will need to bake in several batches. Bake for about 7 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D29439206055244BBBA8E8C5A3F92268', X'4F719CA3FD1E0844A0015675FAD13707', X'4F719CA3FD1E0844A0015675FAD13707', 0, NULL, NULL, 2, NULL, 0, 'Place half of cheese, along with onion, garlic puree, chives, mustard and cayenne, in the bowl of a food processor fitted with the metal blade. Process until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C94D1F8268A5E4793BADA0FD4685EC8', X'4F719CA3FD1E0844A0015675FAD13707', X'4F719CA3FD1E0844A0015675FAD13707', 1, NULL, NULL, 2, NULL, 0, 'With the motor running, add the sherry and olive oil. Process to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2CA000133C6A1429CA0A6A5A14F2EFB', X'4F719CA3FD1E0844A0015675FAD13707', X'4F719CA3FD1E0844A0015675FAD13707', 2, NULL, NULL, 2, NULL, 0, 'Add the remaining cheese and process until very thick and smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92CD0DACC99FA04A9033CD30BE0C52EA', X'4F719CA3FD1E0844A0015675FAD13707', X'4F719CA3FD1E0844A0015675FAD13707', 3, NULL, NULL, 2, NULL, 0, 'Add additional sherry 1 tablespoon at a time to thin, if necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1803AE245EBEE549AA166CFCBB3515E6', X'4F719CA3FD1E0844A0015675FAD13707', X'4F719CA3FD1E0844A0015675FAD13707', 4, NULL, NULL, 2, NULL, 0, 'Pack into 8-ounce crocks. Refrigerate for up to one month. Bring to room temperature before serving with crackers or toasts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84848BD3C107D4497CA57374030994D', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 0, NULL, NULL, 2, NULL, 0, 'Line a rimmed sheet pan with parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A145342622FA934DA3549F6354FB00C8', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 1, NULL, NULL, 2, NULL, 0, 'Place the salmon, skin side down, on the parchment. Brush evenly with the lemon juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B62AA0E5DA1D348B04FEB6857976E53', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 2, NULL, NULL, 2, NULL, 0, 'Combine salt, sugar, and cracked pepper in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECE540A086013343B38962347DB92BC9', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the mixture over the salmon, covering completely. The layer of curing mixture should be a little thicker where the salmon is thicker and thinner where the fillet thins out toward the tail.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BFD10D8501A694CAB0D1446B23D08B2', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 4, NULL, NULL, 2, NULL, 0, 'Cover evenly with the dill.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B2ECDD9861A6148800F27EF8365743A', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 5, NULL, NULL, 2, NULL, 0, 'Cover the salmon with another layer of parchment and then another sheet pan. Weight the top sheet pan with two 16-ounce cans.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F8FCA40D9270C4DB476B151AD09B048', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 40-48 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9554F085F923EF44BB9F43A5650F269D', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 7, NULL, NULL, 2, NULL, 0, 'Scrape the dill and curing mixture from the salmon. Rinse well and pat dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A972D36DE7F92F41B518058F32CEBA99', X'07374ACA3788684CBB8945773D7B9D32', X'07374ACA3788684CBB8945773D7B9D32', 8, NULL, NULL, 2, NULL, 0, 'Slice very thinly to serve. The salmon can be refrigerated for up to 5 days before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'062D3A220C282C4B8AA14EB02596F430', X'7130E60AD5797E47A03C9A0FBC2F5E42', X'7130E60AD5797E47A03C9A0FBC2F5E42', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a very small pot. Bring mixture to a boil over medium heat. Boil for 30 seconds, then remove from heat and let cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40860E6CE5EC2246B3CA4B81F51596C4', X'7130E60AD5797E47A03C9A0FBC2F5E42', X'7130E60AD5797E47A03C9A0FBC2F5E42', 1, NULL, NULL, 2, NULL, 0, 'Pour cooled mixture through a fine-meshed sieve into a small baking dish or other freezer-safe container just big enough to hold the mixture in a shallow layer. (Protip: save the strained shallot and toss into your next salad.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D184B09C2FE3E643963FD904EB0227C5', X'7130E60AD5797E47A03C9A0FBC2F5E42', X'7130E60AD5797E47A03C9A0FBC2F5E42', 2, NULL, NULL, 2, NULL, 0, 'Freeze the mixture, scraping every hour or so with a fork to break up the ice into small pieces, until fully frozen (probably about 3 hours).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1CF8626D36FC74287F7AF013744F0E9', X'7130E60AD5797E47A03C9A0FBC2F5E42', X'7130E60AD5797E47A03C9A0FBC2F5E42', 3, NULL, NULL, 2, NULL, 0, 'Serve in a small, very cold bowl as a condiment for oysters on the half shell.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C6CB22393E3D24DBAB944BA84B755DE', X'DF6CDCFD76DD944FBFB5B9CC48784222', X'DF6CDCFD76DD944FBFB5B9CC48784222', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47A4509992CBD04AA9AF335B7A26A736', X'551F53F8AE028A4C93737B297747A2A9', X'551F53F8AE028A4C93737B297747A2A9', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F with a rack in the center. Grease a 9-by-13-by-2-inch pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F619DF2AF55474B99FAAF226AF4D401', X'551F53F8AE028A4C93737B297747A2A9', X'551F53F8AE028A4C93737B297747A2A9', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the flour, sugar, baking powder, ground ginger, cinnamon, nutmeg, allspice, cloves, and salt. Whisk to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55882D72B2CC2D4F8FB088EF6EE1618A', X'551F53F8AE028A4C93737B297747A2A9', X'551F53F8AE028A4C93737B297747A2A9', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the eggs, oil, milk, and vanilla. Whisk to combine. Pour the wet ingredients over the dry ingredients and stir until just mixed well. Stir in parsnips and walnuts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7130E354D18751459A661D1EBD1A66AE', X'551F53F8AE028A4C93737B297747A2A9', X'551F53F8AE028A4C93737B297747A2A9', 3, NULL, NULL, 2, NULL, 0, 'Pour batter into prepared pan and bake for about 25 minutes, until a tester comes out clean from the center of the cake. Cool completely on a rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'134A63D84AFE9F44AFFEEADFA97A6305', X'551F53F8AE028A4C93737B297747A2A9', X'551F53F8AE028A4C93737B297747A2A9', 4, NULL, NULL, 2, NULL, 0, 'When cool, frost if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49E91C1E4DD09544B609ED975E614C3C', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 0, NULL, NULL, 2, NULL, 0, 'Have all ingredients at room temperature. Preheat oven to 325°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCF718E278E9544FB7DD98D121763662', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 1, NULL, NULL, 2, NULL, 0, 'Spread the butter all over the bottom and sides of a 9-inch springform pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5210827533E747488598F7EB24F5D145', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 2, NULL, NULL, 2, NULL, 0, 'Crush the graham crackers into crumbs. You can do this in a sealed plastic bag with a rolling pin or in a food processor. Pour the graham cracker crumbs into the greased pan and tap to distribute them around the bottom and sides, forming a thin crust. Tap out any excess.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30C3CE7A1F23E24CA834BD0C6D537B4F', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 3, NULL, NULL, 2, NULL, 0, 'Place the raspberries and the two tablespoons of sugar into a blender and process until smooth. Pass the puree through a fine-mesh strainer to remove all the seeds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECA87FA3AD4B87458A9D6B11AFCC0B93', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, beat the softened cream cheese for 30 to 60 seconds, just until smooth. Scrape down the sides of the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06DE9BEBDCE04041A4D424BD2A48805C', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 5, NULL, NULL, 2, NULL, 0, 'Pour in the sugar little by little and beat until smooth and creamy, a minute or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1257A33B75B802419811BDC44763DC12', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 6, NULL, NULL, 2, NULL, 0, 'Add the eggs one at a time, beating until just incorporated and scraping down the sides of the bowl as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1B6CB9284E4F644945B316D9575A969', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 7, NULL, NULL, 2, NULL, 0, 'Add the heavy cream, sour cream, lemon zest, lemon juice, and vanilla. Beat briefly, just until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E45E1C829A6B92429FC90564BBCBDEFD', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 8, NULL, NULL, 2, NULL, 0, 'Pour creamy filling into crust.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF819F4FFF081D44B11ACA9F3C60FB00', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 9, NULL, NULL, 2, NULL, 0, 'Pour raspberry puree over creamy filling. Use a knife to swirl it into the top of the filling any way you like. (Refer to video for visual cues.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47BD889ABEE85E4AB69D750ACD571077', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 10, NULL, NULL, 2, NULL, 0, 'Wrap the outside of the pan tightly with foil. Place the pan into a roasting pan or large baking dish. Position the pan on the center oven rack and then carefully pour boiling water into the outer roasting pan to reach halfway up the sides of the cheesecake pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0763B3C188CA4E4695EFB7BBEF3DD5C6', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 11, NULL, NULL, 2, NULL, 0, 'Bake for 55 to 60 minutes, until the outsides of the cake are set and the center still jiggles when you move the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECD62C9456C0D44DACA72C1870F28AA2', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 12, NULL, NULL, 2, NULL, 0, 'Turn off the oven and leave the door ajar (you can use a utensil to prop it open). Let the cake rest in the oven for an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'993297BE17A3FC47B6B4C2DCBDEE8C55', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 13, NULL, NULL, 2, NULL, 0, 'Remove cake from oven and from water bath. Remove foil. Let cake cool completely in pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E996AE716F71524BAB2713A5A890D1FC', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 14, NULL, NULL, 2, NULL, 0, 'Cover cake and refrigerate overnight to chill through completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F68D9D7D49072449EC2682DED8B4C13', X'06FF32E8A1822E4F9526E40746E13071', X'06FF32E8A1822E4F9526E40746E13071', 15, NULL, NULL, 2, NULL, 0, 'Carefully remove outer ring from cake and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14CD1EAD9B45434CA83F449F28FC3E88', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Butter and flour an 8 x 8-inch baking pan, tapping out any excess flour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4142D6F8A7D1EE4199225F360029ABCB', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', 1, NULL, NULL, 2, NULL, 0, 'Into a medium mixing bowl, sift together the flour, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41C66741E31DE84D886DBE1A2817B285', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl or the bowl of a stand mixer fitted with the whisk, beat butter and sugar together on medium-high until pale and fluffy, about 5 minutes. Beat in the eggs one at a time until incorporated. Add vanilla and beat for 5 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62E630982941B740B5398D2F45CFA45B', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', 3, NULL, NULL, 2, NULL, 0, 'Reduce mixer speed to low and alternate between adding the flour mixture (in 3 parts) and the milk (in two parts), starting and ending with flour. Mix just until batter is smooth — do not overmix.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526A0EAF098E8C44AC8C09261458CE49', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', X'7602C56D2C7C4E4CA7EF9279CAA09BF6', 4, NULL, NULL, 2, NULL, 0, 'Spread batter into pan and bake for 30-35 minutes, just until a cake tester comes out clean from the center. Cake will be pale, not brown. Cool completely before frosting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7552E00B3F88B14F9A21FC87C0475795', X'073A9F649D3CB2488AED2DF355483F61', X'073A9F649D3CB2488AED2DF355483F61', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 300° F. (Yup, just 300!) Spray both pans with cooking spray. Cut out two circles of parchment paper to cover the bottoms of the pans. Place them in the greased pans and spray those, too.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C28F17538AC62C46B1D1529BBC0334EB', X'073A9F649D3CB2488AED2DF355483F61', X'073A9F649D3CB2488AED2DF355483F61', 1, NULL, NULL, 2, NULL, 0, 'Combine the hot coffee with the chopped chocolate in a heatproof bowl or glass measuring cup. Let sit for a few minutes and then stir until the chocolate is completely melted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98C7040D702BE34599116C5C34C6D7AD', X'073A9F649D3CB2488AED2DF355483F61', X'073A9F649D3CB2488AED2DF355483F61', 2, NULL, NULL, 2, NULL, 0, 'In a very large bowl or the bowl of a stand mixer, beat the eggs until thickened a bit and lightened in color, about 4 minutes. Add the coffee-chocolate mixture, oil, buttermilk, and vanilla and beat on low until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A483841E8B828F418EE91AAD5798E049', X'073A9F649D3CB2488AED2DF355483F61', X'073A9F649D3CB2488AED2DF355483F61', 3, NULL, NULL, 2, NULL, 0, 'Into a large bowl, sift together the sugar, flour, cocoa powder, baking soda, baking powder and salt. Add the dry ingredients to the wet ingredients all at once and mix on low just until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D32436E230DCF42BBF499EF5947C99C', X'073A9F649D3CB2488AED2DF355483F61', X'073A9F649D3CB2488AED2DF355483F61', 4, NULL, NULL, 2, NULL, 0, 'Pour half the batter into each pan and bake until a cake tester inserted into the center comes out clean, approximately 60 to 70 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'043CDD51DCF1B7469E852CCFD6EDD238', X'073A9F649D3CB2488AED2DF355483F61', X'073A9F649D3CB2488AED2DF355483F61', 5, NULL, NULL, 2, NULL, 0, 'Let the cake cool completely in the pans on cooling racks. Then run a knife around the sides of each pan and turn out the layers. Remove the parchment. The cake is ready for frosting when completely cool.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6672BDA9AD6C7247B1B09EC1D37144CB', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F with a rack in the center. Butter or spray a 9-inch round cake pan (I have one like this, or try one of these) with cooking spray. Line the bottom of the pan with a circle of parchment paper and butter or spray that too.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83F75E9FD80C5A4997A87B51DD2BC0A0', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 1, NULL, NULL, 2, NULL, 0, 'In a strainer, toss zucchini with 1 cup of the sugar and let sit for 10 minutes to drain. Use your hands to squeeze out all the liquid you can.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C71938ABCC4E3A489EA39461F3C0722C', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, beat eggs with a fork, then beat in zucchini and oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA80F85B6BDCEE46BCB889255C861868', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle in the flour, remaining 1 2/3 cups sugar, cocoa powder, baking soda, baking powder, cinnamon and salt. Use the fork to stir the dry ingredients together a bit and then incorporate into the wet ingredients, mixing gently until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355EDCA7AA2E4444BF1C2DE749957017', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 4, NULL, NULL, 2, NULL, 0, 'Stir in chocolate chips.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DE3E415293EEE4C91E4162B9ADCA53E', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 5, NULL, NULL, 2, NULL, 0, 'Scrape batter into pan and spread evenly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CADF8A7EF3BA7B4DAAC9D5194C96894D', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 6, NULL, NULL, 2, NULL, 0, 'Bake for 50 to 60 minutes, until the center doesn''t jiggle and a tester comes out clean unless you hit a chocolate chip.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D52FD8DE6CE6684E982F87C9B5114101', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 7, NULL, NULL, 2, NULL, 0, 'Cool in pan on a rack for at least 40 minutes before removing from pan to cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F45B88BD688DA4AA21FB899D9A3E0C1', X'7EB92E485DC14046B040108C327EADA6', X'7EB92E485DC14046B040108C327EADA6', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle with powdered sugar and serve at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EAFE400CD9D9046B252F305D854DE28', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 0, NULL, NULL, 2, NULL, 0, 'Have all ingredients at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E88A3DEE33FDE439438AE174C5C3026', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center, and grease a 9 x 13 x 2-inch baking pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F91E4CB7FC1DE043AFADA6D6EC7FC891', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, stir the rhubarb pieces together with two tablespoons of the sugar. Let sit while you make the batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEF44950E151AA4382DC85A7731E089C', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 3, NULL, NULL, 2, NULL, 0, 'In another medium mixing bowl, stir together the flour, baking powder, baking soda, salt, and cardamom.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7196EA8B4FB02F4E81B1358167F67787', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the sour cream and vanilla extract.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06A742F65683CA4A975C4A4ECE4137AE', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 5, NULL, NULL, 2, NULL, 0, 'In a large bowl (or the bowl of a. stand mixer), beat together the butter and the remaining cup sugar until lightened in color and texture, about 3 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F465DF5E72CFE0448D886BC0737786BE', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 6, NULL, NULL, 2, NULL, 0, 'Add the eggs one at a time, beating to incorporate after each addition.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'137E78B4633D1447B4D5F514ECDA0F36', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 7, NULL, NULL, 2, NULL, 0, 'Then add the flour mixture in three parts, alternating with the sour cream in two parts and beating on low after each addition until just incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98D62C4B18A7A64D8B8E5B4251D9B886', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 8, NULL, NULL, 2, NULL, 0, 'Gently fold the rhubarb into the batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01DC1A064870DB40812B9AD2B2758C90', X'BDEDDE25216294448904DEE125E7E986', X'BDEDDE25216294448904DEE125E7E986', 9, NULL, NULL, 2, NULL, 0, 'Scrape the batter evenly into the prepared pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F87F7EAD4DD567419C1AA16959A28ACC', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Spray a half-sheet pan with cooking spray, line it with a piece of parchment paper, and spray the paper too.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28115C44C89CC94F9EFF126ABA46A5DB', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 1, NULL, NULL, 2, NULL, 0, 'Into a large bowl, sift together the flour, ginger, baking soda, cinnamon, cloves, nutmeg, and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8F792CE316EE846A4D0335571D09812', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer, beat the butter with the paddle attachment on medium-high speed until light and fluffy. Add brown sugar and beat until combined with the butter, stopping to scrape down the sides of the bowl with a spatula as needed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC1A73EE86D9634097D57901099F03B1', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 3, NULL, NULL, 2, NULL, 0, 'Beat in the eggs one at a time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97E334C3CED26C47A3B457CE1A81EE55', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 4, NULL, NULL, 2, NULL, 0, 'Lower the mixing speed to medium-low and pour in the molasses. Beat until incorporated. Beat in hot water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD4F6AD3C59E6945A1D3EA8E06195563', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 5, NULL, NULL, 2, NULL, 0, 'Lower mixing speed to low and gradually spoon in the flour mixture, along with the lemon zest. Mix just until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A010C35FC2CEE84595CB558ED6A7DA0B', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 6, NULL, NULL, 2, NULL, 0, 'Pour batter onto prepared baking sheet and smooth with a spatula. Bake for 20-25 minutes, until a tester comes out clean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6038644C807ED448CBC0A66A0D11782', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 7, NULL, NULL, 2, NULL, 0, 'Let gingerbread cool in the pan set over a cooling rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D3F6947D06574D82D2D34220A91DEC', X'4D8E341F454AC743BB0A57831BB96F17', X'4D8E341F454AC743BB0A57831BB96F17', 8, NULL, NULL, 2, NULL, 0, 'Once cooled, remove cake from pan. (If you have a large cutting board, you can place it directly on top of the sheet pan and flip the whole thing over to release the gingerbread onto the cutting board. Peel off parchment paper and cut gingerbread into 1-inch squares.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFD67693E3C62A4D880EFCA555CFB5E0', X'391C36AA7D2B91448E81B1C4F6207C4D', X'391C36AA7D2B91448E81B1C4F6207C4D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Line two standard muffin tins with 24 decorative paper liners.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFA06AAD94040D43B78C503D7635C604', X'391C36AA7D2B91448E81B1C4F6207C4D', X'391C36AA7D2B91448E81B1C4F6207C4D', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle, mix sugar, flour, cocoa, baking powder, baking soda and salt on low speed until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF5FA70939AB6A4690EB96BA7D915FBC', X'391C36AA7D2B91448E81B1C4F6207C4D', X'391C36AA7D2B91448E81B1C4F6207C4D', 2, NULL, NULL, 2, NULL, 0, 'Add eggs, milk, oil and vanilla extract. Mix on medium speed until batter is smooth. Pour in boiling water and carefully mix on low until just incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A957F58024A2F44DA0A2BC105AF50517', X'391C36AA7D2B91448E81B1C4F6207C4D', X'391C36AA7D2B91448E81B1C4F6207C4D', 3, NULL, NULL, 2, NULL, 0, 'Divide batter among muffin cups. Bake cupcakes for about 18 minutes, until a tester comes out clean. Cool completely on a wire rack before frosting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AB06BB523D29247ADD3A9EC5A8ABBDD', X'A64A43B48FFF874D821A62F6F0403756', X'A64A43B48FFF874D821A62F6F0403756', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325° F for cake or 375° F for cupcakes. Grease the bottom of a 10-inch tube or bundt pan, or line two cupcake pans with a total of 20 paper liners. Have all ingredients at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52C8C51A96B8064B9289F02117A3D820', X'A64A43B48FFF874D821A62F6F0403756', X'A64A43B48FFF874D821A62F6F0403756', 1, NULL, NULL, 2, NULL, 0, 'Beat flour and butter together for five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F93F680E31E2849BDABCB067304692C', X'A64A43B48FFF874D821A62F6F0403756', X'A64A43B48FFF874D821A62F6F0403756', 2, NULL, NULL, 2, NULL, 0, 'Add eggs one at a time, beating after each addition.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E58124706493B44F886A84C2B7E73FAE', X'A64A43B48FFF874D821A62F6F0403756', X'A64A43B48FFF874D821A62F6F0403756', 3, NULL, NULL, 2, NULL, 0, 'Add sugar, baking powder, salt and vanilla and almond extracts. Beat for two minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0935F0D014DF5C45A45344B7AD2809D8', X'A64A43B48FFF874D821A62F6F0403756', X'A64A43B48FFF874D821A62F6F0403756', 4, NULL, NULL, 2, NULL, 0, 'For a cake, bake for about one hour, checking after fifty minutes. For cupcakes, bake for 20-25 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2092F1263DCBC64ABC7875ED69835128', X'A64A43B48FFF874D821A62F6F0403756', X'A64A43B48FFF874D821A62F6F0403756', 5, NULL, NULL, 2, NULL, 0, 'Cool in pan for 10 minutes and then completely on a rack before frosting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED7938A1A467C549A8A1F826A44F8768', X'5619B48B83DF3A4B87EA75633B619FF5', X'5619B48B83DF3A4B87EA75633B619FF5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Line three standard muffin tins with 36 paper liners (or bake in batches, one or two tins at a time).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97BE6B4B4663554A883EE191B1CE067F', X'5619B48B83DF3A4B87EA75633B619FF5', X'5619B48B83DF3A4B87EA75633B619FF5', 1, NULL, NULL, 2, NULL, 0, 'In a very large bowl or the bowl of a large stand mixer fitted with the paddle (I have this one, which is 6 quarts, and it still sloshed a little), stir sugar, flour, cocoa powder, baking powder, baking soda and salt until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B9FF0372825594A92E1142F81DB59B7', X'5619B48B83DF3A4B87EA75633B619FF5', X'5619B48B83DF3A4B87EA75633B619FF5', 2, NULL, NULL, 2, NULL, 0, 'Add eggs, milk, oil and vanilla extract. Mix on low speed until batter is smooth. Pour in boiling water and stir with a spoon or very carefully mix on low speed with the splash guard surrounding the bowl until just incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC61B3F66B683143AC4DE3752164FA26', X'5619B48B83DF3A4B87EA75633B619FF5', X'5619B48B83DF3A4B87EA75633B619FF5', 3, NULL, NULL, 2, NULL, 0, 'Pour batter into muffin cups. Bake cupcakes for about 18 minutes, until a tester comes out clean. Cool completely on a wire rack before frosting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BA1136652962A419904D89A5468B63F', X'78ED51327A799544B8CA56E7C2091037', X'78ED51327A799544B8CA56E7C2091037', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Butter and flour two 9-inch round cake pans.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2EC088725B1924092AA4A7094A90900', X'78ED51327A799544B8CA56E7C2091037', X'78ED51327A799544B8CA56E7C2091037', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer, beat together the sugar and eggs for 30 seconds. Add the rest of the ingredients and beat for one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05971B703A1EF643861235C23828F396', X'78ED51327A799544B8CA56E7C2091037', X'78ED51327A799544B8CA56E7C2091037', 2, NULL, NULL, 2, NULL, 0, 'Divide batter evenly between the prepared cake pans and bake for about 30 minutes, until a tester comes out clean and the cake has slightly pulled away from the sides of the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9539F44E9133E1449B34C3F9BBBA757B', X'78ED51327A799544B8CA56E7C2091037', X'78ED51327A799544B8CA56E7C2091037', 3, NULL, NULL, 2, NULL, 0, 'Cool in pans for five minutes before turning out onto a rack to cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'881B6A8F1561BA48868FBDC5592C2E99', X'49390146477BE448A106BD0DD220F425', X'49390146477BE448A106BD0DD220F425', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Line two standard muffin tins with 24 decorative paper liners to match your theme.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7FE3C1E186EB34B8FE6BD7D7A3BFED3', X'49390146477BE448A106BD0DD220F425', X'49390146477BE448A106BD0DD220F425', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle, mix sugar, flour, cocoa, baking powder, baking soda and salt on low speed until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'363FD4ADD013264AB1F89213C201EDD3', X'49390146477BE448A106BD0DD220F425', X'49390146477BE448A106BD0DD220F425', 2, NULL, NULL, 2, NULL, 0, 'Add eggs, milk, oil and vanilla extract. Mix on medium speed until batter is smooth. Pour in boiling water and carefully mix on low until just incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC9F03D3337E4409054D8535C71512B', X'49390146477BE448A106BD0DD220F425', X'49390146477BE448A106BD0DD220F425', 3, NULL, NULL, 2, NULL, 0, 'Divide batter among muffin cups. Bake cupcakes for about 18 minutes, until a tester comes out clean. Cool completely on a wire rack before frosting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9E0FB6598BD354BB076ECE15611F34B', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. In the bowl of a stand mixer fitted with the paddle attachment or a large mixing bowl, beat the eggs and add oil while mixer is running. Add vanilla, and then gradually add the sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD9CBD2D7DEB0846914DAAFF944D06A4', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', 1, NULL, NULL, 2, NULL, 0, 'In a separate medium size bowl, whisk together the flour, baking powder, baking soda, salt and spices. Add these dry ingredients to the wet mixture a little at a time, mixing everything thoroughly to make a smooth batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2047F31315797B4087A1BEADC8D82E3D', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', 2, NULL, NULL, 2, NULL, 0, 'Add the carrots and nuts, then the coconut and pineapple and mix until all the ingredients are blended into the batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6F8AB1E2A0E40459170BFC6FA580BBF', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', X'5098B9C99DE9D24DA99DFD8C0E5CBDE9', 3, NULL, NULL, 2, NULL, 0, 'Pour the batter into a greased 9x13 inch pan and bake for 45 to 50 minutes. Cake is done when a tester inserted in the center comes out clean. Cool completely in the pan on a cooling rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4667A78C82E3B04CB443FDC3D805319E', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 0, NULL, NULL, 2, NULL, 0, 'Have all ingredients at room temperature. Preheat oven to 350°F with a rack in the center, and grease a 9 x 13 x 2-inch baking pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D46E206BCAB01C468CD334C5FE91B954', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, stir together the 2 cups flour, baking powder, baking soda, and 1/2 teaspoon salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D7B317746BFD342AC4F6D6FB8F4A487', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the sour cream and vanilla extract.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F8F106C5978D74B960BA858FDF7C0F2', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 3, NULL, NULL, 2, NULL, 0, 'In another large bowl, beat together the 4 tablespoons butter and the 1 cup sugar until lightened in color and texture, about 3 minutes. Add the eggs one at a time, beating to incorporate after each addition. Then add the flour mixture in three parts, alternating with the sour cream in two parts and beating on low after each addition until just incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'520C3B704DCD4447B6BCD6EFA4EAA44F', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 4, NULL, NULL, 2, NULL, 0, 'Toss the blueberries with the remaining tablespoon of flour and gently fold into the batter. Scrape the batter evenly into the prepared pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85CD04E8CD514849BB9335B034E313DB', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 5, NULL, NULL, 2, NULL, 0, 'To make the streusel topping, stir together the flour, brown sugar, nuts, cinnamon and salt in a small bowl. Pour the melted butter overtop and stir to incorporate evenly into the dry ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD50C2F0039BFC438B35833A98B80CCE', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 6, NULL, NULL, 2, NULL, 0, 'Use your fingers to sprinkle the topping over the batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'872609908B567B4FA8D91D7673D4997D', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 7, NULL, NULL, 2, NULL, 0, 'Bake until a cake tester inserted in the middle of the cake comes out almost clean, 30 to 35 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA645F9309F7A24E91731E3CF9129E13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', X'5C77CC6CA8635D46A65BDBCE1A28BE13', 8, NULL, NULL, 2, NULL, 0, 'Cool in the pan for a few minutes before serving warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B5495ACB9E4A84AA83295367429FD00', X'3AC7C0A6649EA74FAE7DF89FC063EADE', X'3AC7C0A6649EA74FAE7DF89FC063EADE', 0, NULL, NULL, 2, NULL, 0, 'Chop chocolate, pistachios, and raspberries separately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A4F7A064CED98449D2FEC4A2D9CCB08', X'3AC7C0A6649EA74FAE7DF89FC063EADE', X'3AC7C0A6649EA74FAE7DF89FC063EADE', 1, NULL, NULL, 2, NULL, 0, 'Melt the chocolate in a double boiler or on half power in bursts of 30 to 60 seconds the microwave, stirring gently and frequently either way.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'086481A46CF9EE44875D4A09A6C0C6F8', X'3AC7C0A6649EA74FAE7DF89FC063EADE', X'3AC7C0A6649EA74FAE7DF89FC063EADE', 2, NULL, NULL, 2, NULL, 0, 'Pour melted chocolate onto a rimmed baking sheet lined with parchment and spread into an even layer. You can choose the thickness — I prefer something between 1/4 and 1/8 inch.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'644A58E06D253D4AA0B4587F92B2613D', X'3AC7C0A6649EA74FAE7DF89FC063EADE', X'3AC7C0A6649EA74FAE7DF89FC063EADE', 3, NULL, NULL, 2, NULL, 0, 'While chocolate is hot, sprinkle with chopped nuts and raspberries.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60EDB9C499C789498C8DB781B6E1576C', X'3AC7C0A6649EA74FAE7DF89FC063EADE', X'3AC7C0A6649EA74FAE7DF89FC063EADE', 4, NULL, NULL, 2, NULL, 0, 'Let rest until completely set, then break into shards.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D0D8C2CF70A2841AA0786266E027EF5', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 0, NULL, NULL, 2, NULL, 0, 'Sift together the flours, baking powder, and salt into a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81AE5CE74627C4B878011FDACCB67FB', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer fitted with the paddle, beat the butter with the sugars just until it looks like a crumble topping. Refer to the step-by-step photos and the video for visual cues.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DF944543BF23F488FD0ECDE4BB55FD5', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 2, NULL, NULL, 2, NULL, 0, 'Mix in the eggs just until they''re combined and the dough begins to look smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33138E61010A6845A913CA0E6CE55970', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 3, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients all at once. Mix on low just until they''re incorporated. I like to do the last bit of mixing by hand with a wooden spoon or very sturdy silicone spatula, to be sure there’s no unmixed dough on the bottom, without overmixing the rest. Dough will be very stiff at this stage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA44EA13EEAD1146B1E77D8371A1A908', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 4, NULL, NULL, 2, NULL, 0, 'Mix in the walnuts and chocolate chips to distribute evenly. I like to do this part by hand as well, but you can use the paddle on a stand mixer if you like. It will take a bit of elbow grease either way.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E63511741C55E94EA98F6A26A690A3DF', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 5, NULL, NULL, 2, NULL, 0, 'Divide the dough into nine 6-ounce (170-gram) balls. Use your hands to shape them very lightly. The final result should be rough and rustic, not packed and spherical. Line two sturdy baking sheets with parchment and arrange four balls on one sheet, five on the other, leaving plenty of room in between.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB3E4FAE57938F40A8431329B4A5B59F', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 6, NULL, NULL, 2, NULL, 0, 'Place the cookie sheets into the fridge for 30 minutes. It''s important to chill the sheets as well as the dough balls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3851A80ED2EEAF42B2EE27DD5233AE02', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 7, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F (190°C) with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18DC56874E99A64788935926C76F250D', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 8, NULL, NULL, 2, NULL, 0, 'Transfer one cookie sheet from the fridge to the center oven rack. (You''ll bake the sheets one at a time.) Bake until cookies are lightly browned on top and underneath and still very soft inside, about 18 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9622A923FF2DEE4793793AE730962B18', X'2E710CF3F4261341BD9711F35A1A34AF', X'2E710CF3F4261341BD9711F35A1A34AF', 9, NULL, NULL, 2, NULL, 0, 'Let cool for at least 15 to 30 minutes before serving, right on the pan — longer if you can. This helps the cookies set perfectly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81F1F6103A30BD4EAF428D31CC2D46CB', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 0, NULL, NULL, 2, NULL, 0, 'Adjust an oven rack to the middle of the oven. Preheat the oven to 350°F (180°C). Line two sheet pans with parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9803CA361CD986409532C187D3947A67', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the flour and baking powder, and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBFE5B92E3270C4DA0353D6F5262726B', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle, beat the eggs, sugar, and salt on medium-high speed until the mixture is pale and doubled in volume, 6 to 8 minutes. Turn the mixer to low speed and stir in the canola oil and vanilla until just combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'528061618274274EB19A59EF2352EF44', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 3, NULL, NULL, 2, NULL, 0, 'While the egg mixture is beating, melt the butter and chocolate. Place the butter in a small, heavy-bottom saucepan set over low heat. Add the chocolate and melt together, stirring frequently, until smooth. Off the heat, add the cocoa powder to the chocolate and whisk until completely combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F56C83D086144A9A3A648AFB0D5C92', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 4, NULL, NULL, 2, NULL, 0, 'Add the warm chocolate-butter mixture to the egg mixture and mix on low speed until combined. Add the flour mixture and mix on low speed until combined. Let the mixture sit at room temperature for 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'294690AE276B0041AEB3BDB9CCD2A685', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 5, NULL, NULL, 2, NULL, 0, 'Use a small scoop or two spoons to drop heaping tablespoons of batter onto the prepared sheet pans, spacing them at least 2 inches (5 cm) apart, fitting 12 on a pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED00774B5597404782B56B4BCB042160', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 6, NULL, NULL, 2, NULL, 0, 'Bake the cookies one pan at a time, rotating halfway through baking. Bake until the cookies are puffed and cracked and the edges are set, 8 to 12 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E06981B61EF7804E8728D4FF375A7029', X'77A04372301562498752076C7CAFB586', X'77A04372301562498752076C7CAFB586', 7, NULL, NULL, 2, NULL, 0, 'Transfer the sheet pan to a wire rack and let the cookies cool completely on the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D132882223F76B449364A06AAA8BA8D6', X'1D72EB2E7B92D44C953B6B8CE7F90C72', X'1D72EB2E7B92D44C953B6B8CE7F90C72', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F with a rack in the center. Spray an 8-inch x 8-inch baking pan with cooking spray and line it with a piece of parchment paper to cover the bottom and come up two opposite sides. In the microwave or a double boiler, combine the butter and white chocolate. Heat, stirring occasionally, until just melted and smooth. If melting in the microwave, use half power in 30-second bursts, stopping to stir thoroughly in between.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'944B0143A65F8543ACF15CDA0D491AEE', X'1D72EB2E7B92D44C953B6B8CE7F90C72', X'1D72EB2E7B92D44C953B6B8CE7F90C72', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer, beat the eggs until frothy, about two minutes. Add the brown sugar and the vanilla and beat to combine thoroughly. Pour in the butter-white chocolate mixture and beat until well combined. Add the flour and salt, and mix with a spatula until just combined. Add the chocolate chips and walnuts and stir to distribute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04F5605CC468F442B09C1ACAE62E9868', X'1D72EB2E7B92D44C953B6B8CE7F90C72', X'1D72EB2E7B92D44C953B6B8CE7F90C72', 2, NULL, NULL, 2, NULL, 0, 'Scrape the batter into the prepared pan. Bake for 30 to 35 minutes, until a cake tester inserted into the center comes out clean. Cool for at least ten minutes, then run a knife around the edges. If cutting into squares while still very warm, cut in the pan. If cooled, you can use the parchment paper `handles` to remove the whole batch from the pan before cutting. Cut into squares and serve. Blondies are great alone and also pair really well with ice cream. Or use as the base of a blondie sundae.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2378EFAA1E5BF48B57D8C5B9D1A19B5', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 175°F with two racks close to the center. Line two cookie sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4E274F78B8F874E85766C17B1456004', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 1, NULL, NULL, 2, NULL, 0, 'Place the egg whites in a large metal bowl or the bowl of a stand mixer. Beat on medium until very foamy and then on high until soft peaks form, meaning that if you lift the beaters out of the egg whites, the tip of the peak that forms folds softly back onto itself but doesn’t collapse entirely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B8D071B8DB5114999C6AD84D63CBB57', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 2, NULL, NULL, 2, NULL, 0, 'With the mixer running at medium speed, gradually add half the sugar. Beat on high until white and glossy (it will look like marshmallow fluff).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E97F4BE37E922542BA8262A248E2B17F', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 3, NULL, NULL, 2, NULL, 0, 'Stop mixer and add remaining sugar all at once. Sprinkle cornstarch, vinegar, salt, and peppermint and chocolate extracts overtop. Beat again until mixture has enough structure to hold its shape when piped. This will take several minutes, maybe five to seven, depending on your mixer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40E2355EB1B7FB45A92E26898FF2B702', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 4, NULL, NULL, 2, NULL, 0, 'Fit a large piping bag with a star tip. Using a skewer or a small paintbrush, paint three thin lines of red gel food dye onto the inside of the piping bag, starting from the tip and going about halfway up the bag. You don’t really have to do a good job of this at all for the effect to work, so don’t stress about it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31B4D0431005F1419ACE745E2E916DEC', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 5, NULL, NULL, 2, NULL, 0, 'Use a rubber spatula to transfer meringue from bowl into piping bag and twist the top to close.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6C22DE02928684C875C0DC73E0C91AD', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 6, NULL, NULL, 2, NULL, 0, 'Pipe bite-sized meringues (about an inch and a half in diameter) onto parchment-lined cookie sheets, leaving an inch or so of space between them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A42D43ED84443B45A1741D54B35B1C34', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 7, NULL, NULL, 2, NULL, 0, 'Bake for about three hours, until dry and crisp.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C5B6FB0EEAA64FAC8699E21958E80D', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 8, NULL, NULL, 2, NULL, 0, 'Transfer to a wire rack to cool completely. Keep the parchment-lined baking sheets available.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F5B9B4F5D36D041B29304C3AB1C6E09', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 9, NULL, NULL, 2, NULL, 0, 'When meringues are cool, melt chocolate chips. You can do this in a double-boiler or in the microwave in short bursts, stirring in between, until just melted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A6C4E6486BD584A95A2BDCFFBEC5011', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 10, NULL, NULL, 2, NULL, 0, 'Dip the bottom of each kiss into the chocolate and set on parchment until dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D818D2ED35C154385CE47B9EEFC2B88', X'ACD2969234CD814A8A250DDB0BDA432B', X'ACD2969234CD814A8A250DDB0BDA432B', 11, NULL, NULL, 2, NULL, 0, 'Once completely dry, kisses store well in an airtight container for a couple of weeks. Mason jars and cellophane gift bags work well for gifting!');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AD8D88C08154241902B7E09001DA774', X'49A6D6A1728ADA4C99E78AEE79E63307', X'49A6D6A1728ADA4C99E78AEE79E63307', 0, NULL, NULL, 2, NULL, 0, 'Place the water, sugar and egg whites in the bowl of a stand mixer or a large mixing bowl. Using the whisk attachment or an electric mixer, beat ingredients together, slowly at first to avoid a sugar snow shower. Keep going until the icing is smooth, glossy and the consistency of toothpaste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49A7E7EAC9E21D418ADC6E3A9F447031', X'49A6D6A1728ADA4C99E78AEE79E63307', X'49A6D6A1728ADA4C99E78AEE79E63307', 1, NULL, NULL, 2, NULL, 0, 'Transfer a small amount of the icing to a smaller bowl and mix in a few drops of the color you''d like to use to line your cookies. Spoon this icing into a disposable piping bag and snip off a tiny bit of the bag''s tip. Draw a line of icing as close as you''d like to the outer edge of your cookie, making sure to connect the line''s ends to each other so the flood icing won''t have any gaps to flow out.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D799C889EC20AC48BFC2A25517E68723', X'49A6D6A1728ADA4C99E78AEE79E63307', X'49A6D6A1728ADA4C99E78AEE79E63307', 2, NULL, NULL, 2, NULL, 0, 'In another small bowl, mix some more of the icing with a few drops of the color you''d like to use to flood your cookies, along with two additional teaspoons of water. This icing should be thin enough to spread slowly and slightly across the surface of the cookies, but no thinner than that. You may need to experiment a little to find the consistency that''s best for your humidity and, ahem, skill level. Pour this icing into another piping bag or better yet a squeeze bottle.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74FAF847E5EF734091E5DEE42CA50DCA', X'49A6D6A1728ADA4C99E78AEE79E63307', X'49A6D6A1728ADA4C99E78AEE79E63307', 3, NULL, NULL, 2, NULL, 0, 'Squeeze some flood icing onto the surface of a cookie, filling in the entire surface inside the line. You can use a toothpick to fill any gaps that don''t fill themselves and gently rap the cookie onto the work surface if you''re brave and want a perfectly smooth finish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC63E664588AB44DAB40AD232A9C389B', X'49A6D6A1728ADA4C99E78AEE79E63307', X'49A6D6A1728ADA4C99E78AEE79E63307', 4, NULL, NULL, 2, NULL, 0, 'To decorate the cookies further, add sprinkles or other decorations while the icing is still wet, or let the icing dry slightly and then mix other colors of the remaining thick icing and pipe additional designs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE0EB7DD694A20498AFFC73BF3F1E1A3', X'EC2437D4C3E9034AB5EAD69F4E59E35C', X'EC2437D4C3E9034AB5EAD69F4E59E35C', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer fitted with the paddle, beat together the sugar, butter, baking powder, vanilla and salt until lightened and fluffy, about 5 minutes, scraping down bowl as necessary. Beat in egg until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'767A5C2A69CCE14281A1AD6DA33C3ACE', X'EC2437D4C3E9034AB5EAD69F4E59E35C', X'EC2437D4C3E9034AB5EAD69F4E59E35C', 1, NULL, NULL, 2, NULL, 0, 'Into a medium bowl, sift together the cocoas and flour. Beat into wet ingredients all at once, just until well combined. Refrigerate dough for 30 minutes. (Dough will keep in fridge for 24 hours if you want to prepare it the day before.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'166AA698F36F5741A6450DDB406F2F73', X'EC2437D4C3E9034AB5EAD69F4E59E35C', X'EC2437D4C3E9034AB5EAD69F4E59E35C', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with two racks close to the center. Line two baking sheets with parchment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2E315225D87A74788FEE2D7B398DB16', X'EC2437D4C3E9034AB5EAD69F4E59E35C', X'EC2437D4C3E9034AB5EAD69F4E59E35C', 3, NULL, NULL, 2, NULL, 0, 'Divide dough into two discs. Sprinkle with cocoa. Roll out each disc until dough is 1/8` thick and cut into shapes with cookie cutters. Transfer cookies to baking sheets, leaving space between cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B7B2872D8C7E0448F457C555EBBE703', X'EC2437D4C3E9034AB5EAD69F4E59E35C', X'EC2437D4C3E9034AB5EAD69F4E59E35C', 4, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8 to 10 minutes, until edges begin to firm. Cool on pan for a couple of minutes and then completely on a rack before decorating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2CBAFCDED230C4B98A5219D35376F88', X'AC62AF1AE8D759408FD1BFDA64A0E90A', X'AC62AF1AE8D759408FD1BFDA64A0E90A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11B2DE9D462BA14D8A403C3CA622A7DE', X'AC62AF1AE8D759408FD1BFDA64A0E90A', X'AC62AF1AE8D759408FD1BFDA64A0E90A', 1, NULL, NULL, 2, NULL, 0, 'Into a medium bowl, sift together the flour, cream of tartar, baking soda and salt. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76ED9EA1F5F2D14BAC3AAA748D1B27B3', X'AC62AF1AE8D759408FD1BFDA64A0E90A', X'AC62AF1AE8D759408FD1BFDA64A0E90A', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle, mix the butter, sugar and eggs until light and fluffy, about three minutes. (Start on low speed and increase to medium.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2528134E9260C409796DFD7BBE0EE1D', X'AC62AF1AE8D759408FD1BFDA64A0E90A', X'AC62AF1AE8D759408FD1BFDA64A0E90A', 3, NULL, NULL, 2, NULL, 0, 'On low speed, beat in flour mixture until just combined. Do not overmix. Chill dough for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FCDF56D1EFD304CBA8934D67395B09B', X'AC62AF1AE8D759408FD1BFDA64A0E90A', X'AC62AF1AE8D759408FD1BFDA64A0E90A', 4, NULL, NULL, 2, NULL, 0, 'For the coating, stir together sugar and cinnamon on a small plate. Using your hands, roll dough into 1-inch balls and roll each ball in coating to cover completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A51929A31B80B845B60436F1870309DD', X'AC62AF1AE8D759408FD1BFDA64A0E90A', X'AC62AF1AE8D759408FD1BFDA64A0E90A', 5, NULL, NULL, 2, NULL, 0, 'Place cookies 2-3 inches apart on ungreased baking sheets and bake for about 8 minutes, until set but still soft. Cool slightly on pan before removing to a cooling rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7C2B5C81F12B44F844ED7340A8258B4', X'595B867300EFA0478A68E00EFFA59558', X'595B867300EFA0478A68E00EFFA59558', 0, NULL, NULL, 2, NULL, 0, 'In a standard food processor fitted with the blade, pulse the Oreos until reduced to dust. Meanwhile, whip the cream cheese and peppermint extract. (I use the regular paddle attachment of my KitchenAid. You can also use a hand-held electric mixer or a spatula and a lot of elbow grease.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DD2716C6FDFC9419FE705DB90A99282', X'595B867300EFA0478A68E00EFFA59558', X'595B867300EFA0478A68E00EFFA59558', 1, NULL, NULL, 2, NULL, 0, 'Mix the cookie crumbs into the cream cheese until well combined. Chill for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E726CFC3371AB4DA36527B0E3C1FA67', X'595B867300EFA0478A68E00EFFA59558', X'595B867300EFA0478A68E00EFFA59558', 2, NULL, NULL, 2, NULL, 0, 'Roll mixture into 30 1-inch balls and place on a parchment-lined baking sheet. Chill thoroughly. (I pop them in the freezer for an hour.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94BA57CFDFBF484D905F3AA32346E134', X'595B867300EFA0478A68E00EFFA59558', X'595B867300EFA0478A68E00EFFA59558', 3, NULL, NULL, 2, NULL, 0, 'To temper the chocolate, roughly chop HALF of it and place in large microwave-safe bowl. Microwave in bursts (starting with one minute and following with 30 seconds), stirring in between, until chocolate is melted and reaches 115°F. Then place the entire remaining half (not chopped) into the bowl and stir until it is melted and the temperature of the melted chocolate is 90°. This can take a while, but it''s key. When the chocolate reaches 90°, use two forks to dip each truffle in the chocolate and roll to coat completely. Replace on parchment-lined baking sheet. I do about 5 at a time, stop to sprinkle quickly with crushed candy cane before they dry, and then continue. If you work quickly you shouldn''t have to reheat the chocolate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B0770153C0FD4FBC8BED87A52C34DD', X'EF14E380BA659C4A97AED248007E6D76', X'EF14E380BA659C4A97AED248007E6D76', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center. Use cooking spray or butter to lightly grease an 8-inch x 8-inch baking pan, then lightly flour it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55F1F749C4DF214F8AD123FC0F83F5C7', X'EF14E380BA659C4A97AED248007E6D76', X'EF14E380BA659C4A97AED248007E6D76', 1, NULL, NULL, 2, NULL, 0, 'Place the chocolate chips and butter into a large microwave-safe bowl. Heat on half power until just melted — chips may retain their shape until stirred. This takes about 2 minutes in my microwave, but your mileage may vary. Stir until completely smooth. Stir in the brown sugar, coffee and vanilla until smooth. Crack the eggs into the bowl and mix very well into the batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBD142C33CA24B4F8EA436E499FACF84', X'EF14E380BA659C4A97AED248007E6D76', X'EF14E380BA659C4A97AED248007E6D76', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour, cocoa powder, baking powder and salt evenly over the chocolate mixture, then stir until well combined and shiny. Stir in nuts if using. Scrape batter into prepared pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'141629097B9599428B625E86408CF0CE', X'EF14E380BA659C4A97AED248007E6D76', X'EF14E380BA659C4A97AED248007E6D76', 3, NULL, NULL, 2, NULL, 0, 'Bake for about 30 minutes, until a tester comes out without a ton of molten chocolate on it. (It won''t be completely clean.) Let cool in the pan before cutting into 16 squares to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A85B0E103330746A386611F3505D9DD', X'5C118E9AEC63B44592DB5028CE64C98C', X'5C118E9AEC63B44592DB5028CE64C98C', 0, NULL, NULL, 2, NULL, 0, 'Spray an 8 x 8 baking pan with nonstick spray and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FA49AF775338C4C9D2A593CA1A5AAD6', X'5C118E9AEC63B44592DB5028CE64C98C', X'5C118E9AEC63B44592DB5028CE64C98C', 1, NULL, NULL, 2, NULL, 0, 'Place the marshmallows and butter in a large microwave-safe bowl and heat on medium power until melted. Stir together thoroughly. Pour in the cereal and stir to coat completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39D884AD0CB91445B738EEBACE8911E1', X'5C118E9AEC63B44592DB5028CE64C98C', X'5C118E9AEC63B44592DB5028CE64C98C', 2, NULL, NULL, 2, NULL, 0, 'Scrape the mixture into the prepared baking pan and press until reasonably flat. This may be easier after a minute or two, but it’s a sticky procedure. You can spray a spatula or your hands with cooking spray if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41D2DF1C30CBD74CB8767529B02BD403', X'5C118E9AEC63B44592DB5028CE64C98C', X'5C118E9AEC63B44592DB5028CE64C98C', 3, NULL, NULL, 2, NULL, 0, 'While still sticky, press the candy eyeballs into the top of the bars wherever you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A88FB88DF3C6141A408462FC494803A', X'5C118E9AEC63B44592DB5028CE64C98C', X'5C118E9AEC63B44592DB5028CE64C98C', 4, NULL, NULL, 2, NULL, 0, 'Heat chocolate chips until just melted in a small bowl in the microwave. Stir in coconut oil. Use a spoon to drizzle chocolate mixture over bars. Let cool until solid. You can do this in the freezer for about 15 minutes if you like. Cut into squares and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D982365C16307D4B89C7F7264BA7D009', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5263089241AC5A47A6A9CEDAD2BAADE0', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 1, NULL, NULL, 2, NULL, 0, 'Place butter and sugar in a large bowl (or the bowl of a stand mixer). Beat on medium speed until lightened in color and fluffy. Add the vanilla extract and beat to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B45CB26E162A7D4A92BAD7D05E33A657', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and stir in gently with a spatula or wooden spoon until incorporated. Gather dough into your hands and squeeze it together until it forms a ball. If you like, you can divide it into two parts, since it will roll out quite large.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDEA473F1F0DB84DADBDE421DD006752', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 3, NULL, NULL, 2, NULL, 0, 'Dust work surface and rolling pin with flour and gently roll dough into a 1/4-inch thick disk. Cut into 2 to 2 1/2-inch rounds using a cookie or biscuit cutter. (I made some larger and some smaller and got about 40 cookies.) You can re-roll any scraps and use them to make a few more cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'127CCEE351D99043A1346B40FDE601AD', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 4, NULL, NULL, 2, NULL, 0, 'Transfer cookies to cookie sheets lined with parchment and `chill in the fridge for 15 minutes to help cookies retain their shape in the oven.`*');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B8BB841586B124996CC5536540D250D', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 5, NULL, NULL, 2, NULL, 0, 'Bake 12 to 16 minutes, until cooked through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'112F5B25E0D5054D89E8EDF202DD51C1', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer cookies to a wire rack to cool.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43909B92F0A38E4AAD0FD371FC5567FC', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 7, NULL, NULL, 2, NULL, 0, 'When cookies have cooled completely, make the caramel layer by melting the unwrapped caramels and the cream in a large bowl in the microwave. Your mileage may vary, but it took a couple of minutes in my microwave, during which I stopped and stirred every 30 seconds after the initial minute. When caramel is melted, place about a tablespoon (depending on cookie size) in the center of each cookie and let it spread on its own. It won''t be perfectly round, but I like that some of the caramel peeks out from under the chocolate so that people understand what they''re about to eat. Let caramel set before proceeding. This should happen within a few minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8EC61619709A943814D184F22AD0B1C', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 8, NULL, NULL, 2, NULL, 0, 'For the chocolate layer wash and dry the bowl used for the caramel, then melt the chocolate chips and coconut oil together in the same fashion. Spread about a tablespoon of chocolate onto each cookie. You can smooth it as much as you like. I don''t always have a lot of patience for these things and thought (maybe to justify my impatience, but hey) that the texture gave the faces some cute individual personality.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F830EFD2F9F0984F8C260F5714DA83E2', X'CEA151E1D5734946A01E5A5B67311FB1', X'CEA151E1D5734946A01E5A5B67311FB1', 9, NULL, NULL, 2, NULL, 0, 'While the chocolate is still melty, place arrange the candy eyeballs. It was warm and humid the day I made these, and the chocolate took its time hardening. You can pop them in the fridge to harden in about 10 minutes, but then bring them to room temperature with a piece of parchment covering them so they don''t develop a layer of condensation. Unless you prefer your cookie faces sweaty.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9957D933707AC48B8E9620AE134C6D6', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with two racks close to the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC8A20962D6EC34199B1D0B35B3C1A25', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, stir together flour, baking powder, baking soda, cinnamon, ginger, nutmeg allspice, cloves and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17C0D70EC9CB9A44AB841A0E8F044DA3', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer fitted with the paddle, beat together the butter and sugar for two minutes. Add the eggs one at a time, beating each until well incorporated. Add the pumpkin and vanilla and beat until incorporated. (The mixture will look curdled. Don’t worry.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'485C6EAB8926F24DA1F5CB5E7BD2D962', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 3, NULL, NULL, 2, NULL, 0, 'Pour in the dry ingredients and mix on the lowest speed until just incorporated. You may want to do the last little bit of mixing by hand.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D14C327C576C784899D41E46D3E54D9B', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 4, NULL, NULL, 2, NULL, 0, 'Line two large baking sheets with parchment paper (or lightly grease). Drop batter onto baking sheets about two tablespoons at a time, to make 10 cookies on each sheet, nicely spaced out.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC469EC08FA2246B62507F1B4074F35', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 5, NULL, NULL, 2, NULL, 0, 'Bake for a total of 16 minutes (or until the tops spring back a bit when touched), swapping the position of the baking sheets halfway through to ensure even cooking. Cool completely on a rack before filling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'872CF1E9B6696F4097360B486649167C', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 6, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, butter, vanilla and maple syrup in a medium mixing bowl or the bowl of a stand mixer fitted with the paddle. Mix, starting on low and increasing speed to medium as you go, until smooth and well-mixed, about 2 minutes. Sift in powdered sugar and mix on the lowest speed until incorporated. Then increase speed and beat until light and fluffy, about 2 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A9985F8E97ADC4D911D42F25E17C8DE', X'35BC9F63F26D5741A98A27213FAD2911', X'35BC9F63F26D5741A98A27213FAD2911', 7, NULL, NULL, 2, NULL, 0, 'Spread or pipe icing onto the flat side of half the cookies and top with the remaining cookies to make sandwiches. The whoopie pies will keep well overnight on the counter (put them on a plate and cover with foil), or you can refrigerate them for a couple of days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'138EE92B56A1264E974F377D0A0913B0', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA6748299DB5AA4197AEBD3124AB3C6C', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 1, NULL, NULL, 2, NULL, 0, 'Cut the butter into small pieces and place it in a large bowl (or the bowl of a stand mixer) with the sugar. Beat on medium speed until lightened in color and fluffy. Add the vanilla extract and beat to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3D1A452C13D244890D0C9E4F446C5E4', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and stir in gently with a spatula or wooden spoon until incorporated. Gather dough into your hands and shape into two balls. You''ll need to give it a good squeeze to get it to hold together well. You can chill it for 20 minutes or so at this point or proceed directly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC266EF072EFAB41A429F1D3F09B8D35', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 3, NULL, NULL, 2, NULL, 0, 'Dust work surface and rolling pin with flour and gently roll each dough ball into a thin disk, about 1/8 inch, but don''t stress.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0611718D3981784EB868FF4707DE42B8', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 4, NULL, NULL, 2, NULL, 0, 'Using a 2 1/2-inch round cookie/biscuit cutter, cut as many circles as you can. You should get between 90 and 100 in total, after you re-roll any scraps and use them to make a few more cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F67DE06F9C10041879697D1859EA1EE', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 5, NULL, NULL, 2, NULL, 0, 'Using a small snowflake cookie cutter (or any small festive shape you like), cut into the center of half the circles. Remove the snowflakes to bake separately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526F489D3A6E134A8B502AFF43B4EDC6', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 6, NULL, NULL, 2, NULL, 0, 'Transfer cookies to cookie sheets lined with parchment and chill in the fridge for 15 minutes to help cookies retain their shape in the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'450F4470FCC7754CBF8C9D4D34CB4EA8', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 7, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes, until very lightly golden.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E4B9C77BF14084493F7504C4A5FB41D', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 8, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer cookies to a wire rack to cool completely. Cookies will keep well at this stage for a few days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'112BDA0CCDA6354A85E6DA2E199BEB39', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 9, NULL, NULL, 2, NULL, 0, 'The day you plan to present the cookies, spread a nice layer of jam over the flat (bottom) side of each of the intact circles. Heap a little bit of jam in the center and leave a little border around the edge.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C5FBE6CB061AA47950602790A016BBB', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 10, NULL, NULL, 2, NULL, 0, 'Dust the tops of the snowflake-cutout circles, as well as the snowflakes themselves, with plenty of powdered sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D97FD2F03BDC6745BE88DFE42C342ADD', X'FFDF6BB003A3A24C8E4641E186D74846', X'FFDF6BB003A3A24C8E4641E186D74846', 11, NULL, NULL, 2, NULL, 0, 'To assemble the cookies, gently press a cutout circle onto each jam-covered circle, which should help the jam fill in the snowflake shape and also spread to the outside edges of the cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2851E4B21B1579479B2A286170DE715A', X'4D2A9BAFB56D024C9669832C923E9CF3', X'4D2A9BAFB56D024C9669832C923E9CF3', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42843DCBF5710F48A973C86E2A3D9247', X'4D2A9BAFB56D024C9669832C923E9CF3', X'4D2A9BAFB56D024C9669832C923E9CF3', 1, NULL, NULL, 2, NULL, 0, 'Cut the butter into small pieces and place it in a large bowl (or the bowl of a stand mixer) with the sugar. Beat on medium speed until lightened in color and fluffy. Add the vanilla extract and beat to incorporate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'537036ED26DF1B4389F964E72F6476CA', X'4D2A9BAFB56D024C9669832C923E9CF3', X'4D2A9BAFB56D024C9669832C923E9CF3', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and stir in gently with a spatula or wooden spoon until incorporated. Gather dough into your hands and squeeze it together until it forms a cohesive dough. Shape into a ball.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24D1A689994F9D439B25C2935058C0F0', X'4D2A9BAFB56D024C9669832C923E9CF3', X'4D2A9BAFB56D024C9669832C923E9CF3', 3, NULL, NULL, 2, NULL, 0, 'Dust work surface and rolling pin with flour and gently roll dough into a 1/4-inch thick disk. Cut into shapes using a cookie cutter. You can re-roll any scraps and use them to make a few more cookies.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B7937288483AE4B8C945B5405693EB8', X'4D2A9BAFB56D024C9669832C923E9CF3', X'4D2A9BAFB56D024C9669832C923E9CF3', 4, NULL, NULL, 2, NULL, 0, 'Transfer cookies to a cookie sheet lined with parchment and chill in the fridge for 15 minutes to help cookies retain their shape in the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D49AD19AC22F445874A2D67EDBF19E6', X'4D2A9BAFB56D024C9669832C923E9CF3', X'4D2A9BAFB56D024C9669832C923E9CF3', 5, NULL, NULL, 2, NULL, 0, 'Bake about 12 minutes, until very lightly golden.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F3640F5225CB74FAD1EB527D4D05EAF', X'4D2A9BAFB56D024C9669832C923E9CF3', X'4D2A9BAFB56D024C9669832C923E9CF3', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer cookies to a wire rack to cool. Decorate if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBEEBC59D6DB3C41B57A803795337EA8', X'273A61968FE29B44A0BCAA51D78EBE88', X'273A61968FE29B44A0BCAA51D78EBE88', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, beat together the butter and both sugars with an electric mixer until lightened in color and texture, about 3 minutes. Add eggs and vanilla and beat until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C1F3E726CA4F24594A107DA4E44D1EC', X'273A61968FE29B44A0BCAA51D78EBE88', X'273A61968FE29B44A0BCAA51D78EBE88', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the flour, baking powder, baking soda, and salt. Add the dry ingredients to the wet ingredients and stir together with a wooden spoon or silicone spatula. Add the oats and stir to combine. Then stir in the chocolate chips, Hershey''s bars, and nuts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B37E9DC5A814439A79A57DA591827C', X'273A61968FE29B44A0BCAA51D78EBE88', X'273A61968FE29B44A0BCAA51D78EBE88', 2, NULL, NULL, 2, NULL, 0, 'To bake, preheat the oven to 375°F. Using your hands or a couple of spoons, form the batter into loose, golf-ball-sized mounds. Place two inches apart on ungreased cookie sheets. Bake for eight to nine minutes, until tops are just golden and cookies are still soft. On the off chance there are any cookies left once they''re cool enough to handle, cool them completely on racks before storing in an airtight container for up to a couple of days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE570EC0DD617F4DAF912F0FEE5F1B30', X'A3E3C4502DCA134BB421C9DB46809C02', X'A3E3C4502DCA134BB421C9DB46809C02', 0, NULL, NULL, 2, NULL, 0, 'Warm milk to between 105°F and 115°F. This takes about 30 seconds in my microwave when milk is straight from the fridge, but your mileage may vary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'025D2D803EAA634CBBAA88F2F68C5F76', X'A3E3C4502DCA134BB421C9DB46809C02', X'A3E3C4502DCA134BB421C9DB46809C02', 1, NULL, NULL, 2, NULL, 0, 'Pour milk into the bowl of a stand mixer (don''t attach bowl to mixer just yet) and sprinkle with yeast. Add a pinch of the sugar. Stir to dissolve. Let stand in a warm spot until foamy, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'696F0D175248F5448A2E3F50E0C39938', X'A3E3C4502DCA134BB421C9DB46809C02', X'A3E3C4502DCA134BB421C9DB46809C02', 2, NULL, NULL, 2, NULL, 0, 'Add remaining sugar, egg, melted butter, salt, and cardamom. Stir with a wooden spoon until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B56B70D43485ED4EA0A11BD1695C61EE', X'A3E3C4502DCA134BB421C9DB46809C02', X'A3E3C4502DCA134BB421C9DB46809C02', 3, NULL, NULL, 2, NULL, 0, 'Add flour 1/2 cup at a time, stirring between additions, to form a smooth dough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E686E42C688EDB478E0DD2B82B87DA00', X'A3E3C4502DCA134BB421C9DB46809C02', X'A3E3C4502DCA134BB421C9DB46809C02', 4, NULL, NULL, 2, NULL, 0, 'Connect bowl to mixer fitted with dough hook. Turn machine to speed 2 and knead for 10 minutes, until dough is smooth and elastic. Alternatively, you can knead the dough by hand for 10 minutes on a lightly floured work surface.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0B6D8A4673CBD4E9231AA4C0B1F1509', X'A3E3C4502DCA134BB421C9DB46809C02', X'A3E3C4502DCA134BB421C9DB46809C02', 5, NULL, NULL, 2, NULL, 0, 'Pour the teaspoon of oil onto dough and turn to coat lightly but completely. Place a kitchen towel over the bowl and set it in a warm spot to rise until doubled, an hour or more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAADDE620AE01E4EB9BE923649D9E11E', X'E994ECAA02FC7D46B13375DC71A1BC9B', X'E994ECAA02FC7D46B13375DC71A1BC9B', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, beat the butter with an electric mixer until lightened in color and texture, 1 to 2 minutes. Gradually add the sugar and continue to beat until the mixture is light, about 2 minutes more. Add the eggs and beat until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9E475C1B3BF444E946371F77F0BFEA1', X'E994ECAA02FC7D46B13375DC71A1BC9B', X'E994ECAA02FC7D46B13375DC71A1BC9B', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, sift together 3 cups of the flour and the salt, cinnamon, and nutmeg. Add half the flour mixture to the bowl with the wet ingredients and stir to combine. Then add half the sherry and stir to incorporate. Repeat with remaining flour mixture and sherry. When all is well blended, add enough additional flour to stiffen the dough, about 1 cup. Wrap the dough in plastic wrap and refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C894FA5D50A9A41A222C5F79AF7B5D2', X'E994ECAA02FC7D46B13375DC71A1BC9B', X'E994ECAA02FC7D46B13375DC71A1BC9B', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°F. Grease two baking sheets. Flour a work surface and roll out the dough to a mere 1/16 inch thick. Cut out cookies with a cookie cutter. Place on cookie sheets, leaving an inch of space between cookies. You will need to bake in several batches. Bake for about 7 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'252D5450548C6944992657447004BF95', X'B07D2E47EDC1FC469F306C58C37DD43C', X'B07D2E47EDC1FC469F306C58C37DD43C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D9D0203F817B148888F05F7560A098A', X'B07D2E47EDC1FC469F306C58C37DD43C', X'B07D2E47EDC1FC469F306C58C37DD43C', 1, NULL, NULL, 2, NULL, 0, 'To make the filling, peel, quarter, core, and slice the apples into 1/4-inch thick slices from stem end to tip end. Place them in a large mixing bowl and toss with the lemon juice. Sprinkle the sugar, flour, cinnamon, nutmeg, and salt onto the apples and toss gently until the apples are evenly coated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C40648F9C7FAAA42A0C58CD6013DECA2', X'B07D2E47EDC1FC469F306C58C37DD43C', X'B07D2E47EDC1FC469F306C58C37DD43C', 2, NULL, NULL, 2, NULL, 0, 'To make the topping, toss together the oats, brown sugar, and flour in a small bowl. Add butter and use your fingers to pinch the butter into the flour mixture until it forms a coarse crumble with no loose bits of dry ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D0164B50F4DC14891A463A27385D6E0', X'B07D2E47EDC1FC469F306C58C37DD43C', X'B07D2E47EDC1FC469F306C58C37DD43C', 3, NULL, NULL, 2, NULL, 0, 'Place a deep-dish pie plate or a two-quart oven-safe dish onto a cookie sheet to catch any drips. Pour the apple mixture into the dish and sprinkle evenly with the topping.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'831FA5287AE3C94D9F5C61C406B59138', X'B07D2E47EDC1FC469F306C58C37DD43C', X'B07D2E47EDC1FC469F306C58C37DD43C', 4, NULL, NULL, 2, NULL, 0, 'Bake until the filling is soft and bubbly and the topping is nicely browned, about an hour, covering loosely with aluminum foil when the topping is sufficiently browned (probably around the 45 minute mark). Let cool for at least 10 minutes before serving warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FA4E088AD1D474FA234FC3D2F421454', X'88DB77FA1CB02C4EBF8AE677AA19A76D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70946C596A4BCB429630AA259743B8F3', X'88DB77FA1CB02C4EBF8AE677AA19A76D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, toss together the cherries, sugar, cornstarch, vanilla extract, and lemon juice until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D06B4F47F8FB241A1C9CFE410FAB2B3', X'88DB77FA1CB02C4EBF8AE677AA19A76D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', 2, NULL, NULL, 2, NULL, 0, 'Set either a deep-dish pie plate or eight 4-ounce canning jars on a heavy, rimmed baking sheet and spoon in the filling, dividing equally if using jars.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7CCD24A6E1C694494B89AF262529F8D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the oats, flour, brown sugar, and salt. Stir together thoroughly. Add the diced butter and, using your fingers, pinch the ingredients together until they form a cohesive, crumbly topping and no lumps of butter or loose bits of flour or sugar are left.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F05DDFA3C1E14048B355AAEB30FBD639', X'88DB77FA1CB02C4EBF8AE677AA19A76D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', 4, NULL, NULL, 2, NULL, 0, 'Crumble the topping evenly over the filling, again dividing equally if using jars.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DD6495235D4DC4DB199B7A4FEC22872', X'88DB77FA1CB02C4EBF8AE677AA19A76D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', 5, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes, until the filling is bubbly. Around the 20-minute mark, cover lightly with foil to prevent the topping from browning too much.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46FDF8A6F3FA2F42B9639CE1D6AFDF4C', X'88DB77FA1CB02C4EBF8AE677AA19A76D', X'88DB77FA1CB02C4EBF8AE677AA19A76D', 6, NULL, NULL, 2, NULL, 0, 'Serve alone or topped with vanilla ice cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37956824AE04C04BA3B194BE16D7EB69', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D13D88A8CDD81349A2ED9D19A00AEC9D', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 1, NULL, NULL, 2, NULL, 0, 'Make the filling: Trim the rhubarb stalks and cut them crosswise into ½-inch slices. Place into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C7BD38FA5C0804E83AD9B1807E44A8F', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 2, NULL, NULL, 2, NULL, 0, 'Trim the strawberries and cut them in half or in quarters or slices if large.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C179E69F0AEF249B80D82FCE37F8D17', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 3, NULL, NULL, 2, NULL, 0, 'Add the strawberries to the bowl. Add the ½ cup sugar, the cornstarch, and the lemon juice to the bowl and mix thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB9BB81B11DD094F8C7190B8F9597CCC', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 4, NULL, NULL, 2, NULL, 0, 'Pour the fruit mixture into the pie plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4ADA93496F0DA44B189193E00EA5A85', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 5, NULL, NULL, 2, NULL, 0, 'Make the topping: Combine the flour, oats, brown sugar, butter and salt in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56DD5905DA8B9E4EBC9923DE96214FB8', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 6, NULL, NULL, 2, NULL, 0, 'Blend the mixture together using your fingers and thumbs until there is no loose flour at the bottom of the bowl and much of the mixture is in pea-sized pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'398A973AB1F1104392805FB8F51AF6CC', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 7, NULL, NULL, 2, NULL, 0, 'Distribute the topping over the fruit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B4AFA9250780C4B83F239C479CDAE87', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 8, NULL, NULL, 2, NULL, 0, 'Place pie plate on a rimmed baking sheet to catch any drips. Bake uncovered for 20 minutes. Then cover loosely with a piece of foil to prevent further browning and bake for an additional 25 minutes until bubbly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66BFAFD442D78E4DACE2FD8C1AFE9AA2', X'A826B8A3E743D647B6E3828B1CCBE293', X'A826B8A3E743D647B6E3828B1CCBE293', 9, NULL, NULL, 2, NULL, 0, 'Cool for at least 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'775A0D97510BA14ABC703E73155963BD', X'A36B9B867986D049A9AEF67CF1BFE663', X'A36B9B867986D049A9AEF67CF1BFE663', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375° F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73F15203F682934786A3D9E357C47684', X'A36B9B867986D049A9AEF67CF1BFE663', X'A36B9B867986D049A9AEF67CF1BFE663', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the blueberries, sugar, cornstarch and lemon juice. Toss to combine, and pour into a deep-dish pie plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5C63A7D6BA38C4AB3C6E8A9EC96A7AD', X'A36B9B867986D049A9AEF67CF1BFE663', X'A36B9B867986D049A9AEF67CF1BFE663', 2, NULL, NULL, 2, NULL, 0, 'Wash and dry the bowl, and then combine the oats, flour, brown sugar, butter and salt. Mix them together thoroughly with your fingertips to form a crumbly topping.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCDA33DEB5DBF44AADD5983DB15577C5', X'A36B9B867986D049A9AEF67CF1BFE663', X'A36B9B867986D049A9AEF67CF1BFE663', 3, NULL, NULL, 2, NULL, 0, 'Distribute the topping over the fruit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FC019B6F5A2384F84869B375643776A', X'A36B9B867986D049A9AEF67CF1BFE663', X'A36B9B867986D049A9AEF67CF1BFE663', 4, NULL, NULL, 2, NULL, 0, 'Place the pie plate on a baking sheet to catch any drips. Bake until the topping is browned and the filling is bubbly, 40 to 45 minutes. Serve warm or at room temperature. Filling will thicken a bit as it cools.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7DA2364BAE0D641B40EB5953DC14550', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375° F with a rack in the center. Spray two donut pans lightly with cooking spray.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD4E07EFEA2067439C7222202A4E4960', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 1, NULL, NULL, 2, NULL, 0, 'Pour the cider into a frying pan and set over high heat. Boil until reduced to 1/2 cup, which takes about 10 minutes. Set aside to cool. (If you over-reduce it by accident, just top it up with regular cider. Not that I''ve ever done that, ahem.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C19B2528A445154B81A2D263CB3D5D8A', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the all-purpose and whole-wheat flours, regular and brown sugars, baking soda, baking powder, cinnamon, nutmeg and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B3C2566D044DF44BF3B730F11BE0B5B', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the egg, buttermilk, butter and reduced apple cider.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE096D947D9E374B9B02DD1A6CE30412', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 4, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients into the dry and stir just until combined. You don''t want any pockets of flour remaining, but you don''t want to over-mix.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA5B37D299562949ACD1C42D953C9B6D', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 5, NULL, NULL, 2, NULL, 0, 'You can spoon the batter into your prepared donut pans, but I prefer to pour the batter into a gallon-sized zip top plastic bag, snip off a small piece of one of the lower corners, and use it like a pastry bag to fill each well about 2/3 full.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D710E2A82501C24286F3BD288FAA04F2', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 6, NULL, NULL, 2, NULL, 0, 'Bake until the donuts spring back a bit when touched lightly, about eight minutes. Leave to cool for a couple of minutes, then gently remove donuts from pan. Cool on a rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAE3C4CE83C1C549A0EB28064707EBDF', X'30F52F097CB33F4F98068E04D0792FC2', X'30F52F097CB33F4F98068E04D0792FC2', 7, NULL, NULL, 2, NULL, 0, 'For the topping, melt butter and pour into a wide, shallow bowl. In a separate bowl, mix together sugar and cinnamon. Dip the top of each donut into the butter for just a second and then into the cinnamon sugar to coat as much as you like. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58831E7F97E50D48B2F6C4DA7B2C759D', X'D159A3B0F08CFA46A74AA895695441E3', X'D159A3B0F08CFA46A74AA895695441E3', 0, NULL, NULL, 2, NULL, 0, 'Snip open the tea bag and pour the contents into a small microwave-safe bowl (or a small pot if you’d prefer to use the stovetop). Add the butter and warm on medium heat until melted. Set aside to let the tea steep in the butter for five minutes. (You''ll strain out the tea solids later.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB62E0B13B8B5A489AC597EE2C77A4D3', X'D159A3B0F08CFA46A74AA895695441E3', X'D159A3B0F08CFA46A74AA895695441E3', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, stir together the flour, sugar, baking powder, cinnamon, ginger, nutmeg, cloves and allspice. Into the center of the bowl, crack the eggs and pour the buttermilk. Strain the butter into the bowl through a tea strainer or small fine-mesh sieve. Using a fork, beat the eggs a bit and then gently incorporate the dry ingredients into the wet to form a rough dough. Don’t overmix or donuts will be tough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E16371FAB9CC248B8F9732FE31F2437', X'D159A3B0F08CFA46A74AA895695441E3', X'D159A3B0F08CFA46A74AA895695441E3', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle a little flour on your work surface. (I sometimes like to roll out dough on a nice big piece of parchment — flour the parchment if you go that route.) Place the dough on the work surface and knead just a few times to bring it all together. Lightly flour the top of the dough and a rolling pin, and roll out to about 1/4-inch thickness. Using a 3 1/2-inch donut cutter with a 1-inch hole (or similarly sized cookie cutters, biscuit cutters or even an overturned water glass and shot glass) cut out as many donuts and holes as you can. Gather and re-roll the dough one time to make more donuts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FD79A7D93026D4395DA1FA5F462F47E', X'D159A3B0F08CFA46A74AA895695441E3', X'D159A3B0F08CFA46A74AA895695441E3', 3, NULL, NULL, 2, NULL, 0, 'In a Dutch oven or similar heavy pot with high sides, heat the oil over medium-high heat until it reaches 350°F. During cooking, adjust the heat as necessary to maintain the temperature as best as possible. Fry donuts in batches, about three at a time depending on the size of your pot. It should take about 1 1/2 to 2 minutes per side until donuts are golden brown and cooked through. Some cracking on the top before you flip each donut is a good indication that the inside is nicely cooked. Cool donuts on a wire rack lined with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52C3078C50C341489887AA8C842D044D', X'D159A3B0F08CFA46A74AA895695441E3', X'D159A3B0F08CFA46A74AA895695441E3', 4, NULL, NULL, 2, NULL, 0, 'In a wide, shallow bowl, stir together the powdered sugar, cinnamon and nutmeg. Pour in the tea and vanilla and stir until a nice smooth glaze forms. Dip the top of each donut into the glaze and return to rack. The glaze should dry within a few minutes. Serve donuts on the day you make them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B464794CAB95D46A2FBCD27B0F0D608', X'5D503C41F8DAD34BB750EBCF5F31DBAA', X'5D503C41F8DAD34BB750EBCF5F31DBAA', 0, NULL, NULL, 2, NULL, 0, 'Place the raspberries and sugar in a small pot and set over medium-high heat. Cook, stirring and mashing the raspberries with a spoon a bit from time to time, until sugar melts and most of the raspberries have broken down, 5 to 10 minutes. Pour raspberries into a fine-mesh strainer set over a 4-cup glass measuring cup. Stir and press the puree through the strainer, leaving seeds behind. (You''ll inevitably lose some puree too. Don''t worry about it.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B06D082C3297FC419755EA3009574D2F', X'5D503C41F8DAD34BB750EBCF5F31DBAA', X'5D503C41F8DAD34BB750EBCF5F31DBAA', 1, NULL, NULL, 2, NULL, 0, 'Let puree cool to room temperature. Then pour in Prosecco a little at a time, letting bubbles subside, until the total volume measures just shy of 30 ounces (or the total volume of your popsicle molds). Pour in lemon juice and stir well. Pour liquid into molds and freeze for an hour or two, then add popsicle sticks.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEA09B03DE9BBF488E8F201DD50E60FE', X'5D503C41F8DAD34BB750EBCF5F31DBAA', X'5D503C41F8DAD34BB750EBCF5F31DBAA', 2, NULL, NULL, 2, NULL, 0, 'Freeze until solid, at least 8 hours, preferably overnight.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E02077E210A28449A006568CFFBC8EA', X'21C11489C776764B901AE91DEE2CEBC4', X'21C11489C776764B901AE91DEE2CEBC4', 0, NULL, NULL, 2, NULL, 0, 'Combine sugar and water in a small pot and set over medium-high heat to boil. Stir from time to time, and remove from heat as soon as sugar is dissolved. Set aside to cool before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C730FC629E589246AFC2E48ACA001F11', X'21C11489C776764B901AE91DEE2CEBC4', X'21C11489C776764B901AE91DEE2CEBC4', 1, NULL, NULL, 2, NULL, 0, 'Combine sugar syrup, cherries, yogurt and vanilla and almond extracts in a good blender and puree until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24E45E130BAE974AB8C00D16C25C18A0', X'21C11489C776764B901AE91DEE2CEBC4', X'21C11489C776764B901AE91DEE2CEBC4', 2, NULL, NULL, 2, NULL, 0, 'Pour mixture into an ice cream maker and churn according to manufacturer''s instructions until frozen.***');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67C2223717AA574B995AEABD65048152', X'21C11489C776764B901AE91DEE2CEBC4', X'21C11489C776764B901AE91DEE2CEBC4', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately or scoop into a pint container and place in freezer for an hour or two. If freezing overnight, you''ll need to let it sit on the counter for 10 minutes and then dip the ice cream scoop in hot water before each serving. The yogurt will be slightly icy if frozen overnight (as in the photos, which I took the next day) -- but it''s still absolutely delicious.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CA7225E69A371418396908B235CF879', X'CD799BAAF8E9504C917BCA1348FA190C', X'CD799BAAF8E9504C917BCA1348FA190C', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot, whisk together the cream, milk, sugar, cornstarch and salt. Bring to a boil over medium-high heat, whisking occasionally. When the mixture boils, reduce the heat to maintain a lively simmer and cook, whisking constantly, for two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E359D20EDD3E146A036AE05A908562A', X'CD799BAAF8E9504C917BCA1348FA190C', X'CD799BAAF8E9504C917BCA1348FA190C', 1, NULL, NULL, 2, NULL, 0, 'Scrape the mixture into a medium bowl and add the pomegranate juice, liqueur or wine and lemon juice. Whisk to combine thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD6C643CE1C3849B990E12A5BB0957D', X'CD799BAAF8E9504C917BCA1348FA190C', X'CD799BAAF8E9504C917BCA1348FA190C', 2, NULL, NULL, 2, NULL, 0, 'Chill the mixture for at least an hour, either in the fridge or by carefully setting the bowl into a larger bowl of ice water and stirring occasionally.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93AD7C4EB88586478F2F164FC922AF77', X'CD799BAAF8E9504C917BCA1348FA190C', X'CD799BAAF8E9504C917BCA1348FA190C', 3, NULL, NULL, 2, NULL, 0, 'Freeze in an ice cream maker according to the manufacturer''s instructions. The gelato will be very soft (although perfectly edible!) at this point and will firm up if transferred to an airtight container and placed in the freezer for a few hours. Homemade gelato is always best eaten within a day or two, but this will keep in the freezer for a week or more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BDEF251FB22F34799703ADBB540F596', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 0, NULL, NULL, 2, NULL, 0, 'Wash and dry the apples very well. (See notes below.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A38D8D999DF8145B6D91062E45BC46D', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 1, NULL, NULL, 2, NULL, 0, 'Pull the stems out of the apples and insert one end of a popsicle stick, caramel apple stick, or twig into the stem end, about halfway into the apple, depending on size. You want to leave enough of the stick available for gripping while also ensuring it''s firmly in place. Return the apples to the fridge until ready to dip.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4AD2071D4E775449E1F45BE84B72E45', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 2, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with a silicone mat (preferred) or with parchment paper sprayed with nonstick cooking spray, and set aside. If spraying with cooking spray, do NOT place the apples on this surface before coating with caramel.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51322600D96F0247ADF8417D242E24EF', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 3, NULL, NULL, 2, NULL, 0, 'Spread chopped peanuts on a plate and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A416359D44C41745946E2F429E67E38C', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 4, NULL, NULL, 2, NULL, 0, 'Place the water, sugar, and salt into an impeccably clean 3- to 4-quart pot with a heavy bottom. Bring to a boil over medium heat, stirring constantly with a wooden spoon or heat-safe spatula until the sugar dissolves completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3F583E9B6E73C4F864EED2EFD75C328', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 5, NULL, NULL, 2, NULL, 0, 'When the mixture boils, stop stirring. Simmer undisturbed until sugar syrup takes on an amber color, about 10 minutes. (For a long time the mixture will be clear, and you''ll see the bubbles gradually get bigger. Once it starts to take on some color, the rest happens fairly quickly.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0FA943D0CA074E9D39304947D41FB0', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 6, NULL, NULL, 2, NULL, 0, 'As soon as the syrup is a nice, medium-dark amber, carefully pour in the cream. The mixture will boil up furiously. Stir vigorously with a wooden spoon or heatproof spatula. Continue cooking, stirring from time to time, until the caramel reaches 250°F (121°C).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B534C61D743FA46924EB2EA13045540', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 7, NULL, NULL, 2, NULL, 0, 'At this stage you can choose whether to leave the caramel in the pot or pour it into a 2-cup heatproof glass measuring cup. Let the caramel cool to about 212°F (100°C).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41FBFF743A2B5443B01D4258D716C260', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 8, NULL, NULL, 2, NULL, 0, 'One at a time, hold each apple by the stick and swirl to coat in the caramel. Let excess caramel drip back into the cup or pot. The caramel will cool quickly, so work as fast as you reasonably can. Place each apple on the prepared baking sheet. See notes below about reheating if necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74E321928FB04549B6DE57D769C3FA88', X'F089AA3883E968418B8353446A3BCC98', X'F089AA3883E968418B8353446A3BCC98', 9, NULL, NULL, 2, NULL, 0, 'Roll the base of each caramel-coated apple in chopped nuts. Set each apple back on the baking sheet to cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA46D2707B5C1E4B8729AF064F9B882B', X'23BFBF2802CFFF4EB905A667FFE48D8B', X'23BFBF2802CFFF4EB905A667FFE48D8B', 0, NULL, NULL, 2, NULL, 0, 'First, halve the peach by cutting around the perimeter from stem end to bottom and back up the other side.<img width=`1200` height=`673` src=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200.jpg` class=`attachment- size-` alt=`halving a peach from stem end` srcset=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200.jpg 1200w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-525x294.jpg 525w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-920x516.jpg 920w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-768x431.jpg 768w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-480x270.jpg 480w` sizes=`(max-width: 1200px) 100vw, 1200px` />');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76528AC431388B40B02E883AB21930A1', X'23BFBF2802CFFF4EB905A667FFE48D8B', X'23BFBF2802CFFF4EB905A667FFE48D8B', 1, NULL, NULL, 2, NULL, 0, 'Next, twist one half away from you and the other half toward you to separate.<img width=`1200` height=`674` src=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-2.jpg` class=`attachment- size-` alt=`twisting peach halves to separate` srcset=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-2.jpg 1200w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-2-525x295.jpg 525w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-2-920x517.jpg 920w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-2-768x431.jpg 768w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-2-480x270.jpg 480w` sizes=`(max-width: 1200px) 100vw, 1200px` />');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95E82604A5E6A941A9B881D24CEC9B86', X'23BFBF2802CFFF4EB905A667FFE48D8B', X'23BFBF2802CFFF4EB905A667FFE48D8B', 2, NULL, NULL, 2, NULL, 0, 'Remove the pit. Hold the peach half gently in one hand. Using the other hand and starting at the stem end, gently but firmly pry the pit out of the flesh. <img width=`1200` height=`670` src=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-3.jpg` class=`attachment- size-` alt=`prying a pit out of a peach half` srcset=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-3.jpg 1200w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-3-525x293.jpg 525w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-3-920x514.jpg 920w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-3-768x429.jpg 768w` sizes=`(max-width: 1200px) 100vw, 1200px` />');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD976DD1C66E394AA6853C1A5F5A0F56', X'23BFBF2802CFFF4EB905A667FFE48D8B', X'23BFBF2802CFFF4EB905A667FFE48D8B', 3, NULL, NULL, 2, NULL, 0, 'Now the peach is ready to slice or dice. Slice each half into four or five wedges. Then, if you like, cut each wedge crosswise into four or five small pieces. <img width=`1200` height=`672` src=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-4.jpg` class=`attachment- size-` alt=`cutting a peach` srcset=`https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-4.jpg 1200w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-4-525x294.jpg 525w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-4-920x515.jpg 920w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-4-768x430.jpg 768w, https://umamigirl.com/wp-content/uploads/2020/07/How-to-Cut-a-Peach-Step-by-Step-Umami-Girl-1200-4-480x270.jpg 480w` sizes=`(max-width: 1200px) 100vw, 1200px` />');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB0197E121F7124393B9767FA74F6254', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'443EC4D83E02CF45A1F02CD35A6D425D', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry cherries and remove stems. You can decided whether to pit them or leave pits in place.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26E929E57136E045987B9048D3A5E618', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 2, NULL, NULL, 2, NULL, 0, 'Use the butter to grease the baking dish. Add cherries and place in preheated oven for five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D11800F2726894DB69A6F9496AFA363', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the batter. Place milk, 1/3 cup of the sugar, eggs, vanilla, salt and flour into blender and process on high for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC4D6842DE34CC4697417973EA90691E', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 4, NULL, NULL, 2, NULL, 0, 'Remove pan from oven and sprinkle warm cherries with remaining 1/3 cup sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97C2B97031278F4E8D7F2937AB0D9401', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 5, NULL, NULL, 2, NULL, 0, 'Pour batter into dish and place back in oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC1B00CC2DAE2C4D8D98978B5CB0A19B', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 6, NULL, NULL, 2, NULL, 0, 'Bake for about 55 minutes, until clafoutis is browned on top and a knife inserted into the center comes out clean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20BDBB94EEF7C0438E8FA4D95A052E67', X'826E30BB62FD9D4BB07CA55D300DBB0D', X'826E30BB62FD9D4BB07CA55D300DBB0D', 7, NULL, NULL, 2, NULL, 0, 'Serve warm (but not super-hot, because cherries retain heat like crazy and will burn your tongue, I''ve HEARD), sprinkled with powdered sugar right before serving if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CC02C55733FEF4CB123BD56AB0BE17E', X'F71B8023253BA34AB764F020F607F421', X'F71B8023253BA34AB764F020F607F421', 0, NULL, NULL, 2, NULL, 0, 'Wash rhubarb well. Remove and discard any leaves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B72032CA31BD9944AD30F728B9F80A90', X'F71B8023253BA34AB764F020F607F421', X'F71B8023253BA34AB764F020F607F421', 1, NULL, NULL, 2, NULL, 0, 'Trim and discard the ends from the rhubarb stalks.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F0D74553BE6A34BB4A70FBCCEA9B649', X'F71B8023253BA34AB764F020F607F421', X'F71B8023253BA34AB764F020F607F421', 2, NULL, NULL, 2, NULL, 0, 'Slice stalks into segments of about 1/2- to 1 inch.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AFB21D3F9E07748B8A1BAE3703C7B2D', X'F71B8023253BA34AB764F020F607F421', X'F71B8023253BA34AB764F020F607F421', 3, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking sheet with parchment and distribute the rhubarb on the baking sheet, without crowding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33EA485FACE9044B76B86C4C4ED5F45', X'F71B8023253BA34AB764F020F607F421', X'F71B8023253BA34AB764F020F607F421', 4, NULL, NULL, 2, NULL, 0, 'Freeze until solid, about 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CEC96103849EC4797810BC5B4CCD745', X'F71B8023253BA34AB764F020F607F421', X'F71B8023253BA34AB764F020F607F421', 5, NULL, NULL, 2, NULL, 0, 'Place frozen rhubarb into a zip-top freezer bag or other tightly sealed container.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D9A95A93C728C4C9170E269F94CF5B2', X'F71B8023253BA34AB764F020F607F421', X'F71B8023253BA34AB764F020F607F421', 6, NULL, NULL, 2, NULL, 0, 'Keep frozen for up to a year to use in all your favorite rhubarb recipes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50C0F81554719E40B6C21A45512DD59C', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 0, NULL, NULL, 2, NULL, 0, 'Arrange one or two small bowls for the chocolate dip onto the serving platter. (You''ll pour the dip in later.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'899A70D4327DC04F9974B9B85B4DA8E5', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 1, NULL, NULL, 2, NULL, 0, 'Gently rinse and dry all fruit.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF5CCD5443853647A0D69EE9AF709AFB', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 2, NULL, NULL, 2, NULL, 0, 'To prepare the apples, quarter them, remove the core from each quarter by slicing it out diagonally, and then slice each quarter into four or five slices. Granny Smith apples don''t brown quickly, so you can do this in advance without worrying about it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09D9D3796ED0545A47F6E5654B9518D', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 3, NULL, NULL, 2, NULL, 0, 'To prepare the grapes, snip the stems to form little bundles of 5 to 10 grapes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'174B4F74B2353D4A9CEAE13C72B65D75', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 4, NULL, NULL, 2, NULL, 0, 'To prepare the star fruit, trim off and discard a thin slice from each end, then slice the star fruit crosswise into about 8 star-shaped slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA992425C7EDAC4E89DE38607B1EFE1E', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 5, NULL, NULL, 2, NULL, 0, 'To prepare the dragon fruit, trim off and discard a thin slice from the stem end. Quarter the dragon fruit lengthwise and then cut each quarter crosswise into slices. The pink skin is not edible, but it''s so beautiful that I like to leave it on. It''s very easy for eaters to peel away from the edible flesh and gives them something to hold onto while eating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DF68AEE36BAAD44B387B148CFAE997A', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 6, NULL, NULL, 2, NULL, 0, 'To prepare the kiwis, peel off the furry skin with a vegetable peeler or paring knife. Slice kiwis crosswise into rounds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6FA2401190E446B5426B6C0149ED88', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 7, NULL, NULL, 2, NULL, 0, 'To prepare the watermelon, cut a baby watermelon in half crosswise and reserve half for another use. Place the half watermelon cut side down and cut it into four quarters. Slice each quarter crosswise into triangular slices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43D52763DCFCC14D9DE932C099FE1B77', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 8, NULL, NULL, 2, NULL, 0, 'To prepare the strawberries, either leave whole if they''re smaller or remove leaves and cut into halves or quarters if they''re large.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BBE11EA9F347940B47CB32D09E8CDC8', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', X'16ABC8B664A4FC46B8CEFEDC1A34F44F', 9, NULL, NULL, 2, NULL, 0, 'To prepare cape gooseberries, remove papery hulls and wash well to remove as much of sticky exterior coating as is reasonable. You may not be able to remove it all, and that''s okay.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F50570882709214FBF62B0BC12E501A8', X'AF013D995F3A994D9F4C98C0CEF3DDB9', X'AF013D995F3A994D9F4C98C0CEF3DDB9', 0, NULL, NULL, 2, NULL, 0, 'Stem and slice strawberries and place in a mixing bowl. Gently stir in balsamic to coat berries. Let sit while you make the whipped cream (or up to a day in the fridge).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6121BC9905F72E41A79264BFF2D26B4E', X'AF013D995F3A994D9F4C98C0CEF3DDB9', X'AF013D995F3A994D9F4C98C0CEF3DDB9', 1, NULL, NULL, 2, NULL, 0, 'Beat cream with sugar and vanilla until thick and fluffy. Spoon berries into serving bowls and top with whipped cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59E915A31BA2404A94ED3E82A64BCA3C', X'5E904D5470277E4587BD7EF7E1179F78', X'5E904D5470277E4587BD7EF7E1179F78', 0, NULL, NULL, 2, NULL, 0, 'For the meringue: Preheat oven to 250°F.* Place a sheet of parchment paper on a baking sheet (turn the baking sheet upside down if it has a rim, so you can slide the meringue off later). If you like, draw a 9-inch circle as a guide on one side of the parchment, then flip it over so the circle is on the underside. (You can trace a plate or a cake pan to make it easy.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D371D30DE911F74D9775865FB93EADCF', X'5E904D5470277E4587BD7EF7E1179F78', X'5E904D5470277E4587BD7EF7E1179F78', 1, NULL, NULL, 2, NULL, 0, 'Place the egg whites into the bowl of a stand mixer fitted with the whisk, or into a large bowl if using electric beaters. Whip until stiff peaks form. (If you lift the beaters or a spoon out of the egg whites, the tips of the peaks that form should stand up without falling over.) Add sugar a tablespoon at a time, beating for 30 seconds or so between additions. You should have a big, glossy bowl of what looks like marshmallow fluff at this point. Sprinkle in cornstarch, vinegar and vanilla and fold in with a spatula. Spoon the mixture into a 9-inch circle on the parchment, using the guide if you made one. Bake for 60 minutes, then turn off the oven and let meringue cool completely in the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88967FE6AD723B4BA237D08613AE99F4', X'5E904D5470277E4587BD7EF7E1179F78', X'5E904D5470277E4587BD7EF7E1179F78', 2, NULL, NULL, 2, NULL, 0, 'This temperature produces a slightly browned/cream-colored exterior. If you prefer a perfectly white meringue, you can bake in a 180°F oven for 90 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC9D7239348BC4095A8EA56F24F198A', X'5E904D5470277E4587BD7EF7E1179F78', X'5E904D5470277E4587BD7EF7E1179F78', 3, NULL, NULL, 2, NULL, 0, 'For the whipped cream: Combine the heavy cream, sugar and vanilla in a stand mixer or bowl and whip until just firm enough to hold its shape.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E60255367A6E9A42B503569291A317CD', X'5E904D5470277E4587BD7EF7E1179F78', X'5E904D5470277E4587BD7EF7E1179F78', 4, NULL, NULL, 2, NULL, 0, 'For the berry topping: stir together berries, sugar and balsamic in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16141A9506DC7D4EB81C6506D8712928', X'5E904D5470277E4587BD7EF7E1179F78', X'5E904D5470277E4587BD7EF7E1179F78', 5, NULL, NULL, 2, NULL, 0, 'To serve: Right before serving, slide meringue onto a cake plate. Gently spread whipped cream over the surface and top with berries and accumulated juices. Slice into wedges to serve. (Or go at it family style with long spoons -- no judgment.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42C7B98A4AFB7941B27099D54F9216EF', X'4C5552F67862E240AB0A5483AEF77E31', X'4C5552F67862E240AB0A5483AEF77E31', 0, NULL, NULL, 2, NULL, 0, 'Preheat the broiler (on low, if you have a choice), with the top rack about six inches below the heat source.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D95DB48ADFEA194084CE054A7FDF5D6D', X'4C5552F67862E240AB0A5483AEF77E31', X'4C5552F67862E240AB0A5483AEF77E31', 1, NULL, NULL, 2, NULL, 0, 'Cut each fig in half from stem end to base. You''ll be broiling the figs cut-side up, so if your figs aren''t symmetrical, try to make the cut such that you''ll have as stable a base as possible on the uncut side. Arrange the fig halves cut side up on a baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F73B9180DCCD2F42B4AB437D641C8D5F', X'4C5552F67862E240AB0A5483AEF77E31', X'4C5552F67862E240AB0A5483AEF77E31', 2, NULL, NULL, 2, NULL, 0, 'Slice open the vanilla bean with a paring knife and scrape out the seeds with the tip of the knife. Spread a bit of seeds on the cut side of each fig half. You don''t need a lot to get a great vanilla flavor.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFCD218A75668D48A2CB71213855EB0F', X'4C5552F67862E240AB0A5483AEF77E31', X'4C5552F67862E240AB0A5483AEF77E31', 3, NULL, NULL, 2, NULL, 0, 'Drizzle the cut side of each fig half lightly with honey. Again, you don''t need too much.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE216BD4029CA448AD66E4FF977A8A7F', X'4C5552F67862E240AB0A5483AEF77E31', X'4C5552F67862E240AB0A5483AEF77E31', 4, NULL, NULL, 2, NULL, 0, 'Place the pan on the top rack of the oven and broil for a few minutes, until the tops of the figs are bubbly and very lightly browned. (It takes about five minutes in my oven, but broilers vary substantially.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D740D93CC644BC4CBA8F6EB264547666', X'4C5552F67862E240AB0A5483AEF77E31', X'4C5552F67862E240AB0A5483AEF77E31', 5, NULL, NULL, 2, NULL, 0, 'Serve warm by themselves or perched atop a small bowl of vanilla ice cream or Greek yogurt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B55452D0DF056F419509726B344D4B52', X'9873103C89825F4E95015D6DDE0B51DE', X'9873103C89825F4E95015D6DDE0B51DE', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot, combine the water, sugar, vanilla bean (both pod and seeds), lemon zest and salt. Bring to a boil over high heat, and boil for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'571BB812E89CF04FA0F95A2E82C96700', X'9873103C89825F4E95015D6DDE0B51DE', X'9873103C89825F4E95015D6DDE0B51DE', 1, NULL, NULL, 2, NULL, 0, 'Add the apricots, gently arranging them in the pot to submerge as many as possible. Reduce the heat to maintain a low simmer, adjusting as necessary. Poach the apricots for about 5 minutes, until they''re as soft as you''d like them. You''ll probably need to shift them around gently in the pot to give them equal time in the liquid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2232B870BE5E0B498F90843413083DCB', X'9873103C89825F4E95015D6DDE0B51DE', X'9873103C89825F4E95015D6DDE0B51DE', 2, NULL, NULL, 2, NULL, 0, 'Remove the apricots to a bowl and pour the liquid overtop. Let cool completely at room temperature, about 90 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83754D5BE4AF37488FB7670DD51BE4A9', X'084F471CF6CA8B489F7DBF3FD5E7E89E', X'084F471CF6CA8B489F7DBF3FD5E7E89E', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, combine the ginger, vanilla seeds and pod, brown sugar, lemon peel and water. Bring to a gentle boil for three minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'630AE9156B970C46B7EF5DCC2FF115F1', X'084F471CF6CA8B489F7DBF3FD5E7E89E', X'084F471CF6CA8B489F7DBF3FD5E7E89E', 1, NULL, NULL, 2, NULL, 0, 'Place the pears in a medium bowl and pour the syrup over them. Carefully cover the surface with a piece of parchment paper. Leave at room temperature during dinner, or for up to two hours. (Chill if making far in advance.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'271F18D2CC78B149AF9DD7C31EC14923', X'084F471CF6CA8B489F7DBF3FD5E7E89E', X'084F471CF6CA8B489F7DBF3FD5E7E89E', 2, NULL, NULL, 2, NULL, 0, 'Just before serving, whip together the cream, sugar and vanilla extract. Divide the pears among four bowls with a little bit of the syrup (avoid the ginger pieces and vanilla pod), top with a bit of whipped cream, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07475344820F924FBC48AC8D1E79E363', X'CFB76E939581CA45AA1A1CB2A031D5ED', X'CFB76E939581CA45AA1A1CB2A031D5ED', 0, NULL, NULL, 2, NULL, 0, 'Pit and dice the good parts of the fruit (or leave whole if you''re working with berries, halve if cherries, etc.) and gently stir together with the sugar in an appropriately sized pot. Heat the mixture over medium heat until the fruit has released some of its juices and the sugar has dissolved, maybe 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A54B7A6476B668498D8E0360F51F0507', X'CFB76E939581CA45AA1A1CB2A031D5ED', X'CFB76E939581CA45AA1A1CB2A031D5ED', 1, NULL, NULL, 2, NULL, 0, 'Serve by itself, or over yogurt, ice cream, pancakes, waffles, you name it (but for once, probably not a fried egg).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBDB645E881FF345942D17E86DB518CE', X'AC31639CF9DD464EA2EAD0433A38CB14', X'AC31639CF9DD464EA2EAD0433A38CB14', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F. Unfold the puff pastry onto a lightly floured cutting board. Using a 5-inch cookie cutter (or an overturned bowl and a small, sharp knife), cut three 5-inch circles from each sheet of dough. (If this isn''t possible, you can roll out the puff pastry gently with a floured rolling pin just until it''s big enough to accommodate the circles.) Place the rounds on a parchment-lined cookie sheet. Press a 3-inch cookie cutter into the center of each round, stopping 3/4 of the way down so that the outer and inner portions are still attached at the bottom. This will allow the outer ring to rise around the center. Pierce the inner section of each round all over with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E5FD4817F40F4489001DC04C8C6EEA', X'AC31639CF9DD464EA2EAD0433A38CB14', X'AC31639CF9DD464EA2EAD0433A38CB14', 1, NULL, NULL, 2, NULL, 0, 'Place the goat cheese, powdered sugar, lemon juice and salt in a small bowl. Microwave for 30 seconds or until soft but not melted. Stir to combine well, then divide and spread over the inner section of each round. Top with overlapping apple slices, making sure to stay within the inner section of each round.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1F5FF260691904181C08E66DD9E3CC4', X'AC31639CF9DD464EA2EAD0433A38CB14', X'AC31639CF9DD464EA2EAD0433A38CB14', 2, NULL, NULL, 2, NULL, 0, 'Combine the butter and honey in a small bowl and microwave for 30 seconds, or until the butter is almost melted. Stir to combine and complete the melting. (Be careful, as the honey gets hot surprisingly quickly.) Brush some honey butter over the entirety of each round. Dust lightly with ground allspice. Bake for 25 minutes, or until the apples are tender and the pastry is nicely browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'836B8ED209A89442AEFE066097BD6B10', X'AC31639CF9DD464EA2EAD0433A38CB14', X'AC31639CF9DD464EA2EAD0433A38CB14', 3, NULL, NULL, 2, NULL, 0, 'Let the pastries cool slightly, then drizzle with additional honey if desired. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E5F7FDBDEE7E1429B9A449C2D599F23', X'1EB9CE3C23315D4F8054BCB54EED4AFE', X'1EB9CE3C23315D4F8054BCB54EED4AFE', 0, NULL, NULL, 2, NULL, 0, 'Place the butter, powdered sugar and egg yolks in the bowl of a food processor fitted with the blade. Pulse until combined but still speckled with butter. Add the flour and run the machine just until the dough comes together when you pinch it between your fingers (see photo above). Turn dough out onto a large piece of parchment, knead a few times to bring it all together, and pat it into a disc shape. Wrap well in parchment and chill for about half an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'844298272D2BAC44B751DCE16F495A25', X'1EB9CE3C23315D4F8054BCB54EED4AFE', X'1EB9CE3C23315D4F8054BCB54EED4AFE', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Remove dough from the fridge and let it rest on the counter for 15 minutes. Sprinkle a bit of flour on your work surface, and roll out the dough with a rolling pin to an approximately 12-inch circle. Transfer the dough very carefully to a 10-inch tart pan, pressing on the dough lightly so it sits snugly against the bottom and sides of the pan. Prick the bottom of the shell all over with a fork. Place a piece of parchment paper over the shell, making sure to cover the edges. Spread plenty of dried beans over the parchment, covering the whole bottom of the tart shell. Bake for 15 minutes this way, then remove the parchment and beans and return the shell to the oven for an additional 10 minutes. Remove from the oven and let cool for at least 10 minutes before filling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EC4697E4E387143A21937AA3EB30D28', X'6126253580A4BE4A8BB0BFCCDCBEA14A', X'6126253580A4BE4A8BB0BFCCDCBEA14A', 0, NULL, NULL, 2, NULL, 0, 'Place the butter, powdered sugar and egg yolks in the bowl of a food processor fitted with the blade. Pulse until combined but still speckled with butter. Add the flour and run the machine just until the dough comes together when you pinch it between your fingers (see photo above). Turn dough out onto a large piece of parchment, knead a few times to bring it all together, and pat it into a disc shape. Wrap well in parchment and chill for about half an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D847B69E12E6374C91A1F214E894100E', X'6126253580A4BE4A8BB0BFCCDCBEA14A', X'6126253580A4BE4A8BB0BFCCDCBEA14A', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Remove dough from the fridge and let it rest on the counter for 15 minutes. Sprinkle a bit of flour on your work surface, and roll out the dough with a rolling pin to an approximately 12-inch circle. Transfer the dough very carefully to a 10-inch tart pan, pressing on the dough lightly so it sits snugly against the bottom and sides of the pan. Prick the bottom of the shell all over with a fork. Place a piece of parchment paper over the shell, making sure to cover the edges. Spread plenty of dried beans over the parchment, covering the whole bottom of the tart shell. Bake for 15 minutes this way, then remove the parchment and beans and return the shell to the oven for an additional 10 minutes. Remove from the oven and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57D7274A4CBC9741A00CD329E5D59FD3', X'952EE8743690AF41B4121B0F2D27FBCA', X'952EE8743690AF41B4121B0F2D27FBCA', 0, NULL, NULL, 2, NULL, 0, 'Make the crust: Sift the flour, sugar and salt together into the bowl of a food processor fitted with the blade attachment. Add the butter and pulse until the dough has a sandy texture, with no large lumps of butter. Add the egg and water mixture and pulse until the dough begins to form a ball. Add ice water by the teaspoonful, pulsing after each addition, if the dough is too dry to come together. Turn the dough out onto a lightly floured work surface, divide into four equal pieces, and shape into four small discs. Wrap and refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C4E6D4A84CA114899905A5B41C790F6', X'952EE8743690AF41B4121B0F2D27FBCA', X'952EE8743690AF41B4121B0F2D27FBCA', 1, NULL, NULL, 2, NULL, 0, 'Roll out and bake the crust: Preheat the oven to 400°F. On a lightly floured work surface, roll out the dough discs one by one into 7-inch circles. Carefully press each circle into a 5-inch mini tart pan with a removable bottom. Place the pans on a baking sheet and refrigerate for 15 minutes. Then prick the bottoms of the tart shells all over with a fork, line with parchment, and fill with pie weights or dried beans. Bake for about 10 minutes, until edges look chalky and white. Remove the parchment and beans, lower the oven to 350°F, and bake until cooked through and golden brown, about 25 minutes more. Cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC1A95EE7F7F54E9EC4AEE3A6FDAF91', X'952EE8743690AF41B4121B0F2D27FBCA', X'952EE8743690AF41B4121B0F2D27FBCA', 2, NULL, NULL, 2, NULL, 0, 'Make the filling and assemble the tarts: In the bowl of a stand mixer, beat together the cream cheese and muscovado sugar until smooth and lightened in color, about 3 minutes. Beat in the sour cream, lemon juice, and vanilla extract. Spread 1/4 of the filling into each tart shell. Top with berries, cut sides down. Sprinkle lightly with muscovado sugar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59F48A4920B9604CA17E68C0FB78E512', X'78D44E14E75CF64E897235E336628A8F', X'78D44E14E75CF64E897235E336628A8F', 0, NULL, NULL, 2, NULL, 0, 'In a small pot or in the microwave, warm the cream to at least 100°F (you don''t need to use a thermometer — just be sure it''s warm to the touch). A minute or so in the microwave should do it. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3E8BF6F8E0C364DA7C26175B546366B', X'78D44E14E75CF64E897235E336628A8F', X'78D44E14E75CF64E897235E336628A8F', 1, NULL, NULL, 2, NULL, 0, 'Place the sugar, water, and corn syrup into a large, impeccably clean pot (see notes below) and stir them together with a clean spoon or your index finger. Set the pot over medium heat and do not stir at all from this point forward.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'521ECB7EF8C007469B59D0B00C66118A', X'78D44E14E75CF64E897235E336628A8F', X'78D44E14E75CF64E897235E336628A8F', 2, NULL, NULL, 2, NULL, 0, 'Let the mixture come to a boil and then continue to cook just until it turns a beautiful amber color, about the color of an old copper penny. If you like, you can use a candy thermometer and pull the pot off the heat as it''s about to reach 350°F. I find it''s just as easy to do it by eye.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA54480C508FB640B71EA484CB847325', X'78D44E14E75CF64E897235E336628A8F', X'78D44E14E75CF64E897235E336628A8F', 3, NULL, NULL, 2, NULL, 0, 'Immediately and very carefully begin pouring in the cream. It will bubble up furiously. Carefully give it a stir.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA893B470E9284281F1E0779C39F26B', X'78D44E14E75CF64E897235E336628A8F', X'78D44E14E75CF64E897235E336628A8F', 4, NULL, NULL, 2, NULL, 0, 'Stir in the bourbon, sour cream, and salt. Pour into a heat-safe pitcher or jar to cool. Serve slightly warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D361B7833DFE94ABAE713175EA30784', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 0, NULL, NULL, 2, NULL, 0, 'If you''ll be canning the jam, prepare the jars and hot water canner according to the instructions below.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73E6FFB977F4864CABB895A6AC5DB3DD', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 1, NULL, NULL, 2, NULL, 0, 'In a wide pot, stir together strawberries, rhubarb, butter, cardamom, pepper, and calcium water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0AD77F227DF3E49971EB024982A02D3', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 2, NULL, NULL, 2, NULL, 0, 'Cook, stirring frequently, over medium heat until the fruit starts to release its juices.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC08B3980E7E94459850AF13B5F250A2', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 3, NULL, NULL, 2, NULL, 0, 'Raise heat to high and bring to a full boil. Then reduce heat again and simmer, stirring frequently to prevent burning, until fruit has softened completely and largely broken down, about 15 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3540CC0EF2BAA7458482226894B791E9', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk the sugar and pectin together thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'731A0F937EE9654A86AA6FB216C0578A', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 5, NULL, NULL, 2, NULL, 0, 'Add sugar mixture to pan and whisk constantly while mixture returns to a full boil. Simmer briskly for one to two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4783CC7669E5C249AF53F60E48C02E8E', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in vanilla.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D2CB7FBB0E54149A18D2640ED5A6CBA', X'A0D626825B30674BB2FF55757D73E411', X'A0D626825B30674BB2FF55757D73E411', 7, NULL, NULL, 2, NULL, 0, 'Funnel into jars, leaving 1/2-inch headspace. Refrigerate once cool or proceed to canning instructions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2F14C16F0CAE34C8F16AD4DD7B05D92', X'3938A81D01A97A45B1985773E8054805', X'3938A81D01A97A45B1985773E8054805', 0, NULL, NULL, 2, NULL, 0, 'If you like, chill a large mixing bowl (or the bowl of your stand mixer) and the whisk attachment to your mixer for at least 10 minutes before beginning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1DB9217D3850345B5656F4C6F182BEE', X'3938A81D01A97A45B1985773E8054805', X'3938A81D01A97A45B1985773E8054805', 1, NULL, NULL, 2, NULL, 0, 'Pour very cold cream into mixer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F83E361BE3E75B4597BE160966355C87', X'3938A81D01A97A45B1985773E8054805', X'3938A81D01A97A45B1985773E8054805', 2, NULL, NULL, 2, NULL, 0, 'Add sugar and vanilla.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C77013F41BEC17448A1CF7FF4700C8A4', X'3938A81D01A97A45B1985773E8054805', X'3938A81D01A97A45B1985773E8054805', 3, NULL, NULL, 2, NULL, 0, 'Beat on medium to medium-high speed until cream reaches the stage your recipe calls for: soft, medium, or firm peaks. Watch the video in this post and consult the photos above for visual cues.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C7DAEB112F20040BD099A7D2784D852', X'FF073E6A3AF5D34B9E4D599E336789EB', X'FF073E6A3AF5D34B9E4D599E336789EB', 0, NULL, NULL, 2, NULL, 0, 'Fill the pot with one inch of water. Bring to a simmer over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'885A6EE8E5E14D468A052D62D4C53C0E', X'FF073E6A3AF5D34B9E4D599E336789EB', X'FF073E6A3AF5D34B9E4D599E336789EB', 1, NULL, NULL, 2, NULL, 0, 'In a food processor or mortar and pestle, combine the sugar and lemon zest and pulse or mash until the sugar is yellow and the delicious smell of lemon sugar almost knocks you out, about a minute in the food processor or a few minutes in the mortar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B39160A5F4F86748993B3B3003547E8A', X'FF073E6A3AF5D34B9E4D599E336789EB', X'FF073E6A3AF5D34B9E4D599E336789EB', 2, NULL, NULL, 2, NULL, 0, 'Combine the lemon sugar, eggs and egg yolks in the heatproof bowl. Whisk together for one minute to distribute the sugar. Place the bowl over the pot with simmering water and whisk constantly for about 30 seconds, until the sugar is dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2793D9F6419C084CA5C1E28B0A2FEF01', X'FF073E6A3AF5D34B9E4D599E336789EB', X'FF073E6A3AF5D34B9E4D599E336789EB', 3, NULL, NULL, 2, NULL, 0, 'Add the lemon juice and lime juice and cook, whisking constantly for about 10 minutes, until the curd reads 160° F on an instant read thermometer and has the consistency of sour cream. Remove the bowl from the heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBEB14C88C812241A2C9C06CDE1DC0C8', X'FF073E6A3AF5D34B9E4D599E336789EB', X'FF073E6A3AF5D34B9E4D599E336789EB', 4, NULL, NULL, 2, NULL, 0, 'Whisk in the pieces of cold butter one by one until they are completely incorporated. Strain the curd through a fine-mesh sieve into a clean bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5380A50ED4F61428328C9F82821ACF3', X'FF073E6A3AF5D34B9E4D599E336789EB', X'FF073E6A3AF5D34B9E4D599E336789EB', 5, NULL, NULL, 2, NULL, 0, 'If you are giving the curd as gifts, divide it among four small jelly jars while it is still warm. Then chill in the refrigerator. The curd lasts in the refrigerator for up to a week and also freezes beautifully. Especially since people can get so overfed around the holidays, it''s nice to include a label on the jar that lets recipients know they can freeze it for up to three months.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A65CECFF0D556143B12539E61EE8D4F0', X'766084FCA8DD3749AFF94A8306DF8856', X'766084FCA8DD3749AFF94A8306DF8856', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, combine the raspberries and 1/3 cup sugar. Cook over medium-high heat, stirring occasionally, until the raspberries have broken down most of the way and the syrup has thickened slightly, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7873805FE4C2C46B802CE6F3CA960B5', X'766084FCA8DD3749AFF94A8306DF8856', X'766084FCA8DD3749AFF94A8306DF8856', 1, NULL, NULL, 2, NULL, 0, 'Push the syrup through a fine-mesh strainer to remove seeds. You''ll inevitably lose a fair amount of the beautiful red pulp to a fine-mesh strainer, but keep pushing and stirring as long as you can stand it to extract as much as possible. You can do this step up to a few days in advance and keep the strained syrup in the fridge. Either way, be sure to let the syrup cool completely before proceeding. (You can set the bowl with the syrup over a bowl of ice to speed the process if you like.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63B137967EE1E545AAC393D0C837C50D', X'766084FCA8DD3749AFF94A8306DF8856', X'766084FCA8DD3749AFF94A8306DF8856', 2, NULL, NULL, 2, NULL, 0, 'As close to serving time as feasible, beat the heavy cream just shy of the soft peak stage with an electric mixer. Add the remaining 1/3 cup sugar and beat to incorporate. Pour in the raspberry syrup and beat just a touch more. You can finish the mixing by hand to make sure it''s well-mixed without overbeating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F61F298D241164E8BF9C508B33E4F61', X'810D4963BA23544EAA25ABDEA4E1689F', X'810D4963BA23544EAA25ABDEA4E1689F', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle or the whisk, beat the butter until light and fluffy, about 2 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5093BB59259F914A9CA996BBC1A2C26A', X'810D4963BA23544EAA25ABDEA4E1689F', X'810D4963BA23544EAA25ABDEA4E1689F', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and mix on low speed until incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B80A96150EF7145AAD9B5611B7BFBEF', X'810D4963BA23544EAA25ABDEA4E1689F', X'810D4963BA23544EAA25ABDEA4E1689F', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla extract and milk and turn up the speed to medium high. Beat until very light and smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D09471495B0D0F43BEC23861BA86BA99', X'810D4963BA23544EAA25ABDEA4E1689F', X'810D4963BA23544EAA25ABDEA4E1689F', 3, NULL, NULL, 2, NULL, 0, 'If making multiple colors, divide icing into separate bowls. Use a toothpick to swirl a little bit of gel icing color into each bowl, then mix thoroughly, adding more as necessary to achieve desired shade.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D95C79ED0DE5D439F342DAEC96B4BCE', X'810D4963BA23544EAA25ABDEA4E1689F', X'810D4963BA23544EAA25ABDEA4E1689F', 4, NULL, NULL, 2, NULL, 0, 'Spread or pipe onto cupcakes and decorate as you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'846B57E2ABBD874999A1773F501ED196', X'18E692A7AB3E88448AEAFC4C436C1132', X'18E692A7AB3E88448AEAFC4C436C1132', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the whisk (or in a large bowl with electric beaters), beat the butter for three to four minutes until light and creamy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AD4FAD51A99D44A9FCD4BCCB9F4EA38', X'18E692A7AB3E88448AEAFC4C436C1132', X'18E692A7AB3E88448AEAFC4C436C1132', 1, NULL, NULL, 2, NULL, 0, 'Sift together the powdered sugar and cocoa powder, then add to mixer (with mixer off) along with milk and vanilla. Beat on low until incorporated, then on high for three to four minutes until smooth and fluffy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BB953E13D5FAA4D868E301CD008A994', X'18E692A7AB3E88448AEAFC4C436C1132', X'18E692A7AB3E88448AEAFC4C436C1132', 2, NULL, NULL, 2, NULL, 0, 'Let sit for 10 minutes to help powdered sugar chillax a bit into the mix. (Technical term.) If frosting is too thick, beat in up to two tablespoons more milk a little at a time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'977F5206134AF2478387A30D58341694', X'18E692A7AB3E88448AEAFC4C436C1132', X'18E692A7AB3E88448AEAFC4C436C1132', 3, NULL, NULL, 2, NULL, 0, 'This frosting pipes beautifully and spreads equally well with a spatula. I prefer to use it right away, but you can keep it covered in the fridge for several days, bring to cool room temperature and whisk again for a minute to use leftovers.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C94FEF2047628643B7B8BFBC54735E3F', X'945B76FF1D33F142858402DF100A1572', X'945B76FF1D33F142858402DF100A1572', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, heat the cream and corn syrup over medium heat until simmering. Simmer for two minutes. Off the heat, add chocolate chips, vanilla and salt. Let sit for two minutes, then stir to melt chocolate completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F25EBCB976DD4C4184AAE93EB62B0A81', X'945B76FF1D33F142858402DF100A1572', X'945B76FF1D33F142858402DF100A1572', 1, NULL, NULL, 2, NULL, 0, 'Sauce can be used immediately, but it thickens up nicely if left to sit. I prefer using it just slightly warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B13FC5BE45F55443AB104A36731AE988', X'D5CAB7C2BB9BA54BB8E3D67D66165235', X'D5CAB7C2BB9BA54BB8E3D67D66165235', 0, NULL, NULL, 2, NULL, 0, 'With a serrated knife, chop the chocolate into very small pieces. Loosely place the chopped chocolate in a medium heatproof bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'506FF21D9B551D43AF453DBEC81EDF61', X'D5CAB7C2BB9BA54BB8E3D67D66165235', X'D5CAB7C2BB9BA54BB8E3D67D66165235', 1, NULL, NULL, 2, NULL, 0, 'In a very small pot, heat the cream and the coffee just until it comes to a full boil. Immediately pour the cream over the chocolate. Gently shake the bowl so that some of the cream heads toward the bottom of the bowl. Pour the vanilla extract overtop and sprinkle with the salt. Let rest for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E9FEE87AA61E344A95B01EE4B2077B6', X'D5CAB7C2BB9BA54BB8E3D67D66165235', X'D5CAB7C2BB9BA54BB8E3D67D66165235', 2, NULL, NULL, 2, NULL, 0, 'Gently stir the liquid and chocolate together for two full minutes, until the ganache is glossy and smooth. Serve warm or chilled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12E91CA9111CB743AA0A6CBDE9F15241', X'15F527DE34E2A4478630800AEF32EE26', X'15F527DE34E2A4478630800AEF32EE26', 0, NULL, NULL, 2, NULL, 0, 'Pour one inch of water into a medium pot that accommodates your heat-proof glass bowl without letting it touch the water''s surface. Bring water to a simmer over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71F7C3E8515B8B40A5A97033235CD0B4', X'15F527DE34E2A4478630800AEF32EE26', X'15F527DE34E2A4478630800AEF32EE26', 1, NULL, NULL, 2, NULL, 0, 'In a food processor or mortar and pestle, combine the sugar, grapefruit zest, and vanilla seeds, and pulse or mash until well combined and very fragrant.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B09E412F65CCD40BA461FD64042434C', X'15F527DE34E2A4478630800AEF32EE26', X'15F527DE34E2A4478630800AEF32EE26', 2, NULL, NULL, 2, NULL, 0, 'Combine the grapefruit sugar, eggs and egg yolks in the heatproof bowl. Whisk together for one minute to distribute the sugar. Place the bowl over the pot with simmering water and whisk constantly for about 30 seconds, or until the sugar is dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CAC0546697CA443A36C5091C93F2C9F', X'15F527DE34E2A4478630800AEF32EE26', X'15F527DE34E2A4478630800AEF32EE26', 3, NULL, NULL, 2, NULL, 0, 'Add the grapefruit juice and lemon juice and cook, whisking frequently, until the curd reads 170° F and has the consistency of sour cream, about 10 minutes. Remove the bowl from the heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A97AC389C99934CA1AB00E588E76250', X'15F527DE34E2A4478630800AEF32EE26', X'15F527DE34E2A4478630800AEF32EE26', 4, NULL, NULL, 2, NULL, 0, 'Whisk in the pieces of cold butter one by one until they are completely incorporated. Strain the curd through a fine-mesh sieve into a clean bowl. Serve as a condiment with scones or toast for breakfast, or spooned into tart shells for dessert. Will keep, tightly covered, in the fridge for up to two weeks, or frozen for several months.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3897685216755F4B878C487CDDBCC9EA', X'3752EB332D0594498D398250AEA6804A', X'3752EB332D0594498D398250AEA6804A', 0, NULL, NULL, 2, NULL, 0, 'Using a serrated knife, finely chop the 70% chocolate. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F2153A7BC75B540931726E10B2B9F57', X'3752EB332D0594498D398250AEA6804A', X'3752EB332D0594498D398250AEA6804A', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan with a heavy bottom, whisk together the cocoa powder, sugar, cornstarch, and salt. Gently whisk in the milk until the cocoa, sugar, and cornstarch have dissolved almost completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'817F53E53DB75B4F8A17BA11CDF0C6EA', X'3752EB332D0594498D398250AEA6804A', X'3752EB332D0594498D398250AEA6804A', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, whisking often. When the mixture boils, immediately reduce the heat to medium-low. Cook, whisking, for one minute. The pudding will have thickened noticeably. Remove saucepan from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF0024F79B5EDD41ABB57884C489CE4C', X'3752EB332D0594498D398250AEA6804A', X'3752EB332D0594498D398250AEA6804A', 3, NULL, NULL, 2, NULL, 0, 'Off the heat, add the chopped chocolate and the vanilla. Stir until the chocolate is melted and thoroughly incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7793150AE2D0EF4C8E3C7C9167CF6339', X'3752EB332D0594498D398250AEA6804A', X'3752EB332D0594498D398250AEA6804A', 4, NULL, NULL, 2, NULL, 0, 'If you’re that type, pass the pudding through a fine-mesh strainer to remove any possibility of lumps. Otherwise, or afterward, spoon the pudding into six 4-ounce ramekins, filling each about two-thirds full, or into a single serving bowl. If you don’t like the idea of inanimate objects with skin, cover the pudding with plastic wrap directly touching its surface. If you don’t like the idea of plastic wrap directly touching the surface of your food, cover the ramekins or serving bowl without doing so. Chill for at least two hours before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B943339EF92A444A650941D4874873E', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', 0, NULL, NULL, 2, NULL, 0, 'Warm the oil in a 12-inch skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A64447CCB0AF30419BD1103E9F1AA4AF', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', 1, NULL, NULL, 2, NULL, 0, 'Add the onion along with a good pinch of salt and cook, stirring occasionally, until softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7A9C28539856943936B748864794316', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, ginger, turmeric, mustard seeds and cumin seeds. Cook, stirring frequently, until very fragrant and lightly browned, a minute or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AC6C43F8A67A54F9E12BFC0FD840653', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', 3, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium. Add the spinach by the handful, incorporating more as soon as each handful wilts enough to make space. Stir as you go to distribute the onion and spice mixture throughout.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3791AB7E5D4F7145AAD03C87E7E00043', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', 4, NULL, NULL, 2, NULL, 0, 'As soon as you''ve added all the spinach, pour in the chickpeas. Sprinkle with the garam masala, cayenne another pinch of salt, and plenty of freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'609944B31E7D8440ADDA16AB1D09B091', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', X'627DEA167FF0CF4099E3D1FC5E6EAFCE', 5, NULL, NULL, 2, NULL, 0, 'Add the cream, give it all a good stir, and simmer for a minute or two, until the cream thickens slightly but the spinach is still nice and green.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58FEC5A36E9077498DFC8547C6512D06', X'51CC4084B4FAEE41A4D556A740BF40E1', X'51CC4084B4FAEE41A4D556A740BF40E1', 0, NULL, NULL, 2, NULL, 0, 'In a 5 1/2-quart Dutch oven or similar heavy pot, warm the olive oil over medium-high heat. Add the onion, green pepper, carrot, celery and 1 teaspoon salt and cook, stirring only occasionally, until vegetables are beginning to be tender and browned, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B94FCC17F76A2D47B5F13567F62E26FE', X'51CC4084B4FAEE41A4D556A740BF40E1', X'51CC4084B4FAEE41A4D556A740BF40E1', 1, NULL, NULL, 2, NULL, 0, 'Add garlic and cook, stirring frequently, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E344F99ACC82245887A062C6F481DF0', X'51CC4084B4FAEE41A4D556A740BF40E1', X'51CC4084B4FAEE41A4D556A740BF40E1', 2, NULL, NULL, 2, NULL, 0, 'Add tomato paste, chili powder, cumin and paprika and cook, stirring, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A044F1F796618F48B739EC014134D51E', X'51CC4084B4FAEE41A4D556A740BF40E1', X'51CC4084B4FAEE41A4D556A740BF40E1', 3, NULL, NULL, 2, NULL, 0, 'Stir in broth and tomatoes (with all their juices). Bring to a boil, then reduce heat to simmer for 5 minutes. Sauce should be nice and thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D13C35BA0B0864BADFC08F7CA2AE6E2', X'51CC4084B4FAEE41A4D556A740BF40E1', X'51CC4084B4FAEE41A4D556A740BF40E1', 4, NULL, NULL, 2, NULL, 0, 'Add beans and corn (straight from frozen is fine) and simmer until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDABFE7314DB364DBBFC4110BA9616A6', X'51CC4084B4FAEE41A4D556A740BF40E1', X'51CC4084B4FAEE41A4D556A740BF40E1', 5, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in cilantro and lime juice. Serve with shredded cheese, avocado, red onion and sour cream if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47BA7A2387D3A3409086E487928B2568', X'4248B34F22196F40BE9E6ECB641F6AEB', X'4248B34F22196F40BE9E6ECB641F6AEB', 0, NULL, NULL, 2, NULL, 0, 'Dry tofu very well between several layers of paper towels. I usually do this twice with a change of paper towels in between, pressing gently to extract moisture without damaging the tofu.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3746E9B640D29479FC22182D1F5DA82', X'4248B34F22196F40BE9E6ECB641F6AEB', X'4248B34F22196F40BE9E6ECB641F6AEB', 1, NULL, NULL, 2, NULL, 0, 'Cut tofu into approximate 1/2-inch cubes and dry well again, changing paper towels as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F777F65B594EB499CECA24F4C1793EF', X'4248B34F22196F40BE9E6ECB641F6AEB', X'4248B34F22196F40BE9E6ECB641F6AEB', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, stir together cornstarch, salt, and pepper. Add tofu cubes and toss gently to coat evenly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13553BF7098A734FACB6FB8E86FA6BD0', X'4248B34F22196F40BE9E6ECB641F6AEB', X'4248B34F22196F40BE9E6ECB641F6AEB', 3, NULL, NULL, 2, NULL, 0, 'Warm the oil over medium-high heat in a 12-inch nonstick skillet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'354C9A4FB14E9B4FB5BF706CB8B3E476', X'4248B34F22196F40BE9E6ECB641F6AEB', X'4248B34F22196F40BE9E6ECB641F6AEB', 4, NULL, NULL, 2, NULL, 0, 'Add tofu in a single layer and cook until golden brown on the bottom, then stir once in a while and continue cooking until most sides are golden. This will take about 10 minutes total.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA0A09C668A22D45B9A2B93A8B030D3C', X'4248B34F22196F40BE9E6ECB641F6AEB', X'4248B34F22196F40BE9E6ECB641F6AEB', 5, NULL, NULL, 2, NULL, 0, 'Slide tofu onto paper towels to drain any excess oil');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7178BD615FC6A4394A0DE50797D4DCB', X'072838FAD6082A4FAC2EE742654C89AF', X'072838FAD6082A4FAC2EE742654C89AF', 0, NULL, NULL, 2, NULL, 0, 'Rinse lentils well in a fine-mesh sieve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'460EB695093BAE4BA88AE73933B5C076', X'072838FAD6082A4FAC2EE742654C89AF', X'072838FAD6082A4FAC2EE742654C89AF', 1, NULL, NULL, 2, NULL, 0, 'Place in a small (about 2-quart) pot and cover with water by 1 to 2 inches. Stir in herbs and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F931D89D16329041A0490E0606B5D429', X'072838FAD6082A4FAC2EE742654C89AF', X'072838FAD6082A4FAC2EE742654C89AF', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then immediately reduce heat to maintain a gentle simmer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CB1A8A3B31B784DBC86498773D71BAD', X'072838FAD6082A4FAC2EE742654C89AF', X'072838FAD6082A4FAC2EE742654C89AF', 3, NULL, NULL, 2, NULL, 0, 'Cook until lentils are tender but still intact, about 25 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC895C0ED274C94EABC072C5BAF6E018', X'072838FAD6082A4FAC2EE742654C89AF', X'072838FAD6082A4FAC2EE742654C89AF', 4, NULL, NULL, 2, NULL, 0, 'Drain and serve as you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'861D9B0FD4DD9545AD1F7EFA34BC6934', X'072838FAD6082A4FAC2EE742654C89AF', X'072838FAD6082A4FAC2EE742654C89AF', 5, NULL, NULL, 2, NULL, 0, 'Keeps, tightly covered, in the fridge for a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FD64C7AC685DD43B9328A5EA712816F', X'BF35A61C22B7AC48AFCA975E2D97DF84', X'BF35A61C22B7AC48AFCA975E2D97DF84', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, stir together the beans, celery, shallot, olives and parsley.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CCA817A1CAA804C8F479B83FF7B3854', X'BF35A61C22B7AC48AFCA975E2D97DF84', X'BF35A61C22B7AC48AFCA975E2D97DF84', 1, NULL, NULL, 2, NULL, 0, 'Add the oil, vinegar, lemon juice and zest, paprika if using, and salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6C1B5B4DE68F84795EDD47C1C86F36B', X'BF35A61C22B7AC48AFCA975E2D97DF84', X'BF35A61C22B7AC48AFCA975E2D97DF84', 2, NULL, NULL, 2, NULL, 0, 'Stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'655EBE6ECE27444296A91A1BE045B1A4', X'BF35A61C22B7AC48AFCA975E2D97DF84', X'BF35A61C22B7AC48AFCA975E2D97DF84', 3, NULL, NULL, 2, NULL, 0, 'Serve at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'350719D0FF1B04408FE017F27078A13D', X'67A6ABBA5D62BA41855D7F5687EE4FC0', X'67A6ABBA5D62BA41855D7F5687EE4FC0', 0, NULL, NULL, 2, NULL, 0, 'Place the chickpeas in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4A38CEDC5E28A4185AD992437308CFA', X'67A6ABBA5D62BA41855D7F5687EE4FC0', X'67A6ABBA5D62BA41855D7F5687EE4FC0', 1, NULL, NULL, 2, NULL, 0, 'In a small skillet, heat the oil until shimmering.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'390338F06A9FBB43966DFF445904B4D6', X'67A6ABBA5D62BA41855D7F5687EE4FC0', X'67A6ABBA5D62BA41855D7F5687EE4FC0', 2, NULL, NULL, 2, NULL, 0, 'Add the mustard seeds, partially cover the skillet and cook over medium-high heat until the mustard seeds almost stop popping, about a minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EA57D7AC025CA488DD16F15CD6D1315', X'67A6ABBA5D62BA41855D7F5687EE4FC0', X'67A6ABBA5D62BA41855D7F5687EE4FC0', 3, NULL, NULL, 2, NULL, 0, 'Add the cumin and fennel seeds and cook for another minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D06DD0F2833A1B4C82FB1B1047D0AD1A', X'67A6ABBA5D62BA41855D7F5687EE4FC0', X'67A6ABBA5D62BA41855D7F5687EE4FC0', 4, NULL, NULL, 2, NULL, 0, 'Pour the hot oil and spices over the chickpeas.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4765BAE1343EF4081EB54400D9200C0', X'67A6ABBA5D62BA41855D7F5687EE4FC0', X'67A6ABBA5D62BA41855D7F5687EE4FC0', 5, NULL, NULL, 2, NULL, 0, 'Stir in the yogurt, lemon zest and juice, scallions, cilantro and salt. Serve at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22CB9A3992975C4C9FB0F63BFCBAA1E9', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 0, NULL, NULL, 2, NULL, 0, 'In a medium-sized pot, heat the bacon fat or oil over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'431801AEA1B95F468DE6B9B4A25E865C', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 1, NULL, NULL, 2, NULL, 0, 'Add the diced onion and pepper along with the salt, and cook, stirring occasionally, until starting to soften, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'411CDCB82C319F4482C717A10122A72F', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, tomato paste, cumin, and chili powder. Cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41E33313F91D364685283B21A05461EF', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 3, NULL, NULL, 2, NULL, 0, 'Add the beans along with 1/2 cup bean cooking water, broth, or plain water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE626F04DBBF8247A3A1BE7F9EC71CCE', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72DD48673E20AB4DA9EB4587C324DCA1', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 5, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low and simmer for 15 minutes. Add up to 1/2 cup more water if the beans become too dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'015FF088EA3AA042B762EABF2F5FBBD3', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 6, NULL, NULL, 2, NULL, 0, 'Then, using a potato masher, the back of a wooden spoon, or an immersion blender, mash the beans until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A95D0ABE15384F4D8E900363C6190A06', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 7, NULL, NULL, 2, NULL, 0, 'Stir in lime juice and taste for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8786E9AE8BF6294A8720889A005BB784', X'3E4D7CAB36A8D4449A7F564BC8686886', X'3E4D7CAB36A8D4449A7F564BC8686886', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B9CB8CF64217343831978EF0E30A613', X'D6448574FAFDB848BD6A9FB9B3482CF6', X'D6448574FAFDB848BD6A9FB9B3482CF6', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil over medium heat in a large frying pan that has a lid (but don''t but the lid on yet).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9BF26FEA71C6A4CB8C631E3F5ACEA0B', X'D6448574FAFDB848BD6A9FB9B3482CF6', X'D6448574FAFDB848BD6A9FB9B3482CF6', 1, NULL, NULL, 2, NULL, 0, 'Add the onion and ginger, sprinkle with the salt, and cook until the onion is softening and beginning to turn translucent, 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'201456F60086D54DBFDC665E677652E9', X'D6448574FAFDB848BD6A9FB9B3482CF6', X'D6448574FAFDB848BD6A9FB9B3482CF6', 2, NULL, NULL, 2, NULL, 0, 'Add the mustard and cumin seeds and cook, stirring occasionally, until toasted, 2 or 3 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63B65025BAE5E7468A08AF9F321DC30E', X'D6448574FAFDB848BD6A9FB9B3482CF6', X'D6448574FAFDB848BD6A9FB9B3482CF6', 3, NULL, NULL, 2, NULL, 0, 'Add the turmeric, coriander, paprika and cayenne and cook, stirring, one minute. Then add the tomato paste and cook, stirring, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2C851B076F323489F741B311B8C6CF2', X'D6448574FAFDB848BD6A9FB9B3482CF6', X'D6448574FAFDB848BD6A9FB9B3482CF6', 4, NULL, NULL, 2, NULL, 0, 'Add the stock or water, lentils and coconut milk and stir to combine. Bring to a boil, then reduce the heat to maintain a simmer and cover the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE3A185E78500043BD31E722C2F4A316', X'D6448574FAFDB848BD6A9FB9B3482CF6', X'D6448574FAFDB848BD6A9FB9B3482CF6', 5, NULL, NULL, 2, NULL, 0, 'Cook until liquid is absorbed and lentils are tender, about 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F5871C4DC905944AE073766E2ECEA1D', X'D6448574FAFDB848BD6A9FB9B3482CF6', X'D6448574FAFDB848BD6A9FB9B3482CF6', 6, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in some black pepper along with the cilantro and lime juice. Serve with basmati brown rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9426CAD9A3E9474B8DA35149FA3A23DC', X'6A3E146E8FC3FD4384DF38519765167B', X'6A3E146E8FC3FD4384DF38519765167B', 0, NULL, NULL, 2, NULL, 0, 'Steam the cauliflower until crisp-tender and place in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'319EE9F6EEBC9D4B9D2D45B87DA02F59', X'6A3E146E8FC3FD4384DF38519765167B', X'6A3E146E8FC3FD4384DF38519765167B', 1, NULL, NULL, 2, NULL, 0, 'In a small pot, combine lentils, stock, herbes de Provence and a generous pinch of salt. Bring to a boil, then cover and simmer until liquid is almost absorbed and lentils are barely tender, about 20 minutes. Let rest, covered, for 10 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94D0E5ADB70697459B962B40DE7F1DB5', X'6A3E146E8FC3FD4384DF38519765167B', X'6A3E146E8FC3FD4384DF38519765167B', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a large skillet over medium-high heat until it shimmers. Add the onion along with a generous sprinkle of salt and cook, stirring occasionally, until softened and browned in spots. Add garlic and capers and cook until very fragrant. Stir in cooked quinoa and heat through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50A83897F58A654A9E0D16EC8FD1ED6C', X'6A3E146E8FC3FD4384DF38519765167B', X'6A3E146E8FC3FD4384DF38519765167B', 3, NULL, NULL, 2, NULL, 0, 'Tip lentils and onion mixture into the bowl with the cauliflower. Sprinkle with the lemon juice, walnuts and pepper and toss gently to combine. Serve warm, with Pecorino to pass if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'868EFD0738261940B30389809175DB8F', X'0943114BF104B84A853409EC67DA503C', X'0943114BF104B84A853409EC67DA503C', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil or ghee for a minute over medium heat in a large pot with a heavy bottom. Add the onion and cook, stirring occasionally, until translucent, about five minutes. Add the ginger, garlic, jalapeno or cayenne, mustard seed, cumin, turmeric, coriander and cinnamon and cook, stirring almost constantly, until it smells amazing -- about a minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'430C5D836E21BD49BE3524C7B293A241', X'0943114BF104B84A853409EC67DA503C', X'0943114BF104B84A853409EC67DA503C', 1, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, coconut milk and salt, raise the heat to high to bring to a boil, then reduce to a simmer for 10 minutes, stirring occasionally. Add the chickpeas and zucchini and continue to simmer for about 20 minutes, until zucchini is tender but not falling apart.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DECE6601D6471640856B5C534C98334B', X'0943114BF104B84A853409EC67DA503C', X'0943114BF104B84A853409EC67DA503C', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in cilantro. Serve with rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A953328C6F807141BBC2A86377AEAC9E', X'1720A0A2BAB08A45A1181BA63823B0F2', X'1720A0A2BAB08A45A1181BA63823B0F2', 0, NULL, NULL, 2, NULL, 0, 'If using canned chickpeas, drain, rinse, and dry them thoroughly. In a large bowl, combine the chickpeas, shallots, and cilantro.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30752B6BDFA1A54DB3E90A11F75137D1', X'1720A0A2BAB08A45A1181BA63823B0F2', X'1720A0A2BAB08A45A1181BA63823B0F2', 1, NULL, NULL, 2, NULL, 0, 'In a small pan, toast the sesame and cumin seeds over medium-low heat until the sesame seeds begin to turn golden brown and the seeds are very fragrant. Add the warm seeds to the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4174C05E989B3428DEDB0C9B6405ED6', X'1720A0A2BAB08A45A1181BA63823B0F2', X'1720A0A2BAB08A45A1181BA63823B0F2', 2, NULL, NULL, 2, NULL, 0, 'In a blender, combine the lemon juice and zest, olive oil, garlic, salt, and pepper. Blend until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8746182DEACDC64582A9AB64B42FC8C1', X'1720A0A2BAB08A45A1181BA63823B0F2', X'1720A0A2BAB08A45A1181BA63823B0F2', 3, NULL, NULL, 2, NULL, 0, 'Pour dressing over salad and toss to combine well. Serve immediately or store in the fridge for up to three days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3641E920F0FB6441AEC8C6F3F121423E', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a heavy 5-quart pot over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEA5BD09F7ABB145ABCC9E4FA49C28F7', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 1, NULL, NULL, 2, NULL, 0, 'Add the onion and cook, stirring occasionally, until softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFE7A3997DA9254DAAFC7B4BB6564FCB', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, ginger, cumin, coriander, paprika, and turmeric and cook, stirring constantly, for 90 seconds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECE74557B30B8B47ADE731666157F92F', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 3, NULL, NULL, 2, NULL, 0, 'Add the split peas and the salt and pepper along with 5 cups water, and give it all a good stir.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B2DD7EF6C3D1D43B754E8A862D52CDA', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 4, NULL, NULL, 2, NULL, 0, 'Raise the heat to high and bring to a boil, then reduce the heat to maintain a simmer and cover the pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC08DDE2D7C8B5449AF45CF61B3ECDDC', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 5, NULL, NULL, 2, NULL, 0, 'Cook, stirring occasionally, until split peas are very tender and have lost their defined shape, about an hour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBD9D7BB0B1C2E4B9F11FF39791DD9E0', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 6, NULL, NULL, 2, NULL, 0, 'Stir in lemon juice and zest and cilantro.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F997AC39D2623643AA1CF743D2F7F967', X'1BAD65063742624A8DF91D203540D2E4', X'1BAD65063742624A8DF91D203540D2E4', 7, NULL, NULL, 2, NULL, 0, 'Serve as a main dish with rice and leafy greens or as an accompaniment to a larger meal (à la Indian dal).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE3CBACF9434694C906C2DBD69AFCFC7', X'EF609832CF16DA4D8A9E390B51A305F3', X'EF609832CF16DA4D8A9E390B51A305F3', 0, NULL, NULL, 2, NULL, 0, 'Rinse the lentils well under running water, then place them in a bowl with the water and soak for 30 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DE69C3AA2182540A7092279291F6B53', X'EF609832CF16DA4D8A9E390B51A305F3', X'EF609832CF16DA4D8A9E390B51A305F3', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, finely chop the cilantro (including stems), onion, and garlic, and grate the ginger. Set the cilantro aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E2FD899A688DA42B658485348C87310', X'EF609832CF16DA4D8A9E390B51A305F3', X'EF609832CF16DA4D8A9E390B51A305F3', 2, NULL, NULL, 2, NULL, 0, 'Place the mustard seeds in a 5-quart Dutch oven or other pot with a heavy bottom, and cook over medium heat until they begin to pop. Add the onion, garlic, ginger, and olive oil. Reduce heat to low and cook, stirring frequently, for 5 minutes. Add the ground coriander, cumin, turmeric, paprika, and curry leaves, and continue cooking, stirring frequently, for 3 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E6CD4DC3C46DD4386854E5E37CBA90C', X'EF609832CF16DA4D8A9E390B51A305F3', X'EF609832CF16DA4D8A9E390B51A305F3', 3, NULL, NULL, 2, NULL, 0, 'Add the lentils and their soaking water, the tomatoes, honey, fenugreek, and salt, and stir to blend. Cover and simmer for about 30 minutes, until the lentils are fully cooked.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C40D7CBBCD8E8046A0AAE46FC11D66D8', X'EF609832CF16DA4D8A9E390B51A305F3', X'EF609832CF16DA4D8A9E390B51A305F3', 4, NULL, NULL, 2, NULL, 0, 'Stir in the butter, lemon or lime juice, and cilantro. Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7D33D8401F5D4E86F380C8C98C693B', X'0D9A172703C0E04E91FE76C819C284FD', X'0D9A172703C0E04E91FE76C819C284FD', 0, NULL, NULL, 2, NULL, 0, 'If cooking larger, thicker-skinned beans such as chickpeas, great northern, cannellini, soak them for four to eight hours in cool water filtered if possible, to cover by a couple of inches, with two teaspoons of salt. Then proceed as directed in step two, discarding soaking water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D39947C05D3A8044905EE157BDDFD214', X'0D9A172703C0E04E91FE76C819C284FD', X'0D9A172703C0E04E91FE76C819C284FD', 1, NULL, NULL, 2, NULL, 0, 'Rinse beans under running water and pick out any non-beanlike goodies such as small stones. Place beans in a medium pot and add fresh cool water, filtered if possible, to cover by about two inches.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28FFACB46FDF824FB2C898104E1659CA', X'0D9A172703C0E04E91FE76C819C284FD', X'0D9A172703C0E04E91FE76C819C284FD', 2, NULL, NULL, 2, NULL, 0, 'Add salt, bay leaves, garlic, and onion, along with additional ingredients listed above, if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AB6BEC14D2CB242832351619D34598A', X'0D9A172703C0E04E91FE76C819C284FD', X'0D9A172703C0E04E91FE76C819C284FD', 3, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then reduce heat to a gentle simmer. Partially cover the pot and simmer slowly until beans are done to your liking, stirring occasionally and topping up with additional water if necessary. This will take anywhere from an hour to three hours or more, depending on the type and age of the beans you’re using and how tender you''d like them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C6E16CB8A4AB744803401D1BEBE2D0D', X'0AB98C6CFCFA3244822891764E96B130', X'0AB98C6CFCFA3244822891764E96B130', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large frying pan over medium-high heat. Add the onion and sauté until tender, about 5 minutes. Add the tofu and sauté until warm, about 2 minutes. Add the curry paste and stir gently but thoroughly to coat the tofu well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7366485BE1A33429B6ACF549DAF5347', X'0AB98C6CFCFA3244822891764E96B130', X'0AB98C6CFCFA3244822891764E96B130', 1, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and their juices, the cauliflower, and 1 cup water. Bring to a boil, cover, and reduce heat to medium-low. Simmer until cauliflower is almost tender, about 15 minutes. Remove cover and simmer briskly until sauce thickens, about 5 minutes more. Serve spooned over rice. Leftovers will keep well in the fridge for a few days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A12210633B15994C81B1D4DF056DA527', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 250°F (121°C) with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB55ECE727BE684489414EDA1FC917F3', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 1, NULL, NULL, 2, NULL, 0, 'Pat sirloin cap dry with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA126CF0B5B33F4491F2236074450F4F', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 2, NULL, NULL, 2, NULL, 0, 'Score the fat in a crosshatch pattern. Make the cuts deep enough to cut through the fat but not through the meat. See video and step-by-step photos in post for visual cues.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E28906FEBFBD84ABBE7FAFCEB880B3D', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle both sides generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25F626A9BA0D684E933B73EC16E0C129', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 4, NULL, NULL, 2, NULL, 0, 'Place steaks cut-side down in a cast iron pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE517B3DAA8D0A44BFC3CFC0643D96C2', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 5, NULL, NULL, 2, NULL, 0, 'Roast for about 30 minutes, until internal temperature reaches 105°F (41°C).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CF07488092E9A4BA9C109B2F70647B5', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 6, NULL, NULL, 2, NULL, 0, 'Remove steaks from pan and place pan over high heat on stovetop.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47ADD8BAB867D84E8A690440B7482E7F', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 7, NULL, NULL, 2, NULL, 0, 'Place steaks back in pan and sear 1 to 2 minutes per side, until golden brown.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29678910CB28574387A65176B6198669', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 8, NULL, NULL, 2, NULL, 0, 'Rest 10 minutes before slicing against the grain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5841AE93076AC747AF8CB984CC319FA1', X'776E27CB48084745A385E7AEDAC2F09B', X'776E27CB48084745A385E7AEDAC2F09B', 9, NULL, NULL, 2, NULL, 0, 'Serve with chimichurri sauce (if you like).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0E26E23F7D8C543AA0C2F7EF075B451', X'3EE051BAA0FF7F47ABA0366625B38C6C', X'3EE051BAA0FF7F47ABA0366625B38C6C', 0, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron skillet over medium heat for two minutes. Open the hamburger buns and brush each inside face with some of the melted butter. Place buns buttered side down (probably in two batches, depending on your skillet) and toast for a couple of minutes, until light golden brown on the underside. Place buns on serving plates.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9F293F6A3907343BD4478F86687407D', X'3EE051BAA0FF7F47ABA0366625B38C6C', X'3EE051BAA0FF7F47ABA0366625B38C6C', 1, NULL, NULL, 2, NULL, 0, 'Raise heat to medium-high. Divide beef into four hockey pucks and sprinkle both sides generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28976902B2888049B48ABFFFDDF96B23', X'3EE051BAA0FF7F47ABA0366625B38C6C', X'3EE051BAA0FF7F47ABA0366625B38C6C', 2, NULL, NULL, 2, NULL, 0, 'Place pucks onto skillet and use a sturdy metal spatula to firmly smash each puck into a patty that''s about 1/3-inch thick. After this, absolutely ignore the burgers for about three minutes, until the undersides are brown and crisp at the edges and the juices on the surface are hot and bubbly. Scrape up each patty and any brown bits with the spatula and flip it. Cook one to two minutes more. Place a slice of cheese on top of each burger at the very end of cooking to melt just enough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C76D7791392FB448AB76018406D27CCE', X'3EE051BAA0FF7F47ABA0366625B38C6C', X'3EE051BAA0FF7F47ABA0366625B38C6C', 3, NULL, NULL, 2, NULL, 0, 'To make the sauce, stir together all sauce ingredients in a small bowl until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B6DE8D2411C164BBE5D7C0210D357D3', X'3EE051BAA0FF7F47ABA0366625B38C6C', X'3EE051BAA0FF7F47ABA0366625B38C6C', 4, NULL, NULL, 2, NULL, 0, 'Transfer patties to buns. Layer each burger with two tomato slices and a piece of lettuce. Spoon 1/4 of the sauce onto each top bun half. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B08BBE6034B8EE4992D7C3B03669A0C0', X'C16B206B711DFE4EBCEEEB1E685C4E7C', X'C16B206B711DFE4EBCEEEB1E685C4E7C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BA08A0C9657FD4EB30523C6DC78DA91', X'C16B206B711DFE4EBCEEEB1E685C4E7C', X'C16B206B711DFE4EBCEEEB1E685C4E7C', 1, NULL, NULL, 2, NULL, 0, 'Pat filets dry with paper towels and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE5B5011BAF56747BF7CC3B6E309271E', X'C16B206B711DFE4EBCEEEB1E685C4E7C', X'C16B206B711DFE4EBCEEEB1E685C4E7C', 2, NULL, NULL, 2, NULL, 0, 'Heat a 12-inch cast iron pan over high heat until very hot. Carefully pour in oil and coat bottom of pan by swirling or spreading with a spatula.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7FC3147B189C74F8F6E2CEDD1AB296F', X'C16B206B711DFE4EBCEEEB1E685C4E7C', X'C16B206B711DFE4EBCEEEB1E685C4E7C', 3, NULL, NULL, 2, NULL, 0, 'Place filets in pan and cook undisturbed for three minutes to sear. Flip once and cook two minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'546035AFA909A54492167652A6B440E3', X'C16B206B711DFE4EBCEEEB1E685C4E7C', X'C16B206B711DFE4EBCEEEB1E685C4E7C', 4, NULL, NULL, 2, NULL, 0, 'Transfer pan to oven and cook just until meat reads 125°F on an instant read thermometer inserted into the center. Depending on thickness this will take from 2 to more than 5 minutes, so check early and often. The filet will continue to cook off the heat, so pulling it out of the pan at 125°F (or even a little before, if you like), will yield a beautiful, true medium-rare (130°) finished product');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF5FFBA24891947BC20BE35A52E85A5', X'C16B206B711DFE4EBCEEEB1E685C4E7C', X'C16B206B711DFE4EBCEEEB1E685C4E7C', 5, NULL, NULL, 2, NULL, 0, 'Transfer filets to a cutting board to rest while you make the pan sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49662111DB6C78468036B614D770AC18', X'9A878C8412FD41429C8B00FD38830411', X'9A878C8412FD41429C8B00FD38830411', 0, NULL, NULL, 2, NULL, 0, 'Set a heavy 5 1/2-quart pot (this is my favorite) over medium-high heat and add the bacon. Cook, stirring occasionally, until browned. If there is more than about two tablespoons of fat at the bottom of the pot, spoon out the rest and discard or save for another use. Add onion, carrot, and celery and continue cooking, stirring from time to time, until softened, about 5 minutes. Stir in garlic and tomato paste and cook two minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B67EA19B3D1C4F438273C07506853E9F', X'9A878C8412FD41429C8B00FD38830411', X'9A878C8412FD41429C8B00FD38830411', 1, NULL, NULL, 2, NULL, 0, 'Add ground beef, along with one teaspoon of the salt, stirring to break up the meat into small pieces. Cook for about 5 minutes, until beef isn''t pink. Stir in wine and simmer for a few minutes, until liquid is reduced by about half. Stir in tomato sauce, chopped tomatoes, cinnamon, nutmeg, and remaining teaspoon of salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F654B8273AA0B84EBDAAD7E45F543823', X'9A878C8412FD41429C8B00FD38830411', X'9A878C8412FD41429C8B00FD38830411', 2, NULL, NULL, 2, NULL, 0, 'Cover pot and reduce heat to maintain a gentle simmer while you cook the spaghetti. Stir in cream and leave pot uncovered for the last few minutes of cooking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'717EE94919822946BF9C9DE72FA91E0C', X'9A878C8412FD41429C8B00FD38830411', X'9A878C8412FD41429C8B00FD38830411', 3, NULL, NULL, 2, NULL, 0, 'While the sauce simmers, fill a large, heavy pot halfway with well salted water and bring to a boil. Cook spaghetti al dente according to package instructions. Drain, return pasta to pot, and toss with several ladles of sauce. (Psst...I also sometimes throw in a big pat of really good butter while tossing the spaghetti with the sauce.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B1BE38E0BD69344B0164F36E48660C1', X'9A878C8412FD41429C8B00FD38830411', X'9A878C8412FD41429C8B00FD38830411', 4, NULL, NULL, 2, NULL, 0, 'To serve, plate spaghetti in wide, shallow bowls and top with additional sauce. Pass grated cheese and a pepper mill at the table, along with some flaky sea salt if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB8127ED1216B04C8C2A66A75C4178EA', X'5818F9A427B5574CAF68F186E98FF283', X'5818F9A427B5574CAF68F186E98FF283', 0, NULL, NULL, 2, NULL, 0, 'Pat steak dry with paper towels. If pieces are too long to fit on grill, cut into manageable sections.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A15C6C6E708C354584E2A1BD82AAAF28', X'5818F9A427B5574CAF68F186E98FF283', X'5818F9A427B5574CAF68F186E98FF283', 1, NULL, NULL, 2, NULL, 0, 'In a gallon-size zip top bag, mix together the oil, orange and lime juices, garlic, salt, vinegar, and torn cilantro. Add meat and marinate at room temperature for two hours (or up to a day in the fridge), turning the bag over occasionally.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C2DB216A334794EB984EBFB9A741AB9', X'5818F9A427B5574CAF68F186E98FF283', X'5818F9A427B5574CAF68F186E98FF283', 2, NULL, NULL, 2, NULL, 0, '30 minutes before you''re ready to eat, preheat grill to high. Grill the steak until nicely browned on the underside, 3 to 5 minutes. Then flip once and grill about 3 minutes more, depending on thickness, for medium-rare. Let steak rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C3200157050E94681C2C059014E73BD', X'5818F9A427B5574CAF68F186E98FF283', X'5818F9A427B5574CAF68F186E98FF283', 3, NULL, NULL, 2, NULL, 0, 'While steak rests, warm refried beans and tortillas according to package instructions, and assemble toppings in bowls at the table. Slice steak thinly against the grain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54097C2ECC5E40428DEC3CCC904A6D4D', X'5818F9A427B5574CAF68F186E98FF283', X'5818F9A427B5574CAF68F186E98FF283', 4, NULL, NULL, 2, NULL, 0, 'Let everyone assemble their own tacos. Anything goes, but we recommend a nice schmear of refried beans on the bottom, a few slices of steak, and plenty of toppings.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6EC0A56BD60AB458917E72BB577CCD7', X'90171D51D787F14D8F4B545F11BD6068', X'90171D51D787F14D8F4B545F11BD6068', 0, NULL, NULL, 2, NULL, 0, 'In a large zip-top bag or a 9x12 glass baking dish, stir together the vegetable oil, lime juice, sherry vinegar, garlic, shallot, chili powder, soy sauce and salt. Place steak in bag or dish and coat well with marinade. Marinate for two hours at room temperature or 8 hours in the fridge, flipping once if possible to ensure even coverage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FA8C034A9A9BC45A357D7EA5402BC8B', X'90171D51D787F14D8F4B545F11BD6068', X'90171D51D787F14D8F4B545F11BD6068', 1, NULL, NULL, 2, NULL, 0, 'Heat a gas grill to high (the hotter the better) or prepare a charcoal grill for direct heat. Remove steak from marinade and shake off any excess liquid, then grill each side for just a few minutes, until outside is nicely seared in spots and internal temperature reaches 130°F (check with an instant-read thermometer) for medium-rare. Skirt, flatiron and flank steak are best at medium-rare.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84044175842C1B4883EC6AD8E9CD79E8', X'90171D51D787F14D8F4B545F11BD6068', X'90171D51D787F14D8F4B545F11BD6068', 2, NULL, NULL, 2, NULL, 0, 'Remove steak to a cutting board and let rest for 5 to 10 minutes. Place baguette slices on the grill in the coolest spot and toast very briefly, flipping once, to crisp the outside and get some grill marks. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E3B7DB6789D142B5988994D3F7D712', X'90171D51D787F14D8F4B545F11BD6068', X'90171D51D787F14D8F4B545F11BD6068', 3, NULL, NULL, 2, NULL, 0, 'Arrange some greens, tomatoes, cucumber, red onion and avocado in each of four bowls. Slice the steak thinly against the grain and top each bowl with a generous serving. Drizzle salads with olive oil and balsamic glaze. Sprinkle with flaky salt and pepper and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67FC5509A2318C4B9240B72FD8864913', X'A903D2C3007B9E49A91E3E9CFD88FA15', X'A903D2C3007B9E49A91E3E9CFD88FA15', 0, NULL, NULL, 2, NULL, 0, 'Dry the beef cubes well with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0F719DE87B5AF4C9FF1052EDC7433E0', X'A903D2C3007B9E49A91E3E9CFD88FA15', X'A903D2C3007B9E49A91E3E9CFD88FA15', 1, NULL, NULL, 2, NULL, 0, 'Set a large Dutch oven or other heavy, oven-proof lidded pot over medium-high heat. Add chopped bacon and cook, stirring from time to time, until browned. Remove bacon from pot, leaving fat in pot. In batches to avoid crowding, add beef cubes and brown well on all sides. Remove beef from pot and pour off (or spoon out) all but 2 to 3 tablespoons of fat. Preheat oven to 350°F with a rack in the lower third.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9471D6E4EBC660438A688D8F6499FD88', X'A903D2C3007B9E49A91E3E9CFD88FA15', X'A903D2C3007B9E49A91E3E9CFD88FA15', 2, NULL, NULL, 2, NULL, 0, 'Add onions and carrots to pot. Cook, stirring from time to time, until lightly browned, about 10 minutes. Add garlic and cook, stirring, one minute more. Stir in tomato paste. Add beef and bacon back to pot. Pour in wine and enough beef broth to cover meat by about half an inch. Stir in thyme, bay leaves and salt. Cover pot with lid and place in oven. Cook for about two hours, until meat is very tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19FDACF1B6AD874B99957E89F94F8354', X'A903D2C3007B9E49A91E3E9CFD88FA15', X'A903D2C3007B9E49A91E3E9CFD88FA15', 3, NULL, NULL, 2, NULL, 0, 'While stew cooks, set a frying pan over medium-high heat and pour in olive oil. When hot, add mushrooms and cook, stirring occasionally, until well browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7C660ED0BBF894F84A0B00385EB2CBF', X'A903D2C3007B9E49A91E3E9CFD88FA15', X'A903D2C3007B9E49A91E3E9CFD88FA15', 4, NULL, NULL, 2, NULL, 0, 'When meat is very tender, carefully place pot on stovetop over medium heat. Add mushrooms and pearl onions. In a small bowl, mash together the butter and flour with a fork to form a paste. Stir paste into stew. Simmer for 10 minutes. Remove from heat. Serve immediately or bring to room temperature before refrigerating.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E73F3EB5B6812E4AAC9457C501E0EF64', X'A903D2C3007B9E49A91E3E9CFD88FA15', X'A903D2C3007B9E49A91E3E9CFD88FA15', 5, NULL, NULL, 2, NULL, 0, 'Remove thyme stems and bay leaves and stir in parsley just before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'673EC0AE8FB2E64C8E9907918EAD5554', X'48C86BC7C3BA8A42BF3EF628D36BC681', X'48C86BC7C3BA8A42BF3EF628D36BC681', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F. Line a half sheet pan with foil. To make the bacon weave, start by laying four bacon slices next to each other, long sides touching, in the middle of the pan. Then, one by one starting at the top, lay each of the remaining pieces crosswise. Weave the first piece over, under, over, under the original slices. Weave the second piece under, over, under, over, etc. (If you learned a different weaving method in elementary school, go for it.) Bake bacon until done to your liking -- 15 minutes plus depending on the thickness of the cut. Drain on paper towels, reserving the grease in the pan. When cooled enough, cut weave into four squares.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2CA0D044B65F040AF5AB323552BBA78', X'48C86BC7C3BA8A42BF3EF628D36BC681', X'48C86BC7C3BA8A42BF3EF628D36BC681', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, form four burger patties from the ground beef. Use your thumb to create a nice big divot in the middle of each patty to ensure even cooking. Sprinkle generously with salt and pepper immediately before cooking. You can grill the burgers or cook in a cast-iron pan with a couple tablespoons of the bacon grease. To grill, prepare a hot side and a cooler side of the grill with a little oil on the grates. Start burgers over direct heat, flipping once, to form a nice crust on both sides. This should take about three minutes per side. Transfer burgers to indirect heat to cook through to your liking (125°F for medium-rare, 130°F for medium on an instant-read thermometer). For cast iron, preheat a 12-inch cast iron pan over high heat until very hot. Pour in two tablespoons of bacon grease, then set burgers into pan. Cook on high heat to form a crust on both sides, flipping once, then reduce heat to medium and continue cooking according to internal temperature instructions above. Let burgers rest for at least 5 minutes before continuing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E74AD7316AE2AE498B7E277DAFCB2DE5', X'48C86BC7C3BA8A42BF3EF628D36BC681', X'48C86BC7C3BA8A42BF3EF628D36BC681', 2, NULL, NULL, 2, NULL, 0, 'While burgers rest, add another two tablespoons of bacon grease into the hot pan, pile in the onions and sprinkle generously with salt and pepper. Cook, stirring frequently, until browned and wilted. Add marsala and cook until evaporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EB700BEAC245E4DB509FE70275CE0D2', X'48C86BC7C3BA8A42BF3EF628D36BC681', X'48C86BC7C3BA8A42BF3EF628D36BC681', 3, NULL, NULL, 2, NULL, 0, 'To assemble the burgers, toast the buns for a minute or two if you like, then layer on some spinach leaves, a burger patty, bacon, onions, and some crumbled blue cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BEA6798BC0B8B41960DCDFA75529A4E', X'E7A62351E1D5D240BD32E57BC87AC4D7', X'E7A62351E1D5D240BD32E57BC87AC4D7', 0, NULL, NULL, 2, NULL, 0, 'Pat chicken breasts dry with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E518DEB46539D45913BBB109E93DCE1', X'E7A62351E1D5D240BD32E57BC87AC4D7', X'E7A62351E1D5D240BD32E57BC87AC4D7', 1, NULL, NULL, 2, NULL, 0, 'To make the chicken in the Instant Pot: Pour chicken broth into Instant Pot and add chili powder, cumin, paprika, and salt. Arrange chicken breasts in a single layer in the Instant Pot. Close lid and set vent to sealing. Cook on manual, high pressure, for 8 minutes. Let the pressure release naturally for 5 minutes before carefully doing a manual release. See notes section for oven and stovetop cooking instructions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00AC82FE1DAB6E4885FBD05B2CDC4629', X'E7A62351E1D5D240BD32E57BC87AC4D7', X'E7A62351E1D5D240BD32E57BC87AC4D7', 2, NULL, NULL, 2, NULL, 0, 'Shred chicken with two forks while still warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ACDE1070116F74A958554C1C3FB1A7A', X'E7A62351E1D5D240BD32E57BC87AC4D7', X'E7A62351E1D5D240BD32E57BC87AC4D7', 3, NULL, NULL, 2, NULL, 0, 'To assemble the tacos, place some shredded chicken on a flour tortilla and top with avocado, salsa verde, pico de gallo, shredded cheese, and Cholula.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'453555905D770D40835C1B1F1C4D1A94', X'A6B6037F853F1C4D919F1603701F6887', X'A6B6037F853F1C4D919F1603701F6887', 0, NULL, NULL, 2, NULL, 0, 'In a 9x13-inch baking dish or other large, shallow dish, whisk together the marinade ingredients: olive oil, lemon juice, garlic, salt, dill, oregano, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7635C5943A1FF744ACEBAF92F7E8D09E', X'A6B6037F853F1C4D919F1603701F6887', X'A6B6037F853F1C4D919F1603701F6887', 1, NULL, NULL, 2, NULL, 0, 'Cut each chicken breast in half across the middle and pound with a mallet if necessary to make each piece about an inch thick and a little bigger than a hamburger bun. Or, if using chicken tenders, see prep note below.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5772C9248FE7BF4CAD95272006874837', X'A6B6037F853F1C4D919F1603701F6887', X'A6B6037F853F1C4D919F1603701F6887', 2, NULL, NULL, 2, NULL, 0, 'Place chicken pieces into the dish with the marinade and coat well., Leave to marinate at room temperature for 30 minutes or in the fridge for anywhere between 2 and 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CA9C27DDEBB5341BCB9C469087937CA', X'A6B6037F853F1C4D919F1603701F6887', X'A6B6037F853F1C4D919F1603701F6887', 3, NULL, NULL, 2, NULL, 0, 'Cut bell peppers in half from stem end to tip and remove stems, ribs and seeds. Cut a slit of about an inch at both the top and bottom of each pepper half to help it relax into a flatter shape as it grills.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A54FD49EF91F924BAA35BC9D4FFB56EF', X'A6B6037F853F1C4D919F1603701F6887', X'A6B6037F853F1C4D919F1603701F6887', 4, NULL, NULL, 2, NULL, 0, 'Cut halloumi into four slices, not super-thin or they might get too floppy on the grill.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4E5B974DCFE7B4ABFEAA9ED70F272D6', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45747170A900644B9DB79B92B9B0B057', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, stir together mayo, garlic, lemon juice, mustard, salt, some pepper and two tablespoons of the parmesan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54CFCE6583A22B4B8818645E40BBB959', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 2, NULL, NULL, 2, NULL, 0, 'Dry chicken well with paper towels. Remove tough tendons. (See note.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D980DA68C2C6BE46A3B7C224C55F5D2E', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 3, NULL, NULL, 2, NULL, 0, 'Add chicken and stir to coat well. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44BCB45EB052924A8B047FA9695F7C5C', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 4, NULL, NULL, 2, NULL, 0, 'On a rimmed baking sheet, mix together the panko, remaining two tablespoons parmesan and the butter with your hands. Spread out in a single layer and bake for about 4 minutes, depending on your oven, watching very carefully to prevent burning, until just about browned to your liking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0783EC7A2D4A654D8FAF2495BFBB21BE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 5, NULL, NULL, 2, NULL, 0, 'Pour browned panko onto a large plate or into a wide, shallow bowl. Place wire rack onto baking sheet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD51365AA511740A5FC9EEECEBB80FD', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 6, NULL, NULL, 2, NULL, 0, 'Use tongs to remove each chicken tender from the bowl and dredge in panko until well coated, then lay on wire rack with a little bit of space between tenders.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6252CB089AD2D841BF4B70C32B266B6F', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 7, NULL, NULL, 2, NULL, 0, 'Bake for about 15 minutes, until chicken is just cooked through. There''s no need to flip them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C65133C099F0604BB44EA0510C8A482A', X'15C8614DB4BECA4A8F7034EE7CBA69AE', X'15C8614DB4BECA4A8F7034EE7CBA69AE', 8, NULL, NULL, 2, NULL, 0, 'Serve right away with ketchup or dipping sauce of your choice (if you like — the chicken is nice and flavorful on its own).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9487EFAE3B79424491450D14E53BC82D', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 0, NULL, NULL, 2, NULL, 0, 'Pat chicken breasts dry with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AF1FD56F4E5F44E894C4DFF410D6BCB', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 1, NULL, NULL, 2, NULL, 0, 'Pour chicken broth into Instant Pot and add salt and additional seasonings, if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BEBE16A170D374DB14C5ECC2BA12C69', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 2, NULL, NULL, 2, NULL, 0, 'Arrange chicken breasts in a single layer in the Instant Pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A881B8C4C10A940B4019B82C7B074B5', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 3, NULL, NULL, 2, NULL, 0, 'Close lid and set vent to sealing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5FC83F2A06F3F4389D567A5EFC4CCB8', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 4, NULL, NULL, 2, NULL, 0, 'Cook on manual, high pressure, for 8 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D9950F33DFBFD4D9534892519410870', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 5, NULL, NULL, 2, NULL, 0, 'Let the pressure release naturally for 5 minutes before carefully doing a manual release.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1BCCBBECC983044B7290F265025AC95', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 6, NULL, NULL, 2, NULL, 0, 'Shred chicken with two forks, either in the pot or on a cutting board. Place chicken back into pot and stir with cooking liquid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'365EDD96113E8440B782B3BF77A4B71F', X'ACAAE282FCF5BF4285ABD4106620C942', X'ACAAE282FCF5BF4285ABD4106620C942', 7, NULL, NULL, 2, NULL, 0, 'Chicken will keep well, tightly sealed in the fridge, for a week. You can store it right in the cooking liquid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7E460D5D62E224495C6713D8DB1520E', X'8F632F427D47AA4E977E9470A8012216', X'8F632F427D47AA4E977E9470A8012216', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425°F (220°C) with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A941943F5DF7E54490285F78E7889E31', X'8F632F427D47AA4E977E9470A8012216', X'8F632F427D47AA4E977E9470A8012216', 1, NULL, NULL, 2, NULL, 0, 'Remove any giblets from the cavity of the chicken and pat it dry all over.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD3E9FF3F178F445818C6F8CD89B17B3', X'8F632F427D47AA4E977E9470A8012216', X'8F632F427D47AA4E977E9470A8012216', 2, NULL, NULL, 2, NULL, 0, 'Place the chicken into a roasting pan. For a 4 to 5 pound chicken, we like to use the good old 9x13x2-inch metal roasting pan we''ve had for years. Nothing fancy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'491100C5FB1F9044853906B7F0A663DD', X'8F632F427D47AA4E977E9470A8012216', X'8F632F427D47AA4E977E9470A8012216', 3, NULL, NULL, 2, NULL, 0, 'Rub the chicken all over with the olive oil, and sprinkle all over with the salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5216DB406144624397A068503D87E857', X'8F632F427D47AA4E977E9470A8012216', X'8F632F427D47AA4E977E9470A8012216', 4, NULL, NULL, 2, NULL, 0, 'Tuck the tips of the wings under the chicken.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3593A2EAFB2CB444A2E5BBD462F0A0DE', X'8F632F427D47AA4E977E9470A8012216', X'8F632F427D47AA4E977E9470A8012216', 5, NULL, NULL, 2, NULL, 0, 'Roast for about 60 minutes, until the juices run clear when you stick the tip of a knife between one of the legs and thighs, and an instant-read thermometer inserted into the breast reads 145°F (63°C) to 150°F (65°C). Start checking around the 45 minute mark, and don''t worry if it goes a little bit over.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'639079D2111F9C4E9A5B05E0603549DC', X'8F632F427D47AA4E977E9470A8012216', X'8F632F427D47AA4E977E9470A8012216', 6, NULL, NULL, 2, NULL, 0, 'Let the chicken rest on a cutting board for 20 minutes. Then carve and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D64B05B14EDDA4CAD9AC6CC1AA4FF47', X'4E41928DDFB3A54C923B6844D78E588A', X'4E41928DDFB3A54C923B6844D78E588A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center. Choose a roasting pan that will accommodate the chicken and potatoes. (A good old 13` x 9` metal roasting pan that''s on the deeper side and safe for the stovetop usually works well.) Place the chicken in the pan breast-side up and slide your fingers gently between the skin and the breast to make a pocket on each side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A7E1DD0488CD84B93BCA928A3250F01', X'4E41928DDFB3A54C923B6844D78E588A', X'4E41928DDFB3A54C923B6844D78E588A', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mash together four tablespoons of the butter, the chopped prosciutto, garlic, sage, lemon zest and a few generous grinds of black pepper. Use your hands to spread most of the butter mixture between the skin and breast of the chicken, then rub the rest all over the outside of the bird. Tuck the thyme sprigs and lemon halves into the cavity of the chicken. Roast for 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B96D4EDA5FB0D47A06B02CB042CC335', X'4E41928DDFB3A54C923B6844D78E588A', X'4E41928DDFB3A54C923B6844D78E588A', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large pot of very well salted water to the boil, boil the potatoes for 10 minutes, and drain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89B914ACF504D84D8506B873DAFD265D', X'4E41928DDFB3A54C923B6844D78E588A', X'4E41928DDFB3A54C923B6844D78E588A', 3, NULL, NULL, 2, NULL, 0, 'Use tongs to lift up the chicken for a moment and add the parboiled potatoes to the bottom of the roasting pan. Roast for 40+ minutes more, until an instant thermometer inserted into the thickest part of the thigh (but not touching the bone) reads 165°F. Let chicken rest on carving board for 10 minutes and spoon potatoes into a serving bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAA8E5129DF9F44B9018E3AB890807B5', X'4E41928DDFB3A54C923B6844D78E588A', X'4E41928DDFB3A54C923B6844D78E588A', 4, NULL, NULL, 2, NULL, 0, 'Make the gravy while the chicken rests. In a small bowl, mash together the remaining two tablespoons butter with the flour to make a paste. Set roasting pan over medium-high heat on the stove (one or two burners, depending on the size of your pan). Whisk flour paste into pan drippings and let brown for a minute. Then whisk in wine and simmer for a minute or two. Pour in stock, whisk to remove any lumps, and simmer for about 8 minutes until nicely thickened. Taste for salt and grind in some black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAA791AD72FDDB4786CB2D4FC91C4629', X'4E41928DDFB3A54C923B6844D78E588A', X'4E41928DDFB3A54C923B6844D78E588A', 5, NULL, NULL, 2, NULL, 0, 'Carve chicken and serve with potatoes, gravy to pass at the table, and maybe a nice simple kale salad.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FF8A19731DB7D40B121854D5AD4B3A3', X'146282BF3BFB6B49A2FEADBD5E709840', X'146282BF3BFB6B49A2FEADBD5E709840', 0, NULL, NULL, 2, NULL, 0, 'Pat the chicken breasts dry with paper towels. With a very sharp knife, make each breast into a little pocket by cutting a horizontal slit into one of the thin sides. The slit should be almost but not quite the length of the chicken breast and go almost but not quite all the way through to the opposite side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D6719420E478846B0D6E5ABE2AE8508', X'146282BF3BFB6B49A2FEADBD5E709840', X'146282BF3BFB6B49A2FEADBD5E709840', 1, NULL, NULL, 2, NULL, 0, 'Stuff 1/4 of the provolone cubes into each breast and press the chicken lightly to seal around it. Don’t worry if it’s not perfect. Oozing cheese isn’t a terrible thing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3013F9A9D81F4E4BA34C27DDBC14BD8E', X'146282BF3BFB6B49A2FEADBD5E709840', X'146282BF3BFB6B49A2FEADBD5E709840', 2, NULL, NULL, 2, NULL, 0, 'Wrap each stuffed chicken breast in a couple of slices of prosciutto, covering as much of the surface as you can.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C3E9527631C694A97B8E3A9D4248815', X'146282BF3BFB6B49A2FEADBD5E709840', X'146282BF3BFB6B49A2FEADBD5E709840', 3, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon of the olive oil over medium-high in a 12-inch nonstick skillet. Add wrapped chicken breasts and cook, uncovered, on one side until prosciutto is darkened in color and chicken breast is about halfway cooked through. Flip once and cook until chicken breast is jus cooked through. Remove chicken breasts to a plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56323B65B4BB9F42AAD2C9D3FAF3B485', X'146282BF3BFB6B49A2FEADBD5E709840', X'146282BF3BFB6B49A2FEADBD5E709840', 4, NULL, NULL, 2, NULL, 0, 'Heat remaining tablespoon of olive oil in the same skillet. Add shiitakes and cook, stirring occasionally, until lightly browned and tender. Add shallot and garlic and cook, stirring frequently, for a minute or two until softened.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46E8F0508166A440B26A005473A15EEA', X'146282BF3BFB6B49A2FEADBD5E709840', X'146282BF3BFB6B49A2FEADBD5E709840', 5, NULL, NULL, 2, NULL, 0, 'Pour Marsala into pan and stir to scrape up any browned bits on bottom of pan. Simmer until liquid has just evaporated. Add chicken stock and sage and simmer briskly for 5 minutes. Meanwhile, in a small bowl, stir together butter and flour with a fork to form a paste. Stir paste into pan sauce and continue to simmer for 5 minutes more. Add chicken back to pan and simmer until warm. Grind some pepper over everything and taste for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E56C8AC5C6ADEC4ABF79046B53F18A67', X'146282BF3BFB6B49A2FEADBD5E709840', X'146282BF3BFB6B49A2FEADBD5E709840', 6, NULL, NULL, 2, NULL, 0, 'To serve, place a piece of chicken on each of four plates and top with some mushrooms and sauce. Sautéed spinach makes a great, easy accompaniment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7545FDB7F968049A40E5A938BFC6087', X'C4A470742FBE09468895128643F94EC5', X'C4A470742FBE09468895128643F94EC5', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F with a rack in the center. Remove any giblets and pat chickens dry with paper towels. Set the chickens on a rack in a large roasting pan, breast side up, and cut away and discard the two pockets of fat tucked just inside the opening to each chicken’s cavity.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4405361B714BFC49B842EE8BB91BF903', X'C4A470742FBE09468895128643F94EC5', X'C4A470742FBE09468895128643F94EC5', 1, NULL, NULL, 2, NULL, 0, 'Combine the rosemary, thyme, oregano, garlic, salt, pepper and olive oil in a mortar, and smash it all into a paste with the pestle. (Alternatively, use the side of a chef’s knife to smash it all together.) Divide the paste between the two chickens and rub it all over the skin.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A76A301BADD574DBACE7DDF0351AA19', X'C4A470742FBE09468895128643F94EC5', X'C4A470742FBE09468895128643F94EC5', 2, NULL, NULL, 2, NULL, 0, 'Roast the chicken for 20 minutes with the breast side up. Then flip it and cook 20 minutes more, breast side down. Turn it over again and cook for an additional 20 minutes, breast side up. For a smaller chicken, this may be all the time you need. If it’s closer to 4 pounds, you’ll need an additional 20 or so minutes. Remove the chicken from the oven when a thermometer inserted into the thickest part of the thigh reads 165° F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E07EA5A406ED16488C53681044C188D1', X'C4A470742FBE09468895128643F94EC5', X'C4A470742FBE09468895128643F94EC5', 3, NULL, NULL, 2, NULL, 0, 'While the chicken roasts, whisk together the shallot, vinegar, extra-virgin olive oil and a generous amount of salt and pepper to make the dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AEC616FCBF07A479C5A45DC8CFE3E23', X'C4A470742FBE09468895128643F94EC5', X'C4A470742FBE09468895128643F94EC5', 4, NULL, NULL, 2, NULL, 0, 'Lay the baguette slices in a single layer on a sheet pan or two and brush each with a little bit of the dressing. When the chickens are cooked, let them rest on a cutting board, tented with foil, for 15 minutes. Turn off the oven and place the sheet pans inside so the bread toasts a bit from the residual heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'759D14874804DB4CBC59C2B8697FCC68', X'C4A470742FBE09468895128643F94EC5', X'C4A470742FBE09468895128643F94EC5', 5, NULL, NULL, 2, NULL, 0, 'While the chickens rest, set the roasting pan over medium-high heat on a burner (or two) on the stovetop. Whisk the flour into the pan drippings to form a paste. Then pour in the stock and whisk until there are no lumps. Simmer briskly for 5 to 10 minutes, lowering heat as necessary, until gravy is as thick as you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0339A80F6F1C94891B4C854F72B26CF', X'C4A470742FBE09468895128643F94EC5', X'C4A470742FBE09468895128643F94EC5', 6, NULL, NULL, 2, NULL, 0, 'To serve, place some simple greens on each plate, arrange a piece of chicken and a few garlic croutons on top, and drizzle as liberally as you like with both dressing and gravy.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61206665FEB9654FA1F15765A9272AF5', X'34BB87165D39D2419200AC920EF58191', X'34BB87165D39D2419200AC920EF58191', 0, NULL, NULL, 2, NULL, 0, 'Heat a 12-inch cast-iron pan or braiser over medium-high heat. Season the chicken pieces well with salt and pepper. Place skin-side down in the skillet and cook, without disturbing, until golden brown on the skin side, about 5 minutes. Remove chicken to a plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388AD949CEC8234B94665789BA7360CF', X'34BB87165D39D2419200AC920EF58191', X'34BB87165D39D2419200AC920EF58191', 1, NULL, NULL, 2, NULL, 0, 'Pour off all but two tablespoons of fat from the pan. Reduce heat to medium. Add the leeks, mushrooms, garlic, rosemary and sage and cook, stirring often, until all the veggies have softened and substantially reduced in volume, 5-10 minutes. Add flour and cook, stirring constantly, for a minute or two. Stir in the wine, chicken stock and cream and bring to a boil. Add browned chicken, skin-side up, and any juices that have accumulated on the plate. Adjust heat to keep liquid at a simmer, and cook until chicken is done all the way through, about 45 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53E06735AA37B04FAC6E7944B0A713FA', X'34BB87165D39D2419200AC920EF58191', X'34BB87165D39D2419200AC920EF58191', 2, NULL, NULL, 2, NULL, 0, 'Serve with rice, noodles or potatoes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'291F807E19A3124387B29489903892AE', X'39DF90238E0FFD47A46C60AE6D156D25', X'39DF90238E0FFD47A46C60AE6D156D25', 0, NULL, NULL, 2, NULL, 0, 'If you are starting with raw chicken breasts: Preheat oven to 400°F with a rack in the center. Place chicken breasts in a small roasting pan and drizzle with olive oil. Sprinkle with 1/2 teaspoon chili powder, a bit of the salt, and pepper. Turn with tongs to coat evenly. Bake until cooked through, 20 minutes give or take. Set aside to cool. (If you are starting with cooked chicken or turkey, skip this step and simply add the full amount of chili powder to the pot in step 2 below.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C52952F730B5A64D88CA4CCBAAAAC590', X'39DF90238E0FFD47A46C60AE6D156D25', X'39DF90238E0FFD47A46C60AE6D156D25', 1, NULL, NULL, 2, NULL, 0, 'While the chicken bakes, warm the remaining two tablespoons olive oil in a medium Dutch oven or other heavy pot over medium-high heat. Add the onion and pepper along with remaining salt and cook, stirring occasionally, until softened and a little bit browned, about 5 minutes. Add the garlic, reduce heat to medium, and cook, stirring frequently, until very fragrant, about a minute. Add flour, remaining 1 teaspoon chili powder and the cumin and stir to coat vegetables. Cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81768BD3158A8F41B106EE9CB69C51D4', X'39DF90238E0FFD47A46C60AE6D156D25', X'39DF90238E0FFD47A46C60AE6D156D25', 2, NULL, NULL, 2, NULL, 0, 'Pour in the broth and diced chilis and raise heat to high. Stir a few times to incorporate flour, then bring to a boil. Reduce heat to simmer for about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'872E022D31D9D84598CBFB6F2DD12DE0', X'39DF90238E0FFD47A46C60AE6D156D25', X'39DF90238E0FFD47A46C60AE6D156D25', 3, NULL, NULL, 2, NULL, 0, 'Using two forks, shred chicken. Add chicken, beans, and corn to pot along with any accumulated juices from the chicken roasting pan, and simmer for a few minutes more to warm through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6C0ACC379DDA941B670CC17645FB4D1', X'39DF90238E0FFD47A46C60AE6D156D25', X'39DF90238E0FFD47A46C60AE6D156D25', 4, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in cilantro, red onion and lime juice. To serve, ladle into bowls and pass cheese, sour cream and lime wedges at the table if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8696FC60E225DE46941914253682C0AE', X'239AFFDBD98A1049BDB3B88190A71768', X'239AFFDBD98A1049BDB3B88190A71768', 0, NULL, NULL, 2, NULL, 0, 'To make the tuna poke, cut tuna into bite-sized cubes. I like them on the smaller side — maybe 1/2-inch dice — but it''s totally up to you. Place cubes in a small mixing bowl and add scallion, shallot, tamari, sesame oil and chili flakes. Toss gently and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE0DF24516E4D545BD3051AF7DDBC029', X'239AFFDBD98A1049BDB3B88190A71768', X'239AFFDBD98A1049BDB3B88190A71768', 1, NULL, NULL, 2, NULL, 0, 'To make the salmon poke, cut salmon into cubes to match the size of the tuna and place in a small mixing bowl. In another small bowl, stir together the mayonnaise, sriracha and tamari. Spoon sauce over salmon and toss gently to coat. Stir in shallot and scallion and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96BD94E07F21114C8D42E042B15E306D', X'239AFFDBD98A1049BDB3B88190A71768', X'239AFFDBD98A1049BDB3B88190A71768', 2, NULL, NULL, 2, NULL, 0, 'To assemble the bowls, place some rice in the bottom of each serving bowl. Arrange portions of tuna, salmon, and whatever additional ingredients you like. Pictured here are avocado, seaweed salad that I bought already prepared, some simple shelled edamame, a little bit of salmon roe, and a nice sprinkle of gomasio, freshly ground black pepper, and chili toasted sesame oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AE5EC66A1897C41831C4AA022675028', X'2C5C9D0DCA87154A979633F591F6BAA6', X'2C5C9D0DCA87154A979633F591F6BAA6', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 275° F // 135° C with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'321EF35814EFA940BDD31AFF858B1B4A', X'2C5C9D0DCA87154A979633F591F6BAA6', X'2C5C9D0DCA87154A979633F591F6BAA6', 1, NULL, NULL, 2, NULL, 0, 'Place the salmon skin-side down on a rimmed baking sheet or baking dish. Remove and discard any pin bones.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D09E69D52C3654881286A5592BD0B5A', X'2C5C9D0DCA87154A979633F591F6BAA6', X'2C5C9D0DCA87154A979633F591F6BAA6', 2, NULL, NULL, 2, NULL, 0, 'Rub the olive oil all over the flesh.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E698FABC5B43342BC9DD45B4C983E1B', X'2C5C9D0DCA87154A979633F591F6BAA6', X'2C5C9D0DCA87154A979633F591F6BAA6', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the salmon evenly with the shallots, capers, lemon juice, and a generous amount of salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE903A17A0B62043831A96B8F7C4CAC3', X'2C5C9D0DCA87154A979633F591F6BAA6', X'2C5C9D0DCA87154A979633F591F6BAA6', 4, NULL, NULL, 2, NULL, 0, 'Roast for 15 minutes. Flesh will be just barely opaque in the center, and still quite soft, when it''s done.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41BEC0CCCBEAB640A6CF6263953ACE61', X'2C5C9D0DCA87154A979633F591F6BAA6', X'2C5C9D0DCA87154A979633F591F6BAA6', 5, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'478A19C26DB91F4F88B0D257571E52DA', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 0, NULL, NULL, 2, NULL, 0, 'Peel and devein the shrimp, leaving tails on. (See note below.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'406E1D71A6C7C04E8A86020C3BD36CFD', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 1, NULL, NULL, 2, NULL, 0, 'Set a large frying pan over medium-high heat and warm the olive oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FAF6E00B7257641960FEBF581499ACE', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 2, NULL, NULL, 2, NULL, 0, 'Add shallot, garlic, red pepper flakes, and salt. Cook, stirring frequently, for a minute or so until shallots turn slightly translucent.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'429B54C2BF828949809EB7BB23771891', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 3, NULL, NULL, 2, NULL, 0, 'Add shrimp to pan. The two pounds of shrimp in this recipe will not fit in a single layer in almost any frying pan, but here we''re not looking for browning, so it''s totally fine. Use tongs or a spatula to keep the shrimp moving so they cook evenly. Take them out individually and put on a plate just as they start to turn pink. You''ll finish cooking them later.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D8C9E693EADC54893EC41F76886FBED', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 4, NULL, NULL, 2, NULL, 0, 'After removing the shrimp, pour in the wine and lemon juice. Simmer briskly until liquid is reduced by about a third.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52804C2CE3088B41A61FAEAB1CDBDCA2', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 5, NULL, NULL, 2, NULL, 0, 'Turn heat to low and swirl in butter until it''s melted into a silky sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B59FFC62833D5441B2A6EB7128A8213E', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 6, NULL, NULL, 2, NULL, 0, 'Return shrimp to pan and cook, stirring to coat with sauce, just until cooked through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93D26E9FA395F14F92E98544FB7FB1E2', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 7, NULL, NULL, 2, NULL, 0, 'Off the heat, sprinkle with plenty of freshly ground black pepper and the parsley.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54AA5848C679F94994A184C73FB11A91', X'DE79D1B3817B6C4598E09E0B51E23B8E', X'DE79D1B3817B6C4598E09E0B51E23B8E', 8, NULL, NULL, 2, NULL, 0, 'There are lots of ways to serve shrimp scampi. You can place the whole pan on the table along with a couple of torn baguettes and let everyone have at it. You can toss with a pound of cooked spaghetti or linguine. Or you can spoon it atop basmati rice pilaf or orzo. All pretty, pretty, pretty good.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98A177EC2A878B42B17EBF3D510EEC1A', X'0F1D0834589FA447910CAB46DFDB7583', X'0F1D0834589FA447910CAB46DFDB7583', 0, NULL, NULL, 2, NULL, 0, 'Drain water or oil from tuna and place into a medium mixing bowl. Break up any large pieces of tuna with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'291EDF3D43EDD247B7FFC53A094C58D8', X'0F1D0834589FA447910CAB46DFDB7583', X'0F1D0834589FA447910CAB46DFDB7583', 1, NULL, NULL, 2, NULL, 0, 'Add celery, shallot, mustard, mayonnaise, salt, and pepper, along with any of the optional additions. Stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'261C1E8FADDC0B42885640C4F7C7B02B', X'0F1D0834589FA447910CAB46DFDB7583', X'0F1D0834589FA447910CAB46DFDB7583', 2, NULL, NULL, 2, NULL, 0, 'Serve with salad or on a sandwich or tuna melt. For salads and sandwiches, I like to add a little freshly squeezed lemon juice right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21122D8FD4CDAE429AAAABAE22F4739E', X'A6D2F14B2534F646BC89DCC10FD948D1', X'A6D2F14B2534F646BC89DCC10FD948D1', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Into a large, heavy pan with a lid (such as this one or this one), place the shallots, bell pepper, tomatoes, garlic, capers, wine, stock, thyme and 1/2 teaspoon of the salt. Bake, covered, for 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9643DE8037B539489847F824EC76D345', X'A6D2F14B2534F646BC89DCC10FD948D1', X'A6D2F14B2534F646BC89DCC10FD948D1', 1, NULL, NULL, 2, NULL, 0, 'Pat sea bass fillets dry with paper towels. Carefully remove pan from oven and arrange filets in pan on top of sauce, nestling them in a bit. Sprinkle fish with remaining 1/2 teaspoon salt and dot with butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388A638E047D024BACA1BAF154041001', X'A6D2F14B2534F646BC89DCC10FD948D1', X'A6D2F14B2534F646BC89DCC10FD948D1', 2, NULL, NULL, 2, NULL, 0, 'Return pan to oven, uncovered. Bake until fish is just cooked through, about 15 to 20 minutes depending on thickness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AADD74BB3BA344488E89BB7BD818638B', X'A6D2F14B2534F646BC89DCC10FD948D1', X'A6D2F14B2534F646BC89DCC10FD948D1', 3, NULL, NULL, 2, NULL, 0, 'To serve, place about a cup of orzo into each of four wide, shallow bowls and top each with a piece of fish. Spoon some veggies and plenty of sauce overtop, and sprinkle with basil and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F06FE17856903D44890DD82628311E38', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', 0, NULL, NULL, 2, NULL, 0, 'If your scallops still have the little piece of muscle attached to the side, remove and discard it. (See video for technique.) Pat scallops dry with paper towels and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56DE29DD8481354DA5D960117D98083C', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', 1, NULL, NULL, 2, NULL, 0, 'Dice the chorizo into small cubes (about 1/4-inch). Heat a 12-inch nonstick pan over high heat. Add chorizo and cook, stirring occasionally, until browned on several sides — just a few minutes. Remove chorizo to a bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9024315E29119D4297448296D66CBD51', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', 2, NULL, NULL, 2, NULL, 0, 'Add oil to pan and keep over high heat. When very hot, add scallops without crowding. Cook 60 seconds without moving, then add butter and cook 30 seconds more. Use tongs to flip each scallop and cook just until done to your liking. I like to pull them off the heat before they''re opaque in the center, which, depending on size, may mean just 30 seconds or so on the second side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DBF083A852BFA4C8DF7AD4D3411FD07', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', X'08ED60B6BB5FB5419A5478ADBFDE4D3D', 3, NULL, NULL, 2, NULL, 0, 'To serve, place a few scallops and some of the chorizo on each plate and sprinkle with chives, if using. These are great over basmati rice pilaf.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1594C6C6F40E7047B751C12933DED6BE', X'281C41E1ACCD9B478EFA9E9A0968503B', X'281C41E1ACCD9B478EFA9E9A0968503B', 0, NULL, NULL, 2, NULL, 0, 'Bring a medium pot of salted water to the boil. Add the freekeh and cook, uncovered, 22 to 25 minutes, until tender. Drain and return to pot. Set aside in a warm place.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0BC36D2CB790E43A3F23AB35FB221C3', X'281C41E1ACCD9B478EFA9E9A0968503B', X'281C41E1ACCD9B478EFA9E9A0968503B', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a medium nonstick pan, warm two teaspoons of the olive oil over medium-high heat. Add garlic and cook, stirring constantly, until very fragrant, about 30 seconds. Add kale, in batches if necessary, along with some salt and pepper. Cook for a minute or two, until beginning to wilt. Add dates along with 1/2 cup of water. Continue cooking, stirring from time to time, until kale is tender and water has evaporated. Add kale mixture to the freekeh and stir to combine. Squeeze in juice of half the lemon.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF33A838B72A39439ACE5172C981804E', X'281C41E1ACCD9B478EFA9E9A0968503B', X'281C41E1ACCD9B478EFA9E9A0968503B', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the labneh and juice of remaining half lemon. Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50F1414F44E57E44907009C69C458A0A', X'281C41E1ACCD9B478EFA9E9A0968503B', X'281C41E1ACCD9B478EFA9E9A0968503B', 3, NULL, NULL, 2, NULL, 0, 'Wipe out the pan used for the kale and warm remaining two teaspoons olive oil over medium-high heat. Season salmon fillets generously with salt and pepper, then place skin-side down in hot skillet. Cook undisturbed for 5 minutes or so, until skin is browned and crisp. Gently flip fillets and cook a minute or two more, until salmon is done to your liking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55BF6221D587FD40B7367F072503BD97', X'281C41E1ACCD9B478EFA9E9A0968503B', X'281C41E1ACCD9B478EFA9E9A0968503B', 4, NULL, NULL, 2, NULL, 0, 'Divide freekeh mixture between two plates. Top each with a salmon fillet and some lemon labneh. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9600D8CAFA1ED9459425F47F2016B263', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 0, NULL, NULL, 2, NULL, 0, 'Melt 1 tablespoon of the butter over medium-high heat in a 12-inch nonstick frying pan or seasoned cast iron skillet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9615FDB94FA99947906CE0B38069756D', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 1, NULL, NULL, 2, NULL, 0, 'Add the shrimp and cook, turning once, until just opaque. Remove to a plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'191C3628C34CDE4CB190E71CFF7FE910', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 2, NULL, NULL, 2, NULL, 0, 'Melt the remaining tablespoon butter in the same pan over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CB393FCBF8AEA49869E65789E071A6B', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 3, NULL, NULL, 2, NULL, 0, 'Add onion and shiitakes and cook, stirring occasionally, until the onions are softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8648B5D6C432446A1064975C60F5BD2', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 4, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA0666A8719604490F0F903A2930089', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 5, NULL, NULL, 2, NULL, 0, 'Add the kale by the handful and cook, stirring occasionally, until wilted and almost tender, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A229ED397AE65E43A07A131AC3D53634', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 6, NULL, NULL, 2, NULL, 0, 'Add the soy sauce, wine, and cream, and cook until sauce has thickened slightly, 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F6FE7A1BB80DB4899067CD684194070', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 7, NULL, NULL, 2, NULL, 0, 'Return shrimp to pan along with any accumulated juices and continue cooking for a minute or so to rewarm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB564C640210C34BAA21C436A8FB8ACF', X'70BB7E24EE8A9C4CB370B5E124DE825C', X'70BB7E24EE8A9C4CB370B5E124DE825C', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68177CEC9A685242AD80A093263D9A4D', X'AE553678EB9B4747AEF058C12003A75F', X'AE553678EB9B4747AEF058C12003A75F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Cut potatoes into bite-sized pieces, about 1-inch by 1/2-inch, but don''t stress. On a rimmed half-sheet pan (18-inch x 13-inch), toss with olive oil, 1 teaspoon salt, and a few good grinds black pepper. Roast for 30 minutes or until crispy on the outside and tender on the inside. I don''t bother to toss them during cooking, which results in the undersides being nicely browned and no sticking. You can toss them halfway through if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BAED9BBF661744BAD7293DB41069296', X'AE553678EB9B4747AEF058C12003A75F', X'AE553678EB9B4747AEF058C12003A75F', 1, NULL, NULL, 2, NULL, 0, 'Push potatoes to the perimeter of the sheet pan. Lay salmon pieces in center and asparagus in bunches over potatoes as pictured above. Rub a little olive oil onto each pice of salmon and sprinkle with salt and pepper. Sprinkle chopped herbs over everything. Roast for about 10 minutes, until salmon is just cooked through and asparagus are crisp-tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B90C24D2D5A7A4283C7527D09771113', X'AE553678EB9B4747AEF058C12003A75F', X'AE553678EB9B4747AEF058C12003A75F', 2, NULL, NULL, 2, NULL, 0, 'Before serving, garnish with lemon wedges and drizzle with additional olive oil and some flaky sea salt if you like. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03D7C81ABB1BF47B0D2E91F107BE8BE', X'D63B43CB03BDFE43AA1D2EF754530CF1', X'D63B43CB03BDFE43AA1D2EF754530CF1', 0, NULL, NULL, 2, NULL, 0, 'Mix together the ketchup, horseradish, tabasco, gin, celery salt, salt and lemon juice. Chill until ready to serve. Stir in parsley right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A09438189DAFB418DC0D747B57D8B17', X'D63B43CB03BDFE43AA1D2EF754530CF1', X'D63B43CB03BDFE43AA1D2EF754530CF1', 1, NULL, NULL, 2, NULL, 0, 'Peel and devein shrimp, leaving tails attached. Place peels into a large pot. Add water, 1 tablespoon of the salt, wine or vermouth, peppercorns, coriander seeds, bay leaves, onion, tarragon and lemon. Bring to a boil, then reduce heat and simmer uncovered for 20 minutes. Remove from heat and let sit 30 minutes more, then strain broth through a fine sieve into a fresh pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE883AD09F1A69468D325667B8747B19', X'D63B43CB03BDFE43AA1D2EF754530CF1', X'D63B43CB03BDFE43AA1D2EF754530CF1', 2, NULL, NULL, 2, NULL, 0, 'Bring stock to a boil, then remove from heat. Add shrimp and cover. Leave for 3 minutes (still off the heat -- the shrimp `poach` in the residual heat and remain amazingly tender).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB6A3F7718BE44C9591674A74E1D7E6', X'D63B43CB03BDFE43AA1D2EF754530CF1', X'D63B43CB03BDFE43AA1D2EF754530CF1', 3, NULL, NULL, 2, NULL, 0, 'Fill a large bowl with ice water and the remaining tablespoon salt. Add shrimp and leave until just opaque, 3 minutes or a shade more if the shrimp are very big. Chill in fridge (I usually wrap in paper towels and put in a gallon zip top bag) until ready to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5000B6E0ABBE3A4F89C6ED72D7BB4495', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', 0, NULL, NULL, 2, NULL, 0, 'Scrub the oysters well under running water to remove all sand and dirt. To shuck each oyster, hold it in a kitchen towel with the cupped side of the shell facing down so you won''t lose the beautiful liquor when you open it. Insert the tip of an oyster knife firmly into the hinge and twist until it pops open. Then run the knife across the inside of the top shell. Watch the video linked in the notes section below to see this done.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BAD6911B63E7E458FD1D6D68064329C', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', 1, NULL, NULL, 2, NULL, 0, 'Run the oyster knife between the meat and the bottom shell to fully release the meat from the shell so it can be slurped up later. Place shucked oysters on a rimmed baking pan lined with a big sheet of gently crumpled aluminum foil to prevent tipping. Discard top shells.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C13129700A3C6C40AA6247067A167C06', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', 2, NULL, NULL, 2, NULL, 0, 'In a small pot, melt the butter with the shallot over medium heat and simmer for 5 minutes. Remove from heat and stir in the basil, parsley and lemon zest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF6F484A7104034396FC17C08C57AE01', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', X'4484C774ADD0F04E9F3CA23FC1DF3D3B', 3, NULL, NULL, 2, NULL, 0, 'Line the bottom of a heavy 12-inch frying pan with about 1/4 inch of Kosher salt. (I really prefer a cast iron skillet for its superior heat retention). Preheat the pan over medium-high heat for at least 5 minutes, until the salt starts to make some little popping noises. Then quickly arrange the shucked oysters in the pan and top each with a spoonful of the herb butter. Cover the pan with a lid (carefully use foil if you don''t have a lid that fits) and cook for three minutes, until the oysters are just opaque. They''ll continue to cook a bit from the residual heat in the pan. Sprinkle with a little bit of chopped prosciutto and a squeeze of lemon juice. Serve warm straight from the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB2A889828E17D4B97D56D608D9BEA55', X'A5715F03A5FE984FBBC29EF848B2B855', X'A5715F03A5FE984FBBC29EF848B2B855', 0, NULL, NULL, 2, NULL, 0, 'In a 12-inch nonstick pan or shallow Dutch oven, melt 1 tablespoon butter over medium-high heat. Add garlic and cook, stirring, for 30 seconds to a minute, until very fragrant but not browned. Add shrimp to pan in a single layer. Cook for about a minute on one side, until opaque, then flip and heat the other side until just cooked through. Remove from pan, sprinkle with about 1/3 of the lemon juice, and reserve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA976FC5D0600D4C821DFD947E7F2519', X'A5715F03A5FE984FBBC29EF848B2B855', X'A5715F03A5FE984FBBC29EF848B2B855', 1, NULL, NULL, 2, NULL, 0, 'Heat the remaining 2 tablespoons butter and the olive oil over medium-high in the same pan. Add onion and 1/2 teaspoon of the salt and cook, stirring occasionally, until softened, about 5 minutes. Add rice, stir well to coat with butter and oil, and cook for a minute. Pour in wine and stir to incorporate. Simmer, stirring frequently, until almost evaporated. Then add broth by the cupful, continuing to stir frequently and adding more as it absorbs. Add the remaining 1/2 teaspoon salt during this process. Stir in broccoli as you add the last cup of broth. Stop cooking when there’s still a bit of thick broth remaining and the rice is tender but with the tiniest bite in the center of the grain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8B9A3B562E2D14CB4020EAB83F46D78', X'A5715F03A5FE984FBBC29EF848B2B855', X'A5715F03A5FE984FBBC29EF848B2B855', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat, stir in remaining lemon juice and grated cheese and taste for seasoning. Arrange shrimp overtop, sprinkle with parsley and a little freshly ground black pepper, and serve with the rest of that bottle of wine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF496CAC086AF0419E00D1B9F9135F28', X'BD80BB41F0D13A4BB6ADEA1BD643604A', X'BD80BB41F0D13A4BB6ADEA1BD643604A', 0, NULL, NULL, 2, NULL, 0, 'Cut or tear the lobster meat into bite-sized pieces and place in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43BCD56C5BA632428A5380779F54AC20', X'BD80BB41F0D13A4BB6ADEA1BD643604A', X'BD80BB41F0D13A4BB6ADEA1BD643604A', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the mayonnaise, lemon juice, capers, chives and salt and pepper. Pour the dressing over the lobster and toss gently to coat all the meat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02AD4A0AFDF4D444AFD0799ED69D7EDC', X'BD80BB41F0D13A4BB6ADEA1BD643604A', X'BD80BB41F0D13A4BB6ADEA1BD643604A', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter on a skillet over medium-high heat. Set the buns down on the skillet on one of their side edges. Cook for a minute or two until golden brown, then flip and cook the opposite side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91E3F1FF4CA715418E09EE76DD6F5DCE', X'BD80BB41F0D13A4BB6ADEA1BD643604A', X'BD80BB41F0D13A4BB6ADEA1BD643604A', 3, NULL, NULL, 2, NULL, 0, 'Divide the lobster meat among the buns and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'025E772EE914F648A0B9C2983B7372F5', X'9ABA7C81F76A604CA2954CC57E468B85', X'9ABA7C81F76A604CA2954CC57E468B85', 0, NULL, NULL, 2, NULL, 0, 'In a heavy, wide skillet, heat the olive oil over medium-high heat until shimmering. Sprinkle a little bit of salt around the bottom of the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'962F0D7E76380B4A8C7335B072798513', X'9ABA7C81F76A604CA2954CC57E468B85', X'9ABA7C81F76A604CA2954CC57E468B85', 1, NULL, NULL, 2, NULL, 0, 'Place the crabs into the pan in a single layer, shell side down. Cook undisturbed for about five minutes, until the underside is crispy and red with a few browned spots');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6EA756B9C1855408A1FC0872A01C271', X'9ABA7C81F76A604CA2954CC57E468B85', X'9ABA7C81F76A604CA2954CC57E468B85', 2, NULL, NULL, 2, NULL, 0, 'Flip crabs and cook on the other side until cooked through, 3 to 4 minutes more. Serve immediately or pack for lunch.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEF57BD809386849B730E12E2CB791AF', X'76FD4FDF181E774D8BE0058E74A87AFE', X'76FD4FDF181E774D8BE0058E74A87AFE', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour onto a plate and dredge each crab lightly in the flour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'614BE5A964DC33458CE4E0B48C891BD7', X'76FD4FDF181E774D8BE0058E74A87AFE', X'76FD4FDF181E774D8BE0058E74A87AFE', 1, NULL, NULL, 2, NULL, 0, 'Heat butter and oil in a heavy 12-inch skillet over medium heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D71DC2E7FBF2449BD0E745EEFA8269', X'76FD4FDF181E774D8BE0058E74A87AFE', X'76FD4FDF181E774D8BE0058E74A87AFE', 2, NULL, NULL, 2, NULL, 0, 'Add three of the crabs shell-side down in a single layer. Cook for four minutes on the first side, then flip and cook for three minutes on the other side. Repeat with the remaining crabs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'026623541393834690070E4B6539164A', X'76FD4FDF181E774D8BE0058E74A87AFE', X'76FD4FDF181E774D8BE0058E74A87AFE', 3, NULL, NULL, 2, NULL, 0, 'While the crabs are cooking, combine the mayo, soy sauce, sriracha and shallot in a small bowl and mix well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FE6137266490245B66632999C3A39CA', X'76FD4FDF181E774D8BE0058E74A87AFE', X'76FD4FDF181E774D8BE0058E74A87AFE', 4, NULL, NULL, 2, NULL, 0, 'Place a bed of greens and some cucumbers, cherry tomatoes, and avocado slices in each of four wide bowls. Lay two crabs over each, and drizzle with spicy sauce. Serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8F8915E5D90DF4A8CC2A3397E5F4574', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 0, NULL, NULL, 2, NULL, 0, 'Line a rimmed sheet pan with parchment paper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B75E40F1C17CB248A8C1CEABE8EE61D7', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 1, NULL, NULL, 2, NULL, 0, 'Place the salmon, skin side down, on the parchment. Brush evenly with the lemon juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53A15AA427F20F4ABB5DC1E86C855D79', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 2, NULL, NULL, 2, NULL, 0, 'Combine salt, sugar, and cracked pepper in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7796AF6AEE6AA647BCB766E578488EBF', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the mixture over the salmon, covering completely. The layer of curing mixture should be a little thicker where the salmon is thicker and thinner where the fillet thins out toward the tail.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AC019244BFF443B1600BD4228C2417', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 4, NULL, NULL, 2, NULL, 0, 'Cover evenly with the dill.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EEC6F6C2D2F5F4089B7FF5D85DE05B7', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 5, NULL, NULL, 2, NULL, 0, 'Cover the salmon with another layer of parchment and then another sheet pan. Weight the top sheet pan with two 16-ounce cans.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9793E0702DD2DB43AA88AD513E5F0FB1', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 40-48 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'846293BE0BA8D64B8AF939956FEE9C37', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 7, NULL, NULL, 2, NULL, 0, 'Scrape the dill and curing mixture from the salmon. Rinse well and pat dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F592294F22EAFC4B8B8E2E9A1E6CFFB7', X'CB81319C6BFE864395077FCC1E2D71A8', X'CB81319C6BFE864395077FCC1E2D71A8', 8, NULL, NULL, 2, NULL, 0, 'Slice very thinly to serve. The salmon can be refrigerated for up to 5 days before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CF10FB5556D6247A4E9AF48175C553F', X'E55A83FFAB53F54DA05062F93630DCEC', X'E55A83FFAB53F54DA05062F93630DCEC', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a very small pot. Bring mixture to a boil over medium heat. Boil for 30 seconds, then remove from heat and let cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA8790A86F5E894AA9177A56A5AE2840', X'E55A83FFAB53F54DA05062F93630DCEC', X'E55A83FFAB53F54DA05062F93630DCEC', 1, NULL, NULL, 2, NULL, 0, 'Pour cooled mixture through a fine-meshed sieve into a small baking dish or other freezer-safe container just big enough to hold the mixture in a shallow layer. (Protip: save the strained shallot and toss into your next salad.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF68245A0363847BEE4B06D42E81A36', X'E55A83FFAB53F54DA05062F93630DCEC', X'E55A83FFAB53F54DA05062F93630DCEC', 2, NULL, NULL, 2, NULL, 0, 'Freeze the mixture, scraping every hour or so with a fork to break up the ice into small pieces, until fully frozen (probably about 3 hours).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF0015873E4F04BA432D6BD870C7CB4', X'E55A83FFAB53F54DA05062F93630DCEC', X'E55A83FFAB53F54DA05062F93630DCEC', 3, NULL, NULL, 2, NULL, 0, 'Serve in a small, very cold bowl as a condiment for oysters on the half shell.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C631E5382232324F862B16496AAA4D1E', X'2B6A58B64858B044BE56D2137878623C', X'2B6A58B64858B044BE56D2137878623C', 0, NULL, NULL, 2, NULL, 0, 'To cook the rice, combine it in a medium pot with 2 cups water, 1 tablespoon of the soy sauce, and the butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C15D4F61FD9DE94AA57A50AA8E5B1D86', X'2B6A58B64858B044BE56D2137878623C', X'2B6A58B64858B044BE56D2137878623C', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then cover, lower the heat so that you see just a bit of steam escaping, and cook for 40 minutes (or according to the time on the package) without lifting the lid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE406E4075F404E8738DD620F6A0F48', X'2B6A58B64858B044BE56D2137878623C', X'2B6A58B64858B044BE56D2137878623C', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, to cook the lentils, place them in a smallish pot with the bay leaf, Herbes de Provence, and the salt. Cover with about two inches of water, bring to a boil and then simmer for 20 minutes, or until tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28498457F8FDEE41BFDBF62BC095A480', X'2B6A58B64858B044BE56D2137878623C', X'2B6A58B64858B044BE56D2137878623C', 3, NULL, NULL, 2, NULL, 0, 'Drain the lentils and remove the bay leaf.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B82EC0555BB51546AC38398C3EB4B830', X'2B6A58B64858B044BE56D2137878623C', X'2B6A58B64858B044BE56D2137878623C', 4, NULL, NULL, 2, NULL, 0, 'While the rice and lentils cook, whisk together the olive oil, the vinegar, the remaining tablespoon of soy sauce, and a few generous twists of black pepper in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'826DB584AFFB5B42939F6A3C3BB24772', X'2B6A58B64858B044BE56D2137878623C', X'2B6A58B64858B044BE56D2137878623C', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked rice and lentils, shallot, tomatoes, bell pepper, and parsley to the bowl and stir gently to let the dressing infuse it all with savory goodness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1856440074050441AD9FDFDD83BE80B2', X'2B6A58B64858B044BE56D2137878623C', X'2B6A58B64858B044BE56D2137878623C', 6, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2FCD5B01A036E459FA1D1E85B61EBB3', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 0, NULL, NULL, 2, NULL, 0, 'Place rice into a large bowl and cover with several inches of water. The water will become very cloudy right way. Drain this first batch of water immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2D67B3E48EC4A439F7BCF7AB1B91B60', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 1, NULL, NULL, 2, NULL, 0, 'Cover the rice with water again. Use your hand to swirl the rice around for a few seconds, and then drain it again. Repeat this process until the water remains clear when you jostle the rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4CB371B1E221F44BDFBBB4D04047C3B', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 2, NULL, NULL, 2, NULL, 0, 'Drain the water once more and cover the rice by several inches with clean water. Let the rice soak for 30 minutes, then drain well in a fine-mesh sieve. Now the rice is ready to cook.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B11495FB397FA47AE9F30676CB78DC0', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 3, NULL, NULL, 2, NULL, 0, 'Place the rice, kombu if using, and water into the Instant Pot or a medium-sized nonreactive pot with a heavy bottom and a tight-fitting lid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C483E7C0F4B26C438CE2247A45BE11AE', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 4, NULL, NULL, 2, NULL, 0, 'To cook in the Instant Pot: Position the lid, set the vent to sealing position, and cook on manual, high pressure for 5 minutes. Let the pressure release naturally for 10 minutes before carefully releasing any remaining pressure and opening the lid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EDD81CF37E39F48BC0399AEACBA410A', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 5, NULL, NULL, 2, NULL, 0, 'To cook on the stovetop: Position the lid, then turn heat to high and bring to a boil. When the water boils, lower the heat to low or medium-low (depending on your burner) to maintain a gentle simmer. Simmer until the water is absorbed, about 12 minutes. Then remove pot from the heat, leaving the lid closed, and let the rice steam for an additional 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0E38A21F0FA4947A50A4687231CFE6A', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 6, NULL, NULL, 2, NULL, 0, 'While the rice cooks, stir together the vinegar, sugar, and salt in a small pot or microwave-safe bowl. Heat just until salt and sugar have dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E75EC3BF9C9414480B027ECE3424A22', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 7, NULL, NULL, 2, NULL, 0, 'Remove the kombu. Gently spoon cooked rice into a wide, shallow bowl. Pour vinegar mixture over rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ED378C5B24B8243A9700513AA0F9FDE', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 8, NULL, NULL, 2, NULL, 0, 'Using a rice paddle or silicone spatula, gently cut and fold the rice to mix in the vinegar and cool the rice. Refer to the notes section below for details and to the video for visual cues.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51039221D054914384442D7DE62B3599', X'A614F327FD876F448D537AF80D64A1E3', X'A614F327FD876F448D537AF80D64A1E3', 9, NULL, NULL, 2, NULL, 0, 'Let rice cool to body temperature (or all the way to room temperature, if you prefer) before using for sushi.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'992116E33E2B7C4597E761C8B719596B', X'54C12F7066FA0841A88CAC548BDB3020', X'54C12F7066FA0841A88CAC548BDB3020', 0, NULL, NULL, 2, NULL, 0, 'Rinse the quinoa very well in a fine mesh sieve under running water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B4E3B188C9227418CD2548F6399EBEA', X'54C12F7066FA0841A88CAC548BDB3020', X'54C12F7066FA0841A88CAC548BDB3020', 1, NULL, NULL, 2, NULL, 0, 'Place quinoa in a small pot along with water or stock and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48381CD0493F9F4AAF800CC9E7C77320', X'54C12F7066FA0841A88CAC548BDB3020', X'54C12F7066FA0841A88CAC548BDB3020', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then cover and simmer for about 15 minutes, until liquid is almost completely absorbed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A5C030A45A9B54596A82F6B8CF227A1', X'54C12F7066FA0841A88CAC548BDB3020', X'54C12F7066FA0841A88CAC548BDB3020', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and leave covered for 10 minutes more. Stir or fluff with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E77241C624FEFD489C1D2F3FADA13383', X'54C12F7066FA0841A88CAC548BDB3020', X'54C12F7066FA0841A88CAC548BDB3020', 4, NULL, NULL, 2, NULL, 0, 'Quinoa is ready to eat as-is or use in a recipe.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D25B0344414A24B9744E83F56A2A127', X'68C7808ADFBAC64ABA310CA0DE5D981A', X'68C7808ADFBAC64ABA310CA0DE5D981A', 0, NULL, NULL, 2, NULL, 0, 'In a 12-inch nonstick pan or shallow Dutch oven, melt the butter and olive oil over medium-high heat. Add the onion and cook, stirring frequently, until beginning to soften, about five minutes. Stir in the rice and cook, still stirring frequently, for one minute. Add the ham and cook one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE037FEB54CAFA41AFD70D0A8436B6B1', X'68C7808ADFBAC64ABA310CA0DE5D981A', X'68C7808ADFBAC64ABA310CA0DE5D981A', 1, NULL, NULL, 2, NULL, 0, 'Add the wine and cook, stirring almost constantly, until nearly absorbed. Lower heat to medium if necessary to prevent any hint of burning. Then begin adding the broth by the cupful (or so), stirring frequently throughout. Continue adding broth as the rice absorbs it. Add a big sprinkle of salt if your broth isn''t salty. Stir in the peas with the last addition of broth. Risotto is finished cooking when each grain of rice is tender but still has a nice hint of chewy bite, and there''s a little bit of delicious starchy broth remaining. (Keep in mind that the broth will thicken as you add the cheeses and let the risotto rest for a few minutes after cooking.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE90FE68C42538458FE7B622387C0DF8', X'68C7808ADFBAC64ABA310CA0DE5D981A', X'68C7808ADFBAC64ABA310CA0DE5D981A', 2, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in the cheeses and tarragon and taste for salt and pepper. Let the risotto rest for about five minutes before spooning into wide, shallow bowls to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4C9D78B2CEA2F4BA360A0B94B2D4394', X'3A0428CEA2C0404DB708F26BD1A046E3', X'3A0428CEA2C0404DB708F26BD1A046E3', 0, NULL, NULL, 2, NULL, 0, 'In a wide pan with a lid (like this one, this one, or this one), melt butter over medium-high heat. Add onion and cook, stirring occasionally, until translucent and just starting to brown, about five minutes. While onion cooks, rinse rice in a fine-mesh sieve until the water runs clear and shake out any excess water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15E49A96EB70C3408F7164654BB0ED32', X'3A0428CEA2C0404DB708F26BD1A046E3', X'3A0428CEA2C0404DB708F26BD1A046E3', 1, NULL, NULL, 2, NULL, 0, 'Add garlic to pan and cook, stirring, until very fragrant, about a minute. Add rice and stir to coat with butter, then cook, stirring once or twice, for two minutes. Pour in stock and give it all a good stir. Bring to a boil over high heat. Then cover pan, lower heat to maintain a gentle simmer, and cook until broth is absorbed, 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09393CD2D588FB41A5DCC4449337FDBD', X'3A0428CEA2C0404DB708F26BD1A046E3', X'3A0428CEA2C0404DB708F26BD1A046E3', 2, NULL, NULL, 2, NULL, 0, 'Off the heat, place a clean kitchen towel between the pan and the lid to absorb excess steam and let pilaf rest for 5 to 10 minutes. Sprinkle with chives and almonds if using and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B69C298379CA84CB5B009AA59463993', X'19CBC5A813345E4F806C01E1AAAD5B1B', X'19CBC5A813345E4F806C01E1AAAD5B1B', 0, NULL, NULL, 2, NULL, 0, 'In a 12-inch nonstick pan or shallow Dutch oven, melt 1 tablespoon butter over medium-high heat. Add garlic and cook, stirring, for 30 seconds to a minute, until very fragrant but not browned. Add shrimp to pan in a single layer. Cook for about a minute on one side, until opaque, then flip and heat the other side until just cooked through. Remove from pan, sprinkle with about 1/3 of the lemon juice, and reserve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'537B60927A98E942A15B29C2E1B3F08F', X'19CBC5A813345E4F806C01E1AAAD5B1B', X'19CBC5A813345E4F806C01E1AAAD5B1B', 1, NULL, NULL, 2, NULL, 0, 'Heat the remaining 2 tablespoons butter and the olive oil over medium-high in the same pan. Add onion and 1/2 teaspoon of the salt and cook, stirring occasionally, until softened, about 5 minutes. Add rice, stir well to coat with butter and oil, and cook for a minute. Pour in wine and stir to incorporate. Simmer, stirring frequently, until almost evaporated. Then add broth by the cupful, continuing to stir frequently and adding more as it absorbs. Add the remaining 1/2 teaspoon salt during this process. Stir in broccoli as you add the last cup of broth. Stop cooking when there’s still a bit of thick broth remaining and the rice is tender but with the tiniest bite in the center of the grain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8470A69850A0AB4792D472E0534F92F0', X'19CBC5A813345E4F806C01E1AAAD5B1B', X'19CBC5A813345E4F806C01E1AAAD5B1B', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat, stir in remaining lemon juice and grated cheese and taste for seasoning. Arrange shrimp overtop, sprinkle with parsley and a little freshly ground black pepper, and serve with the rest of that bottle of wine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CF7EB9D0E3FD144A80813E4BB6F6AE7', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 0, NULL, NULL, 2, NULL, 0, 'In a large pan with a heavy bottom, melt the butter over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE06833668BCE84A8C1D639A52579D49', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 1, NULL, NULL, 2, NULL, 0, 'Add the onion along with a good pinch of salt and cook, stirring frequently, until beginning to soften, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB356A3DB401714782DAD36FA3195EDF', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 2, NULL, NULL, 2, NULL, 0, 'Stir in the rice and cook, still stirring frequently, for two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A39BD19A48152489E67D24EE0D0C615', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 3, NULL, NULL, 2, NULL, 0, 'Add the wine and cook, stirring almost constantly, until nearly absorbed. Lower heat to medium if necessary to prevent any hint of burning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'175B7DFDA5AB4943A584325B85197C1E', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 4, NULL, NULL, 2, NULL, 0, 'Begin adding the broth by the cupful (or so), stirring frequently throughout. With the first addition, crumble in the saffron threads. Continue adding broth as the rice absorbs it. Add a big sprinkle of salt if your broth isn''t salty.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04B0F4CF68EFFF4492CFBA861270AEC2', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 5, NULL, NULL, 2, NULL, 0, 'Stir in the peas with the last addition of broth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85880D13E2235E4FAE649C7B29B54B85', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 6, NULL, NULL, 2, NULL, 0, 'Risotto is finished cooking when each grain of rice is tender but still has a nice hint of chewy bite, and there''s a little bit of delicious starchy broth remaining. (Keep in mind that the broth will thicken as you add the cheeses and let the risotto rest for a few minutes after cooking.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6E73514C500754EAC393CD438FD2848', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 7, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in the cheeses.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E560346D6A06F949BA7CBB88F839E55A', X'302DB2E346B6434381A49EACC779F901', X'302DB2E346B6434381A49EACC779F901', 8, NULL, NULL, 2, NULL, 0, 'Let the risotto rest for about five minutes. Then drizzle with a little olive oil if you like and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'424D4B307913DB409ED2886C1F57BEFA', X'150A0ABD5EAB614A8AC708F1988D5237', X'150A0ABD5EAB614A8AC708F1988D5237', 0, NULL, NULL, 2, NULL, 0, 'Place shallot, spinach, oats, broth and soy sauce in a small pot and bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE58A144A8BBB749892345A655A81645', X'150A0ABD5EAB614A8AC708F1988D5237', X'150A0ABD5EAB614A8AC708F1988D5237', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat to simmer and cook for 5-10 minutes, until oats have softened into a porridge.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9E94CD7C6F5674B903FAD0DDB3EE2FF', X'150A0ABD5EAB614A8AC708F1988D5237', X'150A0ABD5EAB614A8AC708F1988D5237', 2, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in butter and miso paste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8FE5E0F96D17C48BCB9FF7DDF6FDD37', X'150A0ABD5EAB614A8AC708F1988D5237', X'150A0ABD5EAB614A8AC708F1988D5237', 3, NULL, NULL, 2, NULL, 0, 'Transfer to a bowl and top with a drizzle of sriracha and a boiled egg if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D1DCB65DDDCF94EAEC989184A8EB8AA', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil over medium heat in a medium-sized pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'097FEF59AAD09845A2941FCB0CC21839', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', 1, NULL, NULL, 2, NULL, 0, 'Add the onion and cook, stirring occasionally, until starting to soften, about 5 minutes. Add the garlic and the salt and pepper and cook two minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FF2C82ADDF767458D79F2617F44EC1F', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', 2, NULL, NULL, 2, NULL, 0, 'Add the oats and stock to the pot, bring to a boil over high heat, and then simmer, stirring from time to time, until the oats have absorbed just about all the liquid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A94941DC4D030345B3AC8225D6341D9F', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', 3, NULL, NULL, 2, NULL, 0, 'Stir in the soy sauce and butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B418EE6D548374C9FEF2373539CD6B2', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', X'23B8679D3C2EB54EB9F8FE8D48AFD63E', 4, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and top each bowl with an egg and plenty of crispy shiitakes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BF8CB2021904A4FBD4BCDB4FCE132A8', X'02C0B7C9084C1B4AA50CE3793B14053B', X'02C0B7C9084C1B4AA50CE3793B14053B', 0, NULL, NULL, 2, NULL, 0, 'Steam the cauliflower until crisp-tender and place in a large bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9D00103589E0D4FB38BC9FE791BA42E', X'02C0B7C9084C1B4AA50CE3793B14053B', X'02C0B7C9084C1B4AA50CE3793B14053B', 1, NULL, NULL, 2, NULL, 0, 'In a small pot, combine lentils, stock, herbes de Provence and a generous pinch of salt. Bring to a boil, then cover and simmer until liquid is almost absorbed and lentils are barely tender, about 20 minutes. Let rest, covered, for 10 minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7687C3268FCE5C42B182B763570469F1', X'02C0B7C9084C1B4AA50CE3793B14053B', X'02C0B7C9084C1B4AA50CE3793B14053B', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a large skillet over medium-high heat until it shimmers. Add the onion along with a generous sprinkle of salt and cook, stirring occasionally, until softened and browned in spots. Add garlic and capers and cook until very fragrant. Stir in cooked quinoa and heat through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EB5D784AA3E5D4EA8A8CE89B87D545E', X'02C0B7C9084C1B4AA50CE3793B14053B', X'02C0B7C9084C1B4AA50CE3793B14053B', 3, NULL, NULL, 2, NULL, 0, 'Tip lentils and onion mixture into the bowl with the cauliflower. Sprinkle with the lemon juice, walnuts and pepper and toss gently to combine. Serve warm, with Pecorino to pass if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64FC56AAE77D7041BE0D06A1F2F5A8BA', X'8E3CA8176640F240A2B8633AEE8B8E5D', X'8E3CA8176640F240A2B8633AEE8B8E5D', 0, NULL, NULL, 2, NULL, 0, 'Trim and discard the root end and green parts of the leeks. Slice the white parts in half lengthwise, then crosswise into thin half-moons. Place in a colander and wash very well — leeks can be very sandy. Drain well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'400CA5BB2D788E4A8E560582F1AC4D46', X'8E3CA8176640F240A2B8633AEE8B8E5D', X'8E3CA8176640F240A2B8633AEE8B8E5D', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a wide, shallow pan over medium-high heat. Add the leeks, mushrooms, salt and thyme. Cook, stirring occasionally and without browning, for about 10 minutes, until leeks are becoming tender and the liquid released from the mushrooms has begun to reduce. Add the garlic and cook, stirring, one more minute. Stir in the wine and simmer briskly for two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2902C6593374A047A2E6EA7AB2A88E56', X'8E3CA8176640F240A2B8633AEE8B8E5D', X'8E3CA8176640F240A2B8633AEE8B8E5D', 2, NULL, NULL, 2, NULL, 0, 'Add the farro, stock, tamari, and bay leaf, give it all a good stir, and raise the heat to high. While the liquid comes to a boil, use your fingers to crumble the chestnuts into the pan. When it boils, reduce the heat to maintain a gentle simmer, cover the pan, and simmer until liquid is absorbed, about 30 minutes. Serve warm. Keeps well for several days, tightly covered in the refrigerator.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3197EB87AA9DB149B06D2F869196F610', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 0, NULL, NULL, 2, NULL, 0, 'Rinse the quinoa very well, then place in a medium lidded pot with 1 1/2 cups water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E69D7016A5F2143ADC4EFCB0AFF46BE', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then cover and reduce heat to maintain a gentle simmer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1ACB627FB95AD43AA1E1A0B0472B201', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 2, NULL, NULL, 2, NULL, 0, 'Simmer for 15 minutes or until nearly all the water is absorbed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71E3DDF86F2FBE4898E48A4B7E25A78B', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and keep in covered pot to steam for 5 minutes, until all water is absorbed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EC48945F28EB244856A4C0572A35020', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 4, NULL, NULL, 2, NULL, 0, 'While the quinoa cooks, chop the vegetables and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6B760B345C6C648AA90875A8E1878E3', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the vinegar, soy sauce, mirin, and sesame oil with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70E0B671E0DEAF40897067017CCC64E3', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 6, NULL, NULL, 2, NULL, 0, 'Spoon the quinoa into a serving bowl while still warm and mix together thoroughly with the dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA0721B4AC1A2642BC4FE1962D52508F', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 7, NULL, NULL, 2, NULL, 0, 'After it cools a bit, stir in the chopped vegetables.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65C33D144FFACE479700466DD1DF2DBC', X'59C08F4E29C33748AD8C5FC4778923F6', X'59C08F4E29C33748AD8C5FC4778923F6', 8, NULL, NULL, 2, NULL, 0, 'Serve cold or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE21C2CFC6744C42AE42230C8820A4AF', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 0, NULL, NULL, 2, NULL, 0, 'Place the rice in a medium pot with two scant cups cold water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'940A3E15406CC84BB10AC09CA9FB18E7', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, then cover, reduce heat to maintain a gentle simmer, and cook until water is absorbed, about 40 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E73A70C92AFA244A5C0D78DA1E5F828', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil over medium-high heat in a wide skillet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C20678E7559EF346AF077D652B83974A', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 3, NULL, NULL, 2, NULL, 0, 'Add the onion and cook, stirring occasionally, until it starts to soften, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F2800F1CE36E94F9C0760906985243F', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 4, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and cook, still stirring occasionally, until they have reduced in volume by about half and reabsorbed any water they''ve given off during cooking, about 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C62D933E6A2FFC4096BA8B97700C7614', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 5, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook, stirring, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED7CC4783BA7AE4B972F5D934F58C57D', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 6, NULL, NULL, 2, NULL, 0, 'Add the spinach to the pan and cook, stirring, until just wilted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA6163B5A2510F46B04EAAA8E251262A', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 7, NULL, NULL, 2, NULL, 0, 'When the rice and the mushroom mixture are both cooked, scrape the mushroom mixture into the pot with the rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'305861A6B01CBC4F8F149DDCEDCF7B76', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 8, NULL, NULL, 2, NULL, 0, 'Add the soy sauce and chives and stir together gently but thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5777FDB225FFE945976C5E30D69F08ED', X'0FBE1D197F094349897FC25B5D728D73', X'0FBE1D197F094349897FC25B5D728D73', 9, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'163724370CB56E4292335960EE60C161', X'A08D7442F609B64A8AAD85B88EBC8711', X'A08D7442F609B64A8AAD85B88EBC8711', 0, NULL, NULL, 2, NULL, 0, 'Bring two cups of water to a boil in a small pot. Add the rice and 1/4 teaspoon salt, cover, and cook at a gentle simmer for 40 minutes or until water is absorbed. Cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'591E55BBD1D05A4DA90B47F879CFF435', X'A08D7442F609B64A8AAD85B88EBC8711', X'A08D7442F609B64A8AAD85B88EBC8711', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 375°F with a rack in the middle. On a large rimmed baking sheet, place the diced eggplant, onion, and garlic. Sprinkle with two Tablespoons of the olive oil, the remaining 1/2 teaspoon salt, and the pepper. Roast for about 30 minutes, until tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E397FFE386914B4FAAFAD3535D37D5FC', X'A08D7442F609B64A8AAD85B88EBC8711', X'A08D7442F609B64A8AAD85B88EBC8711', 2, NULL, NULL, 2, NULL, 0, 'Pour the rice, roasted eggplant mixture, olives, feta (if using), basil, and chickpeas (if using) into a large bowl. Sprinkle with the lemon juice and toss gently to combine. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB6219B19FFC304C8A93E04772CC2D3C', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 0, NULL, NULL, 2, NULL, 0, 'Place the farro in a medium pot with cold water to cover by an inch. Soak for 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA30C6789077647B4231590FFE6B8C4', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 1, NULL, NULL, 2, NULL, 0, 'After 20 minutes, drain the soaking water and add fresh water to cover the farro by two inches. Add the salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DF1F5DEEB5A7D42827A9E144A959A41', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 2, NULL, NULL, 2, NULL, 0, 'Bring the farro to a boil over high heat, then reduce the heat to maintain a gentle boil. Cook for about 25 minutes, until the farro is tender but still chewy. Drain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48FB1D7B3D52104788209E63BAF1CF8D', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a 12-inch skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15BD365463597E439AB60C3F3D0CC201', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 4, NULL, NULL, 2, NULL, 0, 'Add the onion, mushrooms and garlic and cook, stirring occasionally, until the onion is soft and the mushrooms have reduced substantially in volume, about 7 minutes. Lower the heat if necessary to prevent the onion and garlic from browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29DA3338239DD8419EE108610C0F9F10', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 5, NULL, NULL, 2, NULL, 0, 'Add the broccoli florets, soy sauce and water to the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD438085798F114C91A19AD481DC3DD6', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 6, NULL, NULL, 2, NULL, 0, 'Cover and simmer until the broccoli is quite tender, 5 to 7 minutes more. (Cook the broccoli only as much as you like it cooked. I prefer this dish without any hint of crunch.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F742AD0C09FF4A9B4014FBC4977B54', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 7, NULL, NULL, 2, NULL, 0, 'Remove the lid and cook one minute more to reduce any remaining liquid to just a few tablespoons.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86BE8C864C0EB846BE705F01044ECFD9', X'E6C9150D93F256459B40DC4D43508ABD', X'E6C9150D93F256459B40DC4D43508ABD', 8, NULL, NULL, 2, NULL, 0, 'Off the heat, add the drained farro and the parmesan cheese to the pan. Stir well and season to taste with pepper. Serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'218F4BAD97A3D24398D9549C29DF84DC', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 0, NULL, NULL, 2, NULL, 0, 'Halve the onions lengthwise and then thinly slice each half crosswise, to form lots of thin half-circles. Melt 1 tablespoon of the butter, along with the olive oil, in a 12-inch nonstick frying pan over medium-low heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'939A4AF6BAC207429309C536C5BC8627', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 1, NULL, NULL, 2, NULL, 0, 'Add the onions and a big pinch of salt, and cook for about 45 minutes to an hour, stirring occasionally with a wooden spoon, until they are dramatically reduced in volume and well on their way to turning a deep golden brown. You may need to reduce the heat to low after a while to prevent them from burning. You can ignore the onions for a while, but watch them carefully at the end, as they will start to change more quickly. When nearly done, pour in Marsala and continue cooking until evaporated. You can do this step up to a couple days in advance if you like and store onions tightly covered in the fridge.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D31CF30934775A44AC0E83E40FE8AA4E', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, dice bacon and place in another large frying pan . Cook over medium heat until just crispy, then remove bacon from pan and set aside. Pour off most of the bacon grease from the pan, leaving two tablespoons.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C63EE48B8D49F34CA3C2A88FC247B07A', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 3, NULL, NULL, 2, NULL, 0, 'To the pan with the bacon grease, add the rice and stir to coat with fat. Cook, stirring occasionally, for about two minutes, until some of the rice grains are translucent.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C7A8C48FD06EF45B83A4B2F0F331731', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 4, NULL, NULL, 2, NULL, 0, 'Raise heat to medium-high. Add the wine and cook, stirring frequently, until absorbed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E50D651931FEA440B28BF8D2E5E4E2F4', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 5, NULL, NULL, 2, NULL, 0, 'Add chicken stock, about a cup at a time, and stir frequently. Wait until most of the liquid has been absorbed and the rest turned into a starchy-looking broth before adding the next ladle-full of stock.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EA06BFF57B3384EBBEC6CADD52F3E87', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 6, NULL, NULL, 2, NULL, 0, 'After the last addition of stock, continue to stir until the rice is tender, with a little chewy bite, and there is still some thick, glossy broth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'434747CCA7C9664CAC16A1B189C1DE98', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 7, NULL, NULL, 2, NULL, 0, 'Add the spinach along with the reserved bacon and caramelized onions and and stir for less than a minute, until spinach is just wilted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91635A0AD7986B44B02E0996D2017F93', X'B87061CD8E9E3745B0C3AA3F999A08C4', X'B87061CD8E9E3745B0C3AA3F999A08C4', 8, NULL, NULL, 2, NULL, 0, 'Turn off the heat and stir in the remaining two tablespoons butter and the grated cheese. Season to taste with salt and pepper. Let rest for just a couple of minutes and serve in shallow bowls with extra grated cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D56AA713A5E6044CBEC5816E88152225', X'66FF668C429F47409B2FFB1469346C6D', X'66FF668C429F47409B2FFB1469346C6D', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil. Add rice noodles and cook according to package instructions, usually just a few minutes (ours cook for 3).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'482470086FF38B44B1D08ECB4E2A9CDD', X'66FF668C429F47409B2FFB1469346C6D', X'66FF668C429F47409B2FFB1469346C6D', 1, NULL, NULL, 2, NULL, 0, 'While the noodles cook: Into a serving bowl, pour the safflower oil, toasted sesame oil, tamari, and sriracha. Whisk to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73477380CF7E1246A75D0DF833B2AAFD', X'66FF668C429F47409B2FFB1469346C6D', X'66FF668C429F47409B2FFB1469346C6D', 2, NULL, NULL, 2, NULL, 0, 'Drain noodles and add to sauce. You can rinse them before adding to the sauce if you like to reduce the starchiness, but I don''t bother. Toss with tongs to coat well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DCFFE071006D54B91CE27B812CDF340', X'66FF668C429F47409B2FFB1469346C6D', X'66FF668C429F47409B2FFB1469346C6D', 3, NULL, NULL, 2, NULL, 0, 'Add scallions and a big squeeze of fresh lime juice and toss. Serve warm or at room temperature with additional lime wedges.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7F859A45DEDF742939E7B38F2E70841', X'1F19D454E7BB644684D3DF695473474A', X'1F19D454E7BB644684D3DF695473474A', 0, NULL, NULL, 2, NULL, 0, 'Cook the orzo in well salted water according to package directions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11A7713AE9EF0541AD624F3F10093D5B', X'1F19D454E7BB644684D3DF695473474A', X'1F19D454E7BB644684D3DF695473474A', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a 12-inch nonstick skillet over medium heat. Add the onions, garlic, and a big pinch of salt and cook, stirring frequently, until the onion is beginning to soften.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B2C27E6868E9428DBACE0C522A4479', X'1F19D454E7BB644684D3DF695473474A', X'1F19D454E7BB644684D3DF695473474A', 2, NULL, NULL, 2, NULL, 0, 'Add the shredded zucchini and the thyme and raise heat to high. Cook, stirring occasionally, until the squash releases its juices and they cook off. The zucchini will reduce in size by at least half and may begin to brown slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE875F82BD4B9745927FCA3F807FE5B1', X'1F19D454E7BB644684D3DF695473474A', X'1F19D454E7BB644684D3DF695473474A', 3, NULL, NULL, 2, NULL, 0, 'Turn off the heat and immediately add the drained orzo, lemon juice and zest, and cheese, along with salt and pepper to taste. Stir well but gently to combine. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7444FC815E38E541A0A842970B70944E', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 0, NULL, NULL, 2, NULL, 0, 'Start with a very large, heatproof mixing bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B30613811BE03D4BADFB64186D6103E4', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the eggs and egg yolks, half the grated cheese, and plenty of freshly ground black pepper. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'978CD6160FEFE24E840368B9C69B2804', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 2, NULL, NULL, 2, NULL, 0, 'Fill a very large pot halfway with water. Add plenty of salt, cover, and bring to a boil. (Proceed to step four at this point, and start cooking the bacon while you wait for the water to boil.) When the water boils, cook the spaghetti one minute less than it says on the package.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D8700B5BFFA6A4D8E783070399D179F', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 3, NULL, NULL, 2, NULL, 0, 'At the same time, add the diced bacon and the smashed garlic cloves to a large pan and then set the pan over medium heat. Cook, stirring occasionally, until the bacon is nicely browned and a lot of the fat has been rendered. If there is a lot of fat in the pan, remove most of it, leaving three or four tablespoons.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDC6476BC8EC2246981106740F8860CC', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 4, NULL, NULL, 2, NULL, 0, 'With the bacon pan set over low heat, carefully stir in 1/4 cup of the pasta cooking water, scraping up any browned bits from the bottom of the pan. When the pasta is ready, use tongs to transfer it from the pot to the pan. It''s fine if some of the cooking water clings to the spaghetti as you transfer it. (Leave the rest of the cooking water in the pasta pot.) Stir the spaghetti to coat with the bacon fat/water emulsion and cook for a minute, then turn off the heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D0739E05CF78F47AEA45DDC4466B563', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 5, NULL, NULL, 2, NULL, 0, 'Give the eggs another good whisk, and then slowly whisk in 1/4 cup of the pasta cooking water. This helps to temper the eggs, bringing up their temperature slowly so they won''t scramble. When the water is incorporated, tip the entire contents of the pan into the bowl with the egg mixture. Use tongs to stir it all up very well, coating all of the spaghetti strands with the sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'012C0C92AB87B04B9718A52BA482829D', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 6, NULL, NULL, 2, NULL, 0, 'At this point, you''ll need to use your judgment a little bit. If all of the sauce clings to the pasta like a nice, creamy, gently thickened coating, you''re all set. You can plate the pasta, sprinkle it with the remaining cheese and some more pepper, and serve. Often this will be the case — the combined heat of all the dish''s elements will be enough cook the sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08D353F149473949B574191549F41775', X'1918C4AA43C58E418AB5A18F5ECA0DD0', X'1918C4AA43C58E418AB5A18F5ECA0DD0', 7, NULL, NULL, 2, NULL, 0, 'If the sauce seems thin and watery rather than silky, set the mixing bowl over the pot of steaming pasta water and toss the pasta constantly with the tongs until the heat from the water thickens the sauce. (If the bowl fits over the pot without touching the water, you can rest it on top — otherwise hold it just above the water with one hand while tossing with the other.) When the sauce is silky and clings to the pasta, you''re ready to plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A43CA579F8CCB74E80F08F8AC416ABE0', X'70CC77BE3BFC624AB20D508E09C411A2', X'70CC77BE3BFC624AB20D508E09C411A2', 0, NULL, NULL, 2, NULL, 0, 'Fill a big pot halfway with water and a teaspoon of salt. Cover and bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80221EBCB503BB4DA7EF46B906105A21', X'70CC77BE3BFC624AB20D508E09C411A2', X'70CC77BE3BFC624AB20D508E09C411A2', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter (or heat the olive oil) in a 12-inch nonstick frying pan over medium heat. Add shallot, garlic, and olives and cook, stirring frequently, for about three minutes. Aim for the shallots to be slightly translucent and garlic slightly soft, without any browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EF6F0B3AD76E141A1DB459168516BE0', X'70CC77BE3BFC624AB20D508E09C411A2', X'70CC77BE3BFC624AB20D508E09C411A2', 2, NULL, NULL, 2, NULL, 0, 'Stir in wine, beans, and cherry tomatoes. Raise heat to medium-high and simmer for two to three minutes, until much of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AFC81085EBCA8449E37901879211831', X'70CC77BE3BFC624AB20D508E09C411A2', X'70CC77BE3BFC624AB20D508E09C411A2', 3, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium and stir in spinach. Cook two to three minutes more, until wilted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A56B7523DD6A934FAA20CF6B9824349F', X'70CC77BE3BFC624AB20D508E09C411A2', X'70CC77BE3BFC624AB20D508E09C411A2', 4, NULL, NULL, 2, NULL, 0, 'Cook tortellini in boiling water according to package instructions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D4CD236170FF2419580684BFC71B6AF', X'70CC77BE3BFC624AB20D508E09C411A2', X'70CC77BE3BFC624AB20D508E09C411A2', 5, NULL, NULL, 2, NULL, 0, 'Drain tortellini and stir into to skillet along with the pecorino. Sprinkle with freshly ground black pepper and some salt if you like (but taste first since there are lots of salty ingredients).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E7DE3364D6AF74583F712F96D8223DD', X'70CC77BE3BFC624AB20D508E09C411A2', X'70CC77BE3BFC624AB20D508E09C411A2', 6, NULL, NULL, 2, NULL, 0, 'Serve with additional grated cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D16DE1E253EF374EA2ECC81D6D9CF72D', X'72B456A561CA4E4281141F35083EEEEF', X'72B456A561CA4E4281141F35083EEEEF', 0, NULL, NULL, 2, NULL, 0, 'Trim the ends off the zucchini and cut into noodles according to the instructions on your spiralizer. Line two rimmed sheet pans with thick layers of paper towels. Spread half of the zucchini noodles onto each pan and sprinkle with the salt. Cover with another thick layer of paper towels and press gently. Leave noodles to dry while you prepare the rest of the dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45891213848B3F4BA0748C421D9E57E0', X'72B456A561CA4E4281141F35083EEEEF', X'72B456A561CA4E4281141F35083EEEEF', 1, NULL, NULL, 2, NULL, 0, 'Warm olive oil over medium heat in a very large pan or wide, shallow pot. (A 5-quart Dutch oven, a 12-inch frying pan with high sides, or even a wok would all work well.) Add shallot, garlic, and capers, and cook, stirring frequently, for two minutes. Stir in tomatoes and cook, stirring occasionally, until most of them have burst.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F9BF3E40E45214591C0B14FF3FF8D19', X'72B456A561CA4E4281141F35083EEEEF', X'72B456A561CA4E4281141F35083EEEEF', 2, NULL, NULL, 2, NULL, 0, 'While tomatoes cook, cut the kernels from the corn cobs. Add corn kernels and chickpeas to pan and cook for about two minutes, until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B43DF699E091D41B0E6BE54DAD252D7', X'72B456A561CA4E4281141F35083EEEEF', X'72B456A561CA4E4281141F35083EEEEF', 3, NULL, NULL, 2, NULL, 0, 'Pat zucchini noodles dry with the paper towels and stir noodles into the vegetable mixture in the pan. (Tongs work really well if you have them.) Cook until noodles are crisp-tender, just a couple of minutes. The tomatoes and zucchini will create a bit of delicious sauciness in the bottom of the pan. Stir this to coat the noodles. Remove pan from heat and divide noodles and saucy vegetables among wide, shallow serving bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'868773273D2A194F97C0784C7470DCFE', X'72B456A561CA4E4281141F35083EEEEF', X'72B456A561CA4E4281141F35083EEEEF', 4, NULL, NULL, 2, NULL, 0, 'Cut slices of Bayonne Ham into long, thin strips, about four per slice. Divide strips among serving bowls. The residual heat from the noodles will warm the ham just enough to be beautifully tender and flavorful, without cooking it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77501D09860B4F4AA08DF4E174F3C4CC', X'72B456A561CA4E4281141F35083EEEEF', X'72B456A561CA4E4281141F35083EEEEF', 5, NULL, NULL, 2, NULL, 0, 'Garnish each bowl with some basil, freshly ground black pepper, and grated cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255FFDAEB72DC24896A1A790AC2EE146', X'E7CF5893E578AD428900EE59F2C25CFE', X'E7CF5893E578AD428900EE59F2C25CFE', 0, NULL, NULL, 2, NULL, 0, 'Place macaroni, broth, shallot, mustard, nutmeg, cayenne, salt, and pepper into Instant Pot and stir.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73254D7068C816439916A8B1613B42DB', X'E7CF5893E578AD428900EE59F2C25CFE', X'E7CF5893E578AD428900EE59F2C25CFE', 1, NULL, NULL, 2, NULL, 0, 'Set vent to sealing position. Cook on manual mode, high pressure, for 4 minutes. (Pot will take about 10 minutes to come up to pressure before timed cooking begins.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A44488F9FA09C84086DB1C9B4967D23D', X'E7CF5893E578AD428900EE59F2C25CFE', X'E7CF5893E578AD428900EE59F2C25CFE', 2, NULL, NULL, 2, NULL, 0, 'Carefully do a manual pressure release by turning the vent to open. (We like to use a towel or a long-handled spoon to do this.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51A1BDEE98D21C4283312EBD217D5610', X'E7CF5893E578AD428900EE59F2C25CFE', X'E7CF5893E578AD428900EE59F2C25CFE', 3, NULL, NULL, 2, NULL, 0, 'Stir in milk and cheeses until cheese is melted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B61B186455A0845A465CA234AE01AF3', X'E7CF5893E578AD428900EE59F2C25CFE', X'E7CF5893E578AD428900EE59F2C25CFE', 4, NULL, NULL, 2, NULL, 0, 'Stir in sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'125D0C6742ED0B4AA3BE623AD83EEDF4', X'E7CF5893E578AD428900EE59F2C25CFE', X'E7CF5893E578AD428900EE59F2C25CFE', 5, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1BDD618A6AFDB4EB9F0AAB9B91C8952', X'6BDACBC7D22E234FB5F5C9090F6D1591', X'6BDACBC7D22E234FB5F5C9090F6D1591', 0, NULL, NULL, 2, NULL, 0, 'Fill a large pot halfway with very well salted water and bring to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50C71F9CF842D847A250A191E11F7C45', X'6BDACBC7D22E234FB5F5C9090F6D1591', X'6BDACBC7D22E234FB5F5C9090F6D1591', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the cream and egg yolk and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B305608542ADD440ABEC884A7DB0546F', X'6BDACBC7D22E234FB5F5C9090F6D1591', X'6BDACBC7D22E234FB5F5C9090F6D1591', 2, NULL, NULL, 2, NULL, 0, 'In a deep 12-inch frying pan, melt one tablespoon of the butter over medium-high heat. Cut chicken tenders into bite-sized pieces. (Protip: try using kitchen shears to avoid dirtying a cutting board.) Place chicken into pan in a single layer and sprinkle generously with salt and pepper. Cook until lightly browned on the underside and opaque halfway through, about three minutes. Flip once and cook until just cooked through. Remove to a plate and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF50B52DCFFA7D4190C7C81CA76A1456', X'6BDACBC7D22E234FB5F5C9090F6D1591', X'6BDACBC7D22E234FB5F5C9090F6D1591', 3, NULL, NULL, 2, NULL, 0, 'When water boils, cook fettuccine until al dente according to package directions. Before draining pasta, scoop out 1/2 cup of the cooking water and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA1EA7C4E6CF547B354FEB04073C783', X'6BDACBC7D22E234FB5F5C9090F6D1591', X'6BDACBC7D22E234FB5F5C9090F6D1591', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, melt remaining tablespoon of butter over medium-low heat in the same pan. Add garlic and cook for one minute, stirring constantly. Pour in cream mixture. Simmer gently, adjusting heat if necessary to prevent boiling, while pasta cooks. When pasta is ready, stir lemon juice and cheese into sauce until smooth and creamy. Whisk in reserved pasta water until smooth. Add spinach and pasta, raise heat to medium, and toss with tongs for a couple of minutes until spinach is wilted and sauce clings nicely to pasta. Stir in chicken along with any accumulated juices. Off the heat, season with freshly ground black pepper (and salt if you like).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C4C14BC236B314D81D261E2C29EA407', X'6BDACBC7D22E234FB5F5C9090F6D1591', X'6BDACBC7D22E234FB5F5C9090F6D1591', 5, NULL, NULL, 2, NULL, 0, 'Divide among four shallow bowls and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC68B1252E8AB47B93DACBE9E9419A3', X'67549043F4F991489484D73DDC60B6A3', X'67549043F4F991489484D73DDC60B6A3', 0, NULL, NULL, 2, NULL, 0, 'Fill a medium pot halfway with water, add 2 tablespoons salt, and bring to a boil. Add orzo, give it one big stir, and then cook undisturbed to al dente according to package instructions. Adjust heat as necessary to maintain a boil or very brisk simmer without letting the pot overflow.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD6AFBE2CB0FED4B95E7EFC443F22EE9', X'67549043F4F991489484D73DDC60B6A3', X'67549043F4F991489484D73DDC60B6A3', 1, NULL, NULL, 2, NULL, 0, 'Drain pasta, place back into pot, and toss with butter and lemon juice until butter is melted. Stir in pecorino and plenty of freshly ground black pepper and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C577A02EAFBB7242A34C995976791C17', X'BC8E015AA5C4A649886E08C2249FE0AC', X'BC8E015AA5C4A649886E08C2249FE0AC', 0, NULL, NULL, 2, NULL, 0, 'Cook pasta to al dente in well-salted water according to package directions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A408DDA14DFAF44F80BC2CF102D14848', X'BC8E015AA5C4A649886E08C2249FE0AC', X'BC8E015AA5C4A649886E08C2249FE0AC', 1, NULL, NULL, 2, NULL, 0, 'While the pasta cooks, warm the olive oil over medium heat in a 5-quart round braiser or other deep, wide pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B38C412F762F19478BD41AFBE3ACC4BE', X'BC8E015AA5C4A649886E08C2249FE0AC', X'BC8E015AA5C4A649886E08C2249FE0AC', 2, NULL, NULL, 2, NULL, 0, 'Add tomatoes and salt and cook, stirring occasionally, until tomatoes have softened, just a few minutes. Add ramps and cook until wilted, about two minutes. Add spinach and cook, stirring occasionally, until wilted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'661041BEEE958746AEF683021D2C5CFF', X'BC8E015AA5C4A649886E08C2249FE0AC', X'BC8E015AA5C4A649886E08C2249FE0AC', 3, NULL, NULL, 2, NULL, 0, 'Turn off heat and add sopressata to the top of the mixture to warm gently.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8321A2B4ACDA74DB38F8F1DE1516817', X'BC8E015AA5C4A649886E08C2249FE0AC', X'BC8E015AA5C4A649886E08C2249FE0AC', 4, NULL, NULL, 2, NULL, 0, 'Drain the pasta and stir it into the vegetable mixture along with the butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E46D6EEA7CE6E341A006254CBF7FB7B4', X'BC8E015AA5C4A649886E08C2249FE0AC', X'BC8E015AA5C4A649886E08C2249FE0AC', 5, NULL, NULL, 2, NULL, 0, 'Spoon into bowls and top with grated cheese, black pepper and, if you like, ramp pesto.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8A7FD4A4C15124A8C1D53E3067BB69B', X'7AC05DCD377B1F49A79478A636820547', X'7AC05DCD377B1F49A79478A636820547', 0, NULL, NULL, 2, NULL, 0, 'Set a heavy 5 1/2-quart pot (this is my favorite) over medium-high heat and add the bacon. Cook, stirring occasionally, until browned. If there is more than about two tablespoons of fat at the bottom of the pot, spoon out the rest and discard or save for another use. Add onion, carrot, and celery and continue cooking, stirring from time to time, until softened, about 5 minutes. Stir in garlic and tomato paste and cook two minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C93657FFC2E8484E8D95BBBD24720C1C', X'7AC05DCD377B1F49A79478A636820547', X'7AC05DCD377B1F49A79478A636820547', 1, NULL, NULL, 2, NULL, 0, 'Add ground beef, along with one teaspoon of the salt, stirring to break up the meat into small pieces. Cook for about 5 minutes, until beef isn''t pink. Stir in wine and simmer for a few minutes, until liquid is reduced by about half. Stir in tomato sauce, chopped tomatoes, cinnamon, nutmeg, and remaining teaspoon of salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C2A0739E89E634ABF19C62DD5CFFDE7', X'7AC05DCD377B1F49A79478A636820547', X'7AC05DCD377B1F49A79478A636820547', 2, NULL, NULL, 2, NULL, 0, 'Cover pot and reduce heat to maintain a gentle simmer while you cook the spaghetti. Stir in cream and leave pot uncovered for the last few minutes of cooking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A18B184DF91D54AB1046C503536AC59', X'7AC05DCD377B1F49A79478A636820547', X'7AC05DCD377B1F49A79478A636820547', 3, NULL, NULL, 2, NULL, 0, 'While the sauce simmers, fill a large, heavy pot halfway with well salted water and bring to a boil. Cook spaghetti al dente according to package instructions. Drain, return pasta to pot, and toss with several ladles of sauce. (Psst...I also sometimes throw in a big pat of really good butter while tossing the spaghetti with the sauce.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18385C28B6A34744A6BE5EE72B59ABFE', X'7AC05DCD377B1F49A79478A636820547', X'7AC05DCD377B1F49A79478A636820547', 4, NULL, NULL, 2, NULL, 0, 'To serve, plate spaghetti in wide, shallow bowls and top with additional sauce. Pass grated cheese and a pepper mill at the table, along with some flaky sea salt if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B307EB98DAAC34BA0B28959DFD9C49D', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 0, NULL, NULL, 2, NULL, 0, 'Melt and lightly brown the butter in a 12-inch skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A96464B9C24DA64A8601E15DDF5E7228', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 1, NULL, NULL, 2, NULL, 0, 'Add shallots, garlic and sage and cook, stirring frequently, until tender, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9A33C657C61334EB5CCF9699D7AF73F', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 2, NULL, NULL, 2, NULL, 0, 'Add wine and simmer briskly until reduced by half.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB993F0536141F4CBA661997F9EF30B7', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 3, NULL, NULL, 2, NULL, 0, 'Add cream, broth and beets and simmer for 5 to 10 minutes to create a nice, slightly thickened sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E04EE36D57AD5542A82E0343125F6F58', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 4, NULL, NULL, 2, NULL, 0, 'Crumble in Bleu de Chèvre, reserving a little bit to garnish if you like. Taste for salt, and sprinkle in plenty of pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F2DA3AE5CE355428B80117AB77D9A0F', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook linguine al dente in well-salted water according to package directions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3CF57D684A8314DBB8DD46B9B5BCB3A', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 6, NULL, NULL, 2, NULL, 0, 'Drain and toss pasta into sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80776D0D0C50754A894D34036C07E800', X'D880B0CE363FA64F9D3879A7D3FDC504', X'D880B0CE363FA64F9D3879A7D3FDC504', 7, NULL, NULL, 2, NULL, 0, 'Stir in parsley and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F58E68CB686E3C43A1666905CEF86FFB', X'7D386EDC5632A442AF4D687C5895FBBC', X'7D386EDC5632A442AF4D687C5895FBBC', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill to high (about 400-500°F). Place the salmon skin-side down on a double layer of aluminum foil. Sprinkle generously with salt and pepper and lay the lemon slices over the flesh. Grill salmon on one side of grill until done to your liking, about 8-10 minutes for just barely opaque in the center, depending on thickness. Meanwhile, place shucked corn directly on grates on available side of grill and grill until nicely browned but still tender, flipping once, about 5 minutes per side,');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6B3424AAE5EF24EA8C54D19EC98C589', X'7D386EDC5632A442AF4D687C5895FBBC', X'7D386EDC5632A442AF4D687C5895FBBC', 1, NULL, NULL, 2, NULL, 0, 'When grill is free, rub asparagus with the olive oil, sprinkle with salt and pepper, and grill until nicely charred, 2-3 minutes per side, flipping once.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21635B0164D1A641834F25F8BCF5EC9F', X'7D386EDC5632A442AF4D687C5895FBBC', X'7D386EDC5632A442AF4D687C5895FBBC', 2, NULL, NULL, 2, NULL, 0, 'Prepare pesto.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F167FCBDE3DAD419F077D4B341FDFA8', X'7D386EDC5632A442AF4D687C5895FBBC', X'7D386EDC5632A442AF4D687C5895FBBC', 3, NULL, NULL, 2, NULL, 0, 'Cook spaghetti al dente according to package directions. Drain and toss with pesto. Flake the salmon into bite-sized pieces, cut corn kernels from cobs, and gently mix salmon and corn into pasta. Divide pasta among serving bowls and garnish each with a few olives, some asparagus spears and a grilled lemon slice. Serve with grated cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEE470300FEDBD498A9A9931CBECC163', X'356016971E44DB4D915518A438F06498', X'356016971E44DB4D915518A438F06498', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C736908DCC60DD4093D931335AE1AE5B', X'356016971E44DB4D915518A438F06498', X'356016971E44DB4D915518A438F06498', 1, NULL, NULL, 2, NULL, 0, 'Boil pasta shells in well salted water according to package instructions. Drain and toss with a teaspoon of the olive oil to prevent sticking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8A2AB2EB4050F4FA29C5DA8E69768F6', X'356016971E44DB4D915518A438F06498', X'356016971E44DB4D915518A438F06498', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, warm the tablespoon of olive oil over medium-high heat in a 12-inch nonstick skillet. Add the onion, garlic and shiitakes along with a big pinch of salt and cook, stirring occasionally, until softened, 5-10 minutes. Add spinach and cook until wilted. Place spinach mixture into a large bowl and stir in ricotta, half the pecorino, the salt and a few good grinds of pepper. Crack the egg into the bowl, beat well with a fork and then stir into mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2EDD16A482CB647AA773046AE6DA97B', X'356016971E44DB4D915518A438F06498', X'356016971E44DB4D915518A438F06498', 3, NULL, NULL, 2, NULL, 0, 'In a standard 9x13-inch baking dish, spread 1/2 cup or so of the tomato sauce. Fill each pasta shell with a couple of tablespoons of the vegetable-cheese mixture and place in a single, snug layer into the baking dish, continuing until all shells and filling are used. Pour remaining tomato sauce over shells and top with mozzarella and remaining pecorino. Bake for about half an hour, until hot and bubbly. Sprinkle with parsley and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05DC9C6AABF01C44BF8B714E59DBA38C', X'9E71AE5624CE4B46A28018FBD81A45F0', X'9E71AE5624CE4B46A28018FBD81A45F0', 0, NULL, NULL, 2, NULL, 0, 'Place noodles in a large bowl and cover with boiling water. Let sit for 10 minutes, stirring occasionally. Prepare the rest of the ingredients while the noodles soften.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3CE943FFD9F49458F9B05064D8A2095', X'9E71AE5624CE4B46A28018FBD81A45F0', X'9E71AE5624CE4B46A28018FBD81A45F0', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the fish sauce, brown sugar, rice vinegar, lime juice, tamarind paste and red pepper flakes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36A306C69A005242BAD37222B160F287', X'9E71AE5624CE4B46A28018FBD81A45F0', X'9E71AE5624CE4B46A28018FBD81A45F0', 2, NULL, NULL, 2, NULL, 0, 'Heat two tablespoons of the oil in a 12-inch nonstick skillet over medium-high. Add shallot and garlic and stir-fry for about two minutes, until very fragrant. Add shrimp and cook, stirring only once or twice, until just opaque, a couple of minutes. Drain noodles and add to pan with some water still clinging to them. Pour in sauce and cook, stirring frequently, until noodles are tender and sauce has thickened enough to cling to noodles without any excess liquid in the pan. Stir in tofu and bean sprouts. Push the pad thai to one side of the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E6EA93905B745498D089FCD39C2A7E1', X'9E71AE5624CE4B46A28018FBD81A45F0', X'9E71AE5624CE4B46A28018FBD81A45F0', 3, NULL, NULL, 2, NULL, 0, 'In the empty side of the pan, heat the remaining tablespoon oil and crack in the eggs. Break the yolks but otherwise leave eggs to set on the bottom. After a minute or two, scramble the eggs a bit to just cook through, leaving whites and yolks distinct. As soon as eggs are cooked, remove pan from heat and stir eggs into noodle mixture. Stir in scallions, cilantro and peanuts, reserving some to garnish if you like. Divide among shallow bowls and serve with lime wedges and sriracha.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5B2CD9FD4B6B84AA93E8FE0C1F6E451', X'22E96E1F62A4F64D95E16121A031C31D', X'22E96E1F62A4F64D95E16121A031C31D', 0, NULL, NULL, 2, NULL, 0, 'Arrange the zoodles on a double layer of paper towels. Sprinkle with a little bit of salt. Cover with a double layer of paper towels and press gently. Leave while you make the sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CEAAA7AD3E2A9479951A01EDC33E761', X'22E96E1F62A4F64D95E16121A031C31D', X'22E96E1F62A4F64D95E16121A031C31D', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch frying pan, heat the oil over medium-high heat. Add the onion, garlic and turkey and sprinkle with 1 teaspoon salt. Cook, stirring from time to time to break up the meat into bite-sized pieces, until turkey is cooked through and nicely browned in spots, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFC6F331EEC1D948AE619AD2AEE15E4A', X'22E96E1F62A4F64D95E16121A031C31D', X'22E96E1F62A4F64D95E16121A031C31D', 2, NULL, NULL, 2, NULL, 0, 'Add tomatoes, fennel, basil, thyme, oregano and red pepper flakes to pan and stir to incorporate. Reduce heat and simmer for about 10 minutes, until nice and thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AB2C48EFBF09644B1DA5FB70A0ADF0D', X'22E96E1F62A4F64D95E16121A031C31D', X'22E96E1F62A4F64D95E16121A031C31D', 3, NULL, NULL, 2, NULL, 0, 'When ready to serve, give the zoodles a gentle squeeze between paper towels to remove as much moisture as possible. Warm the remaining teaspoon olive oil in a second frying pan. Add zoodles to frying pan and cook, tossing with tongs or a rubber spatula, for about a minute. They should retain a nice bite but not look raw.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44F74BB5DC7FC4FBD49E0D929ACE150', X'22E96E1F62A4F64D95E16121A031C31D', X'22E96E1F62A4F64D95E16121A031C31D', 4, NULL, NULL, 2, NULL, 0, 'Divide zoodles among four bowls and top with sauce. Garnish with pepper, cheese and parsley, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'296FB96DCC2CBF42B17A66B034291716', X'DAFEACF08D3F4C48972B45F15625FF6C', X'DAFEACF08D3F4C48972B45F15625FF6C', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil. Cook spaghetti until al dente. Reserve 1/2 cup of the cooking water before draining.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79CFBD1D243B6E41B6B328B4CE9F91E9', X'DAFEACF08D3F4C48972B45F15625FF6C', X'DAFEACF08D3F4C48972B45F15625FF6C', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, place the minced shallot, garlic and ginger into a large mixing bowl (big enough to accommodate a pound of noodles) and stir in the rice vinegar. Let sit for five minutes. Then whisk in peanut butter, soy sauce, sesame oil and sriracha until perfectly smooth. Add some pasta water — probably 1/4 cup total depending upon the thickness of your peanut butter — until the sauce is nice and loose. You can watch the video for visual cues here.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E08D3F2D3A36F4096BC1FFE437E2EA5', X'DAFEACF08D3F4C48972B45F15625FF6C', X'DAFEACF08D3F4C48972B45F15625FF6C', 2, NULL, NULL, 2, NULL, 0, 'In a small pan, toast the sesame seeds over low heat, stirring a few times, until fragrant and lightly browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBBC6C34E587974F8DA60277CCBD41C5', X'DAFEACF08D3F4C48972B45F15625FF6C', X'DAFEACF08D3F4C48972B45F15625FF6C', 3, NULL, NULL, 2, NULL, 0, 'As soon as you drain the pasta, add it to the bowl with the sauce and toss to coat well. Stir in scallions and sesame seeds and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA05FE52359F5E4DA817BAAD19F29E52', X'D3049217BB5F1B43A52B3F35A82E499F', X'D3049217BB5F1B43A52B3F35A82E499F', 0, NULL, NULL, 2, NULL, 0, 'Arrange the zoodles on a thick layer of paper towels. Sprinkle with a little bit of salt. Cover with another thick layer of paper towels and press gently. Leave while you make the sauce, longer if possible (20 minutes or so is ideal).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CF391B2F2F29D46ABD6EF9227EB8BD4', X'D3049217BB5F1B43A52B3F35A82E499F', X'D3049217BB5F1B43A52B3F35A82E499F', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch frying pan, warm the butter and olive oil over medium-high heat. When butter melts, add shallot, garlic and capers and cook, stirring from time to time, until lightly browned, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B1EAC00D082FF46933ACFB008699288', X'D3049217BB5F1B43A52B3F35A82E499F', X'D3049217BB5F1B43A52B3F35A82E499F', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, roll up the paper towels and give the zoodles a squeeze to remove as much moisture as possible. Add zoodles to frying pan and cook, tossing with tongs or a rubber spatula, for about a minute. They should retain a nice bite but not look raw. Turn off the heat and stir in the lemon juice, parsley and some pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47542B84AB57EA4592AB6E737848F29B', X'D3049217BB5F1B43A52B3F35A82E499F', X'D3049217BB5F1B43A52B3F35A82E499F', 3, NULL, NULL, 2, NULL, 0, 'Divide among four bowls, sprinkle with cheese, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'945A74342F79A34C96B42A0DD526765B', X'14C9BF7EADF72A409AE72C05F9A71FB0', X'14C9BF7EADF72A409AE72C05F9A71FB0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Pierce the squash in several places with a small, sharp knife and place in a roasting pan. Bake for about an hour, until flesh is tender enough to insert a fork easily. When cool enough to handle, cut in half. Scoop out and discard seeds and stringy center. Then scoop the flesh out of the skin and into the bowl of a food processor fitted with the blade. Add broth and puree until smooth, stopping to scrape down sides as necessary. This process should yield somewhere in the neighborhood of 4 cups of squash puree, but don’t stress about it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5F7B4D49FD39949AA5DC1A1A40D6F55', X'14C9BF7EADF72A409AE72C05F9A71FB0', X'14C9BF7EADF72A409AE72C05F9A71FB0', 1, NULL, NULL, 2, NULL, 0, 'In a medium pot with a heavy bottom, melt the butter over medium-high heat. Add the thyme and sage and cook for one minute. Whisk in flour and cook for another minute or so, until bubbling substantially. Whisk in milk all at once. Bring to a boil over high heat, then reduce heat and simmer, stirring frequently, for five minutes or so, until slightly thickened. Off the heat, whisk in 1 1/2 teaspoons of the salt along with the nutmeg and cayenne. Then whisk in the squash puree until it all becomes a smooth sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'844530E7DD2A004EA3C32869A3A5568F', X'14C9BF7EADF72A409AE72C05F9A71FB0', X'14C9BF7EADF72A409AE72C05F9A71FB0', 2, NULL, NULL, 2, NULL, 0, 'Warm the olive oil in a 12-inch nonstick skillet over medium heat. Add onion, mushrooms and garlic along with remaining 1/2 teaspoon salt and cook, stirring occasionally, until mushrooms have reduced in volume by about half. Add radicchio and cook, stirring frequently, until wilted. Off the heat, stir in the balsamic vinegar.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'589F258AFFC4F2469C449A23AA058FCB', X'14C9BF7EADF72A409AE72C05F9A71FB0', X'14C9BF7EADF72A409AE72C05F9A71FB0', 3, NULL, NULL, 2, NULL, 0, 'Lightly brush a 9x13-inch pan with oil. Place a thin layer of béchamel sauce at the bottom of the pan, followed by a layer of lasagna noodles. Top with half the mozzarella, half the mushroom mixture, 1/3 of remaining béchamel, and 1/4 cup of pecorino. Repeat with another layer of noodles, mozzarella, mushroom mixture, sauce and pecorino. Finally, add one more layer of noodles and top with remaining sauce and pecorino.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEAC16EAC1866443AB95849CB958C90C', X'14C9BF7EADF72A409AE72C05F9A71FB0', X'14C9BF7EADF72A409AE72C05F9A71FB0', 4, NULL, NULL, 2, NULL, 0, 'Bake for about 45 minutes, until lightly browned and bubbly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3931982DCFD93046A006DD7366026BC0', X'38DEC47DE4790743B4021CA756555CAC', X'38DEC47DE4790743B4021CA756555CAC', 0, NULL, NULL, 2, NULL, 0, 'Warm the olive oil in a 5 1/2-quart Dutch oven or other good-sized heavy pot over medium-high heat. Add the onion along with a pinch of salt and cook, stirring occasionally, until softened, about 5 minutes. Add the garlic and continue cooking for two minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA40FDC8D21DF6468A0A7E90684C60AE', X'38DEC47DE4790743B4021CA756555CAC', X'38DEC47DE4790743B4021CA756555CAC', 1, NULL, NULL, 2, NULL, 0, 'Pour in the tomato sauce and chopped tomatoes and sugar and stir well. Add a teaspoon of salt if your tomatoes are unsalted. Tuck in the celery stalks. Cover and bring to a boil, then reduce heat to simmer for 30 minutes, stirring occasionally. Uncover, reduce heat to a bare simmer and stir in the butter. Taste for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C03408D5645A9E4481D5257844B3A86A', X'38DEC47DE4790743B4021CA756555CAC', X'38DEC47DE4790743B4021CA756555CAC', 2, NULL, NULL, 2, NULL, 0, 'With 15 minutes of cooking time to go, bring a large pot of well-salted water to a boil, then cook spaghetti until al dente according to package directions. Scoop out and reserve half a cup of the cooking water, then drain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'346229B622DE414E8DEDF7827C9C3860', X'38DEC47DE4790743B4021CA756555CAC', X'38DEC47DE4790743B4021CA756555CAC', 3, NULL, NULL, 2, NULL, 0, 'In a restaurant this dish would be finished one portion at a time by transferring some sauce to a wide skillet, bringing it to a simmer and then transferring one serving of pasta directly from the pasta pot to the frying pan with a bit of starchy cooking water still clinging to it. The chef would toss it all together while simmering for a few seconds and then plate it immediately. This technique marries the sauce and the pasta into one simple, magical dream come true. This being real life, I''ve found you can approximate that result by adding a serving of drained spaghetti, a ladle or two of hot sauce, and a tablespoon of the reserved cooking water to a big mixing bowl, tossing it with tongs, and then plating it. When I don''t even feel like doing that, I just ladle a couple of cups of sauce into the drained pasta in the pasta pot, stir to coat, and then plate and add a little extra sauce to the top of each serving. It''s all good.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34E7FB90E36989448D5F34097B382B90', X'38DEC47DE4790743B4021CA756555CAC', X'38DEC47DE4790743B4021CA756555CAC', 4, NULL, NULL, 2, NULL, 0, 'Whatever you do, serve with grated cheese to pass at the table. I also like to include freshly ground black pepper and some fruity olive oil for drizzling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B700ADAD1034064E96412FAFA9BDAA17', X'A64A1B490B92674898A83A6EC80E3500', X'A64A1B490B92674898A83A6EC80E3500', 0, NULL, NULL, 2, NULL, 0, 'Set a spaghetti pot half filled with well-salted water on the stove to boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4797A8C1A6B374BB407F8279FA6C95B', X'A64A1B490B92674898A83A6EC80E3500', X'A64A1B490B92674898A83A6EC80E3500', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil over medium heat in a deep 12-inch frying pan that has a lid (which you''ll use later). Add the garlic, shallot and potato along with a good sprinkle of salt and cook, stirring frequently, until the mixture is golden brown in spots but without a hint of burning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'655678467608D04B884D819BCA222678', X'A64A1B490B92674898A83A6EC80E3500', X'A64A1B490B92674898A83A6EC80E3500', 2, NULL, NULL, 2, NULL, 0, 'Add the olives or capers and the cream. Adjust heat so the cream is bubbling gently, pop a lid on it, and simmer until potatoes are just tender, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6CCDBE187666741AE048FE476975143', X'A64A1B490B92674898A83A6EC80E3500', X'A64A1B490B92674898A83A6EC80E3500', 3, NULL, NULL, 2, NULL, 0, 'While the potatoes cook, boil the spaghetti until al dente and drain, reserving 1/4 cup starchy cooking liquid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A82211696B847F459A7FE267FB48D982', X'A64A1B490B92674898A83A6EC80E3500', X'A64A1B490B92674898A83A6EC80E3500', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, strip the kale leaves from their stems and tear the leaves into large bite-sized pieces.When potatoes are just tender, heap kale leaves into pan, pour over the reserved pasta cooking water, and top with a lid. Cook until kale is wilted, tender, and reduced in volume by at least half, about 5 minutes depending on type, and stir to coat with sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03E3EB4E16AAF4CA8D80B0AEE8DE065', X'A64A1B490B92674898A83A6EC80E3500', X'A64A1B490B92674898A83A6EC80E3500', 5, NULL, NULL, 2, NULL, 0, 'Add the pasta to the frying pan, sprinkle with the lemon juice, pecorino and salt and pepper to taste, and toss everything together. Serve immediately, with more cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF775F1C28AE684F99C613E14075EC02', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 0, NULL, NULL, 2, NULL, 0, 'Fill a large lidded pot halfway with generously salted water and set on the stove to boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0175874DD05CE0459081C8484E0A4DBF', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 1, NULL, NULL, 2, NULL, 0, 'In a wide skillet, heat one tablespoon of the olive oil over medium heat. Add the onion and garlic, sprinkle with salt, and cook, stirring occasionally, until softened.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9520FFCEB69B084A9939A743E5ACE1F5', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the cauliflower in the pot of boiling water until tender, then remove with a slotted spoon. Return water to a boil and cook the spaghetti until al dente (one minute shy of package instructions).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD92DFC3F7CA547807E749B38D16B8D', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 3, NULL, NULL, 2, NULL, 0, 'Add the shiitakes to the skillet with the onions and cook, stirring frequently, until lightly browned and cooked through. Use a metal spatula to lift them from the bottom of the pan if they start to stick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58331DDC8A908845AA99093ED1980473', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 4, NULL, NULL, 2, NULL, 0, 'Pour in the capers and brine and cook for a moment, using the brine to loosen any browned bits from the bottom of the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AA3CBED017FB642BCEF8998D8F2514E', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 5, NULL, NULL, 2, NULL, 0, 'Add the chickpeas and cook until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9079E863B378114FA75B0EF01CABBCB5', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 6, NULL, NULL, 2, NULL, 0, 'Drain the pasta and toss well with the remaining tablespoon of olive oil, the skillet ingredients and the lemon juice, as well as a few tablespoons of grated pecorino or parmesan if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE26F0CFA5DCFC44A374F14BC567F56B', X'E8827DF3AFF28A41BD12BF2C3170C7B4', X'E8827DF3AFF28A41BD12BF2C3170C7B4', 7, NULL, NULL, 2, NULL, 0, 'Serve with additional grated cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48A7AA5459236246885BC0640E3CAF11', X'8CD793459FE568438D3CECBE71553EDC', X'8CD793459FE568438D3CECBE71553EDC', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center. Set aside a 9 x 13 x 2 inch baking dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64B1340A3BE40242B2FCB35CC63C5F45', X'8CD793459FE568438D3CECBE71553EDC', X'8CD793459FE568438D3CECBE71553EDC', 1, NULL, NULL, 2, NULL, 0, 'Boil the pasta until just shy of al dente -- 5 minutes if the package says 7, for example. Drain and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'857CC9FA6A190A43B077ABD1A435D00A', X'8CD793459FE568438D3CECBE71553EDC', X'8CD793459FE568438D3CECBE71553EDC', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the cheese sauce. In a medium saucepan with a heavy bottom, melt the butter over medium heat. Add the flour and whisk until well combined. Cook for one minute, until frothy and just slightly darkened in color. Pour in the milk all at once, and add the bay leaf. Raise the heat to high and bring to a boil, whisking frequently. When the mixture boils, reduce the heat and simmer for five minutes, until slightly thickened. Remove from heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53B0C92359F2BA43AA586C4890DF1184', X'8CD793459FE568438D3CECBE71553EDC', X'8CD793459FE568438D3CECBE71553EDC', 3, NULL, NULL, 2, NULL, 0, 'Remove the bay leaf and stir in the salt, nutmeg, cayenne and black peppers, and cheddar cheese. Stir until cheese is completely melted, then stir in sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B99CC49E5DE3E148B8D83947F33615D1', X'8CD793459FE568438D3CECBE71553EDC', X'8CD793459FE568438D3CECBE71553EDC', 4, NULL, NULL, 2, NULL, 0, 'Spoon drained pasta into cheese sauce and stir to coat, then tip the whole delicious mess into the baking dish. Sprinkle with the Parmigiano-Reggiano. Bake, covered with foil, for 30 minutes, then remove cover and bake 15 minutes more, until lightly browned and bubbly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A709A8A5DFA95E489AFD2290B75E2AA8', X'C90B969A380CB042A7BA0FE5075CF9D8', X'C90B969A380CB042A7BA0FE5075CF9D8', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil. Add the penne and cook until just shy of al dente, about 7 minutes if the package says 11. Drain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'465DF1F15C156748B7B78820612B8904', X'C90B969A380CB042A7BA0FE5075CF9D8', X'C90B969A380CB042A7BA0FE5075CF9D8', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, in heavy 5-quart pot, heat the olive oil over medium heat. Add the minced garlic and cook for just a moment, until aromatic.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C6DAC7D9A8B5F4B94CF8FEF59E78FD6', X'C90B969A380CB042A7BA0FE5075CF9D8', X'C90B969A380CB042A7BA0FE5075CF9D8', 2, NULL, NULL, 2, NULL, 0, 'Add the flour all at once and cook, whisking frequently, for 90 seconds. Add the milk, raise the heat to high, and bring to a boil, stirring frequently. When the milk boils, immediately lower the heat to medium-low and simmer until slightly thickened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'744E9F7CD0B89740AA42B6F5D2C5072F', X'C90B969A380CB042A7BA0FE5075CF9D8', X'C90B969A380CB042A7BA0FE5075CF9D8', 3, NULL, NULL, 2, NULL, 0, 'Off the heat, add half the mozzarella, the fontina, the provolone, and half the Parmigiano. Stir until cheese is melted. Stir in the the basil, parsley, and nutmeg. Season generously to taste with salt and pepper, then stir in the pasta.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E557648047EFB4A801DC8AC4401B329', X'C90B969A380CB042A7BA0FE5075CF9D8', X'C90B969A380CB042A7BA0FE5075CF9D8', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375° F/190° C with a rack in the center. Pour mixture into an extra-deep 9x13-inch baking dish or large braiser. Top with remaining mozzarella and Parmigiano. Bake until lightly browned and bubbly, about 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D8ADBA1ED98F942ABADA881B84407A2', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of well-salted water to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9285DA1E16AB344987420E3235D4C7D0', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, thinly slice the ramps and (or the white and light green parts of the leeks) and the asparagus crosswise from stem end to tip end. You’ll end up with lots and lots of tiny little disks plus, if you’re using ramps, plenty of thin little ribbons of the green leaves. Put the ribbons aside for a moment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1539E42966A9F64CBD9050F9500A6E29', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil and butter in a wide skillet over medium heat. Add the sliced ramps and asparagus along with a big pinch of salt and some pepper and cook, stirring occasionally, for about five minutes, until tender but not limp. Remove from the heat and cool slightly. Note: if you’re substituting leeks, they will need to cook for a bit longer than the asparagus, so add them to the pan a few minutes before.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4C817E392A789428C98B18313EFF893', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the egg, yolks and grated cheese, along with another nice pinch of salt and some pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC7F8702FD417541898F53F81F8FD84E', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 4, NULL, NULL, 2, NULL, 0, 'Cook the pasta until al dente in the boiling, salted water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02E51DA71EB03246B122E09F983C4B17', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 5, NULL, NULL, 2, NULL, 0, 'Toward the end of cooking, scoop out 1/2 cup of pasta cooking liquid and whisk it into the egg and cheese mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B32CD9343ACDB4991339F4E9B150D9A', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 6, NULL, NULL, 2, NULL, 0, 'When pasta is al dente, use tongs to transfer it to the bowl with the egg mixture. Don''t dump the hot cooking water just yet.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30C81EE6D54D7A4BA3A9A7A40F98D171', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 7, NULL, NULL, 2, NULL, 0, 'Toss well to coat pasta with the eggs. The mixture may thicken on its own to a nice silky sauce-like coating from the residual heat of the pasta, but if it needs a little help, set the bowl over the pot of hot cooking water and continue tossing just until the sauce thickens.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'043904C3C9DFAB478D130B5D41C472FA', X'9256D2FBF74EC943803669E93856F19A', X'9256D2FBF74EC943803669E93856F19A', 8, NULL, NULL, 2, NULL, 0, 'Stir in the cooked ramps and asparagus, the reserved ramp ribbons, and the chopped parsley. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88863AFF6D45BF4DBE7DFF3D67EAE954', X'77D7798420C4844FB08544977BAC5334', X'77D7798420C4844FB08544977BAC5334', 0, NULL, NULL, 2, NULL, 0, 'Set a medium pot over medium-low heat. Pour in the entire tin of anchovies, olive oil and all. Add the chopped garlic and cook for a couple of minutes, stirring and mashing the anchovies against the bottom of the pan to break them up, until the anchovies have all but melted away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12076CF1482D5F4DA3A5AFE30A33A71F', X'77D7798420C4844FB08544977BAC5334', X'77D7798420C4844FB08544977BAC5334', 1, NULL, NULL, 2, NULL, 0, 'Add the tomato paste and red pepper flakes and cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'438870286EC0C843959287B01BABC945', X'77D7798420C4844FB08544977BAC5334', X'77D7798420C4844FB08544977BAC5334', 2, NULL, NULL, 2, NULL, 0, 'Add the canned tomatoes, olives, capers, cheese rind and black pepper. Simmer the sauce, stirring occasionally, for about 20 minutes, until thickened slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BE5D5EE7AC4A545A0EECEB7F6AC58FA', X'77D7798420C4844FB08544977BAC5334', X'77D7798420C4844FB08544977BAC5334', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large pot of very well salted water to a boil and cook pasta until al dente according to package directions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B6EEB00DA6FE8479E0B7843AC7B0F56', X'77D7798420C4844FB08544977BAC5334', X'77D7798420C4844FB08544977BAC5334', 4, NULL, NULL, 2, NULL, 0, 'Drain pasta, reserving 1/2 cup of the cooking water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7799B841FC2E424A9D589D94C61686D9', X'77D7798420C4844FB08544977BAC5334', X'77D7798420C4844FB08544977BAC5334', 5, NULL, NULL, 2, NULL, 0, 'Pour drained pasta and reserved 1/2 cup of cooking water into sauce pot and toss to coat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14778A473E17924698E8EF287552E456', X'77D7798420C4844FB08544977BAC5334', X'77D7798420C4844FB08544977BAC5334', 6, NULL, NULL, 2, NULL, 0, 'Spoon into bowls and serve with grated cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16A0B0B878A53F4BB0C7C71D6904EC2C', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375° F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1693540BBBB15F4A8376AD2377552255', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 1, NULL, NULL, 2, NULL, 0, 'Halve the acorn squash. Scoop out and discard the seeds. Place cut-side down on a rimmed baking sheet (lined with parchment for easy cleanup if you like) and roast until tender, about 40 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02832155FE4FED43B948DEB325FF1E4B', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 2, NULL, NULL, 2, NULL, 0, 'Let cool slightly, then scoop out flesh into a medium bowl and mash with a fork. Stir in the ricotta cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC537D0C78259B419E9AC2FF558273D5', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 3, NULL, NULL, 2, NULL, 0, 'While the squash is roasting, make the béchamel sauce. Melt the butter over medium heat in a medium pot. Add the onion, garlic, and sage and cook, stirring occasionally, until the onion begins to soften. Add the flour and cook, stirring constantly and scraping the bottom of the pot to prevent burning, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A519F324BCCC8479BB6249C128E370D', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 4, NULL, NULL, 2, NULL, 0, 'Add the milk along with the nutmeg, salt, and pepper. Raise the heat to high and bring to a boil, stirring constantly. Then reduce the heat to maintain a brisk simmer and cook, stirring occasionally, until the mixture has thickened enough to coat the back of a spoon, about 8 minutes. Remove from the heat and stir in one cup of the Parmigiano cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F367EF7A25CE5440823847893234E9A3', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, lightly beat the eggs. Very slowly pour in about one cup of the sauce while whisking the eggs. (This tempers the eggs so they don''t scramble during the next step.) Then pour the tempered egg mixture into the pot with the sauce and stir to combine thoroughly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A76A12759D7A347863D00E8963F266B', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 6, NULL, NULL, 2, NULL, 0, 'In a 9x13x2-inch pan, assemble the lasagna. Spread 1/4 of the sauce on the bottom of the pan. Lay five lasagna noodles on top, slightly overlapping them and breaking up the fifth noodle as necessary to cover the whole bottom. Top with half the squash mixture, followed by half of the shredded mozzarella.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFDF43B1937B464499DD2E92173D57B8', X'34EA0C7706C6A44F85706095EE3045D9', X'34EA0C7706C6A44F85706095EE3045D9', 7, NULL, NULL, 2, NULL, 0, 'Repeat with a second layer of sauce, noodles, squash mixture, and mozzarella. Add a third layer of noodles and top with the remaining sauce. Sprinkle the remaining 1/2 cup Parmigiano cheese on top. Bake on the center rack until bubbly and slightly browned on top, about 35-40 minutes. Cool for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52D61A5762A4D441A7246CCC5C6ACB59', X'A1EA6EA5C413784283222ACFAE46AB80', X'A1EA6EA5C413784283222ACFAE46AB80', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CC77848A2612043BA8E7765395247E9', X'E1A46E533B3EBA459240BE6EFA1354B9', X'E1A46E533B3EBA459240BE6EFA1354B9', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with two racks close to the center. Line two rimmed sheet pans with parchment.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A616EC411421A3428AD36C0F31ECDAB4', X'E1A46E533B3EBA459240BE6EFA1354B9', X'E1A46E533B3EBA459240BE6EFA1354B9', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, lightly beat the eggs with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D3B0A510CBA448ABB4AD551FCF7FD6', X'E1A46E533B3EBA459240BE6EFA1354B9', X'E1A46E533B3EBA459240BE6EFA1354B9', 2, NULL, NULL, 2, NULL, 0, 'Add the breadcrumbs, cilantro, scallions, ginger, garlic, Tamara, toasted sesame oil, salt, pepper, and chili flakes. Use the fork to mix well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'482DD0AE94974E43A627E566838F0E86', X'E1A46E533B3EBA459240BE6EFA1354B9', X'E1A46E533B3EBA459240BE6EFA1354B9', 3, NULL, NULL, 2, NULL, 0, 'Add the pork. Using the fork, gently but thoroughly mix, without packing the ingredients together.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA19A5D47E3CEE469471DC9DF36E0983', X'E1A46E533B3EBA459240BE6EFA1354B9', X'E1A46E533B3EBA459240BE6EFA1354B9', 4, NULL, NULL, 2, NULL, 0, 'Using a two-tablespoon cookie scoop, portion the mixture into 32 balls, rolling each lightly between your hands to make it round. Arrange on prepared baking sheets.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'781241DDAA546547BBA8D43FACD94277', X'E1A46E533B3EBA459240BE6EFA1354B9', X'E1A46E533B3EBA459240BE6EFA1354B9', 5, NULL, NULL, 2, NULL, 0, 'Bake for 15 minutes, until cooked through and still very tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEBFFEB123415E4C8B046CC8CFDCB870', X'481E7F5DC0D4654EB47DB09337255BA7', X'481E7F5DC0D4654EB47DB09337255BA7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'124BD6F6BD093644929663E1CD29E5CB', X'481E7F5DC0D4654EB47DB09337255BA7', X'481E7F5DC0D4654EB47DB09337255BA7', 1, NULL, NULL, 2, NULL, 0, 'Line a rimmed half-sheet pan with aluminum foil if you like. (We sometimes do and sometimes don''t. It helps a bit with cleanup but isn''t strictly necessary.) Arrange bacon in a single layer. If you have to overlap the slices just a little, it''s okay — they''ll shrink.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1135B6BB6EB323438CCB6FA77ABCF19E', X'481E7F5DC0D4654EB47DB09337255BA7', X'481E7F5DC0D4654EB47DB09337255BA7', 2, NULL, NULL, 2, NULL, 0, 'Bake for 15-20 minutes, more or less, until crisped to your liking. Watch carefully toward the end. Cooking time will vary depending on thickness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EA152696F30E24EA625D090B828C773', X'481E7F5DC0D4654EB47DB09337255BA7', X'481E7F5DC0D4654EB47DB09337255BA7', 3, NULL, NULL, 2, NULL, 0, 'Drain on paper towels and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D5F10C2764A7C4A986699EA812CF18B', X'9299EFDB7E8F3446990EA2110451E63B', X'9299EFDB7E8F3446990EA2110451E63B', 0, NULL, NULL, 2, NULL, 0, 'Warm the olive oil over medium-high heat in a 12-inch nonstick skillet. Add shallot and garlic and cook, stirring frequently, until beginning to soften, about a minute. Stir pork into skillet, breaking up with a spatula into small pieces, and continue cooking for a few minutes until lightly browned. Add beans, tomatoes (along with all their juices), salt, chili powder, cumin, paprika, oregano and cayenne to skillet and stir to combine. Reduce heat to medium-low and simmer for five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F70774B90B0FF46B30ED256952C5427', X'9299EFDB7E8F3446990EA2110451E63B', X'9299EFDB7E8F3446990EA2110451E63B', 1, NULL, NULL, 2, NULL, 0, 'To serve, warm taco shells according to package directions. Fill each shell with some of the pork mixture and top with Cotija, pickled onions and cilantro. Serve with lime wedges.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D198EBA5F903949AA328B28301540C6', X'ADE96A857C49064E8E134D66FA2DA32F', X'ADE96A857C49064E8E134D66FA2DA32F', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, stir together the flour and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A6984E2CB35440AB374E32AAB6F4D5', X'ADE96A857C49064E8E134D66FA2DA32F', X'ADE96A857C49064E8E134D66FA2DA32F', 1, NULL, NULL, 2, NULL, 0, 'Pour in the boiling water a little at a time and mix with a spoon until incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E130B55FB120914DB02E22262177AD65', X'ADE96A857C49064E8E134D66FA2DA32F', X'ADE96A857C49064E8E134D66FA2DA32F', 2, NULL, NULL, 2, NULL, 0, 'Pour in the cold water a little at a time, continuing to mix until you have a very wet, sticky dough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF85F1D0C95A374CB15CC0E6172FCB1A', X'ADE96A857C49064E8E134D66FA2DA32F', X'ADE96A857C49064E8E134D66FA2DA32F', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle plenty of flour onto a work surface and your hands. Scrape dough from bowl onto work surface and knead, adding more flour as necessary to prevent sticking, until dough is smooth. (See video for visual cues here and throughout the recipe.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCEC296F40760640BC8B3EE5B0561469', X'ADE96A857C49064E8E134D66FA2DA32F', X'ADE96A857C49064E8E134D66FA2DA32F', 4, NULL, NULL, 2, NULL, 0, 'Place dough in a clean bowl, cover with a clean kitchen towel, and let rest while you make the filling.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A1D82465D0755469B1AF99A5EB592A9', X'432D41817B4BDB478032377BC36AB4B0', X'432D41817B4BDB478032377BC36AB4B0', 0, NULL, NULL, 2, NULL, 0, 'The night before serving, place a wire rack on a rimmed baking sheet and lay pork chops on top. In a small bowl, combine salt and sugar and rub evenly over chops. Place, uncovered, in refrigerator.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BAE9536EBDE254D854C401A7CE40508', X'432D41817B4BDB478032377BC36AB4B0', X'432D41817B4BDB478032377BC36AB4B0', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 250°F with a rack in the center. Place pan in oven as-is and bake chops until an instant meat thermometer reads 110°F, which should take about 30 minutes. Start checking after 25 minutes. (I need you to trust this process -- don''t raise the oven temperature, and don''t be tempted to cook the chops past 110° at this stage. It will all work out in the end.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A99D7F85652C9B43B386DA4C10883C82', X'432D41817B4BDB478032377BC36AB4B0', X'432D41817B4BDB478032377BC36AB4B0', 2, NULL, NULL, 2, NULL, 0, 'Heat two tablespoons of the oil in a 12-inch cast iron skillet over high heat. Turn on an exhaust fan — things could get exciting.) When the oil begins to smoke, add two pork chops to the pan. Sear for about one minute on the first side without tinkering — chop should be deep golden-brown. Then flip and sear a minute or so on the other side until beautifully golden. Finally, stack the chops and pick them up with tongs, holding the “fat cap” (the fatty side of the chop) against the bottom of the pan to sear. Remove chops to a plate, pour off used oil (but don''t wipe pan), and cook remaining chops two at a time in the same way, starting with fresh oil. (Or use multiple skillets).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B762FD6A377A347A9F1F9CE2D4505A8', X'432D41817B4BDB478032377BC36AB4B0', X'432D41817B4BDB478032377BC36AB4B0', 3, NULL, NULL, 2, NULL, 0, 'To make the sauce, pour the used cooking oil out of the pork chop pan and discard, but don''t wipe or wash the pan. You want to keep all those good cooked-on bits. Set the pan over low heat and melt the butter. Add shallot, thyme and sage and cook, stirring, for just a minute until the shallot is tender but not too brown. Stir in flour, raise heat to medium, and cook for a minute or two, until very frothy. Stir in wine and stock (along with any accumulated juices from the chops) and sprinkle with salt and pepper. Cook until thickened to a gravy consistency, about 5 minutes. Off the heat, stir in parsley. Spoon sauce over plated pork chops just before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9888D7F6AB2774A9730785EC6B60DA0', X'1087ECF54777F246AE417016E9A4AA37', X'1087ECF54777F246AE417016E9A4AA37', 0, NULL, NULL, 2, NULL, 0, 'Cut the bacon slices in half and place in a cold frying pan. Set over medium heat and cook, turning occasionally, until crisped to your liking. Drain on paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3AE869116F4D44CBF84B8022A4A3B1A', X'1087ECF54777F246AE417016E9A4AA37', X'1087ECF54777F246AE417016E9A4AA37', 1, NULL, NULL, 2, NULL, 0, 'If you like, lightly toast the bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'928FCC2170925A4890CFAF7E0B73670E', X'1087ECF54777F246AE417016E9A4AA37', X'1087ECF54777F246AE417016E9A4AA37', 2, NULL, NULL, 2, NULL, 0, 'Layer the turkey slices onto one slice of the bread. Top with an even layer of bacon.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'898020CDE7AC464F84198D2D02D2A9C5', X'1087ECF54777F246AE417016E9A4AA37', X'1087ECF54777F246AE417016E9A4AA37', 3, NULL, NULL, 2, NULL, 0, 'Onto the other slice of bread, spread the mayo and then the sriracha. Sprinkle with scallions. Layer on the avocado slices. Sprinkle the avocado with the salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DAE86A0BAC36E48A2341FB63AC64E2F', X'1087ECF54777F246AE417016E9A4AA37', X'1087ECF54777F246AE417016E9A4AA37', 4, NULL, NULL, 2, NULL, 0, 'Close the sandwich by tipping the avocado-topped slice of bread onto the turkey-topped slice. Cut in half and serve right away.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0624B134E593564A9FE44BF52181C658', X'8160A50368BFC94390A7DD80B99DF199', X'8160A50368BFC94390A7DD80B99DF199', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, warm the olive oil over medium-high heat. Add the shallot, leek, salt, and pepper and cook until beginning to soften, about two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E1895DDF560B4E98DBC5D2B654D9A8', X'8160A50368BFC94390A7DD80B99DF199', X'8160A50368BFC94390A7DD80B99DF199', 1, NULL, NULL, 2, NULL, 0, 'Add rice, stock, water, salt, pepper, and herbs de Provence. If you''re using white rice, add the carrot and celery now. If your rice cooks for longer, hold off until later.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C222E917FEAB5545A656483579CC801C', X'8160A50368BFC94390A7DD80B99DF199', X'8160A50368BFC94390A7DD80B99DF199', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then reduce heat, cover, and simmer for as long as suggested on the rice packaging. (For white rice, about 20 minutes. For brown and wild rice, about 45 minutes.) With 20 minutes left on the rice cooking time, add the carrot and celery.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF0B1149AB1BC44E9D7F710B01475B32', X'8160A50368BFC94390A7DD80B99DF199', X'8160A50368BFC94390A7DD80B99DF199', 3, NULL, NULL, 2, NULL, 0, 'While the soup simmers, make the beurre manié. In a small bowl, combine the room-temperature butter and the flour. Use a fork to mash it all together to form a thick paste. Keep going until it looks homogenous, with no dry flour remaining.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC5D9C3AD448DE45A7CF83C8569D0927', X'8160A50368BFC94390A7DD80B99DF199', X'8160A50368BFC94390A7DD80B99DF199', 4, NULL, NULL, 2, NULL, 0, 'When rice is cooked, add turkey, cream (if using), and beurre manié. Stir very well until the beurre manié is fully incorporated.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8DBC8131F577B4A8A9FDAE3B7B8264E', X'8160A50368BFC94390A7DD80B99DF199', X'8160A50368BFC94390A7DD80B99DF199', 5, NULL, NULL, 2, NULL, 0, 'Raise heat again to bring to a boil. Then reduce heat and simmer, uncovered, for 8 minutes to thicken the soup and cook out the flour. Ladle into bowls and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F36788002D22438FE7F7ABCEE8D2BE', X'242367A70495E84BBF263819D609A3E2', X'242367A70495E84BBF263819D609A3E2', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in a medium pot over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D61B1B3EC7CEB40BBABFB32757967C8', X'242367A70495E84BBF263819D609A3E2', X'242367A70495E84BBF263819D609A3E2', 1, NULL, NULL, 2, NULL, 0, 'Add the minced shallot and cook for a minute or so, whisking once or twice. Place the turkey neck into the pot and continue cooking, turning occasionally, until lightly browned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'222F8F00A2A08643A1622A4B481B796D', X'242367A70495E84BBF263819D609A3E2', X'242367A70495E84BBF263819D609A3E2', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and cook, whisking, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01FA9678E31F864BA1118F5663E24BBE', X'242367A70495E84BBF263819D609A3E2', X'242367A70495E84BBF263819D609A3E2', 3, NULL, NULL, 2, NULL, 0, 'Pour in the stock and whisk well. Whisk in soy sauce, marmite or vegemite, and a few grinds of pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C04D93FBF2A38D4983DE88B5CA7898BB', X'242367A70495E84BBF263819D609A3E2', X'242367A70495E84BBF263819D609A3E2', 4, NULL, NULL, 2, NULL, 0, 'Bring stock to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40382D664DA7DF4993127A499F808BF4', X'242367A70495E84BBF263819D609A3E2', X'242367A70495E84BBF263819D609A3E2', 5, NULL, NULL, 2, NULL, 0, 'Reduce heat and simmer for one hour. You can control the thickness by covering the pot for part of the cooking time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E65911155D7E546993944FC93E25906', X'242367A70495E84BBF263819D609A3E2', X'242367A70495E84BBF263819D609A3E2', 6, NULL, NULL, 2, NULL, 0, 'Taste for seasoning and add salt and additional pepper if desired. Remove turkey neck before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2166512555A4864EA3CCBFFAC616219D', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F with the top rack in the lower third of the oven.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD0EA278CCC88849BA27EA80693312E7', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 1, NULL, NULL, 2, NULL, 0, 'Remove the neck and the bag of innards and giblets from inside the cavities. Sometimes they’re both in the large main cavity, but sometimes the bag is in the smaller neck cavity around the other side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CFAB5183E83CB4E85FF0EC0BCE3A537', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 2, NULL, NULL, 2, NULL, 0, 'If there are any pin feathers still on the bird, pluck them out.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B9519B2C4CE7B408889B958218A25AA', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 3, NULL, NULL, 2, NULL, 0, 'Place the turkey breast-side up into a large roasting pan. No racks or fancy positions necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77FC17CC43A4084B81EFCF7FE074E42F', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 4, NULL, NULL, 2, NULL, 0, 'Place butter into a small bowl. Strip one teaspoon of leaves from the thyme and add it to the bowl, reserving the rest. Add the salt, pepper, and lemon zest, and mix it all together thoroughly with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F63543C8FC559248975D3242EF4A03C3', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 5, NULL, NULL, 2, NULL, 0, 'Use your fingers to carefully separate the turkey skin from the breast meat to create pockets. Tuck some of the compound butter into the pockets and distribute it between the skin and the breast meat by wriggling it in as best as possible. Spread the remaining compound butter all over the outside of the turkey.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17D2309B19B07F4C987EF8AED155FB46', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 6, NULL, NULL, 2, NULL, 0, 'Place the lemon and onion pieces into the large cavity of the bird along with a the remaining thyme sprigs.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB4573DC93F7FB44A8E5B7B2EA76B28E', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 7, NULL, NULL, 2, NULL, 0, 'To truss, tuck the tip of each wing under the bird and tie the tips of the drumsticks together with a small piece of twine if this isn’t already done.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56218F3336D9FE43905C85C965A30E02', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 8, NULL, NULL, 2, NULL, 0, 'Place turkey into oven and roast to your desired level of doneness. For me this means that an instant read thermometer inserted into the deepest part of the breast should read 155-160°F. Refer to the notes section below to learn more. A 10 pound turkey will probably need between 2 and 2 1/2 hours. A 14 pound turkey will probably need between 2 1/2 and 3 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C66B74EB9CBEF54ABB8C1EDE4AF86ECA', X'DD820E7E7ACCFB4B82A26A736419C4AE', X'DD820E7E7ACCFB4B82A26A736419C4AE', 9, NULL, NULL, 2, NULL, 0, 'Remove turkey from oven and let rest for at least 30 minutes before carving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A047B9D47B63B4A9F09D03BDB8E8DAF', X'B37435B670CA2343BDAB443B2552A08B', X'B37435B670CA2343BDAB443B2552A08B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'554FE1C869536544954A75F6442724EB', X'B37435B670CA2343BDAB443B2552A08B', X'B37435B670CA2343BDAB443B2552A08B', 1, NULL, NULL, 2, NULL, 0, 'Melt butter in a 3.5-quart braiser over medium-high heat. (See note 9 below.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CA5090F1D45BC4C995AD590E0F6999F', X'B37435B670CA2343BDAB443B2552A08B', X'B37435B670CA2343BDAB443B2552A08B', 2, NULL, NULL, 2, NULL, 0, 'Add shallot, carrots, celery, and mushrooms. Cook, stirring from time to time, until softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EB6B6B2D25FCF49942AC18959CC3616', X'B37435B670CA2343BDAB443B2552A08B', X'B37435B670CA2343BDAB443B2552A08B', 3, NULL, NULL, 2, NULL, 0, 'Add flour and cook, stirring constantly, for one minute. Ensure that flour coats all vegetables somewhat evenly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'438ED529B28F4346B26CC0DA56DB5ACC', X'B37435B670CA2343BDAB443B2552A08B', X'B37435B670CA2343BDAB443B2552A08B', 4, NULL, NULL, 2, NULL, 0, 'Pour in stock, the half cup of cream, salt, pepper, thyme, and sage and stir very well. Add potatoes. Allow liquid to come to a boil, stirring frequently. Then reduce heat and simmer until thickened, about 5 minutes, stirrinng from time to time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF5208C55713C458C20CE3859E37FA5', X'B37435B670CA2343BDAB443B2552A08B', X'B37435B670CA2343BDAB443B2552A08B', 5, NULL, NULL, 2, NULL, 0, 'Stir in peas, onions, and cooked turkey. Remove from heat. If you''ve cooked the filling in an oven-safe braiser, it''s ready to top with the crust. Otherwise, transfer the filling to your baking dish now. If you like, you can let the filling cool for a while to make the pastry easier to work with — but you don''t have to.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B12D16D09FE143A93B22FC894DE552', X'C01DDF9A57217947B653A77A1B490974', X'C01DDF9A57217947B653A77A1B490974', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients into a 6- or 8-quart Instant Pot. Pour in filtered water, stopping about a half inch below the max fill line.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21E910903508924D8C9090623FB912A3', X'C01DDF9A57217947B653A77A1B490974', X'C01DDF9A57217947B653A77A1B490974', 1, NULL, NULL, 2, NULL, 0, 'Place lid on Instant Pot and set vent to sealing position.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07ECC075E0826146955B162719FCC8D2', X'C01DDF9A57217947B653A77A1B490974', X'C01DDF9A57217947B653A77A1B490974', 2, NULL, NULL, 2, NULL, 0, 'Set Instant Pot to manual (high pressure) for 120 minutes. (Protip: You can use the down arrow to make this faster since the minutes `wrap back around` to 120 after 0.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08309EFBBD6EF347AB9FECD07ACAC655', X'C01DDF9A57217947B653A77A1B490974', X'C01DDF9A57217947B653A77A1B490974', 3, NULL, NULL, 2, NULL, 0, 'The pot will take a while to come up to pressure and then will begin counting down the cooking time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3473C08F01355E4D8B38F83A6986FEB2', X'C01DDF9A57217947B653A77A1B490974', X'C01DDF9A57217947B653A77A1B490974', 4, NULL, NULL, 2, NULL, 0, 'When the cooking time ends, let the pressure release naturally. This may take 30 minutes or so.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'420804CE7653554BADA05402248F8847', X'C01DDF9A57217947B653A77A1B490974', X'C01DDF9A57217947B653A77A1B490974', 5, NULL, NULL, 2, NULL, 0, 'Strain the broth through a fine-mesh sieve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D657DABE3ED0C54FAED33A884781E1A6', X'C01DDF9A57217947B653A77A1B490974', X'C01DDF9A57217947B653A77A1B490974', 6, NULL, NULL, 2, NULL, 0, 'Store broth in airtight containers (such as mason jars) in a cold fridge for up to a week. Or freeze (in ice cube trays, mason jars, or other reusable containers) for up to 6 months. After chilling, a small layer of fat should rise to the top. You can skim this off if you like, and either use it for cooking or discard it. Or you can stir it back into the broth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD0EC74E5FC2AB44A3269C2FA31676B4', X'5F18BCFF453B6E4F85DB3F6AC2342B65', X'5F18BCFF453B6E4F85DB3F6AC2342B65', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center. Line a rimmed baking sheet with parchment or a silpat, or spray with cooking spray.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C371287D2AEC84CB324BEF244092E9C', X'5F18BCFF453B6E4F85DB3F6AC2342B65', X'5F18BCFF453B6E4F85DB3F6AC2342B65', 1, NULL, NULL, 2, NULL, 0, 'Into a large bowl place the bread crumbs, milk, onion, garlic, parsley, cheese, fennel seed, oregano, salt, egg, and pepper. Mix together well with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEA2D3EBEC428C4E8201B43DB82A09CE', X'5F18BCFF453B6E4F85DB3F6AC2342B65', X'5F18BCFF453B6E4F85DB3F6AC2342B65', 2, NULL, NULL, 2, NULL, 0, 'Add ground turkey and use fork to mix well but without overworking.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EDDE9ADCF7D0645861B4767BCCB5BAC', X'5F18BCFF453B6E4F85DB3F6AC2342B65', X'5F18BCFF453B6E4F85DB3F6AC2342B65', 3, NULL, NULL, 2, NULL, 0, 'Use two spoons or a small ice cream scoop to portion the mixture into 12 roughly equal parts. Then roll each meatball gently between your palms to make it spherical.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A09ABC1A0FA8744B9181BCD66C797C8', X'5F18BCFF453B6E4F85DB3F6AC2342B65', X'5F18BCFF453B6E4F85DB3F6AC2342B65', 4, NULL, NULL, 2, NULL, 0, 'Space the meatballs evenly on the prepared baking sheet. Bake for 10 minutes, then flip with a spatula and bake 5 minutes more, until just cooked through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C93F234C4F20F848B2E2E2DED2BA7B27', X'5F18BCFF453B6E4F85DB3F6AC2342B65', X'5F18BCFF453B6E4F85DB3F6AC2342B65', 5, NULL, NULL, 2, NULL, 0, 'Heat tomato sauce in a medium pan, then add meatballs and simmer for a minute or two.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32329CFB9FD55540AE2CFEB1F9E397E8', X'D072D0DCB9450742ACF4CCDC36367C74', X'D072D0DCB9450742ACF4CCDC36367C74', 0, NULL, NULL, 2, NULL, 0, 'Arrange the zoodles on a double layer of paper towels. Sprinkle with a little bit of salt. Cover with a double layer of paper towels and press gently. Leave while you make the sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E68D8C7432589444AC5A374A386DDB7F', X'D072D0DCB9450742ACF4CCDC36367C74', X'D072D0DCB9450742ACF4CCDC36367C74', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch frying pan, heat the oil over medium-high heat. Add the onion, garlic and turkey and sprinkle with 1 teaspoon salt. Cook, stirring from time to time to break up the meat into bite-sized pieces, until turkey is cooked through and nicely browned in spots, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E58EBBD57300AD429298074A405DE8EB', X'D072D0DCB9450742ACF4CCDC36367C74', X'D072D0DCB9450742ACF4CCDC36367C74', 2, NULL, NULL, 2, NULL, 0, 'Add tomatoes, fennel, basil, thyme, oregano and red pepper flakes to pan and stir to incorporate. Reduce heat and simmer for about 10 minutes, until nice and thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A1C0CF2F502664F9C6EEC97BFFA8B25', X'D072D0DCB9450742ACF4CCDC36367C74', X'D072D0DCB9450742ACF4CCDC36367C74', 3, NULL, NULL, 2, NULL, 0, 'When ready to serve, give the zoodles a gentle squeeze between paper towels to remove as much moisture as possible. Warm the remaining teaspoon olive oil in a second frying pan. Add zoodles to frying pan and cook, tossing with tongs or a rubber spatula, for about a minute. They should retain a nice bite but not look raw.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FFB8ECEB4006F40B39E190A7C2877A9', X'D072D0DCB9450742ACF4CCDC36367C74', X'D072D0DCB9450742ACF4CCDC36367C74', 4, NULL, NULL, 2, NULL, 0, 'Divide zoodles among four bowls and top with sauce. Garnish with pepper, cheese and parsley, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7999CFBEA564F4E9971EAE10CB3F273', X'791C6CFB1DC7B94BAFB4801DBD910BD2', X'791C6CFB1DC7B94BAFB4801DBD910BD2', 0, NULL, NULL, 2, NULL, 0, 'Shred zucchini or yellow squash on the large holes of a box grater or in a food processor with the shredding disc.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9208E80A98E864C989AE35042F2B47C', X'791C6CFB1DC7B94BAFB4801DBD910BD2', X'791C6CFB1DC7B94BAFB4801DBD910BD2', 1, NULL, NULL, 2, NULL, 0, 'Set a 10- or 12-inch nonstick skillet over medium heat. Melt butter (or heat olive oil). Add shallot, garlic, shredded squash, salt, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69887FEB3D3F5146AEE1B4788F6F96D3', X'791C6CFB1DC7B94BAFB4801DBD910BD2', X'791C6CFB1DC7B94BAFB4801DBD910BD2', 2, NULL, NULL, 2, NULL, 0, 'Cook, stirring frequently, for about 20 minutes, until reduced to a soft, spreadable, silky consistency. You may need to lower the heat about halfway through to prevent browning.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30F5E8852DB0D74DB8289C44D869573E', X'791C6CFB1DC7B94BAFB4801DBD910BD2', X'791C6CFB1DC7B94BAFB4801DBD910BD2', 3, NULL, NULL, 2, NULL, 0, 'Spread is ready to use right away, or let cool and transfer to an airtight container.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98E83FA964594940A8E165985E045681', X'4935FBAAEF3590439A3A2D3C304D5620', X'4935FBAAEF3590439A3A2D3C304D5620', 0, NULL, NULL, 2, NULL, 0, 'Cook the orzo in well salted water according to package directions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9182E97183DB1348B3A8B6283D5EFAEB', X'4935FBAAEF3590439A3A2D3C304D5620', X'4935FBAAEF3590439A3A2D3C304D5620', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a 12-inch nonstick skillet over medium heat. Add the onions, garlic, and a big pinch of salt and cook, stirring frequently, until the onion is beginning to soften.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AEEB1249FB1A14EA285036A38A53F8E', X'4935FBAAEF3590439A3A2D3C304D5620', X'4935FBAAEF3590439A3A2D3C304D5620', 2, NULL, NULL, 2, NULL, 0, 'Add the shredded zucchini and the thyme and raise heat to high. Cook, stirring occasionally, until the squash releases its juices and they cook off. The zucchini will reduce in size by at least half and may begin to brown slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D386ABA665560C4E817B3C80B6536B62', X'4935FBAAEF3590439A3A2D3C304D5620', X'4935FBAAEF3590439A3A2D3C304D5620', 3, NULL, NULL, 2, NULL, 0, 'Turn off the heat and immediately add the drained orzo, lemon juice and zest, and cheese, along with salt and pepper to taste. Stir well but gently to combine. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5367F39CE63CC9449FAE9552EEFB91BF', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F with a rack in the center. Butter or spray a 9-inch round cake pan (I have one like this, or try one of these) with cooking spray. Line the bottom of the pan with a circle of parchment paper and butter or spray that too.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB46BE819EAEFC4193E4DB4667FD20A6', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 1, NULL, NULL, 2, NULL, 0, 'In a strainer, toss zucchini with 1 cup of the sugar and let sit for 10 minutes to drain. Use your hands to squeeze out all the liquid you can.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C79288B57EB0684C9FA38246988D0FB2', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, beat eggs with a fork, then beat in zucchini and oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DDB46CE557AA640899CAC505BCF315A', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle in the flour, remaining 1 2/3 cups sugar, cocoa powder, baking soda, baking powder, cinnamon and salt. Use the fork to stir the dry ingredients together a bit and then incorporate into the wet ingredients, mixing gently until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA0D8635BBBF22428C080E7F7589DA2F', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 4, NULL, NULL, 2, NULL, 0, 'Stir in chocolate chips.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E15862AAB48FE8428998DB5F720745F8', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 5, NULL, NULL, 2, NULL, 0, 'Scrape batter into pan and spread evenly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13003B9D76C5314A83539FD2147C31C3', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 6, NULL, NULL, 2, NULL, 0, 'Bake for 50 to 60 minutes, until the center doesn''t jiggle and a tester comes out clean unless you hit a chocolate chip.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'448497F808D85A488B75286E648E5DD6', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 7, NULL, NULL, 2, NULL, 0, 'Cool in pan on a rack for at least 40 minutes before removing from pan to cool completely.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15BBE985D74F52458D1E102C9F899FF2', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', X'820E07ECAE03514CAD5CB2B5BEF0EEF5', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle with powdered sugar and serve at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC6C3E95AD60C347AA62FB98D58685FD', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 0, NULL, NULL, 2, NULL, 0, 'Have egg and milk at room temperature for optimum pancake fluffiness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CC0103D7EEAD642AD3EC927DD6935C5', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 1, NULL, NULL, 2, NULL, 0, 'Shred the zucchini on the large holes of a box grater or in a food processor with the shredding disk. Place the shredded zucchini in a colander in the sink and sprinkle with the salt. Toss to combine. Let drain for 30 minutes, then pick up by the handful and squeeze out as much moisture as possible. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02A83EFEA740FD47B70AA32D31AF7040', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 2, NULL, NULL, 2, NULL, 0, 'Into a large mixing bowl, sift together the flour, sugar, baking powder, cinnamon, and nutmeg.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F750A4F75D3F44AB179A0730AFE9C26', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 3, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the milk, egg, and vanilla. Then whisk in the butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73599813F05B3544B84B9D5C62186ED4', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 4, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients over the dry ingredients and stir together just until combined. Some lumps are fine. Don''t overmix. Then gently fold in the zucchini.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A257CD095FF4B845A5F359839E9B8E90', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 5, NULL, NULL, 2, NULL, 0, 'Let batter rest for five minutes before proceeding.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFB1478E72DBF64C9B0D110C6110EF31', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 6, NULL, NULL, 2, NULL, 0, 'Set a griddle or heavy, nonstick pan over medium heat. Coat lightly with safflower oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBDE2FE3DA8C324493323A9B58DF98F4', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 7, NULL, NULL, 2, NULL, 0, 'When griddle is hot, scoop batter by the approximate 1/4-cupful onto the skillet, leaving plenty of room between pancakes. Cook until the edges of the underside are browned and the top is covered with bubbles that have begun to burst and stay hollow. Flip and cook until the underside is browned. Repeat with remaining batter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C2D5DFEF11B6644BEED5FF1360B40AE', X'4C6919B203385344A006681499FE4246', X'4C6919B203385344A006681499FE4246', 8, NULL, NULL, 2, NULL, 0, 'Pancakes are best served right as they come off the griddle, though you can keep them warm in a 200°F oven while you cook the rest if you insist.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71E300180ABDFE42B8112CC660B2A01A', X'26EFCB5A19E9C54D862577D79FF74CD5', X'26EFCB5A19E9C54D862577D79FF74CD5', 0, NULL, NULL, 2, NULL, 0, 'Trim the ends off the zucchini and cut into noodles according to the instructions on your spiralizer. Line two rimmed sheet pans with thick layers of paper towels. Spread half of the zucchini noodles onto each pan and sprinkle with the salt. Cover with another thick layer of paper towels and press gently. Leave noodles to dry while you prepare the rest of the dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FEB844D1C31314CBEF44D526A9E9699', X'26EFCB5A19E9C54D862577D79FF74CD5', X'26EFCB5A19E9C54D862577D79FF74CD5', 1, NULL, NULL, 2, NULL, 0, 'Warm olive oil over medium heat in a very large pan or wide, shallow pot. (A 5-quart Dutch oven, a 12-inch frying pan with high sides, or even a wok would all work well.) Add shallot, garlic, and capers, and cook, stirring frequently, for two minutes. Stir in tomatoes and cook, stirring occasionally, until most of them have burst.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CD1C5C005750242B8743F345E06464E', X'26EFCB5A19E9C54D862577D79FF74CD5', X'26EFCB5A19E9C54D862577D79FF74CD5', 2, NULL, NULL, 2, NULL, 0, 'While tomatoes cook, cut the kernels from the corn cobs. Add corn kernels and chickpeas to pan and cook for about two minutes, until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D1C76E74FB1464B9C7DFD0044524CAB', X'26EFCB5A19E9C54D862577D79FF74CD5', X'26EFCB5A19E9C54D862577D79FF74CD5', 3, NULL, NULL, 2, NULL, 0, 'Pat zucchini noodles dry with the paper towels and stir noodles into the vegetable mixture in the pan. (Tongs work really well if you have them.) Cook until noodles are crisp-tender, just a couple of minutes. The tomatoes and zucchini will create a bit of delicious sauciness in the bottom of the pan. Stir this to coat the noodles. Remove pan from heat and divide noodles and saucy vegetables among wide, shallow serving bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E62C10F43AA3B14BA54388EC83F957E9', X'26EFCB5A19E9C54D862577D79FF74CD5', X'26EFCB5A19E9C54D862577D79FF74CD5', 4, NULL, NULL, 2, NULL, 0, 'Cut slices of Bayonne Ham into long, thin strips, about four per slice. Divide strips among serving bowls. The residual heat from the noodles will warm the ham just enough to be beautifully tender and flavorful, without cooking it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F660374A342422478710FAA6627C9408', X'26EFCB5A19E9C54D862577D79FF74CD5', X'26EFCB5A19E9C54D862577D79FF74CD5', 5, NULL, NULL, 2, NULL, 0, 'Garnish each bowl with some basil, freshly ground black pepper, and grated cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2C4CB134F316B4FAA57914A405977D0', X'FC9DA77C6E08794898F10EA375B914A7', X'FC9DA77C6E08794898F10EA375B914A7', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Line a baking dish with aluminum foil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7038EDD1C292E40994CC8E4A2195EB3', X'FC9DA77C6E08794898F10EA375B914A7', X'FC9DA77C6E08794898F10EA375B914A7', 1, NULL, NULL, 2, NULL, 0, 'Make the chicken filling: In a medium bowl, combine mustard, oil, chili powder, and salt. Add chicken breasts and toss to coat. Place in lined baking dish and bake for 30 minutes or until cooked through and tender. Allow chicken to cool slightly, then shred the breasts using two forks. Set meat aside, along with any juices from the baking dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6304BF68DCB4E94ABBD69707B88DE5BD', X'FC9DA77C6E08794898F10EA375B914A7', X'FC9DA77C6E08794898F10EA375B914A7', 2, NULL, NULL, 2, NULL, 0, 'Make the black bean filling: Coat a large skillet with olive oil, place over medium heat, and sauté onion until soft, 5 to 8 minutes. Add zucchini and continue to sauté, stirring occasionally, until zucchini is tender and browned, about 6 minutes more. Add garlic, beans, chili powder, cumin, and salt, and cook for five minutes. Transfer to a bowl and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21CC9A66FCB4904A9FAC08FF2B6523B5', X'FC9DA77C6E08794898F10EA375B914A7', X'FC9DA77C6E08794898F10EA375B914A7', 3, NULL, NULL, 2, NULL, 0, 'Make the sauce: Wipe out the skillet with a paper towel. Melt butter over medium heat. Add garlic and jalapeños and cook until soft but not brown, about 4 minutes. Add flour and cook, stirring, for 1 minute. Add stock, raise heat to high, and bring to a boil. Reduce heat and simmer until the liquids have reduced and the sauce has become opaque, about 5 minutes. Stir in sour cream, chiles, cumin, chili powder, salt, and cayenne. Coat the bottoms of two 9×13-inch baking dishes with some of the sauce. Reserve the rest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E2184FA8F7E544495313E48F4E7E30A', X'FC9DA77C6E08794898F10EA375B914A7', X'FC9DA77C6E08794898F10EA375B914A7', 4, NULL, NULL, 2, NULL, 0, 'Assemble the enchiladas: Fill 8 tortillas with the chicken and 8 with the black bean and zucchini mixture. Top the filling with a spoonful of shredded cheese and a sprinkle of cilantro. Roll up tortillas and place seam-side down in the baking dishes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD716E2CEB550E48802CC5213F45A264', X'FC9DA77C6E08794898F10EA375B914A7', X'FC9DA77C6E08794898F10EA375B914A7', 5, NULL, NULL, 2, NULL, 0, 'Pour remaining cream sauce evenly over enchiladas and sprinkle with remaining cheese. Bake for 30 minutes, or until sauce is bubbling and cheese is beginning to brown. Garnish with the extra cilantro and the scallions and serve with lime wedges.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E07B9ADAF4BA2429E71584472F18B8C', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', 0, NULL, NULL, 2, NULL, 0, 'Shred the zucchini with the shredding disc of a food processor or on the large holes of a box grater. Place the shredded zucchini in a colander in the sink or over a bowl and sprinkle with the salt. Toss to combine. Let drain while you prepare the rest of the ingredients.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BD22DF7C305AE42B7D85721D4D83836', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', 1, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a large bowl and scramble lightly. Cut the kernels from the corn cobs and add the kernels to the bowl along with the diced onion, sliced scallions, chopped cilantro, flour, grated cheese and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB4671AE6DD26248B6AB618E3ACBB2B6', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', 2, NULL, NULL, 2, NULL, 0, 'Pick up the shredded zucchini in small handfuls and squeeze out and discard as much liquid as you can. Add the zucchini to the bowl. Mix well to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B193E4624A9AA04684C5EE104AC1FB8B', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', 3, NULL, NULL, 2, NULL, 0, 'Pour the oil into a large frying pan to a depth of about 1/4-inch. Heat the oil over medium-high heat until hot but not smoking. Drop batter by the 1/4-cupful into the pan and press lightly to form disc-shaped fritters. Cook in batches without crowding (about 3 or 4 at a time, depending on the size of your pan) until golden brown on the underside (about 2 minutes) and then flip and cook until golden brown on the second side. Remove to paper towels to drain. Add a bit more oil between batches if necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC1810872FCD0841B26ABF0DFDA31122', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', X'7C8CDFD12561F947A94FB9C5AAD3F9EA', 4, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature with sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CB42964FF3FD840B8BD99914A6A47E3', X'40A6BEDD2939624396454A2C56F334B5', X'40A6BEDD2939624396454A2C56F334B5', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350° F with a rack in the center. Grease a 9 x 5-inch (8-cup) loaf pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13E43439AC0D6543B462C7223FDBED50', X'40A6BEDD2939624396454A2C56F334B5', X'40A6BEDD2939624396454A2C56F334B5', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the flour, baking powder, baking soda, salt, and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B3AF8861CCF94D8F9F301A626009C4', X'40A6BEDD2939624396454A2C56F334B5', X'40A6BEDD2939624396454A2C56F334B5', 2, NULL, NULL, 2, NULL, 0, 'Add the zucchini, cheeses, shallot, and herbs and toss to separate and coat with flour.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C2516CEDCB82540995D5D6E64BFBBA5', X'40A6BEDD2939624396454A2C56F334B5', X'40A6BEDD2939624396454A2C56F334B5', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl or glass measuring cup, whisk together the eggs, buttermilk, and melted butter.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32E6164765B038459024411E7B9B1A35', X'40A6BEDD2939624396454A2C56F334B5', X'40A6BEDD2939624396454A2C56F334B5', 4, NULL, NULL, 2, NULL, 0, 'Pour wet ingredients into the bowl with the flour mixture and mix until just combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A2E8D67497234F97C7EDFAA8D65596', X'40A6BEDD2939624396454A2C56F334B5', X'40A6BEDD2939624396454A2C56F334B5', 5, NULL, NULL, 2, NULL, 0, 'Bake for 55-60 minutes, until a cake tester inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A25D168B9054E42BF1A937BBEF51BAA', X'40A6BEDD2939624396454A2C56F334B5', X'40A6BEDD2939624396454A2C56F334B5', 6, NULL, NULL, 2, NULL, 0, 'Let cool in the pan on a rack for 5 to 10 minutes before unmolding to cool completely on a rack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FBEF218E5702842A8521461AD2E123C', X'246376B362983845B6FF735E9298013E', X'246376B362983845B6FF735E9298013E', 0, NULL, NULL, 2, NULL, 0, 'Arrange the zoodles on a double layer of paper towels. Sprinkle with a little bit of salt. Cover with a double layer of paper towels and press gently. Leave while you make the sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E29318505A8E41A8999F065A9FAC60', X'246376B362983845B6FF735E9298013E', X'246376B362983845B6FF735E9298013E', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch frying pan, heat the oil over medium-high heat. Add the onion, garlic and turkey and sprinkle with 1 teaspoon salt. Cook, stirring from time to time to break up the meat into bite-sized pieces, until turkey is cooked through and nicely browned in spots, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5254BD48BD4383479BDFE5E38C6FCE9C', X'246376B362983845B6FF735E9298013E', X'246376B362983845B6FF735E9298013E', 2, NULL, NULL, 2, NULL, 0, 'Add tomatoes, fennel, basil, thyme, oregano and red pepper flakes to pan and stir to incorporate. Reduce heat and simmer for about 10 minutes, until nice and thick.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE9857287FD8CB49B974152A0B831C85', X'246376B362983845B6FF735E9298013E', X'246376B362983845B6FF735E9298013E', 3, NULL, NULL, 2, NULL, 0, 'When ready to serve, give the zoodles a gentle squeeze between paper towels to remove as much moisture as possible. Warm the remaining teaspoon olive oil in a second frying pan. Add zoodles to frying pan and cook, tossing with tongs or a rubber spatula, for about a minute. They should retain a nice bite but not look raw.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98FA3F67A89CD94390613D06D76E4F57', X'246376B362983845B6FF735E9298013E', X'246376B362983845B6FF735E9298013E', 4, NULL, NULL, 2, NULL, 0, 'Divide zoodles among four bowls and top with sauce. Garnish with pepper, cheese and parsley, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4DB2C943A7B084784E8A8E1FC2A3A7A', X'86D9FA0F0B003B498DD8BAAD13B9F11F', X'86D9FA0F0B003B498DD8BAAD13B9F11F', 0, NULL, NULL, 2, NULL, 0, 'Arrange the zoodles on a thick layer of paper towels. Sprinkle with a little bit of salt. Cover with another thick layer of paper towels and press gently. Leave while you make the sauce, longer if possible (20 minutes or so is ideal).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1EC39DD23DC1347BB215CE9DFB3FC60', X'86D9FA0F0B003B498DD8BAAD13B9F11F', X'86D9FA0F0B003B498DD8BAAD13B9F11F', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch frying pan, warm the butter and olive oil over medium-high heat. When butter melts, add shallot, garlic and capers and cook, stirring from time to time, until lightly browned, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'064F0DFED4B3BC48BFCDD421E2CF0FB6', X'86D9FA0F0B003B498DD8BAAD13B9F11F', X'86D9FA0F0B003B498DD8BAAD13B9F11F', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, roll up the paper towels and give the zoodles a squeeze to remove as much moisture as possible. Add zoodles to frying pan and cook, tossing with tongs or a rubber spatula, for about a minute. They should retain a nice bite but not look raw. Turn off the heat and stir in the lemon juice, parsley and some pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4803BCF47956949B8EBE844BE406C31', X'86D9FA0F0B003B498DD8BAAD13B9F11F', X'86D9FA0F0B003B498DD8BAAD13B9F11F', 3, NULL, NULL, 2, NULL, 0, 'Divide among four bowls, sprinkle with cheese, and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'681C33F12F9A4A449706B9088A47899E', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 0, NULL, NULL, 2, NULL, 0, 'In a very large lidded pot (I used a 9-quart dutch oven), warm the olive oil over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26FDF0166F3778469F1A0B7725EA854E', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 1, NULL, NULL, 2, NULL, 0, 'Add the onions, celery and carrots and sprinkle the salt overtop. Cook, stirring occasionally, until the vegetables start to get tender and there’s plenty of nice browning on the bottom of the pot. Don''t rush this step, since the browned bits contribute to the soup''s deep flavor.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'657EC83E24FAF3489A8C14066A66AA75', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook, stirring a couple of times, for a minute or two, until you can really smell the garlic.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FD51C392A56124DA98CF606D4994918', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 3, NULL, NULL, 2, NULL, 0, 'Add the tomato paste and cook, stirring, for a minute or two more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'632C03F41352E245BABF675D021E21D1', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 4, NULL, NULL, 2, NULL, 0, 'Pour in the stock and diced tomatoes, thyme, oregano, red pepper flakes, and parmesan rind. Stir and scrape up all the browned bits from the bottom of the pot. (You won’t be able to see them, but you can feel the difference your spoon.) Bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3C7E0BA36C65D4DA2F68D80A9CBE24B', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 5, NULL, NULL, 2, NULL, 0, 'Add the potatoes, zucchini, green beans and canned beans. Reduce the heat to maintain a gentle simmer, cover, and cook for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A0A084A96B6FA40A8981F8BD59B7586', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 6, NULL, NULL, 2, NULL, 0, 'Add the kale and cook about 10 minutes more, until all the vegetables are tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC773A54C36CE94B9B9CF89A13E80F5E', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 7, NULL, NULL, 2, NULL, 0, 'Off the heat, tear up the basil leaves and stir in.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D66697FBF0975C45B5446C0F26B7BECD', X'8F9E737F0D959D46B0E175E0C8E749EE', X'8F9E737F0D959D46B0E175E0C8E749EE', 8, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve, with grated cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87C7F9046654CB40B1B9340A86F561FC', X'DF9F24A1E31A534393483A45C83033EE', X'DF9F24A1E31A534393483A45C83033EE', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil or ghee for a minute over medium heat in a large pot with a heavy bottom. Add the onion and cook, stirring occasionally, until translucent, about five minutes. Add the ginger, garlic, jalapeno or cayenne, mustard seed, cumin, turmeric, coriander and cinnamon and cook, stirring almost constantly, until it smells amazing -- about a minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE277F4CBF80044AB63F630EE3DAC8FC', X'DF9F24A1E31A534393483A45C83033EE', X'DF9F24A1E31A534393483A45C83033EE', 1, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, coconut milk and salt, raise the heat to high to bring to a boil, then reduce to a simmer for 10 minutes, stirring occasionally. Add the chickpeas and zucchini and continue to simmer for about 20 minutes, until zucchini is tender but not falling apart.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'860E38AA5498AE4DB7F01ED4972FF699', X'DF9F24A1E31A534393483A45C83033EE', X'DF9F24A1E31A534393483A45C83033EE', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in cilantro. Serve with rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57286E4FAE7195429B7D247FEB01022A', X'32078304451DD140A998B9C8A9F91B53', X'32078304451DD140A998B9C8A9F91B53', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 550°F (or as high as your oven will go up to 550°F), with a pizza stone if you have one. Prepare your pizza pan or baking sheet by brushing with the olive oil and sprinkling evenly with the cornmeal. Roll or stretch the dough to cover the surface of the pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3145C5D97F0F24448A58AE2F5045071C', X'32078304451DD140A998B9C8A9F91B53', X'32078304451DD140A998B9C8A9F91B53', 1, NULL, NULL, 2, NULL, 0, 'Slice the zucchini as thinly as possible, on a mandoline if you have one or with a very sharp knife. Place zucchini slices in a bowl and toss with half the pesto. Spread the remaining pesto evenly over the dough and top with overlapping slices of zucchini. Sprinkle the feta and olives overtop. Bake until the dough is cooked through and the zucchini is tender, 8 to 15 minutes, depending on thickness. Slice and serve hot or at room temperature.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'879B5C7D393FD44CB587734E87231F0C', X'89CA80297C8B614989B2C006C59CF84B', X'89CA80297C8B614989B2C006C59CF84B', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil over medium heat in a dutch oven. Add the onion and celery and saute for a few minutes without browning. (Dutch ovens retain heat like a charm, so you may need to turn it down once you get going.) Add the garlic, ginger and tomato paste and cook, stirring, for one minute. Add the zucchini and potatoes and let them hang out for a minute while you get the next ingredients ready.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBDDD2DB35FB82449EC53FA98F545531', X'89CA80297C8B614989B2C006C59CF84B', X'89CA80297C8B614989B2C006C59CF84B', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk the coconut milk, curry paste, soy sauce, fish sauce, and brown sugar together to combine thoroughly. Pour the mixture into the pot. Cover and simmer until the zucchini and potatoes are just tender, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A67CD061EE16D04BBD2EEB0D6E55F758', X'89CA80297C8B614989B2C006C59CF84B', X'89CA80297C8B614989B2C006C59CF84B', 2, NULL, NULL, 2, NULL, 0, 'Add the carrots and tomatoes and cook a couple of minutes longer to warm through. Turn off the heat and stir in the cilantro, basil, lime zest and lime juice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC34F6CA0B098641B1C2F2436AB77E89', X'89CA80297C8B614989B2C006C59CF84B', X'89CA80297C8B614989B2C006C59CF84B', 3, NULL, NULL, 2, NULL, 0, 'Serve over rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E0BEF7A99283C4D9DF9088D51FBCFF3', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 0, NULL, NULL, 2, NULL, 0, 'Place rice into a large bowl and cover with several inches of water. The water will become very cloudy right way. Drain this first batch of water immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1328383838E5D4CA87321959A4B0908', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 1, NULL, NULL, 2, NULL, 0, 'Cover the rice with water again. Use your hand to swirl the rice around for a few seconds, and then drain it again. Repeat this process until the water remains clear when you jostle the rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A4E956DA135CD44BD05DC0D95C5800B', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 2, NULL, NULL, 2, NULL, 0, 'Drain the water once more and cover the rice by several inches with clean water. Let the rice soak for 30 minutes, then drain well in a fine-mesh sieve. Now the rice is ready to cook.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D186B5BCC634B40BF9077067939DCFF', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 3, NULL, NULL, 2, NULL, 0, 'Place the rice, kombu if using, and water into the Instant Pot or a medium-sized nonreactive pot with a heavy bottom and a tight-fitting lid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9055E0AB4CD4DF48AF0AE39571F47690', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 4, NULL, NULL, 2, NULL, 0, 'To cook in the Instant Pot: Position the lid, set the vent to sealing position, and cook on manual, high pressure for 5 minutes. Let the pressure release naturally for 10 minutes before carefully releasing any remaining pressure and opening the lid.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E86FCF10EA1F20419099571F90551ED5', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 5, NULL, NULL, 2, NULL, 0, 'To cook on the stovetop: Position the lid, then turn heat to high and bring to a boil. When the water boils, lower the heat to low or medium-low (depending on your burner) to maintain a gentle simmer. Simmer until the water is absorbed, about 12 minutes. Then remove pot from the heat, leaving the lid closed, and let the rice steam for an additional 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B877475C4907FA4EB0F0028FD486D5C9', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 6, NULL, NULL, 2, NULL, 0, 'While the rice cooks, stir together the vinegar, sugar, and salt in a small pot or microwave-safe bowl. Heat just until salt and sugar have dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'106937C71D2AF644BC4DA438A14E68A9', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 7, NULL, NULL, 2, NULL, 0, 'Remove the kombu. Gently spoon cooked rice into a wide, shallow bowl. Pour vinegar mixture over rice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93A8CA3560A73A409D1BE0EA00FBCFA0', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 8, NULL, NULL, 2, NULL, 0, 'Using a rice paddle or silicone spatula, gently cut and fold the rice to mix in the vinegar and cool the rice. Refer to the notes section below for details and to the video for visual cues.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE33BBB5488AAF41B933568ABD1E394C', X'6067C6D02202604EB3282E43120412A3', X'6067C6D02202604EB3282E43120412A3', 9, NULL, NULL, 2, NULL, 0, 'Let rice cool to body temperature (or all the way to room temperature, if you prefer) before using for sushi.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6923478CC3F622468C0E5FE780877902', X'9451888E3DCAC348A8EB276061E07AA6', X'9451888E3DCAC348A8EB276061E07AA6', 0, NULL, NULL, 2, NULL, 0, 'Place macaroni, broth, shallot, mustard, nutmeg, cayenne, salt, and pepper into Instant Pot and stir.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB3FC57E9F66E74D8B61C6CE73CFFB33', X'9451888E3DCAC348A8EB276061E07AA6', X'9451888E3DCAC348A8EB276061E07AA6', 1, NULL, NULL, 2, NULL, 0, 'Set vent to sealing position. Cook on manual mode, high pressure, for 4 minutes. (Pot will take about 10 minutes to come up to pressure before timed cooking begins.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66848D62BE63DA4BBF7FC86A3B396AAC', X'9451888E3DCAC348A8EB276061E07AA6', X'9451888E3DCAC348A8EB276061E07AA6', 2, NULL, NULL, 2, NULL, 0, 'Carefully do a manual pressure release by turning the vent to open. (We like to use a towel or a long-handled spoon to do this.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DFD7023201AC342BD23E5805FA32DF0', X'9451888E3DCAC348A8EB276061E07AA6', X'9451888E3DCAC348A8EB276061E07AA6', 3, NULL, NULL, 2, NULL, 0, 'Stir in milk and cheeses until cheese is melted.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FEDA157727CF34E8C704BF8FC587C27', X'9451888E3DCAC348A8EB276061E07AA6', X'9451888E3DCAC348A8EB276061E07AA6', 4, NULL, NULL, 2, NULL, 0, 'Stir in sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'150B481A83B1454FA70BAD6D81B6F6D0', X'9451888E3DCAC348A8EB276061E07AA6', X'9451888E3DCAC348A8EB276061E07AA6', 5, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD971BA765ACB34199ED82628F4D3FD6', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 0, NULL, NULL, 2, NULL, 0, 'Trim ends from butternut squash. Peel, scoop out seeds, and dice into 1-inch pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DC813F9BEBB5F4F90DF5BE92092376D', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 1, NULL, NULL, 2, NULL, 0, 'Dice onion and apple.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EEED8F4DEBAA547A9DC3E714E531CB3', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 2, NULL, NULL, 2, NULL, 0, 'Instant Pot Instructions: Place all ingredients except cream in Instant Pot and close lid. Set vent to sealing position. Cook on high pressure for 12 minutes, then carefully release steam manually.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC504E94B66E2A4FBE85232431D1A950', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 3, NULL, NULL, 2, NULL, 0, 'Stovetop Instructions: In a large, heavy pot, melt the butter over medium heat. Add onion, garlic, and apple, and cook, stirring occasionally, until softened, about five minutes. Add butternut squash, broth, cider, maple syrup, bay leaves, salt, cinnamon, and nutmeg. Bring to a boil over high heat, then reduce heat, cover, and simmer until squash is very tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D2E4B0B1C9B7F40ADD7F206F202D5AA', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 4, NULL, NULL, 2, NULL, 0, 'Remove bay leaves and cinnamon sticks if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FE33955B870EE4FBE6BDF86230AC680', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 5, NULL, NULL, 2, NULL, 0, 'Stir in cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCC4C189F072AC48950FA7F6CFC6EA7F', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 6, NULL, NULL, 2, NULL, 0, 'Puree soup with an immersion blender directly in Instant Pot/pot or in batches in a blender. (The Vitamix makes this soup super-creamy and luxurious, if you’re into that sort of thing.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'285A0D8EF78A194BB544F4C8F06C67A7', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 7, NULL, NULL, 2, NULL, 0, 'Grind in some black pepper and adjust for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E541F423FACEDD4CA7A32943367386FA', X'CC1BCD67DE8FBA499298E987334525BB', X'CC1BCD67DE8FBA499298E987334525BB', 8, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CE74015407A3D42BB527B94F94419ED', X'11C5247FE20C274E99DCFF5380CDD4A3', X'11C5247FE20C274E99DCFF5380CDD4A3', 0, NULL, NULL, 2, NULL, 0, 'Place all chili ingredients except lime juice into an Instant Pot and stir to combine.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60D606B93A6D0F4A943FEFCC5E26B5BA', X'11C5247FE20C274E99DCFF5380CDD4A3', X'11C5247FE20C274E99DCFF5380CDD4A3', 1, NULL, NULL, 2, NULL, 0, 'Cover, seal vent and cook on high pressure for 6 minutes, then release pressure manually. Stir in lime juice. Chili is ready to eat right away but, like all chili, will only get better over the next 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'334DFC89A0E32F4DA3ED7670C5D487CF', X'11C5247FE20C274E99DCFF5380CDD4A3', X'11C5247FE20C274E99DCFF5380CDD4A3', 2, NULL, NULL, 2, NULL, 0, 'Ladle into serving bowls and garnish with lime wedges, cilantro, and vegan sour cream.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C30C059641DFB341AD18033FD1B3DDA9', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', 0, NULL, NULL, 2, NULL, 0, 'If cooking larger, thicker-skinned beans such as chickpeas, great northern, cannellini, soak them for four to eight hours in cool water filtered if possible, to cover by a couple of inches, with two teaspoons of salt. Then proceed as directed in step two, discarding soaking water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CA3D610511F4D4ABAED8F244CEF6491', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', 1, NULL, NULL, 2, NULL, 0, 'Rinse beans under running water and pick out any non-beanlike goodies such as small stones. Place beans in a medium pot and add fresh cool water, filtered if possible, to cover by about two inches.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D364BEBF38E3F48BCB0ABBFBB780A30', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', 2, NULL, NULL, 2, NULL, 0, 'Add salt, bay leaves, garlic, and onion, along with additional ingredients listed above, if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF8640B59370BA42A9FD5FF33C365282', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', X'C44BC4E80AE3B74EBAB63CB781CDBB5B', 3, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then reduce heat to a gentle simmer. Partially cover the pot and simmer slowly until beans are done to your liking, stirring occasionally and topping up with additional water if necessary. This will take anywhere from an hour to three hours or more, depending on the type and age of the beans you’re using and how tender you''d like them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D771AB490AEA0844934AC9F74C512A24', X'16EC496143418540A415A931F5DDFD85', X'16EC496143418540A415A931F5DDFD85', 0, NULL, NULL, 2, NULL, 0, 'Pat chicken breasts dry with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8A11C93A829B04FAD5395CD71B641C6', X'16EC496143418540A415A931F5DDFD85', X'16EC496143418540A415A931F5DDFD85', 1, NULL, NULL, 2, NULL, 0, 'To make the chicken in the Instant Pot: Pour chicken broth into Instant Pot and add chili powder, cumin, paprika, and salt. Arrange chicken breasts in a single layer in the Instant Pot. Close lid and set vent to sealing. Cook on manual, high pressure, for 8 minutes. Let the pressure release naturally for 5 minutes before carefully doing a manual release. See notes section for oven and stovetop cooking instructions.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19BD41948E72294A885F31A86F07E72A', X'16EC496143418540A415A931F5DDFD85', X'16EC496143418540A415A931F5DDFD85', 2, NULL, NULL, 2, NULL, 0, 'Shred chicken with two forks while still warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05C3C5F3EAC89D4ABE2B71FA09A18CEC', X'16EC496143418540A415A931F5DDFD85', X'16EC496143418540A415A931F5DDFD85', 3, NULL, NULL, 2, NULL, 0, 'To assemble the tacos, place some shredded chicken on a flour tortilla and top with avocado, salsa verde, pico de gallo, shredded cheese, and Cholula.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F38CBBFE63FCE4F8C98B08A36BBA266', X'E21967963AD88B4FA53F4E670197921C', X'E21967963AD88B4FA53F4E670197921C', 0, NULL, NULL, 2, NULL, 0, 'In a blender, combine the strawberries with 2 cups of the water, the vanilla seeds or extract, lemon juice, and simple syrup.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF6E37D2434694439EEF1F529A44E571', X'E21967963AD88B4FA53F4E670197921C', X'E21967963AD88B4FA53F4E670197921C', 1, NULL, NULL, 2, NULL, 0, 'Puree until completely liquid. Strain the mixture through a fine-mesh sieve or through cheesecloth into a pitcher.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'101797336B57624F8AE6A277523BEF81', X'E21967963AD88B4FA53F4E670197921C', X'E21967963AD88B4FA53F4E670197921C', 2, NULL, NULL, 2, NULL, 0, 'Stir in the remaining 2 cups water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7177C4B372846B49AD60C8DB162C5A5C', X'E21967963AD88B4FA53F4E670197921C', X'E21967963AD88B4FA53F4E670197921C', 3, NULL, NULL, 2, NULL, 0, 'Chill completely. Pour into ice-filled glasses and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'958D98D68CF4D54F89A45344AA9FBE93', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 0, NULL, NULL, 2, NULL, 0, 'Add the flour, cornmeal, and fat to a medium mixing bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'314EB4B5E314354FB5705CF432B2D401', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 1, NULL, NULL, 2, NULL, 0, 'Mix together with your fingers until the fat is incorporated. The mixture will look like coarse sand.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'074C614B73FD534FA92A59748DBBEF15', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 2, NULL, NULL, 2, NULL, 0, 'Pour in water and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12F67BAEAD59554B85F53B68933A060D', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 3, NULL, NULL, 2, NULL, 0, 'Mix together until all the water is incorporated and the dough forms into a ball. Then knead a few times until the dough becomes smooth and no longer sticky. You can knead it right in the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F86FEBBADFC0340AC71F5B1118869D6', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 4, NULL, NULL, 2, NULL, 0, 'Divide dough into 10 equal portions. I like to weigh them on my kitchen scale. They should be about 40 grams each. You can eyeball it if you''d rather.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8123A86B4403A4F8A456F92FD23C662', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 5, NULL, NULL, 2, NULL, 0, 'Use your hands to roll each portion into a little ball. Place on a plate and press with your fingers to flatten into a small disk.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F91101C91F80B42AF14BEB3C0AC1216', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 6, NULL, NULL, 2, NULL, 0, 'Cover with a piece of parchment or a kitchen towel and let rest for 30 minutes. If you''ll be longer than 30 minutes, place the plate in the fridge so the dough doesn''t get too soft.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D46A816B8529DB4F8D1214EF6068C6A3', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 7, NULL, NULL, 2, NULL, 0, 'Set a cast iron skillet over medium-high heat to preheat while you press the tortillas.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBDB1C23AAC50943B554034FADD486A4', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 8, NULL, NULL, 2, NULL, 0, 'Open the tortilla press and line the bottom with a piece of parchment about the size of the press. Place one of the dough disks in the center and cover it with another piece of parchment. Close the press and press down on the lever.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ECB30AEED32804196A6B62D2F273B4A', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 9, NULL, NULL, 2, NULL, 0, 'Open the press. Gently peel away the top layer of parchment. Remove the tortilla from the press, still on the bottom piece of parchment. Gently peel the tortilla from the parchment. (see note)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A080CEC4BF04354190584089CF438254', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 10, NULL, NULL, 2, NULL, 0, 'Place tortilla onto dry preheated pan. Cook until blistered on the underside, then flip once and cook until both sides have some nice dark-brown spots.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'963DD78D8AC62C47A73CF97C370BE298', X'64CA8CC7E311104182238F1208FA850D', X'64CA8CC7E311104182238F1208FA850D', 11, NULL, NULL, 2, NULL, 0, 'Repeat pressing and cooking with remaining tortillas, one at a time, stacking on a plate as you go. Keep covered with a towel until ready to serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7408B0FF523E6249BB573D6FBB971426', X'90EA4BA40712D84FAF8F49942E36098F', X'90EA4BA40712D84FAF8F49942E36098F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F / 218°C with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2298E010988CE40952342F954D87D5A', X'90EA4BA40712D84FAF8F49942E36098F', X'90EA4BA40712D84FAF8F49942E36098F', 1, NULL, NULL, 2, NULL, 0, 'Peel the sweet potatoes and cut into 1/2-inch dice.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B786D642971B840AF44B8F920E18D1C', X'90EA4BA40712D84FAF8F49942E36098F', X'90EA4BA40712D84FAF8F49942E36098F', 2, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking sheet with parchment. Place diced sweet potatoes onto baking sheet and sprinkle with oil, chili powder, and salt. Toss well and arrange in a single layer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4920B1DB329FA04190E0AAF9436BF1A0', X'90EA4BA40712D84FAF8F49942E36098F', X'90EA4BA40712D84FAF8F49942E36098F', 3, NULL, NULL, 2, NULL, 0, 'Place sheet pan on center oven rack and roast for 20 minutes. Then toss with a spatula and return to oven for 15 minutes, until tender inside and lightly crisped and browned outside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0343246D120DC40992C515BF8653289', X'90EA4BA40712D84FAF8F49942E36098F', X'90EA4BA40712D84FAF8F49942E36098F', 4, NULL, NULL, 2, NULL, 0, 'While the sweet potatoes roast, make the beans. In a medium pot, heat the oil over medium-high heat. Add onion and garlic and cook, stirring from time to time, for about five minutes, until softened. Stir in cumin and smoked paprika and cook for 30 seconds. Then add beans and broth or water. Simmer for 10 minutes. Off the heat, use a potato masher to mash about 1/3 of the beans.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B2E0A53BDB4D14D9E0E12F5972FE365', X'90EA4BA40712D84FAF8F49942E36098F', X'90EA4BA40712D84FAF8F49942E36098F', 5, NULL, NULL, 2, NULL, 0, 'Make the cabbage slaw: Using a chef''s knife, cut the core out of the cabbage and thinly slice / shred the cabbage. Place into a medium mixing bowl and stir in lime juice and salt. (I like to use my hands to make sure everything is mixed well.) Let sit while the sweet potatoes and beans cook. Before serving, drain any excess liquid from the bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1C7747C16521F4AB2C74918AD73C5D8', X'90EA4BA40712D84FAF8F49942E36098F', X'90EA4BA40712D84FAF8F49942E36098F', 6, NULL, NULL, 2, NULL, 0, 'To assemble the tacos: Onto each tortilla, layer some black beans, sweet potatoes, slaw, cilantro sauce, and crumbled cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05127E05883A4D469666C6B08335E0E3', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 0, NULL, NULL, 2, NULL, 0, 'Char the poblanos by placing them directly on the flames of a gas burner, turning occasionally, until blackened on all sides. (If you don''t have gas burners, you can char them under the broiler or on a grill.) Place charred poblanos into a large bowl, top bowl with plastic wrap or a plate that fits tightly, and let the poblanos steam until they''re relatively cool. Then, using a damp paper towel or your fingers, peel off and discard the blackened skin.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99ADB3E38A4CCB44B10F73FC31475DE2', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 1, NULL, NULL, 2, NULL, 0, 'Dice peeled poblanos, removing inner ribs and seeds.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB9AED65D9D8BA40B105059D86FB090F', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 2, NULL, NULL, 2, NULL, 0, 'In a heavy 5-quart pot, melt the butter over medium-high heat. Add the onion and celery and cook, stirring occasionally, until slightly softened but not browned, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'251977358532144491B97D9896390C35', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 3, NULL, NULL, 2, NULL, 0, 'Stir in garlic and cook one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'305FD24B2FF95241BEAB2C76DBA537E2', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 4, NULL, NULL, 2, NULL, 0, 'Add broth, raw chicken tenders, and potatoes to pot and raise heat to high until liquid comes to a boil. Then reduce heat to maintain a simmer and cook until chicken is just opaque throughout. (This should only take a few minutes.) Remove chicken to a plate or cutting board, cool slightly, and shred with two forks.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DD1318F8166F84B8558E522152E5C6E', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 5, NULL, NULL, 2, NULL, 0, 'Add salt, cumin, and chili powder, and continue simmering until potatoes are tender.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC8E1FE1D4AE534FB7D6A99D89ED6743', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 6, NULL, NULL, 2, NULL, 0, 'Add corn and beans, along with reserved diced poblanos and shredded chicken, and simmer until warmed through.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ED7F894BE3583419FE9458CC85C796C', X'16B0E1EADD2C0E409999FF4AD4132597', X'16B0E1EADD2C0E409999FF4AD4132597', 7, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in half and half, lime juice, and cilantro.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB44710D8BB4274BB652BCD85E6CAC27', X'2C5AE62C6FC39D40B23B18BA4422B951', X'2C5AE62C6FC39D40B23B18BA4422B951', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Line a baking dish with aluminum foil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CCC1CF1E7A46B48B1BB95D233C71FF9', X'2C5AE62C6FC39D40B23B18BA4422B951', X'2C5AE62C6FC39D40B23B18BA4422B951', 1, NULL, NULL, 2, NULL, 0, 'Make the chicken filling: In a medium bowl, combine mustard, oil, chili powder, and salt. Add chicken breasts and toss to coat. Place in lined baking dish and bake for 30 minutes or until cooked through and tender. Allow chicken to cool slightly, then shred the breasts using two forks. Set meat aside, along with any juices from the baking dish.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DD907529FEC684AABB28B43B030AC43', X'2C5AE62C6FC39D40B23B18BA4422B951', X'2C5AE62C6FC39D40B23B18BA4422B951', 2, NULL, NULL, 2, NULL, 0, 'Make the black bean filling: Coat a large skillet with olive oil, place over medium heat, and sauté onion until soft, 5 to 8 minutes. Add zucchini and continue to sauté, stirring occasionally, until zucchini is tender and browned, about 6 minutes more. Add garlic, beans, chili powder, cumin, and salt, and cook for five minutes. Transfer to a bowl and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94A6B9DA14C4CB46A69CD91C53FC1EC9', X'2C5AE62C6FC39D40B23B18BA4422B951', X'2C5AE62C6FC39D40B23B18BA4422B951', 3, NULL, NULL, 2, NULL, 0, 'Make the sauce: Wipe out the skillet with a paper towel. Melt butter over medium heat. Add garlic and jalapeños and cook until soft but not brown, about 4 minutes. Add flour and cook, stirring, for 1 minute. Add stock, raise heat to high, and bring to a boil. Reduce heat and simmer until the liquids have reduced and the sauce has become opaque, about 5 minutes. Stir in sour cream, chiles, cumin, chili powder, salt, and cayenne. Coat the bottoms of two 9×13-inch baking dishes with some of the sauce. Reserve the rest.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24C5A01030AC7445A9D7E48CC4209426', X'2C5AE62C6FC39D40B23B18BA4422B951', X'2C5AE62C6FC39D40B23B18BA4422B951', 4, NULL, NULL, 2, NULL, 0, 'Assemble the enchiladas: Fill 8 tortillas with the chicken and 8 with the black bean and zucchini mixture. Top the filling with a spoonful of shredded cheese and a sprinkle of cilantro. Roll up tortillas and place seam-side down in the baking dishes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2FF1DC0E2BCB945978F007083B77EC0', X'2C5AE62C6FC39D40B23B18BA4422B951', X'2C5AE62C6FC39D40B23B18BA4422B951', 5, NULL, NULL, 2, NULL, 0, 'Pour remaining cream sauce evenly over enchiladas and sprinkle with remaining cheese. Bake for 30 minutes, or until sauce is bubbling and cheese is beginning to brown. Garnish with the extra cilantro and the scallions and serve with lime wedges.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB88342C9D857347975D6CB6E7930545', X'FDDBEEF131651044BFD406FF35D76874', X'FDDBEEF131651044BFD406FF35D76874', 0, NULL, NULL, 2, NULL, 0, 'In a medium pot, add the chicken broth, tomatoes, taco seasoning, salt, and pepper, and stir to combine. Bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32B3106E75C83C499F1B6C88BA7CDF5B', X'FDDBEEF131651044BFD406FF35D76874', X'FDDBEEF131651044BFD406FF35D76874', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low. Add chicken pieces and lime juice and simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E615F838CA2B647A72E267B1A33FE2F', X'FDDBEEF131651044BFD406FF35D76874', X'FDDBEEF131651044BFD406FF35D76874', 2, NULL, NULL, 2, NULL, 0, 'Adjust seasoning with salt, pepper, taco seasoning, or lime juice, as needed.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DBAE9983BD5F5408FB95C91CA5A4833', X'FDDBEEF131651044BFD406FF35D76874', X'FDDBEEF131651044BFD406FF35D76874', 3, NULL, NULL, 2, NULL, 0, 'Ladle into two bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54C2851527186D42B547FCE47292A757', X'FDDBEEF131651044BFD406FF35D76874', X'FDDBEEF131651044BFD406FF35D76874', 4, NULL, NULL, 2, NULL, 0, 'Garnish with cilantro, avocado, and crumbled chips.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8EC7E7E06B9004CB2902C4B164499F8', X'86D260AD9586844C8DD0428C4244BCEC', X'86D260AD9586844C8DD0428C4244BCEC', 0, NULL, NULL, 2, NULL, 0, 'Pat steak dry with paper towels. If pieces are too long to fit on grill, cut into manageable sections.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22EB39195259634FA0FA0CE420822128', X'86D260AD9586844C8DD0428C4244BCEC', X'86D260AD9586844C8DD0428C4244BCEC', 1, NULL, NULL, 2, NULL, 0, 'In a gallon-size zip top bag, mix together the oil, orange and lime juices, garlic, salt, vinegar, and torn cilantro. Add meat and marinate at room temperature for two hours (or up to a day in the fridge), turning the bag over occasionally.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0C73B4B3A0D6E4A94FED78C29E343D2', X'86D260AD9586844C8DD0428C4244BCEC', X'86D260AD9586844C8DD0428C4244BCEC', 2, NULL, NULL, 2, NULL, 0, '30 minutes before you''re ready to eat, preheat grill to high. Grill the steak until nicely browned on the underside, 3 to 5 minutes. Then flip once and grill about 3 minutes more, depending on thickness, for medium-rare. Let steak rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C47A95B2C8C3F0468B453A919818ADA6', X'86D260AD9586844C8DD0428C4244BCEC', X'86D260AD9586844C8DD0428C4244BCEC', 3, NULL, NULL, 2, NULL, 0, 'While steak rests, warm refried beans and tortillas according to package instructions, and assemble toppings in bowls at the table. Slice steak thinly against the grain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06D429A5FD701940915A3EAAAFE86196', X'86D260AD9586844C8DD0428C4244BCEC', X'86D260AD9586844C8DD0428C4244BCEC', 4, NULL, NULL, 2, NULL, 0, 'Let everyone assemble their own tacos. Anything goes, but we recommend a nice schmear of refried beans on the bottom, a few slices of steak, and plenty of toppings.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10C9C1418EC3794EA5C0381C0EE5E70B', X'D9DD87551EFB414DB1E120F0F7251C62', X'D9DD87551EFB414DB1E120F0F7251C62', 0, NULL, NULL, 2, NULL, 0, 'Warm the olive oil over medium-high heat in a 12-inch nonstick skillet. Add shallot and garlic and cook, stirring frequently, until beginning to soften, about a minute. Stir pork into skillet, breaking up with a spatula into small pieces, and continue cooking for a few minutes until lightly browned. Add beans, tomatoes (along with all their juices), salt, chili powder, cumin, paprika, oregano and cayenne to skillet and stir to combine. Reduce heat to medium-low and simmer for five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6E0E7A831F5944DBD5259681B99541E', X'D9DD87551EFB414DB1E120F0F7251C62', X'D9DD87551EFB414DB1E120F0F7251C62', 1, NULL, NULL, 2, NULL, 0, 'To serve, warm taco shells according to package directions. Fill each shell with some of the pork mixture and top with Cotija, pickled onions and cilantro. Serve with lime wedges.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB61CC9FF101D542992DC56CFF39BE2E', X'E9C5C980425FC1408A4E22F33276DD96', X'E9C5C980425FC1408A4E22F33276DD96', 0, NULL, NULL, 2, NULL, 0, 'Place four of the tortillas on work surface. Spread each with 1/4 of the refried beans and sprinkle evenly with 1/4 of the cheese, leaving about a 1/4-inch border around the edge to account for melting.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30D70CBDDF1DD140A67AC25BCA6001AC', X'E9C5C980425FC1408A4E22F33276DD96', X'E9C5C980425FC1408A4E22F33276DD96', 1, NULL, NULL, 2, NULL, 0, 'In a 12-inch skillet (nonstick or cast iron), warm the olive oil over medium heat. Add garlic and cook, stirring, for 30 seconds. Add spinach and cook until just wilted. Transfer spinach to a colander and squeeze out as much moisture as possible. Divide spinach among tortillas and arrange over cheese.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DDE6BA554C97F4D9538EAD5BA8318F1', X'E9C5C980425FC1408A4E22F33276DD96', X'E9C5C980425FC1408A4E22F33276DD96', 2, NULL, NULL, 2, NULL, 0, 'Place one of the four remaining tortillas on top of each quesadilla.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'798FB33112411E4790DBE78EBF1766FC', X'E9C5C980425FC1408A4E22F33276DD96', X'E9C5C980425FC1408A4E22F33276DD96', 3, NULL, NULL, 2, NULL, 0, 'To cook, you have two options. (1) Wipe out the skillet and set over medium-high heat, then cook quesadillas one at a time until lightly browned and crisp on the outside and melty on the inside, flipping once, about 5 minutes per side. Or (2) Preheat oven and two sheet pans to 400°F. Spray hot pans with cooking spray. Place one tortilla on each sheet pan (or two if you have very large pans and can fit them) and bake for 5 minutes, then flip and bake about 5 minutes more. Psst...I don''t often bother with this, but if you''re the type of cook who likes top-secret pro-tip lifehacks, you can bake your quesadillas between two sheet pans to crisp both sides at the same tine and not have to flip them.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A01FA3185B4E643B00CD142DF8CE7C4', X'E9C5C980425FC1408A4E22F33276DD96', X'E9C5C980425FC1408A4E22F33276DD96', 4, NULL, NULL, 2, NULL, 0, 'Cut into wedges, top with pickled onions, cilantro sauce, sliced avocado and sour cream if you want to go nuts like I always do. Otherwise, they''re great with sour cream and your favorite jarred salsa.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDA968D4599E47408C3E3C3C5C69DCEB', X'663AB239293F1D40A2E869006C770D55', X'663AB239293F1D40A2E869006C770D55', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a 12-inch nonstick skillet over medium-high. Add garlic and cook, stirring, for 30 seconds, then add shrimp. Sprinkle with plenty of salt and pepper. Cook, flipping once, until just shy of opaque in the center -- a couple minutes total, depending on size. Add spinach and another sprinkle of salt and cook until wilted. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1240574A197C114DB05D46AEE20B2020', X'663AB239293F1D40A2E869006C770D55', X'663AB239293F1D40A2E869006C770D55', 1, NULL, NULL, 2, NULL, 0, 'Arrange four of the tortillas on a work surface. Using half the cheese, sprinkle each tortilla evenly, leaving a slight border around the edge to account for melting. Top each tortilla evenly with beans and the shrimp and spinach mixture. Sprinkle with remaining cheese, then top each with one of the four remaining tortillas.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F2B852F6FF2494F98795D8C3FB46CE6', X'663AB239293F1D40A2E869006C770D55', X'663AB239293F1D40A2E869006C770D55', 2, NULL, NULL, 2, NULL, 0, 'Wipe the pan clean and place over medium heat. One at a time, cook quesadillas until tortillas are browned in spots and cheese is fully melted, flipping once.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D4F3346EA0C55429D70C3940739CBA1', X'663AB239293F1D40A2E869006C770D55', X'663AB239293F1D40A2E869006C770D55', 3, NULL, NULL, 2, NULL, 0, 'To serve, cut into wedges. Stir a tablespoon of water into the sour cream and drizzle over quesadillas. Sprinkle with pickled onions if using.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1488603A88544A448CEBA52DB03B5A56', X'D092E26245987A4EAA6237D17BE71F2E', X'D092E26245987A4EAA6237D17BE71F2E', 0, NULL, NULL, 2, NULL, 0, 'Juice the limes into a medium mixing bowl. It might seem like there''s a lot of juice, but you won’t be sorry. Add the onion and stir to coat. Let sit while you prepare the avocados.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA8873A809EDE342962D15269DF3E5D8', X'D092E26245987A4EAA6237D17BE71F2E', X'D092E26245987A4EAA6237D17BE71F2E', 1, NULL, NULL, 2, NULL, 0, 'Halve and pit each avocado. Dice flesh and scoop from skins into mixing bowl, tossing with lime juice as you go to prevent browning. Add salt and mash avocado to desired consistency. I like to leave a some pieces diced and mash some more thoroughly for a little textural interest. Add cilantro and jalapeño if using and stir to combine. Serve with tortilla chips and lots of good beer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AB7E7DEF136C64CBF8F6827174D10ED', X'D52E8B5F9C0D6A43BA314BD78471724C', X'D52E8B5F9C0D6A43BA314BD78471724C', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill to about 400°F. Place shucked corn on grates and grill until nicely browned but still tender, about 3 minutes per `side,` 10-ish minutes total. Place on a serving platter to cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E48AA22D0A4E45ABF49FC83D685E04', X'D52E8B5F9C0D6A43BA314BD78471724C', X'D52E8B5F9C0D6A43BA314BD78471724C', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the mayonnaise, crema or sour cream, chili powder, garlic, lime juice and zest, cilantro and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E74D58984B703C4E92DD12C6E8FF53E1', X'D52E8B5F9C0D6A43BA314BD78471724C', X'D52E8B5F9C0D6A43BA314BD78471724C', 2, NULL, NULL, 2, NULL, 0, 'Spread sauce over corn with a spoon. Sprinkle with Cotija and with extra cilantro and chili powder. Garnish with lime wedges and serve warm.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25990B6DF28CE5418A6D09B09811C49F', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 0, NULL, NULL, 2, NULL, 0, 'In a medium-sized pot, heat the bacon fat or oil over medium-high heat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97854E272F426B4DA567B3922FE7E87D', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 1, NULL, NULL, 2, NULL, 0, 'Add the diced onion and pepper along with the salt, and cook, stirring occasionally, until starting to soften, about five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4E738EFF0703045B10455F4B8DAD38D', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, tomato paste, cumin, and chili powder. Cook, stirring, for one minute.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D16FDCC8F7C5044E8A2801048E488360', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 3, NULL, NULL, 2, NULL, 0, 'Add the beans along with 1/2 cup bean cooking water, broth, or plain water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C34CC4818BEF35478F5247AEBCD57FC7', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43D0EA6D683A2842A955F51E18347ADC', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 5, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low and simmer for 15 minutes. Add up to 1/2 cup more water if the beans become too dry.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F21E4032AB5DDD429F32F94080219D5D', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 6, NULL, NULL, 2, NULL, 0, 'Then, using a potato masher, the back of a wooden spoon, or an immersion blender, mash the beans until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BD1DF8DC04A1E49B953A73467D5A0AE', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 7, NULL, NULL, 2, NULL, 0, 'Stir in lime juice and taste for salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F715FC0FA3D31845BBE68584B7630EAD', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', X'CBDD7A7826BD114CA37CBFB4EA7BEEE2', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FA449D9E5172D428863C2D8ED5A9570', X'467C15D36B94614ABE8927A7E862C80D', X'467C15D36B94614ABE8927A7E862C80D', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, melt the butter over medium heat. Stir in the flour and let bubble for about a minute. Pour in the milk, whisk until smooth, then bring to a boil. Reduce heat and simmer about 5 minutes until quite thick. Off the heat, stir in sour cream and then grated cheese. If cheese doesn''t fully melt, you can help it along over low heat, stirring constantly. Stop when it''s just shy of fully melted and let the residual heat do the rest so the sauce doesn''t break. Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DA4C52855B3A64FA2A5908D618A20E3', X'467C15D36B94614ABE8927A7E862C80D', X'467C15D36B94614ABE8927A7E862C80D', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry the cilantro and trim off the very bottoms of the stems. In a high-speed blender or a food processor, combine cilantro (leaves, stems and all), olive oil, lime juice and salt. Process until smooth.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DE6AEAE9838E24784C9868004541C1E', X'467C15D36B94614ABE8927A7E862C80D', X'467C15D36B94614ABE8927A7E862C80D', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, pour the lime juice and a sprinkle of salt over the cabbage and massage a bit with your hands to tenderize cabbage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B26F8B0BDAEDAD40BBB441E05531E606', X'467C15D36B94614ABE8927A7E862C80D', X'467C15D36B94614ABE8927A7E862C80D', 3, NULL, NULL, 2, NULL, 0, 'Arrange the tortilla chips on a large platter or rimmed baking sheet. Heat the refried beans in the microwave until nice and hot, then dollop over tortilla chips. Pour the cheese sauce overtop, then sprinkle on the cabbage, hatch chilis, tomatoes and red onion. Finish by drizzling with cilantro sauce. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86479C4AF773ED4EB49B5675C27D4448', X'C20B2F46F86FBD448AE00E17008871AB', X'C20B2F46F86FBD448AE00E17008871AB', 0, NULL, NULL, 2, NULL, 0, 'First, marinate the shrimp. Place peeled, cleaned, defrosted shrimp in a medium bowl and toss together with olive oil, garlic, smoked paprika, salt and pepper. Let sit while you prepare the other elements of the tacos.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EB183D95882DE48857372A9EB334B9B', X'C20B2F46F86FBD448AE00E17008871AB', X'C20B2F46F86FBD448AE00E17008871AB', 1, NULL, NULL, 2, NULL, 0, 'Start the cabbage next so it has a chance to marry with the salt and lime juice before serving. Cut a small green cabbage in half from the top through the core, then cut one of the halves into quarters in the same way. Remove the core from one quarter and very thinly slice the cabbage. (Reserve the rest of the cabbage for another use.) Place in a bowl and sprinkle with salt and lime juice. Toss with your hands, rubbing a bit to help the salt and lime juice tenderize the cabbage.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F27770810A54847B74EB306A4B8E922', X'C20B2F46F86FBD448AE00E17008871AB', X'C20B2F46F86FBD448AE00E17008871AB', 2, NULL, NULL, 2, NULL, 0, 'To make the cilantro sauce, mince the garlic and then use the side of a chef''s knife to smash the salt and garlic together into a paste. In a small bowl stir together the mayonnaise, lime juice, garlic-salt paste and pepper. When well combined, gently stir in the chopped cilantro.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC113C5D6B294240A423B4CE29D80E76', X'C20B2F46F86FBD448AE00E17008871AB', X'C20B2F46F86FBD448AE00E17008871AB', 3, NULL, NULL, 2, NULL, 0, 'To make the pico de gallo, stir together the tomatoes, red onion, salt, pepper and olive oil in a small bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2C18603BDA500469DB8CDDC1780AF96', X'C20B2F46F86FBD448AE00E17008871AB', X'C20B2F46F86FBD448AE00E17008871AB', 4, NULL, NULL, 2, NULL, 0, 'Heat the tablespoon of safflower oil in a 12-inch nonstick skillet over medium-high heat. Add the shrimp in a single layer and cook until opaque, about one minute per side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BB27E9C8BD4C6498E88439D46F46C2C', X'C20B2F46F86FBD448AE00E17008871AB', X'C20B2F46F86FBD448AE00E17008871AB', 5, NULL, NULL, 2, NULL, 0, 'Bring ingredients to the table and let everyone assemble their own tacos, or plate by filling each tortilla with some shrimp, cabbage, pico de gallo and a drizzle of cilantro sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'211E4CC738D750429114F420CE1FCC31', X'C3049ADDD8EBF64A81649F405A31275F', X'C3049ADDD8EBF64A81649F405A31275F', 0, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and cut into roughly 1-inch cubes. Place in a small pot with water to cover and two teaspoons of the salt. Bring to a boil, then simmer for about 10 minutes until tender. Drain and return to pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E36C21A97711443988C840BCBF469D4', X'C3049ADDD8EBF64A81649F405A31275F', X'C3049ADDD8EBF64A81649F405A31275F', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, stir together the sour cream, onion powder, paprika, cumin, garlic powder, cayenne, black pepper and remaining 1/2 teaspoon salt. Add mixture to potatoes and mash until smooth. (Nothing fancy — a fork will work fine.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A24FC623E388FE408AD44713F16AEE2B', X'C3049ADDD8EBF64A81649F405A31275F', X'C3049ADDD8EBF64A81649F405A31275F', 2, NULL, NULL, 2, NULL, 0, 'Soften the tortillas by microwaving the stack between moist paper towels for about 20 seconds. (You may need to repeat this if they cool as you work, or heat them in batches.) Spread a generous two tablespoons of potato into each tortilla and gently fold to form a taco. I find hands work best for spreading, but maybe you''re more talented with utensils than I am.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18E030E3A7291F4883FABA35905387F6', X'C3049ADDD8EBF64A81649F405A31275F', X'C3049ADDD8EBF64A81649F405A31275F', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil in a 12-inch frying pan until thin and shimmering. Working three to four tacos at a time, fry on one side until crisp, about 90 seconds, then flip and repeat. Drain on paper towels and sprinkle with a little additional salt if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B57439E2F2A4B74C94D0B72E0FF251EA', X'C3049ADDD8EBF64A81649F405A31275F', X'C3049ADDD8EBF64A81649F405A31275F', 4, NULL, NULL, 2, NULL, 0, 'To serve, add some cheese, cilantro and red onion to the top of each taco, and pass sour cream at the table if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F39E1B30CC536248AE91E2A211FA6767', X'302FD140E95A6246B1FF4BDE022B5EBE', X'302FD140E95A6246B1FF4BDE022B5EBE', 0, NULL, NULL, 2, NULL, 0, 'We like to put all the ingredients in separate bowls in the middle of the table, stick a spoon or fork in each, and let everyone assemble their own taco bowl. You could make the bowls before serving if it''s easier to manage -- just arrange a bit of everything in its own little pile in each bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F2065BE0234FD4FA00D2EACB2BDC461', X'5A23DECCE49DD4499339BF45F6A6BD2D', X'5A23DECCE49DD4499339BF45F6A6BD2D', 0, NULL, NULL, 2, NULL, 0, 'Shuck the corn and cut the kernels off the cobs. Reserve the cobs to make soup (recipe coming soon).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9995E38482E3F840B631E6BF61518182', X'5A23DECCE49DD4499339BF45F6A6BD2D', X'5A23DECCE49DD4499339BF45F6A6BD2D', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil over medium-high heat in a cast iron or other heavy skillet. When it shimmers, add the corn kernels and let sit for a few minutes to start browning. Stir and let sit again. Repeat this process until there is nice browning in lots of spots, 10 to 15 minutes total.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF3A8DE643BDDB44864D4088CADC434D', X'5A23DECCE49DD4499339BF45F6A6BD2D', X'5A23DECCE49DD4499339BF45F6A6BD2D', 2, NULL, NULL, 2, NULL, 0, 'Add the minced garlic to the skillet and cook, stirring, for 30 seconds. Remove from heat and let cool for at least 10 minutes, until just warm. (Place in a bowl if you like to speed cooling.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21FC1F0EAD9CDF49A2A0F5020BF862DC', X'5A23DECCE49DD4499339BF45F6A6BD2D', X'5A23DECCE49DD4499339BF45F6A6BD2D', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the mayonnaise, chili powder, and some salt and pepper. Stir into the corn. Add the scallions, cilantro, lime juice and cotija and toss. Taste for seasoning and add more of anything you like. Salud!');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0376ADF27B088940BC6E04C9F8171589', X'E02A43F8CB1F80479900ECB73EA99E7B', X'E02A43F8CB1F80479900ECB73EA99E7B', 0, NULL, NULL, 2, NULL, 0, 'Roughly chop cilantro. Combine all ingredients in a blender and blend until smooth, stopping to push cilantro down into the blades as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98F8548CD2B2FC47ABEEA8578157F57D', X'C941891DD6652940ABFE9B446E9DF66A', X'C941891DD6652940ABFE9B446E9DF66A', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a medium pot over medium-high heat. Add the onion and pepper and cook, stirring occasionally, until beginning to soften, about 5 minutes. Add garlic and cook, stirring, one minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63EC1993C5D7904CAFA8E9C6640001D3', X'C941891DD6652940ABFE9B446E9DF66A', X'C941891DD6652940ABFE9B446E9DF66A', 1, NULL, NULL, 2, NULL, 0, 'Stir in the beans, tomatoes (if using whole tomatoes, crush in your hands before adding) or broth, chili powder, and cumin, and bring to a boil. Partially cover, reduce heat to maintain a gentle simmer, and cook, stirring occasionally, until juices have thickened and flavors have melded, about 15 minutes. Mixture should be quite thick so it won''t escape from taco shells, but not bone-dry. Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD4B6F72BB446B4FA68F67DB7B01D1D8', X'C941891DD6652940ABFE9B446E9DF66A', X'C941891DD6652940ABFE9B446E9DF66A', 2, NULL, NULL, 2, NULL, 0, 'To serve, place about 1/3 cup bean mixture in the center of a tortilla, and top with pickled onions, guacamole, olives, and cilantro. I like to put the toppings in small bowls on the table and let everyone help themselves. Kids like these tacos anyway, but it helps their enthusiasm level to have been a part of the process.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C1FF2499D3ACF48A7F3BC081E217C5B', X'D4C6C466D349F4478C018B7C95730D6C', X'D4C6C466D349F4478C018B7C95730D6C', 0, NULL, NULL, 2, NULL, 0, 'Peel the onions and halve from tip to root. Very thinly slice each half from tip to root.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90A602B32D6F5040B4F82F78C9992746', X'D4C6C466D349F4478C018B7C95730D6C', X'D4C6C466D349F4478C018B7C95730D6C', 1, NULL, NULL, 2, NULL, 0, 'In a quart-size mason jar or medium bowl with a lid, combine the lime juice, red wine vinegar, white vinegar, sugar, and salt. Stir (or put a lid on the jar and shake gently) to completely dissolve sugar and salt.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99A9623471B21B4AAB5449E4A7660A5E', X'D4C6C466D349F4478C018B7C95730D6C', X'D4C6C466D349F4478C018B7C95730D6C', 2, NULL, NULL, 2, NULL, 0, 'Add the sliced onions and the herbs to the jar or bowl. Stir and press a bit to submerge all the onions and herbs in the liquid. Top off with an extra splash of vinegar if necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B62590732521349B87086867EAAF250', X'D4C6C466D349F4478C018B7C95730D6C', X'D4C6C466D349F4478C018B7C95730D6C', 3, NULL, NULL, 2, NULL, 0, 'Pickle these babies in the fridge for 24 hours or simply on the counter at room temperature for an hour or two before serving as a condiment for tacos, sandwiches, salads — you name it. They keep very well in the fridge for up to a month.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02F6755E8252484B86D099F7A99E33E9', X'C11B5A6F16D10740B2547676A69BBBF4', X'C11B5A6F16D10740B2547676A69BBBF4', 0, NULL, NULL, 2, NULL, 0, 'In a 10-inch skillet, heat the olive oil over medium heat until shimmering.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6CE44EB6943AF498CD7179C2AC3EA07', X'C11B5A6F16D10740B2547676A69BBBF4', X'C11B5A6F16D10740B2547676A69BBBF4', 1, NULL, NULL, 2, NULL, 0, 'Add the pepitas and toss to coat with the oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64A0E7BADE7B434C88777DBFFCFEA751', X'C11B5A6F16D10740B2547676A69BBBF4', X'C11B5A6F16D10740B2547676A69BBBF4', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle on the paprika, salt, garlic powder, and cayenne pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D389BC8557AA840B8CEF3928A3F4AE2', X'C11B5A6F16D10740B2547676A69BBBF4', X'C11B5A6F16D10740B2547676A69BBBF4', 3, NULL, NULL, 2, NULL, 0, 'Cook, tossing frequently, until many of the pepitas have turned light brown and made a popping sound.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'497C60AA8C19CF47B95D0E474E1F25F8', X'C11B5A6F16D10740B2547676A69BBBF4', X'C11B5A6F16D10740B2547676A69BBBF4', 4, NULL, NULL, 2, NULL, 0, 'Pour into a small bowl and serve as a bar snack.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6262A136D456F48B000AA0997DE17D5', X'61D08AE5C218934CBE37D99BBFED963B', X'61D08AE5C218934CBE37D99BBFED963B', 0, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron skillet over medium heat for two minutes. Open the hamburger buns and brush each inside face with some of the melted butter. Place buns buttered side down (probably in two batches, depending on your skillet) and toast for a couple of minutes, until light golden brown on the underside. Place buns on serving plates.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FA0DA9EA971324994F315A303A29541', X'61D08AE5C218934CBE37D99BBFED963B', X'61D08AE5C218934CBE37D99BBFED963B', 1, NULL, NULL, 2, NULL, 0, 'Raise heat to medium-high. Divide beef into four hockey pucks and sprinkle both sides generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17CA54BAC13D6349B3B7494094267E86', X'61D08AE5C218934CBE37D99BBFED963B', X'61D08AE5C218934CBE37D99BBFED963B', 2, NULL, NULL, 2, NULL, 0, 'Place pucks onto skillet and use a sturdy metal spatula to firmly smash each puck into a patty that''s about 1/3-inch thick. After this, absolutely ignore the burgers for about three minutes, until the undersides are brown and crisp at the edges and the juices on the surface are hot and bubbly. Scrape up each patty and any brown bits with the spatula and flip it. Cook one to two minutes more. Place a slice of cheese on top of each burger at the very end of cooking to melt just enough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1828A2D61C952641B0F2562AB252566F', X'61D08AE5C218934CBE37D99BBFED963B', X'61D08AE5C218934CBE37D99BBFED963B', 3, NULL, NULL, 2, NULL, 0, 'To make the sauce, stir together all sauce ingredients in a small bowl until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6B95EFCA7A08D4284C3917782B082B2', X'61D08AE5C218934CBE37D99BBFED963B', X'61D08AE5C218934CBE37D99BBFED963B', 4, NULL, NULL, 2, NULL, 0, 'Transfer patties to buns. Layer each burger with two tomato slices and a piece of lettuce. Spoon 1/4 of the sauce onto each top bun half. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9924D23BA9FCBF4F97D39569650C5693', X'5425C840C6DF784C8167E68DA945B3BF', X'5425C840C6DF784C8167E68DA945B3BF', 0, NULL, NULL, 2, NULL, 0, 'Stir all ingredients together and serve as a sauce or salad dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC4244B6BC10984FA62D7BA02EC4DCC6', X'5425C840C6DF784C8167E68DA945B3BF', X'5425C840C6DF784C8167E68DA945B3BF', 1, NULL, NULL, 2, NULL, 0, 'Store leftovers tightly sealed in the fridge for up to a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8EA93D1DEF2524A87C184400968F5CE', X'E3E9ABF814AFE34A9D848A158B4A464A', X'E3E9ABF814AFE34A9D848A158B4A464A', 0, NULL, NULL, 2, NULL, 0, 'Warm the olive oil over medium-high heat in a 12-inch nonstick skillet. Add shallot and garlic and cook, stirring frequently, until beginning to soften, about a minute. Stir pork into skillet, breaking up with a spatula into small pieces, and continue cooking for a few minutes until lightly browned. Add beans, tomatoes (along with all their juices), salt, chili powder, cumin, paprika, oregano and cayenne to skillet and stir to combine. Reduce heat to medium-low and simmer for five minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F568CFDC1020CB46A97D74C11587E20F', X'E3E9ABF814AFE34A9D848A158B4A464A', X'E3E9ABF814AFE34A9D848A158B4A464A', 1, NULL, NULL, 2, NULL, 0, 'To serve, warm taco shells according to package directions. Fill each shell with some of the pork mixture and top with Cotija, pickled onions and cilantro. Serve with lime wedges.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388073AA25D4094A9ABE51F20EC32752', X'6AE5572956DA614B921414214F34AFCF', X'6AE5572956DA614B921414214F34AFCF', 0, NULL, NULL, 2, NULL, 0, 'In a wide pan with a lid (like this one, this one, or this one), melt butter over medium-high heat. Add onion and cook, stirring occasionally, until translucent and just starting to brown, about five minutes. While onion cooks, rinse rice in a fine-mesh sieve until the water runs clear and shake out any excess water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DF02426794A1C489ADDFF3B6C66074B', X'6AE5572956DA614B921414214F34AFCF', X'6AE5572956DA614B921414214F34AFCF', 1, NULL, NULL, 2, NULL, 0, 'Add garlic to pan and cook, stirring, until very fragrant, about a minute. Add rice and stir to coat with butter, then cook, stirring once or twice, for two minutes. Pour in stock and give it all a good stir. Bring to a boil over high heat. Then cover pan, lower heat to maintain a gentle simmer, and cook until broth is absorbed, 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1523FD2239C2543A328641D9806793C', X'6AE5572956DA614B921414214F34AFCF', X'6AE5572956DA614B921414214F34AFCF', 2, NULL, NULL, 2, NULL, 0, 'Off the heat, place a clean kitchen towel between the pan and the lid to absorb excess steam and let pilaf rest for 5 to 10 minutes. Sprinkle with chives and almonds if using and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BADA941943FCC48AD8487B7483C4DB0', X'A66781E9BD61504692BF6BA5DE542805', X'A66781E9BD61504692BF6BA5DE542805', 0, NULL, NULL, 2, NULL, 0, 'Preheat a 12-inch cast iron skillet on the stovetop for five minutes over high heat. Meanwhile, wipe any dirt off the mushrooms with a paper towel and, if you like (and your mushrooms are whole), rinse under running water and spin dry. Remove stems from shiitakes and oyster mushrooms and trim a slice of the stem end off the baby bellas. Slice baby bellas and shiitakes about 1/4-inch thick and cut oyster mushrooms into small bite-sized pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CC3D2F81B7E0F4392E9AB9B38D1E1CA', X'A66781E9BD61504692BF6BA5DE542805', X'A66781E9BD61504692BF6BA5DE542805', 1, NULL, NULL, 2, NULL, 0, 'Add butter to pan. When melted and bubbly, add mushrooms and toss to coat. Cook, stirring only occasionally, for about 10 minutes, until well browned in plenty of spots. Stir in salt and pepper to taste and garnish with parsley if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D345C51D50412F40ACB7246A84280605', X'600D31AA9D876F42ACFC15824F71234D', X'600D31AA9D876F42ACFC15824F71234D', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large pot over medium-high heat. Add onion, carrots, celery and salt and stir to coat with oil. Cook for about 5 minutes, stirring occasionally, until beginning to soften. If you happen to get distracted and the veggies brown a little bit, it''ll add some nice depth of flavor.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6B24D779B82E3498D3D4FB67AE9B874', X'600D31AA9D876F42ACFC15824F71234D', X'600D31AA9D876F42ACFC15824F71234D', 1, NULL, NULL, 2, NULL, 0, 'Stir in stock, bring to a boil, then cover and reduce heat to simmer until veggies are tender. Remove lid and add spinach and tortellini. Cook according to tortellini package instructions (usually just simmering for a couple of minutes). Off the heat, stir in parsley and black pepper to taste.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC7FE48EA8C0D84D98DC1CC95D5CFA6F', X'600D31AA9D876F42ACFC15824F71234D', X'600D31AA9D876F42ACFC15824F71234D', 2, NULL, NULL, 2, NULL, 0, 'Ladle into bowls and serve with cheese to pass at the table.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E19856633C7C804F9F69D3E777E16BFA', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 0, NULL, NULL, 2, NULL, 0, 'Combine the vegetable broth and dried shiitakes in a medium pot. Bring to a boil, then cover and remove from heat. Let mushrooms steep for at least 30 minutes, up to 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16DB4EC5D0B09E46B462AAE7D461D5CD', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 1, NULL, NULL, 2, NULL, 0, 'Remove mushrooms from pot and roughly chop, removing and discarding stems. Add mushrooms to a blender with one cup of the broth and puree until perfectly smooth. Add this mixture back to the stock pot along with the tamari. The broth can sit at this stage until shortly before serving, up to a few days in the fridge if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AEB12D91C0B1342BA11D93598A7678F', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 2, NULL, NULL, 2, NULL, 0, 'In a large frying pan, heat one tablespoon of the vegetable oil over medium-high heat. Add the sliced onion and cook, stirring frequently, until softened and lightly browned in spots, about 5 minutes. Add the minced garlic and ginger and cook, stirring, two minutes more. Add this mixture to the stock pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0D03F3E2C57214CB4DB906E64F84604', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 3, NULL, NULL, 2, NULL, 0, 'Bring a large pot of unsalted water to a boil. Add the spinach and cook for about a minute, until just wilted. Remove from pot and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'964BD463743DFA4FA76A4F4461836774', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 4, NULL, NULL, 2, NULL, 0, 'Add eggs to pot, reduce heat to simmer, and cook for 7 minutes. Remove eggs to an ice bath and carefully peel when cool enough to touch.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37376DF4C1EFB6499F32B2D1F7D0B0D6', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 5, NULL, NULL, 2, NULL, 0, 'Return water to boil, then add noodles and cook according to package directions. Drain and divide noodles among four large bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0407284DD20F0540A3A02D2F381016D7', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 6, NULL, NULL, 2, NULL, 0, 'While noodles cook, add the remaining tablespoon of vegetable oil to the same frying pan used for the onion and heat over medium-high. Add the sliced shiitakes along with a couple of good pinches of salt. Cook, stirring occasionally, until reduced in volume and nicely browned in spots, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3F970B224F4D34C89681C26BC180B44', X'9B4163DCD222C34CAA259C3DC9E7B523', X'9B4163DCD222C34CAA259C3DC9E7B523', 7, NULL, NULL, 2, NULL, 0, 'To assemble the ramen, bring the broth back up to a simmer and then remove from heat. Off the heat, whisk in the butter, miso paste and mirin. Ladle some broth over the noodles in each bowl, just shy of the level of the noodles. Arrange a little pile of spinach and of shiitakes in each bowl. Slice the tofu and arrange a few slices in each bowl. Cut each egg in half and place two halves in each bowl. Sprinkle with scallions, gomasio and a little toasted sesame chili oil, with extra garnishes to pass at the table. Serve with chopsticks and large spoons.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D736AB21D962EB42B788FCA9C3C12FE7', X'966CB31BEE1F634FA8A98A4028B62E9B', X'966CB31BEE1F634FA8A98A4028B62E9B', 0, NULL, NULL, 2, NULL, 0, 'Cut or tear the lobster meat into bite-sized pieces and place in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F00B863AF2B6724C87D5CCF61FF83595', X'966CB31BEE1F634FA8A98A4028B62E9B', X'966CB31BEE1F634FA8A98A4028B62E9B', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the mayonnaise, lemon juice, capers, chives and salt and pepper. Pour the dressing over the lobster and toss gently to coat all the meat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41375B70759DC64F81069443D9F1121E', X'966CB31BEE1F634FA8A98A4028B62E9B', X'966CB31BEE1F634FA8A98A4028B62E9B', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter on a skillet over medium-high heat. Set the buns down on the skillet on one of their side edges. Cook for a minute or two until golden brown, then flip and cook the opposite side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87A1574529326E4DA2BDC8F00F798515', X'966CB31BEE1F634FA8A98A4028B62E9B', X'966CB31BEE1F634FA8A98A4028B62E9B', 3, NULL, NULL, 2, NULL, 0, 'Divide the lobster meat among the buns and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07BE084D03D6BF46BEA5A8A9B3EC61E8', X'251A14ACD881014C85CE224821634796', X'251A14ACD881014C85CE224821634796', 0, NULL, NULL, 2, NULL, 0, 'Roughly chop cilantro. Combine all ingredients in a blender and blend until smooth, stopping to push cilantro down into the blades as necessary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6974B0FD26D7D4BB9AC8DD257007F52', X'A37532E9E4062643949024BE5D00AAC2', X'A37532E9E4062643949024BE5D00AAC2', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, beat together the butter and both sugars with an electric mixer until lightened in color and texture, about 3 minutes. Add eggs and vanilla and beat until combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AAB102E8DCA4146AA2A7C21FE31D9BA', X'A37532E9E4062643949024BE5D00AAC2', X'A37532E9E4062643949024BE5D00AAC2', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the flour, baking powder, baking soda, and salt. Add the dry ingredients to the wet ingredients and stir together with a wooden spoon or silicone spatula. Add the oats and stir to combine. Then stir in the chocolate chips, Hershey''s bars, and nuts.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E9E7F53A1F4F648854B3240CF66D654', X'A37532E9E4062643949024BE5D00AAC2', X'A37532E9E4062643949024BE5D00AAC2', 2, NULL, NULL, 2, NULL, 0, 'To bake, preheat the oven to 375°F. Using your hands or a couple of spoons, form the batter into loose, golf-ball-sized mounds. Place two inches apart on ungreased cookie sheets. Bake for eight to nine minutes, until tops are just golden and cookies are still soft. On the off chance there are any cookies left once they''re cool enough to handle, cool them completely on racks before storing in an airtight container for up to a couple of days.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B2B93235629F84FA4C8D7BB4418864F', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 250°F (121°C) with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A963FE32D0362840B9CB3B39F8E39FB0', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 1, NULL, NULL, 2, NULL, 0, 'Pat sirloin cap dry with paper towels.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F3CD9B03CBF14FA2C0622F1CBD4651', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 2, NULL, NULL, 2, NULL, 0, 'Score the fat in a crosshatch pattern. Make the cuts deep enough to cut through the fat but not through the meat. See video and step-by-step photos in post for visual cues.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'894B8F3305CCA944ACAE3B72C4DA20A7', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle both sides generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84F7A3B6BE38134ABAC3A0C771E03DCC', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 4, NULL, NULL, 2, NULL, 0, 'Place steaks cut-side down in a cast iron pan.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C15992524FF341A4AA5EB840EBBD77', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 5, NULL, NULL, 2, NULL, 0, 'Roast for about 30 minutes, until internal temperature reaches 105°F (41°C).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0503516C71CFF94AA0BCE14A2E3C66C0', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 6, NULL, NULL, 2, NULL, 0, 'Remove steaks from pan and place pan over high heat on stovetop.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53D639157D5D2547BD1FD1357519C260', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 7, NULL, NULL, 2, NULL, 0, 'Place steaks back in pan and sear 1 to 2 minutes per side, until golden brown.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEBF8549C5E6FA42A22E0585D5F90B90', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 8, NULL, NULL, 2, NULL, 0, 'Rest 10 minutes before slicing against the grain.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15617E05E1F59E4A85BB5800645A5AE2', X'9DECEEDC05F8604694ADA468AB502F71', X'9DECEEDC05F8604694ADA468AB502F71', 9, NULL, NULL, 2, NULL, 0, 'Serve with chimichurri sauce (if you like).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62FCA65924DAA74FA142E64499F6553F', X'44C7F19A756A51438A9B06E2C4BFC001', X'44C7F19A756A51438A9B06E2C4BFC001', 0, NULL, NULL, 2, NULL, 0, 'To make the tuna poke, cut tuna into bite-sized cubes. I like them on the smaller side — maybe 1/2-inch dice — but it''s totally up to you. Place cubes in a small mixing bowl and add scallion, shallot, tamari, sesame oil and chili flakes. Toss gently and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9FFD72B57DF4B45815EF393090E36BA', X'44C7F19A756A51438A9B06E2C4BFC001', X'44C7F19A756A51438A9B06E2C4BFC001', 1, NULL, NULL, 2, NULL, 0, 'To make the salmon poke, cut salmon into cubes to match the size of the tuna and place in a small mixing bowl. In another small bowl, stir together the mayonnaise, sriracha and tamari. Spoon sauce over salmon and toss gently to coat. Stir in shallot and scallion and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'623D235304BF0A4E9D76E045FAFBA4A4', X'44C7F19A756A51438A9B06E2C4BFC001', X'44C7F19A756A51438A9B06E2C4BFC001', 2, NULL, NULL, 2, NULL, 0, 'To assemble the bowls, place some rice in the bottom of each serving bowl. Arrange portions of tuna, salmon, and whatever additional ingredients you like. Pictured here are avocado, seaweed salad that I bought already prepared, some simple shelled edamame, a little bit of salmon roe, and a nice sprinkle of gomasio, freshly ground black pepper, and chili toasted sesame oil.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E13EEB82777C34DACE0F2F978812705', X'9A22FFDD3FDAC346A9E7637C4FD13DB5', X'9A22FFDD3FDAC346A9E7637C4FD13DB5', 0, NULL, NULL, 2, NULL, 0, 'Prepare a batch of sushi rice and let it cool to body temperature (or room temperature if you prefer) while you prepare the fillings.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEF00AE71253E84782F00B1FB60B2089', X'B1C3E14C537FE741B6EB9DBD9AF712A6', X'B1C3E14C537FE741B6EB9DBD9AF712A6', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl (or right in the bowl of your stand mixer, if you like), stir together the sourdough starter, water, and sugar with a fork to break up the starter. It doesn''t have to be perfectly incorporated into the water.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32A0278B0BEDAA4E8B11AB5BF6903F53', X'B1C3E14C537FE741B6EB9DBD9AF712A6', X'B1C3E14C537FE741B6EB9DBD9AF712A6', 1, NULL, NULL, 2, NULL, 0, 'Add the flour and the salt. Mix until there''s no loose flour. I find it''s easiest to start mixing with the fork and then switch to using my hand as the dough stiffens and gets less sticky.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEDB8449F0189646B3D1D121CEB1D172', X'B1C3E14C537FE741B6EB9DBD9AF712A6', X'B1C3E14C537FE741B6EB9DBD9AF712A6', 2, NULL, NULL, 2, NULL, 0, 'Knead the dough for eight minutes. You can do this by hand, but it''s much easier in a stand mixer since this dough is quite stiff. In a stand mixer, use the dough hook on Speed 2.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D87B279B4387AC498F5AFE7986D9DA20', X'B1C3E14C537FE741B6EB9DBD9AF712A6', X'B1C3E14C537FE741B6EB9DBD9AF712A6', 3, NULL, NULL, 2, NULL, 0, 'Clean the bowl and oil it. Use your hands to work the dough into a nice ball and place it in the bowl. Turn it over a few times to coat with oil. Cover with a damp kitchen towel, a plate that''s big enough to sit on the rim of the bowl, or plastic wrap.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3775BA8736C2B6469285E0158C4A088E', X'B1C3E14C537FE741B6EB9DBD9AF712A6', X'B1C3E14C537FE741B6EB9DBD9AF712A6', 4, NULL, NULL, 2, NULL, 0, 'Let the dough rise for eight hours. At this point, you can make the bagels or stash the dough in the fridge overnight.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37F8CED170B7E4E94ECBFFEADA4E1B9', X'5D70DB416A0EF947986DD013079178EB', X'5D70DB416A0EF947986DD013079178EB', 0, NULL, NULL, 2, NULL, 0, 'Drain water or oil from tuna and place into a medium mixing bowl. Break up any large pieces of tuna with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38FA5C52E4BD5C4DB76932C11B96BCE6', X'5D70DB416A0EF947986DD013079178EB', X'5D70DB416A0EF947986DD013079178EB', 1, NULL, NULL, 2, NULL, 0, 'Add celery, shallot, mustard, mayonnaise, salt, and pepper, along with any of the optional additions. Stir to combine well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABDB9698F12A4C48ABC29184AC01779E', X'5D70DB416A0EF947986DD013079178EB', X'5D70DB416A0EF947986DD013079178EB', 2, NULL, NULL, 2, NULL, 0, 'Serve with salad or on a sandwich or tuna melt. For salads and sandwiches, I like to add a little freshly squeezed lemon juice right before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13452F12BE36D24795F77605B219E668', X'E4117728B4D6F7438FF9E093BF879997', X'E4117728B4D6F7438FF9E093BF879997', 0, NULL, NULL, 2, NULL, 0, 'Peel and trim ends from onions. Halve onions from root to tip, then slice thinly from root to tip. See step-by-step photos and video for visual cues. Cutting from root to tip (as opposed to crosswise) is the way to ensure the onion slices will hold enough of their shape while caramelizing, rather than dissolving into mush.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'509C12DF3F8B1744B6996997075A6AD2', X'E4117728B4D6F7438FF9E093BF879997', X'E4117728B4D6F7438FF9E093BF879997', 1, NULL, NULL, 2, NULL, 0, 'Heat oil and melt butter together over medium heat in a 10-inch frying pan with a heavy bottom.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77D9785079B27645A373E1518A0D08EC', X'E4117728B4D6F7438FF9E093BF879997', X'E4117728B4D6F7438FF9E093BF879997', 2, NULL, NULL, 2, NULL, 0, 'Add onions and stir well to coat with oil and butter. Add rosemary, thyme, and salt, and stir again.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CF0FA770EB37742AF93BBCE0EC6094D', X'E4117728B4D6F7438FF9E093BF879997', X'E4117728B4D6F7438FF9E093BF879997', 3, NULL, NULL, 2, NULL, 0, 'For the first 20 minutes or so, the onions will sweat a lot and reduce in volume substantially. Stir occasionally during this time to keep them cooking evenly, and adjust the heat as necessary to prevent burning. Don''t expect to see too much color on the onions during this time.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'387E43AB80B5284289C34CE73C6970CC', X'E4117728B4D6F7438FF9E093BF879997', X'E4117728B4D6F7438FF9E093BF879997', 4, NULL, NULL, 2, NULL, 0, 'During the next 20 to 40 minutes, the onions will really start to caramelize. Your job during this time is to stir the onions not too little but not too much. Allow them to get a little bit browned on the bottom without burning, and then scrape up the browned bits while stirring the onions with a metal spoon or spatula. Typically the heat should be in the medium to medium-low range, but your mileage may vary.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAA051546172BC47B505C01E1DD1C297', X'E4117728B4D6F7438FF9E093BF879997', X'E4117728B4D6F7438FF9E093BF879997', 5, NULL, NULL, 2, NULL, 0, 'Over time you''ll learn exactly how dark you like your caramelized onions. Our favorite point to stop is when they''re a gorgeous mahogany brown and just on the cusp of jammy. At this stage they''re super sweet and savory and not at all burned.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE6B67B027C39F439763AAEBE744A427', X'E4117728B4D6F7438FF9E093BF879997', X'E4117728B4D6F7438FF9E093BF879997', 6, NULL, NULL, 2, NULL, 0, 'Remove any large herb stems before using these babies in whatever recipes you like (or just spooning over all your hopes and dreams).');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EB7B2AFB29A784885CE203335E06EAA', X'6C7817EA34E76B4996D3AFB3AAFC715F', X'6C7817EA34E76B4996D3AFB3AAFC715F', 0, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron skillet over medium heat for two minutes. Open the hamburger buns and brush each inside face with some of the melted butter. Place buns buttered side down (probably in two batches, depending on your skillet) and toast for a couple of minutes, until light golden brown on the underside. Place buns on serving plates.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AFFE4BF9D2174429604AD314FA6129C', X'6C7817EA34E76B4996D3AFB3AAFC715F', X'6C7817EA34E76B4996D3AFB3AAFC715F', 1, NULL, NULL, 2, NULL, 0, 'Raise heat to medium-high. Divide beef into four hockey pucks and sprinkle both sides generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB617FBA9ABF574CA00EA3728206AB88', X'6C7817EA34E76B4996D3AFB3AAFC715F', X'6C7817EA34E76B4996D3AFB3AAFC715F', 2, NULL, NULL, 2, NULL, 0, 'Place pucks onto skillet and use a sturdy metal spatula to firmly smash each puck into a patty that''s about 1/3-inch thick. After this, absolutely ignore the burgers for about three minutes, until the undersides are brown and crisp at the edges and the juices on the surface are hot and bubbly. Scrape up each patty and any brown bits with the spatula and flip it. Cook one to two minutes more. Place a slice of cheese on top of each burger at the very end of cooking to melt just enough.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'218229EF4481E945A201F2C0A5C94B90', X'6C7817EA34E76B4996D3AFB3AAFC715F', X'6C7817EA34E76B4996D3AFB3AAFC715F', 3, NULL, NULL, 2, NULL, 0, 'To make the sauce, stir together all sauce ingredients in a small bowl until well combined.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5886B8156AC0A41BEBF0A05B3EC8544', X'6C7817EA34E76B4996D3AFB3AAFC715F', X'6C7817EA34E76B4996D3AFB3AAFC715F', 4, NULL, NULL, 2, NULL, 0, 'Transfer patties to buns. Layer each burger with two tomato slices and a piece of lettuce. Spoon 1/4 of the sauce onto each top bun half. Serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04A70E98B33F2F48855A111BA5FDD368', X'5B168F1AEAA3784391E88CFFD7317ED9', X'5B168F1AEAA3784391E88CFFD7317ED9', 0, NULL, NULL, 2, NULL, 0, 'In a small pot, stir together sake, mirin, soy sauce, and sugar. Warm over medium heat, stirring occasionally, until sugar is dissolved.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59D6F6091567A04BA0FFB8BC3CB1F1CA', X'5B168F1AEAA3784391E88CFFD7317ED9', X'5B168F1AEAA3784391E88CFFD7317ED9', 1, NULL, NULL, 2, NULL, 0, 'Off the heat, add one heaping cup of ice to the pot to both dilute and chill the marinade. (Alternatively, you can place the ice in a 4-cup glass measuring cup or nonreactive bowl and pour the marinade overtop.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'993A0E8CB170174998F706BD47F05CD1', X'5B168F1AEAA3784391E88CFFD7317ED9', X'5B168F1AEAA3784391E88CFFD7317ED9', 2, NULL, NULL, 2, NULL, 0, 'Place peeled seven-minute eggs into marinade, cover tightly, and refrigerate for about 24 hours, turning once or twice if you think of it.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A726030D2A46B34F87F7ECAFB98A766B', X'5B168F1AEAA3784391E88CFFD7317ED9', X'5B168F1AEAA3784391E88CFFD7317ED9', 3, NULL, NULL, 2, NULL, 0, 'For less intense flavor, you can remove the eggs at any point after about eight hours. For more intense flavor you can let the eggs marinate a bit longer. However, the salt and sugar in the marinade will make the eggs tough eventually, so don''t leave them for more than about 36 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'381827E96E5B9E4FAD490E5FBF037D8B', X'F03C8914C465D84B84C657541E4A23F8', X'F03C8914C465D84B84C657541E4A23F8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4A78088C4675A468A072A0056393BBD', X'F03C8914C465D84B84C657541E4A23F8', X'F03C8914C465D84B84C657541E4A23F8', 1, NULL, NULL, 2, NULL, 0, 'Line a rimmed half-sheet pan with aluminum foil if you like. (We sometimes do and sometimes don''t. It helps a bit with cleanup but isn''t strictly necessary.) Arrange bacon in a single layer. If you have to overlap the slices just a little, it''s okay — they''ll shrink.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60AE3C67858CA54B8E9ADECDD685B5C4', X'F03C8914C465D84B84C657541E4A23F8', X'F03C8914C465D84B84C657541E4A23F8', 2, NULL, NULL, 2, NULL, 0, 'Bake for 15-20 minutes, more or less, until crisped to your liking. Watch carefully toward the end. Cooking time will vary depending on thickness.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0C6E8614473CE4C832C1DFC1312961B', X'F03C8914C465D84B84C657541E4A23F8', X'F03C8914C465D84B84C657541E4A23F8', 3, NULL, NULL, 2, NULL, 0, 'Drain on paper towels and serve.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BD4626FE920B14DAC365D8D0542F6E2', X'107212FA2F777549969E16FAB67B9FEF', X'107212FA2F777549969E16FAB67B9FEF', 0, NULL, NULL, 2, NULL, 0, 'Slice each egg in half lengthwise and carefully remove yolks to a medium bowl. Mash yolks well with a fork.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'457A34E52C0FB240B6F8B0608B686D7C', X'107212FA2F777549969E16FAB67B9FEF', X'107212FA2F777549969E16FAB67B9FEF', 1, NULL, NULL, 2, NULL, 0, 'Add mayonnaise, miso, tamari, mirin, rice vinegar, toasted sesame oil, and pepper and continue mashing and blending with the fork until yolk mixture is creamy. Finely chop the 1/4 cup of shiitake bacon and stir into yolk mixture.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B568A2A41307B644A62D8C51F953D0F4', X'107212FA2F777549969E16FAB67B9FEF', X'107212FA2F777549969E16FAB67B9FEF', 2, NULL, NULL, 2, NULL, 0, 'Using a piping bag fitted with a large star tip or plain tip, a resealable plastic bag with one of the bottom corners snipped off, or a spoon, pipe or spoon the yolk mixture back into the egg halves.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C507306C98EB54DA75A3DE8B3B0458D', X'107212FA2F777549969E16FAB67B9FEF', X'107212FA2F777549969E16FAB67B9FEF', 3, NULL, NULL, 2, NULL, 0, 'Garnish each egg half with a piece of shiitake bacon, a dot of sriracha and a sprinkle of gomasio.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89DA034180AE2E4C847A521BB9F7880A', X'638AFE08317336439D3CC941803929EC', X'638AFE08317336439D3CC941803929EC', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9202408F74E6A24693D68A845F770B37', X'638AFE08317336439D3CC941803929EC', X'638AFE08317336439D3CC941803929EC', 1, NULL, NULL, 2, NULL, 0, 'Pat filets dry with paper towels and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6704F40287E33A429A540E2D2548F1A0', X'638AFE08317336439D3CC941803929EC', X'638AFE08317336439D3CC941803929EC', 2, NULL, NULL, 2, NULL, 0, 'Heat a 12-inch cast iron pan over high heat until very hot. Carefully pour in oil and coat bottom of pan by swirling or spreading with a spatula.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6281810F381914BB35946E2B1BA8959', X'638AFE08317336439D3CC941803929EC', X'638AFE08317336439D3CC941803929EC', 3, NULL, NULL, 2, NULL, 0, 'Place filets in pan and cook undisturbed for three minutes to sear. Flip once and cook two minutes more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1250DABD2C835747B75694F18FFF8E47', X'638AFE08317336439D3CC941803929EC', X'638AFE08317336439D3CC941803929EC', 4, NULL, NULL, 2, NULL, 0, 'Transfer pan to oven and cook just until meat reads 125°F on an instant read thermometer inserted into the center. Depending on thickness this will take from 2 to more than 5 minutes, so check early and often. The filet will continue to cook off the heat, so pulling it out of the pan at 125°F (or even a little before, if you like), will yield a beautiful, true medium-rare (130°) finished product');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1EB329C23BCAE4AA0707664D8171AA7', X'638AFE08317336439D3CC941803929EC', X'638AFE08317336439D3CC941803929EC', 5, NULL, NULL, 2, NULL, 0, 'Transfer filets to a cutting board to rest while you make the pan sauce.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CB329CC15DA484AB0FE7AE841BD0E98', X'952E3C0BA6D62547BE28847EA197E282', X'952E3C0BA6D62547BE28847EA197E282', 0, NULL, NULL, 2, NULL, 0, 'Stir all ingredients together and serve as a sauce or salad dressing.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C87EA5DA8F50E84B9EF3662296BD34D3', X'952E3C0BA6D62547BE28847EA197E282', X'952E3C0BA6D62547BE28847EA197E282', 1, NULL, NULL, 2, NULL, 0, 'Store leftovers tightly sealed in the fridge for up to a week.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C947BEE12B16E049AF6735466D5CAF34', X'0600EE1437C7904383E31114F95DF1B4', X'0600EE1437C7904383E31114F95DF1B4', 0, NULL, NULL, 2, NULL, 0, 'Preheat a 12-inch cast iron skillet on the stovetop for five minutes over high heat. Meanwhile, wipe any dirt off the mushrooms with a paper towel and, if you like (and your mushrooms are whole), rinse under running water and spin dry. Remove stems from shiitakes and oyster mushrooms and trim a slice of the stem end off the baby bellas. Slice baby bellas and shiitakes about 1/4-inch thick and cut oyster mushrooms into small bite-sized pieces.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'306947AE65E209449125B2B0D2FFA213', X'0600EE1437C7904383E31114F95DF1B4', X'0600EE1437C7904383E31114F95DF1B4', 1, NULL, NULL, 2, NULL, 0, 'Add butter to pan. When melted and bubbly, add mushrooms and toss to coat. Cook, stirring only occasionally, for about 10 minutes, until well browned in plenty of spots. Stir in salt and pepper to taste and garnish with parsley if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51579E2BA2D88545BE1572A1E8C477D3', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', 0, NULL, NULL, 2, NULL, 0, 'Warm 1 tablespoon each of the olive oil and butter over medium-high heat in a 5 quart Dutch oven or other heavy lidded pot. Add mushrooms and cook, stirring just a few times, until nicely browned and reduced in volume by half. Remove mushrooms to a bowl and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1325C1F1A452314B8C056422AACA79AA', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', 1, NULL, NULL, 2, NULL, 0, 'In the same pot, heat the remaining butter and olive oil. Add onion, celery, carrots and 1/2 teaspoon of the salt and cook, stirring occasionally, until beginning to soften, about five minutes. Add the garlic and cook, stirring frequently, for a minute. Stir in the tomato paste and cook a minute more.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E550FF1BC912046A2059B16B1F5F143', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', 2, NULL, NULL, 2, NULL, 0, 'Add the vegetable broth, remaining 1/2 teaspoon salt, soy sauce, marmite, bay leaf and thyme. Raise heat to high. Rinse and drain the lentils and add to pot. When broth comes to a boil, reduce heat, cover, and simmer for about 30 minutes, until lentils are nice and tender. Remove from heat and remove thyme stems and bay leaf.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0943B24E9BD57B41893398DCC0862EF8', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', 3, NULL, NULL, 2, NULL, 0, 'If you have an immersion blender, pulse it 4 or 5 times to puree the stew just a little bit. (Alternatively, transfer about two cups of the stew to a blender and puree it, then return to pot, or simply forego this step if you prefer.)');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED25783CEB9E7B47873BBEC0634581CF', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', X'643D15DB7B1B2E4D8D6AD9634EBFCAFF', 4, NULL, NULL, 2, NULL, 0, 'Return the mushrooms and any accumulated juices to the pot along with the spinach and cream. Simmer briskly, uncovered, until spinach is wilted, about two minutes. Off the heat, stir in vinegar. Let sit for 10 minutes, then ladle into bowls and serve with good bread.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67E3BB4D65DBD34ABD56E65561CA447F', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 0, NULL, NULL, 2, NULL, 0, 'Fill a large bowl halfway with cold water. Peel the potatoes and spiralize them with a medium-sized blade according to manufacturer instructions. Submerge spirals in water and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCDD99EBD1623D40806B47FDD3938D29', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 1, NULL, NULL, 2, NULL, 0, 'In a medium pot, melt the butter over medium heat. Add the shallot, garlic and ginger and cook, stirring occasionally, until softened, about five minutes. Pour in the broth, raise heat to high, and bring to a boil. As soon as the broth boils, cover and reduce heat to maintain a bare simmer.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9F35996B4E8E347BB2DECC80B0F2B16', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the tamari, mirin and miso until smooth. Set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECF95CE322E15049A8B67E75B5D31577', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large pot of water to a boil, then reduce to simmer. Carefully lower the eggs into the water and simmer for seven minutes. Remove eggs with a slotted spoon and set aside to peel and halve when slightly cooled.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED908BE13A10394A91106B332DA3CFCB', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 4, NULL, NULL, 2, NULL, 0, 'Bring water back to a rolling boil. Add the bok choy and cook for two minutes. Remove to a large plate. Add snow peas and carrot matchsticks to pot and cook for 30 seconds. Remove to plate.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67A00C553C659D4E9795079DED1A8831', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 5, NULL, NULL, 2, NULL, 0, 'Add two tablespoons salt to the boiling water. Cook potato spirals for about three minutes, until just tender. Divide potatoes among four bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E225D7F28F8BC40AAB1DC8F3C5F9265', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 6, NULL, NULL, 2, NULL, 0, 'Remove broth from heat and whisk in the tamari mixture. Ladle some broth over the potato noodles in each bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'310126B7FFC1EB43971A2DD561C954CC', X'901CB3440A46074BB0FF27131782F4B4', X'901CB3440A46074BB0FF27131782F4B4', 7, NULL, NULL, 2, NULL, 0, 'Top each bowl with little piles of bok choy, snow peas, carrots, peppers, bean sprouts, cilantro and scallion, along with two egg halves. Sprinkle with gomasio and sesame oil. Serve immediately with chopsticks, a big spoon and a lime wedge on the side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E53E54A2540EB4F9AA8D3E6A9E7A224', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 0, NULL, NULL, 2, NULL, 0, 'Combine the vegetable broth and dried shiitakes in a medium pot. Bring to a boil, then cover and remove from heat. Let mushrooms steep for at least 30 minutes, up to 24 hours.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB5A34E35A84E14986342E0047925048', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 1, NULL, NULL, 2, NULL, 0, 'Remove mushrooms from pot and roughly chop, removing and discarding stems. Add mushrooms to a blender with one cup of the broth and puree until perfectly smooth. Add this mixture back to the stock pot along with the tamari. The broth can sit at this stage until shortly before serving, up to a few days in the fridge if you like.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6FC2C332100E648982BFD6FDD71F20F', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 2, NULL, NULL, 2, NULL, 0, 'In a large frying pan, heat one tablespoon of the vegetable oil over medium-high heat. Add the sliced onion and cook, stirring frequently, until softened and lightly browned in spots, about 5 minutes. Add the minced garlic and ginger and cook, stirring, two minutes more. Add this mixture to the stock pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A00344946A47D4E8BDC43B3ADA586B2', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 3, NULL, NULL, 2, NULL, 0, 'Bring a large pot of unsalted water to a boil. Add the spinach and cook for about a minute, until just wilted. Remove from pot and set aside.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00227551A2576840A2917AD0F94EA945', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 4, NULL, NULL, 2, NULL, 0, 'Add eggs to pot, reduce heat to simmer, and cook for 7 minutes. Remove eggs to an ice bath and carefully peel when cool enough to touch.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0A64B49D1A0474AB30BEA3A12C7D1D1', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 5, NULL, NULL, 2, NULL, 0, 'Return water to boil, then add noodles and cook according to package directions. Drain and divide noodles among four large bowls.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'747BBF5E061E0C41AD24FC114BB552F4', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 6, NULL, NULL, 2, NULL, 0, 'While noodles cook, add the remaining tablespoon of vegetable oil to the same frying pan used for the onion and heat over medium-high. Add the sliced shiitakes along with a couple of good pinches of salt. Cook, stirring occasionally, until reduced in volume and nicely browned in spots, about 10 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79321855D0D22E4BB3CE54FD787DF194', X'391D88A2062BEE43A7C2228C7E0D1231', X'391D88A2062BEE43A7C2228C7E0D1231', 7, NULL, NULL, 2, NULL, 0, 'To assemble the ramen, bring the broth back up to a simmer and then remove from heat. Off the heat, whisk in the butter, miso paste and mirin. Ladle some broth over the noodles in each bowl, just shy of the level of the noodles. Arrange a little pile of spinach and of shiitakes in each bowl. Slice the tofu and arrange a few slices in each bowl. Cut each egg in half and place two halves in each bowl. Sprinkle with scallions, gomasio and a little toasted sesame chili oil, with extra garnishes to pass at the table. Serve with chopsticks and large spoons.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'400134F014404B4683221860849031F5', X'E6F4CDC6546A734B87B3867A1176747B', X'E6F4CDC6546A734B87B3867A1176747B', 0, NULL, NULL, 2, NULL, 0, 'Cut or tear the lobster meat into bite-sized pieces and place in a medium bowl.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'427C37DD3C695A43AE2BF2D928A6AB95', X'E6F4CDC6546A734B87B3867A1176747B', X'E6F4CDC6546A734B87B3867A1176747B', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the mayonnaise, lemon juice, capers, chives and salt and pepper. Pour the dressing over the lobster and toss gently to coat all the meat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B34EC5DC7808CE4D9B811AC2FB8D062B', X'E6F4CDC6546A734B87B3867A1176747B', X'E6F4CDC6546A734B87B3867A1176747B', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter on a skillet over medium-high heat. Set the buns down on the skillet on one of their side edges. Cook for a minute or two until golden brown, then flip and cook the opposite side.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'241B2D37FCEA954CAA5E1911EC17687A', X'E6F4CDC6546A734B87B3867A1176747B', X'E6F4CDC6546A734B87B3867A1176747B', 3, NULL, NULL, 2, NULL, 0, 'Divide the lobster meat among the buns and serve immediately.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30FE3B8801228C4081353AE5C64C6BC8', X'E58AA6EBF7C0CB4499C20E3F64683B3D', X'E58AA6EBF7C0CB4499C20E3F64683B3D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F with two racks close to the center. Brush any dirt off shiitakes. Remove stems and slice caps very thinly.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A78099101A3E0C4D932C8E313C48EC3A', X'E58AA6EBF7C0CB4499C20E3F64683B3D', X'E58AA6EBF7C0CB4499C20E3F64683B3D', 1, NULL, NULL, 2, NULL, 0, 'Place sliced shiitakes into a medium mixing bowl. Add the oil, smoked paprika, and salt. Toss thoroughly to coat.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BADD392D51136C4C814C23995DA44B43', X'E58AA6EBF7C0CB4499C20E3F64683B3D', X'E58AA6EBF7C0CB4499C20E3F64683B3D', 2, NULL, NULL, 2, NULL, 0, 'Divide shiitake slices between two rimmed cookie sheets. Spread in a single layer without crowding. Bake for about 20 minutes, until some bits are quite crispy and others a little softer. Let cool slightly before serving. I rarely have leftovers, but if you do, store tightly covered in the fridge for several days and pop in the toaster oven to re-crisp.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D63B6864E05C3E44B57DF6B359B0DE6D', X'BB58E9245600894EBB80F857DF7D3C69', X'BB58E9245600894EBB80F857DF7D3C69', 0, NULL, NULL, 2, NULL, 0, 'Trim and discard the root end and green parts of the leeks. Slice the white parts in half lengthwise, then crosswise into thin half-moons. Place in a colander and wash very well — leeks can be very sandy. Drain well.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93290EAFE9350E4594EEC623A5E21FC3', X'BB58E9245600894EBB80F857DF7D3C69', X'BB58E9245600894EBB80F857DF7D3C69', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a wide, shallow pan over medium-high heat. Add the leeks, mushrooms, salt and thyme. Cook, stirring occasionally and without browning, for about 10 minutes, until leeks are becoming tender and the liquid released from the mushrooms has begun to reduce. Add the garlic and cook, stirring, one more minute. Stir in the wine and simmer briskly for two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D0666295095A44190A4F51A52E594E5', X'BB58E9245600894EBB80F857DF7D3C69', X'BB58E9245600894EBB80F857DF7D3C69', 2, NULL, NULL, 2, NULL, 0, 'Add the farro, stock, tamari, and bay leaf, give it all a good stir, and raise the heat to high. While the liquid comes to a boil, use your fingers to crumble the chestnuts into the pan. When it boils, reduce the heat to maintain a gentle simmer, cover the pan, and simmer until liquid is absorbed, about 30 minutes. Serve warm. Keeps well for several days, tightly covered in the refrigerator.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B65DEE32F47A704B9DA85DB6128F7B38', X'DAD4C674EA63EB48A7FE02B9E2800E28', X'DAD4C674EA63EB48A7FE02B9E2800E28', 0, NULL, NULL, 2, NULL, 0, 'Pour the vegetable stock into a medium pot and drop in the shiitakes. Bring stock to a boil over high heat, then remove from heat and let mushrooms steep for 30 minutes. Pour stock and mushrooms into large spouted measuring cup or bowl, and wipe out the pot with a paper towel.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36AEF613533BC74091A84C57F749A26A', X'DAD4C674EA63EB48A7FE02B9E2800E28', X'DAD4C674EA63EB48A7FE02B9E2800E28', 1, NULL, NULL, 2, NULL, 0, 'Add the butter to the empty pot and melt over medium-high heat. If using the shallot, add and cook for a minute or so, whisking once or twice. Add the flour and cook, whisking, until it turns very lightly golden, about two minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD453077C3780D479E4B36432A37D05C', X'DAD4C674EA63EB48A7FE02B9E2800E28', X'DAD4C674EA63EB48A7FE02B9E2800E28', 2, NULL, NULL, 2, NULL, 0, 'Pour in the stock with the mushrooms in a stream, whisking the whole time. Add soy sauce, marmite, and a few grinds of pepper.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76ED32025EF561418C882A8E657D6CD5', X'DAD4C674EA63EB48A7FE02B9E2800E28', X'DAD4C674EA63EB48A7FE02B9E2800E28', 3, NULL, NULL, 2, NULL, 0, 'Bring stock back to a boil, then reduce heat and simmer, uncovered, until gravy is reduced by almost half, about 20 minutes. Taste for seasoning and add salt and additional pepper if desired. Remove mushrooms before serving.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EBA31DC34E69645B4AA96CBD676A805', X'2B462F439C4D5C47A539AD483CFE85B6', X'2B462F439C4D5C47A539AD483CFE85B6', 0, NULL, NULL, 2, NULL, 0, 'Place the kombu in a medium pot. You don’t need to rinse it, even if the package says you do. Pour in the water. Let sit for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84573300A2A0F4499F2451D2277B9AD3', X'2B462F439C4D5C47A539AD483CFE85B6', X'2B462F439C4D5C47A539AD483CFE85B6', 1, NULL, NULL, 2, NULL, 0, 'Bring the water with the kombu in it to a boil. When it boils, turn down the heat and let it simmer very gently for 10 minutes. Then turn off the heat and add the bonito flakes. Let steep for 10 minutes, then strain the broth through cheesecloth or a coffee strainer into a clean pot.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D786B6F19F86364C904936527C112616', X'380C206B66C7EF4083B19F825955FA12', X'380C206B66C7EF4083B19F825955FA12', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F with a rack in the center.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DFF1B6120797744A2779927D3CE3879', X'380C206B66C7EF4083B19F825955FA12', X'380C206B66C7EF4083B19F825955FA12', 1, NULL, NULL, 2, NULL, 0, 'Slice baguette on the bias into 1/4-inch thick slices and brush with the olive oil. Place onto a baking sheet. Bake until lightly toasted, about 5 minutes.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE1115D9130AD4BBA8447BE2A28DAE6', X'380C206B66C7EF4083B19F825955FA12', X'380C206B66C7EF4083B19F825955FA12', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, melt the butter over medium heat in a wide frying pan. Add the shallot and garlic and cook, stirring once in a while, until beginning to soften, about 1 minute. Add the mushrooms along with a couple of good pinches of salt and some freshly ground black pepper, and stir to coat with butter. Cook, stirring occasionally, 5 to 8 minutes more, until the mushrooms have given off their liquid, browned slightly, and reduced to about half their original volume.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E3C67AE3877AC4B8CCEA768DCD702D2', X'380C206B66C7EF4083B19F825955FA12', X'380C206B66C7EF4083B19F825955FA12', 3, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and pour in the brandy from a measuring cup. (Don''t pour directly from the bottle despite what you may have seen in the movies.) Very carefully light a long match and touch the surface of the mushroom mixture to ignite the brandy. Or, if you''re cooking with gas, you can tip the pan away from you into the burner''s flame until the brandy ignites. I find this method maybe a little too entertaining. Stand back until the flame dies out.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4AD8220F833264884BFD731EA6C5C0E', X'380C206B66C7EF4083B19F825955FA12', X'380C206B66C7EF4083B19F825955FA12', 4, NULL, NULL, 2, NULL, 0, 'Return the pan to medium heat. Add the cream and stir to combine. Simmer until the cream has thickened slightly, a minute or two. Turn off the heat and stir in the Parmesan cheese and parsley. Taste for seasoning and add additional salt and pepper if desired.');
INSERT INTO `VWR INTERNAL Umamigirl recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D9861A9E193A45BD3FF3D8FD41653C', X'380C206B66C7EF4083B19F825955FA12', X'380C206B66C7EF4083B19F825955FA12', 5, NULL, NULL, 2, NULL, 0, 'Spoon mushrooms onto toasted baguette slices and serve.');
