INSERT INTO `VWR INTERNAL Sweetopia recipeIngredient` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredients) VALUES (X'78094E2DC6A89547BA39CE650E5EED22', X'B602E5749A7E2B4A9230236413EC69F1', X'B602E5749A7E2B4A9230236413EC69F1', 2, NULL, NULL, 2, NULL, 0, '1 1/2 tsp pure vanilla extract');
