INSERT INTO `VWR INTERNAL Sweetopia recipeIngredient` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredients) VALUES (X'5D0ACD901C7CC54288000C485D5D9245', X'B602E5749A7E2B4A9230236413EC69F1', X'B602E5749A7E2B4A9230236413EC69F1', 4, NULL, NULL, 2, NULL, 0, '1 tsp white food gel coloring (optional)');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3C07C6551CB5441840870EF606661B9', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together in the bowl of an electric mixer on low to medium speed (Use a paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you’d like a light and fluffy cookie, that’s ideal, however the dough will spread more during baking; not ideal if you’d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'855460FDACE9FC4DAD91F827EC8BF8AC', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 1, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F90B43E9C28BD345BF8C7B3B30DDE25D', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 2, NULL, NULL, 2, NULL, 0, 'Add mint extract. Stir briefly.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D089AACB45FFAD489476268CAA1689AA', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 3, NULL, NULL, 2, NULL, 0, 'Sift or whisk your dry ingredients together. (Flour and cocoa powder).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FFD95C0C25E7045B06D170F8C2C1495', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 4, NULL, NULL, 2, NULL, 0, 'Add the dry flour mixture in 2 or 3 stages to the bowl and mix on low speed for about 30 seconds to 1 minute, and observe the dough mixing. When it starts to come together and the dough clumps around the paddle attachment and peels away from the sides of the mixer, it should be the right texture. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture. (Different factors can affect the moisture of the dough, from the temperature of the butter you used, to water content of the butter, the actual ingredient brands you used, to the temperature of the room etc.).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7E75AF8E85E7D4689DAEA0BA807AD54', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DE7B4192EEDEE42BF4C4E53D29387C3', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 6, NULL, NULL, 2, NULL, 0, 'Roll out the dough further if you need to, and cut out cookie shapes. Place on parchment paper-lined baking sheet. Re-roll scraps and repeat.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2113FD09E530504FB098CE9EDBD2B93F', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 7, NULL, NULL, 2, NULL, 0, 'Put cookie dough shapes back into the fridge for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0E30818E8D54D468DA5C8F5BCAFBC4D', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 8, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E72C04BF1A2D84B83BEF5D63898647D', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the center of the cookie no longer looks wet. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A96C5EA93BC84A8B3420654C0D640F', X'734E02C57FDD844797D64608BD5A5A4B', X'734E02C57FDD844797D64608BD5A5A4B', 10, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B5A0D0386D414DA7BF794E3FC4E837', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together in the bowl of an electric mixer on low to medium speed (Use a paddle attachment). Mix until incorporated. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you’d like a light and fluffy cookie, that’s ideal, however the dough will spread more during baking; not ideal if you’d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DA5D991AB05FB42938D56953EA952CC', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 1, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF67D5292A5A0349916427F44B96651E', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 2, NULL, NULL, 2, NULL, 0, 'Add liquid vanilla extract, the LorAnn Red Velvet emulsion and food food paste coloring. Mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2597C8FA62971F4580D34654D632CB8C', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 3, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour, cocoa powder and salt).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35B1C84C65FDAD479F34ABC8A48FB6B7', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 4, NULL, NULL, 2, NULL, 0, 'Add the flour in 2 or 3 stages to the bowl and mix on low speed for about 30 seconds to 1 minute, and observe the dough mixing. When it starts to come together and the dough clumps around the paddle attachment and peels away from the sides of the mixer, it should be the right texture. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture. (Different factors can affect the moisture of the dough, from the temperature of the butter you used, to water content of the butter, the actual ingredient brands you used etc.).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51322A308E14FF45A89552D9635C2F7D', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CAD5746D21F4B459259893163E843F8', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 6, NULL, NULL, 2, NULL, 0, 'Roll out the dough further if you need to, and cut out cookie shapes. Place on parchment paper-lined baking sheet. Re-roll scraps and repeat.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A101D4D1C92D7B458457E8FA4FDF8D1F', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 7, NULL, NULL, 2, NULL, 0, 'Put cookie dough shapes back into the fridge for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF679B4664BB45439AC71B242EDDD12A', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 8, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFD45BFD240D36449135034486BC443B', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the center of the cookie no longer looks wet. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'718612EFF19D064C8803C43C725B1A2E', X'B4A8EF6B5933D04D899906C0E315D180', X'B4A8EF6B5933D04D899906C0E315D180', 10, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29D6FFB27849114ABCD645D2192C9E43', X'185615BCF3E7924388281EFBE960FB27', X'185615BCF3E7924388281EFBE960FB27', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and 2 sugars together in the bowl of an electric mixer on low to medium speed. (Use the paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you’d like a light and fluffy cookie, that’s ideal, however the dough will spread more during baking; not ideal if you’d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A92F862970F6AA42AC0E604279E71DC5', X'185615BCF3E7924388281EFBE960FB27', X'185615BCF3E7924388281EFBE960FB27', 1, NULL, NULL, 2, NULL, 0, 'Add pumpkin purée and eggs while slowly mixing. Add vanilla extract and mix. Scrape down the bowl with your spatula at least once and mix briefly again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C879654B2EC894EAF0CB39FB6C18C38', X'185615BCF3E7924388281EFBE960FB27', X'185615BCF3E7924388281EFBE960FB27', 2, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour, cinnamon, ginger, nutmeg, all-spice and salt. I do not use baking powder in my cut-out cookies).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D27D093D2AD6AC4CA4745FC45267CE7E', X'185615BCF3E7924388281EFBE960FB27', X'185615BCF3E7924388281EFBE960FB27', 3, NULL, NULL, 2, NULL, 0, 'Add all of the flour mixture to the bowl. Mix on low speed. When the dough is almost fully mixed, pour in the pecans and mix until incorporated.');
