INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'311788DC567CA64F998BAB7D3A457B19', X'185615BCF3E7924388281EFBE960FB27', X'185615BCF3E7924388281EFBE960FB27', 9, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B30E911F8D13D4084BA2812FA69AC5D', X'C350A185E72E58459A74699025DF16A5', X'C350A185E72E58459A74699025DF16A5', 0, NULL, NULL, 2, NULL, 0, 'If making lavender sugar, dry some lavender stalks in a dehydrator at 90 degrees Fahrenheit or 32 degrees Celsius for a few hours. Alternatively, dry lavender stalks out by hanging them upside down in a dark, warm spot for a few days. You could also dry them out in the oven at about 200 degrees Fahrenheit or 100 degrees Celsius, for about 10 minutes. Take the lavender flowers off the stalks and grind 1 Tablespoon of the lavender flowers in a food processor or blender with the 2 cups of sugar until the flowers are pulverized and mixed in with the sugar.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF2702C445F9E34BB7894A5613294C03', X'C350A185E72E58459A74699025DF16A5', X'C350A185E72E58459A74699025DF16A5', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and lavender sugar together in the bowl of an electric mixer on low speed. (Use the paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DE10167AF8B7947B0E12BAA41C53993', X'C350A185E72E58459A74699025DF16A5', X'C350A185E72E58459A74699025DF16A5', 2, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again. Add lavender flavouring if you''d like a bit more of a lavender flavour or if you haven''t used the lavender sugar and mix. Add 1 teaspoon of dried lavender flowers and mix.');
