INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6F9553D238DF947A81B1DFF92E9A48D', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 0, NULL, NULL, 2, NULL, 0, 'Note that you can make the recipe using just hazelnut extract to add the hazelnut flavour, or you can add both the hazelnut extract and hazelnut flour (also known as hazelnut meal).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1CDBEA467CF8747BAA044B0F8146B34', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 1, NULL, NULL, 2, NULL, 0, 'If making the hazelnut meal, grind hazelnuts in a food processor, blender or crush with a meat mallet. Try not to grind so long that it turns into a paste or that it turns into a fine powder (Depending on the method you''re using). If using a meat mallet, place the cooled nuts in a ziplock back, place between tea towels, and crush.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82BD88B3162F554087A59F1D1FBBC058', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugars together in the bowl of an electric mixer on low speed. (Use the paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0AFBFED1CF0174BAD045E58EF8E0A54', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 3, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again. Add hazelnut extract and vanilla extract, mix, and add hazelnut flour if you are using it. Mix until just incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2CCA96C63AA0148BF771144EC06FB38', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 4, NULL, NULL, 2, NULL, 0, 'Add the flour in 2 or 3 stages to the bowl and mix on low speed for about 30 seconds to 1 minute, and observe the dough mixing. When it starts to come together and the dough clumps around the paddle attachment and peels away from the sides of the mixer, it should be the right texture. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3595520AF037840866C48B24E0A4834', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge or freezer for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'787C0FE2C14B7E40ACB6D6123261F963', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 6, NULL, NULL, 2, NULL, 0, 'Cut out cookie shapes. Place on parchment paper-lined baking sheets. Re-roll scraps and repeat. If your dough gets too soft to work with, place in the fridge or freezer periodically to firm up again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A5DBEDC0F9A94498C8185B00F274006', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 7, NULL, NULL, 2, NULL, 0, 'Once all cut out, put cookie dough shapes back into the fridge or freezer for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19F086F1AAAEF24C830D96ED0F752C24', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 8, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E71579ED8D87B4DB4C2A49070F28B6D', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the edges become golden brown. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22A8BFF43895AA4FA75AD74151A67057', X'054E55F39452F647B5F000E9E2395166', X'054E55F39452F647B5F000E9E2395166', 10, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate if you like! If you’re new to decorating cookies, I have basic cookie decorating tutorials with everything you’ll need to know to begin on my blog. Have fun!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'851193F58F2FA14D97C73B3B8D7D6631', X'A8E26711AD9C424BA416BDCDC30382C4', X'A8E26711AD9C424BA416BDCDC30382C4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC8EFC3C15EF234E8CAAD78449128D80', X'A8E26711AD9C424BA416BDCDC30382C4', X'A8E26711AD9C424BA416BDCDC30382C4', 1, NULL, NULL, 2, NULL, 0, 'Cut the leaves off of the rhubarb stalks and rinse the stalks with water. Place stalks on a baking sheet lined with parchment paper and sprinkle with a little granulated sugar (the sugar is optional). Roast the rhubarb in the oven at for 10-15 minutes, until it is soft. Once out of the oven, place stalks in a blender and puree. Let purée cool for a few minutes before using in dough mixture.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'878EDBC29679FC4882C04406B7F3E4AF', X'A8E26711AD9C424BA416BDCDC30382C4', X'A8E26711AD9C424BA416BDCDC30382C4', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and brown sugar together in the bowl of an electric mixer on low speed. (Use the paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9045D474CB968F4E9CFEF18F24CE907D', X'A8E26711AD9C424BA416BDCDC30382C4', X'A8E26711AD9C424BA416BDCDC30382C4', 3, NULL, NULL, 2, NULL, 0, 'Add the ground cinnamon and ginger and mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80484711ED38C047888AEFA0BF1873AE', X'A8E26711AD9C424BA416BDCDC30382C4', X'A8E26711AD9C424BA416BDCDC30382C4', 4, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again. Add the rhubarb extract, mix, add the rhubarb purée, mix, and add the fancy molasses. Mix until just incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2744F7FEA965404B8BB81AA59288BDAC', X'A8E26711AD9C424BA416BDCDC30382C4', X'A8E26711AD9C424BA416BDCDC30382C4', 5, NULL, NULL, 2, NULL, 0, 'Add the flour, cover the mixer bowl loosely with a tea towel to prevent flour escaping, and mix. Remove the towel after about 30 seconds. When the dough starts to come together it clumps around the paddle attachment and peels away from the sides of the mixer. That should be the right texture. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture.');
