INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3C0436A14CE80488B5B178BB5493144', X'149726D8FA7F8E41AAEA0A9D9F7C1B57', X'149726D8FA7F8E41AAEA0A9D9F7C1B57', 2, NULL, NULL, 2, NULL, 0, 'Add all of the flour to the bowl. Place a large tea towel between the edge of the bowl and the electric mixer so that the flour won''t escape. Mix on low speed for 30 seconds. Remove the tea towels and observe the dough mixing; when it starts to come together add the rainbow baking chips and mix until the dough clumps around the paddle attachment. The dough should peel away from the sides of the mixer and clump around the paddle attachment. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture.');
