INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E15B029442E0FA4AB51FD0B572543CB6', X'ED0748F81691864291BE1666BB83A8BC', X'ED0748F81691864291BE1666BB83A8BC', 2, NULL, NULL, 2, NULL, 0, 'Add the almond flour in 2 or 3 stages to the bowl and mix on low speed for about 30 seconds to 1 minute, and observe the dough mixing. When it starts to come together and the dough clumps around the paddle attachment and peels away from the sides of the mixer, it should be the right texture. I used 6 cups of almond flour, you may need to use less or even more. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add almond flour, 1/2 cup at a time, and mix until you’re happy with the texture.');
