INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48333994AA971E40867D3E0BD58B5E1A', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'052FF5658527C44D930FAF198F5136E2', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 1, NULL, NULL, 2, NULL, 0, 'If grinding your own walnuts, spread the walnuts on a cookie sheet lined with parchment paper, and bake for 4 minutes. Toss the walnuts and bake for 4 more minutes. Let them cool completely before grinding them in a food processor, blender of crushing with a meat mallet. Try not to grind so long that it turns into a paste or that it turns into a powder (Depending on the method you''re using). If using a meat mallet, place the cooled nuts in a ziplock back, place between tea towels, and crush.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA3E5BBC2CF4DA4AB3A71CBEF162B27B', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugars together in the bowl of an electric mixer on low speed. (Use the paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9A6189D91E0934FB349F8F17DC2278C', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 3, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again. Add maple syrup and maple extract, mix, and add walnuts or walnut flour. Mix until just incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB32DB05C2E7545B000BF53907C8C3C', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 4, NULL, NULL, 2, NULL, 0, 'Add the flour in 2 or 3 stages to the bowl and mix on low speed for about 30 seconds to 1 minute, and observe the dough mixing. When it starts to come together and the dough clumps around the paddle attachment and peels away from the sides of the mixer, it should be the right texture. I used 6 cups of flour because my walnut mixture was moist, you may need to use less or even more. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E09A22CB1EBB646A79C6AD56A75392E', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge or freezer for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8684E0184198284D87EB0C3A3BEE24CB', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 6, NULL, NULL, 2, NULL, 0, 'Cut out cookie shapes. Place on parchment paper-lined baking sheets. Re-roll scraps and repeat. If your dough gets too soft to work with, place in the fridge or freezer periodically to firm up again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85C31C3FFCF4C84D8892F37EE3F77BA4', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 7, NULL, NULL, 2, NULL, 0, 'Once all cut out, put cookie dough shapes back into the fridge or freezer for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A593F84DEAC524E9F178D4BE779D878', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 8, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26D5AF9E253700488A90E03A38E887F3', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the edges become golden brown. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'901CD4F6C944B040854DFC2853BDC399', X'C82369785F18E94899BEC3A94932A018', X'C82369785F18E94899BEC3A94932A018', 10, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate if you like! If you’re new to decorating cookies, I have basic cookie decorating tutorials with everything you’ll need to know to begin on my blog. Have fun!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57B4B8FD1926014898152C3502087A4C', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together in the bowl of an electric mixer on low to medium speed (Use a paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you’d like a light and fluffy cookie, that’s ideal, however the dough will spread more during baking; not ideal if you’d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'035B945175FD064DBF0DF8ABC09B04BF', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 1, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97E97CC1B45A3B4F9D76202AC9A96EAF', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 2, NULL, NULL, 2, NULL, 0, 'Add liquid hazelnut extract (or vanilla if you don’t have hazelnut). Stir briefly.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3BCD8CB8332BD4EB49129F7BD9734B1', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 3, NULL, NULL, 2, NULL, 0, 'Add the Nutella and mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D12CFDE47D62C84490A2D83910396CB9', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 4, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour and cocoa powder).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'320FEF913952B44D81CABB0F40B81164', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 5, NULL, NULL, 2, NULL, 0, 'Add all of the flour mixture to the bowl. Place a large tea towel or two small tea towels between the edge of the bowl and the electric mixer so that the flour won’t escape. Mix on low speed for 3o seconds. Remove the tea towel, add the flaked hazelnuts and mix until incorporated. Observe the dough mixing; when it clumps around the paddle attachment it’s ready. It’s also important at this stage not to over mix the dough.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'407ABF42AA6CB0448858D689F4EE508B', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 6, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour.');
