INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'613484DB9A406E4F9C9DD3CB7A84A206', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 9, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B28B95E89E7539459AC44A2C9EB378A1', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 10, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the center of the cookie no longer looks wet. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47076439D841964E912D2A9F20CD936A', X'C16A234F61E40245804D3067835DA974', X'C16A234F61E40245804D3067835DA974', 11, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'411D363C974CB8449A4BD465216E78B9', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 0, NULL, NULL, 2, NULL, 0, 'Preaheat the oven to 375 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'278676C3CC3AEC4FB0DC39979C1474E3', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 1, NULL, NULL, 2, NULL, 0, 'In a stand mixer fitted with a paddle attachment, cream the butter and 2 cups of sugar on medium speed until light and fluffy. You will likely need to scrape down the sides of the bowl and attachment at least once.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECDF86710C5E214F82E596FD2C218F43', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 2, NULL, NULL, 2, NULL, 0, 'Add the eggs slowly and mix until they''re incorporated. Scrape down the sides of the bowl. Add the molasses and vanilla extract and mix on high until the mixture looks like a pale brown color. Scrape down sides of the bowl and mix on high briefly.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9F7DA2E1C802341BB202BDA85D1D562', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 3, NULL, NULL, 2, NULL, 0, 'Into a separate bowl, add the flour, baking soda, ground ginger, ground cinnamon and salt. Whisk the ingredients together well.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6320CCEC1746024FA1549DB987157C82', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 4, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients to the wet ingredients and mix until just fully combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B815B3965A03194EA6479624F23553A9', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 5, NULL, NULL, 2, NULL, 0, 'Use an ice cream scoop to portion your batter out. One at a time, dip each ball of dough into the 1/2 cup of granulated sugar. Use a spoon or your fingers to gently roll the dough in the sugar to coat all sides, and then place onto a baking sheet lined with parchment paper. Make sure you leave room around each cookie to spread while baking. For my large cookies I left about 2 inches of space around each.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A27D49C10061C40B5D55924C98C1A25', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 6, NULL, NULL, 2, NULL, 0, 'Slightly flatten the tops of your cookies by pressing gently on each cookie with the palm of your hand.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3532D43A8633B5418641D0A3E21670F0', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 7, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 14-17 minutes or until the cookies are firm around the edges but still somewhat soft in the center.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76EBC58B4E869645B927B396172E4EDF', X'096DBCB05A3BD5469A7F939F95C95B46', X'096DBCB05A3BD5469A7F939F95C95B46', 8, NULL, NULL, 2, NULL, 0, 'Let your cookies cool and enjoy!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A3D0F9A577532449818AE858137162F', X'EC5A8F5BF9279E4399F483473E4CF4D4', X'EC5A8F5BF9279E4399F483473E4CF4D4', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugars together in the bowl of an electric mixer on low speed, using the paddle attachment. Mix until incorporated – for about 30 seconds. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugars in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70701E196EFB674CB6D4F291FDDBA681', X'EC5A8F5BF9279E4399F483473E4CF4D4', X'EC5A8F5BF9279E4399F483473E4CF4D4', 1, NULL, NULL, 2, NULL, 0, 'Add eggs and vanilla extract slowly and mix. Scrape down the bowl with your spatula if needed and mix again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADB666356443954E82E26125D2FD300D', X'EC5A8F5BF9279E4399F483473E4CF4D4', X'EC5A8F5BF9279E4399F483473E4CF4D4', 2, NULL, NULL, 2, NULL, 0, 'Add the oats and briefly mix until incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'672A9B4EC9B5E54EA19A2D9CB801A8E8', X'EC5A8F5BF9279E4399F483473E4CF4D4', X'EC5A8F5BF9279E4399F483473E4CF4D4', 3, NULL, NULL, 2, NULL, 0, 'Whisk the flour and cinnamon together in a separate bowl. Add the flour mixture to the bowl of an electric mixer. Place a large tea towel between the edge of the bowl and the electric mixer so that the flour won''t escape. Mix on low speed for 30 seconds. Remove the tea towel and observe the dough mixing; when it starts to come together add the milk chocolate chips and mix until the dough clumps around the paddle attachment. The dough should peel away from the sides of the mixer and clump around the paddle attachment. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture. There are various reasons why a dough can be too dry or too sticky, ranging from humidity in the environment, how soft your butter is, to what kind of flour you use, the fat content of the butter etc., however, there are ways to fix the issues if you run into them. Already mentioned, add flour if your dough is too sticky, and if it’s too dry, make sure you’ve mixed it as best as you can first, and if it’s still too dry, add a little water or milk until your dough comes together.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'254A9F0AD0F04041A7E0C0B934528182', X'EC5A8F5BF9279E4399F483473E4CF4D4', X'EC5A8F5BF9279E4399F483473E4CF4D4', 4, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour.');
