INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CACAA35B848B5745A2C3FEE6F521ABFA', X'EC5A8F5BF9279E4399F483473E4CF4D4', X'EC5A8F5BF9279E4399F483473E4CF4D4', 8, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the edges become golden brown. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8067546CF41C924599AF23B158D2E7BD', X'EC5A8F5BF9279E4399F483473E4CF4D4', X'EC5A8F5BF9279E4399F483473E4CF4D4', 9, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate if you like! If you’re new to decorating cookies, here is a cookie decorating tutorial with everything you’ll need to know to begin!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C182B6DBE43B243A4F4327DCD802195', X'C0F807548907F149A777C17E5EDF8D35', X'C0F807548907F149A777C17E5EDF8D35', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together in the bowl of an electric mixer on low speed, using the paddle attachment. Mix until incorporated – for about 30 seconds. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13DE8C463C8E5D40AF444CED94D1DABB', X'C0F807548907F149A777C17E5EDF8D35', X'C0F807548907F149A777C17E5EDF8D35', 1, NULL, NULL, 2, NULL, 0, 'Add eggs, vanilla, extract and eggnog slowly and mix. Scrape down the bowl with your spatula if needed and mix again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA4F3791646F0E4AA9E86982D20BC967', X'C0F807548907F149A777C17E5EDF8D35', X'C0F807548907F149A777C17E5EDF8D35', 2, NULL, NULL, 2, NULL, 0, 'Whisk the flour, cinnamon and nutmeg together in a separate bowl. Add all of the flour mixture to the mixer bowl. Place a large tea towel between the edge of the bowl and the electric mixer so that the flour won''t escape. Mix on low speed for 30 seconds. Remove the tea towel and observe the dough mixing; the dough should peel away from the sides of the mixer and clump around the paddle attachment. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture. There are various reasons why a dough can be too dry or too sticky, ranging from humidity in the environment, to what kind of flour you use, the fat content of the butter etc., however, there are ways to fix the issues if you run into them. Already mentioned, add flour if your dough is too sticky, and if it’s too dry, make sure you’ve mixed it as best as you can first, and if it’s still too dry, add a little water or milk until your dough comes together.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5C7413AED449347B4EB4E0F1E3D4E67', X'C0F807548907F149A777C17E5EDF8D35', X'C0F807548907F149A777C17E5EDF8D35', 3, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge or freezer for a minimum of 1 hour.');
