INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71DD3AD5C3FE1B40994135760DA030B8', X'CCFB43229E6DBE4584F19FF606BA2C0C', X'CCFB43229E6DBE4584F19FF606BA2C0C', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and 2 sugars together in the bowl of an electric mixer on low to medium speed. (Use the paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you’d like a light and fluffy cookie, that’s ideal, however the dough will spread more during baking; not ideal if you’d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8307BA17E860A84EA76A3482AE31974E', X'CCFB43229E6DBE4584F19FF606BA2C0C', X'CCFB43229E6DBE4584F19FF606BA2C0C', 1, NULL, NULL, 2, NULL, 0, 'Add pumpkin purée and eggs while slowly mixing. Add toffee flavoring and mix. Scrape down the bowl with your spatula at least once and mix briefly again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CA7D10D7E99484391AB93E32ACE6A73', X'CCFB43229E6DBE4584F19FF606BA2C0C', X'CCFB43229E6DBE4584F19FF606BA2C0C', 2, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour, cinnamon, ginger, nutmeg and all-spice. I do not use baking powder in my cut-out cookies).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A68F7583E1C86444B575FCAAF619E4BD', X'CCFB43229E6DBE4584F19FF606BA2C0C', X'CCFB43229E6DBE4584F19FF606BA2C0C', 3, NULL, NULL, 2, NULL, 0, 'Add all of the flour mixture to the bowl. Place a large tea towel or two small tea towels between the edge of the bowl and the electric mixer so that the flour won’t escape. Mix on low speed for 3o seconds. Remove the tea towel(s). Add the toffee bits. Observe the dough mixing; when it clumps around the paddle attachment and peels away from the sides of the mixing bowl, it’s ready. It’s also important at this stage not to over mix the dough (the glutens in the flour develop and the dough can become tough).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F251216B872521458592220A8BE68AA1', X'CCFB43229E6DBE4584F19FF606BA2C0C', X'CCFB43229E6DBE4584F19FF606BA2C0C', 4, NULL, NULL, 2, NULL, 0, 'If your dough is too wet, add 1/4 cup of flour and mix until it peels away from the sides of the bowl and clumps around the attachment. If the dough is too dry, first mix a little longer, and if still too dry, add a little bit of water or milk until it comes together.');
