INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8916912AAD74174293961044A46B1174', X'9A197FFF9CF3144E972309595D9500BB', X'9A197FFF9CF3144E972309595D9500BB', 3, NULL, NULL, 2, NULL, 0, 'Using the paddle attachment on the LOWEST speed, mix slowly for a full 10 minutes. Icing will get thick and creamy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4DA9B06E7DA8F4EAD500BF58077ADE9', X'9A197FFF9CF3144E972309595D9500BB', X'9A197FFF9CF3144E972309595D9500BB', 4, NULL, NULL, 2, NULL, 0, 'Cover the bowl with a dampened tea-towel to prevent crusting and drying.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4CF15C03858624EAEE25E359D52A7F6', X'9A197FFF9CF3144E972309595D9500BB', X'9A197FFF9CF3144E972309595D9500BB', 5, NULL, NULL, 2, NULL, 0, 'Cover the bowl with a dampened tea-towel to prevent crusting and drying.Tint with food colorings or thin the icing with small amounts of warm water to reach the desired consistency.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B801142A105E344910EFC70721498E3', X'E232D056A71A7B4EAF2ED832E0AD767B', X'E232D056A71A7B4EAF2ED832E0AD767B', 0, NULL, NULL, 2, NULL, 0, 'Using the paddle attachment, cream the butter and sugar together in the bowl of an electric mixer on low speed. Mix until thoroughly incorporated - for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Mix in the cream cheese. Over mixing the butter, sugar and cream cheese in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B5362D60557E46B62E47DEBDD00110', X'E232D056A71A7B4EAF2ED832E0AD767B', X'E232D056A71A7B4EAF2ED832E0AD767B', 1, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again. Add Lorann Cookies & Cream Super Strength Flavor. Mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F55C90491867AF4E988A8F043611B5F7', X'E232D056A71A7B4EAF2ED832E0AD767B', X'E232D056A71A7B4EAF2ED832E0AD767B', 2, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour and salt). Add all of the flour mixture to the bowl. Place a large tea towel between the edge of the bowl and the electric mixer so that the flour won''t escape. Mix on low speed for 30 seconds. Remove the tea towels and observe the dough mixing; when it starts to come together add the Oreo cookie bits and mix until the dough clumps around the paddle attachment. The dough should peel away from the sides of the mixer and clump around the paddle attachment. You can also test the dough by pinching it between your thumb and index finger. If it’s soft but not sticky, it’s just right. If it’s too sticky when you pinch it between your fingers, add flour, ¼ cup at a time, and mix until you’re happy with the texture. There are various reasons why a dough can be too dry or too sticky, ranging from humidity in the environment, to what kind of flour you use, the fat content of the butter etc., however, there are ways to fix the issues if you run into them. Already mentioned, add flour if your dough is too sticky, and if it’s too dry, make sure you’ve mixed it as best as you can first, and if it’s still too dry, add a little water or milk until your dough comes together.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A686EEE4E4A3704FB038141650B38AB5', X'E232D056A71A7B4EAF2ED832E0AD767B', X'E232D056A71A7B4EAF2ED832E0AD767B', 3, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge or freezer for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AACE8378B98354C8CE199E016DD09F7', X'E232D056A71A7B4EAF2ED832E0AD767B', X'E232D056A71A7B4EAF2ED832E0AD767B', 4, NULL, NULL, 2, NULL, 0, 'Cut out cookie shapes. Place on parchment paper-lined baking sheets. Re-roll scraps and repeat. If your dough gets too soft to work with, place in the fridge or freezer periodically to firm up again. Note that the more you re-roll your scraps, the darker the dough will get due to the Oreo bits.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52BB29581D143E48ACAE05810D06BA89', X'E232D056A71A7B4EAF2ED832E0AD767B', X'E232D056A71A7B4EAF2ED832E0AD767B', 5, NULL, NULL, 2, NULL, 0, 'Once all cut out, put cookie dough shapes back into the fridge or freezer for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
