INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E7BA07DE1598349B29EA9266277154F', X'172D379FC49C5B4DAC967DBD9E25DDE1', X'172D379FC49C5B4DAC967DBD9E25DDE1', 4, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge or freezer for a minimum of 1 hour.');
