INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A1D416857F90244B9D3D3BCE90FCE30', X'D3B45AD04E03BF40BE915CD16385495A', X'D3B45AD04E03BF40BE915CD16385495A', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and two sugars together in the bowl of an electric mixer on low to medium speed. (Use the paddle attachment). Mix until thoroughly incorporated - for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugars in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
