INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE774EDEEE55414AB7A1AFEE39DC663D', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 3, NULL, NULL, 2, NULL, 0, 'Place your dry ingredients in a bowl and whisk together. (All purpose flour, hazelnut flour and salt).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36B17054FE570C4FB05577DC6C85ADE2', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 4, NULL, NULL, 2, NULL, 0, 'Add all of the flour mixture to the bowl. Place a large tea towel or two small tea towels between the edge of the bowl and the electric mixer so that the flour won''t escape. Mix on low speed for 30 seconds. Remove the tea towels and observe the dough mixing; when it clumps around the paddle attachment it''s ready. It''s also important at this stage not to over mix the dough (the glutens in the flour develop and the dough can become tough).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'353CEE0F46C99C4181050438A25B1306', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9D2D43B0EEBD34B9D7FFCF6F8503A1F', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 6, NULL, NULL, 2, NULL, 0, 'Roll out the dough further if you need to, and cut out cookie shapes. Place on parchment paper-lined baking sheet. Re-roll scraps and repeat.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21D6012C0AEA3C408291E5FC519E7673', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 7, NULL, NULL, 2, NULL, 0, 'Put cookie dough shapes back into the fridge for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0923E331BB87F24AAA39B30A722C56DF', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 8, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E1A02B061BDB141BBFDA2D9D1BF8819', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the edges become golden brown. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E46EF7FD290EA48B24BB165265D25DD', X'48EACC7C64BE0647BFCF8594EAA4AC0E', X'48EACC7C64BE0647BFCF8594EAA4AC0E', 10, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2B8DC020704D14EA8F67F6FE5C39B10', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the ground flax and water, and let sit to thicken, for approximately 10 minutes.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46BB1EE07E888F45BC50D5FA385C4ED1', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix together the flour and salt, and set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76B6ADD485368C4282B4023940D3D907', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 2, NULL, NULL, 2, NULL, 0, 'In a stand mixer fitted with a paddle attachment, cream the sugar and Crisco coconut oil on low speed for about 3 minutes. You may need to occasionally scrape down the sides of the bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FD75A2922181A4DB099E4F108559178', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 3, NULL, NULL, 2, NULL, 0, 'Add the flax seed mixture and almond extract to the mixer bowl and mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBAFC2401CC8BD44864667B6C67F07C5', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 4, NULL, NULL, 2, NULL, 0, 'Pour the dry ingredients (flour, salt.) into the mixer bowl, cover the bowl loosely with tea towel at first (to prevent the flour from spraying outwards), and mix on low speed until the dry ingredients are well incorporated into the wet ingredients. It may take a few minutes to all be incorporated. You''re looking for the dough to peel away from the walls of the mixer and to clump together onto the paddle attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DA7CA3ADE12AC4C827510ECDAE99E3D', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough between two sheets of parchment paper, place on a baking sheet, and into the fridge for about an hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2114007EA64FD449CBE13B6B076BB3F', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 6, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit (180 degrees Celcius).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8772B31E48202044A6BD24A46E4AEED1', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 7, NULL, NULL, 2, NULL, 0, 'Cut out cookie shapes as desired and place onto a parchment lined baking sheet. Bake for 10-12 minutes, depending on the size of cookies.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF7483945B9044C95619DA3A7B9C95F', X'77BFF47D3782234E8DD04138EE160FBB', X'77BFF47D3782234E8DD04138EE160FBB', 8, NULL, NULL, 2, NULL, 0, 'Enjoy as is or decorate with icing and serve!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EFD1A8CD2681541AB7CFB3F6BB85A3F', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the ground flax and water, and let sit to thicken, for approximately 10 minutes.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4213B61BD022E341BB2AF11F5B403CB0', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix together the flour, cocoa powder and salt, and set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE4AAE259768C640AC1D1C4BB8E5F49D', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 2, NULL, NULL, 2, NULL, 0, 'In a stand mixer fitted with a paddle attachment, cream the sugar and Crisco vegetable shortening on low speed for about 3 minutes. You may need to occasionally scrape down the sides of the bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'361F5CA163D0DC42A4EBD0C1CAB103C9', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 3, NULL, NULL, 2, NULL, 0, 'Add the flax seed mixture and vanilla extract to the mixer bowl and mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E07F20FC8D0D044B8FC65B28FB00FB57', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 4, NULL, NULL, 2, NULL, 0, 'Pour the dry ingredients (flour etc.) into the mixer bowl, cover the bowl loosely with tea towel at first (to prevent the flour from spraying outwards), and mix on low speed until the dry ingredients are well incorporated into the wet ingredients. It may take a few minutes to all be incorporated. You''re looking for the dough to peel away from the walls of the mixer and to clump together onto the paddle attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFB7F05E79B80945A2D5AA1EBBEC9256', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough between two sheets of parchment paper, place on a baking sheet, and into the fridge for about an hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A8F9F569B609A43883ACAD7BACB2E89', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 6, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit (180 degrees Celcius).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'893E5E9AF0644B4894AF56BDF5CD833D', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 7, NULL, NULL, 2, NULL, 0, 'Cut out cookie shapes as desired and place onto a parchment lined baking sheet. Bake for 10-12 minutes, depending on the size of cookies.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF960A16D32F540851757CCDFB66B7D', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', X'BFD9D6BD6FB3A54C8D2E9F4EB005AA76', 8, NULL, NULL, 2, NULL, 0, 'Enjoy as is or decorate with icing and serve!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'181929629A09434C8CC3BA228E57D4F7', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 0, NULL, NULL, 2, NULL, 0, 'Cream the butters and sugar together in the bowl of an electric mixer on low to medium speed. (Use the paddle attachment). Mix until thoroughly incorporated - for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83C2F4584B29B043BECEDB343E393368', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 1, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4452EAB0D72FF43BDE8855F2B62B916', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 2, NULL, NULL, 2, NULL, 0, 'Add liquid vanilla extract. Stir briefly.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD630A4C7D872441A2DD22DA3E185839', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 3, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour and salt). Add all of the flour mixture to the bowl. Place a large tea towel or two small tea towels between the edge of the bowl and the electric mixer so that the flour won''t escape. Mix on low speed for 30 seconds. Remove the tea towels and observe the dough mixing; when it clumps around the paddle attachment and pulls away from the sides of the bowl it''s ready. If you notice your dough is still sticky, add 1/4 cup of flour at a time until, when pinched between two fingers, the dough is soft but not sticky to the touch. (The varying amount of flour depends on the humidity in your environment etc.).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3703F62455D8464B81AD0FE8CD173B05', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 4, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour. I often chill mine in the fridge overnight as I usually need to time-wise.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C79DDF2A534F9E4482D32C8962CF4E92', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 5, NULL, NULL, 2, NULL, 0, 'Cut out cookie shapes. Place on parchment paper-lined baking sheet. Re-roll scraps and repeat.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'676996A049D27744AEC487D63018DD7B', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 6, NULL, NULL, 2, NULL, 0, 'Put cookie dough shapes back into the fridge for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F68AD4E444D38E4BB59EF26389B24839', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 7, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB17704816C57043B0AC18D617412E60', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 8, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the edges become golden brown. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60573F12FE5E7E46BDBF3CD6D9A2DB95', X'8D7120265B77144E83190E7C21791E13', X'8D7120265B77144E83190E7C21791E13', 9, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EB4A96A5E1B2E44AB8F8DAE302A5365', X'920BAA60AD5D8749BFD191657B9A3844', X'920BAA60AD5D8749BFD191657B9A3844', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with the paddle attachment; mix butter and brown sugar on medium speed until fluffy. Scrape down the sides of the bowl and spatula as needed.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C806E9EC2831B341A18F1B2823CBA0F5', X'920BAA60AD5D8749BFD191657B9A3844', X'920BAA60AD5D8749BFD191657B9A3844', 1, NULL, NULL, 2, NULL, 0, 'Mix in eggs, one at a time, and then molasses. Reduce speed to low. Scrape down bowl as needed.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9599803FDE56994D83A30B893506D701', X'920BAA60AD5D8749BFD191657B9A3844', X'920BAA60AD5D8749BFD191657B9A3844', 2, NULL, NULL, 2, NULL, 0, 'Sift together flour, spices and salt and add to bowl; mix on low until just combined. Note that you would sift in the baking powder here as well, if you aren''t concerned about the cookies spreading. Even environment and ingredients used affect baking, so if your dough is a bit sticky add flour until it''s soft to the touch but doesn''t stick to your fingers.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E46A1B1B25E82645B087EBF914796355', X'920BAA60AD5D8749BFD191657B9A3844', X'920BAA60AD5D8749BFD191657B9A3844', 3, NULL, NULL, 2, NULL, 0, 'Roll dough out between parchment or the Dough E-Z mat, and let it rest by refrigerating until cold, about 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECC0103E15D19B428D7B8AB42C9C7FBA', X'920BAA60AD5D8749BFD191657B9A3844', X'920BAA60AD5D8749BFD191657B9A3844', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit. Cut out cookie shapes and place on baking sheet lined with parchment paper, approximately 2 inches apart, and re-roll out dough and cut shapes as needed. Refrigerate until firm; at least 15 minutes to 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1934F389262ABD40A49D06E689DF2C07', X'920BAA60AD5D8749BFD191657B9A3844', X'920BAA60AD5D8749BFD191657B9A3844', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies until lightly golden; 10 to 14 minutes, depending on the size and thickness of the cookie. Let cool on sheets on wire racks and serve as is or decorate with royal icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53DECB8520E6B449BCC0771411374C9E', X'F5912AFACB4DE5498357ACADC8302D85', X'F5912AFACB4DE5498357ACADC8302D85', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and 2 sugars together in the bowl of an electric mixer on low to medium speed. (Use the paddle attachment). Mix until thoroughly incorporated – for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you’d like a light and fluffy cookie, that’s ideal, however the dough will spread more during baking; not ideal if you’d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB4C6A9A9839C4CA25A97E40FD4B70D', X'F5912AFACB4DE5498357ACADC8302D85', X'F5912AFACB4DE5498357ACADC8302D85', 1, NULL, NULL, 2, NULL, 0, 'Add pumpkin purée and eggs while slowly mixing. Add vanilla extract and mix. Scrape down the bowl with your spatula at least once and mix briefly again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E31DC3FE402C5445998C7FA88A5367A9', X'F5912AFACB4DE5498357ACADC8302D85', X'F5912AFACB4DE5498357ACADC8302D85', 2, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour, cinnamon, ginger, nutmeg, all-spice and salt. I do not use baking powder in my cut-out cookies).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'082B8C1050CCE04BA833481F3460783A', X'F5912AFACB4DE5498357ACADC8302D85', X'F5912AFACB4DE5498357ACADC8302D85', 3, NULL, NULL, 2, NULL, 0, 'Add all of the flour mixture to the bowl. Place a large tea towel or two small tea towels between the edge of the bowl and the electric mixer so that the flour won’t escape. Mix on low speed for 3o seconds. Remove the tea towels and observe the dough mixing; when it clumps around the paddle attachment it’s ready. It’s also important at this stage not to over mix the dough (the glutens in the flour develop and the dough can become tough).');
