INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'398A51F11FEA1942982EC95EDF6097E5', X'FD20267D149B1344BDE57A41E3247FEC', X'FD20267D149B1344BDE57A41E3247FEC', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour.');
