INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3726EA0E5CDD104F933957FDB7822C98', X'D96D22841B134545A6F90D4BB21C8F36', X'D96D22841B134545A6F90D4BB21C8F36', 2, NULL, NULL, 2, NULL, 0, 'Sift together flour and salt and add to bowl; mix until just combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53855E5DD8F14C4881C54DF01C26AD38', X'D96D22841B134545A6F90D4BB21C8F36', X'D96D22841B134545A6F90D4BB21C8F36', 3, NULL, NULL, 2, NULL, 0, 'Wrap dough in a disc shape in saran wrap. Let it rest by refrigerating until cold, about 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17B3064EC44B3C43AFE77629A1A9BE93', X'D96D22841B134545A6F90D4BB21C8F36', X'D96D22841B134545A6F90D4BB21C8F36', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit. Roll out dough on a lightly floured work surface or between two sheets of parchment paper to about 1/4-inch thick.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200C846BCFF2114F8AA41EE7166F71D4', X'D96D22841B134545A6F90D4BB21C8F36', X'D96D22841B134545A6F90D4BB21C8F36', 5, NULL, NULL, 2, NULL, 0, 'Cut shapes out with cookie cutters and place them approximately 2 inches apart on baking sheets lined with parchment paper.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6160CF9CB92B1F40849F7DF6639C493E', X'D96D22841B134545A6F90D4BB21C8F36', X'D96D22841B134545A6F90D4BB21C8F36', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate until firm; at least 15 minutes to 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCD318C2B6B56E4E888147A9C244F46C', X'D96D22841B134545A6F90D4BB21C8F36', X'D96D22841B134545A6F90D4BB21C8F36', 7, NULL, NULL, 2, NULL, 0, 'Bake cookies until lightly golden; 12 to 14 minutes. Let cool to room temperature before decorating.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2675F1EC9B2A114585CB477605367508', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D45B1F94A03C34EB0B684FB41482CD1', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with the paddle attachment; mix butter, white sugar and brown sugar on medium speed until fluffy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E323A469BE68F8499E427C8CFB21962A', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla extract and eggs and mix well.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D264D7C62F66E4782F9EE2C7B64416B', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 3, NULL, NULL, 2, NULL, 0, 'Sift together flour and baking soda and add to mixing bowl; mix until just combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59514EC41D61544DB4022F42B5903412', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 4, NULL, NULL, 2, NULL, 0, 'Fold in chocolate chips.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E4F406F6F29064B8FC027D0C4207E99', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 5, NULL, NULL, 2, NULL, 0, 'Form the dough into uniform size balls and space them apart on a baking sheet lined with parchment paper. {A 1 ½ inch ice-cream scoop or a tablespoon can help you create uniform cookie balls}.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'237F1C4D8E097645A47178B74842A89A', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 6, NULL, NULL, 2, NULL, 0, 'Bake for 15 minutes if you like your cookies chewy, longer if you like them crisp. (Approx. 20 minutes or until edges are golden brown and the cookie no longer looks ‘wet’).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'183CAA709266654097B0D95708E2718B', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 7, NULL, NULL, 2, NULL, 0, 'Remove from oven, let cool on the pan for 5 minutes, and then transfer to a wire rack to cool.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8046B612745A614EBEC51AF22E7F0544', X'6E7B319E74E7164DB6765C3A3CA9A2B6', X'6E7B319E74E7164DB6765C3A3CA9A2B6', 8, NULL, NULL, 2, NULL, 0, 'Once cookies are cool, decorate with royal icing if desired!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D34AF9CC853F374BBDF85FCA4E408C8D', X'1F9AD461F507BB41A5AEA4952B318923', X'1F9AD461F507BB41A5AEA4952B318923', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with the paddle attachment; mix butter and brown sugar on medium speed until fluffy. Scrape down the sides of the bowl and spatula as needed.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C714AD7BCDC3F49BEBADA85557C12AF', X'1F9AD461F507BB41A5AEA4952B318923', X'1F9AD461F507BB41A5AEA4952B318923', 1, NULL, NULL, 2, NULL, 0, 'Mix in eggs, one at a time, and then molasses. Reduce speed to low. Scrape down bowl as needed.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4631A483FFFE50458EE1F93DEB345257', X'1F9AD461F507BB41A5AEA4952B318923', X'1F9AD461F507BB41A5AEA4952B318923', 2, NULL, NULL, 2, NULL, 0, 'Sift together flour, spices and salt and add to bowl; mix on low until just combined. Note that you would sift in the baking powder here as well, if you aren''t concerned about the cookies spreading. Even environment and ingredients used affect baking, so if your dough is a bit sticky add flour until it''s soft to the touch but doesn''t stick to your fingers.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'321043F2E221C14BAE154FAD7F1FD9C8', X'1F9AD461F507BB41A5AEA4952B318923', X'1F9AD461F507BB41A5AEA4952B318923', 3, NULL, NULL, 2, NULL, 0, 'Roll dough out between parchment or the Dough E-Z mat, and let it rest by refrigerating until cold, about 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36A29E699F635548B2717FBCFB2D389B', X'1F9AD461F507BB41A5AEA4952B318923', X'1F9AD461F507BB41A5AEA4952B318923', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit. Cut out cookie shapes and place on baking sheet lined with parchment paper, approximately 2 inches apart, and re-roll out dough and cut shapes as needed. Refrigerate until firm; at least 15 minutes to 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B3E4BA19941A943B534E91E077DD342', X'1F9AD461F507BB41A5AEA4952B318923', X'1F9AD461F507BB41A5AEA4952B318923', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies until lightly golden; 10 to 14 minutes, depending on the size and thickness of the cookie. Let cool on sheets on wire racks and serve as is or decorate with royal icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAA9039C84FC5F4BA8B9ACF2069773E9', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together in the bowl of an electric mixer on low to medium speed. (Use the paddle attachment). Mix until thoroughly incorporated - for about one minute. Scrape down the sides of the bowl with a plastic spatula and mix again for a few seconds more. Over mixing the butter and sugar in this step will cause too much air to be incorporated into the dough. If you''d like a light and fluffy cookie, that''s ideal, however the dough will spread more during baking; not ideal if you''d like the cookie to hold its shape.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C05208F4D4052A4A9E731AF117DF162A', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 1, NULL, NULL, 2, NULL, 0, 'Add eggs slowly and mix. Scrape down the bowl with your spatula at least once and mix again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB64EA9303891D43A452B4907A422E14', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 2, NULL, NULL, 2, NULL, 0, 'Add liquid vanilla extract. Stir briefly. Alternatively, cut open your vanilla bean and scrape the seeds out. Add to mixing bowl and stir.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'613C43586188D7458207830F6CEE53A9', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 3, NULL, NULL, 2, NULL, 0, 'Sift your dry ingredients together. (Flour, baking powder and salt).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DE9EB571BB1974581948760BCED8AD9', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 4, NULL, NULL, 2, NULL, 0, 'Add all of the flour mixture to the bowl. Place a large tea towel or two small tea towels between the edge of the bowl and the electric mixer so that the flour won''t escape. Mix on low speed for 30 seconds. Remove the tea towels and observe the dough mixing; when it clumps around the paddle attachment it''s ready. It''s also important at this stage not to over mix the dough (the glutens in the flour develop and the dough can become tough).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15ECE48EE290B74BB5A54A0811A1C0E0', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 5, NULL, NULL, 2, NULL, 0, 'Roll the dough out between 2 large pieces of parchment paper. Place on a baking sheet and into the fridge for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A16C3DC63697D946960460F7F7002721', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 6, NULL, NULL, 2, NULL, 0, 'Roll out the dough further if you need to, and cut out cookie shapes. Place on parchment paper-lined baking sheet. Re-roll scraps and repeat.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F17338EB7F6D93458076FF429BFBD742', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 7, NULL, NULL, 2, NULL, 0, 'Put cookie dough shapes back into the fridge for 10 minutes to 1 hour to chill again. They will then hold their shape better when baked.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129C2DFD398FB74995E22F238A93FEC7', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 8, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F or 176°C.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBCE29E074FC504280096BB658C64402', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-12 minutes or until the edges become golden brown. The baking time will depend on the size of your cookie.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEF1A31A1FD92E40A61B3E5FB7CF990F', X'B95F475235A2084E91A0338A82F89F72', X'B95F475235A2084E91A0338A82F89F72', 10, NULL, NULL, 2, NULL, 0, 'Let cookies cool to room temperature and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B420C72A404C2846B8759690F4FF569F', X'772AB3827BC0ED41A72F6C7278188CD9', X'772AB3827BC0ED41A72F6C7278188CD9', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'690381527BBCA64D91F54A8B49542E50', X'772AB3827BC0ED41A72F6C7278188CD9', X'772AB3827BC0ED41A72F6C7278188CD9', 1, NULL, NULL, 2, NULL, 0, 'Cream the coconut oil and coconut sugar in the bowl of a stand mixer fitted with the paddle attachment, on high speed, for approximately 3 minutes. You may need to occasionally scrape down the sides of the bowl and the attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC573ADD18E44C44BE81248F8B65F53F', X'772AB3827BC0ED41A72F6C7278188CD9', X'772AB3827BC0ED41A72F6C7278188CD9', 2, NULL, NULL, 2, NULL, 0, 'With the mixer on low speed, add the eggs, one at a time, letting each egg be mixed in before you add the next, and scraping down the bowl after each addition. Add the vanilla extract and mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'585C40C78BA9054FA6405E0F66E6FFE5', X'772AB3827BC0ED41A72F6C7278188CD9', X'772AB3827BC0ED41A72F6C7278188CD9', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the flour, baking soda, baking powder and salt. In two parts, alternately add the dry ingredients and the buttermilk to the batter, beginning and ending with the dry. Mix until just barely combined. Fold in the finely crushed coconut chips.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85A4811EABB1E94081CDFB3F163682CC', X'772AB3827BC0ED41A72F6C7278188CD9', X'772AB3827BC0ED41A72F6C7278188CD9', 4, NULL, NULL, 2, NULL, 0, 'Line a muffin tin with paper liners. Fill each liner with batter, until about ¾ full. Bake for 20 minutes or until a toothpick comes out clean. Allow cupcakes to cool for about 15 minutes and then remove to a baking rack to completely cool.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD08BFF614CB754C829CAECAF252F85F', X'772AB3827BC0ED41A72F6C7278188CD9', X'772AB3827BC0ED41A72F6C7278188CD9', 5, NULL, NULL, 2, NULL, 0, 'Garnish with icing or serve as is!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'649FEB6FD3AF85488EEF373C3DEC1770', X'938B5A3364BBB347B2CC18E24975EFA1', X'938B5A3364BBB347B2CC18E24975EFA1', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees. Line muffin tin with paper liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD1D46ADC053FC4B91F5E996927A82B1', X'938B5A3364BBB347B2CC18E24975EFA1', X'938B5A3364BBB347B2CC18E24975EFA1', 1, NULL, NULL, 2, NULL, 0, 'Sift together the flour, baking powder, baking soda, salt, cinnamon and ginger, into a medium bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80D45E37C05A0C4BA2ABADAEF24C623F', X'938B5A3364BBB347B2CC18E24975EFA1', X'938B5A3364BBB347B2CC18E24975EFA1', 2, NULL, NULL, 2, NULL, 0, 'In a larger bowl, whisk together the eggs, pumpkin purée, granulated sugar, brown sugar, and Crisco pure vegetable oil. Add the flour mixture and stir until combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'202B50726CEAC646B1138EE0D9767439', X'938B5A3364BBB347B2CC18E24975EFA1', X'938B5A3364BBB347B2CC18E24975EFA1', 3, NULL, NULL, 2, NULL, 0, 'Divide the batter among the muffin tin and bake for about 20 minutes, until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA0CD6842349EB43B75867BDCE497679', X'938B5A3364BBB347B2CC18E24975EFA1', X'938B5A3364BBB347B2CC18E24975EFA1', 4, NULL, NULL, 2, NULL, 0, 'Set pumpkin cupcakes aside to cool completely. Frost with icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51135391FC67454984F3BBCFC9A8F001', X'915D8F9651A8B44AAAB600F90FA81DCE', X'915D8F9651A8B44AAAB600F90FA81DCE', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer, using the paddle attachment, mix the Crisco All-vegetable shortening and clear vanilla extract on medium speed for about a minute. Scrape down the sides of the bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B33CBD74B8B19643905E8C2AEE6409DC', X'915D8F9651A8B44AAAB600F90FA81DCE', X'915D8F9651A8B44AAAB600F90FA81DCE', 1, NULL, NULL, 2, NULL, 0, 'Add the icing sugar and milk, cover the bowl with a large tea towel, and mix on low speed until the icing sugar is incorporated into the shortening. Take the tea towel away and mix until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'702FF90B571E124EAB8EA96EDC228FD6', X'915D8F9651A8B44AAAB600F90FA81DCE', X'915D8F9651A8B44AAAB600F90FA81DCE', 2, NULL, NULL, 2, NULL, 0, 'Once your cupcakes are cool, ice them with a butter knife or large piping bag and tip of your choice. (I used Ateco #855, #856 and Wilton 1M).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'582D8081FD2EEE4FBC77E1DEF170E0B3', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 0, NULL, NULL, 2, NULL, 0, 'Preaheat oven to 350 degrees Fahrenheit and line your muffin tin with cupcake liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1FB12FFC5F68649B0AE0D012164FE38', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 1, NULL, NULL, 2, NULL, 0, 'Sift flour, baking powder and salt onto parchment paper. Stir thoroughly to blend.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3B0DFFA30DB0244A3182633EA3EA6D1', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 2, NULL, NULL, 2, NULL, 0, 'Cream shortening until fluffy; gradually add sugar mixing until creamy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73420E2DFB7AFF45BF38B5C2A81C285E', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 3, NULL, NULL, 2, NULL, 0, 'Add vanilla and then eggs, one at a time, beating well after each addition.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DED0EBE8FC828A45B10C21CBC0026A41', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients alternately with LACTAID® milk, starting and ending with dry ingredients and mixing until well blended after each addition.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17EE04323F15304F9E7F14E9739A0B7C', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 5, NULL, NULL, 2, NULL, 0, 'Use a 1/4 inch cup or ice cream scoop to add cupcake batter to liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB5BB6FB2A719D478EB2F7911B95C920', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 6, NULL, NULL, 2, NULL, 0, 'Bake for approximately 15 minutes or until an inserted toothpick comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0AB07494135CC4BA33045AFA7112CE7', X'C945E45DEE4226499469B096E15CA360', X'C945E45DEE4226499469B096E15CA360', 7, NULL, NULL, 2, NULL, 0, 'Let cupcakes cool and ice with frosting.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3EB847FBAD346489D6861ED634B564E', X'0F81BCAD4FBF1A41834D1055082B039E', X'0F81BCAD4FBF1A41834D1055082B039E', 0, NULL, NULL, 2, NULL, 0, 'With the rack in the middle position, preheat the oven to 180 °C (350 °F). Line your muffin tray with cupcake liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D94852432713540AFD4638AC4247983', X'0F81BCAD4FBF1A41834D1055082B039E', X'0F81BCAD4FBF1A41834D1055082B039E', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine flour, baking soda, baking powder and salt. Set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1FFAE6F89D92F40BE9C3C4FC1FC187D', X'0F81BCAD4FBF1A41834D1055082B039E', X'0F81BCAD4FBF1A41834D1055082B039E', 2, NULL, NULL, 2, NULL, 0, 'In another bowl, combine eggs, sugar, buttermilk, oil and vanilla with a whisk. Set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC50BC105C71434F8B4BB55554ADDB8F', X'0F81BCAD4FBF1A41834D1055082B039E', X'0F81BCAD4FBF1A41834D1055082B039E', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine cocoa powder and coffee with a whisk. Add to egg mixture. Add dry ingredients and stir until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C46B17035E4BA49A7115C538DC5F497', X'0F81BCAD4FBF1A41834D1055082B039E', X'0F81BCAD4FBF1A41834D1055082B039E', 4, NULL, NULL, 2, NULL, 0, 'Spoon batter into cupcake liners (About 1/4 cup per liner). Bake for 15 minutes or until a toothpick inserted in the centre of the cupcake comes out clean. Let cool completely before filling the cupcake centers with ganache and topping with icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF743C843E654049AB77C29A4E56E932', X'CA67B43464CB764090BC929E6EDD6893', X'CA67B43464CB764090BC929E6EDD6893', 0, NULL, NULL, 2, NULL, 0, 'In a saucepan, bring cream and corn syrup to a boil.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F999486E0A414546A27F6E7638CDD413', X'CA67B43464CB764090BC929E6EDD6893', X'CA67B43464CB764090BC929E6EDD6893', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, pour hot cream mixture over chocolate. Let melt for 2 minutes. Whisk until ganache is smooth. Refrigerate for about 1 hour or until it has thickened and is spreadable, stirring four or five times.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6C53311C3B7EC4D9C00E4F16779BD27', X'CA67B43464CB764090BC929E6EDD6893', X'CA67B43464CB764090BC929E6EDD6893', 2, NULL, NULL, 2, NULL, 0, 'For the ganache center of the cupcake, using a #10 piping tip or other piping tip of your choice, sink your piping tip into the top of the cooled cupcake to about the center, and squeeze out a dollop of icing. Stop squeezing and remove.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AA71A78A4CA214B959C352BCEE6629A', X'1DA7B74F32C7D24D8BE67D0602C5791D', X'1DA7B74F32C7D24D8BE67D0602C5791D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B076E0D940FA4A4580C03E91C1A254BA', X'1DA7B74F32C7D24D8BE67D0602C5791D', X'1DA7B74F32C7D24D8BE67D0602C5791D', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with the paddle attachment; beat the cake mix, pudding mix, water, oil and eggs on medium speed for 2 to 3 minutes, or until well blended. Scrape the sides of the bowl and mix again until all ingredients are well incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73BE1788F9C1A34FB49B975495849669', X'1DA7B74F32C7D24D8BE67D0602C5791D', X'1DA7B74F32C7D24D8BE67D0602C5791D', 2, NULL, NULL, 2, NULL, 0, 'Line 2 cupcake pans with liners. Using an ice cream scoop or ¼ measuring cup, add one scoop of batter into each cupcake liner.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4D554E31ADAA2458A391962542315BB', X'1DA7B74F32C7D24D8BE67D0602C5791D', X'1DA7B74F32C7D24D8BE67D0602C5791D', 3, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes for 15-20 minutes, or until a wooden toothpick comes out clean. Remove the cupcakes from the oven and let them cool for at least 15 minutes before removing them from the tray.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FECC4B5B0E06FF4CB5FE0276A288A495', X'1DA7B74F32C7D24D8BE67D0602C5791D', X'1DA7B74F32C7D24D8BE67D0602C5791D', 4, NULL, NULL, 2, NULL, 0, 'Allow cupcakes to cool completely and decorate!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73DCBB7115550946A733DD967881788F', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with the paddle attachment; mix butter and brown sugar on medium speed until fluffy. Scrape down the sides of the bowl and spatula as needed.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B977A019B4AFC40B9B90E9788DDC7BA', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', 1, NULL, NULL, 2, NULL, 0, 'Mix in eggs, one at a time, and then molasses. Reduce speed to low. Scrape down bowl as needed.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD9C68475EFFC943B5BDFD324CCE21A8', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', 2, NULL, NULL, 2, NULL, 0, 'Sift together flour, spices and salt and add to bowl; mix on low until just combined. Note that you would sift in the baking powder here as well, if you aren''t concerned about the cookies spreading. Even environment and ingredients used affect baking, so if your dough is a bit sticky add flour until it''s soft to the touch but doesn''t stick to your fingers.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72663E334656144D9049AD2D3B4FD6AE', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', 3, NULL, NULL, 2, NULL, 0, 'Roll dough out between parchment or the Dough E-Z mat, and let it rest by refrigerating until cold, about 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39C59E917ADB77449F65E0C119FAFBCD', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit. Cut out cookie shapes and place on baking sheet lined with parchment paper, approximately 2 inches apart, and re-roll out dough and cut shapes as needed. Refrigerate until firm; at least 15 minutes to 1 hour.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4058BFC2552391428250BE66554B7CD0', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', X'4ADD3DA93A3D97428783DF8FE1D1D3EF', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies until lightly golden; 10 to 14 minutes, depending on the size and thickness of the cookie. Let cool on sheets on wire racks and serve as is or decorate with royal icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'983A00E0D9D5104A85C59CA06D132F3B', X'3C835D983943944391626C163B0E3C5B', X'3C835D983943944391626C163B0E3C5B', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit. Line your muffin tin with 24 regular sized baking cups.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8564391461EC6B4BACDC5FE098B88D71', X'3C835D983943944391626C163B0E3C5B', X'3C835D983943944391626C163B0E3C5B', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix the flour, cocoa and salt and set aside. In the bowl of an electric mixer fitted with a paddle attachment, beat the butter and sugar on medium speed until mixed. Slowly add the eggs; beat 1 to 2 minutes or until light and fluffy. Add the red food color and vanilla and mix slowly until incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9524A7C53AB48C4DA3F7FAECFEBA929D', X'3C835D983943944391626C163B0E3C5B', X'3C835D983943944391626C163B0E3C5B', 2, NULL, NULL, 2, NULL, 0, 'Stir the vinegar into the buttermilk.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6611ED2A90B894FA034E712B1283316', X'3C835D983943944391626C163B0E3C5B', X'3C835D983943944391626C163B0E3C5B', 3, NULL, NULL, 2, NULL, 0, 'On low speed, beat in 1/2 of the flour mixture just until mixed, then beat in 1/2 of the buttermilk mixture until mixed. Repeat beating in flour mixture alternately with the buttermilk just until mixed. Beat in the baking soda until mixed. Spoon about 1/4 cup of the batter into the cups.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15530AF2EA7A884F9E756B3B4AD5ABE0', X'3C835D983943944391626C163B0E3C5B', X'3C835D983943944391626C163B0E3C5B', 4, NULL, NULL, 2, NULL, 0, 'Bake about 20 minutes or until a toothpick inserted in the center of the cupcake comes out clean. Remove cupcakes from pan, cool completely and ice.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92528BADD5971B4DB05B7ADD405042A5', X'636FBDD064CCC443A0517359FD613D71', X'636FBDD064CCC443A0517359FD613D71', 0, NULL, NULL, 2, NULL, 0, 'Cream the cream cheese, butter, and vanilla in the bowl of an electric mixer fitted with a paddle attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DDBBC6E2997074C9174CCAC3A4F2C90', X'636FBDD064CCC443A0517359FD613D71', X'636FBDD064CCC443A0517359FD613D71', 1, NULL, NULL, 2, NULL, 0, 'Slowly add the icing sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCA1948F5C2E8740848701DE38B6B496', X'636FBDD064CCC443A0517359FD613D71', X'636FBDD064CCC443A0517359FD613D71', 2, NULL, NULL, 2, NULL, 0, 'Add 1 tsp white food gel coloring and mix until incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA95E6B53B6B5B4590DAEE19EB08EEC6', X'636FBDD064CCC443A0517359FD613D71', X'636FBDD064CCC443A0517359FD613D71', 3, NULL, NULL, 2, NULL, 0, 'Once cupcakes have cooled, ice as desired.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C5E0EC641ADB94D9767026FCEDA4E57', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1C797848A79F64B9573559FD1223A61', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 1, NULL, NULL, 2, NULL, 0, 'Line your cupcake pan with cupcake liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84BB79F118F5E449E3A5082EC53536A', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 2, NULL, NULL, 2, NULL, 0, 'Sift cocoa into a medium bowl. Slowly stir in the boiling water until smooth, then gradually whisk in milk. Set aside to cool.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D25B11C1F0F1D4A9BE4B21F3EF22093', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 3, NULL, NULL, 2, NULL, 0, 'Whisk together flour, baking soda and salt in a bowl. In the bowl of an electric mixer fitted with the whisk attachment, or with an hand mixer on low speed, beat butter until fluffy; 3-5 minutes. Gradually beat in sugar, scraping down the sides of the bowl at least twice. Beat in the vanilla.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8C8A4B9E6AB4E4AAA8CB2FCEC4FA939', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 4, NULL, NULL, 2, NULL, 0, 'Add eggs in 3 batches, beating well after each addition. With the mixer on low speed, add flour mixture and reserved cocoa mixture a little at a time in alternate batches, starting and ending with the flour; beat until just combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2641F5F5818AC439946F8D979E32791', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 5, NULL, NULL, 2, NULL, 0, 'Divide batter into the cupcake liners, 1/4 cup for each liner.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F148674BF60D44285730D3C665E5BD2', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 6, NULL, NULL, 2, NULL, 0, 'Bake for approximately 12-15 minutes or longer - until a toothpick or skewer comes out clean. Time will vary according to your oven.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'204D41D3393A694B9DEA32E037A79C64', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 7, NULL, NULL, 2, NULL, 0, 'Take cupcake out and when cooled for about 5-10 minutes, remove from pans. Let cool completely and cut out a small hole in the top, center of each cupcake and fill with chilled dulce de leche. I only filled mine a little bit as I find it doesn''t need a lot paired with the caramel icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B075CAC1B296C04DB4BC7089FB179A90', X'A3BCA410E4D0904C989E792502BA8A7F', X'A3BCA410E4D0904C989E792502BA8A7F', 8, NULL, NULL, 2, NULL, 0, 'To finish, pipe caramel frosting onto cupcake with a large, round tip and place a gum paste leaf on top of the icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16141376674F3A47A57C2F08A7C9C86F', X'3DA7967310F2154FBA412E647CEC1305', X'3DA7967310F2154FBA412E647CEC1305', 0, NULL, NULL, 2, NULL, 0, 'Place the cream cheese in a mixing bowl fitted with a paddle attachment, and mix on low speed until smooth and creamy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31440A13DE60F847917FF71387B5473D', X'3DA7967310F2154FBA412E647CEC1305', X'3DA7967310F2154FBA412E647CEC1305', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and icing sugar in another mixing bowl until pale and fluffy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C341A0D28F118B4FAA48C3EA092ED2A0', X'3DA7967310F2154FBA412E647CEC1305', X'3DA7967310F2154FBA412E647CEC1305', 2, NULL, NULL, 2, NULL, 0, 'Slowly add the cream cheese to the butter mixture and mix at medium-high speed until the frosting is combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E62C8641562548418C7BEA893ECE128E', X'3DA7967310F2154FBA412E647CEC1305', X'3DA7967310F2154FBA412E647CEC1305', 3, NULL, NULL, 2, NULL, 0, 'Add the dulce de leche and mix until well incorporated and smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94928B3EB99093468DF0955AD942D103', X'3DA7967310F2154FBA412E647CEC1305', X'3DA7967310F2154FBA412E647CEC1305', 4, NULL, NULL, 2, NULL, 0, 'Your frosting will be soft at this point. Chill in the refrigerator until set.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D10BFECBA47C4DA879D3CDE903CF7D', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F7D935CBF7172488BA726B3B2AD4D62', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 1, NULL, NULL, 2, NULL, 0, 'Sift the flour, baking powder, baking soda, and salt into a bowl and set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D2894DCEB67BB4EADE5561573068CD7', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a mixer fitted with a whisk attachment, or in a large bowl using an electric mixer on medium speed, beat the eggs and sugar until lightened to a cream color, about 2 minutes. During mixing, scrape down the sides of the bowl with a spatula as needed.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C97035C74F8B84DAD5D056E63DE0C73', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 3, NULL, NULL, 2, NULL, 0, 'On low speed, mix in the oil and vanilla until blended.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6721A558BB69D4BB7F251611DDC00AB', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 4, NULL, NULL, 2, NULL, 0, 'Mix in the sour cream.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45F5CCD7F5E0D847BDC0CCAA5B8963D3', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 5, NULL, NULL, 2, NULL, 0, 'Add in the flour mixture until it is incorporated and the batter is smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE836DCE31926F40864D03BDDEBBFCD9', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 6, NULL, NULL, 2, NULL, 0, 'Stir in the cinnamon and mashed bananas.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED698FDEEAAA224EBD13CA96CD33D4FB', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 7, NULL, NULL, 2, NULL, 0, 'In a muffin tin lined with cupcake liners, fill each liner with 1/4 cup cup of the batter.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62D56D66204B8542A9DD317234E0818F', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 8, NULL, NULL, 2, NULL, 0, 'Bake for approximately 25 minutes; until the top of the cupcake feels firm and an inserted toothpick comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EE562F2F1880C40A2393CBCF0BF2276', X'E96F0CB0DC2FA74DA92B3515DC77C37A', X'E96F0CB0DC2FA74DA92B3515DC77C37A', 9, NULL, NULL, 2, NULL, 0, 'Let the cupcakes cool completely and decorate.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1667E295D7768846B344BD6A370C8403', X'C5FFD07F3316F840BADA3A407A806F20', X'C5FFD07F3316F840BADA3A407A806F20', 0, NULL, NULL, 2, NULL, 0, 'Cream the cream cheese, butter, and vanilla in the bowl of an electric mixer fitted with a paddle attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05BD70162FFA2C4383ED03AD1E09BC07', X'C5FFD07F3316F840BADA3A407A806F20', X'C5FFD07F3316F840BADA3A407A806F20', 1, NULL, NULL, 2, NULL, 0, 'Slowly add the icing sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD6432518AFD2241A6738AD1010C477E', X'C5FFD07F3316F840BADA3A407A806F20', X'C5FFD07F3316F840BADA3A407A806F20', 2, NULL, NULL, 2, NULL, 0, 'Add 1 tsp white food gel coloring and mix until incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8767D296B2FEA4896432807E92DC414', X'C5FFD07F3316F840BADA3A407A806F20', X'C5FFD07F3316F840BADA3A407A806F20', 3, NULL, NULL, 2, NULL, 0, 'Once cupcakes have cooled, ice as desired.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1689452380BAB247A53BBE5FEAE6FD8D', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', 0, NULL, NULL, 2, NULL, 0, 'In a food processor, pulse together raisins, currants, prunes, candied fruit and citrus peel until a smooth, thick paste forms. Transfer to a large bowl and stir in 2 cups (500 mL) of the rum. Cover tightly and keep in a cool, dark spot, stirring once a day, for at least 2 days, or for up to 2 months.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82EFBC60D7E5E84C94F4E72F7A2A500C', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer, using the paddle attachment, beat butter and brown sugar until smooth. Beat in eggs, 1 at a time. Mix in coconut and almond extract.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0D9286A41D0C8469A48217501C29A71', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whisk together flour, almonds, baking powder, cinnamon, cloves, nutmeg and salt; slowly stir into butter mixture until incorporated. Stir in raisin mixture and glacé cherries.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCCE569CCEDA3A4893FB8AE84E17E732', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F (180°C).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'926017C4C4C42643908A19C947DA344B', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', 4, NULL, NULL, 2, NULL, 0, 'For cupcakes, line cupcake pans with liners, and using a ¼-cup (60-mL) measuring cup (or a #16 / 2 oz ice cream scoop), scoop the fruitcake batter into each liner. Bake for 20 to 25 minutes, or until a toothpick comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC9A961C64D5564CB26448F2BBBFEA76', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', 5, NULL, NULL, 2, NULL, 0, 'For a Bundt cake, spray the Bundt cake pan with a nonstick cooking spray and pour the batter into the pan. Use a plastic spatula to evenly spread out the top of the batter. Bake for 40 to 50 minutes, or until a toothpick comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AD189AD2F74664185781C74C35B46C2', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', X'DFAC5E69B2EDD941AB3E1AF4175C4E82', 6, NULL, NULL, 2, NULL, 0, 'Using a skewer, poke holes in the top of your cupcakes or cake and brush with rum. Let cool in the pan for 24 hours. Dust with icing sugar or top with luscious coconut cream cheese icing and sweetened coconut flakes.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A18AAFBF613A764786CDCBF4A17EB946', X'EC4658DBC7AAB14999AAB7DAC3894C13', X'EC4658DBC7AAB14999AAB7DAC3894C13', 0, NULL, NULL, 2, NULL, 0, 'Cream the cream cheese, butter, and vanilla in the bowl of an electric mixer fitted with a paddle attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A76D69F358BB2D429FDC693F41F95E4F', X'EC4658DBC7AAB14999AAB7DAC3894C13', X'EC4658DBC7AAB14999AAB7DAC3894C13', 1, NULL, NULL, 2, NULL, 0, 'Slowly add the icing sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3D0B1DF2EB01341AE983299DAE37BE8', X'EC4658DBC7AAB14999AAB7DAC3894C13', X'EC4658DBC7AAB14999AAB7DAC3894C13', 2, NULL, NULL, 2, NULL, 0, 'Add 1 tsp white food gel coloring and mix until incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D08290B0D05BAE4F9EEADC730C8478DE', X'EC4658DBC7AAB14999AAB7DAC3894C13', X'EC4658DBC7AAB14999AAB7DAC3894C13', 3, NULL, NULL, 2, NULL, 0, 'Once cupcakes have cooled, ice as desired.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269513B4F68DF042A7D56E4FDB11C429', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FD3CF0272F45D4EB71EA976C9A89062', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 1, NULL, NULL, 2, NULL, 0, 'Sift together cake flour, baking powder and salt. Set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18FC2ABBD4336046AECBC99E72F57EDA', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 2, NULL, NULL, 2, NULL, 0, 'Cream butter for about 3 minutes, scraping down sides of bowl occasionally.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF38B35447463C418D3B58A8CB1CFE63', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 3, NULL, NULL, 2, NULL, 0, 'Gradually add brown sugar, creaming after each addition. Set mixture aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04F71E77FA718A46912B2CBB462837A6', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 4, NULL, NULL, 2, NULL, 0, 'Beat egg yolks until thick and lemon colored.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35B2EBF3F9CCFC46829B3A0D7B6CD7A6', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 5, NULL, NULL, 2, NULL, 0, 'Add to the creamed mixture in thirds, beating thoroughly after each addition.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'480D62606A701D4D9648DF56AEDCC1AF', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 6, NULL, NULL, 2, NULL, 0, 'Blend milk and maple syrup together.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CE8F2821898EE4CA6E3610D1E3681FD', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 7, NULL, NULL, 2, NULL, 0, 'Beating only until smooth after each addition, alternately add the dry ingredients and liquids to the creamed mixture.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28DC783F3FECD54D8879059AA6A1835E', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 8, NULL, NULL, 2, NULL, 0, 'Beat only until smooth and fill cupcake liners using an ice cream scoop.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A38D2AE9E0BA2C44902A3DB6D4358298', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 9, NULL, NULL, 2, NULL, 0, 'Bake for approximately 15 minutes or until a toothpick comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62EB0C5805CF9A48AC584537B21A21EC', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', X'E15F02F3A15CA74FBC1775F0AC8F6C3B', 10, NULL, NULL, 2, NULL, 0, 'Once cool, frost cupcakes.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DD034134FDC7F4A90428764CAFE3495', X'7B81258CE87E6941877FD39B57755FFC', X'7B81258CE87E6941877FD39B57755FFC', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a medium saucepan, and stir over low heat until sugar is dissolved.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'687D632309FB664399F98040772A4232', X'7B81258CE87E6941877FD39B57755FFC', X'7B81258CE87E6941877FD39B57755FFC', 1, NULL, NULL, 2, NULL, 0, 'Increase heat and bring to a boil.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31F2824644830F4BA329E70B7BEA9905', X'7B81258CE87E6941877FD39B57755FFC', X'7B81258CE87E6941877FD39B57755FFC', 2, NULL, NULL, 2, NULL, 0, 'Put candy thermometer in place and continue cooking without stirring. During cooking wash, sugar crystals from sides of pan with a pastry brush dipped in water. Cook until mixture reaches 238 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD94B9638BDAB349B8118C877BD3D750', X'7B81258CE87E6941877FD39B57755FFC', X'7B81258CE87E6941877FD39B57755FFC', 3, NULL, NULL, 2, NULL, 0, 'Remove pan to cooling rack and cool to lukewarm (about 110 degrees Fahrenheit), without stirring or moving the pan.');
