INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'812D13BC48530348A5967DFD05FCDAAF', X'7E49F1207B15E443808B470107E53B37', X'7E49F1207B15E443808B470107E53B37', 6, NULL, NULL, 2, NULL, 0, 'Using an ice cream scoop or 1/4 cup measurer, scoop batter into a muffin tin lined with cupcake liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F447A5797AE8464CBDD4C6D98259BF7E', X'7E49F1207B15E443808B470107E53B37', X'7E49F1207B15E443808B470107E53B37', 7, NULL, NULL, 2, NULL, 0, 'Bake for approximately 15-20 minutes, until cake tester (toothpicks or skewers work well too), inserted in centre comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C15CD768AA53D443AE27F25B318C998F', X'7E49F1207B15E443808B470107E53B37', X'7E49F1207B15E443808B470107E53B37', 8, NULL, NULL, 2, NULL, 0, 'Let cool in muffin tins for about 5 minutes, then remove and let cool completely before adding icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29857A880D52E44EAC3AA080DD987E56', X'C6E5A8A8ACCED0418F2CF160730CEC16', X'C6E5A8A8ACCED0418F2CF160730CEC16', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F. Place liners in cupcake pan.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8373E44D66C11549A726E8ED37AB34AB', X'C6E5A8A8ACCED0418F2CF160730CEC16', X'C6E5A8A8ACCED0418F2CF160730CEC16', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and 2 sugars on high speed until light and fluffy, approximately 5 minutes. (In the bowl of an electric mixer fitted with a paddle attachment is ideal, but a hand mixer can be used as well).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B68C94408441D42A56C4B823FCDE3CB', X'C6E5A8A8ACCED0418F2CF160730CEC16', X'C6E5A8A8ACCED0418F2CF160730CEC16', 2, NULL, NULL, 2, NULL, 0, 'Lower the speed to medium, add the eggs and the vanilla and mix well.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8F1B608B1D79C42AC19A17132EB54BA', X'C6E5A8A8ACCED0418F2CF160730CEC16', X'C6E5A8A8ACCED0418F2CF160730CEC16', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whisk together the buttermilk and sour cream. In another bowl, sift together the flour, cocoa, baking soda, and salt. On low speed, add the buttermilk mixture and the flour mixture alternately in thirds to the mixer bowl, beginning with the buttermilk mixture and ending with the flour mixture. Mix only until blended. Fold the batter with a rubber spatula to be sure it''s completely blended.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7301980491479746BE019569971C7B9E', X'C6E5A8A8ACCED0418F2CF160730CEC16', X'C6E5A8A8ACCED0418F2CF160730CEC16', 4, NULL, NULL, 2, NULL, 0, 'Divide the batter among the cupcake pans (about ¾ of the way for each cupcake). Bake in the middle of the oven for 20 to 25 minutes, until a toothpick comes out clean. Cool for 10 minutes, remove from the pans, and allow to cool completely before frosting.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC2B5A631D1C694784C76B7C82731168', X'E16E0A15AF9FDF45A16580F3B40AA542', X'E16E0A15AF9FDF45A16580F3B40AA542', 0, NULL, NULL, 2, NULL, 0, 'Mix the confectioners'' sugar, peanut butter, butter, vanilla, and salt together. (In the bowl of an electric mixer fitted with a paddle attachment is ideal, but a hand mixer can be used). Mix on medium-low speed until creamy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DAC0F65FF190B409887C7022D93038C', X'E16E0A15AF9FDF45A16580F3B40AA542', X'E16E0A15AF9FDF45A16580F3B40AA542', 1, NULL, NULL, 2, NULL, 0, 'Add the cream and beat on high speed until the mixture is light and smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F3D78C504F2F145B6C70DCAF4D241DE', X'E16E0A15AF9FDF45A16580F3B40AA542', X'E16E0A15AF9FDF45A16580F3B40AA542', 2, NULL, NULL, 2, NULL, 0, 'Garnish with mini Reese''s cups or topper of your choice');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F7C4BC56D9F5B439EBE7CDAA5859384', X'7B41667DBA0AC540B972AAE016C6D582', X'7B41667DBA0AC540B972AAE016C6D582', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees. Line muffin tin with paper liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'746D8072C391F948A91DB0BB14E78CEB', X'7B41667DBA0AC540B972AAE016C6D582', X'7B41667DBA0AC540B972AAE016C6D582', 1, NULL, NULL, 2, NULL, 0, 'Sift together the flour, baking powder, baking soda, salt, cinnamon and ginger, into a medium bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E503BBF94D0F784A96F5581198726096', X'7B41667DBA0AC540B972AAE016C6D582', X'7B41667DBA0AC540B972AAE016C6D582', 2, NULL, NULL, 2, NULL, 0, 'In a larger bowl, whisk together the eggs, pumpkin purée, granulated sugar, brown sugar, and vegetable oil. Add the flour mixture and stir until combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24B9DA031C51FA4C9A8421AA607CF499', X'7B41667DBA0AC540B972AAE016C6D582', X'7B41667DBA0AC540B972AAE016C6D582', 3, NULL, NULL, 2, NULL, 0, 'Divide the batter among the muffin tin and bake for about 20 minutes, until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F2106A38D21844FB2A163FFC266F916', X'7B41667DBA0AC540B972AAE016C6D582', X'7B41667DBA0AC540B972AAE016C6D582', 4, NULL, NULL, 2, NULL, 0, 'Set pumpkin cupcakes aside to cool completely. These are delicious frosted with cream cheese icing or just as is!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62774BCBCBDBB747A1D6480C222ADE28', X'17F0EA702D227644931B61CEBE755C5C', X'17F0EA702D227644931B61CEBE755C5C', 0, NULL, NULL, 2, NULL, 0, 'Cream the cream cheese, butter and maple syrup in the bowl of an electric mixer fitted with the paddle attachment. Slowly add the icing sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1A568043DABC44C9F5426C2948A312C', X'17F0EA702D227644931B61CEBE755C5C', X'17F0EA702D227644931B61CEBE755C5C', 1, NULL, NULL, 2, NULL, 0, 'Once your cupcakes are cool, ice them with a butter knife or large piping bag and tip of your choice. (I used a large round tip – Ateco #809).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE3A4909A77A1F4C893F0AD9FA2122B5', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DB2A81365991B499DE2D0962E217EE2', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 1, NULL, NULL, 2, NULL, 0, 'Sift together the sugar, flour, freshly ground hazelnuts, cocoa powder, baking powder, baking soda, and salt; set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA68FB1243A80D42B1BFE12BF4C38A56', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, briefly whisk together the sour cream and vanilla extract; set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B59F4DD4A51B4F408E80BB466EFA3698', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 3, NULL, NULL, 2, NULL, 0, 'In the bowl of a mixer fitted with a paddle attachment, beat together the eggs, vegetable oil, and milk, until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FBD41AF97CBC24AA59F8A1684DB019E', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 4, NULL, NULL, 2, NULL, 0, 'Add the sour cream mixture and stir until well incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82C2FB706D215E4FAF10EEDDDF4F4A86', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 5, NULL, NULL, 2, NULL, 0, 'Add the flour mixture and stir on low speed until just incorporated. (The batter is fairly thin, don’t worry, you’ve done it correctly).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D4B1D6A8F0B1243A1C081CF00605DDD', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 6, NULL, NULL, 2, NULL, 0, 'Using an ice cream scoop, pour the batter into the cupcake liners (in muffin cupcake pan). Fill each liner no more than 2/3’s full.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D876AD24AC4557499D812342F39C351E', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 7, NULL, NULL, 2, NULL, 0, 'Bake in preheated oven for about 20 minutes, or until a toothpick inserted into the center comes out clean. (Ovens vary). Cool in the pan for a few minutes, then remove from the pan, and allow to cool once again.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB913E394237034DB847C0DE26E6AC83', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 8, NULL, NULL, 2, NULL, 0, 'Optional – Nutella center: Fill a pastry bag fitted with a #10 circle piping tip, with Nutella. Insert the tip into the center of the cupcake, and squeeze for a few seconds.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E21C92533716AE4886CED0819DED09F2', X'EDAD9FBC55D667468D534C5FAA56A982', X'EDAD9FBC55D667468D534C5FAA56A982', 9, NULL, NULL, 2, NULL, 0, 'Top cupcake with icing of your choice.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D4C6356FFC8BE4C9A851388EE3F4D2D', X'337BA2BC6C611A4684F42CD860D28EEB', X'337BA2BC6C611A4684F42CD860D28EEB', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B4A02709C3E4F47B4B7DAEBB7FA05E9', X'337BA2BC6C611A4684F42CD860D28EEB', X'337BA2BC6C611A4684F42CD860D28EEB', 1, NULL, NULL, 2, NULL, 0, 'Beat the oil, sugar, and vanilla together in the bowl of an electric mixer fitted with a paddle attachment. Add the eggs, mixing briefly between each egg.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287C9489B3159742823559AD14DCA87C', X'337BA2BC6C611A4684F42CD860D28EEB', X'337BA2BC6C611A4684F42CD860D28EEB', 2, NULL, NULL, 2, NULL, 0, 'In a different bowl, sift together the dry ingredients (flour, baking soda, salt cinnamon and nutmeg).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'827C9DCCF84F84458E94C8CC2FB2E18B', X'337BA2BC6C611A4684F42CD860D28EEB', X'337BA2BC6C611A4684F42CD860D28EEB', 3, NULL, NULL, 2, NULL, 0, 'With the mixer on low speed, add 1/2 of the dry ingredients to the wet ingredients. Add the grated carrots and raisins to the remaining flour, mix well, and add to the batter. Mix until just combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36B8C480FDBE1442AA4AE10F6989E46B', X'337BA2BC6C611A4684F42CD860D28EEB', X'337BA2BC6C611A4684F42CD860D28EEB', 4, NULL, NULL, 2, NULL, 0, 'Using an ice cream scoop, spoon the batter into cupcake liners (in your muffin pan), until each is 3/4 full.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'319641DB724B824191C324F977AC205E', X'337BA2BC6C611A4684F42CD860D28EEB', X'337BA2BC6C611A4684F42CD860D28EEB', 5, NULL, NULL, 2, NULL, 0, 'Bake at 350 degrees Fahrenheit for 25 minutes or until a toothpick comes out clean. Let cool.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4BAE872C43CCE4885FFC67DE2DF3CFB', X'EC12AE575AC4A94EB2B66B7DE8D9B762', X'EC12AE575AC4A94EB2B66B7DE8D9B762', 0, NULL, NULL, 2, NULL, 0, 'Cream the cream cheese, butter, and vanilla in the bowl of an electric mixer fitted with a paddle attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52A94FCEF1E3C644B5256BA59C4381E0', X'EC12AE575AC4A94EB2B66B7DE8D9B762', X'EC12AE575AC4A94EB2B66B7DE8D9B762', 1, NULL, NULL, 2, NULL, 0, 'Slowly add the icing sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7496E12CC0FBA4592EDE62ED0731EDA', X'EC12AE575AC4A94EB2B66B7DE8D9B762', X'EC12AE575AC4A94EB2B66B7DE8D9B762', 2, NULL, NULL, 2, NULL, 0, 'Add 1 tsp white food gel coloring and mix until incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BE49D88DDDFFC44899268DD5C1FD58C', X'EC12AE575AC4A94EB2B66B7DE8D9B762', X'EC12AE575AC4A94EB2B66B7DE8D9B762', 3, NULL, NULL, 2, NULL, 0, 'Once cupcakes have cooled, ice as desired.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BEEBB25AB1A7F4488651996049EE349', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. Line your muffin tin with liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'074857D05EE1324E9AA955783AD5F81D', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, sift together the flour, cocoa powder, baking powder, baking soda, and salt. Set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0161EA6E68C5D4408B8F04DD06629605', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine the sour cream and vanilla extract. Set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E6B6D122A1F0346B1C6735575238427', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 3, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with a paddle attachment, combine the butter and sugar and beat on medium speed until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85617DEA0B81DE4D80BFED18B91C4C15', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 4, NULL, NULL, 2, NULL, 0, 'Set the mixer to low speed and add the eggs, one at a time, scraping thoroughly between each addition.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A12C0B147F4124E848FAADB4C9BBCA8', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 5, NULL, NULL, 2, NULL, 0, 'Alternately add the flour and sour cream mixtures to the butter mixture in two batches, starting and ending with the flour mixture. Scrape down the bowl after each addition.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E417D1962B459D45B4B2217EE9EF9387', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 6, NULL, NULL, 2, NULL, 0, 'Gradually pour in the coffee. Scrape down the bowl and beat until thoroughly combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2153D47AFD3AD45BD5FFA8DF5ED107E', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 7, NULL, NULL, 2, NULL, 0, 'Scoop the batter into your cupcake liners with a spoon or ice cream scoop.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC1B8375B3E3D042AF2BF795BC44C885', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 8, NULL, NULL, 2, NULL, 0, 'Bake for 26 minutes or until the center of the cupcake has just set. (I insert a barbeque skewer to check).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4BDA3362B64864BB41668288DEAA8B9', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 9, NULL, NULL, 2, NULL, 0, 'Take out the cupcakes, make a little hole in the center with a knife and insert a Turtle chocolate as far as the cupcake allows.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A5AA03303D9BD45B1351B6B51C25C29', X'D64033BB635A454CA229690A3D800E40', X'D64033BB635A454CA229690A3D800E40', 10, NULL, NULL, 2, NULL, 0, 'Bake for another 4 minutes. Once cupcakes have cooled and you’ve removed them from the muffin tin, top with chocolate icing and *optional toppings.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAE46FBA9CE3634DB8A2AE16ADB7CB56', X'20AF729DD775BD4E9D91E95EA2462CCA', X'20AF729DD775BD4E9D91E95EA2462CCA', 0, NULL, NULL, 2, NULL, 0, 'Use a double boiler to melt the chocolate and cream together. (Make your own version by placing a glass or stainless steel bowl on top of a pot with simmering water). Stir occasionally until the chips are melted.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'295D5CCCD57144428B3301432893C1E2', X'20AF729DD775BD4E9D91E95EA2462CCA', X'20AF729DD775BD4E9D91E95EA2462CCA', 1, NULL, NULL, 2, NULL, 0, 'Off the heat, stir in the corn syrup and vanilla and allow the mixture to cool to room temperature.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B474DE9B490C4AB1EFA51264C2B72A', X'20AF729DD775BD4E9D91E95EA2462CCA', X'20AF729DD775BD4E9D91E95EA2462CCA', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with the whisk attachment, whisk the chocolate mixture, softened butter and icing sugar on medium speed for a few minutes, until it has thickened.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'633D62EBAA687541818CF3D1B3B9D3EE', X'5642B7F87E7D6D4C9184152EAD0A214D', X'5642B7F87E7D6D4C9184152EAD0A214D', 0, NULL, NULL, 2, NULL, 0, 'In mixer bowl, pour in the warm water and the meringue powder. Mix it with a whisk by hand until it is frothy and thickened…about 30 seconds.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B58181A8CDE7CE4D8F2B02344443DC9A', X'5642B7F87E7D6D4C9184152EAD0A214D', X'5642B7F87E7D6D4C9184152EAD0A214D', 1, NULL, NULL, 2, NULL, 0, 'Add in caramel flavoring and whisk for a few seconds.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6920ACA1A531B049B18D75D295A56649', X'5642B7F87E7D6D4C9184152EAD0A214D', X'5642B7F87E7D6D4C9184152EAD0A214D', 2, NULL, NULL, 2, NULL, 0, 'Sift the icing sugar. Pour all the icing sugar into the mixer bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15D48509330661409A0CADA15C077C95', X'5642B7F87E7D6D4C9184152EAD0A214D', X'5642B7F87E7D6D4C9184152EAD0A214D', 3, NULL, NULL, 2, NULL, 0, 'Using the paddle attachment on the LOWEST speed, mix slowly for a full 10 minutes. Icing will get thick and creamy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C6D5845F6275047A0D055F590215A47', X'5642B7F87E7D6D4C9184152EAD0A214D', X'5642B7F87E7D6D4C9184152EAD0A214D', 4, NULL, NULL, 2, NULL, 0, 'Cover the bowl with a dampened tea-towel to prevent crusting and drying.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85A0881B0986474AA671991F61C37E21', X'5642B7F87E7D6D4C9184152EAD0A214D', X'5642B7F87E7D6D4C9184152EAD0A214D', 5, NULL, NULL, 2, NULL, 0, 'Cover the bowl with a dampened tea-towel to prevent crusting and drying.Tint with food colorings or thin the icing with small amounts of warm water to reach the desired consistency.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B323C9C23465754FA46ECEC86E4F8821', X'286B0773CE4A1A40A09522AD8CF86893', X'286B0773CE4A1A40A09522AD8CF86893', 0, NULL, NULL, 2, NULL, 0, 'In mixer bowl, pour in the warm water and the meringue powder. Mix it with a whisk by hand until it is frothy and thickened…about 30 seconds.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'738D9426220020488388B8DF0DAE8DD4', X'286B0773CE4A1A40A09522AD8CF86893', X'286B0773CE4A1A40A09522AD8CF86893', 1, NULL, NULL, 2, NULL, 0, 'Sift the icing sugar and cocoa powder together. Give it a whisk to make sure the cocoa powder is incorporated fairly evenly.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60B6DAE4B885724D9F7606171AE343CA', X'286B0773CE4A1A40A09522AD8CF86893', X'286B0773CE4A1A40A09522AD8CF86893', 2, NULL, NULL, 2, NULL, 0, 'Pour in all the icing sugar and cocoa powder at once and place the bowl on the mixer.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A6BBD77B79E14ABB2A09B6CCF0FF1A', X'286B0773CE4A1A40A09522AD8CF86893', X'286B0773CE4A1A40A09522AD8CF86893', 3, NULL, NULL, 2, NULL, 0, 'Using the paddle attachment on the LOWEST speed, mix slowly for a full 10 minutes. Icing will get thick and creamy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE3B43A9B00C26438A6A3039CC2D22A4', X'286B0773CE4A1A40A09522AD8CF86893', X'286B0773CE4A1A40A09522AD8CF86893', 4, NULL, NULL, 2, NULL, 0, 'Cover the bowl with a dampened tea-towel to prevent crusting and drying.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F8420CE7A608F4B847A4D760785C766', X'286B0773CE4A1A40A09522AD8CF86893', X'286B0773CE4A1A40A09522AD8CF86893', 5, NULL, NULL, 2, NULL, 0, 'Tint with food colorings and/or thin the icing with small amounts of warm water to reach the desired consistency.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A1294282909B44B82D56777C6125BE6', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28A59CED96D0FE4080284E78CFC46441', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', 1, NULL, NULL, 2, NULL, 0, 'Cream the coconut oil and coconut sugar in the bowl of a stand mixer fitted with the paddle attachment, on high speed, for approximately 3 minutes. You may need to occasionally scrape down the sides of the bowl and the attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F205EB6D2A6AA64B9BC84B7446012523', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', 2, NULL, NULL, 2, NULL, 0, 'With the mixer on low speed, add the eggs, one at a time, letting each egg be mixed in before you add the next, and scraping down the bowl after each addition. Add the vanilla extract and mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7656CE3E4A779244A6CCBF08F4DA78A2', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the flour, baking soda, baking powder and salt. In two parts, alternately add the dry ingredients and the buttermilk to the batter, beginning and ending with the dry. Mix until just barely combined. Fold in the finely crushed coconut chips.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65297EC544A6F3438E4D1CEB80A8EBCB', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', 4, NULL, NULL, 2, NULL, 0, 'Line a muffin tin with paper liners. Fill each liner with batter, until about ¾ full. Bake for 20 minutes or until a toothpick comes out clean. Allow cupcakes to cool for about 15 minutes and then remove to a baking rack to completely cool.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B96563944A644443848663AA989DC7D3', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', X'35C9A2F43ABE4C4592EA280EEBE4EEF2', 5, NULL, NULL, 2, NULL, 0, 'Garnish with icing or serve as is!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F68C1298B45B4F9533B8176AB1DA16', X'07516A09772CC9469A49C1F614937540', X'07516A09772CC9469A49C1F614937540', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees. Line muffin tin with paper liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37986B34451DD944A1117C1EDF985BA6', X'07516A09772CC9469A49C1F614937540', X'07516A09772CC9469A49C1F614937540', 1, NULL, NULL, 2, NULL, 0, 'Sift together the flour, baking powder, baking soda, salt, cinnamon and ginger, into a medium bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFA320BFE4610D4183D706F890D026D4', X'07516A09772CC9469A49C1F614937540', X'07516A09772CC9469A49C1F614937540', 2, NULL, NULL, 2, NULL, 0, 'In a larger bowl, whisk together the eggs, pumpkin purée, granulated sugar, brown sugar, and Crisco pure vegetable oil. Add the flour mixture and stir until combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66D9928B46CB3D479754D91F67C0A1AB', X'07516A09772CC9469A49C1F614937540', X'07516A09772CC9469A49C1F614937540', 3, NULL, NULL, 2, NULL, 0, 'Divide the batter among the muffin tin and bake for about 20 minutes, until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F414F0E4CC9F9F4E8E46B1F24F0CC47B', X'07516A09772CC9469A49C1F614937540', X'07516A09772CC9469A49C1F614937540', 4, NULL, NULL, 2, NULL, 0, 'Set pumpkin cupcakes aside to cool completely. Frost with icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EDC92C03CBCD345B00ECA10408B0576', X'97E5F5E0F9F4A842841385C787CD859B', X'97E5F5E0F9F4A842841385C787CD859B', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer, using the paddle attachment, mix the Crisco All-vegetable shortening and clear vanilla extract on medium speed for about a minute. Scrape down the sides of the bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'131FD4E8E257AA499175A201BBAA6F46', X'97E5F5E0F9F4A842841385C787CD859B', X'97E5F5E0F9F4A842841385C787CD859B', 1, NULL, NULL, 2, NULL, 0, 'Add the icing sugar and milk, cover the bowl with a large tea towel, and mix on low speed until the icing sugar is incorporated into the shortening. Take the tea towel away and mix until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E24B0660E70BD94DBCF9C46CF3472FC6', X'97E5F5E0F9F4A842841385C787CD859B', X'97E5F5E0F9F4A842841385C787CD859B', 2, NULL, NULL, 2, NULL, 0, 'Once your cupcakes are cool, ice them with a butter knife or large piping bag and tip of your choice. (I used Ateco #855, #856 and Wilton 1M).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E24233501C1D244890425B0B24118A9', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'132BE8621EFD014E9FD7698594C3EB71', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 1, NULL, NULL, 2, NULL, 0, 'Sift together cake flour, baking powder and salt. Set aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4042C77EC932048B59608ED623E8B5C', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 2, NULL, NULL, 2, NULL, 0, 'Cream butter for about 3 minutes, scraping down sides of bowl occasionally.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D90E6ED7DC911D4A81D186576EAD63AF', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 3, NULL, NULL, 2, NULL, 0, 'Gradually add brown sugar, creaming after each addition. Set mixture aside.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70C2A55AB45B6442B1923F4DAECBCF80', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 4, NULL, NULL, 2, NULL, 0, 'Beat egg yolks until thick and lemon colored.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5FBEDE166DA5C469CD6EE5BA73D0954', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 5, NULL, NULL, 2, NULL, 0, 'Add to the creamed mixture in thirds, beating thoroughly after each addition.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3AE76053EBDED49881CE1F1D54BC0AC', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 6, NULL, NULL, 2, NULL, 0, 'Blend milk and maple syrup together.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3468B87FD2AFD4785F9170A5780E7DE', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 7, NULL, NULL, 2, NULL, 0, 'Beating only until smooth after each addition, alternately add the dry ingredients and liquids to the creamed mixture.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51F7C724ED85B9428E8B250C42E36D75', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 8, NULL, NULL, 2, NULL, 0, 'Beat only until smooth and fill cupcake liners using an ice cream scoop.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A355E4D20AC79F4EAD46E123E5452618', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 9, NULL, NULL, 2, NULL, 0, 'Bake for approximately 15 minutes or until a toothpick comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13AD4C0872E2D948A264A39035060357', X'C9DDCBD4E0056D4B84145EA57B52B3B2', X'C9DDCBD4E0056D4B84145EA57B52B3B2', 10, NULL, NULL, 2, NULL, 0, 'Once cool, frost cupcakes.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A9061DE926EF54FBE880EDA731E03F1', X'5BDF3104487C8F4CA963C534557A04CE', X'5BDF3104487C8F4CA963C534557A04CE', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a medium saucepan, and stir over low heat until sugar is dissolved.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94017657D7B5A44AB28BE72821ACE2B4', X'5BDF3104487C8F4CA963C534557A04CE', X'5BDF3104487C8F4CA963C534557A04CE', 1, NULL, NULL, 2, NULL, 0, 'Increase heat and bring to a boil.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBB7FEEDCA6D894BAF993B2416A40123', X'5BDF3104487C8F4CA963C534557A04CE', X'5BDF3104487C8F4CA963C534557A04CE', 2, NULL, NULL, 2, NULL, 0, 'Put candy thermometer in place and continue cooking without stirring. During cooking wash, sugar crystals from sides of pan with a pastry brush dipped in water. Cook until mixture reaches 238 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F85FA01DAA61468E2141EEC113115A', X'5BDF3104487C8F4CA963C534557A04CE', X'5BDF3104487C8F4CA963C534557A04CE', 3, NULL, NULL, 2, NULL, 0, 'Remove pan to cooling rack and cool to lukewarm (about 110 degrees Fahrenheit), without stirring or moving the pan.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC158B36026B5847B95F4CC2C9F2896D', X'5BDF3104487C8F4CA963C534557A04CE', X'5BDF3104487C8F4CA963C534557A04CE', 4, NULL, NULL, 2, NULL, 0, 'Beat vigorously with a wooden spoon or electric mixer until mixture begins to lose its gloss and is of spreading consistency. Ice cupcakes. If frosting becomes too thick, beat in a few drops of cream or milk.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3BDF489B834A47B13F00E60A83ED25', X'2CA45960ED505542BDDCC8AC29C2CF96', X'2CA45960ED505542BDDCC8AC29C2CF96', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of stand mixer, using a paddle attachment, whip butter and cocoa powder together until well incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16EE01E343AFA1478B7BD2C613D0D7EA', X'2CA45960ED505542BDDCC8AC29C2CF96', X'2CA45960ED505542BDDCC8AC29C2CF96', 1, NULL, NULL, 2, NULL, 0, 'Add icing sugar, stir, and slowly pour in milk until mixed. Add in vanilla extract and mix.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62AEB9235295C242A8F33E720301E59A', X'2CA45960ED505542BDDCC8AC29C2CF96', X'2CA45960ED505542BDDCC8AC29C2CF96', 2, NULL, NULL, 2, NULL, 0, 'Using a rubber spatula, scrape down the sides of the bowl and whip on medium speed until light and fluffy (approximately 1 minute).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22473368469F4B47B2964EF70F90D0D8', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit or 180 degrees Celsius.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07789A8F15C9344EB0E5EA96E6AED06E', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 1, NULL, NULL, 2, NULL, 0, 'In a bowl of an electric mixer fitted with the paddle attachment, or in a large bowl, beat butter with sugar until fluffy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F7DE567C70C994F8FBCE6E885D5B250', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 2, NULL, NULL, 2, NULL, 0, 'Beat in eggs, 1 at a time.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCC2D688440C264880F4157F9057ED0C', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 3, NULL, NULL, 2, NULL, 0, 'Beat in lemon zest and lemon juice.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'407FB9C45136234EADA136684D365C43', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, baking powder and salt; stir into butter mixture alternately with milk, making 3 additions of dry ingredients and 2 of milk.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCAB836C451BFE438810FCD4596CFC63', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 5, NULL, NULL, 2, NULL, 0, 'Fold raspberries into the batter.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDCAA9D0E38DED47A842675B5F3470C2', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 6, NULL, NULL, 2, NULL, 0, 'Scoop into paper-lined or a greased muffin tin.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'880BC398F531E14A84ADBB9B77DC5579', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 7, NULL, NULL, 2, NULL, 0, 'Bake in oven until a cake tester comes out clean, 20 - 25 minutes.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F508BB37FA67F44892AAAF66A63263BF', X'DE0BC119F0377142BB3D5605066FD14F', X'DE0BC119F0377142BB3D5605066FD14F', 8, NULL, NULL, 2, NULL, 0, 'Transfer to rack an let cool before icing.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1604C54FE2DBCD46BA095B25211916F3', X'763D7F210628DB488C146B90BA58DB90', X'763D7F210628DB488C146B90BA58DB90', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer fitted with the paddle attachment, cream butter until smooth and creamy, 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'275053721406A94A9DC68BC3FFDBFF37', X'763D7F210628DB488C146B90BA58DB90', X'763D7F210628DB488C146B90BA58DB90', 1, NULL, NULL, 2, NULL, 0, 'With the mixer on low speed, add 6 cups sugar, milk, and raspberry coulis; mix until light and fluffy. If necessary, gradually add remaining 2 cups sugar to reach desired consistency.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'744323F04DF00942A4BFE4D483D498D3', X'D2A01CDD9149904C88B3D9D511BE13C5', X'D2A01CDD9149904C88B3D9D511BE13C5', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F. Place liners in cupcake pan.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCB2753AE34D9849842BF55F1EDC4E8E', X'D2A01CDD9149904C88B3D9D511BE13C5', X'D2A01CDD9149904C88B3D9D511BE13C5', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and 2 sugars on high speed until light and fluffy, approximately 5 minutes. (In the bowl of an electric mixer fitted with a paddle attachment is ideal, but a hand mixer can be used as well).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'128EB73C07120949AF88C46148551699', X'D2A01CDD9149904C88B3D9D511BE13C5', X'D2A01CDD9149904C88B3D9D511BE13C5', 2, NULL, NULL, 2, NULL, 0, 'Lower the speed to medium, add the eggs and the vanilla and mix well.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72420B0E176C8A4EA2125C2B8CFAEEB0', X'D2A01CDD9149904C88B3D9D511BE13C5', X'D2A01CDD9149904C88B3D9D511BE13C5', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whisk together the buttermilk and sour cream. In another bowl, sift together the flour, cocoa, baking soda, and salt. On low speed, add the buttermilk mixture and the flour mixture alternately in thirds to the mixer bowl, beginning with the buttermilk mixture and ending with the flour mixture. Mix only until blended. Fold the batter with a rubber spatula to be sure it''s completely blended.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F24028DF8161F94EA4B80BB5B85C206C', X'D2A01CDD9149904C88B3D9D511BE13C5', X'D2A01CDD9149904C88B3D9D511BE13C5', 4, NULL, NULL, 2, NULL, 0, 'Divide the batter among the cupcake pans (about ¾ of the way for each cupcake). Bake in the middle of the oven for 20 to 25 minutes, until a toothpick comes out clean. Cool for 10 minutes, remove from the pans, and allow to cool completely before frosting.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EDE0E0D69248548AEB6C3B93DC93974', X'CEEEABBD1ADBCD4E981966B38E9BCE8F', X'CEEEABBD1ADBCD4E981966B38E9BCE8F', 0, NULL, NULL, 2, NULL, 0, 'Mix the confectioners'' sugar, peanut butter, butter, vanilla, and salt together. (In the bowl of an electric mixer fitted with a paddle attachment is ideal, but a hand mixer can be used). Mix on medium-low speed until creamy.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CDB33D114E83341A96FBBBCCD89CF8A', X'CEEEABBD1ADBCD4E981966B38E9BCE8F', X'CEEEABBD1ADBCD4E981966B38E9BCE8F', 1, NULL, NULL, 2, NULL, 0, 'Add the cream and beat on high speed until the mixture is light and smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2C015606D8D7C448239D5FACAAB2C90', X'CEEEABBD1ADBCD4E981966B38E9BCE8F', X'CEEEABBD1ADBCD4E981966B38E9BCE8F', 2, NULL, NULL, 2, NULL, 0, 'Garnish with mini Reese''s cups or topper of your choice');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A29A5F69B66194487F2A092FF18182B', X'92D02DDE469C2F4AA84298078995DA0C', X'92D02DDE469C2F4AA84298078995DA0C', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees. Line muffin tin with paper liners.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8C4C1BE0897884187599F7ED7E88629', X'92D02DDE469C2F4AA84298078995DA0C', X'92D02DDE469C2F4AA84298078995DA0C', 1, NULL, NULL, 2, NULL, 0, 'Sift together the flour, baking powder, baking soda, salt, cinnamon and ginger, into a medium bowl.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7A4D6AD1870324EAA37314F80014551', X'92D02DDE469C2F4AA84298078995DA0C', X'92D02DDE469C2F4AA84298078995DA0C', 2, NULL, NULL, 2, NULL, 0, 'In a larger bowl, whisk together the eggs, pumpkin purée, granulated sugar, brown sugar, and vegetable oil. Add the flour mixture and stir until combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B9B6FCCAEA15847A5F66009C7DAB384', X'92D02DDE469C2F4AA84298078995DA0C', X'92D02DDE469C2F4AA84298078995DA0C', 3, NULL, NULL, 2, NULL, 0, 'Divide the batter among the muffin tin and bake for about 20 minutes, until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D2B9A0322E9C9409AF52683C3FC4E92', X'92D02DDE469C2F4AA84298078995DA0C', X'92D02DDE469C2F4AA84298078995DA0C', 4, NULL, NULL, 2, NULL, 0, 'Set pumpkin cupcakes aside to cool completely. These are delicious frosted with cream cheese icing or just as is!');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA78261D4B37BA4C83B7765D4517AD4C', X'0937CA69808FBA40868F5751A337CBE2', X'0937CA69808FBA40868F5751A337CBE2', 0, NULL, NULL, 2, NULL, 0, 'Cream the cream cheese, butter and maple syrup in the bowl of an electric mixer fitted with the paddle attachment. Slowly add the icing sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37C0E682665ABC4586B0E8EED0D01634', X'0937CA69808FBA40868F5751A337CBE2', X'0937CA69808FBA40868F5751A337CBE2', 1, NULL, NULL, 2, NULL, 0, 'Once your cupcakes are cool, ice them with a butter knife or large piping bag and tip of your choice. (I used a large round tip – Ateco #809).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E4761AFC0652148A01562BEDE42F347', X'17210FF49BC4564382D4E5ECDEDA14A6', X'17210FF49BC4564382D4E5ECDEDA14A6', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF05113587381449B27FB3444CF9895C', X'17210FF49BC4564382D4E5ECDEDA14A6', X'17210FF49BC4564382D4E5ECDEDA14A6', 1, NULL, NULL, 2, NULL, 0, 'Beat the oil, sugar, and vanilla together in the bowl of an electric mixer fitted with a paddle attachment. Add the eggs, mixing briefly between each egg.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E33F0650B33452469BD6B8C2FED6B622', X'17210FF49BC4564382D4E5ECDEDA14A6', X'17210FF49BC4564382D4E5ECDEDA14A6', 2, NULL, NULL, 2, NULL, 0, 'In a different bowl, sift together the dry ingredients (flour, baking soda, salt cinnamon and nutmeg).');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4EA9D78A26FA4419478F4ABF523F935', X'17210FF49BC4564382D4E5ECDEDA14A6', X'17210FF49BC4564382D4E5ECDEDA14A6', 3, NULL, NULL, 2, NULL, 0, 'With the mixer on low speed, add 1/2 of the dry ingredients to the wet ingredients. Add the grated carrots and raisins to the remaining flour, mix well, and add to the batter. Mix until just combined.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'722A40ABB5E2D74AAF2555D66DBE4984', X'17210FF49BC4564382D4E5ECDEDA14A6', X'17210FF49BC4564382D4E5ECDEDA14A6', 4, NULL, NULL, 2, NULL, 0, 'Using an ice cream scoop, spoon the batter into cupcake liners (in your muffin pan), until each is 3/4 full.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'100AB8D602C64A4296614A1F5666FB7F', X'17210FF49BC4564382D4E5ECDEDA14A6', X'17210FF49BC4564382D4E5ECDEDA14A6', 5, NULL, NULL, 2, NULL, 0, 'Bake at 350 degrees Fahrenheit for 25 minutes or until a toothpick comes out clean. Let cool.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A0F841AE6FE3840A89797ED03149F59', X'B602E5749A7E2B4A9230236413EC69F1', X'B602E5749A7E2B4A9230236413EC69F1', 0, NULL, NULL, 2, NULL, 0, 'Cream the cream cheese, butter, and vanilla in the bowl of an electric mixer fitted with a paddle attachment.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA74E66943CCE641AE143F2C854A3397', X'B602E5749A7E2B4A9230236413EC69F1', X'B602E5749A7E2B4A9230236413EC69F1', 1, NULL, NULL, 2, NULL, 0, 'Slowly add the icing sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA12BD5DFC03D64084E3FA13AB2792B2', X'B602E5749A7E2B4A9230236413EC69F1', X'B602E5749A7E2B4A9230236413EC69F1', 2, NULL, NULL, 2, NULL, 0, 'Add 1 tsp white food gel coloring and mix until incorporated.');
INSERT INTO `VWR INTERNAL Sweetopia recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABB7BE19F6B7124BA95E1CF12447CC89', X'B602E5749A7E2B4A9230236413EC69F1', X'B602E5749A7E2B4A9230236413EC69F1', 3, NULL, NULL, 2, NULL, 0, 'Once cupcakes have cooled, ice as desired.');
